INSERT INTO [t20766] VALUES (268,'قوله تعالى: {وأنزلنا إليكم نورا مبينا (١٧٤)}','543','208','0');
INSERT INTO [t20766] VALUES (269,'قوله تعالى: {فإن كانتا اثنتين فلهما الثلثان مما ترك}','544','208','0');
INSERT INTO [t20766] VALUES (270,'سورة المائدة','546','0','0');
INSERT INTO [t20766] VALUES (271,'قوله تعالى: {أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم}،','547','270','0');
INSERT INTO [t20766] VALUES (272,'قوله تعالى: {وإذا حللتم فاصطادوا}','547','270','0');
