INSERT INTO [t20766] VALUES (401,'قوله تعالى: {وقطعنا دابر الذين كذبوا بآياتنا}','924','365','0');
INSERT INTO [t20766] VALUES (402,'قوله تعالى: {فعقروا الناقة}','924','365','0');
INSERT INTO [t20766] VALUES (403,'قوله تعالى: {وقالوا ياصالح ائتنا بما تعدنا}','924','365','0');
INSERT INTO [t20766] VALUES (404,'قوله تعالى: {فأخذتهم الرجفة فأصبحوا في دارهم جاثمين}','925','365','0');
INSERT INTO [t20766] VALUES (405,'قوله تعالى: {فتولى عنهم وقال ياقوم لقد أبلغتكم رسالات ربي}','925','365','0');
INSERT INTO [t20766] VALUES (406,'قوله تعالى: {أتأتون الفاحشة ما سبقكم بها من أحد من العالمين (٨٠)}','925','365','0');
INSERT INTO [t20766] VALUES (407,'قوله تعالى: {فأنجيناه وأهله}','926','365','0');
INSERT INTO [t20766] VALUES (408,'قوله تعالى: {وتصدون عن سبيل الله من آمن به وتبغونها عوجا}','926','365','0');
INSERT INTO [t20766] VALUES (409,'قوله تعالى: {وإن كان طائفة منكم آمنوا بالذي أرسلت به وطائفة لم يؤمنوا فاصبروا حتى يحكم الله بيننا وهو خير الحاكمين (٨٧)}','927','365','0');
INSERT INTO [t20766] VALUES (410,'قوله تعالى: {فتولى عنهم وقال ياقوم لقد أبلغتكم رسالات ربي ونصحت لكم فكيف آسى على قوم كافرين (٩٣)}','927','365','0');
INSERT INTO [t20766] VALUES (411,'قوله تعالى: {تلك القرى نقص عليك من أنبائها}','928','365','0');
INSERT INTO [t20766] VALUES (412,'قوله تعالى: {فما كانوا ليؤمنوا بما كذبوا به من قبل}','928','365','0');
INSERT INTO [t20766] VALUES (413,'قوله تعالى: {ثم بعثنا من بعدهم موسى بآياتنا إلى فرعون وملئه فظلموا بها}','930','365','0');
INSERT INTO [t20766] VALUES (414,'قوله تعالى: {ونزع يده فإذا هي بيضاء للناظرين (١٠٨)}','930','365','0');
INSERT INTO [t20766] VALUES (415,'قوله تعالى: {قال الملأ من قوم فرعون إن هذا لساحر عليم}','930','365','0');
INSERT INTO [t20766] VALUES (416,'قوله تعالى: {فلما ألقوا سحروا أعين الناس واسترهبوهم وجاءوا بسحر عظيم (١١٦)}','931','365','0');
INSERT INTO [t20766] VALUES (417,'قوله تعالى: {ثم لأصلبنكم أجمعين (١٢٤)}','931','365','0');
INSERT INTO [t20766] VALUES (418,'قوله تعالى: {وإن تصبهم سيئة يطيروا بموسى ومن معه}','931','365','0');
INSERT INTO [t20766] VALUES (419,'قوله تعالى: {وأورثنا القوم الذين كانوا يستضعفون مشارق الأرض ومغاربها}','932','365','0');
INSERT INTO [t20766] VALUES (420,'قوله تعالى: {وتمت كلمت ربك الحسنى على بني إسرائيل}','932','365','0');
INSERT INTO [t20766] VALUES (421,'قوله تعالى: {قال رب أرني أنظر إليك قال لن تراني}','932','365','0');
INSERT INTO [t20766] VALUES (422,'قوله تعالى: {ألم يروا أنه لا يكلمهم ولا يهديهم سبيلا اتخذوه وكانوا ظالمين (١٤٨)}','933','365','0');
INSERT INTO [t20766] VALUES (423,'قوله تعالى: {ولما سقط في أيديهم ورأوا أنهم قد ضلوا قالوا لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخاسرين (١٤٩)}','934','365','0');
INSERT INTO [t20766] VALUES (424,'قوله تعالى: {ولما رجع موسى إلى قومه غضبان أسفا قال بئسما خلفتموني من بعدي أعجلتم أمر ربكم}','934','365','0');
INSERT INTO [t20766] VALUES (425,'قوله تعالى: {وأخذ برأس أخيه يجره إليه قال ابن أم إن القوم استضعفوني}','935','365','0');
INSERT INTO [t20766] VALUES (426,'قوله تعالى: {قل يا أيها الناس إني رسول الله إليكم جميعا}','935','365','0');
INSERT INTO [t20766] VALUES (427,'قوله تعالى: {فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته}','935','365','0');
INSERT INTO [t20766] VALUES (428,'قوله تعالى: {ألم يؤخذ عليهم ميثاق الكتاب أن لا يقولوا على الله إلا الحق}','936','365','0');
INSERT INTO [t20766] VALUES (429,'قوله تعالى: {وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم وأشهدهم على أنفسهم ألست بربكم قالوا بلى شهدنا أن تقولوا يوم القيامة إنا كنا عن هذا غافلين (١٧٢) أو تقولوا إنما أشرك آباؤنا من قبل وكنا ذرية من بعدهم أفتهلكنا بما فعل المبطلون (١٧٣)}','936','365','0');
INSERT INTO [t20766] VALUES (430,'قوله تعالى: {وذروا الذين يلحدون في أسمائه سيجزون ما كانوا يعملون (١٨٠)}','941','365','0');
INSERT INTO [t20766] VALUES (431,'قوله تعالى: {إنما علمها عند ربي لا يجليها لوقتها إلا هو}','941','365','0');
INSERT INTO [t20766] VALUES (432,'قوله تعالى: {ولو كنت أعلم الغيب لاستكثرت من الخير}','942','365','0');
INSERT INTO [t20766] VALUES (433,'قوله تعالى: {وجعل منها زوجها ليسكن إليها}','942','365','0');
INSERT INTO [t20766] VALUES (434,'قوله تعالى: {فلما آتاهما صالحا جعلا له شركاء فيما آتاهما فتعالى الله عما يشركون (١٩٠)}','943','365','0');
INSERT INTO [t20766] VALUES (435,'قوله تعالى: {خذ العفو وأمر بالعرف وأعرض عن الجاهلين (١٩٩)}','944','365','0');
INSERT INTO [t20766] VALUES (436,'قوله تعالى: {وإخوانهم يمدونهم في الغي ثم لا يقصرون}','944','365','0');
INSERT INTO [t20766] VALUES (437,'سورة الأنفال','946','0','0');
INSERT INTO [t20766] VALUES (438,'قوله تعالى: {يسألونك عن الأنفال قل الأنفال لله والرسول}','947','437','0');
INSERT INTO [t20766] VALUES (439,'قوله تعالى: {وإذا تليت عليهم آياته زادتهم إيمانا}','950','437','0');
INSERT INTO [t20766] VALUES (440,'قوله تعالى: {إذ يغشيكم النعاس أمنة منه}','951','437','0');
INSERT INTO [t20766] VALUES (441,'قوله تعالى: {إن تستفتحوا فقد جاءكم الفتح}','951','437','0');
INSERT INTO [t20766] VALUES (442,'قوله تعالى: {واعلموا أنما أموالكم وأولادكم فتنة وأن الله عنده أجر عظيم (٢٨)}','952','437','0');
INSERT INTO [t20766] VALUES (443,'قوله تعالى: {يا أيها الذين آمنوا إن تتقوا الله يجعل لكم فرقانا ويكفر عنكم سيئاتكم ويغفر لكم والله ذو الفضل العظيم (٢٩)}','953','437','0');
INSERT INTO [t20766] VALUES (444,'قوله تعالى: {قالوا قد سمعنا لو نشاء لقلنا مثل هذا}','954','437','0');
INSERT INTO [t20766] VALUES (445,'قوله تعالى: {وإذ قالوا اللهم إن كان هذا هو الحق من عندك فأمطر علينا حجارة من السماء أو ائتنا بعذاب أليم (٣٢)}','955','437','0');
INSERT INTO [t20766] VALUES (446,'قوله تعالى: {وما لهم ألا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا أولياءه إن أولياؤه إلا المتقون}','955','437','0');
INSERT INTO [t20766] VALUES (447,'قوله تعالى: {وما كان صلاتهم عند البيت إلا مكاء وتصدية}','956','437','0');
INSERT INTO [t20766] VALUES (448,'قوله تعالى: {واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل}','956','437','0');
INSERT INTO [t20766] VALUES (449,'قوله تعالى: {يا أيها الذين آمنوا إذا لقيتم فئة فاثبتوا واذكروا الله كثيرا لعلكم تفلحون (٤٥)}','1027','437','0');
INSERT INTO [t20766] VALUES (450,'قوله تعالى: {ولا تنازعوا فتفشلوا وتذهب ريحكم}','1029','437','0');
INSERT INTO [t20766] VALUES (451,'قوله تعالى: {وإذ زين لهم الشيطان أعمالهم وقال لا غالب لكم اليوم من الناس}','1030','437','0');
INSERT INTO [t20766] VALUES (452,'قوله تعالى: {يا أيها النبي حسبك الله ومن اتبعك من المؤمنين (٦٤)}','1031','437','0');
INSERT INTO [t20766] VALUES (453,'قوله تعالى: {وأولو الأرحام بعضهم أولى ببعض في كتاب الله إن الله بكل شيء عليم (٧٥)}','1033','437','0');
INSERT INTO [t20766] VALUES (454,'سورة التوبة','1042','0','0');
INSERT INTO [t20766] VALUES (455,'قوله تعالى: {براءة من الله ورسوله إلى الذين عاهدتم}','1045','454','0');
INSERT INTO [t20766] VALUES (456,'قوله تعالى: {فسيحوا في الأرض أربعة أشهر}','1046','454','0');
INSERT INTO [t20766] VALUES (457,'قوله تعالى: {إلا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا ولم يظاهروا عليكم أحدا فأتموا إليهم عهدهم إلى مدتهم}','1047','454','0');
INSERT INTO [t20766] VALUES (458,'قوله تعالى: {فإذا انسلخ الأشهر الحرم}','1047','454','0');
INSERT INTO [t20766] VALUES (459,'قوله تعالى: {وهموا بإخراج الرسول}','1048','454','0');
INSERT INTO [t20766] VALUES (460,'قوله تعالى: {يا أيها الذين آمنوا لا تتخذوا آباءكم وإخوانكم أولياء إن استحبوا الكفر على الإيمان}','1048','454','0');
INSERT INTO [t20766] VALUES (461,'قوله تعالى: {ويوم حنين إذ أعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت ثم وليتم مدبرين (٢٥)}','1049','454','0');
INSERT INTO [t20766] VALUES (462,'قوله تعالى: {انفروا خفافا وثقالا}','1095','454','0');
INSERT INTO [t20766] VALUES (463,'قوله تعالى: {وفي الرقاب}','1095','454','0');
INSERT INTO [t20766] VALUES (464,'قوله تعالى: {والذين يؤذون رسول الله لهم عذاب أليم (٦١)}','1095','454','0');
INSERT INTO [t20766] VALUES (465,'قوله تعالى: {يحذر المنافقون أن تنزل عليهم سورة}','1096','454','0');
INSERT INTO [t20766] VALUES (466,'قوله تعالى: {وما نقموا إلا أن أغناهم الله ورسوله من فضله}','1096','454','0');
INSERT INTO [t20766] VALUES (467,'قوله تعالى: {قل نار جهنم أشد حرا لو كانوا يفقهون (٨١)}','1097','454','0');
INSERT INTO [t20766] VALUES (468,'قوله تعالى: {فإن رجعك الله إلى طائفة منهم فاستأذنوك للخروج فقل لن تخرجوا معي أبدا}','1098','454','0');
INSERT INTO [t20766] VALUES (469,'قوله تعالى: {وإذا أنزلت سورة أن آمنوا بالله وجاهدوا مع رسوله استأذنك أولو الطول منهم وقالوا ذرنا نكن مع القاعدين (٨٦)}','1099','454','0');
INSERT INTO [t20766] VALUES (470,'قوله تعالى: {والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه}','1099','454','0');
INSERT INTO [t20766] VALUES (471,'قوله تعالى: {ومن أهل المدينة مردوا على النفاق لا تعلمهم نحن نعلمهم}','1100','454','0');
INSERT INTO [t20766] VALUES (472,'قوله تعالى: {وما كان استغفار إبراهيم لأبيه إلا عن موعدة وعدها إياه}','1101','454','0');
INSERT INTO [t20766] VALUES (473,'قوله تعالى: {لقد جاءكم رسول من أنفسكم عزيز عليه ماعنتم حريص عليكم بالمؤمنين رءوف رحيم (١٢٨)}','1101','454','0');
INSERT INTO [t20766] VALUES (474,'قوله تعالى: {عليه توكلت وهو رب العرش العظيم (١٢٩)}','1101','454','0');
INSERT INTO [t20766] VALUES (475,'قوله تعالى: {والذين كفروا لهم شراب من حميم}','1103','454','0');
INSERT INTO [t20766] VALUES (476,'قوله تعالى: {وتحيتهم فيها سلام}','1104','454','0');
INSERT INTO [t20766] VALUES (477,'قوله تعالى: {وإذا مس الإنسان الضر دعانا لجنبه أو قاعدا أو قائما فلما كشفنا عنه ضره مر كأن لم يدعنا إلى ضر مسه}','1104','454','0');
INSERT INTO [t20766] VALUES (478,'قوله تعالى: {قل ما يكون لي أن أبدله من تلقاء نفسي}','1105','454','0');
INSERT INTO [t20766] VALUES (479,'قوله تعالى: {فقد لبثت فيكم عمرا من قبله أفلا تعقلون}','1105','454','0');
INSERT INTO [t20766] VALUES (480,'قوله تعالى: {إنما مثل الحياة الدنيا كماء أنزلناه من السماء}','1106','454','0');
INSERT INTO [t20766] VALUES (481,'قوله تعالى: {ويوم نحشرهم جميعا}','1107','454','0');
INSERT INTO [t20766] VALUES (482,'قوله تعالى: {قل من يرزقكم من السماء والأرض أمن يملك السمع والأبصار ومن يخرج الحي من الميت ويخرج}','1108','454','0');
INSERT INTO [t20766] VALUES (483,'وقوله تعالى: {قل هل من شركائكم من يبدأ الخلق} إلى قوله: {فأنى تؤفكون (٣٤)}','1109','454','0');
INSERT INTO [t20766] VALUES (484,'قوله تعالى: {وما كان هذا القرآن أن يفترى من دون الله ولكن تصديق الذي بين يديه وتفصيل الكتاب لا ريب فيه من رب العالمين (٣٧)}','1110','454','0');
INSERT INTO [t20766] VALUES (485,'قوله تعالى: {بل كذبوا بما لم يحيطوا بعلمه ولما يأتهم تأويله}','1111','454','0');
INSERT INTO [t20766] VALUES (486,'قوله تعالى: {وإن كذبوك فقل لي عملي ولكم عملكم أنتم بريئون مما أعمل وأنا بريء مما تعملون (٤١)}','1111','454','0');
INSERT INTO [t20766] VALUES (487,'قوله تعالى: {ويوم يحشرهم كأن لم يلبثوا إلا ساعة من النهار}','1112','454','0');
INSERT INTO [t20766] VALUES (488,'قوله تعالى: {يتعارفون بينهم}','1113','454','0');
INSERT INTO [t20766] VALUES (489,'قوله تعالى: {قد خسر الذين كذبوا بلقاء الله وما كانوا مهتدين (٤٥)}','1113','454','0');
INSERT INTO [t20766] VALUES (490,'قوله تعالى: {ولكل أمة رسول}','1116','454','0');
INSERT INTO [t20766] VALUES (491,'قوله تعالى: {فإذا جاء رسولهم قضي بينهم بالقسط وهم لا يظلمون (٤٧)}','1116','454','0');
INSERT INTO [t20766] VALUES (492,'قوله تعالى: {لكل أمة أجل إذا جاء أجلهم فلا يستأخرون ساعة ولا يستقدمون (٤٩)}','1117','454','0');
INSERT INTO [t20766] VALUES (493,'قوله تعالى: {أثم إذا ما وقع آمنتم به آلآن وقد كنتم به تستعجلون (٥١)}','1117','454','0');
INSERT INTO [t20766] VALUES (494,'قوله تعالى: {إن الله سيبطله}','1118','454','0');
INSERT INTO [t20766] VALUES (495,'قوله تعالى: {إن الذين حقت عليهم كلمت ربك لا يؤمنون}','1118','454','0');
INSERT INTO [t20766] VALUES (496,'قوله تعالى: {إلا قوم يونس لما آمنوا كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم إلى حين (٩٨)}','1119','454','0');
INSERT INTO [t20766] VALUES (497,'قوله تعالى: {ولو شاء ربك لآمن من في الأرض كلهم جميعا}','1119','454','0');
INSERT INTO [t20766] VALUES (498,'قوله تعالى: {أفأنت تكره الناس حتى يكونوا مؤمنين (٩٩)}','1120','454','0');
INSERT INTO [t20766] VALUES (499,'وقوله تعالى: {قل انظروا ماذا في السماوات والأرض}','1120','454','0');
INSERT INTO [t20766] VALUES (500,'قوله تعالى: {وأن أقم وجهك للدين}','1121','454','0');
INSERT INTO [t20766] VALUES (501,'قوله تعالى: {واصبر حتى يحكم الله وهو خير الحاكمين (١٠٩)}','1121','454','0');
INSERT INTO [t20766] VALUES (502,'سورة هود','1123','0','0');
INSERT INTO [t20766] VALUES (503,'قوله تعالى: {الر كتاب أحكمت آياته ثم فصلت من لدن حكيم خبير (١)}','1124','502','0');
INSERT INTO [t20766] VALUES (504,'قوله تعالى: {ألا تعبدوا إلا الله إنني لكم منه نذير وبشير (٢)}','1129','502','0');
INSERT INTO [t20766] VALUES (505,'قوله تعالى: {وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى}','1130','502','0');
INSERT INTO [t20766] VALUES (506,'قوله تعالى: {ألا إنهم يثنون صدورهم ليستخفوا منه ألا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون إنه عليم بذات الصدور (٥)}','1131','502','0');
INSERT INTO [t20766] VALUES (507,'قوله تعالى: {وهو الذي خلق السماوات والأرض في ستة أيام وكان عرشه على الماء ليبلوكم أيكم أحسن عملا}','1135','502','0');
INSERT INTO [t20766] VALUES (508,'قوله تعالى: {ولئن أخرنا عنهم العذاب إلى أمة معدودة}','1136','502','0');
INSERT INTO [t20766] VALUES (509,'قوله تعالى: {من كان يريد الحياة الدنيا وزينتها نوف إليهم أعمالهم فيها وهم فيها لا يبخسون (١٥)}','1137','502','0');
INSERT INTO [t20766] VALUES (510,'قوله تعالى: {ومن يكفر به من الأحزاب فالنار موعده}','1137','502','0');
INSERT INTO [t20766] VALUES (511,'قوله تعالى: {فلا تك في مرية منه إنه الحق من ربك}','1138','502','0');
INSERT INTO [t20766] VALUES (512,'قوله تعالى: {ولكن أكثر الناس لا يؤمنون (١٧)}','1138','502','0');
INSERT INTO [t20766] VALUES (513,'قوله تعالى: {يضاعف لهم العذاب}','1138','502','0');
INSERT INTO [t20766] VALUES (514,'قوله تعالى: {ما كانوا يستطيعون السمع وما كانوا يبصرون}','1139','502','0');
INSERT INTO [t20766] VALUES (515,'قوله تعالى: {مثل الفريقين كالأعمى والأصم والبصير والسميع}','1141','502','0');
INSERT INTO [t20766] VALUES (516,'قوله تعالى: {وما نراك اتبعك إلا الذين هم أراذلنا بادي الرأي}','1142','502','0');
INSERT INTO [t20766] VALUES (517,'قوله تعالى: {قال ياقوم أرأيتم إن كنت على بينة من ربي وآتاني رحمة من عنده فعميت عليكم أنلزمكموها وأنتم لها كارهون (٢٨)}','1142','502','0');
INSERT INTO [t20766] VALUES (518,'قوله تعالى: {وياقوم لا أسألكم عليه مالا إن أجري إلا على الله}','1143','502','0');
