INSERT INTO [t20766] VALUES (649,'قوله تعالى: {خلق الإنسان من نطفة}','1378','644','0');
INSERT INTO [t20766] VALUES (650,'قوله تعالى: {والأنعام خلقها لكم فيها دفء ومنافع ومنها تأكلون (٥)}','1381','644','0');
INSERT INTO [t20766] VALUES (651,'قوله تعالى: {ويخلق ما لا تعلمون (٨)}','1384','644','0');
INSERT INTO [t20766] VALUES (652,'قوله تعالى: {وعلى الله قصد السبيل ومنها جائر}','1386','644','0');
INSERT INTO [t20766] VALUES (653,'قوله تعالى: {ولو شاء لهداكم أجمعين (٩)}','1387','644','0');
INSERT INTO [t20766] VALUES (654,'قوله تعالى: {هو الذي أنزل من السماء ماء لكم منه شراب}','1388','644','0');
INSERT INTO [t20766] VALUES (655,'قوله تعالى: {وسخر لكم الليل والنهار والشمس والقمر والنجوم مسخرات بأمره إن في ذلك لآيات لقوم يعقلون (١٢)}','1392','644','0');
INSERT INTO [t20766] VALUES (656,'قوله تعالى: {وما ذرأ لكم في الأرض مختلفا ألوانه إن في ذلك لآية لقوم يذكرون (١٣)}','1394','644','0');
INSERT INTO [t20766] VALUES (657,'قوله تعالى: {وهو الذي سخر البحر لتأكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون (١٤)}','1396','644','0');
INSERT INTO [t20766] VALUES (658,'قوله تعالى: {وألقى في الأرض رواسي أن تميد بكم وأنهارا وسبلا لعلكم تهتدون (١٥) وعلامات وبالنجم هم يهتدون (١٦)}','1425','644','0');
INSERT INTO [t20766] VALUES (659,'قوله تعالى: {أفمن يخلق كمن لا يخلق}','1426','644','0');
INSERT INTO [t20766] VALUES (660,'قوله تعالى: {وإن تعدوا نعمة الله لا تحصوها إن الله لغفور رحيم (١٨)}','1427','644','0');
INSERT INTO [t20766] VALUES (661,'قوله تعالى: {وإذا قيل لهم ماذا أنزل ربكم قالوا أساطير الأولين}','1427','644','0');
INSERT INTO [t20766] VALUES (662,'قوله تعالى: {ليحملوا أوزارهم كاملة يوم القيامة ومن أوزار الذين يضلونهم بغير علم ألا ساء ما يزرون (٢٥)}','1428','644','0');
INSERT INTO [t20766] VALUES (663,'قوله تعالى: {قد مكر الذين من قبلهم}','1432','644','0');
INSERT INTO [t20766] VALUES (664,'قوله تعالى: {فأتى الله بنيانهم من القواعد}','1432','644','0');
INSERT INTO [t20766] VALUES (665,'قوله تعالى: {ثم يوم القيامة يخزيهم}','1433','644','0');
INSERT INTO [t20766] VALUES (666,'قوله تعالى: {ويقول أين شركائي الذين كنتم تشاقون فيهم}','1433','644','0');
INSERT INTO [t20766] VALUES (667,'قوله تعالى: {فألقوا السلم}','1434','644','0');
INSERT INTO [t20766] VALUES (668,'وقوله تعالى: {ما كنا نعمل من سوء بلى إن الله عليم بما كنتم تعملون (٢٨)}','1435','644','0');
INSERT INTO [t20766] VALUES (669,'قوله تعالى: {فادخلوا أبواب جهنم}','1437','644','0');
INSERT INTO [t20766] VALUES (670,'قوله تعالى: {وقيل للذين اتقوا ماذا أنزل ربكم قالوا خيرا}','1438','644','0');
INSERT INTO [t20766] VALUES (671,'قوله تعالى: {للذين أحسنوا في هذه الدنيا حسنة}','1438','644','0');
INSERT INTO [t20766] VALUES (672,'قوله تعالى: {ولدار الآخرة خير}','1438','644','0');
INSERT INTO [t20766] VALUES (673,'قوله تعالى: {ولنعم دار المتقين}','1440','644','0');
INSERT INTO [t20766] VALUES (674,'قوله تعالى: {جنات عدن يدخلونها تجري من تحتها الأنهار لهم فيها ما يشاءون كذلك يجزي الله المتقين (٣١)}','1441','644','0');
INSERT INTO [t20766] VALUES (675,'قوله تعالى: {ولقد بعثنا في كل أمة رسولا أن اعبدوا الله واجتنبوا الطاغوت}','1444','644','0');
INSERT INTO [t20766] VALUES (676,'قوله تعالى: {فمنهم من هدى الله ومنهم من حقت عليه الضلالة}','1445','644','0');
INSERT INTO [t20766] VALUES (677,'قوله تعالى: {إن تحرص على هداهم فإن الله لا يهدي من يضل وما لهم من ناصرين (٣٧)}','1446','644','0');
INSERT INTO [t20766] VALUES (678,'قوله تعالى: {إنما قولنا لشيء إذا أردناه أن نقول له كن فيكون (٤٠)}','1448','644','0');
INSERT INTO [t20766] VALUES (679,'قوله تعالى: {وما أرسلنا من قبلك إلا رجالا نوحي إليهم}','1449','644','0');
INSERT INTO [t20766] VALUES (680,'قوله تعالى: {وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم ولعلهم يتفكرون}','1452','644','0');
INSERT INTO [t20766] VALUES (681,'قوله تعالى: {أفأمن الذين مكروا السيئات أن يخسف الله بهم الأرض أو يأتيهم العذاب من حيث لا يشعرون (٤٥)}','1453','644','0');
INSERT INTO [t20766] VALUES (682,'قوله تعالى: {أولم يروا إلى ما خلق الله من شيء يتفيأ. .}','1455','644','0');
INSERT INTO [t20766] VALUES (683,'قوله تعالى: {وقال الله لا تتخذوا إلهين اثنين إنما هو إله واحد فإياي فارهبون (٥١)}','1455','644','0');
INSERT INTO [t20766] VALUES (684,'قوله تعالى: {وله الدين واصبا}','1456','644','0');
INSERT INTO [t20766] VALUES (685,'وقوله تعالى: {أفغير الله تتقون}','1458','644','0');
INSERT INTO [t20766] VALUES (686,'قوله تعالى: {ثم إذا كشف الضر عنكم إذا فريق منكم بربهم يشركون (٥٤)}','1459','644','0');
INSERT INTO [t20766] VALUES (687,'قوله تعالى: {فتمتعوا فسوف تعلمون (٥٥)}','1460','644','0');
INSERT INTO [t20766] VALUES (688,'قوله تعالى: {ويجعلون لما لا يعلمون نصيبا مما رزقناهم تالله لتسألن عما كنتم تفترون (٥٦)}','1461','644','0');
INSERT INTO [t20766] VALUES (689,'قوله تعالى: {ويجعلون لله البنات سبحانه ولهم ما يشتهون (٥٧) وإذا بشر أحدهم بالأنثى ظل وجهه مسودا وهو كظيم (٥٨) يتوارى من القوم من سوء ما بشر به أيمسكه على هون أم يدسه في التراب ألا ساء ما يحكمون (٥٩)}','1462','644','0');
INSERT INTO [t20766] VALUES (690,'وقوله تعالى: {ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة ولكن يؤخرهم إلى أجل مسمى فإذا جاء أجلهم لا يستأخرون ساعة ولا يستقدمون (٦١)}','1467','644','0');
INSERT INTO [t20766] VALUES (691,'قوله تعالى: {ويجعلون لله ما يكرهون}','1471','644','0');
INSERT INTO [t20766] VALUES (692,'قوله تعالى: {وتصف ألسنتهم الكذب أن لهم الحسنى}','1472','644','0');
INSERT INTO [t20766] VALUES (693,'قوله تعالى: {لا جرم أن لهم النار وأنهم مفرطون (٦٢)}','1473','644','0');
INSERT INTO [t20766] VALUES (694,'قوله تعالى: {وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه}','1475','644','0');
INSERT INTO [t20766] VALUES (695,'قوله تعالى: {ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا}.}','1487','644','0');
INSERT INTO [t20766] VALUES (696,'قوله تعالى: {وأوحى ربك إلى النحل}','1495','644','0');
INSERT INTO [t20766] VALUES (697,'قوله تعالى: {ومنكم من يرد إلى أرذل العمر لكي لا يعلم بعد علم شيئا إن الله عليم قدير (٧٠)}','1496','644','0');
INSERT INTO [t20766] VALUES (698,'قوله تعالى: {والله فضل بعضكم على بعض في الرزق فما الذين فضلوا برادي رزقهم على ما ملكت أيمانهم فهم فيه سواء أفبنعمة الله يجحدون (٧١)}','1498','644','0');
INSERT INTO [t20766] VALUES (699,'قوله تعالى: {والله جعل لكم من أنفسكم أزواجا وجعل لكم من أزواجكم بنين وحفدة}','1500','644','0');
INSERT INTO [t20766] VALUES (700,'قوله تعالى: {ويعبدون من دون الله ما لا يملك لهم رزقا من السماوات والأرض شيئا ولا يستطيعون (٧٣)}','1507','644','0');
INSERT INTO [t20766] VALUES (701,'قوله تعالى: {فلا تضربوا لله الأمثال}','1509','644','0');
INSERT INTO [t20766] VALUES (702,'قوله تعالى: {وما أمر الساعة إلا كلمح البصر}','1509','644','0');
INSERT INTO [t20766] VALUES (703,'قوله تعالى: {وجعل لكم السمع والأبصار والأفئدة لعلكم تشكرون (٧٨)}','1510','644','0');
INSERT INTO [t20766] VALUES (704,'قوله تعالى: {ألم يروا إلى الطير مسخرات في جو السماء ما يمسكهن إلا الله إن في ذلك لآيات لقوم يؤمنون (٧٩)}','1510','644','0');
INSERT INTO [t20766] VALUES (705,'قوله تعالى: {وجعل لكم سرابيل تقيكم الحر وسرابيل تقيكم بأسكم}','1512','644','0');
INSERT INTO [t20766] VALUES (706,'قوله تعالى: {يعرفون نعمت الله ثم ينكرونها}','1513','644','0');
INSERT INTO [t20766] VALUES (707,'قوله تعالى: {ثم لا يؤذن للذين كفروا}','1514','644','0');
INSERT INTO [t20766] VALUES (708,'قوله تعالى: {ولا هم يستعتبون (٨٤)}','1516','644','0');
INSERT INTO [t20766] VALUES (709,'قوله تعالى: {وإذا رأى الذين ظلموا العذاب فلا يخفف عنهم ولا هم ينظرون (٨٥)}','1517','644','0');
INSERT INTO [t20766] VALUES (710,'قوله تعالى: {وإذا رأى الذين أشركوا شركاءهم قالوا ربنا هؤلاء شركاؤنا الذين كنا ندعو من دونك فألقوا إليهم القول إنكم لكاذبون (٨٦)}','1518','644','0');
INSERT INTO [t20766] VALUES (711,'قوله تعالى: {وألقوا إلى الله يومئذ السلم وضل عنهم ما كانوا يفترون (٨٧)}','1519','644','0');
INSERT INTO [t20766] VALUES (712,'قوله تعالى: {الذين كفروا وصدوا عن سبيل الله زدناهم عذابا فوق العذاب بما كانوا يفسدون (٨٨)}','1520','644','0');
INSERT INTO [t20766] VALUES (713,'قوله تعالى: {ونزلنا عليك الكتاب تبيانا لكل شيء}','1522','644','0');
INSERT INTO [t20766] VALUES (714,'وقوله تعالى: {وهدى ورحمة وبشرى للمسلمين (٨٩)}','1534','644','0');
INSERT INTO [t20766] VALUES (715,'قوله تعالى: {إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون}','1535','644','0');
INSERT INTO [t20766] VALUES (716,'قوله تعالى: {وأوفوا بعهد الله إذا عاهدتم}','1539','644','0');
INSERT INTO [t20766] VALUES (717,'قوله تعالى: {ما عندكم ينفد وما عند الله باق}','1540','644','0');
INSERT INTO [t20766] VALUES (718,'قوله تعالى: {ولنجزين الذين صبروا أجرهم بأحسن ما كانوا يعملون (٩٦)}','1540','644','0');
INSERT INTO [t20766] VALUES (719,'قوله تعالى: {من عمل صالحا من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة ولنجزينهم أجرهم بأحسن ما كانوا يعملون (٩٧)}','1541','644','0');
INSERT INTO [t20766] VALUES (720,'قوله تعالى: {فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم}','1546','644','0');
INSERT INTO [t20766] VALUES (721,'قوله تعالى: {إنه ليس له سلطان على الذين آمنوا وعلى ربهم يتوكلون (٩٩) إنما سلطانه على الذين يتولونه والذين هم به مشركون (١٠٠)}','1547','644','0');
INSERT INTO [t20766] VALUES (722,'قوله تعالى: {وإذا بدلنا آية مكان آية والله أعلم بما ينزل قالوا إنما أنت مفتر بل أكثرهم لا يعلمون (١٠١)}','1549','644','0');
INSERT INTO [t20766] VALUES (723,'قوله تعالى: {قل نزله روح القدس من ربك بالحق .. }','1560','644','0');
INSERT INTO [t20766] VALUES (724,'قوله تعالى: {ولقد نعلم أنهم يقولون إنما يعلمه بشر}','1561','644','0');
INSERT INTO [t20766] VALUES (725,'قوله تعالى: {ولا تقولوا لما تصف ألسنتكم الكذب هذا حلال وهذا حرام لتفتروا على الله الكذب}','1573','644','0');
INSERT INTO [t20766] VALUES (726,'قوله تعالى: {وعلى الذين هادوا حرمنا ما قصصنا عليك من قبل .. }','1576','644','0');
INSERT INTO [t20766] VALUES (727,'قوله تعالى: {إن إبراهيم كان أمة قانتا لله حنيفا ولم يك من المشركين (١٢٠) شاكرا لأنعمه اجتباه وهداه إلى صراط مستقيم}','1577','644','0');
INSERT INTO [t20766] VALUES (728,'قوله تعالى: {وآتيناه في الدنيا حسنة .. }','1578','644','0');
INSERT INTO [t20766] VALUES (729,'قوله تعالى: {ثم أوحينا إليك أن اتبع ملة إبراهيم حنيفا وما كان من المشركين (١٢٣)}','1578','644','0');
INSERT INTO [t20766] VALUES (730,'قوله تعالى: {وجادلهم بالتي هي أحسن}','1579','644','0');
INSERT INTO [t20766] VALUES (731,'قوله تعالى: {إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين (١٢٥)}','1580','644','0');
INSERT INTO [t20766] VALUES (732,'قوله تعالى: {وإن عاقبتم فعاقبوا بمثل ما عوقبتم به ولئن صبرتم لهو خير للصابرين (١٢٦)}','1581','644','0');
INSERT INTO [t20766] VALUES (733,'قوله تعالى: {واصبر وما صبرك إلا بالله}','1583','644','0');
INSERT INTO [t20766] VALUES (734,'قوله تعالى: {إن الله مع الذين اتقوا والذين هم محسنون (١٢٨)}','1584','644','0');
INSERT INTO [t20766] VALUES (735,'سورة بني إسرائيل','1585','0','0');
INSERT INTO [t20766] VALUES (736,'قوله تعالى: {سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصى. .}','1586','735','0');
INSERT INTO [t20766] VALUES (737,'قوله تعالى: {الذي باركنا حوله}','1597','735','0');
INSERT INTO [t20766] VALUES (738,'قوله تعالى: {لنريه من آياتنا}','1597','735','0');
INSERT INTO [t20766] VALUES (739,'قوله تعالى: {وآتينا موسى الكتاب}','1598','735','0');
INSERT INTO [t20766] VALUES (740,'قوله تعالى: {ألا تتخذوا من دوني وكيلا (٢)}','1598','735','0');
INSERT INTO [t20766] VALUES (741,'قوله تعالى: {ذرية من حملنا مع نوح إنه كان عبدا شكورا (٣)}','1600','735','0');
INSERT INTO [t20766] VALUES (742,'قوله تعالى: {وقضينا إلى بني إسرائيل}','1602','735','0');
INSERT INTO [t20766] VALUES (743,'قوله تعالى: {إن أحسنتم أحسنتم لأنفسكم وإن أسأتم فلها}','1602','735','0');
INSERT INTO [t20766] VALUES (744,'قوله تعالى: {فإذا جاء وعد الآخرة ليسوءوا وجوهكم ... }','1603','735','0');
INSERT INTO [t20766] VALUES (745,'قوله تعالى: {وإن عدتم عدنا}','1604','735','0');
INSERT INTO [t20766] VALUES (746,'قوله تعالى: {وجعلنا جهنم للكافرين حصيرا (٨)}','1605','735','0');
INSERT INTO [t20766] VALUES (747,'قوله تعالى: {إن هذا القرآن يهدي للتي هي أقوم}','1606','735','0');
INSERT INTO [t20766] VALUES (748,'قوله تعالى: {ويدع الإنسان بالشر دعاءه بالخير وكان الإنسان عجولا (١١)}','1662','735','0');
INSERT INTO [t20766] VALUES (749,'قوله تعالى: {وجعلنا الليل والنهار آيتين فمحونا آية الليل وجعلنا آية النهار مبصرة لتبتغوا فضلا من ربكم ولتعلموا عدد السنين والحساب وكل شيء فصلناه تفصيلا (١٢)}','1663','735','0');
INSERT INTO [t20766] VALUES (750,'قوله تعالى: {وكل إنسان ألزمناه طائره في عنقه ونخرج له يوم القيامة كتابا يلقاه منشورا (١٣) اقرأ كتابك كفى بنفسك اليوم عليك حسيبا (١٤)}','1669','735','0');
INSERT INTO [t20766] VALUES (751,'قوله تعالى: {من اهتدى فإنما يهتدي لنفسه ومن ضل فإنما يضل عليها}','1674','735','0');
INSERT INTO [t20766] VALUES (752,'قوله تعالى: {وما كنا معذبين حتى نبعث رسولا (١٥)}','1678','735','0');
INSERT INTO [t20766] VALUES (753,'قوله تعالى: {وإذا أردنا أن نهلك قرية أمرنا مترفيها ففسقوا فيها فحق عليها القول فدمرناها تدميرا (١٦)}','1692','735','0');
INSERT INTO [t20766] VALUES (754,'قوله تعالى: {وكم أهلكنا من القرون من بعد نوح وكفى بربك بذنوب عباده خبيرا بصيرا (١٧)}','1698','735','0');
INSERT INTO [t20766] VALUES (755,'قوله تعالى: {ومن أراد الآخرة وسعى لها سعيها وهو مؤمن فأولئك كان سعيهم مشكورا (١٩)}','1701','735','0');
INSERT INTO [t20766] VALUES (756,'قوله تعالى: {لا تجعل مع الله إلها آخر فتقعد مذموما مخذولا}','1704','735','0');
INSERT INTO [t20766] VALUES (757,'قوله تعالى: {وقضى ربك ألا تعبدوا إلا إياه وبالوالدين إحسانا}','1707','735','0');
INSERT INTO [t20766] VALUES (758,'قوله تعالى: {وإما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا (٢٨)}','1708','735','0');
INSERT INTO [t20766] VALUES (759,'قوله تعالى: {ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل إنه كان منصورا (٣٣)}','1710','735','0');
INSERT INTO [t20766] VALUES (760,'قوله تعالى: {ولا تقف ما ليس لك به علم إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا (٣٦)}','1801','735','0');
INSERT INTO [t20766] VALUES (761,'قوله تعالى: {ولا تمش في الأرض مرحا إنك لن تخرق الأرض ولن تبلغ الجبال طولا (٣٧)}','1818','735','0');
INSERT INTO [t20766] VALUES (762,'قوله تعالى: {قل لو كان معه آلهة كما يقولون إذا لابتغوا إلى ذي العرش سبيلا (٤٢)}','1821','735','0');
INSERT INTO [t20766] VALUES (763,'قوله تعالى: {وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا}','1822','735','0');
INSERT INTO [t20766] VALUES (764,'قوله تعالى: {وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا}','1824','735','0');
INSERT INTO [t20766] VALUES (765,'قوله تعالى: {وإذا ذكرت ربك في القرآن وحده ولوا على أدبارهم نفورا (٤٦)}','1825','735','0');
INSERT INTO [t20766] VALUES (766,'قوله تعالى: {وإن من قرية إلا نحن مهلكوها قبل يوم القيامة أو معذبوها عذابا شديدا كان ذلك في الكتاب مسطورا (٥٨)}','1828','735','0');
INSERT INTO [t20766] VALUES (767,'قوله تعالى: {وآتينا ثمود الناقة مبصرة فظلموا بها}','1831','735','0');
INSERT INTO [t20766] VALUES (768,'قوله تعالى: {وإذ قلنا لك إن ربك أحاط بالناس. .}','1831','735','0');
INSERT INTO [t20766] VALUES (769,'قوله تعالى: {وما جعلنا الرؤيا التي أريناك إلا فتنة للناس والشجرة الملعونة في القرآن}','1831','735','0');
INSERT INTO [t20766] VALUES (770,'قوله تعالى: {قال أرأيتك هذا الذي كرمت علي لئن أخرتن إلى يوم القيامة لأحتنكن ذريته إلا قليلا (٦٢)}','1833','735','0');
INSERT INTO [t20766] VALUES (771,'قوله تعالى: {قال اذهب فمن تبعك منهم فإن جهنم جزاؤكم جزاء موفورا (٦٣)}','1835','735','0');
INSERT INTO [t20766] VALUES (772,'قوله تعالى: {واستفزز من استطعت منهم بصوتك وأجلب عليهم بخيلك ورجلك وشاركهم في الأموال والأولاد وعدهم وما يعدهم الشيطان إلا غرورا (٦٤)}','1836','735','0');
INSERT INTO [t20766] VALUES (773,'قوله تعالى: {إن عبادي ليس لك عليهم سلطان .. }','1841','735','0');
INSERT INTO [t20766] VALUES (774,'قوله تعالى: {وإذا مسكم الضر في البحر ضل من تدعون إلا إياه فلما نجاكم إلى البر أعرضتم وكان الإنسان كفورا (٦٧) أفأمنتم أن يخسف بكم جانب البر أو يرسل عليكم حاصبا ثم لا تجدوا لكم وكيلا (٦٨) أم أمنتم أن يعيدكم فيه تارة أخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ثم لا تجدوا لكم علينا به تبيعا (٦٩)}','1841','735','0');
INSERT INTO [t20766] VALUES (775,'قوله تعالى: {ولقد كرمنا بني آدم}','1846','735','0');
INSERT INTO [t20766] VALUES (776,'قوله تعالى: {يوم ندعو كل أناس بإمامهم}','1847','735','0');
INSERT INTO [t20766] VALUES (777,'قوله تعالى: {ومن كان في هذه أعمى فهو في الآخرة أعمى وأضل سبيلا (٧٢)}','1849','735','0');
INSERT INTO [t20766] VALUES (778,'قوله تعالى: {وإن كادوا ليفتنونك عن الذي أوحينا إليك لتفتري علينا غيره وإذا لاتخذوك خليلا (٧٣)}','1850','735','0');
INSERT INTO [t20766] VALUES (779,'قوله تعالى: {ولولا أن ثبتناك لقد كدت تركن إليهم شيئا قليلا (٧٤) إذا لأذقناك ضعف الحياة وضعف الممات ثم لا تجد لك علينا نصيرا (٧٥)}','1852','735','0');
INSERT INTO [t20766] VALUES (780,'قوله تعالى: {أقم الصلاة لدلوك الشمس .. }','1853','735','0');
INSERT INTO [t20766] VALUES (781,'قوله تعالى: {وقل جاء الحق وزهق الباطل إن الباطل كان زهوقا}','1853','735','0');
INSERT INTO [t20766] VALUES (782,'قوله تعالى: {وننزل من القرآن ما هو شفاء ورحمة للمؤمنين ولا يزيد الظالمين إلا خسارا (٨٢)}','1856','735','0');
INSERT INTO [t20766] VALUES (783,'قوله تعالى: {وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر كان يئوسا (٨٣)}','1856','735','0');
INSERT INTO [t20766] VALUES (784,'قوله تعالى: {وما أوتيتم من العلم إلا قليلا (٨٥)}','1858','735','0');
INSERT INTO [t20766] VALUES (785,'قوله تعالى: {إن فضله كان عليك كبيرا (٨٧)}','1858','735','0');
INSERT INTO [t20766] VALUES (786,'قوله تعالى: {وقالوا لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا (٩٠) أو تكون لك جنة من نخيل وعنب فتفجر الأنهار خلالها تفجيرا (٩١) أو تسقط السماء كما زعمت علينا كسفا أو تأتي بالله والملائكة قبيلا (٩٢) أو يكون لك بيت من زخرف أو ترقى في السماء ولن نؤمن لرقيك حتى تنزل علينا كتابا نقرؤه قل سبحان ربي هل كنت إلا بشرا رسولا (٩٣)}','1859','735','0');
INSERT INTO [t20766] VALUES (787,'قوله تعالى: {وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا أبعث الله بشرا رسولا (٩٤)}','1861','735','0');
INSERT INTO [t20766] VALUES (788,'قوله تعالى: {قل لو كان في الأرض ملائكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا (٩٥)}','1862','735','0');
INSERT INTO [t20766] VALUES (789,'قوله تعالى: {أولم يروا أن الله الذي خلق السماوات والأرض قادر على أن يخلق مثلهم وجعل}','1863','735','0');
INSERT INTO [t20766] VALUES (790,'قوله تعالى: {قل لو أنتم تملكون خزائن رحمة ربي إذا لأمسكتم خشية الإنفاق وكان الإنسان قتورا (١٠٠)}','1863','735','0');
INSERT INTO [t20766] VALUES (791,'قوله تعالى: {ولقد آتينا موسى تسع آيات بينات .. }','1864','735','0');
INSERT INTO [t20766] VALUES (792,'قوله تعالى: {قال لقد علمت ما أنزل هؤلاء إلا رب السماوات والأرض بصائر .. }','1865','735','0');
INSERT INTO [t20766] VALUES (793,'قوله تعالى: {وبالحق أنزلناه وبالحق نزل}','1865','735','0');
INSERT INTO [t20766] VALUES (794,'قوله تعالى: {وقرآنا فرقناه لتقرأه على الناس على مكث}','1866','735','0');
INSERT INTO [t20766] VALUES (795,'قوله تعالى: {قل ادعوا الله أو ادعوا الرحمن أيا ما تدعوا فله الأسماء الحسنى}','1867','735','0');
INSERT INTO [t20766] VALUES (796,'قوله تعالى: {وقل الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك ولم يكن له ولي من الذل وكبره تكبيرا (١١١)}','1867','735','0');
INSERT INTO [t20766] VALUES (797,'سورة الكهف','1870','0','0');
INSERT INTO [t20766] VALUES (798,'قوله تعالى: {فلعلك باخع نفسك على آثارهم إن لم يؤمنوا بهذا الحديث أسفا (٦)}','1885','797','0');
INSERT INTO [t20766] VALUES (799,'قوله تعالى: {إنا جعلنا ما على الأرض زينة لها لنبلوهم أيهم أحسن عملا (٧) وإنا لجاعلون ما عليها صعيدا جرزا (٨)}','1888','797','0');
INSERT INTO [t20766] VALUES (800,'قوله تعالى: {أم حسبت أن أصحاب الكهف والرقيم كانوا من آياتنا عجبا (٩)}','1890','797','0');
INSERT INTO [t20766] VALUES (801,'قوله تعالى: {إذ أوى الفتية إلى الكهف فقالوا ربنا آتنا من لدنك رحمة وهيئ لنا من أمرنا رشدا (١٠)}','1893','797','0');
INSERT INTO [t20766] VALUES (802,'قوله تعالى: {فضربنا على آذانهم في الكهف سنين عددا (١١)}','1895','797','0');
INSERT INTO [t20766] VALUES (803,'قوله تعالى: {ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا (١٢)}','1896','797','0');
INSERT INTO [t20766] VALUES (804,'قوله تعالى: {نحن نقص عليك نبأهم بالحق إنهم فتية آمنوا بربهم وزدناهم هدى (١٣)}','1904','797','0');
INSERT INTO [t20766] VALUES (805,'قوله تعالى: {وربطنا على قلوبهم إذ قاموا}','1905','797','0');
INSERT INTO [t20766] VALUES (806,'قوله تعالى: {فقالوا ربنا رب السماوات والأرض لن ندعو من دونه إلها لقد قلنا إذا شططا (١٤)}','1906','797','0');
INSERT INTO [t20766] VALUES (807,'قوله تعالى: {هؤلاء قومنا اتخذوا من دونه آلهة لولا يأتون عليهم بسلطان بين}','1907','797','0');
INSERT INTO [t20766] VALUES (808,'قوله تعالى: {فمن أظلم ممن افترى على الله كذبا (١٥)}','1908','797','0');
INSERT INTO [t20766] VALUES (809,'قوله تعالى: {وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا (١٦)}','1909','797','0');
INSERT INTO [t20766] VALUES (810,'قوله تعالى: {وترى الشمس إذا طلعت تزاور عن كهفهم ذات اليمين وإذا غربت تقرضهم ذات الشمال وهم في فجوة منه ذلك من آيات الله}','1911','797','0');
INSERT INTO [t20766] VALUES (811,'قوله تعالى: {من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا (١٧)}','1919','797','0');
INSERT INTO [t20766] VALUES (812,'قوله تعالى: {وتحسبهم أيقاظا وهم رقود}','1920','797','0');
INSERT INTO [t20766] VALUES (813,'قوله تعالى: {وكلبهم باسط ذراعيه بالوصيد}','1921','797','0');
INSERT INTO [t20766] VALUES (814,'قوله تعالى: {فابعثوا أحدكم بورقكم هذه إلى المدينة فلينظر أيها أزكى طعاما فليأتكم برزق منه}','1925','797','0');
INSERT INTO [t20766] VALUES (815,'قوله تعالى: {إنهم إن يظهروا عليكم يرجموكم أو يعيدوكم في ملتهم ولن تفلحوا إذا أبدا (٢٠)}','1962','797','0');
INSERT INTO [t20766] VALUES (816,'قوله تعالى: {قال الذين غلبوا على أمرهم لنتخذن عليهم مسجدا (٢١)}','1963','797','0');
INSERT INTO [t20766] VALUES (817,'قوله تعالى: {سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ويقولون سبعة وثامنهم كلبهم قل ربي أعلم بعدتهم ما يعلمهم إلا قليل}','1964','797','0');
INSERT INTO [t20766] VALUES (818,'قوله تعالى: {ولا تقولن لشيء إني فاعل ذلك غدا (٢٣) إلا أن يشاء الله}','1965','797','0');
INSERT INTO [t20766] VALUES (819,'قوله تعالى: {واذكر ربك إذا نسيت}','1967','797','0');
INSERT INTO [t20766] VALUES (820,'قوله تعالى: {له غيب السماوات والأرض}','1970','797','0');
INSERT INTO [t20766] VALUES (821,'قوله تعالى: {أبصر به وأسمع}','1971','797','0');
