INSERT INTO [t20766] VALUES (831,'قوله تعالى: {فمن شاء فليؤمن ومن شاء فليكفر}','1985','797','0');
INSERT INTO [t20766] VALUES (832,'قوله تعالى: {إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملا (٣٠)}','1992','797','0');
INSERT INTO [t20766] VALUES (833,'قوله تعالى: {أولئك لهم جنات عدن تجري من تحتهم الأنهار -إلى قوله- وحسنت مرتفقا (٣١)}','1994','797','0');
INSERT INTO [t20766] VALUES (834,'قوله تعالى: {ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا (٣٥) وما أظن الساعة قائمة ولئن رددت إلى ربي لأجدن خيرا منها منقلبا (٣٦)}','1995','797','0');
INSERT INTO [t20766] VALUES (835,'قوله تعالى: {قال له صاحبه وهو يحاوره أكفرت بالذي خلقك من تراب ثم من نطفة ثم سواك رجلا (٣٧) لكنا هو الله ربي ولا أشرك بربي أحدا (٣٨)}','1997','797','0');
INSERT INTO [t20766] VALUES (836,'قوله تعالى: {أو يصبح ماؤها غورا فلن تستطيع له طلبا (٤١)}','2002','797','0');
INSERT INTO [t20766] VALUES (837,'قوله تعالى: {ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا (٤٣) هنالك الولاية لله الحق هو خير ثوابا وخير عقبا (٤٤)}','2003','797','0');
