INSERT INTO [t20766] VALUES (843,'قوله تعالى: {ووجدوا ما عملوا حاضرا}','2018','797','0');
INSERT INTO [t20766] VALUES (844,'قوله تعالى: {ولا يظلم ربك أحدا (٤٩)}','2019','797','0');
