INSERT INTO [t20766] VALUES (846,'قوله تعالى: {ما أشهدتهم خلق السماوات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا (٥١)}','2026','797','0');
INSERT INTO [t20766] VALUES (847,'قوله تعالى: {ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا (٥٢)}','2027','797','0');
INSERT INTO [t20766] VALUES (848,'قوله تعالى: {ورأى المجرمون النار فظنوا أنهم مواقعوها ولم يجدوا عنها مصرفا (٥٣)}','2032','797','0');
