INSERT INTO [t20766] VALUES (850,'قوله تعالى: {وما منع الناس أن يؤمنوا إذ جاءهم الهدى ويستغفروا ربهم إلا أن تأتيهم سنة الأولين أو يأتيهم العذاب قبلا (٥٥)}','2039','797','0');
INSERT INTO [t20766] VALUES (851,'قوله تعالى: {وما نرسل المرسلين إلا مبشرين ومنذرين}','2044','797','0');
INSERT INTO [t20766] VALUES (852,'قوله تعالى: {ويجادل الذين كفروا بالباطل ليدحضوا به الحق}','2044','797','0');
INSERT INTO [t20766] VALUES (853,'قوله تعالى: {واتخذوا آياتي وما أنذروا هزوا (٥٦)}','2046','797','0');
INSERT INTO [t20766] VALUES (854,'قوله تعالى: {ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه}','2047','797','0');
INSERT INTO [t20766] VALUES (855,'قوله تعالى: {وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا}','2050','797','0');
INSERT INTO [t20766] VALUES (856,'قوله تعالى: {وإن تدعهم إلى الهدى فلن يهتدوا إذا أبدا (٥٧)}','2054','797','0');
INSERT INTO [t20766] VALUES (857,'قوله تعالى: {وربك الغفور ذو الرحمة}','2058','797','0');
INSERT INTO [t20766] VALUES (858,'قوله تعالى: {بل لهم موعد لن يجدوا من دونه موئلا (٥٨)}','2059','797','0');
INSERT INTO [t20766] VALUES (859,'وقوله تعالى: {وتلك القرى أهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا (٥٩)}','2061','797','0');
INSERT INTO [t20766] VALUES (860,'قوله تعالى: {فلما بلغا مجمع بينهما نسيا حوتهما}','2066','797','0');
