INSERT INTO [t20766] VALUES (865,'قوله تعالى: {قال هذا رحمة من ربي فإذا جاء وعد ربي جعله دكاء وكان وعد ربي حقا (٩٨) وتركنا بعضهم يومئذ يموج في بعض ونفخ في الصور فجمعناهم جمعا (٩٩)}','2097','797','0');
INSERT INTO [t20766] VALUES (866,'قوله تعالى: {وعرضنا جهنم يومئذ للكافرين عرضا (١٠٠)}','2105','797','0');
INSERT INTO [t20766] VALUES (867,'قوله تعالى: {الذين كانت أعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا (١٠١)}','2106','797','0');
INSERT INTO [t20766] VALUES (868,'قوله تعالى: {أفحسب الذين كفروا أن يتخذوا عبادي من دوني أولياء إنا أعتدنا جهنم للكافرين نزلا (١٠٢)}','2107','797','0');
INSERT INTO [t20766] VALUES (869,'قوله تعالى: {قل هل ننبئكم بالأخسرين أعمالا (١٠٣) الذين ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا (١٠٤)}','2109','797','0');
INSERT INTO [t20766] VALUES (870,'قوله تعالى: {إن الذين آمنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا (١٠٧)}','2116','797','0');
INSERT INTO [t20766] VALUES (871,'قوله تعالى: {خالدين فيها لا يبغون عنها حولا (١٠٨)}','2117','797','0');
INSERT INTO [t20766] VALUES (872,'قوله تعالى: {قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله مددا (١٠٩)}','2117','797','0');
INSERT INTO [t20766] VALUES (873,'قوله تعالى: {قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد}','2118','797','0');
INSERT INTO [t20766] VALUES (874,'قوله تعالى: {فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه أحدا (١١٠)}','2119','797','0');
INSERT INTO [t20766] VALUES (875,'سورة مريم','2123','0','0');
INSERT INTO [t20766] VALUES (876,'قوله تعالى: {كهيعص (١) ذكر رحمت ربك عبده زكريا (٢) إذ نادى ربه نداء خفيا (٣) قال رب إني وهن العظم مني واشتعل الرأس شيبا ولم أكن بدعائك رب شقيا (٤)}','2123','875','0');
INSERT INTO [t20766] VALUES (877,'قوله تعالى: {يازكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا (٧)}','2135','875','0');
INSERT INTO [t20766] VALUES (878,'قوله تعالى: {قال رب أنى يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا (٨)}','2137','875','0');
INSERT INTO [t20766] VALUES (879,'قوله تعالى: {قال كذلك قال ربك هو علي هين وقد خلقتك من قبل ولم تك شيئا (٩)}','2139','875','0');
INSERT INTO [t20766] VALUES (880,'قوله تعالى: {قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاث ليال سويا (١٠)}','2141','875','0');
INSERT INTO [t20766] VALUES (881,'قوله تعالى: {فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا (١١)}','2143','875','0');
INSERT INTO [t20766] VALUES (882,'قوله تعالى: {يايحيى خذ الكتاب بقوة وآتيناه الحكم صبيا (١٢) وحنانا من لدنا وزكاة وكان تقيا (١٣) وبرا بوالديه ولم يكن جبارا عصيا (١٤) وسلام عليه يوم ولد ويوم يموت ويوم يبعث حيا (١٥)}','2151','875','0');
INSERT INTO [t20766] VALUES (883,'قوله تعالى: {واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا (١٦)}','2162','875','0');
INSERT INTO [t20766] VALUES (884,'قوله تعالى: {فاتخذت من دونهم حجابا فأرسلنا إليها روحنا}','2163','875','0');
INSERT INTO [t20766] VALUES (885,'قوله تعالى: {فتمثل لها بشرا سويا (١٧)}','2164','875','0');
INSERT INTO [t20766] VALUES (886,'قوله تعالى: {قال إنما أنا رسول ربك لأهب لك غلاما زكيا (١٩)}','2164','875','0');
INSERT INTO [t20766] VALUES (887,'قوله تعالى: {قالت أنى يكون لي غلام ولم يمسسني بشر ولم أك بغيا (٢٠)}','2166','875','0');
INSERT INTO [t20766] VALUES (888,'قوله تعالى: {قال كذلك قال ربك هو علي هين}','2167','875','0');
INSERT INTO [t20766] VALUES (889,'قوله تعالى: {ولنجعله آية للناس ورحمة منا وكان أمرا مقضيا (٢١)}','2168','875','0');
INSERT INTO [t20766] VALUES (890,'قوله تعالى: {فحملته فانتبذت به مكانا قصيا (٢٢) فأجاءها المخاض إلى جذع النخلة قالت ياليتني مت قبل هذا وكنت نسيا منسيا (٢٣)}','2169','875','0');
INSERT INTO [t20766] VALUES (891,'قوله تعالى: {فناداها من تحتها ألا تحزني قد جعل ربك تحتك سريا (٢٤)}','2175','875','0');
INSERT INTO [t20766] VALUES (892,'قوله تعالى: {وهزي إليك بجذع النخلة تساقط عليك رطبا جنيا (٢٥) فكلي واشربي وقري عينا}','2180','875','0');
INSERT INTO [t20766] VALUES (893,'قوله تعالى: {فإما ترين من البشر أحدا فقولي إني نذرت للرحمن صوما فلن أكلم اليوم إنسيا (٢٦)}','2186','875','0');
INSERT INTO [t20766] VALUES (894,'قوله تعالى: {فأتت به قومها تحمله قالوا يامريم لقد جئت شيئا فريا (٢٧) ياأخت هارون ما كان أبوك امرأ سوء وما كانت أمك بغيا (٢٨)}','2205','875','0');
INSERT INTO [t20766] VALUES (895,'قوله تعالى: {فأشارت إليه}','2209','875','0');
INSERT INTO [t20766] VALUES (896,'قوله تعالى: {قال إني عبد الله آتاني الكتاب وجعلني نبيا (٣٠) وجعلني مباركا أين ما كنت وأوصاني بالصلاة والزكاة ما دمت حيا (٣١) وبرا بوالدتي ولم يجعلني جبارا شقيا (٣٢) والسلام علي يوم ولدت ويوم أموت ويوم أبعث حيا (٣٣)}','2209','875','0');
INSERT INTO [t20766] VALUES (897,'وقوله تعالى: {ذلك عيسى ابن مريم قول الحق الذي فيه يمترون (٣٤)}','2211','875','0');
INSERT INTO [t20766] VALUES (898,'قوله تعالى: {ما كان لله أن يتخذ من ولد سبحانه إذا قضى أمرا فإنما يقول له كن فيكون (٣٥)}','2214','875','0');
INSERT INTO [t20766] VALUES (899,'قوله تعالى: {فاختلف الأحزاب من بينهم فويل للذين كفروا من مشهد يوم عظيم (٣٧)}','2215','875','0');
INSERT INTO [t20766] VALUES (900,'قوله تعالى: {أسمع بهم وأبصر يوم يأتوننا لكن الظالمون اليوم في ضلال مبين (٣٨)}','2217','875','0');
INSERT INTO [t20766] VALUES (901,'قوله تعالى: {إنا نحن نرث الأرض ومن عليها وإلينا يرجعون (٤٠)}','2220','875','0');
INSERT INTO [t20766] VALUES (902,'قوله تعالى: {واذكر في الكتاب إبراهيم إنه كان صديقا نبيا (٤١) إذ قال لأبيه ياأبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا (٤٢) ياأبت إني قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا سويا (٤٣) ياأبت لا تعبد الشيطان إن الشيطان كان للرحمن عصيا (٤٤) ياأبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا (٤٥)}','2220','875','0');
INSERT INTO [t20766] VALUES (903,'قوله تعالى: {قال أراغب أنت عن آلهتي ياإبراهيم لئن لم تنته لأرجمنك واهجرني مليا (٤٦) قال سلام عليك سأستغفر لك ربي إنه كان بي حفيا (٤٧)}','2226','875','0');
INSERT INTO [t20766] VALUES (904,'قوله تعالى: {واذكر في الكتاب موسى إنه كان مخلصا وكان رسولا نبيا (٥١)}','2230','875','0');
INSERT INTO [t20766] VALUES (905,'قوله تعالى: {وناديناه من جانب الطور الأيمن وقربناه نجيا (٥٢)}','2230','875','0');
INSERT INTO [t20766] VALUES (906,'قوله تعالى: {ووهبنا له من رحمتنا أخاه هارون نبيا (٥٣)}','2239','875','0');
INSERT INTO [t20766] VALUES (907,'قوله تعالى: {واذكر في الكتاب إسماعيل إنه كان صادق الوعد وكان رسولا نبيا (٥٤)}','2240','875','0');
INSERT INTO [t20766] VALUES (908,'قوله تعالى: {فخلف من بعدهم خلف أضاعوا الصلاة واتبعوا الشهوات فسوف يلقون غيا (٥٩) إلا من تاب وآمن وعمل صالحا فأولئك يدخلون الجنة ولا يظلمون شيئا (٦٠)}','2250','875','0');
INSERT INTO [t20766] VALUES (909,'قوله تعالى: {جنات عدن التي وعد الرحمن عباده بالغيب إنه كان وعده مأتيا (٦١)}','2283','875','0');
INSERT INTO [t20766] VALUES (910,'قوله تعالى: {لا يسمعون فيها لغوا إلا سلاما ولهم رزقهم فيها بكرة وعشيا (٦٢)}','2284','875','0');
INSERT INTO [t20766] VALUES (911,'قوله تعالى: {تلك الجنة التي نورث من عبادنا من كان تقيا (٦٣)}','2292','875','0');
INSERT INTO [t20766] VALUES (912,'قوله تعالى: {ويقول الإنسان أإذا ما مت لسوف أخرج حيا (٦٦) أولا يذكر الإنسان أنا خلقناه من قبل ولم يك شيئا (٦٧)}','2294','875','0');
INSERT INTO [t20766] VALUES (913,'قوله تعالى: {فوربك لنحشرنهم والشياطين ثم لنحضرنهم حول جهنم جثيا (٦٨)}','2297','875','0');
INSERT INTO [t20766] VALUES (914,'قوله تعالى: {ثم لننزعن من كل شيعة أيهم أشد على الرحمن عتيا (٦٩) ثم لنحن أعلم بالذين هم أولى بها صليا (٧٠)}','2299','875','0');
INSERT INTO [t20766] VALUES (915,'قوله تعالى: {وإن منكم إلا واردها كان على ربك حتما مقضيا (٧١) ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا (٧٢)}','2301','875','0');
INSERT INTO [t20766] VALUES (916,'قوله تعالى: {وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للذين آمنوا أي الفريقين خير مقاما وأحسن نديا (٧٣) وكم أهلكنا قبلهم من قرن هم أحسن أثاثا ورئيا (٧٤)}','2309','875','0');
INSERT INTO [t20766] VALUES (917,'قوله تعالى: {قل من كان في الضلالة فليمدد له الرحمن مدا حتى إذا رأوا ما يوعدون إما العذاب وإما الساعة فسيعلمون من هو شر مكانا وأضعف جندا (٧٥)}','2315','875','0');
INSERT INTO [t20766] VALUES (918,'قوله تعالى: {ويزيد الله الذين اهتدوا هدى والباقيات الصالحات خير عند ربك ثوابا وخير مردا (٧٦)}','2319','875','0');
INSERT INTO [t20766] VALUES (919,'قوله تعالى: {أفرأيت الذي كفر بآياتنا وقال لأوتين مالا وولدا (٧٧)}','2321','875','0');
INSERT INTO [t20766] VALUES (920,'قوله تعالى: {سنكتب ما يقول ونمد له من العذاب مدا (٧٩) ونرثه ما يقول ويأتينا فردا (٨٠)}','2346','875','0');
INSERT INTO [t20766] VALUES (921,'قوله تعالى: {واتخذوا من دون الله آلهة ليكونوا لهم عزا (٨١) كلا سيكفرون بعبادتهم ويكونون عليهم ضدا (٨٢)}','2348','875','0');
INSERT INTO [t20766] VALUES (922,'قوله تعالى: {ألم تر أنا أرسلنا الشياطين على الكافرين تؤزهم أزا (٨٣)}','2351','875','0');
INSERT INTO [t20766] VALUES (923,'قوله تعالى: {فلا تعجل عليهم إنما نعد لهم عدا (٨٤)}','2352','875','0');
INSERT INTO [t20766] VALUES (924,'قوله تعالى: {يوم نحشر المتقين إلى الرحمن وفدا (٨٥) ونسوق المجرمين إلى جهنم وردا (٨٦)}','2354','875','0');
INSERT INTO [t20766] VALUES (925,'قوله تعالى: {إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن ودا (٩٦)}','2361','875','0');
INSERT INTO [t20766] VALUES (926,'قوله تعالى: {فإنما يسرناه بلسانك لتبشر به المتقين وتنذر به قوما لدا (٩٧)}','2362','875','0');
INSERT INTO [t20766] VALUES (927,'قوله تعالى: {وكم أهلكنا قبلهم من قرن هل تحس منهم من أحد أو تسمع لهم ركزا (٩٨)}','2363','875','0');
INSERT INTO [t20766] VALUES (928,'سورة طه','2365','0','0');
INSERT INTO [t20766] VALUES (929,'قوله تعالى: {طه}','2365','928','0');
INSERT INTO [t20766] VALUES (930,'قوله تعالى: {ما أنزلنا عليك القرآن لتشقى (٢)}','2366','928','0');
INSERT INTO [t20766] VALUES (931,'قوله تعالى: {تنزيلا ممن خلق الأرض والسماوات العلى (٤)}','2368','928','0');
INSERT INTO [t20766] VALUES (932,'وقوله تعالى: {إلا تذكرة لمن يخشى (٣)}','2368','928','0');
INSERT INTO [t20766] VALUES (933,'قوله تعالى: {الرحمن على العرش استوى (٥)}','2369','928','0');
INSERT INTO [t20766] VALUES (934,'قوله تعالى: {وإن تجهر بالقول فإنه يعلم السر وأخفى (٧)}','2369','928','0');
INSERT INTO [t20766] VALUES (935,'قوله تعالى: {الله لا إله إلا هو له الأسماء الحسنى (٨)}','2371','928','0');
INSERT INTO [t20766] VALUES (936,'وقوله تعالى: {هل أتاك حديث موسى (١٥)}','2372','928','0');
INSERT INTO [t20766] VALUES (937,'قوله تعالى: {واحلل عقدة من لساني (٢٧) يفقهوا قولي (٢٨)}','2372','928','0');
INSERT INTO [t20766] VALUES (938,'قوله تعالى: {ولقد مننا عليك مرة أخرى (٣٧) إذ أوحينا إلى أمك ما يوحى (٣٨) أن اقذفيه في التابوت فاقذفيه في اليم فليلقه اليم بالساحل يأخذه عدو لي وعدو له}','2372','928','0');
INSERT INTO [t20766] VALUES (939,'قوله تعالى: {وألقيت عليك محبة مني}','2375','928','0');
INSERT INTO [t20766] VALUES (940,'قوله تعالى: {إذ تمشي أختك فتقول هل أدلكم على من يكفله فرجعناك إلى أمك كي تقر عينها ولا تحزن}','2375','928','0');
INSERT INTO [t20766] VALUES (941,'قوله تعالى: {وقتلت نفسا فنجيناك من الغم وفتناك فتونا}','2378','928','0');
INSERT INTO [t20766] VALUES (942,'قوله تعالى: {فلبثت سنين في أهل مدين ثم جئت على قدر ياموسى (٤٠)}','2380','928','0');
INSERT INTO [t20766] VALUES (943,'وقوله تعالى: {اذهب أنت وأخوك بآياتي ولا تنيا في ذكري (٤٢)}','2381','928','0');
INSERT INTO [t20766] VALUES (944,'قوله تعالى: {فقولا له قولا لينا لعله يتذكر أو يخشى (٤٤)}','2382','928','0');
INSERT INTO [t20766] VALUES (945,'قوله تعالى: {فأتياه فقولا إنا رسولا ربك فأرسل معنا بني إسرائيل ولا تعذبهم قد جئناك بآية من ربك والسلام على من اتبع الهدى (٤٧)}','2384','928','0');
INSERT INTO [t20766] VALUES (946,'قوله تعالى: {إنا قد أوحي إلينا أن العذاب على من كذب وتولى (٤٨)}','2386','928','0');
INSERT INTO [t20766] VALUES (947,'قوله تعالى: {قال فمن ربكما ياموسى (٤٩) قال ربنا الذي أعطى كل شيء خلقه ثم هدى (٥٠)}','2387','928','0');
INSERT INTO [t20766] VALUES (948,'قوله تعالى: {الذي جعل لكم الأرض مهدا وسلك لكم فيها سبلا وأنزل من السماء ماء فأخرجنا به أزواجا من نبات شتى (٥٣) كلوا وارعوا أنعامكم إن في ذلك لآيات لأولي النهى (٥٤)}','2390','928','0');
INSERT INTO [t20766] VALUES (949,'قوله تعالى: {منها خلقناكم وفيها نعيدكم ومنها نخرجكم تارة أخرى (٥٥)}','2395','928','0');
INSERT INTO [t20766] VALUES (950,'قوله تعالى: {ولقد أريناه آياتنا كلها فكذب وأبى (٥٦}','2398','928','0');
INSERT INTO [t20766] VALUES (951,'قوله تعالى: {قال أجئتنا لتخرجنا من أرضنا بسحرك ياموسى (٥٧)}','2400','928','0');
INSERT INTO [t20766] VALUES (952,'قوله تعالى: {فلنأتينك بسحر مثله}','2400','928','0');
INSERT INTO [t20766] VALUES (953,'قوله تعالى: {فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا أنت مكانا سوى (٥٨) قال موعدكم يوم الزينة وأن يحشر الناس ضحى (٥٩)}','2401','928','0');
INSERT INTO [t20766] VALUES (954,'قوله تعالى: {فتولى فرعون فجمع كيده ثم أتى (٦٠)}','2407','928','0');
INSERT INTO [t20766] VALUES (955,'قوله تعالى: {قالوا ياموسى إما أن تلقي وإما أن نكون أول من ألقى (٦٥)}','2408','928','0');
INSERT INTO [t20766] VALUES (956,'قوله تعالى: {قال بل ألقوا}','2409','928','0');
INSERT INTO [t20766] VALUES (957,'قوله تعالى: {فإذا حبالهم وعصيهم يخيل إليه من سحرهم أنها تسعى (٦٦)}','2410','928','0');
INSERT INTO [t20766] VALUES (958,'قوله تعالى: {وألق ما في يمينك تلقف ما صنعوا إنما صنعوا كيد ساحر}','2413','928','0');
INSERT INTO [t20766] VALUES (959,'قوله تعالى: {ولا يفلح الساحر حيث أتى (٦٩)}','2417','928','0');
INSERT INTO [t20766] VALUES (960,'قوله تعالى: {فألقي السحرة سجدا قالوا آمنا برب هارون وموسى (٧٠)}','2453','928','0');
INSERT INTO [t20766] VALUES (961,'قوله تعالى: {قالوا لن نؤثرك على ما جاءنا من البينات والذي فطرنا فاقض ما أنت قاض إنما تقضي هذه الحياة الدنيا (٧٢)}','2457','928','0');
INSERT INTO [t20766] VALUES (962,'قوله تعالى: {إنا آمنا بربنا ليغفر لنا خطايانا وما أكرهتنا عليه من السحر والله خير وأبقى (٧٣)}','2458','928','0');
INSERT INTO [t20766] VALUES (963,'قوله تعالى: {إنه من يأت ربه مجرما فإن له جهنم لا يموت فيها ولا يحيى (٧٤)}','2461','928','0');
INSERT INTO [t20766] VALUES (964,'قوله تعالى: {ومن يأته مؤمنا قد عمل الصالحات فأولئك لهم الدرجات العلى (٧٥)}','2462','928','0');
INSERT INTO [t20766] VALUES (965,'قوله تعالى: {ولقد أوحينا إلى موسى أن أسر بعبادي فاضرب لهم طريقا في البحر يبسا لا تخاف دركا ولا تخشى (٧٧)}','2462','928','0');
INSERT INTO [t20766] VALUES (966,'قوله تعالى: {فأتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم (٧٨)}','2466','928','0');
INSERT INTO [t20766] VALUES (967,'قوله تعالى: {وأضل فرعون قومه وما هدى (٧٩)}','2468','928','0');
INSERT INTO [t20766] VALUES (968,'قوله تعالى: {يابني إسرائيل قد أنجيناكم من عدوكم وواعدناكم جانب الطور الأيمن ونزلنا عليكم المن والسلوى (٨٠) كلوا من طيبات ما رزقناكم}','2469','928','0');
INSERT INTO [t20766] VALUES (969,'قوله تعالى: {وإني لغفار لمن تاب وآمن وعمل صالحا ثم اهتدى (٨٢)}','2474','928','0');
INSERT INTO [t20766] VALUES (970,'قوله تعالى: {وما أعجلك عن قومك ياموسى (٨٣) قال هم أولاء على أثري وعجلت إليك رب لترضى (٨٤)}','2474','928','0');
INSERT INTO [t20766] VALUES (971,'قوله تعالى: {قال فإنا قد فتنا قومك من بعدك وأضلهم السامري (٨٥)}','2476','928','0');
INSERT INTO [t20766] VALUES (972,'قوله تعالى: {فرجع موسى إلى قومه غضبان أسفا}','2478','928','0');
INSERT INTO [t20766] VALUES (973,'قوله تعالى: {قال ياقوم ألم يعدكم ربكم وعدا حسنا أفطال عليكم العهد أم أردتم أن يحل عليكم غضب من ربكم فأخلفتم موعدي (٨٦) قالوا ما أخلفنا موعدك بملكنا}','2479','928','0');
INSERT INTO [t20766] VALUES (974,'قوله تعالى: {قالوا ما أخلفنا موعدك بملكنا ولكنا حملنا أوزارا من زينة القوم فقذفناها فكذلك ألقى السامري (٨٧) فأخرج لهم عجلا جسدا له خوار فقالوا هذا إلهكم وإله موسى فنسي (٨٨)}','2483','928','0');
INSERT INTO [t20766] VALUES (975,'قوله تعالى: {أفلا يرون ألا يرجع إليهم قولا ولا يملك لهم ضرا ولا نفعا (٨٩)}','2485','928','0');
INSERT INTO [t20766] VALUES (976,'قوله تعالى: {ولقد قال لهم هارون من قبل ياقوم إنما فتنتم به وإن ربكم الرحمن فاتبعوني وأطيعوا أمري (٩٠) قالوا لن نبرح عليه عاكفين حتى يرجع إلينا موسى (٩١)}','2489','928','0');
INSERT INTO [t20766] VALUES (977,'قوله تعالى: {قال ياهارون ما منعك إذ رأيتهم ضلوا (٩٢) ألا تتبعن}','2492','928','0');
INSERT INTO [t20766] VALUES (978,'قوله تعالى: {أفعصيت أمري (٩٣)}','2494','928','0');
INSERT INTO [t20766] VALUES (979,'قوله تعالى: {قال يبنؤم لا تأخذ بلحيتي ولا برأسي إني خشيت أن تقول فرقت بين بني إسرائيل ولم ترقب قولي (٩٤)}','2495','928','0');
INSERT INTO [t20766] VALUES (980,'قوله تعالى: {إنما إلهكم الله الذي لا إله إلا هو وسع كل شيء علما (٩٨)}','2498','928','0');
INSERT INTO [t20766] VALUES (981,'قوله تعالى: {كذلك نقص عليك من أنباء ما قد سبق}','2499','928','0');
INSERT INTO [t20766] VALUES (982,'وقوله تعالى: {وقد آتيناك من لدنا ذكرا (٩٩)}','2500','928','0');
INSERT INTO [t20766] VALUES (983,'قوله تعالى: {من أعرض عنه فإنه يحمل يوم القيامة وزرا (١٠٠) خالدين فيه وساء لهم يوم القيامة حملا (١٠١)}','2501','928','0');
INSERT INTO [t20766] VALUES (984,'قوله تعالى: {ويسألونك عن الجبال فقل ينسفها ربي نسفا (١٠٥)}','2503','928','0');
INSERT INTO [t20766] VALUES (985,'قوله تعالى: {فيذرها قاعا صفصفا (١٠٦) لا ترى فيها عوجا ولا أمتا (١٠٧)}','2505','928','0');
INSERT INTO [t20766] VALUES (986,'قوله تعالى: {يومئذ يتبعون الداعي لا عوج له وخشعت الأصوات للرحمن فلا تسمع إلا همسا (١٠٨)}','2507','928','0');
INSERT INTO [t20766] VALUES (987,'قوله تعالى: {وعنت الوجوه للحي القيوم وقد خاب من حمل ظلما (١١١)}','2509','928','0');
INSERT INTO [t20766] VALUES (988,'قوله تعالى: {ولا تعجل بالقرآن من قبل أن يقضى إليك وحيه وقل رب زدني علما (١١٤)}','2511','928','0');
INSERT INTO [t20766] VALUES (989,'قوله تعالى: {ولقد عهدنا إلى آدم من قبل فنسي ولم نجد له عزما (١١٥)}','2512','928','0');
INSERT INTO [t20766] VALUES (990,'قوله تعالى: {فقلنا ياآدم إن هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى (١١٧) إن لك ألا تجوع فيها ولا تعرى (١١٨)}','2517','928','0');
INSERT INTO [t20766] VALUES (991,'قوله تعالى: {فوسوس إليه الشيطان قال ياآدم هل أدلك على شجرة الخلد وملك لا يبلى (١٢٠)}','2522','928','0');
INSERT INTO [t20766] VALUES (992,'قوله تعالى: {فأكلا منها فبدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة}','2526','928','0');
INSERT INTO [t20766] VALUES (993,'قوله تعالى: {وعصى آدم ربه فغوى (١٢١)}','2532','928','0');
INSERT INTO [t20766] VALUES (994,'قوله تعالى: {ثم اجتباه ربه فتاب عليه وهدى (١٢٢)}','2537','928','0');
INSERT INTO [t20766] VALUES (995,'قوله تعالى: {قال اهبطا منها جميعا بعضكم لبعض عدو}','2537','928','0');
INSERT INTO [t20766] VALUES (996,'قوله تعالى: {فإما يأتينكم مني هدى فمن اتبع هداي فلا يضل ولا يشقى (١٢٣)}','2546','928','0');
INSERT INTO [t20766] VALUES (997,'قوله تعالى: {ومن أعرض عن ذكري فإن له معيشة ضنكا}','2546','928','0');
INSERT INTO [t20766] VALUES (998,'قوله تعالى: {ونحشره يوم القيامة أعمى (١٢٤)}','2549','928','0');
INSERT INTO [t20766] VALUES (999,'قوله تعالى: {وكذلك نجزي من أسرف}','2552','928','0');
INSERT INTO [t20766] VALUES (1000,'قوله تعالى: {ولعذاب الآخرة أشد وأبقى (١٢٧)}','2552','928','0');
INSERT INTO [t20766] VALUES (1001,'قوله تعالى: {وقالوا لولا يأتينا بآية من ربه أولم تأتهم بينة ما في الصحف الأولى (١٣٣)}','2553','928','0');
INSERT INTO [t20766] VALUES (1002,'قوله تعالى: {ولو أنا أهلكناهم بعذاب من قبله لقالوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك من قبل أن نذل ونخزى (١٣٤)}','2554','928','0');
INSERT INTO [t20766] VALUES (1003,'قوله تعالى: {فستعلمون من أصحاب الصراط السوي ومن اهتدى (١٣٥)}','2555','928','0');
INSERT INTO [t20766] VALUES (1004,'سورة الأنبياء','2557','0','0');
INSERT INTO [t20766] VALUES (1005,'قوله تعالى: {وأسروا النجوى الذين ظلموا هل هذا إلا بشر مثلكم}','2557','1004','0');
INSERT INTO [t20766] VALUES (1006,'قوله تعالى: {أفتأتون السحر وأنتم تبصرون (٣)}','2558','1004','0');
INSERT INTO [t20766] VALUES (1007,'قوله تعالى: {بل قالوا أضغاث أحلام بل افتراه بل هو شاعر}','2559','1004','0');
INSERT INTO [t20766] VALUES (1008,'قوله تعالى: {فليأتنا بآية كما أرسل الأولون (٥)}','2561','1004','0');
INSERT INTO [t20766] VALUES (1009,'وقوله: {وما أرسلنا قبلك إلا رجالا -إلى قوله- وما كانوا خالدين (٨)}','2562','1004','0');
INSERT INTO [t20766] VALUES (1010,'قوله تعالى: {ثم صدقناهم الوعد فأنجيناهم ومن نشاء وأهلكنا المسرفين (٩)}','2562','1004','0');
INSERT INTO [t20766] VALUES (1011,'قوله تعالى: {وكم قصمنا من قرية كانت ظالمة وأنشأنا بعدها قوما آخرين (١١)}','2563','1004','0');
INSERT INTO [t20766] VALUES (1012,'قوله تعالى: {وما خلقنا السماء والأرض وما بينهما لاعبين (١٦)}','2564','1004','0');
INSERT INTO [t20766] VALUES (1013,'قوله تعالى: {وقالوا اتخذ الرحمن ولدا سبحانه بل عباد مكرمون (٢٦) لا يسبقونه بالقول وهم بأمره يعملون (٢٧)}','2564','1004','0');
INSERT INTO [t20766] VALUES (1014,'قوله تعالى: {ومن يقل منهم إني إله من دونه فذلك نجزيه جهنم كذلك نجزي الظالمين (٢٩)}','2566','1004','0');
INSERT INTO [t20766] VALUES (1015,'قوله تعالى: {أولم ير الذين كفروا أن السماوات والأرض كانتا رتقا ففتقناهما}','2567','1004','0');
INSERT INTO [t20766] VALUES (1016,'قوله تعالى: {وجعلنا من الماء كل شيء حي أفلا يؤمنون (٣٠)}','2570','1004','0');
INSERT INTO [t20766] VALUES (1017,'قوله تعالى: {وجعلنا في الأرض رواسي أن تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون (٣١)}','2572','1004','0');
INSERT INTO [t20766] VALUES (1018,'قوله تعالى: {وجعلنا السماء سقفا محفوظا وهم عن آياتها معرضون (٣٢)}','2572','1004','0');
INSERT INTO [t20766] VALUES (1019,'قوله تعالى: {وما جعلنا لبشر من قبلك الخلد أفإن مت فهم الخالدون (٣٤) كل نفس ذائقة الموت}','2574','1004','0');
INSERT INTO [t20766] VALUES (1020,'قوله تعالى: {ونبلوكم بالشر والخير فتنة وإلينا ترجعون (٣٥)}','2577','1004','0');
INSERT INTO [t20766] VALUES (1021,'قوله تعالى: {وإذا رآك الذين كفروا إن يتخذونك إلا هزوا أهذا الذي يذكر آلهتكم وهم بذكر الرحمن هم كافرون (٣٦)}','2578','1004','0');
INSERT INTO [t20766] VALUES (1022,'قوله تعالى: {خلق الإنسان من عجل سأريكم آياتي فلا تستعجلون (٣٧)}','2581','1004','0');
INSERT INTO [t20766] VALUES (1023,'قوله تعالى: {لو يعلم الذين كفروا حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون (٣٩)}','2583','1004','0');
INSERT INTO [t20766] VALUES (1024,'قوله تعالى: {ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون (٤١)}','2586','1004','0');
INSERT INTO [t20766] VALUES (1025,'قوله تعالى: {قل من يكلؤكم بالليل والنهار من الرحمن}','2587','1004','0');
INSERT INTO [t20766] VALUES (1026,'قوله تعالى: {أم لهم آلهة تمنعهم من دوننا لا يستطيعون نصر أنفسهم ولا هم منا يصحبون (٤٣)}','2589','1004','0');
INSERT INTO [t20766] VALUES (1027,'قوله تعالى: {بل متعنا هؤلاء وآباءهم حتى طال عليهم العمر}','2591','1004','0');
INSERT INTO [t20766] VALUES (1028,'قوله تعالى: {أفلا يرون أنا نأتي الأرض ننقصها من أطرافها أفهم الغالبون (٤٤)}','2592','1004','0');
INSERT INTO [t20766] VALUES (1029,'قوله تعالى: {ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا وإن كان مثقال حبة من خردل أتينا بها وكفى بنا حاسبين (٤٧)}','2594','1004','0');
INSERT INTO [t20766] VALUES (1030,'قوله تعالى: {وهذا ذكر مبارك أنزلناه أفأنتم له منكرون (٥٠)}','2599','1004','0');
INSERT INTO [t20766] VALUES (1031,'قوله تعالى: {ولقد آتينا إبراهيم رشده من قبل}','2600','1004','0');
INSERT INTO [t20766] VALUES (1032,'قوله تعالى: {قالوا حرقوه وانصروا آلهتكم إن كنتم فاعلين (٦٨)}','2600','1004','0');
INSERT INTO [t20766] VALUES (1033,'قوله تعالى: {قلنا يانار كوني بردا وسلاما على إبراهيم (٦٩) وأرادوا به كيدا فجعلناهم الأخسرين (٧٠)}','2601','1004','0');
INSERT INTO [t20766] VALUES (1034,'قوله تعالى: {ونجيناه ولوطا إلى الأرض التي باركنا فيها للعالمين (٧١)}','2603','1004','0');
INSERT INTO [t20766] VALUES (1035,'قوله تعالى: {ووهبنا له إسحاق ويعقوب نافلة وكلا جعلنا صالحين (٧٢)}','2605','1004','0');
INSERT INTO [t20766] VALUES (1036,'قوله تعالى: {وجعلناهم أئمة يهدون بأمرنا وأوحينا إليهم فعل الخيرات وإقام الصلاة وإيتاء الزكاة وكانوا لنا عابدين (٧٣)}','2606','1004','0');
INSERT INTO [t20766] VALUES (1037,'قوله تعالى: {ونوحا إذ نادى من قبل فاستجبنا له فنجيناه وأهله من الكرب العظيم (٧٦) ونصرناه من القوم الذين كذبوا بآياتنا إنهم كانوا قوم سوء فأغرقناهم أجمعين (٧٧)}','2610','1004','0');
INSERT INTO [t20766] VALUES (1038,'قوله تعالى: {وداوود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين (٧٨) ففهمناها سليمان وكلا آتينا حكما وعلما}','2611','1004','0');
INSERT INTO [t20766] VALUES (1039,'وقوله تعالى: {وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم فهل أنتم شاكرون (٨٠)}','2707','1004','0');
INSERT INTO [t20766] VALUES (1040,'قوله تعالى: {ولسليمان الريح عاصفة تجري بأمره إلى الأرض التي باركنا فيها وكنا بكل شيء عالمين (٨١)}','2710','1004','0');
INSERT INTO [t20766] VALUES (1041,'قوله تعالى: {ومن الشياطين من يغوصون له ويعملون عملا دون ذلك وكنا لهم حافظين (٨٢)}','2712','1004','0');
INSERT INTO [t20766] VALUES (1042,'قوله تعالى: {وأيوب إذ نادى ربه أني مسني الضر وأنت أرحم الراحمين (٨٣) فاستجبنا له فكشفنا ما به من ضر وآتيناه أهله ومثلهم معهم رحمة من عندنا وذكرى للعابدين (٨٤)}','2714','1004','0');
INSERT INTO [t20766] VALUES (1043,'قوله تعالى: {وذا النون إذ ذهب مغاضبا فظن أن لن نقدر عليه فنادى في الظلمات أن لا إله إلا أنت سبحانك إني كنت من الظالمين (٨٧) فاستجبنا له ونجيناه من الغم وكذلك ننجي المؤمنين (٨٨)}','2719','1004','0');
INSERT INTO [t20766] VALUES (1044,'قوله تعالى: {إن هذه أمتكم أمة واحدة وأنا ربكم فاعبدون (٩٢) وتقطعوا أمرهم بينهم كل إلينا راجعون (٩٣)}','2727','1004','0');
INSERT INTO [t20766] VALUES (1045,'قوله تعالى: {لهم فيها زفير}','2729','1004','0');
INSERT INTO [t20766] VALUES (1046,'قوله تعالى: {وهم فيها لا يسمعون (١٠٠)}','2729','1004','0');
INSERT INTO [t20766] VALUES (1047,'قوله تعالى: {إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون (١٠١)}','2730','1004','0');
INSERT INTO [t20766] VALUES (1048,'قوله تعالى: {وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون (١٠٣)}','2730','1004','0');
INSERT INTO [t20766] VALUES (1049,'قوله تعالى: {يوم نطوي السماء كطي السجل للكتب}','2731','1004','0');
INSERT INTO [t20766] VALUES (1050,'قوله تعالى: {إن في هذا لبلاغا لقوم عابدين (١٠٦)}','2734','1004','0');
INSERT INTO [t20766] VALUES (1051,'قوله تعالى: {وما أرسلناك إلا رحمة للعالمين (١٠٧)}','2734','1004','0');
INSERT INTO [t20766] VALUES (1052,'قوله تعالى: {فإن تولوا فقل آذنتكم على سواء}','2735','1004','0');
INSERT INTO [t20766] VALUES (1053,'قوله تعالى: {إنه يعلم الجهر من القول ويعلم ما تكتمون (١١٠)}','2736','1004','0');
INSERT INTO [t20766] VALUES (1054,'قوله تعالى: {قال رب احكم بالحق}','2736','1004','0');
INSERT INTO [t20766] VALUES (1055,'سورة الحج','2738','0','0');
INSERT INTO [t20766] VALUES (1056,'قوله تعالى: {يا أيها الناس اتقوا ربكم إن زلزلة الساعة شيء عظيم (١) يوم ترونها تذهل كل مرضعة عما أرضعت وتضع كل ذات حمل حملها وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد (٢)}','2739','1055','0');
INSERT INTO [t20766] VALUES (1057,'وقوله تعالى: {ثقلت في السماوات والأرض لا تأتيكم إلا بغتة}','2740','1055','0');
INSERT INTO [t20766] VALUES (1058,'قوله تعالى: {ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد (٣) كتب عليه أنه من تولاه فأنه يضله ويهديه إلى عذاب السعير (٤)}','2750','1055','0');
INSERT INTO [t20766] VALUES (1059,'قوله تعالى: {يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ونقر في الأرحام ما نشاء إلى أجل مسمى ثم نخرجكم طفلا ثم لتبلغوا أشدكم ومنكم من يتوفى ومنكم من يرد إلى أرذل العمر لكيلا يعلم من بعد علم شيئا}','2754','1055','0');
INSERT INTO [t20766] VALUES (1060,'قوله تعالى: {وترى الأرض هامدة فإذا أنزلنا عليها الماء اهتزت وربت وأنبتت من كل زوج بهيج (٥)}','2774','1055','0');
INSERT INTO [t20766] VALUES (1061,'قوله تعالى: {ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير (٨) ثاني عطفه ليضل عن سبيل الله له في الدنيا خزي ونذيقه يوم القيامة عذاب الحريق (٩)}','2776','1055','0');
INSERT INTO [t20766] VALUES (1062,'قوله تعالى: {ذلك بما قدمت يداك وأن الله ليس بظلام للعبيد (١٠)}','2779','1055','0');
INSERT INTO [t20766] VALUES (1063,'قوله تعالى: {يدعو من دون الله ما لا يضره وما لا ينفعه ذلك هو الضلال البعيد (١٢)}','2782','1055','0');
INSERT INTO [t20766] VALUES (1064,'قوله تعالى: {من كان يظن أن لن ينصره الله في الدنيا والآخرة فليمدد بسبب إلى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ (١٥)}','2786','1055','0');
INSERT INTO [t20766] VALUES (1065,'قوله تعالى: {ألم تر أن الله يسجد له من في السماوات ومن في الأرض}','2789','1055','0');
INSERT INTO [t20766] VALUES (1066,'قوله تعالى: {فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم (١٩) يصهر به ما في بطونهم والجلود (٢٠) ولهم مقامع من حديد (٢١)}','2789','1055','0');
INSERT INTO [t20766] VALUES (1067,'قوله تعالى: {كلما أرادوا أن يخرجوا منها من غم أعيدوا فيها وذوقوا عذاب الحريق (٢٢)}','2792','1055','0');
INSERT INTO [t20766] VALUES (1068,'قوله تعالى: {إن الذين كفروا ويصدون عن سبيل الله والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد ومن يرد فيه بإلحاد بظلم نذقه من عذاب أليم (٢٥)}','2793','1055','0');
INSERT INTO [t20766] VALUES (1069,'قوله تعالى: {وإذ بوأنا لإبراهيم مكان البيت أن لا تشرك بي شيئا وطهر بيتي للطائفين والقائمين والركع السجود (٢٦)}','2798','1055','0');
INSERT INTO [t20766] VALUES (1070,'قوله تعالى: {وأذن في الناس بالحج يأتوك رجالا وعلى كل ضامر يأتين من كل فج عميق (٢٧)}','2802','1055','0');
INSERT INTO [t20766] VALUES (1071,'قوله تعالى: {ليشهدوا منافع لهم}','3265','1055','0');
INSERT INTO [t20766] VALUES (1072,'قوله تعالى: {ويذكروا اسم الله في أيام معلومات على ما رزقهم من بهيمة الأنعام}','3270','1055','0');
INSERT INTO [t20766] VALUES (1073,'قوله تعالى: {فكلوا منها وأطعموا البائس الفقير (٢٨)}','3389','1055','0');
INSERT INTO [t20766] VALUES (1074,'قوله تعالى: {وليوفوا نذورهم}','3452','1055','0');
INSERT INTO [t20766] VALUES (1075,'قوله تعالى: {وليطوفوا بالبيت العتيق (٢٩)}','3482','1055','0');
INSERT INTO [t20766] VALUES (1076,'قوله تعالى: {وأحلت لكم الأنعام إلا ما يتلى عليكم}','3483','1055','0');
INSERT INTO [t20766] VALUES (1077,'قوله تعالى: {فاجتنبوا الرجس من الأوثان}','3484','1055','0');
INSERT INTO [t20766] VALUES (1078,'قوله تعالى: {واجتنبوا قول الزور (٣٠) حنفاء لله غير مشركين به}','3484','1055','0');
INSERT INTO [t20766] VALUES (1079,'قوله تعالى: {ومن يشرك بالله فكأنما خر من السماء فتخطفه الطير أو تهوي به الريح في مكان سحيق (٣١)}','3486','1055','0');
INSERT INTO [t20766] VALUES (1080,'قوله تعالى: {ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب (٣٢)}','3488','1055','0');
INSERT INTO [t20766] VALUES (1081,'قوله تعالى: {وبشر المخبتين (٣٤) الذين إذا ذكر الله وجلت قلوبهم والصابرين على ما أصابهم}','3489','1055','0');
INSERT INTO [t20766] VALUES (1082,'قوله تعالى: {فكلوا منها وأطعموا القانع والمعتر}','3491','1055','0');
INSERT INTO [t20766] VALUES (1083,'قوله تعالى: {كذلك سخرناها لكم لعلكم تشكرون (٣٦)}','3491','1055','0');
INSERT INTO [t20766] VALUES (1084,'قوله تعالى: {إن الله يدافع عن الذين آمنوا}','3493','1055','0');
INSERT INTO [t20766] VALUES (1085,'قوله تعالى: {إن الله لا يحب كل خوان كفور (٣٨)}','3495','1055','0');
INSERT INTO [t20766] VALUES (1086,'قوله تعالى: {أذن للذين يقاتلون بأنهم ظلموا وإن الله على نصرهم لقدير (٣٩)}','3495','1055','0');
INSERT INTO [t20766] VALUES (1087,'قوله تعالى: {الذين أخرجوا من ديارهم بغير حق إلا أن يقولوا ربنا الله}','3499','1055','0');
INSERT INTO [t20766] VALUES (1088,'قوله تعالى: {ولينصرن الله من ينصره إن الله لقوي عزيز (٤٠)}','3500','1055','0');
INSERT INTO [t20766] VALUES (1089,'قوله تعالى: {وإن يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود (٤٢) وقوم إبراهيم وقوم لوط (٤٣) وأصحاب مدين وكذب موسى فأمليت للكافرين ثم أخذتهم فكيف كان نكير (٤٤)}','3501','1055','0');
INSERT INTO [t20766] VALUES (1090,'قوله تعالى: {فكأين من قرية أهلكناها وهي ظالمة فهي خاوية على عروشها وبئر معطلة وقصر مشيد (٤٥)}','3506','1055','0');
INSERT INTO [t20766] VALUES (1091,'قوله تعالى: {أفلم يسيروا في الأرض فتكون لهم قلوب يعقلون بها أو آذان يسمعون بها}','3513','1055','0');
INSERT INTO [t20766] VALUES (1092,'قوله تعالى: {فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور (٤٦)}','3514','1055','0');
INSERT INTO [t20766] VALUES (1093,'قوله تعالى: {ويستعجلونك بالعذاب ولن يخلف الله وعده}','3514','1055','0');
INSERT INTO [t20766] VALUES (1094,'قوله تعالى: {وإن يوما عند ربك كألف سنة مما تعدون (٤٧)}','3517','1055','0');
INSERT INTO [t20766] VALUES (1095,'قوله تعالى: {وكأين من قرية أمليت لها وهي ظالمة ثم أخذتها وإلي المصير (٤٨)}','3522','1055','0');
INSERT INTO [t20766] VALUES (1096,'قوله تعالى: {ياأيها الناس إنما أنا لكم نذير مبين (٤٩)}','3522','1055','0');
INSERT INTO [t20766] VALUES (1097,'قوله تعالى: {فالذين آمنوا وعملوا الصالحات لهم مغفرة ورزق كريم (٥٠) والذين سعوا في آياتنا معاجزين أولئك أصحاب الجحيم (٥١)}','3523','1055','0');
INSERT INTO [t20766] VALUES (1098,'قوله تعالى: {وما أرسلنا من قبلك من رسول ولا نبي إلا إذا تمنى ألقى الشيطان في أمنيته فينسخ الله ما يلقي الشيطان ثم يحكم الله آياته والله عليم حكيم (٥٢)}','3527','1055','0');
INSERT INTO [t20766] VALUES (1099,'قوله تعالى: {ولا يزال الذين كفروا في مرية منه حتى تأتيهم الساعة بغتة أو يأتيهم عذاب يوم عقيم (٥٥)}','3536','1055','0');
INSERT INTO [t20766] VALUES (1100,'قوله تعالى: {الملك يومئذ لله يحكم بينهم}','3537','1055','0');
INSERT INTO [t20766] VALUES (1101,'قوله تعالى: {فالذين آمنوا وعملوا الصالحات في جنات النعيم (٥٦) والذين كفروا وكذبوا بآياتنا فأولئك لهم عذاب مهين (٥٧)}','3538','1055','0');
INSERT INTO [t20766] VALUES (1102,'قوله تعالى: {والذين هاجروا في سبيل الله ثم قتلوا أو ماتوا ليرزقنهم الله رزقا حسنا وإن الله لهو خير الرازقين (٥٨)}','3538','1055','0');
INSERT INTO [t20766] VALUES (1103,'قوله تعالى: {ذلك بأن الله يولج الليل في النهار ويولج النهار في الليل وأن الله سميع بصير (٦١) ذلك بأن الله هو الحق وأن ما يدعون من دونه هو الباطل وأن الله هو العلي الكبير (٦٢)}','3539','1055','0');
INSERT INTO [t20766] VALUES (1104,'قوله تعالى: {ألم تر أن الله أنزل من السماء ماء فتصبح الأرض مخضرة إن الله لطيف خبير (٦٣)}','3541','1055','0');
INSERT INTO [t20766] VALUES (1105,'قوله تعالى: {ألم تر أن الله سخر لكم ما في الأرض والفلك تجري في البحر بأمره}','3544','1055','0');
INSERT INTO [t20766] VALUES (1106,'قوله تعالى: {ويمسك السماء أن تقع على الأرض إلا بإذنه}','3545','1055','0');
INSERT INTO [t20766] VALUES (1107,'قوله تعالى: {وهو الذي أحياكم ثم يميتكم ثم يحييكم إن الإنسان لكفور (٦٦)}','3546','1055','0');
INSERT INTO [t20766] VALUES (1108,'قوله تعالى: {لكل أمة جعلنا منسكا هم ناسكوه}','3546','1055','0');
INSERT INTO [t20766] VALUES (1109,'قوله تعالى: {وادع إلى ربك إنك لعلى هدى مستقيم (٦٧)}','3547','1055','0');
INSERT INTO [t20766] VALUES (1110,'قوله تعالى: {وإن جادلوك فقل الله أعلم بما تعملون (٦٨)}','3548','1055','0');
INSERT INTO [t20766] VALUES (1111,'قوله تعالى: {ما قدروا الله حق قدره}','3549','1055','0');
INSERT INTO [t20766] VALUES (1112,'قوله تعالى: {الله يصطفي من الملائكة رسلا ومن الناس}','3549','1055','0');
INSERT INTO [t20766] VALUES (1113,'قوله تعالى: {هو اجتباكم}','3550','1055','0');
INSERT INTO [t20766] VALUES (1114,'قوله تعالى: {وما جعل عليكم في الدين من حرج}','3550','1055','0');
INSERT INTO [t20766] VALUES (1115,'قوله تعالى: {ملة أبيكم إبراهيم}','3552','1055','0');
INSERT INTO [t20766] VALUES (1116,'قوله تعالى: {هو سماكم المسلمين من قبل وفي هذا}','3552','1055','0');
INSERT INTO [t20766] VALUES (1117,'قوله تعالى: {ليكون الرسول شهيدا عليكم وتكونوا شهداء على الناس}','3554','1055','0');
INSERT INTO [t20766] VALUES (1118,'سورة المؤمنون','3556','0','0');
INSERT INTO [t20766] VALUES (1119,'قوله تعالى: {قد أفلح المؤمنون (١) الذين هم في صلاتهم خاشعون (٢)}','3557','1118','0');
INSERT INTO [t20766] VALUES (1120,'قوله تعالى: {والذين هم عن اللغو معرضون (٣)}','3559','1118','0');
INSERT INTO [t20766] VALUES (1121,'قوله تعالى: {والذين هم للزكاة فاعلون (٤)}','3560','1118','0');
INSERT INTO [t20766] VALUES (1122,'قوله تعالى: {والذين هم لفروجهم حافظون (٥) إلا على أزواجهم أو ما ملكت أيمانهمما ملكت أيمانهم فإنهم غير ملومين (٦) فمن ابتغى وراء ذلك فأولئك هم العادون (٧)}','3562','1118','0');
INSERT INTO [t20766] VALUES (1123,'قوله تعالى: {والذين هم لأماناتهم وعهدهم راعون (٨)}','3578','1118','0');
INSERT INTO [t20766] VALUES (1124,'قوله تعالى: {والذين هم على صلواتهم يحافظون (٩)}','3579','1118','0');
INSERT INTO [t20766] VALUES (1125,'قوله تعالى: {أولئك هم الوارثون (١٠) الذين يرثون الفردوس هم فيها خالدون (١١)}','3580','1118','0');
INSERT INTO [t20766] VALUES (1126,'قوله تعالى: {ولقد خلقنا الإنسان من سلالة من طين (١٢) ثم جعلناه نطفة في قرار مكين (١٣) ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم أنشأناه خلقا آخر فتبارك الله أحسن الخالقين (١٤)}','3581','1118','0');
INSERT INTO [t20766] VALUES (1127,'قوله تعالى: {ثم إنكم بعد ذلك لميتون (١٥) ثم إنكم يوم القيامة تبعثون (١٦)}','3587','1118','0');
INSERT INTO [t20766] VALUES (1128,'قوله تعالى: {ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غافلين (١٧)}','3587','1118','0');
INSERT INTO [t20766] VALUES (1129,'قوله تعالى: {وأنزلنا من السماء ماء بقدر فأسكناه في الأرض وإنا على ذهاب به لقادرون (١٨)}','3589','1118','0');
INSERT INTO [t20766] VALUES (1130,'قوله تعالى: {فأنشأنا لكم به جنات من نخيل وأعناب لكم فيها فواكه كثيرة ومنها تأكلون (١٩)}','3593','1118','0');
INSERT INTO [t20766] VALUES (1131,'قوله تعالى: {وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للآكلين (٢٠)}','3593','1118','0');
INSERT INTO [t20766] VALUES (1132,'قوله تعالى: {وإن لكم في الأنعام لعبرة نسقيكم مما في بطونها ولكم فيها منافع كثيرة ومنها تأكلون (٢١)}','3595','1118','0');
INSERT INTO [t20766] VALUES (1133,'قوله تعالى: {وعليها وعلى الفلك تحملون (٢٢)}','3595','1118','0');
INSERT INTO [t20766] VALUES (1134,'قوله تعالى: {ولقد أرسلنا نوحا}','3596','1118','0');
INSERT INTO [t20766] VALUES (1135,'قوله تعالى: {ثم أرسلنا رسلنا تترى كل ما جاء أمة رسولها كذبوه فأتبعنا بعضهم بعضا وجعلناهم أحاديث فبعدا لقوم لا يؤمنون (٤٤)}','3596','1118','0');
INSERT INTO [t20766] VALUES (1136,'قوله تعالى: {ياأيها الرسل كلوا من الطيبات واعملوا صالحا إني بما تعملون عليم (٥١)}','3599','1118','0');
INSERT INTO [t20766] VALUES (1137,'قوله تعالى: {وإن هذه أمتكم أمة واحدة وأنا ربكم فاتقون (٥٢) فتقطعوا أمرهم بينهم زبرا كل حزب بما لديهم فرحون (٥٣)}','3600','1118','0');
INSERT INTO [t20766] VALUES (1138,'قوله تعالى: {فذرهم في غمرتهم حتى حين (٥٤)}','3600','1118','0');
INSERT INTO [t20766] VALUES (1139,'قوله تعالى: {أيحسبون أنما نمدهم به من مال وبنين (٥٥) نسارع لهم في الخيرات بل لا يشعرون (٥٦)}','3602','1118','0');
INSERT INTO [t20766] VALUES (1140,'قوله تعالى: {ولا نكلف نفسا إلا وسعها}','3602','1118','0');
INSERT INTO [t20766] VALUES (1141,'قوله تعالى: {ولدينا كتاب ينطق بالحق وهم لا يظلمون (٦٢)}','3602','1118','0');
INSERT INTO [t20766] VALUES (1142,'قوله تعالى: {حتى إذا أخذنا مترفيهم بالعذاب إذا هم يجأرون (٦٤) لا تجأروا اليوم إنكم منا لا تنصرون (٦٥)}','3603','1118','0');
INSERT INTO [t20766] VALUES (1143,'قوله تعالى: {قد كانت آياتي تتلى عليكم فكنتم على أعقابكم تنكصون (٦٦)}','3605','1118','0');
INSERT INTO [t20766] VALUES (1144,'قوله تعالى: {أفلم يدبروا القول}','3606','1118','0');
INSERT INTO [t20766] VALUES (1145,'قوله تعالى: {أم لم يعرفوا رسولهم فهم له منكرون (٦٩)}','3607','1118','0');
INSERT INTO [t20766] VALUES (1146,'قوله تعالى: {أم يقولون به جنة بل جاءهم بالحق وأكثرهم للحق كارهون (٧٠)}','3607','1118','0');
INSERT INTO [t20766] VALUES (1147,'قوله تعالى: {ولو اتبع الحق أهواءهم لفسدت السماوات والأرض ومن فيهن}','3610','1118','0');
INSERT INTO [t20766] VALUES (1148,'قوله تعالى: {بل أتيناهم بذكرهم فهم عن ذكرهم معرضون (٧١)}','3612','1118','0');
INSERT INTO [t20766] VALUES (1149,'قوله تعالى: {أم تسألهم خرجا فخراج ربك خير وهو خير الرازقين (٧٢)}','3613','1118','0');
INSERT INTO [t20766] VALUES (1150,'قوله تعالى: {وإنك لتدعوهم إلى صراط مستقيم (٧٣)}','3614','1118','0');
INSERT INTO [t20766] VALUES (1151,'قوله تعالى: {وإن الذين لا يؤمنون بالآخرة عن الصراط لناكبون (٧٤)}','3614','1118','0');
INSERT INTO [t20766] VALUES (1152,'قوله تعالى: {ولو رحمناهم وكشفنا ما بهم من ضر للجوا في طغيانهم يعمهون (٧٥)}','3615','1118','0');
INSERT INTO [t20766] VALUES (1153,'قوله تعالى: {ولقد أخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون (٧٦)}','3615','1118','0');
INSERT INTO [t20766] VALUES (1154,'قوله تعالى: {وهو الذي أنشأ لكم السمع والأبصار والأفئدة قليلا ما تشكرون (٧٨)}','3616','1118','0');
INSERT INTO [t20766] VALUES (1155,'قوله تعالى: {وهو الذي ذرأكم في الأرض وإليه تحشرون (٧٩)}','3616','1118','0');
INSERT INTO [t20766] VALUES (1156,'قوله تعالى: {وهو الذي يحيي ويميت}','3617','1118','0');
INSERT INTO [t20766] VALUES (1157,'قوله تعالى: {وله اختلاف الليل والنهار أفلا تعقلون (٨٠)}','3617','1118','0');
INSERT INTO [t20766] VALUES (1158,'قوله تعالى: {بل قالوا مثل ما قال الأولون (٨١) قالوا أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون (٨٢)}','3618','1118','0');
INSERT INTO [t20766] VALUES (1159,'قوله تعالى: {لقد وعدنا نحن وآباؤنا هذا من قبل إن هذا إلا أساطير الأولين (٨٣)}','3620','1118','0');
INSERT INTO [t20766] VALUES (1160,'قوله تعالى: {قل لمن الأرض ومن فيها إن كنتم تعلمون (٨٤) سيقولون لله قل أفلا تذكرون (٨٥) قل من رب السماوات السبع ورب العرش العظيم (٨٦) سيقولون لله قل أفلا تتقون (٨٧) قل من بيده ملكوت كل شيء وهو يجير ولا يجار عليه إن كنتم تعلمون (٨٨) سيقولون لله قل فأنى تسحرون (٨٩)}','3621','1118','0');
INSERT INTO [t20766] VALUES (1161,'قوله تعالى: {ما اتخذ الله من ولد وما كان معه من إله إذا لذهب كل إله بما خلق ولعلا بعضهم على بعض سبحان الله عما يصفون (٩١)}','3624','1118','0');
INSERT INTO [t20766] VALUES (1162,'قوله تعالى: {قل رب إما تريني ما يوعدون (٩٣) رب فلا تجعلني في القوم الظالمين (٩٤)}','3625','1118','0');
INSERT INTO [t20766] VALUES (1163,'قوله تعالى: {ادفع بالتي هي أحسن السيئة نحن أعلم بما يصفون (٩٦) وقل رب أعوذ بك من همزات الشياطين (٩٧) وأعوذ بك رب أن يحضرون (٩٨)}','3626','1118','0');
INSERT INTO [t20766] VALUES (1164,'قوله تعالى: {حتى إذا جاء أحدهم الموت قال رب ارجعون (٩٩) لعلي أعمل صالحا فيما تركت كلا}','3627','1118','0');
INSERT INTO [t20766] VALUES (1165,'قوله تعالى: {فإذا نفخ في الصور فلا أنساب بينهم يومئذ ولا يتساءلون (١٠١)}','3631','1118','0');
INSERT INTO [t20766] VALUES (1166,'قوله تعالى: {فمن ثقلت موازينه فأولئك هم المفلحون (١٠٢) ومن خفت موازينه فأولئك الذين خسروا أنفسهم في جهنم خالدون (١٠٣)}','3632','1118','0');
INSERT INTO [t20766] VALUES (1167,'قوله تعالى: {تلفح وجوههم النار وهم فيها كالحون (١٠٤)}','3632','1118','0');
INSERT INTO [t20766] VALUES (1168,'قوله تعالى: {ألم تكن آياتي تتلى عليكم فكنتم بها تكذبون (١٠٥) قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين (١٠٦)}','3633','1118','0');
INSERT INTO [t20766] VALUES (1169,'قوله تعالى: {ربنا أخرجنا منها فإن عدنا فإنا ظالمون (١٠٧) قال اخسئوا فيها ولا تكلمون (١٠٨)}','3635','1118','0');
INSERT INTO [t20766] VALUES (1170,'قوله تعالى: {إنه كان فريق من عبادي يقولون ربنا آمنا فاغفر لنا وارحمنا وأنت خير الراحمين (١٠٩) فاتخذتموهم سخريا حتى أنسوكم ذكري وكنتم منهم تضحكون (١١٠)}','3636','1118','0');
INSERT INTO [t20766] VALUES (1171,'قوله تعالى: {إني جزيتهم اليوم بما صبروا أنهم هم الفائزون (١١١)}','3638','1118','0');
INSERT INTO [t20766] VALUES (1172,'قوله تعالى: {قال كم لبثتم في الأرض عدد سنين (١١٢) قالوا لبثنا يوما أو بعض يوم فاسأل العادين (١١٣)}','3639','1118','0');
INSERT INTO [t20766] VALUES (1173,'قوله تعالى: {أفحسبتم أنما خلقناكم عبثا وأنكم إلينا لا ترجعون (١١٥) فتعالى الله الملك الحق لا إله إلا هو رب العرش الكريم (١١٦)}','3641','1118','0');
INSERT INTO [t20766] VALUES (1174,'قوله تعالى: {ومن يدع مع الله إلها آخر لا برهان له به فإنما حسابه عند ربه إنه لا يفلح الكافرون (١١٧)}','3642','1118','0');
INSERT INTO [t20766] VALUES (1175,'سورة النور','3646','0','0');
INSERT INTO [t20766] VALUES (1176,'قوله تعالى: {الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة}','3647','1175','0');
INSERT INTO [t20766] VALUES (1177,'قوله تعالى: {الزاني لا ينكح إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك وحرم ذلك على المؤمنين (٣)}','3722','1175','0');
INSERT INTO [t20766] VALUES (1178,'قوله تعالى: {والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدا وأولئك هم الفاسقون (٤) إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم (٥)}','3737','1175','0');
INSERT INTO [t20766] VALUES (1179,'قوله تعالى: {ويدرأ عنها العذاب أن تشهد أربع شهادات بالله إنه لمن الكاذبين (٨)}','3788','1175','0');
INSERT INTO [t20766] VALUES (1180,'قوله تعالى: {ولولا فضل الله عليكم ورحمته ما زكى منكم من أحد أبدا ولكن الله يزكي من يشاء والله سميع عليم (٢١)}','3819','1175','0');
INSERT INTO [t20766] VALUES (1181,'قوله تعالى: {ولا يأتل أولو الفضل منكم والسعة أن يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله وليعفوا وليصفحوا ألا تحبون أن يغفر الله لكم والله غفور رحيم (٢٢)}','3820','1175','0');
INSERT INTO [t20766] VALUES (1182,'قوله تعالى: {يوم تشهد عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون (٢٤)}','3827','1175','0');
INSERT INTO [t20766] VALUES (1183,'قوله تعالى: {يومئذ يوفيهم الله دينهم الحق}','3828','1175','0');
INSERT INTO [t20766] VALUES (1184,'قوله تعالى: {ياأيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها ذلكم خير لكم لعلكم تذكرون (٢٧)}','3828','1175','0');
INSERT INTO [t20766] VALUES (1185,'قوله تعالى: {قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم ذلك أزكى لهم إن الله خبير بما يصنعون (٣٠) وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن}','3850','1175','0');
INSERT INTO [t20766] VALUES (1186,'قوله تعالى: {ولا يبدين زينتهن إلا ما ظهر منها}','3856','1175','0');
INSERT INTO [t20766] VALUES (1187,'قوله تعالى: {وتوبوا إلى الله جميعا أيه المؤمنون لعلكم تفلحون (٣١)}','3868','1175','0');
INSERT INTO [t20766] VALUES (1188,'قوله تعالى: {وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم إن يكونوا فقراء يغنهم الله من فضله}','3880','1175','0');
INSERT INTO [t20766] VALUES (1189,'قوله تعالى: {وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله}','3886','1175','0');
INSERT INTO [t20766] VALUES (1190,'قوله تعالى: {ومن يكرههن فإن الله من بعد إكراههن غفور رحيم (٣٣)}','3886','1175','0');
INSERT INTO [t20766] VALUES (1191,'قوله تعالى: {ولقد أنزلنا إليكم آيات مبينات ومثلا من الذين خلوا من قبلكم وموعظة للمتقين (٣٤)}','3887','1175','0');
INSERT INTO [t20766] VALUES (1192,'قوله تعالى: {يسبح له فيها بالغدو والآصال (٣٦) رجال لا تلهيهم تجارة ولا بيع عن ذكر الله}','3893','1175','0');
INSERT INTO [t20766] VALUES (1193,'قوله تعالى: {يخافون يوما تتقلب فيه القلوب والأبصار (٣٧)}','3909','1175','0');
INSERT INTO [t20766] VALUES (1194,'قوله تعالى: {ليجزيهم الله أحسن ما عملوا ويزيدهم من فضله}','3910','1175','0');
INSERT INTO [t20766] VALUES (1195,'قوله تعالى: {والذين كفروا أعمالهم كسراب بقيعة يحسبه الظمآن ماء حتى إذا جاءه لم يجده شيئا}','3911','1175','0');
INSERT INTO [t20766] VALUES (1196,'قوله تعالى: {ألم تر أن الله يسبح له من في السماوات والأرض والطير صافات كل قد علم صلاته وتسبيحه والله عليم بما يفعلون (٤١)}','3913','1175','0');
INSERT INTO [t20766] VALUES (1197,'قوله تعالى: {وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم}','3915','1175','0');
INSERT INTO [t20766] VALUES (1198,'قوله تعالى: {وليمكنن لهم دينهم الذي ارتضى لهم}','3916','1175','0');
INSERT INTO [t20766] VALUES (1199,'قوله تعالى: {وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون (٥٦)}','3916','1175','0');
INSERT INTO [t20766] VALUES (1200,'قوله تعالى: {لا تحسبن الذين كفروا معجزين في الأرض}','3917','1175','0');
INSERT INTO [t20766] VALUES (1201,'قوله تعالى: {لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا}','3920','1175','0');
INSERT INTO [t20766] VALUES (1202,'قوله تعالى: {فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم (٦٣)}','3922','1175','0');
INSERT INTO [t20766] VALUES (1203,'قوله تعالى: {ويوم يرجعون إليه فينبئهم بما عملوا والله بكل شيء عليم (٦٤)}','3927','1175','0');
INSERT INTO [t20766] VALUES (1204,'سورة الفرقان','3928','0','0');
INSERT INTO [t20766] VALUES (1205,'قوله تعالى: {تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا (١)}','3929','1204','0');
INSERT INTO [t20766] VALUES (1206,'قوله تعالى: {الذي له ملك السماوات والأرض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا (٢)}','3933','1204','0');
INSERT INTO [t20766] VALUES (1207,'قوله تعالى: {واتخذوا من دونه آلهة لا يخلقون شيئا وهم يخلقون ولا يملكون لأنفسهم ضرا ولا نفعا ولا يملكون موتا ولا حياة ولا نشورا (٣)}','3937','1204','0');
INSERT INTO [t20766] VALUES (1208,'قوله تعالى: {وقال الذين كفروا إن هذا إلا إفك افتراه وأعانه عليه قوم آخرون فقد جاءوا ظلما وزورا (٤)}','3943','1204','0');
INSERT INTO [t20766] VALUES (1209,'قوله تعالى: {وقالوا أساطير الأولين اكتتبها فهي تملى عليه بكرة وأصيلا (٥) قل أنزله الذي يعلم السر في السماوات والأرض إنه كان غفورا رحيما}','3945','1204','0');
INSERT INTO [t20766] VALUES (1210,'قوله تعالى: {وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق}','3948','1204','0');
INSERT INTO [t20766] VALUES (1211,'قوله تعالى: {لولا أنزل إليه ملك فيكون معه نذيرا (٧) أو يلقى إليه كنز أو تكون له جنة يأكل منها}','3950','1204','0');
INSERT INTO [t20766] VALUES (1212,'قوله تعالى: {وقال الظالمون إن تتبعون إلا رجلا مسحورا (٨) انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا (٩)}','3954','1204','0');
INSERT INTO [t20766] VALUES (1213,'قوله تعالى: {بل كذبوا بالساعة وأعتدنا لمن كذب بالساعة سعيرا (١١)}','3956','1204','0');
INSERT INTO [t20766] VALUES (1214,'قوله تعالى: {إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا (١٢)}','3958','1204','0');
INSERT INTO [t20766] VALUES (1215,'قوله تعالى: {وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا (١٣) لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا (١٤)}','3961','1204','0');
INSERT INTO [t20766] VALUES (1216,'قوله تعالى: {أذلك خير أم جنة الخلد التي وعد المتقون كانت لهم جزاء ومصيرا (١٥) لهم فيها ما يشاءون خالدين كان على ربك وعدا مسئولا (١٦)}','3965','1204','0');
INSERT INTO [t20766] VALUES (1217,'قوله تعالى: {من دون الله فيقول أأنتم أضللتم عبادي هؤلاء أم هم ضلوا السبيل (١٧) قالوا سبحانك ما كان ينبغي لنا أن نتخذ من دونك من أولياء ولكن متعتهم وآباءهم حتى نسوا الذكر وكانوا قوما بورا (١٨)}','3969','1204','0');
INSERT INTO [t20766] VALUES (1218,'قوله تعالى: {فقد كذبوكم بما تقولون}','3972','1204','0');
INSERT INTO [t20766] VALUES (1219,'قوله تعالى: {ومن يظلم منكم نذقه عذابا كبيرا (١٩)}','3973','1204','0');
INSERT INTO [t20766] VALUES (1220,'قوله تعالى: {وجعلنا بعضكم لبعض فتنة}','3973','1204','0');
INSERT INTO [t20766] VALUES (1221,'قوله تعالى: {وقال الذين لا يرجون لقاءنا لولا أنزل علينا الملائكة أو نرى ربنا لقد استكبروا في أنفسهم وعتوا عتوا كبيرا (٢١)}','3975','1204','0');
INSERT INTO [t20766] VALUES (1222,'قوله تعالى: {يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا (٢٢)}','3977','1204','0');
INSERT INTO [t20766] VALUES (1223,'قوله تعالى: {وقدمنا إلى ما عملوا من عمل فجعلناه هباء منثورا (٢٣)}','3980','1204','0');
INSERT INTO [t20766] VALUES (1224,'قوله تعالى: {أصحاب الجنة يومئذ خير مستقرا وأحسن مقيلا (٢٤)}','3980','1204','0');
INSERT INTO [t20766] VALUES (1225,'قوله تعالى: {ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا (٢٥)}','3984','1204','0');
INSERT INTO [t20766] VALUES (1226,'قوله تعالى: {الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا (٢٦)}','3986','1204','0');
INSERT INTO [t20766] VALUES (1227,'قوله تعالى: {ويوم يعض الظالم على يديه يقول ياليتني اتخذت مع الرسول سبيلا (٢٧) ياويلتى ليتني لم أتخذ فلانا خليلا (٢٨) لقد أضلني عن الذكر بعد إذ جاءني وكان الشيطان للإنسان خذولا (٢٩)}','3986','1204','0');
INSERT INTO [t20766] VALUES (1228,'قوله تعالى: {وقال الرسول يارب إن قومي اتخذوا هذا القرآن مهجورا (٣٠)}','3990','1204','0');
INSERT INTO [t20766] VALUES (1229,'قوله تعالى: {وكذلك جعلنا لكل نبي عدوا من المجرمين وكفى بربك هاديا ونصيرا (٣١)}','3995','1204','0');
INSERT INTO [t20766] VALUES (1230,'قوله تعالى: {كذلك لنثبت به فؤادك ورتلناه ترتيلا (٣٢)}','3996','1204','0');
INSERT INTO [t20766] VALUES (1231,'قوله تعالى: {الذين يحشرون على وجوههم إلى جهنم أولئك شر مكانا وأضل سبيلا (٣٤)}','3996','1204','0');
INSERT INTO [t20766] VALUES (1232,'قوله تعالى: {ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا (٣٥) فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا (٣٦)}','3998','1204','0');
INSERT INTO [t20766] VALUES (1233,'قوله تعالى: {وقوم نوح لما كذبوا الرسل أغرقناهم وجعلناهم للناس آية}','3998','1204','0');
INSERT INTO [t20766] VALUES (1234,'قوله تعالى: {وعادا وثمود وأصحاب الرس وقرونا بين ذلك كثيرا (٣٨)}','3998','1204','0');
INSERT INTO [t20766] VALUES (1235,'قوله تعالى: {وكلا ضربنا له الأمثال وكلا تبرنا تتبيرا (٣٩)}','4001','1204','0');
INSERT INTO [t20766] VALUES (1236,'قوله تعالى: {ولقد أتوا على القرية التي أمطرت مطر السوء أفلم يكونوا يرونها بل كانوا لا يرجون نشورا (٤٠)}','4003','1204','0');
INSERT INTO [t20766] VALUES (1237,'قوله تعالى: {وإذا رأوك إن يتخذونك إلا هزوا أهذا الذي بعث الله رسولا (٤١) إن كاد ليضلنا عن آلهتنا لولا أن صبرنا عليها}','4004','1204','0');
INSERT INTO [t20766] VALUES (1238,'قوله تعالى: {أرأيت من اتخذ إلهه هواه أفأنت تكون عليه وكيلا (٤٣)}','4005','1204','0');
INSERT INTO [t20766] VALUES (1239,'قوله تعالى: {أم تحسب أن أكثرهم يسمعون أو يعقلون إن هم إلا كالأنعام بل هم أضل سبيلا (٤٤)}','4007','1204','0');
INSERT INTO [t20766] VALUES (1240,'قوله تعالى: {وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا (٤٧)}','4008','1204','0');
INSERT INTO [t20766] VALUES (1241,'قوله تعالى: {وهو الذي أرسل الرياح بشرا بين يدي رحمته}','4011','1204','0');
INSERT INTO [t20766] VALUES (1242,'قوله تعالى: {ولقد صرفناه بينهم ليذكروا فأبى أكثر الناس إلا كفورا (٥٠)}','4011','1204','0');
INSERT INTO [t20766] VALUES (1243,'قوله تعالى: {ولو شئنا لبعثنا في كل قرية نذيرا (٥١) فلا تطع الكافرين وجاهدهم به جهادا كبيرا (٥٢)}','4013','1204','0');
INSERT INTO [t20766] VALUES (1244,'قوله تعالى: {وهو الذي مرج البحرين هذا عذب فرات وهذا ملح أجاج وجعل بينهما برزخا وحجرا محجورا (٥٣)}','4014','1204','0');
INSERT INTO [t20766] VALUES (1245,'قوله تعالى: {وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا (٥٤)}','4017','1204','0');
INSERT INTO [t20766] VALUES (1246,'قوله تعالى: {ويعبدون من دون الله ما لا ينفعهم ولا يضرهم}','4019','1204','0');
INSERT INTO [t20766] VALUES (1247,'قوله تعالى: {وكان الكافر على ربه ظهيرا (٥٥)}','4019','1204','0');
INSERT INTO [t20766] VALUES (1248,'قوله تعالى: {وما أرسلناك إلا مبشرا ونذيرا (٥٦)}','4020','1204','0');
INSERT INTO [t20766] VALUES (1249,'قوله تعالى: {قل ما أسألكم عليه من أجر إلا من شاء أن يتخذ إلى ربه سبيلا (٥٧)}','4021','1204','0');
INSERT INTO [t20766] VALUES (1250,'قوله تعالى: {وتوكل على الحي الذي لا يموت}','4021','1204','0');
INSERT INTO [t20766] VALUES (1251,'قوله تعالى: {وكفى به بذنوب عباده خبيرا (٥٨)}','4021','1204','0');
INSERT INTO [t20766] VALUES (1252,'قوله تعالى: {الذي خلق السماوات والأرض وما بينهما في ستة أيام}','4021','1204','0');
INSERT INTO [t20766] VALUES (1253,'قوله تعالى: {ثم استوى على العرش الرحمن فاسأل به خبيرا (٥٩)}','4021','1204','0');
INSERT INTO [t20766] VALUES (1254,'قوله تعالى: {وإذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا وزادهم نفورا (٦٠)}','4022','1204','0');
INSERT INTO [t20766] VALUES (1255,'قوله تعالى: {تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا (٦١)}','4023','1204','0');
INSERT INTO [t20766] VALUES (1256,'قوله تعالى: {وعباد الرحمن الذين يمشون على الأرض هونا}','4026','1204','0');
INSERT INTO [t20766] VALUES (1257,'قوله تعالى: {وإذا خاطبهم الجاهلون قالوا سلاما}','4026','1204','0');
INSERT INTO [t20766] VALUES (1258,'قوله تعالى: {والذين يبيتون لربهم سجدا وقياما (٦٤)}','4027','1204','0');
INSERT INTO [t20766] VALUES (1259,'قوله تعالى: {والذين يقولون ربنا اصرف عنا عذاب جهنم إن عذابها كان غراما (٦٥)}','4027','1204','0');
INSERT INTO [t20766] VALUES (1260,'قوله تعالى: {والذين إذا أنفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما (٦٧)}','4028','1204','0');
INSERT INTO [t20766] VALUES (1261,'قوله تعالى: {وإذا مروا باللغو مروا كراما (٧٢)}','4034','1204','0');
INSERT INTO [t20766] VALUES (1262,'قوله تعالى: {والذين إذا ذكروا بآيات ربهم لم يخروا عليها صما وعميانا (٧٣)}','4034','1204','0');
INSERT INTO [t20766] VALUES (1263,'قوله تعالى: {أولئك يجزون الغرفة بما صبروا}','4036','1204','0');
INSERT INTO [t20766] VALUES (1264,'قوله تعالى: {ويلقون فيها تحية وسلاما (٧٥)}','4036','1204','0');
INSERT INTO [t20766] VALUES (1265,'قوله تعالى: {خالدين فيها حسنت مستقرا ومقاما (٧٦)}','4037','1204','0');
INSERT INTO [t20766] VALUES (1266,'قوله تعالى: {قل ما يعبأ بكم ربي لولا دعاؤكم فقد كذبتم فسوف يكون لزاما (٧٧)}','4037','1204','0');
INSERT INTO [t20766] VALUES (1267,'سورة الشعراء','4043','0','0');
INSERT INTO [t20766] VALUES (1268,'قوله تعالى: {لعلك باخع نفسك ألا يكونوا مؤمنين (٣)}','4044','1267','0');
INSERT INTO [t20766] VALUES (1269,'قوله تعالى {أولم يروا إلى الأرض كم أنبتنا فيها من كل زوج كريم (٧) إن في ذلك لآية وما كان أكثرهم مؤمنين (٨)}','4044','1267','0');
INSERT INTO [t20766] VALUES (1270,'قوله تعالى: {وإذ نادى ربك موسى أن ائت القوم الظالمين (١٠) قوم فرعون ألا يتقون (١١)}','4045','1267','0');
INSERT INTO [t20766] VALUES (1271,'قوله تعالى: {قال رب إني أخاف أن يكذبون (١٢) ويضيق صدري ولا ينطلق لساني}','4045','1267','0');
INSERT INTO [t20766] VALUES (1272,'قوله تعالى: {فأرسل إلى هارون (١٣)}','4046','1267','0');
INSERT INTO [t20766] VALUES (1273,'قوله تعالى: {ولهم علي ذنب فأخاف أن يقتلون (١٤)}','4046','1267','0');
INSERT INTO [t20766] VALUES (1274,'قوله تعالى: {قال كلا فاذهبا بآياتنا إنا معكم مستمعون (١٥)}','4046','1267','0');
INSERT INTO [t20766] VALUES (1275,'قوله تعالى: {فأتيا فرعون فقولا إنا رسول رب العالمين (١٦)}','4047','1267','0');
INSERT INTO [t20766] VALUES (1276,'قوله تعالى: {قال ألم نربك فينا وليدا}','4047','1267','0');
INSERT INTO [t20766] VALUES (1277,'قوله تعالى: {قال فعلتها إذا وأنا من الضالين (٢٠)}','4048','1267','0');
INSERT INTO [t20766] VALUES (1278,'قوله تعالى: {ففررت منكم لما خفتكم}','4051','1267','0');
INSERT INTO [t20766] VALUES (1279,'قوله تعالى: {فوهب لي ربي حكما وجعلني من المرسلين (٢١)}','4051','1267','0');
INSERT INTO [t20766] VALUES (1280,'قوله تعالى: {قال فرعون وما رب العالمين (٢٣)}','4052','1267','0');
INSERT INTO [t20766] VALUES (1281,'قوله تعالى: {قال أولو جئتك بشيء مبين (٣٠) قال فأت به إن كنت من الصادقين (٣١) فألقى عصاه. . .}','4052','1267','0');
INSERT INTO [t20766] VALUES (1282,'قوله تعالى: {واتل عليهم نبأ إبراهيم (٦٩) إذ قال لأبيه وقومه ما تعبدون (٧٠) قالوا نعبد أصناما فنظل لها عاكفين (٧١)}','4052','1267','0');
INSERT INTO [t20766] VALUES (1283,'قوله تعالى: {فكبكبوا فيها هم والغاوون (٩٤) وجنود إبليس أجمعون (٩٥)}','4053','1267','0');
INSERT INTO [t20766] VALUES (1284,'قوله تعالى: {قالوا وهم فيها يختصمون (٩٦) تالله إن كنا لفي ضلال مبين (٩٧)}','4053','1267','0');
INSERT INTO [t20766] VALUES (1285,'قوله تعالى: {فما لنا من شافعين (١٠٠)}','4054','1267','0');
INSERT INTO [t20766] VALUES (1286,'قوله تعالى: {فلو أن لنا كرة فنكون من المؤمنين (١٠٢)}','4054','1267','0');
INSERT INTO [t20766] VALUES (1287,'قوله تعالى: {كذبت قوم نوح المرسلين (١٠٥)}','4054','1267','0');
INSERT INTO [t20766] VALUES (1288,'قوله تعالى: {قالوا أنؤمن لك واتبعك الأرذلون (١١١)}','4055','1267','0');
INSERT INTO [t20766] VALUES (1289,'قوله تعالى: {وما أنا بطارد المؤمنين (١١٤)}','4055','1267','0');
INSERT INTO [t20766] VALUES (1290,'قوله تعالى: {قال رب إن قومي كذبون (١١٧) فافتح بيني وبينهم فتحا ونجني ومن معي من المؤمنين (١١٨) فأنجيناه ومن معه في الفلك المشحون (١١٩) ثم أغرقنا بعد الباقين (١٢٠)}','4055','1267','0');
INSERT INTO [t20766] VALUES (1291,'قوله تعالى: {كذب أصحاب الأيكة المرسلين (١٧٦)}','4057','1267','0');
INSERT INTO [t20766] VALUES (1292,'قوله تعالى: {واتقوا الذي خلقكم والجبلة الأولين (١٨٤)}','4058','1267','0');
INSERT INTO [t20766] VALUES (1293,'قوله تعالى: {وإنه لتنزيل رب العالمين (١٩٢) نزل به الروح الأمين (١٩٣) على قلبك لتكون من المنذرين (١٩٤) بلسان عربي مبين (١٩٥)}','4058','1267','0');
INSERT INTO [t20766] VALUES (1294,'قوله تعالى: {ولو نزلناه على بعض الأعجمين (١٩٨) فقرأه عليهم ما كانوا به مؤمنين (١٩٩)}','4060','1267','0');
INSERT INTO [t20766] VALUES (1295,'قوله تعالى: {كذلك سلكناه في قلوب المجرمين (٢٠٠) لا يؤمنون به حتى يروا العذاب الأليم (٢٠١)}','4060','1267','0');
INSERT INTO [t20766] VALUES (1296,'قوله تعالى: {فيقولوا هل نحن منظرون (٢٠٣)}','4061','1267','0');
INSERT INTO [t20766] VALUES (1297,'قوله تعالى: {أفبعذابنا يستعجلون (٢٠٤)}','4062','1267','0');
INSERT INTO [t20766] VALUES (1298,'قوله تعالى: {أفرأيت إن متعناهم سنين (٢٠٥) ثم جاءهم ما كانوا يوعدون (٢٠٦) ما أغنى عنهم ما كانوا يمتعون (٢٠٧)}','4062','1267','0');
INSERT INTO [t20766] VALUES (1299,'قوله تعالى: {وما أهلكنا من قرية إلا لها منذرون (٢٠٨)}','4062','1267','0');
INSERT INTO [t20766] VALUES (1300,'قوله تعالى: {ذكرى وما كنا ظالمين (٢٠٩)}','4062','1267','0');
INSERT INTO [t20766] VALUES (1301,'قوله تعالى: {إنهم عن السمع لمعزولون (٢١٢)}','4063','1267','0');
INSERT INTO [t20766] VALUES (1302,'قوله تعالى: {فلا تدع مع الله إلها آخر فتكون من المعذبين (٢١٣)}','4063','1267','0');
INSERT INTO [t20766] VALUES (1303,'قوله تعالى: {وأنذر عشيرتك الأقربين (٢١٤)}','4064','1267','0');
INSERT INTO [t20766] VALUES (1304,'قوله تعالى: {واخفض جناحك لمن اتبعك من المؤمنين (٢١٥)}','4064','1267','0');
INSERT INTO [t20766] VALUES (1305,'قوله تعالى: {وتوكل على العزيز الرحيم (٢١٧) الذي يراك حين تقوم (٢١٨) وتقلبك في الساجدين (٢١٩)}','4067','1267','0');
INSERT INTO [t20766] VALUES (1306,'قوله تعالى: {والشعراء يتبعهم الغاوون (٢٢٤)}','4069','1267','0');
INSERT INTO [t20766] VALUES (1307,'قوله تعالى: {وأنهم يقولون ما لا يفعلون (٢٢٦)}','4072','1267','0');
INSERT INTO [t20766] VALUES (1308,'قوله تعالى: {إلا الذين آمنوا وعملوا الصالحات}','4072','1267','0');
INSERT INTO [t20766] VALUES (1309,'قوله تعالى: {وذكروا الله كثيرا}','4073','1267','0');
INSERT INTO [t20766] VALUES (1310,'قوله تعالى: {وانتصروا من بعد ما ظلموا}','4073','1267','0');
INSERT INTO [t20766] VALUES (1311,'قوله تعالى: {وسيعلم الذين ظلموا أي منقلب ينقلبون (٢٢٧)}','4073','1267','0');
INSERT INTO [t20766] VALUES (1312,'سورة النمل','4075','0','0');
INSERT INTO [t20766] VALUES (1313,'قوله تعالى: {هدى وبشرى للمؤمنين (٢)}','4076','1312','0');
INSERT INTO [t20766] VALUES (1314,'قوله تعالى: {إذ قال موسى لأهله إني آنست نارا}','4076','1312','0');
INSERT INTO [t20766] VALUES (1315,'قوله تعالى: {وورث سليمان داوود}','4076','1312','0');
INSERT INTO [t20766] VALUES (1316,'قوله تعالى: {ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون (٢٥)}','4076','1312','0');
INSERT INTO [t20766] VALUES (1317,'قوله تعالى: {ومن شكر فإنما يشكر لنفسه}','4085','1312','0');
INSERT INTO [t20766] VALUES (1318,'قوله تعالى: {ومن كفر فإن ربي غني كريم (٤٠)}','4086','1312','0');
INSERT INTO [t20766] VALUES (1319,'قوله تعالى: {ولقد أرسلنا إلى ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون (٤٥)}','4086','1312','0');
INSERT INTO [t20766] VALUES (1320,'قوله تعالى: {قال ياقوم لم تستعجلون بالسيئة قبل الحسنة}','4086','1312','0');
INSERT INTO [t20766] VALUES (1321,'قوله تعالى: {قالوا اطيرنا بك وبمن معك قال طائركم عند الله بل أنتم قوم تفتنون (٤٧)}','4087','1312','0');
INSERT INTO [t20766] VALUES (1322,'قوله تعالى: {قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون (٤٩)}','4089','1312','0');
INSERT INTO [t20766] VALUES (1323,'قوله تعالى: {فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين (٥١) فتلك بيوتهم خاوية بما ظلموا إن في ذلك لآية لقوم يعلمون (٥٢) وأنجينا الذين آمنوا وكانوا يتقون (٥٣)}','4091','1312','0');
INSERT INTO [t20766] VALUES (1324,'قوله تعالى: {ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون (٥٤)}','4093','1312','0');
INSERT INTO [t20766] VALUES (1325,'قوله تعالى: {أمن خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة}','4093','1312','0');
INSERT INTO [t20766] VALUES (1326,'وقوله تعالى: {أمن جعل الأرض قرارا وجعل خلالها أنهارا}','4094','1312','0');
INSERT INTO [t20766] VALUES (1327,'قوله تعالى: {قل لا يعلم من في السماوات والأرض الغيب إلا الله وما يشعرون أيان يبعثون (٦٥)}','4094','1312','0');
INSERT INTO [t20766] VALUES (1328,'قوله تعالى: {بل ادارك علمهم في الآخرة بل هم في شك منها بل هم منها عمون (٦٦)}','4094','1312','0');
INSERT INTO [t20766] VALUES (1329,'قوله تعالى: {إن هذا القرآن يقص على بني إسرائيل أكثر الذي هم فيه يختلفون (٧٦)}','4096','1312','0');
INSERT INTO [t20766] VALUES (1330,'قوله تعالى: {وإنه لهدى ورحمة للمؤمنين (٧٧)}','4097','1312','0');
INSERT INTO [t20766] VALUES (1331,'قوله تعالى: {إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين (٨٠)}','4097','1312','0');
INSERT INTO [t20766] VALUES (1332,'قوله تعالى: {ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون (٨٣)}','4123','1312','0');
INSERT INTO [t20766] VALUES (1333,'قوله تعالى: {حتى إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أماذا كنتم تعملون (٨٤)}','4124','1312','0');
INSERT INTO [t20766] VALUES (1334,'قوله تعالى: {ووقع القول عليهم بما ظلموا فهم لا ينطقون (٨٥)}','4124','1312','0');
INSERT INTO [t20766] VALUES (1335,'قوله تعالى: {ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا إن في ذلك لآيات لقوم يؤمنون (٨٦)}','4126','1312','0');
INSERT INTO [t20766] VALUES (1336,'قوله تعالى: {وترى الجبال تحسبها جامدة وهي تمر مر السحاب صنع الله الذي أتقن كل شيء إنه خبير بما تفعلون (٨٨)}','4126','1312','0');
INSERT INTO [t20766] VALUES (1337,'قوله تعالى: {من جاء بالحسنة فله خير منها}','4128','1312','0');
INSERT INTO [t20766] VALUES (1338,'قوله تعالى: {وهم من فزع يومئذ آمنون (٨٩)}','4129','1312','0');
INSERT INTO [t20766] VALUES (1339,'قوله تعالى: {ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون (٩٠)}','4130','1312','0');
INSERT INTO [t20766] VALUES (1340,'قوله تعالى: {إنما أمرت أن أعبد رب هذه البلدة}','4131','1312','0');
INSERT INTO [t20766] VALUES (1341,'قوله تعالى: {وأمرت أن أكون من المسلمين (٩١) وأن أتلو القرآن}','4131','1312','0');
INSERT INTO [t20766] VALUES (1342,'قوله تعالى: {ومن ضل فقل إنما أنا من المنذرين (٩٢)}','4132','1312','0');
INSERT INTO [t20766] VALUES (1343,'قوله تعالى: {وقل الحمد لله سيريكم آياته فتعرفونها}','4132','1312','0');
INSERT INTO [t20766] VALUES (1344,'قوله تعالى: {وما ربك بغافل عما تعملون (٩٣)}','4132','1312','0');
INSERT INTO [t20766] VALUES (1345,'سورة القصص','4133','0','0');
INSERT INTO [t20766] VALUES (1346,'قوله تعالى: {ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة ونجعلهم الوارثين (٥)}','4134','1345','0');
INSERT INTO [t20766] VALUES (1347,'قوله تعالى: {فالتقطه آل فرعون ليكون لهم عدوا وحزنا}','4135','1345','0');
INSERT INTO [t20766] VALUES (1348,'قوله تعالى: {فقال لأهله امكثوا إني آنست نارا}','4138','1345','0');
INSERT INTO [t20766] VALUES (1349,'قوله تعالى: {وأتبعناهم في هذه الدنيا لعنة ويوم القيامة هم من المقبوحين (٤٢)}','4139','1345','0');
INSERT INTO [t20766] VALUES (1350,'قوله تعالى: {إنك لا تهدي من أحببت ولكن الله يهدي من يشاء وهو أعلم بالمهتدين (٥٦)}','4139','1345','0');
INSERT INTO [t20766] VALUES (1351,'قوله تعالى: {وما كنت ترجو أن يلقى إليك الكتاب إلا رحمة من ربك}','4140','1345','0');
INSERT INTO [t20766] VALUES (1352,'قوله تعالى: {كل شيء هالك إلا وجهه}','4140','1345','0');
INSERT INTO [t20766] VALUES (1353,'قوله تعالى: {وله الحكم وإليه ترجعون (٧٠)}','4140','1345','0');
INSERT INTO [t20766] VALUES (1354,'سورة العنكبوت','4141','0','0');
INSERT INTO [t20766] VALUES (1355,'قوله تعالى: {الم (١) أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون (٢)}','4142','1354','0');
INSERT INTO [t20766] VALUES (1356,'قوله تعالى: {أم حسب الذين يعملون السيئات أن يسبقونا ساء ما يحكمون (٤)}','4143','1354','0');
INSERT INTO [t20766] VALUES (1357,'قوله تعالى: {ووصينا الإنسان بوالديه حسنا}','4143','1354','0');
INSERT INTO [t20766] VALUES (1358,'قوله تعالى: {ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس كعذاب الله}','4143','1354','0');
INSERT INTO [t20766] VALUES (1359,'قوله تعالى: {ولئن جاء نصر من ربك ليقولن إنا كنا معكم}','4144','1354','0');
INSERT INTO [t20766] VALUES (1360,'قوله تعالى: {وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم}','4145','1354','0');
INSERT INTO [t20766] VALUES (1361,'قوله تعالى: {فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين (١٥)}','4145','1354','0');
INSERT INTO [t20766] VALUES (1362,'قوله تعالى: {إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه}','4145','1354','0');
INSERT INTO [t20766] VALUES (1363,'قوله تعالى: {وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا}','4146','1354','0');
INSERT INTO [t20766] VALUES (1364,'قوله تعالى: {وجعلنا في ذريته النبوة والكتاب}','4146','1354','0');
INSERT INTO [t20766] VALUES (1365,'قوله تعالى: {وآتيناه أجره في الدنيا وإنه في الآخرة لمن الصالحين (٢٧)}','4146','1354','0');
INSERT INTO [t20766] VALUES (1366,'قوله تعالى: {ولما جاءت رسلنا إبراهيم بالبشرى قالوا إنا مهلكو أهل هذه القرية}','4147','1354','0');
INSERT INTO [t20766] VALUES (1367,'قوله تعالى: {ولما جاءت رسلنا لوطا}','4147','1354','0');
INSERT INTO [t20766] VALUES (1368,'قوله تعالى: {وإلى مدين أخاهم شعيبا}','4147','1354','0');
INSERT INTO [t20766] VALUES (1369,'قوله تعالى: {وعادا وثمود وقد تبين لكم من مساكنهم وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين (٣٨) وقارون وفرعون وهامان ولقد جاءهم موسى بالبينات فاستكبروا في الأرض وما كانوا سابقين (٣٩) فكلا أخذنا بذنبه فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا}','4148','1354','0');
INSERT INTO [t20766] VALUES (1370,'قوله تعالى: {مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا وإن أوهن البيوت لبيت العنكبوت لو كانوا يعلمون (٤١) إن الله يعلم ما يدعون من دونه من شيء وهو العزيز الحكيم (٤٢) وتلك الأمثال نضربها للناس وما يعقلها إلا العالمون (٤٣)}','4149','1354','0');
INSERT INTO [t20766] VALUES (1371,'قوله تعالى: {اتل ما أوحي إليك من الكتاب}','4149','1354','0');
INSERT INTO [t20766] VALUES (1372,'قوله تعالى: {وأقم الصلاة إن الصلاة تنهى عن الفحشاء والمنكر}','4150','1354','0');
INSERT INTO [t20766] VALUES (1373,'قوله تعالى: {ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم}','4150','1354','0');
INSERT INTO [t20766] VALUES (1374,'قوله تعالى: {أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم إن في ذلك لرحمة وذكرى لقوم يؤمنون (٥١)}','4150','1354','0');
INSERT INTO [t20766] VALUES (1375,'قوله تعالى: {ويستعجلونك بالعذاب ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون (٥٣) يستعجلونك بالعذاب وإن جهنم لمحيطة بالكافرين (٥٤)}','4150','1354','0');
INSERT INTO [t20766] VALUES (1376,'قوله تعالى: {ياعبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون (٥٦)}','4151','1354','0');
INSERT INTO [t20766] VALUES (1377,'قوله تعالى: {كل نفس ذائقة الموت}','4151','1354','0');
INSERT INTO [t20766] VALUES (1378,'قوله تعالى: {والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا}','4152','1354','0');
INSERT INTO [t20766] VALUES (1379,'قوله تعالى: {وكأين من دابة لا تحمل رزقها الله يرزقها}','4152','1354','0');
INSERT INTO [t20766] VALUES (1380,'قوله تعالى: {ولئن سألتهم من خلق السماوات والأرض وسخر الشمس والقمر}','4152','1354','0');
INSERT INTO [t20766] VALUES (1381,'قوله تعالى: {فإذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم إلى البر إذا هم يشركون (٦٥)}','4153','1354','0');
INSERT INTO [t20766] VALUES (1382,'قوله تعالى: {أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم}','4153','1354','0');
INSERT INTO [t20766] VALUES (1383,'قوله تعالى: {والذين جاهدوا فينا لنهدينهم سبلنا}','4153','1354','0');
INSERT INTO [t20766] VALUES (1384,'قوله تعالى: {وإن الله لمع المحسنين (٦٩)}','4154','1354','0');
INSERT INTO [t20766] VALUES (1385,'سورة الروم','4155','0','0');
INSERT INTO [t20766] VALUES (1386,'قوله تعالى: {وعد الله لا يخلف الله وعده ولكن أكثر الناس لا يعلمون (٦) يعلمون ظاهرا من الحياة الدنيا وهم عن الآخرة هم غافلون (٧)}','4156','1385','0');
INSERT INTO [t20766] VALUES (1387,'قوله تعالى: {أولم يتفكروا في أنفسهم ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى وإن كثيرا من الناس بلقاء ربهم لكافرون (٨)}','4161','1385','0');
INSERT INTO [t20766] VALUES (1388,'قوله تعالى: {أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم}','4164','1385','0');
INSERT INTO [t20766] VALUES (1389,'قوله تعالى: {ثم كان عاقبة الذين أساءوا السوأى أن كذبوا بآيات الله وكانوا بها يستهزئون (١٠)}','4165','1385','0');
INSERT INTO [t20766] VALUES (1390,'قوله تعالى: {الله يبدأ الخلق ثم يعيده}','4166','1385','0');
INSERT INTO [t20766] VALUES (1391,'قوله تعالى: {ولم يكن لهم من شركائهم شفعاء}','4166','1385','0');
INSERT INTO [t20766] VALUES (1392,'قوله تعالى: {وكانوا بشركائهم كافرين (١٣)}','4166','1385','0');
INSERT INTO [t20766] VALUES (1393,'قوله تعالى: {فسبحان الله حين تمسون وحين تصبحون (١٧) وله الحمد في السماوات والأرض وعشيا وحين تظهرون (١٨)}','4167','1385','0');
INSERT INTO [t20766] VALUES (1394,'قوله تعالى: {ويحي الأرض بعد موتها وكذلك تخرجون (١٩)}','4167','1385','0');
INSERT INTO [t20766] VALUES (1395,'قوله تعالى: {ومن آياته أن خلقكم من تراب}','4167','1385','0');
INSERT INTO [t20766] VALUES (1396,'قوله تعالى: {ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها}','4167','1385','0');
INSERT INTO [t20766] VALUES (1397,'قوله تعالى: {ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم إن في ذلك لآيات للعالمين (٢٢)}','4168','1385','0');
INSERT INTO [t20766] VALUES (1398,'قوله تعالى: {ومن آياته منامكم بالليل والنهار وابتغاؤكم من فضله إن في ذلك لآيات لقوم يسمعون (٢٣)}','4169','1385','0');
INSERT INTO [t20766] VALUES (1399,'قوله تعالى: {ومن آياته يريكم البرق خوفا وطمعا}','4169','1385','0');
INSERT INTO [t20766] VALUES (1400,'قوله تعالى: {ضرب لكم مثلا من أنفسكم هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم}','4169','1385','0');
INSERT INTO [t20766] VALUES (1401,'قوله تعالى: {وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله}','4169','1385','0');
INSERT INTO [t20766] VALUES (1402,'قوله تعالى: {يومئذ يصدعون (٤٣)}','4170','1385','0');
INSERT INTO [t20766] VALUES (1403,'قوله تعالى: {فإنك لا تسمع الموتى}','4170','1385','0');
INSERT INTO [t20766] VALUES (1404,'قوله تعالى: {الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة}','4170','1385','0');
INSERT INTO [t20766] VALUES (1405,'قوله تعالى: {ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة كذلك كانوا يؤفكون (٥٥)}','4171','1385','0');
INSERT INTO [t20766] VALUES (1406,'قوله تعالى: {وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون (٥٦)}','4172','1385','0');
INSERT INTO [t20766] VALUES (1407,'قوله تعالى: {ولا هم يستعتبون (٥٧)}','4173','1385','0');
INSERT INTO [t20766] VALUES (1408,'قوله تعالى: {ولئن جئتهم بآية ليقولن الذين كفروا إن أنتم إلا مبطلون (٥٨)}','4173','1385','0');
INSERT INTO [t20766] VALUES (1409,'قوله تعالى: {ولا يستخفنك الذين لا يوقنون (٦٠)}','4173','1385','0');
INSERT INTO [t20766] VALUES (1410,'سورة لقمان','4176','0','0');
INSERT INTO [t20766] VALUES (1411,'قوله تعالى: {الم (١) تلك آيات الكتاب الحكيم (٢) هدى ورحمة للمحسنين (٣)}','4177','1410','0');
INSERT INTO [t20766] VALUES (1412,'قوله تعالى: {وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا فبشره بعذاب أليم (٧)}','4177','1410','0');
INSERT INTO [t20766] VALUES (1413,'قوله تعالى: {خلق السماوات بغير عمد ترونها}','4178','1410','0');
INSERT INTO [t20766] VALUES (1414,'قوله تعالى: {هذا خلق الله فأروني ماذا خلق الذين من دونه}','4178','1410','0');
INSERT INTO [t20766] VALUES (1415,'قوله تعالى: {وإذ قال لقمان لابنه وهو يعظه يابني لا تشرك بالله إن الشرك لظلم عظيم (١٣)}','4178','1410','0');
INSERT INTO [t20766] VALUES (1416,'قوله تعالى: {ولا تصعر خدك للناس}','4179','1410','0');
INSERT INTO [t20766] VALUES (1417,'قوله تعالى: {ولا تمش في الأرض مرحا}','4180','1410','0');
INSERT INTO [t20766] VALUES (1418,'قوله تعالى: {واقصد في مشيك}','4180','1410','0');
INSERT INTO [t20766] VALUES (1419,'قوله تعالى: {ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير (٨)}','4180','1410','0');
INSERT INTO [t20766] VALUES (1420,'قوله تعالى: {أولو كان الشيطان يدعوهم إلى عذاب السعير (٢١)}','4180','1410','0');
INSERT INTO [t20766] VALUES (1421,'قوله تعالى: {ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل الحمد لله بل أكثرهم لا يعلمون (٢٥)}','4180','1410','0');
INSERT INTO [t20766] VALUES (1422,'قوله تعالى: {ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله}','4181','1410','0');
INSERT INTO [t20766] VALUES (1423,'قوله تعالى: {ما خلقكم ولا بعثكم إلا كنفس واحدة}','4181','1410','0');
INSERT INTO [t20766] VALUES (1424,'قوله تعالى: {وإذا غشيهم موج كالظلل دعوا الله مخلصين له الدين}','4181','1410','0');
INSERT INTO [t20766] VALUES (1425,'قوله تعالى: {إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام وما تدري نفس ماذا تكسب غدا وما تدري نفس بأي أرض تموت إن الله عليم خبير (٣٤)}','4181','1410','0');
INSERT INTO [t20766] VALUES (1426,'سورة السجدة','4183','0','0');
INSERT INTO [t20766] VALUES (1427,'قوله تعالى: {أم يقولون افتراه بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك}','4184','1426','0');
INSERT INTO [t20766] VALUES (1428,'قوله تعالى: {يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون (٥)}','4184','1426','0');
INSERT INTO [t20766] VALUES (1429,'قوله تعالى: {قل يتوفاكم ملك الموت الذي وكل بكم}','4185','1426','0');
INSERT INTO [t20766] VALUES (1430,'قوله تعالى: {ولو ترى إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون (١٢)}','4187','1426','0');
INSERT INTO [t20766] VALUES (1431,'قوله تعالى: {ولو شئنا لآتينا كل نفس هداها ولكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين (١٣)}','4187','1426','0');
INSERT INTO [t20766] VALUES (1432,'قوله تعالى: {ومن أظلم ممن ذكر بآيات ربه ثم أعرض عنها إنا من المجرمين منتقمون (٢٢)}','4187','1426','0');
INSERT INTO [t20766] VALUES (1433,'قوله تعالى: {أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم}','4188','1426','0');
INSERT INTO [t20766] VALUES (1434,'قوله تعالى: {أولم يروا أنا نسوق الماء إلى الأرض الجرز فنخرج به زرعا تأكل منه أنعامهم وأنفسهم أفلا يبصرون (٢٧)}','4188','1426','0');
INSERT INTO [t20766] VALUES (1435,'قوله تعالى: {ويقولون متى هذا الفتح إن كنتم صادقين (٢٨) قل يوم الفتح لا ينفع الذين كفروا إيمانهم ولا هم ينظرون (٢٩)}','4188','1426','0');
INSERT INTO [t20766] VALUES (1436,'قوله تعالى: {وانتظر إنهم منتظرون (٣٠)}','4190','1426','0');
INSERT INTO [t20766] VALUES (1437,'سورة الأحزاب','4191','0','0');
INSERT INTO [t20766] VALUES (1438,'قوله تعالى: {ياأيها النبي اتق الله ولا تطع الكافرين والمنافقين}','4192','1437','0');
INSERT INTO [t20766] VALUES (1439,'قوله تعالى: {وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم}','4192','1437','0');
INSERT INTO [t20766] VALUES (1440,'قوله تعالى: {وأزواجه أمهاتهم}','4255','1437','0');
INSERT INTO [t20766] VALUES (1441,'قوله تعالى: {وأولو الأرحام بعضهم أولى ببعض}','4257','1437','0');
INSERT INTO [t20766] VALUES (1442,'قوله تعالى: {وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم وأخذنا منهم ميثاقا غليظا (٧)}','4257','1437','0');
INSERT INTO [t20766] VALUES (1443,'قوله تعالى: {ياأيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها}','4258','1437','0');
INSERT INTO [t20766] VALUES (1444,'قوله تعالى: {ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم إلا إيمانا وتسليما (٢٢)}','4259','1437','0');
INSERT INTO [t20766] VALUES (1445,'قوله تعالى: {ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال}','4260','1437','0');
INSERT INTO [t20766] VALUES (1446,'قوله تعالى: {يانساء النبي من يأت منكن بفاحشة مبينة يضاعف لها العذاب ضعفين}','4260','1437','0');
INSERT INTO [t20766] VALUES (1447,'قوله تعالى: {ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين}','4260','1437','0');
INSERT INTO [t20766] VALUES (1448,'قوله تعالى: {إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا (٣٣)}','4262','1437','0');
INSERT INTO [t20766] VALUES (1449,'قوله تعالى: {وتخفي في نفسك ما الله مبديه}','4266','1437','0');
INSERT INTO [t20766] VALUES (1450,'قوله تعالى: {ياأيها الذين آمنوا اذكروا الله ذكرا كثيرا (٤١)}','4269','1437','0');
INSERT INTO [t20766] VALUES (1451,'قوله تعالى: {وبشر المؤمنين بأن لهم من الله فضلا كبيرا (٤٧)}','4269','1437','0');
INSERT INTO [t20766] VALUES (1452,'قوله تعالى: {وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ذلكم أطهر لقلوبكم وقلوبهن}','4269','1437','0');
INSERT INTO [t20766] VALUES (1453,'قوله تعالى: {يسألك الناس عن الساعة قل إنما علمها عند الله}','4291','1437','0');
INSERT INTO [t20766] VALUES (1454,'قوله تعالى: {وما يدريك لعل الساعة تكون قريبا (٦٣)}','4292','1437','0');
INSERT INTO [t20766] VALUES (1455,'قوله تعالى: {إن الله لعن الكافرين وأعد لهم سعيرا (٦٤)}','4292','1437','0');
INSERT INTO [t20766] VALUES (1456,'قوله تعالى: {إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان إنه كان ظلوما جهولا (٧٢)}','4293','1437','0');
INSERT INTO [t20766] VALUES (1457,'سورة سبأ','4296','0','0');
INSERT INTO [t20766] VALUES (1458,'قوله تعالى: {وله الحمد في الآخرة}','4297','1457','0');
INSERT INTO [t20766] VALUES (1459,'قوله تعالى: {يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور (٢)}','4297','1457','0');
INSERT INTO [t20766] VALUES (1460,'قوله تعالى: {وقال الذين كفروا لا تأتينا الساعة قل بلى وربي لتأتينكم}','4298','1457','0');
INSERT INTO [t20766] VALUES (1461,'قوله تعالى: {وقال الذين كفروا لا تأتينا الساعة قل بلى وربي لتأتينكم عالم الغيب لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين (٣)}','4300','1457','0');
INSERT INTO [t20766] VALUES (1462,'قوله تعالى: {والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز أليم (٥)}','4301','1457','0');
INSERT INTO [t20766] VALUES (1463,'قوله تعالى: {وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد (٧) أفترى على الله كذبا أم به جنة بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد (٨)}','4302','1457','0');
INSERT INTO [t20766] VALUES (1464,'قوله تعالى: {أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض}','4302','1457','0');
INSERT INTO [t20766] VALUES (1465,'قوله تعالى: {إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء}','4303','1457','0');
INSERT INTO [t20766] VALUES (1466,'قوله تعالى: {ولقد آتينا داوود منا فضلا}','4304','1457','0');
INSERT INTO [t20766] VALUES (1467,'قوله تعالى: {ياجبال أوبي معه والطير}','4305','1457','0');
INSERT INTO [t20766] VALUES (1468,'قوله تعالى: {وألنا له الحديد (١٠) أن اعمل سابغات وقدر في السرد}','4305','1457','0');
INSERT INTO [t20766] VALUES (1469,'قوله تعالى: {ولسليمان الريح غدوها شهر ورواحها شهر}','4305','1457','0');
INSERT INTO [t20766] VALUES (1470,'قوله تعالى: {ومن الجن من يعمل بين يديه بإذن ربه}','4305','1457','0');
INSERT INTO [t20766] VALUES (1471,'قوله تعالى: {ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين (٢٠)}','4306','1457','0');
INSERT INTO [t20766] VALUES (1472,'قوله تعالى: {وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة}','4306','1457','0');
INSERT INTO [t20766] VALUES (1473,'قوله تعالى: {قل ادعوا الذين زعمتم من دون الله لا يملكون مثقال ذرة في السماوات ولا في الأرض}','4306','1457','0');
INSERT INTO [t20766] VALUES (1474,'قوله تعالى: {ولا تنفع الشفاعة عنده إلا لمن أذن له}','4307','1457','0');
INSERT INTO [t20766] VALUES (1475,'قوله تعالى: {قل من يرزقكم من السماوات والأرض قل الله}','4307','1457','0');
INSERT INTO [t20766] VALUES (1476,'قوله تعالى: {قل لا تسألون عما أجرمنا ولا نسأل عما تعملون (٢٥)}','4307','1457','0');
INSERT INTO [t20766] VALUES (1477,'قوله تعالى: {قل أروني الذين ألحقتم به شركاء كلا بل هو الله العزيز الحكيم (٢٧)}','4308','1457','0');
INSERT INTO [t20766] VALUES (1478,'قوله تعالى: {وما أرسلناك إلا كافة للناس بشيرا ونذيرا}','4309','1457','0');
INSERT INTO [t20766] VALUES (1479,'قوله تعالى: {ولكن أكثر الناس لا يعلمون (٢٨)}','4311','1457','0');
INSERT INTO [t20766] VALUES (1480,'قوله تعالى: {قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون (٣٠)}','4312','1457','0');
INSERT INTO [t20766] VALUES (1481,'قوله تعالى: {ولو ترى إذ الظالمون موقوفون عند ربهم}','4312','1457','0');
INSERT INTO [t20766] VALUES (1482,'قوله تعالى: {وجعلنا الأغلال في أعناق الذين كفروا}','4312','1457','0');
INSERT INTO [t20766] VALUES (1483,'قوله تعالى: {وما أرسلنا في قرية من نذير إلا قال مترفوها إنا بما أرسلتم به كافرون (٣٤)}','4312','1457','0');
INSERT INTO [t20766] VALUES (1484,'قوله تعالى: {وقالوا نحن أكثر أموالا وأولادا وما نحن بمعذبين (٣٥)}','4313','1457','0');
INSERT INTO [t20766] VALUES (1485,'قوله تعالى: {ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون (٤٠) قالوا سبحانك أنت ولينا من دونهم بل كانوا يعبدون الجن}','4313','1457','0');
INSERT INTO [t20766] VALUES (1486,'قوله تعالى: {وإذا تتلى عليهم آياتنا بينات قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم}','4313','1457','0');
INSERT INTO [t20766] VALUES (1487,'قوله تعالى: {وما آتيناهم من كتب يدرسونها وما أرسلنا إليهم قبلك من نذير (٤٤)}','4314','1457','0');
INSERT INTO [t20766] VALUES (1488,'قوله تعالى: {وكذب الذين من قبلهم وما بلغوا معشار ما آتيناهم فكذبوا رسلي فكيف كان نكير (٤٥)}','4314','1457','0');
INSERT INTO [t20766] VALUES (1489,'قوله تعالى: {ثم تتفكروا ما بصاحبكم من جنة إن هو إلا نذير لكم بين يدي عذاب شديد (٤٦)}','4314','1457','0');
INSERT INTO [t20766] VALUES (1490,'قوله تعالى: {قل ما سألتكم من أجر فهو لكم}','4315','1457','0');
INSERT INTO [t20766] VALUES (1491,'قوله تعالى: {قل جاء الحق وما يبدئ الباطل وما يعيد (٤٩)}','4315','1457','0');
INSERT INTO [t20766] VALUES (1492,'قوله تعالى: {قل إن ضللت فإنما أضل على نفسي وإن اهتديت فبما يوحي إلي ربي}','4315','1457','0');
INSERT INTO [t20766] VALUES (1493,'قوله تعالى: {وقالوا آمنا به وأنى لهم التناوش من مكان بعيد (٥٢)}','4315','1457','0');
INSERT INTO [t20766] VALUES (1494,'سورة فاطر','4317','0','0');
INSERT INTO [t20766] VALUES (1495,'قوله تعالى: {الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنى وثلاث ورباع}','4318','1494','0');
INSERT INTO [t20766] VALUES (1496,'قوله تعالى: {ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له من بعده}','4320','1494','0');
INSERT INTO [t20766] VALUES (1497,'قوله تعالى: {هل من خالق غير الله يرزقكم من السماء والأرض}','4321','1494','0');
INSERT INTO [t20766] VALUES (1498,'قوله تعالى: {وإن يكذبوك فقد كذبت رسل من قبلك وإلى الله ترجع الأمور (٤)}','4322','1494','0');
INSERT INTO [t20766] VALUES (1499,'قوله تعالى: {إن الشيطان لكم عدو فاتخذوه عدوا}','4322','1494','0');
INSERT INTO [t20766] VALUES (1500,'قوله تعالى: {إنما يدعو حزبه ليكونوا من أصحاب السعير (٦)}','4322','1494','0');
INSERT INTO [t20766] VALUES (1501,'قوله تعالى: {فلا تذهب نفسك عليهم حسرات}','4323','1494','0');
INSERT INTO [t20766] VALUES (1502,'قوله تعالى: {والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها كذلك النشور (٩)}','4323','1494','0');
INSERT INTO [t20766] VALUES (1503,'قوله تعالى: {من كان يريد العزة فلله العزة جميعا}','4323','1494','0');
INSERT INTO [t20766] VALUES (1504,'قوله تعالى: {والذين يمكرون السيئات لهم عذاب شديد}','4324','1494','0');
INSERT INTO [t20766] VALUES (1505,'قوله تعالى: {والله خلقكم من تراب ثم من نطفة}','4325','1494','0');
INSERT INTO [t20766] VALUES (1506,'قوله تعالى: {وما تحمل من أنثى ولا تضع إلا بعلمه}','4325','1494','0');
INSERT INTO [t20766] VALUES (1507,'قوله تعالى: {وما يعمر من معمر ولا ينقص من عمره إلا في كتاب}','4325','1494','0');
INSERT INTO [t20766] VALUES (1508,'قوله تعالى: {وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح أجاج}','4325','1494','0');
INSERT INTO [t20766] VALUES (1509,'قوله تعالى: {ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها}','4325','1494','0');
INSERT INTO [t20766] VALUES (1510,'قوله تعالى: {ويوم القيامة يكفرون بشرككم}','4326','1494','0');
INSERT INTO [t20766] VALUES (1511,'قوله تعالى: {ياأيها الناس أنتم الفقراء إلى الله والله هو الغني الحميد (١٥)}','4326','1494','0');
INSERT INTO [t20766] VALUES (1512,'قوله تعالى: {إن يشأ يذهبكم ويأت بخلق جديد (١٦) وما ذلك على الله بعزيز (١٧)}','4327','1494','0');
INSERT INTO [t20766] VALUES (1513,'قوله تعالى: {ولا تزر وازرة وزر أخرى}','4327','1494','0');
INSERT INTO [t20766] VALUES (1514,'قوله تعالى: {وإن تدع مثقلة إلى حملها لا يحمل منه شيء}','4327','1494','0');
INSERT INTO [t20766] VALUES (1515,'قوله تعالى: {إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة}','4328','1494','0');
INSERT INTO [t20766] VALUES (1516,'قوله تعالى: {وما يستوي الأعمى والبصير (١٩)}','4328','1494','0');
INSERT INTO [t20766] VALUES (1517,'قوله تعالى: {وما يستوي الأحياء ولا الأموات}','4328','1494','0');
INSERT INTO [t20766] VALUES (1518,'قوله تعالى: {إن الله يسمع من يشاء وما أنت بمسمع من في القبور (٢٢)}','4329','1494','0');
INSERT INTO [t20766] VALUES (1519,'قوله تعالى: {ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود (٢٧) ومن الناس والدواب والأنعام مختلف ألوانه كذلك}','4329','1494','0');
INSERT INTO [t20766] VALUES (1520,'قوله تعالى: {ثم أورثنا الكتاب الذين اصطفينا من عبادنا}','4330','1494','0');
INSERT INTO [t20766] VALUES (1521,'قوله تعالى: {وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل}','4330','1494','0');
INSERT INTO [t20766] VALUES (1522,'قوله تعالى: {قل أرأيتم شركاءكم الذين تدعون من دون الله أروني ماذا خلقوا من الأرض}','4330','1494','0');
INSERT INTO [t20766] VALUES (1523,'قوله تعالى: {إن الله يمسك السماوات والأرض أن تزولا}','4331','1494','0');
INSERT INTO [t20766] VALUES (1524,'قوله تعالى: {وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدى من إحدى الأمم}','4331','1494','0');
INSERT INTO [t20766] VALUES (1525,'قوله تعالى: {ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة}','4331','1494','0');
INSERT INTO [t20766] VALUES (1526,'سورة يس','4332','0','0');
INSERT INTO [t20766] VALUES (1527,'قوله تعالى: {يس (١)}','4333','1526','0');
INSERT INTO [t20766] VALUES (1528,'قوله تعالى: {والقرآن الحكيم (٢) إنك لمن المرسلين (٣)}','4333','1526','0');
INSERT INTO [t20766] VALUES (1529,'قوله تعالى: {لتنذر قوما ما أنذر آباؤهم فهم غافلون (٦)}','4334','1526','0');
INSERT INTO [t20766] VALUES (1530,'قوله تعالى: {لقد حق القول على أكثرهم فهم لا يؤمنون (٧)}','4334','1526','0');
INSERT INTO [t20766] VALUES (1531,'قوله تعالى: {إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون (٨) وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون (٩)}','4335','1526','0');
INSERT INTO [t20766] VALUES (1532,'قوله تعالى: {إنما تنذر من اتبع الذكر وخشي الرحمن بالغيب}','4338','1526','0');
INSERT INTO [t20766] VALUES (1533,'قوله تعالى: {إنا نحن نحي الموتى ونكتب ما قدموا وآثارهم وكل شيء أحصيناه في إمام مبين (١٢)}','4339','1526','0');
INSERT INTO [t20766] VALUES (1534,'قوله تعالى: {قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن أنتم إلا تكذبون (١٥)}','4341','1526','0');
INSERT INTO [t20766] VALUES (1535,'قوله تعالى: {قالوا إنا تطيرنا بكم لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم (١٨) قالوا طائركم معكم}','4342','1526','0');
INSERT INTO [t20766] VALUES (1536,'قوله تعالى: {اتبعوا من لا يسألكم أجرا}','4342','1526','0');
INSERT INTO [t20766] VALUES (1537,'قوله تعالى: {وما لي لا أعبد الذي فطرني وإليه ترجعون (٢٢)}','4343','1526','0');
INSERT INTO [t20766] VALUES (1538,'قوله تعالى: {أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون (٢٣) إني إذا لفي ضلال مبين (٢٤)}','4343','1526','0');
INSERT INTO [t20766] VALUES (1539,'قوله تعالى: {ياحسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون (٣٠)}','4344','1526','0');
INSERT INTO [t20766] VALUES (1540,'قوله تعالى: {وآية لهم الأرض الميتة}','4346','1526','0');
INSERT INTO [t20766] VALUES (1541,'قوله تعالى: {وآية لهم أنا حملنا ذريتهم في الفلك المشحون (٤١) وخلقنا لهم من مثله ما يركبون (٤٢)}','4346','1526','0');
INSERT INTO [t20766] VALUES (1542,'قوله تعالى: {وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين (٤٦)}','4347','1526','0');
INSERT INTO [t20766] VALUES (1543,'قوله تعالى: {ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون (٥١)}','4347','1526','0');
INSERT INTO [t20766] VALUES (1544,'قوله تعالى: {قالوا ياويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون (٥٢)}','4349','1526','0');
INSERT INTO [t20766] VALUES (1545,'قوله تعالى: {ألم أعهد إليكم يابني آدم أن لا تعبدوا الشيطان إنه لكم عدو مبين (٦٠) وأن اعبدوني هذا صراط مستقيم (٦١)}','4349','1526','0');
INSERT INTO [t20766] VALUES (1546,'قوله تعالى: {ولقد أضل منكم جبلا كثيرا أفلم تكونوا تعقلون (٦٢)}','4349','1526','0');
INSERT INTO [t20766] VALUES (1547,'قوله تعالى: {وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون (٦٥)}','4350','1526','0');
INSERT INTO [t20766] VALUES (1548,'قوله تعالى: {ومن نعمره ننكسه في الخلق أفلا يعقلون (٦٨)}','4351','1526','0');
INSERT INTO [t20766] VALUES (1549,'قوله تعالى: {وما علمناه الشعر وما ينبغي له}','4352','1526','0');
INSERT INTO [t20766] VALUES (1550,'قوله تعالى: {لينذر من كان حيا ويحق القول على الكافرين (٧٠)}','4352','1526','0');
INSERT INTO [t20766] VALUES (1551,'قوله تعالى: {أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين (٧٧)}','4352','1526','0');
INSERT INTO [t20766] VALUES (1552,'قوله تعالى: {وضرب لنا مثلا ونسي خلقه}','4352','1526','0');
INSERT INTO [t20766] VALUES (1553,'قوله تعالى: {إنما أمره إذا أراد شيئا أن يقول له كن فيكون (٨٢)}','4353','1526','0');
INSERT INTO [t20766] VALUES (1554,'سورة الصافات','4354','0','0');
INSERT INTO [t20766] VALUES (1555,'قوله تعالى: {والصافات صفا (١) فالزاجرات زجرا (٢) فالتاليات ذكرا (٣) إن إلهكم لواحد (٤) رب السماوات والأرض وما بينهما ورب المشارق (٥)}','4355','1554','0');
INSERT INTO [t20766] VALUES (1556,'قوله تعالى: {إنا زينا السماء الدنيا بزينة الكواكب (٦)}','4361','1554','0');
INSERT INTO [t20766] VALUES (1557,'قوله تعالى: {وحفظا من كل شيطان مارد (٧)}','4361','1554','0');
INSERT INTO [t20766] VALUES (1558,'قوله تعالى: {فاستفتهم أهم أشد خلقا أم من خلقنا إنا خلقناهم من طين لازب (١١)}','4362','1554','0');
INSERT INTO [t20766] VALUES (1559,'قوله تعالى: {بل عجبت ويسخرون (١٢)}','4364','1554','0');
INSERT INTO [t20766] VALUES (1560,'قوله تعالى: {وقالوا ياويلنا هذا يوم الدين (٢٠) هذا يوم الفصل الذي كنتم به تكذبون (٢١)}','4365','1554','0');
INSERT INTO [t20766] VALUES (1561,'قوله تعالى: {احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون (٢٢) من دون الله فاهدوهم إلى صراط الجحيم (٢٣)}','4365','1554','0');
INSERT INTO [t20766] VALUES (1562,'قوله تعالى: {وقفوهم إنهم مسئولون (٢٤) ما لكم لا تناصرون (٢٥)}','4367','1554','0');
INSERT INTO [t20766] VALUES (1563,'قوله تعالى: {وأقبل بعضهم على بعض يتساءلون (٢٧)}','4368','1554','0');
INSERT INTO [t20766] VALUES (1564,'قوله تعالى: {فحق علينا قول ربنا إنا لذائقون (٣١) فأغويناكم إنا كنا غاوين (٣٢)}','4368','1554','0');
INSERT INTO [t20766] VALUES (1565,'قوله تعالى: {فإنهم يومئذ في العذاب مشتركون (٣٣)}','4368','1554','0');
INSERT INTO [t20766] VALUES (1566,'قوله تعالى: {إنا كذلك نفعل بالمجرمين (٣٤) إنهم كانوا إذا قيل لهم لا إله إلا الله يستكبرون (٣٥)}','4369','1554','0');
INSERT INTO [t20766] VALUES (1567,'قوله تعالى: {ويقولون أئنا لتاركو آلهتنا لشاعر مجنون (٣٦)}','4370','1554','0');
INSERT INTO [t20766] VALUES (1568,'قوله تعالى: {لا فيها غول ولا هم عنها ينزفون (٤٧)}','4370','1554','0');
INSERT INTO [t20766] VALUES (1569,'قوله تعالى: {وعندهم قاصرات الطرف عين (٤٨) كأنهن بيض مكنون (٤٩)}','4370','1554','0');
INSERT INTO [t20766] VALUES (1570,'قوله تعالى: {أذلك خير نزلا أم شجرة الزقوم (٦٢)}','4373','1554','0');
INSERT INTO [t20766] VALUES (1571,'قوله تعالى: {إنا جعلناها فتنة للظالمين (٦٣) إنها شجرة تخرج في أصل الجحيم (٦٤)}','4373','1554','0');
INSERT INTO [t20766] VALUES (1572,'قوله تعالى: {فإنهم لآكلون منها فمالئون منها البطون (٦٦) ثم إن لهم عليها لشوبا من حميم (٦٧)}','4373','1554','0');
INSERT INTO [t20766] VALUES (1573,'قوله تعالى: {إنهم ألفوا آباءهم ضالين (٦٩) فهم على آثارهم يهرعون (٧٠)}','4374','1554','0');
INSERT INTO [t20766] VALUES (1574,'قوله تعالى: {ولقد ضل قبلهم أكثر الأولين (٧١)}','4375','1554','0');
INSERT INTO [t20766] VALUES (1575,'قوله تعالى: {ولقد نادانا نوح فلنعم المجيبون (٧٥) ونجيناه وأهله من الكرب العظيم (٧٦) وجعلنا ذريته هم الباقين (٧٧)}','4375','1554','0');
INSERT INTO [t20766] VALUES (1576,'قوله تعالى: {إذ قال لأبيه وقومه ماذا تعبدون (٨٥) أئفكا آلهة دون الله تريدون (٨٦)}','4375','1554','0');
INSERT INTO [t20766] VALUES (1577,'قوله تعالى: {وقال إني ذاهب إلى ربي سيهدين (٩٩) رب هب لي من الصالحين (١٠٠) فبشرناه بغلام حليم (١٠١)}','4376','1554','0');
INSERT INTO [t20766] VALUES (1578,'قوله تعالى: {ومن ذريتهما محسن وظالم لنفسه مبين (١١٣)}','4379','1554','0');
INSERT INTO [t20766] VALUES (1579,'قوله تعالى: {ولقد مننا على موسى وهارون (١١٤)}','4379','1554','0');
INSERT INTO [t20766] VALUES (1580,'قوله تعالى: {ونجيناهما وقومهما من الكرب العظيم (١١٥)}','4379','1554','0');
INSERT INTO [t20766] VALUES (1581,'قوله تعالى: {ونصرناهم فكانوا هم الغالبين (١١٦)}','4380','1554','0');
INSERT INTO [t20766] VALUES (1582,'قوله تعالى: {وآتيناهما الكتاب المستبين (١١٧)}','4380','1554','0');
INSERT INTO [t20766] VALUES (1583,'قوله تعالى: {وإنكم لتمرون عليهم مصبحين (١٣٧) وبالليل أفلا تعقلون (١٣٨)}','4381','1554','0');
INSERT INTO [t20766] VALUES (1584,'قوله تعالى: {فلولا أنه كان من المسبحين (١٤٣) للبث في بطنه إلى يوم يبعثون (١٤٤)}','4381','1554','0');
INSERT INTO [t20766] VALUES (1585,'قوله تعالى: {فآمنوا فمتعناهم إلى حين (١٤٨)}','4381','1554','0');
INSERT INTO [t20766] VALUES (1586,'قوله تعالى: {فاستفتهم ألربك البنات ولهم البنون (١٤٩)}','4382','1554','0');
INSERT INTO [t20766] VALUES (1587,'قوله تعالى: {وإن كانوا ليقولون (١٦٧) لو أن عندنا ذكرا من الأولين (١٦٨) لكنا عباد الله المخلصين (١٦٩) فكفروا به فسوف يعلمون (١٧٠)}','4382','1554','0');
INSERT INTO [t20766] VALUES (1588,'قوله تعالى: {ولقد سبقت كلمتنا لعبادنا المرسلين (١٧١) إنهم لهم المنصورون (١٧٢) وإن جندنا لهم الغالبون (١٧٣)}','4382','1554','0');
INSERT INTO [t20766] VALUES (1589,'قوله تعالى: {أفبعذابنا يستعجلون (١٧٦) فإذا نزل بساحتهم فساء صباح المنذرين (١٧٧)}','4383','1554','0');
INSERT INTO [t20766] VALUES (1590,'قوله تعالى: {وسلام على المرسلين (١٨١) والحمد لله رب العالمين (١٨٢)}','4383','1554','0');
INSERT INTO [t20766] VALUES (1591,'سورة ص','4385','0','0');
INSERT INTO [t20766] VALUES (1592,'قوله تعالى: {ص والقرآن ذي الذكر (١)}','4386','1591','0');
INSERT INTO [t20766] VALUES (1593,'قوله تعالى: {بل الذين كفروا في عزة وشقاق (٢)}','4395','1591','0');
INSERT INTO [t20766] VALUES (1594,'قوله تعالى: {كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص (٣)}','4396','1591','0');
INSERT INTO [t20766] VALUES (1595,'قوله تعالى: {وعجبوا أن جاءهم منذر منهم}','4402','1591','0');
INSERT INTO [t20766] VALUES (1596,'قوله تعالى: {وانطلق الملأ منهم أن امشوا واصبروا على آلهتكم}','4405','1591','0');
INSERT INTO [t20766] VALUES (1597,'قوله تعالى: {أأنزل عليه الذكر من بيننا}','4405','1591','0');
INSERT INTO [t20766] VALUES (1598,'قوله تعالى: {أم لهم ملك السماوات والأرض وما بينهما}','4406','1591','0');
INSERT INTO [t20766] VALUES (1599,'قوله تعالى: {كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد (١٢) وثمود وقوم لوط وأصحاب الأيكة أولئك الأحزاب (١٣) إن كل إلا كذب الرسل فحق عقاب (١٤)}','4406','1591','0');
INSERT INTO [t20766] VALUES (1600,'قوله تعالى: {وقالوا ربنا عجل لنا قطنا قبل يوم الحساب (١٦)}','4406','1591','0');
INSERT INTO [t20766] VALUES (1601,'قوله تعالى: {إنا سخرنا الجبال معه}','4407','1591','0');
INSERT INTO [t20766] VALUES (1602,'قوله تعالى {وظن داوود أنما فتناه فاستغفر ربه وخر راكعا وأناب (٢٤) فغفرنا له ذلك}','4407','1591','0');
INSERT INTO [t20766] VALUES (1603,'قوله تعالى: {ياداوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله}','4408','1591','0');
INSERT INTO [t20766] VALUES (1604,'قوله تعالى: {وما خلقنا السماء والأرض وما بينهما باطلا}','4410','1591','0');
INSERT INTO [t20766] VALUES (1605,'قوله تعالى: {ذلك ظن الذين كفروا فويل للذين كفروا من النار (٢٧)}','4411','1591','0');
INSERT INTO [t20766] VALUES (1606,'قوله تعالى: {أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار (٢٨)}','4413','1591','0');
INSERT INTO [t20766] VALUES (1607,'قوله تعالى: {كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب (٢٩)}','4414','1591','0');
INSERT INTO [t20766] VALUES (1608,'قوله تعالى: {ووهبنا لداوود سليمان}','4417','1591','0');
INSERT INTO [t20766] VALUES (1609,'قوله تعالى: {ولقد فتنا سليمان وألقينا على كرسيه جسدا}','4418','1591','0');
INSERT INTO [t20766] VALUES (1610,'قوله تعالى: {فسخرنا له الريح تجري بأمره رخاء حيث أصاب (٣٦)}','4418','1591','0');
INSERT INTO [t20766] VALUES (1611,'قوله تعالى: {والشياطين كل بناء وغواص (٣٧)}','4419','1591','0');
INSERT INTO [t20766] VALUES (1612,'قوله تعالى: {واذكر عبدنا أيوب إذ نادى ربه أني مسني الشيطان بنصب وعذاب (٤١) اركض برجلك هذا مغتسل بارد وشراب (٤٢) ووهبنا له أهله ومثلهم معهم رحمة منا وذكرى لأولي الألباب (٤٣)}','4419','1591','0');
INSERT INTO [t20766] VALUES (1613,'قوله تعالى: {واذكر عبادنا إبراهيم وإسحاق}','4419','1591','0');
INSERT INTO [t20766] VALUES (1614,'قوله تعالى: {واذكر إسماعيل واليسع}','4420','1591','0');
INSERT INTO [t20766] VALUES (1615,'قوله تعالى: {وعندهم قاصرات الطرف أتراب (٥٢)}','4420','1591','0');
INSERT INTO [t20766] VALUES (1616,'قوله تعالى: {إن هذا لرزقنا ما له من نفاد (٥٤)}','4420','1591','0');
INSERT INTO [t20766] VALUES (1617,'قوله تعالى: {إن ذلك لحق تخاصم أهل النار (٦٤)}','4420','1591','0');
INSERT INTO [t20766] VALUES (1618,'قوله تعالى: {قال ما منعك ألا تسجد إذ أمرتك قال أنا خير منه خلقتني من نار وخلقته من طين (١٢)}','4421','1591','0');
INSERT INTO [t20766] VALUES (1619,'قوله تعالى: {قل ما أسألكم عليه من أجر وما أنا من المتكلفين (٨٦)}','4421','1591','0');
INSERT INTO [t20766] VALUES (1620,'قوله تعالى: {ولتعلمن نبأه بعد حين (٨٨)}','4421','1591','0');
INSERT INTO [t20766] VALUES (1621,'سورة الزمر','4423','0','0');
INSERT INTO [t20766] VALUES (1622,'قوله تعالى: {تنزيل الكتاب من الله العزيز الحكيم (١)}','4424','1621','0');
INSERT INTO [t20766] VALUES (1623,'قوله تعالى: {فاعبد الله مخلصا له الدين (٢) ألا لله الدين الخالص}','4425','1621','0');
INSERT INTO [t20766] VALUES (1624,'قوله تعالى: {لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار (٤)}','4427','1621','0');
INSERT INTO [t20766] VALUES (1625,'قوله تعالى: {خلقكم من نفس واحدة وخلق منها زوجها}','4427','1621','0');
INSERT INTO [t20766] VALUES (1626,'قوله تعالى: {وأنزل لكم من الأنعام ثمانية أزواج}','4428','1621','0');
INSERT INTO [t20766] VALUES (1627,'قوله تعالى: {يخلقكم في بطون أمهاتكم خلقا من بعد خلق}','4428','1621','0');
INSERT INTO [t20766] VALUES (1628,'قوله تعالى: {إن تكفروا فإن الله غني عنكم}','4428','1621','0');
INSERT INTO [t20766] VALUES (1629,'قوله تعالى: {ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم}','4429','1621','0');
INSERT INTO [t20766] VALUES (1630,'قوله تعالى: {وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله}','4429','1621','0');
INSERT INTO [t20766] VALUES (1631,'قوله تعالى: {قل تمتع بكفرك قليلا إنك من أصحاب النار (٨)}','4429','1621','0');
INSERT INTO [t20766] VALUES (1632,'قوله تعالى: {وأرض الله واسعة}','4430','1621','0');
INSERT INTO [t20766] VALUES (1633,'قوله تعالى: {قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ألا ذلك هو الخسران المبين (١٥)}','4430','1621','0');
INSERT INTO [t20766] VALUES (1634,'قوله تعالى: {لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده}','4430','1621','0');
INSERT INTO [t20766] VALUES (1635,'قوله تعالى: {والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله}','4431','1621','0');
INSERT INTO [t20766] VALUES (1636,'قوله تعالى: {الذين يستمعون القول فيتبعون أحسنه}','4431','1621','0');
INSERT INTO [t20766] VALUES (1637,'قوله تعالى: {أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار (١٩)}','4434','1621','0');
INSERT INTO [t20766] VALUES (1638,'قوله تعالى: {لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية}','4435','1621','0');
INSERT INTO [t20766] VALUES (1639,'قوله تعالى: {ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض}','4435','1621','0');
INSERT INTO [t20766] VALUES (1640,'قوله تعالى: {ثم يخرج به زرعا مختلفا ألوانه}','4436','1621','0');
INSERT INTO [t20766] VALUES (1641,'قوله تعالى: {ثم يهيج فتراه مصفرا ثم يجعله حطاما إن في ذلك لذكرى لأولي الألباب (٢١)}','4436','1621','0');
INSERT INTO [t20766] VALUES (1642,'قوله تعالى: {أفمن شرح الله صدره للإسلام فهو على نور من ربه}','4437','1621','0');
INSERT INTO [t20766] VALUES (1643,'قوله تعالى: {ومن يضلل الله فما له من هاد (٣٦)}','4437','1621','0');
INSERT INTO [t20766] VALUES (1644,'قوله تعالى: {قرآنا عربيا غير ذي عوج}','4437','1621','0');
INSERT INTO [t20766] VALUES (1645,'قوله تعالى: {والذي جاء بالصدق}','4438','1621','0');
INSERT INTO [t20766] VALUES (1646,'قوله تعالى: {لهم ما يشاءون عند ربهم ذلك جزاء المحسنين (٣٤)}','4439','1621','0');
INSERT INTO [t20766] VALUES (1647,'قوله تعالى: {ويجزيهم أجرهم بأحسن الذي كانوا يعملون (٣٥)}','4439','1621','0');
INSERT INTO [t20766] VALUES (1648,'قوله تعالى: {أليس الله بكاف عبده}','4440','1621','0');
INSERT INTO [t20766] VALUES (1649,'قوله تعالى: {ويخوفونك بالذين من دونه}','4440','1621','0');
INSERT INTO [t20766] VALUES (1650,'قوله تعالى: {قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته}','4441','1621','0');
INSERT INTO [t20766] VALUES (1651,'قوله تعالى: {وإذا ذكر الله وحده اشمأزت قلوب الذين لا يؤمنون بالآخرة وإذا ذكر الذين من دونه إذا هم يستبشرون (٤٥)}','4442','1621','0');
INSERT INTO [t20766] VALUES (1652,'قوله تعالى: {ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة}','4442','1621','0');
INSERT INTO [t20766] VALUES (1653,'قوله تعالى: {وبدا لهم سيئات ما كسبوا}','4443','1621','0');
INSERT INTO [t20766] VALUES (1654,'قوله تعالى: {فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته على علم}','4444','1621','0');
INSERT INTO [t20766] VALUES (1655,'قوله تعالى: {واتبعوا أحسن ما أنزل إليكم من ربكم}','4444','1621','0');
INSERT INTO [t20766] VALUES (1656,'قوله تعالى: {أو تقول حين ترى العذاب لو أن لي كرة فأكون من المحسنين (٥٨)}','4444','1621','0');
INSERT INTO [t20766] VALUES (1657,'قوله تعالى: {ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة}','4445','1621','0');
INSERT INTO [t20766] VALUES (1658,'قوله تعالى: {أليس في جهنم مثوى للمتكبرين (٦٠)}','4445','1621','0');
INSERT INTO [t20766] VALUES (1659,'قوله تعالى: {ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك}','4445','1621','0');
INSERT INTO [t20766] VALUES (1660,'قوله تعالى: {ثم نفخ فيه أخرى فإذا هم قيام ينظرون (٦٨)}','4445','1621','0');
INSERT INTO [t20766] VALUES (1661,'قوله تعالى: {ووضع الكتاب}','4446','1621','0');
INSERT INTO [t20766] VALUES (1662,'قوله تعالى: {وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون (٦٩) ووفيت كل نفس ما عملت}','4446','1621','0');
INSERT INTO [t20766] VALUES (1663,'قوله تعالى: {وسيق الذين كفروا إلى جهنم زمرا}','4448','1621','0');
INSERT INTO [t20766] VALUES (1664,'قوله تعالى: {حتى إذا جاءوها وفتحت أبوابها}','4449','1621','0');
INSERT INTO [t20766] VALUES (1665,'قوله تعالى: {وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم وينذرونكم لقاء يومكم هذا قالوا بلى ولكن حقت كلمة العذاب على الكافرين (٧١)}','4449','1621','0');
INSERT INTO [t20766] VALUES (1666,'قوله تعالى: {وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين (٧٣)}','4449','1621','0');
INSERT INTO [t20766] VALUES (1667,'قوله تعالى: {وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء}','4450','1621','0');
INSERT INTO [t20766] VALUES (1668,'سورة غافر','4451','0','0');
INSERT INTO [t20766] VALUES (1669,'قوله تعالى: {غافر الذنب وقابل التوب شديد العقاب ذي الطول}','4452','1668','0');
INSERT INTO [t20766] VALUES (1670,'قوله تعالى: {ما يجادل في آيات الله إلا الذين كفروا}','4452','1668','0');
INSERT INTO [t20766] VALUES (1671,'قوله تعالى: {فلا يغررك تقلبهم في البلاد}','4453','1668','0');
INSERT INTO [t20766] VALUES (1672,'قوله تعالى: {وكذلك حقت كلمت ربك علي الذين كفروا أنهم أصحاب النار (٦)}','4454','1668','0');
INSERT INTO [t20766] VALUES (1673,'قوله تعالى: {ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم}','4455','1668','0');
INSERT INTO [t20766] VALUES (1674,'قوله تعالى: {قالوا ربنا أمتنا اثنتين وأحييتنا اثنتين}','4455','1668','0');
INSERT INTO [t20766] VALUES (1675,'قوله تعالى: {فاعترفنا بذنوبنا فهل إلى خروج من سبيل (١١)}','4456','1668','0');
INSERT INTO [t20766] VALUES (1676,'قوله تعالى: {ذلكم بأنه إذا دعي الله وحده كفرتم وإن يشرك به تؤمنوا}','4457','1668','0');
INSERT INTO [t20766] VALUES (1677,'قوله تعالى: {فالحكم لله العلي الكبير (١٢)}','4457','1668','0');
INSERT INTO [t20766] VALUES (1678,'قوله تعالى: {هو الذي يريكم آياته}','4457','1668','0');
INSERT INTO [t20766] VALUES (1679,'قوله تعالى: {وينزل لكم من السماء رزقا}','4459','1668','0');
INSERT INTO [t20766] VALUES (1680,'قوله تعالى: {وما يتذكر إلا من ينيب (١٣)}','4461','1668','0');
INSERT INTO [t20766] VALUES (1681,'قوله تعالى: {فادعوا الله مخلصين له الدين}','4462','1668','0');
INSERT INTO [t20766] VALUES (1682,'قوله تعالى: {يلقي الروح من أمره على من يشاء من عباده لينذر يوم التلاق (١٥) يوم هم بارزون}','4462','1668','0');
INSERT INTO [t20766] VALUES (1683,'قوله تعالى: {وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين}','4463','1668','0');
INSERT INTO [t20766] VALUES (1684,'قوله تعالى: {ما للظالمين من حميم ولا شفيع يطاع (١٨)}','4466','1668','0');
INSERT INTO [t20766] VALUES (1685,'قوله تعالى: {يعلم خائنة الأعين وما تخفي الصدور (١٩)}','4466','1668','0');
INSERT INTO [t20766] VALUES (1686,'قوله تعالى: {ولقد أرسلنا موسى بآياتنا وسلطان مبين (٢٣) إلى فرعون وهامان وقارون فقالوا ساحر كذاب (٢٤)}','4466','1668','0');
INSERT INTO [t20766] VALUES (1687,'قوله تعالى: {وقال موسى إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب (٢٧)}','4467','1668','0');
INSERT INTO [t20766] VALUES (1688,'قوله تعالى: {وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله}','4468','1668','0');
INSERT INTO [t20766] VALUES (1689,'قوله تعالى: {قال فرعون ما أريكم إلا ما أري وما أهديكم إلا سبيل الرشاد (٢٩)}','4469','1668','0');
INSERT INTO [t20766] VALUES (1690,'قوله تعالى: {من عمل سيئة فلا يجزى إلا مثلها}','4471','1668','0');
INSERT INTO [t20766] VALUES (1691,'قوله تعالى: {ومن عمل صالحا من ذكر أو أنثي وهو مؤمن فأولئك يدخلون الجنة يرزقون فيها بغير حساب (٤٠)}','4472','1668','0');
INSERT INTO [t20766] VALUES (1692,'قوله تعالى: {وتدعونني إلي النار (٤١) تدعونني لأكفر بالله وأشرك به ما ليس لي به علم}','4472','1668','0');
INSERT INTO [t20766] VALUES (1693,'قوله تعالى: {فستذكرون ما أقول لكم وأفوض أمري إلى الله إن الله بصير بالعباد (٤٤) فوقاه الله سيئات ما مكروا وحاق بآل فرعون سوء العذاب (٤٥)}','4473','1668','0');
INSERT INTO [t20766] VALUES (1694,'قوله تعالى: {وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار (٤٧) قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد (٤٨)}','4476','1668','0');
INSERT INTO [t20766] VALUES (1695,'قوله تعالى: {وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب (٤٩)}','4477','1668','0');
INSERT INTO [t20766] VALUES (1696,'قوله تعالى: {قالوا أولم تك تأتيكم رسلكم بالبينات}','4478','1668','0');
INSERT INTO [t20766] VALUES (1697,'قوله تعالى: {إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد (٥١)}','4478','1668','0');
INSERT INTO [t20766] VALUES (1698,'قوله تعالى: {ولقد آتينا موسى الهدى وأورثنا بني إسرائيل الكتاب (٥٣) هدى وذكرى لأولي الألباب (٥٤)}','4479','1668','0');
INSERT INTO [t20766] VALUES (1699,'قوله تعالى: {إن في صدورهم إلا كبر ما هم ببالغيه}','4480','1668','0');
INSERT INTO [t20766] VALUES (1700,'قوله تعالى: {لخلق السماوات والأرض أكبر من خلق الناس}','4480','1668','0');
INSERT INTO [t20766] VALUES (1701,'قوله تعالى: {وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء}','4480','1668','0');
INSERT INTO [t20766] VALUES (1702,'قوله تعالى: {إن الساعة لآتية لا ريب فيها ولكن أكثر الناس لا يؤمنون (٥٩)}','4481','1668','0');
INSERT INTO [t20766] VALUES (1703,'قوله تعالى: {وقال ربكم ادعوني أستجب لكم إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين (٦٠)}','4481','1668','0');
INSERT INTO [t20766] VALUES (1704,'قوله تعالى: {الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون (٦١)}','4482','1668','0');
INSERT INTO [t20766] VALUES (1705,'قوله تعالى: {هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا أشدكم ثم لتكونوا شيوخا ومنكم من يتوفى من قبل ولتبلغوا أجلا مسمى ولعلكم تعقلون (٦٧)}','4482','1668','0');
INSERT INTO [t20766] VALUES (1706,'قوله تعالى: {فإذا قضى أمرا فإنما يقول له كن فيكون (٦٨)}','4482','1668','0');
INSERT INTO [t20766] VALUES (1707,'قوله تعالى: {قيل ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين (٧٢)}','4483','1668','0');
INSERT INTO [t20766] VALUES (1708,'قوله تعالى: {ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك}','4483','1668','0');
INSERT INTO [t20766] VALUES (1709,'قوله تعالى: {فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون (٧٨)}','4483','1668','0');
INSERT INTO [t20766] VALUES (1710,'قوله تعالى: {الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون (٧٩) ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون (٨٠)}','4484','1668','0');
INSERT INTO [t20766] VALUES (1711,'قوله تعالى: {أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم}','4486','1668','0');
INSERT INTO [t20766] VALUES (1712,'قوله تعالى: {فلم يك ينفعهم إيمانهم لما رأوا بأسنا سنت الله التي قد خلت في عباده وخسر هنالك الكافرون (٨٥)}','4487','1668','0');
INSERT INTO [t20766] VALUES (1713,'سورة فصلت','4488','0','0');
INSERT INTO [t20766] VALUES (1714,'قوله تعالى: {حم (١) تنزيل من الرحمن الرحيم (٢)}','4489','1713','0');
INSERT INTO [t20766] VALUES (1715,'قوله تعالى: {كتاب فصلت آياته}','4489','1713','0');
INSERT INTO [t20766] VALUES (1716,'قوله تعالى: {قرآنا عربيا لقوم يعلمون (٣) بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون (٤)}','4490','1713','0');
INSERT INTO [t20766] VALUES (1717,'قوله تعالى: {وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب}','4492','1713','0');
INSERT INTO [t20766] VALUES (1718,'قوله تعالى: {قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد}','4497','1713','0');
INSERT INTO [t20766] VALUES (1719,'قوله تعالى: {وويل للمشركين (٦) الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون (٧)}','4498','1713','0');
INSERT INTO [t20766] VALUES (1720,'قوله تعالى: {إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون (٨)}','4499','1713','0');
INSERT INTO [t20766] VALUES (1721,'قوله تعالى: {وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام}','4501','1713','0');
INSERT INTO [t20766] VALUES (1722,'قوله تعالى: {وزينا السماء الدنيا بمصابيح وحفظا}','4505','1713','0');
INSERT INTO [t20766] VALUES (1723,'قوله تعالى: {قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون (١٤)}','4506','1713','0');
INSERT INTO [t20766] VALUES (1724,'قوله تعالى: {فأرسلنا عليهم ريحا صرصرا في أيام نحسات}','4506','1713','0');
INSERT INTO [t20766] VALUES (1725,'قوله تعالى: {وأما ثمود فهديناهم فاستحبوا العمى على الهدى}','4510','1713','0');
INSERT INTO [t20766] VALUES (1726,'قوله تعالى: {فأخذتهم صاعقة العذاب الهون}','4512','1713','0');
INSERT INTO [t20766] VALUES (1727,'قوله تعالى: {ونجينا الذين آمنوا وكانوا يتقون (١٨)}','4516','1713','0');
INSERT INTO [t20766] VALUES (1728,'قوله تعالى: {ويوم يحشر أعداء الله إلى النار فهم يوزعون (١٩)}','4516','1713','0');
INSERT INTO [t20766] VALUES (1729,'قوله تعالى: {حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون (٢٠)}','4518','1713','0');
INSERT INTO [t20766] VALUES (1730,'قوله تعالى: {ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون (٢٢) وذلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين (٢٣) فإن يصبروا فالنار مثوى لهم}','4518','1713','0');
INSERT INTO [t20766] VALUES (1731,'قوله تعالى: {وإن يستعتبوا فما هم من المعتبين (٢٤)}','4518','1713','0');
INSERT INTO [t20766] VALUES (1732,'قوله تعالى: {وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم}','4519','1713','0');
INSERT INTO [t20766] VALUES (1733,'قوله تعالى: {وحق عليهم القول}','4522','1713','0');
INSERT INTO [t20766] VALUES (1734,'قوله تعالى: {وقال الذين كفروا لا تسمعوا لهذا القرآن}','4522','1713','0');
INSERT INTO [t20766] VALUES (1735,'قوله تعالى: {ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم (٣٤) وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم (٣٥) وإما ينزغنك من الشيطان نزغ فاستعذ بالله إنه هو السميع العليم (٣٦)}','4523','1713','0');
INSERT INTO [t20766] VALUES (1736,'قوله تعالى: {ومن آياته الليل والنهار}','4523','1713','0');
INSERT INTO [t20766] VALUES (1737,'قوله تعالى: {لا تسجدوا للشمس ولا للقمر}','4523','1713','0');
INSERT INTO [t20766] VALUES (1738,'قوله تعالى: {فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون (٣٨)}','4523','1713','0');
INSERT INTO [t20766] VALUES (1739,'قوله تعالى: {ومن آياته أنك ترى الأرض خاشعة فإذا أنزلنا عليها الماء اهتزت وربت}','4525','1713','0');
INSERT INTO [t20766] VALUES (1740,'قوله تعالى: {أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة}','4525','1713','0');
INSERT INTO [t20766] VALUES (1741,'قوله تعالى: {قل هو للذين آمنوا هدى وشفاء}','4525','1713','0');
INSERT INTO [t20766] VALUES (1742,'قوله تعالى: {من عمل صالحا فلنفسه ومن أساء فعليها}','4526','1713','0');
INSERT INTO [t20766] VALUES (1743,'قوله تعالى: {وما ربك بظلام للعبيد (٤٦)}','4526','1713','0');
INSERT INTO [t20766] VALUES (1744,'قوله تعالى: {إليه يرد علم الساعة}','4529','1713','0');
INSERT INTO [t20766] VALUES (1745,'قوله تعالى: {وما تحمل من أنثى ولا تضع إلا بعلمه}','4530','1713','0');
INSERT INTO [t20766] VALUES (1746,'قوله تعالى: {وظنوا ما لهم من محيص (٤٨)}','4530','1713','0');
INSERT INTO [t20766] VALUES (1747,'قوله تعالى: {ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى}','4531','1713','0');
INSERT INTO [t20766] VALUES (1748,'قوله تعالى: {وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر فذو دعاء عريض (٥١)}','4532','1713','0');
INSERT INTO [t20766] VALUES (1749,'قوله تعالى: {سنريهم آياتنا في الآفاق وفي أنفسهم}','4532','1713','0');
INSERT INTO [t20766] VALUES (1750,'قوله تعالى: {ألا إنهم في مرية من لقاء ربهم}','4532','1713','0');
INSERT INTO [t20766] VALUES (1751,'سورة الشورى','4533','0','0');
INSERT INTO [t20766] VALUES (1752,'قوله تعالى: {حم (١) عسق (٢) كذلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم (٣)}','4534','1751','0');
INSERT INTO [t20766] VALUES (1753,'قوله تعالى: {وهو العلي العظيم (٤)}','4536','1751','0');
INSERT INTO [t20766] VALUES (1754,'قوله تعالى: {تكاد السماوات يتفطرن من فوقهن والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض}','4536','1751','0');
INSERT INTO [t20766] VALUES (1755,'قوله تعالى: {ألا إن الله هو الغفور الرحيم (٥)}','4540','1751','0');
INSERT INTO [t20766] VALUES (1756,'قوله تعالى: {والذين اتخذوا من دونه أولياء الله حفيظ عليهم وما أنت عليهم بوكيل (٦)}','4541','1751','0');
INSERT INTO [t20766] VALUES (1757,'قوله تعالى: {وكذلك أوحينا إليك قرآنا عربيا}','4544','1751','0');
INSERT INTO [t20766] VALUES (1758,'قوله تعالى: {ولتنذر أم القرى ومن حولها}','4544','1751','0');
INSERT INTO [t20766] VALUES (1759,'قوله تعالى: {وتنذر يوم الجمع لا ريب فيه}','4545','1751','0');
INSERT INTO [t20766] VALUES (1760,'قوله تعالى: {فريق في الجنة وفريق في السعير (٧)}','4547','1751','0');
INSERT INTO [t20766] VALUES (1761,'قوله تعالى: {وما اختلفتم فيه من شيء فحكمه إلى الله}','4548','1751','0');
INSERT INTO [t20766] VALUES (1762,'قوله تعالى: {فاطر السماوات والأرض جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا يذرؤكم فيه}','4560','1751','0');
INSERT INTO [t20766] VALUES (1763,'قوله تعالى: {ليس كمثله شيء وهو السميع البصير (١١)}','4563','1751','0');
INSERT INTO [t20766] VALUES (1764,'قوله تعالى: {له مقاليد السماوات والأرض يبسط الرزق لمن يشاء ويقدر}','4563','1751','0');
INSERT INTO [t20766] VALUES (1765,'قوله تعالى: {شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين}','4565','1751','0');
INSERT INTO [t20766] VALUES (1766,'قوله تعالى: {ولا تتفرقوا فيه}','4565','1751','0');
INSERT INTO [t20766] VALUES (1767,'قوله تعالى: {كبر على المشركين ما تدعوهم إليه}','4567','1751','0');
INSERT INTO [t20766] VALUES (1768,'قوله تعالى: {الله يجتبي إليه من يشاء ويهدي إليه من ينيب (١٣)}','4569','1751','0');
INSERT INTO [t20766] VALUES (1769,'قوله تعالى: {وقل آمنت بما أنزل الله من كتاب وأمرت لأعدل بينكم}','4570','1751','0');
INSERT INTO [t20766] VALUES (1770,'قوله تعالى: {الله الذي أنزل الكتاب بالحق والميزان}','4570','1751','0');
INSERT INTO [t20766] VALUES (1771,'قوله تعالى: {وما يدريك لعل الساعة قريب (١٧)}','4574','1751','0');
INSERT INTO [t20766] VALUES (1772,'قوله تعالى: {يستعجل بها الذين لا يؤمنون بها والذين آمنوا مشفقون منها ويعلمون أنها الحق}','4575','1751','0');
INSERT INTO [t20766] VALUES (1773,'قوله تعالى: {ألا إن الذين يمارون في الساعة لفي ضلال بعيد (١٨)}','4576','1751','0');
INSERT INTO [t20766] VALUES (1774,'قوله تعالى: {قل لا أسألكم عليه أجرا إلا المودة في القربى}','4576','1751','0');
INSERT INTO [t20766] VALUES (1775,'قوله تعالى: {ومن يقترف حسنة نزد له فيها حسنا}','4580','1751','0');
INSERT INTO [t20766] VALUES (1776,'قوله تعالى: {وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات}','4580','1751','0');
INSERT INTO [t20766] VALUES (1777,'قوله تعالى: {ولكن ينزل بقدر ما يشاء}','4581','1751','0');
INSERT INTO [t20766] VALUES (1778,'قوله تعالى: {وما أنتم بمعجزين في الأرض}','4581','1751','0');
INSERT INTO [t20766] VALUES (1779,'قوله تعالى: {ومن آياته الجوار في البحر كالأعلام (٣٢)}','4581','1751','0');
INSERT INTO [t20766] VALUES (1780,'قوله تعالى: {والذين يجتنبون كبائر الإثم والفواحش}','4583','1751','0');
INSERT INTO [t20766] VALUES (1781,'قوله تعالى: {وجزاء سيئة سيئة مثلها}','4588','1751','0');
INSERT INTO [t20766] VALUES (1782,'قوله تعالى: {ولمن انتصر بعد ظلمه فأولئك ما عليهم من سبيل (٤١)}','4588','1751','0');
INSERT INTO [t20766] VALUES (1783,'قوله تعالى: {وترى الظالمين لما رأوا العذاب}','4588','1751','0');
INSERT INTO [t20766] VALUES (1784,'قوله تعالى: {وكذلك أوحينا إليك روحا من أمرنا}','4589','1751','0');
INSERT INTO [t20766] VALUES (1785,'قوله تعالى: {ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا}','4589','1751','0');
INSERT INTO [t20766] VALUES (1786,'قوله تعالى: {وإنك لتهدي إلى صراط مستقيم (٥٣)}','4591','1751','0');
INSERT INTO [t20766] VALUES (1787,'قوله تعالى: {ألا إلى الله تصير الأمور (٥٣)}','4592','1751','0');
INSERT INTO [t20766] VALUES (1788,'سورة الزخرف','4593','0','0');
INSERT INTO [t20766] VALUES (1789,'قوله تعالى: {حم (١) والكتاب المبين (٢) إنا جعلناه قرآنا عربيا}','4594','1788','0');
INSERT INTO [t20766] VALUES (1790,'قوله تعالى: {فأهلكنا أشد منهم بطشا ومضى مثل الأولين (٨)}','4594','1788','0');
INSERT INTO [t20766] VALUES (1791,'قوله تعالى: {ولئن سألتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم (٩)}','4596','1788','0');
INSERT INTO [t20766] VALUES (1792,'قوله تعالى: {الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون (١٠)}','4597','1788','0');
INSERT INTO [t20766] VALUES (1793,'قوله تعالى: {والذي نزل من السماء ماء بقدر فأنشرنا به بلدة ميتا كذلك تخرجون (١١)}','4598','1788','0');
INSERT INTO [t20766] VALUES (1794,'قوله تعالى: {والذي خلق الأزواج كلها}','4599','1788','0');
INSERT INTO [t20766] VALUES (1795,'قوله تعالى: {وجعل لكم من الفلك والأنعام ما تركبون (١٢) لتستووا على ظهوره ثم تذكروا نعمة ربكم إذا استويتم عليه}','4599','1788','0');
INSERT INTO [t20766] VALUES (1796,'قوله تعالى: {وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين (١٣)}','4600','1788','0');
INSERT INTO [t20766] VALUES (1797,'قوله تعالى: {وجعلوا له من عباده جزءا}','4602','1788','0');
INSERT INTO [t20766] VALUES (1798,'قوله تعالى: {أم اتخذ مما يخلق بنات وأصفاكم بالبنين (١٦)}','4605','1788','0');
INSERT INTO [t20766] VALUES (1799,'قوله تعالى: {وجعلوا الملائكة الذين هم عباد الرحمن إناثا أشهدوا خلقهم ستكتب شهادتهم ويسألون (١٩)}','4606','1788','0');
INSERT INTO [t20766] VALUES (1800,'قوله تعالى: {وقالوا لو شاء الرحمن ما عبدناهم ما لهم بذلك من علم إن هم إلا يخرصون (٢٠)}','4609','1788','0');
INSERT INTO [t20766] VALUES (1801,'قوله تعالى: {أم آتيناهم كتابا من قبله فهم به مستمسكون (٢١)}','4615','1788','0');
INSERT INTO [t20766] VALUES (1802,'قوله تعالى: {وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون قال أولو جئتكم بأهدى مما وجدتم عليه آباءكم}','4616','1788','0');
INSERT INTO [t20766] VALUES (1803,'قوله تعالى: {وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون (٢٦) إلا الذي فطرني فإنه سيهدين (٢٧)}','4618','1788','0');
INSERT INTO [t20766] VALUES (1804,'قوله تعالى: {وجعلها كلمة باقية في عقبه لعلهم يرجعون (٢٨) بل متعت هؤلاء وآباءهم حتى جاءهم الحق ورسول مبين (٢٩) ولما جاءهم الحق قالوا هذا سحر وإنا به كافرون (٣٠)}','4619','1788','0');
INSERT INTO [t20766] VALUES (1805,'قوله تعالى: {وقالوا لولا نزل هذا القرآن على رجل من القريتين عظيم (٣١) أهم يقسمون رحمت ربك نحن قسمنا بينهم معيشتهم في الحياة الدنيا ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ورحمت ربك خير مما يجمعون (٣٢)}','4630','1788','0');
INSERT INTO [t20766] VALUES (1806,'قوله تعالى: {ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون (٣٣) ولبيوتهم أبوابا وسررا عليها يتكئون (٣٤) وزخرفا وإن كل ذلك لما متاع الحياة الدنيا والآخرة عند ربك للمتقين (٣٥)}','4636','1788','0');
INSERT INTO [t20766] VALUES (1807,'قوله تعالى: {ومن يعش عن ذكر الرحمن نقيض له شيطانا فهو له قرين (٣٦) وإنهم ليصدونهم عن السبيل ويحسبون أنهم مهتدون (٣٧) حتى إذا جاءنا قال ياليت بيني وبينك بعد المشرقين فبئس القرين (٣٨)}','4642','1788','0');
INSERT INTO [t20766] VALUES (1808,'قوله تعالى: {ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون (٣٩)}','4642','1788','0');
INSERT INTO [t20766] VALUES (1809,'قوله تعالى: {أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين (٤٠)}','4642','1788','0');
INSERT INTO [t20766] VALUES (1810,'قوله تعالى: {فاستمسك بالذي أوحي إليك إنك على صراط مستقيم (٤٣)}','4643','1788','0');
INSERT INTO [t20766] VALUES (1811,'قوله تعالى: {ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه}','4644','1788','0');
INSERT INTO [t20766] VALUES (1812,'قوله تعالى: {وأخذناهم بالعذاب لعلهم يرجعون (٤٨)}','4645','1788','0');
INSERT INTO [t20766] VALUES (1813,'قوله تعالى: {وقالوا ياأيه الساحر ادع لنا ربك بما عهد عندك إننا لمهتدون (٤٩) فلما كشفنا عنهم العذاب إذا هم ينكثون (٥٠)}','4645','1788','0');
INSERT INTO [t20766] VALUES (1814,'قوله تعالى: {فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين (٥٣)}','4645','1788','0');
INSERT INTO [t20766] VALUES (1815,'قوله تعالى: {فلما آسفونا انتقمنا منهم}','4646','1788','0');
INSERT INTO [t20766] VALUES (1816,'قوله تعالى: {فجعلناهم سلفا ومثلا للآخرين (٥٦)}','4646','1788','0');
INSERT INTO [t20766] VALUES (1817,'قوله تعالى {ولما ضرب ابن مريم مثلا إذا قومك منه يصدون (٥٧) وقالوا أآلهتنا خير أم هو ما ضربوه لك إلا جدلا بل هم قوم خصمون (٥٨)}','4646','1788','0');
INSERT INTO [t20766] VALUES (1818,'قوله تعالى: {إن هو إلا عبد أنعمنا عليه}','4653','1788','0');
INSERT INTO [t20766] VALUES (1819,'قوله تعالى: {وإنه لعلم للساعة فلا تمترن بها}','4653','1788','0');
INSERT INTO [t20766] VALUES (1820,'قوله تعالى: {ولا يصدنكم الشيطان إنه لكم عدو مبين (٦٢)}','4667','1788','0');
INSERT INTO [t20766] VALUES (1821,'قوله تعالى: {فويل للذين ظلموا من عذاب يوم أليم (٦٥)}','4667','1788','0');
INSERT INTO [t20766] VALUES (1822,'قوله تعالى: {هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون (٦٦)}','4667','1788','0');
INSERT INTO [t20766] VALUES (1823,'قوله تعالى: {ياعباد لا خوف عليكم اليوم ولا أنتم تحزنون (٦٨) الذين آمنوا بآياتنا وكانوا مسلمين (٦٩)}','4668','1788','0');
INSERT INTO [t20766] VALUES (1824,'قوله تعالى: {ادخلوا الجنة أنتم وأزواجكم تحبرون (٧٠)}','4672','1788','0');
INSERT INTO [t20766] VALUES (1825,'قوله تعالى: {يطاف عليهم بصحاف من ذهب}','4673','1788','0');
INSERT INTO [t20766] VALUES (1826,'قوله تعالى: {وفيها ما تشتهيه الأنفس وتلذ الأعين وأنتم فيها خالدون (٧١)}','4673','1788','0');
INSERT INTO [t20766] VALUES (1827,'قوله تعالى: {وتلك الجنة التي أورثتموها بما كنتم تعملون (٧٢)}','4676','1788','0');
INSERT INTO [t20766] VALUES (1828,'قوله تعالى: {ونادوا يامالك ليقض علينا ربك قال إنكم ماكثون (٧٧)}','4677','1788','0');
INSERT INTO [t20766] VALUES (1829,'قوله تعالى: {لقد جئناكم بالحق ولكن أكثركم للحق كارهون (٧٨)}','4679','1788','0');
INSERT INTO [t20766] VALUES (1830,'قوله تعالى: {بلى ورسلنا لديهم يكتبون (٨٠)}','4679','1788','0');
INSERT INTO [t20766] VALUES (1831,'قوله تعالى: {قل إن كان للرحمن ولد فأنا أول العابدين (٨١)}','4679','1788','0');
INSERT INTO [t20766] VALUES (1832,'قوله تعالى: {سبحان رب السماوات والأرض رب العرش عما يصفون (٨٢)}','4704','1788','0');
INSERT INTO [t20766] VALUES (1833,'قوله تعالى: {فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون (٨٣)}','4705','1788','0');
INSERT INTO [t20766] VALUES (1834,'قوله تعالى: {وهو الذي في السماء إله وفي الأرض إله}','4705','1788','0');
INSERT INTO [t20766] VALUES (1835,'قوله تعالى: {وعنده علم الساعة}','4705','1788','0');
INSERT INTO [t20766] VALUES (1836,'قوله تعالى: {ولا يملك الذين يدعون من دونه الشفاعة}','4705','1788','0');
INSERT INTO [t20766] VALUES (1837,'قوله تعالى: {ولئن سألتهم من خلقهم ليقولن الله فأنى يؤفكون (٨٧)}','4706','1788','0');
INSERT INTO [t20766] VALUES (1838,'قوله تعالى: {وقيله يا رب إن هؤلاء قوم لا يؤمنون (٨٨)}','4706','1788','0');
INSERT INTO [t20766] VALUES (1839,'قوله تعالى: {فاصفح عنهم وقل سلام فسوف يعلمون}','4708','1788','0');
INSERT INTO [t20766] VALUES (1840,'سورة الدخان','4710','0','0');
INSERT INTO [t20766] VALUES (1841,'قوله تعالى: {إنا أنزلناه في ليلة مباركة}','4711','1840','0');
INSERT INTO [t20766] VALUES (1842,'قوله تعالى: {فيها يفرق كل أمر حكيم (٤) أمرا من عندنا}','4712','1840','0');
INSERT INTO [t20766] VALUES (1843,'قوله تعالى: {إنا كنا مرسلين (٥) رحمة من ربك}','4715','1840','0');
INSERT INTO [t20766] VALUES (1844,'قوله تعالى: {ثم تولوا عنه وقالوا معلم مجنون (١٤)}','4715','1840','0');
INSERT INTO [t20766] VALUES (1845,'قوله تعالى: {وجاءهم رسول كريم (١٧) أن أدوا إلي عباد الله}','4716','1840','0');
INSERT INTO [t20766] VALUES (1846,'قوله تعالى: {وإني عذت بربي وربكم}','4717','1840','0');
INSERT INTO [t20766] VALUES (1847,'قوله تعالى: {كذلك وأورثناها قوما آخرين (٢٨)}','4717','1840','0');
INSERT INTO [t20766] VALUES (1848,'قوله تعالى: {ولقد نجينا بني إسرائيل من العذاب المهين (٣٠) من فرعون إنه كان عاليا من المسرفين (٣١)}','4717','1840','0');
INSERT INTO [t20766] VALUES (1849,'قوله تعالى: {ثم صبوا فوق رأسه من عذاب الحميم (٤٨)}','4718','1840','0');
INSERT INTO [t20766] VALUES (1850,'قوله تعالى: {فإنما يسرناه بلسانك لعلهم يتذكرون (٥٨)}','4719','1840','0');
INSERT INTO [t20766] VALUES (1851,'سورة الجاثية','4720','0','0');
INSERT INTO [t20766] VALUES (1852,'قوله تعالى: {إن في السماوات والأرض لآيات للمؤمنين (٣) وفي خلقكم وما يبث من دابة آيات لقوم يوقنون (٤) واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون (٥)}','4721','1851','0');
INSERT INTO [t20766] VALUES (1853,'قوله تعالى: {تلك آيات الله نتلوها عليك بالحق}','4729','1851','0');
INSERT INTO [t20766] VALUES (1854,'قوله تعالى: {وإذا علم من آياتنا شيئا اتخذها هزوا أولئك لهم عذاب مهين (٩)}','4736','1851','0');
INSERT INTO [t20766] VALUES (1855,'قوله تعالى: {من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ولهم عذاب عظيم (١٠)}','4737','1851','0');
INSERT INTO [t20766] VALUES (1856,'قوله تعالى: {هذا هدى والذين كفروا بآيات ربهم لهم عذاب من رجز أليم (١١)}','4741','1851','0');
INSERT INTO [t20766] VALUES (1857,'قوله تعالى: {الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون (١٢)}','4744','1851','0');
INSERT INTO [t20766] VALUES (1858,'قوله تعالى: {من عمل صالحا فلنفسه ومن أساء فعليها}','4744','1851','0');
INSERT INTO [t20766] VALUES (1859,'قوله تعالى: {وفضلناهم على العالمين (١٦)}','4744','1851','0');
INSERT INTO [t20766] VALUES (1860,'قوله تعالى: {ثم جعلناك على شريعة من الأمر فاتبعها}','4746','1851','0');
INSERT INTO [t20766] VALUES (1861,'قوله تعالى: {ولا تتبع أهواء الذين لا يعلمون (١٨)}','4746','1851','0');
INSERT INTO [t20766] VALUES (1862,'قوله تعالى: {وإن الظالمين بعضهم أولياء بعض}','4747','1851','0');
INSERT INTO [t20766] VALUES (1863,'قوله تعالى: {والله ولي المتقين (١٩)}','4748','1851','0');
INSERT INTO [t20766] VALUES (1864,'قوله تعالى: {هذا بصائر للناس وهدى ورحمة لقوم يوقنون (٢٠)}','4749','1851','0');
INSERT INTO [t20766] VALUES (1865,'قوله تعالى: {أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات}','4751','1851','0');
INSERT INTO [t20766] VALUES (1866,'قوله تعالى: {أفرأيت من اتخذ إلهه هواه}','4751','1851','0');
INSERT INTO [t20766] VALUES (1867,'قوله تعالى: {وختم على سمعه وقلبه وجعل على بصره غشاوة}','4751','1851','0');
INSERT INTO [t20766] VALUES (1868,'قوله تعالى: {وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا}','4751','1851','0');
INSERT INTO [t20766] VALUES (1869,'قوله تعالى: {ويوم تقوم الساعة يومئذ يخسر المبطلون (٢٧)}','4752','1851','0');
INSERT INTO [t20766] VALUES (1870,'قوله تعالى: {كل أمة تدعى إلى كتابها}','4752','1851','0');
INSERT INTO [t20766] VALUES (1871,'قوله تعالى: {هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون (٢٩)}','4753','1851','0');
INSERT INTO [t20766] VALUES (1872,'قوله تعالى: {وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا}','4753','1851','0');
INSERT INTO [t20766] VALUES (1873,'قوله تعالى: {فاليوم لا يخرجون منها ولا هم يستعتبون (٣٥)}','4753','1851','0');
INSERT INTO [t20766] VALUES (1874,'قوله تعالى: {فلله الحمد رب السماوات ورب الأرض رب العالمين (٣٦)}','4753','1851','0');
INSERT INTO [t20766] VALUES (1875,'قوله تعالى: {وله الكبرياء في السماوات والأرض وهو العزيز الحكيم (٣٧)}','4754','1851','0');
INSERT INTO [t20766] VALUES (1876,'سورة الأحقاف','4756','0','0');
INSERT INTO [t20766] VALUES (1877,'قوله تعالى: {حم (١) تنزيل الكتاب من الله العزيز الحكيم (٢)}','4757','1876','0');
INSERT INTO [t20766] VALUES (1878,'قوله تعالى: {ما خلقنا السماوات والأرض وما بينهما إلا بالحق وأجل مسمى}','4757','1876','0');
INSERT INTO [t20766] VALUES (1879,'قوله تعالى: {والذين كفروا عما أنذروا معرضون (٣)}','4764','1876','0');
INSERT INTO [t20766] VALUES (1880,'قوله تعالى: {قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين (٤)}','4765','1876','0');
INSERT INTO [t20766] VALUES (1881,'قوله تعالى: {ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون (٥) وإذا حشر الناس كانوا لهم أعداء}','4766','1876','0');
INSERT INTO [t20766] VALUES (1882,'قوله تعالى: {وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين (٧)}','4766','1876','0');
INSERT INTO [t20766] VALUES (1883,'قوله تعالى: {أم يقولون افتراه قل إن افتريته فلا تملكون لي من الله شيئا}','4767','1876','0');
INSERT INTO [t20766] VALUES (1884,'قوله تعالى: {قل ما كنت بدعا من الرسل}','4769','1876','0');
INSERT INTO [t20766] VALUES (1885,'قوله تعالى: {وما أدري ما يفعل بي ولا بكم}','4770','1876','0');
INSERT INTO [t20766] VALUES (1886,'قوله تعالى: {قل أرأيتم إن كان من عند الله وكفرتم به}','4772','1876','0');
INSERT INTO [t20766] VALUES (1887,'قوله تعالى: {وشهد شاهد من بني إسرائيل على مثله}','4773','1876','0');
INSERT INTO [t20766] VALUES (1888,'قوله تعالى: {وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه}','4774','1876','0');
INSERT INTO [t20766] VALUES (1889,'قوله تعالى: {وهذا كتاب مصدق لسانا عربيا لينذر}','4776','1876','0');
INSERT INTO [t20766] VALUES (1890,'قوله تعالى: {لينذر الذين ظلموا وبشرى للمحسنين (١٢)}','4776','1876','0');
INSERT INTO [t20766] VALUES (1891,'قوله تعالى: {إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون (١٣)}','4776','1876','0');
INSERT INTO [t20766] VALUES (1892,'قوله تعالى: {ووصينا الإنسان بوالديه إحسانا}','4777','1876','0');
INSERT INTO [t20766] VALUES (1893,'قوله تعالى: {حملته أمه كرها ووضعته كرها}','4777','1876','0');
INSERT INTO [t20766] VALUES (1894,'قوله تعالى: {وحمله وفصاله ثلاثون شهرا}','4779','1876','0');
INSERT INTO [t20766] VALUES (1895,'قوله تعالى: {حتى إذا بلغ أشده وبلغ أربعين سنة}','4779','1876','0');
INSERT INTO [t20766] VALUES (1896,'قوله تعالى: {ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون (٢٠)}','4783','1876','0');
INSERT INTO [t20766] VALUES (1897,'قوله تعالى: {واذكر أخا عاد إذ أنذر قومه بالأحقاف}','4790','1876','0');
INSERT INTO [t20766] VALUES (1898,'قوله تعالى: {ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم (٢١)}','4790','1876','0');
INSERT INTO [t20766] VALUES (1899,'قوله تعالى: {قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين (٢٢)}','4791','1876','0');
INSERT INTO [t20766] VALUES (1900,'قوله تعالى: {وأبلغكم ما أرسلت به}','4791','1876','0');
INSERT INTO [t20766] VALUES (1901,'قوله تعالى: {بل هو ما استعجلتم به ريح فيها عذاب أليم (٢٤)}','4792','1876','0');
INSERT INTO [t20766] VALUES (1902,'قوله تعالى: {ولقد مكناهم فيما إن مكناكم فيه}','4792','1876','0');
INSERT INTO [t20766] VALUES (1903,'قوله تعالى: {فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة بل ضلوا عنهم وذلك إفكهم وما كانوا يفترون (٢٨)}','4794','1876','0');
INSERT INTO [t20766] VALUES (1904,'قوله تعالى: {وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا فلما قضي ولوا إلى قومهم منذرين (٢٩) قالوا ياقومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقا لما بين يديه يهدي إلى الحق وإلى طريق مستقيم (٣٠)}','4794','1876','0');
INSERT INTO [t20766] VALUES (1905,'قوله تعالى: {ياقومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم (٣١)}','4795','1876','0');
INSERT INTO [t20766] VALUES (1906,'قوله تعالى: {أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى بلى إنه على كل شيء قدير (٣٣)}','4802','1876','0');
INSERT INTO [t20766] VALUES (1907,'قوله تعالى: {فاصبر كما صبر أولو العزم من الرسل}','4802','1876','0');
INSERT INTO [t20766] VALUES (1908,'قوله تعالى: {ولا تستعجل لهم}','4803','1876','0');
INSERT INTO [t20766] VALUES (1909,'قوله تعالى: {كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار}','4804','1876','0');
INSERT INTO [t20766] VALUES (1910,'قوله تعالى: {بلاغ}','4804','1876','0');
INSERT INTO [t20766] VALUES (1911,'سورة محمد','4806','0','0');
INSERT INTO [t20766] VALUES (1912,'قوله تعالى: {الذين كفروا وصدوا عن سبيل الله أضل أعمالهم (١) والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم وأصلح بالهم (٢) ذلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم كذلك يضرب الله للناس أمثالهم (٣)}','4807','1911','0');
INSERT INTO [t20766] VALUES (1913,'قوله تعالى: {فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها}','4811','1911','0');
INSERT INTO [t20766] VALUES (1914,'قوله تعالى: {يا أيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم (٧)}','4817','1911','0');
INSERT INTO [t20766] VALUES (1915,'قوله تعالى: {أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين أمثالها (١٠)}','4818','1911','0');
INSERT INTO [t20766] VALUES (1916,'قوله تعالى: {وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم (١٣)}','4818','1911','0');
INSERT INTO [t20766] VALUES (1917,'قوله تعالى: {مثل الجنة التي وعد المتقون فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين}','4819','1911','0');
INSERT INTO [t20766] VALUES (1918,'قوله تعالى: {ولهم فيها من كل الثمرات}','4820','1911','0');
INSERT INTO [t20766] VALUES (1919,'قوله تعالى: {وسقوا ماء حميما فقطع أمعاءهم (١٥)}','4821','1911','0');
INSERT INTO [t20766] VALUES (1920,'قوله تعالى: {فهل ينظرون إلا الساعة أن تأتيهم بغتة}','4821','1911','0');
INSERT INTO [t20766] VALUES (1921,'قوله تعالى: {فأنى لهم إذا جاءتهم ذكراهم (١٨)}','4821','1911','0');
INSERT INTO [t20766] VALUES (1922,'قوله تعالى: {وفإذا أنزلت سورة محكمة وذكر فيها القتال رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت}','4822','1911','0');
INSERT INTO [t20766] VALUES (1923,'قوله تعالى {أفلا يتدبرون القرآن أم على قلوب أقفالها (٢٤)}','4823','1911','0');
INSERT INTO [t20766] VALUES (1924,'وقوله تعالى: {أم على قلوب أقفالها (٢٤)}','4823','1911','0');
INSERT INTO [t20766] VALUES (1925,'قوله تعالى: {ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو أخباركم (٣١)}','4992','1911','0');
INSERT INTO [t20766] VALUES (1926,'قوله تعالى: {إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط أعمالهم (٣٢)}','4995','1911','0');
