INSERT INTO [t20766] VALUES (1945,'قوله تعالى: {يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله واتقوا الله إن الله سميع عليم (١)}','5013','1944','0');
INSERT INTO [t20766] VALUES (1946,'قوله تعالى: {يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون (٢)}','5015','1944','0');
