INSERT INTO [t20766] VALUES (2044,'قوله تعالى: {وكل شيء فعلوه في الزبر (٥٢) وكل صغير وكبير مستطر (٥٣)}','5134','2024','0');
INSERT INTO [t20766] VALUES (2045,'قوله تعالى: {إن المتقين في جنات ونهر (٥٤)}','5135','2024','0');
INSERT INTO [t20766] VALUES (2046,'سورة الرحمن','5136','0','0');
INSERT INTO [t20766] VALUES (2047,'قوله تعالى: {الرحمن (١) علم القرآن}','5137','2046','0');
INSERT INTO [t20766] VALUES (2048,'قوله تعالى: {علم القرآن (٢)}','5137','2046','0');
INSERT INTO [t20766] VALUES (2049,'قوله تعالى: {خلق الإنسان (٣) علمه البيان (٤)}','5139','2046','0');
INSERT INTO [t20766] VALUES (2050,'قوله تعالى: {الشمس والقمر بحسبان (٥)}','5140','2046','0');
INSERT INTO [t20766] VALUES (2051,'قوله تعالى: {والنجم والشجر يسجدان (٦)}','5141','2046','0');
INSERT INTO [t20766] VALUES (2052,'قوله تعالى: {والسماء رفعها ووضع الميزان (٧)}','5142','2046','0');
INSERT INTO [t20766] VALUES (2053,'قوله تعالى: {وأقيموا الوزن بالقسط ولا تخسروا الميزان (٩)}','5142','2046','0');
INSERT INTO [t20766] VALUES (2054,'قوله تعالى: {والأرض وضعها للأنام (١٠) فيها فاكهة والنخل ذات الأكمام (١١) والحب ذو العصف والريحان (١٢)}','5143','2046','0');
INSERT INTO [t20766] VALUES (2055,'قوله تعالى: {خلق الإنسان من صلصال كالفخار (١٤) وخلق الجان من مارج من نار (١٥)}','5151','2046','0');
INSERT INTO [t20766] VALUES (2056,'قوله تعالى: {رب المشرقين ورب المغربين (١٧)}','5152','2046','0');
INSERT INTO [t20766] VALUES (2057,'قوله تعالى: {مرج البحرين يلتقيان (١٩) بينهما برزخ لا يبغيان (٢٠)}','5153','2046','0');
INSERT INTO [t20766] VALUES (2058,'قوله تعالى: {يخرج منهما اللؤلؤ والمرجان (٢٢)}','5153','2046','0');
INSERT INTO [t20766] VALUES (2059,'قوله تعالى: {وله الجوار المنشآت في البحر كالأعلام (٢٤)}','5155','2046','0');
INSERT INTO [t20766] VALUES (2060,'قوله تعالى: {كل من عليها فان (٢٦) ويبقى وجه ربك ذو الجلال والإكرام (٢٧)}','5155','2046','0');
INSERT INTO [t20766] VALUES (2061,'قوله تعالى: {يامعشر الجن والإنس إن استطعتم أن تنفذوا من أقطار السماوات والأرض فانفذوا لا تنفذون إلا بسلطان (٣٣)}','5155','2046','0');
INSERT INTO [t20766] VALUES (2062,'قوله تعالى: {فإذا انشقت السماء فكانت وردة كالدهان (٣٧)}','5156','2046','0');
INSERT INTO [t20766] VALUES (2063,'قوله تعالى: {فيومئذ لا يسأل عن ذنبه إنس ولا جان (٣٩)}','5158','2046','0');
INSERT INTO [t20766] VALUES (2064,'قوله تعالى: {يعرف المجرمون بسيماهم فيؤخذ بالنواصي والأقدام (٤١)}','5160','2046','0');
INSERT INTO [t20766] VALUES (2065,'قوله تعالى: {هذه جهنم التي يكذب بها المجرمون (٤٣) يطوفون بينها وبين حميم آن (٤٤)}','5161','2046','0');
INSERT INTO [t20766] VALUES (2066,'قوله تعالى: {ولمن خاف مقام ربه جنتان (٤٦)}','5161','2046','0');
INSERT INTO [t20766] VALUES (2067,'قوله تعالى: {متكئين على فرش بطائنها من إستبرق}','5162','2046','0');
INSERT INTO [t20766] VALUES (2068,'قوله تعالى: {فيهن قاصرات الطرف}','5163','2046','0');
INSERT INTO [t20766] VALUES (2069,'قوله تعالى: {حور مقصورات في الخيام (٧٢)}','5163','2046','0');
INSERT INTO [t20766] VALUES (2070,'سورة الواقعة','5164','0','0');
INSERT INTO [t20766] VALUES (2071,'قوله تعالى: {إذا وقعت الواقعة (١) ليس لوقعتها كاذبة (٢)}','5165','2070','0');
INSERT INTO [t20766] VALUES (2072,'قوله تعالى: {خافضة رافعة (٣)}','5167','2070','0');
INSERT INTO [t20766] VALUES (2073,'قوله تعالى: {إذا رجت الأرض رجا (٤) وبست الجبال بسا (٥) فكانت هباء منبثا (٦)}','5169','2070','0');
INSERT INTO [t20766] VALUES (2074,'قوله تعالى: {وكنتم أزواجا ثلاثة (٧)}','5171','2070','0');
INSERT INTO [t20766] VALUES (2075,'قوله تعالى: {ثلة من الأولين (١٣) وقليل من الآخرين (١٤)}','5174','2070','0');
INSERT INTO [t20766] VALUES (2076,'قوله تعالى: {على سرر موضونة (١٥) متكئين عليها متقابلين (١٦)}','5177','2070','0');
INSERT INTO [t20766] VALUES (2077,'قوله تعالى: {يطوف عليهم ولدان مخلدون (١٧)}','5178','2070','0');
INSERT INTO [t20766] VALUES (2078,'قوله تعالى: {وكأس من معين (١٨) لا يصدعون عنها ولا ينزفون (١٩)}','5178','2070','0');
INSERT INTO [t20766] VALUES (2079,'قوله تعالى: {وفاكهة مما يتخيرون (٢٠) ولحم طير مما يشتهون (٢١)}','5179','2070','0');
INSERT INTO [t20766] VALUES (2080,'قوله تعالى: {وحور عين (٢٢) كأمثال اللؤلؤ المكنون (٢٣)}','5179','2070','0');
INSERT INTO [t20766] VALUES (2081,'قوله تعالى: {لا يسمعون فيها لغوا ولا تأثيما (٢٥) إلا قيلا سلاما سلاما (٢٦)}','5179','2070','0');
INSERT INTO [t20766] VALUES (2082,'قوله تعالى: {وظل ممدود (٣٠) وماء مسكوب (٣١) وفاكهة كثيرة (٣٢) لا مقطوعة ولا ممنوعة (٣٣)}','5179','2070','0');
INSERT INTO [t20766] VALUES (2083,'قوله تعالى: {إنا أنشأناهن إنشاء (٣٥) فجعلناهن أبكارا (٣٦) عربا أترابا (٣٧) لأصحاب اليمين (٣٨)}','5180','2070','0');
INSERT INTO [t20766] VALUES (2084,'قوله تعالى: {وأصحاب الشمال ما أصحاب الشمال (٤١) في سموم وحميم (٤٢) وظل من يحموم (٤٣)}','5183','2070','0');
INSERT INTO [t20766] VALUES (2085,'قوله تعالى: {إنهم كانوا قبل ذلك مترفين (٤٥) وكانوا يصرون على الحنث العظيم (٤٦)}','5183','2070','0');
INSERT INTO [t20766] VALUES (2086,'قوله تعالى: {وكانوا يقولون أئذا متنا وكنا ترابا وعظاما أإنا لمبعوثون (٤٧)}','5183','2070','0');
INSERT INTO [t20766] VALUES (2087,'قوله تعالى: {قل إن الأولين والآخرين (٤٩) لمجموعون إلى ميقات يوم معلوم (٥٠)}','5189','2070','0');
INSERT INTO [t20766] VALUES (2088,'قوله تعالى: {ثم إنكم أيها الضالون المكذبون (٥١) لآكلون من شجر من زقوم (٥٢) فمالئون منها البطون (٥٣) فشاربون عليه من الحميم (٥٤) فشاربون شرب الهيم (٥٥)}','5190','2070','0');
INSERT INTO [t20766] VALUES (2089,'قوله تعالى: {هذا نزلهم يوم الدين (٥٦)}','5190','2070','0');
INSERT INTO [t20766] VALUES (2090,'قوله تعالى: {نحن خلقناكم فلولا تصدقون (٥٧)}','5191','2070','0');
INSERT INTO [t20766] VALUES (2091,'قوله تعالى: {أفرأيتم ما تمنون (٥٨) أأنتم تخلقونه أم نحن الخالقون (٥٩)}','5192','2070','0');
INSERT INTO [t20766] VALUES (2092,'قوله تعالى: {نحن قدرنا بينكم الموت وما نحن بمسبوقين (٦٠) على أن نبدل أمثالكم وننشئكم في ما لا تعلمون (٦١)}','5194','2070','0');
INSERT INTO [t20766] VALUES (2093,'قوله تعالى: {أفرأيتم ما تحرثون (٦٣) أأنتم تزرعونه أم نحن الزارعون (٦٤) لو نشاء لجعلناه حطاما فظلتم تفكهون (٦٥)}','5197','2070','0');
INSERT INTO [t20766] VALUES (2094,'قوله تعالى: {أفرأيتم الماء الذي تشربون (٦٨) أأنتم أنزلتموه من المزن أم نحن المنزلون (٦٩) لو نشاء جعلناه أجاجا فلولا تشكرون (٧٠)}','5200','2070','0');
INSERT INTO [t20766] VALUES (2095,'قوله تعالى: {أفرأيتم النار التي تورون (٧١) أأنتم أنشأتم شجرتها أم نحن المنشئون (٧٢) نحن جعلناها تذكرة ومتاعا للمقوين (٧٣)}،','5203','2070','0');
INSERT INTO [t20766] VALUES (2096,'قوله تعالى: {فلا أقسم بمواقع النجوم (٧٥) وإنه لقسم لو تعلمون عظيم (٧٦)}','5205','2070','0');
INSERT INTO [t20766] VALUES (2097,'قوله تعالى: {إن هذا لهو حق اليقين (٩٥) فسبح باسم ربك العظيم (٩٦)}','5205','2070','0');
INSERT INTO [t20766] VALUES (2098,'سورة الحديد','5209','0','0');
INSERT INTO [t20766] VALUES (2099,'قوله تعالى: {سبح لله ما في السماوات والأرض وهو العزيز الحكيم (١)}','5210','2098','0');
INSERT INTO [t20766] VALUES (2100,'قوله تعالى: {هو الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش}','5213','2098','0');
INSERT INTO [t20766] VALUES (2101,'قوله تعالى: {يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها}','5214','2098','0');
INSERT INTO [t20766] VALUES (2102,'قوله تعالى: {وهو معكم أين ما كنتم}','5214','2098','0');
INSERT INTO [t20766] VALUES (2103,'قوله تعالى: {هو الذي ينزل على عبده آيات بينات ليخرجكم من الظلمات إلى النور}','5214','2098','0');
INSERT INTO [t20766] VALUES (2104,'قوله تعالى: {ولله ميراث السماوات والأرض}','5215','2098','0');
INSERT INTO [t20766] VALUES (2105,'قوله تعالى: {يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ذلك هو الفوز العظيم (١٢)}','5216','2098','0');
INSERT INTO [t20766] VALUES (2106,'قوله تعالى: {ينادونهم ألم نكن معكم قالوا بلى ولكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتى جاء أمر الله وغركم بالله الغرور (١٤)}','5217','2098','0');
INSERT INTO [t20766] VALUES (2107,'قوله تعالى: {فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا}','5219','2098','0');
INSERT INTO [t20766] VALUES (2108,'قوله تعالى: {ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم وكثير منهم فاسقون (١٦)}','5219','2098','0');
INSERT INTO [t20766] VALUES (2109,'قوله تعالى: {كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما}','5222','2098','0');
INSERT INTO [t20766] VALUES (2110,'قوله تعالى: {ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها إن ذلك على الله يسير (٢٢)}','5222','2098','0');
INSERT INTO [t20766] VALUES (2111,'قوله تعالى: {لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط}','5224','2098','0');
INSERT INTO [t20766] VALUES (2112,'قوله تعالى: {وأنزلنا الحديد فيه بأس شديد}','5224','2098','0');
INSERT INTO [t20766] VALUES (2113,'قوله تعالى: {فمنهم مهتد وكثير منهم فاسقون (٢٦)}','5225','2098','0');
INSERT INTO [t20766] VALUES (2114,'قوله تعالى: {يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم (٢٨)}','5225','2098','0');
INSERT INTO [t20766] VALUES (2115,'قوله تعالى: {وأن الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم (٢٩)}','5225','2098','0');
INSERT INTO [t20766] VALUES (2116,'سورة المجادلة','5227','0','0');
INSERT INTO [t20766] VALUES (2117,'قوله تعالى: {الذين يظاهرون منكم من نسائهم ما هن أمهاتهم}','5228','2116','0');
INSERT INTO [t20766] VALUES (2118,'قوله تعالى: {ألم تر أن الله يعلم ما في السماوات وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم}','5228','2116','0');
INSERT INTO [t20766] VALUES (2119,'قوله تعالى: {ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان}','5228','2116','0');
INSERT INTO [t20766] VALUES (2120,'قوله تعالى: {ألم تر إلى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم}','5229','2116','0');
INSERT INTO [t20766] VALUES (2121,'قوله تعالى: {اتخذوا أيمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين (١٦)}','5230','2116','0');
INSERT INTO [t20766] VALUES (2122,'قوله تعالى: {استحوذ عليهم الشيطان فأنساهم ذكر الله}','5231','2116','0');
INSERT INTO [t20766] VALUES (2123,'قوله تعالى: {إن الذين يحادون الله ورسوله أولئك في الأذلين (٢٠)}','5232','2116','0');
INSERT INTO [t20766] VALUES (2124,'قوله تعالى: {كتب الله لأغلبن أنا ورسلي إن الله قوي عزيز (٢١)}','5232','2116','0');
INSERT INTO [t20766] VALUES (2125,'قوله تعالى: {لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم}','5233','2116','0');
