INSERT INTO [t20866] VALUES (1,'٨٩٩ - قول النبي -صلى الله عليه وسلم-: "أجر الطاعم الشاكر كأجر الصائم"','2','0','0');
INSERT INTO [t20866] VALUES (2,'٩٠٠ - "إحرام المرأة في وجهها"','2','0','0');
INSERT INTO [t20866] VALUES (3,'٩٠١ - قول النبي -صلى الله عليه وسلم-: "أخنع اسم عند الله يوم القيامة: رجل تسمى بملك الأملاك"','3','0','0');
INSERT INTO [t20866] VALUES (4,'٩٠٢ - قول النبي -صلى الله عليه وسلم-: "أد إلى من ائتمنك"','3','0','0');
INSERT INTO [t20866] VALUES (5,'٩٠٣ - "إذا بلغك شيء عن أخيك فاحمله على أحسنه حتى لا تجد له محملا"','4','0','0');
INSERT INTO [t20866] VALUES (6,'٩٠٤ - قول النبي -صلى الله عليه وسلم-: "إذا طبخت قدرا فأكثر ماءه وأهد لجيرانك"','4','0','0');
INSERT INTO [t20866] VALUES (7,'٩٠٥ - قول النبي -صلى الله عليه وسلم-: "إذا لم تستحي: فاصنع ما شئت"','5','0','0');
INSERT INTO [t20866] VALUES (8,'٩٠٦ - قول النبي -صلى الله عليه وسلم-: "أذنت لك أن ترفع الحجاب، وتستمع سوادي، حتى أنهاك"','6','0','0');
INSERT INTO [t20866] VALUES (9,'٩٠٧ - قول الحسن: (أرهقوا القبلة)','6','0','0');
INSERT INTO [t20866] VALUES (10,'٩٠٨ - قول النبي -صلى الله عليه وسلم-: "أقروا الطير على مكناتها"','7','0','0');
INSERT INTO [t20866] VALUES (11,'٩٠٩ - قول النبي -صلى الله عليه وسلم-: "أمرت بقرية تأكل القرى"','8','0','0');
INSERT INTO [t20866] VALUES (12,'٩١٠ - قول النبي -صلى الله عليه وسلم-: "وإن أبا بكر وعمر منهم وأنعما"','8','0','0');
INSERT INTO [t20866] VALUES (13,'٩١١ - قول النبي -صلى الله عليه وسلم-: "إن إبراهيم اختتن بالقدوم"','9','0','0');
INSERT INTO [t20866] VALUES (14,'٩١٢ - قول النبي -صلى الله عليه وسلم-: "إن الله عز وجل يكره عقوق الأمهات. . "','10','0','0');
INSERT INTO [t20866] VALUES (15,'٩١٣ - (إن امرأتي لا تمنع يد لامس. .)','10','0','0');
INSERT INTO [t20866] VALUES (16,'٩١٤ - قول شعبة: (إن هذا الحديث يصدكم عن ذكر الله، وعن الصلاة)','11','0','0');
INSERT INTO [t20866] VALUES (17,'٩١٥ - قول جبير بن مطعم: (إن هذا من الحمس)','11','0','0');
INSERT INTO [t20866] VALUES (18,'٩١٦ - قول علي: (إنهما كفرسي رهان)','11','0','0');
INSERT INTO [t20866] VALUES (19,'٩١٧ - قول النبي -صلى الله عليه وسلم-: "إياكم والغلو، فإنما أهلك من كان قبلكم الغلو"','12','0','0');
INSERT INTO [t20866] VALUES (20,'٩١٨ - قول حكيم بن حزام: (بايعت النبي -صلى الله عليه وسلم- على أن لا أخر إلا قائما)','13','0','0');
INSERT INTO [t20866] VALUES (21,'٩١٩ - قول النبي -صلى الله عليه وسلم-: "البذاذة من الإيمان. . "','13','0','0');
INSERT INTO [t20866] VALUES (22,'٩٢٠ - قول النبي -صلى الله عليه وسلم-: "تراصوا فإني أراكم من خلفي. . "','14','0','0');
INSERT INTO [t20866] VALUES (23,'٩٢١ - قول النبي -صلى الله عليه وسلم-: "تصدقوا ولو بفرسن شاة"','14','0','0');
INSERT INTO [t20866] VALUES (24,'٩٢٢ - (جاء سيل في الجاهلية فكسا ما بين الجبلين)','15','0','0');
INSERT INTO [t20866] VALUES (25,'٩٢٣ - قول النبي -صلى الله عليه وسلم-: "الحج عرفات"','15','0','0');
INSERT INTO [t20866] VALUES (26,'٩٢٤ - حديث النبي -صلى الله عليه وسلم- في الجوار','17','0','0');
INSERT INTO [t20866] VALUES (27,'٩٢٥ - قول النبي -صلى الله عليه وسلم-: "حذف السلام سنة"','18','0','0');
INSERT INTO [t20866] VALUES (28,'٩٢٦ - قول النبي -صلى الله عليه وسلم-: "الحلال بين، والحرام بين"','19','0','0');
INSERT INTO [t20866] VALUES (29,'٩٢٧ - قول عمر بن الخطاب: (خدوا بحظكم من العزلة)','20','0','0');
INSERT INTO [t20866] VALUES (30,'٩٢٨ - قول النبي -صلى الله عليه وسلم-: "زينوا القرآن بأصواتكم"','21','0','0');
INSERT INTO [t20866] VALUES (31,'٩٢٩ - قول عمر: (السائبة والصدقة ليومهما)','21','0','0');
INSERT INTO [t20866] VALUES (32,'٩٣٠ - قول النبي -صلى الله عليه وسلم-: "شر السير الحقحقة"','22','0','0');
INSERT INTO [t20866] VALUES (33,'٩٣١ - قول النبي -صلى الله عليه وسلم-: "الصدقة لا تحل لغني. . "','22','0','0');
INSERT INTO [t20866] VALUES (34,'٩٣٢ - قول معاوية: (قصرت عن النبي -صلى الله عليه وسلم- على المروة بمشقص)','23','0','0');
INSERT INTO [t20866] VALUES (35,'٩٣٣ - (الكالئ بالكالئ)','24','0','0');
INSERT INTO [t20866] VALUES (36,'٩٣٤ - (كانوا لا يختلفون في الأهلة حتى قتل عثمان)','24','0','0');
INSERT INTO [t20866] VALUES (37,'٩٣٥ - قول سفيان: (كره السلم في اللحم)','24','0','0');
INSERT INTO [t20866] VALUES (38,'٩٣٦ - حديث ابن مسعود: (كفانا بالمعك ظلما)','25','0','0');
INSERT INTO [t20866] VALUES (39,'٩٣٧ - قول طاوس: (كفر لا ينقل عن الملة)','25','0','0');
INSERT INTO [t20866] VALUES (40,'٩٣٨ - قول النبي -صلى الله عليه وسلم-: "كفى بالمرء إثما أن يضيع من يقوت"','25','0','0');
INSERT INTO [t20866] VALUES (41,'٩٣٩ - قول النبي -صلى الله عليه وسلم-: "كل قرض جر منفعة حرام"','26','0','0');
INSERT INTO [t20866] VALUES (42,'٩٤٠ - قول النبي -صلى الله عليه وسلم-: "لا إغرار في صلاة ولا تسليم"','27','0','0');
INSERT INTO [t20866] VALUES (43,'٩٤١ - قول النبي -صلى الله عليه وسلم-: "لا أكف شعرا ولا ثوبا"','27','0','0');
INSERT INTO [t20866] VALUES (44,'٩٤٢ - قول النبي -صلى الله عليه وسلم-: "لا تجتمع قبلتان"','28','0','0');
INSERT INTO [t20866] VALUES (45,'٩٤٣ - قول النبي -صلى الله عليه وسلم-: "لا تجوز العوراء، ولا العجفاء. . "','28','0','0');
INSERT INTO [t20866] VALUES (46,'٩٤٤ - قول النبي -صلى الله عليه وسلم-: "لا تزال طائفة من أمتي ظاهرين على الحق. . "','29','0','0');
INSERT INTO [t20866] VALUES (47,'٩٤٥ - قول النبي -صلى الله عليه وسلم-: "لا تغزي قريش بعدها؟ "','29','0','0');
INSERT INTO [t20866] VALUES (48,'٩٤٦ - قول مسروق: (لا تنشر بزك إلا عند من يبغيه)','30','0','0');
INSERT INTO [t20866] VALUES (49,'قول شريح: (لا حبس عن فرائض الله)','30','0','0');
INSERT INTO [t20866] VALUES (50,'٩٤٧ - قول النبي -صلى الله عليه وسلم-: "لا ضرر ولا ضرار"','33','0','0');
INSERT INTO [t20866] VALUES (51,'٩٤٨ - قول النبي -صلى الله عليه وسلم-: "لا فرع ولا عتيرة"','34','0','0');
INSERT INTO [t20866] VALUES (52,'٩٤٩ - قول النبي -صلى الله عليه وسلم-: "لا يبقى دينان بجزيرة العرب"','34','0','0');
INSERT INTO [t20866] VALUES (53,'٩٥٠ - قول النبي -صلى الله عليه وسلم-: "لا يكونن أحدكم إمعة"','35','0','0');
INSERT INTO [t20866] VALUES (54,'٩٥١ - قول عائشة -رضي الله عنها-: (لقست نفسي)','35','0','0');
INSERT INTO [t20866] VALUES (55,'٩٥٢ - قول النبي -صلى الله عليه وسلم-: "لو كان القرآن في إهاب ما مسته النار"','35','0','0');
INSERT INTO [t20866] VALUES (56,'٩٥٣ - قول الحسن: (ليس في الطعام إسراف)','36','0','0');
INSERT INTO [t20866] VALUES (57,'٩٥٤ - قول النبي -صلى الله عليه وسلم-: "ما أذن الله لشيء كإذنه لنبي يتغنى بالقرآن يجهر به"','36','0','0');
INSERT INTO [t20866] VALUES (58,'٩٥٥ - قول النبي -صلى الله عليه وسلم-: "ما بين المشرق والمغرب قبلة"','37','0','0');
INSERT INTO [t20866] VALUES (59,'٩٥٦ - قول إبراهيم: (ما دخر عن قوم خبئ لكم. .)','37','0','0');
INSERT INTO [t20866] VALUES (60,'٩٥٧ - قول يهود: (محمد والخميس)','37','0','0');
INSERT INTO [t20866] VALUES (61,'٩٥٨ - قول النبي -صلى الله عليه وسلم-: "المرأة تموت بجمع"','38','0','0');
INSERT INTO [t20866] VALUES (62,'٩٥٩ - قول النبي -صلى الله عليه وسلم-: "من اتقى الشبهات استبرأ لدينه وعرضه"','38','0','0');
INSERT INTO [t20866] VALUES (63,'٩٦٠ - قول النبي -صلى الله عليه وسلم-: "من أطاع الله، فقد ذكر الله. . "','38','0','0');
INSERT INTO [t20866] VALUES (64,'٩٦١ - حديث عمر: (من جلب إلينا طعاما، فأنا له جار. .)','39','0','0');
INSERT INTO [t20866] VALUES (65,'٩٦٢ - قول النبي -صلى الله عليه وسلم-: "من ستر على أخيه عورة، فكأنما أحيا موءودة"','40','0','0');
INSERT INTO [t20866] VALUES (66,'٩٦٣ - قول النبي -صلى الله عليه وسلم-: "من غسل واغتسل. . "','40','0','0');
INSERT INTO [t20866] VALUES (67,'٩٦٤ - قول النبي -صلى الله عليه وسلم-: "من غشنا فليس منا"','40','0','0');
INSERT INTO [t20866] VALUES (68,'٩٦٥ - قول النبي -صلى الله عليه وسلم-: "من قرأ: {قل هو الله أحد} فكأنما قرأ ثلت القرآن"','41','0','0');
INSERT INTO [t20866] VALUES (69,'٩٦٦ - قول النبي -صلى الله عليه وسلم-: "من لم يتغن بالقرآن"','42','0','0');
INSERT INTO [t20866] VALUES (70,'٩٦٧ - قول النبي -صلى الله عليه وسلم-: "من مات وليس له إمام. . "','42','0','0');
INSERT INTO [t20866] VALUES (71,'٩٦٨ - حديث النبي -صلى الله عليه وسلم- أنه نهى عن اختناث الأسقية','43','0','0');
INSERT INTO [t20866] VALUES (72,'٩٦٩ - حديث النبي -صلى الله عليه وسلم- أنه نهى عن بيع الثنيا حتى تعلم','43','0','0');
INSERT INTO [t20866] VALUES (73,'٩٧٠ - حديث النبي -صلى الله عليه وسلم- أنه نهى عن بيع الغرر','43','0','0');
INSERT INTO [t20866] VALUES (74,'٩٧١ - حديث النبي -صلى الله عليه وسلم- أنه نهى عن لبس الذهب إلا مقطعا','44','0','0');
INSERT INTO [t20866] VALUES (75,'٩٧٢ - قول عطاء: الوصية لا تضمن','44','0','0');
INSERT INTO [t20866] VALUES (76,'٩٧٣ - قول النبي -صلى الله عليه وسلم-: "يبقى حثالة من الناس"','45','0','0');
INSERT INTO [t20866] VALUES (77,'٩٧٤ - قول النبي -صلى الله عليه وسلم-: "يطهره ما بعده"','45','0','0');
INSERT INTO [t20866] VALUES (78,'٩٧٥ - قول النبي -صلى الله عليه وسلم-: "خير لك من حمر النعم"','45','0','0');
