INSERT INTO [t21113] VALUES (1360,'ذكر سهم ذي القربى وذكر الدليل على أن الله جل ثناؤه أراد بقوله: (ولذي القربى) بعض قرابة رسول الله صلى الله عليه وسلم دون بعض','4707','1353','0');
INSERT INTO [t21113] VALUES (1361,'ذكر اختلاف أهل العلو في سهو ذي القربى','4709','1353','0');
INSERT INTO [t21113] VALUES (1362,'ذكر الأخبار الدالة على أن السلب يستحقه القاتل من جملة الغنيمة قبل أن يخمس المال','4717','1353','0');
INSERT INTO [t21113] VALUES (1363,'ذكر اختلاف أهل العلم في هذا الباب','4718','1353','0');
INSERT INTO [t21113] VALUES (1364,'ذكر طالب السلب البينة على أنه القاتل المستحق للسلب','4725','1353','0');
INSERT INTO [t21113] VALUES (1365,'ذكر الخبر الدال على أن القاتل يستحق بالسلب قتله مبارزا أو غير مبارزا','4726','1353','0');
INSERT INTO [t21113] VALUES (1366,'ذكر اختلاف أهل العلم في هذا الباب','4728','1353','0');
INSERT INTO [t21113] VALUES (1367,'ذكر النفر يضربون الرجل ضربات مختلفات','4732','1353','0');
INSERT INTO [t21113] VALUES (1368,'ذكر خبر احتج به من قال: إن الرجلان اشتركا في قتل الرجل أن الخيار في ذلك إلى الإمام يجعل السلب لأيهما شاء','4734','1353','0');
INSERT INTO [t21113] VALUES (1369,'ذكر خبر روي لا أحسبه يثبت أن سلب الأسير يجب لمن أسره','4736','1353','0');
INSERT INTO [t21113] VALUES (1370,'ذكر السلب الذي يستحقه القاتل','4738','1353','0');
INSERT INTO [t21113] VALUES (1371,'ذكر أخبار رويت عن رسول الله صلى الله عليه وسلم في الأنفال مختصرة تدل على معانيها الأخبار التي أنا ذاكرها بعد إن شاء الله تعالى','4743','1353','0');
INSERT INTO [t21113] VALUES (1372,'ذكر الأخبار المفسرة لهذه الأخبار الدالة على أن النفل الذي أجمل ذكره في خبر ابن عمر، وحبيب بن مسلمة، إنما هو نقل السرايا','4746','1353','0');
INSERT INTO [t21113] VALUES (1373,'ذكر اختلاف أهل العلم في نفل هذه السرية التي بعثها رسول الله صلى الله عليه وسلم من أين نفلوا كان الشافعي يقول: حديث ابن عمر يدل على أنهم أعطوا ما لهم مما أصابوا على أنهم نفلوا بعيرا بعيرا، والنفل هو شيء زيدوه غير الذي كان لهم، وقول ابن المسيب: كان','4749','1353','0');
INSERT INTO [t21113] VALUES (1374,'ذكر قدر النفل للسرايا الذي لا تجوز الزيادة عليه في البدأة والقفول، وتفضيل بعض ذلك على بعض','4753','1353','0');
INSERT INTO [t21113] VALUES (1375,'ذكر الخبر الدال على أن الذي كان ينفلهم في البدأة الربع بعد الخمس وفي القفول الثلث بعد الخمس','4755','1353','0');
INSERT INTO [t21113] VALUES (1376,'ذكر الاختلاف في هذا الباب واختلفوا في الإمام ينفل في البدأة الربع، وإذا قفل الثلث، فأباحت طائفة ذلك، وممن رأى أن ينفل الإمام في البدأة الربع بعد الخمس، وفي الرجعة الثلث بعد الخمس حبيب بن مسلمة، والحسن البصري، وبه قال الأوزاعي، وأحمد بن حنبل، وقال','4758','1353','0');
INSERT INTO [t21113] VALUES (1377,'ذكر العطايا التي أعطى رسول الله صلى الله عليه وسلم من غنائم هوزان','4762','1353','0');
INSERT INTO [t21113] VALUES (1378,'ذكر الأخبار المفسرة لهذه الأخبار الدالة على أن رسول الله صلى الله عليه وسلم إنما أعطى تلك العطايا من نصيبه من الخمس، لا من جملة الغنيمة','4766','1353','0');
INSERT INTO [t21113] VALUES (1379,'كتاب قسم أربعة أخماس الغنيمة','4770','0','0');
INSERT INTO [t21113] VALUES (1380,'ذكر قسم الغنائم بين أهل العسكر، وإن اختلفت أفعالهم، وحازها بعض دون بعض قال الله تبارك وتعالى: واعلموا أنما غنمتم من شيء فأن لله خمسه الآية','4771','1379','0');
INSERT INTO [t21113] VALUES (1381,'ذكر جيش يلحقهم ناس لم يشهدوا القتال','4774','1379','0');
INSERT INTO [t21113] VALUES (1382,'ذكر اختلاف أهل العلم في الجيش يلحق جيشا قد غنموا اختلف أهل العلم فيمن لحق بجيش قد غنموا غنائم، فجاؤهم بعد الفراغ من الحرب، فقالت طائفة: لا سهم لهم، واحتجت بقول عمر بن الخطاب: إن الغنيمة لمن شهد الوقعة','4776','1379','0');
INSERT INTO [t21113] VALUES (1383,'ذكر رد السرايا ما تغنم على العسكر جاء الحديث عن رسول الله صلى الله عليه وسلم، أنه قال: " وترد سراياهم على قاعدهم ".','4780','1379','0');
INSERT INTO [t21113] VALUES (1384,'ذكر ما يستحقه الفارس والراجل من السهام قال الله جل ذكره: واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول الآية. فأعلم الله جل وعز في كتابه من يستحق خمس الغنيمة، ولم يذكر في كتابه مستحقي أربعة أخماسها، فتولى رسول الله صلى الله عليه وسلم قسم ذلك وبيان','4782','1379','0');
INSERT INTO [t21113] VALUES (1385,'ذكر الفرسين يكونان مع الفارس الواحد أو أكثر من فرسين أجمع كل من نحفظ عنه من أهل العلم على أن الرجل إذا حضر معه بأفراس في أرض العدو، وأن سهمه وسهم فرس واحد يجب واختلفوا في إعطاء الفارسين لأكثر من سهم فرس واحد، فقالت طائفة: لا يسهم لأكثر من فرس واحد،','4790','1379','0');
INSERT INTO [t21113] VALUES (1386,'ذكر إباحة جمع الإمام للراجل سهم الفارس والراجل إذا وصل إلى فتح بفنائه وقوته','4794','1379','0');
INSERT INTO [t21113] VALUES (1387,'ذكر الهجن والبراذين والإسهام لها أجمع كل من نحفظ عنه من أهل العلم على أن من قاتل أو حضر القتال على العراب من الخيل، أن سهم فارس يجب له واختلفوا فيمن يقاتل على الهجن والبراذين، فقالت طائفة: البراذين والمقاريف يسهم لها سهمان الخيل العربية، لأنها تغني','4796','1379','0');
INSERT INTO [t21113] VALUES (1388,'ذكر غزاة البحر يكون معهم الخيل واختلفوا في الإسهام للخيل في فتح الحصون التي لا تقاتل عليها إلا راجل وفي غزاة البحر يكون مع بعضهم الخيل، فقالت طائفة في غزاة البحر: إذا كان مع بعضهم الخيل أسهم للفارس سهم فارس، وللراجل سهم الراجل، كذلك قال مالك،','4800','1379','0');
INSERT INTO [t21113] VALUES (1389,'ذكر الدابة تموت بعد دخول الجيش أرض العدو قبل القسمة أجمع أهل العلم على أن من قاتل على دابته حتى يغنم الناس ويحوزوا الغنائم، ثم تموت الدابة، أن صاحبها مستحق لسهم الفارس واختلفوا فيمن ماتت دابته قبل ذلك بعد دخولهم أرض العدو، فقالت طائفة: إنما يسهم','4801','1379','0');
INSERT INTO [t21113] VALUES (1390,'ذكر موت الرجل قبل الوقعة أو بعدها واختلفوا فيمن مات بعد دخول بلاد العدو قبل أن تحاز القسمة أو بعد ذلك، فقالت طائفة: إذا حضر القتال ومات بعد أن تحاز الغنيمة ضرب له بسهمه، وأعطي ورثته من بعده، هذا قول الشافعي وأبي ثور، وفي قولهما: إن مات قبل القتال','4804','1379','0');
INSERT INTO [t21113] VALUES (1391,'مسألة قال مالك رحمه الله: إذا أدخل فرسا كسيرا، فلم يكن فيه ما يركب، أو ينتفع به حتى فرغ الناس من الغنائم، فلا يسهم لصاحبه سهم فارس وقال الشافعي: ينبغي للإمام أن يتعاهد الخيل، ولا يدخل إلا شديدا، ولا يدخل حطما، ولا قحما ضعيفا، وأعجفا رازحا، فإن','4807','1379','0');
INSERT INTO [t21113] VALUES (1392,'مسألة قال أبو بكر: فأما من حضر القتال مريضا، أو كان صحيحا ممن لا يقاتل، أو ممن يقاتل فلم يقاتل، فله سهم المقاتل، وهذا قول مالك، والليث بن سعد، والشافعي. وقال سفيان الثوري: كل من حضر القتال يسهم له','4808','1379','0');
INSERT INTO [t21113] VALUES (1393,'ذكر التجار يحضرون القتال قال أبو بكر: وإذا حضر التاجر القتال، قاتل، أو لم يقاتل وجب سهمه كسائر الجيش، وهذا قول الحسن البصري، ومحمد بن سيرين، وسفيان الثوري، والشافعي، وأحمد بن حنبل، وقال الأوزاعي كذلك إلا القديديين، قلت: وما القديديون؟ قال:','4809','1379','0');
INSERT INTO [t21113] VALUES (1394,'ذكر الأجير يحضر الوقعة واختلفوا في الأجير يحضر الحرب، فقالت طائفة: لا يسهم له كذلك قال الأوزاعي عن المستأجر على خدمة القوم لا يسهم له، وقال إسحاق: لا يسهم له، وفيه قول ثان: وهو أن يسهم له إن قاتل، ولا يسهم له إن اشتغل بالخدمة، وهذا قول الليث بن','4810','1379','0');
INSERT INTO [t21113] VALUES (1395,'ذكر اكتراء الدابة غزاة إلى أن رجع الناس واختلفوا في الرجل يكتري الدابة الغزاة بشيء معلوم، فكان مالك بن أنس يقول في الذي يكري دابته إلى الضائعة وهم لا يدرون متى ينصرفون، فقال: قد عرف وجه ذلك، وأرجو أن يكون خفيفا وكان الأوزاعي يقول: يسمي له حين يؤجره','4812','1379','0');
INSERT INTO [t21113] VALUES (1396,'مسألة واختلفوا في الرجل يعطي الرجل فرسه على شطر ما يصيب عليه، فكرهت طائفة ذلك، وممن كره ذلك مالك بن أنس، وقال أحمد بن حنبل في الرجل يعطي فرسه على النصف قال: أرجو ألا يكون بذلك بأس وسئل الأوزاعي عن الرجل يدفع فرسه، أو بغله إلى الرجل يغزو، ويشترط','4813','1379','0');
INSERT INTO [t21113] VALUES (1397,'*','4822','0','0');
INSERT INTO [t21113] VALUES (1398,'ذكر النهي عن الاستعانة بالمشركين على المشركين','4822','1397','0');
INSERT INTO [t21113] VALUES (1399,'ذكر الاختلاف في المشرك يستعان به على العدو واختلفوا فيما يعطاه المشرك إذا استعين به على حرب العدو، فقالت طائفة: يعطون سهاما كسهام المسلمين، كذلك قال الزهري، وعبد الرحمن بن عمرو، والأوزاعي، قيل للأوزاعي: وإن كان معه فرسان، خمسة أسهم؟ ، قال: نعم','4825','1397','0');
INSERT INTO [t21113] VALUES (1400,'ذكر الخبر الدال على أن غير البالغ لا سهم له','4827','1397','0');
INSERT INTO [t21113] VALUES (1401,'ذكر اختلاف أهل العلم فيما يجب لمن حضر ممن لم يبلغ واختلفوا فيما يعطى غير البالغ، إذا حضر القتال، فقالت طائفة: يرضخ لهم، وليس لهم سهم البالغ، كذلك قال الليث بن سعد، وسفيان الثوري، والشافعي، والنعمان، وأبو ثور، وقال أحمد: أرجو أن لا يكون له سهم','4829','1397','0');
INSERT INTO [t21113] VALUES (1402,'ذكر الخبر الدال على أن القسم إنما يجب للأحرار دون العبيد','4830','1397','0');
INSERT INTO [t21113] VALUES (1403,'ذكر الاختلاف في العبيد يحضرون الحرب، وما يعطون اختلف أهل العلم في العبيد يحضرون قسم الغنائم، وقد حضروا الوقعة، فقالت طائفة: يسهم لهم، روينا عن الأسود بن يزيد أنه قال: شهد القادسية عبيد، فضرب لهم سهمانهم، وهذا قول الحسن البصري، وإبراهيم النخعي،','4832','1397','0');
INSERT INTO [t21113] VALUES (1404,'ذكر قدر ما يحذى العبد من الغنيمة','4837','1397','0');
INSERT INTO [t21113] VALUES (1405,'ذكر خبر احتج به من زعم أن النساء لا سهم لهن كسهم الرجال فإنما يجب أن يعطين ما لا خمس فيه من الأطعمة المباحة للناس','4840','1397','0');
INSERT INTO [t21113] VALUES (1406,'ذكر الخبر الذي احتج به من قال: إن سهم النساء إنما زال؛ لأن فرض الجهاد ساقط عنهن','4842','1397','0');
INSERT INTO [t21113] VALUES (1407,'ذكر إباحة خروج النساء في الغزو','4844','1397','0');
INSERT INTO [t21113] VALUES (1408,'ذكر إباحة قتال نساء المسلمين المشركين، ودفعهن إياهم عن أنفسهن','4849','1397','0');
INSERT INTO [t21113] VALUES (1409,'ذكر اختلاف أهل العلم في المرأة تحضر القتال مع الناس واختلفوا في المرأة تحضر القتال مع الرجال، فقالت طائفة: يرضخ لها، وليس لها سهم، روينا هذا القول عن ابن عباس','4851','1397','0');
INSERT INTO [t21113] VALUES (1410,'ذكر الجماعة يدخلون بلاد العدو، ويغنمون بغير إذن الإمام واختلفوا في الواحد، أو الجماعة يدخلون دار الحرب، ويغنم، فقالت طائفة: يخمس، ويكون الباقي لها، أو له، هكذا قول سفيان الثوري، والأوزاعي، والشافعي، وأبي ثور. وفيه قول ثان: وهو أن لا شيء لها','4854','1397','0');
INSERT INTO [t21113] VALUES (1411,'مسألة قال سفيان الثوري في المشركين يدخلون بغير إذن الإمام فيصيبون غنيمة، حالهم في ذلك كحال المسلمين، إذا فعلوا ذلك بغير إذن الإمام قال أبو بكر: وقد قال الثوري في الذي يغير وحده: يخمس ما أخذه، وبقيته له، وقال الأوزاعي: يؤخذ منهم الخمس، وسائره','4855','1397','0');
INSERT INTO [t21113] VALUES (1412,'ذكر المال يغلب عليه العدو، ويستنقذه المسلمون، ثم يدركه صاحبه قبل القسم وبعده اختلف أهل العلم في مال المسلمين يغلب عليه العدو، لم يأخذه المسلمون منهم، فيأتي صاحبه قبل القسم، أو بعده، فقالت طائفة: صاحبه أحق به ما لم يقسم، فإذا أدركه، وقد قسم فهو','4856','1397','0');
INSERT INTO [t21113] VALUES (1413,'ذكر أم الولد تسبى واختلفوا في أم الولد تسبى، ثم يأخذها المسلمون، ويجري فيها القسم، فقالت طائفة: يأخذها سيدها بقيمة عدل، كذلك قال الزهري، وقال مالك: لا أرى أن تسترق، وأرى أن يفيدها الإمام لسيدها، فإن لم يفعل، فأرى على سيدها أن يفديها، ولا يدعها','4865','1397','0');
INSERT INTO [t21113] VALUES (1414,'ذكر الجارية يشتريها الرجل من الغنم، فيجد معها مالا واختلفوا في الجارية تشترى من الغنم، فيجد معها المشتري مالا، فقالت طائفة: يجعل في بيت المال، كذلك قال الشعبي، وفيه قول ثان: وهو أنه يرد إلى مغانم الجيش التي غنموها، هذا قياس قول الشافعي، واستدلال','4866','1397','0');
INSERT INTO [t21113] VALUES (1415,'ذكر قسم الغنائم في دار الحرب واختلفوا في قسم الغنائم في دار الحرب، فكان مالك، والأوزاعي، والشافعي، وأبو ثور يقولون: يقسمها الإمام في دار الحرب إن شاء، وقال أصحاب الرأي: لا ينبغي لإمام المسلمين إذا أصابوا غنائم في دار الحرب، أن يقسموا شيئا من ذلك','4868','1397','0');
INSERT INTO [t21113] VALUES (1416,'ذكر إباحة تأخير الإمام قسم الغنائم إذا غنم من أموال المشركين إلى أن يمكنه قسمها بعد الخروج من دار الحرب','4872','1397','0');
INSERT INTO [t21113] VALUES (1417,'ذكر استئجار الإمام على الغنائم من يحملها ويقوم بحفظها أو سوق رقيق أو حيوان إن كان فيها غنم واختلفوا في استئجار من يحمل الغنائم ويقوم بحفظها، فكان الأوزاعي يقول في الإمام تجتمع عنده الغنائم، فيخاف أن تضيع الغنيمة: لا أعلم بأسا أن يقول: من حرس الليلة','4876','1397','0');
INSERT INTO [t21113] VALUES (1418,'ذكر اختلاف أهل العلم في قسم أشياء مما يغنم مما يختلف في بيعها اختلفت أهل العلم في المصحف من مصاحف المسلمين ويوجد في المغانم، فكان الثوري، والأوزاعي يقولان: إن لم يوجد صاحبه جعل في المغانم فبيع، وفي قول الشافعي: إذا علم أنه مما أخذ من المسلمين يوقف','4878','1397','0');
INSERT INTO [t21113] VALUES (1419,'ذكر بيع الرقيق الذين لم يسلموا من أهل الشرك واختلفوا في بيع السبي الرجال والنساء من أهل الحرب منهم، فقالت طائفة: لا بأس ببيعهم، منهم هذا قول الشافعي في الرجال والنساء يباعون منهم، وكذلك الصبيان إذا كانوا مع آبائهم، وقال مالك: لا بأس ببيعهم من أهل','4887','1397','0');
INSERT INTO [t21113] VALUES (1420,'جماع أبواب الحكم في رقاب أهل العنوة من الأسارى أو الفداء أو القتل','4890','1397','0');
INSERT INTO [t21113] VALUES (1421,'ذكر إثبات الخيار للإمام بين قتل البالغين من رجال العدو، وبين المن، أو الفداء بهم قال الله جل ذكره: حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها الآية.','4891','1397','0');
INSERT INTO [t21113] VALUES (1422,'ذكر خبر ثابت احتج به من استدل من أصحابنا على إثبات الرق على العرب إذا سبوا واختار الإمام استرقاقهم','4894','1397','0');
INSERT INTO [t21113] VALUES (1423,'ذكر الخبر الدال على إباحة إطلاق الأسارى والمن عليهم من غير أخذ مال قال الله: فإما منا بعد وإما فداء الآية.','4897','1397','0');
INSERT INTO [t21113] VALUES (1424,'ذكر إباحة أخذ المال على إطلاق الأسارى','4900','1397','0');
INSERT INTO [t21113] VALUES (1425,'ذكر إباحة إطلاق الأسير على عمل معلوم يعمله، تجوز الإجارة عليه','4904','1397','0');
INSERT INTO [t21113] VALUES (1426,'ذكر خبر احتج به من قال: إن أسر من خرج من المشركين كرها إلى قتال المسلمين مباح','4906','1397','0');
INSERT INTO [t21113] VALUES (1427,'ذكر الخبر الدال على أن حكم من خرج من المشركين كرها إلى القتال حكم من خرج طائعا فيما يؤخذ منه من الفداء','4908','1397','0');
INSERT INTO [t21113] VALUES (1428,'ذكر إباحة إيثاق الأسارى إلى أن يرى الإمام فيهم رأيه','4910','1397','0');
INSERT INTO [t21113] VALUES (1429,'ذكر السنن في الأسارى من المشركين جاءت الأخبار عن رسول الله صلى الله عليه وسلم بأنه سن في الأسارى سننا ثلاثا، المن، والفداء، والقتل، فمما يدل على المن من سنته وقوله في أسارى بدر: " لو كان المطعم بن عدي حيا وكلمني في هؤلاء النتنى لتركتهم له "، ودل','4913','1397','0');
INSERT INTO [t21113] VALUES (1430,'ذكر اختلاف أهل العلم في هذا الباب اختلف أهل العلم في الأسارى، فقالت طائفة: الإمام بالخيار إن شاء من عليهم، وإن شاء قتلهم، وإن شاء فادى بهم، وإن شاء من على بعضهم، وقتل بعضهم، وفادى بعضهم، ولا ينبغي له أن يقتلهم إلا على النظر للمسلمين من تقوية دين','4917','1397','0');
INSERT INTO [t21113] VALUES (1431,'ذكر قول من مال إلى القتل ورأى أنه أعلى من المن والفداء روينا عن أبي بكر الصديق، وليس بثابت عنه أنه قال في أسير: يعطى به كذا وكذا: اقتلوه، قتل رجل من المشركين أحب إلي من كذا وكذا، وكان عمر بن عبد العزيز، وعياض بن عقبة بن نافع يقتلان الأسارى، وقال','4920','1397','0');
INSERT INTO [t21113] VALUES (1432,'ذكر قول من رأى أن أخذ الفداء والمن على الأسير أعلى من القتل قال أبو بكر: من حجة من مال إلى أخذ الفداء والمن على الأسير أعلى من القتل، أن أكثر أسارى بدر أخذ منهم الفداء، وخبر عمران بن الحصين أن النبي صلى الله عليه وسلم فادى الرجل برجلين اللذين أسرا','4927','1397','0');
INSERT INTO [t21113] VALUES (1433,'ذكر الأسير يقتله الرجل من العامة واختلفوا في الأسير يقتله الرجل من العامة، فقالت طائفة: لا شيء عليه، وقد أساء، وليس عليه غرم من قبل أنه لما كان للإمام أن يرسله، ويقتله، ويفادي، وكان حكمه عن حكم الأموال التي ليس للإمام إلا إعطاؤها من أوجف عليه،','4930','1397','0');
INSERT INTO [t21113] VALUES (1434,'ذكر فداء المأسورات بالمال يؤخذ من أهل الحرب مكانهن','4931','1397','0');
INSERT INTO [t21113] VALUES (1435,'ذكر إطلاق الأسير بغير مال من معان جامعة','4933','1397','0');
INSERT INTO [t21113] VALUES (1436,'ذكر النهي عن أخذ الدية لجيفة المشرك','4937','1397','0');
INSERT INTO [t21113] VALUES (1437,'اختلاف أهل العلم في بيع الدرهم بالدرهمين من أهل الحرب واختلفوا في بيع الدرهم بالدرهمين من أهل الحرب، فقالت طائفة: لا يجوز ذلك، هذا قول الأوزاعي، والشافعي، وإسحاق بن راهويه، ويعقوب، وقال يعقوب: وإنما أحل أبو حنيفة هذا؛ لأن بعض المشيخة حدثنا عن','4940','1397','0');
INSERT INTO [t21113] VALUES (1438,'وجوب فكاك الأسارى من أيدي المشركين','4941','1397','0');
INSERT INTO [t21113] VALUES (1439,'ذكر الأخبار عن الأوائل في هذا الباب','4946','1397','0');
INSERT INTO [t21113] VALUES (1440,'ذكر ما يجب من حياطة أهل الذمة ومنهم مما يجب منه منع المسلمين','4949','1397','0');
INSERT INTO [t21113] VALUES (1441,'ذكر الحكم في الرجل من المسلمين يشتري أسيرا من أسرى المسلمين من العدو بإذن الأسير أو بغير إذنه أجمع كل من نحفظ عنه من أهل العلم على أن الرجل اشترى أسيرا من أسرى المسلمين من العدو بأمره بمال معلوم، ودفع عنه بأمره، أن له أن يرجع بذلك عليه. واختلفوا فيه','4951','1397','0');
INSERT INTO [t21113] VALUES (1442,'ذكر الأسير يرسله العدو على أن يجيئهم بمال أو يبعث به إليهم واختلفوا في الأسير يشتري نفسه من العدو، على أن يبعث بالثمن إليهم. فقالت طائفة: يفي لهم كذلك، قال الحسن البصري، وقال عطاء بن أبي رباح في هذا: يفي لهم، وكذلك، قال أحمد بن حنبل، وقال','4954','1397','0');
INSERT INTO [t21113] VALUES (1443,'ذكر رقيق أهل الحرب يخرجون إلى دار الإسلام','4958','1397','0');
INSERT INTO [t21113] VALUES (1444,'ذكر التفرقة بين الجماعة من السبي يصيرون في ملك الرجل من المسلمين جاء الحديث عن رسول الله صلى الله عليه وسلم أنه قال: " من فرق بين الوالدة وولدها فرق الله بينه وبين أحبته يوم القيامة "','4962','1397','0');
INSERT INTO [t21113] VALUES (1445,'ذكر الأخبار التي روينا عن الأوائل في هذا الباب','4964','1397','0');
INSERT INTO [t21113] VALUES (1446,'ذكر التفرقة بين الإخوة وبين سائر القرابات اختلف أهل العلم في التفرقة بين الإخوة وكل ذي محرم من الرجال والنساء. فقالت طائفة: لا يفرق بين شيء من السبي، وكذلك قال أحمد بن حنبل، قيل له: والصغير والكبير، والذكر والأنثى سواء؟ قال: نعم','4970','1397','0');
INSERT INTO [t21113] VALUES (1447,'جماع أبواب الأمان','4974','0','0');
INSERT INTO [t21113] VALUES (1448,'ذكر الخبر الدال على أن دم الكافر يحرم بأن يعطيه الأمان رجل من العامة غير الإمام','4975','1447','0');
INSERT INTO [t21113] VALUES (1449,'ذكر خبر يوافق ظاهر خبر أبي عبيدة بإجازة أمان كل مسلم كان أدناهم أو أفضلهم','4978','1447','0');
INSERT INTO [t21113] VALUES (1450,'ذكر إجازة أمان العبد المسلم وإن لم يقاتل','4980','1447','0');
INSERT INTO [t21113] VALUES (1451,'ذكر أمان العبد أجمع أهل العلم أن أمان والي الجيش أو الرجل الحر الذي يقاتل جائز على جمعهم. واختلفوا في أمان العبد فأجازت طائفة، ممن أجاز ذلك عمر بن الخطاب:','4984','1447','0');
INSERT INTO [t21113] VALUES (1452,'ذكر أمان المرأة','4986','1447','0');
INSERT INTO [t21113] VALUES (1453,'ذكر ما حفظناه عن أهل العلم في هذا الباب أجمع عامة من تحفظ عنه من أهل العلم على أن أمان المرأة الحرة جائز " وقالت عائشة: إن كانت المرأة لتجير على المسلمين " فيجوز','4990','1447','0');
INSERT INTO [t21113] VALUES (1454,'ذكر أمان الذمي أجمع أكثر من نحفظ من أهل العلم على أن أمان الذمي لا يجوز، كذلك قال الأوزاعي، والليث بن سعد، وسفيان الثوري، والشافعي، وأحمد، وإسحاق، وأصحاب الرأي. قال أبو بكر: وكذلك نقول: ولو قال قائل: إن في قول رسول الله صلى الله عليه وسلم:','4992','1447','0');
INSERT INTO [t21113] VALUES (1455,'ذكر أمان الصبي وأجمع كل من نحفظ عنه من أهل العلم أن أمان الصبي غير جائز، وممن حفظت عنه ذلك سفيان الثوري، والأوزاعي، والشافعي، وأحمد، وإسحاق، وأصحاب الرأي','4993','1447','0');
INSERT INTO [t21113] VALUES (1456,'ذكر الإشارة بالأمان','4994','1447','0');
INSERT INTO [t21113] VALUES (1457,'ذكر إعطاء الأمان بأي لغة تفهم أعطوا بها الأمان','4996','1447','0');
INSERT INTO [t21113] VALUES (1458,'ذكر أمان الأسير والتاجر كان سفيان الثوري يقول في أمان الأسير، والتاجر من المسلمين في أرض الحرب يؤمنان المشركين: لا يجوز أمانهما على المسلمين. وقال أحمد في أمان الأجير جائز، وقيل لأحمد: لو أن أسرى في عمورية نزل بهم المسلمون، فقال الأسرى: أنتم آمنون','4999','1447','0');
INSERT INTO [t21113] VALUES (1459,'ذكر المشرك يطلب الأمان ليسمع كتاب الله، وشرائع الإسلام، أيرد هذا، ومن أشبهه إلى مأمنه قال الله جل ذكره: وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه الآية. روينا عن قتادة، أنه قال في قوله: حتى يسمع كلام الله أي كتاب الله،','5000','1447','0');
INSERT INTO [t21113] VALUES (1460,'ذكر الحربي يصاب في دار الإسلام ويقول: جئت مستأمنا اختلف أهل العلم في الحربي يوجد في دار الإسلام، ويقول: جئت مستأمنا، فقالت طائفة: الإمام في ذلك بالخيار يرى فيهم رأيه، كذلك قال مالك بن أنس، وقال الأوزاعي: إذا وجد ليس معه سلاح فأمره إلى الإمام، إن','5002','1447','0');
INSERT INTO [t21113] VALUES (1461,'ذكر أمان الرجل الرجل ثم يخفى ويشتبه ذلك واختلفوا في العلج يشرف من حصن، فيؤمن، ثم لما فتح الباب، ادعى كل واحد منهم أنه الذي أمن. فقالت طائفة: لا يقتل أحد منهم، هذا قول أحمد بن حنبل، وكان الشافعي يقول: وإذا وادع الإمام قوما من أهل الحرب فنقض بعضهم','5006','1447','0');
INSERT INTO [t21113] VALUES (1462,'ذكر الحربي يسلم في دار الحرب وله بها مال واختلفوا في الحربي يسلم في دار الحرب وله بها مال، ثم يظهر المسلمون على تلك الدار. فقالت طائفة: يترك له ما كان في يديه من ماله، ورقيقه، ومتاعه، وولد صغار، وما كان من أرض فهو فيء، وامرأته فيء إذا كانت كافرة','5008','1447','0');
INSERT INTO [t21113] VALUES (1463,'مسألة قال النعمان في المستأمن إذا مات في دار الإسلام وترك مالا، وورثة في دار الحرب: يوقف حتى يقدم ورثته، قيل له: فإن جاء الورثة مستأمنين بكتاب من ملك أرضهم أنهم من الورثة، هل يقبض ذلك؟ قال: لا، قيل له: فإن كان في الكتاب أنه قد ثبت الشهود عنده،','5010','1447','0');
INSERT INTO [t21113] VALUES (1464,'ذكر الشهادة على الأمان واختلفوا في شهادة الشاهد الواحد على أمان الحربي. فقالت طائفة: إذا قال رجل من المسلمين: إني قد أمنتهم، جاز أمانة عليهم، فإن رسول الله صلى الله عليه وسلم قال: " ويعقد عليهم أدناهم " ولم يقل إن جاءوا على ذلك ببينة، وإلا فلا','5011','1447','0');
INSERT INTO [t21113] VALUES (1465,'ذكر العلج يضمن له أن يعطى كذا على أن يفتح باب حصن أو يدل عليه، ووجوب الوفاء له به قال أبو بكر: إذا قال العلج للإمام: أفتح لكم باب هذا الحصن على أن لي كذا وكذا الشيء يذكره، فمما يجوز ملكه معلوما ففتحه على ذلك، فعلى الإمام أن يفي له بذلك، ولم أحفظ عن','5012','1447','0');
INSERT INTO [t21113] VALUES (1466,'مسألة واختلفوا في المشرك يخرج إلينا بأمان، ثم يسلم فغزا المسلمون تلك الدار، فأصابوا أهله وماله. فقالت طائفة: أهله وماله فيء المسلمين، كذلك قال مالك بن أنس، وقال الليث بن سعد مثله في صبية صغار، وكبار تركه الرجل الذي أتى فأسلم ببلاد العدو، قال: ما','5014','1447','0');
INSERT INTO [t21113] VALUES (1467,'ذكر المستأمن يسرق أو يزني أو يصيب حدا واختلفوا في المستأمن يسرق، أو يقذف، أو يزني، أو يصيب بعض الحدود، فكان الشافعي يقول: ذلك وجهان ما كان منها لله لا حق للآدميين فيه، يكون لهم عفوها، وإكذاب شهود لو شهدوا لهم به، فهو معطل عنهم؛ لأنه لا حق فيه','5016','1447','0');
INSERT INTO [t21113] VALUES (1468,'ذكر إقامة الحدود في دار الحرب اختلف أهل العلم في إقامة الحدود في دار الحرب فمنعت طائفة من ذلك','5018','1447','0');
INSERT INTO [t21113] VALUES (1469,'ذكر إسلام رقيق أهل الذمة قال أبو بكر: أجمع عامة من أحفظ عنه من أهل العلم على أن رقيق أهل الذمة إذا أسلموا بيعوا عليهم. روينا هذا القول عن عمر بن عبد العزيز، والحسن، والنخعي، والشعبي، وبه قال مالك، والليث بن سعد، والشافعي، وأحمد، وأبو ثور،','5020','1447','0');
INSERT INTO [t21113] VALUES (1470,'ذكر الرجل من المسلمين يطلع عليه أنه عين المشركين، قد كتب بأخبار المسلمين إليهم واختلفوا فيما يفعل بالرجل من المسلمين قد كاتب المشركين، وأخبرهم بأخبار المسلمين، فكان مالك بن أنس يقول: ما سمعت فيه بشيء، وأرى فيه اجتهاد الإمام، وقال الأوزاعي في جاسوس','5022','1447','0');
INSERT INTO [t21113] VALUES (1471,'ذكر المستأمن يطلع عليه أنه عين للمشركين يكتب إليهم بأخبار المسلمين قد ذكرت قول الأوزاعي فيما مضى في هذه المسألة، أنه إن كان ذميا قتل، فإنه قد نقض عهده. قال الشافعي: في الذي يكتب بعورة المسلمين، أو يخبر عنهم، بأنهم أرادوا بالعدو شيئا، ليحذروه يعزر','5025','1447','0');
INSERT INTO [t21113] VALUES (1472,'ذكر أم ولد الحربي تسلم وتخرج إلى دار الإسلام قال أبو بكر: قد ذكرنا فيما مضى أن النبي صلى الله عليه وسلم أعتق يوم الطائف من خرج إليه من رقيق المشركين، وذكرنا أن كل من نحفظ عنه من أهل العلم قال به. واختلفوا في أم ولد الحربي تسلم في دار الحرب، ثم تخرج','5027','1447','0');
INSERT INTO [t21113] VALUES (1473,'مسألة قال النعمان في امرأة أسلمت من أهل الحرب، وخرجت إلى دار الإسلام، وليست بحبلى: إنه لا عدة عليها، ولو أن زوجها طلقها، لم يقع عليها طلاقه. وقال الأوزاعي: بلغنا أن المهاجرات قدمن على رسول الله صلى الله عليه وسلم وأزواجهن بمكة مشركون، فمن أسلم','5028','1447','0');
INSERT INTO [t21113] VALUES (1474,'ذكر النهي عن السفر، بالقرآن إلى أرض المشرك','5030','1447','0');
INSERT INTO [t21113] VALUES (1475,'ذكر وطء الرجل جارية يشتريها في دار الحرب واختلفوا في وطء الرجل أمته التي يبتاعها في أرض الحرب، فأباحت طائفة وطأها على ظاهر قوله: " والذين هم لفروجهم حافظون إلا على أزواجهم أو ما ملكت أيمانهم الآية، ومن أباح ذلك مالك، والأوزاعي، والشافعي، وسئل مالك','5033','1447','0');
INSERT INTO [t21113] VALUES (1476,'ذكر وطء الرجل زوجته وأم ولده اللتين قد سباهما العدو واختلفوا في وطء الرجل زوجته أو أم ولده إذا أمكنه وطؤهما وهما بأيدي العدو، فقالت طائفة: لا بأس أن يطأهما إذا لقيهما، هذا قول النعمان. وكان الأوزاعي يقول: إذا أحرزهم عدو كانوا أقدر على فروجهن سرا','5035','1447','0');
INSERT INTO [t21113] VALUES (1477,'ذكر المسلم يدخل دار الحرب بأمان فيغدر واختلفوا في الأسير المسلم في الحرب، أو المسلم يدخل دار الحرب بأمان هل له أن يأخذ من أموالهم أم لا؟ فكان الشافعي يقول: معروف عندهم في أمانهم إياه، وهم قادرون عليه، أنه يلزمه لهم، أن يكونوا مثله آمنين، وهذا على','5036','1447','0');
INSERT INTO [t21113] VALUES (1478,'مسألة قال النعمان في رجل مسلم دخل دار الحرب بأمان، فأدانه حربي دينا، ثم خرجا إلينا، خرج الحربي مستأمنا , فأراد الحربي أن يأخذه بماله. قال: لا يقضى له على المسلم بدينه، وكذلك لو كان المسلم هو أدان الحربي دينا، كان سواء، ولم يقض له على الحربي بدين','5038','1447','0');
INSERT INTO [t21113] VALUES (1479,'جماع أبواب الصلح والعهود الجائزة بين أهل الإسلام وأهل الشرك سوى أهل الكتاب','5039','1447','0');
INSERT INTO [t21113] VALUES (1480,'ذكر مصالحة الإمام أهل الشرك على أن يتركوا مالهم , ولا يتعرضوا لأموالهم وذراريهم من غير مال يؤخذ منهم ولا جزية','5040','1447','0');
INSERT INTO [t21113] VALUES (1481,'ذكر إباحة موادعة عبدة الأوثان','5050','1447','0');
INSERT INTO [t21113] VALUES (1482,'ذكر أخبار رويت في الوفاء بالعهد','5053','1447','0');
INSERT INTO [t21113] VALUES (1483,'ذكر النهي عن التأهب لقتال من بين المسلمين وبينهم عهد مدة حتى تنقضي المدة','5055','1447','0');
INSERT INTO [t21113] VALUES (1484,'ذكر تخوف الفتنة وظهور القتل إذا نقض العهد','5059','1447','0');
INSERT INTO [t21113] VALUES (1485,'ذكر إباحة دم المعاهد، وسبي ذراريه، وأخذ أمواله إذا نقض العهد','5061','1447','0');
INSERT INTO [t21113] VALUES (1486,'ذكر ما يكون نقضا للعهد وما لا يكون نقضا له قال أبو بكر: كانت قريظة قد عاهدت النبي صلى الله عليه وسلم، فلما نزل أبو سفيان ومن معه من قريش وغطفان بقرب المدينة، أتى حيي بن أخطب النصري كعب بن أسد القرظي، صاحب عقد بني قريظة وعهدهم، فلم يزل به حتى أجابه','5063','1447','0');
INSERT INTO [t21113] VALUES (1487,'ذكر الصلح والهدنة بين المسلمين والمشركين إلى مدة من المدد اختلف أهل العلم في المدة التي كانت بين رسول الله صلى الله عليه وسلم وبين أهل مكة','5067','1447','0');
INSERT INTO [t21113] VALUES (1488,'ذكر مصالحة الإمام أهل الشرك على مال يقبضه منهم في كل عام أو على مال يعطيهم، وما يجوز من ذلك وما لا يجوز قال الشافعي: وإذا ضعف المسلمون عن قتال المشركين، أو طائفة منهم، جاز لهم الكف عنهم، ومهادنتهم على غير شيء يأخذونه من المشركين، ولا خير في أن','5071','1447','0');
INSERT INTO [t21113] VALUES (1489,'ذكر ما يجوز من الشروط بين الإمام وبين العدو، وما روي في هذا الباب من الأخبار','5074','1447','0');
INSERT INTO [t21113] VALUES (1490,'ذكر نساء المهاجرين قال الله عز وجل: واسألوا ما أنفقتم وليسألوا ما أنفقوا ذلكم حكم الله يحكم بينكم والله عليم حكيم الآية. قال أبو بكر: فاحتمل قوله: واسألوا ما أنفقتم وليسألوا ما أنفقوا من النفقات، واحتمل الصداق الذي أعطوه، فوجدنا قول جماعة من أهل','5078','1447','0');
INSERT INTO [t21113] VALUES (1491,'ذكر من قال إن الآية منسوخة كان قتادة يقول في قوله: " وإن فاتكم شيء من أزواجكم إلى الكفار الآية، يقول: إلى كفار ليس بينهم وبين أصحاب محمد عهد يأخذون به، فعاقبتم، وهي الغنيمة إذا أغنموا أن يعطوا زوجها صداقه الذي كان ساق من الغنيمة، ثم يقسم الغنيمة','5081','1447','0');
INSERT INTO [t21113] VALUES (1492,'ذكر إباحة إعطاء الإمام العهد والأمان من قد غلب على أرضيهم، وهو مشرف على فتح حصونهم','5082','1447','0');
INSERT INTO [t21113] VALUES (1493,'ذكر ما يصالح عليه الإمام أهل الذمة قبل محاربتهم','5084','1447','0');
