INSERT INTO [t21485] VALUES (1,'لا تغتابوا المسلمين، ولا تتبعوا عوراتهم، فإنه من يتبع عورة أخيه اتبع الله عورته، ومن اتبع','4','0','0');
INSERT INTO [t21485] VALUES (2,'أشد الناس عذابا يوم القيامة أصحاب المائدة، وآل فرعون، والمنافقون» .','5','0','0');
INSERT INTO [t21485] VALUES (3,'أسفل أهل النار المنافقون، الذين في الدرك الأسفل من النار»','6','0','0');
INSERT INTO [t21485] VALUES (4,'" الإيمان هاهنا، وأشار بيده إلى لسانه، والنفاق هاهنا، ووضع يده على صدره، ولا نذكر الله إلا','7','0','0');
INSERT INTO [t21485] VALUES (5,'اللهم جنبني منكرات الأخلاق، والأهواء، والأدواء»','8','0','0');
INSERT INTO [t21485] VALUES (6,'اللهم اهدني لأحسن الأعمال وأحسن الأخلاق، فإنه لا يهدي لأحسنها إلا أنت، وقني سيء الأعمال','9','0','0');
INSERT INTO [t21485] VALUES (7,'اللهم إني أعوذ بك من النفاق، وسوء الأخلاق» قال الله تعالى: {وما أصابكم يوم التقى الجمعان','10','0','0');
INSERT INTO [t21485] VALUES (8,'كنت جالسا مع عبد الله بن أبي، فمر رسول الله صلى الله عليه وسلم في أناس من أصحابه، فقال','11','0','0');
INSERT INTO [t21485] VALUES (9,'كنت مع رسول الله صلى الله عليه وسلم " في غزوة تبوك، فقال عبد الله بن أبي: {لئن رجعنا إلى','12','0','0');
INSERT INTO [t21485] VALUES (10,'لما قال ابن أبي ما قال، أتيت رسول الله صلى الله عليه وسلم، فأخبرته، فجاء فحلف ما قال،','14','0','0');
INSERT INTO [t21485] VALUES (11,'غزونا مع رسول الله صلى الله عليه وسلم ومعنا ناس من الأعراب، وكنا نبتدر الماء، وكان','15','0','0');
INSERT INTO [t21485] VALUES (12,'خرجنا مع رسول الله صلى الله عليه وسلم في سفر أصاب الناس فيه شدة، فقال عبد الله بن أبي','17','0','0');
INSERT INTO [t21485] VALUES (13,'لما توفي عبد الله بن أبي بن سلول جاء ابنه عبد الله بن عبد الله إلى رسول الله صلى الله','21','0','0');
INSERT INTO [t21485] VALUES (14,'لما توفي عبد الله بن أبي بن سلول دعي رسول الله صلى الله عليه وسلم، فقام إليه، فلما وقف','23','0','0');
INSERT INTO [t21485] VALUES (15,'أراد أن يصلي على عبد الله بن أبي، فأخذ جبريل عليه السلام بثوبه، فقال: {ولا تصل على أحد','24','0','0');
INSERT INTO [t21485] VALUES (16,'رأيت الدخان يخرج من مسجد الضرار»','25','0','0');
INSERT INTO [t21485] VALUES (17,'المسجد الذي أسس على التقوى، فقال أحدهما: مسجد المدينة. وقال الآخر: مسجد قباء، فأتوا النبي','26','0','0');
INSERT INTO [t21485] VALUES (18,'المسجد الذي أسس على التقوى، قال: مسجدي "','27','0','0');
INSERT INTO [t21485] VALUES (19,'رأيت عبد الله بن أبي يشتد بين يدي رسول الله صلى الله عليه وسلم والحجارة تنكب رجليه، وهو','28','0','0');
INSERT INTO [t21485] VALUES (20,'مثل المنافق كمثل الشاة العائرة بين الغنمين، في هذه مرة، وهذه مرة، لا تدري في أيهما تتبع» .','29','0','0');
INSERT INTO [t21485] VALUES (21,'مثل المنافق مثل الشاة بين الغنمين»','30','0','0');
INSERT INTO [t21485] VALUES (22,'مثل المنافق مثل الشاة بين الغنمين، ينطحها هذه مرة، وهذه مرة» . فقال ابن عمر: بين الربيضين،','31','0','0');
INSERT INTO [t21485] VALUES (23,'إذا سمع من رسول الله صلى الله عليه وسلم حديثا، أو شهد مشهدا لا يحب أن يجاوزه إلى','32','0','0');
INSERT INTO [t21485] VALUES (24,'إذا سمع من نبي الله صلى الله عليه وسلم شيئا، أو شهد معه مشهدا لم يقصر دونه. . . . .» وذكر','33','0','0');
INSERT INTO [t21485] VALUES (25,'القتلة ثلاثة رجال: رجل مؤمن يجاهد بنفسه وماله في سبيل الله، حتى إذا لقي العدو قاتلهم حتى','34','0','0');
INSERT INTO [t21485] VALUES (26,'يبعث كل عبد على ما مات عليه» . ورواه أبو معاوية، عن الأعمش مثله','36','0','0');
INSERT INTO [t21485] VALUES (27,'اللهم طهر قلبي من النفاق، وعملي من الرياء، ولساني من الكذب، وعيني من الخيانة، فإنك تعلم','37','0','0');
INSERT INTO [t21485] VALUES (28,'ثلاث من كن فيه فهو منافق وإن صلى وصام وزعم أنه مسلم: من إذا حدث كذب، وإذا وعد أخلف، وإذا','39','0','0');
INSERT INTO [t21485] VALUES (29,'إن المنافق إذا حدث كذب، وإذا ائتمن خان، وإذا وعد أخلف» .','41','0','0');
INSERT INTO [t21485] VALUES (30,'إن المنافق إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان»','42','0','0');
INSERT INTO [t21485] VALUES (31,'من علامات المنافق ثلاث: إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان "','43','0','0');
INSERT INTO [t21485] VALUES (32,'ثلاث في المنافق: إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان "','43','0','0');
INSERT INTO [t21485] VALUES (33,'ثلاث من كن فيه فهو منافق وإن صام وزعم أنه مؤمن: من إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن','44','0','0');
INSERT INTO [t21485] VALUES (34,'ثلاث من كن فيه فهو منافق وإن صام وصلى وحج وزعم أنه مؤمن: إذا حدث كذب، وإذا وعد أخلف، وإذا','45','0','0');
INSERT INTO [t21485] VALUES (35,'ثلاث من كن فيه فهو منافق: من إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان، قال: فقالوا: يا','45','0','0');
INSERT INTO [t21485] VALUES (36,'ثلاث من كن فيه كان منافقا، وإن كانت فيه خصلة منهن كانت فيه خصلة من النفاق، حتى يدعها: إذا','46','0','0');
INSERT INTO [t21485] VALUES (37,'آية المنافق ثلاث: إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان ".','47','0','0');
INSERT INTO [t21485] VALUES (38,'أربع من كن فيه فهو منافق، وإن كان فيه واحدة منهن كان فيه خصلة من النفاق حتى يدعها: من إذا','47','0','0');
INSERT INTO [t21485] VALUES (39,'أربع خلال من كن فيه كان منافقا خالصا: من إذا حدث كذب، وإذا وعد أخلف، وإذا عاهد غدر، وإذا','50','0','0');
INSERT INTO [t21485] VALUES (40,'اعتبروا المنافق بثلاث: إذا حدث كذب، وإذا وعد أخلف، وإذا عاهد غدر، وذلك قول الله عز وجل:','51','0','0');
INSERT INTO [t21485] VALUES (41,'أربع من كن فيه كان منافقا خالصا: من إذا حدث كذب، وإذا ائتمن خان، وإذا عاهد غدر، وإذا خاصم','52','0','0');
INSERT INTO [t21485] VALUES (42,'ثلاثة من أخلاق المنافق: إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمنته خانك "','53','0','0');
INSERT INTO [t21485] VALUES (43,'ثلاث من كن فيه فهو منافق، وإن صام وصلى وحج واعتمر وزعم أنه مسلم: من إذا حدث كذب، وإذا وعد','54','0','0');
INSERT INTO [t21485] VALUES (44,'ثلاث من كن فيه فهو منافق وإن صام وصلى وزعم أنه مسلم: من إذا حدث كذب، وإذا وعد أخلف، وإذا','54','0','0');
INSERT INTO [t21485] VALUES (45,'ثلاث من كن فيه فهو منافق وإن صام وصلى وتصدق وزعم أنه مسلم: من إذا حدث كذب، وإذا وعد أخلف،','55','0','0');
INSERT INTO [t21485] VALUES (46,'ثلاث من كن فيه فهو منافق، وإن صام وصلى وزعم أنه مسلم: من إذا حدث كذب، وإذا وعد أخلف، وإذا','56','0','0');
INSERT INTO [t21485] VALUES (47,'{فما لكم في المنافقين فئتين} [النساء: ٨٨] ، قال " كان ناس من المنافقين وأصحاب النبي صلى','59','0','0');
INSERT INTO [t21485] VALUES (48,'الحياء والعي شعبتان من الإيمان، والبذاء والبيان شعبتان من النفاق»','60','0','0');
INSERT INTO [t21485] VALUES (49,'لا يسمع النداء أحد في مسجدي هذا، ثم يخرج منه، إلا لحاجة، ثم لا يرجع إلا','61','0','0');
INSERT INTO [t21485] VALUES (50,'من أدركه الأذان في المسجد، ثم خرج لم يخرج لحاجة وهو لا يريد الرجعة، فهو','62','0','0');
INSERT INTO [t21485] VALUES (51,'أثقل الصلاة على المنافقين صلاة العشاء وصلاة الفجر، ولو يعلمون ما فيها لأتوهما ولو','63','0','0');
INSERT INTO [t21485] VALUES (52,'ما شهدهما منافق، يعني: صلاة الصبح والعشاء ". قال أبو بشر: يعني: لا يواظب','64','0','0');
INSERT INTO [t21485] VALUES (53,'تلك صلاة المنافقين، تلك صلاة المنافقين، تلك صلاة المنافقين، يجلس أحدهم حتى إذا اصفرت','65','0','0');
INSERT INTO [t21485] VALUES (54,'أرواح المؤمنين في الهواء أجناد مجندة تلتقي في الهواء، فتشام كما يتشام الخيل، فما تعارف','67','0','0');
INSERT INTO [t21485] VALUES (55,'أمارة المنافق الذي لا تسؤه سيئته، ولا تسره حسنته، إن عمل خيرا لم يرجو من الله في ذلك','69','0','0');
INSERT INTO [t21485] VALUES (56,'الشح والفحش والبذاء من النفاق، وإنهن يزدن في الدنيا، وينقصن من الآخرة، وما ينقصن من','70','0','0');
INSERT INTO [t21485] VALUES (57,'للمنافقين علامات يعرفون بها: تحيتهم لعنة، وطعامهم نهبة، وغنيمتهم غلول، ولا يأتون المساجد','71','0','0');
INSERT INTO [t21485] VALUES (58,'أنه لا يحبك إلا مؤمن، ولا يبغضك إلا منافق»','77','0','0');
INSERT INTO [t21485] VALUES (59,'أمي فاطمة اشترك في حبها الكافر والمؤمن، وإنه كتب لي: أن يحبني كل مؤمن، ويبغضني كل','78','0','0');
INSERT INTO [t21485] VALUES (60,'لا يبغض عليا مؤمن، ولا يحبه منافق»','78','0','0');
INSERT INTO [t21485] VALUES (61,'لو ضربت المؤمن على أنفه ما أبغضني، ولو أعطيت المنافق الذهب والفضة ما','79','0','0');
INSERT INTO [t21485] VALUES (62,'والذي نبأ محمدا وأكرمه بالنبوة، إنك لأنت الذائد عن حوضي يوم القيامة، يذاد الرجال عنه كما','79','0','0');
INSERT INTO [t21485] VALUES (63,'ما كنا نعرف منافقينا إلا ببغضهم عليا»','80','0','0');
INSERT INTO [t21485] VALUES (64,'نعرف منافقينا على عهد رسول الله صلى الله عليه وسلم ببغضهم علي بن أبي','81','0','0');
INSERT INTO [t21485] VALUES (65,'نعرف المنافقين على عهد رسول الله صلى الله عليه وسلم ببغضهم عليا رضي الله','82','0','0');
INSERT INTO [t21485] VALUES (66,'ما كنا نعرف المنافقين إلا بثلاث خصال: بتكذيبهم الله ورسوله، والتخلف عن الصلاة، وبغض علي','82','0','0');
INSERT INTO [t21485] VALUES (67,'حب أبي بكر وعمر إيمان، وبغضهما كفر»','83','0','0');
INSERT INTO [t21485] VALUES (68,'لا يبغض أبا بكر وعمر مؤمن، ولا يحبهما منافق» .','84','0','0');
INSERT INTO [t21485] VALUES (69,'لا يبغض أبا بكر وعمر إلا منافق»','85','0','0');
INSERT INTO [t21485] VALUES (70,'الإيمان حب الأنصار، وإن النفاق بغض الأنصار» .','86','0','0');
INSERT INTO [t21485] VALUES (71,'الأنصار لا يحبهم إلا مؤمن، ولا يبغضهم إلا منافق» .','87','0','0');
INSERT INTO [t21485] VALUES (72,'حب الأنصار إيمان، وبغضهم نفاق» .','88','0','0');
INSERT INTO [t21485] VALUES (73,'لا يبغض الأنصار إلا منافق»','89','0','0');
INSERT INTO [t21485] VALUES (74,'لا يبغضنا أهل البيت إلا منافق»','89','0','0');
INSERT INTO [t21485] VALUES (75,'من مات ولم يغز، ولم يحدث نفسه بالغزو، مات على شعبة من نفاق» . ورواه عبد الله بن رجاء المكي،','90','0','0');
INSERT INTO [t21485] VALUES (76,'حب الغناء ينبت النفاق في القلب، كما ينبت العشب في الماء» . وفيه عن عبد الله بن','91','0','0');
INSERT INTO [t21485] VALUES (77,'خصلتان لا تجتمعان إلا في مؤمن، وقال: خصلتان لا تجتمعان في منافق: حسن سمت، ولا فقه في','92','0','0');
INSERT INTO [t21485] VALUES (78,'المنافق يملك عينيه، يبكي كما شاء»','93','0','0');
INSERT INTO [t21485] VALUES (79,'ندخل على الإمام فيقضي بالقضاء جورا، فنقول: وفقك الله، وننظر إلى الرجل منا يثني عليه، فقال','94','0','0');
INSERT INTO [t21485] VALUES (80,'نحضر الإمام فيحكم بالحكم نراه جورا، فنقول: وفقك الله، وننظر إلى الرجل منا فيثني عليه','95','0','0');
INSERT INTO [t21485] VALUES (81,'إذا دخلنا على هؤلاء نقول ما يشتهون، فإذا خرجنا من عندهم قلنا خلاف ذلك، قال: كنا نعد ذلك','96','0','0');
INSERT INTO [t21485] VALUES (82,'ندخل على أمرائنا، فنقول القول، فإذا خرجنا قلنا غيره، قال: كنا نعد ذلك نفاقا على عهد رسول','96','0','0');
INSERT INTO [t21485] VALUES (83,'ندخل على سلطاننا، فنقول له ما نتكلم بخلافه إذا خرجنا من عنده، قال: كنا نعد هذا نفاقا ". قال','97','0','0');
INSERT INTO [t21485] VALUES (84,'من ترك ثلاث جمعات متواليات من غير علة، طبع على قلبه، وهو منافق»','98','0','0');
INSERT INTO [t21485] VALUES (85,'لا يحافظ المنافق أربعين ليلة على صلاة العشاء الآخرة، يعني: في جماعة "','99','0','0');
INSERT INTO [t21485] VALUES (86,'الفجور كل الفجور، والنفاق، من سمع منادي الله ينادي بالصلاة والفلاح فلم','100','0','0');
INSERT INTO [t21485] VALUES (87,'عسى الله أن يقمصك قميصا من بعدي، فإن أرادك المنافقون على خلعه فلا تخلعه» . قالها ثلاث','101','0','0');
INSERT INTO [t21485] VALUES (88,'أعلمت أن الله مقمصك قميصا، فأرادك المنافقون على خلعه فلا تخلعه حتى','101','0','0');
INSERT INTO [t21485] VALUES (89,'إن الله مقمصك قميصا، فإن أرادك المنافقون على خلعه فلا تخلعه حتى تلقاني» . ورواه عبد الرحمن','102','0','0');
INSERT INTO [t21485] VALUES (90,'لا تقوم الساعة حتى يسود كل قبيلة منافقوها» . حدثناه أبو عمرو بن حمدان، قال: حدثنا الحسن بن','103','0','0');
INSERT INTO [t21485] VALUES (91,'يأتي على الناس زمان يستخفي المؤمن فيهم، كما يستخفي المنافق فيكم اليوم» .','104','0','0');
INSERT INTO [t21485] VALUES (92,'يوشك أن يصلوا في آخر الزمان في مساجدهم، فلا يكون فيهم مؤمن» . قلت: يا رسول الله، ويكون فيهم','105','0','0');
INSERT INTO [t21485] VALUES (93,'المنافقون اليوم أشر منهم على عهد رسول الله صلى الله عليه وسلم , كانوا إذ ذاك يكتمونه، وهم','106','0','0');
INSERT INTO [t21485] VALUES (94,'المنافقون الذين فيكم اليوم شر من المنافقين الذين كانوا في عهد رسول الله صلى الله عليه','106','0','0');
INSERT INTO [t21485] VALUES (95,'المنافقون اليوم أكثر أم على عهد النبي صلى الله عليه وسلم؟ قال: بل هم اليوم أكثر، لأنه كان','107','0','0');
INSERT INTO [t21485] VALUES (96,'المنافقون اليوم شر منهم على عهد رسول الله صلى الله عليه وسلم، قيل: وكيف ذلك؟ قال: إنهم','108','0','0');
INSERT INTO [t21485] VALUES (97,'" ذهب النفاق فلا نفاق، إنما هو الكفر بعد الإيمان. فقال له عبد الله: تعلم ما تقول، قال: فقرأ','108','0','0');
INSERT INTO [t21485] VALUES (98,'إنما كان النفاق على عهد رسول الله صلى الله عليه وسلم، فأما اليوم فهو الكفر بعد الإيمان» .','110','0','0');
INSERT INTO [t21485] VALUES (99,'توفي رسول الله صلى الله عليه وسلم واشرأب النفاق بالمدينة، وارتدت العرب، والله لو نزل','111','0','0');
INSERT INTO [t21485] VALUES (100,'إن كان الرجل منكم ليتكلم بالكلمة على عهد رسول الله صلى الله عليه وسلم، فيصير بها منافقا،','112','0','0');
INSERT INTO [t21485] VALUES (101,'إن كان الرجل ليتكلم على عهد رسول الله صلى الله عليه وسلم فيصير بها منافقا، وإني لأسمعها','113','0','0');
INSERT INTO [t21485] VALUES (102,'إنكم معشر العرب اليوم لتأتون أمورا إنها لفي عهد رسول الله صلى الله عليه وسلم النفاق على','113','0','0');
INSERT INTO [t21485] VALUES (103,'فهاجت ريح، فقال رسول الله صلى الله عليه وسلم: بعثت هذه الريح لموت','114','0','0');
INSERT INTO [t21485] VALUES (104,'فهاجت ريح تكاد تدفن الراكب، فقال رسول الله صلى الله عليه وسلم: بعثت هذه الريح لموت منافق.','114','0','0');
INSERT INTO [t21485] VALUES (105,'أنهم غزوا فيما بين مكة والمدينة، فهاجت عليهم ريح شديدة، حتى وقعت الرحال، فقال رسول الله','115','0','0');
INSERT INTO [t21485] VALUES (106,'غزوا غزوة بين مكة والمدينة، فهاجت عليهم ريح شديدة حتى دفنت الرحال، فقال النبي صلى الله','116','0','0');
INSERT INTO [t21485] VALUES (107,'من حمى مؤمنا من منافق بغيبة، بعث له ملك يحمي لحمه من نار جهنم يوم القيامة، ومن قفا مؤمنا','117','0','0');
INSERT INTO [t21485] VALUES (108,'من المنافق؟ قال: الذي يحب الحمد، ويكره الذم "','121','0','0');
INSERT INTO [t21485] VALUES (109,'لما حضر شهر رمضان: " سبحان الله، ماذا تستقبلون، وماذا يستقبلكم؟ قالها ثلاثا، فقال عمر بن','122','0','0');
INSERT INTO [t21485] VALUES (110,'من لقي الله لا يشرك به شيئا دخل الجنة، ومن لقي الله يشرك به دخل النار» . وسأله: هل في','123','0','0');
INSERT INTO [t21485] VALUES (111,'أكنتم تعدون الذنوب شركا؟ فقال: معاذ الله، ولم نكن ندعوا منافقا مشركا، ولم نكن نرى في','124','0','0');
INSERT INTO [t21485] VALUES (112,'لي جارا منافقا يصنع كذا وكذا، ويقول كذا وكذا. فقال رسول الله صلى الله عليه وسلم: يقول لا','125','0','0');
INSERT INTO [t21485] VALUES (113,'تجد من شر الناس ذا الوجهين» .','126','0','0');
INSERT INTO [t21485] VALUES (114,'شر الناس منزلة يوم القيامة الذي يلقى هذا بوجه، وهذا بوجه»','126','0','0');
INSERT INTO [t21485] VALUES (115,'أربعة ثابت فيهم النفاق: الكذاب، والنمام، والعياب، والموشي بين الناس','127','0','0');
INSERT INTO [t21485] VALUES (116,'{يأيها النبي جاهد الكفار والمنافقين واغلظ عليهم} [التوبة: ٧٣] ، قال: أمر نبي الله صلى','128','0','0');
INSERT INTO [t21485] VALUES (117,'إنك تلقى المنافق دائما سيء العمل، قبيح الأثر، بعيد الأمل»','128','0','0');
INSERT INTO [t21485] VALUES (118,'أخاف عليكم ثلاثا، وهو كائن، زلة عالم، وجدال منافق بالقرآن، ودنيا تفتح','129','0','0');
INSERT INTO [t21485] VALUES (119,'هلاك أمتي في الكتاب واللبن، فقيل: يا رسول الله، ما الكتاب واللبن؟ قال: يتعلمون القرآن','130','0','0');
INSERT INTO [t21485] VALUES (120,'إنك إن بقيت سيقرأ القرآن ثلاث أصناف: صنف لله، وصنف للجدال، وصنف للدنيا، ومن طلب به','131','0','0');
INSERT INTO [t21485] VALUES (121,'حذرنا كل منافق عليم اللسان، وإنك لست منهم إن شاء الله، فالحق ببلدك "','132','0','0');
INSERT INTO [t21485] VALUES (122,'يهلك هذه الأمة كل منافق عليم اللسان»','132','0','0');
INSERT INTO [t21485] VALUES (123,'أخوف ما أخاف عليكم كل منافق عليم اللسان» . رواه يزيد بن هارون، عن ديلم بن غزوان النحات','133','0','0');
INSERT INTO [t21485] VALUES (124,'غشينا النعاس ونحن في مصافنا يوم أحد، فجعل سيفي يسقط من يدي، وآخذه ويسقط وآخذه، قال:','134','0','0');
INSERT INTO [t21485] VALUES (125,'المنافق ليصلي فيكذبه الله، ويصوم فيكذبه الله، ويتصدق فيكذبه الله، ويقاتل فيكذبه الله،','135','0','0');
INSERT INTO [t21485] VALUES (126,'أكثر منافقي أمتي قراؤها» .','136','0','0');
INSERT INTO [t21485] VALUES (127,'أكثر منافقي أمتي قراؤها» . ورواه زيد بن الحباب وابن وهب، عن أبي شريح عبد الرحمن بن شريح','137','0','0');
INSERT INTO [t21485] VALUES (128,'أراد أن يصلي على عبد الله بن أبي، فأخذ جبريل عليه السلام بثوبه، فقال: لا','138','0','0');
INSERT INTO [t21485] VALUES (129,'مر بمجلس فيه عبد الله بن أبي بن سلول، فإذا في المجلس أخلاط من المسلمين والمشركين وعبدة','139','0','0');
INSERT INTO [t21485] VALUES (130,'بينما نحن مع رسول الله صلى الله عليه وسلم في سفر، فهاجت ريح منتنة، فقال رسول الله صلى','140','0','0');
INSERT INTO [t21485] VALUES (131,'القلوب أربعة: فقلب أجرد، فيه مثل السراج يزهر، فذلك قلب المؤمن وسراجه فيه نوره، وقلب أغلف','142','0','0');
INSERT INTO [t21485] VALUES (132,'الإيمان يبدو نقطة في المظهر، بيضاء في القلب، كلما زاد الإيمان زاد ذلك البياض، فإذا استكمل','144','0','0');
INSERT INTO [t21485] VALUES (133,'{فمن يرد الله أن يهديه يشرح صدره للإسلام ومن يرد أن يضله يجعل صدره ضيقا حرجا} [الأنعام:','145','0','0');
INSERT INTO [t21485] VALUES (134,'الدجال يطأ الأرض كلها، إلا مكة والمدينة، فيأتي المدينة، فيجد بكل نقب من نقابها صفوفا من','146','0','0');
INSERT INTO [t21485] VALUES (135,'ليس من بلدة إلا سيطؤها الدجال إلا مكة والمدينة، وليس نقب من نقابها إلا عليه الملائكة','147','0','0');
INSERT INTO [t21485] VALUES (136,'أكثر خطبته، ما حدثنا عن الدجال، ويحذرناه، فكان من قوله «وإنه لا يبقى موضع من الأرض إلا','148','0','0');
INSERT INTO [t21485] VALUES (137,'من صلى ركعتين في السر رفع عنه اسم النفاق»','149','0','0');
INSERT INTO [t21485] VALUES (138,'من واظب على الصلاة المكتوبة أربعين صباحا، لا يفوته خيرها، كتب الله براءتين: براءة من','151','0','0');
INSERT INTO [t21485] VALUES (139,'من أكثر ذكر الله فقد برئ من النفاق»','151','0','0');
INSERT INTO [t21485] VALUES (140,'أثقل الصلاة على المنافقين العشاء والفجر، ولو يعلمون ما فيهما لأتوهما ولو','152','0','0');
INSERT INTO [t21485] VALUES (141,'هلكنا ورب الكعبة، فقال: وما ذاك؟ قالوا: النفاق، قال: ألستم تشهدون أن لا إله إلا الله وحده','152','0','0');
INSERT INTO [t21485] VALUES (142,'إنا نكون عندك على حال، حتى إذا فارقناك نكون على غيره.','154','0','0');
INSERT INTO [t21485] VALUES (143,'{والذي تولى كبره منهم له عذاب عظيم} [النور: ١١] قال: أنزلت في علي.','154','0','0');
INSERT INTO [t21485] VALUES (144,'أخاف على نفسي النفاق. فقال له رسول الله صلى الله عليه وسلم: أرأيت لو حضرتك الصلاة وأنت','155','0','0');
INSERT INTO [t21485] VALUES (145,'منكم، أو فيكم منافقين، فمن سميت فليقم، فقال: قم يا فلان، قم يا فلان، حتى عد ستة وثلاثين،','156','0','0');
INSERT INTO [t21485] VALUES (146,'في قوله عز وجل " {وممن حولكم من الأعراب منافقون ومن أهل المدينة مردوا على النفاق لا تعلمهم','158','0','0');
INSERT INTO [t21485] VALUES (147,'الحمام الطيار ماشية المنافقين "','159','0','0');
INSERT INTO [t21485] VALUES (148,'الغيرة من الإيمان، والمذاء من النفاق» .','160','0','0');
INSERT INTO [t21485] VALUES (149,'«قلما ساهر الليل منافق»','161','0','0');
