INSERT INTO [t21495] VALUES (104,'كتاب اللقطة (بضم اللام وفتح القاف وإسكانها: لغة الشيء الملتقط وشرعا ما وجد من حق محترم غير محروز لا يعرف الواحد مستحقه) :','1597','0','0');
INSERT INTO [t21495] VALUES (105,'باب ما جاء في اللقيط (اللقيط: يقال ملقوطا ومنبوذا ودعيا) :','1601','104','0');
INSERT INTO [t21495] VALUES (106,'كتاب الوقف (الوقف هو التحبيس والتسبيل بمعنى واحد وهو لغة الحبس يقال: وقفت كذا أي حبسته ولا يقال أوقفته إلا في لغة تميمية وشرعا: حبس مال يمكن الإنتفاع به مع بقاء عينه) .','1603','0','0');
INSERT INTO [t21495] VALUES (107,'كتاب البيوع (قال الأزهري: تقول العرب بعت بمعنى بعت ما كنت ملكته وقال ابن قتيبة: يقال نعت الشيء بمعنى بعته وبمعنى اشتريته وشريت الشيء بمعنى اشتريته وبمعنى بعته والإبتياع الإشتراء وتبايعا وبايعته ويقال: استبعته أي سألته البيع وأبعت الشيء أي عرضته للبيع وبيع الشيء بكسر الباء وضمها) وفيه أربعة أبواب','1607','0','0');
INSERT INTO [t21495] VALUES (108,'الباب الأول فيما نهى عنه من البيوع وأحكام آخر:','1608','107','0');
INSERT INTO [t21495] VALUES (109,'الباب الثاني في خيار المجلس:','1678','107','0');
INSERT INTO [t21495] VALUES (110,'الباب الثالث في الربا (أصل الربا الزيادة يقال: ربا الشئ يربوإذا زاد) :','1686','107','0');
INSERT INTO [t21495] VALUES (111,'الباب الرابع في السلم (السلم يقال السلم والسلف وأسلم وسلم وأسلف وسلف والسلم إثبات مال في الذمة بمبذول في الحال وحده أنه عقد على موصوف في الذمة ببذل يعطى عاجلا سمي سلما لتسليم رأس المال في المجلس) :','1708','107','0');
INSERT INTO [t21495] VALUES (112,'كتاب التفليس (أفلس الرجل: لم يبق له مال يراد به أنه صار إلى حال يقال فيها ليس معه فلس فهو مفلس) .','1714','0','0');
INSERT INTO [t21495] VALUES (113,'كتاب الرهن (الرهن لغة: إسم لما وضع وثيقة للدين وقيل الحبس مطلقا وشرعا: حبس مال متقدم بحق يمكن أخذه منه) .','1718','0','0');
INSERT INTO [t21495] VALUES (114,'كتاب الشفعة (الشفعة من شفعت الشئ إذا ضممته وثنيته ومنه شفع الأذان وسميت شفعه لضم نصيب إلى نصيب) .','1725','0','0');
INSERT INTO [t21495] VALUES (115,'كتاب الإجارات (الأجرة الكراء: تقول استأجرت الرجل فهو يأجرني ثماني حجج أي يصير أجيري وأتجر عليه بكذا من الأجر فهو مؤتجر أي استؤجر على العمل) .','1732','0','0');
INSERT INTO [t21495] VALUES (116,'كتاب الهبة والعمرى (الهبة تطلق على كل ما يعم الصدقة والهدية وما يقابلهما والعمرى إذا أعمر شيئا) .','1739','0','0');
INSERT INTO [t21495] VALUES (117,'كتاب القراض (القراض مشتق من القرض وهو القطع سمي بذلك لأن المالك قطع للعامل قطعة من ماله يتصرف فيها وقطعة من الربح ويسمى أيضا مضاربة ومقارضة) .','1750','0','0');
INSERT INTO [t21495] VALUES (118,'كتاب الاستقراض','1753','0','0');
INSERT INTO [t21495] VALUES (119,'كتاب الصيد والذبائح (الصيد مصدر صاد يصيد ثم أطلق الصيد على المصيد قال تعالى: ولا تقتلوا الصيد وأنتم حرم"والذبائح جمع ذبيحة بمعنى مذبوحة) .','1758','0','0');
