INSERT INTO [t21513] VALUES (2,'تقديم المقرئ الشيخ أحمد بن خليل بن شاهين','4','0','0');
INSERT INTO [t21513] VALUES (3,'تقديم فضيلة الدكتور عبد الله بن علي بصفر','6','0','0');
INSERT INTO [t21513] VALUES (4,'مقدمة','9','0','0');
INSERT INTO [t21513] VALUES (5,'الباب الأول تعريف القرآن وبيان بعض فضله وشرف أهله','12','0','0');
INSERT INTO [t21513] VALUES (6,'معنى القرآن لغة:','13','5','0');
INSERT INTO [t21513] VALUES (7,'تعريف القرآن اصطلاحا:','14','5','0');
INSERT INTO [t21513] VALUES (8,'بيان بعض فضل القرآن وشرف أهله:','15','5','0');
INSERT INTO [t21513] VALUES (9,'الباب الثاني بيان الترتيل وهو: ... (التلاوة بتجويد الأداء)','20','0','0');
INSERT INTO [t21513] VALUES (10,'التعريف بمصطلحات وكلمات، يكثر تكرار ذكرها في هذا العلم.','22','9','0');
INSERT INTO [t21513] VALUES (11,'س١: عرف القراءة.','22','9','0');
INSERT INTO [t21513] VALUES (12,'س٢: ما هي أركان القراءة الصحيحة؟','22','9','0');
INSERT INTO [t21513] VALUES (13,'س٣: ... وضح المراد بالركن الأول من الأركان الثلاثة للقراءة الصحيحة، مع ضرب مثال لما تقول.','23','9','0');
INSERT INTO [t21513] VALUES (14,'س٤: ... وضح المراد بالركن الثاني للقراءة الصحيحة، مع ضرب مثال لما تقول.','24','9','0');
INSERT INTO [t21513] VALUES (15,'س٥: ... وضح المراد بالركن الثالث للقراءة الصحيحة، مع ضرب مثال لما تقول.','25','9','0');
INSERT INTO [t21513] VALUES (16,'س٦: ... عرف الاستعاذة، واذكر صيغتها المختارة.','26','9','0');
INSERT INTO [t21513] VALUES (17,'س٧: ... هل تمنع الزيادة في لفظ الاستعاذة؟','26','9','0');
INSERT INTO [t21513] VALUES (18,'س٨: ... هل الاستعاذة من القرآن؟','27','9','0');
INSERT INTO [t21513] VALUES (19,'س٩: ... عرف البسملة.','27','9','0');
INSERT INTO [t21513] VALUES (20,'س١٠: اذكر حكم البسملة عند التلاوة.','28','9','0');
INSERT INTO [t21513] VALUES (21,'س١١: ... علل لمنع الإتيان بالبسملة في أول سورة التوبة {براءة} .','29','9','0');
INSERT INTO [t21513] VALUES (22,'س١٢: اذكر الوجوه الجائزة وصلا - أي حال مواصلة القراءة - لحفص، عند آخر كلمة من سورة الأنفال، وأول سورة براءة.','30','9','0');
INSERT INTO [t21513] VALUES (23,'س١٣: إذا أردت قراءة القرآن، فكيف تبدأ بالاستعاذة والبسملة مع أول السورة؟','31','9','0');
INSERT INTO [t21513] VALUES (24,'س١٤: ... هل يجوز - عند الوصل بين سورتين - أن تصل آخر السورة بالبسملة، ثم تفصل البسملة عن أول السورة التالية؟','32','9','0');
INSERT INTO [t21513] VALUES (25,'س١٥: هل البسملة آية من كل سورة "غير براءة "؟','32','9','0');
INSERT INTO [t21513] VALUES (26,'س١٦: ما المقصود بالألف المدية، والواو المدية، والياء المدية؟','33','9','0');
INSERT INTO [t21513] VALUES (27,'س١٧: ما المقصود بالأوزان الزمنية؟ مثل لما تقول.','33','9','0');
INSERT INTO [t21513] VALUES (28,'س١٨: ما المقصود بالغنة؟ مثل لما تقول.','35','9','0');
INSERT INTO [t21513] VALUES (29,'س١٩: هل هناك حروف تلازمها صفة الغنة، ولا تنفك عنها؟','35','9','0');
INSERT INTO [t21513] VALUES (30,'س٢٠: ... ما المقصود بقولهم - في كتب التجويد - مع الغنة بمقدار حركتين؟','36','9','0');
INSERT INTO [t21513] VALUES (31,'س٢١: ... ما المقصود بالابتداء؟','36','9','0');
INSERT INTO [t21513] VALUES (32,'س٢٢: ... ثمة ابتداء قبيح، بينه، ومثل لما تقول.','36','9','0');
INSERT INTO [t21513] VALUES (33,'س٢٣: ... ما المقصود بمصطلح القصر؟ مع التمثيل له.','37','9','0');
INSERT INTO [t21513] VALUES (34,'س٢٤: ... عرف المد اصطلاحا، ثم مثل لما تقول.','37','9','0');
INSERT INTO [t21513] VALUES (35,'س٢٥: ... دل ما ذكر آنفا على أن المد قسمان رئيسان، فما هما؟','38','9','0');
INSERT INTO [t21513] VALUES (36,'س٢٦: ... ما المقصود بالمد الأصلي؟ مثل لما تقول.','38','9','0');
INSERT INTO [t21513] VALUES (37,'س٢٧: ... هناك ثلاثة أنواع للمد الطبيعي، اذكرها، ثم مثل لكل منها.','39','9','0');
INSERT INTO [t21513] VALUES (38,'س٢٨: ... عرفت - فيما سبق - أن المد اصطلاحا هو إطالة الصوت بحروف المد، أكثر من حركتين، فماذا يسمى هذا النوع من المدود؟','41','9','0');
INSERT INTO [t21513] VALUES (39,'س٢٩: ... ما وجه تسميته بالمد الفرعي؟','41','9','0');
INSERT INTO [t21513] VALUES (40,'س٣٠: ... ما هو السبب الذي ينشأ عنه المد الفرعي؟','41','9','0');
INSERT INTO [t21513] VALUES (41,'س٣١: ... لو أتى همز قبل حرف المد، فما المد المتفرع عن ذلك؟ اذكر مثالا لما تقول، مع التوضيح.','42','9','0');
INSERT INTO [t21513] VALUES (42,'س٣٢: عرفت أن الهمز لو جاء قبل حرف المد، فإن المد يسمى عندها: المد الفرعي البدل، فماذا لو جاء الهمز بعد حرف المد؟','43','9','0');
INSERT INTO [t21513] VALUES (43,'س٣٣: ... ما هو المد المتصل؟ ولم سمي بذلك؟ ومثل له.','43','9','0');
INSERT INTO [t21513] VALUES (44,'س٣٤: ... هل يخير القارئ في المد المتصل بين أن يقصر أو يمد؟','44','9','0');
INSERT INTO [t21513] VALUES (45,'س٣٥: ... ما مقدار المد المتصل؟','44','9','0');
INSERT INTO [t21513] VALUES (46,'س٣٦ ... : من المدود الفرعية أيضا، المد المنفصل، فما سبب تسميته بهذا الاسم؟ ثم مثل لما تقول.','44','9','0');
INSERT INTO [t21513] VALUES (47,'س٣٧: ... ما حكم المد الفرعي المنفصل؟ وما مقدار مده؟','45','9','0');
INSERT INTO [t21513] VALUES (48,'س٣٨: ... عرفت - أخي الكريم - الآن الأنواع الثلاثة من المدود الفرعية الناشئة عن وجود همز قبل حرف المد أو بعده، وهي: البدل والمتصل والمنفصل، فماذا لو أتى السبب الثاني للمد الفرعي - وهو السكون - بعد حرف المد؟','46','9','0');
INSERT INTO [t21513] VALUES (49,'س٣٩: ... لم سمي هذا المد الفرعي بالمد اللازم؟','46','9','0');
INSERT INTO [t21513] VALUES (50,'س٤٠: ... قد يقع المد الفرعي اللازم في كلمة، مثل لذلك مع التوضيح.','46','9','0');
INSERT INTO [t21513] VALUES (51,'س٤١: ... كما أن المد الفرعي اللازم يقع في كلمة، فإنه يقع كذلك في حرف، مثل لذلك، مع التوضيح.','48','9','0');
INSERT INTO [t21513] VALUES (52,'س٤٢: ... لو وقع السكون اللازم في حرف نوراني هجاؤه من حرفين فقط فهل نمده؟ مثل لما تقول.','50','9','0');
INSERT INTO [t21513] VALUES (53,'س٤٣: ... لم خصصنا هذه الحروف الثلاثة عشر من بين حروف الهجاء بالمد اللازم والمد الطبيعي حسب التفصيل السابق؟','51','9','0');
INSERT INTO [t21513] VALUES (54,'س٤٤: عرفت - فيما سبق - أن السكون الأصلي يتفرع عنه ما سمي بالمد اللازم بأنواعه، فما الحكم لو كان السكون بعد حرف المد طارئا عند الوقف؟ وضح ذلك بالمثال.','54','9','0');
INSERT INTO [t21513] VALUES (55,'س٤٥: ... عرف صفة اللين، واذكر حرفي اللين. ثم وضح بذكر أمثلة.','55','9','0');
INSERT INTO [t21513] VALUES (56,'س٤٦: ... لو أتى بعد أحد حرفي اللين سكون عرض لأجل الوقف، فما المد المتفرع عن ذلك؟ فصل ما تقول.','56','9','0');
INSERT INTO [t21513] VALUES (57,'س٤٧: ... ما المقصود بمد التمكين؟ وضح ما تقول بأمثلة.','57','9','0');
INSERT INTO [t21513] VALUES (58,'س٤٨: ... كيف تطبق في تلاوتك المتصل والمنفصل، من حيث مقدار إطالة الصوت بهما؟','58','9','0');
INSERT INTO [t21513] VALUES (59,'س٤٩: ... تنقسم صفات الحروف إلى قسمين، اذكرهما.','61','9','0');
INSERT INTO [t21513] VALUES (60,'س٥٠: ... عرف كلا من قسمي صفات الحروف، ومثل لبعضها.','61','9','0');
INSERT INTO [t21513] VALUES (61,'س٥١ ... : أين يمكنك تصنيف المصطلحات، كثيرة الدوران في كتب علم التجويد، كالإظهار، والإخفاء، والإقلاب، والإدغام؟ فصل القول، مع التمثيل.','64','9','0');
INSERT INTO [t21513] VALUES (62,'س٥٢: ... اذكر فكرة ميسرة موجزة عن مخارج الحروف.','72','9','0');
INSERT INTO [t21513] VALUES (63,'س٥٣: ... يقال في علم التجويد: حرفان متماثلان أو متقاربان أو متجانسان أو متباعدان، وضح هذه المصطلحات، ثم مثل لما تقول.','76','9','0');
INSERT INTO [t21513] VALUES (64,'س٥٤: ... ما المقصود بهمزة القطع؟ ولم سميت بذلك؟ وكيف تقرؤها بحسب رواية حفص؟ مثل لما تقول.','79','9','0');
INSERT INTO [t21513] VALUES (65,'س٥٥: ... عرف همزة الوصل، ولم سميت بذلك؟ وكيف تبدأ بها في القراءة؟ مثل لما تقول.','80','9','0');
INSERT INTO [t21513] VALUES (66,'س٥٦: ... اذكر فكرة ميسرة عن أحكام اللامات في علم التجويد.','84','9','0');
INSERT INTO [t21513] VALUES (67,'س٥٧: ... إذا وقعت اللام المتحركة بعد حرف من حروف الاستعلاء، المجموعة في قولك [قظ خص ضغط] ، فهل تفخم اللام أم ترقق؟ وضح قولك بالأمثلة.','92','9','0');
INSERT INTO [t21513] VALUES (68,'س٥٨ ... : بين ما يسمى بأحكام الراء، وذلك بتفصيل الأحوال التي ترقق فيها الراء، والأحوال التي تفخم فيها، والأحوال التي يجوز فيها الوجهان: التفخيم والترقيق، مع التمثيل لكل من ذلك.','93','9','0');
INSERT INTO [t21513] VALUES (69,'س٥٩: ... ما المقصود بباب الهاءات في علم أحكام التجويد؟','101','9','0');
INSERT INTO [t21513] VALUES (70,'س٦٠: ... عرفت أن الهاءات أربعة أنواع، فما تعريف كل منها؟ مع التوضيح بمثال لما تقول.','101','9','0');
INSERT INTO [t21513] VALUES (71,'س٦١: ... ما الأصل في تحريك هاء الضمير؟ هل هو الضم أو الكسر؟','104','9','0');
INSERT INTO [t21513] VALUES (72,'س٦٢: ... متى تسمى هاء الضمير - أو هاء الكناية - بهاء الصلة؟','105','9','0');
INSERT INTO [t21513] VALUES (73,'س٦٣: ... هل لهاء الصلة هذه حكم تجويدي خاص؟','105','9','0');
INSERT INTO [t21513] VALUES (74,'س٦٤: ... ماذا يسمى مد هاء الصلة عند مده حركتين، وماذا يسمى عند مده أربعا أو خمسا؟','105','9','0');
INSERT INTO [t21513] VALUES (75,'س٦٥: ... ثمة كلمات احتوت على هاء الضمير، إلا أنها لم تشبع، وذلك تبعا لرواية حفص عن عاصم، اذكرها.','106','9','0');
INSERT INTO [t21513] VALUES (76,'س٦٦: ... ما المقصود بباب الياءات في علم أحكام التجويد؟','106','9','0');
INSERT INTO [t21513] VALUES (77,'س٦٧: ... عرف ياءات الإضافة، ومثل لما تقول.','107','9','0');
INSERT INTO [t21513] VALUES (78,'س٦٨: ... كيف تقرأ ياء الإضافة بالفتح، أم بالإسكان؟','107','9','0');
INSERT INTO [t21513] VALUES (79,'س٦٩: ... عرف ياءات الزوائد، ومثل لما تقول.','108','9','0');
INSERT INTO [t21513] VALUES (80,'س٧٠: ... كيف قرأ حفص بروايته عن عاصم ياءات الزوائد؟','109','9','0');
INSERT INTO [t21513] VALUES (81,'س٧١: ... هل لحفص وجه آخر في قراءته الياء الزائدة في قوله تعالى: {فما آتاني الله خير} [النمل: ٣٦] ؟','109','9','0');
INSERT INTO [t21513] VALUES (82,'س٧٢: ... اذكر الفرق بين ياءات الإضافة، وياءات الزوائد في علم التجويد.','110','9','0');
INSERT INTO [t21513] VALUES (83,'س٧٣: ... يعتبر باب معرفة الوقوف والابتداء من أهم أبواب علم الترتيل، فهلا ذكرت شيئا من أهمية ذلك من أقوال السلف رحمهم الله.','111','9','0');
INSERT INTO [t21513] VALUES (84,'س٧٤: ... بين مهمات ما يحتاج إليه القارئ من أحكام الوقف والابتداء.','112','9','0');
INSERT INTO [t21513] VALUES (85,'س٧٥: ... عرف الوقف لغة، واصطلاحا.','113','9','0');
INSERT INTO [t21513] VALUES (86,'س٧٦: ... هل يحظر الوقف على ما اتصل برسم المصحف؟','113','9','0');
INSERT INTO [t21513] VALUES (87,'س٧٧: ... اذكر الراجح في حكم الوقف على فواصل الآيات.','114','9','0');
INSERT INTO [t21513] VALUES (88,'س٧٨: ... عرف الوقف الاختياري، وبين أهميته.','114','9','0');
INSERT INTO [t21513] VALUES (89,'س٧٩: ... اذكر أنواع الوقف الجائز، من الوقف الاختياري.','115','9','0');
INSERT INTO [t21513] VALUES (90,'س٨٠: ... عرف الوقف الاختياري الجائز التام، واذكر أقسامه ومثل لما تقول.','115','9','0');
INSERT INTO [t21513] VALUES (91,'س٨١: ... عرف الوقف الاختياري الجائز الكافي، ومثل لما تقول.','117','9','0');
INSERT INTO [t21513] VALUES (92,'س٨٢: ... عرف الوقف الاختياري الجائز الحسن، ومثل لما تقول.','119','9','0');
INSERT INTO [t21513] VALUES (93,'س٨٣: ... ما المذهب الراجح في الوقف الحسن عند رأس الآية من قوله تعالى: [الماعون: ٤] {فويل للمصلين *} ؟','120','9','0');
INSERT INTO [t21513] VALUES (94,'س٨٤: ... عرفنا - فيما سبق - الوقف الاختياري، وأنواعه، وأمثلة كل منها، فهلا بينت لنا معنى الوقف القبيح، وأنواعه، ومثلت لكل منها؟','121','9','0');
INSERT INTO [t21513] VALUES (95,'س٨٥: ... ما حكم الوقف على ما لا يؤدي معنى، وعلى ما يؤدي معنى غير صحيح؟','122','9','0');
INSERT INTO [t21513] VALUES (96,'س٨٦: ... هل الكلام في جواز الوقف، وعدم جوازه، وقبحه ونحو ذلك محمول على الوقف الاختياري أم الاضطراري؟','124','9','0');
INSERT INTO [t21513] VALUES (97,'س٨٧: ... اذكر ما تعرفه عن علامات الوقف المثبتة في المصحف الشريف وبين معنى كل منها.','124','9','0');
INSERT INTO [t21513] VALUES (98,'س٨٨: ... هل هناك علامات اصطلاحية أخرى للوقف أثبتت في المصحف الشريف؟','126','9','0');
INSERT INTO [t21513] VALUES (99,'س٨٩: ... يتكلف بعض القارئين الوقف عند مواضع يختارونها، متأولين تمام المعنى المقصود، فما حكم ذلك؟ مع التمثيل لما تقول.','126','9','0');
INSERT INTO [t21513] VALUES (100,'س٩٠: ... عرف السكت، وبين المواضع التي يقرأ فيها حفص بالسكت، من طريق الشاطبية.','127','9','0');
INSERT INTO [t21513] VALUES (101,'س٩١: ... عرف الابتداء، واذكر أنواعه، مع التمثيل لما تقول.','128','9','0');
INSERT INTO [t21513] VALUES (102,'س٩٢: ... ما حكم من توقف توقفا غير جائز؟','131','9','0');
INSERT INTO [t21513] VALUES (103,'س٩٣: ... من المعلوم أن الأصل عند الوقف هو إسكان','131','9','0');
INSERT INTO [t21513] VALUES (104,'س٩٤: ... وضح معنى كل من (الإشمام) و (الروم) ، ومثل لكل منهما.','132','9','0');
INSERT INTO [t21513] VALUES (105,'س٩٥: ... ما فائدة الروم والإشمام، مع أن الوقف بالإسكان هو الأصل؟','134','9','0');
INSERT INTO [t21513] VALUES (106,'س٩٦: ... ثمة كلمات ينبغي للقارئ التنبه عند قراءتها، يختبر بها أهل الأداء طلابهم لمعرفة إتقانهم ذلك، فهلا ذكرت لنا بعضا منها، وبينت كيفية قراءتها؟','135','9','0');
INSERT INTO [t21513] VALUES (107,'س٩٧: ... سبق أن عرفت أن في المد المنفصل - برواية حفص - وجهان: القصر والمد، فهلا فصلت ذلك؟','137','9','0');
INSERT INTO [t21513] VALUES (108,'س٩٨: ... بعد أن عرفت جواز قصر المنفصل برواية حفص عن عاصم من طريق «طيبة النشر» لابن الجزري، فهل يصح لكل قارئ أن يقصر المنفصل اتباعا لهذه الرواية؟ فصل القول.','138','9','0');
INSERT INTO [t21513] VALUES (109,'س١٠٠: اذكر فكرة ميسرة موجزة عن علم الرسم في المصحف الإمام، مع التمثيل لما تقول.','146','9','0');
INSERT INTO [t21513] VALUES (110,'الباب الثالث في بيان طريق ميسر لختم القرآن العظيم: حفظا وتلاوة','153','0','0');
INSERT INTO [t21513] VALUES (111,'الباب الرابع في فضائل بعض ... الآيات والسور','167','0','0');
INSERT INTO [t21513] VALUES (112,'الباب الخامس في بيان مواضع السجدات في القرآن الكريم','178','0','0');
INSERT INTO [t21513] VALUES (113,'الباب السادس في نبذة يسيرة من علم القراءات','184','0','0');
INSERT INTO [t21513] VALUES (114,'الباب السابع فرائد من فوائد لها ... صلة بالقرآن','195','0','0');
INSERT INTO [t21513] VALUES (115,'١٣- سور القرآن أربعة أقسام:','201','114','0');
INSERT INTO [t21513] VALUES (116,'الباب الثامن في أحكام متعلقة بإكرام المصحف','207','0','0');
INSERT INTO [t21513] VALUES (117,'خاتمة:','210','0','0');
INSERT INTO [t21513] VALUES (118,'ثبت المراجع','211','0','0');
