INSERT INTO [t21528] VALUES (1,'مقدمة','1','0','0');
INSERT INTO [t21528] VALUES (2,'س١: ما هي المفطرات التي تفطر الصائم؟','8','0','0');
INSERT INTO [t21528] VALUES (3,'س٢: هل لقيام رمضان عدد معين أم لا؟','10','0','0');
INSERT INTO [t21528] VALUES (4,'س٣: إذا صلى الإنسان خلف إمام يزيد على إحدى عشرة ركعة، فهل يوافق الإمام أم ينصرف إذا أتم إحدى عشرة؟','11','0','0');
INSERT INTO [t21528] VALUES (5,'س٤: بعض الأشخاص يأكلون والأذان الثاني يؤذن في الفجر لشهر رمضان، فما هي صحة صومهم؟','12','0','0');
INSERT INTO [t21528] VALUES (6,'س٥: كثير من الناس في رمضان أصبح همهم الوحيد هو جلب الطعام والنوم، فأصبح رمضان شهر كسل وخمول، كما أن بعضهم يلعب في الليل وينام في النهار، فما توجيهكم لهؤلاء؟','12','0','0');
INSERT INTO [t21528] VALUES (7,'س٦: بعض أئمة المساجد في رمضان يطيلون في الدعاء، وبعضهم يقصر، فما هو الصحيح؟','13','0','0');
INSERT INTO [t21528] VALUES (8,'س٧: ما صحة حديث «أفطر الحاجم والمحجوم» ؟','13','0','0');
INSERT INTO [t21528] VALUES (9,'س٨: ما حكم ذهاب أهل جدة إلى مكة لصلاة التراويح؟','14','0','0');
INSERT INTO [t21528] VALUES (10,'س٩: ما حكم تتبع الأئمة الذين في أصواتهم جمال؟','14','0','0');
INSERT INTO [t21528] VALUES (11,'س١٠: هل سحب الدم بكثرة يؤدي إلى إفطار الصائم؟','15','0','0');
INSERT INTO [t21528] VALUES (12,'س١١: بالنسبة لصلاة التراويح في ليلة العيد، هل تكمل أم لا؟','15','0','0');
INSERT INTO [t21528] VALUES (13,'س١٢: هل للمعتكف في الحرم أن يخرج للأكل أو الشرب، وهل يجوز له الصعود إلى سطح المسجد لسماع الدروس؟','15','0','0');
INSERT INTO [t21528] VALUES (14,'س١٣: شاب استمنى في رمضان جاهلا بأنه يفطر وفي حالة غلبت عليه شهوته، فما الحكم؟','16','0','0');
INSERT INTO [t21528] VALUES (15,'س١٤: ما حكم الصوم مع ترك الصلاة في رمضان؟','16','0','0');
INSERT INTO [t21528] VALUES (16,'س١٥: يقول بعض الناس: إن الأشهر جميعا لا يعرف دخولها كلها وخروجها بالرؤية، وبالتالي فإن المفروض إكمال عدة شعبان ثلاثين وكذا عدة رمضان.. فما حكم الشرع في مثل هذا القول؟','17','0','0');
INSERT INTO [t21528] VALUES (17,'س١٦: ما هي الطريقة الشرعية التي يثبت بها دخول الشهر؟ وهل يجوز اعتماد حساب المراصد الفلكية في ثبوت الشهر وخروجه؟ وهل يجوز للمسلم أن يستعمل ما يسمى بـ (الدربيل) في رؤية الهلال؟','18','0','0');
INSERT INTO [t21528] VALUES (18,'س١٧: هل يلزم المسلمين جميعا في كل الدول الصيام برؤية واحدة؟ وكيف يصوم المسلمون في بعض بلاد الكفار التي ليس فيها رؤية شرعية؟','18','0','0');
INSERT INTO [t21528] VALUES (19,'س١٨: إذا تيقن شخص من دخول الشهر برؤية الهلال ولم يستطع إبلاغ المحكمة فهل يجب عليه الصيام؟','21','0','0');
INSERT INTO [t21528] VALUES (20,'س١٩: هل ورد عن الرسول صلى الله عليه وسلم دعاء خاص يقوله من رأى الهلال؟ وهل يجوز لمن سمع خبر الهلال أن يدعو به ولو لم ير الهلال؟','21','0','0');
INSERT INTO [t21528] VALUES (21,'س٢٠: إذا لم يعلم الناس دخول الشهر إلا بعد مضي وقت من النهار فهل يجب عليهم إمساك بقية اليوم؟ أم قضاؤه؟','22','0','0');
INSERT INTO [t21528] VALUES (22,'س٢١: هل يأثم المسلمون جميعا إذا لم يتراء أحد منهم هلال رمضان دخولا أو خروجا؟','22','0','0');
INSERT INTO [t21528] VALUES (23,'س٢٢: إذا أسلم رجل بعد مضي أيام من شهر رمضان فهل يطالب بصيام الأيام السابقة؟','22','0','0');
INSERT INTO [t21528] VALUES (24,'س٢٣: هل يؤمر الصبيان دون الخامسة عشر بالصيام كما في الصلاة؟','23','0','0');
INSERT INTO [t21528] VALUES (25,'س٢٤: إذا برأ شخص من مرض سبق أن قرر الأطباء استحالة شفائه منه وكان ذلك بعد مضي أيام من رمضان فهل يطالب بقضاء الأيام السابقة؟','24','0','0');
INSERT INTO [t21528] VALUES (26,'س٢٥: بعض أئمة المساجد في صلاة التراويح يقلدون قراءة غيرهم وذلك لتحسين أصواتهم بالقرآن.. فهل هذا عمل مشروع وجائز؟','24','0','0');
INSERT INTO [t21528] VALUES (27,'س٢٦: بعض أئمة المساجد يحاول ترقيق قلوب الناس والتأثير فيهم بتغيير نبرة صوته أحيانا أثناء صلاة التراويح وفي دعاء القنوت، وقد سمعت بعض الناس ينكر ذلك فما قولكم حفظكم الله في هذا؟','25','0','0');
INSERT INTO [t21528] VALUES (28,'س٢٧: ما القول في قوم ينامون طول نهار رمضان وبعضهم يصلي مع الجماعة وبعضهم لا يصلي. فهل صيام هؤلاء صحيح؟','25','0','0');
INSERT INTO [t21528] VALUES (29,'س٢٨: نلاحظ بعض المسلمين يتهاونون في أداء الصلاة خلال أشهر العام، فإذا جاء شهر رمضان بادروا بالصلاة والصيام وقراءة القرآن.. فكيف يكون صيام هؤلاء؟ وما نصيحتكم لهم؟','26','0','0');
INSERT INTO [t21528] VALUES (30,'س٢٩: هل نية صيام رمضان كافية عن نية صوم كل يوم على حدة؟','27','0','0');
INSERT INTO [t21528] VALUES (31,'س٣٠: ما حكم الأكل والشرب والمؤذن يؤذن أو بعد الأذان بوقت يسير ولاسيما إذا لم يعلم طلوع الفجر تحديدا؟','27','0','0');
INSERT INTO [t21528] VALUES (32,'س٣١: يطول النهار في بعض البلاد طولا غير معتاد يصل إلى عشرين ساعة أحيانا، هل يطالب المسلمون في تلك البلاد بصيام جميع النهار؟','29','0','0');
INSERT INTO [t21528] VALUES (33,'س٣٢: صاحب شركة لديه عمال غير مسلمين، فهل يجوز له أن يمنعهم من الأكل والشرب أمام غيرهم من العمال المسلمين في نفس الشركة خلال نهار رمضان؟','30','0','0');
INSERT INTO [t21528] VALUES (34,'س٣٣: هل الغيبة والنميمة تفطران الصائم في نهار رمضان؟','30','0','0');
INSERT INTO [t21528] VALUES (35,'س٣٤: إذا رئي صائم يأكل أو يشرب في نهار رمضان ناسيا فهل يذكر أم لا؟','30','0','0');
INSERT INTO [t21528] VALUES (36,'س٣٥: هل يعتبر ختم القرآن في رمضان للصائم أمرا واجبا؟','32','0','0');
INSERT INTO [t21528] VALUES (37,'س٣٦: ما حكم صلاة التراويح، وما هي السنة في عدد ركعاتها؟','32','0','0');
INSERT INTO [t21528] VALUES (38,'س٣٧: ما حكم جمع صلاة التراويح كلها أو بعضها مع الوتر في سلام واحد؟','33','0','0');
INSERT INTO [t21528] VALUES (39,'س٣٨: ما قولكم فيما يذهب إليه بعض الناس من أن دعاء ختم القرآن من البدع المحدثة؟','35','0','0');
INSERT INTO [t21528] VALUES (40,'س٣٩: اعتاد بعض المسلمين وصف ليلة سبع وعشرين من رمضان بأنها ليلة القدر. فهل لهذا التحديد أصل؟ وهل عليه دليل؟','36','0','0');
INSERT INTO [t21528] VALUES (41,'س٤٠: إذا شق الصيام على المرأة المرضع فهل يجوز لها الفطر؟','37','0','0');
INSERT INTO [t21528] VALUES (42,'س٤١: في بعض الصيدليات بخاخ يستعمله بعض مرضى الربو، فهل يجوز للصائم استعماله في نهار رمضان؟','37','0','0');
INSERT INTO [t21528] VALUES (43,'س٤٢: ما حكم استعمال معجون الأسنان للصائم في نهار رمضان؟','38','0','0');
INSERT INTO [t21528] VALUES (44,'س٤٣: هل صحيح أن المضمضة في الوضوء تسقط عن الصائم في نهار رمضان؟','38','0','0');
INSERT INTO [t21528] VALUES (45,'س٤٤: هل يفطر الصائم بأخذ الإبر في الوريد؟','38','0','0');
INSERT INTO [t21528] VALUES (46,'س٤٥: هل أخذ شيء من الدم بغرض التحليل أو التبرع في نهار رمضان يفطر الصائم أم لا؟','39','0','0');
INSERT INTO [t21528] VALUES (47,'س٤٦: ما حكم استعمال السواك للصائم بعد الزوال؟','39','0','0');
INSERT INTO [t21528] VALUES (48,'س٤٧: ما توجيهكم ـ حفظكم الله ـ لبعض أئمة المساجد الذين يتركون مساجدهم في رمضان ويذهبون إلى مكة للعمرة والصلاة في الحرم خلال هذا الشهر؟','39','0','0');
INSERT INTO [t21528] VALUES (49,'س٤٨: يعتقد بعض الناس أن العمرة في رمضان أمر واجب على كل مسلم لابد أن يؤديه ولو مرة في العمر، فهل هذا صحيح؟','40','0','0');
