INSERT INTO [t21536] VALUES (279,'فصل ـ الإيمان بالدجال وخروجه حق','1169','0','0');
INSERT INTO [t21536] VALUES (280,'باب ما يمنع الدجال أن يدخله من البلاد إذ خرج','1170','0','0');
INSERT INTO [t21536] VALUES (281,'باب منه وما جاء أنه إذا خرج يزعم أنه الله ويحصر المؤمنين في بيت المقدس','1171','0','0');
INSERT INTO [t21536] VALUES (282,'باب منه وفي عظم خلق الدجال وعظم فتنته وسبب خروجه وصفة حماره وسعة خطوه وفي حصره المسلمين في جبال الدخان وكم ينكث في الأرض وفي نزول عيسى عليه السلام وقت السحر لقتل الدجال ومن اتبعه','1172','0','0');
INSERT INTO [t21536] VALUES (283,'باب منه آخر في خروج الدجال وما يجيء به من الفتن والشبهات وسرعة سيره في الأرض وكم يلبث فيها، وفي نزول عيسى عليه السلام ونعته كم يكون في الأرض يومئذ من الصلحاء وفي قتله الدجال واليهود وخروج يأجوج ومأجوج وموتهم، وفي حج عيسى وتزويجه ومكثه في ا','1175','0','0');
INSERT INTO [t21536] VALUES (284,'فصل في الاختلاف في لفظة المسيح حواري عيسى عليه السلام ـ إذا نزل','1192','0','0');
INSERT INTO [t21536] VALUES (285,'باب ما جاء في أن حواري عيسى عليه السلام ـ إذا نزل ـ هم أصحاب الكهف وفي حجهم معه','1201','0','0');
INSERT INTO [t21536] VALUES (286,'باب ما جاء أن عيسى إذا نزل يجد في أمة محمد صلى الله عليه وسلم خلقا من حواريه','1202','0','0');
INSERT INTO [t21536] VALUES (287,'باب ما جاء أن الدجال لا يضر مسلما','1203','0','0');
INSERT INTO [t21536] VALUES (288,'باب ما ذكر من أن ابن صياد: الدجال واسمه صاف ويكنى أبا يوسف وسبب خروجه وصفة أبويه وأنه على دين اليهود','1204','0','0');
INSERT INTO [t21536] VALUES (289,'فصل في اختلاف الناس في ابن صياد','1209','0','0');
INSERT INTO [t21536] VALUES (290,'باب في ما جاء في نقب يأجوج ومأجوج السد وخروجهم وصفتهم وفي لباسهم وطعامهم وبيان قوله تعالى " فإذا جاء وعد ربي جعله دكا "','1210','0','0');
INSERT INTO [t21536] VALUES (291,'باب ذكر الدابة وصفتها ومتى تخرج ومن أين تخرج وكم لها من خرجة وصفة خروجها وما معها إذا خرجت وحديث الجساسة وما فيها من ذكر الدجال قال الله تعالى: " وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم "','1218','0','0');
INSERT INTO [t21536] VALUES (292,'باب طلوع الشمس من مغربها وإغلاق باب التوبة وكم يمكث الناس بعد ذلك؟','1231','0','0');
