INSERT INTO [t21618] VALUES (1,'كتاب الإيمان','1','0','0');
INSERT INTO [t21618] VALUES (2,'بيان الأعمال والفرائض التي إذا أداها بالقول والعمل دخل الجنة، والدليل على أنه لا ينفعه الإقرار حتى يستيقن قلبه ويريد به وجه الله بما يحرم به على النار','2','1','0');
INSERT INTO [t21618] VALUES (3,'بيان المعاصي التي يخرج صاحبها من الإيمان عند فعلها والمعاصي التي يكون بها منافقا وإن صلى وصام وأقر بالإسلام','31','1','0');
INSERT INTO [t21618] VALUES (4,'بيان المعاصي التي إذا قالها الرجل وعملها كان كفرا وفسقا واستوجب بها النار','37','1','0');
INSERT INTO [t21618] VALUES (5,'بيان المعاصي التي إذا قالها العبد أو عملها لم يدخل الجنة بمعصيته','64','1','0');
INSERT INTO [t21618] VALUES (6,'بيان نفي الإيمان عن الذي يحرم هذه الأخلاق المثبتة في هذا الباب وإيجاب النهي عن المنكر، ونفي الإيمان عمن لا ينكره بقلبه','78','1','0');
INSERT INTO [t21618] VALUES (7,'بيان الأعمال التي يستوجب صاحبها عذاب الله وغضبه والدليل على أنه لا ينفعه معها عمل إذا لقي الله بها','94','1','0');
INSERT INTO [t21618] VALUES (8,'بيان التشديد في الذي يقتل نفسه وفي لعن المؤمن وأخذ ماله والدليل على أن القاتل إذا مات بغير توبة لم ينفعه إسلامه واجتهاده ويخلد في نار جهنم، وأن من قتل على المعصية استوجب بمعصيته النار، ولا يكون ذلك كفارة معصيته، وبيان إباحة قتل من تعمد لقتاله، وأنه','104','1','0');
INSERT INTO [t21618] VALUES (9,'بيان الخروج من الإيمان لمنفعة ينالها من عرض الدنيا في الفتنة والدليل على ذهابه: بميله إلى صاحبها لمنفعة الدنيا، وإيجاب مبادرة العمل قبل حلولها. وأن السريرة إذا كانت بخلاف العلانية لم ينتفع بعمله وأن العمل بخواتيمه','117','1','0');
INSERT INTO [t21618] VALUES (10,'بيان انتزاع الأمانة من القلوب ورفعها، وأن القلب إذا أشربه الميل إلى الفتنة وإلى صاحبها ولم ينكرها بقلبه وركن إلى صاحبه ران على قلبه وانتزع الإيمان منه','120','1','0');
INSERT INTO [t21618] VALUES (11,'بيان الكبائر والذنوب الموبقات','123','1','0');
INSERT INTO [t21618] VALUES (12,'بيان كبائر الذنوب','127','1','0');
INSERT INTO [t21618] VALUES (13,'بيان الأعمال التي برئ رسول الله صلى الله عليه وسلم من عاملها','130','1','0');
INSERT INTO [t21618] VALUES (14,'بيان الأخلاق والأعمال المحمودة التي جعلها رسول الله صلى الله عليه وسلم من الإيمان ونسبها إلى أهل الحجاز وما يليها، والأخلاق والأعمال المذمومة التي نسبها إلى الكفر وأنها قبل المشرق، وبالله التوفيق','139','1','0');
INSERT INTO [t21618] VALUES (15,'بيان أفضل الأعمال والدليل على أن الإيمان قول وعمل، وأن من ترك الصلاة فقد كفر والدليل على أنها أعلى الأعمال إذ تاركها يصير بتركها كافرا','149','1','0');
INSERT INTO [t21618] VALUES (16,'بيان حقن دماء من يقر بالإسلام من الكفار في المحاربة وإن كان إقراره تقية، ودرء القود عنه بعد إقراره فيما أصاب في كفره ومحاربته ولا يفتش باطنه، والدليل على أن المؤمن يخرج من إيمانه إذا قتل المقر بالإسلام','162','1','0');
INSERT INTO [t21618] VALUES (17,'بيان رفع الإثم عن الذي يأتي الشيء المنهي عنه قبل علمه بالنهي عنه، وأن الكافر ساقط عنه ما عمل في كفره إذا أسلم وحسن إسلامه، ومن أساء في إسلامه لم يسقط عنه ما كان منه في كفره وأخذ بها','169','1','0');
INSERT INTO [t21618] VALUES (18,'بيان: أن الكافر لا يبطل معروفه في كفره إذا أسلم وكان على ذلك وأن الشرك يسمى ظلما','176','1','0');
INSERT INTO [t21618] VALUES (19,'بيان رفع الخطأ والنسيان عن المسلمين وما حدثت به أنفسها ووسوست','184','1','0');
INSERT INTO [t21618] VALUES (20,'بيان الوسوسة التي يجدها المؤمن في نفسه مما يستعظم أن يتكلم به، التي جعلها النبي صلى الله عليه وسلم من الإيمان إذا أنكرها واجدها','189','1','0');
INSERT INTO [t21618] VALUES (21,'بيان المسألة المكروهة التي لا يجوز السؤال عنها وعن رد جوابها، والدليل على إيجاب ترك التفكر فيها وإنها من سؤال الشيطان وما يجب أن يقول المسئول عنها أو من يجدها في نفسه','195','1','0');
INSERT INTO [t21618] VALUES (22,'بيان ثواب حسنة يعملها المسلم الذي كان حسن إسلامه، وثواب الذي هم بها ولم يعملها، وثواب من ترك السيئة التي يهم بعملها فلم يعملها من خشية الله، وأن الإثم ساقط عنه عن الذي يهم بالسيئة حتى يعملها','202','1','0');
INSERT INTO [t21618] VALUES (23,'بيان الأعمال المكروهة التي إذا اجتنبها المؤمن والمحمودة التي من يستعملها دخل الجنة بغير حساب','209','1','0');
INSERT INTO [t21618] VALUES (24,'بيان أنه لا يدخل الجنة إلا نفس مسلمة، وأن نصف أهل الجنة هم أمة محمد صلى الله عليه وسلم، والدليل على أنه لا يكون من أمة محمد صلى الله عليه وسلم إلا مسلما وأن شفاعته لأمته دون سائر الأمم الذين يتبعونه ويقتدون به من الأقربين والأبعدين، وأن التقرب إلى','213','1','0');
INSERT INTO [t21618] VALUES (25,'بيان تهوين العذاب على أبي طالب بشفاعة محمد صلى الله عليه وسلم، وأنه لا تناله شفاعته بنجاته ونجاه الله من النار، والكافر لا ينفعه معروفه إذا مات','232','1','0');
INSERT INTO [t21618] VALUES (26,'بيان أن الساعة لا تقوم ما دام في الأرض من يوحد الله، وأن الإسلام يعز في جميع الأرض ويعود إلى المدينة كما بدأ منها، والدليل على ذهاب الإسلام في الفتنة','242','1','0');
INSERT INTO [t21618] VALUES (27,'بيان ثواب من آمن بمحمد صلى الله عليه وسلم من أهل الكتاب، وأن من أدرك منهم محمدا صلى الله عليه وسلم أو سمع به فلم يؤمن به وبما أرسل به كان من أهل النار، وأن عيسى عليه السلام إذا نزل يحكم بكتاب الله وسنة محمد صلى الله عليه وسلم ويكون إمامهم من أمة محمد','249','1','0');
INSERT INTO [t21618] VALUES (28,'بيان الآيات الثلاث التي من آمن بعد خروجها لم يقبل منه، وأنه لم يبق أحد من الكفار يومئذ إلا آمن ورجع عن كفره وصفة طلوع الشمس من مغربها ومستقرها وأنها لا تطلع كل يوم حتى تستأذن','262','1','0');
INSERT INTO [t21618] VALUES (29,'بيان صفة مبعث النبي صلى الله عليه وسلم وأنه أكثر الأنبياء تبعا، والدليل على أنه قبل أن يوحى إليه كان مؤمنا بالله متعبدا، وعلى أن أول ما أنزل من القرآن اقرأ باسم ربك ثم سورة المدثر','268','1','0');
INSERT INTO [t21618] VALUES (30,'بيان غسل قلب النبي صلى الله عليه وسلم بماء زمزم بعد ما أخرج من جوفه، ثم خيط أثره وحشي إيمانا وحكمة. وصفة البراق والمعراج والدليل على أن السماوات بعضها فوق بعض، وأن لها أبوابا وحجابا، وأنه عرج بنفس النبي صلى الله عليه وسلم لا بروحه وأن الأنبياء يرفعون','280','1','0');
INSERT INTO [t21618] VALUES (31,'مبتدأ أبواب في الرد على الجهمية وبيان أن الجنة مخلوقة، وأن النبي صلى الله عليه وسلم دخلها، وأنها فوق السموات، وأن السدرة المنتهى فوقها، وأن الله فوقها، وأن النبي صلى الله عليه وسلم انتهى إليها، وأنه دنا من رب العزة ورب العزة دنا منه قاب قوسين أو','296','1','0');
INSERT INTO [t21618] VALUES (32,'بيان ضحك الله تبارك وتعالى من عبده وإلى عبيده، وأن أول من يدخل الجنة تكون وجوههم على صورة القمر، ثم من دخلها بعدهم نور وجوههم دون نور من تقدمهم','304','1','0');
INSERT INTO [t21618] VALUES (33,'بيان نزول الرب تبارك وتعالى إلى السماء الدنيا، وأن الله لا ينام، وأنه يخفض القسط ويرفعه، وأن أعمال النهار ترفع إليه كل يوم وأعمال الليل ترفع إليه كل ليلة، والدليل على أن النبي صلى الله عليه وسلم حجبه نور الرب تعالى عن النظر إلى وجهه الكريم','313','1','0');
INSERT INTO [t21618] VALUES (34,'بيان إثبات خازن النار، والدليل على أنها مخلوقة، وإثبات عذاب القبر وصفة الدجال','321','1','0');
INSERT INTO [t21618] VALUES (35,'بيان رؤية النبي جبريل عليهما السلام في صورته، وصفة جبريل، واختلاف تفسير قاب قوسين أو أدنى فأوحى إلى عبده ما أوحى ما كذب الفؤاد ما رأى','332','1','0');
INSERT INTO [t21618] VALUES (36,'بيان نظر أهل الجنة إلى وجه ربهم تبارك وتعالى','344','1','0');
INSERT INTO [t21618] VALUES (37,'بيان تضرع النبي صلى الله عليه وسلم إلى الله عز وجل واجتهاده في الدعاء لأمته حتى أعطي رضاه فيهم، وأنه أول من يشفع، وأنه أول من يفتح له خازن الجنة بابها','347','1','0');
INSERT INTO [t21618] VALUES (38,'بيان في رؤية رب العزة يوم القيامة، وصفة الصراط وأنه جسر جهنم وأن أول من يجوزه محمد وأمته، وأن النار تأكل ابن آدم إلا أثر السجود ممن يشهد أن لا إله إلا الله، وصفة آخر من يخرج من النار، وآخر من يدخل الجنة وما يعطى فيها من النعيم، وأن المرائين بأعمالهم','351','1','0');
INSERT INTO [t21618] VALUES (39,'باب في صفة الشفاعة، وأن نبينا صلى الله عليه وسلم سيد الناس يوم القيامة، وأن آدم خلقه الله بيده','364','1','0');
INSERT INTO [t21618] VALUES (40,'الدليل على أن أول من يستشفع إلى الأنبياء وإلى محمد صلوات الله عليهم أجمعين هم المؤمنون ليريحهم الله من مقامهم، وأن الشفاعة لأهل النار بعد فراغ الرب من القضاء','372','1','0');
INSERT INTO [t21618] VALUES (41,'بيان الدليل على أن الشفاعة لمن قال: لا إله إلا الله وكان في قلبه شيء من الخير، وأنه لا تحرق النار صورهم، وأن الشفاعة لا تنفع من قال: لا إله إلا الله ولم يكن في قلبه من الخير شيء','376','1','0');
INSERT INTO [t21618] VALUES (42,'صفة أهل النار المخلدون فيها وأنه يلقى فيها وتقول هل من مزيد حتى يضع الرب تبارك وتعالى قدمه فيها، وأن أهل النار يدخلونها ثم يخرجون فيعرضون على ربهم، وصفة خلق آدم','383','1','0');
INSERT INTO [t21618] VALUES (43,'مبتدأ كتاب الطهارة','390','1','0');
INSERT INTO [t21618] VALUES (44,'بيان الطهارات التي تجب على الإنسان في بدنه، من ذلك إيجاب جز الشوارب وإحفائه، وإيجاب إعفاء اللحية، وإيجاب مخالفة المجوس والتشبه بأمورهم','391','1','0');
INSERT INTO [t21618] VALUES (45,'إيجاب حلق العانة وقص الشارب وتقليم الأظفار ونتف الإبط، والتوقيت فيها ومنه الختان والسواك وغسل البراجم وانتقاص الماء','396','1','0');
INSERT INTO [t21618] VALUES (46,'بيان الترغيب في السواك عند كل صلاة والدليل على إباحة تركه، وأن استعماله في الوضوء وغير الوضوء غير حتم','401','1','0');
INSERT INTO [t21618] VALUES (47,'صفة السواك، وأنه للسان والفم','405','1','0');
INSERT INTO [t21618] VALUES (48,'بيان حظر الخلاء في طرق الناس وظلهم وإيثار التباعد به من الناس والدليل على إيجاب الارتياد للبول والاستنثار منه','411','1','0');
INSERT INTO [t21618] VALUES (49,'بيان إيثار التستر بالهدف للمتغوط، والدليل على إباحة الخلاء في ظل الشجر والهدف، والإباحة للباطن أن لا يخلو ببوله عن الناس، وأن يبول قائما في ظل الحائط','419','1','0');
INSERT INTO [t21618] VALUES (50,'بيان إيثار ترك البول قائما، والدليل على أنه منسوخ من فعل النبي صلى الله عليه وسلم','424','1','0');
INSERT INTO [t21618] VALUES (51,'بيان حظر استقبال القبلة واستدبارها بالغائط والبول، والدليل على إباحة استقبالهما في البيوت وفيما سواها على الحظر وإيجاب الاستقبال بهما شرقا وغربا','426','1','0');
INSERT INTO [t21618] VALUES (52,'بيان تطهير الثوب الذي يصلي فيه من بول المولود الذكر الذي لم يطعم لا الأنثى','437','1','0');
INSERT INTO [t21618] VALUES (53,'بيان تطهير الثوب الذي يصلي فيه من المني والدم والدليل على أن المني طاهر','447','1','0');
INSERT INTO [t21618] VALUES (54,'صفة تطهير الإناء إذا ولغ فيه الكلب، وإيجاب إهراق ما فيه','461','1','0');
INSERT INTO [t21618] VALUES (55,'تطهير جلود الميتة، والدليل على أن الجلد والإهاب واحد','470','1','0');
INSERT INTO [t21618] VALUES (56,'بيان إباحة الانتفاع بجلد الميتة التي يؤكل لحمها، وإن لم يدبغ، وأن الحرام منها أكلها، والدليل على أن شعرها يطهر بالدباغ، وأن ما لا يؤكل مما يقع عليه اسم الإهاب - وإن لم يعرف ماهي - طاهرة إذا كانت مدبوغة','479','1','0');
INSERT INTO [t21618] VALUES (57,'بيان تطهير الأرض التي يصلي عليها إذا أصابها البول، والدليل على أن النجاسة إذا خالطها الماء والماء أقل من قلتين فلم يغير طعمه ولا ريحه كان طاهرا','486','1','0');
INSERT INTO [t21618] VALUES (58,'بيان حظر البول في الماء الراكد، والدليل على إباحة البول في الماء الجاري','492','1','0');
INSERT INTO [t21618] VALUES (59,'بيان ما يقال عند دخول الخلاء، والدليل على إباحة ذكر الله والدعاء في الموضع الذي يتغوط فيه، وبيان إباحة ذكر الله في الأحوال كلها وجميع المواضع','496','1','0');
INSERT INTO [t21618] VALUES (60,'صفة ما يجب في دخول الخلاء من ذلك إيجاب ترك استقبال القبلة بالغائط والبول، وحظر الاستنجاء باليمين والتمسح بالعظم والبعر، والدليل على الاستنجاء بغيرها مما يجوز الاستنجاء به وعلى أنه لا يجزي فيه دون ثلاث مرات، وعلى أن ما زاد جائز إذا كان وترا، وبيان حظر','500','1','0');
INSERT INTO [t21618] VALUES (61,'بيان حظر إمساك البائل ذكره بيمينه أو يمسه في الخلاء بيمينه، والاستنجاء به والتنفس في إنائه، وبيان الاستنجاء بالماء، والدليل في هذا الباب والذي قبله على الجمع بين الاستنجاء بالحجارة والاستنجاء بالماء إذا ظهر البول على الحشفة والغائط على المسربة','508','1','0');
INSERT INTO [t21618] VALUES (62,'الترغيب في التيمن في الطهور والترجل والانتعال، والدليل في الابتداء بغسل الكف الأيمن وبالمنخر الأيمن في الاستنشاق واليد اليمنى والرجل اليمنى وما يليها من اليسرى','516','1','0');
INSERT INTO [t21618] VALUES (63,'الترغيب في الوضوء وثواب إسباغه، وثواب من يقول بعد فراغه من وضوءه: أشهد أن لا إله إلا الله وأن محمدا عبده ورسوله، وبيان ثوابه','519','1','0');
INSERT INTO [t21618] VALUES (64,'بيان إيجاب إسباغ الوضوء، وثواب إسباغه على المكاره','536','1','0');
INSERT INTO [t21618] VALUES (65,'بيان الاقتصاد في صب الماء في الوضوء والغسل، وتقدير الماء فيهما وتوقيته، والدليل على إبطال إيجاب التوقيت والتقدير في الماء لهما','543','1','0');
INSERT INTO [t21618] VALUES (66,'الدليل على إيجاب الوضوء لكل صلاة وأنها لا تقبل إلا من طاهر وما عليه طاهر، وبيان نسخ الوضوء لكل صلاة والإباحة لمن يصلي الصلوات بوضوء واحد، وأن المتطهر لا يجب عليه وضوء لصلاة بوضوء واحد ولا لغيرها حتى يحدث، والدليل على أنه لا يزيل طهارته ظنه أنه أحدث','552','1','0');
INSERT INTO [t21618] VALUES (67,'بيان وضوء النبي صلى الله عليه وسلم وأن أتم الوضوء وأسبغه ثلاثا ثلاثا، وبيان الابتداء بغسل الكفين ثلاثا قبل إدخالهما في وضوئه وإباحة الوضوء من الإناء','568','1','0');
INSERT INTO [t21618] VALUES (68,'باب إباحة الوضوء مرتين مرتين، وبيان إباحة المضمضة والاستنشاق بغرفة واحدة، وبيان الوضوء من التور','573','1','0');
INSERT INTO [t21618] VALUES (69,'بيان الدليل على إباحة الوضوء مرة مرة إذا أسبغه المتوضئ، وبيان إباحة مجاوزة المرفقين والكعبين بالغسل في الوضوء إلى المنكبين والساقين، والدليل على أن الفضيلة في ترك مجاوزتهما','578','1','0');
INSERT INTO [t21618] VALUES (70,'بيان ثواب المضمضة والاستنشاق وصفتهما، وثواب غسل سائر أعضاء الوضوء، والدليل على أنه إذا وقع على كل عضو منها اسم الغسل مرة كانت أو أكثر كان وضوءا جائزا، وعلى أن الفضيلة في ترك التمسح بالمنديل','583','1','0');
INSERT INTO [t21618] VALUES (71,'بيان إيجاب الاستنشاق في الوضوء وإيجاب الاستنثار على المستيقظ من نومه ثلاثا، وبيان علة إيجابه حين يستيقظ','586','1','0');
INSERT INTO [t21618] VALUES (72,'صفة مسح الرأس وأنه مرة واحدة ويمسح بماء جديد','593','1','0');
INSERT INTO [t21618] VALUES (73,'بيان إثبات غسل الرجلين حتى تنقيا، وإبطال المسح عليهما، والدليل على أن المتوضئ إذا ترك غسل بعض أعضاء الوضوء رجع في وضوئه فأعاده، وأنه لا يجزيه إن مسحه ببلل وضوئه والتشديد في السهو في إسباغ الوضوء، وأنه يجب عليه أن ينقيه حتى يستيقن أنه قد نقاه، وإباحة','596','1','0');
INSERT INTO [t21618] VALUES (74,'بيان إثبات المسح على الخفين','610','1','0');
INSERT INTO [t21618] VALUES (75,'بيان إباحة المسح على الخفين إذا أدخل رجليه فيهما وهما طاهرتان','615','1','0');
INSERT INTO [t21618] VALUES (76,'باب بيان الإباحة للمتوضيء أن يعينه على وضوئه غيره ويصبه عليه، والدليل على إجازة المسح على الخفين كيف ما مسح إذا وقع عليه اسم المسح','619','1','0');
INSERT INTO [t21618] VALUES (77,'إباحة المسح على العمامة إذا مسحها مع ناصيته وعلى الخمار','626','1','0');
INSERT INTO [t21618] VALUES (78,'باب بيان التوقيت في المسح على الخفين','633','1','0');
INSERT INTO [t21618] VALUES (79,'إيجاب غسل اليدين ثلاثا على المستيقظ من نومه، والدليل على أنه إن غسلهما دون ذلك جائز، وعلى أن النائم إذا نام زالت طهارته، وأن عليه الوضوء ومن مس الذكر','638','1','0');
INSERT INTO [t21618] VALUES (80,'باب الدليل على أن أمر النبي صلى الله عليه وسلم للمستيقظ من النوم غسل يديه على إباحة، وأن النائم في المسجد لانتظار الصلاة لا يجب عليه الوضوء','647','1','0');
INSERT INTO [t21618] VALUES (81,'باب إيجاب الوضوء من الريح، والدليل على إيجاب الوضوء على من يدافع الأخبثين عند القيام إلى الصلاة، والدليل على أن من وجد شيئا من ذلك وهو في صلاته لم يجب عليه الانصراف وصلاته جائزة','652','1','0');
INSERT INTO [t21618] VALUES (82,'بيان إيجاب الوضوء مما مست النار، وبيان ما يعارضه من الأخبار، والدليل على أن الوضوء مما مست النار منسوخ، وإثبات الوضوء من لحوم الإبل','658','1','0');
INSERT INTO [t21618] VALUES (83,'باب في المضمضة من شرب اللبن والدسم، والدليل على إباحة تركه، وبالله التوفيق','667','1','0');
INSERT INTO [t21618] VALUES (84,'باب إيجاب الوضوء من المذي والاستنجاء بالماء منه، ونضح الفرج بالماء','674','1','0');
INSERT INTO [t21618] VALUES (85,'باب في إباحة ترك الوضوء للمتغوط إذا أراد أن يطعم، وللجنب ترك الاغتسال إذا أراد أن يطعم أو يعمل عملا','678','1','0');
INSERT INTO [t21618] VALUES (86,'بيان حظر اغتسال الجنب في الماء الدائم، وإباحة الاغتسال به والوضوء منه إذا تناوله بيده تناولا، وحظر الاغتسال بالماء الدائم إذا بال فيه','688','1','0');
INSERT INTO [t21618] VALUES (87,'بيان إيجاب الوضوء على الجنب إذا أراد أن ينام أو يأكل، وإيجاب غسل الذكر مع الوضوء إذا أراد النوم','692','1','0');
INSERT INTO [t21618] VALUES (88,'بيان صفة وضوء البائل إذا أراد النوم، والرخصة للجنب إذا توضأ وضوءا خفيفا، أو أراد أن ينام قبل أن يغتسل','699','1','0');
INSERT INTO [t21618] VALUES (89,'بيان إيجاب الوضوء على الجنب إذا أراد أن يعود في الجماع، والإباحة لمن طاف على نسائه بغسل واحد','705','1','0');
INSERT INTO [t21618] VALUES (90,'باب إباحة التعري عند الاغتسال وغيره، وبيان حظر النظر إلى الفروج','710','1','0');
INSERT INTO [t21618] VALUES (91,'باب الإباحة للرجل أن يغتسل بفضل ماء المرأة، والاغتسال معها في إناء واحد، والدليل على إبطال توقيت الماء في الغسل، وإباحة اغتسال الجماعة من الحوض والأوقة وغيرها','717','1','0');
INSERT INTO [t21618] VALUES (92,'باب ذكر إباحة ترك الاغتسال من الجماع إذا لم ينزل، وما يعارضه من الأخبار الدالة على إيجاب الاغتسال من مس الختان الختان، وإن لم ينزل','725','1','0');
INSERT INTO [t21618] VALUES (93,'باب صفة الأواني التي كان يغتسل منها رسول الله صلى الله عليه وسلم من الجنابة، وصفة غسل رأسه من الجنابة دون سائر جسده','747','1','0');
INSERT INTO [t21618] VALUES (94,'بيان غسل ما ابتدأ به رسول الله صلى الله عليه وسلم في غسله وأنه ابتدأ بغسل يمينه من الجنابة، والابتداء بالوضوء، ثم غسل الجسد، والدليل على أنه لا يجب عليه الوضوء بعد الغسل','758','1','0');
INSERT INTO [t21618] VALUES (95,'بيان دلك الشمال بالأرض بعد غسل الفرج، وغسل الرجلين في الوضوء بعد غسل الجسد بعد أن ينحي مقامه، والدليل على إباحة القيام من مقعده بين وضوئه وقعوده في مكان آخر لإتمام وضوئه، والترغيب في ترك المسح بالمنديل بعد الغسل، وبيان الابتداء بغسل يمينه يصب عليه','763','1','0');
INSERT INTO [t21618] VALUES (96,'باب إباحة ترك نقض ضفر الرأس في الغسل من الجنابة','767','1','0');
INSERT INTO [t21618] VALUES (97,'بيان نزول التيمم والدليل على أن تراب الأرض كلها طهور إذا لم يوجد الماء','770','1','0');
INSERT INTO [t21618] VALUES (98,'بيان صفة التيمم: وأنه ضربة واحدة بالكفين ومسح الشمال على اليمين، والدليل على أنه يمسح الكف اليسرى بظهر كف اليمنى','774','1','0');
INSERT INTO [t21618] VALUES (99,'بيان إباحة النفخ في التيمم قبل المسح بالوجه والكفين، وبيان الابتداء فيه بالوجه ثم بالكفين، وأن الجنب وغيره في الضربة الواحدة سواء','777','1','0');
INSERT INTO [t21618] VALUES (100,'بيان إباحة التيمم بالجدار في الحضر، والدليل على إباحة التيمم عند عدم الماء، وإن كان الماء قريبا منه إذا خاف فوت وقت الصلاة','780','1','0');
INSERT INTO [t21618] VALUES (101,'بيان المتيمم للجنابة إذا وجد الماء يغسل جسده','782','1','0');
INSERT INTO [t21618] VALUES (102,'مبتدأ كتاب الحيض والاستحاضة','784','1','0');
INSERT INTO [t21618] VALUES (103,'بيان إباحة مباشرة الحائض وبينهما ثوب من غير أن يقضي الرجل حاجته دون الإزار','785','1','0');
INSERT INTO [t21618] VALUES (104,'بيان إباحة شرب سؤر الحائض، والدليل على أنها ليست بنجسة في حالتها تلك، وعلى إباحة مرورها في المسجد وطهارة الماء الذي تدخل يدها فيه وما يعارضه من الخبر لإباحة دخولها المسجد وإباحة إصابتها دون النكاح','795','1','0');
INSERT INTO [t21618] VALUES (105,'باب الإباحة للحائض ترك نقض ضفر رأسها للاغتسال إذا وصل الماء إلى شئون رأسها','806','1','0');
INSERT INTO [t21618] VALUES (106,'بيان صفة اغتسال الحائض وإيجاب دلك رأسها بالسدر، واتباع الفرصة الممسكة حوالي فرجها بعد اغتسالها','810','1','0');
INSERT INTO [t21618] VALUES (107,'باب في المستحاضة','818','1','0');
INSERT INTO [t21618] VALUES (108,'بيان صفة قصة أم حبيبة بنت جحش، والدليل على أن المستحاضة التي يغلبها الدم وكانت في مثل معنى قصة أم حبيبة اغتسلت لكل صلاة','821','1','0');
INSERT INTO [t21618] VALUES (109,'في المستحاضة التي لا تعرف إقبال الحيضة من إدبارها بتغيير الدم وعرفت أيام أقرائها','828','1','0');
INSERT INTO [t21618] VALUES (110,'بيان إباحة ترك قضاء الصلاة التي تترك الحائض في أيام حيضتها','832','1','0');
INSERT INTO [t21618] VALUES (111,'كتاب الصلاة','837','0','0');
INSERT INTO [t21618] VALUES (112,'مبتدأ بدو الأذان وما جاء فيه، وأن الصلاة قبلها وبمكة كانت بلا أذان، وأن النبي صلى الله عليه وسلم أمر به، عن قول عمر، وبيان إيجاب التأذين قائما','838','111','0');
INSERT INTO [t21618] VALUES (113,'بيان أذان بلال وإقامته، والدليل على أنه شفع لا وتر والإقامة وتر لا شفع، والدليل على أن النبي صلى الله عليه وسلم أمر بلالا أن يشفع الأذان ويوتر الإقامة بقوله صلى الله عليه وسلم في حديث ابن جريج: يا بلال قم فأذن بالصلاة، وصفة تحريف بلال رأسه في أذانه','840','111','0');
INSERT INTO [t21618] VALUES (114,'بيان أذان أبي محذورة، وإيجاب الترجيع فيه، والدليل بعدما أمر بلال بالأذان، وعلى أن الإقامة إقامة بلال وتر لم ينسخ إذ لم يصح في حديث أبي محذورة تثنية الإقامة في رواية إلا وحديث أنس في الإفراد أصح منه، فإذا تعارض الخبران وأحدهما أصح كان الأخذ به أولى','857','111','0');
INSERT INTO [t21618] VALUES (115,'باب إيجاب الأذان والإقامة عند حضور الصلاة، وأن يؤذن لها مؤذنان','860','111','0');
INSERT INTO [t21618] VALUES (116,'باب الترغيب في الأذان، والدليل على أن المؤذن في أذانه وإقامته إلى أن يفرغ منفي عنه الوسوسة والرئاء لتباعد الشيطان منه','865','111','0');
INSERT INTO [t21618] VALUES (117,'باب الإباحة في اتخاذ الأعمى مؤذنا','873','111','0');
INSERT INTO [t21618] VALUES (118,'باب في ثواب الأذان','876','111','0');
INSERT INTO [t21618] VALUES (119,'بيان إيجاب إجابة المؤذن إذا أذن، والصلاة على النبي صلى الله عليه وسلم وسؤال الوسيلة له وثواب من قال ذلك','880','111','0');
INSERT INTO [t21618] VALUES (120,'بيان إيجاب إجابة المؤذن مثل ما يؤذن، وإجابة النبي صلى الله عليه وسلم المنادي','882','111','0');
INSERT INTO [t21618] VALUES (121,'بيان ثواب من قال مثل ما يقول المؤذن، وقال لا حول ولا قوة إلا بالله عند قول المؤذن حي على الصلاة حي على الفلاح','889','111','0');
INSERT INTO [t21618] VALUES (122,'بيان ثواب من قال إذا سمع المؤذن يؤذن: أشهد أن لا إله إلا الله وحده لا شريك له، وما بعده من القول','891','111','0');
INSERT INTO [t21618] VALUES (123,'مبتدأ أبواب مواقيت الصلاة','893','111','0');
INSERT INTO [t21618] VALUES (124,'وأن جبريل أم بالنبي صلى الله عليه وسلم فصلى فبين له المواقيت وقتا واحدا','894','111','0');
INSERT INTO [t21618] VALUES (125,'الترغيب في المحافظة على الصلوات على وقتها وإيجاب الصلاة لوقتها، والتشديد في تأخيرها','899','111','0');
INSERT INTO [t21618] VALUES (126,'بيان وقت الظهر وإيجاب تعجيلها وإن كان حرا مؤذيا، وإباحة السجود على الثوب إذا آذاه الحر','905','111','0');
INSERT INTO [t21618] VALUES (127,'باب إيجاب الإبراد بصلاة الظهر في الحر وبيان العلة في إبرادها','912','111','0');
INSERT INTO [t21618] VALUES (128,'في صفة وقت الظهر','924','111','0');
INSERT INTO [t21618] VALUES (129,'صفة وقت صلاة العصر','927','111','0');
INSERT INTO [t21618] VALUES (130,'بيان إيجاب المحافظة على وقت صلاة العصر','939','111','0');
INSERT INTO [t21618] VALUES (131,'باب في التشديد في وقت العصر','942','111','0');
INSERT INTO [t21618] VALUES (132,'بيان ما يجب فيمن تفوته صلاة العصر حتى تغرب الشمس، والدليل على أن من فاتته صلاة مفروضة حتى دخل وقت صلاة أخرى أنه يبدأ بالفائتة وإن خشي أن يفوته وقت هذه الصلاة الأخرى','951','111','0');
INSERT INTO [t21618] VALUES (133,'باب في آخر وقت صلاة العصر','954','111','0');
INSERT INTO [t21618] VALUES (134,'بيان ثواب من حافظ على صلاة العصر، وأنه لا صلاة بعدها حتى يطلع النجم، وما يعارضه من الخبر الدال على أنه يصلي بعدها حين تغرب الشمس وأن هذا منسوخ','958','111','0');
INSERT INTO [t21618] VALUES (135,'بيان صفة أول صلاة المغرب وآخره','961','111','0');
INSERT INTO [t21618] VALUES (136,'بيان صفة وقت صلاة العشاء وإثبات التختم في الخنصر اليسرى','968','111','0');
INSERT INTO [t21618] VALUES (137,'بيان إباحة تعجيل العشاء وكراهية النوم قبلها والحديث بعدها، والدليل على أنهما على الإباحة بتأخير الإقامة لانتظار أهل المسجد وتعجيلها إذا اجتمعوا','977','111','0');
INSERT INTO [t21618] VALUES (138,'بيان اسم صلاة العشاء الآخرة','983','111','0');
INSERT INTO [t21618] VALUES (139,'باب صفة وقت الفجر، وآخر وقتها، وصفة الفجر الذي إذا طلع حل أداء صلاة الفجر إذا صلى الفجر، وإباحة الأذان بالليل لها والدليل على أن الفجر هو المستطير الذي تخالطه الحمرة','986','111','0');
INSERT INTO [t21618] VALUES (140,'بيان الأخبار التي ثبتت عن النبي صلى الله عليه وسلم في الوقتين، والدليل على أن وقت الصلوات بين وقت الأول ووقت الآخر، وعلى أن من صلى في الوقت الأول والآخر كان وقتا، وأن من صلى في الوقت الآخر كان مؤديا','1000','111','0');
INSERT INTO [t21618] VALUES (141,'بيان ثواب المحافظة على صلاة الفجر والعصر وفضيلتهما','1004','111','0');
INSERT INTO [t21618] VALUES (142,'بيان المواقيت التي نهي عن الصلاة , فيها النهي عن الصلاة بعد صلاة الفجر حتى تطلع الشمس، وعن الصلاة بعد العصر حتى تغرب الشمس، وما يعارضه من الخبر الدال على إباحة الصلاة بعد العصر','1013','111','0');
INSERT INTO [t21618] VALUES (143,'بيان النهي عن الصلاة لمن يتحرى فيصلي عند طلوع الشمس وغروبها، وليس هو بمعارض الباب الأول إذ هذا النهي بخلاف النهي الأول وليس فيه عن النبي صلى الله عليه وسلم دليل على إباحة الصلاة قبلها','1020','111','0');
INSERT INTO [t21618] VALUES (144,'بيان حظر الصلاة كلها وإيجاب تأخيرها كلها إذا بدا حاجب الشمس حتى تبرز، وإذا غاب حاجب الشمس حتى تغيب، والدليل على أن قضاء الصلاة الفائتة من السنن التي يوجبها المرء على نفسه قبل هذه الساعة جائزة، وعلى أن قضاء الصلاة المفروضة في هاتين الساعتين وغيرهما','1025','111','0');
INSERT INTO [t21618] VALUES (145,'بيان حظر الصلاة في ثلاث ساعات وإيجاب الإمساك عن الصلاة فيها لعلل تكون عندها','1034','111','0');
INSERT INTO [t21618] VALUES (146,'مبتدأ أبواب في المساجد وما فيها','1037','111','0');
INSERT INTO [t21618] VALUES (147,'من ذلك فضل بعيد الدار من المسجد على القريب في إتيان صلاة الجماعة، وبيان فضل الخطى إلى المسجد وثوابه وإيجاب ترك الانتقال للاقتراب من المسجد','1038','111','0');
INSERT INTO [t21618] VALUES (148,'بيان فضيلة المساجد وثواب بانيها','1046','111','0');
INSERT INTO [t21618] VALUES (149,'بيان أول مسجد وضع في الأرض وأول قبلة النبي صلى الله عليه وسلم التي كان يصلي إليها، وتحويلها والدليل على إباحة اتخاذها في جميع المواطن إذا كان طيبا إلا فيما استثني منها، وعلى إباحة الصلاة في الطريق وفي مرابض الغنم وعلى أن أي موضع صلى فيه سمي مسجدا','1050','111','0');
INSERT INTO [t21618] VALUES (150,'بيان صفة موضع مسجد النبي صلى الله عليه وسلم، والدليل على إباحة اتخاذ المسجد في المقابر إذا أزيل عنها ترابها وما فيها وعلى الأرض إذا كان قذرا، ثم فرشت بشيء طاهر جازت الصلاة عليها','1065','111','0');
INSERT INTO [t21618] VALUES (151,'بيان حظر الصلاة إلى المقابر والدليل على حظر اتخاذ المساجد في المقابر، وبيان حظر اتخاذها في مبارك الإبل والصلاة فيها','1068','111','0');
INSERT INTO [t21618] VALUES (152,'بيان النهي عن البصاق في المسجد وعلى جداره، وما يجب على المتنخع في المسجد والصلاة أن يعمل فيه، وحظر البصاق بين يديه وعن يمينه','1080','111','0');
INSERT INTO [t21618] VALUES (153,'بيان الكراهية فيمن ينشد الضالة في مسجد، وما يجب على السامع في جوابه، والدليل على كراهية العمل ورفع الصوت في المسجد من أمر الدنيا','1096','111','0');
INSERT INTO [t21618] VALUES (154,'بيان حظر دخول المسجد بريح منتنة وريح الثوم، والتشديد فيه، وإيجاب القعود في بيته واعتزال المسجد حتى يذهب ريحها','1101','111','0');
INSERT INTO [t21618] VALUES (155,'بيان النهي عن أكل البصل، والكراث، والدليل على إباحة أكلها وأن من أكلها لا يقرب المسجد حتى يذهب ريحها','1107','111','0');
INSERT INTO [t21618] VALUES (156,'بيان حظر السعي لإتيان المسجد، وإثبات إتيانه بالسكينة والوقار، وإيجاب التسليم عند دخوله والدعاء لنفسه وعند خروجه منه وثواب من قصده ليصلي فيه','1115','111','0');
INSERT INTO [t21618] VALUES (157,'بيان إيجاب الركعتين على من يدخل المسجد قبل أن يجلس وعلى القادم من السفر أن يبدأ بالمسجد فيصلي فيه ركعتين، ثم يرجع إلى منزله','1122','111','0');
INSERT INTO [t21618] VALUES (158,'ابتداء أبواب الصلوات وما فيها','1128','111','0');
INSERT INTO [t21618] VALUES (159,'من ذلك فضل صلاة الجماعة مع الإمام على صلاة الفذ وفي السوق','1129','111','0');
INSERT INTO [t21618] VALUES (160,'بيان فضل صلاة الفجر والعشاء في جماعة والتشديد في تركهما في الجماعة','1137','111','0');
INSERT INTO [t21618] VALUES (161,'بيان إيجاب إتيان الجماعة والفريضة إذا نودي بها بسكينة ووقار وحظر السعي إليها والنهي عنها في بيته','1141','111','0');
INSERT INTO [t21618] VALUES (162,'باب الدليل على أن من صلى المكتوبة وحده ليس عليه إعادتها وهو تارك لفضيلتها، وبيان الخبر المعارض لحديث يزيد بن الأصم هو الآخر الناسخ له','1159','111','0');
INSERT INTO [t21618] VALUES (163,'بيان العذر والعلل التي تسقط عن صاحبها حضور الجماعة وإجازة صلاته وحده','1167','111','0');
INSERT INTO [t21618] VALUES (164,'بيان ثواب الصلوات الخمس وإنهن كفارات الذنوب التي دون الكبائر','1187','111','0');
INSERT INTO [t21618] VALUES (165,'بيان ثواب من جلس في المسجد وثبت في مكانه الذي صلى فيه بعد ما يصلي، وثواب من ينتظر الصلاة في المسجد والترغيب في القعود في المسجد بعدما يصلي الصبح حتى تطلع الشمس','1192','111','0');
INSERT INTO [t21618] VALUES (166,'بيان أصل فرض الصلوات وعددها وما حط منها وخفف عن المسلمين، وما أثبت عليهم منها، وما زيد منها فرضا على الحاضر منه، وما قصر منها عن الخائف الموازي أعداء الله وما تركت مجالها مما أثبت عليهم منها، والدليل على أن ما سواها من الصلوات ركعتين ركعتين بالليل','1201','111','0');
INSERT INTO [t21618] VALUES (167,'بيان النهي عن القيام إذا أقيمت الصلاة في المسجد من المأمومين حتى يروا الإمام، وما يعارضه من الأخبار الدالة على إباحة القيام إذا أقيمت الصلاة، وأن الناس يقومون في مصافهم، ثم يقوم الإمام في مقامه','1212','111','0');
INSERT INTO [t21618] VALUES (168,'بيان إباحة تأخير قيام الإمام في مقامه بعدما تقام الصلاة وتأخير المؤذن الإقامة بعد ما يؤذن لانتظار الإمام','1220','111','0');
INSERT INTO [t21618] VALUES (169,'باب في الصلاة بين الأذان والإقامة في صلاة المغرب وغيره','1226','111','0');
INSERT INTO [t21618] VALUES (170,'بيان حظر الصلاة إذا أقيمت الصلاة إلا المكتوبة','1231','111','0');
INSERT INTO [t21618] VALUES (171,'بيان ما يستحق به الرجل الإمامة وحظر التقدم بين يدي السلطان في صلاته والقعود في بيته إلا بإذنه والتقدم بين يدي صاحب المنزل إلا بإذنه','1239','111','0');
INSERT INTO [t21618] VALUES (172,'الترغيب في الصف الأول للرجال، وللنساء صف المؤخر، وحظر رفع رءوسهن قبل الرجال','1243','111','0');
INSERT INTO [t21618] VALUES (173,'بيان إيجاب قيامة الصفوف وأن تسوية الصف من تمام الصلاة، والتشديد في ترك تسويته وإيجاب إتمام الصف الأول، ثم الذي يليه','1249','111','0');
INSERT INTO [t21618] VALUES (174,'باب إيجاب تقدم أولي الأحلام والنهى من الإمام، ثم الذين يلونهم ثم كذلك، وحظر التأخر عن الإمام وإيجاب التقرب منه قدر الإمكان وأن الإمام يقول لمن خلفه استووا','1260','111','0');
INSERT INTO [t21618] VALUES (175,'بيان إيجاب تقدم المصلي إلى سترة، وأن لا يدع أحدا يمر بين يديه، وقتال المار بين يديه، والتشديد فيمن يمر بين يدي المصلي','1265','111','0');
INSERT INTO [t21618] VALUES (176,'بيان مقدار السترة التي لا يضر المصلي من يمر بين يديه من ورائها، فإذا صلى إلى غير السترة قطع عليه صلاته المرأة والحمار والكلب إذا مروا بين يديه، والدليل على أن الخط لا ينفعه ولا يكون له سترة','1273','111','0');
INSERT INTO [t21618] VALUES (177,'بيان أن العنزة إذ نصبت بين يدي المصلي لم يقطع عليه المرأة والحمار والكلب صلاته إذا مروا بين يديه من ورائها','1281','111','0');
INSERT INTO [t21618] VALUES (178,'بيان إباحة الصلاة إلى البعير المناخ وإلى المرأة النائمة وبجنبها وإن كانت حائضا، وكذلك بحذائها وإلى الحربة الموضوعة بين يدي المصلي، والدليل على أنهن سترة للمصلي وعلى أن المارة بخلاف النائمة، وعلى أن الصلاة خلف النائم جائزة','1292','111','0');
INSERT INTO [t21618] VALUES (179,'الدليل على أن النبي صلى الله عليه وسلم كان لا يوتر خلف عائشة وهي نائمة، وعلى أنه كان إذا سجد سجد وليست هي بينه وبين القبلة','1304','111','0');
INSERT INTO [t21618] VALUES (180,'الدليل على أن الإمام سترة لمن خلفه، وأن الحمار إذا مر بين يدي من خلف الإمام لم تقطع عليهم الصلاة','1307','111','0');
INSERT INTO [t21618] VALUES (181,'بيان مقدار وقوف الإمام من القبلة والتقرب من السترة','1312','111','0');
INSERT INTO [t21618] VALUES (182,'باب في النهي عن منع النساء إذا أردن الخروج إلى المسجد، وعن إتيانهن المساجد متطيبات، والدليل على أن حضورهن الجماعة على الاختيار','1316','111','0');
INSERT INTO [t21618] VALUES (183,'بيان إباحة الصلاة في الثوب الواحد، وفي الثوب بين الاثنين، وفي الإزار الضيق المشدود طرفه على الرقبة','1329','111','0');
INSERT INTO [t21618] VALUES (184,'بيان حظر الصلاة في الثوب الواحد إذا لم يكن على عاتقه منه شيء، واشتمال الثوب على المنكب الواحد وأحد منكبيه بادي','1335','111','0');
INSERT INTO [t21618] VALUES (185,'بيان إباحة الصلاة في الثوب الواحد المتوشح به إذا اشتمل به المصلي، وإن كان واجدا لثوب آخر ولأكثر منه، وإباحة الصلاة في النعلين','1341','111','0');
INSERT INTO [t21618] VALUES (186,'بيان اللباس المنهي للرجال عن لبسه، وصفة اللبس المكروه في الصلاة، وإباحة الصلاة على الحصير والبسط والخمرة، وعلى تنحي ما يشغل المصلي عن القبلة','1348','111','0');
INSERT INTO [t21618] VALUES (187,'بيان حظر كفات الشعر والثياب في الصلاة، وتغيير حلية شعر الرجل بالسواد، ووصله شعر المرأة بغيره','1381','111','0');
INSERT INTO [t21618] VALUES (188,'بيان قيام المأموم مع الإمام إذا لم يكن معهما ثالث، ووقوف المرأة إذا صلت معهما، والدليل على أن المأموم إذا قام معهما آخر ليصلي معهما صبيا كان أو رجلا رجع حتى يقوم مع الآخر خلف الإمام ولا يتحرك الإمام عن مقامه، وأن الاثنان جماعة صبيا كان مع الإمام أو','1387','111','0');
INSERT INTO [t21618] VALUES (189,'بيان إباحة ترك انتظار الجماعة للصلاة إذا أخروها عن وقتها، وإيجاب أدائها لوقتها، وإعادتها مع الجماعة إذا صلاها وحده وينويها تطوعا، والترغيب في أداء صلاة المكتوبة في المسجد إذا فاتته في الجماعة','1394','111','0');
INSERT INTO [t21618] VALUES (190,'بيان إدراك صلاة الجماعة كلها إذا أدرك ركعة منها مع الإمام، والدليل على إدراك فضلها كلها','1402','111','0');
INSERT INTO [t21618] VALUES (191,'الدليل على أن المصلي إذا صلى لغير القبلة وهو على يقين أنها القبلة، ثم تبين له وهو في صلاته أنه يبني، وعلى قبول خبر المخبر الواحد','1410','111','0');
INSERT INTO [t21618] VALUES (192,'الدليل على أن ما أدرك المأموم من صلاة الإمام يجعل أول صلاته وافتتاحه لها، وما يعارضه من الخبر الدال على أن ما فاتته من الصلاة هي أول صلاته، وإيجاب المشي إليها إذا أقيمت','1414','111','0');
INSERT INTO [t21618] VALUES (193,'بيان النهي عن الاختصار في الصلاة، وإيجاب الانتصاب والسكون في الصلاة إلا لصاحب العذر','1419','111','0');
INSERT INTO [t21618] VALUES (194,'بيان معارضة الخبر الدال على أنه على الإباحة لا على الحتم، والترغيب في طول القنوت','1426','111','0');
INSERT INTO [t21618] VALUES (195,'بيان رفع اليدين في افتتاح الصلاة قبل التكبير بحذاء منكبيه، وللركوع ولرفع رأسه من الركوع، وأنه لا يرفع بين السجدتين','1445','111','0');
INSERT INTO [t21618] VALUES (196,'ذكر الأخبار المتضادة للباب الذي قبله في رفع اليدين، والبينة أن رفع اليدين بعد التكبير بحذاء الأذنين، والخبر الذي يدل على أنها على الإباحة','1450','111','0');
INSERT INTO [t21618] VALUES (197,'بيان التكبير في الصلاة في كل رفع وخفض','1460','111','0');
INSERT INTO [t21618] VALUES (198,'باب الدليل على أن تحريم الصلاة التكبير وتحليلها التسليم','1465','111','0');
INSERT INTO [t21618] VALUES (199,'باب إباحة الالتحاف بثوبه بعد تكبيرة الافتتاح ووضع يده اليمنى على اليسرى، والدليل على أن النبي صلى الله عليه وسلم كان يغطي يديه في صلاته ويخرجهما إذا كبر وإذا رفع رأسه من الركوع رفع يديه قبل قوله: سمع الله لمن حمده','1467','111','0');
INSERT INTO [t21618] VALUES (200,'باب ما يقال في السكتة لتكبيرة الافتتاح والقراءة، والدليل على أن جميع ما بين في هذا الباب من القول على الإباحة وكذلك الاستعاذة، وأن هذه السكتة في الركعة الأولى دون سائرها','1470','111','0');
INSERT INTO [t21618] VALUES (201,'بيان صفة الصلاة إذا استعملها المصلي كانت صلاته جائزة، والصفة التي إذا أداها بتلك الصفة لم يكن مصليا وكان عليه الإعادة','1479','111','0');
INSERT INTO [t21618] VALUES (202,'بيان الائتمام بالإمام في الصلاة وحظر مبادرته وحظر صلاة المأموم قائما إذا صلى الإمام قاعدا، وإباحة الإيماء والإشارة في الصلاة وتكبير المأموم بالجهر ليسمع الناس تكبير الإمام','1483','111','0');
INSERT INTO [t21618] VALUES (203,'بيان إباحة ترك الائتمام بالإمام في الصلاة قاعدا إذا صلى الإمام قاعدا، والدليل على نسخ صلاة المأموم قاعدا من غير عذر خلف الإمام إذا صلى قاعدا من علة، وعلى أن المأموم إذا لم يقف على ركوع الإمام وسجوده وخفي عليه تكبيره جاز له أن يقتدي بالمأموم الذي يعاين','1495','111','0');
INSERT INTO [t21618] VALUES (204,'بيان إثبات بسم الله الرحمن الرحيم في أوائل السور وترك الجهر به في افتتاح فاتحة الكتاب في الصلاة وغيرها من السور','1512','111','0');
INSERT INTO [t21618] VALUES (205,'باب النهي عن رفع الإمام صوته بالقرآن فيما يجهر فيه رفعا عاليا، والمخافتة، وإيجاب رفع صوته رفعا وسطا بين الجهر والمخافتة، وكذلك سائر المصلين، وبيان الخبر المعارض بتفسير الآية','1518','111','0');
INSERT INTO [t21618] VALUES (206,'بيان الدليل على إيجاب إعادة الصلاة لمن لم يقرأ بفاتحة الكتاب فصاعدا، وما يعارضه من الخبر المبين، وعلى إجازة الصلاة فيها إذا قرأ فيها فاتحة الكتاب وحده','1523','111','0');
INSERT INTO [t21618] VALUES (207,'ذكر الأخبار التي تبين أن الإمام والمأموم تجب عليهم قراءة فاتحة الكتاب، وأن من لم يقرأ كانت صلاته ناقصة، والدليل على أن من لم يقرأها كما بينه رسول الله صلى الله عليه وسلم كانت صلاته ناقصة ووجب عليه إعادتها، وبيان ثواب قارئها','1531','111','0');
INSERT INTO [t21618] VALUES (208,'بيان إجازة القراءة خلف الإمام، والدليل على إيجابه فيما لا يجهر فيه إلى أن يركع، وإيجاب الإنصات للإمام إذا جهر بالقراءة، وما يعارضه من الخبر الدال على إيجاب القراءة لفاتحة الكتاب خلفه وإن جهر','1544','111','0');
INSERT INTO [t21618] VALUES (209,'بيان صفة صلاة رسول الله صلى الله عليه وسلم، وتقدير ركوعها وسجودها، والاستواء من الركوع والسجود، وأن التمكث فيها على قدر القنوت فيهما، وأن الوقوف في الاستواء من الركوع قدر القعود في الاستواء بين السجدتين على قدر التسبيح في الركوع والسجود','1551','111','0');
INSERT INTO [t21618] VALUES (210,'بيان حظر مبادرة المأموم إمامه بالركوع والسجود ورفع الرأس من الركوع والسجود والتشديد فيه، والدليل على أن المأموم إذا دخل مع الإمام في صلاته، ثم سبقه الإمام بركوع أو سجود أو كبر منها فلحقه في صلاته وأتم به أن صلاته جائزة','1560','111','0');
INSERT INTO [t21618] VALUES (211,'باب إيجاب إقامة الركوع والسجود وإتمامهما','1566','111','0');
INSERT INTO [t21618] VALUES (212,'بيان حظر الكلام في الصلاة بعد إباحته فيها، والدليل على أن من تكلم فيها على الخطأ وفي الموضع الذي يظن أنه جائز له كانت صلاته جائزة، وإباحة رد السلام إشارة بيده','1570','111','0');
INSERT INTO [t21618] VALUES (213,'بيان صفة العمل الذي يجوز للمصلي أن يعمله في صلاته مما ليس منها، ودفع من يريد به سوءا عن نفسه، ولعن الشيطان فيها إذا تعرض له بتخويف، والدليل أن إباحة دفع الحية والعقرب عن نفسه بقتل أو ضرب، وإباحة التعوذ في الصلاة','1582','111','0');
INSERT INTO [t21618] VALUES (214,'بيان ذكر حمل النبي صلى الله عليه وسلم أمامة بنت زينب في الصلاة على العاتق، وإجازة الصلاة معها، وفتلته أذن ابن عباس، وتحويله من موضع إلى موضع','1588','111','0');
INSERT INTO [t21618] VALUES (215,'بيان الإباحة للإمام إذا صلى على مكان أرفع من مكان المأموم وإجازة النزول عنها والصعود إليها، والدليل على إباحة تأخر المصلي عن الصف إلى ورائه والتقدم فيها إلى صف أمامه','1594','111','0');
INSERT INTO [t21618] VALUES (216,'بيان صفة طول القيام في صلاة الظهر في الركعة الأولى والثانية، وأن القراءة في كل ركعة منها بفاتحة الكتاب وسورة وتخفيف القيام في الركعتين الأخريين، وأن القراءة في كل ركعة منهما بفاتحة الكتاب وحدها، وما يعارضه من الخبر الدال على إجازة القراءة في كل ركعة','1597','111','0');
INSERT INTO [t21618] VALUES (217,'بيان ذكر الأخبار التي تبين القراءة في صلاة المغرب','1609','111','0');
INSERT INTO [t21618] VALUES (218,'ذكر الأخبار التي تبين القراءة في صلاة العشاء','1616','111','0');
INSERT INTO [t21618] VALUES (219,'ذكر الخبر الذي فيه النهي عن طول القنوت في صلاة العشاء، وبيان سوره التي تقرأ فيها وإباحة إعادة الصلاة في الجماعة إذا كان صلاها في الجماعة مرة أخرى، والدليل على إباحة المصلي فرضه خلف الإمام المتطوع بصلاته، وإجازة صلاة المخالف نيته لنية الإمام وأن','1620','111','0');
INSERT INTO [t21618] VALUES (220,'بيان الأخبار التي تبين القراءة في صلاة الصبح، والدليل على قراءة بعض السورة فيها وقراءة سورة في ركعتين','1630','111','0');
INSERT INTO [t21618] VALUES (221,'بيان إباحة سورتين وثلاثة في ركعة، والترغيب في قراءة سورة في كل ركعة','1642','111','0');
INSERT INTO [t21618] VALUES (222,'بيان صفة الركوع في الصلاة وتسوية الظهر فيه، وصفة وضع اليدين على الركبتين فيه، وإباحة التطبيق فيه، وبيان الخبر المعارض للتطبيق المبين أنه منسوخ، والدليل على أن الجماعة إذا كانوا ثلاثة لا يتقدمهم إمامهم ويقوم وسطهم، فإذا كانوا أربعة تقدمهم إمامهم','1649','111','0');
INSERT INTO [t21618] VALUES (223,'بيان الخبر المبين قول النبي صلى الله عليه وسلم في ركوعه','1657','111','0');
INSERT INTO [t21618] VALUES (224,'باب إيجاب تعظيم الرب عز وجل في الركوع، والاجتهاد في الدعاء في السجود، وحظر القراءة في الركوع والسجود','1667','111','0');
INSERT INTO [t21618] VALUES (225,'بيان ما يقول المصلي إذا رفع رأسه من الركوع ومقدار وقوفه وثبات المأمومين قياما حتى يسجد الإمام، ثم يسجدون وبيان طول الجلوس بين السجدتين','1683','111','0');
INSERT INTO [t21618] VALUES (226,'بيان ثواب السجود والترغيب في كثرة السجود','1696','111','0');
INSERT INTO [t21618] VALUES (227,'بيان صفة السجود وإيجابه على سبعة أعظم، وحظر كف الشعر والثوب','1702','111','0');
INSERT INTO [t21618] VALUES (228,'بيان إيجاب الاعتدال في السجود ووضع اليدين ورفع المرفقين، وحظر بسط الذراعين فيه كبسط الكلب ذراعيه','1708','111','0');
INSERT INTO [t21618] VALUES (229,'بيان قول المصلي في سجوده، وبيان انتصاب القدمين في السجود','1719','111','0');
INSERT INTO [t21618] VALUES (230,'بيان إيجاب الاستواء في القعود والثبات بين السجدتين والنهي عن عقب الشيطان، وإباحة الإقعاء على القدمين في الصلاة بين السجدتين','1730','111','0');
INSERT INTO [t21618] VALUES (231,'بيان الرخصة في تسوية الحصا والتراب لموضع السجود في الصلاة مرة واحدة، والدليل على أنه مكروه إلا عند الاضطرار إليه','1733','111','0');
INSERT INTO [t21618] VALUES (232,'بيان إيجاب سجدتي السهو على الملبس عليه صلاته فلم يدر كم صلى، والدليل على إجازتها وهو قاعد في التشهد من غير أن يقوم لها، وعلى إجازة صلاته دون رجوعه إلى يقينه وبنائه عليه، وبيان الخبر المعارض له الدال على أنها غير جائزة إذا لم يرجع إلى اليقين وبيان','1739','111','0');
INSERT INTO [t21618] VALUES (233,'باب الإباحة لناسي التشهد في الركعتين الأوليين من الظهر وغيره ونهض أن يمضي في صلاته ولا يقعد وأن يسجد سجدتين يكبر في كل سجدة منهما قبل التسليم ثم يسلم','1746','111','0');
INSERT INTO [t21618] VALUES (234,'بيان الإباحة للمسلم في الركعتين الأوليين من الظهر والعصر ناسيا أن يبني على صلاته وإن ولى ظهره إلى القبلة أو خرج من المسجد أو تكلم وسجد سجدتي السهو بعد فراغه من صلاته أو بعد أن يسلم وكذلك الإمام والمأمومون إذا تكلموا في أمر الصلاة، والدليل على أن الإمام','1750','111','0');
INSERT INTO [t21618] VALUES (235,'باب التسليم بعد سجدتي السهو والبناء على صلاته بعد دخوله منزله ورجوعه إلى مصلاه إذا كان ناسيا','1755','111','0');
INSERT INTO [t21618] VALUES (236,'بيان إيجاب سجدتي السهو على الشاك في صلاته، وأن الإمام إذا نسي من صلاته يجب على المأموم أن يذكره، وأن الشاك في صلاته إذا لم يرجع إلى اليقين في الزيادة والنقصان فتوخى الصواب سجد سجدتي السهو بعد الصلاة ثم سلم','1760','111','0');
INSERT INTO [t21618] VALUES (237,'بيان الدليل على إجازة صلاة الشاك فيها إذا كان أكثر وهمه أنه الصواب وإن لم يرجع إلى يقينه إذا سجد سجدتي السهو، وصفة سجوده وأنه يسجدهما بعد ما يسلم','1762','111','0');
INSERT INTO [t21618] VALUES (238,'بيان الدليل على إيجاب سجدتي السهو إذا نسيهما الساهي في صلاته وإن انصرف منهما أو تكلم عاد فسجدهما','1768','111','0');
INSERT INTO [t21618] VALUES (239,'باب إيجاب سجدتي السهو على الساهي في صلاته وعلى من زاد فيها أو نقص، وإجازة الصلاة إذا صلى الظهر أو العصر خمسا أو زاد في صلاته على ما يجب، والدليل على أن المصلي إذا رجع إلى اليقين بأنه زاد في صلاته ركعة سجد سجدتي السهو بعد ما يسلم وكانت صلاته تامة','1777','111','0');
INSERT INTO [t21618] VALUES (240,'باب الدليل على إيجاب السجود على من قرأ السجدة وإثبات السجدات في السور','1781','111','0');
INSERT INTO [t21618] VALUES (241,'باب إثبات السجدة في سورة النجم، والدليل على أن القارئ إذا قرأ فسجد سجد من معه وأن من يسمعها لا يجب عليه السجود حتى يسجد القارئ','1786','111','0');
INSERT INTO [t21618] VALUES (242,'بيان إثبات السجدة في إذا السماء انشقت واقرأ باسم ربك الذي خلق','1790','111','0');
INSERT INTO [t21618] VALUES (243,'باب إيجاب الصلاة على النبي صلى الله عليه وسلم بعد السلام عليه وعلى عباد الله الصالحين في التشهد وثوابه','1797','111','0');
INSERT INTO [t21618] VALUES (244,'باب إجازة الصلاة من يأتم بمن لا ينوي أن يكون هو إمامه، والدليل على أن من أدرك مع الإمام بعض صلاته أنه أول صلاته، وإباحة ترك المؤذن انتظار الإمام إذا دخل وقت الصلاة','1804','111','0');
INSERT INTO [t21618] VALUES (245,'باب الدليل على أن المصلي إذا رفع رأسه من السجود من الركعة الأولى والثانية نهض ولا يثبت قاعدا قبل القيام','1806','111','0');
INSERT INTO [t21618] VALUES (246,'باب الإباحة للمصلي إذا افتتح الصلاة قائما أن يركع قاعدا وإذا افتتح قاعدا أن يركع قائما، وبيان الخبر المعارض له الدال على حظر الركوع قائما إذا افتتح قاعدا والركوع قاعدا إذا افتتح قائما','1808','111','0');
INSERT INTO [t21618] VALUES (247,'ذكر الأخبار التي تبين أن النبي صلى الله عليه وسلم كان لا يصلي قاعدا حتى كان في آخر حياته كان يصلي في تطوعه قاعدا','1818','111','0');
INSERT INTO [t21618] VALUES (248,'بيان فضل صلاة القائم على صلاة القاعد، والدليل على أن الصلاة المكتوبة لا تجوز أن يصلي قاعدا','1825','111','0');
INSERT INTO [t21618] VALUES (249,'باب صفة الجلوس في الصلاة، والدليل على أن القعود في الركعتين الأوليين والأخريين واحدة ويطمئن على فخذه اليسرى ويجعل قدمه اليسرى بين فخذه وساقه ويفرش قدمه اليمنى، وأن في كل ركعتين التشهد، والخبر المعارض لفرش القدم اليمنى','1828','111','0');
INSERT INTO [t21618] VALUES (250,'باب صفة وضع اليدين على الركبتين في التشهد وعقد الأصابع والإشارة في السبابة، والدليل على أن وضع اليدين على الفخذين والركبتين جائز','1835','111','0');
INSERT INTO [t21618] VALUES (251,'بيان التحامل بيده اليسرى على فخذه اليسرى في التشهد وأخذ الركبة اليسرى بيد اليسرى، ووضع يده اليمنى على فخذه اليمنى واليسرى على ركبتيه','1841','111','0');
INSERT INTO [t21618] VALUES (252,'بيان الإشارة بالسبابة إلى القبلة ورمي البصر إليها وترك تحريكها بالإشارة','1844','111','0');
INSERT INTO [t21618] VALUES (253,'باب إيجاب قراءة التشهد عند القعدة وافتتاحه بالتحيات، والدليل على أنه ليس فيه بسم الله','1848','111','0');
INSERT INTO [t21618] VALUES (254,'باب إيجاب اختيار الدعاء بعد الفراغ من التشهد وحكم السلام على عباد الله وإيجاب السلام على نفسه وعلى الصالحين، والدليل على أن السلام اسم من أسماء الله','1855','111','0');
INSERT INTO [t21618] VALUES (255,'بيان حظر التصفيق في الصلاة للرجال وإباحته للنساء، وإباحة التسبيح فيها للمأموم والمصلي وحده إذا نابته في صلاته نائبة يريد بها أن يعلم غيره، وإباحة الالتفاف للإمام وغيره ليقف عليها فيعمل فيها ما يجب عليه، وإباحة انصرافه قهقرى إذا صلى بعض الصلاة إذا علم','1862','111','0');
INSERT INTO [t21618] VALUES (256,'بيان الدعاء الذي يدعو به المصلي بعد فراغه من التشهد قبل السلام، وإيجاب التعوذ من أربعة أشياء في التشهد الأخير','1870','111','0');
INSERT INTO [t21618] VALUES (257,'بيان التسليمتين عند الفراغ من التشهد','1878','111','0');
INSERT INTO [t21618] VALUES (258,'بيان الدليل على أن التسليمة الواحدة غير كافية في جماعة من تسليم التشهد حتى يسلم تسليمتين، والدليل على إباحة تسليمة الواحدة للمصلي وحده','1884','111','0');
INSERT INTO [t21618] VALUES (259,'ذكر الأخبار التي تبين قول النبي صلى الله عليه وسلم على عقب تسليمه من التشهد وإعلامه من خلفه انقضاء صلاته ممن يخفى عليه فراغه من الصلاة بالتكبير، وقدر قعوده بعد التسليم في مكانه','1891','111','0');
INSERT INTO [t21618] VALUES (260,'بيان قول النبي صلى الله عليه وسلم في دبر كل صلاة من الثناء على الله تعالى','1903','111','0');
INSERT INTO [t21618] VALUES (261,'الترغيب في التسبيح والتحميد والتكبير في دبر كل صلاة وثوابه','1907','111','0');
INSERT INTO [t21618] VALUES (262,'باب صفة انصراف الإمام بعد انقضاء صلاته وحظر انصراف المأموم قبله','1915','111','0');
INSERT INTO [t21618] VALUES (263,'باب كراهية الصلاة في الموضع الذي ينام فيه فلا يستيقظ حتى يفوته وقت الصلاة','1921','111','0');
INSERT INTO [t21618] VALUES (264,'باب إيجاب قضاء صلاة المكتوبة إذا نسيها المسلم أو نام عنها في الساعة التي ذكرها أو يستيقظ من غير مدافعة، وبيان الخبر المبيح لمدافعتها، والدليل على استعمال الواجب فيها أن يصليها من غير مدافعة في أي وقت كان','1924','111','0');
INSERT INTO [t21618] VALUES (265,'باب رفع الإثم عن النائم والناسي لصلاته وأنه ليس فيها تفريط، وأن التفريط فيمن يترك أداء فرضه حتى يدخل وقت صلاة أخرى، وإيجاب إعادتها على من نام عنها من الغد لوقتها بعد ما يقضيها عند استيقاظه وبيان الخبر الدال على إباحة ترك إعادتها من الغد وأنه يكفيه','1929','111','0');
INSERT INTO [t21618] VALUES (266,'باب ثواب الصلوات السنن التي تصلى مع الصلوات المكتوبات وهي: ركعتان قبل الفجر، وأربع قبل الظهر وركعتان بعدها، وركعتان بعد صلاة المغرب، وركعتان بعد صلاة العشاء، وثواب الركعتين لا يحدث مصليهما نفسه فيهما بشيء','1936','111','0');
INSERT INTO [t21618] VALUES (267,'باب الصلوات الخمس السنن التي كان يصلي رسول الله صلى الله عليه وسلم بالنهار يداوم عليها','1940','111','0');
INSERT INTO [t21618] VALUES (268,'باب إيجاب الصلاة بين كل أذان وإقامة، والدليل على أنها على الإباحة، وإباحة صلاة النافلة قبل صلاة المغرب','1950','111','0');
INSERT INTO [t21618] VALUES (269,'بيان ثواب صلاة الضحى، والدليل على أنها ركعتان فما فوقها وإيجابها، وبيان الخبر المعارض لإباحتها المبيح لتركها','1956','111','0');
INSERT INTO [t21618] VALUES (270,'بيان إثبات صلاة الضحى من فعل رسول الله صلى الله عليه وسلم، وأنها ركعتان وأربع فما فوقها، وبيان الخبر المبين أن النبي صلى الله عليه وسلم لم يكن يداوم عليها','1961','111','0');
INSERT INTO [t21618] VALUES (271,'ذكر الأخبار التي رويت عن أم هانئ عن النبي صلى الله عليه وسلم في صلاة الضحى، وبيان وقتها وأنها لم تره صلاها إلا مرة واحدة، وأنه صلاها ثمان ركعات، وصفتها، وأن القيام والركوع والسجود فيها متقارب','1966','111','0');
INSERT INTO [t21618] VALUES (272,'باب الترغيب في الصلاة بالهاجرة وعند قرب الزوال، والدليل على أنها أفضل من صلاة الضحى','1971','111','0');
INSERT INTO [t21618] VALUES (273,'باب فضل الصلاة بين صلاة الفجر وبين صلاة الظهر على سائر صلوات النوافل التي تصلى بالنهار في غير هذا الوقت، والدليل على أنها تعدل بصلاة الليل','1974','111','0');
INSERT INTO [t21618] VALUES (274,'بيان إيجاب الركعتين يصليهما الرجل في المسجد إذا أراد الجلوس فيه، والدليل على أنه ليستا على المار فيه، وإيجابهما فيه على القادم من السفر','1976','111','0');
INSERT INTO [t21618] VALUES (275,'بيان فضل الركعتين قبل صلاة الفجر','1981','111','0');
INSERT INTO [t21618] VALUES (276,'بيان الوقت الذي يصلي فيه الركعتين قبل صلاة الفجر، والدليل على أنه يصلي إذا انفجر الفجر إلى أن تقام الصلاة، وأنهما خفيفتان، ولا صلاة بعد الفجر إلا هاتان الركعتان إلى أن تقام الصلاة','1984','111','0');
INSERT INTO [t21618] VALUES (277,'باب إباحة الاضطجاع بعد ركعتي الفجر، والحديث بعدهما قبل صلاة المكتوبة وقراءة فيهما التي كان يصليهما في بيته، وأن التطوع في البيت وركعتي الفجر في البيت أفضل منه في المسجد','1996','111','0');
INSERT INTO [t21618] VALUES (278,'بيان إباحة القنوت في صلاة الفجر إذا أراد أن يدعو لأحد أو يدعو على أحد، بعد ما يرفع رأسه من الركوع ويقول: سمع الله لمن حمده ربنا ولك الحمد، ثم يدعو شيئا يسيرا، والدليل على أنه لا يزيد فيه على الدعاء الذي يدعو لمن أراد أو يدعو عليه، ويسجد، وعلى أن','2007','111','0');
INSERT INTO [t21618] VALUES (279,'ذكر الخبر الذي يبين أن القنوت بعد ما يرفع رأسه من آخر الركعة من صلاة الفجر، والدليل على أنه ليس فيه تكبير إذا أراد أن يقنت','2017','111','0');
INSERT INTO [t21618] VALUES (280,'باب السنة في القنوت والدعاء فيه للمسلمين إذا غلب العدو عليهم، وترك القنوت إذا سلموا ورجعوا إلى أهاليهم','2020','111','0');
INSERT INTO [t21618] VALUES (281,'بيان إباحة القنوت على الأعداء الذين يصيبون بعض المسلمين بالقتل وإن لم يكن منهم غلبة ولا خوف على المسلمين في وقت القنوت، والدليل على أن قنوت النبي صلى الله عليه وسلم كان بالمدينة وقنت بعد الركوع','2023','111','0');
INSERT INTO [t21618] VALUES (282,'إباحة القنوت في صلاة الظهر في الركعة الآخرة يدعو للمؤمنين ويلعن الكافرين','2030','111','0');
INSERT INTO [t21618] VALUES (283,'باب إباحة القنوت في المغرب والعشاء في الركعة الآخرة','2032','111','0');
INSERT INTO [t21618] VALUES (284,'باب الترغيب في قيام الليل والدعاء فيه، والدليل على أن أفضل الصلوات صلاة الليل، وأن أجوب الدعاء بعد ثلث الليل، وأن الساعة التي يستجاب فيها دعاء كل مسلم لا يوقف على وقتها من الليل','2036','111','0');
INSERT INTO [t21618] VALUES (285,'باب فضل صلاة نصف الليل على سائره إلى أن يبقى سدسه','2042','111','0');
INSERT INTO [t21618] VALUES (286,'باب فضل صلاة آخر الليل على أوله','2045','111','0');
INSERT INTO [t21618] VALUES (287,'بيان الدليل على إيجاب القيام بالليل، وبيان الخبر المبين على أن القيام بالليل غير واجب، وأن الآيتين من آخر سورة البقرة تجزئ من القراءة بالليل','2048','111','0');
INSERT INTO [t21618] VALUES (288,'باب الدليل على كراهية النوم للمطيق للقيام بالليل إلى أن يصبح، وبيان بول الشيطان في أذن من ابتلي بذلك','2058','111','0');
INSERT INTO [t21618] VALUES (289,'بيان إيجاب النوم والاضطجاع إذا نعس المصلي في صلاته إذا استعجم القرآن على لسانه، والدليل على حظر الصلاة حتى يعقل صلاته وقراءته','2062','111','0');
INSERT INTO [t21618] VALUES (290,'بيان حظر الصلاة عند الكسل والفتور والحمل على النفس فيما فوق طاقتها حتى يكون نشيطا مطيقا لها','2065','111','0');
INSERT INTO [t21618] VALUES (291,'ذكر الخبر المبين دعاء النبي صلى الله عليه وسلم إذا قام من الليل إلى الصلاة، وقراءته الآيات من آخر سورة آل عمران، وبيان إباحة النظر ورفع الرأس إلى السماء، والدليل على أن التفكر فيها من السنة، وأنه إذا انصرف من العشاء صلى ركعتين في بيته ثم نام','2070','111','0');
INSERT INTO [t21618] VALUES (292,'باب إيجاب ركعتين خفيفتين للقائم بالليل للصلاة إذا أراد أن يفتتح الصلاة','2079','111','0');
INSERT INTO [t21618] VALUES (293,'باب ذكر الخبر المبين قول النبي صلى الله عليه وسلم إذا افتتح صلاته بالليل قبل القراءة','2084','111','0');
INSERT INTO [t21618] VALUES (294,'بيان وقت قيام النبي صلى الله عليه وسلم من الليل، وأنه كان ينام عند السحر، ويأتي أهله في ذلك الوقت بعد فراغه من الصلاة','2086','111','0');
INSERT INTO [t21618] VALUES (295,'بيان إباحة أداء الوتر في أية ساعة كانت من الليل، وأن النبي صلى الله عليه وسلم ربما كان يوتر أول الليل، وإباحة الجهر بالقراءة في الصلاة بالليل وإخفائه','2093','111','0');
INSERT INTO [t21618] VALUES (296,'باب إيجاب الوتر، وأنه يجب على المصلي بالليل أن يجعل آخر صلاته وترا، والدليل على أنه ليس بحتم، وأن وقت الوتر بالليل، فإذا فات الوتر بالليل وصلى الفجر لم يقضه بالنهار','2099','111','0');
INSERT INTO [t21618] VALUES (297,'بيان صفة قيام رسول الله صلى الله عليه وسلم بالليل، ووضوئه وصلاته واضطجاعه بعد صلاته، ودعائه إذا فرغ من صلاته، وأنه صلى ثلاث عشرة ركعة أوتر منها بركعة','2112','111','0');
INSERT INTO [t21618] VALUES (298,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم أوتر بتسع وبسبع وبخمس، وأنه صلى ثمان ركعات لم يقعد إلا في آخرها في صلاة الليل، ثم صلى ركعة، وأنه صلى خمس ركعات لم يجلس إلا في آخرها','2127','111','0');
INSERT INTO [t21618] VALUES (299,'بيان الأخبار التي تعارض أخبار عائشة المتقدمة في الوتر من روايتها، وأنه صلى الله عليه وسلم كان يسلم في كل ركعتين، ثم يوتر بركعة','2144','111','0');
INSERT INTO [t21618] VALUES (300,'بيان الإباحة للمصلي بالليل إذا أوتر أن يصلي بعد الوتر ركعتين سوى الركعتين قبل الفجر، من رواية عائشة، وبيان الخبر المعارض له من أن النبي صلى الله عليه وسلم جعل آخر صلاته وترا، وأن النبي صلى الله عليه وسلم كان يصلي تطوعا قبل الصلاة وبعد في بيته','2150','111','0');
INSERT INTO [t21618] VALUES (301,'باب الخبر المبين أن النبي صلى الله عليه وسلم أمر المصلي بالليل أن يصلي مثنى مثنى، ويسلم في كل ركعتين، ويوتر بواحدة، ويجعلها آخر صلاته، من غير معارض له، ومبادرة الصبح بالوتر','2155','111','0');
INSERT INTO [t21618] VALUES (302,'ذكر الخبر المبين أن الوتر ركعة من آخر الليل، وأنها توتر ما صلى المصلي قبلها من الصلاة','2166','111','0');
INSERT INTO [t21618] VALUES (303,'باب فرض صلاة المسافر والدليل على أن من وقع عليه اسم المقيم صلى أربعا','2172','111','0');
INSERT INTO [t21618] VALUES (304,'بيان صلاة النبي صلى الله عليه وسلم في السفر وتركه صلوات السنن التي كان يصليها في الحضر','2175','111','0');
INSERT INTO [t21618] VALUES (305,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم صلى في السفر وفي الأمن والسعة ركعتين، وأنه صلى ركعتي التطوع قبل الفجر','2189','111','0');
INSERT INTO [t21618] VALUES (306,'بيان إباحة الوتر في السفر على الراحلة حيث ما توجهت به، وأن النبي صلى الله عليه وسلم كان يصلي النافلة على البعير في السفر يومئ إيماء، السجود أخفض من الركوع','2193','111','0');
INSERT INTO [t21618] VALUES (307,'بيان التوقيت في قصر الصلاة إذا خرج المسافر من بلده، والسفر الذي يجوز القصر فيه، وإباحة القصر للمسافر إذا أقام ببلدة عشرا','2209','111','0');
INSERT INTO [t21618] VALUES (308,'بيان إباحة الصلاة في الرحال في السفر إذا قال المؤذن في أذانه: صلوا في رحالكم، والدليل على أنه يجب حضور الجماعة إذا لم يناد به المؤذن، وإيجاب الأذان في السفر للصلوات','2218','111','0');
INSERT INTO [t21618] VALUES (309,'بيان إباحة الجمع بين الصلاتين في السفر، والدليل على أن الجمع بينهما عند الضرورة، وأنه يؤخر المغرب إلى العشاء فيصلى بعد الشفق، ولا يقدم العشاء فيصلى مع المغرب','2224','111','0');
INSERT INTO [t21618] VALUES (310,'بيان وجه الجمع بين الظهر والعصر، وأنه يؤخر الظهر حتى يدخل وقت العصر ثم يصلى بينهما، ولا يعجل العصر فيصلى مع الظهر، وأن ذلك في السفر عند الضرورة','2230','111','0');
INSERT INTO [t21618] VALUES (311,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم كان إذا ارتحل من السفر قبل دخول وقت الظهر أخرها حتى يصليها مع العصر في وقت العصر، وإذا ارتحل بعد دخول وقت الظهر صلى الظهر وحدها، ولم يقدم العصر فيجمع بينهما','2233','111','0');
INSERT INTO [t21618] VALUES (312,'ذكر خبر ابن عباس ومعاذ عن النبي صلى الله عليه وسلم في جمعه بين الصلاتين في السفر','2235','111','0');
INSERT INTO [t21618] VALUES (313,'ذكر خبر ابن عباس عن النبي صلى الله عليه وسلم في جمعه بين الصلوات في الحضر، وأنه أخر المغرب، وبيان الخبر المعارض له الموجب لأداء صلاة الفريضة في وقتها والنهي عن تأخيرها','2238','111','0');
INSERT INTO [t21618] VALUES (314,'بيان فرض صلاة الخوف، وأنها ركعة','2249','111','0');
INSERT INTO [t21618] VALUES (315,'بيان ذكر خبر ابن عمر عن النبي صلى الله عليه وسلم في صلاة الخوف، والدليل على أنها ركعتان، وعلى الإباحة للمأموم إذا صلى مع الإمام ركعة والعدو خلفهم أن ينصرفوا إلى أصحابهم الذين هم في وجه العدو فيقفوا في مكانهم، وينصرف من لم يصل فيصلي مع الإمام ركعة، ثم','2251','111','0');
INSERT INTO [t21618] VALUES (316,'ذكر خبر جابر عن النبي صلى الله عليه وسلم في صلاة الخوف، وهي ركعتان، وفي صفتها أن العدو إذا كانوا بين المسلمين وبين القبلة يصفون خلف الإمام بأجمعهم ويدخلون معهم في صلاة، ويركعون معه، فإذا رفع رأسه وسجد سجد من يليه معه، ويثبت الآخرون قياما يحرسونهم،','2254','111','0');
INSERT INTO [t21618] VALUES (317,'ذكر خبر سهل بن أبي حثمة عن النبي صلى الله عليه وسلم في صلاة الخوف، وهي ركعتان، وصفتها أن طائفة من المسلمين يكبرون مع الإمام، وطائفة تحرسهم وجوههم إلى العدو، فإذا صلت الطائفة مع الإمام ركعة ثبت الإمام قائما وصلت لأنفسها ركعة وانصرفت إلى مكان من يحرسهم','2261','111','0');
INSERT INTO [t21618] VALUES (318,'بيان صلاة الخوف من العدو قبل اجتماعهم ووقوفهم للمسلمين، وصفتها أن الإمام يصلي بطائفة ركعتين والطائفة الأخرى تحرسهم، ثم تنصرف التي صلت فتقف مكانهم وتنصرف الطائفة التي بإزاء العدو إلى الإمام فيصلي بهم ركعتين، فيكون للإمام أربعا ولهم ركعتين ركعتين','2267','111','0');
INSERT INTO [t21618] VALUES (319,'بيان وجوب صلاة الكسوف','2270','111','0');
INSERT INTO [t21618] VALUES (320,'ذكر وجوب ذكر الله واستغفاره عند الكسوف، والدليل على أنه نذير وتحذير للعباد لينتهوا عن المعاصي ويخافوا نقمة الله، وبيان المبادرة إلى المسجد والاجتماع فيه للصلاة والنداء بها، وطول القنوت فيها والركوع والسجود','2273','111','0');
INSERT INTO [t21618] VALUES (321,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم صلى في الكسوف ست ركعات في أربع سجدات في ركعتين','2280','111','0');
INSERT INTO [t21618] VALUES (322,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم صلى في الكسوف أربع ركعات في أربع سجدات في ركعتين، وأنه أطال القيام بين الركوع والسجود، وقرأ في قيامه بين الركوعين بسورة، وأنه خطب بعد الصلاة ووعظ الناس','2283','111','0');
INSERT INTO [t21618] VALUES (323,'بيان الجهر بالقراءة في صلاة الكسوف، وقدر القيام والقراءة فيها، وأنهما ركعتان فيهما أربع ركعات وأربع سجدات، يقول في رفع الرأس من الركوع في كل مرة: " سمع الله لمن حمده، ربنا ولك الحمد "','2293','111','0');
INSERT INTO [t21618] VALUES (324,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم صلى في الكسوف ثمان ركعات وأربع سجدات في ركعتين','2296','111','0');
INSERT INTO [t21618] VALUES (325,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم كان رافعا يديه قائما في كسوف الشمس إلى القبلة، يسبح، ويحمد، ويهلل، ويكبر، ويدعو، حتى يحسر عنها فصلى ركعتين وقرأ فيهما سورتين','2300','111','0');
INSERT INTO [t21618] VALUES (326,'باب الأمر بالصدقة والعتاقة والدعاء عند كسوف الشمس والصلاة من حين تكسف حتى ينكشف عنها','2304','111','0');
INSERT INTO [t21618] VALUES (327,'كتاب الاستسقاء','2310','0','0');
INSERT INTO [t21618] VALUES (328,'باب بيان وقت تحويل الرداء، وأن الإمام إذا أراد أن يدعو يحول ظهره إلى الناس ويستقبل القبلة ويحول رداءه ويدعو ثم يصلي ركعتين ويجهر فيهما','2312','327','0');
INSERT INTO [t21618] VALUES (329,'باب الدليل على أن النبي صلى الله عليه وسلم استسقى ووجهه إلى الناس، ثم حول وجهه إلى القبلة ودعا بعد','2322','327','0');
INSERT INTO [t21618] VALUES (330,'باب صفة رفع اليدين في دعاء الاستسقاء','2324','327','0');
INSERT INTO [t21618] VALUES (331,'باب بيان الدعاء الذي دعاه النبي صلى الله عليه وسلم في الاستسقاء، وإباحة الاستسقاء في الخطبة، والدليل على أن الخطبة في الاستسقاء قبل الصلاة، وإباحة الدعاء لحبس المطر إذا كثر','2330','327','0');
INSERT INTO [t21618] VALUES (332,'باب ذكر الخبر المبين أن المطر رحمة، والترغيب في كشف الثوب عن رأسه وحسره عند المطر حتى يصيبه منه','2341','327','0');
INSERT INTO [t21618] VALUES (333,'باب بيان ما يخاف من الريح إذا هبت، وإيجاب التعوذ من شرها والسؤال من خيرها، والدليل على أنها من عند الله ربما كانت رحمة، وربما كانت نقمة وأن النبي صلى الله عليه وسلم كان يتغير لونه عند هبوبها فإذا جاء المطر سري عنه','2345','327','0');
INSERT INTO [t21618] VALUES (334,'زيادات في الاستسقاء، ما لم يخرجه مسلم رحمه الله في كتابه','2353','327','0');
INSERT INTO [t21618] VALUES (335,'مبتدأ كتاب الجمعة','2371','327','0');
INSERT INTO [t21618] VALUES (336,'والتشديد في ترك حضورها، والدليل على أنها مفروضة وحضورها حتم','2372','327','0');
INSERT INTO [t21618] VALUES (337,'باب بيان فضل الجمعة والترغيب في الدعاء والصلاة فيها','2383','327','0');
INSERT INTO [t21618] VALUES (338,'باب ذكر الخبر المبين أن في الجمعة ساعة خفيفة لا يوافقها مصل قائم يدعو فيها إلا استجيب له، والدليل على أنها ليست بعد العصر في الساعة التي لا يصلى فيها، وبيان وقتها','2389','327','0');
INSERT INTO [t21618] VALUES (339,'باب بيان السورة التي تقرأ يوم الجمعة في صلاة الفجر','2395','327','0');
INSERT INTO [t21618] VALUES (340,'باب بيان الخبر الذي يوجب الغسل يوم الجمعة على كل محتلم والطيب والسواك، والدليل على أنه على الرجال دون النساء ممن يحضر الجمعة ومن لا يحضرها، وبيان الخبر الذي يوجب الغسل في كل سبعة أيام مرة واحدة، وليس فيه ذكر الجمعة','2400','327','0');
INSERT INTO [t21618] VALUES (341,'باب ذكر الخبر المبين الذي يوجب الغسل على من يأتي الجمعة، والدليل على أنه ليس بواجب على من لم يأت','2405','327','0');
INSERT INTO [t21618] VALUES (342,'كتاب الزكاة','2447','0','0');
INSERT INTO [t21618] VALUES (343,'باب بيان تحريم الصدقة للنبي صلى الله عليه وسلم ولمن هو منه من الصغير الذي لم يبلغ والكبير، والدليل على أن من أكل الحرام ولم يعلم به ثم علم أنه يجب عليه إلقاؤه بقيء وغيره إذا قدر على ذلك، وأن الصبي إذا عمل ما لا يجوز يجب على متوليه نهيه عنه والأخذ فوق','2449','342','0');
INSERT INTO [t21618] VALUES (344,'باب الترغيب فيمن يأتي بصدقته إلى الإمام قبل أن يسألها','2455','342','0');
INSERT INTO [t21618] VALUES (345,'باب بيان فرض الزكاة وأن الإمام إذا بعث المتولي إلى بلدة أخرى لأخذها من الأغنياء أمر بردها على فقرائهم، والدليل على أنها لا تخرج من بلدة إلى بلدة غيرها، وأن فقراءها أولى بها من غيرهم، وعلى أن من وجب عليه الزكاة يسمى غنيا، ومن لم يجب لم يسم غنيا','2457','342','0');
INSERT INTO [t21618] VALUES (346,'باب بيان الإباحة للمتولي أخذ الصدقة والزكاة أن يأخذ على ذلك أجرة عمله','2459','342','0');
INSERT INTO [t21618] VALUES (347,'باب الدليل على الإباحة للإمام أن يؤخر الصدقة على من يجب عليه في ماله الصدقة، وعلى تركها لمن ينفقها في سبيل الله ويحتاج إليها ولمن له في الخمس نصيب','2462','342','0');
INSERT INTO [t21618] VALUES (348,'باب الدليل على وجوب الزكاة في حلي النساء إذا ما بلغ ما يجب فيه الزكاة، وعلى الإباحة للنساء أن يعطين أزواجهن منها إذا كانوا فقراء، وعلى إباحة أكلهن إذا أنفقوا عليهن منها، وعلى الإباحة للمزكي قبول الهبة من المزكى عليه وإعطائه منها','2465','342','0');
INSERT INTO [t21618] VALUES (349,'باب بيان الإباحة للمتصدق قبول الهبة من صدقته التي تصدق بها، وبيان الخبر المبين حظر العود في صدقته باشتراء وغيرها إن كانت تباع','2472','342','0');
INSERT INTO [t21618] VALUES (350,'باب. . . . . .','2477','342','0');
INSERT INTO [t21618] VALUES (351,'باب بيان الأطعمة التي يجب منها إخراجها وهي الطعام والشعير والتمر والزبيب والأقط، والدليل على أنها لا تخرج إلا يوم الفطر، على أنها لا تؤدى أقل من صاع','2481','342','0');
INSERT INTO [t21618] VALUES (352,'باب الدليل على أنها لا تؤدى هذه الزكاة أقل من صاع وإيجاب إخراجها على الكبير والصغير','2484','342','0');
INSERT INTO [t21618] VALUES (353,'باب الخبر الموجب بإخراجها من ثلاثة أصناف وليس فيها الحنطة','2487','342','0');
INSERT INTO [t21618] VALUES (354,'باب بيان إباحة اللعب في يوم العيد والضرب بالدف في أيام التشريق، والدليل على أنها في أيام غير العيد مكروه','2489','342','0');
INSERT INTO [t21618] VALUES (355,'باب إباحة اللعب في المسجد والنظر إليه والاشتغال به يوم العيد','2494','342','0');
INSERT INTO [t21618] VALUES (356,'باب بيان إباحة نصف العشر مما يسقى بالسانية','2511','342','0');
INSERT INTO [t21618] VALUES (357,'مبتدأ كتاب الصيام','2515','342','0');
INSERT INTO [t21618] VALUES (358,'وما فيه، وبيان فضل الصيام، وثواب الصيام','2516','342','0');
INSERT INTO [t21618] VALUES (359,'باب بيان الخبر الذي يوجب على الصائم حفظ صومه، وحظر السخب والرفث في يوم صومه، وإباحة إعلامه، والدليل على أنه ليس فيه رياء','2524','342','0');
INSERT INTO [t21618] VALUES (360,'باب بيان فضل شهر رمضان على سائر الشهور، والدليل على أن أعمال البر فيه على المسلم أيسر منه في غيره من الشهور','2529','342','0');
INSERT INTO [t21618] VALUES (361,'باب بيان ثواب من صام رمضان، وفضيلة صومه إذا أتبع بصوم ستة أيام من شوال','2533','342','0');
INSERT INTO [t21618] VALUES (362,'باب بيان النهي عن تقدم رمضان بصوم يوم أو يومين من آخر شهر شعبان، وأن الخبر الموجب لصيام آخر شهر شعبان الدال على أن النهي عن صومه لمن صامه بحال شهر رمضان، وعن صوم يوم الشك','2539','342','0');
INSERT INTO [t21618] VALUES (363,'باب بيان النهي عن صوم آخر النصف من شعبان، وبيان الخبر المعارض له المبيح صومه، والخبر المبين فضيلة صومه على صوم سائر الشهور، والدال على توهين الخبر الناهي عن صيامه','2543','342','0');
INSERT INTO [t21618] VALUES (364,'باب ذكر الخبر المبين أن الشهر يكون تسعا وعشرين ويكون ثلاثين، وأن الشهرين في السنة لا يجتمعان في التسع والعشرين رمضان وذا الحجة','2562','342','0');
INSERT INTO [t21618] VALUES (365,'باب ذكر الخبر المبين أن الأهلة بعضها أعظم من بعض، وأنه إذا كان عظيما لا يعد إلا لليلة، والدليل على أن رؤية الهلال هي حيث يهل الليل','2569','342','0');
INSERT INTO [t21618] VALUES (366,'باب بيان الخبر الذي يوجب على من يريد الصوم أن يتسحر والترغيب فيه، وبيان الخبر الدال على أنه على الإباحة','2571','342','0');
INSERT INTO [t21618] VALUES (367,'باب بيان وقت أكل السحر، وإباحة أكله إلى أن يتبين الفجر الصادق، وإن سمع الأذان قبل ذلك','2585','342','0');
INSERT INTO [t21618] VALUES (368,'باب بيان إباحة التسحر حتى يتبين بياض النهار، والدليل على أن الشاك فيه جائز له أن يأكل حتى يستيقن بالنهار','2594','342','0');
INSERT INTO [t21618] VALUES (369,'باب بيان صفة الفجر الذي به يحرم الطعام والشراب، وإباحة الأكل والشرب والجماع قبله، وأن ما قبله من بياض الفجر لا يسمى فجرا، وبيان السنة في إعلام المؤذن الناس لأذانه ليستيقظوا فيتسحروا ويترك المصلي صلاته فيتسحر، والدليل على أنه يجب عليه أن يعلم الناس','2599','342','0');
INSERT INTO [t21618] VALUES (370,'باب بيان الوقت الذي يحصل للصائم الإفطار، والدليل على أنه إذا دخل ذلك الوقت كان الصائم مفطرا وإن لم يأكل ولم يشرب','2603','342','0');
INSERT INTO [t21618] VALUES (371,'باب بيان الترغيب في تعجيل الإفطار للصائم','2606','342','0');
INSERT INTO [t21618] VALUES (372,'باب بيان النهي عن الوصال في رمضان، والدليل على إباحته لمن أطاقه، وعلى أن النهي عنه رفقا بالناس','2609','342','0');
INSERT INTO [t21618] VALUES (373,'باب الدليل على أن الصائم إذا واصل كان مفطرا إذا غابت الشمس','2621','342','0');
INSERT INTO [t21618] VALUES (374,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم كان يصوم في السفر في رمضان ويفطر أصحابه','2626','342','0');
INSERT INTO [t21618] VALUES (375,'باب بيان إبطال فضل الصوم في السفر، والدليل على أن الفطر في السفر أفضل من الصوم، وبيان الخبر المعارض لإبطال فضل الصوم والمبين ثوابه في سبيل الله','2629','342','0');
INSERT INTO [t21618] VALUES (376,'باب بيان حظر الصوم في الغزو عند توقع الاجتماع مع العدو بعد يوم وإباحته قبله','2635','342','0');
INSERT INTO [t21618] VALUES (377,'باب ذكر الخبر المبين أن الصائم في السفر لا يجوز له أن يعيب المفطر بفطره، ولا المفطر أن يعيب الصائم، وأن النبي صلى الله عليه وسلم لم يعب على هؤلاء ولا على هؤلاء، والدليل على أن ذلك كان من النبي صلى الله عليه وسلم في الغزو والحج كليهما','2638','342','0');
INSERT INTO [t21618] VALUES (378,'باب ذكر الخبر الدال على إباحة الإفطار في كل سفر، وإباحة الإفطار إذا ابتدأ بالصوم في أول الشهر، وإباحة الصوم إذا ابتدأ الإفطار','2647','342','0');
INSERT INTO [t21618] VALUES (379,'باب بيان إيجاب الصوم على من أدرك الشهر، وإيجاب الإفطار في السفر، وبيان الخبر المبين أنه على الإباحة، ونسخ الدية على من لم يطق الصوم، والدليل على أن من لم يستيقن بشهوده لا يصومه','2649','342','0');
INSERT INTO [t21618] VALUES (380,'باب بيان إباحة صيام الآكل والشارب ناسيا وأنه ليس عليه إعادة، والدليل على أن من تسحر وهو على يقين أنه الليل ثم تبين خلافه أن عليه إعادة ذلك اليوم، وكذلك المفطر الذي هو على يقين أنه الليل ثم تبين خلافه','2653','342','0');
INSERT INTO [t21618] VALUES (381,'باب بيان إباحة إفطار الصائم من صيام التطوع، والدليل على أنه ليس عليه إعادة ذلك اليوم، وعلى أن الصائم إذا أراد الصوم نواه من الليل وأصبح صائما','2657','342','0');
INSERT INTO [t21618] VALUES (382,'باب بيان إجازة الصوم إذا أدركه الصبح وهو جنب من الجماع، وإباحة الجماع في شهر رمضان بالليل','2662','342','0');
INSERT INTO [t21618] VALUES (383,'باب بيان حظر الجماع في شهر رمضان بالنهار وما فيه من الكفارة، والدليل على أن عليه الكفارة في الأحوال كلها بحال، ووجوب الصوم عليه أو لم يجب إذا كان ذلك في رمضان بالنهار','2670','342','0');
INSERT INTO [t21618] VALUES (384,'باب الدليل على أن الصدقة واجبة على الذي يقع على امرأته في رمضان نهارا وإن لم يكن واجدا لها، وأنها غير ساقطة عنه لعدمها، وأنها إذا وصل إليها تصدق بها','2677','342','0');
INSERT INTO [t21618] VALUES (385,'باب بيان وجوب الكفارة على من يفطر في رمضان متعمدا أن يعتق رقبة، أو صيام شهرين متتابعين، أو إطعام ستين مسكينا','2681','342','0');
INSERT INTO [t21618] VALUES (386,'باب بيان إباحة المباشرة والقبلة للصائم في شهر رمضان وغيره، والدليل على استحباب تركهما','2684','342','0');
INSERT INTO [t21618] VALUES (387,'باب بيان إسقاط صوم رمضان عن الحائض، ووجوب إعادته وإباحة تأخيرها إلى شهر رمضان','2698','342','0');
INSERT INTO [t21618] VALUES (388,'باب الخبر الموجب على ولي الميت قضاء صومه عنه إذا مات وعليه صوم واجب','2708','342','0');
INSERT INTO [t21618] VALUES (389,'باب بيان الأيام التي نهى النبي صلى الله عليه وسلم عن صيامهن، منهن يوم الفطر ويوم الأضحى','2721','342','0');
INSERT INTO [t21618] VALUES (390,'باب بيان النهي عن صوم أيام منى وهي أيام التشريق','2728','342','0');
INSERT INTO [t21618] VALUES (391,'باب بيان النهي عن أن يخص يوم الجمعة بصيام من بين الأيام وحظر صومها إلا أن يصوم معها يوما قبلها أو بعدها','2731','342','0');
INSERT INTO [t21618] VALUES (392,'باب ذكر الأخبار الدالة على حظر صوم الدهر وإبطال فضيلته','2737','342','0');
INSERT INTO [t21618] VALUES (393,'باب ذكر الأخبار التي تعارض حظر سرد الصوم والدليل على إبطال فضيلة صوم رجب','2745','342','0');
INSERT INTO [t21618] VALUES (394,'باب بيان حظر صوم المرأة تطوعا إلا بإذن زوجها إذا كان شاهدا','2754','342','0');
INSERT INTO [t21618] VALUES (395,'باب بيان فضيلة صوم عرفة وثوابه، وثواب صوم يوم عاشوراء، والترغيب في صوم يوم الاثنين، وفضيلة صوم ثلاثة أيام من كل شهر، والدليل على أنه ليس لنصف الشهر في الصوم فضل على أوله وآخره، وأنه إذا صام ثلاثة أيام من الشهر من أيه كان كتب به صيام الدهر','2761','342','0');
INSERT INTO [t21618] VALUES (396,'باب ذكر الخبر الذي يبين أنه ليس في السنة شهر يصام فيه بعد رمضان أفضل من المحرم وأنه ليس يوم في السنة بعد رمضان يصومه الصائم أفضل من يوم عاشوراء','2768','342','0');
INSERT INTO [t21618] VALUES (397,'باب صفة بدء عاشوراء وأمر النبي صلى الله عليه وسلم أصحابه بصومه','2772','342','0');
INSERT INTO [t21618] VALUES (398,'باب الخبر الموجب لصوم يوم عاشوراء، والخبر المبين له الدال على أن الأمر بصومه منسوخ وأن صومه تطوع لمن صامه، وذكر الخبر المبين أن النبي صلى الله عليه وسلم تركه بعد ما صامه وكان يصومه قبل أن يقدم المدينة، لا أنه صامه لذكر يهود ما فيه ولصومهم','2778','342','0');
INSERT INTO [t21618] VALUES (399,'باب ذكر الخبر المبين أن صوم يوم عاشوراء لم يكن في الأصل صومه واجبا، وأن النبي صلى الله عليه وسلم صامه بعد ما أخبر بإباحة فعله، وأنه صلى الله عليه وسلم كان يحث أصحابه على صومه قبل نزول صوم شهر رمضان','2800','342','0');
INSERT INTO [t21618] VALUES (400,'باب ذكر الخبر المبين على أن النبي صلى الله عليه وسلم صام يوم عاشوراء يوم العاشر، والدليل على أن السنة في صومه؛ يوم التاسع','2805','342','0');
INSERT INTO [t21618] VALUES (401,'باب بيان الترغيب في صوم شعبان، وصفة صوم النبي صلى الله عليه وسلم، وأنه لم يصم في عشر ذي الحجة ولا يوم عرفة، وبيان الترغيب في العمل في عشر ذي الحجة','2810','342','0');
INSERT INTO [t21618] VALUES (402,'باب ذكر الخبر المبين أن أحب الصيام إلى الله عز وجل وأفضله صيام داود عليه السلام، صوم يوم وإفطار يوم','2832','342','0');
INSERT INTO [t21618] VALUES (403,'باب الترغيب في قيام الليل والصلاة في شهر رمضان وثوابه، وأن النبي صلى الله عليه وسلم صلى هذه الصلوات في المسجد وصلاها معه ناس، والدليل على أنه صلى الله عليه وسلم كمن في البيت وأخفاها عن الناس رفقا بهم، وأن عمر بن الخطاب رضي الله عنه سنها اتباعا','2838','342','0');
INSERT INTO [t21618] VALUES (404,'باب بيان إباحة التعفيف في شهر رمضان بالليل للصلاة والاجتماع لها في المسجد','2849','342','0');
INSERT INTO [t21618] VALUES (405,'باب مبلغ عدد الركعات التي كان رسول الله صلى الله عليه وسلم يصليها من الليل في شهر رمضان، وأنه كان يداوم عليها في سائر الشهور','2852','342','0');
INSERT INTO [t21618] VALUES (406,'باب ذكر الخبر المعارض لخبر علقمة عن عائشة في إيثار أيام من بين الأيام بالعمل، المبين أن النبي صلى الله عليه وسلم كان يجتهد في العشر الأواخر من رمضان ما لا يجتهد في غيرها من الأيام، الدال على أنه صلى الله عليه وسلم ربما طول في هذه الركعات المعلومات التي','2856','342','0');
INSERT INTO [t21618] VALUES (407,'باب بيان خروج النبي صلى الله عليه وسلم من بيته بالليل إلى المسجد لصلاة الليل ورفع يديه في صلاته وصلاة أصحابه خلفه والإباحة للإمام أن يحتجر من المسجد حجرة للصلاة فيها والإباحة للمصلي أن يصلي صلاة من يحول بينه وبين النظر إليه جدار أو سترة وإباحة صلاة','2859','342','0');
INSERT INTO [t21618] VALUES (408,'باب صفة بدء اعتكاف النبي صلى الله عليه وسلم في المسجد في شهر رمضان وإنه إنما اعتكف تلمسا لليلة القدر وكان لا يزيد على عشرة أيام إذا اعتكف من أول الشهر أو من أوسطه وبيان الليالي التي ترجى فيها ليلة القدر','2867','342','0');
INSERT INTO [t21618] VALUES (409,'باب الدليل على إيجاب الاعتكاف في شهر رمضان في العشر الأواخر وعلى أن الاتباع والسنة في ترك الاعتكاف قبل العشر وعلى أن الليلة التي ترجى أن تكون ليلة القدر تمطر فيها وعلى أن النبي صلى الله عليه وسلم كان يعتكف في العشر الأواخر إذا أصبح من عشرين','2870','342','0');
INSERT INTO [t21618] VALUES (410,'باب بيان الساعة والوقت التي كان يعتكف النبي صلى الله عليه وسلم والدليل على أنه صلى الله عليه وسلم لم يبق في اعتكافه بالليل','2876','342','0');
INSERT INTO [t21618] VALUES (411,'باب بيان الإباحة للنساء أن يعتكفن في المسجد، والدليل على حظر اعتكافهن إلا بإذن أزواجهن، وأنه ليس عليهن قضاء إذا نقضن اعتكافهن إذا اعتكفن بغير إذن أزواجهن، وأن النبي صلى الله عليه وسلم كان إذا فرغ من صلاته لم يثبت في مصلاه ورجع إلى خبائه','2879','342','0');
INSERT INTO [t21618] VALUES (412,'باب بيان الخبر أن النبي صلى الله عليه وسلم كان يعتكف العشر الأواخر','2882','342','0');
INSERT INTO [t21618] VALUES (413,'كتاب الحج','2883','0','0');
INSERT INTO [t21618] VALUES (414,'باب بيان الإباحة للمحرم غسل رأسه ودلكه رأسه بالماء','2884','413','0');
INSERT INTO [t21618] VALUES (415,'باب بيان خطبة التزويج في الإحرام أو الخطبة','2887','413','0');
INSERT INTO [t21618] VALUES (416,'باب ذكر تزويج رسول الله صلى الله عليه وسلم في إحرامه ميمونة والخبر المعارض المبين تزوجها وهو حلال','2895','413','0');
INSERT INTO [t21618] VALUES (417,'باب صفة الكفن إذا مات المحرم وغسله وحظر تخمير وجهه ورأسه، وتطييبه وتحنيطه والأمر بكشف وجهه','2900','413','0');
INSERT INTO [t21618] VALUES (418,'باب بيان بعض المساجد التي كان يصلي فيها رسول الله صلى الله عليه وسلم في طريقه إلى مكة بعد خروجه من ذي الحليفة','2915','413','0');
INSERT INTO [t21618] VALUES (419,'باب ذكر الخبر المبين أن رسول الله صلى الله عليه وسلم كان إذا قدم مكة بات بذي طوى ولا يدخل مكة ليلا ويصلي الصبح بذي طوى','2919','413','0');
INSERT INTO [t21618] VALUES (420,'باب ذكر الخبر المبين أن رسول الله صلى الله عليه وسلم كان إذا بلغ الحرم والعرش قطع التلبية حتى يدخل مكة، وأنه كان يصلي الغداة ثم يغتسل ثم يدخل مكة، وبيان الخبر المبين أنه صلى الله عليه وسلم أول شيء بدأ به حين قدم مكة توضأ، ثم طاف بالبيت، وأنه كان يلبي','2928','413','0');
INSERT INTO [t21618] VALUES (421,'باب بيان الطريق الذي منه دخل النبي صلى الله عليه وسلم مكة والطريق الذي منه خرج والرخصة في دخول مكة بغير إحرام لعلة تحدث','2938','413','0');
INSERT INTO [t21618] VALUES (422,'باب بيان إباحة الركوب للناذر بالمشي إلى بيت الله تبارك وتعالى','2952','413','0');
INSERT INTO [t21618] VALUES (423,'باب ذكر الخبر الموجب قضاء النذر بالحج عن الناذر إذا مات ولم يف به أوصى بذلك أم لا، والدليل على أن الحج الواجب من جميع المال أمر به الميت أم لا يقضي عنه وليه','2955','413','0');
INSERT INTO [t21618] VALUES (424,'باب بيان إسقاط الهدي عن المرأة التي تعتمر ثم تحيض يفسد عمرتها حيضها وتهل بالحج ثم تعتمر بعد، والدليل على إسقاط الهدي عن المتمتع الذي يفسد عمرته ويهل بالحج','2959','413','0');
INSERT INTO [t21618] VALUES (425,'باب ذكر الخبر المبين أن عائشة رضي الله عنها أهلت بعمرة مع النبي صلى الله عليه وسلم في حجة الوداع، والدليل على أن من أهل بعمرة فأفسدها حل ثم أهل بالحج يوم التروية فإذا فرغ من قضاء نسكه وخرج من منى مال إلى ناحية التنعيم قبل أن يقدم مكة وقبل طواف الإفاضة','2962','413','0');
INSERT INTO [t21618] VALUES (426,'باب ذكر الخبر المبيح للمحرم الرجوع إلى سنته في الإهلال إن شاء أحرم بالحج، وإن شاء أحرم بعمرة، والدليل على أن الاختيار منهما ما اختاره المهل به حجا كان أو عمرة، وعلى أن عائشة رضي الله عنها قضت عمرتها من نحو الموضع الذي حاضت به','2973','413','0');
INSERT INTO [t21618] VALUES (427,'باب الدليل على الإباحة للمعتمر أن يضم إلى عمرته حجة إن اضطر إلى ذلك فلم يقدر على أن يحل من عمرته، وعلى أن عائشة رضي الله عنها لم يجب عليها قضاء عمرتها التي لم تحل منها عمرة جائزة، وكذلك المفسد عمرته وأهل بحجة، وعلى أن عائشة طافت بعمرتها وحجها، ثم','2975','413','0');
INSERT INTO [t21618] VALUES (428,'باب ذكر الخبر المبين أن فسخ الحج بعمرة لمن لا يكون معه هدي على الإباحة لا على الحتم، وأن المهل بالحج إذا قدم مكة ولم يكن معه هدي إن أحب أقام على إحرامه إلى انقضاء نسكه، وإن أحب جعلها عمرة، وحظر فسخ الحج لمن معه هدي، والدليل على أن فسخ الحج لا يكون','2983','413','0');
INSERT INTO [t21618] VALUES (429,'باب الإباحة للحائض أن تقضي المناسك كلها وتقف المواقف كلها إلا الطواف بالبيت، والدليل على أن عمرة عائشة من التنعيم كانت أفضل من عمرة سائر أزواج النبي صلى الله عليه وسلم لزيادة نصبها وتعبها، وأن العمرة من الميقات أفضل منه من التنعيم','2988','413','0');
INSERT INTO [t21618] VALUES (430,'باب ذكر الخبر الموجب على المعتمر الطواف بالبيت والطواف بين الصفا والمروة، وتقصير الرأس إن أراد أن يحل، ثم يهل بالحج، وإن من فعل ذلك كان عليه الهدي، فإن لم يجد صام ثلاثة أيام في الحج، وسبعة إذا رجع إلى أهله','3002','413','0');
INSERT INTO [t21618] VALUES (431,'باب ذكر الخبر المبيح للمعتمر أن يحل إذا طاف بالبيت، وإن لم يطف بين الصفا والمروة، وأن الحاج إذا طاف بالبيت قبل خروجه إلى منى حل وكان طوافه عمرة، والخبر المعارض له المبين أن طوافهم بالبيت دون الصفا والمروة قبل نزول هذه الآية إن الصفا والمروة وأنهم','3012','413','0');
INSERT INTO [t21618] VALUES (432,'باب ذكر الخبر المبين الموجب على من ينحر بمنى أن ينحر في رحله حيث كان من منى، وأن منى كلها منحر، وصفة نحر البدنة والذبيحة','3025','413','0');
INSERT INTO [t21618] VALUES (433,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم حلق رأسه في حجة الوداع بعد ما نحر بدنه، والدليل على أن السنة في نحر البدنة أن ينحر صاحبها بيده والحلاق ينتظره فلا يشتغل بشيء بعد نحره إلا بحلق الرأس، وعلى أن شعور المسلمين طاهرة مباح للمسلم إمساكها،','3031','413','0');
INSERT INTO [t21618] VALUES (434,'باب الترغيب في حلق الرأس بعد رمي الجمار، والدليل على إباحة التقصير، وعلى أن السنة بعد الحلق تقليم الأظفار','3044','413','0');
INSERT INTO [t21618] VALUES (435,'باب بيان إجازة حج من قدم الذبح قبل رمي الجمرة أو حلق قبل الذبح، والدليل على أن ذلك للجاهل والناسي','3056','413','0');
INSERT INTO [t21618] VALUES (436,'باب بيان حظر الانتفاع بشيء من لحوم الهدي الواجب وجلودها وجلالها والأكل منها، ودفع شيء منها إلى الجزار','3062','413','0');
INSERT INTO [t21618] VALUES (437,'باب بيان إباحة أكل الرجل من بدنته التي ينحرها بنفسه المتطوع بها','3068','413','0');
INSERT INTO [t21618] VALUES (438,'باب بيان الإباحة للمتمتع ذبح البقرة والاشتراك فيها، وأنها كافية عن سبعة، وأنها من البدن وهي والإبل سواء','3070','413','0');
INSERT INTO [t21618] VALUES (439,'باب ذكر الخبر الموجب على المنفسخ حجه الهدي، وإجازته البدنة فيه عن سبعة، وأن من ذبح عمن يجب عليه الهدي كان جائزا عنه، والدليل على أن المنفسخ عمرته يهدي هديا','3075','413','0');
INSERT INTO [t21618] VALUES (440,'باب في الإفاضة إلى البيت، والدليل على أن وقته إذا فرغ من النحر وتفريق ذبيحته والأكل منها، ثم يفيض فيصلي الظهر بمكة، وبيان الخبر المعارض لصلاة الظهر بمكة وأنه يرجع إلى منى فيصلي الظهر بمنى، والترغيب في الاستقاء من زمزم للناس والشرب منه إذا أفاض','3082','413','0');
INSERT INTO [t21618] VALUES (441,'باب بيان إجازة حج من أفاض إلى البيت قبل أن يرمي الجمرة جاهلا','3087','413','0');
INSERT INTO [t21618] VALUES (442,'باب بيان إباحة التطيب بالطيب يوم النحر قبل الإفاضة وزيارة البيت والإحلال، وأن من طاف للإفاضة حل له كل شيء حرم عليه','3090','413','0');
INSERT INTO [t21618] VALUES (443,'باب بيان إتيان النساء في أيام منى','3103','413','0');
INSERT INTO [t21618] VALUES (444,'باب بيان الإباحة للحائض ترك طواف الوداع إذا كانت أفاضت يوم النحر وطافت بالبيت، والدليل على حظر خروجهن إلا بالطواف بالبيت بعد فراغهن من رمي جمرة العقبة','3106','413','0');
INSERT INTO [t21618] VALUES (445,'باب الدليل على إباحة ترك الرمل في طواف الزيارة للمفرد بالحج وللقارن، وعلى أنه ليس على أحد في طواف الإفاضة الطواف بين الصفا والمروة','3118','413','0');
INSERT INTO [t21618] VALUES (446,'باب بيان إباحة البيتوتة بمكة أيام منى لمتولي السقاية، والدليل على أنه غير جائز لغيرهم البيتوتة أيام منى إلا بمنى','3122','413','0');
INSERT INTO [t21618] VALUES (447,'باب ذكر الخبر الموجب على متولي السقاية اتخاذ النبيذ فيها، وسقي الناس فيه، وصفة شرب النبي صلى الله عليه وسلم','3125','413','0');
INSERT INTO [t21618] VALUES (448,'باب ذكر الخبر الموجب على أن من أفرد الحج ولم يسق الهدي أن عليه فسخ حجه بعمرة، ويحل الحل كله من النساء وغيرهن لم يهل بالحج، وبيان الخبر المعارض له المبيح لمن أهل بالحج ألا يفسخه حتى يقضي نسكه','3134','413','0');
INSERT INTO [t21618] VALUES (449,'باب ذكر الخبر المبين بأن فسخ الحج والمتعة خاص، وأنها منسوخة، والنهي عنها والأمر بالفصل بينهما','3146','413','0');
INSERT INTO [t21618] VALUES (450,'باب ذكر الأخبار المعارضة للنهي عن المتعة وفسخ الحج والجمع بينه وبين العمرة، وأنها عام لا خاص، والدليل على أن العمرة واجبة مع الحج في أشهر الحج، وأن التمتع أفضل من الإفراد والقران مع سوق الهدي، وإثباتها وأنها غير مفسوخة','3159','413','0');
INSERT INTO [t21618] VALUES (451,'باب بيان الإباحة للمحرم أن يهل كإهلال من تقدمه في الإحرام من غير أن يعلم بما أهل، والدليل على أن المهل به إذا لم يكن معه الهدي وكان المقتدي به ساق الهدي أن عليه أن يجعلها عمرة، ثم يهل بالحج يوم التروية، وأنه وإن كان معه الهدي ثبت على إحرامه وأهدى','3178','413','0');
INSERT INTO [t21618] VALUES (452,'باب ذكر الخبر المبين أن القارن إذا قدم مكة طاف بالبيت وبالصفا والمروة طوافا واحدا ويكفيه هذا الطواف لحجه وعمرته وينحر ويحلق يوم النحر ويكفيه طوافه الأول','3186','413','0');
INSERT INTO [t21618] VALUES (453,'باب بيان الإباحة للمهل أن لا يذكر حجا ولا عمرة إذا نوى واحدا منهما، والدليل على أن من لا ينوي واحدا منهما ونوى الإحرام جعلها عمرة، وأن المعتمر إذا طاف وحل ثم أهل بالحج جاز له أن لا يطوف لإهلاله','3193','413','0');
INSERT INTO [t21618] VALUES (454,'باب ذكر صفة طواف رسول الله صلى الله عليه وسلم أول ما يقدم مكة وابتداء طوافه باستلام الركن الأسود، والرمل في طوافه وصفته، وبيان العلة التي لها أمر النبي صلى الله عليه وسلم بالرمل، وصفة صلاته بعد طوافه والقراءة فيها','3198','413','0');
INSERT INTO [t21618] VALUES (455,'باب بيان الركوب في الطواف بالكعبة وإباحة استلام الركن بالمحجن إذا زوحم عليه','3219','413','0');
INSERT INTO [t21618] VALUES (456,'باب بيان ما يستلم الطائف بالكعبة من أركانها بيده ومحجنه، وتقبيله يده ومحجنه بعد الاستلام','3224','413','0');
INSERT INTO [t21618] VALUES (457,'باب ذكر الخبر أن النبي صلى الله عليه وسلم كان يقبل الحجر، والسنة في استقباله لمن يريد استلامه','3238','413','0');
INSERT INTO [t21618] VALUES (458,'باب بيان صفة الطواف بين الصفا والمروة ومكان موضع السعي فيه، وموضع المقام على الصفا والمروة، والثناء على الله عز وجل والدعاء، وأنه سبعة أطواف يبدأ بالصفا ويختم بالمروة','3248','413','0');
INSERT INTO [t21618] VALUES (459,'باب بيان إباحة الركوب في الطواف بين الصفا والمروة، وأن المشي والسعي بينهما أفضل، وذكر العلة التي لها ركب النبي صلى الله عليه وسلم في طوافه بينهما، والعلة التي لها أمر بالسعي بينهما','3254','413','0');
INSERT INTO [t21618] VALUES (460,'باب بيان اليوم الذي فيه خرج رسول الله صلى الله عليه وسلم من مكة وإلى منى، ومقدار مقامه بمنى، وأنه دفع من منى يوم عرفة لما طلعت الشمس فلم يزل بالمشعر وجازه حتى نزل بنمرة في قبة ضربت له من شعر وهي عرفات، وأنه لما زاغت الشمس ركب راحلته وأتى بطن الوادي','3262','413','0');
INSERT INTO [t21618] VALUES (461,'باب ذكر الخبر المبيح لمن يدفع من منى إلى عرفات قبل طلوع الشمس يوم عرفة قبل طلوع الفجر ملبيا إلى عرفات وإباحة التكبير بدل التلبية','3268','413','0');
INSERT INTO [t21618] VALUES (462,'باب ذكر الخبر الموجب لنزول عرفات والوقوف بها للصلاة والإفاضة منها إلى الموقف، والنهي عن الإفاضة من منى ومن جمع إلى الموقف','3274','413','0');
INSERT INTO [t21618] VALUES (463,'باب بيان ثواب من يقف بعرفة والموقف، وأن عرفة كلها موقف','3283','413','0');
INSERT INTO [t21618] VALUES (464,'باب بيان دفع رسول الله صلى الله عليه وسلم من الموقف، وموضع مناخه قبل أن يأتي المزدلفة بعد المغرب، ووضوئه ونزوله بالمزدلفة ودفعه من قبل أن يصلي المغرب، وأقام صلاة المغرب قبل أن يفتح الناس رحالهم فصلاها، ثم أناخ الناس في منازلهم ولم يحطوا رحالهم حتى','3286','413','0');
INSERT INTO [t21618] VALUES (465,'باب الدليل على أنه لا يصلي المغرب قبل الوصول إلى جمع، وأن المزدلفة هي المصلى، وأن النبي صلى الله عليه وسلم توضأ بالشعب لبوله ولم يسبغ، ثم أعاده بجمع وأسبغه، وأنه هو أقام الصلاة وصلى المغرب','3289','413','0');
INSERT INTO [t21618] VALUES (466,'باب ذكر صفة سير النبي صلى الله عليه وسلم حين دفع من عرفة حتى أتى المزدلفة والاختلاف في سيره، وأنه أناخ بالشعب قبل أن يأتي جمعا','3294','413','0');
INSERT INTO [t21618] VALUES (467,'باب ذكر الخبر المبين أن رسول الله صلى الله عليه وسلم صلى المغرب والعشاء بالمزدلفة بأذان واحد وإقامتين، وأنه لم يتطوع بينهما، والدليل على أنه لم يتطوع تلك الليلة','3302','413','0');
INSERT INTO [t21618] VALUES (468,'باب ذكر الخبر المخالف لما قبله في الإقامة لصلاة المغرب والعشاء بالمزدلفة، وأنه صلى الله عليه وسلم صلاهما بإقامة واحدة','3305','413','0');
INSERT INTO [t21618] VALUES (469,'باب ذكر الخبر المبين عدد صلاة المغرب والعشاء بالمزدلفة، وأن النبي صلى الله عليه وسلم جمع بها','3309','413','0');
INSERT INTO [t21618] VALUES (470,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم صلى صلاة الفجر بالمزدلفة قبل ميقاتها، والدليل على أن حكم الصلاة بالمزدلفة وفي الحج بخلاف حكم الصلوات في السفر والحضر، وأن النبي صلى الله عليه وسلم كان يصلي بمنى صلاة المسافر','3317','413','0');
INSERT INTO [t21618] VALUES (471,'باب ذكر الخبر المخالف لما قبله من صلاة رسول الله صلى الله عليه وسلم صلاة الفجر قبل ميقاتها، وأنه أذن للفجر وأقام بجمع','3325','413','0');
INSERT INTO [t21618] VALUES (472,'باب بيان إباحة دفع ضعفة الناس من المزدلفة إلى منى بالليل، والوقوف بالمشعر بالليل، والإباحة لهم ترك الوقوف مع الإمام','3327','413','0');
INSERT INTO [t21618] VALUES (473,'باب دفع النبي صلى الله عليه وسلم من المزدلفة، وصفة وقوفه بالمشعر ودعائه ودفعه من المشعر قبل طلوع الشمس، وتحريكه راحلته ببطن محسر، وصفة طريقه إلى الجمرة الكبرى وتلبيته في طريقه حتى رمى جمرة العقبة','3347','413','0');
INSERT INTO [t21618] VALUES (474,'باب بيان صفة سير النبي صلى الله عليه وسلم حين دفع من جمع، وبيان صفة الحصى التي ترمى بها الجمرة، والدليل على حمله من محسر، والإيضاع في وادي محسر','3353','413','0');
INSERT INTO [t21618] VALUES (475,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم لما رجع من جمع إلى منى لم ينزل عن راحلته وبدأ بجمرة العقبة فرماها، ثم انصرف فوقف الناس وخطبهم','3367','413','0');
INSERT INTO [t21618] VALUES (476,'باب بيان الموضع الذي منه يرمي الجمرة وبيان إباحة رمي الجمار راكبا، والدليل على أن السنة في الركوب للإمام المقتدى به، وأن النبي صلى الله عليه وسلم رمى راكبا ليؤخذ عنه','3373','413','0');
INSERT INTO [t21618] VALUES (477,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم رمى جمرة العقبة يوم النحر عند الضحى، ولم يرم غيرها، وأنه رماها بسبع حصيات يكبر مع كل حصاة، وأنه كان يرمي بعد ذلك الجمرات كلها بعد زوال الشمس، وأن الجمار وتر ورميها وتر، وصفة رمي الجمار أيام منى،','3381','413','0');
INSERT INTO [t21618] VALUES (478,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم رمى الجمرة وانصرف إلى رحله فنحر، والدليل على أنه صلى الله عليه وسلم لم يصل يوم النحر صلاة العيد','3388','413','0');
INSERT INTO [t21618] VALUES (479,'باب. . . . وحظر إتيان المنصرف من حجه من ظهر بيته، ووجوب إتيانه من بابه وتعجيله إلى أهله بعد فراغه من حجه','3391','413','0');
INSERT INTO [t21618] VALUES (480,'باب ذكر دعاء النبي صلى الله عليه وسلم للمدينة وأهلها وصاعها ومدها','3400','413','0');
INSERT INTO [t21618] VALUES (481,'باب ذكر الخبر المبين أن المدينة حرام آمن','3408','413','0');
INSERT INTO [t21618] VALUES (482,'باب ذكر الخبر الدال على كراهية أكل لحم الصيد لمن صيد من أجله، والخبر المعارض له المبيح للمحرم أكله','3421','413','0');
INSERT INTO [t21618] VALUES (483,'باب بيان الإباحة للمحرم قتل الحدأة والغراب والفأرة والكلب العقور والحية','3425','413','0');
INSERT INTO [t21618] VALUES (484,'باب بيان الإباحة للمحرم في الحجامة على وسط رأسه','3444','413','0');
INSERT INTO [t21618] VALUES (485,'باب بيان الإباحة للمحرم حلق رأسه إذا آذاه القمل، وما يجب عليه فيه من الفدية، والدليل على أن الكفارة بعد الحنث','3450','413','0');
INSERT INTO [t21618] VALUES (486,'. . يجده والجماع عند إحرامه','3457','413','0');
INSERT INTO [t21618] VALUES (487,'باب الأمكنة التي رأى يونس وموسى صلوات الله عليهما أجمعين ما رآهما يلبيان، وصفتهما، ورفع صوتهما بالتلبية، ومهل عيسى بن مريم صلى الله عليه وسلم','3487','413','0');
INSERT INTO [t21618] VALUES (488,'باب بيان المكان الذي يبتدئ رسول الله صلى الله عليه وسلم فيه بالتلبية عند إحرامه','3492','413','0');
INSERT INTO [t21618] VALUES (489,'باب بيان طريق النبي صلى الله عليه وسلم عند خروجه من المدينة إلى مكة، وموضع نزوله بذي الحليفة وبيتوتته بها، والصلاة التي كان يحرم دبرها','3499','413','0');
INSERT INTO [t21618] VALUES (490,'باب بيان الأمكنة التي هي مهل أهل الآفاق وأن مهل من وراء هذه الأمكنة من منازلهم وأهاليهم، ولا يجب عليهم الرجوع إلى المواقيت التي وقت لأهل الآفاق وبيان المكان الذي هو مهل أهل مكة، والدليل على الإباحة لعمار أهل مكة أن يعتمروا بها من غير أن يخرجوا منها','3509','413','0');
INSERT INTO [t21618] VALUES (491,'باب بيان تلبية رسول الله صلى الله عليه وسلم عند إحرامه وتلبيده رأسه عند إحرامه، والسنة في رفع الصوت بالإهلال للحج والعمرة','3522','413','0');
INSERT INTO [t21618] VALUES (492,'باب بيان حظر شجر مكة والحرم واختلاء شوكها، وتنفير صيدها، والرخصة في الإذخر أن يحش، والدليل على إباحة القود فيها، وعلى أن اللقطة لا تحل لملتقطها أبدا، وإن لم يجد يعني صاحبها','3532','413','0');
INSERT INTO [t21618] VALUES (493,'باب في معناه','3534','413','0');
INSERT INTO [t21618] VALUES (494,'باب بيان حظر إهراق الدم بالمدينة وحمل السلاح فيها للقتال، وقطع أشجارها، وإباحة قطعها للعلف','3537','413','0');
INSERT INTO [t21618] VALUES (495,'باب بيان حراسة الملائكة مدينة الرسول صلى الله عليه وسلم وشعابها ونقابها، وأنه لا يدخلها الدجال ولا الطاعون','3539','413','0');
INSERT INTO [t21618] VALUES (496,'باب دعاء النبي صلى الله عليه وسلم للمدينة إذا أتي بالباكورة','3542','413','0');
INSERT INTO [t21618] VALUES (497,'باب ذكر أسامي المدينة، وأنها تنفي شرار أهلها، وأن النبي صلى الله عليه وسلم أمر بالهجرة إليها','3547','413','0');
INSERT INTO [t21618] VALUES (498,'باب عقاب من يريد بالمدينة سوءا وبأهلها','3557','413','0');
INSERT INTO [t21618] VALUES (499,'مبتدأ فضائل القرآن','3565','413','0');
INSERT INTO [t21618] VALUES (500,'وما فيه من ذلك فضل القراء على غيرهم، وفضيلة من يتعلمه ويعلمه، والدليل على أن حافظ كتاب الله ترفع درجته على غيره، وإن كان غيره أسن منه وأشرف وأقدم','3566','413','0');
INSERT INTO [t21618] VALUES (501,'باب بيان ثواب قراءة ثلاث آيات، وتعلم آيتين وثلاث وأكثر، وفضيلة المبكر إلى المسجد كل يوم لتعليم القرآن أو قراءته، والدليل على فضيلة من يحفظ القرآن على من يقرؤه ولا يحفظ','3577','413','0');
INSERT INTO [t21618] VALUES (502,'باب الخبر الموجب لاستماع قراءة القارئ والإنصات له، والدليل على أن المتعلم إذا أنصت للقارئ، واستمع يكون أوعى له من الذي يقرأ مع القارئ','3581','413','0');
INSERT INTO [t21618] VALUES (503,'باب ذكر قراءة النبي صلى الله عليه وسلم القرآن على الجن واستماعهم له، والدليل على أن القارئ بقراءته القرآن يحال بينه وبين الشيطان','3584','413','0');
INSERT INTO [t21618] VALUES (504,'باب بيان فضل المؤمن الذي يقرأ القرآن على المؤمن الذي لا يقرؤه، وفضل المنافق الذي يقرأ القرآن في الظاهر على المنافق الذي لا يقرؤه','3593','413','0');
INSERT INTO [t21618] VALUES (505,'باب ثواب الماهر بالقرآن، والحافظ له، وفضله على غير الماهر، وثواب الذي يشق عليه قراءته','3595','413','0');
INSERT INTO [t21618] VALUES (506,'باب ذكر الخبر الموجب لتعاهد القرآن وحفظه','3598','413','0');
INSERT INTO [t21618] VALUES (507,'باب ذكر الخبر الموجب لاستذكار القرآن ودراسته، وأن حامله إذا قام به فقرأه بالليل والنهار ذكره وإن لم يقم به نسيه، والدليل على أنه إذا غفل عن تعاهده نزع منه','3600','413','0');
INSERT INTO [t21618] VALUES (508,'باب ذكر الخبر الناهي عن قول الرجل نسيت آية كيت وكيت، والدليل على أنه ينسى صاحبه إذا لم يقم بواجبه','3607','413','0');
INSERT INTO [t21618] VALUES (509,'باب ذكر الخبر المبيح لرفع الصوت بالقرآن في الليل وفي الطريق وفي المسجد والترغيب فيه ليؤخذ عنه، وأن من ينسى منه الآية وأكثر غير مأخوذ به','3614','413','0');
INSERT INTO [t21618] VALUES (510,'باب الترغيب في سؤال القارئ قراءة القرآن والاستماع إليه ليستمع قراءته، فإن ابن مسعود قرأ على رسول الله صلى الله عليه وسلم القرآن، وقرأ عليه رسول الله صلى الله عليه وسلم','3618','413','0');
INSERT INTO [t21618] VALUES (511,'باب ذكر الدليل على أن قراءة القرآن على من جمع القرآن من السنة، وإن كان القارئ أعلم به من المقرئ','3626','413','0');
INSERT INTO [t21618] VALUES (512,'باب بيان السعة في قراءة القرآن إذا لم يخل المعنى ولم يختلف في حلال ولا حرام، وأن النبي صلى الله عليه وسلم أعطي بكل حرف مسألة سألها','3628','413','0');
INSERT INTO [t21618] VALUES (513,'باب حظر الحسد إلا في اثنتين: رجل آتاه الله القرآن فهو يقوم به، والحكمة فهو يعلمها ويقضي فيها، والدليل على أن القارئ بهذه الصفة، والعالم الذي يعلم الناس علمه ويحدثهم ويقضي بينهم محسود','3639','413','0');
INSERT INTO [t21618] VALUES (514,'باب ذكر الخبر المبيح للقارئ أن يتغنى بالقرآن إذا كان حسن الصوت، ويجهر به ويحبر ويرجع، والدليل على أن السنة في رفع الصوت بالقراءة وتحزينه إذا كان القاريء حسن الصوت وعلى أنه له فعل هذه لغيره، وبيان نفي اتباع النبي عمن لم يتغن بالقرآن حدثنا يونس بن عبد','3645','413','0');
INSERT INTO [t21618] VALUES (515,'باب ذكر الخبر المبيح للقارئ أن يرجع في قراءته ويتلاحن، وأن حسن الصوت بالقرآن محبوب، والخبر الموجب في تزيين القرآن بالصوت','3660','413','0');
INSERT INTO [t21618] VALUES (516,'باب الخبر الموجب قراءة القرآن في شهر، وبيان حظر قراءته في دون سبعة أيام، وبيان الخبر الدال على إباحة قراءته في أكثر من شهر وشهرين وأكثر من ذلك، وعلى قراءته ما دام قلب القارئ فيه فإذا. . . عنه. . . .','3671','413','0');
INSERT INTO [t21618] VALUES (517,'باب بيان فضيلة فاتحة الكتاب وخواتيم سورة البقرة','3677','413','0');
INSERT INTO [t21618] VALUES (518,'باب بيان نزول الملائكة لقراءة سورة البقرة ودنوها من القارئ، وفضل البيت الذي تقرأ فيه سورة البقرة على غيره','3681','413','0');
INSERT INTO [t21618] VALUES (519,'باب ذكر الخبر الموجب قراءة البقرة، وآل عمران وفضيلتهما وتعظيم آية الكرسي، وأن البطلة لا يقدرون على حفظ سورة البقرة','3706','413','0');
INSERT INTO [t21618] VALUES (520,'ذكر نزول السكينة عند قراءة سورة الكهف وقراءة القرآن، وثواب قراءة سورة الكهف','3711','413','0');
INSERT INTO [t21618] VALUES (521,'باب بيان فضيلة سورة قل هو الله أحد وثواب من يقرؤها، وأنها صفة الرحمن تعالى','3715','413','0');
INSERT INTO [t21618] VALUES (522,'باب بيان فضيلة المعوذتين','3726','413','0');
INSERT INTO [t21618] VALUES (523,'باب ذكر سورة لم يكن الذين كفروا. .','3732','413','0');
INSERT INTO [t21618] VALUES (524,'باب ذكر سورة والليل إذا يغشى','3734','413','0');
INSERT INTO [t21618] VALUES (525,'باب ذكر سورة والضحى','3740','413','0');
INSERT INTO [t21618] VALUES (526,'باب ذكر سورة الصف وأن أولها سبح لله','3744','413','0');
INSERT INTO [t21618] VALUES (527,'باب قراءة النبي صلى الله عليه وسلم هذا الحرف من سورة اقتربت','3746','413','0');
INSERT INTO [t21618] VALUES (528,'باب ذكر الخبر الناهي أن يسافر بالقرآن، والدليل على أنه المصاحف، وبيان العلة التي لها نهي عنه، والدليل على أن المصاحف التي فيها كتابة القرآن هو القرآن، ووجوب جمعه وتعليمه والاستغناء به','3750','413','0');
INSERT INTO [t21618] VALUES (529,'مبتدأ كتاب النكاح وما يشاكله','3760','413','0');
INSERT INTO [t21618] VALUES (530,'باب ذكر السنة في التزويج والترغيب فيه والنفي اتباع النبي صلى الله عليه وسلم عمن رغب عنه والدليل على أن الراغب عنه عاص مخالف لما ندب إليه النبي صلى الله عليه وسلم إذا قدر عليه','3761','413','0');
INSERT INTO [t21618] VALUES (531,'باب ذكر الخبر الموجب تزويج النساء لمن قدر على ذلك، والصوم لمن عجز عنه، وأنه له وجاء، والدليل على إيجاب النكاح فرضا على القادر المحتاج إليه، وإباحة تركه للعاجز عنه وعلى أن النكاح تحصين لدين الناكح','3763','413','0');
INSERT INTO [t21618] VALUES (532,'باب بيان حظر التبتل وهو ترك النكاح، والدليل على الحض للتزويج','3767','413','0');
INSERT INTO [t21618] VALUES (533,'باب النهي عن الاختصاء، وإن خاف الرجل على نفسه، وعجز عن التزويج، وخاف العيلة، والعجز عن النفقة على زوجته، والدليل على الحض على التزويج، وأن الكراهية في الاختصاء لم تتقدم عليه','3774','413','0');
INSERT INTO [t21618] VALUES (534,'باب ذكر حض النبي صلى الله عليه وسلم على تزويج ذات الدين، وترك إيثار ذات المال، والحسب والجمال على ذات الدين','3780','413','0');
INSERT INTO [t21618] VALUES (535,'باب ذكر حض النبي صلى الله عليه وسلم على تزويج الأبكار الودود الولود على ابتغاء النسل، فيكاثر بهن الأمم','3785','413','0');
INSERT INTO [t21618] VALUES (536,'باب بيان تحذير النبي صلى الله عليه وسلم الرجال من فتنة النساء، والدخول عليهن، والنظر إليهن من حيث يحل، والاعتصام منهن بالتزويج، ومواقعة امرأته إذا بصر بامرأة، فأعجبته وبيان ثوابه في مواقعتها','3795','413','0');
INSERT INTO [t21618] VALUES (537,'باب إباحة النظر إلى المرأة التي يريد أن يخطبها، والإباحة لمن يستشار فيها أن يخبر بعيبها، والدليل على أن له أن ينظر إلى جميع ما يريد منها إذا توهم بها عيبا','3807','413','0');
INSERT INTO [t21618] VALUES (538,'باب بيان إبطال نكاح المرأة التي تنكح بلا ولي، وفساده، وإثبات ولاية السلطان لها، وتزويجها إذا لم تكن لها ولي، وإيجابه مهرها على المتقدم عليها بلا ولي إذا دخل بها','3811','413','0');
INSERT INTO [t21618] VALUES (539,'باب إبطال نكاح المتشاغرين، والنهي عن الشغار','3815','413','0');
INSERT INTO [t21618] VALUES (540,'باب بيان إبطال نكاح المتعة، وأنها أبيحت عام الفتح ثلاثة أيام، ثم حرمت','3828','413','0');
INSERT INTO [t21618] VALUES (541,'باب بيان الرد على ابن عباس في إباحة نكاح المتعة، وأن النبي صلى الله عليه وسلم نهى عنها يوم خيبر','3843','413','0');
INSERT INTO [t21618] VALUES (542,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم حرم نكاح المتعة بتحريم الله، وأن النبي صلى الله عليه وسلم أباح لهم أن يؤجلوا أجلا مسمى في الاستمتاع، وأنه لما حرمها نهاهم أن يأخذوا منهن ما أعطوهن، وإن لم ينقض أجلهن','3860','413','0');
INSERT INTO [t21618] VALUES (543,'باب الدليل على أن نكاح المتعة قبل تحريمها رخصة في الغزو للمضطر','3866','413','0');
INSERT INTO [t21618] VALUES (544,'باب بيان إبطال نكاح الرجل المرأة وعنده عمتها وخالتها','3876','413','0');
INSERT INTO [t21618] VALUES (545,'باب بيان إبطال نكاح الرجل المرأة وعنده ابنة أخيها، وبنت أختها','3879','413','0');
INSERT INTO [t21618] VALUES (546,'باب حظر الجمع بين أربع نسوة بين المرأة وعمتها، وبين المرأة وخالتها، والدليل على إباحة الجمع بين سائرهن من الأقارب','3881','413','0');
INSERT INTO [t21618] VALUES (547,'بيان حظر سؤال المرأة خاطبها طلاق امرأته لتحتوي على ما عنده دونها، ووجوب رضا تزويجه بها، وبإمساك امرأته التي عنده','3890','413','0');
INSERT INTO [t21618] VALUES (548,'باب حظر المسلم أن يخطب على خطبة المسلم حتى يترك، أو يأذن له الخاطب، وحظر الخطبة للمحرم ١٠','3895','413','0');
INSERT INTO [t21618] VALUES (549,'باب ذكر الأخبار الدالة على الإباحة للرجل أن يخطب المرأة المخطوبة في وقت دون وقت، وأن يخبر المستشار عيوب الخاطب','3906','413','0');
INSERT INTO [t21618] VALUES (550,'باب بيان تثبيت وجوب الخطبة عند التزويج، وما يجب أن يخطب به الخطبة للنكاح','3910','413','0');
INSERT INTO [t21618] VALUES (551,'باب ذكر الخبر الدال على الكراهية للرجل أن يغالي بصداق امرأته، وأن يبلغ بمهر امرأته أربعة أواق من فضة، وبيان الخبر المبيح للرجل أن يبلغ بمهرها أكثر منه','3912','413','0');
INSERT INTO [t21618] VALUES (552,'بيان الخبر المبيح أن يصدق الرجل المرأة وزن نواة، والنواة وزن خمسة دراهم، والإباحة للمتزوج إظهار الصفرة على جسده وثيابه، وما يقال له إذا تزوج، ووجوب الوليمة والدليل على أن الدون منها شاة ٢٠','3915','413','0');
INSERT INTO [t21618] VALUES (553,'بيان الخبر المبيح للرجل أن يتزوج على خاتم من حديد إذا لم يجد غيره، وعلى تعليم سورة من القرآن','3924','413','0');
INSERT INTO [t21618] VALUES (554,'باب الخبر الموجب اتخاذ الوليمة إذا بنى الرجل بأهله، وجمع الناس عليها، وأن الشاة الواحدة تجزئ فيها، والدليل على أنها أدناها، وبيان الخبر المبيح اتخاذها دون الشاة، وصفة وليمة رسول الله صلى الله عليه وسلم على بعض نسائه، وأنه كان يدعو قوما، فإذا أكلوا','3929','413','0');
INSERT INTO [t21618] VALUES (555,'باب ذكر الخبر الموجب إجابة الداعي إلى الوليمة والأكل منها، وإباحة ترك الأكل منها للصائم وعليه أن يدعو، ويبرك عليهم إذا لم يأكل وكان صائما','3947','413','0');
INSERT INTO [t21618] VALUES (556,'باب إيجاب إجابة الداعي والإباحة للمدعو ترك الأكل عنده إلا أن يحب أن يطعم','3953','413','0');
INSERT INTO [t21618] VALUES (557,'بيان إيجاب إجابة الدعوة عرسا كان أو غيره','3957','413','0');
INSERT INTO [t21618] VALUES (558,'باب ذكر الدليل على إيجاب إجابة الداعي إلى طعام الوليمة، وإن منعها من هو أحق بها ممن يدعى إليها، وأنها شر الأطعمة التي تتخذ إذا خص بها الأغنياء دون الفقراء','3966','413','0');
INSERT INTO [t21618] VALUES (559,'باب ذكر الخبر الموجب إتيان الوليمة إذا دعي إليها وإيجاب الإجابة إليها ولو كراعا','3972','413','0');
INSERT INTO [t21618] VALUES (560,'باب ذكر الخبر الدال على الإباحة للصائم ترك إجابة الداعي إلى طعام، وإعلامه أنه صائم','3976','413','0');
INSERT INTO [t21618] VALUES (561,'باب ذكر الخبر الدال على الإباحة لمتخذ الوليمة، والداعي إليها أن يخص من أحب منهم بزيادة لون منها','3978','413','0');
INSERT INTO [t21618] VALUES (562,'بيان الإباحة للمعتق جاريته لله أن يتزوج بها، ويصدقها عتقها','3980','413','0');
INSERT INTO [t21618] VALUES (563,'باب ذكر ثواب من يعتق جارية، ثم يتزوج بها، والدليل على الإباحة لولي المرأة أن يزوجها من نفسه برضاها','3990','413','0');
INSERT INTO [t21618] VALUES (564,'بيان إباحة الشروط في النكاح، وإيجاب الوفاء بها بعد التزويج، والدليل على إجازة النكاح، أو العقد بأي شرط كان','3996','413','0');
INSERT INTO [t21618] VALUES (565,'باب ذكر الخبر المبيح لوالد المرأة أن يمتنع من الإذن لزوج الابنة أن يتزوج بامرأة أخرى، ويقوم بمنعه عن التزويج عليها أو طلاقها، والدليل على أن له أن يشكو زوج ابنته إلى إخوانه وأصحابه','4001','413','0');
INSERT INTO [t21618] VALUES (566,'باب حظر إنكاح الأيم، حتى تستأمر، وإنكاح البكر حتى تأذن، وأن إذنها السكوت','4008','413','0');
INSERT INTO [t21618] VALUES (567,'باب ذكر الخبر الدال على أن الثيب إذا رغبت في رجل لم يكن لوليها أن يمتنع من تزويجها منه، وإن كرهه وليها، ورغب فيمن هو خير لها منه، وعلى أنه ليس للأب أن يزوج البكر المدركة، حتى تأذن له بسكوتها، وعلى إبطال نكاح المرأة التي تزوج نفسها ثيبا كانت، أو بكرا','4018','413','0');
INSERT INTO [t21618] VALUES (568,'باب الإباحة للأب أن يزوج الصغيرة ولا يستأذنها والإباحة لزوجها أن يدخل بها قبل البلوغ، والدليل على أن السنة في البناء بها نهارا','4028','413','0');
INSERT INTO [t21618] VALUES (569,'بيان الإباحة والترغيب في التزويج في شوال، والبناء بهن في شوال إذ النبي صلى الله عليه وسلم تزوج بعائشة فيه، وبنى بها فيه، وأوحي أنها امرأتك قبل تزويجه بها','4039','413','0');
INSERT INTO [t21618] VALUES (570,'باب ذكر الدعاء والترغيب في القول به للزوج عند دخوله بأهله، ومجامعتها','4044','413','0');
INSERT INTO [t21618] VALUES (571,'بيان إباحة إتيان الرجل امرأته من دبرها في قبلها، وحظر إتيانها في دبرها ٢٠','4049','413','0');
INSERT INTO [t21618] VALUES (572,'بيان حظر بيتوتة المرأة في غير بيت زوجها، واعتزالها عن فراش زوجها إلا بإذنه، والتشديد فيه وفي إعلامها الناس ما يكون بينهما من المجامعة والمباشرة','4060','413','0');
INSERT INTO [t21618] VALUES (573,'بيان السنة في المكث عند المرأة الثيب التي يتزوجها الرجل، وعنده أخرى، ومكثه عندها إذا كانت بكرا ١٠','4065','413','0');
INSERT INTO [t21618] VALUES (574,'بيان حظر نكاح المطلقة ثلاثا على المطلق، وإن تزوجت زوجا غيره، حتى يجامعها ويصيب منها هذا الزوج الأخير، والدليل على أن المباشرة والخلوة دون المجامعة لا يوجبان حكم الجماع','4077','413','0');
INSERT INTO [t21618] VALUES (575,'باب النهي عن العزل','4084','413','0');
INSERT INTO [t21618] VALUES (576,'بيان إباحة العزل ٢٧','4098','413','0');
INSERT INTO [t21618] VALUES (577,'بيان إباحة إتيان الرجل امرأته، وهي ترضع ولده، وحظر العزل فيه ١٠','4106','413','0');
INSERT INTO [t21618] VALUES (578,'باب ذكر حظر نكاح الحبالى ووطء الحبلى من السبايا، والدليل على إثبات الاستبراء في الإماء، وعلى أن الولد إذا لم يكن من نكاح لم يرث من والده، وإن ادعاه والده','4112','413','0');
INSERT INTO [t21618] VALUES (579,'باب إباحة وطء المحصنات ذوات الأزواج من السبايا','4117','413','0');
INSERT INTO [t21618] VALUES (580,'بيان تحريم النكاح بالرضاع بما تحرم به الولادة','4121','413','0');
INSERT INTO [t21618] VALUES (581,'بيان تحريم النكاح بالرضاع بلبن الفحل','4125','413','0');
INSERT INTO [t21618] VALUES (582,'باب تحريم نكاح ابنة الأخ من الرضاعة','4138','413','0');
INSERT INTO [t21618] VALUES (583,'باب تحريم الجمع بين الأختين، وتحريم نكاح الربيبة التي هي تربية الرجل، وتحريم الجمع بين المرأة وابنتها ٦٦','4145','413','0');
INSERT INTO [t21618] VALUES (584,'باب الخبر الدال على تحريم النكاح بأقل ما يقع عليه اسم الرضاع قل، أو كثر وبيان الخبر المعارض له المبينة أن الرضعة والرضعتين لا تحرمان، والدلالة على أن الثلاث يحرمن','4151','413','0');
INSERT INTO [t21618] VALUES (585,'باب ذكر الخبر المبيح للرجل تزويج المرضعة بلبن أمه، أو أخته دون خمس رضعات، وحظر تزويجها إذا رضعت خمس رضعات','4166','413','0');
INSERT INTO [t21618] VALUES (586,'باب الأخبار المبيحة رضاع الكبير، وتحريم النكاح بها لما يحرم بها النسب، وبيان الخبر المعارض لها الدال على أن التحريم بالرضاع ما كان في الحولين','4171','413','0');
INSERT INTO [t21618] VALUES (587,'باب الخبر الدال على إجازة الحكم بشهادة المرأة الواحدة في الرضاع','4182','413','0');
INSERT INTO [t21618] VALUES (588,'باب إلحاق نسب الولد بمن يولد على فراشه، وإن ادعاه مدع، وأثبت شبهه به، والدليل على إبطال الحكم بقول القافة فيه، وكذلك في الولد الذي ينتفي منه من ولد على فراشه، ويرميه لم ينكر رميه به، وبيان الخبر الدال على إجازة الحكم بقول القافة، وبالشبه في الولد','4188','413','0');
INSERT INTO [t21618] VALUES (589,'بيان التسوية بين الأزواج في الكينونة معهن، والقسم لهن، والإباحة ترك القسم لبعضهن ٢٠','4208','413','0');
INSERT INTO [t21618] VALUES (590,'بيان الإباحة للمرأة أن تهب نصيبها من قسمة زوجها ممن أحبت من يشاء زوجها، والإباحة لزوجها ترك إتيانها والكينونة معها بعد هبتها نصيبها','4216','413','0');
INSERT INTO [t21618] VALUES (591,'باب ذكر الخبر الموجب إقراع الرجل بين نسائه إذا أراد سفرا، وإخراج من أصابتها القرعة واحدة كانت أو اثنتين، والإباحة لمن ترك القسمة بينهما','4221','413','0');
INSERT INTO [t21618] VALUES (592,'باب ذكر الآية التي أنزلت في اللاتي وهبن أنفسهن للنبي صلى الله عليه وسلم، وأنها له خاص.','4223','413','0');
INSERT INTO [t21618] VALUES (593,'باب حظر تزويج الرجل اليتيمة التي تكون في حجره وهو وليها رغبة منه في مالها وجمالها بدون مهر مثلها، إلا أن يقسط في تزويجها ومهرها أغلى المهور التي تمهر مثلها، والإباحة له أن يتزوج سواها بأي مهر كان، وأن الزانية لا مهر لها','4227','413','0');
INSERT INTO [t21618] VALUES (594,'مبتدأ كتاب الطلاق','4234','413','0');
INSERT INTO [t21618] VALUES (595,'باب ذكر الأخبار الدالة على إيجاب مداراة الرجل امرأته على ما فيها من الأخلاق المذمومة، والخلاف، وإمساكها وكراهية طلاقها، وإظهار البغض لها، وأنها جبلت على الخلاف، والعوج وعلى خيانة زوجها لحملها إياه على ترك الطاعة، وما لا يجوز، وأن المرأة الصالحة لا','4235','413','0');
INSERT INTO [t21618] VALUES (596,'بيان طلاق السنة والعدة التي أمر الله عز وجل أن تطلق لها النساء، والدليل على أن النبي صلى الله عليه وسلم جعل لكل تطليقة حيضا وطهرا','4246','413','0');
INSERT INTO [t21618] VALUES (597,'باب ذكر الخبر الموجب مراجعة الرجل امرأته إذا طلقها تطليقة واحدة وهي حائض، ثم يمسكها، حتى تطهر، ثم تحيض حيضة أخرى','4250','413','0');
INSERT INTO [t21618] VALUES (598,'باب ذكر الخبر المبين أن التطليقة التي طلق ابن عمر امرأته، وهي حائض أوقعت عليها وأنه راجعها على تطليقتين','4256','413','0');
INSERT INTO [t21618] VALUES (599,'باب ذكر الخبر الموجب مراجعة الرجل امرأته إذا طلقها، وهي حائض حتى تطهر والإباحة له أن يطلقها في هذا الطهر قبل أن تحيض حيضة أخرى','4263','413','0');
INSERT INTO [t21618] VALUES (600,'باب الدليل على أن المطلق واحدة لا تحل له، ولا تكون امرأته حتى يراجعها، والدليل على أن القرء الطهر','4267','413','0');
INSERT INTO [t21618] VALUES (601,'باب الخبر المبين أن طلاق الثلاث كانت ترد على عهد رسول الله صلى الله عليه وسلم وأبي بكر إلى واحدة وبيان الأخبار المعارضة له الدالة على إبطاله استعمال هذا الخبر، وأن المطلق ثلاثا لا تحل له حتى تنكح زوجا غيره','4271','413','0');
INSERT INTO [t21618] VALUES (602,'باب ذكر الخبر الموجب على من يقول الحل عليه حرام، أو يحرم عليه امرأته يمينا','4287','413','0');
INSERT INTO [t21618] VALUES (603,'باب الخبر المبين أن الرجل إذا قال لامرأته: اختاري، أو خيرها في فراقها لم يكن ذلك طلاقا','4295','413','0');
INSERT INTO [t21618] VALUES (604,'باب الدليل على أن الرجل إذا حلف أن لا يأتي امرأته شهرا لا يسمى موليا، ولا يكون لامرأته مطالبته بالفيء، ولا يكون ذلك طلاقا','4314','413','0');
INSERT INTO [t21618] VALUES (605,'بيان الخبر الدال على إيجاب النفقة للنساء على أزواجهن، وعلى أن الرجل إذا عجز عن النفقة على امرأته كان لها الخيار بين المقام معه، والصبر على ضيق العيش وبين مفارقته','4319','413','0');
INSERT INTO [t21618] VALUES (606,'بيان الأخبار التي لا تجعل للمطلقة ثلاثا على زوجها نفقة، ولا سكنى وإيجاب خروجها من بيته، والانتقال إلى منزل لا يراها الرجال فتعتد فيه، والدليل على أن السكنى والنفقة لمن لزوجها عليها رجعة','4322','413','0');
INSERT INTO [t21618] VALUES (607,'باب الإباحة للمطلقة أن تستشير في حاجتها، والخروج من بيتها في عدتها إلى ضيعتها في جداد نخلها، والدليل على أن لها الخروج إلى غير ذلك من الأعمال إذا كان ذلك على وجه المعروف، والتحول من منزلها في عدتها إذا خافت','4360','413','0');
INSERT INTO [t21618] VALUES (608,'بيان الإباحة للحامل المتوفى عنها زوجها أن تتزوج حين تضع حملها قبل انقضاء أربعة أشهر، وعشر','4363','413','0');
INSERT INTO [t21618] VALUES (609,'باب الإباحة للمرأة أن تحد على زوجها أربعة أشهر وعشرا، وحظر الاكتحال ومس الطيب في عدتها، وإن رمدت واختضابها، والرخصة لها عند طهرها من حيضها في التبخر بالقسط، وحظر ذلك على غير زوجها فوق ثلاث ليال، والدليل على الإباحة لها ذلك ثلاث ليال على ميتها','4369','413','0');
INSERT INTO [t21618] VALUES (610,'باب السنة في المتلاعنين، والتفريق بينهما إذا فرغا من الملاعنة، وأي المتلاعنين حلف يبدأ بالرجل فيحلف، ثم بالمرأة في المسجد ولا يجتمعان أبدا','4392','413','0');
INSERT INTO [t21618] VALUES (611,'باب الخبر الموجب التفريق بين المتلاعنين، وإلحاق الولد بأمه ووجوب صداقها على زوجها','4402','413','0');
INSERT INTO [t21618] VALUES (612,'باب ذكر الدليل على أن الرجل إذا رمى رجلا بامرأته لا يجب عليه الحد لهما إلا أن يكذب نفسه، فلا يلاعن امرأته، وأنه إذا التعن وجب على امرأته الحد إلا أن تلتعن، ولا يجب الحد على المرمي بها بالتعانه','4413','413','0');
INSERT INTO [t21618] VALUES (613,'باب ذكر الدلائل على أن الملاعنة ليست ببينة ولا شهادة، والدليل على أن الملتعنة إذا أقيمت البينة على زنائها رجمت، وأن المرأة الحبلى إذا لم تقر على نفسها بالزنا، ولم تقم البينة أنها زنت لم تحد','4421','413','0');
INSERT INTO [t21618] VALUES (614,'باب الخبر الناهي عن قتل الرجل الزاني إذا رآه يزني بامرأته، والدليل على أنه لا يجوز لأحد أن يقيم الحد على الزاني والزانية إلا بأمر السلطان','4425','413','0');
INSERT INTO [t21618] VALUES (615,'باب ذكر الخبر الدال على أن الملاعنة بين الزوجين إنما تجب إذا رماها، وهي حامل أو رآها على فاحشة','4432','413','0');
INSERT INTO [t21618] VALUES (616,'باب السنة في الاختلاع، والدليل على أنه لا يكون طلاقا وعدتها حيضة، وأنها لا تسمى عدة، وأنها إذا رغبت عن زوجها جاز للزوج أن يأخذ منها ما شاء على ذلك، ثم يخلي سبيلها، والخبر الدال على أن العنين إذا سألت امرأته الحاكم فراقه لم يفرق بينهما بقولها','4437','413','0');
INSERT INTO [t21618] VALUES (617,'مبتدأ كتاب العتق والولاء','4440','413','0');
INSERT INTO [t21618] VALUES (618,'باب الخبر الدال على أن المعتق بعض مملوكه، أو شيئا من جسده يكون عتيقا كله','4441','413','0');
INSERT INTO [t21618] VALUES (619,'بيان الخبر الدال على أن المعتق نصيبا له من عبد بينه، وبين غيره كان عتيقا كله','4447','413','0');
INSERT INTO [t21618] VALUES (620,'بيان الخبر الموجب عتق المعتق نصيب غيره من العبد الذي أعتق نصيبه منه، ودفع ثمنه إلى شريكه','4450','413','0');
INSERT INTO [t21618] VALUES (621,'بيان الخبر الدال على أن المعتق نصيبه من عبد منه بينه، وبين شركائه يجبر على عتق نصيب غيره، وعلى بيع ماله، ودفع قيمة نصيب غيره من العبد إليه إن كان له مال','4452','413','0');
INSERT INTO [t21618] VALUES (622,'باب الخبر الدال على أن المعتق نصيبه من عبد بينه، وبين شركائه لم يجبر على عتق أنصباء شركائه إذا لم يكن له مال يحيط بثمنه، وأن بقيته رقيق لأربابه يستعملونه على قدر حصتهم','4457','413','0');
INSERT INTO [t21618] VALUES (623,'باب الخبر المروي عن أبي هريرة الدال على أن العبد، إذا كان بين قوم، فأعتق أحدهم نصيبه يصير عتيقا كله كان للمعتق شقصه مال، أو لم يكن','4470','413','0');
INSERT INTO [t21618] VALUES (624,'باب ذكر الولاء وأن ولاء المعتق لمن أدى فيه الثمن، وأن المعتقة لها الخيار إذا كانت تحت عبد','4482','413','0');
INSERT INTO [t21618] VALUES (625,'باب إبطال الشرط في الولاء، وإن اشترطه البائع لنفسه في عقده البيع إذ هو شرط بخلاف حكم رسول الله صلى الله عليه وسلم، وقضائه، ونهى النبي صلى الله عليه وسلم عن بيعه وهبته','4493','413','0');
INSERT INTO [t21618] VALUES (626,'بيان الإباحة لمن يكاتب مملوكه إلى أجل ثم يتعجل ما له عليه، وإثبات الولاء لمن يقضي عنه ما عليه، والدليل على أن المكاتب مملوك إلى أن يقضي ما عليه','4501','413','0');
INSERT INTO [t21618] VALUES (627,'بيان حظر بيع الولاء وهبته، وحظر موالاة مولى مسلم وموالي قوم بغير إذنهم والتشديد فيه','4513','413','0');
INSERT INTO [t21618] VALUES (628,'بيان ثواب المعتق رقبة مؤمنة','4537','413','0');
INSERT INTO [t21618] VALUES (629,'باب الخبر الدال على أن الرجل يملك أباه بالشري حتى يعتقه وأنه إذا أحب أن يجازيه بعد موته أعتق عنه','4544','413','0');
INSERT INTO [t21618] VALUES (630,'مبتدأ كتاب البيوع','4547','413','0');
INSERT INTO [t21618] VALUES (631,'بيان البيع المنعقد بشرط فاسد، فيمضى البيع ويرد الشرط','4548','413','0');
INSERT INTO [t21618] VALUES (632,'باب إجازة البيع المنعقد بشرط جائز، وإجازة الشرط فيه، مثل الرجل يبيع البعير ويشترط له ظهره إلى مكان مسمى، والدليل على من اشترط شرطا جائزا في بيع جائز فالبيع والشرط معا جائزان مثل أن يبيع الرجل السلعة، ويستثني منه شيئا معلوما','4552','413','0');
INSERT INTO [t21618] VALUES (633,'باب ذكر الخبر الموجب على الوازن أن يرجح إذا وزن، والإباحة لمن له ذهب أو فضة على رجل أن يأخذ منه أرجح مما عليه','4563','413','0');
INSERT INTO [t21618] VALUES (634,'باب حظر بيعتان، المسماتان الملامسة والمنابذة، والدليل على أنهما بيعتان مجهولتان، وهما من الغرر','4579','413','0');
INSERT INTO [t21618] VALUES (635,'بيان حظر بيع الغرر، وبيع الحصى وبيع حبل الحبلة','4592','413','0');
INSERT INTO [t21618] VALUES (636,'بيان النهي عن بيع الرجل على بيع أخيه إلا بإذنه وعلى السوم على سومه، والدليل على أن من فعله كان عاصيا بفعله','4599','413','0');
INSERT INTO [t21618] VALUES (637,'باب النهي عن تلقي البيوع، والجلب، والركبان، للبيع، والدليل على أن من تلقاه، فاشتراه منه فبيعه غير جائز','4604','413','0');
INSERT INTO [t21618] VALUES (638,'باب الخبر المبين أن المتبايعين إذا تبايعا كان لهما الخيار حتى يتفرقا بأبدانهما','4622','413','0');
INSERT INTO [t21618] VALUES (639,'بيان إبطال الخيار قبل الافتراق إذا قال البائع للمشتري بعد وجوب البيع: اختر أو يقوله المشتري للبائع','4626','413','0');
INSERT INTO [t21618] VALUES (640,'بيان إجازة الخيار في البيع ثلاثة أيام، والدليل على إباحة أكثر من ذلك من غير توقيت','4630','413','0');
INSERT INTO [t21618] VALUES (641,'باب ذكر الخبر المبطل بيع البيعين ما داما جميعا، وإن خير أحدهما صاحبه فاختار البيع','4634','413','0');
INSERT INTO [t21618] VALUES (642,'بيان محق البركة من بيع البيعين إذا كذبا وخانا، ولم ينصح أحدهما صاحبه، وأنها إذا صدقا وبين أحدهما صاحبه ما في المبيع مما يخافا عليه بورك لهما فيه','4637','413','0');
INSERT INTO [t21618] VALUES (643,'بيان حظر الخديعة في البيوع والدليل على أن البائع المخدع للمشتري مأثوم ماض بيعه، وأن المخدوع إذا قال للبائع قبل انعقاد البيع لا خديعة كان بيعا فاسدا','4641','413','0');
INSERT INTO [t21618] VALUES (644,'باب حظر النجش، والناجش هو الرجل تدفع إليه السلعة يبيعها، ويؤمر أن يعطي بها عطية','4647','413','0');
INSERT INTO [t21618] VALUES (645,'بيان حظر بيع الحاضر للبادي','4652','413','0');
INSERT INTO [t21618] VALUES (646,'بيان حظر التصرية وبيع المصراة، والدليل على إجازة البيع','4662','413','0');
INSERT INTO [t21618] VALUES (647,'بيان إجازة اشتراء المصراة، والإباحة له حلبها بعد معرفته بتصريتها، والدليل على أنه ليس أن يردها حتى يحلبها له، ولا له أن يردها إن رضي حلابها','4665','413','0');
INSERT INTO [t21618] VALUES (648,'بيان إباحة المشتري المصراة أن يتأنى في ردها إلى بائعها ثلاثة أيام، والدليل على أنه إن سخطها، ثم رضيها كان له إمساكها فإن رضيها، ثم سخطها كان له ردها في الثلاثة الأيام','4671','413','0');
INSERT INTO [t21618] VALUES (649,'باب ذكر الخبر الموجب على مشتري المصراة إذا ردها أن يرد معها صاعا من طعام سوى البر، والخيار ثلاث ولا يرد البر','4674','413','0');
INSERT INTO [t21618] VALUES (650,'بيان حظر بيع الطعام المشترى حتى يستوفى، ويقبض والدليل على أن ما سوى الطعام مما لا يكال جائز بيعه','4679','413','0');
INSERT INTO [t21618] VALUES (651,'بيان حظر بيع الطعام الذي يشتريه المشتري حتى يكتاله، والدليل على أن المشتري إذا اشترى طعاما كان بيعا جائزا، وإن لم يكتله وأن بيعه من غيره جائز حتى يكتاله','4696','413','0');
INSERT INTO [t21618] VALUES (652,'باب النهي عن بيع الصكوك','4701','413','0');
INSERT INTO [t21618] VALUES (653,'بيان حظر بيع المشتري طعاما جزافا حتى ينقله إلى مكان آخر والدليل على إجازته إذا لم يكن جزافا','4703','413','0');
INSERT INTO [t21618] VALUES (654,'بيان حظر بيع الصبرة من التمر بالتمر كيلا إذا كانت الصبرة غير معلوم كيلها، والدليل على أن بيع التمر بالتمر مكايلة جائز وإن احتمل أن يكون أحدهما أخف من الآخر في الوزن','4711','413','0');
INSERT INTO [t21618] VALUES (655,'باب حظر بيع الصبرة بالصبرة، والدليل على أن ما لا يجوز بعضه ببعض إلا مثلا بمثل لا يجوز بعضه ببعض جزافا','4713','413','0');
INSERT INTO [t21618] VALUES (656,'باب حظر بيع الثمر حتى يبدو صلاحها واشتراؤها، والدليل على أنها إذا لم تدرك كلها جائز بيعها','4715','413','0');
INSERT INTO [t21618] VALUES (657,'بيان تفسير بدو الصلاح في الثمرة، وأنه لا يحل بيعها حتى تطيب ويؤكل منها، وعن بيع النخل حتى يحزر','4723','413','0');
INSERT INTO [t21618] VALUES (658,'بيان حظر بيع السنبل حتى يبيض ويأمن العاهة، وعن بيع النخل حتى يزهو، والدليل على إجازة بيع الزرع قبل أن يسنبل، وبيان حظر بيع الزرع بالحنطة، والدليل على إجازته بالذهب والفضة','4735','413','0');
INSERT INTO [t21618] VALUES (659,'بيان حظر بيع الرطب بالتمر وبيع ثمر النخل بتمر النخل','4742','413','0');
INSERT INTO [t21618] VALUES (660,'بيان الرخصة في بيع العرايا، وأنه مستثنى من جملة النهي','4747','413','0');
INSERT INTO [t21618] VALUES (661,'باب ذكر الأخبار الدالة على أن الرخص في بيع العرية لمن يشتريها يأكلها رطبا، وأن من يشتريها يتخذ تمرا على الحظر','4760','413','0');
INSERT INTO [t21618] VALUES (662,'باب ذكر الخبر المبيح بيع العرايا بخرصها تمرا ما دون خمسة أوسق، والدليل على أن ما فوقها غير جائز بيعها بالتمر','4768','413','0');
INSERT INTO [t21618] VALUES (663,'باب حظر بيع الرطب بالتمر كيلا وبيع العنب بالزبيب كيلا أو بيع ثمر النخل بالتمر، وبيع الكرم بالزبيب وأنها المزابنة، وعن بيع كل ثمر بخرصه بجنسه يابسه بكيل معلوم أو وزن معلوم','4770','413','0');
INSERT INTO [t21618] VALUES (664,'باب ذكر الخبر الدال على أن للإمام أن يمنع من يبيع الطعام بكيل، أو وزن من الناس إلا في سوق الطعام','4779','413','0');
INSERT INTO [t21618] VALUES (665,'باب ذكر الخبر الموجب لبائع النخل ثمرته بعد الإبار، والدليل على أنها للمشتري إذا اشتراه قبل الإبار، والإبار هو أن يؤخذ شيء من طلع النخل فيدخل من ظهراني الإناث من النخل','4782','413','0');
INSERT INTO [t21618] VALUES (666,'باب ذكر الخبر الموجب لبائع العبد ماله إلا أن يشترط المشتري، والدليل على أن العبد يملك المال، ولا يملكه سيده ما دام العبد في ملكه','4789','413','0');
INSERT INTO [t21618] VALUES (667,'باب ذكر الخبر الناهي عن بيع الثمار على النخيل والكروم إلا بالدنانير والدراهم، والدليل على إباحة بيع الزرع بالدرهم والدينار وعلى أن الزرع للباذر','4798','413','0');
INSERT INTO [t21618] VALUES (668,'باب بيع حظر المعاومة','4804','413','0');
INSERT INTO [t21618] VALUES (669,'بيان الخبر المبيح مواكرة الأرض مع الأشجار بالنصف والثلث مما يخرج الله منها، وإباحة الشرط في المساقاة على من يأخذ أن يكتفي صاحبها جميع عملها، وإباحة دفع الأرض على ذلك الشرط، وعلى أن يكون العمل والبذر من قبل الحراث، والدليل على إباحة الإجارة إذا كانت','4818','413','0');
INSERT INTO [t21618] VALUES (670,'باب ذكر الأخبار المعارضة لإباحة المزارعة بالثلث والربع المبينة فسادها وحظرها، والدليل على أن حظرها بعد إباحتها','4829','413','0');
INSERT INTO [t21618] VALUES (671,'باب ذكر الأخبار الناهية عن كراء الأرض، وإيجاب منحها وإعادتها إذا استغنى عنها صاحبها، وحظر استئجارها بالطعام وإن كان معلوما','4853','413','0');
INSERT INTO [t21618] VALUES (672,'باب ذكر الأخبار المبيحة مؤاجرة الأرض البيضاء بالذهب والفضة، والدليل على إباحة مؤاجرتها بغيرهما إذا كانت الأجرة معلومة مضمونة في رقبة المستأجر','4872','413','0');
INSERT INTO [t21618] VALUES (673,'بيان حظر أخذ ثمن الثمر الذي بيع فأصابته جائحة بعد البيع، وأنه لا يحل لبائعه أخذ ثمنه','4905','413','0');
INSERT INTO [t21618] VALUES (674,'باب ذكر الخبر الدال على حظر أخذ ثمن الثمر الذي بيع فأصابته جائحة هو الثمر الذي يباع قبل أن يبدو صلاحها، والدليل على أنه إذا بيع ليقطع من مبتاعيه، فجائز بيعه','4908','413','0');
INSERT INTO [t21618] VALUES (675,'باب ذكر الخبر الموجب وضع الجوائح، والدليل أنه في الثمار','4912','413','0');
INSERT INTO [t21618] VALUES (676,'بيان الإباحة للمديون أن يستوضع صاحب المال، ويسأله مما له عليه، ويستمهله منه','4918','413','0');
INSERT INTO [t21618] VALUES (677,'باب ذكر الخبر الدال على أن الرجل إذا باع من معدوم سلعة أن له الارتجاع في سلعته، فيأخذها وكذلك إن لم يكن معدما في وقت بيعها منه، ثم صار معدما في وقت بيعها منه، وأنه لا يحتاج فيه إلى حكم حاكم وله فسخها','4922','413','0');
INSERT INTO [t21618] VALUES (678,'باب الخبر المبين أن المفلس هو الذي يفلس بمال قوم، وأن من وجد منهم متاعه بعينه لم يكن للباقين فيه حق، والدليل على نفي الإفلاس عن المعدم الذي ليس له دين إلا لواحد','4924','413','0');
INSERT INTO [t21618] VALUES (679,'باب ذكر الخبر الدال على أن الرجل إذا باع من رجل سلعة فوجده مفلسا كان له نقض بيعها، وأخذها منه ما كره المشتري فعله، أو لم يكره','4928','413','0');
INSERT INTO [t21618] VALUES (680,'باب ذكر الخبر الدال على أن الرجل إذا باع من رجل متاعا ولا يعلم أنه مفلس، ثم وجده مفلسا أو كان مليا ثم أفلس، فأحدث المشتري في بعض المتاع وصرفه في شيء آخر أن له فيه أسوة الغرماء يقسم ذلك بينهم','4930','413','0');
INSERT INTO [t21618] VALUES (681,'باب ذكر الترغيب في إنظار المعسر والتجاوز عنه، وبيان ثوابهما وثواب الوضع عنه','4934','413','0');
INSERT INTO [t21618] VALUES (682,'باب الترغيب في التحرز في انتقاد الدراهم، والدليل على إباحة أخذ الدراهم البهرج','4941','413','0');
INSERT INTO [t21618] VALUES (683,'باب الترغيب في التجاوز عن الموسر، في الدين وإنظاره، والوهن به في مطالبته، والدليل على إباحة مماطلة المديون بإذن صاحب الدين','4943','413','0');
INSERT INTO [t21618] VALUES (684,'باب الخبر المعارض لإباحة مماطلة الموسر المبين أن مماطلته ظلم، وأن الحوالة إذا أحيلت على مليء لم يكن للمحال عليه أن يرجع بها عن المحيل إذا أفلس الذي احتال أو أقال، والدليل على أنها إذا أحيلت على غير مليء كان له الرجوع فيها على المحيل','4949','413','0');
INSERT INTO [t21618] VALUES (685,'بيان حظر بيع فضل الماء، والدليل على النهي فيه وعن بيعه، بعضه دون بعض','4953','413','0');
INSERT INTO [t21618] VALUES (686,'بيان العلة التي لها نهى النبي صلى الله عليه وسلم عن بيع الماء، والدليل على أن الأرض إذا أكريت مع الماء أو بيعت مع الماء أنهما مفسوخان، وعلى أن الماء بيعه محظور، وأن الكراء معناه معنى البيع','4955','413','0');
INSERT INTO [t21618] VALUES (687,'باب حظر منع فضل الماء ليمنع به الكلأ، والدليل على أن النهي عن بيعه في موضع دون موضع، والتشديد في منع ابن السبيل فضله','4961','413','0');
INSERT INTO [t21618] VALUES (688,'باب ذكر الخبر الدال على أن المحتاج إلى الماء الممنوع منه له استعماله إذا قدر على ذلك، وعليه أن يعوض صاحبه منه حتى يرضيه','4969','413','0');
INSERT INTO [t21618] VALUES (689,'باب حظر بيع الكلب وأخذ ثمنه، وإعطاء الكاهن على كهانته، وحظر أخذ ثمن السنور','4971','413','0');
INSERT INTO [t21618] VALUES (690,'باب ذكر الخبر المعارض لحظر كسب الحجام المبيح أخذه، الدال على أن الزجر عنه ليس عن التحريم، ولكنه على الدناءة','4988','413','0');
INSERT INTO [t21618] VALUES (691,'باب إثبات تحريم ثمن الكلب، ووجوب قتله','4997','413','0');
INSERT INTO [t21618] VALUES (692,'بيان الكلاب التي رخص في إمساكها، ونهى عن قتلها بعد ما أمر بقتلها','5009','413','0');
INSERT INTO [t21618] VALUES (693,'باب الكراهية في إمساك الكلاب، والدليل على نفي التحريم في إمساكها، والإباحة في إمساك كلب الماشية، والغنم، والصيد، والزرع','5016','413','0');
INSERT INTO [t21618] VALUES (694,'باب ذكر الخبر المبين أن الملائكة لا تدخل بيتا فيه كلب، وإباحة اتخاذ كلب الصيد في الدار، وبيان ثواب من سقاها','5034','413','0');
INSERT INTO [t21618] VALUES (695,'باب تحريم التجارة في الخمر، وبيعها، والانتفاع بها بعد إباحتها، والدليل على أنه لا يحل إمساكها لتصير خلا','5038','413','0');
INSERT INTO [t21618] VALUES (696,'باب تحريم بيع الميتة، والخنزير، والأصنام، والانتفاع بها وبأثمانها','5044','413','0');
INSERT INTO [t21618] VALUES (697,'بيان تحريم الذهب بالذهب والورق بالورق، إلا وزنا بوزن، والدينار بالدينارين','5055','413','0');
INSERT INTO [t21618] VALUES (698,'بيان حظر بيع الذهب بالذهب والورق بالورق، إلا مثلا بمثل يدا بيد هاء وهاء، وحظر بيع الفضة بالذهب والذهب بالفضة','5066','413','0');
INSERT INTO [t21618] VALUES (699,'باب حظر بيع البر بالبر، والشعير بالشعير، والتمر بالتمر، والملح بالملح إلا سواء بسواء عينا بعين','5080','413','0');
INSERT INTO [t21618] VALUES (700,'باب الأخبار الدالة على إباحة بيع الذهب بالفضة كيف شاء البائع والمشتري، وبيان الأخبار المعارضة لإباحته المحظورة بيع أحدهما بالآخر دينا','5090','413','0');
INSERT INTO [t21618] VALUES (701,'باب حظر الذهب في قلادة، وغيرها فيها غيره حتى يفصل الذهب، فيباع وحده','5098','413','0');
INSERT INTO [t21618] VALUES (702,'باب ذكر الأخبار المبيحة التفاضل في الصرف إذا كان يدا بيد','5104','413','0');
INSERT INTO [t21618] VALUES (703,'باب حظر مبادلة التمر بالتمر، والحنطة بالحنطة، وجواز بيع كل منهما على حدة بالدرهم، واشتراء ما يحتاج إليه من ذلك بثمنه، أو يباع بسلعة ويشترى بها تمر','5124','413','0');
INSERT INTO [t21618] VALUES (704,'باب إباحة اللعن لآكل الربا، وموكله، وكاتبه، وشاهديه وأنهم سواء','5135','413','0');
INSERT INTO [t21618] VALUES (705,'باب الخبر الدال على إيجاب اجتناب ما اختلف فيه من البيوع، واستعمال الاحتياط فيه','5139','413','0');
INSERT INTO [t21618] VALUES (706,'باب حظر كثرة الحلف في البيع ووجوب اجتنابه فيه، فيمحق الربح، والبركة منه','5153','413','0');
INSERT INTO [t21618] VALUES (707,'باب الخبر الناهي عن الاحتكار والكراهية منه، والدليل على أنه ليس على الحتم','5158','413','0');
INSERT INTO [t21618] VALUES (708,'بيان الخبر الدال على أن الرجل إذا اشترى عبدا أو شاة، أو دابة ثم ظهر بها عيب أن له أن يردها، ويحبس عليها التي في ملكه، وبيان الخبر المبين أن خراجها له بالضمان، والدليل على أن الضمان هو الملك','5163','413','0');
INSERT INTO [t21618] VALUES (709,'باب الدليل على إباحة اقتضاء الدنانير من الدراهم، والدراهم من الدنانير، واقتضاء التمر على رءوس النخل من تمر مكيل معلوم المبلغ','5170','413','0');
INSERT INTO [t21618] VALUES (710,'باب الإباحة لبائع الشيء بالنسيئة أن يسترهن من المشتري رهنا، وذكر الترجمة','5173','413','0');
INSERT INTO [t21618] VALUES (711,'بيان إباحة الاستسلاف في الحيوان، واستقراضه والسلم فيه، والإباحة للمستسلف، والمستقرض إعطاء المسلف، والمقرض أجود مما يجب عليه','5178','413','0');
INSERT INTO [t21618] VALUES (712,'بيان إباحة العبد بالعبدين يدا بيد، والدليل على أجازته فيه من الحيوان يدا بيد ونسيئة','5186','413','0');
INSERT INTO [t21618] VALUES (713,'باب إباحة السلم في الثمار بكيل معلوم، ووزن معلوم إلى أجل معلوم، وذكر الترجمة','5189','413','0');
INSERT INTO [t21618] VALUES (714,'باب حظر بيع الرجل شركا له في ربعة، أو أرض، أو دار، أو نخل حتى يعرضه على شريكه، فإن لم يأخذه بثمنه جاز له بيعه من غيره، والدليل على أنه يجب عليه عرضه، وكان شريكه على دينه، أو لم يكن','5195','413','0');
INSERT INTO [t21618] VALUES (715,'بيان وجوب الشفعة للشريك في الحائط والربعة ما لم يقسم إذا باع شريكه شركته فيما دون عرضها عليه، والدليل على أنه إذا عرضها عليه، فلم يشترها لم يكن له فيها شفعة، وأن لا شفعة لغير الشريك، وأنه إذا قسم لم يكن فيه شفعة، وأن للشفيع أن يأخذ المبيع، وإن لم','5200','413','0');
INSERT INTO [t21618] VALUES (716,'بيان عقوبة من أخذ شبرا من الأرض ظلما، والدليل على أنه يدخل في ذلك من لا يكون له شفعة في أرض، فيأخذها بعلة الشفعة، وكذلك من ذهب بشفعة شفيع ظلما','5203','413','0');
INSERT INTO [t21618] VALUES (717,'بيان النهي عن منع الجار جاره إذا سأله أن يضع خشبة في جداره أو يغرزها فيه، والدليل على أنه له منعه إذا كان أكبر منها من بناء وغيره، وبيان عرض الطريق إذا اختلف الشركاء فيه، والدليل على أنهم إن اتفقوا على أقل منه جاز ذلك','5213','413','0');
INSERT INTO [t21618] VALUES (718,'باب الخبر الدال على أن المشتري إذا اشترى الدار بما فيها، وفيها ما لم يقع عليه البيع بعينه لم يصلح له أخذها، وأنه يجب على البائع والمشتري أن يوقفا البيع على كل شيء فيها بعينه','5222','413','0');
INSERT INTO [t21618] VALUES (719,'باب ذكر الخبر الدال على الإباحة لمتولي مال غيره أن يصرفه في تجارة، ومعاملة لمنفعة صاحبه، والإباحة لصاحبه أخذ ربحته ومنفعته','5224','413','0');
INSERT INTO [t21618] VALUES (720,'بيان الخبر الدال على الإجازة لمتولي مال غيره، ومتولي الأمر أن يحدث في مال غيره بنقصان فيه','5235','413','0');
INSERT INTO [t21618] VALUES (721,'أبواب المواريث','5238','413','0');
INSERT INTO [t21618] VALUES (722,'باب ذكر الخبر المبين أن الكافر لا يرث المسلم، ولا يرث المسلم الكافر، وإن كان الكافر ذميا، أو غيره','5239','413','0');
INSERT INTO [t21618] VALUES (723,'باب ذكر الخبر الموجب قسم المال بين أهل الفرائض على كتاب الله، وإعطاء بقيته الذكور من العصبة','5243','413','0');
INSERT INTO [t21618] VALUES (724,'باب فريضة الأخت الواحدة والأختين إذا لم يكن للميت ولد ولا والد، والدليل على أنه لا يجب رد البقية عليهم','5247','413','0');
INSERT INTO [t21618] VALUES (725,'باب ذكر تفسير الكلالة وأنها آخر آية نزلت "','5255','413','0');
INSERT INTO [t21618] VALUES (726,'باب الخبر الموجب على الإمام قضاء ديون من مات من المسلمين، ولم يترك وفاء لقضاء دينه، وأنه إن ترك مالا فهو لورثته، والدليل على أن الإمام لا يرثه إذا لم يترك وارثا','5265','413','0');
INSERT INTO [t21618] VALUES (727,'باب ذكر الخبر الدال على أن بيت المال عصبة من لا عصبة له، وأن ذوي الأرحام لا يرثون','5275','413','0');
INSERT INTO [t21618] VALUES (728,'باب ذكر الخبر المورث الخال إذا لم يكن للميت وارث، والدليل على فساد من يقول بورث ذوي الأرحام إذ من قولهم أن الخال يرث مع ورثة كثيرة من ذوي الأرحام','5279','413','0');
INSERT INTO [t21618] VALUES (729,'أبواب في الهبة، والعمرى وما فيها من السنن، وبيان تحريم العود في الهبة لمن كانت من الواهب طمع في ثوابها أم لا','5290','413','0');
INSERT INTO [t21618] VALUES (730,'بيان حظر الناحل بعض بنيه نحلا دون بعض، والدليل على صحة نحل الأب للمنحول إذا لم يترجع فيه، وعلى أن للأب أن يرتجع في عطيته ولده متى ما أراد إذا لم يكن سوى بينهم، وأنه ليس ذلك لغير الأب','5306','413','0');
INSERT INTO [t21618] VALUES (731,'باب الخبر الدال على أن الأب إذا نحل أولاده الذكور والإناث عبدا نحل كل واحد منهم مثله','5322','413','0');
INSERT INTO [t21618] VALUES (732,'بيان الخبر الدال على أن الأب إذا نحل أولاده الذكور، والإناث نحلا، وأحب أن يسوي بينهم أعطى الذكور مثل الأنثيين','5330','413','0');
INSERT INTO [t21618] VALUES (733,'بيان الخبر الدال على أن الرجل إذا نحل ابنا له نحلا، وله بنون سواه فلم يعطهم مثله أنه باطل غير جائز، والإباحة لمن دعي إلى شهادة يعلم أنه غير جائز أن يأبى، ويمتنع من قبولها','5337','413','0');
INSERT INTO [t21618] VALUES (734,'باب الخبر الناهي عن العمرى، والخبر المجيز لها، وأن الشرط فيها بالرجوع إلى المعمر باطل إذا جعلها له ولعقبه','5339','413','0');
INSERT INTO [t21618] VALUES (735,'بيان الخبر المبيح للمعمر الارتجاع فيها إذا لم يجعلها لعقبه من بعده، وجعلها له حياته','5344','413','0');
INSERT INTO [t21618] VALUES (736,'بيان إثبات العمرى للمعمر ولعقبه من بعده، وأن له أن يجعلها لعقبه','5351','413','0');
INSERT INTO [t21618] VALUES (737,'مبتدأ كتاب الوصايا','5370','413','0');
INSERT INTO [t21618] VALUES (738,'باب ذكر الخبر الموجب على المسلم الذي له شيء أن لا يبيت ليلتين إلا، ووصيته مكتوبة عنده، والدليل على أنه ليس ذلك على من لا شيء له','5371','413','0');
INSERT INTO [t21618] VALUES (739,'بيان وجوب الوصية على من له مال يريد أن يوصي فيه والدليل على إباحة تركها لمن لا تجب عليه الوصية في ماله بأداء دين أو تبعة أو غيرها، ولا يريد أن يوصي منها بصدقة','5378','413','0');
INSERT INTO [t21618] VALUES (740,'بيان الخبر المبين أن النبي صلى الله عليه وسلم لم يوص شيئا إلى أحد، والدليل على أنه لم يوص في المال لأنه لم يترك شيئا من الأموال ميراثا، وبيان الخبر المبين أنه أوصى بما وجب عليه، والدليل على أن من ليس له شيء يوصي فيه يجب عليه أن يوصي بالقول بما يجب','5380','413','0');
INSERT INTO [t21618] VALUES (741,'باب الخبر الدال على أن الموصي إذا لم ينصب وصيا بعينه، وأوصى إلى من حضره يجب على الحاكم إنفاذ وصيته، وإثبات إخراج المشركين من جزيرة العرب','5391','413','0');
INSERT INTO [t21618] VALUES (742,'باب منع المريض من ماله أن يتصدق منه في مرضه بأكثر من الثلث إذا أشفى على الموت، والخبر المانع عنه إذا مرض، وأنه مبيح له أن يقسم ثلثه على من أحب','5394','413','0');
INSERT INTO [t21618] VALUES (743,'باب حظر الوصية بأكثر من الثلث وإجازتها بالثلث، والدليل على أن من أوصى بأكثر من الثلث فهو مردود، وعلى أن الوصية للأبعدين جائز، وأن المعتق والمتصدق في المرض بأكثر من الثلث يرد إلى الثلث','5402','413','0');
INSERT INTO [t21618] VALUES (744,'باب إباحة الرجوع في التدبير، والدليل على أنها وصية وأن الموصي بشيء له أن يرجع عنه متى ما أحب، وعلى أن الموصي بماله كله مردود يوم يوصي','5414','413','0');
INSERT INTO [t21618] VALUES (745,'باب الدليل على أن من مات بغير وصية، وله ولد يصدق عنه، لتكونن له كفارة بتركه الوصية','5435','413','0');
INSERT INTO [t21618] VALUES (746,'بيان انقطاع العمل عن الميت، إلا من ثلاثة من صدقة حبيسة جارية، ومن علم ينتفع به ومن ولد صالح يدعو له','5443','413','0');
INSERT INTO [t21618] VALUES (747,'مبتدأ أبواب في النذور','5447','413','0');
INSERT INTO [t21618] VALUES (748,'باب الخبر الموجب على الولد إذا نذر أبواه نذرا أن يقضيه عنهما إذا ماتا، ولم يقضيانه','5448','413','0');
INSERT INTO [t21618] VALUES (749,'بيان الأخبار الناهية إيجاب المرء على نفسه نذرا في ماله، وإن لم يكن معصية، والدليل على أنه إذا نذر وجب عليه إخراجه، وقضاؤه، وعلى أن النذر غير مقرب بعيدا، ولا دافع عن صاحبه سوءا ولا جارا إليه نفعا ولا خيرا','5456','413','0');
INSERT INTO [t21618] VALUES (750,'باب الخبر المبين أن المقدور كائن، وأن الله عز وجل قدر الأشياء قبل كونها، وأن النذر لا يردها، ولكنه ربما يوافق القدر','5463','413','0');
INSERT INTO [t21618] VALUES (751,'بيان حظر النذر في معصية، وفيما لا يملكه الناذر، وأنه لا يوفى به والدليل على أنه ليس فيه كفارة، ولا في نذر يعجز عن الوفاء به، وعلى أن الملك هو الذي يملك بحق، وأنه لا يحكم لمن في يديه الشيء إذا ثبت للمدعي أنه كان له','5467','413','0');
INSERT INTO [t21618] VALUES (752,'باب الإباحة لمن نذر أن يمشي، وأجهد في مشيه أن يركب، والدليل على أن الحمل على النفس فوق طاقتها فيما لم يفرض عليها ليس من البر، وأن النذر إذا لم يكن مسمى جاز فسخه','5476','413','0');
INSERT INTO [t21618] VALUES (753,'بيان وجوب الكفارة في النذور، وأن كفارته كفارة يمين، وبيان الخبر الدال على إسقاطه','5481','413','0');
INSERT INTO [t21618] VALUES (754,'باب الخبر الموجب على الناذر أن يعتكف في المسجد الحرام أن يوفي بنذره، وأن الكافر إذا كان عليه نذر في حال كفره فأسلم، ولما قضاه أن يوفي به في الإسلام','5487','413','0');
INSERT INTO [t21618] VALUES (755,'باب الخبر المبيح لمن نذر أن يصلي في بيت المقدس أن يصلي بدلها في مسجد الحرام، والدليل على أن من نذر أن يصلي في مسجد سوى مسجد الحرام، ومسجد المدينة، ومسجد بيت المقدس جاز له أن يصلي في أي مسجد أحب','5502','413','0');
INSERT INTO [t21618] VALUES (756,'باب الخبر الذي احتج به بعض أهل العلم في الإباحة لمن نذر أن يتصدق بجميع ماله أن يمسك الثلثين منه، ويتصدق بالثلث، وبيان الخبر الدال على أن من نذر نذرا كان من المال وجب إخراجه','5504','413','0');
INSERT INTO [t21618] VALUES (757,'مبتدأ أبواب في الأيمان','5508','413','0');
INSERT INTO [t21618] VALUES (758,'باب حظر الحلف بالآباء، ووجوب الحلف بالله، وحظر الحلف إلا به عز وجل','5509','413','0');
INSERT INTO [t21618] VALUES (759,'باب ما يجب أن يقول الحالف إذا حلف باللات والعزى خطأ، وبيان من يقول تعال أقامرك','5528','413','0');
INSERT INTO [t21618] VALUES (760,'باب وجوب حنث اليمين إذا رأى الحالف خيرا منها وكفارتها، وعلى أن الكفارة قبل الحنث','5533','413','0');
INSERT INTO [t21618] VALUES (761,'بيان الخبر المعارض لتكفير اليمين قبل الحنث الموجبة كفارتها بعد الحنث','5555','413','0');
INSERT INTO [t21618] VALUES (762,'بيان الأخبار الدالة على أن الحالف إذا رأى غير ما حلف عليه خيرا منها أتى الذي هو خير بلا كفارة، وبيان الخبر الموجب تكفير اليمين الذي يرى حالفها خيرا منها، وأنه أجر له إذا حنث، وإثم له إذا ثبت على يمينه','5574','413','0');
INSERT INTO [t21618] VALUES (763,'بيان الخبر الدال على أن من قال: هو يهودي أو نصراني أو حلف بملة سوى الإسلام كاذبا','5584','413','0');
INSERT INTO [t21618] VALUES (764,'بيان ذكر التشديد فيمن حلف بعد العصر كاذبا وعند منبر النبي صلى الله عليه وسلم وعقابه','5596','413','0');
INSERT INTO [t21618] VALUES (765,'باب ذكر الخبر الدال على أن من وجبت عليه يمين لأحد من الناس فحلف على شيء ونوى الحلف على خلاف الظاهر أنه لا تنفعه نيته، وأنه يلزمه ما حلف لصاحبه، وأن النية في ذلك نية المستحلف','5603','413','0');
INSERT INTO [t21618] VALUES (766,'باب الخبر الدال على أن قسم المرء على غيره ليس بيمين توجب كفارة، وأن للمقسوم عليه أن يحنث صاحبه إن شاء، وأن اليمين بالله تسمى قسما، والدليل على أن اليمين على ما لا يملكه ليس بيمين','5607','413','0');
INSERT INTO [t21618] VALUES (767,'بيان ذكر الخبر المبيح للحالف إذا استثنى أن يترك يمينه ولا يكون حانثا','5611','413','0');
INSERT INTO [t21618] VALUES (768,'باب الخبر الموجب البينة على المدعي، واليمين على المدعى عليه، وإن كان ممن لا يوثق به، والدليل على إبطال رد اليمين على المدعي إذا لم يكن له بينة','5623','413','0');
INSERT INTO [t21618] VALUES (769,'باب الخبر الموجب اليمين على المدعي مع الشاهد الواحد والدليل على أنه يحلف أي مدعي كان مع شاهده','5630','413','0');
INSERT INTO [t21618] VALUES (770,'بيان ذكر الخبر الذي يجعل الحل عليه حراما يمينا','5648','413','0');
INSERT INTO [t21618] VALUES (771,'باب إيجاب القرعة بين الاثنين وقعت اليمين بينهما في الشيء الذي ليس في يدي واحد منهما','5650','413','0');
INSERT INTO [t21618] VALUES (772,'باب ما يجب في القسامة، وفي الأيمان فيها، وأن القوم إذا قتل لهم قتيلا لا يدري من قتله فادعى أولياؤه دمه على قوم يبدأ أكبر ولي له فيدعيه، وعلى أنه يجب على الحاكم أن يبدأ فيعرض الأيمان على المدعين','5652','413','0');
INSERT INTO [t21618] VALUES (773,'باب بيان الخبر الموجب البينة على المدعي في قصة القسامة والأيمان على المدعى عليهم وعلى رد اليمين على المدعي إذا لم يرضى بيمين المدعى عليه، وأنه إن لم يحلف بطل دعواه وبرئ المدعى عليه وبيان وجوب دية المقتول على الإمام إذا لم يدر قاتله','5657','413','0');
INSERT INTO [t21618] VALUES (774,'باب ذكر الخبر الدال على أن القتيل إذا وجد بين ظهراني أعدائه طولبوا بديته، فإن حلفوا أنهم لم يقتلوه برئوا فإن لم يرض أولياء المقتول بأيمانهم واستحقوا دية المقتول فإن أبوا أن يحلفوا بطل دعواهم عليهم ووداه الإمام من عنده بمائة من الإبل دفعة واحدة وأن الذي','5660','413','0');
INSERT INTO [t21618] VALUES (775,'باب ذكر الخبر المبين أن القسامة كانت في الجاهلية فقضى بها رسول الله صلى الله عليه وسلم وأقرها على ما كانت','5663','413','0');
INSERT INTO [t21618] VALUES (776,'أبواب في المماليك، والتشديد في ضربهم وأن كفارة الرجل إذا ضرب مملوكه أن يعتقه','5669','413','0');
INSERT INTO [t21618] VALUES (777,'بيان التشديد في قذف الرجل مملوكه وضربه، والدليل على أنه لا يحد في قذفه ويحد يوم القيامة ويقاد منه','5683','413','0');
INSERT INTO [t21618] VALUES (778,'بيان الخبر الموجب إطعام الرجل مملوكه، وأجيره مما يأكل، ويلبسهما مما يلبس، والنهي عن استعمالهما ما لا يطيقان، وأن يعير صاحبه بأمه أو أبيه','5687','413','0');
INSERT INTO [t21618] VALUES (779,'بيان الخبر الموجب على الرجل أن يجلس مملوكه معه للأكل أو يناوله مما يأكل إذا ولي صنعته، وبيان وجوب نفقة المملوك عليه لطعامه وكسوته','5693','413','0');
INSERT INTO [t21618] VALUES (780,'بيان فضل المملوك المسلم الناصح لسيده','5699','413','0');
INSERT INTO [t21618] VALUES (781,'كتاب الحدود','5708','0','0');
INSERT INTO [t21618] VALUES (782,'باب بيان إقامة الحد على من يرتد عن الإسلام فيصيب من دماء المسلمين وأموالهم غدرا في ارتداده','5709','781','0');
INSERT INTO [t21618] VALUES (783,'باب إباحة رضخ رأس القاتل بالحجارة إذا كان قتله بها وأن القاتل بالحجارة يقاد منه ولا يسمى قتل خطأ والدليل على أن المريض إذا اعتقل لسانه فأشار برأسه إشارة يفهم منها أنفذت وصيته وحكم الحاكم بإشارته','5732','781','0');
INSERT INTO [t21618] VALUES (784,'باب بيان الإباحة للإمام رجم الكافر حتى يموت إذا قتل مسلمة، ورضخ رأسها بالحجارة','5736','781','0');
INSERT INTO [t21618] VALUES (785,'باب ذكر الخبر المبين أن النبي صلى الله عليه وسلم أقاد من اليهودي الذي قتل الجارية بعد ما أقر','5738','781','0');
INSERT INTO [t21618] VALUES (786,'باب بيان إبطال دية سن العاض يد صاحبه فتسقط أو تنكسر بانتزاع صاحبه يده من فيه، وإسقاط القود من أن يعض يد العاض','5742','781','0');
INSERT INTO [t21618] VALUES (787,'باب إثبات القصاص في الجراح، والإباحة للإمام أن يتأنى في القصاص إذا امتنع الجارح من القصاص، وطلب المجروح الدية، والإباحة لمن يتشفع في ترك القود','5752','781','0');
INSERT INTO [t21618] VALUES (788,'باب الخبر الموجب قتل الثيب الزاني، وقتل قاتل النفس، وقتل التارك دينه المفارق للجماعة، وحظر قتل غير هؤلاء','5755','781','0');
INSERT INTO [t21618] VALUES (789,'باب بيان الخبر الدال على أن من سن القتل في قوم لم يجب عليهم أو قتل قتيلا بغير كتاب، ولا سنة فاستنوا به كان عليه كفل من دمائهم والخبر المبين أن أول ما يقضى يوم القيامة في الدماء','5762','781','0');
INSERT INTO [t21618] VALUES (790,'بيان تحريم دم المسلم على المسلم وأن قتاله كفر والدليل على أن قاتل المسلم يعتبر كافرا بقتله المسلم','5769','781','0');
INSERT INTO [t21618] VALUES (791,'بيان الخبر الموجب على الإمام إذا ادعى على رجل قتل رجل أن يسأله أقتلته؟','5780','781','0');
INSERT INTO [t21618] VALUES (792,'باب دية الجنين إذا سقط ميتا ودية أمه إذا قتلت بحجر، وأن ديتها على عاقلة القاتل، والدليل على أن الرمية بالحجر يحكم فيها بحكم الخطأ','5789','781','0');
INSERT INTO [t21618] VALUES (793,'بيان الخبر الدال على أن المضروبة إذا سقط جنينها ميتا لم يكن على ضاربها لضربها شيء وعليه من جنينها غرة عبد أو أمة وأن الذكر والأنثى فيه سواء','5793','781','0');
INSERT INTO [t21618] VALUES (794,'بيان الخبر الدال على أن الحامل إذا قتلت حكم على القاتل ديتها ودية ما في بطنها وأن الضارب بعمود فسطاط، وشبهه فيحدث منه القتل بحكم الخطأ والدليل على أن العاقلة تحمل الدية وما دون ثلث الدية','5796','781','0');
INSERT INTO [t21618] VALUES (795,'بيان الخبر الموجب قطع يد السارق في ربع دينار وأنه لا قطع فيما دون ذلك','5802','781','0');
INSERT INTO [t21618] VALUES (796,'بيان الخبر الدال على إيجاب القطع على سارق الترس والمجن إذا كان ذا ثمن','5813','781','0');
INSERT INTO [t21618] VALUES (797,'بيان الخبر الموجب قطع يد السارق فيما يبلغ ثمنه ثلاثة دراهم','5816','781','0');
INSERT INTO [t21618] VALUES (798,'بيان الخبر الموجب القطع على السارق البيضة والحبل، والإباحة لمن يلعن السارق','5826','781','0');
INSERT INTO [t21618] VALUES (799,'بيان الخبر الناهي أن يشفع إلى الإمام في قطع السارق، والدليل على أن القطع في السرقة إلى الإمام، وليس إلى المسروق منه شيء، وأنه لا يجوز للإمام أن يعفو عنه، وأن المستعير إذا جحد وجب الحكم فيه بحكم السرقة','5828','781','0');
INSERT INTO [t21618] VALUES (800,'بيان الخبر الموجب على الزاني الثيب جلد مائة ثم الرجم، وعلى البكر جلد مائة ثم نفي سنة، وأن إمساكهن في البيوت منسوخ','5837','781','0');
INSERT INTO [t21618] VALUES (801,'باب ذكر الخبر المبين أن الرجم في آية من كتاب الله عز وجل كانت على عهد رسول الله صلى الله عليه وسلم تتلى في القرآن','5842','781','0');
INSERT INTO [t21618] VALUES (802,'بيان السنة في رجم من يقر على نفسه بالزنا، وصفة سؤال الإمام إياه وإعراضه عنه في إقراره حتى يقر أربع مرات، وأنه لا يصلي عليه الإمام ويرجم بالمصلى، وأنه إن فر من الرجم يلحق ويرجم','5847','781','0');
INSERT INTO [t21618] VALUES (803,'بيان الخبر الموجب رجم المقر على نفسه بالزنا مرتين، والدليل على أن السنة أن ينكل به مع الرجم، وعلى أن المخبر الإمام بفجور فاجر بامرأة يدعي عليه بأنه فجر بامرأة لم يحده الإمام','5855','781','0');
INSERT INTO [t21618] VALUES (804,'باب إباحة الرجم بالعظام والمدر والخزف والمرجوم منتصب لمن يرجمه من غير أن يحفر له، والدليل على أن الإمام يجب عليه أن لا يرجم المقر على نفسه بالزنا حتى يسأل عن عقله','5864','781','0');
INSERT INTO [t21618] VALUES (805,'باب بيان الإباحة للإمام أن يصلي على الزانية المرجومة، والنهي عن رجمها وهي حبلى وحظر رجمها قبل أن يطعم ولدها إلا أن يكون هناك من يكفل صبيها، والدليل على أن توبة الزانية والزاني الرجم، وبيان الأمر برجمهما في حفيرة تحفر لهما إلى صدرهما، والإباحة للإمام','5869','781','0');
INSERT INTO [t21618] VALUES (806,'بيان الخبر الدال على إسقاط جلد الزانية إذا رجمت، وأن البكر إذا زنا غرب عاما ثم جاز له الرجوع، وعلى أن المقر على نفسه بالزنا مرة يرجم','5878','781','0');
INSERT INTO [t21618] VALUES (807,'بيان الخبر الموجب رجم الزاني من أهل الكتاب إذا رفع أمره إلى حاكم المسلمين، وبيان قبول حاكم المسلمين قول أهل الذمة في الزنا، والدليل على أن الحكم فيهم بأحكام المسلمين','5884','781','0');
INSERT INTO [t21618] VALUES (808,'بيان الموضع الذي أمر فيه رسول الله صلى الله عليه وسلم برجم اليهوديين','5892','781','0');
INSERT INTO [t21618] VALUES (809,'بيان الخبر الموجب على الإمام تغيير حكم أهل الكتاب إذا رآهم حكموا فيه بخلاف حكم الله عز وجل وإن لم يتحاكموا فيه إليه، وأن الزاني منهم يقام عليه حكم الله تعالى، وإن لم يرفع أمره إلى حاكم المسلمين','5894','781','0');
INSERT INTO [t21618] VALUES (810,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم رجم من أهل الإسلام وأهل الكفار','5897','781','0');
INSERT INTO [t21618] VALUES (811,'باب ذكر الخبر المبين الموجب على سيد الأمة جلدها إذا زنت، وتبين له ذلك من غير تثريب، وإعادة الجلد عليها إذا زنت مرة أخرى وبيعها في المرة الثالثة، وإجازة جلد السيد دون السلطان','5903','781','0');
INSERT INTO [t21618] VALUES (812,'باب الخبر الموجب على سيد العبد والأمة إقامة الحد عليهما إذا زنيا، والدليل على أن عليهما الجلد أحصنا أو لم يحصنا، وعلى إباحة ترك جلد الأمة إذا كانت حديث عهد بالنفاس إذا خيف عليها الموت','5911','781','0');
INSERT INTO [t21618] VALUES (813,'باب مبلغ حد شارب الخمر وصفة ضربه وما يضرب به','5913','781','0');
INSERT INTO [t21618] VALUES (814,'بيان حظر جلد المسلم فوق عشرة إلا في الحد الذي أوجبه الله أو أوجبه رسول الله صلى الله عليه وسلم، والدليل على الإباحة للإمام ضرب من يرى ضربه عشرة أسواط من غير الحد','5921','781','0');
INSERT INTO [t21618] VALUES (815,'بيان الكبائر التي إذا ارتكبها المسلم فأقيم عليه حدها وعوقب بها كانت كفارة له','5924','781','0');
INSERT INTO [t21618] VALUES (816,'باب إسقاط الحكم في الدية عن أصحاب الدواب، والأنعام فيما يصبن من الناس والدواب والأنعام، وكذلك أصحاب الآبار والمعادن فيما يسقط فيها من الناس والدواب','5932','781','0');
INSERT INTO [t21618] VALUES (817,'مبتدأ كتاب الأحكام','5950','781','0');
INSERT INTO [t21618] VALUES (818,'بيان الخبر الموجب على الحاكم أن يحكم بالظاهر بحجة المدعي، والدليل على أن أحكام الحاكم ربما تكون بخلاف الحق عند الله تعالى وأنه قد يكون الحكم في الشيء بخلاف ما يجب في الباطن','5951','781','0');
INSERT INTO [t21618] VALUES (819,'باب الخبر الموجب نصيحة الحاكم والإمام، والنهي عن قيل وقال، وكثرة السؤال، وإضاعة المال، والدليل على كراهية الخصومات في الأموال مع الناس، والخوض والكلام فيها، وعلى الترغيب في تعاهد صنوف الأموال، والنهي عن إضاعته ليستغني عن مسألة الناس','5960','781','0');
INSERT INTO [t21618] VALUES (820,'باب ما للحاكم من الأجر إذا اجتهد في إصابة الحكم، والدليل على أنه إذا اجتهد فأخطأ ويرى أنه مصيب فيه أنه غير آثم فيه ويؤجر على اجتهاده','5968','781','0');
INSERT INTO [t21618] VALUES (821,'بيان حظر الحكم بين اثنين، والحاكم غضبان والدليل على أن الغضب يزيل الفهم','5972','781','0');
INSERT INTO [t21618] VALUES (822,'بيان رد حكم الحاكم إذا حكم بغير الحق، ورد القضايا إذا كانت خلاف السنة، والدليل على أن الخصمين إذا ادعى أحدهما ما له أن يدعيه وكان في دعواه ضرر به وبخصمه أن يرد الحاكم دعواه إلى ما هو أنفع لهما، وأن الجهالات ترد إلى السنة','5980','781','0');
INSERT INTO [t21618] VALUES (823,'بيان الترغيب في إقامة الشهادة، وإن لم يسألها، والخبر الدال على كراهية شهادة الشاهد ولا يستشهد، وعلى أنها الشهادة التي لا تجب','5985','781','0');
INSERT INTO [t21618] VALUES (824,'بيان الإباحة للحاكم أن يفزع الخصمين، ويحتال عليهما ليقر المنكر منهما بالحق أو يتبين له طالب الحق','5988','781','0');
INSERT INTO [t21618] VALUES (825,'بيان الحكم في اللقطة، ووجوب تعريفها وإباحة أكلها، واستنفاقها، والاستمتاع بها بعد تعريفها حولا، ووجوب حفظ عددها ووعائها ووكائها وردها بعد ذلك على صاحبها إن جاء فأخبر بعلاماتها وعلى أن أخذها أفضل من تركها','5995','781','0');
INSERT INTO [t21618] VALUES (826,'باب إباحة أخذ الضالة من الغنم، والدليل على أنها إذا وجدت بمهلكة كان له أخذها من غير أن يعرفها وأنه إذا استهلكها ثم جاء صاحبها لم يجب عليه ردها، ولا قيمتها، وعلى أنه إذا وجدها في موضع لا يخاف عليها الذئب، والتلف وجب عليه تعريفها سنة وردها على صاحبها','6004','781','0');
INSERT INTO [t21618] VALUES (827,'بيان الخبر الدال على إيجاب تعريف الضوال، وأنها لا ترد على صاحبها إلا ببينة، وحظر حلب ماشية من كان إلا بأمر صاحبها، والدليل على أنه لا يجوز لأحد أخذهن إذا كن في مأمن، وعلى حظر دخول الحيطان، وأكل ثمارها إلا بأمر صاحبها','6007','781','0');
INSERT INTO [t21618] VALUES (828,'باب الخبر الدال على أن الملتقط لقطة، إذا عرفها سنة، فلم يعترف كانت مالا من ماله، وليس عليه ردها بعد ولا تعريفه عفاصها ووكاءها، وبيان الخبر المبين أنها بعد السنة وديعة عند ملتقطها، ويجب عليه ردها بعد إذا جاء صاحبها وأنه مباح له الانتفاع بها بعد السنة','6016','781','0');
INSERT INTO [t21618] VALUES (829,'بيان الخبر الدال على إيجاب تعريف كل لقطة قليلا كان أو كثيرا ذهبا كان أو فضة متاعا كان أو طعاما أو ثمارا إذا وقع عليها اسم اللقطة، واللقطة التي لا يجب تعريفها وإباحة أكلها','6020','781','0');
INSERT INTO [t21618] VALUES (830,'باب الخبر الناهي عن لقطة الحاج، والخبر الدال على إباحة التقاطها لمنشدها ولا ينتفع بها','6023','781','0');
INSERT INTO [t21618] VALUES (831,'باب الخبر الموجب الحكم بأصل الشيء للمدعي فيه إذا أثبت أنه كان لأبيه أو له إذا كان الشيء في يد المدعى عليه فإن لم يكن له بينة على دعواه حلف المدعى عليه فاجرا كان أو غير ذلك وأقر الشيء في يديه','6027','781','0');
INSERT INTO [t21618] VALUES (832,'بيان الخبر المبيح لمن يحكم عليه بحكم فرضي به أن يرتجع فيه إذا تبين له أن الحق بخلاف ما حكم عليه، وأن الماضي من حكم الحاكم مردود، ولو بعد حين إذا قضى بخلاف الحق وأن الخبر الواحد والحكم بقوله مقبول وعلى أن حكم النبي صلى الله عليه وسلم كله بكتاب الله عز','6030','781','0');
INSERT INTO [t21618] VALUES (833,'بيان الخبر الدال على إبطال الحكم بقول السكران وما يلفظ به ويقر على نفسه والحكم على المرأة برضاعة ولدها، وإن الشارب إذا وجد منه ريح الخمر حكم عليه بحكم السكران','6032','781','0');
INSERT INTO [t21618] VALUES (834,'باب الخبر الموجب على الحاكم أن يحكم بما يظهر له من حجة الخصمين، والدليل على أن الحاكم إذا قضى لأحد الخصمين ببينة أو بيمينه ثم أقام المحكوم عليه بينة ظهرت له بعد تنقض حجة المقضي له أو يمينه إن ذلك القضاء مردود على الحاكم، وأنه يسأل عن تعديل الشاهد','6035','781','0');
INSERT INTO [t21618] VALUES (835,'باب السنة في الداخل على الإمام إذا جلس للحكم أن يقف إذا انتهى إلى مجلسه حتى يأمره بالدنو منه أو الجلوس','6038','781','0');
INSERT INTO [t21618] VALUES (836,'مبتدأ كتاب الجهاد','6040','781','0');
INSERT INTO [t21618] VALUES (837,'بيان الخبر المبين بلوغ الصغار وقبول قولهم والحكم عليهم إذا بلغوا تلك المدة أو ظهرت العلامة التي تدل على بلوغهم قبلها، والدليل على إبطال قبول قولهم والحكم عليهم قبل ذلك','6041','781','0');
INSERT INTO [t21618] VALUES (838,'باب الخبر المبيح للبعث الذين يبعثهم الإمام أخذ حق الضيف الذي ينبغي لهم أن يقروهم، والدليل على ذلك وأنه يوم وليلة، وإباحة مقام الضيف عند من يضيفه ثلاثة أيام، والدليل على الكراهة في كونه عنده فوق ذلك وفي كونه عند من ليس له سعة بقوته','6053','781','0');
INSERT INTO [t21618] VALUES (839,'باب الخبر الموجب على من له فضل ظهر أو زاد أو غير ذلك وهو في سفر أن يدفع ذلك إلى من لا ظهر معه أو من لا زاد معه، وعلى من هيأ ظهرا وزادا ليخرج فمنعته علة أن يدفعه إلى من يخرج','6058','781','0');
INSERT INTO [t21618] VALUES (840,'بيان الخبر الدال على الإباحة لأمير القوم في السفر أن يأمر من عنده فضل زاد أن يطعم منه من لا زاد معه، وعلى إباحة إحضار القوم طعامهم فيخلطونها ويجتمعون على أكلها','6061','781','0');
INSERT INTO [t21618] VALUES (841,'باب السنة في توجيه البعث، وما يجب على الإمام أن يتقدم إليهم فيما يجب عليهم في وجوههم، وحظر الغدر في غزوهم والمثلة، وقتل الولائد، ووجوب دعوة المشركين قبل قتالهم إلى ما يجب عليهم، وحظر إنزالهم من حصونهم على حكم الله حذارا على إصابته وإباحة قبول الجزية','6063','781','0');
INSERT INTO [t21618] VALUES (842,'بيان الخبر المبيح للإمام قتال المشركين قبل دعوتهم فإن وجوب الدعوة قبل قتالهم منسوخ، وإباحة الوقوع بهم على غرة منهم، وسبي ذراريهم، وبيان إباحة الإنكار فيهم، والخداع في محاربتهم وتنفيرهم','6085','781','0');
INSERT INTO [t21618] VALUES (843,'بيان الخبر الموجب على الموجه لقتال المشركين، وداعيهم إلى الإسلام أن ييسر في العرض عليهم، ولا يشدد، ويسكنهم، ولا يفزعهم فينفرهم','6111','781','0');
INSERT INTO [t21618] VALUES (844,'باب حظر تمني لقاء العدو، ووجوب مصابرتهم إذا التقى المسلمون معهم، والدليل على أنهم يتركون ما تركوا المسلمين إلا من يحب المسلمون غزوهم ودعوتهم إلى الإسلام، وبيان الدعاء لمن أراد أن يغزو','6118','781','0');
INSERT INTO [t21618] VALUES (845,'بيان حظر قتل النساء والصبيان في دار الحرب والغزو','6131','781','0');
INSERT INTO [t21618] VALUES (846,'بيان الخبر المبيح بيات المشركين والغارة عليهم بالليل، وقتلهم، وإن أصيب في قتلهم نساؤهم وصبيانهم حتى قتلوا معهم، والدليل على أن نهيه عن قتل النساء والصبيان هو المتأخر، وعلى أن السنة في ترك الغارة بالليل حتى يصبح، وعلى أنه لا يجوز حرق القرية التي فيها','6138','781','0');
INSERT INTO [t21618] VALUES (847,'بيان الإباحة للإمام الحريق في أرض العدو','6145','781','0');
INSERT INTO [t21618] VALUES (848,'باب حظر الغنائم على من كان قبل هذه الأمة، وإباحتها للنبي صلى الله عليه وسلم وأمته، وأنها حلال طيب، والإباحة للإمام أن يمنع من الغزو من لا يصلح له، ويختار من لا يترك خلفه همه يشتغل قلبه بها','6152','781','0');
INSERT INTO [t21618] VALUES (849,'بيان منع النفل من الخمس من له في الغنيمة نصيب، والأخبار المبيحة للإمام أن ينفلهم منه بعد نصيبهم، وأن يؤثر به السرية دون الجيش وصفة الشيء المباح أخذه لواجده أخذه بحضرة الإمام قبل القسمة','6156','781','0');
INSERT INTO [t21618] VALUES (850,'باب إباحة سلب المقتول لقاتله ووجوب الحكم له به إذا استولى عليه غيره','6174','781','0');
INSERT INTO [t21618] VALUES (851,'بيان الإباحة للإمام إذا قتل رجلان قتيلا أن يعطي سلب المقتول أحدهما دون الآخر','6179','781','0');
INSERT INTO [t21618] VALUES (852,'بيان الخبر الدال على أن دفع سلب المقتول إلى قاتله إلى الإمام إن رأى دفعه إليه دفعه، وإن استكثره، وإن رأى منعه منه منعه','6187','781','0');
INSERT INTO [t21618] VALUES (853,'باب ما يجب للإمام من القرية إذا فتحت عنوة، ولمن فتحها من سهامها، وما لمن يغنم المسلمين بها','6196','781','0');
INSERT INTO [t21618] VALUES (854,'باب الأخبار الدالة على الإباحة أن يعمل في أموال من لم يوجف عليه خيلا ولا ركابا من المشركين مثل ما عمل النبي صلى الله عليه وسلم فإنها لا تورث','6199','781','0');
INSERT INTO [t21618] VALUES (855,'بيان قسم الفرس، والرجل من النفل','6217','781','0');
INSERT INTO [t21618] VALUES (856,'باب إباحة قتل الأسارى المشركين، وترك قبول الفدية منهم، والإثخان فيهم إن خيف غائلتهم، والخبر المبيح للإمام الإطلاق عما لا يخافه','6221','781','0');
INSERT INTO [t21618] VALUES (857,'باب الخبر الموجب إخراج اليهود والنصارى من جزيرة العرب','6229','781','0');
INSERT INTO [t21618] VALUES (858,'بيان الإباحة للإمام إذ أنزل الإمام على حكمه أن يرد فيهم الحكم إلى غيره فإذا حكم فيهم أمضى الإمام فيهم','6237','781','0');
INSERT INTO [t21618] VALUES (859,'بيان الخبر الدال على أن النبي صلى الله عليه وسلم قسم غنائم خيبر في المهاجرين وغيرهم على ما وجب','6247','781','0');
INSERT INTO [t21618] VALUES (860,'بيان كتاب النبي صلى الله عليه وسلم إلى هرقل، وأنه كتب إلى كسرى وقيصر وإلى الجبابرة','6251','781','0');
INSERT INTO [t21618] VALUES (861,'بيان محاربة رسول الله صلى الله عليه وسلم المشركين يوم حنين، والدليل على الإباحة للرجل محاربة الفئة وحده','6268','781','0');
INSERT INTO [t21618] VALUES (862,'بيان محاربة النبي صلى الله عليه وسلم أهل الطائف، وانصرافه عنهم قبل فتحها','6282','781','0');
INSERT INTO [t21618] VALUES (863,'بيان صفة فتح النبي صلى الله عليه وسلم بمكة، وتوجيه الزبير وخالد بن الوليد، وأبا عبيدة رضي الله عنهم قدامه','6295','781','0');
INSERT INTO [t21618] VALUES (864,'بيان حظر قتل أحد من قريش صبرا، والدليل على أنهم قتلوا يوم الفتح صبرا، وعلى إباحة قتل غيرهم من المشركين صبرا','6303','781','0');
INSERT INTO [t21618] VALUES (865,'بيان مصالحة النبي صلى الله عليه وسلم المشركين يوم الحديبية، والدليل على الإباحة للإمام صرف أصحابه عن العدو، وإجابتهم إلى ما ليس لهم في الصلح إذا ظن أن ذلك أصلح للمسلمين','6308','781','0');
INSERT INTO [t21618] VALUES (866,'باب عدد أصحاب النبي صلى الله عليه وسلم يوم الحديبية، وأنهم بايعوه تحت الشجرة','6330','781','0');
INSERT INTO [t21618] VALUES (867,'بيان الخبر الدال على أن الشهيد في المعركة جائز غسله، والصلاة عليه، وأن القاتل نفسه خطأ في حرب العدو وهوشهيد يعطى أجره مرتين','6343','781','0');
INSERT INTO [t21618] VALUES (868,'بيان السنة فيمن يأخذه العدو فيعطيهم عهد الله عز وجل وميثاقه أنه لا يعين عليهم والدليل على إيجاب الأيمان المكرهة','6350','781','0');
INSERT INTO [t21618] VALUES (869,'بيان السنة في توجيه الطليعة، والمخاطرة به، والسنة في ترك التعرض للعدو إن قدر على ذلك، وثوابه، وثواب حارس المسلمين','6354','781','0');
INSERT INTO [t21618] VALUES (870,'بيان الشدة التي أصابت النبي صلى الله عليه وسلم وأصحابه في غزوة ذات الرقاع، ويوم أحد ومحاربته أعداءه','6360','781','0');
INSERT INTO [t21618] VALUES (871,'بيان شدة غضب الله سبحانه على من قتله رسول الله صلى الله عليه وسلم في سبيل الله، والإباحة لمن يخاطر بنفسه في حرب العدو عن الإمام، وبيان ثوابه، والدليل على أنه يكره للإمام إذا أمر رعيته بذلك ولم ينصفهم','6379','781','0');
INSERT INTO [t21618] VALUES (872,'بيان الإباحة في الاستعانة بالنساء والعبيد للإمام في مغازيه','6383','781','0');
INSERT INTO [t21618] VALUES (873,'بيان السنة في ترك الاستعانة للإمام بمن لا يؤمن بالله ورسوله وبالمشركين في مغازيه والدليل على أنهم إن حضروا الفتح لم يسهم لهم','6405','781','0');
INSERT INTO [t21618] VALUES (874,'بيان الشدة التي أصابت النبي صلى الله عليه وسلم يوم العقبة، وعفوه عمن عصاه بعد قدرته عليه وعمن آذاه بالقول','6407','781','0');
INSERT INTO [t21618] VALUES (875,'بيان عفو النبي صلى الله عليه وسلم عمن دعاه إلى الإيمان بالله فرد عليه قوله وأسمعه','6418','781','0');
INSERT INTO [t21618] VALUES (876,'بيان ندب النبي صلى الله عليه وسلم أصحابه إلى عدوه، والمؤذي له، وإباحته لهم المكر به بالقول والفعل','6423','781','0');
INSERT INTO [t21618] VALUES (877,'بيان صفة حفر الخندق، ونقل النبي صلى الله عليه وسلم التراب والشدة التي أصابتهم يوم هزم الأحزاب','6425','781','0');
INSERT INTO [t21618] VALUES (878,'بيان موافات النبي صلى الله عليه وسلم خيبر، وصفة محاربتهم ومحاصرتهم وفتحها','6446','781','0');
INSERT INTO [t21618] VALUES (879,'بيان عدد غزوات النبي صلى الله عليه وسلم','6457','781','0');
INSERT INTO [t21618] VALUES (880,'بقية باب عدد غزوات النبي صلى الله عليه وسلم وقد كان رسول الله صلى الله عليه وسلم بعث أمراءه وعماله على الصدقات إلى كل ما أوطأ الإسلام من البلدان: فبعث المهاجر بن أبي أمية بن المغيرة المخزومي رضي الله عنه إلى صنعاء، فخرج العنسي عليه وهو بها، وبعث زياد','6481','781','0');
INSERT INTO [t21618] VALUES (881,'مبتدأ كتاب الأمراء','6486','781','0');
INSERT INTO [t21618] VALUES (882,'بيان إثبات الخلافة لقريش وأنها فيهم أبدا، وأنهم المقتدى بهم في الإسلام والكفر','6487','781','0');
INSERT INTO [t21618] VALUES (883,'بيان عدد الخلفاء بعد رسول الله صلى الله عليه وسلم الذين ينصرون على من خالفهم، ويعز الله بهم الدين، وأنهم كلهم من قريش، والدليل على إبطال قول الخوارج','6493','781','0');
INSERT INTO [t21618] VALUES (884,'ذكر الخبر المبين أن النبي صلى الله عليه وسلم لم يستخلف، والدليل على أن المستخلف خليفة يكون عليه مثل وزره فيما يأتي إلى رعيته مما لا يجوز','6518','781','0');
INSERT INTO [t21618] VALUES (885,'بيان حظر طلب الإمارة، والاستشراف لها، والدليل على إباحة الدخول فيها إذا قلدها من غير سؤال، وأن الإمام يجب عليه منعها من يسألها أو يحرص عليها','6523','781','0');
INSERT INTO [t21618] VALUES (886,'بيان الترغيب في اجتناب الإمارة والكراهية في الدخول فيها','6537','781','0');
INSERT INTO [t21618] VALUES (887,'بيان ثواب الإمام العادل المقسط','6539','781','0');
INSERT INTO [t21618] VALUES (888,'بيان الأخبار الدالة على أنه يجب على الإمام حفظ رعيته، وتعاهدهم، وحفظ أحوالهم، وحياطتهم والذب عنهم، وأنه مسئول عنهم إذا ضيعهم، ولن يحوطهم','6546','781','0');
INSERT INTO [t21618] VALUES (889,'بيان عقاب الوالي الذي يلي أمر الناس، ولا ينصح لهم، ويغشهم، ولا يحتاط لهم','6561','781','0');
INSERT INTO [t21618] VALUES (890,'بيان التشديد في قبول الوالي هدايا رعيته، وحبسها لنفسه، وكتمانه إمامه ما يصيب في إمرته','6569','781','0');
INSERT INTO [t21618] VALUES (891,'بيان الخبر الموجب محاسبة الإمام عامله عند انصرافه من عمله، والبحث عما أصاب من ولايته','6576','781','0');
INSERT INTO [t21618] VALUES (892,'باب الأخبار الموجبة طاعة الأمير الذي يؤمره الإمام، وأن من أطاعه فقد أطاع الإمام','6594','781','0');
INSERT INTO [t21618] VALUES (893,'بيان الأخبار الموجبة على الرعية فرضا طاعة من يؤمر عليها عبدا كان الأمير أو غيره','6607','781','0');
INSERT INTO [t21618] VALUES (894,'بيان الأخبار المبيحة ترك طاعة الأمير إذا أمر بمعصية، ووجوب طاعته في جميع ما يدعو إليه من إجابته، واتباعه في غير معصية','6618','781','0');
INSERT INTO [t21618] VALUES (895,'بيان حظر منازعة الإمام أمره، وأمر أمرائه، ووجوب طاعتهم في الشدة، والحمل على النفس فيها','6627','781','0');
INSERT INTO [t21618] VALUES (896,'بيان الخبر الدال على إباحة منازعة الإمام أمره إذا ظهر منه الكفر الذي يخرج به من الإيمان، وعلى ترك منازعته إذا أمر بغير العدل، والتقوى وأن عليه منه وزرا','6632','781','0');
INSERT INTO [t21618] VALUES (897,'بيان الخبر الموجب على الرعية الوفاء ببيعة الإمام، وترك الامتناع من إعطاء حقهم الذي يجب لهم','6635','781','0');
INSERT INTO [t21618] VALUES (898,'بيان وجوب نصرة الخليفة إذا بويع لغيره، وإباحة قتل الآخر منهما ومحاربته ودفعه','6641','781','0');
INSERT INTO [t21618] VALUES (899,'بيان وجوب الصبر على الأثرة، وحبس الإمام، وترك التعرض له، وحظر حبس ما يجب له وأن حبس ما يجب عليه ظلم','6652','781','0');
INSERT INTO [t21618] VALUES (900,'بيان عقاب من ترك الطاعة، ونكث البيعة','6655','781','0');
INSERT INTO [t21618] VALUES (901,'ذكر حظر قتال الوالي الفاجر بفجوره، وتعديه إذا صلى، والدليل على إباحته إذا ترك الصلاة','6659','781','0');
INSERT INTO [t21618] VALUES (902,'بيان الخبر الموجب الاعتصام بالإمام، والجماعة في الفتنة','6665','781','0');
INSERT INTO [t21618] VALUES (903,'بيان ذكر الخبر الموجب طاعة الإمام، وإن لم يهتد بهدي النبي صلى الله عليه وسلم، ولم يستن بسنته، وإن ضرب ظهور رعيته','6668','781','0');
INSERT INTO [t21618] VALUES (904,'بيان الخبر الموجب للإخراج من أمة محمد من يقاتل للعصبة، ومن يخرج عليها يضرب برها وفاجرها، ومن يخرج من الطاعة','6670','781','0');
INSERT INTO [t21618] VALUES (905,'بيان الخبر الموجب نقض ما يأتي الوالي من المعصية، وعلامة خيار الأئمة وعلامة شرارها','6679','781','0');
INSERT INTO [t21618] VALUES (906,'بيان صفة بيعة الإمام، والسنة فيها، وإباحته التعرب بعد الهجرة، وبعد الفتح، وبيان السنة في حفظ الهجرة والبيعة','6686','781','0');
INSERT INTO [t21618] VALUES (907,'بيان صفة بيعة النساء، وبيعة من كان يأتي النبي صلى الله عليه وسلم بعد الفتح','6717','781','0');
INSERT INTO [t21618] VALUES (908,'مبتدأ كتاب الجهاد','6725','781','0');
INSERT INTO [t21618] VALUES (909,'بيان الخبر الموجب على كل مسلم أن ينفر إذا استنفر، ووجوب الجهاد مع النية وبيان إسقاط من لم يبلغ خمس عشرة سنة، والدليل على أن الإمام يجب عليه أن لا يأذن في الجهاد والخروج فيه من لم يبلغ','6726','781','0');
INSERT INTO [t21618] VALUES (910,'بيان الخبر الناهي عن إخراج الرجل بمصاحف القرآن مع نفسه إلى أرض العدو','6737','781','0');
INSERT INTO [t21618] VALUES (911,'بيان الخبر المبيح مسابقة الخيل المضمرة، وغير المضمرة إذا كان مبدؤها ومنتهاها معلومة','6743','781','0');
INSERT INTO [t21618] VALUES (912,'باب فضل الخيل على غيرها من الدواب، وما يكره من الخيل، والدليل على أن الجهاد لا ينقطع إلى يوم القيامة','6752','781','0');
INSERT INTO [t21618] VALUES (913,'بيان صفة ارتباط الخيل التي يؤجر عليها مرتبطها، وبيان ثوابها، وصفة ارتباط من يأثم عليها','6793','781','0');
INSERT INTO [t21618] VALUES (914,'بيان ثواب من يكلم في سبيل الله، والدليل على أن الإمام يحمل من لا يجد سعة','6796','781','0');
INSERT INTO [t21618] VALUES (915,'بيان ثواب الشهيد الذي يقتل في سبيل الله عز وجل','6820','781','0');
INSERT INTO [t21618] VALUES (916,'بيان ثواب المجاهد في سبيل الله، وأنه لا يعدله شيء من أعمال البر، وثواب الرجل يغدو في سبيل الله أو يروح','6841','781','0');
INSERT INTO [t21618] VALUES (917,'بيان ثواب من يضرب بسيفه في سبيل الله، ومن يقتل صابرا محتسبا، وأن ذلك يكون تكفير جميع خطاياه','6854','781','0');
INSERT INTO [t21618] VALUES (918,'بيان تفسير قول الله عز وجل: ولا تحسبن الذين قتلوا في سبيل الله، وثواب المجاهد في سبيل الله بنفسه وماله','6861','781','0');
INSERT INTO [t21618] VALUES (919,'بيان صفة وجوب الجنة للمقتول ولقاتله','6874','781','0');
INSERT INTO [t21618] VALUES (920,'بيان ثواب من يقتل كافرا','6880','781','0');
INSERT INTO [t21618] VALUES (921,'بيان مضاعفة نفقة المسلم في سبيل الله، وثواب ذلك المجاهد على ما يعطيه النفقة، ويحمله ليجاهد، والدليل على أن من يقوم مع من يجمع للسبيل فيسأل له أو يدله على من يعطيه أو يحمله أن له مثل أجر المعطي أو الحامل','6885','781','0');
INSERT INTO [t21618] VALUES (922,'بيان ثواب مجهز الغازي، ومتعاهد مخلفيه، وأنهما إذا فعلا ذلك فقد وقع عليهما اسم الغزاة، وبيان فضل المواسي مخلفي الغزاة والمواسي أصحابه','6893','781','0');
INSERT INTO [t21618] VALUES (923,'بيان السنة في بعث الإمام رعيته في الغزو إذا احتاج إليهم','6897','781','0');
INSERT INTO [t21618] VALUES (924,'بيان فضل نساء المجاهدين، وعظم وجوب حقهن على القاعدين','6902','781','0');
INSERT INTO [t21618] VALUES (925,'بيان فضل المجاهدين على القاعدين، والدليل على أن من خلفهم في أهلهم بخير لم يبلغوا درجتهم إلا من به ضر','6911','781','0');
INSERT INTO [t21618] VALUES (926,'بيان الخبر الدال على أن من أحب أن يكون ممن يقاتل في سبيل الله يجب عليه أن ينوي بقتاله للأعداء لتكون كلمة الله هي العليا، فإذا نوى ذلك كان منهم، وإن قاتل أيضا غضبا، أو حمية، أو ليذكر أو ليغنم','6915','781','0');
INSERT INTO [t21618] VALUES (927,'باب الخبر الدال على أن من قاتل للمغنم، أو لدنيا يصيبها، أو ليذكر، أو للرياء لم يكن له من قتاله إلا ما أراد، والخبر الموجب لمن قاتل ليقال شجاع خزي النار','6925','781','0');
INSERT INTO [t21618] VALUES (928,'بيان صفة الجهاد الذي به يتم أجر صاحبه، وأن من أصاب غنيمة في جهاده كان له الثلث من تمام الأجر','6929','781','0');
INSERT INTO [t21618] VALUES (929,'بيان وجوب الشهادة لمن يسألها بصدق نية، وتطلبها، وإبلاغه الله منازل الشهداء، وإن لم يستشهد ومات على فراشه','6931','781','0');
INSERT INTO [t21618] VALUES (930,'بيان عقاب من مات، ولم يغز في حياته، ولم يحدث نفسه بالغزو، وثواب من كانت نيته الغزو فصده عن ذلك أو إن كان له عذر','6935','781','0');
INSERT INTO [t21618] VALUES (931,'بيان فضل الغزو في البحر، وإن مات في وجهه ذلك إذا أو رجع','6940','781','0');
INSERT INTO [t21618] VALUES (932,'بيان فضل المرابط، وثوابه، والدليل على أنه إن رابط يوما، وليلة كان رباطا تماما، وأن المرابط إذا مات لم ينقطع عمله','6947','781','0');
INSERT INTO [t21618] VALUES (933,'بيان ثواب الفقير الذي يجاهد في سبيل الله سبحانه بلا راحلة','6954','781','0');
INSERT INTO [t21618] VALUES (934,'بيان الخبر الموجب الشهادة لمن مات في سبيل الله، وفي الطاعون، والبطن، والغرق، والهدم، والنفساء','6956','781','0');
INSERT INTO [t21618] VALUES (935,'بيان ثواب الحارس في سبيل الله','6965','781','0');
INSERT INTO [t21618] VALUES (936,'بيان ثواب من أنفق زوجين في سبيل الله عز وجل وصفتهما','6967','781','0');
INSERT INTO [t21618] VALUES (937,'باب بيان الترغيب في الرمي، وإيجابه على المسلم، والدليل على أنه من اللهو المباح، وبيان عقاب من تعلم الرمي ثم تركه','6974','781','0');
INSERT INTO [t21618] VALUES (938,'باب بيان إثبات الجهاد، وأنه ماض إلى يوم القيامة، وأنه لا يزال قوم من أمة محمد صلى الله عليه وسلم على الحق يذبون عن الدين، ويقاتلون عنه، وينصرون على من خالفهم إلى يوم القيامة، والدليل على أنه لا يظهر عليهم أحد من أهل الأديان','6979','781','0');
INSERT INTO [t21618] VALUES (939,'باب بيان الخبر الدال على أن أهل الحجاز لا يزالون على الحق حتى تقوم الساعة، وأن قريشا، وأهل المغرب يكونون ظاهرين على أهل المشرق والعجم','6990','781','0');
INSERT INTO [t21618] VALUES (940,'باب بيان إباحة سرعة السير في اليبوسة، والسنة، وفي الجدبة، ووجوب سرعة الرجوع إلى الأهل في مثل هذه السنة، ووجوب المهل في السير في الخصب، وإعطاء الإبل حظها من نبات الأرض، وحظر التعريس على الطرق، والعلة التي لها نهي عنه','6994','781','0');
INSERT INTO [t21618] VALUES (941,'باب بيان السنة في دخول الرجل على أهله إذا قدم من غزوة، والعلة التي لها نهي الرجل أن يطرق أهله ليلا، وإباحة الرجوع إلى منزله من سفره بكرة أو عشيا، والدليل على أنه لا يفاجئ الأهل حتى يعلموا','7000','781','0');
INSERT INTO [t21618] VALUES (942,'مبتدأ كتاب الصيد','7028','781','0');
INSERT INTO [t21618] VALUES (943,'باب إباحة صيد الكلب المعلم إذا ذكر صاحبه عليه اسم الله وإن قتل، وحظر أكله إذا شرك فيه كلب آخر، أو كان الصائد كلب غير معلم، أو لم يذكر اسم الله عليه، وإباحة أكل الصيد الذي يصاب بالمعراض الذي يخزق، ويصيب عنده وحظر أكله إذا صيد بعرضه','7029','781','0');
INSERT INTO [t21618] VALUES (944,'بيان إباحة أكل صيد الكلب غير المعلم، إذا أدرك صاحبه ذكاته، وأكل ما أصيب بالسهم، وقتله إذا ذكر اسم الله عليه فذكره','7048','781','0');
INSERT INTO [t21618] VALUES (945,'بيان إباحة أكل صيد، إذا غاب عن صاحبه، ولم يدركه، إلا بعد ثلاث، وحظر أكلها إذا أنتن، فذكره','7057','781','0');
INSERT INTO [t21618] VALUES (946,'بيان تحريم أكل الصيد، من السباع الذي له ناب، والدليل على إباحة لحوم السباع، الذي ليس له ناب، وعلى أن غير السباع، إذا كان له ناب جائز أكله','7059','781','0');
INSERT INTO [t21618] VALUES (947,'بيان حظر أكل كل ذي مخلب، من الطير، وإباحة أكل كل طير ليس له مخلب','7072','781','0');
INSERT INTO [t21618] VALUES (948,'بيان إباحة صيد دواب البحر، وإباحة أكل ما يقذف البحر من دوابه الميتة','7077','781','0');
INSERT INTO [t21618] VALUES (949,'بيان إباحة صيد الحمر الوحش، وأكل لحمها، ولحم الخيل، والفرس، وتحريم أكل الحمر الأهلية، وبيان العلة، التي لها نهى','7090','781','0');
INSERT INTO [t21618] VALUES (950,'بيان الخبر الموجب، إكفاء القدور، بما فيه من لحوم الحمر إذا طبخ فيه، وكسره، أو غسله، وتحريم لحمه نيه، ونضيجه، وأن النبي صلى الله عليه وسلم نهى عنه، بنهي الله عز وجل، وأنه رجس، والدليل على أن ما أصابه منه نجس','7129','781','0');
INSERT INTO [t21618] VALUES (951,'بيان الخبر الدال على إباحة صيد الضب، وأكل لحمه، والترغيب في ترك أكله','7141','781','0');
INSERT INTO [t21618] VALUES (952,'بيان الخبر المحل، أكل لحم الضب','7151','781','0');
INSERT INTO [t21618] VALUES (953,'بيان الأخبار الدالة، على تحريم أكل ما مسخ، من الصيد وغيره، وإثبات المسخ، وأنه لا يكون للمسخ نسل، والترغيب في ترك، أكل ما يشتبه عليه','7162','781','0');
INSERT INTO [t21618] VALUES (954,'بيان إباحة صيد الأرنب، وأكل لحمه','7170','781','0');
INSERT INTO [t21618] VALUES (955,'بيان إباحة صيد الجراد، وأكله، والدليل على أن صيد الخاذف، وصيد الرامي بالبندقة، والحجر لا يؤكل، إذا لم يدرك ذكاته','7175','781','0');
INSERT INTO [t21618] VALUES (956,'مبتدأ كتاب الذبائح','7188','781','0');
INSERT INTO [t21618] VALUES (957,'بيان صفة السنة في الذبح، والتسمية، والتكبير عنده، ووجوب قطع الحلقوم والودجين، فما يذبح يضجع، وما ينحر يقام، ومنع ذبيحة المشرك، والمجنون، ومن لم يبلغ من الصبيان، والسكران','7189','781','0');
INSERT INTO [t21618] VALUES (958,'باب النهي عن أن تصبر البهائم، واتخاذ شيء مما فيه الروح غرضا، وعقاب من فعل ذلك، والدليل على تحريم أكل لحومها','7201','781','0');
INSERT INTO [t21618] VALUES (959,'بيان ما يجوز الذبح، وما لا يجوز أن يذكى به، والدليل على إباحة أكل كل ذبيحة، كيف ما ذبحت، إذا ذبحت بما يجوز به، الذبح، وأنهر الدم','7214','781','0');
INSERT INTO [t21618] VALUES (960,'مبتدأ كتاب الأضاحي','7224','781','0');
INSERT INTO [t21618] VALUES (961,'من ذلك وجوب من أراد أن يضحي الإمساك، عن أخذ الشعر، والظفر، والنورة، في أيام العشر','7225','781','0');
INSERT INTO [t21618] VALUES (962,'بيان السنة في اختيار الكبش، في الأضحية، وصفته وصفة ذبحه، والترغيب في ذبحه بيده، وما يجب أن يقول صاحبه عند ذبحه','7236','781','0');
INSERT INTO [t21618] VALUES (963,'بيان السنة للإمام، توزيع الضحايا بين أصحابه، وتوجيهها إليهم، والإباحة أن يضحي بالجذع، والعتود، وهو الذكر من المعز، وأن الذبح، والنحر للإمام، أيهما فعل جائز','7249','781','0');
INSERT INTO [t21618] VALUES (964,'بيان النهي عن أن يضحي بالجذع من المعز والعناق منه، والدليل على أن الإباحة أن يضحي بها منسوخ، وإباحة الأكل يوم النحر قبل الصلاة، وأن الإمام لا يذبح بالمصلى ويرجع فيذبح','7257','781','0');
INSERT INTO [t21618] VALUES (965,'بيان الأخبار الناهية، عن أن يضحي، قبل الصلاة يوم النحر، وأن من ذبح قبل الصلاة، وقبل ذبح الإمام يجب عليه، أن يذبح مكانها أخرى مثلها بعد الصلاة، ووجوب التسمية عند ذبحها، والإباحة للأب أن يضحي عن ابنه، وأن له أكله، ويطعم أهله إذا ذبح قبل الصلاة،','7271','781','0');
INSERT INTO [t21618] VALUES (966,'باب بيان وجوب الأضحية، بالمسنة، وإجازتها بالجذع من الضأن','7285','781','0');
INSERT INTO [t21618] VALUES (967,'بيان وجوب اللعنة، على من نسك لغير الله، والدليل على أن من ضحى، يريد به رياء، وسمعة، ولا يريد بشيء منه وجه الله، أن اللعنة تحل به، وكذلك كل ذبيحة تذبح لغير الله تعالى','7287','781','0');
INSERT INTO [t21618] VALUES (968,'بيان الأخبار الناهية عن ادخار، لحوم الأضاحي، وأكلها فوق ثلاثة أيام','7293','781','0');
INSERT INTO [t21618] VALUES (969,'بيان الأخبار المبيحة، ادخار لحوم الأضاحي فوق ثلاث، ونهي الأكل منها بعد ثلاث منسوخ، والعلة التي كان لها، نهى عنه النبي صلى الله عليه وسلم','7305','781','0');
INSERT INTO [t21618] VALUES (970,'بيان إبطال الفرع، والعتيرة وهما ذبيحتان كانتا لأهل الجاهلية، أما العتيرة، فكانوا يضحون في شهر رجب عن كل أهل بيت شاة، والفرع هو أول النتاج ينتجونه من مواشيهم يضحونه لآلهتهم','7328','781','0');
INSERT INTO [t21618] VALUES (971,'بيان الخبر المجيز البعير، بعشرة من الغنم، وصفة نحرها، والدليل على إجازة شركة العشرة، في البعير الواحد للأضحية','7335','781','0');
INSERT INTO [t21618] VALUES (972,'بيان الخبر الدال، على إجازة شركة السبعة، في البقرة للأضحية','7340','781','0');
INSERT INTO [t21618] VALUES (973,'مبتدأ كتاب تحريم الخمر، وتحريم المسكر، وما جاء فيها','7343','781','0');
INSERT INTO [t21618] VALUES (974,'باب الخبر الدال على أن الخمر، وشربها في الابتداء كان مباحا، وصفة تحريمها، وأن خمورهم يوم حرمت كانت تمرا، وبسرا، وهي الفضيخ','7344','781','0');
INSERT INTO [t21618] VALUES (975,'بيان الخبر المبين أن الخمر هي من التمر، والعنب','7359','781','0');
INSERT INTO [t21618] VALUES (976,'باب تحريم الشراب من العسل المسمى البتع إذا أسكر، وكذلك من الشعير، والذرة، والمسمى، المزر، والشراب من البر','7370','781','0');
INSERT INTO [t21618] VALUES (977,'باب ذكر الخبر الدال على أن السكر من شراب العسل، والشعير هو الذي يسكر، عن الصلاة، وأنه حرام','7384','781','0');
INSERT INTO [t21618] VALUES (978,'بيان عقاب من يشرب المسكر، من أي شراب كان قليلا كان، أو كثيرا، وأن كل مسكر خمر، وكل مسكر حرام، وعقاب من يدمن شرب الخمر، والدليل على أن من تاب منها، قبل موته قبلت توبته','7388','781','0');
INSERT INTO [t21618] VALUES (979,'بيان النهي عن اتخاذ الخمر، خلا','7406','781','0');
INSERT INTO [t21618] VALUES (980,'بيان النهي عن اتخاذ النبيذ، من البسر، والرطب إذا جمعا، أو خلطا، وكذلك من الزبيب، والتمر، وغيره، إذا جمعا، وكذلك من البسر، والتمر','7419','781','0');
INSERT INTO [t21618] VALUES (981,'بيان الأوعية المنهية، عن الانتباذ فيها، والأوعية التي يجوز الانتباذ فيها، ووجوب وكاء السقاء الذي ينبذ فيه','7453','781','0');
INSERT INTO [t21618] VALUES (982,'بيان صفة الأوعية، التي كانت ينبذ لرسول الله صلى الله عليه وسلم فيها، وصفة شربه، والمدة التي كان يشربه فيها، ثم يمسك عنها، والدليل على إباحة شربه، في الوقت الذي كان يمسك، عن شربه، وحظر شربه يوم الرابع','7528','781','0');
INSERT INTO [t21618] VALUES (983,'مبتدأ كتاب الأشربة، من ذلك إباحة شرب النبيذ في جماعة ليوم، وليلة، وإباحة شرب الماء، وغيره في القدح، والسنة فيه، وإباحة السؤال بشرب الماء واللبن، والدليل على أن أفضل الشراب اللبن','7540','781','0');
INSERT INTO [t21618] VALUES (984,'بيان الأخبار الموجبة تغطية الإناء، وإيكاء السقاء، وإطفاء المصابيح عند النوم، وغلق الأبواب، وإمساك الصبيان عند المساء، والدليل على أن تغطية الإناء، وإيكاء السقاء، بالليل أوجب منه بالنهار','7554','781','0');
INSERT INTO [t21618] VALUES (985,'بيان حظر شرب الرجل بشماله، ووجوب شربه بيمينه','7581','781','0');
INSERT INTO [t21618] VALUES (986,'باب النهي عن اختناث الأسقية، والشرب من أفواهها','7586','781','0');
INSERT INTO [t21618] VALUES (987,'باب النهي، عن الشرب قائما، ووجوب النهي على من يشرب قائما','7590','781','0');
INSERT INTO [t21618] VALUES (988,'بيان الخبر المبيح الشرب قائما','7602','781','0');
INSERT INTO [t21618] VALUES (989,'بيان النهي، عن التنفس، في الإناء، والخبر المبيح التنفس فيه، والترغيب للشارب، أن يتنفس في شربه ثلاثا','7610','781','0');
INSERT INTO [t21618] VALUES (990,'بيان وجوب دفع الشارب فضل شرابه إلى من عن يمينه، وإن كان من عن يساره أكبر منه، وأفضل','7625','781','0');
INSERT INTO [t21618] VALUES (991,'مبتدأ كتاب الأطعمة','7638','781','0');
INSERT INTO [t21618] VALUES (992,'من ذلك وجوب التسمية عند حضور الطعام، وحضور الشيطان، إذا تركت التسمية، وبيان السنة في القوم إذا حضروا الطعام، مع إمام أن لا يبدءوا بالأكل، قبل الإمام، وثواب الطاعم الشاكر','7639','781','0');
INSERT INTO [t21618] VALUES (993,'بيان النهي، عن الأكل، بالشمال، وحظره والتشديد فيه، ووجوب الأكل باليمين','7645','781','0');
INSERT INTO [t21618] VALUES (994,'باب الخبر الموجب أكل، الذي يأكل مما يليه، والدليل على أن الطعام إذا اختلف لونه، لم يجاوز ما يليه، ووجوب التسمية عنده','7653','781','0');
INSERT INTO [t21618] VALUES (995,'بيان الخبر الموجب لعق الأصابع، إذا أراد مسح يده','7659','781','0');
INSERT INTO [t21618] VALUES (996,'بيان السنة في الأكل بثلاث أصابع','7665','781','0');
INSERT INTO [t21618] VALUES (997,'باب الخبر الموجب لعق القصاع، والنهي، عن رفعها، حتى يلعق الأصابع','7670','781','0');
INSERT INTO [t21618] VALUES (998,'بيان الخبر المبيح، اتخاذ المناديل لمسح اليد من الطعام، والنهي، عن المسح به، حتى يلعق الآكل، أو يمص أصابعه','7677','781','0');
INSERT INTO [t21618] VALUES (999,'بيان الخبر الموجب، أخذ اللقمة إذا سقطت من يد آكلها، وإماطة الأذى عنها، وأكلها، والدليل على أنها إن تركت كانت للشيطان، وأن الشيطان حضر الإنسان في شأنه كله','7687','781','0');
INSERT INTO [t21618] VALUES (1000,'باب إباحة تطيب المرقة، وترك إجابة الدعوة، إذا كان المدعو عنده آخر لم يدع، والإباحة للمدعو استتباع جليسه، أو ضيفه إلى طعام الداعي بعد أن يأذن له','7692','781','0');
INSERT INTO [t21618] VALUES (1001,'باب إباحة أن يتبع قوما، وقد دعوا إلى طعام، والتعرض لصاحب الطعام، وحظر الدخول معهم، حتى يأذن له صاحبه','7696','781','0');
INSERT INTO [t21618] VALUES (1002,'باب الإباحة لمن أصابته حاجة، أو جوع، أن يأتي من يثق به، وبدينه متعرضا ليطعمه، أو يقضي حاجته، وإباحة التكلف للضيف','7705','781','0');
INSERT INTO [t21618] VALUES (1003,'باب اتخاذ الطعام للأضياف، يسمى سورا، وأن اتخاذها بعد ما يدعون، وأن أهل البيت يأكلون بعد الأضياف','7710','781','0');
INSERT INTO [t21618] VALUES (1004,'بيان صفة اتخاذ الخطيفة، وأن النبي صلى الله عليه وسلم، عصب بطنه من الجوع، وإباحة إلقاء الطعام على الحصير، وتقليب الوعاء لإخراج ما فيه، ووجوب توجيه فضل الطعام إلى الجيران','7716','781','0');
INSERT INTO [t21618] VALUES (1005,'بيان إباحة استتباع الرجل خادمه، إذا دعي إلى طعام، والإباحة للرجل إذا أكل معه غيره، أن يتتبع حول الصحفة، فيلتقط منها أحب ذلك إليه، والسنة في أكل الدباء','7723','781','0');
INSERT INTO [t21618] VALUES (1006,'بيان صفة إلقاء النوى إذا أكل التمر، ودعاء الضيف لمن يأكل عنده، والدليل على إباحة ترك الدعاء له، إلا أن يسأله صاحب الطعام، أن يدعو له، فيدعو عند خروجه','7731','781','0');
INSERT INTO [t21618] VALUES (1007,'باب في مناقب التمر، والدليل على أن التمر طعام، وأنه يجزئ وحده من الأطعمة','7740','781','0');
INSERT INTO [t21618] VALUES (1008,'بيان فضل التمور، التي تكون بين لابتي المدينة على غيرها، وأن من تصبح منها بسبع تمرات لم يضره سم','7744','781','0');
INSERT INTO [t21618] VALUES (1009,'بيان فضل تمر العجوة، وأنه حرز من السم، والسحر','7747','781','0');
INSERT INTO [t21618] VALUES (1010,'بيان فضل تمر عجوة العالية، وأنها شفاء لمن بكر بأكلها، وأن السنة في أن يحنك المولود أول ما يولد، بالتمر','7750','781','0');
INSERT INTO [t21618] VALUES (1011,'باب بيان فضيلة الكمأة، والترغيب في الاستشفاء بمائها','7754','781','0');
INSERT INTO [t21618] VALUES (1012,'بيان فضيلة الخل، والترغيب في الائتدام به، والسنة في التسوية في وضع الخبز بين يدي كل ضيف','7763','781','0');
INSERT INTO [t21618] VALUES (1013,'بيان إباحة استعمال المرقة الثومية، والأكل منها، والترغيب في تركها','7787','781','0');
INSERT INTO [t21618] VALUES (1014,'باب اجتناء الكباث، ووجوب تخيير الأسود منه','7794','781','0');
INSERT INTO [t21618] VALUES (1015,'بيان فضيلة إيثار الرجل ضيفه في الطعام على نفسه، وولده، وثوابه، والترغيب فيه، والسنة للإمام في التسوية بين أصحابه في الطعام، وحبس نصيب الغائب منهم، إلى أن يوافي، والسنة في إبرار قسم الأضياف، إذا حلفوا أن يطعم معهم، صاحب المنزل بعد أن يحلف أن لا','7797','781','0');
INSERT INTO [t21618] VALUES (1016,'بيان طعام يتخذ لواحد أنه يكفي الاثنين، والدليل على أن البركة في الاجتماع على الطعام','7806','781','0');
INSERT INTO [t21618] VALUES (1017,'بيان كراهية كثرة الأكل، والترغيب في قلة الأكل، والطعن على الرجل الأكول','7812','781','0');
INSERT INTO [t21618] VALUES (1018,'بيان كراهية عيب الطعام، إذا قدم إلى الرجل','7832','781','0');
INSERT INTO [t21618] VALUES (1019,'بيان الصحاف التي يكره الأكل فيها، والأواني التي يكره الشرب فيها','7841','781','0');
INSERT INTO [t21618] VALUES (1020,'مبتدأ كتاب اللباس','7853','781','0');
INSERT INTO [t21618] VALUES (1021,'باب الخبر الناهي عن اتخاذ المياثر، والقسي، وآنية الفضة، والذهب، ولبس الحرير، والاستبرق، والديباج، وخاتم الذهب، والحلل، وإباحة بيعها، وشرائها، ولبسها النساء، وبيان الخبر الدال على الكراهية للنساء لبسها','7854','781','0');
INSERT INTO [t21618] VALUES (1022,'بيان إباحة لبس الثوب، الذي فيه العلم من الحرير، والثوب المكفوف بالديباج','7889','781','0');
INSERT INTO [t21618] VALUES (1023,'بيان إباحة الحرير للرجال، إذا كانت بهم علة، أو حكة','7902','781','0');
INSERT INTO [t21618] VALUES (1024,'بيان النهي، عن لبس الرجل الثوب المعصفر، والتشديد فيه','7908','781','0');
INSERT INTO [t21618] VALUES (1025,'بيان الترغيب في لبس ثياب الحبر، وأنها كانت أحب الثياب، إلى النبي صلى الله عليه وسلم، وبيان الأمتعة، التي كان يلبسها، ويتوسدها، ويفترشها، والسنة في التقنع بالإزار','7920','781','0');
INSERT INTO [t21618] VALUES (1026,'بيان إباحة الأنماط، والدليل على أنها مباحة، وإن كثرت','7933','781','0');
INSERT INTO [t21618] VALUES (1027,'بيان إباحة مبلغ اتخاذ الفرش، وكراهية زيادة اتخاذها، فوق ذلك','7936','781','0');
INSERT INTO [t21618] VALUES (1028,'بيان التشديد في اغترار المرء بلباسه، وتبختره فيه، وعقوبته، إذا اختال فيه','7939','781','0');
INSERT INTO [t21618] VALUES (1029,'بيان الأخبار الناهية عن جر الرجل، إزاره بطرا، وخيلاء، والتشديد فيه، والدليل على أن من لم يرد به خيلاء، لم تكن عليه تلك الشدة','7951','781','0');
INSERT INTO [t21618] VALUES (1030,'بيان الخبر الموجب، رفع الرجل إزاره إلى أنصاف الساقين، والتشديد على من يجعل دون الكعبين','7976','781','0');
INSERT INTO [t21618] VALUES (1031,'بيان الخبر الناهي، عن خاتم الذهب، والتشديد فيه','7981','781','0');
INSERT INTO [t21618] VALUES (1032,'بيان اتخاذ النبي صلى الله عليه وسلم، خاتم الذهب، وتختمه به في الابتداء، وطرحه بعد لبسه، وحظره على نفسه لبسه، وكان يجعل فصه، في قبل كفه','7984','781','0');
INSERT INTO [t21618] VALUES (1033,'باب اتخاذ النبي صلى الله عليه وسلم، خاتما من ورق، وطرحه لما رأى الناس لبسوا، والدليل على إباحة الخاتم للإمام، وكراهية اتخاذه لرعيته','7993','781','0');
INSERT INTO [t21618] VALUES (1034,'بيان العلة التي اتخذ لها النبي صلى الله عليه وسلم الخاتم، وصفة فصه، ولبسه في إصبعه','7998','781','0');
INSERT INTO [t21618] VALUES (1035,'بيان الأخبار المبينة، أن النبي صلى الله عليه وسلم، لبس خاتمه في يمينه، فصه في باطن كفه، والخبر الدال على أن لبسه في يمينه منسوخ','8008','781','0');
INSERT INTO [t21618] VALUES (1036,'بيان إثبات تختم النبي صلى الله عليه وسلم، في الخنصر من يده اليسرى','8014','781','0');
INSERT INTO [t21618] VALUES (1037,'بيان النهي عن التختم في الإصبع الوسطى والتي تليها، والتختم في طرف الأصابع','8017','781','0');
INSERT INTO [t21618] VALUES (1038,'بيان نقش خاتم النبي صلى الله عليه وسلم، واتخاذه من الفضة، وإمساكه حياته، وكره أن ينقش على نقشه','8027','781','0');
INSERT INTO [t21618] VALUES (1039,'بيان الترغيب في اتخاذ النعل، والانتعال به، والعلة التي لها رغب فيها','8034','781','0');
INSERT INTO [t21618] VALUES (1040,'بيان وجوب الابتداء في لبس النعل، بالرجل اليمنى، وفي خلعها باليسرى، وحظر المشي في النعل الواحدة','8037','781','0');
INSERT INTO [t21618] VALUES (1041,'بيان حظر المشي في خف واحد، والاحتباء بالثوب الواحد، إذا كشف العورة، واشتمال الصماء، ووضع إحدى الرجلين على الأخرى، إذا استلقى','8053','781','0');
INSERT INTO [t21618] VALUES (1042,'بيان الخبر المبيح استلقاء الرجل، ووضع إحدى رجليه على الأخرى','8064','781','0');
INSERT INTO [t21618] VALUES (1043,'بيان النهي، عن التزعفر، والأمر بخضاب اللحية، وصبغها، وحظر الخضاب بالسواد','8072','781','0');
