INSERT INTO [t21741] VALUES (47,'شرح قوله: حدثنا يحيى بن بكير حدثنا الليث عن عقيل عن ابن شهاب عن سالم عن عبد الله بن عمر أن رسول الله -صلى الله عليه وسلم- قال:','191','46','0');
INSERT INTO [t21741] VALUES (48,'شرح قوله: حدثنا عبدان أخبرني أبي عن شعبة عن عبد الملك عن ربعي عن حذيفة عن النبي -صلى الله عليه وسلم- قال في الدجال:','193','46','0');
INSERT INTO [t21741] VALUES (49,'قال أبو مسعود: أنا سمعته من رسول الله -صلى الله عليه وسلم-.','193','46','0');
INSERT INTO [t21741] VALUES (50,'شرح قوله: حدثنا سليمان بن حرب حدثنا شعبة عن قتادة عن أنس -رضي الله عنه- قال: قال النبي -صلى الله عليه وسلم-:','194','46','0');
INSERT INTO [t21741] VALUES (51,'فيه أبو هريرة وابن عباس عن النبي -صلى الله عليه وسلم-.','194','46','0');
