INSERT INTO [t21751] VALUES (2849,'مسألة: يقول لامرأته أنت طالق البتة إن لم تدفعي إلي مائة دينار','2655','2820','0');
INSERT INTO [t21751] VALUES (2850,'مسألة: يطلق امرأته واحدة ثم قيل له ارتجع امرأتك فقال إن ارتجعتها فهي طالق','2656','2820','0');
INSERT INTO [t21751] VALUES (2851,': قال لامرأته أنت طالق إن لم أضربك حتى أشتفي عليك فضربها حتى اشتفى','2657','2820','0');
INSERT INTO [t21751] VALUES (2852,'مسألة: حلف بطلاقها ألا يأخذ شيئا من مالها إلا بإذنها فأذنت ثم رجعت في الإذن','2658','2820','0');
INSERT INTO [t21751] VALUES (2853,'مسألة: قال إن مات ابني وليست لك قبلي تباعة من هذا الدين فأنت طالق','2659','2820','0');
INSERT INTO [t21751] VALUES (2854,'مسألة: عبد حلف لغريم له بطلاق امرأته أن يدفع إليه أول دينار يدخل عليه فباعه سيده','2660','2820','0');
INSERT INTO [t21751] VALUES (2855,'مسألة: الرجل يكون تحته امرأتان فيقول لكل واحدة منهما إذا طلقتك ففلانة طالق','2660','2820','0');
INSERT INTO [t21751] VALUES (2856,'مسألة: قال لامرأته انتقلي معي فأبت فقال أنت طالق إن لم تنتقلي معي ثم بدا له ألا ينتقل','2661','2820','0');
INSERT INTO [t21751] VALUES (2857,'مسألة: قال للتي قد دخل بها شأنك بأهلك أو شأنكم بها فهي البتة','2661','2820','0');
INSERT INTO [t21751] VALUES (2858,'مسألة: يقول لامرأته أنت طالق إن شئت فقالت قد شئت إن شاء فلان','2662','2820','0');
INSERT INTO [t21751] VALUES (2859,'مسألة: يحلف على الشيء بطلاق امرأته ولا يدري أعلى حق حلف أم على باطل','2663','2820','0');
INSERT INTO [t21751] VALUES (2860,'مسألة: الرجل يكون تحته امرأتان فيقول لإحداهما إن لم أتزوج عليك فأنت طالق','2665','2820','0');
INSERT INTO [t21751] VALUES (2861,'مسألة: يحلف ألا يساكن رجلا هل يأتيه زائرا فيقيم عنده الأيام والليالي','2665','2820','0');
