INSERT INTO [t21751] VALUES (2864,'مسألة: حلف لامرأته بطلاق كل امرأة يتزوجها عليه البتة','2668','2820','0');
INSERT INTO [t21751] VALUES (2865,'مسألة: حلف بالطلاق إن فلانا يتعرض لجارية فلان','2669','2820','0');
INSERT INTO [t21751] VALUES (2866,'مسألة: قال أنت طالق إن شاء الله','2669','2820','0');
INSERT INTO [t21751] VALUES (2867,'مسألة: استسلف منها دينارا فقالت أدفعه لفلان يدفعه لك ويستحلفك بالطلاق','2671','2820','0');
INSERT INTO [t21751] VALUES (2868,'مسألة: حلف ألا يفعل فعلا فأكره عليه','2672','2820','0');
INSERT INTO [t21751] VALUES (2869,'مسألة: دخلت عليه امرأته فقال هي طالق البتة إن لم أفترعها الليلة','2673','2820','0');
INSERT INTO [t21751] VALUES (2870,'مسألة: يقول لامرأته وجهي من وجهك حرام','2674','2820','0');
INSERT INTO [t21751] VALUES (2871,': أتاه غريمه فسأله أن يقضيه فقال امرأته طالق إن قضاه اليوم','2674','2820','0');
INSERT INTO [t21751] VALUES (2872,'مسألة: يقول قد طلقت امرأتي أو يتكلم بالطلاق وهو لا يريده','2675','2820','0');
INSERT INTO [t21751] VALUES (2873,'مسألة: افتقد بضاعة فاتهم امرأته فأنكرت فقال أنت طالق إن كان أخذها غيرك','2675','2820','0');
INSERT INTO [t21751] VALUES (2874,'مسألة: غراب طائر فقال امرأته طالق البتة إن لم يكن هذا الغراب ذكرا','2676','2820','0');
INSERT INTO [t21751] VALUES (2875,'مسألة: قال امرأته طالق إن لم يتزوج امرأة يمسكها سنة','2677','2820','0');
INSERT INTO [t21751] VALUES (2876,'مسألة: قال لامرأته أنت طالق إن لم أتزوج عليك إلى سنة فتزوج قبل تمامها','2677','2820','0');
INSERT INTO [t21751] VALUES (2877,'مسألة: رجل كان له ربيب يأتي إلى بيته بخبز فقال له رابه أمك طالق إن أنت جئت إلي بيتي بخبز','2678','2820','0');
INSERT INTO [t21751] VALUES (2878,'مسألة: حلف سيد الجارية بطلاق امرأته البتة إن لم يبعها إياه بمائة دينار قائمة','2679','2820','0');
INSERT INTO [t21751] VALUES (2879,'مسألة: قال لغريمه امرأته طالق إن لم يوفه حقه في شعبان ورمضان','2681','2820','0');
INSERT INTO [t21751] VALUES (2880,'مسألة: قال لامرأته أنت طالق اليوم إن دخل فلان غدا الحمام','2682','2820','0');
INSERT INTO [t21751] VALUES (2881,'مسألة: يقول إن أكلت من طعام أخي فامرأته طالق فيصحبه في سفر فيشتريان طعاما','2682','2820','0');
INSERT INTO [t21751] VALUES (2882,'مسألة: حلف بطلاق امرأته ألا يستخدم عبد فلان وعتق العبد فاستخدمه','2683','2820','0');
INSERT INTO [t21751] VALUES (2883,'مسألة: حلف أن يطأ امرأته فوطئها وهي حائض','2684','2820','0');
INSERT INTO [t21751] VALUES (2884,'حلف لرجل فقال امرأته طالق البتة إن لم أقضك حقك غدا يوم الجمعة وهو يظن أنه الجمعة','2684','2820','0');
INSERT INTO [t21751] VALUES (2885,'مسألة: حلف على قريب له ألا يعيره ثوبا أبدا فوهبه له','2685','2820','0');
INSERT INTO [t21751] VALUES (2886,'مسألة: قال لامرأته أنت طالق إن قبلتني فتستغفله وتقبله','2686','2820','0');
INSERT INTO [t21751] VALUES (2887,'مسألة: حلف بالله أو بالطلاق أو غيره أن يصوم غدا فيصبح صائما ثم يأكل شيئا','2687','2820','0');
INSERT INTO [t21751] VALUES (2888,'مسألة: حلف بطلاق كل امرأة يتزوجها إلا كفؤا','2688','2820','0');
INSERT INTO [t21751] VALUES (2889,'مسألة: يقول لامرأته إذا قدم أبي فأنت طالق','2689','2820','0');
INSERT INTO [t21751] VALUES (2890,'مسألة: قال لامرأته إذا قدمت بلد كذا فأنت طالق','2689','2820','0');
INSERT INTO [t21751] VALUES (2891,'مسألة: طلق إلى أجل يعلم أنه لا يبلغه عمر أحد','2690','2820','0');
INSERT INTO [t21751] VALUES (2892,'مسألة: يقول كل امرأة أتزوجها بالمدينة طالق','2691','2820','0');
INSERT INTO [t21751] VALUES (2893,'مسألة: قال كل امرأة أتزوجها اثني عشر شهرا فهي طالق','2691','2820','0');
INSERT INTO [t21751] VALUES (2894,'مسألة: يقول لامرأته أنت طالق بعد أن أموت أو تموت','2692','2820','0');
