INSERT INTO [t21751] VALUES (2896,'مسألة: قال لامرأته إن تغيبت عنك فأنت طالق','2693','2820','0');
INSERT INTO [t21751] VALUES (2897,'مسألة: قال إذا وطئتك أو قال إن وطئتك كذا وكذا وطئة فأنت طالق البتة','2693','2820','0');
INSERT INTO [t21751] VALUES (2898,'مسألة: قال لامرأته أنت طالق البتة إن خرجت إلى دار فلان إلا بإذني','2694','2820','0');
INSERT INTO [t21751] VALUES (2899,'مسألة: قال يميني في يمينك ولم يبين شيئا حين قال ذلك له فحلف فأنكر ذلك','2696','2820','0');
INSERT INTO [t21751] VALUES (2900,'مسألة: قال لامرأته إن ولدت غلاما فلك مائة دينار وإن ولدت جارية فأنت طالق','2697','2820','0');
INSERT INTO [t21751] VALUES (2901,'مسألة: حلف ألا يكلم رجلا فمر به وهو نائم فقال أيها النائم الصلاة فإذا هو المحلوف عليه','2697','2820','0');
INSERT INTO [t21751] VALUES (2902,'مسألة: يقول لامرأته أنت طالق البتة أنت طالق البتة أنت طالق البتة إن أذنت لك إلى موضع كذا','2698','2820','0');
INSERT INTO [t21751] VALUES (2903,'مسألة: قال لامرأته إن ولدت غلاما إن لم أحج بك فأنت طالق فولدت غلاما فأبت الحج','2699','2820','0');
INSERT INTO [t21751] VALUES (2904,'مسألة: حلف بطلاق امرأته البتة إن كلم فلانا إلا ألا يعرفه، فكلمه وهو يعرفه ناسيا ليمينه','2699','2820','0');
INSERT INTO [t21751] VALUES (2905,'مسألة: يقول لامرأته إن خرجت من هذه الدار إلى رأس الحول لدار يسكنها فأنت طالق','2700','2820','0');
INSERT INTO [t21751] VALUES (2906,'مسألة: قال امرأته طالق إن دفعت إليكم منهما إلا حقكم','2701','2820','0');
INSERT INTO [t21751] VALUES (2907,'مسألة: كان مع ختنته في سفر فشجر بينهما شيء فحلف بطلاق امرأته ثلاثا ألا يصحبها','2701','2820','0');
INSERT INTO [t21751] VALUES (2908,'مسألة: قال لامرأته إن فعلت كذا وكذا إلا أن يقدر فأنت طالق','2702','2820','0');
