INSERT INTO [t21751] VALUES (2911,'مسألة: قال لامرأته إن تزوجت عليك فالتي أتزوج عليك طالق البتة','2704','2820','0');
INSERT INTO [t21751] VALUES (2912,'مسألة: سأل امرأته سلفا فأبت فقال امرأته طالق البتة إن أخذت منها درهما','2705','2820','0');
INSERT INTO [t21751] VALUES (2913,'مسألة: شجر بين الرجل وبين بائع الخادم أمر فحلف بطلاق امرأته أن يخرجها من بيته','2706','2820','0');
INSERT INTO [t21751] VALUES (2914,'مسألة: يبيع العبد من الرجل إلى أجل ويتخذ عليه يمينا أن يوفيه الثمن إلى أجل فيوجد بالعبد عيب','2707','2820','0');
INSERT INTO [t21751] VALUES (2915,'مسألة: سلف مائة دينار في قمح إلى أجل واتخذ عليه بالطلاق أن يوفيه إلى أجل فاستقاله','2707','2820','0');
INSERT INTO [t21751] VALUES (2916,'مسألة: حلف بطلاق امرأته ألا يتزوج عليها فيتزوج امرأة ثم يطلقها','2708','2820','0');
INSERT INTO [t21751] VALUES (2917,'مسألة: يتخذ على غريمه يمينا بطلاق امرأته البتة ليقضينه حقه رأس الهلال أو يرهنه داره','2709','2820','0');
INSERT INTO [t21751] VALUES (2918,'كتاب الأيمان بالطلاق الثالث','2710','0','0');
INSERT INTO [t21751] VALUES (2919,'شجر بينه وبين أختانه أمر فقالوا طلق أختنا فقال إن ارتحلت عني اليوم فهي طالق','2710','2918','0');
INSERT INTO [t21751] VALUES (2920,'مسألة: قال إن ارتحلت عني امرأتي فهي طالق','2711','2918','0');
INSERT INTO [t21751] VALUES (2921,'مسألة: قال امرأته طالق إن كان شرب مسكرا','2712','2918','0');
INSERT INTO [t21751] VALUES (2922,'مسألة: حلف بطلاق امرأته البتة إن كان بغاه عند رجل','2713','2918','0');
INSERT INTO [t21751] VALUES (2923,'مسألة: حلف غريما له بطلاق امرأته أن يقضيه إلى أجل','2713','2918','0');
INSERT INTO [t21751] VALUES (2924,'مسألة: حلف لامرأته بطلاقها إن خرجت من داره فأتاها سيل','2714','2918','0');
INSERT INTO [t21751] VALUES (2925,'مسألة: حلف بطلاقها ألا يغرم في خياطة ثيابها شيئا','2715','2918','0');
