INSERT INTO [t21751] VALUES (2927,'مسألة: عليه حق لرجل فاتخذ عليه طلاق امرأته البتة أن يؤديه لأجل سماه','2716','2918','0');
INSERT INTO [t21751] VALUES (2928,'مسألة: يكون تحته أربع نسوة فيحلف بالطلاق البتة إلا يحلف بالطلاق فيطلق واحدة','2717','2918','0');
INSERT INTO [t21751] VALUES (2929,'قال لامرأته أنت علي كظهر أمي وهو يريد الطلاق','2718','2918','0');
