INSERT INTO [t21751] VALUES (2933,'مسألة: يقول لامرأته إن لم أتزوج عليك فأنت طالق فتزوج أخته جاهلا بها','2720','2918','0');
INSERT INTO [t21751] VALUES (2934,'مسألة: أراد أن يتزوج امرأة فقالت له أمه إنها أختك من الرضاعة قد أرضعتها','2721','2918','0');
INSERT INTO [t21751] VALUES (2935,'حلف بالطلاق ألا يستعير من رجل سماه شيئا فاستعار من امرأته','2721','2918','0');
