INSERT INTO [t21751] VALUES (2937,'مسألة: قال إن صليت ركعتين فامرأتي طالق فصلى ركعة وانصرف','2722','2918','0');
INSERT INTO [t21751] VALUES (2938,'مسألة: حلف ألا يكلم رجلا فكاتبه ثم رد الكتاب قبل أن يصل إليه فخرقه','2723','2918','0');
INSERT INTO [t21751] VALUES (2939,'مسألة: كتب بطلاق امرأته ثم بدا له فحبس الكتاب','2723','2918','0');
INSERT INTO [t21751] VALUES (2940,'حلف بطلاق امرأته ألا يدخل بيت فلان فيدخل داره ولم يدخل البيت','2724','2918','0');
