INSERT INTO [t21751] VALUES (2944,'مسألة: خرجت امرأته تزور بعض أهلها فحلف زوجها بطلاقها واحدة ألا يرسل إليها بنفقتها','2727','2918','0');
INSERT INTO [t21751] VALUES (2945,'مسألة: حلف بطلاق امرأته ألا ينفع فلانا فأمر غلامه أن يسقيه فسقاه','2729','2918','0');
INSERT INTO [t21751] VALUES (2946,'مسألة: قال إن لم أحج فامرأتي طالق البتة ولم يسم العام الذي يحج فيه','2729','2918','0');
INSERT INTO [t21751] VALUES (2947,'مسألة: حلف بطلاق امرأته ألا يدعها تخرج شهرا إلا لنقلة من منزل إلى منزل','2731','2918','0');
INSERT INTO [t21751] VALUES (2948,'مسألة: يأمر امرأته أن تعمل عملا ويقول أنت طالق إن لم تفرغي منه','2731','2918','0');
