INSERT INTO [t21751] VALUES (2952,'مسألة: قال لامرأته أنت طالق إلى ألف سنة أو خمسمائة سنة','2735','2918','0');
INSERT INTO [t21751] VALUES (2953,'مسألة: قال قد حلفت بالطلاق حتى إن امرأتي مني حرام','2735','2918','0');
INSERT INTO [t21751] VALUES (2954,'مسألة: يحلف لامرأته بطلاق كل امرأة ينكحها عليها فهي طالق البتة فيتزوج عليها سرا','2736','2918','0');
INSERT INTO [t21751] VALUES (2955,'مسألة: يقول لامرأته أنت طالق إلى سنة إلا أن يبدو لي ألا أطلقك','2737','2918','0');
INSERT INTO [t21751] VALUES (2956,'حلف بالطلاق في خصومة بينه وبين رجل ألا يفارقه حتى يبلغ أقصى حقه','2737','2918','0');
