INSERT INTO [t21751] VALUES (2958,'مسألة: ضايقه أبوه في شيء فحلف بالطلاق ألا يدخل تلك القرية سنة إلا عابر سبيل','2739','2918','0');
INSERT INTO [t21751] VALUES (2959,'قال لختنه زوج ابنته إني أسألك حاجة فاحلف لي بالطلاق أن تقضيها','2739','2918','0');
INSERT INTO [t21751] VALUES (2960,'مسألة: عبد قال كل امرأة أتزوجها ما دمت عبدا فهي طالق','2742','2918','0');
INSERT INTO [t21751] VALUES (2961,'قال لامرأته إن أمكنتني من حلق رأسك فلم أحلقه فأنت طالق البتة','2743','2918','0');
INSERT INTO [t21751] VALUES (2962,'مسألة: قال الرجل لامرأته أنت طالق يوم يجيء أبي','2744','2918','0');
