INSERT INTO [t21801] VALUES (1,'١. ... سئل فضيلة الشيخ: عما يقول بعض الناس من أن تصحيح الألفاظ غير مهم مع سلامة القلب؟','1','0','0');
INSERT INTO [t21801] VALUES (2,'٢. ... سئل فضيلة الشيخ: عن هذه الأسماء وهي: أبرار - ملاك - إيمان - جبريل - جنى؟','1','0','0');
INSERT INTO [t21801] VALUES (3,'٣. ... سئل فضيلة الشيخ: عن صحة هذه العبارة: (أجعل','1','0','0');
INSERT INTO [t21801] VALUES (4,'٤. ... سئل فضيلة الشيخ عن هذا القول (أحبائي في رسول الله؟','2','0','0');
INSERT INTO [t21801] VALUES (5,'٥. ... سئل فضيلة الشيخ إذا كتب الإنسان رسالة وقال فيها (إلى والدي العزيز) أو (إلى أخي الكريم) فهل في هذا شئ؟','3','0','0');
INSERT INTO [t21801] VALUES (6,'٦. ... وسئل: عن عبارة (أدام الله أيامك) ؟','4','0','0');
INSERT INTO [t21801] VALUES (7,'٧. ... وسئل ما رأي فضيلتكم في هذه الألفاظ جلاله وصاحب الجلالة، وصاحب السمو؟ وأرجو وآمل؟','5','0','0');
INSERT INTO [t21801] VALUES (8,'٨. ... سئل فضيلة الشيخ عن هذه الألفاظ (أرجوك) ، (تحياتي) ، و (أنعم صباحا) ، و (أنعم مساء) ؟','5','0','0');
INSERT INTO [t21801] VALUES (9,'٩. ... وسئل فضيلة الشيخ: عمن يسأل بوجه الله فيقول أسألك بوجه الله كذا وكذا فما الحكم في هذا لقول؟','6','0','0');
INSERT INTO [t21801] VALUES (10,'١٠. ... وسئل فضيلة الشيخ حفظه الله: ما رأيكم فيمن يقول (آمنت بالله، وتوكلت على الله، واعتصمت بالله، واستجرت برسول الله صلى الله عليه وسلم؟','6','0','0');
INSERT INTO [t21801] VALUES (11,'١١. ... سئل فضيلة الشيخ: ما حكم قول (أطال الله بقاءك) (طال عمرك) ؟','7','0','0');
INSERT INTO [t21801] VALUES (12,'١٢. ... سئل فضيلة الشيخ: عن قول أحد الخطباء في كلامه حول عزوة بدر: (التقى إله وشيطان) . فقد قال بعض العلماء أن هذه العبارة كفر صريح، لأن ظاهر العبارات إثبات الحركة لله - عز وجل- نرجو من سيادتكم توضيح ذلك؟','8','0','0');
INSERT INTO [t21801] VALUES (13,'١٤. ... وسئل: عن هذه الكلمة (الله غير مادي) ؟ .','13','0','0');
INSERT INTO [t21801] VALUES (14,'١٥. ... سئل فضيلته: عن قول بعض الناس إذا انتقم الله من الظالم (الله ما يضرب بعصا) ؟ .','14','0','0');
INSERT INTO [t21801] VALUES (15,'١٦. ... سئل فضيلة الشيخ: كثيرا ما نرى على الجدران كتابة لفظ الجلالة (الله) ، وبجانبها لفظ محمد صلى الله عليه وسلم أو نجد ذلك على الرقاع، أو على الكتب، أو على بعض المصاحف فهل موضعها هذا صحيح؟.','14','0','0');
INSERT INTO [t21801] VALUES (16,'١٧. ... سئل فضيلة الشيخ: كيف نجمع بين قول الصحابة (الله ورسوله أعلم) بالعطف بالواو وإقرارهم على ذلك وإنكاره صلى الله عليه وسلم، على من قال (ما شاء وشئت) ؟ .','15','0','0');
INSERT INTO [t21801] VALUES (17,'١٨. ... سئل فضيلة الشيخ: عن هذه العبارة (أعطني الله لا يهينك) ؟ .','16','0','0');
INSERT INTO [t21801] VALUES (18,'١٩. ... وسئل فضيلة الشيخ عن هذه العبارة (الله يسأل عن حالك) ؟ .','17','0','0');
INSERT INTO [t21801] VALUES (19,'٢٠. ... وسئل: هل يجوز على الإنسان أن يقسم على الله؟ .','18','0','0');
INSERT INTO [t21801] VALUES (20,'٢١. ... وسئل فضيلة الشيخ: عن التسمي بالإمام؟ .','20','0','0');
INSERT INTO [t21801] VALUES (21,'٢٢. ... سئل فضيلة الشيخ: عن إطلاق بعض الأزواج على زوجاتهم وصف أم المؤمنين؟ .','21','0','0');
INSERT INTO [t21801] VALUES (22,'٢٣. ... سئل فضيلة الشيخ: ما حكم قول (يا عبدي) و (يا أمتي) ؟ .','21','0','0');
INSERT INTO [t21801] VALUES (23,'٢٥. ... سئل فضيلة الشيخ: عن قول العاصي عند الإنكار عليه (أنا حر في تصرفاتي) ؟ .','23','0','0');
INSERT INTO [t21801] VALUES (24,'٢٦. ... سئل فضيلة الشيخ: عن قول الإنسان: (إن الله على ما يشاء قدير) عند ختم الدعاء ونحوه؟ .','23','0','0');
INSERT INTO [t21801] VALUES (25,'٢٨. ... سئل فضيلة الشيخ: عن قول (فلان المرحوم) . و (تغمده الله برحمته) و (انتقل إلى رحمه الله) ؟ .','30','0','0');
INSERT INTO [t21801] VALUES (26,'٢٩. ... سئل فضيلة الشيخ: عن عبارة (لكم تحياتنا) وعبارة (اهدي لكم تحياتي) ؟','31','0','0');
INSERT INTO [t21801] VALUES (27,'٣٠. ... سئل فضيلة الشيخ: يقول بعض الناس: (أوجد الله كذا) ، فما مدى صحتها؟ وما الفرق بينها وبين: (خلق الله كذا) أو (صور الله كذا) ؟ .','31','0','0');
INSERT INTO [t21801] VALUES (28,'٣١. ... سئل فضيلة الشيخ: عن حكم التسمي بإيمان؟ .','32','0','0');
INSERT INTO [t21801] VALUES (29,'٣٢. ... سئل فضيلته: عن التسمي بإيمان؟ .','33','0','0');
INSERT INTO [t21801] VALUES (30,'٣٣. ... سئل فضيلة الشيخ: ما حكم هذه الألقاب (حجة الله) (حجة الإسلام) (أية الله) ؟ .','34','0','0');
INSERT INTO [t21801] VALUES (31,'٣٥. ... وسئل فضيلته: هل هذه العبارة صحيحة (بفضل فلان تغير هذا الأمر، أو بجهدي صار كذا) ... ؟ .','35','0','0');
INSERT INTO [t21801] VALUES (32,'٣٦. ... سئل فضيلة الشيخ: عن حكم قول: (البقية في حياتك) ، عند التعزية ورد أهل الميت بقولهم: (حياتك الباقية) ؟ .','36','0','0');
INSERT INTO [t21801] VALUES (33,'٣٧. ... وسئل حفظه الله تعالى: عن حكم ثناء الإنسان على الله تعالى بهذه العبارة (بيده الخير والشر) ... ؟ .','37','0','0');
INSERT INTO [t21801] VALUES (34,'٣٨. ... سئل فضيلة الشيخ: عن قول العامة (تباركت علينا؟) (زارتنا البركة؟) .','39','0','0');
INSERT INTO [t21801] VALUES (35,'٣٩. ... سئل فضيلة الشيخ: عن إطلاق عبارة (كتب التراث) على كتب السلف؟ .','41','0','0');
INSERT INTO [t21801] VALUES (36,'٤٠. ... سئل فضيلة الشيخ: هل في الإسلام تجديد تشريع؟','41','0','0');
INSERT INTO [t21801] VALUES (37,'٤١. ... وسئل: عن حكم قولهم: تدخل القدر؟ وتدخلت عناية الله؟','43','0','0');
INSERT INTO [t21801] VALUES (38,'٤٢. ... وسئل: عن حكم التسمي بأسماء الله مثل كريم، وعزيز ونحوهما؟','43','0','0');
INSERT INTO [t21801] VALUES (39,'٤٣. ... وسئل: عن حكم التسمي بأسماء الله تعالى مثل الرحيم والحكيم؟','45','0','0');
INSERT INTO [t21801] VALUES (40,'٤٤. ... وسئل فضيلة الشيخ: عن حكم ثناء الإنسان على نفسه؟','47','0','0');
INSERT INTO [t21801] VALUES (41,'٤٥. ... سئل فضيلة الشيخ: عن قول (يا حاج) ، و (السيد فلان) ؟','48','0','0');
INSERT INTO [t21801] VALUES (42,'٤٦. ... وسئل أيضا: عن حكم ما درج على ألسنة بعض الناس من قولهم (حرام عليك أن تفعل كذا وكذا) ؟','49','0','0');
INSERT INTO [t21801] VALUES (43,'٤٧. ... سئل فضيلة الشيخ: قلتم في الفتوى رقم (ههه٤٦) أن التحريم يكون قدريا ويكون شرعيا فنأمل من سيادتكم التكرم ببيان بعض الأمثلة؟','50','0','0');
INSERT INTO [t21801] VALUES (44,'٤٨. ... وسئل فضيلة الشيخ: نسمع ونقرأ كلمة (حرية الفكر) ، وهي دعوة إلى حرية الاعتقاد، فيما تعليقكم على ذلك؟','51','0','0');
INSERT INTO [t21801] VALUES (45,'٤٩. ... سئل فضيلة الشيخ: هل يجوز أن يقول الإنسان للمفتي ما حكم الإسلام في كذا وكذا؟ أو ما رأي الإسلام؟','53','0','0');
INSERT INTO [t21801] VALUES (46,'٥٠. ... سئل فضيلة الشيخ: عن وصف الإنسان بأنه حيوان ناطق؟','54','0','0');
INSERT INTO [t21801] VALUES (47,'٥١. ... سئل فضيلة الشيخ: عن قول بعض الناس: (وخسرت في الحج كذا، وخسرت في العمرة كذا، وخسرت في الجهاد كذا، وكذا؟','55','0','0');
INSERT INTO [t21801] VALUES (48,'٥٢. ... سئل فضيلة الشيخ: عن قول الإنسان لرجل: (أنت يا فلان خليفة الله في أرضه) ؟','55','0','0');
INSERT INTO [t21801] VALUES (49,'٥٣. ... وسئل فضيلته: يستخدم بضع الناس عبارة (راعني) ويقصدون بها انظرني، فما صحة هذه الكلمة؟','56','0','0');
INSERT INTO [t21801] VALUES (50,'٥٤. ... وسئل حفظه الله: ما حكم قول (رب البيت) ؟ (رب المنزل) ؟','57','0','0');
INSERT INTO [t21801] VALUES (51,'٥٥. ... سئل فضيلة الشيخ: عن قول ما يقول إن الإنسان يتكون من عنصرين عنصر من التراب وهو الجسد، وعنصر من الله وهو الروح؟ .','60','0','0');
INSERT INTO [t21801] VALUES (52,'٥٦. ... سئل فضيلة الشيخ: عن المراد بالروح والنفس؟ والفرق بينهما؟ .','64','0','0');
INSERT INTO [t21801] VALUES (53,'٥٧. ... سئل فضيلة الشيخ: عن حكم إطلاق لفظ (السيد) على غير الله تعالى؟ .','67','0','0');
INSERT INTO [t21801] VALUES (54,'٥٨. ... سئل فضيلة الشيخ: من الذي يستحق أن يوصف بالسيادة؟ .','67','0','0');
INSERT INTO [t21801] VALUES (55,'٥٩. ... وسئل فضيلته عن الجمع بين حديث عبد الله بن الشخير - رضي الله عنه - قال (انطلقت في وفد بني عامر إلى رسول الله صلى الله عليه وسلم، فقلنا أنت سيدنا فقال " السيد الله تبارك وتعالى ". وما جاء في التشهد " اللهم صل على سيدنا محمد وعلى آل سيدنا محمد ". وحديث " أنا سيد ولد آدم "؟ .','69','0','0');
INSERT INTO [t21801] VALUES (56,'٦٠. ... وسئل فضيلته عن هذه العبارة (السيدة عائشة - رضي الله عنها -) ؟ .','71','0','0');
INSERT INTO [t21801] VALUES (57,'٦٢. ... وسئل فضيلة الشيخ: عن قول: (شاءت الظروف أن يحصل كذا وكذا) ، و (شاءت الأقدار كذا وكذا) ؟ ... .','74','0','0');
INSERT INTO [t21801] VALUES (58,'٦٣. ... سئل فضيلته: عن حكم قول (وشاءت قدرة الله) و (شاء القدر) ؟ .','75','0','0');
INSERT INTO [t21801] VALUES (59,'٦٤. ... سئل فضيلته: هل يجوز إطلاق (شهيد) على شخص بعينه فيقال الشهيد فلان؟ .','76','0','0');
INSERT INTO [t21801] VALUES (60,'٦٦. ... سئل فضيلة الشيخ: عن لقب (شيخ الإسلام) هل يجوز؟ .','81','0','0');
INSERT INTO [t21801] VALUES (61,'٦٧. ... وسئل ما رأي فضيلتكم في استعمال كلمة (صدفة) ؟ .','81','0','0');
INSERT INTO [t21801] VALUES (62,'٦٨. ... سئل فضيلة الشيخ عن تسمية بعض الزهور بـ (عباد الشمس لأنه يستقبل الشمس عند الشروق وعند الغروب؟ .','82','0','0');
INSERT INTO [t21801] VALUES (63,'٦٩. ... وسئل فضيلة الشيخ لماذا كان التسمي بعبد الحارث من الشرك مع أن الله هو الحارث؟ .','83','0','0');
INSERT INTO [t21801] VALUES (64,'٧٠. ... سئل فضيلة الشيخ عن هذه العبارة: (العصمة لله وحده) ، مع أن العصمة لابد فيها من عاصم؟ .','84','0','0');
INSERT INTO [t21801] VALUES (65,'٧١. ... وسئل فضيلة الشيخ: عن عبارة: (فال الله ولا فالك) ؟ .','85','0','0');
INSERT INTO [t21801] VALUES (66,'٧٢. ... سئل فضيلة الشيخ: عن مصطلح (فكر إسلامي) و (مفكر إسلامي) ؟ .','85','0','0');
INSERT INTO [t21801] VALUES (67,'٧٣. ... سئل فضيلة الشيخ: جاء في الفتوى رقم (٧٢ههه) أن كلمة الفكر الإسلامي كلمة لا تجوز لأنها تعني أن الإسلام قد يكون عبارة عن أفكار قد تصح أو لا تصح وهكذا، بينما قلتم أن إطلاق كلمة (المفكر الإسلامي) تجوز لأن فكر الشخص يتغير وقد يكون صحيحا أو العكس، ولكن الأشخاص الذين يستخدمون مصطلح (الفكر الإسلامي) يقولون أننا نقصد فكر الأشخاص ولا نتكلم عن الإسلام ككل أو عن الشريعة الإسلامية بالتحديد فهل هذا المصطلح (الفكر الإسلامي) جائز بهذا التفسير أم لا وما هو الدليل؟ .','86','0','0');
INSERT INTO [t21801] VALUES (68,'٧٤. ... سئل فضيلة الشيخ: عن قول بعض الناس إذا شاهد من أسرف على نفسه بالذنوب: (فلان بعيد عن الهداية، أو عن الجنة، أو عن مغفرة الله) فما حكم ذلك؟ .','87','0','0');
INSERT INTO [t21801] VALUES (69,'٧٥. ... وسئل فضيلته: عن قول الإنسان إذا سئل عن شخص قد توفاه الله قريبا: " فلان ربنا افتكره "؟ .','88','0','0');
INSERT INTO [t21801] VALUES (70,'٧٦. ... سئل فضيلة الشيخ: عن حكم التسمي بقاضي القضاة؟ .','89','0','0');
INSERT INTO [t21801] VALUES (71,'٧٧. ... وسئل فضيلة الشيخ: عن تقسيم الدين إلى قشور ولب، (مثل اللحية) ؟ .','91','0','0');
INSERT INTO [t21801] VALUES (72,'٧٨. ... سئل فضيلة الشيخ: عن عبارة (كل عام وانتم بخير) ؟ .','93','0','0');
INSERT INTO [t21801] VALUES (73,'٧٩. ... سئل فضيلة الشيخ: عن حكم لعن الشيطان؟ .','93','0','0');
INSERT INTO [t21801] VALUES (74,'٨٠. ... وسئل فضيلة الشيخ: عن قول الإنسان متسخطا: (لو إني فعلت كذا لكان كذا) ، أو يقول (لعنه الله على المرض الذي أعاقني) ؟ .','94','0','0');
INSERT INTO [t21801] VALUES (75,'٨١. ... وسئل: عن قول (لك الله) ؟ .','95','0','0');
INSERT INTO [t21801] VALUES (76,'٨٢. ... سئل فضيلة الشيخ: عن عبارة لم تسمح لي الظروف؟ أو لم يسمح لي الوقت؟ .','95','0','0');
INSERT INTO [t21801] VALUES (77,'٨٣. ... سئل فضيلة الشيخ: عن حكم استعمال لو؟ .','96','0','0');
INSERT INTO [t21801] VALUES (78,'٨٤. ... سئل فضيلة الشيخ: عن العبارة (لولا الله وفلان) ؟ .','99','0','0');
INSERT INTO [t21801] VALUES (79,'٨٥. ... وسئل فضيلة الشيخ: عن قولهم (المادة لا تفنى ولا تزول ولم تخلق من عدم) ؟ .','101','0','0');
INSERT INTO [t21801] VALUES (80,'٨٦. ... سئل فضيلة الشيخ: ما حكم قول (شاءت قدرة الله) ، وإذا كان الجواب بعدمه فلماذا؟ مع أن الصفة تتبع موصوفها، والصفة لا تنفعك عن ذات الله؟ .','102','0','0');
INSERT INTO [t21801] VALUES (81,'٨٧. ... سئل فضيلة الشيخ: عن هذه العبارة: (ما صدقت على الله أن يكون كذا وكذا) ؟ .','103','0','0');
INSERT INTO [t21801] VALUES (82,'٨٨. ... سئل فضيلة الشيخ: عن قول الإنسان إذا شاهد جنازة: (من المتوفي) بالياء؟ .','104','0','0');
INSERT INTO [t21801] VALUES (83,'٨٩. ... سئل فضيلة الشيخ: عن قول (إن فلان له المثل الأعلى) ؟ .','104','0','0');
INSERT INTO [t21801] VALUES (84,'٩٠. ... سئل فضيلة الشيخ: ما حكم قولهم (دفن في مثواه الأخير) ؟ .','105','0','0');
INSERT INTO [t21801] VALUES (85,'٩١. ... وسئل عن قول: (مسيجيد، مصيحيف) ؟ .','106','0','0');
INSERT INTO [t21801] VALUES (86,'٩٢. ... سئل فضيلة الشيخ: عن إطلاق المسيحية على النصرانية؟ والمسيحي على النصراني؟ .','106','0','0');
INSERT INTO [t21801] VALUES (87,'٩٤. ... سئل فضيلة الشيخ: عن هذه العبارة (المكتوب على الجبين لابد تراه العين) ؟ .','110','0','0');
INSERT INTO [t21801] VALUES (88,'٩٥. ... سئل فضيلة الشيخ: عن قول الإنسان إذا خاطب ملكا (يا مولاي) ؟ .','111','0','0');
INSERT INTO [t21801] VALUES (89,'٩٦. ... وسئل فضيلة الشيخ: يحتج بعض الناس إذا نهي عن أمر مخالف للشريعة أو للآداب الإسلامية بقوله (الناس يفعلون كذا) ؟ .','112','0','0');
INSERT INTO [t21801] VALUES (90,'٩٧. ... وسئل فضيلة الشيخ: عن قول الإنسان لضيفه: (وجه الله إلا أن تأكل) ؟ .','113','0','0');
INSERT INTO [t21801] VALUES (91,'٩٨. ... سئل الشيخ: عن قولهم (هذا نوء محمود) ؟ .','113','0','0');
INSERT INTO [t21801] VALUES (92,'٩٩. ... وسئل فضيلة الشيخ: - حفظه الله -: عن قول (لا حول الله) ... ؟ .','114','0','0');
INSERT INTO [t21801] VALUES (93,'١٠٠. ... سئل فضيلة الشيخ: ما رأيكم في هذه العبارة (لا سمح الله) ... ؟ .','114','0','0');
INSERT INTO [t21801] VALUES (94,'١٠١. ... سئل فضيلة الشيخ غفر الله له: ما حكم قول (لا قدر الله) ؟ .','115','0','0');
INSERT INTO [t21801] VALUES (95,'١٠٢. ... سئل فضيلة الشيخ: عن قول بعض الناس إذا مات شخص) يا أيتها النفس المطمئنة أرجعي إلى ربك راضية مرضية (؟ .','116','0','0');
INSERT INTO [t21801] VALUES (96,'١٠٣. ... سئل فضيلة الشيخ: ما رأيكم في قول بعض الناس (يا هادي، يا دليل؟ .','116','0','0');
INSERT INTO [t21801] VALUES (97,'١٠٤. ... وسئل غفر الله له: عن قول بعض الناس (يعلم الله كذا وكذا) ؟ .','116','0','0');
INSERT INTO [t21801] VALUES (98,'١٠٥. ... وسئل فضيلة الشيخ: عن قول: (على هواك) وقول بعض الناس في مثل مشهور: (العين وما ترى والنفس ما تشتهي) ؟ .','118','0','0');
