INSERT INTO [t21823] VALUES (1,'المقدمة','1','0','0');
INSERT INTO [t21823] VALUES (2,'فتاوى العقيدة','2','0','0');
INSERT INTO [t21823] VALUES (3,'السؤال (١) : فضيلة الشيخ، ما هي الغاية من خلق البشر؟','3','2','0');
INSERT INTO [t21823] VALUES (4,'السؤال (٢) : فضيلة الشيخ، لكن هل للعبادة مفهوم يمكن أن نعرفه، وهل لها مفهوم عام، ومفهوم خاص؟','4','2','0');
INSERT INTO [t21823] VALUES (5,'السؤال (٣) : فضيلة الشيخ، هل يثاب من اختصوا بالعبادة الكونية عن هذه العبادة الشرعية؟','5','2','0');
INSERT INTO [t21823] VALUES (6,'السؤال (٤) : فضيلة الشيخ، ما هو أول واجب على الخلق؟','6','2','0');
INSERT INTO [t21823] VALUES (7,'السؤال (٥) : فضيلة الشيخ، لكن هل تشمل الشهادة أنواع التوحيد؟','7','2','0');
INSERT INTO [t21823] VALUES (8,'السؤال (٦) : فضيلة الشيخ، ما معنى التوحيد؟','8','2','0');
INSERT INTO [t21823] VALUES (9,'السؤال (٧) : فضيلة الشيخ، ما هي أنواع التوحيد على سبيل الإجمال؟','9','2','0');
INSERT INTO [t21823] VALUES (10,'السؤال (٨) : فضيلة الشيخ، ما هي أنواع التوحيد مع التوضيح والأمثلة لذلك؟','10','2','0');
INSERT INTO [t21823] VALUES (11,'السؤال (٩) : فضيلة الشيخ، نريد زيادة تفصيل في القسم الأخيرة من أقسام التوحيد وهو توحيد الأسماء والصفات؟','11','2','0');
INSERT INTO [t21823] VALUES (12,'السؤال (١٠) : فضيلة الشيخ، ما هو الواجب علينا نحو كل نوع منها على حدة؟','12','2','0');
INSERT INTO [t21823] VALUES (13,'السؤال (١١) : فضيلة الشيخ، ما حكم صرف شيء من أنواع العبادة لغير الله سبحانه؟','13','2','0');
INSERT INTO [t21823] VALUES (14,'السؤال (١٢) : فضيلة الشيخ، ما معنى الشهادتين، شهادة أن لا إله إلا الله وأن محمدا رسول الله؟','14','2','0');
INSERT INTO [t21823] VALUES (15,'السؤال (١٣) : فضيلة الشيخ، هذا معنى شهادة لا إله إلا الله، فما معنى شهادة أن محمدا رسول الله؟','15','2','0');
INSERT INTO [t21823] VALUES (16,'السؤال (١٤) : فضيلة الشيخ، لكن ما الفرق بين الاعتراف باللسان والقلب، وهل يلزم الجمع بينهما؟','16','2','0');
INSERT INTO [t21823] VALUES (17,'السؤال (١٥) : فضيلة الشيخ، الذي جرنا إلى هذا السؤال أن هناك فريقا من الناس الآن إذا دعي أحدهم إلى العبادة قال: إن الله رب قلوب، وهذا أيضا الذي نريد التعليق عليه؟','17','2','0');
INSERT INTO [t21823] VALUES (18,'السؤال (١٦) : فضيلة الشيخ، ما هو مفهوم الإيمان وأركانه بصورة مختصرة؟','18','2','0');
INSERT INTO [t21823] VALUES (19,'السؤال (١٧) : فضيلة الشيخ، هل هذا المفهوم هو المفهوم الذي قاله رسول الله صلى الله عليه وسلم لجبريل عليه السلام حينما سأله عن الإيمان؟','19','2','0');
INSERT INTO [t21823] VALUES (20,'السؤال (١٨) : فضيلة الشيخ، لكن إذا سئل الإنسان عن الإيمان هل يقول هو الإقرار المستلزم للقبول والإذعان، أو يقول: أن تؤمن بالله وملائكته وكتبه ورسله، كما قال الرسول صلى الله عليه وسلم؟','20','2','0');
INSERT INTO [t21823] VALUES (21,'السؤال (١٩) : فضيلة الشيخ، نريد أن نتوسع في مفهوم الإيمان، وكذلك نريد أن نعرف أركان الإيمان؟','21','2','0');
INSERT INTO [t21823] VALUES (22,'السؤال (٢٠) : لكن نجد الدهريين مثلا وهم كثير الآن وهم من العقلاء، لأنهم يفكرون وينتجون، لكنهم يجمعون على عدم وجود الله عز وجل، فكيف يرد على مثل هؤلاء؟','22','2','0');
INSERT INTO [t21823] VALUES (23,'السؤال (٢١) : فضيلة الشيخ، بقي معنا أن نحدد أركان الإيمان؟','23','2','0');
INSERT INTO [t21823] VALUES (24,'السؤال (٢٢) : فضيلة الشيخ، هل بقي شيء يتعلق بالإيمان بالملائكة تريدون أن تتحدثوا عنه أم ننتقل إلى بقية الأركان؟','24','2','0');
INSERT INTO [t21823] VALUES (25,'السؤال (٢٣) : فضيلة الشيخ، بقي الركن الثالث من أركان الإيمان؟','25','2','0');
INSERT INTO [t21823] VALUES (26,'السؤال (٢٤) : فضيلة الشيخ، هذا بالنسبة للركن الثالث، فما قولكم في الركن الرابع الذي هو الإيمان بالرسل؟','26','2','0');
INSERT INTO [t21823] VALUES (27,'السؤال (٢٥) : فضيلة الشيخ، كيف يكون الإيمان بالركن الخامس وهو اليوم الآخر؟','27','2','0');
INSERT INTO [t21823] VALUES (28,'السؤال (٢٦) : فضيلة الشيخ، بقي الإيمان بالقدر نريد أن تحدثنا عنه أثابكم الله؟','28','2','0');
INSERT INTO [t21823] VALUES (29,'السؤال (٢٧) : فضيلة الشيخ، هل الإيمان يزيد وينقص؟ ونود أن نعرف بأي شيء تحصل الزيادة، وبأي شيء يحصل النقصان؟','29','2','0');
INSERT INTO [t21823] VALUES (30,'السؤال (٢٨) : فضيلة الشيخ، بالنسبة لزيادة الإيمان ونقصانه هناك من يرى أن الإيمان لا يزيد ولا ينقص، وأن المعصية تذهب الإيمان كله ويكفر الإنسان، كيف يرد على هؤلاء؟','30','2','0');
INSERT INTO [t21823] VALUES (31,'السؤال (٢٩) : فضيلة الشيخ، لكن ما حكم عدم الإقرار بزيادة الإيمان ونقصانه؟','31','2','0');
INSERT INTO [t21823] VALUES (32,'السؤال (٣٠) : فضيلة الشيخ، ما هي صفة الحكم بغير ما أنزل الله؟','32','2','0');
INSERT INTO [t21823] VALUES (33,'السؤال (٣١) : فضيلة الشيخ، ذكرتم في الظالم والفاسق أشياء متقاربة أو يمكن أن تكون متداخلة، وهي أن الظالم يحكم بغير ما أنزل الله وهو يعلم أن حكم الله أفضل لكنه يريد أن يتشفى من أحد فيطبق حكما على شخص ما جاء عن الله، والفاسق يحكم وهو يعلم بحكم الله، ويعلم أنه هو الحكم السديد، لكنه لمصلحته أو هوى في نفسه، أو ليوافق هوى لغيره يحكم بغير ما أنزل الله، فما الفرق بينهما؟','33','2','0');
INSERT INTO [t21823] VALUES (34,'السؤال (٣٢) : فضيلة الشيخ، ما هي الكهانة؟','34','2','0');
INSERT INTO [t21823] VALUES (35,'السؤال (٣٣) : فضيلة الشيخ، حبذا أيضا لو عرفنا أحوال الناس الذين يرتادون الكهنة والكهان؟','35','2','0');
INSERT INTO [t21823] VALUES (36,'السؤال (٣٤) : فضيلة الشيخ، نريد أن نعرف التنجيم وحكمه؟','36','2','0');
INSERT INTO [t21823] VALUES (37,'السؤال (٣٥) : فضيلة الشيخ يعني أن هذا يكون من باب استقراء السنن الكونية؟','37','2','0');
INSERT INTO [t21823] VALUES (38,'السؤال (٣٦) : فضيلة الشيخ، لكن هل هناك علاقة بين التنجيم والكهانة؟','38','2','0');
INSERT INTO [t21823] VALUES (39,'السؤال (٣٧) : فضيلة الشيخ، لكن أيهما أخطر على المسلمين؟','39','2','0');
INSERT INTO [t21823] VALUES (40,'السؤال (٣٨) : فضيلة الشيخ، ذكرتم في حديثكم عن التنجيم أنه نوع من السحر فما هو السحر؟','40','2','0');
INSERT INTO [t21823] VALUES (41,'السؤال (٣٩) : فضيلة الشيخ، لكن ما المقصود باللطافة في قولكم: السحر كل ما لطف وخفي سببه؟','41','2','0');
INSERT INTO [t21823] VALUES (42,'السؤال (٤٠) : فضيلة الشيخ، ما حكم السحر وما حكم تعلمه؟','42','2','0');
INSERT INTO [t21823] VALUES (43,'السؤال (٤١) : فضيلة الشيخ، هل السحر حقيقة أم أنه تخيل أو تخيلات على الناس؟','43','2','0');
INSERT INTO [t21823] VALUES (44,'السؤال (٤٢) : فضيلة الشيخ، تحدثتم عن الكهانة وعرفتم الكاهن، وعرفتم أيضا السحر، لكن هل هناك علاقة بين الكهانة والسحر؟','44','2','0');
INSERT INTO [t21823] VALUES (45,'السؤال (٤٣) : فضيلة الشيخ، جاء عن رسول الله صلى الله عليه وسلم أنه سحر، فنريد أن تتحدثوا لنا عما سحر به النبي صلى الله عليه وسلم؟ وأيضا هل حصول السحر للنبي صلى الله عليه وسلم ينافي مقام النبوة؟','45','2','0');
INSERT INTO [t21823] VALUES (46,'السؤال (٤٤) : فضيلة الشيخ، ما هو الإلحاد في أسماء الله وصفاته؟','46','2','0');
INSERT INTO [t21823] VALUES (47,'السؤال (٤٥) : فضيلة الشيخ، ما هي أنواع الشرك؟','47','2','0');
INSERT INTO [t21823] VALUES (48,'السؤال (٤٦) : فضيلة الشيخ، عرفنا أنواع الشرك لكن هل هناك تعريف محدد لكل نوع منها؟','48','2','0');
INSERT INTO [t21823] VALUES (49,'السؤال (٤٧) : فضيلة الشيخ، ورد فيما رواه مسلم قوله صلى الله عليه وسلم "إن بين الرجل وبين الشرك الكفر ترك الصلاة" هل ترك العبادة يكون شركا؟','49','2','0');
INSERT INTO [t21823] VALUES (50,'السؤال (٤٨) : فضيلة الشيخ، ما هو دين الإسلام؟','50','2','0');
INSERT INTO [t21823] VALUES (51,'السؤال (٤٩) : فضيلة الشيخ، إذن هل نفهم من ذلك أن لدينا تعريفا للإسلام بالمعنى العام وتعريفا له بالمعنى الخاص؟','51','2','0');
INSERT INTO [t21823] VALUES (52,'السؤال (٥٠) : فضيلة الشيخ، ما هو الطاغوت، وما هي اشتقاقاته؟','52','2','0');
INSERT INTO [t21823] VALUES (53,'السؤال (٥١) : فضيلة الشيخ، ما هي عقيدة المسلمين في عيسى ابن مريم عليه السلام؟ وما حكم القول بقتله وصلبه.','53','2','0');
INSERT INTO [t21823] VALUES (54,'السؤال (٥٢) : فضيلة الشيخ، إلى كم افترقت الأمة الإسلامية بعد نبيها محمد صلى الله عليه وسلم؟','54','2','0');
INSERT INTO [t21823] VALUES (55,'السؤال (٥٣) : فضيلة الشيخ، ما هي أبرز خصائص الفرقة الناجية؟','55','2','0');
INSERT INTO [t21823] VALUES (56,'السؤال (٥٤) : فضيلة الشيخ، لكن هل يلزم توافر أو تكامل هذه الخصائص في الأمور الأربعة وهي: العقيدة، والعبادة، والأخلاق، والمعاملات، دون نقص؟ وهل إذا نقص منها شيء يخرج الإنسان بذلك من الفرقة الناجية أم أن النقص لا يخرجه من ذلك؟','56','2','0');
INSERT INTO [t21823] VALUES (57,'السؤال (٥٥) : فضيلة الشيخ، هل هناك إضافة حول خصائص هذه الفرقة الناجية؟','57','2','0');
INSERT INTO [t21823] VALUES (58,'السؤال (٥٦) : فضيلة الشيخ، ما هو التوسل الصحيح والتوسل الباطل؟','58','2','0');
INSERT INTO [t21823] VALUES (59,'السؤال (٥٧) : فضيلة الشيخ، هل هناك أنواع أخرى من التوسل غير أنواع التوسل الأربعة التي ذكرتموها؟','59','2','0');
INSERT INTO [t21823] VALUES (60,'السؤال (٥٨) : فضيلة الشيخ، بعد أن عرفنا التوسل الصحيح وأقسامه، لابد لنا من معرفة التوسل الباطل، وهل له أقسام أيضا؟','60','2','0');
INSERT INTO [t21823] VALUES (61,'السؤال (٥٩) : فضيلة الشيخ، ما هي الشفاعة المثبتة والشفاعة المنفية؟','61','2','0');
INSERT INTO [t21823] VALUES (62,'السؤال (٦٠) : فضيلة الشيخ، ما هي عقيد السلف في القرآن الكريم؟','62','2','0');
INSERT INTO [t21823] VALUES (63,'السؤال (٦١) : فضيلة الشيخ، ما هي أبرز أحكام التلاوة؟','63','2','0');
INSERT INTO [t21823] VALUES (64,'السؤال (٦٢) : فضيلة الشيخ، ما حكم التلاوة لروح الميت؟','64','2','0');
INSERT INTO [t21823] VALUES (65,'السؤال (٦٣) : فضيلة الشيخ، بالنسبة للذين يوصون أن تقرأ الفاتحة لروح النبي صلى الله عليه وسلم أو له عند قبر النبي صلى الله عليه وسلم؟','65','2','0');
INSERT INTO [t21823] VALUES (66,'فتاوى الطهارة','66','0','0');
INSERT INTO [t21823] VALUES (67,'السؤال (٦٤) : فضيلة الشيخ، ما هي الطهارة؟','67','66','0');
INSERT INTO [t21823] VALUES (68,'السؤال (٦٥) : فضيلة الشيخ، ما هو الأصل في التطهير؟','68','66','0');
INSERT INTO [t21823] VALUES (69,'السؤال (٦٦) : فضيلة الشيخ، ما هو البدل عن هذا الأصل الذي هو الماء؟','69','66','0');
INSERT INTO [t21823] VALUES (70,'السؤال (٦٧) : فضيلة الشيخ، إذن لو كان على الإنسان نجاسة، ولا يستطيع إزالتها، فإنه لا يتيمم عنها؟','70','66','0');
INSERT INTO [t21823] VALUES (71,'السؤال (٦٨) : فضيلة الشيخ، ما هي صفة الوضوء؟','71','66','0');
INSERT INTO [t21823] VALUES (72,'السؤال (٦٩) : فضيلة الشيخ، لكن بالنسبة للأذنين، هل يلزم أخذ ماء خاص لهما أم مع الرأس؟','72','66','0');
INSERT INTO [t21823] VALUES (73,'السؤالان (٧٠-٧١) : فضيلة الشيخ، ما هي نواقض الوضوء؟','73','66','0');
INSERT INTO [t21823] VALUES (74,'السؤال (٧٢) : فضيلة الشيخ، ولكن بالنسبة للنوم هل هناك فرق بين نوم الليل ونوم النهار؟','74','66','0');
INSERT INTO [t21823] VALUES (75,'السؤال (٧٣) : فضيلة الشيخ، ما هي موجبات الغسل؟ وما صفته؟','75','66','0');
INSERT INTO [t21823] VALUES (76,'السؤال (٧٤) : فضيلة الشيخ، ما هو حكم المسح على الخفين وشروط ذلك؟','76','66','0');
INSERT INTO [t21823] VALUES (77,'السؤال (٧٥) : فضيلة الشيخ، لكن هل هناك شروط تتعلق بالممسوح عليه من خف وجورب؟','77','66','0');
INSERT INTO [t21823] VALUES (78,'السؤال (٧٦) : فضيلة الشيخ، ما حكم المسح على الجورب أو الخف المخروق أو الجورب الخفيف؟','78','66','0');
INSERT INTO [t21823] VALUES (79,'السؤال (٧٧) : فضيلة الشيخ، هل موجبات الغسل تعد من نواقض الوضوء أم لا؟','79','66','0');
INSERT INTO [t21823] VALUES (80,'السؤال (٧٨) : فضيلة الشيخ، ذكرتم من موجبات الغسل الجنابة، فما هي الأحكام المتعلقة بالجنابة؟','80','66','0');
INSERT INTO [t21823] VALUES (81,'السؤال (٧٩) : فضيلة الشيخ، أيضا مما يتعلق بالطهارة الشك فيها، فما هو الشك في الطهارة، ومتى يكون مؤثرا؟','81','66','0');
INSERT INTO [t21823] VALUES (82,'السؤال (٨٠) : فضيلة الشيخ، ما هي النجاسات الحكمية من حيث المفهوم والأنواع؟','82','66','0');
INSERT INTO [t21823] VALUES (83,'السؤال (٨١) : فضيلة الشيخ، ما هي الأحكام المتعلقة بالحيض والنفاس؟','83','66','0');
INSERT INTO [t21823] VALUES (84,'السؤال (٨٢) : فضيلة الشيخ، بالنسبة للمرأة إذا طهرت من النفاس أو إذا لم ينزل منها الدم فهل تعتبر نفساء؟','84','66','0');
INSERT INTO [t21823] VALUES (85,'السؤال (٨٣) : فضيلة الشيخ، هل يجوز للمرأة أن تأخذ ما يمنع عنها الحيض أثناء حجها حتى تتمكن من أداء الحج، كالحبوب المانعة للحمل أو أي نوع من أنواع ما يتطبب به؟','85','66','0');
INSERT INTO [t21823] VALUES (86,'السؤال (٨٤) : فضيلة الشيخ، لكن إذا ثبت ضررها فما حكمها؟','86','66','0');
INSERT INTO [t21823] VALUES (87,'فتاوى الصلاة','87','0','0');
INSERT INTO [t21823] VALUES (88,'السؤال (٨٥) : فضيلة الشيخ، ما حكم الصلاة؟ وما أهميتها؟','88','87','0');
INSERT INTO [t21823] VALUES (89,'السؤال (٨٦) : فضيلة الشيخ، على من تجب الصلاة؟','89','87','0');
INSERT INTO [t21823] VALUES (90,'السؤال (٨٧) : فضيلة الشيخ، عرفنا حكم الصلاة وعلى من تجب، فما حكم ترك الصلاة؟','90','87','0');
INSERT INTO [t21823] VALUES (91,'السؤال (٨٨) : فضيلة الشيخ، ما الذي يترتب على الحكم بالكفر على تارك الصلاة؟','91','87','0');
INSERT INTO [t21823] VALUES (92,'السؤال (٨٩) : فضيلة الشيخ، ما هي شروط الصلاة؟ وماذا يترتب عليها؟','92','87','0');
INSERT INTO [t21823] VALUES (93,'السؤال (٩٠) : فضيلة الشيخ، قبل أن نخرج من الشرط الثاني، قلتم إذا كان فيه خرق يناقش فيه، كيف يناقش فيه؟','93','87','0');
INSERT INTO [t21823] VALUES (94,'السؤال (٩١) : فضيلة الشيخ، نحب أن نسأل إذا لم يعلم الإمام أن وضوءه منتقض إلا بعد انتهاء الصلاة، فهل يلزمه الإعادة هو والمأمومون أم لا؟','94','87','0');
INSERT INTO [t21823] VALUES (95,'السؤال (٩٢) : فضيلة الشيخ، أيضا ربما يستفسر: هل يجوز أن يؤم متيمم متوضئا؟','95','87','0');
INSERT INTO [t21823] VALUES (96,'السؤال (٩٣) : فضيلة الشيخ، نريد أن نستكمل معكم بقية شروط الصلاة، وقد ذكرتم منها: الوقت، وستر العورة، والطهارة، واستقبال القبلة؟','96','87','0');
INSERT INTO [t21823] VALUES (97,'السؤال (٩٤) : فضيلة الشيخ، ما هي صفة الصلاة المفروضة؟','97','87','0');
INSERT INTO [t21823] VALUES (98,'السؤال: (٩٥) : فضيلة الشيخ، ذكرتم- جزاكم الله خيرا - بالتفصيل وضع الأيدي في القيام وفي الركوع، وكذلك في السجود، وكذلك في الجلسة بين السجدتين، لكننا لم نسمع شيئا عن وضع الرجلين، ونحن نشاهد الآن كثيرا من الناس يفرج ما بين رجليه، فيتسع ما بين مناكب المصلين. فما الصحيح في ذلك؟','98','87','0');
INSERT INTO [t21823] VALUES (99,'السؤال (٩٦) : فضيلة الشيخ، ما هي أركان الصلاة؟','99','87','0');
INSERT INTO [t21823] VALUES (100,'السؤال (٩٧) : فضيلة الشيخ، ما حكم من ترك ركنا من هذه الأركان؟','100','87','0');
INSERT INTO [t21823] VALUES (101,'السؤال (٩٨) : فضيلة الشيخ، هذا بالنسبة لمن تأكد لديه أنه ترك ركنا من الأركان، لكن لو شك في تركه ماذا يفعل؟','101','87','0');
INSERT INTO [t21823] VALUES (102,'السؤال (٩٩) : فضيلة الشيخ، بعض الناس يأتي بعد إقامة الصلاة، ويدخل مع الإمام، وينسى عدد الركعات التي فاتته، ثم يقتدي بمن في جانبه ممن دخل الصلاة معه فما حكم ذلك؟','102','87','0');
INSERT INTO [t21823] VALUES (103,'السؤال (١٠٠) : فضيلة الشيخ، عرفنا صفة الصلاة وأركانها، ونود أن نعرف ما هي واجبات الصلاة؟','103','87','0');
INSERT INTO [t21823] VALUES (104,'السؤال (١٠٢) : فضيلة الشيخ، نود أن نعرف أيضا سجود السهو في الصلاة من حيث موجباته ومواضعه؟','104','87','0');
INSERT INTO [t21823] VALUES (105,'السؤال (١٠٣) : فضيلة الشيخ، لكن إذا كان سجود السهو بعد الصلاة هل يلزم أيضا سلام؟','105','87','0');
INSERT INTO [t21823] VALUES (106,'السؤال (١٠٤) : فضيلة الشيخ، وهل يجب له التشهد.','106','87','0');
INSERT INTO [t21823] VALUES (107,'السؤال (١٠٥) : فضيلة الشيخ، ما هي مبطلات الصلاة ولو على سبيل الإجمال؟','107','87','0');
INSERT INTO [t21823] VALUES (108,'السؤال (١٠٦) : فضيلة الشيخ، تحدثنا عن الصلاة، وحكمها، وشروطها، وكذلك الأركان، والواجبات، وأيضا عن السجود للسهو لها، ونود أن نسأل ونركز على حكم صلاة الجماعة؟','108','87','0');
INSERT INTO [t21823] VALUES (109,'السؤال (١٠٧) : فضيلة الشيخ، مادمنا عرفنا حكم صلاة الجماعة، فما هي علاقة المأموم بإمامه','109','87','0');
INSERT INTO [t21823] VALUES (110,'السؤال (١٠٨) : فضيلة الشيخ، لكن أي الحالات الثلاث أشد: المسابقة، أم الموافقة أم التخلف عنه؟','110','87','0');
INSERT INTO [t21823] VALUES (111,'السؤال (١٠٩) : فضيلة الشيخ، نود أن تحدثونا عن صلاة التطوع من حيث الفضل والأنواع؟','111','87','0');
INSERT INTO [t21823] VALUES (112,'السؤال (١١٠) : فضيلة الشيخ، هل هناك فرق بين صلاة الفرض والنافلة؟','112','87','0');
INSERT INTO [t21823] VALUES (113,'فتاوى الزكاة','113','0','0');
INSERT INTO [t21823] VALUES (114,'السؤال (١١١) : فضيلة الشيخ، ما المقصود بالزكاة في اللغة، وفي الشرع؟ وما العلاقة بين المفهومين؟','114','113','0');
INSERT INTO [t21823] VALUES (115,'السؤال (١١٢) : فضيلة الشيخ، ذكرتم تعريف الزكاة أو مفهوم الزكاة اللغوي والشرعي، والعلاقة بينهما، ثم تحدثتم أيضا عن الآثار التي تنعكس على الفرد، لكن أيضا مادمنا عرفنا الآثار التي تنعكس على الفرد، فما هي الآثار التي تنعكس على المجتمع، وعلى الاقتصاد الإسلامي أيضا؟','115','113','0');
INSERT INTO [t21823] VALUES (116,'السؤال (١١٣) : فضيلة الشيخ، حبذا لو عرفنا شروط وجوب الزكاة؟','116','113','0');
INSERT INTO [t21823] VALUES (117,'السؤال (١١٤) : فضيلة الشيخ، ذكرتم من شروط وجوب الزكاة وعددتم منها أن يكون مالك المال حرا، وتحدثتم عن مال المملوك وأن المملوك لا يؤدي أو لا يجب عليه زكاة، لأن المال مال مالكه، لكن: هل يعفى المال من التزكية أم يدفع المالك من المال؟','117','113','0');
INSERT INTO [t21823] VALUES (118,'السؤال (١١٥) : فضيلة الشيخ، ما هي الأموال التي تجب فيها الزكاة، ومقدار الزكاة في كل نوع منها؟','118','113','0');
INSERT INTO [t21823] VALUES (119,'السؤال (١١٦) : فضيلة الشيخ، بالنسبة للفواكه التي لا زكاة فيها، هل إذا باعها الإنسان وجب عليه الزكاة في قيمتها"؟.','119','113','0');
INSERT INTO [t21823] VALUES (120,'السؤال (١١٧) : فضيلة الشيخ، لكن بالنسبة للأراضي التي اشتراها أصحابها، وكسدت في أيديهم نظرا لقلة قيمتها، فهم يقدرونها بتقديرات عالية، مع أنها لا تساوي إلا القليل في السوق، فكيف تزكى هذه الأراضي؟','120','113','0');
INSERT INTO [t21823] VALUES (121,'السؤال (١١٨) : فضيلة الشيخ، كيف تزكى الديون التي في ذمم الناس؟','121','113','0');
INSERT INTO [t21823] VALUES (122,'السؤال (١١٩) : فضيلة الشيخ، هل يجوز خرص التجارة أو عروض التجارة إذا تعذر إحصاؤها أو شق على التجار؟','122','113','0');
INSERT INTO [t21823] VALUES (123,'السؤال (١٢٠) : فضيلة الشيخ، هل تجب الزكاة في مال غير المكلف، كالصغير والمجنون؟','123','113','0');
INSERT INTO [t21823] VALUES (124,'السؤال (١٢١) : فضيلة الشيخ، ما هي المصارف التي يجب أن تصرف فيها الزكاة؟','124','113','0');
INSERT INTO [t21823] VALUES (125,'السؤال (١٢٢) : فضيلة الشيخ، ما حكم جعل الزكاة في الأقارب المحتاجين؟','125','113','0');
INSERT INTO [t21823] VALUES (126,'السؤال (١٢٣) : فضيلة الشيخ، نريد التمثيل أيضا لدفع المال للوالد أو الوالدة، فيما لا يجب على الإنسان؟','126','113','0');
INSERT INTO [t21823] VALUES (127,'السؤال (١٢٤) : فضيلة الشيخ، هل يجوز إسقاط الدين عن المدين ويكون ذلك من الزكاة؟','127','113','0');
INSERT INTO [t21823] VALUES (128,'السؤال (١٢٥) : فضيلة الشيخ، هل يجوز دفعها للفقير المدين بشرط أن يردها للدافع؟','128','113','0');
INSERT INTO [t21823] VALUES (129,'السؤال (١٢٦) : فضيلة الشيخ، ما حكم الزكاة في الإسلام؟','129','113','0');
INSERT INTO [t21823] VALUES (130,'فتاوى الصيام','130','0','0');
INSERT INTO [t21823] VALUES (131,'السؤال (١٢٧) : فضيلة الشيخ، ما المقصود بالصيام لغة وشرعا؟','131','130','0');
INSERT INTO [t21823] VALUES (132,'السؤال (١٢٨) : فضيلة الشيخ، ما هي أقسام الصيام؟','132','130','0');
INSERT INTO [t21823] VALUES (133,'السؤال (١٢٩) : فضيلة الشيخ، ما حكم صيام شهر رمضان؟','133','130','0');
INSERT INTO [t21823] VALUES (134,'السؤال (١٣٠) : فضيلة الشيخ، ما هي مكانة الصيام في الدين، وفضله في العبادة وخاصة في شهر رمضان؟','134','130','0');
INSERT INTO [t21823] VALUES (135,'السؤال (١٣١) : فضيلة الشيخ، ما حكم الفطر في نهار رمضان بدون عذر؟','135','130','0');
INSERT INTO [t21823] VALUES (136,'السؤال (١٣٢) : فضيلة الشيخ، بماذا يثبت شهر رمضان؟','136','130','0');
INSERT INTO [t21823] VALUES (137,'السؤال (١٣٣) : فضيلة الشيخ، ما حكم من رأى الهلال وحده ولم يصم معه الناس؟','137','130','0');
INSERT INTO [t21823] VALUES (138,'السؤال (١٣٤) : فضيلة الشيخ، ما هي أركان الصيام؟','138','130','0');
INSERT INTO [t21823] VALUES (139,'السؤال (١٣٥) : فضيلة الشيخ، على من يجب الصيام؟','139','130','0');
INSERT INTO [t21823] VALUES (140,'السؤال (١٣٦) : فضيلة الشيخ، ما حكم صيام تارك الصلاة؟','140','130','0');
INSERT INTO [t21823] VALUES (141,'السؤال (١٣٧) : فضيلة الشيخ، ما حكم من يصوم ويصلي إذا جاء رمضان لكن إذا انسلخ رمضان انسلخ من الصلاة الصيام؟','141','130','0');
INSERT INTO [t21823] VALUES (142,'السؤال (١٣٨) : فضيلة الشيخ، ما حكم من يصوم أياما ويفطر أخرى؟','142','130','0');
INSERT INTO [t21823] VALUES (143,'السؤال (١٣٩) : فضيلة الشيخ، إذا كان الإنسان قد ترك أشهرا من رمضان بعد بلوغه ثم التزم الآن، فهل يلزمه قضاء هذه الأشهر؟','143','130','0');
INSERT INTO [t21823] VALUES (144,'السؤال (١٤٠) : فضيلة الشيخ، ما هي الأعذار المبيحة للفطر في شهر رمضان المبارك؟','144','130','0');
INSERT INTO [t21823] VALUES (145,'السؤال (١٤١) : فضيلة الشيخ، لكن ما الفرق بين هذه الحالة لو جاء العلم بدخول رمضان في أثناء النهار؟','145','130','0');
INSERT INTO [t21823] VALUES (146,'السؤال (١٤٢) : فضيلة الشيخ، ما هي مفسدات الصوم؟ وهل لها شروط؟','146','130','0');
INSERT INTO [t21823] VALUES (147,'السؤال (١٤٣) : فضيلة الشيخ، ما هو حكم صيام الصبي الذي لم يبلغ؟','147','130','0');
INSERT INTO [t21823] VALUES (148,'السؤال (١٤٤) : فضيلة الشيخ، ما حكم صيام من يعقل زمنا ويجن زمنا آخر، أو يعقل زمنا ويخرف ويهذري زمنا آخر؟','148','130','0');
INSERT INTO [t21823] VALUES (149,'السؤال (١٤٥) : فضيلة الشيخ، لكن لو حدث له أثناء النهار أن كان عاقلا ثم ذهب عقله؟','149','130','0');
INSERT INTO [t21823] VALUES (150,'السؤال (١٤٦) : فضيلة الشيخ، ما حكم صيام يوم الشك خشية أنه من رمضان؟','150','130','0');
INSERT INTO [t21823] VALUES (151,'السؤال (١٤٧) : فضيلة الشيخ، ما حكم من صام في بلد مسلم ثم انتقل إلى بلد آخر تأخر أهله عن البلد الأول ولزم من متابعتهم صيام أكثر من ثلاثين يوم أو العكس؟','151','130','0');
INSERT INTO [t21823] VALUES (152,'السؤال (١٤٨) : فضيلة الشيخ، لكن قد يقول قائل: لماذا يؤمر بصيام أكثر من ثلاثين يوما في الأولى ويقضي في الثانية؟','152','130','0');
INSERT INTO [t21823] VALUES (153,'السؤال (١٤٩) : فضيلة الشيخ، ما هي آداب الصيام؟','153','130','0');
INSERT INTO [t21823] VALUES (154,'السؤال (١٥٠) : فضيلة الشيخ، ما حكم أكل وشرب من شك في طلوع الفجر؟','154','130','0');
INSERT INTO [t21823] VALUES (155,'السؤال (١٥١) : فضيلة الشيخ، أيضا كثير من الناس يأكل أثناء أذان الفجر حتى يكتمل الأذان، فما حكم هذا الأكل الذي يكون في أثناء الأذان؟','155','130','0');
INSERT INTO [t21823] VALUES (156,'السؤال (١٥٢) : فضيلة الشيخ، ما حكم العوم للصائم أو الغوص في الماء؟','156','130','0');
INSERT INTO [t21823] VALUES (157,'السؤال (١٥٣) : فضيلة الشيخ، ما حكم القطرة والمرهم في العين؟','157','130','0');
INSERT INTO [t21823] VALUES (158,'السؤال (١٥٤) : فضيلة الشيخ، ما حكم ضرب الأسنان بالفرشاة والمعجون بعد أذان الفجر أو أثناء الأذان؟','158','130','0');
INSERT INTO [t21823] VALUES (159,'السؤال (١٥٥) : فضيلة الشيخ، ما حكم التحليل والتبرع بالدم للصائم؟','159','130','0');
INSERT INTO [t21823] VALUES (160,'السؤال (١٥٦) : فضيلة الشيخ، هناك بعض الناس من الصوام يجد نشوفة في أنفه أو في شفاهه، فيستعمل بعض المراهم أو المرطبات لذلك فما حكمه؟','160','130','0');
INSERT INTO [t21823] VALUES (161,'السؤال (١٥٧) : فضيلة الشيخ، لكن لو وصل شيء من غير قصد؟','161','130','0');
INSERT INTO [t21823] VALUES (162,'السؤال (١٥٨) : فضيلة الشيخ، ما حكم حقن الإبر في العضل وفي الوريد؟','162','130','0');
INSERT INTO [t21823] VALUES (163,'السؤال (١٥٩) : فضيلة الشيخ، ما حكم المبالغة في المضمضة والاستنشاق في نهار رمضان للصائم؟','163','130','0');
INSERT INTO [t21823] VALUES (164,'السؤال (١٦٠) : فضيلة الشيخ، ما حكم شم الطيب سواء كان من الرذاذ (البخاخ) أو شم الطيب الذي هو البخور؟','164','130','0');
INSERT INTO [t21823] VALUES (165,'السؤال (١٦١) : فضيلة الشيخ، ربما يقال: ما الفرق بين البخور والقطرة التي تنزل إلى الحلق ويطعم بها؟','165','130','0');
INSERT INTO [t21823] VALUES (166,'السؤال (١٦٢) : فضيلة الشيخ، ما حكم من أكل أو شرب ناسيا؟ وكيف يصنع إذا ذكر أثناء ذلك؟','166','130','0');
INSERT INTO [t21823] VALUES (167,'السؤال (١٦٣) : فصيلة الشيخ، ينتشر عند كثير من الناس أن الإنسان إذا رأى صائما يأكل ألا يذكره، فما مدى صحة هذا الكلام، وكيف يصنع من يرى صائما يأكل؟','167','130','0');
INSERT INTO [t21823] VALUES (168,'السؤال (١٦٤) : فضيلة الشيخ، ما حكم خروج الدم من الصائم من فمه أو أنفه أو بقية جسمه؟','168','130','0');
INSERT INTO [t21823] VALUES (169,'السؤال (١٦٥) : فضيلة الشيخ، فإن تسبب في خروج الدم كأن يخلع ضرسه مثلا؟','169','130','0');
INSERT INTO [t21823] VALUES (170,'السؤال (١٦٦) : فضيلة الشيخ، إذا أفطر في الأرض مثلا ثم أقعلت الطائرة وبانت له الشمس فما الحكم؟','170','130','0');
INSERT INTO [t21823] VALUES (171,'السؤال (١٦٧) : فضيلة الشيخ، ما حكم الجماع في نهار رمضان ذاكرا أو ناسيا؟ وما الذي يلزمه؟','171','130','0');
INSERT INTO [t21823] VALUES (172,'السؤال (١٦٨) : فضيلة الشيخ، إذا تعدد الجماع في اليوم أو في شهر رمضان، فهل تتعدد هذه الكفارة؟','172','130','0');
INSERT INTO [t21823] VALUES (173,'السؤال (١٦٩) : فضيلة الشيخ، ما حكم صيام المسافر إذا شق عليه؟','173','130','0');
INSERT INTO [t21823] VALUES (174,'السؤال (١٧٠) : فضيلة الشيخ، هل للفطر في السفر أيام معدودة؟','174','130','0');
INSERT INTO [t21823] VALUES (175,'السؤال (١٧١) : فضيلة الشيخ، يعني لو كان الإنسان يريد أن يسافر مثلا أو يبقى في مدينة غير مدينته أكثر من خمسة أيام أو ستة أيام؟','175','130','0');
INSERT INTO [t21823] VALUES (176,'السؤال (١٧٢) : فضيلة الشيخ، كثير من المسلمين يعتمر في شهر رمضان المبارك، لكنه يتحرج عن الإفطار؛ لأنه ذهب لعبادة، فما حكم صيام المعتمر في رمضان أثناء بقائه في مكة؟','176','130','0');
INSERT INTO [t21823] VALUES (177,'السؤال (١٧٣) : فضيلة الشيخ، ما حكم السفر في شهر رمضان من أجل الإفطار؟ وكيف يكون ذلك؟','177','130','0');
INSERT INTO [t21823] VALUES (178,'السؤال (١٧٤) : فضيلة الشيخ، ما حكم قضاء الفائت من رمضان، ومتى يكون ذلك؟','178','130','0');
INSERT INTO [t21823] VALUES (179,'السؤال (١٧٥) : فضيلة الشيخ، هناك بعض المسلمين يعتبرون العبادة إذا فاتت أنها تسقط، فإذا فاتت الصلاة عن مكانها لا تؤدى، وإذا فات رمضان أو فات شيء من رمضان لا يصومونه، فما حكم صيام الفائت من رمضان؟','179','130','0');
INSERT INTO [t21823] VALUES (180,'السؤال (١٧٦) : فضيلة الشيخ، إذا أفطر الإنسان في شهر رمضان ثم أتى رمضان الثاني دون عذر في قضاء هذا الفائت فهل يلزمه شيء مع الأداء؟','180','130','0');
INSERT INTO [t21823] VALUES (181,'السؤال (١٧٧) : فضيلة الشيخ، هل هناك فوارق بين الأداء والقضاء في شهر رمضان؟','181','130','0');
INSERT INTO [t21823] VALUES (182,'السؤال (١٧٨) : فضيلة الشيخ، ما حكم من مات وعليه قضاء من شهر رمضان؟','182','130','0');
INSERT INTO [t21823] VALUES (183,'السؤال (١٧٩) : فضيلة الشيخ، إذا صام المسلم بعض رمضان ثم توفي عن بقيته، فهل يلزم وليه أن يكمل عنه؟','183','130','0');
INSERT INTO [t21823] VALUES (184,'السؤال (١٨٠) : فضيلة الشيخ، مما يتعبد أو يتقرب به إلى الله عز وجل في شهر رمضان التراويح، فما المقصود بالتراويح والتهجد؟','184','130','0');
INSERT INTO [t21823] VALUES (185,'السؤال (١٨١) : فضيلة الشيخ، نود الإشارة إلى بعض الأخطاء التي تقع في صلاة التراويح؟','185','130','0');
INSERT INTO [t21823] VALUES (186,'السؤال (١٨٢) : فضيلة الشيخ: هل يلزم المحافظة على صلاة التراويح في جميع رمضان؟','186','130','0');
INSERT INTO [t21823] VALUES (187,'السؤال (١٨٣) : فضيلة الشيخ، بعض الأئمة يبكي بكاء شديدا وينحب أيضا، وهناك من يؤاخذه على ذلك ويرى أنه تكلف، فما حكم هذا العمل، وما حكم أيضا من يؤاخذ الإمام على هذا العمل؟','187','130','0');
INSERT INTO [t21823] VALUES (188,'السؤال (١٨٤) : فضيلة الشيخ، بعض المأمومين يحضر مصحفا في رمضان لمتابعة الإمام في صلاة الليل، وقد يكون الإمام لا يحتاج إلى من يفتح عليه لأنه يقرأ من مصحف أيضا، فما حكم ذلك؟','188','130','0');
INSERT INTO [t21823] VALUES (189,'السؤال (١٨٥) : فضيلة الشيخ، بعض الناس يحيون ليلة القدر بالصلاة والعبادة ولا يحيون غيرها في رمضان، فهل هذا موافق للصواب؟','189','130','0');
INSERT INTO [t21823] VALUES (190,'السؤال (١٨٦) : فضيلة الشيخ، ما المقصود بالاعتكاف وما حكمه؟','190','130','0');
INSERT INTO [t21823] VALUES (191,'السؤال (١٨٧) : فضيلة الشيخ، هل الاعتكاف له أقسام أم أنه قسم واحد؟','191','130','0');
INSERT INTO [t21823] VALUES (192,'السؤال (١٨٨) : فضيلة الشيخ، هل الاعتكاف له زمان محدد، أي أنه يقتصر على رمضان أم يجوز في غير رمضان؟','192','130','0');
INSERT INTO [t21823] VALUES (193,'السؤال (١٨٩) : فضيلة الشيخ، هل للاعتكاف شروط محددة أو أركان أيضا محددة؟','193','130','0');
INSERT INTO [t21823] VALUES (194,'السؤال (١٩٠) : فضيلة الشيخ، إذا هل يجوز للمرأة أن تعتكف في مسجدها، في منزلها؟','194','130','0');
INSERT INTO [t21823] VALUES (195,'السؤال (١٩١) : فضيلة الشيخ، ما الذي يستحب في الاعتكاف وما الذي يكره له أيضا؟','195','130','0');
INSERT INTO [t21823] VALUES (196,'السؤال (١٩٢) : فضيلة الشيخ، ما الذي يباح للمعتكف؟','196','130','0');
INSERT INTO [t21823] VALUES (197,'السؤال (١٩٣) : فضيلة الشيخ، هل يلتزم المعتكف مكانا محددا في المسجد أو يجوز له التنقل في أنحائه؟','197','130','0');
INSERT INTO [t21823] VALUES (198,'السؤال: (١٩٤) فضيلة الشيخ، ما المقصود بزكاة الفطر، وهل لها سبب؟','198','130','0');
INSERT INTO [t21823] VALUES (199,'السؤال (١٩٥) : فضيلة الشيخ، ما حكم زكاة الفطر؟','199','130','0');
INSERT INTO [t21823] VALUES (200,'السؤال (١٩٦) : فضيلة الشيخ على من تجب زكاة الفطر وعلى من تستحب؟','200','130','0');
INSERT INTO [t21823] VALUES (201,'السؤال (١٩٧) : فضيلة الشيخ، ما حكم منعها وكيف يعامل مانعها؟','201','130','0');
INSERT INTO [t21823] VALUES (202,'السؤال (١٩٨) : فضيلة الشيخ، ما هي مصارف زكاة الفطر؟','202','130','0');
INSERT INTO [t21823] VALUES (203,'السؤال (١٩٩) : فضيلة الشيخ، هل يجوز إعطاؤها للعمال من غير المسلمين؟','203','130','0');
INSERT INTO [t21823] VALUES (204,'السؤال (٢٠٠) : فضيلة الشيخ، ما حكم نقلها إلى البلدان البعيدة بحجة وجود الفقراء الكثيرين فيها؟','204','130','0');
INSERT INTO [t21823] VALUES (205,'السؤال (٢٠١) : فضيلة الشيخ، ما حكم وضعها عند الجار حتى يأتي الفقير دون توكيل من الفقير؟','205','130','0');
INSERT INTO [t21823] VALUES (206,'السؤال (٢٠٢) : فضيلة الشيخ، لو وضعها عند جاره ولم يأت من يستحقها قبل العيد وفات وقتها فما الحكم؟','206','130','0');
INSERT INTO [t21823] VALUES (207,'السؤال (٢٠٣) : فضيلة الشيخ، في هذه الحال هل يعيدها إلى ماله أو يلزمه إخراجها؟','207','130','0');
INSERT INTO [t21823] VALUES (208,'السؤال (٢٠٤) : فضيلة الشيخ، ما الذي يقوله المسلم إذا رئي هلال شوال قبل صلاة العيد؟','208','130','0');
INSERT INTO [t21823] VALUES (209,'السؤال (٢٠٥) : فضيلة الشيخ، ما صفة التكبير والتهليل أثابكم الله؟','209','130','0');
INSERT INTO [t21823] VALUES (210,'فتاوى الحج','210','0','0');
INSERT INTO [t21823] VALUES (211,'السؤال (٢٠٦) : فضيلة الشيخ، ما هو النسك وعلى ماذا يطلق؟','211','210','0');
INSERT INTO [t21823] VALUES (212,'السؤال (٢٠٧) : فضيلة الشيخ، ما هو حكم الحج؟','212','210','0');
INSERT INTO [t21823] VALUES (213,'السؤال (٢٠٨) : فضيلة الشيخ، ما حكم العمرة؟','213','210','0');
INSERT INTO [t21823] VALUES (214,'السؤال (٢٠٩) : فضيلة الشيخ، وجوب الحج هل هو على الفور، أم على التراخي؟','214','210','0');
INSERT INTO [t21823] VALUES (215,'السؤال (٢١٠) : فضيلة الشيخ، ما هي شروط وجوب الحج والعمرة؟','215','210','0');
INSERT INTO [t21823] VALUES (216,'السؤال (٢١١) : فضيلة الشيخ، ما دمنا عرفنا شروط الوجوب للحج والعمرة، فما هي شروط الإجزاء؟','216','210','0');
INSERT INTO [t21823] VALUES (217,'السؤال (٢١٢) : فضيلة الشيخ، حبذا لو أشرتم ولو بإشارات سريعة إلى أبرز آداب السفر إلى الحج؟','217','210','0');
INSERT INTO [t21823] VALUES (218,'السؤال (٢١٣) : فضيلة الشيخ، ماذا ينبغي أن يستعد به المسلم لحجه سواء كان قبل السفر أو في أثناء السفر؟','218','210','0');
INSERT INTO [t21823] VALUES (219,'السؤال (٢١٤) : فضيلة الشيخ، لكن أليس هناك استعداد معنوي غير الاستعداد المادي؟','219','210','0');
INSERT INTO [t21823] VALUES (220,'السؤال (٢١٥) : فضيلة الشيخ، بالنسبة للمواقيت، ما هي مواقيت الحج الزمانية؟','220','210','0');
INSERT INTO [t21823] VALUES (221,'السؤال (٢١٦) : فضيلة الشيخ، لكن ما حكم الإحرام بالحج قبل دخول هذه المواقيت الزمانية؟','221','210','0');
INSERT INTO [t21823] VALUES (222,'السؤال (٢١٧) : فضيلة الشيخ، عرفنا مواقيت الحج الزمانية، فما هي مواقيت الحج المكانية؟','222','210','0');
INSERT INTO [t21823] VALUES (223,'السؤال (٢١٨) : فضيلة الشيخ، ما حكم الإحرام بالحج قبل هذه المواقيت المكانية؟','223','210','0');
INSERT INTO [t21823] VALUES (224,'السؤال (٢١٩) : فضيلة الشيخ، ما حكم من تجاوز الميقات بدون إحرام؟','224','210','0');
INSERT INTO [t21823] VALUES (225,'السؤال (٢٢٠) : فضيلة الشيخ، ما الفرق بين الإحرام كواجب، والإحرام كركن من أركان الحج؟','225','210','0');
INSERT INTO [t21823] VALUES (226,'السؤال (٢٢١) : فضيلة الشيخ، لكن نية الدخول في النسك هل هي التي يتلفظ بها في التلبية؟','226','210','0');
INSERT INTO [t21823] VALUES (227,'السؤال (٢٢٢) : فضيلة الشيخ، نود أيضا أن تبينوا لنا كيفية إحرام القادم إلى مكة جوا؟','227','210','0');
INSERT INTO [t21823] VALUES (228,'السؤال (٢٢٣) : فضيلة الشيخ، ما هي أركان الحج؟','228','210','0');
INSERT INTO [t21823] VALUES (229,'السؤال (٢٢٤) : فضيلة الشيخ، ما هي أركان العمرة، حيث إنها في التمتع تسبق الحج؟','229','210','0');
INSERT INTO [t21823] VALUES (230,'السؤال (٢٢٥) : فضيلة الشيخ، ما هي أركان الحج؟','230','210','0');
INSERT INTO [t21823] VALUES (231,'السؤال (٢٢٦) : فضيلة الشيخ، ما حكم الإخلال بشيء من هذه الأركان؟','231','210','0');
INSERT INTO [t21823] VALUES (232,'السؤال (٢٢٧) : فضيلة الشيخ، ما هي واجبات الحج؟','232','210','0');
INSERT INTO [t21823] VALUES (233,'السؤال (٢٢٨) : فضيلة الشيخ، ما حكم الإخلال بشيء من واجبات الحج أو العمرة؟','233','210','0');
INSERT INTO [t21823] VALUES (234,'السؤال (٢٢٩) فضيلة الشيخ، تحدثتم عن صفة التمتع في حديثكم عن صفة الحج، حبذا أيضا لو تحدثتم عن صفة القران؟','234','210','0');
INSERT INTO [t21823] VALUES (235,'السؤال (٢٣٠) فضيلة الشيخ، عرفنا صفة الحج، وعرفنا التمتع والقران والإفراد، وقلتم في الإفراد: إن المسلم يأتي بالحج وحده ولا يأتي بعمرة معه، لكننا نرى كثيرا من الناس إذا انتهى من الإفراد اعتمر، فما حكم هذا العمل؟','235','210','0');
INSERT INTO [t21823] VALUES (236,'السؤال (٢٣١) : فضيلة الشيخ، لكن ما الأولى بالنسبة لهذا الحاج الذي يعرف أن الإتيان إلى مكة يصعب عليه؟','236','210','0');
INSERT INTO [t21823] VALUES (237,'السؤال (٢٣٢) : لكن أليس الأولى أن يأتي مثلا متمتعا أو قارنا ليسلم من المحظور؟','237','210','0');
INSERT INTO [t21823] VALUES (238,'السؤال (٢٣٣) : فضيلة الشيخ، ما حكم الانتقال من نسك إلى نسك آخر؟','238','210','0');
INSERT INTO [t21823] VALUES (239,'السؤال (٢٣٤) : فضيلة الشيخ، هل يجوز أن يتحول من التمتع إلى الإفراد؟','239','210','0');
INSERT INTO [t21823] VALUES (240,'السؤال (٢٣٥) : فضيلة الشيخ، لو تحدثنا أيضا عن النيابة الكلية في الحج من حيث الأحكام والضوابط؟','240','210','0');
INSERT INTO [t21823] VALUES (241,'السؤال (٢٣٦) : فضيلة الشيخ، ما هي شروط النائب في الحج؟','241','210','0');
INSERT INTO [t21823] VALUES (242,'السؤال (٢٣٧) فضيلة الشيخ، ما حكم من أخذ نقودا ليحج عن غيره، وليس في نيته إلا جمع الدراهم؟','242','210','0');
INSERT INTO [t21823] VALUES (243,'السؤال (٢٣٨) : فضيلة الشيخ، إذا أمن هذا، هل يمكن أن يقع ثواب بعض الأعمال للنائب؟','243','210','0');
INSERT INTO [t21823] VALUES (244,'السؤال (٢٣٩) : فضيلة الشيخ، حبذا لو حدثتمونا فضيلتكم عن النيابة الجزئية في الحج؟','244','210','0');
INSERT INTO [t21823] VALUES (245,'السؤال (٢٤٠) : فضيلة الشيخ، ذكرتم أن التوكيل في الجزئية يكون مثلا في الطواف أو الرمي أو الوقوف أو ما أشبه ذلك، فهل إذا جاز التوكيل في الرمي مثلا يقاس عليه بقية أجزاء الحج؟','245','210','0');
INSERT INTO [t21823] VALUES (246,'السؤال (٢٤١) : فضيلة الشيخ، لكن إذا جاز التوكيل في الرمي، هل هناك شروط للنائب والمنيب؟','246','210','0');
INSERT INTO [t21823] VALUES (247,'السؤال (٢٤٢) : فضيلة الشيخ، إذا عجز الحاج عن إكمال النسك فماذا يصنع؟','247','210','0');
INSERT INTO [t21823] VALUES (248,'السؤال (٢٤٣) : فضيلة الشيخ، هذا ما يتعلق بالحاج إذا عجز عن النسك، لكن لو توفي الحاج أثناء تلبسه بالنسك ما الحكم؟','248','210','0');
INSERT INTO [t21823] VALUES (249,'السؤال (٢٤٤) : فضيلة الشيخ، لكن هل يقتصر هذا الحكم على الوقت الذي يلبى فيه، يعني: قبل رمي جمرة العقبة أم يشمل جميع الحج؟','249','210','0');
INSERT INTO [t21823] VALUES (250,'السؤال (٢٤٥) : فضيلة الشيخ، ذكرتم الاشتراط إذا عجز الحاج عن إكمال النسك، نود أيضا أن نعرف حكم الاشتراط، وما هي صفته؟','250','210','0');
INSERT INTO [t21823] VALUES (251,'السؤال (٢٤٦) : فضيلة الشيخ، لكن بالنسبة للمشترط هل يلزمه أن يأتي بالصيغة التي وردت عن الرسول صلى الله عليه وسلم، أم يشترط بأي كلام يعبر به عما في نفسه؟','251','210','0');
INSERT INTO [t21823] VALUES (252,'السؤال (٢٤٧) : فضيلة الشيخ، ما هي محظورات الإحرام؟','252','210','0');
INSERT INTO [t21823] VALUES (253,'السؤال (٢٤٨) : فصيلة الشيخ، قلتم: إنه لا يستر المحرم رأسه أو لا يضع على رأسه ملاصقا؛ كالغترة والطاقية، هل يشمل ذلك أيضا وضع قطعة ورق أو كرتون أو بطانية على رأسه؟','253','210','0');
INSERT INTO [t21823] VALUES (254,'السؤال (٢٤٩) : فضيلة الشيخ، ما الفرق بين النقاب والبرقع وهل يجوز للمرأة المحرمة أن تلبس البرقع؟','254','210','0');
INSERT INTO [t21823] VALUES (255,'السؤال (٢٥٠) : فضيلة الشيخ: قلتم بوجوب ستر المحرمة وجهها إذا حضر الرجال، فهل تستر وجهها بالنقاب أم بشيء آخر؟','255','210','0');
INSERT INTO [t21823] VALUES (256,'السؤال (٢٥١) : فضيلة الشيخ، فصلتم في الجماع كمحظور من محظورات الإحرام، وذكرتم أنه يترتب عليه خمسة أمور، لكن بقية المحظورات ما ذكرتم لنا حكم من تلبس بشيء منها؟','256','210','0');
INSERT INTO [t21823] VALUES (257,'السؤال (٢٥٢) : فضيلة الشيخ، ما الذي يجب على من ارتكب محظورا من هذه المحظورات؟','257','210','0');
INSERT INTO [t21823] VALUES (258,'السؤال (٢٥٣) : فضيلة الشيخ، ما حكم من ارتكب محظورا من هذه المحظورات ناسيا أو جاهلا؟','258','210','0');
INSERT INTO [t21823] VALUES (259,'السؤال (٢٥٤) : فضيلة الشيخ، ما حكم استبدال المحرم لباس الإحرام؟','259','210','0');
INSERT INTO [t21823] VALUES (260,'السؤال (٢٥٥) : فضيلة الشيخ، الترفه ممنوع منه المحرم؟ كتقليم الظافر وغيره، لكن هل يجوز للمحرم أن يغتسل من أجل النظافة؟','260','210','0');
INSERT INTO [t21823] VALUES (261,'السؤال (٢٥٦) : فضيلة الشيخ، بالنسبة للمحرم والنبات الذي ينبت في مكة المكرمة، في الحرم، ما حكم قلع هذا النبات والتعرض له بشيء من الإتلاف؟','261','210','0');
INSERT INTO [t21823] VALUES (262,'السؤال (٢٥٧) : فضيلة الشيخ، إذا جاء الحاج إلى البيت، وطاف وتحلل من العمرة، ومكث في مكة، فمتى يحرم بالحج، ومن أين يحرم؟','262','210','0');
INSERT INTO [t21823] VALUES (263,'السؤال (٢٥٨) : فضيلة الشيخ، لكن هل يلزم المحرم في يوم التروية أن يطوف بالبيت، أو يحرم من البيت؟','263','210','0');
INSERT INTO [t21823] VALUES (264,'السؤال (٢٥٩) : فضيلة الشيخ، عرفنا في صفة الحج أن الحاج يخرج من منى في اليوم التاسع من ذي الحجة ضحى، لكن لو لم يدرك الوقوف بعرفة إلا متأخرا فما الحكم؟','264','210','0');
INSERT INTO [t21823] VALUES (265,'السؤال (٢٦٠) : فضيلة الشيخ، متى يبدأ الوقوف بمزدلفة، ومتى ينتهي، وما حكمه أيضا؟','265','210','0');
INSERT INTO [t21823] VALUES (266,'السؤال (٢٦١) : فضيلة الشيخ، متى ينتهي الوقوف بمزدلفة بحيث إن الحاج لو أتى لا يعتبر واقفا بها؟','266','210','0');
INSERT INTO [t21823] VALUES (267,'السؤال (٢٦٢) : فضيلة الشيخ، ذكرتم أن من الأعمال التي يقوم بها الحاج يوم النحر المبيت بمني، لكن ما حكم هذا المبيت؟','267','210','0');
INSERT INTO [t21823] VALUES (268,'السؤال (٢٦٣) : فضيلة الشيخ، نرى بعضا من الناس يتهاونون في المبيت بمنى؛ فيقلون من البقاء فيها، ويذهبون خارجها معظم الوقت، ولا يأتون إليها إلا ساعات محدودة، فما هو المقدار الكافي للبقاء في منى أو المبيت في منى؟','268','210','0');
INSERT INTO [t21823] VALUES (269,'السؤال (٢٦٤) : فضيلة الشيخ، ما هي الآداب التي ينبغي أن يتحلى بها الحاج أثناء بقائه في منى يوم الحادي عشر، والثاني عشر، والثالث عشر لمن أراد أن يتأخر؟','269','210','0');
INSERT INTO [t21823] VALUES (270,'السؤال (٢٦٥) : فضيلة الشيخ، بعض الناس يقضي هذه الأيام في مني: إما بالاستماع إلى الملاهي، أو بالتفكه بالحديث في أعراض الناس، فما حكم هذا العمل؟','270','210','0');
INSERT INTO [t21823] VALUES (271,'السؤال (٢٦٧) : فضيلة الشيخ، أيضا بالنسبة للجمار، نود أن تذكروا لنا صفة رمي الجمار؟','271','210','0');
INSERT INTO [t21823] VALUES (272,'السؤال (٢٦٨) : فضيلة الشيخ، لكن هل هناك أدعية عند رمي الجمرات؟','272','210','0');
INSERT INTO [t21823] VALUES (273,'السؤال (٢٦٩) : فضيلة الشيخ، وهل هناك دعاء مخصوص؟','273','210','0');
INSERT INTO [t21823] VALUES (274,'السؤال (٢٧٠) : فضيلة الشيخ، هل تلزم الطهارة لرمي الجمار؟','274','210','0');
INSERT INTO [t21823] VALUES (275,'السؤال (٢٧١) : فضيلة الشيخ، ما حكم غسل الجمار؟','275','210','0');
INSERT INTO [t21823] VALUES (276,'السؤال (٢٧٢) : فضيلة الشيخ، ما حكم من نسي شيئا من أشواط الطواف أو السعي؟','276','210','0');
INSERT INTO [t21823] VALUES (277,'السؤال (٢٧٣) : فضيلة الشيخ، إذا أقيمت الصلاة وهو في الطواف أو السعي، فماذا يفعل؟','277','210','0');
INSERT INTO [t21823] VALUES (278,'السؤال (٢٧٤) : فصيلة الشيخ، لكن هل يلزمه قطع الطواف أو السعي للصلاة أو يجوز له؟','278','210','0');
INSERT INTO [t21823] VALUES (279,'السؤال (٢٧٥) : فضيلة الشيخ، إذا أذن للصلاة، وهو يسعى بين الصفا والمروة، وهو على غير طهارة، وهذا جائز، فهل يخرج خارج الحرم ليتوضأ، ويرجع ويصلي مع الناس، ويكمل سعيه، أم يبتدئه من جديد؟','279','210','0');
INSERT INTO [t21823] VALUES (280,'السؤال (٢٧٦) : فضيلة الشيخ، في أثناء الطواف يشاهد بعض الناس يتمسحون بجدار الكعبة، وبكسوتها، وبالمقام، والحجر، فما حكم ذلك العمل؟','280','210','0');
INSERT INTO [t21823] VALUES (281,'السؤال (٢٧٧) : لكن أيضا ما حكم الذين يتمسكون بأستار الكعبة، ويدعون طويلا؟','281','210','0');
INSERT INTO [t21823] VALUES (282,'السؤال (٢٧٨) : فضيلة الشيخ، لكن ما صفة هذا الالتزام هل هو تعلق بهذا الجزء من الكعبة الذي بين الحجر الأسود والبيت، أم أنه وقوف ودعاء؟','282','210','0');
INSERT INTO [t21823] VALUES (283,'السؤال (٢٧٩) : فضيلة الشيخ، ذكرتم أيضا أنه لا يجوز التمسح بزمزم، أو بشيء منها، لكن ما هي خصائص ماء زمزم؟','283','210','0');
INSERT INTO [t21823] VALUES (284,'السؤال (٢٨٠) : فضيلة الشيخ، هل من خصائص مكة أو الكعبة التبرك بأحجارها أو آثارها؟','284','210','0');
INSERT INTO [t21823] VALUES (285,'السؤال (٢٨١) : فضيلة الشيخ، أيضا يطلق على جبل عرفة: جبل الرحمة، فما حكم هذه التسمية، وهل لها أصل؟','285','210','0');
INSERT INTO [t21823] VALUES (286,'السؤال (٢٨٢) : فضيلة الشيخ، يلتزم بعض الحجاج زيارة هذا الجبل قبل الحج أو بعده، ويصلون في أعلاه، فما حكم زيارة هذا الجبل، وما حكم الصلاة فيه؟','286','210','0');
INSERT INTO [t21823] VALUES (287,'السؤال (٢٨٣) : فضيلة الشيخ، أيضا بخصوص هذا الجبل، كثير من الناس في يوم عرفة، يستقبلون الجبل ويستدبرون الكعبة، فما حكم هذا العمل، وما حكم رفع الأيدي والدعاء له؟','287','210','0');
INSERT INTO [t21823] VALUES (288,'السؤال (٢٨٤) : فضيلة الشيخ، هناك مواقف يقفها الحجاج، وأمور يفعلونها في الحج، وهذه المواقف والأمور يقع فيها أخطاء، ولعله من الترتيب أن نبدأ بالإحرام وما يقع فيه من أخطاء، إذا كان هناك أخطاء ترونها في ذلك؟','288','210','0');
INSERT INTO [t21823] VALUES (289,'السؤال (٢٨٥) : فضيلة الشيخ، بالنسبة للإحرام يوم التروية هل هناك أخطاء يرتكبها الحجاج؟ وما علاجها؟','289','210','0');
INSERT INTO [t21823] VALUES (290,'السؤال (٢٨٦) : فضيلة الشيخ، إذا انتقلنا من الإحرام، فهل هناك أخطاء تقع من الحجاج بعد الإحرام وما هي؟','290','210','0');
INSERT INTO [t21823] VALUES (291,'السؤال (٢٨٧) : فضيلة الشيخ، بقي علينا أن نعرف- أثابكم الله- الأخطاء التي تأتي عند دخول الحرم؟','291','210','0');
INSERT INTO [t21823] VALUES (292,'السؤال (٢٨٨) : فضيلة الشيخ، إذا دخل الحاج أو المعتمر أو غيرهما الحرم وأراد أن يطوف، لا شك أنه يقع هناك بعض الأخطاء، حبذا لو بينتم هذه الأخطاء التي تقع في الطواف؟','292','210','0');
INSERT INTO [t21823] VALUES (293,'السؤال (٢٨٩) : فضيلة الشيخ، كنا نتحدث عن الأخطاء التي تقع من الحجاج في الطواف وأخذنا طرفا منها، فهل لنا أن نسمع البقية؟','293','210','0');
INSERT INTO [t21823] VALUES (294,'السؤال (٢٩٠) بعض الحجاج إذا جاء إلى هذا الخط الذي وضع علامة على ابتداء الطواف، وقف طويلا، وحجر على إخوانه أن يستمروا في الطواف، فما حكم الوقوف على هذا الخط والدعاء الطويل؟','294','210','0');
INSERT INTO [t21823] VALUES (295,'السؤال (٢٩١) : فضيلة الشيخ، سألنا عن الأخطاء التي تقع من بعض الحجاج في الإحرام، ودخول الحرم، والطواف، وبقي علينا ركعتا الطواف، هل هناك أخطاء في هاتين الركعتين يقع فيها الحجاج ينبغي التنبيه عليها؟','295','210','0');
INSERT INTO [t21823] VALUES (296,'السؤال (٢٩٢) : فضيلة الشيخ، ذكرتم من الأخطاء في ركعتي الطواف أن يدعو الإنسان بعد الركعتين، وهناك أيضا من يدعو طويلا ثم يمسح وجهه، فهل هذا خاص بركعتي الطواف، أو يعم في جميع السنن التي يصليها الإنسان؟','296','210','0');
INSERT INTO [t21823] VALUES (297,'السؤال (٢٩٣) : فضيلة الشيخ، وصلنا في أسئلتنا عن الأخطاء التي تقع في الحج إلى الأخطاء التي تقع في ركعتي الطواف، وما يكون فيها أيضا من دعاء وإطالة، وما إلى ذلك، الآن نريد أن نعرف الأخطاء التي يرتكبها بعض الحجاج أو يقعون فيها عند الخروج إلى المسعى، وفي المسعى، وفي الأدعية التي تقال فيه؟','297','210','0');
INSERT INTO [t21823] VALUES (298,'السؤال (٢٩٤) : فضيلة الشيخ، من المعروف أن الصفا ضيق والمروة أضيق منه، ومع ذلك نرى النساء يصعدن إلى الصفا والمروة ويزاحمن الرجال، فهل من السنة صعود المرأة على الصفا؟','298','210','0');
INSERT INTO [t21823] VALUES (299,'السؤال (٢٩٥) : فضيلة الشيخ، أيضا ذكرتم من الأخطاء ترك السعي الشديد بين العلمين الأخضرين، وذكرتم أنه أقرب إلى الصفا، وذكرتم أن السعي يكون في الذهاب من الصفا إلى المروة، فهل يلزم أيضا السعي الشديد في العودة بين العلمين الأخضرين من المروة إلى الصفا؟','299','210','0');
INSERT INTO [t21823] VALUES (300,'السؤال (٢٩٦)) فضيلة الشيخ، أيضا ذكرتم أن من الأخطاء أن بعض الناس يدعو أو يتلو الآية: (إن الصفا والمروة..) عند الصعود إلى الصفا أو المروة كل شوط، وقلتم، إن الرسول صلى الله عليه وسلم تلا أول الآية: (إن الصفا والمروة من شعائر الله) (أبدأ بما بدأ الله به)) ، فهل يقول مثل الرسول: ((أبدأ بما بدأ الله به)) ، أو يكمل الآية؟','300','210','0');
INSERT INTO [t21823] VALUES (301,'السؤال (٢٩٧) : فضيلة الشيخ، أيضا ذكرتم من الأخطاء التي تقع في السعي: الدعاء من خلال كتاب، فهل ينطبق هذا أيضا على الذين يطوفون بالناس ويسعون بهم، ويقولون أدعية ويرددها الناس خلفهم؟','301','210','0');
INSERT INTO [t21823] VALUES (302,'السؤال (٢٩٨) : فضيلة الشيخ، بالنسبة للتقصير والحلق بعد السعي للعمرة، أو للإحلال من الحج في منى، هل هناك أخطاء؟','302','210','0');
INSERT INTO [t21823] VALUES (303,'السؤال (٢٩٩) : فضيلة الشيخ، نود أيضا أن نعرف الأخطاء التي تكون في منى، وفي المبيت فيه؟','303','210','0');
INSERT INTO [t21823] VALUES (304,'السؤال (٣٠٠) : فضيلة الشيخ، بالنسبة للأخطاء التي يمكن أن يقع فيها بعض الحجاج في الخروج إلى عرفة، والوقوف بها؟','304','210','0');
INSERT INTO [t21823] VALUES (305,'السؤال (٣٠١) : فضيلة الشيخ، هل هناك أخطاء أيضا في عرفة يفعلها الحجاج غير ما ذكرتم؟','305','210','0');
INSERT INTO [t21823] VALUES (306,'السؤال (٣٠٢) : فضيلة الشيخ، بعد أن عرفنا أهم الأخطاء التي تقع من الحجاج في عرفة نود أن نعرف أيضا إذا كان هناك أخطاء يقع فيها بعض الحجاج في الطريق إلى المزدلفة وفي المزدلفة نفسها؟','306','210','0');
INSERT INTO [t21823] VALUES (307,'السؤال (٣٠٣) : فضيلة الشيخ، هل هناك أخطاء أخرى غير ما ذكرتم في الطريق إلى مزدلفة والمبيت بها؟','307','210','0');
INSERT INTO [t21823] VALUES (308,'السؤال (٣٠٤) : فضيلة الشيخ، ما هي الأخطاء التي يرتكبها بعض الحجاج في الرمي؟','308','210','0');
INSERT INTO [t21823] VALUES (309,'السؤال (٣٠٥) : فضيلة الشيخ، ذكرتم شيئا من الأخطاء التي تقع عند الرمي منها: الظن بأن الحصى لابد أن تلتقط من مزدلفة، وأيضا غسل الحصى، وأنه خلاف السنة والظن بأن الجمرات شياطين، والرمي بالأحجار الكبيرة والرمي بالأحذية والخشب وما شابهها، فهل هناك أخطاء أخرى تقع من بعض الحجاج في الرمي ينبغي التنبيه عليها والاستفادة من تجنبها؟','309','210','0');
INSERT INTO [t21823] VALUES (310,'السؤال (٣٠٦) : فضيلة الشيخ، سألنا عن الأخطاء التي تقع عند رمي الجمار أو في الرمي، وذكرتم من هذه الأخطاء: الظن بأن الحصى لابد أن يكون من مزدلفة، وغسل الحصى، والظن بأن الجمرات شياطين، والرمي بالأحجار الكبيرة، والرمي بالأحذية والخشب وما إلى ذلك، وأيضا: الرمي دون تحقق وقوع الحصى في الحوض، والظن بأنه لابد من إصابة العمود، والتهاون أيضا في التوكيل في الرمي مع القدرة، وعكس الترتيب في الرمي ورمي الجمرات قبل الزوال، فهل هناك أخطاء أيضا غير هذه الأخطاء التي ذكرتم؟','310','210','0');
INSERT INTO [t21823] VALUES (311,'السؤال (٣٠٧) : فضيلة الشيخ، كنا قد سألنا عن الإقامة بمنى في اليوم الثامن قبل الخروج إلى عرفة، وذكرتم الأخطاء التي تقع فيها، لكن حبذا أيضا لو عرفنا الأخطاء التي قد تقع من بعض الحجاج في الإقامة بمنى في أيام التشريق؟','311','210','0');
INSERT INTO [t21823] VALUES (312,'السؤال (٣٠٨) : فضيلة الشيخ، تحدثنا عن الأخطاء التي يقع فيها الحجاج في بعض أعمال الحج، وفي بعض المشاعر أيضا، بقي علينا أن نعرف إذا كانت هناك أخطاء يقع فيها الحجاج بالنسبة للهدي؟','312','210','0');
INSERT INTO [t21823] VALUES (313,'السؤال (٣٠٩) : فضيلة الشيخ، هناك بعض الحجاج إذا أراد أن يحج، دفع نقودا لبعض المؤسسات الخيرية التي تتولى ذبح هديه في أماكن المجاعة في شرق الأرض وغربها، فما حكم هذا العمل، أثابكم الله؟','313','210','0');
INSERT INTO [t21823] VALUES (314,'السؤال (٣١٠) فضيلة الشيخ، هل ينطبق هذا الحكم على الضحايا أيضا؟','314','210','0');
INSERT INTO [t21823] VALUES (315,'السؤال (٣١١) : فضيلة الشيخ، تحدثنا عن الذين يرسلون نقودا لبعض البلاد الإسلامية ليذبح هديهم هناك أو أضحيتهم هناك، وذكرتم أن ذلك مخالف لمقاصد الشريعة، فهل من إضافة أو نصيحة تتعلق بهذا الموضوع؟','315','210','0');
INSERT INTO [t21823] VALUES (316,'السؤال (٣١٢) : فضيلة الشيخ، آخر أعمال الحج الوداع، فهل هناك أخطاء ترون أن بعض الحجاج يقعون فيها، ما هي هذه الأخطاء جزاكم الله خيرا؟','316','210','0');
INSERT INTO [t21823] VALUES (317,'السؤال (٣١٣) : فضيلة الشيخ، إذن بعد أن عرفنا الشيء الكثير عن الحج وأعماله والأخطاء التي تقع فيه، نود أن ننتقل مع الإخوة الحجاج إلى ما يهمهم في الزيارة، زيارة المسجد النبوي الشريف، فما حكم زيارة المسجد النبوي، وهل لها تعلق بالحج؟','317','210','0');
INSERT INTO [t21823] VALUES (318,'السؤال (٣١٤) : فضيلة الشيخ، أشرتم إلى زيارة قبر الرسول عليه الصلاة والسلام إذا وصل المسلم إلى المدينة المنورة وأيضا قبر صاحبيه، فما الآداب المشروعة لزيارة قبر الرسول صلى الله عليه وسلم؟','318','210','0');
INSERT INTO [t21823] VALUES (319,'السؤال (٣١٥) : فضيلة الشيخ، أيضا ما حكم زيارة بعض مقابر المدينة؛ كالبقيع، وشهداء أحد؟','319','210','0');
INSERT INTO [t21823] VALUES (320,'السؤال (٣١٦) : فضيلة الشيخ، سألنا عن حكم زيارة بعض المقابر في المدينة التي تزار، وذكرتم أن المزارات في المدينة خمسة، وقلتم: إنه لا يجوز للإنسان أن يدعو أصحاب هذه المقابر أي دعاء، لكن ما الذي يلزم من وجد في قلبه ميلا إلى طلب الشفاعة من أصحاب هذه القبور، أو قضاء الحوائج أو الشفاء، أو ما إلى ذلك؟','320','210','0');
INSERT INTO [t21823] VALUES (321,'السؤال (٣١٧) : فضيلة الشيخ، ذكرتم أن المواضع التي تزار في المدينة خمسة، لكن لم ترد إشارة مثلا للمساجد السبعة أو مسجد الغمامة، أو بعض هذه المزارات التي يزورها بعض الحجاج، فما حكم زيارتها؟','321','210','0');
INSERT INTO [t21823] VALUES (322,'السؤال (٣١٨) : فضيلة الشيخ، ما الذي ينبغي لمن وفقه الله تعالى لإتمام نسكه من الحج والعمرة؟ وما الذي ينبغي له بعد ذلك؟','322','210','0');
INSERT INTO [t21823] VALUES (323,'السؤال (٣١٩) : فضيلة الشيخ، هل هناك علامات يمكن أن تظهر على المقبولين في أداء الحج والعمرة؟','323','210','0');
INSERT INTO [t21823] VALUES (324,'السؤال (٣٢٠) : فضيلة الشيخ، ما الذي يجب على المسلم إذا انتهى من حجة وسافر عن هذه الأماكن المقدسة ما الذي يجب عليه تجاه أهله وجماعته ومن يعيش في وسطهم؟','324','210','0');
INSERT INTO [t21823] VALUES (325,'السؤال (٣٢١) : فضيلة الشيخ، ما هي آثار الحج على المسلم؟','325','210','0');
INSERT INTO [t21823] VALUES (326,'السؤال (٣٢٢) : فضيلة الشيخ، ما هي نصيحتكم لمن أدى فريضة الحج؟','326','210','0');
