INSERT INTO [t22367] VALUES (21,'باب حمد السؤال والإلحاح في طلب العلم وذم ما منع منه قال رسول الله صلى الله عليه وسلم: " شفاء العي السؤال " وقالت عائشة رضي الله عنها: " رحم الله نساء الأنصار لم يمنعهن الحياء أن يسألن عن أمر دينهن " وقالت أم سليم: " يا رسول الله، إن الله لا يستحيي من','422','0','0');
