INSERT INTO [t22367] VALUES (33,'باب جامع لنشر العلم روى سهل بن سعد، أن رسول الله صلى الله عليه وسلم قال لعلي رضي الله عنه: " لأن يهدي الله بك رجلا واحدا خير لك من حمر النعم " ومن حديث أبي رافع قال: قال رسول الله صلى الله عليه وسلم لعلي عليه السلام: " يا علي، لأن يهدي الله على يديك','599','0','0');
