INSERT INTO [t22367] VALUES (40,'فصل في فضل الصمت وحمده ثبت عن النبي صلى الله عليه وسلم أنه قال: " من صمت نجا " وأنه صلى الله عليه وسلم قال: " من كان يؤمن بالله واليوم الآخر فليقل خيرا أو ليصمت " وقد ذكرنا هذا المعنى مجردا في التمهيد','680','0','0');
