INSERT INTO [t22547] VALUES (1,'الجزء ١','2','0','0');
INSERT INTO [t22547] VALUES (2,'باب ما قد روي عن رسول الله عليه السلام في أشد الناس عذابا يوم القيامة','7','1','0');
INSERT INTO [t22547] VALUES (3,'باب بيان ما أشكل علينا مما قد روي عنه عليه السلام من العشر الخواتم من سورة آل عمران التي تلاها في ليلة عند استيقاظه من نومه , وما روي عنه في ذلك','12','1','0');
INSERT INTO [t22547] VALUES (4,'باب بيان مشكل ما روي عنه، فيما يقال عند المساء مما لا يضر معه قائله لدغة حمة حتى يصبح قال أبو جعفر: فمما روي في ذلك من حديث أبي صالح السمان الذي رواه عنه ابنه سهيل مما قد اختلف عليه فيمن ذكره في إسناده بعد أبيه , فرواه بعضهم عنه أنه أبو هريرة','17','1','0');
INSERT INTO [t22547] VALUES (5,'باب بيان ما أشكل علينا مما قد روي عنه عليه السلام من نهيه عن اتخاذ الدواب مجالس , ومن نهيه عن اتخاذها كراسي','32','1','0');
INSERT INTO [t22547] VALUES (6,'باب بيان مشكل ما روي عن النبي عليه السلام في نهيه أبا ذر أن يتولى قضاء بين اثنين وأن يؤوي أمانة','40','1','0');
INSERT INTO [t22547] VALUES (7,'باب بيان مشكل ما روي في السبب الذي فيه نزلت وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم مما قد ظن بعض الناس أنه قد تضادت الروايات فيه عن رسول الله عليه السلام مما هو في الحقيقة بخلاف ذلك','53','1','0');
INSERT INTO [t22547] VALUES (8,'باب بيان مشكل ما روي عن رسول الله عليه السلام ثم عن ابن عباس، مما يحيط علما أنه لم يقله إلا بأخذه إياه عنه إذ كان مثله لا يوجد إلا عنه ولا مما يدرك بالرأي , ولا من استنباط ولا من استخراج في التسع الآيات التي أوتيها موسى صلى الله عليه وسلم','57','1','0');
INSERT INTO [t22547] VALUES (9,'باب بيان ما أشكل مما روي عنه، صلى الله عليه وسلم في السبب الذي كان فيه نزول قوله تعالى: يا أيها الذين آمنوا لا تكونوا كالذين آذوا موسى الآية وما روي عن علي في ذلك مما يحيط علما أن عليا لم يقل ذلك رأيا ولا استنباطا إذ كان مثله لا يقال بالرأي ولا','64','1','0');
INSERT INTO [t22547] VALUES (10,'باب بيان مشكل ما روي عنه، عليه السلام مما كان منه في عبد الله بن أبي ابن سلول رأس المنافقين بعد موته من صلاته عليه ومما يدل على خلاف ذلك كان منه فيه','67','1','0');
INSERT INTO [t22547] VALUES (11,'باب بيان مشكل ما روي عنه، صلى الله عليه وسلم في الأعداد من الزمان التي لو وقفها من مر بين يدي المصلي كانت خيرا له من مروره من بين يديه ما هي وهل هي من السنين أو من الشهور أو من الأيام','83','1','0');
INSERT INTO [t22547] VALUES (12,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إن الأمير إذا ابتغى الريبة في الناس أفسدهم "','87','1','0');
INSERT INTO [t22547] VALUES (13,'باب بيان ما أشكل مما روي عنه عليه السلام أن ابن آدم خلق على ثلاث مائة وستين مفصلا فإذا كبر الله تعالى وهلله وحمده واستغفره وسبحه وعزل العظم والحجر والشوك عن طريق الناس وأمر بالمعروف ونهى عن المنكر عد ذلك ثلاث مائة مفصل','94','1','0');
INSERT INTO [t22547] VALUES (14,'باب بيان ما أشكل علينا مما رويناه عن النبي عليه السلام من قوله: " وعلى المقتتلين أن ينحجزوا الأدنى فالأدنى وإن كانت امرأة "','98','1','0');
INSERT INTO [t22547] VALUES (15,'باب بيان مشكل ما قد روي عنه عليه السلام: " ليوشكن أن ينزل فيكم ابن مريم عليه السلام حكما مقسطا يكسر الصليب ويقتل الخنزير ويضع الجزية "','102','1','0');
INSERT INTO [t22547] VALUES (16,'باب بيان مشكل ما روي عنه، عليه السلام في الشيطان أنه يجري من ابن آدم مجرى الدم وهل النبي عليه السلام كان في ذلك كمن سواه من الناس أو بخلافهم','105','1','0');
INSERT INTO [t22547] VALUES (17,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما أمر به في السير على الإبل في حال الخصب وفي حال الجدب','112','1','0');
INSERT INTO [t22547] VALUES (18,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيما بين وضع المسجد الحرام والمسجد الأقصى في الأرض من المدة','116','1','0');
INSERT INTO [t22547] VALUES (19,'باب بيان مشكل ما روي عن رسول الله عليه السلام في المعوذتين وما روي عنه ما يوجب أنهما من القرآن','119','1','0');
INSERT INTO [t22547] VALUES (20,'باب بيان مشكل ما روي عن رسول الله عليه السلام في السبب الذي فيه نزلت: وما كنتم تستترون أن يشهد عليكم سمعكم إلى قوله: فما هم من المعتبين','130','1','0');
INSERT INTO [t22547] VALUES (21,'باب بيان مشكل ما روي عن رسول الله عليه السلام في المراد بقول الله تعالى: ثم إنكم يوم القيامة عند ربكم تختصمون','133','1','0');
INSERT INTO [t22547] VALUES (22,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " وحدثوا عن بني إسرائيل ولا حرج "','136','1','0');
INSERT INTO [t22547] VALUES (23,'باب بيان مشكل ما روي عن رسول الله عليه السلام من نهيه عن بيع الثنيا','142','1','0');
INSERT INTO [t22547] VALUES (24,'باب بيان مشكل ما روي عن رسول الله عليه السلام في أفضل بناته من هي منهن','147','1','0');
INSERT INTO [t22547] VALUES (25,'باب بيان مشكل ما روي عن رسول الله عليه السلام مما كان أمر به عمر بن أبي سلمة من الأكل مما يليه من الطعام دون ما سواه منه وما يدخل في هذا المعنى سواه','157','1','0');
INSERT INTO [t22547] VALUES (26,'باب بيان مشكل ما رواه جابر عن النبي عليه السلام أنه استأذن عليه فقال له: " من هذا؟ " فقال جابر: أنا، فقال له النبي عليه السلام: " أنا أنا " وكأنه كره ذلك','170','1','0');
INSERT INTO [t22547] VALUES (27,'باب بيان مشكل ما روي عن رسول الله عليه السلام من نهيه عن الجلوس بالصعدات ومن إباحته ذلك على الشرائط التي اشترطها في إباحته ذلك','173','1','0');
INSERT INTO [t22547] VALUES (28,'باب بيان مشكل ما روي عن رسول الله عليه السلام في اسم الله الأعظم أي أسمائه هو؟','180','1','0');
INSERT INTO [t22547] VALUES (29,'باب بيان مشكل ما روي عن رسول الله عليه السلام من دعائه " اللهم قو في طاعتك ضعفي "','188','1','0');
INSERT INTO [t22547] VALUES (30,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إذا سجد أحدكم فلا يبرك كما يبرك البعير , ولكن ليضع يديه ثم ركبتيه "','190','1','0');
INSERT INTO [t22547] VALUES (31,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إن الشمس والقمر ثوران مكوران في النار يوم القيامة "','192','1','0');
INSERT INTO [t22547] VALUES (32,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " بئس مطية الرجل زعموا "','195','1','0');
INSERT INTO [t22547] VALUES (33,'باب بيان مشكل ما روي عن رسول الله عليه السلام من أمره من قبله مظلمة لأخيه في عرض أو في مال أن يتحلله منها في الدنيا','198','1','0');
INSERT INTO [t22547] VALUES (34,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيمن قتل نفسه متعمدا هل يجوز أن يغفر له أم لا؟','205','1','0');
INSERT INTO [t22547] VALUES (35,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيما كان من بعثه محمد بن مسلمة لقتله كعب بن الأشرف بما يدفع التضاد عن ما توهم بعض الناس أنه قد ضاد ما فيه','210','1','0');
INSERT INTO [t22547] VALUES (36,'باب بيان مشكل ما روي عن حكيم بن حزام، من قوله: " بايعت رسول الله صلى الله عليه وسلم على أن لا أخر إلا قائما "','216','1','0');
INSERT INTO [t22547] VALUES (37,'باب بيان مشكل ما روي عن رسول الله عليه السلام في المؤذنين أنهم أطول الناس أعناقا يوم القيامة','221','1','0');
INSERT INTO [t22547] VALUES (38,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله لأزواجه رضي الله عنهن: " أسرعكن بي لحاقا أطولكن يدين "','223','1','0');
INSERT INTO [t22547] VALUES (39,'باب بيان مشكل ما روي عن رسول الله عليه السلام في إنزاء الحمير على الخيل','226','1','0');
INSERT INTO [t22547] VALUES (40,'باب بيان مشكل ما روي عن رسول الله عليه السلام في الذل بالزرع','238','1','0');
INSERT INTO [t22547] VALUES (41,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله عند قسمته بين أزواجه بالعدل عليهن: " اللهم إن هذه قسمتي فيما أملك فلا تلمني فيما تملك ولا أملك "','241','1','0');
INSERT INTO [t22547] VALUES (42,'باب بيان مشكل ما روي عن رسول الله عليه السلام من نهيه أمته أن يقولوا: ما شاء الله وشاء محمد وأمره إياهم أن يقولوا مكان ذلك: ما شاء الله , ثم ما شاء محمد','244','1','0');
INSERT INTO [t22547] VALUES (43,'باب بيان مشكل ما قرأه رسول الله عليه السلام من قوله تعالى: والأرحام في أول سورة النساء هل كان بالنصب أو الجر؟','251','1','0');
INSERT INTO [t22547] VALUES (44,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إذا مات الإنسان انقطع عمله إلا من صدقة جارية , وعلم ينتفع به , وولد صالح يدعو له "','258','1','0');
INSERT INTO [t22547] VALUES (45,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " وإياك واللو فإنها تفتح عمل الشيطان "','267','1','0');
INSERT INTO [t22547] VALUES (46,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيمن صلت عليه من الموتى جماعة من المسلمين فشفعوا له أنهم يشفعون فيه إذا كان لهم عدد ذكر مقداره فيما روي عنه في ذلك','273','1','0');
INSERT INTO [t22547] VALUES (47,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إن للقبر لضغطة لو نجا منها أحد نجا منها سعد بن معاذ رضي الله عنه "','282','1','0');
INSERT INTO [t22547] VALUES (48,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيما تغرب فيه الشمس','288','1','0');
INSERT INTO [t22547] VALUES (49,'باب بيان مشكل ما روي عن رسول الله عليه السلام في جوابه كان لزوجتيه أم سلمة وميمونة رضوان الله عليهما لما دخل عليه ابن أم مكتوم الأعمى وهما عنده بعدما أنزل الحجاب: " احتجبا منه " فقلنا: يا رسول الله إنه أعمى لا يرانا ولا يعرفنا ومن قوله لهما: "','300','1','0');
INSERT INTO [t22547] VALUES (50,'باب بيان مشكل ما روي عن رسول الله عليه السلام في قوله لأم سلمة زوجته: " إذا كان لإحداكن مكاتب وكان عنده ما يؤدي فلتحتجب منه "','309','1','0');
INSERT INTO [t22547] VALUES (51,'باب بيان مشكل ما روي عن رسول الله عليه السلام في رفع العلم عن الناس وقبضه منهم','312','1','0');
INSERT INTO [t22547] VALUES (52,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيمن كان ينزل عليه الوحي , وهو في لحافها','327','1','0');
INSERT INTO [t22547] VALUES (53,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن تقليد الخيل الأوتار','330','1','0');
INSERT INTO [t22547] VALUES (54,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " نحن أحق بالشك من إبراهيم " وما ذكر معه سواه في الحديث المذكور ذلك فيه','334','1','0');
INSERT INTO [t22547] VALUES (55,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في مراد الله بقوله: وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم هل هو عبد الله بن سلام أو من سواه','337','1','0');
INSERT INTO [t22547] VALUES (56,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي أنزلت فيه الآيتان اللتان أول سورة الحجرات: يا أيها الذين آمنوا لا تقدموا الآية ويا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي الآية.','346','1','0');
INSERT INTO [t22547] VALUES (57,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الشيطان يعقد على قافية رأس أحدكم ثلاث عقد إذا نام، كل عقدة منها يضرب مكانها عليك ليل طويل فإذا أصبح ولم يصل أصبح كسلان خبيث النفس "','357','1','0');
INSERT INTO [t22547] VALUES (58,'باب بيان مشكل ما روي عن رسول الله عليه السلام فيما كان منه في هديته إلى النجاشي ومن وعده بها أم سلمة إن رجعت إليه بموت النجاشي قبل وصولها إليه , ومن إعطائه بعد رجوعها إليه أم سلمة بعضها وسائر نسائه سواها بقيتها','363','1','0');
INSERT INTO [t22547] VALUES (59,'باب بيان مشكل ما روي عن رسول الله عليه السلام في قول الله عز وجل: ثلة من الأولين وقليل من الآخرين وفي قوله تعالى: ثلة من الأولين وثلة من الآخرين','371','1','0');
INSERT INTO [t22547] VALUES (60,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقوله تعالى: ولا تطرد الذين يدعون ربهم بالغداة والعشي يريدون وجهه وفي قوله: واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه','380','1','0');
INSERT INTO [t22547] VALUES (61,'باب بيان مشكل ما روي عن رسول الله عليه السلام في نهيه رديفه عند عثور جمله أو حماره أن يقول: تعس الشيطان','383','1','0');
INSERT INTO [t22547] VALUES (62,'باب بيان مشكل ما رواه أبو مسعود عقبة بن عمرو عن رسول الله عليه السلام من قوله: " لا يبقى على الأرض بعد مائة سنة نفس منفوسة "','388','1','0');
INSERT INTO [t22547] VALUES (63,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " من كذب علي متعمدا فليتبوأ مقعده من النار " على ما قد روي عنه في ذلك قوله: " من كذب علي متعمدا " ومن قوله: " من كذب علي مطلقا " وفي السبب الذي كان ذلك منه','396','1','0');
INSERT INTO [t22547] VALUES (64,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من حدث عني حديثا يرى أنه كذب فهو أحد الكاذبين "','430','1','0');
INSERT INTO [t22547] VALUES (65,'باب بيان مشكل ما روي عنه عليه السلام في " صلاته على الجهنية التي رجمها بإقرارها عنده بالزنى وفي تركه الصلاة على ماعز الذي رجمه بإقراره عنده "','432','1','0');
INSERT INTO [t22547] VALUES (66,'باب بيان مشكل ما روي عنه عليه السلام من قوله للذي حلف عنده لخصمه الذي كان خاصمه إليه فيما كان ادعى عليه: " أما إنك قد فعلت فادفع إليه حقه وستكفر عنك لا إله إلا الله ما صنعت "','442','1','0');
INSERT INTO [t22547] VALUES (67,'باب بيان مشكل ما روي عن رسول الله عليه السلام في الحسد هل يتسع لأحد من الناس في حال من الأحوال أم لا.','456','1','0');
INSERT INTO [t22547] VALUES (68,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرويبضة الذي ذكره في وصفه السنين التي أمام الدجال من هو من الناس؟','466','1','0');
INSERT INTO [t22547] VALUES (69,'باب بيان مشكل ما روي عنه عليه السلام في تأويل قول الله تعالى: ثم لتسألن يومئذ عن النعيم','469','1','0');
INSERT INTO [t22547] VALUES (70,'باب بيان مشكل ما روي عنه عليه السلام في جواب من سأله عن الساعة.','476','1','0');
INSERT INTO [t22547] VALUES (71,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " فصل ما بين صيامنا وصيام أهل الكتاب أكلة السحر ".','479','1','0');
INSERT INTO [t22547] VALUES (72,'باب بيان مشكل ما روي عنه عليه السلام مما رواه ابن عباس في رؤية هلال رمضان','483','1','0');
INSERT INTO [t22547] VALUES (73,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المقدار من الحال الذي تحرم به المسألة','489','1','0');
INSERT INTO [t22547] VALUES (74,'باب بيان مشكل ما روي عنه عليه السلام من قوله لقبيصة بن المخارق الهلالي: " إن المسألة حرمت إلا في ثلاث " ثم ذكرهن , ثم أعقب ذلك بقوله: " وما سوى ذلك من المسألة فهي سحت "','494','1','0');
INSERT INTO [t22547] VALUES (75,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " شهرا عيد لا ينقصان رمضان وذو الحجة ".','496','1','0');
INSERT INTO [t22547] VALUES (76,'باب بيان مشكل ما قد روي عن رسول الله صلى الله عليه وسلم من قوله: " من أحسن في الإسلام لم يؤاخذ بما عمل في الجاهلية ومن أساء في الإسلام أخذ بالأول والآخر ".','502','1','0');
INSERT INTO [t22547] VALUES (77,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا هلك كسرى فلا كسرى بعده وإذا هلك قيصر فلا قيصر بعده ".','505','1','0');
INSERT INTO [t22547] VALUES (78,'باب بيان مشكل ما روي عن عائشة، وأم سلمة زوجي النبي عليه السلام أن رسول الله صلى الله عليه وسلم , لم يمت حتى أحل له النساء','515','1','0');
INSERT INTO [t22547] VALUES (79,'الجزء ٢','527','0','0');
INSERT INTO [t22547] VALUES (80,'باب بيان مشكل ما روي عنه عليه السلام في العاطس الذي أمر بتشميته أي العاطسين هو؟','527','79','0');
INSERT INTO [t22547] VALUES (81,'باب بيان مشكل ما روي عنه عليه السلام في صدق أبي ذر رضي الله عنه','534','79','0');
INSERT INTO [t22547] VALUES (82,'باب بيان مشكل ما روي عنه عليه السلام في من أصبح جنبا في يوم من شهر رمضان هل يصوم ذلك اليوم أم لا؟','536','79','0');
INSERT INTO [t22547] VALUES (83,'باب بيان مشكل ما روي عن رسول الله عليه السلام من قوله: " إذا نهيتكم عن شيء فانتهوا عنه، وإذا أمرتكم بأمر فافعلوا منه ما استطعتم "','544','79','0');
INSERT INTO [t22547] VALUES (84,'باب بيان مشكل ما روي عنه عليه السلام في الرجل الذي أوصى بنيه إذا مات أن يحرقوه، ثم يسحقوه , ثم يذروه في الريح في البر والبحر، وفي غفران الله له مع ذلك','547','79','0');
INSERT INTO [t22547] VALUES (85,'باب بيان مشكل احتمال السبب الذي نزلت فيه: ليس لك من الأمر شيء','557','79','0');
INSERT INTO [t22547] VALUES (86,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " ولن يؤتى اثنا عشر ألفا من قلة "','563','79','0');
INSERT INTO [t22547] VALUES (87,'باب بيان مشكل ما روي عنه عليه السلام في المساجد التي لا تشد الرحال إلا إليها، ومن فضل الصلاة فيها على غيرها من المساجد، وفي تساويها في ذلك، أو في فضل بعضها بعضا فيه','567','79','0');
INSERT INTO [t22547] VALUES (88,'باب بيان مشكل ما روي عنه عليه السلام في الصلاة التي لها هذا الفضل الذي ذكرناه في الباب الأول هل هي من الفرائض أو من النوافل؟','590','79','0');
INSERT INTO [t22547] VALUES (89,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " من كسر، أو عرج فقد حل وعليه حجة أخرى "','594','79','0');
INSERT INTO [t22547] VALUES (90,'باب بيان مشكل ما روي عنه عليه السلام من نهيه عن كسب الإماء','600','79','0');
INSERT INTO [t22547] VALUES (91,'باب بيان مشكل ما روي عنه عليه السلام في صفوف الناس وراءه للصلاة، وفي قيامه منهم مقام المصلي بهم، وذكره بعد ذلك أنه كان جنبا، وإشارته إليهم: أي كما أنتم، حتى أتاهم قد اغتسل ورأسه يقطر ماء، هل كان ذلك منه بعد أن كان كبر للصلاة أو قبل تكبيره كان لها؟','605','79','0');
INSERT INTO [t22547] VALUES (92,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " لا يقضي الحاكم بين اثنين وهو غضبان "','610','79','0');
INSERT INTO [t22547] VALUES (93,'باب بيان مشكل ما روي عنه الله عليه السلام مما كان منه في المستعيذة منه من نسائه لما أدخلت عليه','615','79','0');
INSERT INTO [t22547] VALUES (94,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " فإن الله لا يمل حتى تملوا "','641','79','0');
INSERT INTO [t22547] VALUES (95,'باب بيان مشكل ما روي عنه عليه السلام في قتيلة ابنة قيس التي لم يدخل بها بعد تزويجه إياها حتى توفي عنها','645','79','0');
INSERT INTO [t22547] VALUES (96,'باب بيان مشكل ما روي عنه عليه السلام: " لا عتاق ولا طلاق في إغلاق','652','79','0');
INSERT INTO [t22547] VALUES (97,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " لا طلاق إلا من بعد نكاح، ولا عتاق إلا من بعد ملك "','655','79','0');
INSERT INTO [t22547] VALUES (98,'باب بيان مشكل ما روي عنه عليه السلام فيمن استلجج بيمين على أهله','666','79','0');
INSERT INTO [t22547] VALUES (99,'باب بيان مشكل ما روي عنه عليه السلام في تعبير أبي بكر رضي الله عنه بأمره الرؤيا التي عبرها، ومن قوله له في عبارته إياها: " أصبت بعضا، وأخطأت بعضا "','669','79','0');
INSERT INTO [t22547] VALUES (100,'باب بيان مشكل ما روي عنه عليه السلام في حديث الظلة الذي ذكرناه في الباب الذي قبل هذا الباب من قوله لأبي بكر فيه: " لا تقسم " هل هو لكراهية القسم أم لما سوى ذلك؟ قد روينا في هذا الباب الذي قبل هذا الباب قول أبي بكر لرسول الله صلى الله عليه وسلم لما عبر','673','79','0');
INSERT INTO [t22547] VALUES (101,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " الرؤيا على رجل طائر ما لم تعبر فإذا عبرت سقطت','683','79','0');
INSERT INTO [t22547] VALUES (102,'باب بيان مشكل ما روي عنه عليه السلام في الأشياء التي هي الفطرة في الأبدان، أو من الفطرة','685','79','0');
INSERT INTO [t22547] VALUES (103,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " إن الإسلام بدأ غريبا وسيعود كما بدأ فطوبى للغرباء "','690','79','0');
INSERT INTO [t22547] VALUES (104,'باب بيان مشكل ما روي عنه عليه السلام في الشيء الذي يذهب المذمة في الرضاع، عن المرضع لمن أرضعه','697','79','0');
INSERT INTO [t22547] VALUES (105,'باب بيان مشكل ما روي في انشقاق القمر في زمن رسول الله عليه السلام تصديقا لقول الله عز وجل: اقتربت الساعة وانشق القمر','702','79','0');
INSERT INTO [t22547] VALUES (106,'باب بيان مشكل ما روي عنه عليه السلام من نهيه عن قفيز الطحان','714','79','0');
INSERT INTO [t22547] VALUES (107,'باب بيان مشكل ما كان من رسول الله عليه السلام فيما بين سجدتيه في صلاته هل هو ذكر الله تعالى، أو سكوت بلا ذكر؟','717','79','0');
INSERT INTO [t22547] VALUES (108,'باب بيان مشكل ما روي عنه عليه السلام في ثواب من أعتق رقبة وفي من قصد إليه بذلك من الرقاب من الذكران، ومن الإناث','719','79','0');
INSERT INTO [t22547] VALUES (109,'باب بيان مشكل ما روي عنه عليه السلام فيما كان أمر به الذين ذكروا له من بني سليم أن صاحبا لهم أوجب في العتاق لذلك','732','79','0');
INSERT INTO [t22547] VALUES (110,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " لقد هممت أن لا أصلي عليه " يعني: المعتق لعبيده الستة الذين هم جميع ماله عند موته، ومن غضبه صلى الله عليه وسلم من ذلك','739','79','0');
INSERT INTO [t22547] VALUES (111,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " الحلال بين والحرام بين وبين ذلك أمور مشتبهات "','746','79','0');
INSERT INTO [t22547] VALUES (112,'باب بيان مشكل ما روي عنه عليه السلام فيما سكت الله تعالى عنه','752','79','0');
INSERT INTO [t22547] VALUES (113,'باب بيان مشكل ما روي عنه عليه السلام في المراد بقول الله تعالى: إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا من هم؟','760','79','0');
INSERT INTO [t22547] VALUES (114,'باب بيان مشكل ما روي عنه عليه السلام في إثبات الشؤم، وما روي عنه في نفيه','776','79','0');
INSERT INTO [t22547] VALUES (115,'باب بيان مشكل ما روي عنه عليه السلام في الغول من إثباته، ومن نفيه','784','79','0');
INSERT INTO [t22547] VALUES (116,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " أقروا الطير على مكناتها','786','79','0');
INSERT INTO [t22547] VALUES (117,'باب بيان مشكل ما روي عنه عليه السلام في أمره علي بن أبي طالب في حجه بالقيام على بدنه وبما أمره به في ذلك وخاطبه به فيه','789','79','0');
INSERT INTO [t22547] VALUES (118,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " أتاكم أهل اليمن هم ألين قلوبا وأرق أفئدة الإيمان يمان والحكمة يمانية " ومن أهل اليمن الذين عناهم بذلك؟','796','79','0');
INSERT INTO [t22547] VALUES (119,'باب بيان مشكل ما روي عنه عليه السلام في قوله: " أقرؤهم، يعني أمته لكتاب الله أبي بن كعب، وأفرضهم زيد، وأعلمهم بالحلال والحرام معاذ بن جبل "','808','79','0');
INSERT INTO [t22547] VALUES (120,'باب بيان مشكل ما روي عنه عليه السلام من نهيه عن الحلف بغير الله تعالى، ومن ما روي عنه من حلفه بغيره تعالى، وما نسخ من ضده منه','813','79','0');
INSERT INTO [t22547] VALUES (121,'باب بيان مشكل ما روي عنه عليه السلام فيمن حلف بغير الله تعالى ما حكمه في ذلك؟','823','79','0');
INSERT INTO [t22547] VALUES (122,'باب بيان مشكل ما روي عنه عليه السلام مما أمر به من حلف باللات والعزى أن يقول','829','79','0');
INSERT INTO [t22547] VALUES (123,'باب بيان مشكل ما روي عنه عليه السلام فيمن حلف بملة سوى ملة الإسلام كاذبا','832','79','0');
INSERT INTO [t22547] VALUES (124,'باب بيان مشكل ما روي عنه عليه السلام في النذر أنه لا يؤخر شيئا','834','79','0');
INSERT INTO [t22547] VALUES (125,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " سباب المسلم فسوق وقتاله كفر "','840','79','0');
INSERT INTO [t22547] VALUES (126,'باب بيان مشكل ما روي عنه عليه السلام فيمن قال لأخيه: يا كافر','849','79','0');
INSERT INTO [t22547] VALUES (127,'باب بيان مشكل ما روي عنه عليه السلام من نهيه عن قتل النملة، والنحلة والهدهد والصرد','856','79','0');
INSERT INTO [t22547] VALUES (128,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " يستجاب لأحدكم ما لم يعجل فيقول: دعوت فلم يستجب لي "','865','79','0');
INSERT INTO [t22547] VALUES (129,'باب بيان مشكل ما روي عنه عليه السلام في تأخر جبريل عنه عليه السلام عنه في الوقت الذي كان وعده أن يأتيه فيه في منزله بسبب الجرو الذي كان في بيته، ولم يعلم به','870','79','0');
INSERT INTO [t22547] VALUES (130,'باب بيان مشكل ما روي عنه عليه السلام في الكبائر التي وعد الله تعالى مجتنبيها من عباده بتكفير سيئاتهم سواها قال الله تعالى: إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما. فكان ما كان منه تعالى نهاية الكرم؛ لأنه كفر عن مجتنبي هذه','876','79','0');
INSERT INTO [t22547] VALUES (131,'باب بيان مشكل ما روي عنه عليه السلام من قوله لابن عمر، ولأصحابه لما رجعوا إليه بعد فرارهم من الزحف، وقولهم له: نحن الفرارون قال: " بل أنتم العكارون','888','79','0');
INSERT INTO [t22547] VALUES (132,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " إذا رضي الله تعالى عن العبد أثنى عليه سبعة أضعاف من الخير لم يعملها " وما روي عنه في السخط مثل ذلك','892','79','0');
INSERT INTO [t22547] VALUES (133,'باب بيان مشكل ما روي عنه عليه السلام من قوله: " لو جعل القرآن في إهاب ثم ألقي في النار لما احترق "','894','79','0');
INSERT INTO [t22547] VALUES (134,'باب بيان مشكل ما رواه أبو هريرة عنه عليه السلام أنه قال: " ولد الزنا شر الثلاثة "','896','79','0');
INSERT INTO [t22547] VALUES (135,'باب بيان مشكل ما روي عنه عليه السلام أنه قال: " لا يدخل الجنة ولد زنية "','900','79','0');
INSERT INTO [t22547] VALUES (136,'باب بيان مشكل ما روي عنه عليه السلام من ظهور أولاد الحنث في آخر الزمان','906','79','0');
INSERT INTO [t22547] VALUES (137,'باب بيان مشكل ما روي عنه عليه السلام من قوله في عتاق ولد الزنى: " إنه لا خير فيه "','908','79','0');
INSERT INTO [t22547] VALUES (138,'باب بيان مشكل ما في كتاب الله تعالى مما ذكر الرحمة بالريح وبالرياح مما قد روي عن رسول الله عليه السلام مما يدل على الأولى في ذلك من تينك القراءتين','912','79','0');
INSERT INTO [t22547] VALUES (139,'باب بيان مشكل ما روي عنه عليه السلام في حديث أبي هريرة أن سعد بن عبادة قال له: يا رسول الله أرأيت إن وجدت مع امرأتي رجلا أمهله حتى آتي بأربعة شهداء؟ قال: " نعم "','920','79','0');
INSERT INTO [t22547] VALUES (140,'باب بيان مشكل ما روي عنه عليه السلام فيمن اطلع على رجل في منزله بغير إذنه هل له فقء عينه لذلك أم لا؟','924','79','0');
INSERT INTO [t22547] VALUES (141,'باب بيان مشكل ما روي عنه عليه السلام في جوابه المقداد لما سأله عن الكافر الذي قطع يده، ثم لاذ بشجرة فقال: أسلمت لله جل وعز أأقتله؟','933','79','0');
INSERT INTO [t22547] VALUES (142,'باب بيان مشكل ما روي عنه عليه السلام من قوله في حديث النسعة لأخي المقتول المذكور فيه: " أما إنك إن قتلته يعني قاتل أخيه " كنت مثله "','935','79','0');
INSERT INTO [t22547] VALUES (143,'باب بيان مشكل ما في جواب كل واحد من أبي بكر، ومن عمر، ومن سهيل بن بيضاء رسول الله صلى الله عليه وسلم عند سؤاله إياه ما يفعل برجل لو وجده مع امرأته؟','940','79','0');
INSERT INTO [t22547] VALUES (144,'باب بيان مشكل ما روي عنه، عليه السلام من قوله: " لي الواجد يحل عرضه وعقوبته "','943','79','0');
INSERT INTO [t22547] VALUES (145,'باب بيان مشكل ما روي عنه عليه السلام في النهي عن اتخاذ الغرف، وما روي عنه في إباحة ذلك','947','79','0');
INSERT INTO [t22547] VALUES (146,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قول الله تعالى عز وجل: يوم تأتي السماء بدخان مبين','953','79','0');
INSERT INTO [t22547] VALUES (147,'باب بيان مشكل ما روي عن رسول الله عليه السلام من استغفاره في صلاته على الميت الصغير','957','79','0');
INSERT INTO [t22547] VALUES (148,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله في الصلاة على الميت مخلوطا بالدعاء له: " ولا نعلم إلا خيرا "','959','79','0');
INSERT INTO [t22547] VALUES (149,'باب بيان مشكل حديث النبي صلى الله عليه وسلم في تركه أخذ ميراث مولاه الذي سقط من نخلة فمات فأمره بدفع ميراثه إلى أهل قريته.','962','79','0');
INSERT INTO [t22547] VALUES (150,'الجزء ٣','962','0','0');
INSERT INTO [t22547] VALUES (151,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ما تركت بعد نفقة أهلي ونفقة عاملي فهو صدقة "','969','150','0');
INSERT INTO [t22547] VALUES (152,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل إنكم وما تعبدون من دون الله حصب جهنم الآية','972','150','0');
INSERT INTO [t22547] VALUES (153,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا تدع مضر عبدا لله إلا فتنوه أو قتلوه "','979','150','0');
INSERT INTO [t22547] VALUES (154,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اسم الصلاة التالية لصلاة المغرب من الصلوات الخمس','983','150','0');
INSERT INTO [t22547] VALUES (155,'باب بيان مشكل حديث النبي صلى الله عليه وسلم: " لو كنت متخذا خليلا لاتخذت أبا بكر خليلا وإن صاحبكم خليل الله "','991','150','0');
INSERT INTO [t22547] VALUES (156,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا تخيروني على موسى صلى الله عليه وسلم " للسبب الذي ذكره في الحديث الذي روي ذلك عنه فيه','1003','150','0');
INSERT INTO [t22547] VALUES (157,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه أن يقال: هو خير من يونس بن متى','1005','150','0');
INSERT INTO [t22547] VALUES (158,'باب بيان مشكل جواب رسول الله صلى الله عليه وسلم للذي قال له: يا خير البرية، بقوله: " ذاك إبراهيم صلى الله عليه وسلم "','1008','150','0');
INSERT INTO [t22547] VALUES (159,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا تخيروا بين أنبياء الله عز وجل وصلوات الله عليهم أجمعين "','1017','150','0');
INSERT INTO [t22547] VALUES (160,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تسمية المولود يوم سابعه وفي تسميته صلى الله عليه وسلم بعض المولودين قبل ذلك','1020','150','0');
INSERT INTO [t22547] VALUES (161,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يذبح عن المولود الذكر يوم سابعه هل هو شاة أو شاتان','1030','150','0');
INSERT INTO [t22547] VALUES (162,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " وأميطوا عنه الأذى " يعني ما يفعل بالمولود في يوم سابعه','1041','150','0');
INSERT INTO [t22547] VALUES (163,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العقيقة وهل هو على الوجوب أو على الاختيار؟ قال أبو جعفر: قد روينا فيما تقدم منا في هذه الأبواب في الذبائح أن رسول الله صلى الله عليه وسلم قال: " المولود مرتهن بعقيقته " وفي ذلك ما قد دل على وجوب','1048','150','0');
INSERT INTO [t22547] VALUES (164,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العتيرة، وهل هي الرجبية أم لا؟','1053','150','0');
INSERT INTO [t22547] VALUES (165,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الفرعة قال أبو جعفر: قد روينا فيما تقدم منا في هذه الأبواب عن رسول الله صلى الله عليه وسلم في الفرع ما قد رويناه عنه فيها فأردنا أن نعلم ما ذلك الفرع؟ فوجدنا المزني قد حدثنا قال: قال أبو عبد الله','1065','150','0');
INSERT INTO [t22547] VALUES (166,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في مسألته الله عز وجل أن يرد الشمس عليه بعد غيبوبتها ورد الله عز وجل إياها عليه , وما روي عنه مما يوهم من توهم مضاد ذلك','1066','150','0');
INSERT INTO [t22547] VALUES (167,'باب المستخرج من حديث عبد الله بن عباس الذي يرفعه بعض رواته إلى النبي صلى الله عليه وسلم , ويوقفه بعضهم على ابن عباس في المراد بقول الله عز وجل: (وأتبعناهم ذرياتهم بإيمان ألحقنا بهم ذرياتهم)','1079','150','0');
INSERT INTO [t22547] VALUES (168,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أخنع الأسماء ما هو منها','1083','150','0');
INSERT INTO [t22547] VALUES (169,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الشيطان يستحل طعام القوم إذا لم يذكروا اسم الله عليه ما المراد بذلك الاستحلال؟','1085','150','0');
INSERT INTO [t22547] VALUES (170,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الصلاة التي سماها خداجا ما هي؟ وما حكمها في ذلك؟ هل هو فسادها ووجوب إعادتها أو ما سوى ذلك؟','1096','150','0');
INSERT INTO [t22547] VALUES (171,'باب بيان مشكل ما روي في فضل صلاة الجماعة على صلاة الفذ','1103','150','0');
INSERT INTO [t22547] VALUES (172,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الرجل ليصلي الصلاة وما يكتب له منها إلا عشرها " أو ما سوى ذلك مما ذكر من أجزائها','1106','150','0');
INSERT INTO [t22547] VALUES (173,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قطع المسلمين نخل بني النضير وتحريقها , وفي السبب الذي فيه نزلت: ما قطعتم من لينة أو تركتموها قائمة على أصولها','1110','150','0');
INSERT INTO [t22547] VALUES (174,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قيام الرجال بعضهم إلى بعض','1118','150','0');
INSERT INTO [t22547] VALUES (175,'باب بيان مشكل مراد رسول الله صلى الله عليه وسلم بلعنه الواصلة والمستوصلة','1127','150','0');
INSERT INTO [t22547] VALUES (176,'باب بيان مشكل ما روي من قوله صلى الله عليه وسلم: " أطت السماء وحق لها أن تئط، ما منها موضع قدم " في أحد الحديثين المرويين في ذلك وفي الآخر منهما " ما منها موضع أربع أصابع إلا وفيه ملك ساجد "','1134','150','0');
INSERT INTO [t22547] VALUES (177,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رده على البراء بن عازب لما سأله عما يقوله إذا أوى إلى فراشه مما ذكره أنه يقوله فيه " ورسولك الذي أرسلت " بقوله " ونبيك الذي أرسلت "','1137','150','0');
INSERT INTO [t22547] VALUES (178,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره زوجة النحام أن لا تكحل ابنتها في عدتها من وفاة زوجها بعد أن أعلمته خوفها على عينها إن لم تفعل ذلك','1140','150','0');
INSERT INTO [t22547] VALUES (179,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أسمائه','1146','150','0');
INSERT INTO [t22547] VALUES (180,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العيدين يجتمعان في اليوم الواحد','1150','150','0');
INSERT INTO [t22547] VALUES (181,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي من أجله قال عبد الله بن مسعود ما كان بين إسلامنا وبين أن عاتبنا الله عز وجل بقوله: ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله الآية','1159','150','0');
INSERT INTO [t22547] VALUES (182,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله في أبي موسى: " لقد أوتي من مزامير آل داود صلى الله عليه وسلم "','1163','150','0');
INSERT INTO [t22547] VALUES (183,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما ينبغي أن يفعل بمن رأى منه منكرا وبقوله في ذلك: " ولتأطرنه على الحق أطرا "','1169','150','0');
INSERT INTO [t22547] VALUES (184,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم','1172','150','0');
INSERT INTO [t22547] VALUES (185,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الزمان الذي يجب على الناس فيه الإقبال على خاصتهم وترك عامتهم','1180','150','0');
INSERT INTO [t22547] VALUES (186,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا اختلفتم في طريق فاجعلوه سبعة أذرع "','1188','150','0');
INSERT INTO [t22547] VALUES (187,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " وارفعوا عن بطن عرنة " يعني في الوقوف','1195','150','0');
INSERT INTO [t22547] VALUES (188,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الدليل على مراد الله عز وجل بقوله: فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام الآية','1203','150','0');
INSERT INTO [t22547] VALUES (189,'باب بيان مشكل ما روي عن النبي صلى الله عليه وسلم في تأويل قول الله جل وعز: ولقد آتيناك سبعا من المثاني والقرآن العظيم','1208','150','0');
INSERT INTO [t22547] VALUES (190,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره للناس بالاقتداء بأبي بكر وعمر والاهتداء بهدي عمار والتمسك بعهد ابن أم عبد رضي الله عنهم','1232','150','0');
INSERT INTO [t22547] VALUES (191,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لكل عمل شرة "','1241','150','0');
INSERT INTO [t22547] VALUES (192,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " المسلمون تكافأ دماؤهم ويسعى بذمتهم أدناهم وهم يد على من سواهم، لا يقتل مؤمن بكافر ولا ذو عهد في عهده "','1248','150','0');
INSERT INTO [t22547] VALUES (193,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله جوابا لابن عمر لما سأله عن أخذه الدنانير بالدراهم، والدراهم بالدنانير في البيع: " إذا كان ذلك من صرف يومكما وافترقتما , وليس بينكما شيء فلا بأس "','1253','150','0');
INSERT INTO [t22547] VALUES (194,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من دعائه لأهل مدينته أن يبارك لهم في صاعهم ومدهم','1255','150','0');
INSERT INTO [t22547] VALUES (195,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الوزن وزن أهل مكة والمكيال مكيال أهل المدينة "','1259','150','0');
INSERT INTO [t22547] VALUES (196,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحب الصيام إلى الله عز وجل','1261','150','0');
INSERT INTO [t22547] VALUES (197,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إنكم ستفتحون أرضا يذكر فيها القيراط " ما مراده بذلك القيراط؟','1265','150','0');
INSERT INTO [t22547] VALUES (198,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القيراط المستحق بالصلاة على الجنازة، هل هو بالصلاة عليها خاصة، أو بما سواه معه من تشييعها من منزلها؟','1268','150','0');
INSERT INTO [t22547] VALUES (199,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في كسر عظم الميت','1282','150','0');
INSERT INTO [t22547] VALUES (200,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا قام أحدكم من مجلسه ثم رجع إليه فهو أحق به "','1286','150','0');
INSERT INTO [t22547] VALUES (201,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا صرورة في الإسلام "','1290','150','0');
INSERT INTO [t22547] VALUES (202,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك','1300','150','0');
INSERT INTO [t22547] VALUES (203,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من أشار بحديدة إلى أحد من المسلمين يريد بها قتله فقد وجب دمه "','1303','150','0');
INSERT INTO [t22547] VALUES (204,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي عض ذراع رجل فانتزعها فسقطت ثنيتا العاض','1308','150','0');
INSERT INTO [t22547] VALUES (205,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الأشياء الموزونات أنها كالأشياء المكيلات في دخول الربا فيها كدخوله في الأشياء المكيلات','1314','150','0');
INSERT INTO [t22547] VALUES (206,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الرجل ليكون من أهل الصلاة ومن أهل الزكاة حتى ذكر سهام الخير وما يجزى يوم القيامة إلا بقدر عقله "','1321','150','0');
INSERT INTO [t22547] VALUES (207,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ما أذن الله لشيء ما أذن لنبي يتغنى بالقرآن "','1324','150','0');
INSERT INTO [t22547] VALUES (208,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ليس منا من لم يتغن بالقرآن "','1326','150','0');
INSERT INTO [t22547] VALUES (209,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم، من قوله: " من انتهب فليس منا "','1331','150','0');
INSERT INTO [t22547] VALUES (210,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في بقية الأشياء التي من كانت منه أن يكون منه صلى الله عليه وسلم','1342','150','0');
INSERT INTO [t22547] VALUES (211,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المسلم عند الانتهاء إلى القوم، وعند القيام عنهم , وهل سلام من انتهى إليهم يكون وهو قائم أو يكون بعد أن يجلس؟','1366','150','0');
INSERT INTO [t22547] VALUES (212,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لن يجزي ولد والدا إلا أن يجده مملوكا فيشتريه فيعتقه "','1369','150','0');
INSERT INTO [t22547] VALUES (213,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المشي في النعل الواحد وفي الخف الواحد','1371','150','0');
INSERT INTO [t22547] VALUES (214,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في استغفاره يوم الحديبية للمحلقين مرتين وللمقصرين مرة','1377','150','0');
INSERT INTO [t22547] VALUES (215,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المفصل من القرآن ما هو؟','1385','150','0');
INSERT INTO [t22547] VALUES (216,'باب بيان مشكل ما اختلف فيه عن عثمان بن عفان وعبد الله بن عباس رضي الله عنهما في الأنفال وبراءة وهل هما سورتان أو سورة واحدة؟','1391','150','0');
INSERT INTO [t22547] VALUES (217,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ستر العورة','1401','150','0');
INSERT INTO [t22547] VALUES (218,'باب طلاق الرجال نساءهم اللاتي يكرههن آباؤهم بذلك، هل ذلك مما عليهم في بر آبائهم أم لا؟ قال أبو جعفر: قد كان هذا المعنى أشكل على أبي الدرداء رضي الله عنه حتى قال في ذلك لمن سأله عنه','1405','150','0');
INSERT INTO [t22547] VALUES (219,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المبادرة بالموت النشو الذين يتخذون القرآن مزامير يقدمون أحدهم ليغنيهم , وإن كان أقلهم فقها','1408','150','0');
INSERT INTO [t22547] VALUES (220,'الجزء ٤','1408','0','0');
INSERT INTO [t22547] VALUES (221,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " كل مولود يولد على الفطرة " مما ينفرد به بعض رواته بأنه قال فما يزال عليها حتى يعرب عنه لسانه فأبواه يهودانه وينصرانه ويشركانه','1410','220','0');
INSERT INTO [t22547] VALUES (222,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إباحته تحلية السيف بالفضة','1417','220','0');
INSERT INTO [t22547] VALUES (223,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في استعماله الفضة برة لهديه','1427','220','0');
INSERT INTO [t22547] VALUES (224,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره الذي أصيب أنفه أن يتخذ مكانه أنفا من ذهب','1431','220','0');
INSERT INTO [t22547] VALUES (225,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الشرب في آنية الذهب وفي آنية الفضة وهل يدخل في ذلك الأواني من الخشب المضببة أم لا؟','1440','220','0');
INSERT INTO [t22547] VALUES (226,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الوقوع على الحامل المسبية وهي كذلك','1460','220','0');
INSERT INTO [t22547] VALUES (227,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من مس الحصى في الصلاة','1464','220','0');
INSERT INTO [t22547] VALUES (228,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من نام عن حزبه أو عن شيء منه فقرأه فيما بين الفجر وصلاة الظهر كتب له كأنما قرأه بالليل "','1473','220','0');
INSERT INTO [t22547] VALUES (229,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " الدين النصيحة " ومن جوابه لمن قال: له لمن يا رسول الله بما أجابه عن ذلك','1476','220','0');
INSERT INTO [t22547] VALUES (230,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " إن الزمان قد استدار كهيئته يوم خلق الله عز وجل السموات والأرض "','1485','220','0');
INSERT INTO [t22547] VALUES (231,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " لا يلدغ مؤمن من جحر مرتين "','1491','220','0');
INSERT INTO [t22547] VALUES (232,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " إن الناس كإبل مائة لا تجد فيها راحلة "','1496','220','0');
INSERT INTO [t22547] VALUES (233,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي فيه أنزلت يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم','1501','220','0');
INSERT INTO [t22547] VALUES (234,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " لا تقولوا للعنب الكرم: ولكن قولوا: حدائق الأعناب','1510','220','0');
INSERT INTO [t22547] VALUES (235,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في التقليس في الأعياد','1515','220','0');
INSERT INTO [t22547] VALUES (236,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " إن أعظم المسلمين في المسلمين جرما من سأل عن أمر لم يكن حراما فحرم من أجل مسألته "','1522','220','0');
INSERT INTO [t22547] VALUES (237,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي نزلت فيه غير أولي الضرر بعد أن نزل قبلها: لا يستوي القاعدون من المؤمنين والمجاهدون في سبيل الله بأموالهم وأنفسهم الآية','1527','220','0');
INSERT INTO [t22547] VALUES (238,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما كان منه يوم فتح مكة من أمانة الناس جميعا إلا الأربعة الرجال الذين سماهم، وإلا القينتين اللتين كان سماهما معهم','1540','220','0');
INSERT INTO [t22547] VALUES (239,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " لا يقتل قرشي بعد اليوم صبرا "','1542','220','0');
INSERT INTO [t22547] VALUES (240,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لمن كان دعاه وهو يصلي فلم يجبه حتى فرغ من صلاته ثم أتاه مجيبا له بقوله " ما منعك أن تجيبني " قال كنت أصلي قال أفلم تجد فيما أنزل الله عز وجل علي يا أيها الذين آمنوا استجيبوا لله وللرسول إذا','1546','220','0');
INSERT INTO [t22547] VALUES (241,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الكافر الذي قد كان في أصحابه فنذر رجل منهم إن قدر عليه أن يقتله فحال بينه وبين ذلك إسلامه فلم يقتله لذلك','1549','220','0');
INSERT INTO [t22547] VALUES (242,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره الذي أفطر يوما من شهر رمضان متعمدا بقضاء يوم مع الكفارة التي أمره بها فيها قال أبو جعفر: كل ما يروى عن رسول الله صلى الله عليه وسلم من حديث أبي هريرة عنه في هذا الباب ليس فيه ذكر قضاء يوم مكان','1553','220','0');
INSERT INTO [t22547] VALUES (243,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل وأولي الأمر منكم','1557','220','0');
INSERT INTO [t22547] VALUES (244,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " الحياء من الإيمان "','1567','220','0');
INSERT INTO [t22547] VALUES (245,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " البذاذة من الإيمان "','1571','220','0');
INSERT INTO [t22547] VALUES (246,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " إن مما أدركنا من كلام النبوة الأولى إذا لم تستحي فاصنع ما شئت "','1574','220','0');
INSERT INTO [t22547] VALUES (247,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " من سن سنة حسنة فعمل بها من بعده كان له أجرها وأجر من عمل بها من بعده لا ينتقص من أجورهم شيء ومن سن سنة سيئة فعمل بها من بعده " فذكر من وزرها ووزر من عمل بها من بعده مثل ما ذكر في الحسنة','1579','220','0');
INSERT INTO [t22547] VALUES (248,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " من بنى لله مسجدا بنى الله له بيتا أو مسجدا على ما روي في ذلك في الجنة "','1588','220','0');
INSERT INTO [t22547] VALUES (249,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في افتتاح الصلاة وبذلك أمرت وأنا أول المسلمين','1595','220','0');
INSERT INTO [t22547] VALUES (250,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في افتتاحه الصلاة بعد الذي ذكرناه عنه في الباب الأول','1597','220','0');
INSERT INTO [t22547] VALUES (251,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إذنه للعباس بن عبد المطلب في البيتوتة بمكة ليالي منى من أجل السقاية','1599','220','0');
INSERT INTO [t22547] VALUES (252,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه أن يقول الرجل: عبدي وأمتي وأمره إياه أن يقول مكان ذلك: فتاي وفتاتي','1604','220','0');
INSERT INTO [t22547] VALUES (253,'باب بيان مشكل ما روي عن أبي هريرة , مما لا يشك أنه لم يقله من رأيه وأنه إنما قاله لأخذه إياه عن رسول الله صلى الله عليه وسلم إذ كان مثله لا يقال بالرأي وهو قوله " لا يقول أحدكم: ربي يعني لمالكه ولكن ليقل: سيدي "','1607','220','0');
INSERT INTO [t22547] VALUES (254,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " ما قطع من حي فهو ميت "','1610','220','0');
INSERT INTO [t22547] VALUES (255,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السلام عند وقوف الرجل عند باب أخيه كم هو من مرة','1614','220','0');
INSERT INTO [t22547] VALUES (256,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الاستئذان كم هو من مرة','1616','220','0');
INSERT INTO [t22547] VALUES (257,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره كلدة لما دخل عليه بغير إذن أن يخرج ثم يقول: السلام عليكم أأدخل؟','1623','220','0');
INSERT INTO [t22547] VALUES (258,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعبد الله بن مسعود: " إذنك علي أن يرفع الحجاب وأن تسمع سوادي حتى أنهاك "','1625','220','0');
INSERT INTO [t22547] VALUES (259,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " رسول الرجل إلى الرجل إذنه "','1628','220','0');
INSERT INTO [t22547] VALUES (260,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله إن " من أشراط الساعة تسليم المعرفة أو تسليم الخاصة "','1632','220','0');
INSERT INTO [t22547] VALUES (261,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " لا غرار في صلاة ولا تسليم "','1640','220','0');
INSERT INTO [t22547] VALUES (262,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " أنت ومالك لأبيك "','1642','220','0');
INSERT INTO [t22547] VALUES (263,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " رب حامل فقه إلى من هو أفقه منه ورب حامل فقه ليس فقيه "','1645','220','0');
INSERT INTO [t22547] VALUES (264,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من اكتتابه العهدة التي اكتتبها للعداء بن خالد بن هوذة في بيعه إياه عبدا أو أمة " بيع المسلم للمسلم، لا داء، ولا غائلة، ولا خبثة "','1650','220','0');
INSERT INTO [t22547] VALUES (265,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " تدور أو تزول رحى الإسلام لخمس وثلاثين، أو لست وثلاثين، أو لسبع وثلاثين " وما ذكر في الحديث الذي روي عنه فيه','1652','220','0');
INSERT INTO [t22547] VALUES (266,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " لا حلف في الإسلام وتمسكوا بحلف الجاهلية "','1657','220','0');
INSERT INTO [t22547] VALUES (267,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يفعل على المزاح مما يروع المفعول به هل هو مباح لفاعله؟ أو محظور عليه؟','1664','220','0');
INSERT INTO [t22547] VALUES (268,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقوله عز وجل: لله ما في السموات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه','1670','220','0');
INSERT INTO [t22547] VALUES (269,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من أصحابه رضوان الله عليهم , عندما يتلى عليهم آمن الرسول بما أنزل إليه من ربه إلى آخر سورة البقرة وما كان من الله مما أنزله على رسوله صلى الله عليه وسلم لذلك جوابا لهم','1675','220','0');
INSERT INTO [t22547] VALUES (270,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " تجاوز الله لي عن أمتي ما حدثت به أنفسها ما لم ينطق به لسان أو تعمله يد "','1678','220','0');
INSERT INTO [t22547] VALUES (271,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الصرعة من هو من الرجال','1690','220','0');
INSERT INTO [t22547] VALUES (272,'باب بيان الحجة من كتاب الله ثم من سنة رسول الله صلى الله عليه وسلم على من كره للرجل أن يسأل الله عز وجل أن يتصدق عليه بشيء يذكره قال أبو جعفر: كره قوم أن يقول الرجل في دعائه: اللهم تصدق علي بكذا ورووا ذلك عن أبي وائل شقيق بن سلمة','1695','220','0');
INSERT INTO [t22547] VALUES (273,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " قد كان في الأمم قبلكم قوم محدثون فإن يكن في أمتي أحد منهم فهو عمر بن الخطاب "','1698','220','0');
INSERT INTO [t22547] VALUES (274,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله " أيكم مال وارثه أحب إليه من ماله "','1703','220','0');
INSERT INTO [t22547] VALUES (275,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من قوله وأبو هريرة حاضره " أيكم بسط ثوبه ثم أخذ من حديثي هذا، فإنه لا ينسى شيئا سمعه " وأن أبا هريرة فعل ذلك فما نسي بعد ذلك شيئا سمعه','1707','220','0');
INSERT INTO [t22547] VALUES (276,'باب بيان مشكل الحجة على من كره أن يقول: اللهم أعتقني من النار من سنة رسول الله صلى الله عليه وسلم قال: أبو جعفر: كره قوم أن يقول الرجل: اللهم أعتقني من النار وقالوا: إنما يضاف العتاق إلى من يرجى له الثواب، وروي ذلك عن أبي وائل','1717','220','0');
INSERT INTO [t22547] VALUES (277,'باب بيان مشكل ما روي عن علي , رضي الله عنه أو عن أبي ذر مما نحيط علما أنه لم يأخذه إلا عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: هذان خصمان اختصموا في ربهم إلى قوله: وهدوا إلى صراط الحميد','1719','220','0');
INSERT INTO [t22547] VALUES (278,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في فضل بر الأم على بر الأب من ولدهما','1724','220','0');
INSERT INTO [t22547] VALUES (279,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم " أنه قاء فأفطر "','1733','220','0');
INSERT INTO [t22547] VALUES (280,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الوعيد على الشفاعة في الحدود التي لله عز وجل','1739','220','0');
INSERT INTO [t22547] VALUES (281,'باب بيان مشكل قول رسول الله صلى الله عليه وسلم " من يرد الله به خيرا يفقهه في الدين "','1743','220','0');
INSERT INTO [t22547] VALUES (282,'باب بيان مشكل ما روي عن عمران بن حصين في كيفية الصلاة التي أمره النبي صلى الله عليه وسلم بها لما كان به الناصور وفي صلاة القاعد ما عدلها من صلاة القائم، وفي صلاة النائم وهو المضطجع ما عدلها من صلاة القاعد','1753','220','0');
INSERT INTO [t22547] VALUES (283,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ذكر الفخذ هل هو من العورة أم لا؟','1756','220','0');
INSERT INTO [t22547] VALUES (284,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه عند دخول عثمان عليه بعد دخول أبي بكر وعمر عليه قبل ذلك ومن تغييره من أحواله عند دخول عثمان عليه ما لم يغيره عند دخولهما رضوان الله عليهم قبل ذلك','1772','220','0');
INSERT INTO [t22547] VALUES (285,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للناس لما أمرهم بترك تأبير النخل ففعلوا ذلك فشيص ما قاله لهم عند ذلك','1777','220','0');
INSERT INTO [t22547] VALUES (286,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في بيعة المهاجر، وفي بيعة الأعرابي ما يلزم كل واحد منهما في بيعته التي بايعها','1781','220','0');
INSERT INTO [t22547] VALUES (287,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إطلاقه لأسلم أن يبدوا في الشعاب والأودية بعد بيعتهم إياه قبل ذلك على الهجرة','1786','220','0');
INSERT INTO [t22547] VALUES (288,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في التسمي برباح , وأفلح , ويسار , ويسير , وعلاء , ونافع , وبركة من كراهته، ومما يدل على إباحته','1791','220','0');
INSERT INTO [t22547] VALUES (289,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان ينوب في الصلاة من التسبيح والتصفيق والتنحنح','1800','220','0');
INSERT INTO [t22547] VALUES (290,'الجزء ٥','1800','0','0');
INSERT INTO [t22547] VALUES (291,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله يوم غدير خم لعلي رضي الله عنه: " من كنت مولاه فعلي مولاه "','1808','290','0');
INSERT INTO [t22547] VALUES (292,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعائشة رضي الله عنها لما أشار لها إلى القمر " استعيذي بالله من شر هذا فإنه الغاسق إذا وقب "','1819','290','0');
INSERT INTO [t22547] VALUES (293,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن قتل الضفدع','1825','290','0');
INSERT INTO [t22547] VALUES (294,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في النجوى من نهي ومن إباحة','1827','290','0');
INSERT INTO [t22547] VALUES (295,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما روي عنه فيما كان فعله بالذين أغاروا على لقاحه وارتدوا عن الإسلام هل كان ذلك عقوبة منه لهم لمحاربتهم بما يكون عقوبة للمحاربين لذلك مرتدين كانوا أو غير مرتدين , أو لارتدادهم مع أفعالهم التي فعلوها؟','1839','290','0');
INSERT INTO [t22547] VALUES (296,'بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في كيفية عقوبات أهل اللقاح','1856','290','0');
INSERT INTO [t22547] VALUES (297,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اللقاح التي كان من عقوبته لآخذيها ما كان , هل كانت من إبل الصدقة , أو كانت لرسول الله صلى الله عليه وسلم؟','1870','290','0');
INSERT INTO [t22547] VALUES (298,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أمرت بقرية تأكل القرى "','1875','290','0');
INSERT INTO [t22547] VALUES (299,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي فيه نزلت: لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا','1878','290','0');
INSERT INTO [t22547] VALUES (300,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أد الأمانة إلى من ائتمنك ولا تخن من خانك "','1883','290','0');
INSERT INTO [t22547] VALUES (301,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أنه كان يعجبه الفأل الحسن','1891','290','0');
INSERT INTO [t22547] VALUES (302,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره في الحمى أن تبرد بالماء , هل يريد به كل المياه , أو يريد به خاصا منها؟','1899','290','0');
INSERT INTO [t22547] VALUES (303,'باب بيان مشكل ما روي , عن أبي طلحة , في أكله البرد وهو صائم , ورفع بعضهم ذلك إلى النبي صلى الله عليه وسلم في تحسينه ذلك منه','1908','290','0');
INSERT INTO [t22547] VALUES (304,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعلي رضي الله عنه: " إن لك كنزا في الجنة , وإنك ذو قرنيها , فلا تتبع النظرة النظرة , فإنما لك الأولى وليست لك الآخرة "','1913','290','0');
INSERT INTO [t22547] VALUES (305,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " يمينك على ما صدقك عليه صاحبك "','1918','290','0');
INSERT INTO [t22547] VALUES (306,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في بيعه حرا في دين كان عليه لما لم يجد له مالا يقضي ذلك الدين عنه منه','1922','290','0');
INSERT INTO [t22547] VALUES (307,'باب بيان مشكل ما قد اختلف الناس فيه من المعسر بالدين الذي عليه , هل يؤاجر في ذلك حتى يقضي دينه من أجرته أم لا؟ وهل روي عن رسول الله صلى الله عليه وسلم في ذلك شيء أم لا؟ قال أبو جعفر: ما علمنا أحدا من أهل العلم ذهب إلى إجارة المدين الذي لا شيء له حتى','1927','290','0');
INSERT INTO [t22547] VALUES (308,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبق بما لا يكون','1929','290','0');
INSERT INTO [t22547] VALUES (309,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا جلب ولا جنب "','1936','290','0');
INSERT INTO [t22547] VALUES (310,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن إدخال فرس بين فرسين في السبق إذا كان مما يؤمن أن يسبق','1939','290','0');
INSERT INTO [t22547] VALUES (311,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه أن يسافر بالقرآن إلى أرض العدو','1946','290','0');
INSERT INTO [t22547] VALUES (312,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العزل , وأنه الوأد الخفي , وفيما روي عنه في تكذيبه من قال ذلك','1951','290','0');
INSERT INTO [t22547] VALUES (313,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الاستثناء في الأيمان إن شاء الله','1958','290','0');
INSERT INTO [t22547] VALUES (314,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الأيمان الموصول بعضها ببعض , بختم إن شاء الله , هل يكون ذلك استثناء في جميعها أو استثناء في اليمين الأخيرة منها؟','1967','290','0');
INSERT INTO [t22547] VALUES (315,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على الصحيح فيما اختلف فيه أهل العلم في الاستثناء في الأيمان إذا قدم منها ذكر الطلاق أو أخر منها , هل يكونان سواء , أو يكونان بخلاف ذلك؟ قال أبو جعفر: كان أهل العلم يسوون بين هذين المعنيين ولا','1971','290','0');
INSERT INTO [t22547] VALUES (316,'باب بيان مشكل اللدود: ما هو؟ وهل يجوز للناس أن يعالجوا به لعلة ما؟','1978','290','0');
INSERT INTO [t22547] VALUES (317,'باب بيان مشكل ما اختلف فيه أصحاب رسول الله صلى الله عليه وسلم في سنه التي مات عليها فيما روي عنه كان قاله في حياته','1980','290','0');
INSERT INTO [t22547] VALUES (318,'باب بيان مشكل فساد من ذهب إلى أن الشاب من كانت سنه أربعين سنة إلى ما دونها بعد بلوغه , بما يروى عن رسول الله صلى الله عليه وسلم مما يدفع ما قال في ذلك','1995','290','0');
INSERT INTO [t22547] VALUES (319,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يدل على أن الكهول من هم','1999','290','0');
INSERT INTO [t22547] VALUES (320,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الحسن , والحسين سيدا شباب أهل الجنة "','2003','290','0');
INSERT INTO [t22547] VALUES (321,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ثلاثة يؤتون أجرهم مرتين: رجل آمن بنبيه ثم أدركه النبي صلى الله عليه وسلم فآمن به , وعبد أدى حق الله وحق مولاه , ورجل أدب جارية فأحسن تأديبها ثم أعتقها وتزوجها "','2005','290','0');
INSERT INTO [t22547] VALUES (322,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما خاطب به قيصر في كتابه إليه من قوله: " أسلم يؤتك الله أجرك مرتين , وإن توليت فعليك إثم الأريسيين "','2010','290','0');
INSERT INTO [t22547] VALUES (323,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا حضر العشاء وأقيمت الصلاة فابدءوا بالعشاء "','2012','290','0');
INSERT INTO [t22547] VALUES (324,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن الصلاة بمدافعة الغائط والبول','2018','290','0');
INSERT INTO [t22547] VALUES (325,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " المؤمن يأكل في معاء واحد , والكافر يأكل في سبعة أمعاء "','2023','290','0');
INSERT INTO [t22547] VALUES (326,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المجوس وفيما ذكر عن علي رضي الله عنه أنهم كانوا أهل كتاب','2030','290','0');
INSERT INTO [t22547] VALUES (327,'باب بيان مشكل قول الله عز وجل: (ما ننسخ من آية أو ننسأها) الآية , بما روي عن رسول الله صلى الله عليه وسلم مما يستدل به على ذلك قال أبو جعفر: قال أهل العلم بالتأويل: إن النسخ وجهان , أحدهما: نسخ العمل بما في الآي المنسوخة , وإن كانت الآي المنسوخة','2039','290','0');
INSERT INTO [t22547] VALUES (328,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره زيد بن ثابت أن يتعلم السريانية , وقوله مع ذلك: " إني لا آمن يهودا على كتبي "','2050','290','0');
INSERT INTO [t22547] VALUES (329,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لولا الهجرة لكنت امرأ من الأنصار "','2053','290','0');
INSERT INTO [t22547] VALUES (330,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من تشكي امرأة صفوان بن المعطل صفوانا إلى رسول الله صلى الله عليه وسلم , أنه يضربها إذا صلت , ويفطرها إذا صامت , وينام حتى تطلع الشمس','2057','290','0');
INSERT INTO [t22547] VALUES (331,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من سؤال العبد ربه أن يعذبه في الدنيا بما يعذبه في الآخرة','2060','290','0');
INSERT INTO [t22547] VALUES (332,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " خير الناس مؤمن بين كريمين "','2063','290','0');
INSERT INTO [t22547] VALUES (333,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي قيل له: إنه يصلي الليل كله فإذا أصبح سرق , فقال: " ستمنعه صلاته "','2069','290','0');
INSERT INTO [t22547] VALUES (334,'باب بيان مشكل ما روي عن عمر بن الخطاب رضي الله عنه , أن الرجم مما أنزله الله تعالى في كتابه , وما روي عن غيره من أصحاب رسول الله صلى الله عليه وسلم من نسخ الله عز وجل ذلك من القرآن','2071','290','0');
INSERT INTO [t22547] VALUES (335,'باب بيان مشكل ما روي عن عائشة رضي الله عنها أنه كان نزل عشر رضاعات يحرمن في القرآن فنسخن بخمس رضاعات , وأن رسول الله صلى الله عليه وسلم توفي وهو مما يقرأ من القرآن','2078','290','0');
INSERT INTO [t22547] VALUES (336,'باب بيان مشكل ما روي عن عائشة , وحفصة , زوجي النبي صلى الله عليه وسلم , وعن أم كلثوم , عن رسول الله صلى الله عليه وسلم في هذه الآية: حافظوا على الصلوات والصلاة الوسطى وصلاة العصر','2083','290','0');
INSERT INTO [t22547] VALUES (337,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أنه " كان لا يطأ عقبه رجلان "','2088','290','0');
INSERT INTO [t22547] VALUES (338,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن التجار هم الفجار "','2093','290','0');
INSERT INTO [t22547] VALUES (339,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أما أنا فلا آكل متكئا "','2101','290','0');
INSERT INTO [t22547] VALUES (340,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن الشرب قائما','2104','290','0');
INSERT INTO [t22547] VALUES (341,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ما بعث الله من نبي , ولا استخلف من خليفة إلا وله بطانتان: بطانة تأمره بالخير وتحضه عليه , وبطانة لا تألوه خبالا "','2115','290','0');
INSERT INTO [t22547] VALUES (342,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جهاد ذوي الأبوين العدو: أهو أفضل له أم لزوم أبويه وتركه جهاد العدو؟','2120','290','0');
INSERT INTO [t22547] VALUES (343,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الفحل الذي نهى عن أخذه في الصدقة','2126','290','0');
INSERT INTO [t22547] VALUES (344,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ذي الواحد من أبويه: هل بره بلزومه إياه أفضل من الجهاد , أو الجهاد أفضل منه؟','2128','290','0');
INSERT INTO [t22547] VALUES (345,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المرادين بقول الله عز وجل: وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم','2131','290','0');
INSERT INTO [t22547] VALUES (346,'باب بيان مشكل ما روي عن أصحاب رسول الله صلى الله عليه وسلم مما نحيط علما أنهم لم يقولوه إلا بتوقيفه صلى الله عليه وسلم إياهم عليه في معنى قول الله عز وجل: ولا تبطلوا أعمالكم','2135','290','0');
INSERT INTO [t22547] VALUES (347,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في البر والإثم ما هما؟','2137','290','0');
INSERT INTO [t22547] VALUES (348,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في واعظ الله عز وجل الذي في قلب المؤمن','2141','290','0');
INSERT INTO [t22547] VALUES (349,'باب بيان مشكل ما روي عن أصحاب رسول الله صلى الله عليه وسلم في النذر بما هو معصية','2144','290','0');
INSERT INTO [t22547] VALUES (350,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا نذر في معصية الله , وكفارته كفارة اليمين "','2154','290','0');
INSERT INTO [t22547] VALUES (351,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا نذر في غضب , وكفارته كفارة يمين "','2157','290','0');
INSERT INTO [t22547] VALUES (352,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره أبا إسرائيل لما نذر أن يقوم في الشمس وأن لا يتكلم بما أمره به في ذلك','2160','290','0');
INSERT INTO [t22547] VALUES (353,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرؤيا: كم هي من جزء من الأجزاء التي هي النبوة؟','2162','290','0');
INSERT INTO [t22547] VALUES (354,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن أصاب ذنبا في الدنيا , فعوقب به , وفيمن أصاب ذنبا في الدنيا , فستره الله عز وجل عليه في الدنيا وعفا عنه','2171','290','0');
INSERT INTO [t22547] VALUES (355,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الإمام ضامن , والمؤذن مؤتمن "','2177','290','0');
INSERT INTO [t22547] VALUES (356,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من أم الناس فأتم الصلاة , وأصاب الوقت , فله ولهم , وإن انتقص من ذلك شيء فعليه ولا عليهم "','2181','290','0');
INSERT INTO [t22547] VALUES (357,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جوابه من قال له لما قال في الأذان ما قال: تركتنا ونحن نتقاتل على الأذان , ما أجابه به عنه','2185','290','0');
INSERT INTO [t22547] VALUES (358,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إجازته قضاء علي بن أبي طالب رضي الله عنه في القوم الذين سقطوا في الزبية المحفورة باليمن , المتعلقين بعضهم ببعض حتى كان موتهم لذلك','2191','290','0');
INSERT INTO [t22547] VALUES (359,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه سعد بن أبي وقاص لما سأله: من أشد الناس بلاء؟','2194','290','0');
INSERT INTO [t22547] VALUES (360,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان يصيبه من الوعك أنه كان يكون له فيه أجران','2198','290','0');
INSERT INTO [t22547] VALUES (361,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما ينزل بمن سوى الأنبياء صلوات الله عليهم في أبدانهم: هل يؤجرون على ذلك أم لا؟','2202','290','0');
INSERT INTO [t22547] VALUES (362,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حط الخطايا','2208','290','0');
INSERT INTO [t22547] VALUES (363,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أن الأمراض يكتب بها الحسنات , أو تحط بها الخطيئات','2219','290','0');
INSERT INTO [t22547] VALUES (364,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في كيفية الصلاة عليه','2222','290','0');
INSERT INTO [t22547] VALUES (365,'الجزء ٦','2222','0','0');
INSERT INTO [t22547] VALUES (366,'باب بيان مشكل الوجه فيما ذكرناه من الاختلاف في الصلاة على النبي صلى الله عليه وسلم في آخر الصلوات هل هو فرض لا تجزئ الصلاة إلا به؟ أو هو من السنن المأمور بها في الصلوات التي تجزئ وإن لم يؤت بها فيها؟','2234','365','0');
INSERT INTO [t22547] VALUES (367,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ليس على المسلم في عبده ولا في فرسه صدقة "','2239','365','0');
INSERT INTO [t22547] VALUES (368,'باب بيان مشكل ما روي عن قيس بن سعد بن عبادة الأنصاري في نسخ زكاة الفطر وفي نسخ فرض صوم يوم عاشوراء','2248','365','0');
INSERT INTO [t22547] VALUES (369,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في طلوع النجم الذي ترتفع بطلوعه العاهة أو تخف أي النجوم هو','2266','365','0');
INSERT INTO [t22547] VALUES (370,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " كل ابن آدم يأكله التراب غير عجب الذنب "','2271','365','0');
INSERT INTO [t22547] VALUES (371,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لو كان الإيمان بالثريا، ومن قوله: لو كان الدين بالثريا لناله رجال من أبناء فارس','2275','365','0');
INSERT INTO [t22547] VALUES (372,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره بقطع المخزومية التي كانت تستعير الحلي فتجحده','2282','365','0');
INSERT INTO [t22547] VALUES (373,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرجلين اللذين كانا هاجرا إليه فاستشهد أحدهما، وعاش الآخر بعده سنة ثم توفي، ففضل صاحبه المستشهد قبله','2289','365','0');
INSERT INTO [t22547] VALUES (374,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما قد تقدم ذكرنا له في كتابنا هذا من انقطاع عمل الرجل بموته إلا من الثلاثة الذين ذكرناهم في الباب الذي قبل هذا الباب قال أبو جعفر: قال قائل: قد رويت في الباب الذي قبل هذا الباب حديث سلمان في الرباط','2297','365','0');
INSERT INTO [t22547] VALUES (375,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن أدرك ركعة من الصلاة أنه قد أدرك الصلاة وفضلها','2299','365','0');
INSERT INTO [t22547] VALUES (376,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الطيرة على من تطير "','2305','365','0');
INSERT INTO [t22547] VALUES (377,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " تكون هنات وهنات، فمن أراد أن يفرق بين أمة محمد وهي جميع فاضربوه بالسيف كائنا من كان "','2307','365','0');
INSERT INTO [t22547] VALUES (378,'باب بيان مشكل ما روي في الشهب التي أرسلت على مستمعي أخبار السماء الدنيا من الشياطين عند مبعث رسول الله صلى الله عليه وسلم هل كان من ذلك شيء قبل مبعثه أم لا؟','2314','365','0');
INSERT INTO [t22547] VALUES (379,'باب بيان مشكل ما جاء في السبب الذي نزلت فيه: أولئك الذين يدعون يبتغون إلى ربهم الوسيلة الآية مما أضيف إلى عبد الله بن مسعود رضي الله عنه مما نحيط علما أنه لم يقله رأيا، وإنما قاله توقيفا','2319','365','0');
INSERT INTO [t22547] VALUES (380,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من صام رمضان ثم أتبعه ستا من شوال فكأنما صام السنة "','2322','365','0');
INSERT INTO [t22547] VALUES (381,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في التي كان لا يقسم لها من نسائه التسع اللاتي توفي عنهن من هي منهن؟','2339','365','0');
INSERT INTO [t22547] VALUES (382,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من الوصية بقبط مصر وإخباره في ذلك بأن لهم ذمة ورحما','2344','365','0');
INSERT INTO [t22547] VALUES (383,'باب بيان مشكل ما روي عن ابن عباس أنه لم يأخذ إلا عن رسول الله صلى الله عليه وسلم في بيان مشكل قول الله عز وجل: إن من أزواجكم وأولادكم عدوا لكم فاحذروهم قال الله عز وجل: يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذروهم','2347','365','0');
INSERT INTO [t22547] VALUES (384,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إقالة ذوي الهيئات عثراتهم إلا في حد من حدود الله عز وجل','2349','365','0');
INSERT INTO [t22547] VALUES (385,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لصفوان بن أمية لما تصدق بردائه على سارقه منه بعد أمر النبي صلى الله عليه وسلم بقطعه: " فهلا قبل أن تأتيني به "','2361','365','0');
INSERT INTO [t22547] VALUES (386,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أخذه على أصحابه في بيعته إياهم أن لا يعضه بعضهم بعضا','2367','365','0');
INSERT INTO [t22547] VALUES (387,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الواجب فيمن مات وعليه صيام هل هو الصيام أو الإطعام عنه','2372','365','0');
INSERT INTO [t22547] VALUES (388,'باب بيان مشكل ما روي عن ابن عباس وعن سلمة بن الأكوع رضي الله عنهما مما نحيط علما أنهما لم يقولاه إلا بأخذهما إياه من النبي صلى الله عليه وسلم في بيان مشكل قول الله عز وجل: وعلى الذين يطيقونه فدية طعام مسكين','2384','365','0');
INSERT INTO [t22547] VALUES (389,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أجاب به من سأله عن ميراث رجل من الأزد في يده لما ذكر له أنه لم يجد أزديا','2391','365','0');
INSERT INTO [t22547] VALUES (390,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يمنع أحدكم جاره أن يغرس خشبة في جداره "','2400','365','0');
INSERT INTO [t22547] VALUES (391,'باب بيان مشكل ما روي في السبب الذي به قطع رسول الله صلى الله عليه وسلم ما كان المشركون عليه من تحريمهم العمرة في الوقت الذي كانوا يحرمونها فيه من الزمان','2412','365','0');
INSERT INTO [t22547] VALUES (392,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يجلد فوق عشر جلدات إلا في حد من حدود الله عز وجل "، وفي وجوب الاقتصار على ذلك، وفيما روي عنه مما يوجب خلاف ذلك، وفي الأولى منهما ما هو','2427','365','0');
INSERT INTO [t22547] VALUES (393,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تحسينه لعمرو بن العاص من صلاته بالناس جنبا عند خوفه الموت على نفسه من البرد إن اغتسل','2444','365','0');
INSERT INTO [t22547] VALUES (394,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جوابه لأبي عبيدة بن الجراح رضي الله عنه لما قال له: " هل أحد خير منا؟ أسلمنا معك، وجاهدنا معك " بقوله له: " نعم، قوم من بعدكم يؤمنون بي ولم يروني "','2447','365','0');
INSERT INTO [t22547] VALUES (395,'باب بيان مشكل أحكام من كان بعد من حمده رسول الله صلى الله عليه وسلم في الآثار التي رويناها في الباب الذي تقدم','2458','365','0');
INSERT INTO [t22547] VALUES (396,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تزويجه المرأة التي وهبت له نفسها الرجل الذي سأله أن يزوجها إياه بغير رجوع منه إليها في ذلك ولا مؤامرة منه إياها فيه','2461','365','0');
INSERT INTO [t22547] VALUES (397,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على الوجه مما أهل العلم مختلفون فيه من الشيء يكون بين الشريكين هل لأحدهما أن يستعمله بحقه فيه أم لا','2465','365','0');
INSERT INTO [t22547] VALUES (398,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الاستغفار للمشركين من نهي أو إباحة','2467','365','0');
INSERT INTO [t22547] VALUES (399,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في مسحه على خفيه هل كان بعد نزول المائدة أو قبلها','2476','365','0');
INSERT INTO [t22547] VALUES (400,'باب بيان مشكل ما روي في إسلام جرير متى كان في سوى ما رويناه في الباب الذي قبل هذا الباب','2486','365','0');
INSERT INTO [t22547] VALUES (401,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في سورة المائدة هل هي آخر سورة أنزلت أم لا','2491','365','0');
INSERT INTO [t22547] VALUES (402,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه أسامة لما قال له: انزل في دارك بمكة: " وهل ترك لنا عقيل من رباع أو دور "','2499','365','0');
INSERT INTO [t22547] VALUES (403,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله: " من توضأ وضوءه، ثم أتى المسجد فركع ركعتين؛ غفر له ما تقدم من ذنبه، ولا تغتروا "','2501','365','0');
INSERT INTO [t22547] VALUES (404,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله في الصدقة: " لا حق فيها لغني ولا لقوي مكتسب "','2503','365','0');
INSERT INTO [t22547] VALUES (405,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله وهو على قبر إحدى ابنتيه اللتين كان عثمان تزوجهما: " لا يدخل القبر أحد قارف أهله الليلة "','2507','365','0');
INSERT INTO [t22547] VALUES (406,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن كان إليه إدخال من توفي من أزواج رسول الله صلى الله عليه وسلم في قبورهن','2511','365','0');
INSERT INTO [t22547] VALUES (407,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تأويل قول الله عز وجل: هو الذي أنزل عليك الكتاب منه آيات محكمات إلى قوله: وما يذكر إلا أولو الألباب','2515','365','0');
INSERT INTO [t22547] VALUES (408,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ضرب الرجال نساءهم من منع ومن إباحة','2520','365','0');
INSERT INTO [t22547] VALUES (409,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لحصين الخزاعي أبي عمران بن حصين لما علمه أن يدعو: " اللهم اغفر لي ما أخطأت , وما عمدت , وما علمت , وما جهلت "','2525','365','0');
INSERT INTO [t22547] VALUES (410,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله كان إذا أراد دخول قرية: " ورب الشياطين وما أضلت " مما كان يستعيذ به','2528','365','0');
INSERT INTO [t22547] VALUES (411,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الثلاثة الذين يدعون الله عز وجل فلا يستجيب لهم','2531','365','0');
INSERT INTO [t22547] VALUES (412,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من أم سليم من أخذها عرقه واستعمالها إياه في طيبها هل هو إمضاؤه ذلك لها أو نهيه إياها عنه','2533','365','0');
INSERT INTO [t22547] VALUES (413,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جعله قضاء الحج عن من قد كان وجب عليه كقضاء الدين الذي قد كان وجب عليه','2538','365','0');
INSERT INTO [t22547] VALUES (414,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن لم يحج عن نفسه حجة الإسلام هل له أن يحج عن غيره حجة الإسلام أم لا؟','2550','365','0');
INSERT INTO [t22547] VALUES (415,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الصبي أن له حجا','2561','365','0');
INSERT INTO [t22547] VALUES (416,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في هدايا الكفار إليه من قبول منه لها ومن رد منه إياها','2566','365','0');
INSERT INTO [t22547] VALUES (417,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في استعانته بمن طلب الاستعانة به من الكفار , وفي منعه من منعه من الكفار من القتال معه','2573','365','0');
INSERT INTO [t22547] VALUES (418,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العدد الذين يجوز أن يضحى بالبدنة عنهم.','2582','365','0');
INSERT INTO [t22547] VALUES (419,'الجزء ٧','2582','0','0');
INSERT INTO [t22547] VALUES (420,'باب بيان مشكل ما روي في البدن، أمن الإبل هي خاصة أم من الإبل ومن البقر جميعا؟','2596','419','0');
INSERT INTO [t22547] VALUES (421,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المرور بين يدي المصلي في البيت الحرام , وفي الغيبة عنه','2602','419','0');
INSERT INTO [t22547] VALUES (422,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الهجرة وهل قطعها فتح مكة أم لم يقطعها.','2606','419','0');
INSERT INTO [t22547] VALUES (423,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الله عز وجل إذا أراد بامرئ خيرا عسله "','2626','419','0');
INSERT INTO [t22547] VALUES (424,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تمتيع النساء المطلقات','2629','419','0');
INSERT INTO [t22547] VALUES (425,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أسآر السباع والدواب سواها من طهارة ومن غيرها','2637','419','0');
INSERT INTO [t22547] VALUES (426,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره بالعلانية وتحذيره من السر','2658','419','0');
INSERT INTO [t22547] VALUES (427,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ماء الرجل وماء المرأة , وفي عمل كل واحد منهما في الولد الذي يخلق منهما','2661','419','0');
INSERT INTO [t22547] VALUES (428,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في سؤال الملك في الرحم ربه عز وجل عن المخلوق من النطفة: أذكر أو أنثى بعدما أتى على النطفة للرحم قبل ذلك ما أتى عليها من الزمان , وهل هو مخالف لما قد ذكرناه في الباب الذي قبل أم لا؟','2666','419','0');
INSERT INTO [t22547] VALUES (429,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن زرع في أرض رجل بغير أمره زرعا لمن يكون ذلك الزرع من رب الأرض ومن زارعه','2671','419','0');
INSERT INTO [t22547] VALUES (430,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن زرع في أرض غيره زرعا على مزارعة فاسدة كيف الحكم فيه','2673','419','0');
INSERT INTO [t22547] VALUES (431,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المساقاة على النخل بجزء من أجزاء ثمرها وفي المعاملة على الأرض بجزء مما يخرج منها.','2676','419','0');
INSERT INTO [t22547] VALUES (432,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أمر به عمارا لما سأله عن المذي بغسل مذاكيره والتوضؤ منه','2704','419','0');
INSERT INTO [t22547] VALUES (433,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أيما عبد تزوج بغير إذن مواليه فهو عاهر "','2714','419','0');
INSERT INTO [t22547] VALUES (434,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان أمر به حمنة ابنة جحش في الاستحاضة التي كانت بها','2722','419','0');
INSERT INTO [t22547] VALUES (435,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يدل على مقدار قليل الحيض كم هو','2725','419','0');
INSERT INTO [t22547] VALUES (436,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الدم الأسود، والدم الذي ليس كذلك هل يدلان على حقيقة الحيض أو على حقيقة الاستحاضة أم لا؟','2730','419','0');
INSERT INTO [t22547] VALUES (437,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تفريقه بين عتق النسمة وفك الرقبة','2741','419','0');
INSERT INTO [t22547] VALUES (438,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " والخال وارث من لا وارث له "','2746','419','0');
INSERT INTO [t22547] VALUES (439,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم: " من أتبع على مليء فليتبع "','2750','419','0');
INSERT INTO [t22547] VALUES (440,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره بإخراج اليهود والنصارى من جزيرة العرب','2755','419','0');
INSERT INTO [t22547] VALUES (441,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في النجباء من أصحاب رسول الله صلى الله عليه وسلم الذين أعطيهم','2765','419','0');
INSERT INTO [t22547] VALUES (442,'باب بيان مشكل ما روي عن حذيفة بن اليمان رضي الله عنه عن رسول الله صلى الله عليه وسلم في المساجد التي لا يجوز الاعتكاف إلا فيها','2771','419','0');
INSERT INTO [t22547] VALUES (443,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من سعادة المرء بالمسكن الواسع والجار الصالح والمركب الهني','2773','419','0');
INSERT INTO [t22547] VALUES (444,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الثواب على الصبر على الجار السوء','2777','419','0');
INSERT INTO [t22547] VALUES (445,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ما زال جبريل يوصيني بالجار حتى ظننت أنه سيورثه ".','2780','419','0');
INSERT INTO [t22547] VALUES (446,'باب بيان مشكل ما اختلف فيه أهل العلم في الجار من هو وما روي عن رسول الله صلى الله عليه وسلم مما قد كشف ذلك','2785','419','0');
INSERT INTO [t22547] VALUES (447,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في خير الجيران , من هو؟','2790','419','0');
INSERT INTO [t22547] VALUES (448,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في سورة ص هل فيها سجدة أم لا','2792','419','0');
INSERT INTO [t22547] VALUES (449,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره باتخاذ المساجد في الدور','2802','419','0');
INSERT INTO [t22547] VALUES (450,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الضيافة من إيجابه إياها ومما سوى ذلك','2806','419','0');
INSERT INTO [t22547] VALUES (451,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " اللحد لنا والشق لغيرنا أو لأهل الكتاب "','2819','419','0');
INSERT INTO [t22547] VALUES (452,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الولاء بالموالاة','2835','419','0');
INSERT INTO [t22547] VALUES (453,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إسلام الرجل على يد الرجل أنه يكون بذلك أولى الناس بمحياه وبمماته هل يكون بذلك مولى له أو لا يكون بذلك مولى له حتى يكون بينه وبينه موالاة مستأنفة','2839','419','0');
INSERT INTO [t22547] VALUES (454,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إقراعه بين المدعيين عنده في اليمين أيهما يبدأ به فيها','2847','419','0');
INSERT INTO [t22547] VALUES (455,'باب بيان مشكل ما اختلف أهل العلم فيه من أكثر مدة الحمل بما روي عن رسول الله صلى الله عليه وسلم في ذلك','2850','419','0');
INSERT INTO [t22547] VALUES (456,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رسل الكفار أنهم لا يقتلون , وإن كان منهم ما لو لم يكونوا رسلا وجب به له قتله','2856','419','0');
INSERT INTO [t22547] VALUES (457,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من بدل دينه فاقتلوه "','2860','419','0');
INSERT INTO [t22547] VALUES (458,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " تحرز المرأة ثلاثة مواريث عتيقها , ولقيطها وولدها الذي تلاعن عليه "','2866','419','0');
INSERT INTO [t22547] VALUES (459,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " بين قبري ومنبري روضة من رياض الجنة "','2871','419','0');
INSERT INTO [t22547] VALUES (460,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان يعوذ به حسنا، وحسينا رضي الله عنهما من قوله: " من كل شيطان وهامة , ومن كل عين لامة "','2884','419','0');
INSERT INTO [t22547] VALUES (461,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العين أنها حق وفي الاغتسال لمن بلي بها','2890','419','0');
INSERT INTO [t22547] VALUES (462,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الحبوة يوم الجمعة والإمام يخطب','2900','419','0');
INSERT INTO [t22547] VALUES (463,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المدد يقدمون على الإمام في دار الحرب بعدما غنم فيها غنائم , ولم يخرج منها , ولم يقسمها , ولم يبعها هل يشركون من معه في تلك الغنائم أم لا','2904','419','0');
INSERT INTO [t22547] VALUES (464,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما رخص فيه من الكلام الذي يراد به الصلاح بين الناس والكلام الذي يحدث به الرجل امرأته , والكلام الذي تحدث به المرأة زوجها , والكلام في الحرب','2913','419','0');
INSERT INTO [t22547] VALUES (465,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الحيات من إطلاق قتلها ومن ترك الرخصة في ذلك , وما روي عنه فيها مما يخالف ذلك','2927','419','0');
INSERT INTO [t22547] VALUES (466,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ابن صياد اليهودي مما أطلق به قوم عليه الدجال ومما منع به قوم أن يكون هو الدجال','2942','419','0');
INSERT INTO [t22547] VALUES (467,'باب بيان مشكل ما اختلف أهل العلم فيه في إسلام الصبيان الذين لم يبلغوا بما روي عن رسول الله صلى الله عليه وسلم فيه من سؤاله ابن صياد قبل بلوغه أتشهد أنه رسول الله صلى الله عليه وسلم','2949','419','0');
INSERT INTO [t22547] VALUES (468,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الكذابين الثلاثين الذين يخرجون بعده هل هم دجالون أم لا؟','2952','419','0');
INSERT INTO [t22547] VALUES (469,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حمل رءوس القتلى المقتولين نكالا من بلد إلى بلد ومن ناحية إلى ناحية من الإباحة وما روي عن أبي بكر رضي الله عنه مما يخالف ذلك','2957','419','0');
INSERT INTO [t22547] VALUES (470,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يقضي بين المختلفين من أهل العلم في الواجب على قاذف الجماعة هل هو حد واحد أو حد لكل واحد منهم','2964','419','0');
INSERT INTO [t22547] VALUES (471,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صوم يوم عرفة من حض عليه ومن نهي عنه','2969','419','0');
INSERT INTO [t22547] VALUES (472,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صيام العشر الأول من ذي الحجة مما يدل على تركه كان إياه وعلى حض منه عليه','2974','419','0');
INSERT INTO [t22547] VALUES (473,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " كل عمل ابن آدم فهو له إلا الصيام فإنه لي , وأنا أجزي به " يعني لله تبارك وتعالى','2981','419','0');
INSERT INTO [t22547] VALUES (474,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قطع السدر من نهي , ومن إباحة','2984','419','0');
INSERT INTO [t22547] VALUES (475,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أنه قال: " أكثر أهل الجنة البله " وما يدخل في ذلك','2994','419','0');
INSERT INTO [t22547] VALUES (476,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في البضع ما هو','2999','419','0');
INSERT INTO [t22547] VALUES (477,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما ذبحه من لا يملكه من الأنعام بغير إذن مالكه هل يكون ذلك ذكاة له يحل أكله أم لا؟','3005','419','0');
INSERT INTO [t22547] VALUES (478,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يقضي بين المختلفين من الفقهاء في الشاة المغصوبة إذا ذبحت وشويت , هل للمغصوبة منه أن يأخذها وهي كذلك أم لا؟','3015','419','0');
INSERT INTO [t22547] VALUES (479,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما تعلق به قوم من أن العبد لا طلاق له','3017','419','0');
INSERT INTO [t22547] VALUES (480,'باب بيان مشكل حكم المعصفر: هل هو من الطيب أو ليس من الطيب فيما يروى عن رسول الله صلى الله عليه وسلم','3032','419','0');
INSERT INTO [t22547] VALUES (481,'الجزء ٨','3032','0','0');
INSERT INTO [t22547] VALUES (482,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القتيل الذي أدركه سلمة بن الأكوع حتى قتله دون من كان بحضرته من الناس لا في معمعة حرب ومن قوله صلى الله عليه وسلم " له سلبه أجمع " يعني لسلمة','3034','481','0');
INSERT INTO [t22547] VALUES (483,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أجر الأجير على العمل، متى يجب له أخذه من مستأجره عليه','3037','481','0');
INSERT INTO [t22547] VALUES (484,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الطعام الذي يجب على من دعي عليه إتيانه','3041','481','0');
INSERT INTO [t22547] VALUES (485,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رفيع اللباس وفي خسيسه','3061','481','0');
INSERT INTO [t22547] VALUES (486,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في خطابه لأبي أبي الأحوص المختلف في اسمه فقائل يقول: إنه عوف بن مالك، وقائل يقول إنه مالك بن عوف، وذكر البخاري أنه عوف بن مالك بن نضلة، ولا يختلفون أنه من بني جشم، بقوله له: " إذا آتاك الله عز','3067','481','0');
INSERT INTO [t22547] VALUES (487,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من خروجه على مخرمة أبي المسور بن مخرمة وهو لابس القباء الذي كان خبأه له','3071','481','0');
INSERT INTO [t22547] VALUES (488,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في استبراء المسبيات من الحوامل وممن سواهن','3075','481','0');
INSERT INTO [t22547] VALUES (489,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من علي رضي الله عنه في قسمته خمس ما بعث في قسمته من السبي ووقوع الوصيفة التي كانت فيه في آله، وما كان منه فيها من وطئه لها، ومن تناهي ذلك إلى رسول الله صلى الله عليه وسلم بلا استبراء مذكور','3080','481','0');
INSERT INTO [t22547] VALUES (490,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لحوم الخيل من كراهة ومن إباحة من حديث جابر بن عبد الله','3083','481','0');
INSERT INTO [t22547] VALUES (491,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من غير حديث جابر بن عبد الله في لحوم الخيل من كراهة ومن إباحة','3092','481','0');
INSERT INTO [t22547] VALUES (492,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يرد القضاء إلا الدعاء، ولا يزيد في العمر إلا البر "','3098','481','0');
INSERT INTO [t22547] VALUES (493,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يدفع عن الإنسان بقوله حين يصبح وحين يمسي: بسم الله الذي لا يضر مع اسمه شيء في الأرض ولا في السماء، وهو السميع العليم','3103','481','0');
INSERT INTO [t22547] VALUES (494,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أنزل القرآن على سبعة أحرف، لكل آية منها ظهر وبطن "','3107','481','0');
INSERT INTO [t22547] VALUES (495,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قضائه بحضانة ابنة حمزة رضي الله عنها لخالتها أسماء ابنة عميس، وترك منعه إياها من ذلك بالزوج الذي لها، وهو جعفر بن أبي طالب رضي الله عنه، إذ كان غير ذي رحم محرم منها','3109','481','0');
INSERT INTO [t22547] VALUES (496,'باب بيان مشكل ما روي في الطفل والطفلة إذا تنازعه أبواه، أيهما أولى أن يكون عنده منهما','3115','481','0');
INSERT INTO [t22547] VALUES (497,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " نزل القرآن على سبعة أحرف "','3126','481','0');
INSERT INTO [t22547] VALUES (498,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أنزل القرآن على ثلاثة أحرف "','3146','481','0');
INSERT INTO [t22547] VALUES (499,'باب بيان مشكل ما روي في الحروف المتفقة في الخط، المختلفة في اللفظ','3148','481','0');
INSERT INTO [t22547] VALUES (500,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما قد اختلف القراء فيه فزاد بعضهم على بعض فيه ما قصر عنه غيره منهم','3151','481','0');
INSERT INTO [t22547] VALUES (501,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في المؤمن: " أنه غر كريم " وفي الفاجر: " أنه خب لئيم "','3154','481','0');
INSERT INTO [t22547] VALUES (502,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أن: " للقرشي مثلي قوة الرجل من غير قريش "','3156','481','0');
INSERT INTO [t22547] VALUES (503,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " انظروا إلى قريش، فاسمعوا من قولهم، وذروا فعلهم "','3158','481','0');
INSERT INTO [t22547] VALUES (504,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الاختيار مما قرئ عليه قول الله عز وجل: الله الذي خلقكم من ضعف أو: (من ضعف) على ما قرئ عليه من هذين الحرفين','3160','481','0');
INSERT INTO [t22547] VALUES (505,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره الملتقط بالإشهاد على ما التقطه , وفي المراد بذلك ما هو','3162','481','0');
INSERT INTO [t22547] VALUES (506,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في شجر مكة، وفي خلاها، ومن قول العباس له عند ذلك لما وقف على منعه منه: " إلا الإذخر "، ومن قوله له جوابا لكلامه: " إلا الإذخر "','3167','481','0');
INSERT INTO [t22547] VALUES (507,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في خلا مكة، هل هو على حرمته في الأحوال كلها، أو على حرمته في حال دون حال، أو بفعل دون فعل قال أبو جعفر: اختلف أهل العلم في حشيش مكة وفيما سواه مما حرمه رسول الله صلى الله عليه وسلم في حصده , وفي','3173','481','0');
INSERT INTO [t22547] VALUES (508,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المعنى الذي يحل به لمن اشترى طعاما جزافا أن يبيعه','3177','481','0');
INSERT INTO [t22547] VALUES (509,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تارك الصلاة من المسلمين، لا على الجحود بها، هل يكون بذلك مرتدا عن الإسلام أم لا.','3190','481','0');
INSERT INTO [t22547] VALUES (510,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من لم يحافظ على الصلوات الخمس كان يوم القيامة مع فرعون، وهامان، وقارون، وأبي صاحب العظام "','3198','481','0');
INSERT INTO [t22547] VALUES (511,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن ترك الجمعة ثلاث مرار','3200','481','0');
INSERT INTO [t22547] VALUES (512,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي أمر بجلده في قبره مائة جلدة، فلم يزل يسأل ويدعو حتى رد إلى جلدة واحدة','3203','481','0');
INSERT INTO [t22547] VALUES (513,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لينتهين أقوام عن ودعهم الجمعات، أو ليختمن الله على قلوبهم، أو ليكونن من الغافلين "','3205','481','0');
INSERT INTO [t22547] VALUES (514,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من فاتته صلاة العصر فكأنما وتر أهله وماله "','3207','481','0');
INSERT INTO [t22547] VALUES (515,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن إضاعة المال','3209','481','0');
INSERT INTO [t22547] VALUES (516,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما دعا بدعوى الجاهلية، أو تعزى بعزاء الجاهلية','3221','481','0');
INSERT INTO [t22547] VALUES (517,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي كان يكتب له لما كان يملي عليه: غفورا رحيما، فيكتب: عليما حكيما، ويقول للنبي صلى الله عليه وسلم: أكتب كذا وكذا من هذا الجنس، فيقول: " نعم، اكتب كيف شئت "','3227','481','0');
INSERT INTO [t22547] VALUES (518,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما استدل به محمد بن الحسن مما كان أبو حنيفة يقوله في إباحة الربا بين المسلمين وبين المشركين في دار الحرب','3229','481','0');
INSERT INTO [t22547] VALUES (519,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المواريث التي قسمت في الجاهلية، وفي المواريث التي أدركها الإسلام من مواريث الجاهلية قبل أن تقسم','3236','481','0');
INSERT INTO [t22547] VALUES (520,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحكام الغصوب في الجاهلية التي اختصموا إليه فيها في الإسلام','3238','481','0');
INSERT INTO [t22547] VALUES (521,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرجل الذي كان يكتب له فكان يملي عليه: عليما حكيما، فيكتب: سميعا عليما، ولا ينكر ذلك رسول الله صلى الله عليه وسلم منه، فارتد عن الإسلام، هل كان من قريش أو من الأنصار أو من غيرهم','3241','481','0');
INSERT INTO [t22547] VALUES (522,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لأسامة بن زيد في الرجل الذي قتله بعد أن قال له إني مسلم، ما قال له في ذلك','3243','481','0');
INSERT INTO [t22547] VALUES (523,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القوم الذين قتلهم خالد بن الوليد بعد أن كان منهم أن قالوا: صبأنا صبأنا','3247','481','0');
INSERT INTO [t22547] VALUES (524,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان من عمار بن ياسر ومن خالد بن الوليد في القوم الذين بعثا إليهم، فاعتصموا بالتوحيد فقتلهم خالد','3249','481','0');
INSERT INTO [t22547] VALUES (525,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في النفر الخثعميين الذين كان بعث إليهم خالدا، ومن قتله إياهم بعد اعتصامهم بالسجود','3251','481','0');
INSERT INTO [t22547] VALUES (526,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إلقاء الأرض الرجل المدفون فيها القاتل للذي قال: لا إله إلا الله، وقتله إياه على أن ذلك كان تعوذا منه','3253','481','0');
INSERT INTO [t22547] VALUES (527,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جلود الميتة في طهارتها بالدباغ، وفيما يخالف ذلك','3255','481','0');
INSERT INTO [t22547] VALUES (528,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن الركوب على جلود السباع','3265','481','0');
INSERT INTO [t22547] VALUES (529,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن المكامعة والمعاكمة','3278','481','0');
INSERT INTO [t22547] VALUES (530,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " قفلة كغزوة "','3284','481','0');
INSERT INTO [t22547] VALUES (531,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " للغازي أجره، وللجاعل أجره وأجر الغازي "','3287','481','0');
INSERT INTO [t22547] VALUES (532,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القردة والخنازير، أهي مما مسخ من الأمم أم لا','3294','481','0');
INSERT INTO [t22547] VALUES (533,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في خشيته أن تكون الفأرة من الممسوخ وهل كان بعد ذلك مأ رفع تلك الخشية وبان به له صلى الله عليه وسلم أنها ليست من المسوخ','3299','481','0');
INSERT INTO [t22547] VALUES (534,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الضباب مما يبيح أكلها وما يمنع','3302','481','0');
INSERT INTO [t22547] VALUES (535,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا سقط الذباب في طعام أحدكم فليمقله؛ فإن في أحد جناحيه شفاء، وفي الآخر داء، وإنما يقدم الداء ويؤخر الشفاء "','3315','481','0');
INSERT INTO [t22547] VALUES (536,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من قال لأخيه تعال أقامرك فليتصدق " وما في حديث الأوزاعي زيادة على ذلك " فليتصدق بالقمار " قال أبو جعفر: وقد روينا فيما تقدم منا في كتابنا هذا الحديث من حديث يونس بن عبد الأعلى','3320','481','0');
INSERT INTO [t22547] VALUES (537,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في كل واحدة من الجنازتين اللتين مر بهما عليه، فأثني على إحداهما خير، وأثني على الأخرى منهما شر','3324','481','0');
INSERT INTO [t22547] VALUES (538,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي فيه نزلت: لولا كتاب من الله سبق لمسكم فيما أخذتم عذاب عظيم','3332','481','0');
INSERT INTO [t22547] VALUES (539,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن لبس الخاتم إلا لذي سلطان','3340','481','0');
INSERT INTO [t22547] VALUES (540,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على أنه لا ينبغي للرجل في كلامه أن يقطعه إلا على ما يحسن قطعه عليه، ولا يحول به معناه عن ما تكلم به من أجله','3344','481','0');
INSERT INTO [t22547] VALUES (541,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من الكلام الذي ادعى قوم أنه شعر، ونفى آخرون أن يكون كذلك','3346','481','0');
INSERT INTO [t22547] VALUES (542,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما كان منه عند تحريم الله عز وجل الخمر مما أمر به من سأله عن تخليله إياها، فنهاه عن ذلك، ولم يطلقه له.','3359','481','0');
INSERT INTO [t22547] VALUES (543,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رخصته للمحرم أن يضمد عينيه بالصبر إذا اشتكاهما','3374','481','0');
INSERT INTO [t22547] VALUES (544,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ولاة الأمر بعده، الذين هم في ولايتهم إياه خلفاء نبوة، من هم؟','3379','481','0');
INSERT INTO [t22547] VALUES (545,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الحين الذي يقع فيه ترك الأمر بالمعروف والنهي عن المنكر','3383','481','0');
INSERT INTO [t22547] VALUES (546,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الواجب في إتلاف الأشياء التي ليست موزونات ولا مكيلات، ما الواجب على متلفها مكانها','3386','481','0');
INSERT INTO [t22547] VALUES (547,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تربية الشعر على الرءوس من الجمم، ومن فرقه، ومن سدله','3390','481','0');
INSERT INTO [t22547] VALUES (548,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الأيام المرادة في قول الله عز وجل: واذكروا الله في أيام معدودات، فمن تعجل في يومين فلا إثم عليه، ومن تأخر فلا إثم عليه لمن اتقى','3402','481','0');
INSERT INTO [t22547] VALUES (549,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " اللهم إن فلانا هجاني وهو يعلم أني لست بشاعر فأهجوه، فالعنه عدد ما هجاني، أو مكان ما هجاني " قال أبو جعفر: قد ذكرنا هذا الحديث بإسناده فيما تقدم منا في كتابنا هذا. فقال قائل: في هذا','3404','481','0');
INSERT INTO [t22547] VALUES (550,'باب بيان مشكل ما روي عن ابن عباس، مما نحيط به علما أنه لم يأخذه إلا عن رسول الله صلى الله عليه وسلم من المراد بقول الله عز وجل: ما جعل الله لرجل من قلبين في جوفه','3407','481','0');
INSERT INTO [t22547] VALUES (551,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي نزلت فيه: إن الذين توفاهم الملائكة ظالمي أنفسهم الآية','3412','481','0');
INSERT INTO [t22547] VALUES (552,'باب بيان مشكل ما اختلف القراء فيه من قراءاتهم: لقد كان لسبإ هل هو مما يدخله الإعراب فيكون كما قرأه من قرأه: لقد كان لسبإ في مسكنهم أو بخلاف ذلك من ترك دخول الإعراب إياه، فيكون كما قرأه من قرأه: (لقد كان لسبأ في مسكنهم)','3416','481','0');
INSERT INTO [t22547] VALUES (553,'باب بيان مشكل ما روي عن أصحاب رسول الله صلى الله عليه وسلم فيما كانوا يعدون الآيات','3419','481','0');
INSERT INTO [t22547] VALUES (554,'الجزء ٩','3419','0','0');
INSERT INTO [t22547] VALUES (555,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان أسره. هل لمن كان أسره إليه أن يبديه في حياته أو بعد وفاته؟ قد روينا فيما تقدم منا في كتابنا هذا حديث مسروق عن عائشة رضي الله عنها في اجتماع نساء رسول الله صلى الله عليه وسلم ومجيء فاطمة','3421','554','0');
INSERT INTO [t22547] VALUES (556,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في مقدار صدقة الفطر من البر ومن ما سواه','3429','554','0');
INSERT INTO [t22547] VALUES (557,'باب بيان مشكل ما روي في صدقة الفطر مما قصد به فيها إلى المسلمين','3464','554','0');
INSERT INTO [t22547] VALUES (558,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما فيه نفي انتقاض وضوئه بنومه على الحال التي ينتقض فيها وضوء غيره من أمته لنومه كذلك','3472','554','0');
INSERT INTO [t22547] VALUES (559,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في النوم الذي ينتقض به وضوء من سواه من أمته','3476','554','0');
INSERT INTO [t22547] VALUES (560,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في التزام عبد الله بن المغفل الجراب الشحم الذي دلي يوم خيبر , ومن قوله مع ذلك: لا أعطي أحدا اليوم منه شيئا " , وتبسم رسول الله صلى الله عليه وسلم عند ذلك.','3496','554','0');
INSERT INTO [t22547] VALUES (561,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لأبي الدرداء " طف الصاع "','3503','554','0');
INSERT INTO [t22547] VALUES (562,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الستة الذين لعنهم , وأدخل فيهم المتسلط بالجبروت','3508','554','0');
INSERT INTO [t22547] VALUES (563,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الضبع في حل أكل لحمها وفي حرمته','3514','554','0');
INSERT INTO [t22547] VALUES (564,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الدليل على المراد بقول الله عز وجل: وحرم عليكم صيد البر ما دمتم حرما قال أبو جعفر: قد ذكرنا في الباب الذي قبل هذا الباب حديث عبد الرحمن بن أبي عمار الذي ذكرناه فيه , وذكرنا مع ذلك ما قد لحقه مما','3534','554','0');
INSERT INTO [t22547] VALUES (565,'باب بيان مشكل الصحيح مما يختلف أهل العلم في وقته من يوم النحر الذي ترمى فيه جمرة العقبة التي يجزئ رميها فيه: هل هو قبل طلوع الشمس أو بعد طلوعها؟ بما يروى عن رسول الله صلى الله عليه وسلم في ذلك','3542','554','0');
INSERT INTO [t22547] VALUES (566,'باب بيان مشكل ما روي عن ابن عباس , وعن جابر , في قولهما: " ما ندري بكم رمى رسول الله صلى الله عليه وسلم الجمرة من الحصى " ثم ما روى غيرهما مما فيه ذكر عدد ما رماها به','3551','554','0');
INSERT INTO [t22547] VALUES (567,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من ما كان منه في حجته من أمره أم سلمة زوجته أن توافي معه صلاة الصبح في يوم النحر بمكة','3566','554','0');
INSERT INTO [t22547] VALUES (568,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اللطمة: هل فيها قصاص أم لا؟','3576','554','0');
INSERT INTO [t22547] VALUES (569,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي كان من الأعرابي إليه في جره رداءه على رقبته حتى حمرها , ومن طلبه منه القود في ذلك','3584','554','0');
INSERT INTO [t22547] VALUES (570,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الموالي: " ليقاتلنكم على هذا الدين عودا كما قاتلتموهم عليه بدءا "','3586','554','0');
INSERT INTO [t22547] VALUES (571,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الناقة التي لعنتها صاحبتها من قوله لها: " خلي عنها , فإنها ملعونة "','3592','554','0');
INSERT INTO [t22547] VALUES (572,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حريم النخلة','3597','554','0');
INSERT INTO [t22547] VALUES (573,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الباب الذي استثناه من الأبواب التي كانت إلى مسجده , فأمر بسدها غير ذلك الباب','3602','554','0');
INSERT INTO [t22547] VALUES (574,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه رسله إلى الكفار في قتالهم أن ينزلوا أهل حصن من الحصون التي يحاصرونها على حكم الله عز وجل','3623','554','0');
INSERT INTO [t22547] VALUES (575,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القضاة: من منهم في النار , ومن منهم في الجنة؟ قال أبو جعفر: قد ذكرنا فيما تقدم منا في كتابنا هذا في بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لأبي ذر: " لا تقضين بين اثنين "','3627','554','0');
INSERT INTO [t22547] VALUES (576,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الحجة التي كانت قبل حجته من التأمير فيها , ومن قراءة: براءة , على الناس فيها , ومن كان أميره فيها , ومن كان المبلغ عنه فيها من أبي بكر , ومن علي','3633','554','0');
INSERT INTO [t22547] VALUES (577,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم بعثته أبا بكر على الحج في تلك الحجة التي ذكرناها في الباب الذي قبل هذا الباب من انشماره إلى ذي المجاز. كما روي عن ابن عباس مما يخالف حديث جابر الذي ذكرناه في الباب الذي قبل هذا الباب','3643','554','0');
INSERT INTO [t22547] VALUES (578,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المفصل من القرآن من سجوده فيه , ومن تركه السجود فيه','3648','554','0');
INSERT INTO [t22547] VALUES (579,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله لأبي بن كعب: " أمرت أن أقرأ عليك القرآن " , أو: " أمرت أن أقرئك القرآن "','3668','554','0');
INSERT INTO [t22547] VALUES (580,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا ينبغي , أو لا يحل لرجل يؤمن بالله واليوم الآخر يبيت ليلتين إلا ووصيته عنده مكتوبة "','3676','554','0');
INSERT INTO [t22547] VALUES (581,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم أنه كان يحب موافقة أهل الكتاب فيما لم يؤمر فيه بشيء','3683','554','0');
INSERT INTO [t22547] VALUES (582,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من إطلاقه للفريعة النقلة في عدتها من وفاة زوجها من الدار التي جاءها فيها بغتة , ومن أمره إياها بعد ذلك أن تمكث فيها حتى يبلغ الكتاب أجله','3686','554','0');
INSERT INTO [t22547] VALUES (583,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الغيل من كراهة له , ومن هم بنهي عنه , ومن نهي عنه , ومما سوى ذلك مما كان منه فيه','3688','554','0');
INSERT INTO [t22547] VALUES (584,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الخضاب للشعر من كراهة ومن إباحة قال أبو جعفر: قد ذكرنا فيما تقدم منا من كتابنا هذا حديث عبد الرحمن بن حرملة , عن عبد الله بن مسعود في العشرة الأشياء التي كان رسول الله صلى الله عليه وسلم يكرهها ,','3696','554','0');
INSERT INTO [t22547] VALUES (585,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تصفير اللحية من كراهة , ومن إباحة , ومن استحسان لذلك , وتقديم له على ما سواه قال أبو جعفر: قد ذكرنا في حديث عبد الرحمن بن حرملة , عن ابن مسعود , عن رسول الله صلى الله عليه وسلم: العشرة الأشياء','3712','554','0');
INSERT INTO [t22547] VALUES (586,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من النهي عن التبرج بالزينة قبل محلها قال أبو جعفر: قد ذكرنا في حديث عبد الرحمن بن حرملة , عن ابن مسعود رضي الله عنه في الأشياء التي كان رسول الله صلى الله عليه وسلم يكرهها التبرج بالزينة قبل محلها.','3721','554','0');
INSERT INTO [t22547] VALUES (587,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من كراهية عزل الماء عن محله قد ذكرنا في حديث عبد الرحمن بن حرملة , عن ابن مسعود كراهة رسول الله صلى الله عليه وسلم في الأشياء التي كان يكرهها: عزل الماء عن محله , وقد روينا عنه صلى الله عليه وسلم فيما','3722','554','0');
INSERT INTO [t22547] VALUES (588,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لعن الرجل أخاه','3730','554','0');
INSERT INTO [t22547] VALUES (589,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من سرته حسنته , وساءته سيئته , فهو مؤمن "','3733','554','0');
INSERT INTO [t22547] VALUES (590,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم عن ربه عز وجل في عبده الذي عمل ذنبا فاعترف به وسأله أن يغفر له','3738','554','0');
INSERT INTO [t22547] VALUES (591,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا زنت الأمة ولم تحصن فاجلدوها , ثم إن زنت فاجلدوها , ثم إن زنت فاجلدوها , ثم إن زنت فبيعوها ولو بضفير "','3740','554','0');
INSERT INTO [t22547] VALUES (592,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الخطبة للعيد , هل يجب على الناس القعود لها , والاستماع إليها كما يجب ذلك في الخطبة للجمعة أم لا؟','3758','554','0');
INSERT INTO [t22547] VALUES (593,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في دخول المواضع التي قد غضب الله عز وجل على أهلها من نهي , ومن إباحة','3760','554','0');
INSERT INTO [t22547] VALUES (594,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الوادي الذي مروا به في غزوة تبوك أنه واد ملعون','3764','554','0');
INSERT INTO [t22547] VALUES (595,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه في قبر أبي رغال وفي إخباره الناس أنه من ثمود , وأن الحرم منعه من ما نزل بسائر ثمود سواه , حتى خرج منه , فأدركته النقمة فأهلك','3770','554','0');
INSERT INTO [t22547] VALUES (596,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الهلال: " فإن غم عليكم فاقدروا له "','3781','554','0');
INSERT INTO [t22547] VALUES (597,'باب بيان مشكل ما رواه النعمان بن بشير الأنصاري , عن رسول الله صلى الله عليه وسلم في الوقت الذي كان يصلي فيه العشاء من الليل , أي وقت هو؟','3796','554','0');
INSERT INTO [t22547] VALUES (598,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أسباب المحبة , وأسباب البغضة , في قلوب الناس','3801','554','0');
INSERT INTO [t22547] VALUES (599,'باب بيان مشكل ما روي عن أبي معمر , عن ابن مسعود مما كانوا يقولونه في حياة رسول الله صلى الله عليه وسلم في التشهد في الصلاة: " السلام عليك أيها النبي ورحمة الله وبركاته " , وأنهم قالوه بعد النبي عليه السلام: " السلام على النبي "','3808','554','0');
INSERT INTO [t22547] VALUES (600,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من أنظر معسرا فله بكل يوم صدقة " , " ومن أنظر معسرا فله بكل يوم مثله صدقة "','3817','554','0');
INSERT INTO [t22547] VALUES (601,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله: " من أنظر معسرا , ووضع عنه , أظله الله عز وجل في ظله يوم لا ظل إلا ظله "','3820','554','0');
INSERT INTO [t22547] VALUES (602,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المقتول في الغزو مما نعلم يقينا أنه أراد إذا كان مجتعلا في غزوة أنه الأجير إلى أقصى قطرة من دمه','3830','554','0');
INSERT INTO [t22547] VALUES (603,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حكم ما بين الخطبة يوم الجمعة , وبين الدخول في الصلاة: هل هو موضع كلام , أو موضع سكوت؟','3832','554','0');
INSERT INTO [t22547] VALUES (604,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في من وقع على بهيمة','3836','554','0');
INSERT INTO [t22547] VALUES (605,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن وقع على ذات محرم منه','3840','554','0');
INSERT INTO [t22547] VALUES (606,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن وجد يعمل بعمل قوم لوط','3842','554','0');
INSERT INTO [t22547] VALUES (607,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ظل المؤمن يوم القيامة صدقته "','3849','554','0');
INSERT INTO [t22547] VALUES (608,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الطواف الواجب على القارن للعمرة والحج , هل هو طواف واحد , أو طوافان؟','3852','554','0');
INSERT INTO [t22547] VALUES (609,'باب بيان مشكل حديث ابن مسعود: " حدثنا رسول الله صلى الله عليه وسلم , وهو الصادق المصدوق " وما فيه مما هو عن رسول الله صلى الله عليه وسلم , وما فيه مما هو من كلام ابن مسعود','3869','554','0');
INSERT INTO [t22547] VALUES (610,'باب بيان مشكل ما رواه عياض بن حمار، عن النبي صلى الله عليه وسلم أنه قال: إن الله عز وجل قال: " إني خلقت عبادي حنفاء كلهم، وإنه أتتهم الشياطين فاجتالتهم عن دينهم، فحرمت عليهم ما أحللت لهم، وأمرتهم أن يشركوا بي ما لم أنزل عليهم به سلطانا "','3877','554','0');
INSERT INTO [t22547] VALUES (611,'الجزء ١٠','3877','0','0');
INSERT INTO [t22547] VALUES (612,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في دفعه ميراث المتوفى في زمنه إلى مولاه الأسفل الذي كان أعتقه','3881','611','0');
INSERT INTO [t22547] VALUES (613,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من أقرض قرضين، كان له أجر أحدهما لو تصدق به "','3888','611','0');
INSERT INTO [t22547] VALUES (614,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم إلى قوله: أفتهلكنا بما فعل المبطلون','3890','611','0');
INSERT INTO [t22547] VALUES (615,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المتحابين في الله عز وجل، والمتباذلين فيه، والمتزاورين فيه','3893','611','0');
INSERT INTO [t22547] VALUES (616,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المصلي لا يقيم صلبه بين ركوعه وبين سجوده','3901','611','0');
INSERT INTO [t22547] VALUES (617,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يقضي بين أهل العلم فيما اختلفوا فيه من تزويج العربي الأمة لغيره بإذن مولاها الذي هو عربي أو غير عربي، فتلد منه هل يكون ولدها رقيقا لمولاها أم لا؟ قال أبو جعفر: لا نعلم أحدا من أهل العلم الذين','3907','611','0');
INSERT INTO [t22547] VALUES (618,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبايا الوثنيات من حل وطئهن للمسلمين، ومن دليل على نسخ لذلك','3917','611','0');
INSERT INTO [t22547] VALUES (619,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يقضي بين المختلفين من أصحابه في المرادات بقوله: والمحصنات من النساء إلا ما ملكت أيمانكم','3924','611','0');
INSERT INTO [t22547] VALUES (620,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: إن الصفا والمروة من شعائر الله فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما','3936','611','0');
INSERT INTO [t22547] VALUES (621,'باب بيان مشكل ما روي عن جابر بن عبد الله أن رسول الله صلى الله عليه وسلم وأصحابه لم يطوفوا بين الصفا والمروة بعد أن أحرموا بالحج غير طوافهم الذي كانوا طافوه على أنهم في حجة، ثم حولوها إلى عمرة وحلوا منها، إلا من كان منهم معه الهدي','3942','611','0');
INSERT INTO [t22547] VALUES (622,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من باع تالدا سلط الله عليه تالفا','3947','611','0');
INSERT INTO [t22547] VALUES (623,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أرواث الأنعام المأكولة لحومها أنها لا تنجس ما تصيبه من الثياب، وأن الصلاة في الثياب التي أصابتها جائزة','3952','611','0');
INSERT INTO [t22547] VALUES (624,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أولى الناس بالإمامة','3957','611','0');
INSERT INTO [t22547] VALUES (625,'باب ما روي عن رسول الله صلى الله عليه وسلم مما يقضي بين المختلفين في الإمامة في الصلوات على الجنائز هل يدخل في قول النبي صلى الله عليه وسلم: " ولا يؤم أمير في إمارته " أم لا؟ قال أبو جعفر: روينا في الباب الذي قبل هذا الباب عن رسول الله صلى الله عليه','3963','611','0');
INSERT INTO [t22547] VALUES (626,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما تعلق به في إمامة الصبيان الذين لم يبلغوا في الفرائض من الصلوات','3966','611','0');
INSERT INTO [t22547] VALUES (627,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما سأل ربه عز وجل ثم ود أنه ما سأله إياه','3971','611','0');
INSERT INTO [t22547] VALUES (628,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن الصلاة بعد طلوع الشمس حتى ترتفع وبعد قيامها حتى تميل، وبعد تغيرها حتى تغرب، وهل كان ذلك على سائر الأيام، وهل كان ذلك على فرائض الصلوات ونوافلها أم لا؟','3975','611','0');
INSERT INTO [t22547] VALUES (629,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نومه ونوم أصحابه عن صلاة الصبح حتى أيقظهم حر الشمس','3987','611','0');
INSERT INTO [t22547] VALUES (630,'باب بيان مشكل السبب الذي أخر رسول الله صلى الله عليه وسلم الصلاة التي نام هو وأصحابه عنها حتى طلعت الشمس إلى الوقت الذي أخرها إليه ما هو قد ذكرنا في الآثار التي رويناها في الباب الذي ذكرنا فيه نوم رسول الله صلى الله عليه وسلم وأصحابه عن هذه الصلاة حتى','3995','611','0');
INSERT INTO [t22547] VALUES (631,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه لأبي الدرداء لما تلا صلى الله عليه وسلم وهو على المنبر: ولمن خاف مقام ربه جنتان، فقال له أبو الدرداء: وإن زنى وإن سرق بقوله له: " وإن زنى وإن سرق "','4000','611','0');
INSERT INTO [t22547] VALUES (632,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه لمن قال له بعد قوله: " من مات لا يشرك بالله شيئا دخل الجنة ": وإن زنى، وإن سرق؟ وبقوله له: " وإن زنى، وإن سرق "','4003','611','0');
INSERT INTO [t22547] VALUES (633,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعائشة: " إياك ومحقرات الذنوب، فإن لها من الله عز وجل طالبا "','4012','611','0');
INSERT INTO [t22547] VALUES (634,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما أمر به المشمت عند العطاس أن يقوله من: " يهديكم الله ويصلح بالكم " ومن: " يغفر الله لكم "','4014','611','0');
INSERT INTO [t22547] VALUES (635,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " يوشك أن يضرب الناس أكباد الإبل في طلب العلم، فلا يجدون عالما أعلم من عالم المدينة "','4022','611','0');
INSERT INTO [t22547] VALUES (636,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الذي قيل له فيه: إن فلانا نام الليلة حتى أصبح: " ذاك الذي بال الشيطان في أذنه "','4026','611','0');
INSERT INTO [t22547] VALUES (637,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حكم اللحم الذكي إذا أنتن','4031','611','0');
INSERT INTO [t22547] VALUES (638,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السمك الطافي من المنع من أكله وما روي عنه مما استدل به قوم على إباحة ذلك','4035','611','0');
INSERT INTO [t22547] VALUES (639,'باب بيان مشكل القضاء بين المختلفين من أهل العلم في الصلح من الأشياء المعلومة مقاديرها على الأجزاء من أجناسها المجهولة بما يروى عن رسول الله صلى الله عليه وسلم في ذلك','4053','611','0');
INSERT INTO [t22547] VALUES (640,'باب بيان مشكل ما اختلف فيه أهل العلم في أكفان الموتى فقال بعضهم: هي من رءوس تركاتهم، وقال بعضهم: هي من أثلاث تركاتهم بما يروى عن رسول الله صلى الله عليه وسلم مما يدل على ذلك قال أبو جعفر: لا نعلم أحدا من أهل العلم ذهب إلى أن أكفان الموتى من أثلاث','4059','611','0');
INSERT INTO [t22547] VALUES (641,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للقرشيين الذين كانوا جاءوا من مكة فقالوا: يا محمد، إنه قد لحق بك أبناؤنا وأرقاؤنا، فارددهم علينا، فقال: " يا معشر قريش، ليبعثن الله عليكم رجلا منكم، امتحن الله قلبه للإيمان، يضربكم على','4071','611','0');
INSERT INTO [t22547] VALUES (642,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن منكم من يقاتل الناس على تأويل القرآن كما قاتلتهم على تنزيله "','4077','611','0');
INSERT INTO [t22547] VALUES (643,'باب بيان مشكل ما روي في مقدار المدة التي كان أبو بكر رضي الله عنه أقامها مع رسول الله صلى الله عليه وسلم في الغار الذي كانا استترا فيه من الزمان','4093','611','0');
INSERT INTO [t22547] VALUES (644,'باب بيان مشكل ما روي في نوم علي رضي الله عنه في مكان النبي صلى الله عليه وسلم ولبوسه برده في الليلة التي خرج فيها رسول الله صلى الله عليه وسلم من مكة يريد دار الهجرة','4098','611','0');
INSERT INTO [t22547] VALUES (645,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا التقى المسلمان بسيفيهما، فالقاتل والمقتول في النار " وما كان من أبي بكرة من خطابه للأحنف بذلك لما خاطبه به من أجله','4101','611','0');
INSERT INTO [t22547] VALUES (646,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أمر به الناس أن يلزموه بعد الصلوات الفرائض من الذكر','4105','611','0');
INSERT INTO [t22547] VALUES (647,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما أمر به من يريد النوم أن يقوله عند نومه قال أبو جعفر: قد ذكرنا في الباب الذي قبل هذا الباب ما قد روي عن رسول الله صلى الله عليه وسلم في هذا المعنى','4115','611','0');
INSERT INTO [t22547] VALUES (648,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ثواب من حفظ العشر الآيات الأول من سورة قد أفلح المؤمنون','4117','611','0');
INSERT INTO [t22547] VALUES (649,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يتطوع به بعد صلاة الجمعة من الركوع في الموطن الذي يصلي فيه','4119','611','0');
INSERT INTO [t22547] VALUES (650,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الموضع الذي يصلي فيه ركعتي الفجر من المسجد أو من البيوت','4128','611','0');
INSERT INTO [t22547] VALUES (651,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا أقيمت الصلاة، فلا صلاة إلا المكتوبة "','4134','611','0');
INSERT INTO [t22547] VALUES (652,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن يفوته أن يصلي ركعتي الفجر حتى يصلي الفجر، أيصليهما عقيبا لها أم بعد ذلك؟','4144','611','0');
INSERT INTO [t22547] VALUES (653,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحكام الكفالات بالديون عن الموتى وفيما يدل من ذلك على أحكامها على الأحياء بغير أمورهم، وفي أداء ما كفل به عنهم، كذلك هل لمؤديه عنهم أن يرجع بما أداه عنهم عليهم في حياتهم أو في تركاتهم بعد وفاتهم','4154','611','0');
INSERT INTO [t22547] VALUES (654,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما ادعى قوم أنه يدل على جواز الاعتكاف بغير صوم','4161','611','0');
INSERT INTO [t22547] VALUES (655,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في النذر في الشرك مما لو نذره المسلم وجب عليه أن يفي به، ثم أسلم الذي نذر ذلك: هل يجب عليه في إسلامه الوفاء بذلك أم لا؟ قد ذكرنا في الباب الذي قبل هذا الباب أمر رسول الله صلى الله عليه وسلم عمر بعد','4180','611','0');
INSERT INTO [t22547] VALUES (656,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اهتزاز العرش لموت سعد بن معاذ','4186','611','0');
INSERT INTO [t22547] VALUES (657,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لما كان من الجذع الذي كان يخطب الناس إليه لما تحول عنه إلى المنبر الذي اتخذه ليخطب عليه','4196','611','0');
INSERT INTO [t22547] VALUES (658,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا أقيمت الصلاة فلا تقوموا حتى تروني "','4208','611','0');
INSERT INTO [t22547] VALUES (659,'باب بيان مشكل ما روي في الإمام في الصلاة التي كانت آخر صلوات رسول الله صلى الله عليه وسلم، فكان يصلي فيها جالسا وأبو بكر يصلي فيها قائما، والناس يصلون قياما من كان الإمام فيها من رسول الله صلى الله عليه وسلم، ومن أبي بكر رضي الله عنه','4212','611','0');
INSERT INTO [t22547] VALUES (660,'باب بيان مشكل ما روي في خروج المصلي خلف معاذ بن جبل إلى صلاة نفسه هل كان بتكبير مستأنف أو ببناء على دخوله كان مع معاذ','4222','611','0');
INSERT INTO [t22547] VALUES (661,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي من أجله صلى عثمان بن عفان رضي الله عنه في حجه بالناس بمنى أربعا','4228','611','0');
INSERT INTO [t22547] VALUES (662,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعبد الله بن عمر في امرأته التي كان طلقها وهي حائض أن يراجعها، فإذا طهرت، طلقها وهي طاهر أو حامل','4233','611','0');
INSERT INTO [t22547] VALUES (663,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن أصاب امرأته وهي حائض','4246','611','0');
INSERT INTO [t22547] VALUES (664,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من الواجب على من ترك الجمعة متعمدا','4263','611','0');
INSERT INTO [t22547] VALUES (665,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الغازي يغل من قتله ومن إحراق رحله','4265','611','0');
INSERT INTO [t22547] VALUES (666,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه في ابن أمة زمعة الذي ادعاه سعد لأخيه وادعاه عبد بن زمعة لأبيه','4270','611','0');
INSERT INTO [t22547] VALUES (667,'الجزء ١١','4270','0','0');
INSERT INTO [t22547] VALUES (668,'باب بيان مشكل ما اختلف فيه أهل العلم من إباحة إتمام الصلاة في السفر للمسافر ومن منعه من ذلك بما روي عن النبي صلى الله عليه وسلم فيه','4283','667','0');
INSERT INTO [t22547] VALUES (669,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن الله تبارك وتعالى وضع عن المسافر شطر صلاته "','4291','667','0');
INSERT INTO [t22547] VALUES (670,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إعتاقه من خرج إليه من عبيد الطائف، وأن ممن خرج إليه منهم أبا بكرة، وأنه بذلك مولى لرسول الله صلى الله عليه وسلم','4295','667','0');
INSERT INTO [t22547] VALUES (671,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تقديمه المحررين في العطاء على غيرهم من الناس، ما كان مراده في ذلك','4301','667','0');
INSERT INTO [t22547] VALUES (672,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لبني النضير لما أمر بإجلائهم من المدينة عند قولهم له: إن لنا ديونا لم تحل: " ضعوا وتعجلوا "','4305','667','0');
INSERT INTO [t22547] VALUES (673,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن إخافة الأنفس بالدين','4316','667','0');
INSERT INTO [t22547] VALUES (674,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " المستشار مؤتمن "','4325','667','0');
INSERT INTO [t22547] VALUES (675,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أمر به أصحابه في الحجة التي حجوها معه لما طافوا بالبيت وبالصفا والمروة أن يحلوا إلا من كان معه الهدي','4330','667','0');
INSERT INTO [t22547] VALUES (676,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعلي لما قدم عليه من اليمن في حجته: " بماذا أهللت؟ " فقال: قلت: اللهم إني أهل بما أهل به رسولك "، ومن أمره إياه أن يمكث على إحرامه حتى يحل من حجه، وما روي عنه في أبي موسى بعد إعلامه إياه','4343','667','0');
INSERT INTO [t22547] VALUES (677,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " ما تركت بعدي فتنة هي أضر على الرجال من النساء "، ومن قوله: " لكل أمة فتنة، وفتنة أمتي المال "','4346','667','0');
INSERT INTO [t22547] VALUES (678,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أكل ذي الدين من مال من له عليه ذلك الدين بطيب نفسه: هل ذلك مباح له أم لا؟','4350','667','0');
INSERT INTO [t22547] VALUES (679,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الهدايا إلى ولاة الأمور','4362','667','0');
INSERT INTO [t22547] VALUES (680,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قبوله الهدايا من ملوك الأعاجم واستئثاره بها، وما روي مما يدل على أنه صلى الله عليه وسلم في ذلك بخلاف من تولى أمور المسلمين بعده','4369','667','0');
INSERT INTO [t22547] VALUES (681,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الأعمى: " اذهبوا بنا نعود ذلك البصير "','4385','667','0');
INSERT INTO [t22547] VALUES (682,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في جوابه من سأله عن ذوي المكارم في الجاهلية ممن لم يدرك الإسلام','4387','667','0');
INSERT INTO [t22547] VALUES (683,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يدل على مراد الله عز وجل بقوله في آية المكاتبين: وآتوهم من مال الله الذي آتاكم','4396','667','0');
INSERT INTO [t22547] VALUES (684,'باب بيان مشكل ما روي في حديث عائشة رضي الله عنها عن رسول الله صلى الله عليه وسلم في أمره إياها بابتياع بريرة وهي مكاتبة قبل خروجها منها قال أبو جعفر: في حديث عائشة الذي رويناه في الباب الذي قبل هذا الباب قول رسول الله صلى الله عليه وسلم لها في بريرة بعد','4406','667','0');
INSERT INTO [t22547] VALUES (685,'باب بيان مشكل ما روي مما قد اختلف فيه أهل العلم في بيع الأمة ذات الزوج فيقول بعضهم: إنه طلاق لها، ويقول بعضهم: إنه غير طلاق لها بما قد روي عن رسول الله صلى الله عليه وسلم أنه كان منه في بريرة','4408','667','0');
INSERT INTO [t22547] VALUES (686,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تخييره بريرة بين فراق زوجها وبين المقام معه هل كان ذلك للعتاق الذي وقع عليها على كل أحوال زوجها من حرية أم عبودية خاصة دون الحرية','4421','667','0');
INSERT INTO [t22547] VALUES (687,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الخيار الذي جعله لبريرة لما أعتقت هل هو كخيارها لو خيرها زوجها، أو بخلاف ذلك؟','4435','667','0');
INSERT INTO [t22547] VALUES (688,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله فيما تصدق به على بريرة فأهدته إلى عائشة: " هو عليها صدقة ولنا هدية "','4444','667','0');
INSERT INTO [t22547] VALUES (689,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه في بريرة لما سأل أهلها عائشة أن يكون ولاؤها لهم بأدائها مكاتبتها إليهم أو بابتياعها إياها أو إعتاقها بعد ذلك قال أبو جعفر: وقد ذكرنا فيما تقدم منا في كتابنا هذا حديث مالك بن أنس عن هشام','4451','667','0');
INSERT INTO [t22547] VALUES (690,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما استدل به غير واحد من أهل العلم على جواز بيع الرجل عبده من رجل على أن يعتقه قال أبو جعفر: قد روينا فيما تقدم منا في أبواب مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمور بريرة قوله لعائشة رضي','4471','667','0');
INSERT INTO [t22547] VALUES (691,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه في الجمل الذي ابتاعه من جابر بن عبد الله في إطلاقه له ركوبه إلى المدينة: هل كان ذلك بشرط وقع البيع بينه وبينه عليه أم بخلاف ذلك؟','4476','667','0');
INSERT INTO [t22547] VALUES (692,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه الأعراب حين سألوه: ما خير ما أعطي العبد؟ بقوله لهم: " خلق حسن "','4487','667','0');
INSERT INTO [t22547] VALUES (693,'باب بيان مشكل ما روي عن النبي صلى الله عليه وسلم من قوله: " أكمل المؤمنين إيمانا أحسنهم خلقا "','4495','667','0');
INSERT INTO [t22547] VALUES (694,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إنما بعثت لأتمم صالح الأخلاق "','4498','667','0');
INSERT INTO [t22547] VALUES (695,'باب بيان مشكل ما روي من خلق رسول الله صلى الله عليه وسلم','4500','667','0');
INSERT INTO [t22547] VALUES (696,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تركه عقوبة حاطب بن أبي بلتعة على ما كان منه في كتابه إلى أهل مكة من كفار قريش يخبرهم ببعض أمر رسول الله صلى الله عليه وسلم','4504','667','0');
INSERT INTO [t22547] VALUES (697,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعبد الله بن مسعود لما مر به هو وأبو بكر وهو يرعى الغنم التي كان يرعاها لعقبة بن أبي معيط: " أمعك لبن؟ " قال: إني مؤتمن، ومما في هذا الحديث سوى ذلك','4510','667','0');
INSERT INTO [t22547] VALUES (698,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الإدام: ما هي؟','4512','667','0');
INSERT INTO [t22547] VALUES (699,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العارية مما يحتج به من يوجب ضمانها ومما سوى ذلك، مما روي عنه فيها','4520','667','0');
INSERT INTO [t22547] VALUES (700,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على لزوم الكفالات بالأنفس','4532','667','0');
INSERT INTO [t22547] VALUES (701,'باب بيان مشكل ما روي عن عبد الله بن عباس في السبب الذي أنزلت فيه: فإن جاءوك فاحكم بينهم، إلى قوله: وإن حكمت فاحكم بينهم بالقسط','4538','667','0');
INSERT INTO [t22547] VALUES (702,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الحباء، والعدة، والصداق قبل عصمة النكاح وفي ذلك بعد عصمته','4550','667','0');
INSERT INTO [t22547] VALUES (703,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من البينة على المدعي واليمين على المدعى عليه، هل يقومان عنه من طريق الإسناد أم لا؟ قال أبو جعفر: الذي وجدناه عن رسول الله صلى الله عليه وسلم مما لا يتدافع صحته أهل الأسانيد.','4552','667','0');
INSERT INTO [t22547] VALUES (704,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اختلاف المتبايعين في الثمن قال أبو جعفر: هذا باب يزعم أهل العلم بالأسانيد أن الذي يجدونه فيه عن النبي صلى الله عليه وسلم هو:','4561','667','0');
INSERT INTO [t22547] VALUES (705,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن أكل برجل مسلم، أو اكتسى به أو قام به مقام سمعة','4565','667','0');
INSERT INTO [t22547] VALUES (706,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الصعيد المذكور في كتاب الله للمتيمم به عند إعواز الماء، ما هو','4567','667','0');
INSERT INTO [t22547] VALUES (707,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الوتر، هل له وقت معلوم لا يصلى إلا فيه وإن لم يصل فيه لم يصل بعده؟ أو هل الدهر له وقت؟','4573','667','0');
INSERT INTO [t22547] VALUES (708,'باب بيان مشكل ما اختلف أهل العلم فيه من القنوت في الوتر، وهل هو قبل الركوع أو بعده؟ وما روي عن رسول الله صلى الله عليه وسلم مما يقضي بينهم في ذلك قال أبو جعفر: قد روي عن غير واحد من أصحاب رسول الله صلى الله عليه وسلم في قنوته في الوتر أنه كان قبل','4584','667','0');
INSERT INTO [t22547] VALUES (709,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما اختلف ألوانه من الحنطة ومن الشعير ومن التمر ومن الملح أنه لا بأس به مثلين بمثل','4596','667','0');
INSERT INTO [t22547] VALUES (710,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المقدار الذي ورثه الجد من ابن ابنه','4599','667','0');
INSERT INTO [t22547] VALUES (711,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لو كان مطعم بن عدي حيا وكلمني في هؤلاء النتنى، يعني أسرى بدر، لأطلقتهم له "','4602','667','0');
INSERT INTO [t22547] VALUES (712,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما كان منه في سبايا هوازن لما سألوه أن يمن عليهم وأنه لم يفعل ذلك إلا بعد رضا المسلمين به','4604','667','0');
INSERT INTO [t22547] VALUES (713,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه من الرجوع إلى أقوال عرفاء المسلمين فيما ذكروه له مما كان من القوم الذين هم عرفاؤهم في السبايا اللائي أراد إطلاقهم لقومهم قال أبو جعفر: قد ذكرنا في الباب الذي قبل هذا الباب من حديث ابن أبي','4609','667','0');
INSERT INTO [t22547] VALUES (714,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الأسارى هل جائز أن يقتلوا أم لا؟ قال أبو جعفر: قد كان عطاء بن أبي رباح يكره قتل الأسير صبرا.','4611','667','0');
INSERT INTO [t22547] VALUES (715,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الغرة التي قضى بها في الجنين، وما مقدارها من الدية','4621','667','0');
INSERT INTO [t22547] VALUES (716,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما قاله ليزيد أبي معن في صدقته التي أخذها معن من الرجل الذي كان وضعها عنده: " لك ما نويت يا يزيد، ولك يا معن ما أخذت "','4628','667','0');
INSERT INTO [t22547] VALUES (717,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الزيادات في أثمان الأشياء المبيعات: هل تلحق بالأثمان التي عقدت تلك البياعات عليها أم لا؟ قال أبو جعفر: قد ذكرنا فيما تقدم منا في كتابنا هذا حديث جابر بن عبد الله في بيعه جمله رسول الله صلى الله','4630','667','0');
INSERT INTO [t22547] VALUES (718,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الزيادة فيما لا تجوز الزيادة فيه، بل ترجع إلى زائدها، أو تكون هبة منه للذي زادها إياه','4638','667','0');
INSERT INTO [t22547] VALUES (719,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أنزل الله عليه في أهل الكتاب إذا تحاكموا إليه في حدودهم من الحكم بينهم فيها، ومن الإعراض عنهم فيها، وهل نسخ ذلك بقوله: وأن احكم بينهم بما أنزل الله أم لا؟','4640','667','0');
INSERT INTO [t22547] VALUES (720,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حدود أهل الكتاب في الزنى وهل هي الرجم؟ وهل هو باق فيهم إلى يوم القيامة، أو قد نسخ ذلك وأعيد إلى غيره؟ قال أبو جعفر: قال قائل: فقد رويت في الباب الذي قبل هذا الباب عن رسول الله صلى الله عليه','4644','667','0');
INSERT INTO [t22547] VALUES (721,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رجمه من رجمه من اليهود هل كان ذلك بشهادة من سواهم من اليهود عليهم وما يدخل في ذلك من قبول شهادة أهل الكتاب بعضهم على بعض ومن ردها. فإن قال قائل: فقد رويتم حديث ابن عمر أن اليهود أتوا رسول الله صلى','4649','667','0');
INSERT INTO [t22547] VALUES (722,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت الآية، وفي حكمها، هل هو باق أو لحقه نسخ؟','4658','667','0');
INSERT INTO [t22547] VALUES (723,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا ينكح الزاني إلا مجلودا مثله "','4676','667','0');
INSERT INTO [t22547] VALUES (724,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرضاع الذي تجب به الحرمة: هل له عدد معلوم أم لا؟','4684','667','0');
INSERT INTO [t22547] VALUES (725,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يحبه الله من الخيلاء','4707','667','0');
INSERT INTO [t22547] VALUES (726,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما اختلف أهل العلم فيه في القتيل يوجد بين ظهراني قوم ولا يعلم من قتله هل تجب بذلك ديته عليهم أم لا؟','4709','667','0');
INSERT INTO [t22547] VALUES (727,'باب بيان مشكل كيفية القسامة كيف كانت مما روي عن رسول الله صلى الله عليه وسلم فيه؟','4724','667','0');
INSERT INTO [t22547] VALUES (728,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القسامة التي قضى بها على اليهود وجعل الدية عليهم هل تكون كذلك الأحكام فيمن بعدهم تكون الدية على ساكني الموضع الموجود فيه ذلك القتيل، وإن لم يكونوا يملكونه أو على مالكيه؟ قال أبو جعفر: قد روينا في','4728','667','0');
INSERT INTO [t22547] VALUES (729,'باب بيان مشكل الواجب بالقسامة هل يكون فيه سفك دم من يقسم عليه كما قال مالك، أو غرم ديته كما قال مخالفوه؟ قال قائل: في حديث يحيى بن سعيد عن بشر بن سهل أن رسول الله صلى الله عليه وسلم قال للأنصار: " أتحلفون خمسين يمينا، وتستحقون دم قاتلكم أو صاحبكم؟','4731','667','0');
INSERT INTO [t22547] VALUES (730,'باب بيان مشكل ما روي في الدية التي ودي بها الأنصاري، هل كانت من عند الرسول الله صلى الله عليه وسلم، أو من إبل الصدقة، أو من عند اليهود؟ قال أبو جعفر: قد روينا في حديث أبي سلمة، وسليمان، عن رجال من الأنصار أن النبي صلى الله عليه وسلم جعل ديته على','4732','667','0');
INSERT INTO [t22547] VALUES (731,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قول أيوب نبي الله عليه السلام: تعلم أني كنت أمر على الرجلين يتنازعان، فيذكران الله عز وجل، فأرجع إلى بيتي، فأكفر عنهما كراهة أن يذكرا الله إلا في حق','4736','667','0');
INSERT INTO [t22547] VALUES (732,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إخوانه هل هم أصحابه أو هل هم سواهم؟','4739','667','0');
INSERT INTO [t22547] VALUES (733,'الجزء ١٢','4739','0','0');
INSERT INTO [t22547] VALUES (734,'باب بيان مشكل ما روي في المراد بقول الله عز وجل لأمهات المؤمنين ولا تبرجن تبرج الجاهلية الأولى','4742','733','0');
INSERT INTO [t22547] VALUES (735,'باب بيان مشكل الواجب فيما اختلف فيه أهل العلم في الرجل يشتري السلعة فيفلس أو يموت، وعليه ديون، هل يكون بائعها أحق بها من غرمائه أم لا؟','4747','733','0');
INSERT INTO [t22547] VALUES (736,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في طلاقه حفصة وفي مراجعته إياها بعد ذلك','4756','733','0');
INSERT INTO [t22547] VALUES (737,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في السبب الذي فيه نزلت: إن في خلق السموات والأرض واختلاف الليل والنهار لآيات لأولي الألباب','4762','733','0');
INSERT INTO [t22547] VALUES (738,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الدليل على الواجب فيما اختلف فيه أهل العلم في حكم الحكم الذي يحكمه الرجلان بينهما هل يكون جائزا عليهما كما يكون حكم الحاكم عليهما به، وحتى لا يكون للحاكم إذا رفع إليه نقضه إذا كان مما يراه بعض أهل','4766','733','0');
INSERT INTO [t22547] VALUES (739,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الركعتين الأوليين من الصلوات التي تجاوز عدد ركعاتها ركعتين إلى أربع أو إلى ثلاث هل تطال إحداهما على الأخرى في القراءة أو يسوى بينهما فيها','4769','733','0');
INSERT INTO [t22547] VALUES (740,'باب بيان مشكل ما روي عن أصحاب رسول الله صلى الله عليه وسلم في الركعتين الأخريين من الصلوات التي تزيد على ركعتين، هل القراءة في توكيدهما فيهما كهي في الركعتين الأوليين، أو بخلاف ذلك؟ وهل لمصليهما ترك القراءة فيهما بما روي عن رسول الله صلى الله عليه','4775','733','0');
INSERT INTO [t22547] VALUES (741,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من دعي إلى حكم الرسول ليقضي بينه وبين خصمه، فلم يجئ، فلا حق له "','4784','733','0');
INSERT INTO [t22547] VALUES (742,'باب بيان مشكل حديث رسول الله صلى الله عليه وسلم: " أعف الناس قتلة أهل الإيمان " في إسناده ومتنه','4786','733','0');
INSERT INTO [t22547] VALUES (743,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أثمان الكلاب، في حلها، وفي النهي عنها','4794','733','0');
INSERT INTO [t22547] VALUES (744,'باب بيان مشكل ما اختلف العلماء فيه من المراد بقول الله عز وجل: ولا تلقوا بأيديكم إلى التهلكة مما روي عن أصحاب رسول الله صلى الله عليه وسلم من السبب الذي كان نزولها فيه، ومما تأوله بعضهم عليه','4826','733','0');
INSERT INTO [t22547] VALUES (745,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حديث عروة بن مضرس: " ومن لم يدرك الوقوف بجمع، فلا حج له "','4837','733','0');
INSERT INTO [t22547] VALUES (746,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحكام اللقطة','4844','733','0');
INSERT INTO [t22547] VALUES (747,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن لقطة الحاج','4860','733','0');
INSERT INTO [t22547] VALUES (748,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لقطة مكة','4862','733','0');
INSERT INTO [t22547] VALUES (749,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للناس في قلادة ابنته زينب لما رآها في الأموال المجتمعة لفداء الأسرى: " إن رأيتم أن تطلقوا لها أسيرها وتردوا عليها الذي لها، فافعلوا "','4866','733','0');
INSERT INTO [t22547] VALUES (750,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الموطن الذي تعتكف فيه النساء','4868','733','0');
INSERT INTO [t22547] VALUES (751,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره الملتقط بالإشهاد على ما التقطه','4872','733','0');
INSERT INTO [t22547] VALUES (752,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحكام الضوال','4877','733','0');
INSERT INTO [t22547] VALUES (753,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يدل على المسجد الذي أسس على التقوى أي المساجد هو؟','4890','733','0');
INSERT INTO [t22547] VALUES (754,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لعنه زائرات القبور والمتخذين عليها المساجد والسرج','4901','733','0');
INSERT INTO [t22547] VALUES (755,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم , ثم ما قال أصحابه وتابعوهم، ومن سواهم من أهل اللغة في أختان الرجل، من هم؟ وفي أصهاره، من هم؟','4907','733','0');
INSERT INTO [t22547] VALUES (756,'باب بيان مشكل حديث أبي موسى في البعير الذي ادعاه رجلان، فقضى به رسول الله صلى الله عليه وسلم بينهما، وما اختلف فيه أن ذلك كان ببينة أقامها كل واحد منهما أو بما سوى ذلك','4919','733','0');
INSERT INTO [t22547] VALUES (757,'باب بيان مشكل ما في حديث رسول الله صلى الله عليه وسلم المذكور فيه استحلاف المهاجرات على ما كان يستحلفهن عليه','4933','733','0');
INSERT INTO [t22547] VALUES (758,'باب بيان مشكل مراد رسول الله صلى الله عليه وسلم في تلاوته: وكان الإنسان أكثر شيء جدلا عند قول علي عليه السلام لما قال له ولفاطمة عليهما السلام: " ألا تصليان " إنما أنفسنا بيد الله عز وجل إن شاء أن يبعثا بعثنا','4935','733','0');
INSERT INTO [t22547] VALUES (759,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في كراهية ذهب المعادن، وإخباره أنه لا خير فيه','4937','733','0');
INSERT INTO [t22547] VALUES (760,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذهب الذي كان علي عليه السلام بعث به إليه من اليمن، فدفعه إلى من دفعه إليه من المؤلفة قلوبهم، هل في ذلك ما يدل على أن الواجب فيما وجد في المعادن هو الصدقة، أم لا؟','4941','733','0');
INSERT INTO [t22547] VALUES (761,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحكام أقوال السكران وأفعاله، وفي الحكم الذي يكون به سكرانا ما هو؟ قال أبو جعفر: أول ما نبتدئ به في هذا الباب قول الله جل ثناؤه: يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما','4945','733','0');
INSERT INTO [t22547] VALUES (762,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من سروره بقول مجزز المدلجي في زيد بن حارثة، وأسامة ابنه لما رأى أقدامهما بادية ووجوههما مغطاة: " إن هذه لأقدام بعضها من بعض "','4954','733','0');
INSERT INTO [t22547] VALUES (763,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القاتل في الحرب، من يقتله من العدو هل يستحق بذلك سلبه، أم لا؟','4964','733','0');
INSERT INTO [t22547] VALUES (764,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في سلب المددي صاحب عوف الذي دفع إليه خالد بن الوليد بعضه، ومنعه بقيته، ثم أمره رسول الله صلى الله عليه وسلم بتسليم بقيته إليه، ثم أمره بأن لا يفعل ذلك','4967','733','0');
INSERT INTO [t22547] VALUES (765,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان منه في سلب أبي جهل، ومن نفله إياه من الناس، وفيما احتج به محمد بن الحسن مما ذكر أن ما روي في ذلك يوجب ما قاله فيه قال محمد بن الحسن: " لو أن عسكرا من المسلمين دخل أرض الحرب، وعليهم أمير،','4970','733','0');
INSERT INTO [t22547] VALUES (766,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حرمة صيد المدينة، وفي الواجب على منتهكها فيه','4974','733','0');
INSERT INTO [t22547] VALUES (767,'باب بيان مشكل ما اختلف أهل العلم في كيفية الشهادات في الحقوق عند الحكام بما روي عن رسول الله صلى الله عليه وسلم في ذلك','4984','733','0');
INSERT INTO [t22547] VALUES (768,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لبس النساء الذهب من تحليل ومن تحريم','4989','733','0');
INSERT INTO [t22547] VALUES (769,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لبس النساء الحرير من تحريم، ومن تحليل','5010','733','0');
INSERT INTO [t22547] VALUES (770,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما تأوله ابن الزبير عليه من تحريمه لبس الحرير في الدنيا: أن من لبسه فيها لم يدخل الجنة، هل هو كما تأوله عليه، أم لا؟','5015','733','0');
INSERT INTO [t22547] VALUES (771,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صلاته على النجاشي بالمدينة، وهل كان ذلك، والنجاشي حينئذ بأرض الحبشة أو بالمدينة','5020','733','0');
INSERT INTO [t22547] VALUES (772,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إثبات الحجر على السفيه في ماله، وفي نفي الحجر عنه','5024','733','0');
INSERT INTO [t22547] VALUES (773,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من أدرك عرفة، فقد أدرك الحج "','5035','733','0');
INSERT INTO [t22547] VALUES (774,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن أحق ما وفيتم به من الشروط ما استحللتم به الفروج "','5037','733','0');
INSERT INTO [t22547] VALUES (775,'باب بيان مشكل ما روى بعض الناس عن رسول الله صلى الله عليه وسلم في رد شهادة المحدود في الإسلام','5041','733','0');
INSERT INTO [t22547] VALUES (776,'باب بيان مشكل ما روي في أم عبد الله بن شداد عنه وعن أهل الأنساب من هي من الأخوات المؤمنات؟ ما روي عن رسول الله صلى الله عليه وسلم في ذلك','5052','733','0');
INSERT INTO [t22547] VALUES (777,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يحكيه عن ربه عز وجل من قوله: " كل عمل ابن آدم هو له، إلا الصيام هو لي، وأنا أجزي به "','5055','733','0');
INSERT INTO [t22547] VALUES (778,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لأبي بردة بن نيار في أضحيته التي ذبحها: " أعد أخرى مكانها "، ومن قوله له، لما قال له: إن عندي جذعة خير من مسنة، فقال له: " اذبحها ولا تجزئ عن أحد بعدك "','5057','733','0');
INSERT INTO [t22547] VALUES (779,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في بعثه من كان بعثه في قتال من بعثه لقتاله بلا إمرة كان أمره في ذلك','5060','733','0');
INSERT INTO [t22547] VALUES (780,'باب بيان مشكل ما روي فيما كان عن رسول الله صلى الله عليه وسلم في أشهر الحرم من غزو لأعدائه، أو ترك لذلك حتى تنقضي','5062','733','0');
INSERT INTO [t22547] VALUES (781,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن صلى، وهو معقوص الشعر','5067','733','0');
INSERT INTO [t22547] VALUES (782,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن هذا المال حلوة خضرة "','5071','733','0');
INSERT INTO [t22547] VALUES (783,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يقضى لبعض القراء على بعض مما يختلفون فيه في قراءتهم: من لدني من التثقيل ومن التخفيف','5077','733','0');
INSERT INTO [t22547] VALUES (784,'باب بيان مشكل ما روي عن أبي بكر الصديق رضي الله عنه من قوله لأبي برزة لما استأذنه في قتل الرجل الذي استأذنه في قتله: " إنها لم تكن لأحد بعد رسول الله صلى الله عليه وسلم "، وفي ذلك الشيء ما هو؟','5081','733','0');
INSERT INTO [t22547] VALUES (785,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من قتل عمدا فقود يده "','5089','733','0');
INSERT INTO [t22547] VALUES (786,'باب بيان مشكل قول الله عز وجل في آية القصاص: فمن عفي له من أخيه شيء فاتباع بالمعروف وأداء إليه بإحسان، وما اختلف أهل العلم فيه بما روي عن رسول الله صلى الله عليه وسلم في ذلك قال أبو جعفر: قال الله: يا أيها الذين آمنوا كتب عليكم القصاص في القتلى الحر','5095','733','0');
INSERT INTO [t22547] VALUES (787,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صلاته على قبر الذي صلى على قبره بغير حضره دفنه','5100','733','0');
INSERT INTO [t22547] VALUES (788,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صلاته على قتلى أحد بعد مقتلهم بثماني سنين','5102','733','0');
INSERT INTO [t22547] VALUES (789,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يحتج به من ذهب إلى إطلاق بيع المدبر','5115','733','0');
INSERT INTO [t22547] VALUES (790,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إقامته حد الزنى على المقر به عنده من المرأة التي أنكرت ذلك','5140','733','0');
INSERT INTO [t22547] VALUES (791,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في القتل، هل يكون منه شبه عمد كما يقول الكوفيون، أو لا شبه عمد فيه كما يقول الحجازيون؟','5145','733','0');
INSERT INTO [t22547] VALUES (792,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره علي بن أبي طالب عليه السلام في القبطي الذي كان يختلف إلى مارية أم إبراهيم ابن رسول الله صلى الله عليه وسلم أن يقتله','5150','733','0');
INSERT INTO [t22547] VALUES (793,'باب بيان خلاف ما روى أبو بحرية، عن عمر في طلحة بن عبيد الله رضي الله عنهما من موت رسول الله صلى الله عليه وسلم، وهو عليه عاتب','5152','733','0');
INSERT INTO [t22547] VALUES (794,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تركه الصلاة على من قتل نفسه','5158','733','0');
INSERT INTO [t22547] VALUES (795,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تشبيهه الصلوات الخمس في محو الله عز وجل بهن الذنوب عن من يصليهن بالاغتسال بالماء الذي ينقي درن أبدانهم','5161','733','0');
INSERT INTO [t22547] VALUES (796,'باب بيان مشكل جواب رسول الله صلى الله عليه وسلم في البتع لما سئل عنه','5166','733','0');
INSERT INTO [t22547] VALUES (797,'باب بيان مشكل ما روي عن ابن عباس من قوله في ما حرم من كل شراب، هل هو السكر أو المسكر','5176','733','0');
INSERT INTO [t22547] VALUES (798,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إن بني هشام بن المغيرة استأذنوني في أن ينكحوا ابنتهم علي بن أبي طالب " وما كان منه في ذلك','5181','733','0');
INSERT INTO [t22547] VALUES (799,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يقضى بين المختلفين في الرقبة المؤمنة، هل يجزئ فيها من لم يصم، ولم يصل ممن قد أقر بالإيمان، أم لا؟ قال أبو جعفر: قد روي عن الحسن البصري وإبراهيم النخعي في هذا المعنى','5186','733','0');
INSERT INTO [t22547] VALUES (800,'باب بيان مشكل ما يقضى بين المختلفين في بيع الولاء وفي هبته بما يروى عن رسول الله صلى الله عليه وسلم في ذلك','5193','733','0');
INSERT INTO [t22547] VALUES (801,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من ذكره مما لا تصلح له المساجد، ومما هي له','5196','733','0');
INSERT INTO [t22547] VALUES (802,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إمامته في الليلة التي أسري به فيها إلى بيت المقدس، هل كانت لكل الأنبياء صلوات الله عليهم، أو لبعضهم دون بعض؟','5201','733','0');
INSERT INTO [t22547] VALUES (803,'باب بيان مشكل ما روي عن سعيد بن جبير في المكان الذي نزلت فيه: واسأل من أرسلنا من قبلك من رسلنا بما يروى عن رسول الله صلى الله عليه وسلم في ذلك','5210','733','0');
INSERT INTO [t22547] VALUES (804,'الجزء ١٣','5210','0','0');
INSERT INTO [t22547] VALUES (805,'باب بيان مشكل ما روي في المراد بقول الله عز وجل: فإن كنت في شك مما أنزلنا إليك','5212','804','0');
INSERT INTO [t22547] VALUES (806,'باب بيان مشكل ما روي عن عثمان بن عفان رضي الله عنه من احتجاجه فيما احتج به من صدقته ببئر رومة، ومن منعهم إياه من الشرب منها، ومن زيادته في مسجد رسول الله صلى الله عليه وسلم ما زاده فيه، ومن منعهم إياه من الصلاة فيه','5216','804','0');
INSERT INTO [t22547] VALUES (807,'باب بيان مشكل ما روي في منع رسول الله صلى الله عليه وسلم عمر بن الخطاب عن العود في صدقته، هل ذلك بكل الوجوه، حتى لا تصلح له بوجه منها، أو على خاص من الوجوه؟','5219','804','0');
INSERT INTO [t22547] VALUES (808,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رده حكم العائد في صدقته إلى العائد في قيئه، من هو؟ قد روينا في الباب الذي قبل هذا الباب منع رسول الله صلى الله عليه وسلم من العود في الصدقة بما منع من العود فيها به، فاحتمل أن يكون ذلك على أنه لا','5229','804','0');
INSERT INTO [t22547] VALUES (809,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرجوع في الهبة، ومن تشبيهه إياه برجوع الكلب في قيئه','5232','804','0');
INSERT INTO [t22547] VALUES (810,'باب بيان مشكل ما روي عن عمر بن الخطاب، وسهل بن حنيف رضي الله عنهما في أمرهما باتهام الرأي، بما يروى عن رسول الله صلى الله عليه وسلم في ذلك','5242','804','0');
INSERT INTO [t22547] VALUES (811,'باب بيان مشكل ما روي عن البراء من قوله: " كان ركوع رسول الله صلى الله عليه وسلم، وقيامه، وإذا رفع رأسه من الركوع، وسجوده ما بين السجدتين، قريبا من السواء " سمعت بكار بن قتيبة يقول: لما حملت من البصرة، لما حملت له، فقدمت الحضرة، وكان القاضي بها','5247','804','0');
INSERT INTO [t22547] VALUES (812,'باب بيان مشكل ما روي عن عمر رضي الله عنه من نهيه أن يغالى في صدقات النساء، ومن احتجاجه في ذلك بأصدقة رسول الله صلى الله عليه وسلم نساءه، ومن أصدقة أزواج بناته بناته','5252','804','0');
INSERT INTO [t22547] VALUES (813,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يحل للواهب أن يرجع في هبته، إلا الوالد لولده "','5267','804','0');
INSERT INTO [t22547] VALUES (814,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من ما ذكره النعمان بن بشير، عنه من نحله أبيه إياه شيئا، ومن قول النبي صلى الله عليه وسلم له لما أشهده على ذلك: " أكل ولدك نحلت مثل هذا؟ " قال: لا، قال: " فارجعه "','5275','804','0');
INSERT INTO [t22547] VALUES (815,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الضرير في بصره، هل عليه حضور الجماعات، كما على من سواه ممن لا ضرر ببصره، أم لا؟','5286','804','0');
INSERT INTO [t22547] VALUES (816,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا استيقظ أحدكم من نومه، فلا يدخل يده في الإناء، حتى يغسلها، فإنه لا يدري أين باتت يده، أو فيما باتت يده "','5297','804','0');
INSERT INTO [t22547] VALUES (817,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الشهداء، من هم؟','5303','804','0');
INSERT INTO [t22547] VALUES (818,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " خياركم من تعلم القرآن وعلمه "','5310','804','0');
INSERT INTO [t22547] VALUES (819,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الولد للفراش، وللعاهر الحجر "، هل يوجد ذلك مضاده ما روي عنه صلى الله عليه وسلم في نفي الولد باللعان؟','5319','804','0');
INSERT INTO [t22547] VALUES (820,'باب بيان مشكل ما روي عن عبد الله بن مسعود عن رسول الله صلى الله عليه وسلم، من قوله بعد ملاعنته بين الزوجين اللذين لاعن بينهما: " لعلها أن تجيء به أسود جعدا " وأنها جاءت به كذلك','5326','804','0');
INSERT INTO [t22547] VALUES (821,'باب بيان مشكل ما روي عن عبد الله بن عباس، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5329','804','0');
INSERT INTO [t22547] VALUES (822,'باب بيان مشكل ما روي عن عبد الله بن عمرو، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5333','804','0');
INSERT INTO [t22547] VALUES (823,'باب بيان مشكل ما روي عن أنس بن مالك، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5335','804','0');
INSERT INTO [t22547] VALUES (824,'باب بيان مشكل ما روي عن سهل بن سعد الساعدي، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5338','804','0');
INSERT INTO [t22547] VALUES (825,'باب بيان مشكل ما قد تنازعه أهل العلم بعد ذلك في وجوب اللعان بالحمل المنفي، وفي سقوط اللعان به قال أبو جعفر: قد كان بعض من يذهب إلى إثبات اللعان ينفي الحمل قبل وضع أمه إياه، يحتج لما يذهب إليه في ذلك على مخالفيه فيه، بقول الله في المطلقات: وإن كن','5340','804','0');
INSERT INTO [t22547] VALUES (826,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من سلم على أخيه، ثم لقيه بعد ذلك، وقد حالت بينهما شجرة أو حائط، فليسلم عليه "','5344','804','0');
INSERT INTO [t22547] VALUES (827,'باب بيان مشكل ما روى أنس مما كانوا يظنونه برسول الله صلى الله عليه وسلم في إطالته القيام بعد رفعه رأسه من الركوع، وفي إطالته القعود بين السجدتين أنه قد أوهم','5347','804','0');
INSERT INTO [t22547] VALUES (828,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في جيش الأمراء: " الأمير زيد، فإن قتل زيد فالأمير جعفر، فإن قتل جعفر فالأمير عبد الله بن رواحة "، واستخراج ما فيه من الفقه','5351','804','0');
INSERT INTO [t22547] VALUES (829,'باب بيان مشكل تأويل قول الله عز وجل: فما لكم في المنافقين فئتين الآية، بما روي عن رسول الله صلى الله عليه وسلم في ذلك','5356','804','0');
INSERT INTO [t22547] VALUES (830,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تأويل قول الله عز وجل: ألهاكم التكاثر حتى زرتم المقابر كلا سوف تعلمون ثم كلا سوف تعلمون','5362','804','0');
INSERT INTO [t22547] VALUES (831,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يعذب به الناس في قبورهم','5371','804','0');
INSERT INTO [t22547] VALUES (832,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله: " أكثر عذاب القبر بالبول "','5374','804','0');
INSERT INTO [t22547] VALUES (833,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في دفعه: أن الناس يعذبون في قبورهم لما سئل عن ذلك بعد قول اليهودية لعائشة: " أعاذك الله من عذاب القبر "','5377','804','0');
INSERT INTO [t22547] VALUES (834,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في عذاب القبر، هل يسمعه أحد، أم لا؟','5384','804','0');
INSERT INTO [t22547] VALUES (835,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذي نهى من نهاه من بني إسرائيل عن قطع ما قطع من بدنه بالمقراض من البول الذي كان أصابه، فعذب بذلك في قبره','5388','804','0');
INSERT INTO [t22547] VALUES (836,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في خير الناس: " أنه من طال عمره، وحسن عمله "','5390','804','0');
INSERT INTO [t22547] VALUES (837,'باب بيان مشكل ما يروى عن علي بن أبي طالب، وعبد الله بن عباس، مما يرفعه بعضهم عن علي إلى النبي صلى الله عليه وسلم في المراد بقول الله عز وجل: " وتجعلون شكركم " مكان ما نقرأه نحن: رزقكم أنكم تكذبون','5394','804','0');
INSERT INTO [t22547] VALUES (838,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في ضحك المطر، ومنطقه','5400','804','0');
INSERT INTO [t22547] VALUES (839,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لسعد لما عاده في مرضه الذي كان عاده فيه، لما قال له سعد: أميت أنا من مرضي هذا في الدار التي هاجرت منها؟ ، فقال له: " إني أرجو ليرفعنك الله، حتى ينفع بك قوم، ويضر بك آخرون "','5402','804','0');
INSERT INTO [t22547] VALUES (840,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بالكلالة، من هو؟','5406','804','0');
INSERT INTO [t22547] VALUES (841,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صلاة القاعد متربعا، هل هي مكروهة، أم لا؟','5427','804','0');
INSERT INTO [t22547] VALUES (842,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على حكم من دعي إلى وليمة قد أمر بالإجابة إليها إذا علم أن هناك لهوا، لا يصلح حضوره في غيرها، هل فرض الإجابة عليه كما لو لم يكن ذلك، أو قد سقط عنه؟','5433','804','0');
INSERT INTO [t22547] VALUES (843,'باب بيان ما روي مما يدل على إمكان ما قال من قال من أهل الأخبار: إن ممن بايع رسول الله صلى الله عليه وسلم يوم بايع الناس بمكة، ابن صغير لعبد الله بن أبي بكر، أو لعبد الرحمن بن أبي بكر الصديق','5436','804','0');
INSERT INTO [t22547] VALUES (844,'باب بيان مشكل ما رواه نافع، عن ابن عمر عن رسول الله صلى الله عليه وسلم في المتبايعين أنهما بالخيار حتى يتفرقا، إلا بيع الخيار','5439','804','0');
INSERT INTO [t22547] VALUES (845,'باب بيان مشكل ما رواه عبد الله بن دينار، عن ابن عمر، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5452','804','0');
INSERT INTO [t22547] VALUES (846,'باب بيان مشكل ما روى عبد الله بن عمرو بن العاص، عن النبي صلى الله عليه وسلم في هذا المعنى','5455','804','0');
INSERT INTO [t22547] VALUES (847,'باب بيان مشكل ما رواه حكيم بن حزام عن النبي صلى الله عليه وسلم في هذا المعنى','5457','804','0');
INSERT INTO [t22547] VALUES (848,'باب بيان مشكل ما روى أبو برزة عن النبي صلى الله عليه وسلم في هذا المعنى','5460','804','0');
INSERT INTO [t22547] VALUES (849,'باب بيان مشكل ما رواه أبو هريرة، عن النبي صلى الله عليه وسلم في هذا المعنى','5463','804','0');
INSERT INTO [t22547] VALUES (850,'باب بيان مشكل ما رواه سمرة بن جندب، عن النبي صلى الله عليه وسلم في هذا المعنى','5465','804','0');
INSERT INTO [t22547] VALUES (851,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في أهل بدر رضوان الله عليهم: " إنهم أفضل الناس "، ومن قوله: " خير أمتي قرني الذين بعثت فيهم "، وأنه ليس واحد منهما مخالفا للآخر','5467','804','0');
INSERT INTO [t22547] VALUES (852,'باب بيان مشكل ما روي عن علي بن أبي طالب، عن رسول الله صلى الله عليه وسلم، من قوله: " لا تصلوا بعد العصر، إلا أن تكون الشمس مرتفعة "','5469','804','0');
INSERT INTO [t22547] VALUES (853,'باب بيان مشكل ما روي عن عائشة في تأويلها نهي عمر بن الخطاب رضي الله عنه عن الصلاة بعد العصر عليه','5477','804','0');
INSERT INTO [t22547] VALUES (854,'باب بيان مشكل ما اختلف الناس فيه من أسنان الدية من الإبل الواجبة في القتل الخطأ، ما هي؟ ، بما قد روي عن رسول الله صلى الله عليه وسلم في ذلك','5479','804','0');
INSERT INTO [t22547] VALUES (855,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للملاعن بعد فراغه، وبعد فراغ زوجته من اللعان: " لا سبيل لك عليها "','5483','804','0');
INSERT INTO [t22547] VALUES (856,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما قرأه لما تعار من الليل مما رواه ابن عباس عنه من سورة آل عمران','5491','804','0');
INSERT INTO [t22547] VALUES (857,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تخييره الأعرابي بعد ابتياعه منه ما كان ابتاعه منه','5493','804','0');
INSERT INTO [t22547] VALUES (858,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره عمر، أو عميرا مولى آل أبي اللحم لما سأله ما سأله من غنائم خيبر أن يتقلد السيف قبل أن يأمر له بشيء منها','5498','804','0');
INSERT INTO [t22547] VALUES (859,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أحب الناس كان إليه','5503','804','0');
INSERT INTO [t22547] VALUES (860,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعثمان رضي الله عنه: " إن الله عز وجل مقمصك قميصا، فإن أرادوك على خلعه، فلا تخلعه "','5514','804','0');
INSERT INTO [t22547] VALUES (861,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في سب الوالدين: أنه أكبر الذنوب، أو أكبر الكبائر','5517','804','0');
INSERT INTO [t22547] VALUES (862,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم، فيما كان منه في بروع ابنة واشق، وتصحيح أسانيده عنه، وبيان ما فيه من الأحكام','5521','804','0');
INSERT INTO [t22547] VALUES (863,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد فيما كان يستعمله في خطبه، وفي كلامه من قوله: " أما بعد "','5532','804','0');
INSERT INTO [t22547] VALUES (864,'باب بيان مشكل الواجب فيما اختلف فيه أهل العلم في تمثيل الرجل بعبده من عتاق عليه بذلك، ومن سواه مما لا عتاق معه','5535','804','0');
INSERT INTO [t22547] VALUES (865,'باب بيان مشكل ما روي عن عبد الله بن مسعود رضي الله عنه، عن رسول الله صلى الله عليه وسلم في أكبر الذنوب','5545','804','0');
INSERT INTO [t22547] VALUES (866,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على الصور الذي ذكره الله في كتابه، ما هو؟','5548','804','0');
INSERT INTO [t22547] VALUES (867,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في كتابه ببحر أيلة لملكها','5554','804','0');
INSERT INTO [t22547] VALUES (868,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الفأرة تموت في سمن، من حل الانتفاع به','5557','804','0');
INSERT INTO [t22547] VALUES (869,'باب بيان مشكل ما ينبغي للابس الخاتم في وضوئه للصلاة من تحريك له، وغير ذلك','5571','804','0');
INSERT INTO [t22547] VALUES (870,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الشفاعة عند الله يوم القيامة من أهل الجنة لأهل النار','5574','804','0');
INSERT INTO [t22547] VALUES (871,'باب بيان مشكل ما رواه سالم بن عبد الله بن عمر، عن أبيه، عن رسول الله صلى الله عليه وسلم في العبد يكون بين الشركاء، فيعتقه أحدهم مع يسار منه بقيمة أنصباء شركائه فيه، ومن سوى ذلك من اعتباريتها','5576','804','0');
INSERT INTO [t22547] VALUES (872,'باب بيان مشكل ما رواه نافع مولى عبد الله بن عمر، عن عبد الله بن عمر، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5580','804','0');
INSERT INTO [t22547] VALUES (873,'باب بيان مشكل ما رواه أبو هريرة، عن رسول الله صلى الله عليه وسلم في هذا المعنى','5598','804','0');
INSERT INTO [t22547] VALUES (874,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في مراده بقوله: " لن يجزي ولد والده، إلا أن يجده مملوكا، فيشتريه فيعتقه "','5603','804','0');
INSERT INTO [t22547] VALUES (875,'باب بيان مشكل ما روته عائشة، وأم سلمة، وغيرهما عن رسول الله صلى الله عليه وسلم في قراءة فاتحة الكتاب: (ملك يوم الدين) ، أو: مالك يوم الدين','5612','804','0');
INSERT INTO [t22547] VALUES (876,'الجزء ١٤','5612','0','0');
INSERT INTO [t22547] VALUES (877,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا تسبقوني بالركوع ولا بالسجود، فإن ما أسبقكم به إذا ركعت، تدركوني به إذا رفعت "','5625','876','0');
INSERT INTO [t22547] VALUES (878,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره بالمبالغة بالاستنشاق في الوضوء للصلاة، إلا أن يكون المتوضئ صائما','5631','876','0');
INSERT INTO [t22547] VALUES (879,'باب بيان مشكل ما جاء به كتاب الله عز وجل من الأمر بغسل ما يغسل من الأعضاء، وبمسح ما يمسح منها في الوضوء للصلاة، ثم بما روي عن رسول الله صلى الله عليه وسلم في ذلك: هل هو على الفرض: يفعل الرجل ذلك بنفسه، أم على مماسة الماء تلك الأعضاء، وإن كان بغير','5633','876','0');
INSERT INTO [t22547] VALUES (880,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من إظهار التكبير في العيد، وفي أي حال يكون من الطريق إليه، أم بعد الجلوس فيه','5635','876','0');
INSERT INTO [t22547] VALUES (881,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في لباس الرجال الخفاف في الإحرام، أمباح ذلك لهم، كما يباح في الإحلال؟ أو مباح لهم في حال الإعواز من النعال بعد قطعها أسفل من الكعبين؟','5646','876','0');
INSERT INTO [t22547] VALUES (882,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الماء الذي يمر على الأرضين، ويكون مروره على بعضها قبل بعض , كيف الحكم فيه؟ وفيما يحبسه أهلها حتى يبلغ منها ما يبلغ، ثم يرسلونه بعد ذلك؟','5655','876','0');
INSERT INTO [t22547] VALUES (883,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الرقبى','5658','876','0');
INSERT INTO [t22547] VALUES (884,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في العمرى: في كيفيتها، وفي الحكم فيها حدثنا يونس، حدثنا سفيان، عن عمرو بن دينار، عن سليمان بن يسار، أن أميرا كان على المدينة يقال له: طارق، قضى بالعمرى للوارث، عن قول جابر، عن النبي صلى الله','5662','876','0');
INSERT INTO [t22547] VALUES (885,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن بيع الحصاة','5682','876','0');
INSERT INTO [t22547] VALUES (886,'باب بيان مشكل ما روي في المراد بقول الله تعالى: فويل للمصلين، إلى آخر السورة المذكور ذلك فيها , بما يروى مما كان يقال فيه على عهد رسول الله صلى الله عليه وسلم، وبما روي عن أصحابه فيه','5685','876','0');
INSERT INTO [t22547] VALUES (887,'ومما روي عن عبد الله بن مسعود مما لم يذكر فيه ما كانوا يقولونه في ذلك على عهد رسول الله صلى الله عليه وسلم','5688','876','0');
INSERT INTO [t22547] VALUES (888,'ومما روي عن عبد الله بن عباس رضي الله عنه','5691','876','0');
INSERT INTO [t22547] VALUES (889,'باب بيان مشكل الأولى فيما يذكر ما مضى من أيام الشهر: هل يكون ذلك بذكر الماضي منها، أو بذكر الأقل من الماضي، ومن الباقي منها بما روي عن رسول الله صلى الله عليه وسلم، ثم بما روي عمن روي عنه من أصحابه فيه شيء','5697','876','0');
INSERT INTO [t22547] VALUES (890,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله للذي قال له: عندي دينار: " أنفقه على نفسك "، وفي قوله له لما قال له: عندي آخر: " أنفقه على ولدك "، وفي قوله لما قال له: عندي آخر قال: " أنفقه على خادمك "، وفي قوله لما قال له: عندي','5702','876','0');
INSERT INTO [t22547] VALUES (891,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " موضع سوط في الجنة خير من الدنيا وما فيها "','5707','876','0');
INSERT INTO [t22547] VALUES (892,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم، ثم ما روي عن أصحابه بعده في الصلاة بعد أذان المغرب، من إباحة ومن نهي','5711','876','0');
INSERT INTO [t22547] VALUES (893,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تسمية السحور غداء','5724','876','0');
INSERT INTO [t22547] VALUES (894,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من رأى منكم هلال ذي الحجة، فأراد أن يضحي فلا يأخذ من شعره وأظفاره حتى يضحي "','5728','876','0');
INSERT INTO [t22547] VALUES (895,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما يفعله في يوم النحر من ضحى في شعره وفي أظفاره','5740','876','0');
INSERT INTO [t22547] VALUES (896,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على إباحة إنفاق الزائف من الدراهم','5742','876','0');
INSERT INTO [t22547] VALUES (897,'باب بيان مشكل الوجه فيما اختلف فيه أهل العلم من كيفية استقبال القبلة عند الموت','5748','876','0');
INSERT INTO [t22547] VALUES (898,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تفضيله من اعتزل شرور الناس حتى صار بذلك منقطعا عنهم على من سواه ممن يخالط الناس','5750','876','0');
INSERT INTO [t22547] VALUES (899,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لبلال في الصلاة: " أرحنا بها يا بلال "','5758','876','0');
INSERT INTO [t22547] VALUES (900,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما وصف به المرأة أنها تقبل بصورة شيطان، وأنها تدبر بصورة شيطان','5760','876','0');
INSERT INTO [t22547] VALUES (901,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يدخل النار من كان في قلبه مثقال حبة من خردل من إيمان "','5762','876','0');
INSERT INTO [t22547] VALUES (902,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا يدخل الجنة من في قلبه مثقال ذرة من كبر "','5768','876','0');
INSERT INTO [t22547] VALUES (903,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه مريد الصلاة عن تشبيك أصابعه في طريقه إليها','5774','876','0');
INSERT INTO [t22547] VALUES (904,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الطواف بالبيت صلاة إلا أن الله تعالى أحل فيه المنطق، فمن نطق - يعني فيه - فلا ينطق إلا بخير "','5782','876','0');
INSERT INTO [t22547] VALUES (905,'باب بيان مشكل مراد رسول الله صلى الله عليه وسلم في قوله لأبي بكرة لما ركع دون الصف، وقد حفزه النفس: " زادك الله حرصا، ولا تعد "','5784','876','0');
INSERT INTO [t22547] VALUES (906,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدفع ما رواه بعض الناس عن أبي حنيفة فيمن تنحنح له وهو يصلي فانتظر المتنحنح له روى بعض الناس، عن أبي حنيفة الخوارزمي، عن أبي حنيفة النعمان بن ثابت أن من فعل ذلك كانت صلاته فاسدة، وأخشى عليه.','5791','876','0');
INSERT INTO [t22547] VALUES (907,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " خذوا القرآن من أربعة " فذكر أربعة ممن جمع القرآن دون من سواهم ممن قد جمعه','5796','876','0');
INSERT INTO [t22547] VALUES (908,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله لأبي بن كعب رضي الله عنه: " أمرت أن أقرأ عليك "','5804','876','0');
INSERT INTO [t22547] VALUES (909,'باب بيان مشكل ما روي فيمن قرأ قوله: (وما هو على الغيب بظنين) أو بضنين قال أبو جعفر: قد ذكرنا مخرج قراءة عاصم فيما تقدم من كتابنا هذا، ورجوعها إلى علي، وعبد الله، وزيد رضي الله عنهم، وذكرنا في رواية أبي بكر بن عياش أخذه إياها عنه حرفا حرفا، وأنه','5815','876','0');
INSERT INTO [t22547] VALUES (910,'باب بيان مشكل ما روي فيما اختلف القراء في قراءتهم إياه من قوله: وما كان لنبي أن يغل، أو (يغل) ، وفي السبب الذي فيه نزلت','5823','876','0');
INSERT INTO [t22547] VALUES (911,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لنسائه بعد حجة الوداع: " هذه الحجة، ثم ظهور الحصر "','5826','876','0');
INSERT INTO [t22547] VALUES (912,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لنسائه: " أيتكن صاحبة الجمل الأدبب "، ومن قوله لعلي: " إنه سيكون بينك وبين عائشة شيء، فإذا كان ذلك فأبلغها مأمنها "','5836','876','0');
INSERT INTO [t22547] VALUES (913,'باب بيان مشكل ما روي عن ابن مسعود، من قوله لما فرض التشهد - يعني التشهد في الصلاة -','5839','876','0');
INSERT INTO [t22547] VALUES (914,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من تركه النكير على من خاطبه: بجعلني الله فداك','5844','876','0');
INSERT INTO [t22547] VALUES (915,'باب بيان مشكل فعل رسول الله صلى الله عليه وسلم لمن فداه أباه وأمه','5848','876','0');
INSERT INTO [t22547] VALUES (916,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للناس بعدما أقيمت الصلاة: " سووا صفوفكم، وتراصوا، إني لأراكم من خلف ظهري "','5854','876','0');
INSERT INTO [t22547] VALUES (917,'باب بيان مشكل ما روي عن بلال رضي الله عنه من اشتراطه على رسول الله صلى الله عليه وسلم أن لا يسبقه بآمين','5857','876','0');
INSERT INTO [t22547] VALUES (918,'باب بيان مشكل قول الله عز وجل: ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون، ومما يروى عن النبي صلى الله عليه وسلم فيه قال أبو جعفر: قال الله عز وجل: ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون، فتأملنا الذكر','5865','876','0');
INSERT INTO [t22547] VALUES (919,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الإمام: " إذا صلى جالسا فصلوا جلوسا أجمعين "، هل ذلك الحكم باق على حاله، أو قد نسخ بوفاة رسول الله صلى الله عليه وسلم بغيره؟','5875','876','0');
INSERT INTO [t22547] VALUES (920,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من لعنه الرائش، أو الراشي مع لعنه الراشي والمرتشي','5894','876','0');
INSERT INTO [t22547] VALUES (921,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يدل على أن الرجل قد يجوز أن ينسب إلى موضع لم يكن من أهله بأن صار من أهله','5902','876','0');
INSERT INTO [t22547] VALUES (922,'باب بيان مشكل قول الله عز وجل في أهل النار، وفي أهل الجنة: خالدين فيها ما دامت السموات والأرض إلا ما شاء ربك، مما روي عن رسول الله صلى الله عليه وسلم مما استدل به على ذلك قال أبو جعفر: قال الله تعالى: فأما الذين شقوا ففي النار خالدين فيها ما دامت','5905','876','0');
INSERT INTO [t22547] VALUES (923,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " العجوة من الجنة "','5913','876','0');
INSERT INTO [t22547] VALUES (924,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في هذه الآثار في العجوة، هل هو على العجوة من سائر النخل الذي في البلدان، أو من خاص منها؟','5919','876','0');
INSERT INTO [t22547] VALUES (925,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الكمأة، وفي السبب الذي من أجله قال للناس: " إنها من المن "','5924','876','0');
INSERT INTO [t22547] VALUES (926,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يقضي بين الفقهاء المختلفين في الرطب: هل هو من الفاكهة، أم ليس هو منها؟ قال أبو جعفر: قال أبو حنيفة، وكان تفرد فيما','5927','876','0');
INSERT INTO [t22547] VALUES (927,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله بعدما صلى بالناس صلاة الكسوف: " إني رأيت الجنة، أو أريت الجنة، فتناولت منها عنقودا، ولو أخذته لأكلتم منه ما بقيت الدنيا "','5931','876','0');
INSERT INTO [t22547] VALUES (928,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الدجال أن معه جبال خبز','5933','876','0');
INSERT INTO [t22547] VALUES (929,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أول مبعوث من أنبياء الله عز وجل من هو؟','5940','876','0');
INSERT INTO [t22547] VALUES (930,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره في حلب الناقة بترك دواعي اللبن','5944','876','0');
INSERT INTO [t22547] VALUES (931,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في إتيانه مسجد قباء وفي صلاته فيه','5949','876','0');
INSERT INTO [t22547] VALUES (932,'باب بيان مشكل ما روي عن عقبة بن عامر، في أمره إياه أن يضحي بعتود','5962','876','0');
INSERT INTO [t22547] VALUES (933,'باب بيان مشكل قول الله تعالى: وإن خفتم ألا تقسطوا في اليتامى الآية، مما روي عن رسول الله صلى الله عليه وسلم، وأصحابه في ذلك','5969','876','0');
INSERT INTO [t22547] VALUES (934,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في المراد بقول الله عز وجل: ذلك أدنى ألا تعولوا','5977','876','0');
INSERT INTO [t22547] VALUES (935,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الأيم أحق بنفسها من وليها، والبكر تستأذن، وإذنها صماتها "','5982','876','0');
INSERT INTO [t22547] VALUES (936,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قبوله من العباس تزويجه إياه ميمونة','5993','876','0');
INSERT INTO [t22547] VALUES (937,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لأم سلمة لما خطبها، فقالت: إنه ليس أحد من أوليائي شاهدا، ليس عمر، وهو صغير لم يبلغ، فزوجها رسول الله صلى الله عليه وسلم بأمرها','5995','876','0');
INSERT INTO [t22547] VALUES (938,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " رؤيا المؤمن جزء من الأجزاء التي أخبر أنها منها من النبوة " قال أبو جعفر: قد ذكرنا هذا الباب وما روي فيه، من الآثار بالأسانيد عن رسول الله صلى الله عليه وسلم فيما تقدم منا في كتابنا هذا،','5999','876','0');
INSERT INTO [t22547] VALUES (939,'باب بيان مشكل ما روي عن عبد الله بن عباس، من قوله: كانت رؤيا الأنبياء وحيا , مما نحيط علما أنه لم يقله رأيا، وإنما قاله من أخذه إياه من حيث يؤخذ مثله','6003','876','0');
INSERT INTO [t22547] VALUES (940,'باب بيان مشكل ما روي عن عبد الله بن عباس، مما يعلم يقينا أنه لم يقله رأيا، وإنما قال توقيفا: لا وحي إلا القرآن','6005','876','0');
INSERT INTO [t22547] VALUES (941,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تأويل قول الله عز وجل: إنا فتحنا لك فتحا مبينا','6010','876','0');
INSERT INTO [t22547] VALUES (942,'باب بيان مشكل ما روي في السبب الذي من أجله قيل: بيعة الرضوان كان سببها عثمان بن عفان مع غيبته عنها','6015','876','0');
INSERT INTO [t22547] VALUES (943,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله للنفر الذين كان فيهم سمرة: " آخركم موتا في النار "','6020','876','0');
INSERT INTO [t22547] VALUES (944,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أتاني جبريل عليه السلام، فأمرني أن آمر أصحابي أن يرفعوا أصواتهم "','6025','876','0');
INSERT INTO [t22547] VALUES (945,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من وده أنه لم يكن دخل الكعبة بعدما كان دخلها','6036','876','0');
INSERT INTO [t22547] VALUES (946,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " لا ينكح المحرم، ولا ينكح، ولا يخطب "، ومما روي عنه مع ذلك في الحال التي تزوج فيها ميمونة من حرم أو حل','6040','876','0');
INSERT INTO [t22547] VALUES (947,'باب بيان مشكل السبب الذي نزلت فيه: وإذ يمكر بك الذين كفروا ليثبتوك أو يقتلوك أو يخرجوك','6055','876','0');
INSERT INTO [t22547] VALUES (948,'الجزء ١٥','6055','0','0');
INSERT INTO [t22547] VALUES (949,'باب بيان مشكل ما اختلف فيه أهل العلم من البيع الذي يقع بين الناس بالأثمان التي لا يتغابنون فيها، هل يكون ذلك بيعا منعقدا أو لا يكون كذلك','6057','948','0');
INSERT INTO [t22547] VALUES (950,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في دعائه للأنصار، هل دخل في ذلك أبناؤهم أم لا؟','6059','948','0');
INSERT INTO [t22547] VALUES (951,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الصدقة في المواشي','6065','948','0');
INSERT INTO [t22547] VALUES (952,'باب بيان مشكل ما روي عن علي بن أبي طالب رضي الله عنه في رفع الأيدي في التكبير لافتتاح الصلاة، وفيما سوى ذلك مما يختلف أهل العلم فيه من رفع','6072','948','0');
INSERT INTO [t22547] VALUES (953,'باب بيان مشكل ما روي عن عبد الله بن مسعود عن النبي صلى الله عليه وسلم في هذا المعنى','6076','948','0');
INSERT INTO [t22547] VALUES (954,'باب بيان مشكل ما روي عن عبد الله بن عمر رضي الله عنهما في هذا المعنى','6079','948','0');
INSERT INTO [t22547] VALUES (955,'باب بيان مشكل ما روى أبو هريرة عن النبي صلى الله عليه وسلم في هذا المعنى','6091','948','0');
INSERT INTO [t22547] VALUES (956,'باب بيان مشكل ما روي عن مالك بن الحويرث عن رسول الله صلى الله عليه وسلم في هذا المعنى','6093','948','0');
INSERT INTO [t22547] VALUES (957,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه للذي قال له عند قوله: " لن ينجي أحدا منكم عمله؟ "، قالوا: ولا أنت يا رسول الله؟ بما أجابه في ذلك','6095','948','0');
INSERT INTO [t22547] VALUES (958,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تركه مالك البعير الذي اشتكى إليه أنه يجيعه ويدئبه في العمل بترك أخذه إياه بعلفه','6097','948','0');
INSERT INTO [t22547] VALUES (959,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في تركه قتل مسيلمة الكذاب لما قدم عليه المدينة، وأبي أن يؤمن به إلا أن يجعل له الأمر من بعده','6099','948','0');
INSERT INTO [t22547] VALUES (960,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الذين يظلهم الله في ظله يوم لا ظل إلا ظله عز وجل','6101','948','0');
INSERT INTO [t22547] VALUES (961,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما كان منه في الذي طعنت رجله بقرن، فسأل القود فأقاده، فشلت رجل المقتص، وبرأت رجل المقتص منه','6107','948','0');
INSERT INTO [t22547] VALUES (962,'باب بيان مشكل ما روي عن أبي بكر الصديق رضي الله عنه من قوله: " والله لو منعوني عناقا أو عقالا، - على ما روي عنه من هاتين الكلمتين -، مما كانوا يؤدونه إلى رسول الله صلى الله عليه وسلم، لقاتلتهم عليه','6110','948','0');
INSERT INTO [t22547] VALUES (963,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره اليهود لما جاءوه بالرجل والمرأة اللذين زنيا منهم محكمين له فيهما أن يأتوه بالتوراة في شأن الرجم، ورجمه إياهما بعد ذلك','6113','948','0');
INSERT INTO [t22547] VALUES (964,'باب بيان مشكل ما روي عن عبد الله بن مسعود، عن النبي صلى الله عليه وسلم في الصلاة التي واعد رسول الله صلى الله عليه وسلم المتخلفين عنها بإحراق بيوتهم، أي الصلوات هي؟','6117','948','0');
INSERT INTO [t22547] VALUES (965,'باب بيان مشكل ما روي عن أبي هريرة، عن رسول الله صلى الله عليه وسلم في الصلاة التي كان من رسول الله صلى الله عليه وسلم في التخلف عنها الوعيد المذكور في الحديث الذي ذكرناه في الباب الأول، أي الصلوات هي؟','6121','948','0');
INSERT INTO [t22547] VALUES (966,'باب بيان مشكل ما روي عن جابر بن عبد الله عن رسول الله صلى الله عليه وسلم أنه قال ذلك القول من أجل شيء كان من رجل','6129','948','0');
INSERT INTO [t22547] VALUES (967,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في رفعه القصاص عن العبد الذي قطع أذن عبد لغير مواليه','6137','948','0');
INSERT INTO [t22547] VALUES (968,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في الصيام الذي كان أمر به عبد الله بن عمرو وما جعله في صوم يوم منه في عشرة أيام، وفي صوم يومين منه تسعة أيام، وفي صوم ثلاثة أيام ثمانية أيام','6141','948','0');
INSERT INTO [t22547] VALUES (969,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صوم داود عليه السلام يوما وإفطاره يوما، وأنه أحب الصيام إلى الله عز وجل','6144','948','0');
INSERT INTO [t22547] VALUES (970,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن بيع الطعام حتى يجري فيه الصاعان','6151','948','0');
INSERT INTO [t22547] VALUES (971,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره ضباعة بنت الزبير بن عبد المطلب أن تشترط في إحرامها أن حلها حيث تحبس','6153','948','0');
INSERT INTO [t22547] VALUES (972,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في صلاته بالناس وهو حامل أمامة فيها على عنقه بوضعه إياها إذا ركع، وإعادته إياها إذا رفع','6171','948','0');
INSERT INTO [t22547] VALUES (973,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من اقتطع مال امرئ مسلم بيمينه حرم الله عليه الجنة، وأوجب له النار "','6180','948','0');
INSERT INTO [t22547] VALUES (974,'باب بيان مشكل الواجب فيما اختلف الناس فيه من بقاء السحر، هل يعمل شيئا، ومن بطلانه حتى لا يعمل، مما روي عن رسول الله صلى الله عليه وسلم في ذلك','6188','948','0');
INSERT INTO [t22547] VALUES (975,'باب بيان مشكل الواجب فيما اختلف فيه أهل العلم من قول الرجل: لفلان علي ما بين كذا إلى كذا، بما روي عن رسول الله صلى الله عليه وسلم','6191','948','0');
INSERT INTO [t22547] VALUES (976,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم، مما يدل على أن الرجل إذا قال: أحدثك فلان بكذا؟ فقال: نعم. أنه يكون بذلك في حكم المبتدئ به، الناطق بجميعه','6193','948','0');
INSERT INTO [t22547] VALUES (977,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما كان يقول عند وداعه من كان يودعه','6197','948','0');
INSERT INTO [t22547] VALUES (978,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في " مرحبا وأهلا " ما المراد بهما؟ قال أبو جعفر رحمه الله: قد ذكرنا فيما تقدم منا في كتابنا هذا حديث مسروق، عن عائشة رضي الله عنها، في مجيء فاطمة رضي الله عنها إلى رسول الله صلى الله عليه وسلم في','6201','948','0');
INSERT INTO [t22547] VALUES (979,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم عن الجمع بين العمتين، والجمع بين الخالتين، وعن الجمع بين الخالة والعمة','6207','948','0');
INSERT INTO [t22547] VALUES (980,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " شهدت مع عمومتي حلف المطيبين "','6217','948','0');
INSERT INTO [t22547] VALUES (981,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " الطواف بالبيت صلاة، إلا أن الله تعالى أحل فيه المنطق، فمن نطق فلا ينطق إلا بخير "','6227','948','0');
INSERT INTO [t22547] VALUES (982,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه الذي سأله: متى كنت نبيا؟ بقوله له: " وآدم بين الروح والجسد "','6230','948','0');
INSERT INTO [t22547] VALUES (983,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما يقضي بين المختلفين من أهل العلم في الارتزاق على القضاء مما يبيحه بعضهم، ومما يمنع منه غيرهم منه قال أبو جعفر: لا نعلم أحدا من المتقدمين روي عنه النهي عن ذلك إلا عمر بن الخطاب رضي الله عنه، من','6233','948','0');
INSERT INTO [t22547] VALUES (984,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في اكتتابه على كل بطن عقوله','6238','948','0');
INSERT INTO [t22547] VALUES (985,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه أن يقال للمنافق: سيد','6241','948','0');
INSERT INTO [t22547] VALUES (986,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " العبادة في الهرج كهجرة إلي "','6243','948','0');
INSERT INTO [t22547] VALUES (987,'باب بيان مشكل ما روي مما اختلف فيه أهل العلم في الحلفاء هل يعقلون مع من حالفوه جناية بعضهم، أو هل يعقل عنهم من حالفوهم جناياتهم مما روي عن رسول الله صلى الله عليه وسلم في ذلك','6245','948','0');
INSERT INTO [t22547] VALUES (988,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أسرع الخير ثوابا، وفي أسرع الذنوب عقوبة','6251','948','0');
INSERT INTO [t22547] VALUES (989,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره عثمان بن أبي العاص أن يتخذ مؤذنا لا يأخذ على أذانه أجرا','6254','948','0');
INSERT INTO [t22547] VALUES (990,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " أي المسلمين جلدته، أو لعنته، أو سببته، فاجعل ذلك له زكاة وقربة "','6256','948','0');
INSERT INTO [t22547] VALUES (991,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قوله لسائله: إنه سعى قبل أن يطوف: " لا حرج "','6269','948','0');
INSERT INTO [t22547] VALUES (992,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في أمره بالدعاء الجامع','6278','948','0');
INSERT INTO [t22547] VALUES (993,'باب بيان مشكل ما روي عن علي بن أبي طالب رضي الله عنه فيما كان يفعله فيما حدثه به غيره عن رسول الله صلى الله عليه وسلم','6285','948','0');
INSERT INTO [t22547] VALUES (994,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من أمره بالتبليغ عنه، وحمده فاعل ذلك، وما يدخل في هذا المعنى، وما قد روي عن عمر من حبسه بعد رسول الله صلى الله عليه وسلم ذوي الرواية الكثيرة عنه قال أبو جعفر: قد ذكرنا فيما تقدم منا في كتابنا هذا','6294','948','0');
INSERT INTO [t22547] VALUES (995,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في حب الغنى الذي يتوهم بعض الناس أنه الغنى من المال، وما روي عنه في ذلك من سؤال الله عز وجل الغنى','6303','948','0');
INSERT INTO [t22547] VALUES (996,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن نزل به فاقة، فأنزلها بالله تعالى، أو أنزلها بالناس','6307','948','0');
INSERT INTO [t22547] VALUES (997,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله لعمرو بن العاص: " نعما بالمال الصالح للمرء الصالح "','6309','948','0');
INSERT INTO [t22547] VALUES (998,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما أجاب به زيد بن أرقم، والبراء بن عازب الأنصاريين فيما كانا سألاه عنه من ابتياعهما شيئا بنسيئة، وشيئا بنقد، وكلاهما مما لا يصلح فيه النساء وقوله لهما: " ما كان يدا بيد، فخذوه، وما كان نسيئة،','6311','948','0');
INSERT INTO [t22547] VALUES (999,'باب بيان مشكل الصحيح من ما اختلف فيه أهل العلم من هبة المرأة نفسها من رجل على سبيل التزويج، هل يكون ذلك تزويجا، أو لا يكون تزويجا، وما روي فيه من الآثار','6317','948','0');
INSERT INTO [t22547] VALUES (1000,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيمن كان وهب له نفسه من النساء، هل كان منه في شيء منهن قبولا، واحتباسا لها زوجة، أو لم يكن؟','6322','948','0');
INSERT INTO [t22547] VALUES (1001,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا سمعتم عني حديثا تعرفه قلوبكم، وتلين له أشعاركم، وأبشاركم، فترون أنه منكم قريب، فأنا أولاكم به، وإذا سمعتم عني بحديث تنكره قلوبكم، وتنفر منه أشعاركم، وأبشاركم، وترون أنه منكر،','6324','948','0');
INSERT INTO [t22547] VALUES (1002,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " إذا حدثتم عني حديثا تعرفونه، ولا تنكرونه، فصدقوا به قلته، أو لم أقله، فإني أقول ما يعرف، ولا ينكر، وإذا حدثتم عني حديثا تنكرونه ولا تعرفونه، فكذبوه، فإني لا أقول ما ينكر "','6327','948','0');
INSERT INTO [t22547] VALUES (1003,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم مما اختلف فيه أهل العلم، هل عليه بعد رفعه رأسه من السجدة الأخيرة من الركعة التي هي شفع صلاته أن يقعد قعدة ثم يقوم للثانية، أو يقوم إلى الثانية ولا يقعد؟','6329','948','0');
INSERT INTO [t22547] VALUES (1004,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في قول المؤذن في أذان الصبح: الصلاة خير من النوم، هل ذلك فيما علمه صلى الله عليه وسلم أبا محذورة، أو هو من سنة الأذان، أو ليس من سنته؟','6335','948','0');
INSERT INTO [t22547] VALUES (1005,'باب بيان مشكل ما روي فيما يقال فيه في المطر: الصلاة في الرحال قال أبو جعفر: في هذا آثار كثيرة يستغنى بشهرتها، واستفاضتها عن ذكرها في هذا الكتاب، غير أنا أردنا أن نعرف المواضع التي أمر بها رسول الله صلى الله عليه وسلم أن تفعل فيه','6343','948','0');
INSERT INTO [t22547] VALUES (1006,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في عهدة الرقيق','6346','948','0');
INSERT INTO [t22547] VALUES (1007,'باب بيان مشكل ما روي عن فضالة بن عبيد في القلادة ذات الذهب والخرز التي بيعت بذهب، وما رواه بعضهم في ذلك مما رفعه إلى النبي صلى الله عليه وسلم أنها لا تباع، حتى تفصل، وما رواه بعضهم موقوفا على فضالة','6355','948','0');
INSERT INTO [t22547] VALUES (1008,'باب بيان مشكل ما روي عن عبد الله بن عباس في السبب الذي نزل قوله تعالى: لا إكراه في الدين','6373','948','0');
INSERT INTO [t22547] VALUES (1009,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " اغد عالما، أو متعلما، أو محبا، أو مستمعا، ولا تكن الخامس، فتهلك "، وما روي عن ابن مسعود مما يدل في ذلك من قوله: " ولا تغد إمعة فيما بين ذلك "','6379','948','0');
INSERT INTO [t22547] VALUES (1010,'باب بيان مشكل ما روي في السبب الذي نزل فيه قوله تعالى: نساؤكم حرث لكم فأتوا حرثكم أنى شئتم، وما كان من النبي صلى الله عليه وسلم عند نزولها مما أعلم الناس به المراد بها','6383','948','0');
INSERT INTO [t22547] VALUES (1011,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من نهيه عن قتل أصحاب الصوامع','6406','948','0');
INSERT INTO [t22547] VALUES (1012,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله: " من ظلم شبرا من الأرض طوقه من سبع أرضين','6411','948','0');
INSERT INTO [t22547] VALUES (1013,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في عقوبة من أخذ شبرا من الأرض في الدنيا، كيف هي يوم القيامة؟ مما يخالف ما في الباب الأول','6420','948','0');
INSERT INTO [t22547] VALUES (1014,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من قوله في الرهن: " الظهر يركب بنفقته إذا كان مرهونا، ولبن الدر يشرب بنفقته إذا كان مرهونا "','6424','948','0');
INSERT INTO [t22547] VALUES (1015,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم من جوابه من سأله عن الإسلام هل له منتهى؟','6428','948','0');
INSERT INTO [t22547] VALUES (1016,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم فيما أفسدت المواشي شيئا من الزرع في الليل، وفي النهار','6431','948','0');
INSERT INTO [t22547] VALUES (1017,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن بيع الرطب بالتمر','6434','948','0');
INSERT INTO [t22547] VALUES (1018,'باب بيان مشكل ما روي عن رسول الله صلى الله عليه وسلم في نهيه عن الإقعاء في الصلاة ما هو','6446','948','0');
