INSERT INTO [t22592] VALUES (1,'[مقدمات]','2','0','0');
INSERT INTO [t22592] VALUES (2,'مقدمة الناشر الطبعة الثانية','2','1','0');
INSERT INTO [t22592] VALUES (3,'مقدمة الناشر','3','1','0');
INSERT INTO [t22592] VALUES (4,'مقدمة المؤلف','5','1','0');
INSERT INTO [t22592] VALUES (5,'ترجمة المؤلف الشيخ إبراهيم بن محمد بن سالم بن ضويان بقلم الشيخ عبد العزيز الناصر الرشيد مع تتمتها بقلم الشيخ العلامة محمد بن عبد العزيز بن مانع','11','1','0');
INSERT INTO [t22592] VALUES (6,'نسبه:','11','5','0');
INSERT INTO [t22592] VALUES (7,'أخلاقه:','11','5','0');
INSERT INTO [t22592] VALUES (8,'مشايخه:','12','5','0');
INSERT INTO [t22592] VALUES (9,'تلاميذه:','12','5','0');
INSERT INTO [t22592] VALUES (10,'مصنفاته:','13','5','0');
INSERT INTO [t22592] VALUES (11,'تتمة الترجمة بقلم الشيخ العلامة محمد بن عبد العزيز بن مانع','15','1','0');
INSERT INTO [t22592] VALUES (12,'مقدمة منار السبيل بقلم زهير الشاويش','19','1','0');
INSERT INTO [t22592] VALUES (13,'ترجمة صاحب المتن العلامة الشيخ مرعي بن يوسف الكرمي','23','1','0');
INSERT INTO [t22592] VALUES (14,'تخريج أحاديث المقدمة','24','0','0');
INSERT INTO [t22592] VALUES (15,'(١) - (حديث: "كل أمر ذي بال لا يبدأ فيه ب‍بسم الله الرحمن الرحيم فهو أبتر".','24','14','0');
INSERT INTO [t22592] VALUES (16,'(٢) - (حديث: " كل أمر ذى بال لا يبدأ فيه بالحمد لله , فهو أقطع ".','25','14','0');
INSERT INTO [t22592] VALUES (17,'(٣) - (حديث عمر " هذا جبريل أتاكم يعلمكم دينكم ")','27','14','0');
INSERT INTO [t22592] VALUES (18,'(٤) - (قوله صلى الله عليه وسلم: " أكثروا على من الصلاة ".','29','14','0');
INSERT INTO [t22592] VALUES (19,'(٥) - (قوله صلى الله عليه وسلم: " البخيل من ذكرت عنده فلم يصل علي "','30','14','0');
INSERT INTO [t22592] VALUES (20,'(٦) - حديث: " رغم أنف رجل ذكرت عنده فلم يصل علي "','31','14','0');
INSERT INTO [t22592] VALUES (21,'(٧) - (" وبعد , فى الخطب والمكاتبات , فعله عليه السلام "','31','14','0');
INSERT INTO [t22592] VALUES (22,'كتاب الطهارة','34','0','0');
INSERT INTO [t22592] VALUES (23,'[الأحاديث من ٨ - ٢٦]','34','22','0');
INSERT INTO [t22592] VALUES (24,'(٨) - (قول النبى صلى الله عليه وآله وسلم:" اللهم طهرنى بالماء والثلج والبرد "','34','23','0');
INSERT INTO [t22592] VALUES (25,'(٩) - (قوله فى البحر: " هو الطهور ماؤه , الحل ميتته ".','35','23','0');
INSERT INTO [t22592] VALUES (26,'(١٠) - (قوله صلى الله عليه وسلم فى خطبته يوم النحر بمنى: " إن دماءكم وأموالكم عليكم حرام كحرمة يومكم هذا فى شهركم هذا , فى بلدكم هذا".','36','23','0');
INSERT INTO [t22592] VALUES (27,'(١١) - (حديث الحكم بن عمرو الغفارى رضى الله عنه: " أن رسول الله صلى الله عليه وسلم نهى أن يتوضأ الرجل بفضل طهور المرأة ".','36','23','0');
INSERT INTO [t22592] VALUES (28,'(١٢) - (حديث: " دع ما يريبك إلى ما لا يريبك ".','37','23','0');
INSERT INTO [t22592] VALUES (29,'(١٣) - (حديث أسامة (١) : " أن رسول الله صلى الله عليه وآله وسلم','37','23','0');
INSERT INTO [t22592] VALUES (30,'(١٤) - حديث أبى سعيد قال: " قيل: يا رسول الله أنتوضأ من بئر بضاعة؟ - وهى بئر يلقى فيها الحيض ولحوم الكلاب والنتن - فقال صلى الله عليه وسلم: " الماء طهور لا ينجسه شىء ".','38','23','0');
INSERT INTO [t22592] VALUES (31,'(١٥) - (حديث: " أرأيتم لو أن نهرا بباب أحدكم يغتسل منه كل يوم خمس مرات , هل يبقى من درنه شىء؟ "','40','23','0');
INSERT INTO [t22592] VALUES (32,'(١٦) - (روى الدارقطنى بإسناد صحيح عن عمر: " أنه كان يسخن له ماء فى قمقم , فيغتسل به "','41','23','0');
INSERT INTO [t22592] VALUES (33,'(١٧) - (روى ابن أبي شيبة عن ابن عمر أنه كان يغتسل بالحميم)','43','23','0');
INSERT INTO [t22592] VALUES (34,'(١٨) - (حديث: " لا تفعلى فإنه يورث البرص ".','43','23','0');
INSERT INTO [t22592] VALUES (35,'(١٩) - (حديث:" أن النبى صلى الله عليه وسلم صب على جابر من وضوئه ".','47','23','0');
INSERT INTO [t22592] VALUES (36,'(٢٠) - (فى حديث صلح الحديبية: " وإذا توضأ كادوا يقتتلون على وضوئه ") .','47','23','0');
INSERT INTO [t22592] VALUES (37,'(٢١) - (قوله صلى الله عليه وسلم: " إذا استيقظ أحدكم من نومه فليغسل يديه قبل أن يدخلهما فى الإناء ثلاثا , فإن أحدكم لا يدرى أين باتت يده ".','52','23','0');
INSERT INTO [t22592] VALUES (38,'(٢٢) - (حديث عمر: " إنما الأعمال بالنيات "','52','23','0');
INSERT INTO [t22592] VALUES (39,'(٢٣) - (حديث ابن عمر قال: " سمعت رسول الله صلى الله عليه وسلم وهو يسئل عن الماء يكون فى الفلاة من الأرض , وما ينوبه من السباع والدواب؟ فقال: إذا كان الماء قلتين لم يحمل الخبث ". رواه الخمسة , وفى لفظ ابن ماجه وأحمد: " لم ينجسه شىء "','53','23','0');
INSERT INTO [t22592] VALUES (40,'(٢٤) - (قول النبى صلى الله عليه وسلم: " إذا ولغ الكلب فى إناء أحدكم , فليغسله سبع مرات ".','53','23','0');
INSERT INTO [t22592] VALUES (41,'(٢٥) - (حديث: " بئر بضاعة "','55','23','0');
INSERT INTO [t22592] VALUES (42,'(٢٦) - (حديث: " الدين النصيحة "','55','23','0');
INSERT INTO [t22592] VALUES (43,'باب الآنية','57','22','0');
INSERT INTO [t22592] VALUES (44,'(٢٧) - (حديث: " أن النبى صلى الله عليه وسلم اغتسل من جفنة "','57','43','0');
INSERT INTO [t22592] VALUES (45,'(٢٨) - (" وتوضأ من تور من صفر "','58','43','0');
INSERT INTO [t22592] VALUES (46,'(٢٩) - " وتوضأ من تور من حجارة "','58','43','0');
INSERT INTO [t22592] VALUES (47,'(٣٠) - (" وتوضأ من قربة ")','59','43','0');
INSERT INTO [t22592] VALUES (48,'(٣١) - " وتوضأ من إداوة "','59','43','0');
INSERT INTO [t22592] VALUES (49,'(٣٢) - روى حذيفة أن النبى صلى الله عليه وسلم قال: " لا تشربوا فى آنية الذهب','60','43','0');
INSERT INTO [t22592] VALUES (50,'(٣٣) - (قال صلى الله عليه وسلم: " الذى يشرب فى آنية الذهب والفضة إنما يجرجر فى بطنه نار جهنم ".','61','43','0');
INSERT INTO [t22592] VALUES (51,'(٣٤) - (روى أنس رضى الله عنه: " أن قدح النبى صلى الله عليه وسلم انكسر فاتخذ مكان الشعب سلسلة من فضة ".','63','43','0');
INSERT INTO [t22592] VALUES (52,'(٣٥) - (حديث: " أن النبى صلى الله عليه وسلم أضافه يهودى بخبز وإهالة سنخة ") .','64','43','0');
INSERT INTO [t22592] VALUES (53,'(٣٦) - (" توضأ صلى الله عليه وسلم من مزادة مشركة ")','65','43','0');
INSERT INTO [t22592] VALUES (54,'(٣٧) - (روى أبو ثعلبة الخشنى قال: " قلت: يا رسول الله! إنا بأرض قوم , أهل كتاب , أفنأكل فى آنيتهم؟ قال: لا تأكلوا فيها إلا أن لا تجدوا غيرها , فاغسلوها , ثم كلوا فيها ".','67','43','0');
INSERT INTO [t22592] VALUES (55,'(٣٨) - (روى أحمد عن يحيى بن سعيد عن شعبة عن الحكم عن ابن أبى ليلى عن عبد الله بن عكيم قال: " قرىء علينا كتاب رسول الله صلى الله عليه وسلم فى أرض جهينة وأنا غلام شاب: أن لا تنتفعوا من الميتة بإهاب ولا عصب ".','69','43','0');
INSERT INTO [t22592] VALUES (56,'(٣٩) - (حديث جابر أن النبى صلى الله عليه وسلم قال: " أوك سقاءك , واذكر اسم الله','72','43','0');
INSERT INTO [t22592] VALUES (57,'باب الإستنجاء وآداب التخلي','74','22','0');
INSERT INTO [t22592] VALUES (58,'(٤٠) - (حديث سلمان عند مسلم: " نهانا أن نستنجى برجيع أو عظم "','74','57','0');
INSERT INTO [t22592] VALUES (59,'(٤١) - (قول سلمان: " نهانا - يعنى النبى صلى الله عليه ولم - أن نستنجى باليمين','74','57','0');
INSERT INTO [t22592] VALUES (60,'(٤٢) - (قول عائشة رضى الله عنها: " مرن أزواجكن أن يتبعوا الحجارة بالماء من أثر الغائط والبول , فإنى أستحييهم , وإن النبى صلى الله عليه وسلم كان يفعله ") .','75','57','0');
INSERT INTO [t22592] VALUES (61,'(٤٣) - (حديث أنس: " كان النبى صلى الله عليه وسلم يدخل الخلاء فأحمل أنا وغلام نحوى إداوة (١) من ماء وعنزة فيستنجى بالماء ".','76','57','0');
INSERT INTO [t22592] VALUES (62,'(٤٤) - (حديث عائشة مرفوعا: " إذا ذهب أحدكم إلى الغائط فليستطب بثلاثة أحجار فإنها تجزىء عنه ".','77','57','0');
INSERT INTO [t22592] VALUES (63,'(٤٥) - (روى أبو داود من حديث أبى هريرة مرفوعا: " نزلت هذه الآية فى أهل قباء (فيه رجال يحبون أن يتطهروا) قال: كانوا يستنجون بالماء فنزلت هذه الآية "','77','57','0');
INSERT INTO [t22592] VALUES (64,'(٤٦) - (حديث ابن مسعود أن النبى صلى الله عليه وسلم قال: " لا تستنجوا بالروث ولا بالعظام , فإنه زاد إخوانكم من الجن ".','78','57','0');
INSERT INTO [t22592] VALUES (65,'(٤٧) - (قوله صلى الله عليه وسلم: " يغسل ذكره ويتوضأ "','79','57','0');
INSERT INTO [t22592] VALUES (66,'(٤٨) - (قال صلى الله عليه وسلم: " إذا ذهب أحدكم إلى الغائط فليذهب معه بثلاثة أحجار فإنها تجزىء عنه ".','79','57','0');
INSERT INTO [t22592] VALUES (67,'(٤٩) - (حديث: " من استنجى من الريح , فليس منا ".','79','57','0');
INSERT INTO [t22592] VALUES (68,'فصل ما ليس لداخل الخلاء','80','22','0');
INSERT INTO [t22592] VALUES (69,'(٥٠) - (حديث على مرفوعا: " ستر ما بين الجن وعورات بنى آدم إذا دخل الخلاء أن يقول: بسم الله ".','80','68','0');
INSERT INTO [t22592] VALUES (70,'(٥١) - (عن أنس قال: " كان النبى صلى الله عليه وسلم إذا دخل الخلاء قال: اللهم إنى أعوذ بك من الخبث والخبائث ".','83','68','0');
INSERT INTO [t22592] VALUES (71,'(٥٢) - (حديث عائشة: " كان صلى الله عليه وسلم إذا خرج من الخلاء قال: غفرانك ".','84','68','0');
INSERT INTO [t22592] VALUES (72,'(٥٣) - (عن أنس: " كان صلى الله عليه وسلم إذا خرج من الخلاء يقول:','84','68','0');
INSERT INTO [t22592] VALUES (73,'(٥٤) - (قول ابن عمر: " مر رجل بالنبى صلى الله عليه وسلم فسلم عليه وهو يبول فلم يرد عليه ".','85','68','0');
INSERT INTO [t22592] VALUES (74,'(٥٥) - (حديث قتادة عن عبد الله بن سرجس: " نهى رسول الله صلى الله عليه وسلم أن يبال فى الجحر قالوا لقتادة: ما يكره من البول فى الجحر؟ قال: يقال: إنها مساكن الجن ".','86','68','0');
INSERT INTO [t22592] VALUES (75,'(٥٦) - (" وروى أن سعد بن عبادة بال فى جحر بالشام ثم استلقى ميتا "','87','68','0');
INSERT INTO [t22592] VALUES (76,'(٥٧) - (قال حذيفة: " انتهى النبى صلى الله عليه وسلم إلى سباطة قوم فبال قائما ".','88','68','0');
INSERT INTO [t22592] VALUES (77,'(٥٨) - (روى الخطابى عن أبى هريرة: " أن النبى صلى الله عليه وسلم بال قائما من جرح كان بمأبضه "','89','68','0');
INSERT INTO [t22592] VALUES (78,'(٥٩) - (قال ابن مسعود: " إن من الجفاء أن تبول قائما "','89','68','0');
INSERT INTO [t22592] VALUES (79,'(٦٠) - (قال رسول الله صلى الله عليه وسلم: (" إذا أتيتم الغائط فلا تستقبلوا القبلة ولا تستدبروها , ولكن شرقوا أو غربوا ". قال أبو أيوب: فقدمنا الشام , فو جدنا مراحيض قد بنيت نحو الكعبة , فننحرف عنها , ونستغفر الله) .','92','68','0');
INSERT INTO [t22592] VALUES (80,'(٦١) - (قال مروان الأصغر: " أناخ ابن عمر بعيره مستقبل القبلة ثم جلس يبول إليها (١) فقلت: أبا عبد الرحمن أليس قد نهى عن هذا؟ قال: بلى إنما نهى عن هذا فى الفضاء , أما إذا كان بينك وبين القبلة شىء يسترك فلا بأس ".','93','68','0');
INSERT INTO [t22592] VALUES (81,'(٦٢) - (روى معاذ قال: قال رسول الله صلى الله عليه وسلم: " اتقوا الملاعن الثلاث: البراز فى الموارد , وقارعة الطريق , والظل ".','93','68','0');
INSERT INTO [t22592] VALUES (82,'(٦٣) - (حديث عقبة بن عامر مرفوعا , وفيه: " ولا أبالى أوسط القبور قضيت حاجتى , أو وسط السوق ".','95','68','0');
INSERT INTO [t22592] VALUES (83,'(٦٤) - (روى الترمذى عن عمر مرفوعا: " إياكم والتعرى , فإن معكم من لا يفارقكم إلا عند الغائط وحين يفضى الرجل إلى أهله فاستحيوهم وأكرموهم "','95','68','0');
INSERT INTO [t22592] VALUES (84,'باب السواك','97','22','0');
INSERT INTO [t22592] VALUES (85,'(٦٥) - (" كان النبى صلى الله عليه وسلم يستاك بعود أراك "','97','84','0');
INSERT INTO [t22592] VALUES (86,'(٦٦) - (قال صلى الله عليه وسلم: " السواك مطهرة للفم مرضاة للرب ".','98','84','0');
INSERT INTO [t22592] VALUES (87,'(٦٧) - (حديث على مرفوعا: " إذا صمتم فاستاكوا بالغداة ولا تستاكوا بالعشى ".','99','84','0');
INSERT INTO [t22592] VALUES (88,'(٦٨) - (قال عامر بن ربيعة: " رأيت رسول الله صلى الله عليه وسلم مالا أحصى يتسوك وهو صائم ".','100','84','0');
INSERT INTO [t22592] VALUES (89,'(٦٩) - (حديث أنس مرفوعا: " يجزىء من السواك الأصابع "','100','84','0');
INSERT INTO [t22592] VALUES (90,'(٧٠) - (قال صلى الله عليه وسلم: " لولا أن أشق على أمتى لأمرتهم بالسواك','101','84','0');
INSERT INTO [t22592] VALUES (91,'(٧١) - (عن حذيفة: " كان رسول الله صلى الله عليه وسلم إذا قام من الليل يشوص فاه بالسواك ".','104','84','0');
INSERT INTO [t22592] VALUES (92,'(٧٢) - (روى شريح بن هانىء قال: " سألت عائشة بأى شىء يبدأ النبى صلى الله عليه وسلم إذا دخل بيته؟ قالت بالسواك ".','105','84','0');
INSERT INTO [t22592] VALUES (93,'(٧٣) - (حديث أبى هريرة مرفوعا: " الفطرة خمس: الختان , والاستحداد , وقص الشارب , وتقليم الأظافر , ونتف الإبط ".','105','84','0');
INSERT INTO [t22592] VALUES (94,'(٧٤) - (" اللهم كما حسنت خلقى فحسن خلقى " رواه البيهقى عن عائشة. ورواه ابن مردويه وزاد: " وحرم وجهى على النار "','106','84','0');
INSERT INTO [t22592] VALUES (95,'(٧٥) - (حديث أبى أيوب مرفوعا: " أربع من سنن المرسلين: الحياء , والتعطر , والسواك , والنكاح ".','109','84','0');
INSERT INTO [t22592] VALUES (96,'(٧٦) - (حديث ابن عباس: " كان النبى صلى الله عليه وسلم يكتحل بالإثمد كل ليلة قبل أن ينام , وكان يكتحل فى كل عين ثلاثة أميال ".','112','84','0');
INSERT INTO [t22592] VALUES (97,'(٧٧) - (حديث ابن عمر مرفوعا: "خالفوا المشركين: أحفوا الشوارب , وأوفوا اللحى ".','112','84','0');
INSERT INTO [t22592] VALUES (98,'(٧٨) - (حديث " اختتن إبراهيم بعد ما أتت عليه ثمانون سنة "','113','84','0');
INSERT INTO [t22592] VALUES (99,'(٧٩) - (قال صلى الله عليه وسلم لرجل أسلم: " ألق عنك شعر الكفر واختتن ".','113','84','0');
INSERT INTO [t22592] VALUES (100,'(٨٠) - (قال صلى الله عليه وسلم: " إذا التقى الختانان وجب الغسل ".','114','84','0');
INSERT INTO [t22592] VALUES (101,'باب الوضوء','115','22','0');
INSERT INTO [t22592] VALUES (102,'(٨١) - (حديث أبى هريرة مرفوعا: " لا صلاة لمن لا وضوء له ولا وضوء لمن لم يذكر اسم الله عليه ".','115','101','0');
INSERT INTO [t22592] VALUES (103,'(٨٢) - (حديث: " عفى لأمتى عن الخطأ والنسيان "','116','101','0');
INSERT INTO [t22592] VALUES (104,'(٨٣) - (حديث عثمان فى صفة وضوئه صلى الله عليه وسلم وفيه: " فمضمض واستنثر ".','117','101','0');
INSERT INTO [t22592] VALUES (105,'(٨٤) - (قوله صلى الله عليه وسلم: " الأذنان من الرأس "','117','101','0');
INSERT INTO [t22592] VALUES (106,'(٨٥) - (توضأ رسول الله صلى الله عليه وسلم مرتبا وقال: " هذا وضوء لا يقبل الله الصلاة إلا به "','118','101','0');
INSERT INTO [t22592] VALUES (107,'(٨٦) - (حديث خالد بن معدان أن النبى صلى الله عليه وسلم: " رأى رجلا يصلى , وفى ظهر قدمه (١) لمعة قدر الدرهم لم يصبها الماء , فأمره أن يعيد','119','101','0');
INSERT INTO [t22592] VALUES (108,'(٨٧) - (" إنما الأعمال بالنيات "','121','101','0');
INSERT INTO [t22592] VALUES (109,'(٨٨) - (حديث: " من عمل عملا ليس عليه أمرنا فهو رد "','121','101','0');
INSERT INTO [t22592] VALUES (110,'(٨٩) - (روى عن عثمان: " أنه دعا بإناء , فأفرغ على كفيه ثلاث مرات فغسلهما , ثم أدخل يمينه فى الإناء , فمضمض واستنثر , ثم غسل وجهه ثلاثا , ويديه إلى المرفقين ثلاث مرات , ثم مسح برأسه ثم غسل رجليه','121','101','0');
INSERT INTO [t22592] VALUES (111,'(٩٠) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم مسح برأسه وأذنيه ظاهرهما وباطنهما ".','122','101','0');
INSERT INTO [t22592] VALUES (112,'(٩١) - (قول على لابن عباس: " ألا أتوضأ لك وضوء النبى صلى الله عليه وسلم؟ قال: بلى فداك أبى وأمى. قال: فوضع إناء فغسل يديه , ثم مضمض واستنشق واستنثر , ثم أخذ بيديه فصك بهما وجهه وألقم','122','101','0');
INSERT INTO [t22592] VALUES (113,'(٩٢) - (حديث أنس: " أن النبى صلى الله عليه وسلم كان إذا توضأ أخذ كفا من ماء فأدخله تحت حنكه فخلل به لحيته وقال هكذا أمرنى ربى عز وجل ".','123','101','0');
INSERT INTO [t22592] VALUES (114,'(٩٣) - (حديث: " كان صلى الله عليه وسلم يعجبه التيمن فى ترجله , وتنعله , وطهوره وفى شأنه كله ".','124','101','0');
INSERT INTO [t22592] VALUES (115,'(٩٤) - (حديث: " أن أبا هريرة توضأ فغسل يده حتى أشرع فى العضد , ورجله حتى أشرع فى الساق , ثم قال: هكذا رأيت رسول الله صلى الله عليه وسلم يتوضأ "','125','101','0');
INSERT INTO [t22592] VALUES (116,'(٩٤) - وقال أبو هريرة: قال رسول صلى الله عليه وسلم: "أنتم الغر المحجلون يوم القيامة من إسباغ الوضوء فمن استطاع منكم فليطل غرته وتحجيله"','125','101','0');
INSERT INTO [t22592] VALUES (117,'(٩٥) - (حديث: " أن النبى صلى الله عليه وسلم توضأ مرة مرة وقال: هذا وضوء من لم يتوضأه لم يقبل الله له صلاة , ثم توضأ مرتين ثم قال: هذا وضوئى ووضوء المرسلين قبلى ".','127','101','0');
INSERT INTO [t22592] VALUES (118,'(٩٦) - (حديث عمر مرفوعا: " ما منكم من أحد يتوضأ فيسبغ الوضوء ثم يقول: أشهد أنه لا إله إلا الله وحده لا شريك له , وأشهد أن محمدا عبده ورسوله , إلا فتحت له أبواب الجنة الثمانية يدخل من أيها شاء ".','127','101','0');
INSERT INTO [t22592] VALUES (119,'(٩٧) - (حديث المغيرة: " أنه أفرغ على النبى صلى الله عليه وسلم فى وضوئه ".','128','101','0');
INSERT INTO [t22592] VALUES (120,'(٩٨) - (قالت عائشة: " كنا نعد له صلى الله عليه وسلم طهوره وسواكه "','129','101','0');
INSERT INTO [t22592] VALUES (121,'باب مسح الخفين','129','22','0');
INSERT INTO [t22592] VALUES (122,'(٩٩) - (وعن جرير قال: " رأيت رسول الله صلى الله عليه وسلم بال ثم توضأ ومسح على خفيه ".','129','121','0');
INSERT INTO [t22592] VALUES (123,'(١٠٠) - (روى المغيرة قال: " كنت مع النبى صلى الله عليه وسلم فى سفر فأهويت لأنزع خفيه فقال: دعهما فإنى أدخلتهما طاهرتين فمسح عليهما ".','130','121','0');
INSERT INTO [t22592] VALUES (124,'(١٠١) - (روى المغيرة: " أن النبى صلى الله عليه وسلم مسح على الجوربين والنعلين ".','130','121','0');
INSERT INTO [t22592] VALUES (125,'(١٠٢) - (عن عوف بن مالك: " أن النبي صلى الله عليه وسلم أمر بالمسح علي الخفين في غزوة تبوك ثلاثة أيام ولياليهن للمسافر ويوما وليلة للمقيم "','131','121','0');
INSERT INTO [t22592] VALUES (126,'(١٠٣) - (قال علي: " لو كان الدين بالرأى لكن أسفل الخف أولى بالمسح من أعلاه وقد رأيت النبى صلى الله عليه وسلم يمسح على ظاهر خفيه ".','133','121','0');
INSERT INTO [t22592] VALUES (127,'(١٠٤) - (حديث صفوان بن عسال قال: " كان النبى صلى الله عليه وسلم يأمرنا إذا كنا سفرا أن لا ننزع خفافنا ثلاثة أيام ولياليهن إلا من جنابة ".','133','121','0');
INSERT INTO [t22592] VALUES (128,'فصل','135','22','0');
INSERT INTO [t22592] VALUES (129,'(١٠٥) - (حديث صاحب الشجة: " إنما كان يكفيه أن يتيمم ويعصر (١) أو يعصب على جرحه خرقة ثم (٢) يمسح عليها ويغسل سائر ج','135','128','0');
INSERT INTO [t22592] VALUES (130,'باب نواقض الوضوء','137','22','0');
INSERT INTO [t22592] VALUES (131,'(١٠٦) - (قوله صلى الله عليه وسلم: " ولكن من غائط وبول ونوم ".','137','130','0');
INSERT INTO [t22592] VALUES (132,'(١٠٧) - (قوله صلى الله عليه وسلم: " فلا ينصرف حتى يسمع صوتا أو يجد ريحا ".','137','130','0');
INSERT INTO [t22592] VALUES (133,'(١٠٨) - (قوله فى المذى: " يغسل ذكره ويتوضأ ".','138','130','0');
INSERT INTO [t22592] VALUES (134,'(١٠٩) - (حديث أنه قال للمستحاضة: " توضئى لكل صلاة ".','139','130','0');
INSERT INTO [t22592] VALUES (135,'(١١٠) - (قال صلى الله عليه وسلم لفاطمة بنت أبى حبيش: " إنه دم عرق فتوضئى لكل صلاة ".','139','130','0');
INSERT INTO [t22592] VALUES (136,'(١١١) - (روى معدان بن أبى طلحة عن أبى الدرداء: " أن النبى صلى الله عليه وسلم قاء فتوضأ فلقيت ثوبان فى مسجد دمشق فذكرت له ذلك فقال: صدق أنا صببت له وضوءه ".','140','130','0');
INSERT INTO [t22592] VALUES (137,'(١١٢) - (قال صلى الله عليه وسلم: " ولكن من غائط وبول ونوم "','141','130','0');
INSERT INTO [t22592] VALUES (138,'(١١٣) - (قال صلى الله عليه وسلم: " العين وكاء السه فمن نام فليتوضأ ".','141','130','0');
INSERT INTO [t22592] VALUES (139,'(١١٤) - (حديث أنس: " إن أصحاب النبى صلى الله عليه وسلم كانوا ينتظرون العشاء فينامون ثم يصلون ولا يتوضئون "','142','130','0');
INSERT INTO [t22592] VALUES (140,'(١١٥) - (فى حديث ابن عباس: " فجعلت إذا أغفيت يأخذ بشحمة أذنى ".','143','130','0');
INSERT INTO [t22592] VALUES (141,'(١١٦) - (حديث بسرة بنت صفوان أن النبى صلى الله عليه وسلم قال: " من مس ذكره فليتوضأ ".','143','130','0');
INSERT INTO [t22592] VALUES (142,'(١١٧) - (حديث أبى أيوب وأم حبيبة: " من مس فرجه فليتوضأ ".','143','130','0');
INSERT INTO [t22592] VALUES (143,'(١١٨) - (حديث جابر بن سمرة أن رجلا سأل النبى صلى الله عليه وسلم: " أأتوضأ من لحوم الغنم؟ قال إن شئت توضأ وإن شئت لا تتوضأ , قال أأتوضأ (١) من لحوم الإبل؟ قال: نعم توضأ من لحوم الإبل ".','145','130','0');
INSERT INTO [t22592] VALUES (144,'فصل','146','22','0');
INSERT INTO [t22592] VALUES (145,'(١١٩) - (قال صلى الله عليه وسلم: " إذا وجد أحدكم فى بطنه شيئا فأشكل عليه هل خرج منه شىء أم لا؟ فلا يخرجن من المسجد حتى يسمع صوتا أو يجد ريحا ".','146','144','0');
INSERT INTO [t22592] VALUES (146,'(١٢٠) - (حديث ابن عمر مرفوعا: " لا يقبل الله صلاة بغير طهور ولا صدقة من غلول ".','146','144','0');
INSERT INTO [t22592] VALUES (147,'(١٢١) - (قال صلى الله عليه وسلم: " الطواف بالبيت صلاة إلا أن الله أباح فيه الكلام ".','147','144','0');
INSERT INTO [t22592] VALUES (148,'(١٢٢) - (حديث أبى بكر بن محمد بن عمرو بن حزم عن أبيه عن جده: " أن النبى صلى الله عليه وسلم كتب إلى أهل اليمن كتابا , وفيه: لا يمس القرآن إلا طاهر ".','151','144','0');
INSERT INTO [t22592] VALUES (149,'(١٢٣) - (حديث على رضى الله عنه: " كان النبى صلى الله عليه وسلم لا يحجبه وربما قال: لا يحجزه عن القرآن شىء ليس الجنابة ".','154','144','0');
INSERT INTO [t22592] VALUES (150,'(١٢٤) - (قوله صلى الله عليه وسلم: " لا أحل المسجد لحائض ولا جنب ".','155','144','0');
INSERT INTO [t22592] VALUES (151,'باب ما يوجب الغسل','155','22','0');
INSERT INTO [t22592] VALUES (152,'(١٢٥) - (قال صلى الله عليه وسلم: " إذا فضخت الماء فاغتسل ".','155','151','0');
INSERT INTO [t22592] VALUES (153,'(١٢٦) - (قال صلى الله عليه وسلم - لما سئل هل على المرأة غسل إذا احتلمت؟ -: " نعم إذا رأت الماء ".','155','151','0');
INSERT INTO [t22592] VALUES (154,'(١٢٧) - (قال صلى الله عليه وسلم: " إذا جلس بين شعبها الأربع ومس الختان الختان وجب الغسل ".','156','151','0');
INSERT INTO [t22592] VALUES (155,'(١٢٨) - (حديث أن النبى صلى الله عليه وسلم: " أمر قيس بن عاصم أن يغتسل حين أسلم ".','156','151','0');
INSERT INTO [t22592] VALUES (156,'(١٢٩) - (قال صلى الله عليه وسلم: " اغسلنها ".','157','151','0');
INSERT INTO [t22592] VALUES (157,'(١٣٠) - (قال فى المحرم: " اغسلوه بماء وسدر "','158','151','0');
INSERT INTO [t22592] VALUES (158,'فصل','158','22','0');
INSERT INTO [t22592] VALUES (159,'(١٣١) - (حديث ميمونة: " وضع رسول الله صلى الله عليه وسلم وضوء الجنابة فأفرغ على يديه فغسلهما مرتين أو ثلاثا , ثم تمضمض واستنشق وغسل وجهه وذراعيه , ثم أفاض الماء على رأسه , ثم غسل جسده , فأتيته بالمنديل فلم يردها وجعل ينفض الماء بيديه ".','158','158','0');
INSERT INTO [t22592] VALUES (160,'(١٣٢) - (فى حديث عائشة: " ثم يخلل شعره بيده حتى إذا ظن أنه قد أروى (١) بشرته أفاض عليه الماء ثلاث مرات ثم غسل سائر جسده ".','159','158','0');
INSERT INTO [t22592] VALUES (161,'(١٣٣) - (عن على مرفوعا: " من ترك موضع شعرة من جنابة لم يصبها الماء فعل الله به كذا وكذا من النار ". قال على: فمن ثم عاديت شعرى.','159','158','0');
INSERT INTO [t22592] VALUES (162,'(١٣٤) - (قال صلى الله عليه وسلم لعائشة: " انقضى شعرك واغتسلى ".','160','158','0');
INSERT INTO [t22592] VALUES (163,'(١٣٥) - (فى بعض ألفاظ حديث أم سلمة أفأنقضه للحيضة؟ قال: " لا ".','161','158','0');
INSERT INTO [t22592] VALUES (164,'(١٣٦) - (حديث: قالت أم سلمة: قلت: يا رسول الله إنى امرأة أشد ضفر رأسى أفأنقضه لغسل الجنابة؟ فقال: " لا , إنما يكفيك أن تحثى على رأسك ثلاث حثيات ثم تفيضين عليك الماء فتطهرين ".','161','158','0');
INSERT INTO [t22592] VALUES (165,'(١٣٧) - (قول عائشة: " حتى إذا ظن أنه (١) أروى بشرته أفاض عليه الماء ".','162','158','0');
INSERT INTO [t22592] VALUES (166,'(١٣٨) - (حديث عائشة وميمونة فى " صفة غسله صلى الله عليه وسلم " متفق عليهما. وفى حديث ميمونة: " ثم تنحى فغسل قدميه "','162','158','0');
INSERT INTO [t22592] VALUES (167,'(١٣٩) - (حديث أنس رضى الله عنه قال: " كان النبى صلى الله عليه وسلم يغتسل بالصاع إلى خمسة أمداد ويتوضأ بالمد "','163','158','0');
INSERT INTO [t22592] VALUES (168,'(١٤٠) - (روى ابن ماجه: أن النبى صلى الله عليه وسلم مر بسعد وهو يتوضأ فقال: " ماهذا السرف؟ " فقال: أفى الوضوء إسراف قال: " نعم وإن كنت على نهر جار "','164','158','0');
INSERT INTO [t22592] VALUES (169,'(١٤١) - (حديث: " أن عائشة كانت تغتسل هى والنبى صلى الله عليه وسلم من إناء واحد يسع ثلاثة أمداد أو قريبا من ذلك ".','164','158','0');
INSERT INTO [t22592] VALUES (170,'(١٤٢) - (روى أبو داود والنسائى عن أم عمارة بنت كعب: " أن النبى صلى الله عليه وسلم توضأ فأتى بماء فى إناء قدر ثلثى المد ".','165','158','0');
INSERT INTO [t22592] VALUES (171,'فصل','165','22','0');
INSERT INTO [t22592] VALUES (172,'(١٤٣) (حديث أبى سعيد مرفوعا: " غسل الجمعة واجب على كل محتلم ".','165','171','0');
INSERT INTO [t22592] VALUES (173,'(١٤٤) - (حديث أبى هريرة مرفوعا: " من غسل ميتا فليغتسل ومن حمله فليتوضأ ".','166','171','0');
INSERT INTO [t22592] VALUES (174,'(١٤٥) - (قال صلى الله عليه وسلم: " من جاء منكم الجمعة فليغتسل ".','168','171','0');
INSERT INTO [t22592] VALUES (175,'(١٤٦) - (حديث ابن عباس والفاكه بن سعد: " أن النبى صلى الله عليه وسلم كان يغتسل يوم الفطر والأضحى ".','168','171','0');
INSERT INTO [t22592] VALUES (176,'(١٤٧) - (" اغتسل صلى الله عليه وسلم من الإغماء ".','170','171','0');
INSERT INTO [t22592] VALUES (177,'(١٤٨) - (قال صلى الله عليه وسلم لزينب بنت جحش لما استحيضت: " اغتسلى لكل صلاة ".','171','171','0');
INSERT INTO [t22592] VALUES (178,'(١٤٩) - (حديث زيد بن ثابت: " أنه رأى النبى صلى الله عليه وسلم تجرد لإهلاله واغتسل ".','171','171','0');
INSERT INTO [t22592] VALUES (179,'(١٥٠) - (" كان ابن عمر لا يقدم مكة إلا بات بذى طوى حتى يصبح ويغتسل ويدخل نهارا , ويذكر عن النبى صلى الله عليه وسلم أنه فعله ".','172','171','0');
INSERT INTO [t22592] VALUES (180,'باب التيمم','173','22','0');
INSERT INTO [t22592] VALUES (181,'(١٥١) - (حديث أن النبى صلى الله عليه وسلم تيمم لرد السلام)','173','180','0');
INSERT INTO [t22592] VALUES (182,'(١٥٢) - (حديث أبى أمامة مرفوعا: " جعلت الأرض كلها لى ولأمتى مسجدا وطهورا فأينما أدركت رجلا من أمتى الصلاة فعنده مسجده وعنده طهوره ".','173','180','0');
INSERT INTO [t22592] VALUES (183,'(١٥٣) - (قال صلى الله عليه وسلم: " إن الصعيد الطيب طهور المسلم وإن لم يجد الماء عشر سنين فإذا وجد الماء فليمسه بشرته فإن ذلك خير ".','174','180','0');
INSERT INTO [t22592] VALUES (184,'(١٥٤) - (عن عمرو بن العاص أنه لما بعث فى غزوة ذات السلاسل قال: " احتلمت فى ليلة باردة شديدة فأشفقت إن اغتسلت أن أهلك فتيممت ثم صليت بأصحابى صلاة الصبح ... "','174','180','0');
INSERT INTO [t22592] VALUES (185,'(١٥٥) - (قال صلى الله عليه وسلم: " إذا أمرتكم بأمر فأتوا منه ما استطعتم "','176','180','0');
INSERT INTO [t22592] VALUES (186,'(١٥٦) - (حديث عمران بن حصين: " عليك بالصعيد فإنه يكفيك ".','176','180','0');
INSERT INTO [t22592] VALUES (187,'(١٥٧) - (لأنه صلى الله عليه وسلم: " ضرب بيده الحائط ومسح وجهه ويديه "','177','180','0');
INSERT INTO [t22592] VALUES (188,'(١٥٨) - (وفى حديث عمار: " إنما كان يكفيك أن تقول بيديك هكذا ثم ضرب بيديه الأرض ضربة واحدة , ثم مسح الشمال على اليمين , وظاهر كفيه ووجهه ".','177','180','0');
INSERT INTO [t22592] VALUES (189,'(١٥٩) - (حديث: " إنما الأعمال بالنيات "','177','180','0');
INSERT INTO [t22592] VALUES (190,'(١٦٠) - (قال صلى الله عليه وسلم: " فإذا وجد الماء فليمسه بشرته فإن ذلك خير ".','178','180','0');
INSERT INTO [t22592] VALUES (191,'(١٦١) - (حديث عمار: " التيمم ضربة للوجه والكفين ".','178','180','0');
INSERT INTO [t22592] VALUES (192,'(١٦٢) - (قوله صلى الله عليه وسلم: " وإنما لكل امرىء مانوى "','179','180','0');
INSERT INTO [t22592] VALUES (193,'باب إزالة النجاسة','179','22','0');
INSERT INTO [t22592] VALUES (194,'(١٦٣) - (لقول ابن عمر: " أمرنا بغسل الأنجاس سبعا "','179','193','0');
INSERT INTO [t22592] VALUES (195,'(١٦٤) - (أمره صلى الله عليه وسلم: " القائم من نوم الليل أن يغسل يديه ثلاثا فإنه لا يدرى أين باتت يده "','180','193','0');
INSERT INTO [t22592] VALUES (196,'(١٦٥) - (قال صلى الله عليه وسلم لأسماء فى دم الحيض يصيب الثوب: " حتيه ثم اقرصيه ثم اغسليه بالماء "','180','193','0');
INSERT INTO [t22592] VALUES (197,'(١٦٦) - (حديث على مرفوعا: " بول الغلام ينضح , وبول الجارية يغسل "','181','193','0');
INSERT INTO [t22592] VALUES (198,'(١٦٧) - (حديث أبى هريرة مرفوعا: " إذا ولغ الكلب فى إناء أحدكم فليغلسه سبعا أولاهن بالتراب ".','181','193','0');
INSERT INTO [t22592] VALUES (199,'(١٦٨) - (حديث أن خولة بنت يسار قالت: يا رسول الله أرأيت لو بقى أثره - تعنى الدم -؟ فقال: " يكفيك الماء ولا يضرك أثره ".','182','193','0');
INSERT INTO [t22592] VALUES (200,'(١٦٩) - (حديث أم قيس بنت محصن: " أنها أتت بابن لها صغير لم يأكل الطعام إلى رسول الله صلى الله عليه وسلم فأجلسه فى حجره فبال على ثوبه فدعا بماء فنضحه ولم يغسله ".','183','193','0');
INSERT INTO [t22592] VALUES (201,'(١٧٠) - (عن على مرفوعا: " بول الغلام ينضح وبول الجارية يغسل ".','183','193','0');
INSERT INTO [t22592] VALUES (202,'(١٧١) - (قوله صلى الله عليه وسلم فى بول الأعرابى: " أريقوا عليه ذنوبا من ماء ".','183','193','0');
INSERT INTO [t22592] VALUES (203,'(١٧٢) - (حديث ابن عمر: أنه سمع النبى صلى الله عليه وسلم وهو يسأل عن الماء يكون فى الفلاة من الأرض وما ينوبه من السباع والدواب يقول: " إذا بلغ الماء قلتين لم يحمل الخبث - وفى رواية - لم ينجسه شىء "','184','193','0');
INSERT INTO [t22592] VALUES (204,'(١٧٣) - (حديث أبى قتادة مرفوعا - وفيه -: " فجاءت هرة فأصغى لها الإناء حتى شربت وقال: إنها ليست بنجس إنها من الطوافين عليكم والطوافات "','184','193','0');
INSERT INTO [t22592] VALUES (205,'(١٧٤) - (حديث: " المؤمن لا ينجس ".','186','193','0');
INSERT INTO [t22592] VALUES (206,'(١٧٥) - (حديث: " إذا وقع الذباب فى إناء أحدكم فليمقله - وفى لفظ -: فليغمسه فإن فى أحد جناحيه داء وفى الآخر شفاء ".','187','193','0');
INSERT INTO [t22592] VALUES (207,'(١٧٦) - (قوله صلى الله عليه وسلم: " صلوا فى مرابض الغنم ".','187','193','0');
INSERT INTO [t22592] VALUES (208,'(١٧٧) - (وقال للعرنيين: " انطلقوا إلى إبل الصدقة فاشربوا من أبوالها ".','188','193','0');
INSERT INTO [t22592] VALUES (209,'(١٧٨) - (قوله صلى الله عليه وسلم فى الذى يعذب فى قبره: " إنه كان لا يتنزه من بوله ".','188','193','0');
INSERT INTO [t22592] VALUES (210,'(١٧٩) - (قوله لعلى فى المذى: " اغسل ذكرك "','189','193','0');
INSERT INTO [t22592] VALUES (211,'(١٨٠) - (قول عائشة: " كنت أفرك المنى من ثوب رسول الله صلى الله عليه وسلم ثم يذهب فيصلى به ".','189','193','0');
INSERT INTO [t22592] VALUES (212,'(١٨١) - (قوله صلى الله عليه وسلم لأسماء فى الدم: " اغسليه بالماء ".','190','193','0');
INSERT INTO [t22592] VALUES (213,'(١٨٢) - (لقول عائشة: " يكون لإحدانا الدرع فيه تحيض ثم ترى فيه قطرة من الدم فتقصعه بريقها ـ وفى رواية ـ تبله بريقها ثم تقصعه بظفرها ".','190','193','0');
INSERT INTO [t22592] VALUES (214,'(١٨٣) - (قال ابن مسعود: " كنا لا نتوضأ من موطىء "','191','193','0');
INSERT INTO [t22592] VALUES (215,'(١٨٤) - (روى مسلم عن أبى هريرة مرفوعا , وفيه: " فإذا انتخع أحدكم فلينتخع عن يساره (١) تحت قدمه فإن لم يجد فليقل هكذا , فتفل فى ثوبه ثم مسح بعضه على (٢) بعض "','191','193','0');
INSERT INTO [t22592] VALUES (216,'باب الحيض','192','22','0');
INSERT INTO [t22592] VALUES (217,'(١٨٥) - (وقد روى عن عائشة أنها قالت: " إذا بلغت الجارية تسع سنين فهى امرأة ".','192','216','0');
INSERT INTO [t22592] VALUES (218,'(١٨٦) - (لقول عائشة: " إذا بلغت المرأة خمسين سنة خرجت من حد الحيض ".','193','216','0');
INSERT INTO [t22592] VALUES (219,'(١٨٧) - (لقوله صلى الله عليه وسلم فى سبايا أوطاس: " لا توطأ حامل حتى تضع , ولا حائل حتى تستبرىء بحيضة ".','193','216','0');
INSERT INTO [t22592] VALUES (220,'(١٨٨) - (قوله صلى الله عليه وسلم لحمنة بنت جحش: " تحيضى فى علم الله ستة أيام , أو سبعة , ثم اغتسلى وصلى أربعة وعشرين يوما , أو ثلاثة وعشرين يوما , كما يحيض النساء ويطهرن لميقات حيضهن وطهرهن ".','195','216','0');
INSERT INTO [t22592] VALUES (221,'(١٨٩) - (قوله صلى الله عليه وسلم: " إذا أقبلت الحيضة فدعى الصلاة ".','196','216','0');
INSERT INTO [t22592] VALUES (222,'(١٩٠) - (قوله صلى الله عليه وسلم: " أليس إحداكن إذا حاضت لم تصم ولم تصل؟ قلن: بلى "','197','216','0');
INSERT INTO [t22592] VALUES (223,'(١٩١) - (لقوله صلى الله عليه وسلم لعائشة لما حاضت: " افعلى ما يفعل الحاج غير أنه لا تطوفى بالبيت حتى تطهرى ".','199','216','0');
INSERT INTO [t22592] VALUES (224,'(١٩٢) - (قوله صلى الله عليه وسلم: " لا يقرأ الجنب ولا الحائض شيئا من القرآن "','199','216','0');
INSERT INTO [t22592] VALUES (225,'(١٩٣) - (قوله صلى الله عليه وسلم: " لا أحل المسجد لجنب ولا لحائض "','203','216','0');
INSERT INTO [t22592] VALUES (226,'(١٩٤) - (قوله صلى الله عليه وسلم لعائشة: " ناولينى الخمرة من المسجد فقالت: إنى حائض , فقال: إن حيضتك ليست بيدك ".','205','216','0');
INSERT INTO [t22592] VALUES (227,'(١٩٥) - قوله صلى الله عليه وسلم: "دعي الصلاة قدر الأيام التي كنت تحيضين فيها، ثم اغتسلي وصلي".','206','216','0');
INSERT INTO [t22592] VALUES (228,'(١٩٦) - (قوله صلى الله عليه وسلم: " لا يقبل الله صلاة حائض إلا بخمار "','207','216','0');
INSERT INTO [t22592] VALUES (229,'(١٩٧) - (روى ابن عباس عن النبى صلى الله عليه وسلم: " فى الذى يأتى امرأته وهى حائض: يتصدق بدينار أو نصف دينار ".','210','216','0');
INSERT INTO [t22592] VALUES (230,'(١٩٨) - (روى مالك عن علقمة عن أمه: أن النساء كن يرسلن بالدرجة فيها الشىء من الصفرة إلى عائشة فتقول: " لا تعجلن حتى ترين القصة البيضاء "','211','216','0');
INSERT INTO [t22592] VALUES (231,'(١٩٩) - (قول أم عطية: " كنا لا نعد الصفرة والكدرة بعد الطهر شيئا ".','212','216','0');
INSERT INTO [t22592] VALUES (232,'(٢٠٠) - (حديث معاذة: " إنها سألت عائشة رضى الله عنها: ما بال الحائض تقضى الصوم ولا تقضى الصلاة؟ فقالت: كان يصيبنا ذلك مع رسول الله صلى الله عليه وسلم فنؤمر بقضاء الصوم ولا نؤمر بقضاء الصلاة "','213','216','0');
INSERT INTO [t22592] VALUES (233,'(٢٠١) - (وقالت أم سلمة: " كانت المرأة من نساء النبى صلى الله عليه وسلم تقعد فى النفاس أربعين ليلة لا يأمرها النبى صلى الله عليه وسلم بقضاء صلاة النفاس "','215','216','0');
INSERT INTO [t22592] VALUES (234,'(٢٠٢) - (قوله صلى الله عليه وسلم لأم حبيبة: " امكثى قدر ما كانت تحبسك حيضتك ثم اغتسلى وصلى "','216','216','0');
INSERT INTO [t22592] VALUES (235,'(٢٠٣) - (حديث: أن فاطمة بنت أبى حبيش قالت: " يا رسول الله إنى أستحاض فلا أطهر أفأدع الصلاة؟ فقال: لا إن ذلك عروق وليست بالحيضة فإذا أقبلت الحيضة فدعى الصلاة فإذا أدبرت فاغسلى عنك الدم وصلى "','216','216','0');
INSERT INTO [t22592] VALUES (236,'(٢٠٤) - (وفى لفظ: " إذا كان دم الحيض فإنه أسود يعرف فأمسكى عن الصلاة , فإذا كان الآخر فتوضئى إنما هو عرق "','216','216','0');
INSERT INTO [t22592] VALUES (237,'(٢٠٥) - (حديث حمنة بنت جحش قالت: " قلت يا رسول الله إنى أستحاض حيضة شديدة فما ترى فيها؟ قال: أنعت لك الكرسف فإنه يذهب الدم , قالت: هو أكثر من ذلك قال: فاتخذى ثوبا , قالت: هو أكثر من ذلك قال: فتلجمى , قالت , إنما أثج ثجا , فقال لها: سآمرك بأمرين أيهما','217','216','0');
INSERT INTO [t22592] VALUES (238,'(٢٠٦) - (قوله صلى الله عليه وسلم لفاطمة بنت أبى حبيش: " وتوضئى لكل صلاة حتى يجىء ذلك الوقت "','217','216','0');
INSERT INTO [t22592] VALUES (239,'(٢٠٧) - (وقال فى المستحاضة: " وتتوضأ عند كل صلاة "','217','216','0');
INSERT INTO [t22592] VALUES (240,'(٢٠٨) - (حديث: " صلى وإن قطر على الحصير "','218','216','0');
INSERT INTO [t22592] VALUES (241,'(٢٠٩) - (" صلى عمر وجرحه يثعب دما "','218','216','0');
INSERT INTO [t22592] VALUES (242,'(٢١٠) - (وروى: " أن امرأة ولدت على عهده صلى الله عليه وسلم فلم تر دما فسميت ذات الجفوف "','219','216','0');
INSERT INTO [t22592] VALUES (243,'(٢١١) - (عن أم سلمة: " كانت النفساء على عهد النبى صلى الله عليه وسلم تجلس أربعين يوما "','219','216','0');
INSERT INTO [t22592] VALUES (244,'(٢١٢) - (حديث عثمان بن أبى العاص: " أنها أتته قبل الأربعين فقال: لا تقربينى "','219','216','0');
INSERT INTO [t22592] VALUES (245,'باب الأذان والإقامة','220','22','0');
INSERT INTO [t22592] VALUES (246,'(٢١٣) - (حديث: " إذا حضرت الصلاة , فليؤذن لكم أحدكم وليؤمكم أكبركم "','220','245','0');
INSERT INTO [t22592] VALUES (247,'(٢١٤) - (حديث عقبة بن عامر مرفوعا: " يعجب ربك من راعى غنم فى رأس شظية جبل يؤذن بالصلاة ويصلى فيقول الله عز وجل: انظروا إلى عبدى هذا يؤذن ويقيم الصلاة يخاف منى قد غفرت لعبدى وأدخلته الجنة "','223','245','0');
INSERT INTO [t22592] VALUES (248,'(٢١٥) - (قوله صلى الله عليه وسلم لمالك بن الحويرث ولابن عم له: " إذا سافرتما فأذنا وأقيما وليؤمكما أكبركما "','223','245','0');
INSERT INTO [t22592] VALUES (249,'(٢١٦) - (حديث: " إنما الأعمال بالنيات "','224','245','0');
INSERT INTO [t22592] VALUES (250,'(٢١٧) - (حديث: " أنه صلى الله عليه وسلم وصف المؤذنين بالأمانة "','224','245','0');
INSERT INTO [t22592] VALUES (251,'(٢١٨) - (حديث: " إذا حضرت الصلاة فليؤذن لكم أحدكم "','228','245','0');
INSERT INTO [t22592] VALUES (252,'(٢١٩) - (حديث: " إن بلالا يؤذن بليل فكلوا واشربوا حتى يؤذن ابن أم مكتوم ".','228','245','0');
INSERT INTO [t22592] VALUES (253,'(٢٢٠) - (قوله صلى الله عليه وسلم لعبد الله بن زيد: " ألقه على بلال فإنه أندى صوتا منك "','232','245','0');
INSERT INTO [t22592] VALUES (254,'(٢٢١) - (حديث: " أمناء الناس على صلاتهم وسحورهم المؤذنون ".','232','245','0');
INSERT INTO [t22592] VALUES (255,'(٢٢٢) - (حديث أبى هريرة: " لا يؤذن إلا متوضىء "','233','245','0');
INSERT INTO [t22592] VALUES (256,'(٢٢٣) - (قوله صلى الله عليه وسلم لبلال: " قم فأذن "','233','245','0');
INSERT INTO [t22592] VALUES (257,'(٢٢٤) - (" كان مؤذنو رسول الله صلى الله عليه وسلم يؤذنون قياما "','234','245','0');
INSERT INTO [t22592] VALUES (258,'(٢٢٥) - (قال الحسن العبدى: " رأيت أبا زيد صاحب رسول الله صلى الله عليه وسلم يؤذن قاعدا وكانت رجله أصيبت فى سبيل الله "','235','245','0');
INSERT INTO [t22592] VALUES (259,'(٢٢٦) - (قال ابن المنذر: " ثبت أن ابن عمر كان يؤذن على البعير فينزل فيقيم "','235','245','0');
INSERT INTO [t22592] VALUES (260,'(٢٢٧) - (حديث: " إن بلالا كان يؤذن فى أول الوقت لا يخرم وربما أخر الإقامة شيئا ".','236','245','0');
INSERT INTO [t22592] VALUES (261,'(٢٢٨) - (قوله صلى الله عليه وسلم لبلال: " إذا أذنت فترسل وإذا أقمت فاحدر ".','236','245','0');
INSERT INTO [t22592] VALUES (262,'(٢٢٩) - (روى أن: " بلالا كان يؤذن على سطح امرأة من بنى النجار بيتها من أطول بيت حول المسجد "','239','245','0');
INSERT INTO [t22592] VALUES (263,'(٢٣٠) - (قول أبى جحيفة: " إن بلالا وضع أصبعيه فى أذنيه ".','241','245','0');
INSERT INTO [t22592] VALUES (264,'(٢٣١) - (عن سعد القرظ: " أن رسول الله صلى الله عليه وسلم أمر بلالا أن يجعل أصبعيه فى أذنيه وقال إنه أرفع لصوتك "','242','245','0');
INSERT INTO [t22592] VALUES (265,'(٢٣٢) - (" مستقبلا القبلة لفعل مؤذنيه صلى الله عليه وسلم "','243','245','0');
INSERT INTO [t22592] VALUES (266,'(٢٣٣) - (لقول أبى جحيفة: " رأيت بلالا يؤذن فجعلت أتتبع فاه ها هنا وها هنا يقول يمينا وشمالا حى على الصلاة حى على الفلاح "','244','245','0');
INSERT INTO [t22592] VALUES (267,'(٢٣٤) - (" ولا يزيل قدميه للخبر "','244','245','0');
INSERT INTO [t22592] VALUES (268,'(٢٣٥) - (قول بلال:" أمرنى رسول الله صلى الله عليه وسلم أن أثوب فى الفجر ونهانى','245','245','0');
INSERT INTO [t22592] VALUES (269,'(٢٣٦) - (دخل ابن عمر مسجدا يصلى فيه فسمع رجلا يثوب فى أذان الظهر فخرج , وقال: " أخرجتنى البدعة "','247','245','0');
INSERT INTO [t22592] VALUES (270,'(٢٣٧) - (قوله صلى الله عليه وسلم: " إن أخا صداء قد أذن ومن أذن فهو يقيم "','248','245','0');
INSERT INTO [t22592] VALUES (271,'(٣٣٨) - (قول جابر: " صلى النبى صلى الله عليه وسلم الظهر والعصر بعرفة بأذان وإقامتين "','248','245','0');
INSERT INTO [t22592] VALUES (272,'(٢٣٩) - (حديث ابن مسعود في الخندق: " أن المشركين شغلوا رسول الله صلى الله عليه وسلم عن أربع صلوات حتى ذهب من الليل ما شاء الله ثم أمر بلالا فأذن ثم أقام فصلى الظهر ثم أقام فصلى العصر , ثم أقام فصلى المغرب , ثم أقام فصلى العشاء "','249','245','0');
INSERT INTO [t22592] VALUES (273,'(٢٤٠) - (حديث عمر مرفوعا: " إذا قال المؤذن: الله أكبر الله أكبر , فقال أحدكم: الله أكبر الله أكبر , ثم قال: أشهد أن لا إله إلا الله , فقال: أشهد أن لا إله إلا الله , ثم قال: أشهد أن محمدا رسول الله فقال: أشهد أن محمدا رسول الله , ثم قال: حي على الصلاة','251','245','0');
INSERT INTO [t22592] VALUES (274,'(٢٤١) - (روى أبو داود عن بعض أصحاب النبى صلى الله عليه وسلم: أن بلالا أخذ فى الإقامة فلما أن قال: قد قامت الصلاة قال النبى صلى الله عليه وسلم: (أقامها الله وأدامها) "','251','245','0');
INSERT INTO [t22592] VALUES (275,'(٢٤٢) - (حديث عبد الله بن عمر مرفوعا: " إذا سمعتم المؤذن فقولوا مثل ما يقول , ثم صلوا على فإنه من صلى على صلاة صلى الله عليه بها عشرا , ثم سلوا الله لى الوسيلة , فإنها منزلة فى الجنة لا تنبغى إلا لعبد من عباد الله , وأرجو أن أكون أنا هو , فمن سأل الل','252','245','0');
INSERT INTO [t22592] VALUES (276,'(٢٤٣) - (روى البخارى , وغيره عن جابر مرفوعا: " من قال حين يسمع النداء: اللهم رب هذه الدعوة التامة , والصلاة القائمة , آت محمدا الوسيلة والفضيلة , وابعثه مقاما محمودا الذى وعدته','252','245','0');
INSERT INTO [t22592] VALUES (277,'(٢٤٤) - (حديث أنس مرفوعا:','254','245','0');
INSERT INTO [t22592] VALUES (278,'(٢٤٥) - (قال الترمذي: حديث ابى هريرة: " أما هذا فقد عصى أبا القاسم صلى الله عليه وسلم ".','256','245','0');
INSERT INTO [t22592] VALUES (279,'(٢٤٦) - (حديث عبد الله بن زيد أنه قال: " لما أمر رسول الله صلى الله عليه وسلم بالناقوس ليضرب به للناس لجمع الصلاة طاف بى وأنا نائم رجل يحمل ناقوسا فقلت: يا عبد الله أتبيع الناقوس؟ فقال: وما تصنع به؟ فقلت: ندعو به الى الصلاة , قال: أفلا أدلك على ما','257','245','0');
INSERT INTO [t22592] VALUES (280,'باب شروط الصلاة','259','22','0');
INSERT INTO [t22592] VALUES (281,'(٢٤٧) - (حديث: " مروا أبناءكم بالصلاة لسبع "','259','280','0');
INSERT INTO [t22592] VALUES (282,'(٢٤٨) - (قوله صلى الله عليه وسلم: " لا يقبل الله صلاة بغير طهور ".','260','280','0');
INSERT INTO [t22592] VALUES (283,'(٢٤٩) - (حديث جبريل حين أم النبى صلى الله عليه وسلم بالصلوات الخمس ثم قال: " ما بين هذين وقت "','261','280','0');
INSERT INTO [t22592] VALUES (284,'(٢٥٠) - (حديث جابر:" أن النبى صلى الله عليه وسلم جاءه جبريل عليه السلام فقال: قم فصله , فصلى الظهر حين زالت الشمس , ثم جاءه العصر فقال: قم فصله فصلى العصر حين صار ظل كل شىء مثله , ثم جاءه المغرب فقال: قم فصله فصلى المغرب حين وجبت الشمس , ثم جاءه العشا','263','280','0');
INSERT INTO [t22592] VALUES (285,'(٢٥١) - (حديث أبى موسى: " أن رجلا سأل النبى صلى الله عليه وسلم عن مواقيت الصلاة - قال فى آخره -: ثم أخر المغرب حتى كان عند سقوط الشفق - وفى لفظ فصلى المغرب قبل أن يغيب الشفق - , وأخر العشاء حتى كان ثلث الليل الأول ثم أصبح فدعا السائل فقال: الوقت فيم','264','280','0');
INSERT INTO [t22592] VALUES (286,'(٢٥٢) - (حديث عائشة مرفوعا: " من أدرك من العصر سجدة قبل أن تغرب الشمس أو من الصبح قبل أن تطلع فقد أدركها "','265','280','0');
INSERT INTO [t22592] VALUES (287,'(٢٥٣) - (فى المتفق عليه: " من أدرك ركعة من الصبح قبل أن تطلع الشمس فقد أدرك الصبح "','266','280','0');
INSERT INTO [t22592] VALUES (288,'(٢٥٤) - (حديث: " أنه صلى الله عليه وسلم كان يصلى الظهر بالهاجرة "','268','280','0');
INSERT INTO [t22592] VALUES (289,'(٢٥٥) - (حديث: " بكروا بالصلاة فى يوم الغيم , فإن من فاتته صلاة العصر حبط عمله ".','269','280','0');
INSERT INTO [t22592] VALUES (290,'(٢٥٦) - (حديث رافع بن خديج: " كنا نصلى المغرب مع رسول الله صلى الله عليه وسلم فينصرف أحدنا وإنه ليبصر مواقع نبله "','270','280','0');
INSERT INTO [t22592] VALUES (291,'(٢٥٧) - (حديث: " كان يصلى الصبح بغلس "','271','280','0');
INSERT INTO [t22592] VALUES (292,'(٢٥٨) - (حديث: " أسفروا بالفجر فانه أعظم للأجر "','274','280','0');
INSERT INTO [t22592] VALUES (293,'(٢٥٩) - (حديث ابن عمر مرفوعا: " الوقت الأول من الصلاة رضوان الله والآخر عفو الله "','280','280','0');
INSERT INTO [t22592] VALUES (294,'(٢٦٠) - (وروى الدارقطنى من حديث أبى محذورة نحوه وفيه: " ووسط الوقت رحمة الله "','283','280','0');
INSERT INTO [t22592] VALUES (295,'(٢٦١) - (روى أحمد: أنه صلى الله عليه وسلم عام الأحزاب صلى المغرب فلما فرغ قال: " هل علم أحد منكم أنى صليت العصر؟ " قالوا: يا رسول الله ما صليتها. فأمر المؤذن فأقام الصلاة فصلى العصر ثم أعاد المغرب "','283','280','0');
INSERT INTO [t22592] VALUES (296,'(٢٦٢) - (حديث: " صلوا كما رأيتمونى أصلى "','284','280','0');
INSERT INTO [t22592] VALUES (297,'(٢٦٣) - (حديث: " من نام عن صلاة أو نسيها فليصلها إذا ذكرها "','284','280','0');
INSERT INTO [t22592] VALUES (298,'(٢٦٤) - (حديث: " أنه صلى الله عليه وسلم لما فاتته صلاة الفجر صلى سنتها قبلها ".','286','280','0');
INSERT INTO [t22592] VALUES (299,'(٢٦٥) - (حديث: " عفى لأمتى عن الخطأ والنسيان "','287','280','0');
INSERT INTO [t22592] VALUES (300,'(٢٦٦) - (حديث: " من نام عن صلاة أو نسيها فليصلها إذا ذكرها لا كفارة لها إلا ذلك "','287','280','0');
INSERT INTO [t22592] VALUES (301,'(٢٦٧) - (قوله صلى الله عليه وسلم: " لا يقبل صلاة الله حائض إلا بخمار "','288','280','0');
INSERT INTO [t22592] VALUES (302,'(٢٦٨) - (حديث سلمة بن الأكوع قال: قلت يارسول الله إنى أكون فى الصيد وأصلى فى القميص الواحد قال: " نعم , وأزرره ولو بشوكة "','288','280','0');
INSERT INTO [t22592] VALUES (303,'(٢٦٩) - (حديث على مرفوعا: " لا تبرز فخذك ولا تنظر إلى فخذ','288','280','0');
INSERT INTO [t22592] VALUES (304,'(٢٧٠) - (حديث أبى أيوب يرفعه: " أسفل السرة وفوق الركبتين من العورة ".','295','280','0');
INSERT INTO [t22592] VALUES (305,'(٢٧١) - (عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " ما بين السرة والركبة عورة ".','295','280','0');
INSERT INTO [t22592] VALUES (306,'(٢٧٢) - (" لا يقبل الله صلاة حائض إلا بخمار "','296','280','0');
INSERT INTO [t22592] VALUES (307,'(٢٧٣) - (حديث: " المرأة عورة "','296','280','0');
INSERT INTO [t22592] VALUES (308,'(٢٧٤) - (حديث أم سلمة قالت: " يا رسول الله تصلى المرأة فى درع وخمار وليس عليها إزار؟ قال: نعم إذا كان سابغا يغطى ظهور قدميها "','296','280','0');
INSERT INTO [t22592] VALUES (309,'(٢٧٥) - (حديث أبى هريرة أن النبى صلى الله عليه وسلم قال: " لا يصلى الرجل فى ثوب واحد ليس على عاتقه منه شىء "','297','280','0');
INSERT INTO [t22592] VALUES (310,'(٢٧٦) - (قوله صلى الله عليه وسلم: " من عمل عملا ليس عليه أمرنا فهو رد "','298','280','0');
INSERT INTO [t22592] VALUES (311,'(٢٧٧) - (حديث أبى موسى أن رسول الله صلى الله عليه وسلم قال: " حرم لباس الحرير والذهب على ذكور أمتى وأحل لإناثهم "','298','280','0');
INSERT INTO [t22592] VALUES (312,'(٢٧٨) - (حديث عمر مرفوعا: " لا تلبسوا الحرير فإنه من لبسه فى الدنيا لم يلبسه فى الآخرة "','302','280','0');
INSERT INTO [t22592] VALUES (313,'(٢٧٩) - (قول ابن عباس: " إنما نهى النبى صلى الله عليه وسلم عن الثوب المصمت , أما العلم , وسدا الثوب , فليس به بأس "','303','280','0');
INSERT INTO [t22592] VALUES (314,'(٢٨٠) - (قوله صلى الله عليه وسلم: " تنزهوا من البول فإن عامة عذاب القبر منه "','303','280','0');
INSERT INTO [t22592] VALUES (315,'(٢٨١) - (قوله لأسماء فى دم الحيض: " تحته ثم تقرصه بالماء ثم تنضحه ثم تصلى فيه "','305','280','0');
INSERT INTO [t22592] VALUES (316,'(٢٨٢) - (" أمره صلى الله عليه وسلم بصب ذنوب من ماء على بول الأعرابى الذى بال فى طائفة المسجد ".','305','280','0');
INSERT INTO [t22592] VALUES (317,'(٢٨٣) - (حديث القبرين، وفيه: "أما أحدهما فكان لا يستنزه من بوله")','306','280','0');
INSERT INTO [t22592] VALUES (318,'(٢٨٤) - (حديث أبى سعيد رضى الله عنه: بينما رسول الله صلى الله عليه وسلم يصلى بأصحابه إذ خلع نعليه فوضعهما عن يساره فخلع الناس نعالهم فلما قضى رسول الله صلى الله عليه وسلم صلاته قال: ما حملكم على إلقائكم نعالكم؟ قالوا رأيناك ألقيت نعلك فألقينا نعالنا','307','280','0');
INSERT INTO [t22592] VALUES (319,'(٢٨٥) - (حديث: " جعلت لى الأرض مسجدا وطهورا "','308','280','0');
INSERT INTO [t22592] VALUES (320,'(٢٨٦) - (قوله صلى الله عليه وسلم: " لا تتخذوا القبور مساجد فإنى أنهاكم عن ذلك ".','311','280','0');
INSERT INTO [t22592] VALUES (321,'(٢٨٧) - (روى ابن ماجه والترمذى وعبد بن حميد فى مسنده عن ابن عمر: أن النبى صلى الله عليه وسلم: نهى أن يصلى فى سبعة (١) مواطن: المزبلة , والمجزرة , والمقبرة , وقارعة الطريق , وفى الحمام , وفى مواطن [١] الإبل وفوق ظهر بيت الله "','311','280','0');
INSERT INTO [t22592] VALUES (322,'(٢٨٨) - (حديث: " لأن النبى صلى الله عليه وسلم صلى فى البيت ركعتين "','313','280','0');
INSERT INTO [t22592] VALUES (323,'(٢٨٩) - (حديث: "إذا قمت إلى الصلاة فأسبغ الوضوء ثم استقبل القبلة")','314','280','0');
INSERT INTO [t22592] VALUES (324,'(٢٩٠) - (حديث: " ابن عمر فى أهل قباء لما حولت القبلة "','315','280','0');
INSERT INTO [t22592] VALUES (325,'(٢٩١) - (حديث: عبد الله بن عامر بن ربيعة عن أبيه قال: " كنا مع النبى صلى الله عليه وسلم فى سفر فى ليلة مظلمة فلم ندر أين القبلة , فصلى كل رجل حياله فلما أصبحنا ذكرنا ذلك لرسول الله صلى الله عليه وسلم فنزل (فأينما تولوا فثم وجه الله) "','316','280','0');
INSERT INTO [t22592] VALUES (326,'(٢٩٢) - (قوله صلى الله عليه وسلم: " ما بين المشرق والمغرب قبلة "','317','280','0');
INSERT INTO [t22592] VALUES (327,'(٢٩٣) - (حديث أبى أيوب: " ولكن شرقوا أو غربوا "','319','280','0');
INSERT INTO [t22592] VALUES (328,'(٢٩٤) - (حديث: " أنه صلى الله عليه وسلم قام يتهجد وحده فجاء ابن عباس فأحرم معه فصلى به النبى صلى الله عليه وسلم "','320','280','0');
INSERT INTO [t22592] VALUES (329,'(٢٩٥) - (حديث: " قصة معاذ "','321','280','0');
INSERT INTO [t22592] VALUES (330,'كتاب الصلاة','325','0','0');
INSERT INTO [t22592] VALUES (331,'[الأحاديث من ٢٩٦ - ٣٦٩]','325','330','0');
INSERT INTO [t22592] VALUES (332,'(٢٩٦) - (حديث طلحة بن عبيد الله: أن أعرابيا قال: يا رسول الله ماذا فرض الله على من الصلاة؟ قال: خمس صلوات فى اليوم والليلة , قال: هل على غيرهن؟ قال: لا إلا أن تطوع شيئا "','325','331','0');
INSERT INTO [t22592] VALUES (333,'(٢٩٧) - (حديث: " رفع القلم عن ثلاثة ... إلخ "','326','331','0');
INSERT INTO [t22592] VALUES (334,'(٢٩٨) - (حديث عمرو بن شعيب عن أبيه عن جده أن رسول الله صلى الله عليه وسلم قال: " مروا أبناءكم بالصلاة وهم أبناء سبع سنين وأضربوهم عليها لعشر وفرقوا بينهم فى المضاجع ".','329','331','0');
INSERT INTO [t22592] VALUES (335,'(٢٩٩) - (قال صلى الله عليه وسلم لعمران بن حصين: " صل قائما , فإن لم تستطع فقاعدا , فإن لم تستطع فعلى جنب "','330','331','0');
INSERT INTO [t22592] VALUES (336,'(٣٠٠) - (قوله فى حديث المسىء: " إذا قمت إلى الصلاة فكبر "','330','331','0');
INSERT INTO [t22592] VALUES (337,'(٣٠١) - (حديث: " تحريمها التكبير وتحليلها التسليم "','330','331','0');
INSERT INTO [t22592] VALUES (338,'(٣٠٢) - (قوله صلى الله عليه وسلم: " لا صلاة لمن لم يقرأ بفاتحة الكتاب "','332','331','0');
INSERT INTO [t22592] VALUES (339,'(٣٠٣) - (حديث عبد الله بن أبى أوفى: " جاء رجل إلى النبى صلى الله عليه وسلم فقال: إنى لا أستطيع أن آخذ شيئا من القرآن فعلمنى ما يجزئنى فقال: قل: سبحان الله والحمد لله ولا إله إلا الله والله أكبر ولا حول ولا قوة إلا بالله ".','334','331','0');
INSERT INTO [t22592] VALUES (340,'(٣٠٤) - (حديث: " المسىء ".','335','331','0');
INSERT INTO [t22592] VALUES (341,'(٣٠٥) - (حديث أبى حميد: " أن رسول الله صلى الله عليه وسلم: كان إذا ركع أمكن يديه من ركبتيه ثم هصر ظهره " وفى لفظ: " فلم يصوب رأسه ولم يقنع "','335','331','0');
INSERT INTO [t22592] VALUES (342,'(٣٠٦) - (قوله للمسىء فى صلاته: " ثم ارفع حتى تعتدل قائما "','336','331','0');
INSERT INTO [t22592] VALUES (343,'(٣٠٧) - (قول أنس: " كان النبى صلى الله عليه وسلم إذا قال: سمع الله لمن حمده " قام حتى نقول قد أوهم "','337','331','0');
INSERT INTO [t22592] VALUES (344,'(٣٠٨) - (قوله صلى الله عليه وسلم: " ثم اسجد حتى تطمئن ساجدا "','337','331','0');
INSERT INTO [t22592] VALUES (345,'(٣٠٩) - (حديث أبى حميد: " كان صلى الله عليه وسلم إذا سجد أمكن جبهته وأنفه من الأرض "','337','331','0');
INSERT INTO [t22592] VALUES (346,'(٣١٠) - (قوله صلى الله وسلم: " أمرت أن أسجد على سبعة أعظم الجبهة , وأشار بيده إلى أنفه ـ واليدين والركبتين وأطراف القدمين ".','338','331','0');
INSERT INTO [t22592] VALUES (347,'(٣١١) - (قول أنس: " كنا نصلى مع النبى صلى الله عليه وسلم فيضع أحدنا طرف الثوب من شدة الحر فى مكان السجود "','338','331','0');
INSERT INTO [t22592] VALUES (348,'(٣١٢) - (عن عبد الله بن عبد الرحمن قال: جاءنا النبي صلى الله عليه وسلم فصلى بنا في مسجد بني عبد الأشهل، فرأيته واضعا يديه في ثوبه، إذا سجد)','339','331','0');
INSERT INTO [t22592] VALUES (349,'(٣١٣) - (حديث ابن عمر مرفوعا: " إن اليدين يسجدان كما يسجد الوجه فإذا وضع أحدكم وجهه فليضع يديه وإذا رفعه فليرفعهما ".','339','331','0');
INSERT INTO [t22592] VALUES (350,'(٣١٤) - (قوله صلى الله عليه وسلم:" إذا أمرتم بأمر فأتوا منه ما استطعتم "','341','331','0');
INSERT INTO [t22592] VALUES (351,'(٣١٥) - (قوله صلى الله عليه وسلم للمسىء: " ثم ارفع حتى تطمئن جالسا "','342','331','0');
INSERT INTO [t22592] VALUES (352,'(٣١٦) - (قول عائشة: " كان النبى صلى الله عليه وسلم يفرش رجله اليسرى وينصب اليمنى وينهى عن عقبة الشيطان "','342','331','0');
INSERT INTO [t22592] VALUES (353,'(٣١٧) - (قال ابن عمر: " من سنة الصلاة أن ينصب القدم اليمنى , واستقباله بأصابعها القبلة "','345','331','0');
INSERT INTO [t22592] VALUES (354,'(٣١٨) - (حديث: " أمره صلى الله عليه وسلم الأعرابى بالطمأنينة فى جميع الأركان ولما أخل بها قال: ارجع فصل فإنك لم تصل "','345','331','0');
INSERT INTO [t22592] VALUES (355,'(٣١٩) - (حديث ابن مسعود: كنا نقول قبل أن يفرض علينا التشهد:','345','331','0');
INSERT INTO [t22592] VALUES (356,'(٣٢٠) - (قوله صلى الله عليه وسلم فى حديث كعب بن عجرة لما قالوا: قد عرفنا أو علمنا كيف السلام عليك فكيف الصلاة عليك؟ قال: قولوا اللهم صل على محمد ".','346','331','0');
INSERT INTO [t22592] VALUES (357,'(٣٢١) - (تشهد ابن مسعود: علمنى رسول الله صلى الله عليه وسلم التشهد كفى بين كفيه كما يعلمنى السورة من القرآن:" التحيات لله والصلوات والطيبات , السلام عليك أيها النبى ورحمة الله وبركاته , السلام علينا وعلى عباد الله الصالحين , أشهد أن لا إله إلا','348','331','0');
INSERT INTO [t22592] VALUES (358,'(٣٢٢) - (" حديث أنه عليه الصلاة والسلام أمر ابن مسعود أن يعلم الناس "','349','331','0');
INSERT INTO [t22592] VALUES (359,'(٣٢٣) - (حديث: " أنه صلى الله عليه وسلم جلس للتشهد وداوم عليه "','350','331','0');
INSERT INTO [t22592] VALUES (360,'(٣٢٤) - (حديث: " صلوا كما رأيتمونى أصلى "','350','331','0');
INSERT INTO [t22592] VALUES (361,'(٣٢٥) - (قوله صلى الله عليه وسلم: " وتحليلها التسليم "','350','331','0');
INSERT INTO [t22592] VALUES (362,'(٣٢٦) - (حديث ابن مسعود أن النبى صلى الله عليه وسلم: " كان يسلم عن يمينه السلام عليكم ورحمة الله وعن يساره السلام عليكم ورحمة الله "','351','331','0');
INSERT INTO [t22592] VALUES (363,'(٣٢٧) - (قول ابن عمر: " كان النبى صلى الله عليه وسلم يفصل بين الشفع والوتر بتسليمة ليسمعناها ".','354','331','0');
INSERT INTO [t22592] VALUES (364,'(٣٢٨) - (" صلوا كما رأيتمونى أصلى "','357','331','0');
INSERT INTO [t22592] VALUES (365,'(٣٢٩) - (حديث: " أنه صلى الله عليه وسلم علم الصلاة المسىء فى صلاته مرتبة بـ " ثم "','357','331','0');
INSERT INTO [t22592] VALUES (366,'(٣٣٠) - (قول ابن مسعود: " رأيت النبى صلى الله عليه وسلم يكبر فى كل رفع وخفض وقيام وقعود ".','357','331','0');
INSERT INTO [t22592] VALUES (367,'(٣٣١) - حديث أبى هريرة: " كان رسول الله صلى الله عليه وسلم: يكبر حين يقوم إلى الصلاة ثم يكبر حين يركع ثم يقول: سمع الله لمن حمده حين يرفع صلبه من الركعة ثم يقول وهو قائم: ربنا ولك الحمد ".','358','331','0');
INSERT INTO [t22592] VALUES (368,'(٣٣٢) - حديث أبى موسى وفيه: " وإذا قال: سمع الله لمن حمده فقولوا: اللهم ربنا لك الحمد ".','359','331','0');
INSERT INTO [t22592] VALUES (369,'(٣٣٣) - (قول حذيفة فى حديثه: " فكان ـ يعنى النبى صلى الله عليه وسلم - يقول فى ركوعه: سبحان ربى العظيم , وفى سجوده: سبحان ربى الأعلى "','361','331','0');
INSERT INTO [t22592] VALUES (370,'(٣٣٤) - (وعن عقبة بن عامر قال: لما نزلت (فسبح باسم ربك العظيم) قال رسول الله صلى الله عليه وسلم " اجعلوها فى ركوعكم " فلما نزلت: (سبح اسم ربك الأعلى) قال: اجعلوها فى سجودكم "','362','331','0');
INSERT INTO [t22592] VALUES (371,'(٣٣٥) - (حديث حذيفة " أن النبى صلى الله عليه وسلم كان يقول بين السجدتين: رب اغفر لى رب اغفر لى ".','363','331','0');
INSERT INTO [t22592] VALUES (372,'(٣٣٦) - (حديث ابن مسعود مرفوعا: " إذا قعدتم فى كل ركعتين فقولوا: التحيات لله ... "','365','331','0');
INSERT INTO [t22592] VALUES (373,'(٣٣٧) - (حديث رفاعة بن رافع: " فإذا جلست فى وسط الصلاة فاطمئن وافترش فخذك اليسرى ثم تشهد "','366','331','0');
INSERT INTO [t22592] VALUES (374,'(٣٣٨) - (حديث: " إنه صلى الله عليه وسلم لما نسى الجلوس فى التشهد الأول فى صلاة الظهر سجد سجدتين قبل أن يسلم مكان مانسى من الجلوس ".','367','331','0');
INSERT INTO [t22592] VALUES (375,'(٣٣٩) - (قوله صلى الله عليه وسلم: " إذا نسى أحدكم فليسجد سجدتين "','367','331','0');
INSERT INTO [t22592] VALUES (376,'(٣٤٠) - (حديث الأسود: أنه صلى خلف عمر فسمعه كبر ثم قال: " سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك "','370','331','0');
INSERT INTO [t22592] VALUES (377,'(٣٤١) - (حديث عائشة وأبى سعيد قالا: " كان رسول الله صلى الله عليه وسلم إذا استفتح الصلاة قال ذلك "','372','331','0');
INSERT INTO [t22592] VALUES (378,'(٣٤٢) - (حديث أن النبى صلى الله عليه وسلم كان يقول قبل القراءة: " أعوذ بالله من الشيطان الرجيم "','375','331','0');
INSERT INTO [t22592] VALUES (379,'(٣٤٣) - (حديث أم سلمة: " أن النبى صلى الله عليه وسلم قرأ فى الصلاة بسم الله الرحمن الرحيم , وعدها آية "','381','331','0');
INSERT INTO [t22592] VALUES (380,'(٣٤٤) - (حديث: " إذا أمن الإمام فأمنوا " متفق عليه','384','331','0');
INSERT INTO [t22592] VALUES (381,'(٣٤٥) - (حديث: " أن النبى صلى الله عليه وسلم كان يجهر فى الصبح والجمعة والأوليين من المغرب والعشاء "','384','331','0');
INSERT INTO [t22592] VALUES (382,'(٣٤٦) - (حديث أبى سعيد وابن أبى أوفى: " أن النبى صلى الله عليه وسلم كان إذا رفع رأسه قال: سمع الله لمن حمده ربنا لك الحمد ملء السماء وملء الأرض , وملء ما شئت من شىء بعد ".','386','331','0');
INSERT INTO [t22592] VALUES (383,'(٣٤٧) - (حديث: " وإذا قال سمع الله لمن حمده فقولوا: ربنا ولك الحمد "','387','331','0');
INSERT INTO [t22592] VALUES (384,'(٣٤٨) - (حديث سعيد بن جبير عن أنس قال: " ما صليت وراء أحد بعد رسول الله صلى الله عليه وسلم أشبه صلاة به من هذا الفتى ـ يعنى عمر بن عبد العزيز - قال: فحزرنا فى ركوعه عشر تسبيحات وفى سجوده عشر تسبيحات ".','387','331','0');
INSERT INTO [t22592] VALUES (385,'(٣٤٩) - حديث كعب بن عجرة: " خرج علينا النبى صلى الله عليه وسلم فقلنا: يا رسول الله قد علمنا كيف نسلم عليك , فكيف نصلى عليك؟ قال: قولوا: اللهم صل على محمد , وعلى آل محمد , كما صليت على آل إبراهيم , إنك حميد مجيد وبارك على محمد وعلى آل محمد كما بار','388','331','0');
INSERT INTO [t22592] VALUES (386,'(٣٥٠) - (حديث أبى هريرة مرفوعا: " إذا فرغ أحدكم من التشهد الأخير فليتعوذ بالله من أربع: من عذاب جهنم , ومن عذاب القبر , ومن فتنة المحيا والممات ومن شر فتنة المسيح الدجال ".','388','331','0');
INSERT INTO [t22592] VALUES (387,'(٣٥١) - (حديث " أن مالك بن الحويرث [كان] إذا صلى كبر ورفع يديه وإذا أراد أن يركع رفع يديه , وإذا رفع رأسه رفع يديه وحدث أن رسول الله صلى الله عليه وسلم صنع هكذا "','388','331','0');
INSERT INTO [t22592] VALUES (388,'(٣٥٢) - (حديث وائل بن حجر وفيه: " ثم وضع اليمنى على اليسرى ".','390','331','0');
INSERT INTO [t22592] VALUES (389,'(٣٥٣) - حديث على رضى الله عنه قال: " أن من السنة فى الصلاة وضع الأكف على الأكف تحت السرة ".','391','331','0');
INSERT INTO [t22592] VALUES (390,'(٣٥٤) - (روى ابن سيرين: " أن رسول الله صلى الله عليه وسلم كان يقلب بصره فى السماء فنزلت هذه الآية (والذين هم فى صلاتهم خاشعون) فطأطأ رأسه " رواه أحمد فى الناسخ والمنسوخ وسعيد بن منصور فى سننه " بنحوه وزاد فيه: " وكان يستحبون للرجل أن لا يجاوز بص','393','331','0');
INSERT INTO [t22592] VALUES (391,'(٣٥٥) - (حديث " ابن مسعود فى المراوحة بين القدمين وهو قائم "','395','331','0');
INSERT INTO [t22592] VALUES (392,'(٣٥٦) - (حديث أبى (١) مسعود: " أنه ركع فجافى يديه , ووضع يديه على ركبتيه وفرج بين أصابعه من وراء ركبتيه وقال: هكذا رأيت رسول الله صلى الله عليه وسلم يصلى ".','396','331','0');
INSERT INTO [t22592] VALUES (393,'(٣٥٧) - (حديث وائل بن حجر قال: " رأيت رسول الله صلى الله عليه وسلم إذا سجد وضع ركبتيه قبل يديه وإذا نهض رفع يديه قبل ركبتيه ".','397','331','0');
INSERT INTO [t22592] VALUES (394,'(٣٥٨) - (حديث أبى حميد فى صفة صلاة رسول الله صلى الله عليه وسلم قال فيه: " وإذا سجد فرج بين فخذيه غير حامل بطنه على شىء من فخذيه "','402','331','0');
INSERT INTO [t22592] VALUES (395,'(٣٥٩) - (حديث ابن بحينه: " كان صلى الله عليه وسلم إذا سجد يجنح فى سجوده حتى يرى وضح إبطيه ".','402','331','0');
INSERT INTO [t22592] VALUES (396,'(٣٦٠) - (فى حديث أبى حميد: " ووضع كفيه حذو منكبيه " رواه أبو داود والترمذى وصححه. وفى لفظ: " سجد غير مفترش ولا قابضهما واستقبل بأطراف رجليه القبلة "','403','331','0');
INSERT INTO [t22592] VALUES (397,'(٣٦١) - (" حديث وائل بن حجر فى رفع اليدين أولا فى قيامه إلى الركعة "','403','331','0');
INSERT INTO [t22592] VALUES (398,'(٣٦٢) - (حديث أبى هريرة: " كان ينهض على صدور قدميه "','403','331','0');
INSERT INTO [t22592] VALUES (399,'(٣٦٣) - (حديث وائل بن حجر: " وإذا نهض نهض على ركبتيه واعتمد','405','331','0');
INSERT INTO [t22592] VALUES (400,'٣٦٤) - (حديث أبى حميد: " ثم ثنى رجله اليسرى وقعد عليها ". وقال: " وإذا جلس فى الركعتين جلس على اليسرى ونصب الأخرى ". وفى لفظ: " وأقبل بصدر اليمنى على قبلته "','406','331','0');
INSERT INTO [t22592] VALUES (401,'(٣٦٥) - (حديث أبى حميد: " فإذا كانت السجدة التى فيها التسليم أخرج رجله اليسرى وجلس متوركا على شقه الأيسر وقعد على مقعدته ".','406','331','0');
INSERT INTO [t22592] VALUES (402,'(٣٦٦) - (حديث ابن عمر: " كان رسول الله صلى الله عليه وسلم إذا جلس فى الصلاة وضع يديه على ركبتيه , ورفع إصبعه اليمنى التى تلى الإبهام , فدعا بها ".','407','331','0');
INSERT INTO [t22592] VALUES (403,'(٣٦٧) - (فى حديث وائل بن حجر:','407','331','0');
INSERT INTO [t22592] VALUES (404,'(٣٦٨) - (حديث عامر بن سعد عن أبيه قال: " كنت أرى النبى صلى الله عليه وسلم يسلم عن يمينه وعن يساره حتى أرى (١) بياض خده ".','408','331','0');
INSERT INTO [t22592] VALUES (405,'(٣٦٩) - (حديث جابر:" أمرنا النبى صلى الله عليه وسلم أن نرد على الإمام وأن يسلم بعضنا على بعض "','409','331','0');
INSERT INTO [t22592] VALUES (406,'فصل فيما يكره فى الصلاة','412','330','0');
INSERT INTO [t22592] VALUES (407,'(٣٧٠) - (حديث عائشة: " هو اختلاس يختلسه الشيطان من صلاة العبد ".','412','406','0');
INSERT INTO [t22592] VALUES (408,'(٣٧١) - (حديث سهل بن الحنظلية قال: " ثوب بالصلاة , [يعنى صلاة الصبح] (٢) فجعل رسول الله صلى الله عليه وسلم يصلى وهو يلتفت إلى الشعب. قال [أبو داود] (٣) : وكان أرسل فارسا إلى الشعب يحرس "','412','406','0');
INSERT INTO [t22592] VALUES (409,'(٣٧٢) - (حديث أنس مرفوعا: " اعتدلوا فى السجود ولا يبسط أحدكم ذراعيه انبساط الكلب ".','413','406','0');
INSERT INTO [t22592] VALUES (410,'(٣٧٣) - (حديث أنه رأى رجلا يعبث فى صلاته فقال: " لو خشع قلب هذا لخشعت جوارحه "','414','406','0');
INSERT INTO [t22592] VALUES (411,'(٣٧٤) - (حديث أبى هريرة: " نهى النبى صلى الله عليه وسلم أن يصلى الرجل مختصرا ".','415','406','0');
INSERT INTO [t22592] VALUES (412,'(٣٧٥) - (حديث: " نهيه صلى الله عليه وسلم عن الصلاة إلى النائم والمتحدث "','416','406','0');
INSERT INTO [t22592] VALUES (413,'(٣٧٦) - (حديث عائشة: " أن النبى صلى الله عليه وسلم صلى فى خميصة لها أعلام , فنظر إلى أعلامها نظرة , فلما انصرف قال: اذهبوا بخميصتى هذه إلى أبى جهم , وائتونى بأنبجانيته , فإنها ألهتنى آنفا عن صلاتى ".','419','406','0');
INSERT INTO [t22592] VALUES (414,'(٣٧٧) - (حديث أبى ذر مرفوعا: " إذا قام أحدكم إلى الصلاة فلا يمسح الحصى فإن الرحمة تواجهه ".','419','406','0');
INSERT INTO [t22592] VALUES (415,'(٣٧٨) - (حديث على مرفوعا: " لا تقعقع أصابعك وأنت فى الصلاة "','421','406','0');
INSERT INTO [t22592] VALUES (416,'(٣٧٩) - (عن كعب بن عجرة: " أن رسول الله صلى الله عليه وسلم رأى رجلا قد شبك أصابعه فى الصلاة ففرج رسول الله صلى الله عليه وسلم بين أصابعه ".','421','406','0');
INSERT INTO [t22592] VALUES (417,'(٣٨٠) - (قال ابن عمر فى الذى يصلى وهو مشبك: " تلك صلاة المغضوب عليهم ".','424','406','0');
INSERT INTO [t22592] VALUES (418,'(٣٨١) - (حديث: " ولا أكف ثوبا ولا شعرا "','426','406','0');
INSERT INTO [t22592] VALUES (419,'(٣٨٢) - (قول ابن مسعود: " إن من الجفاء أن يكثر الرجل مسح جبهته قبل أن يفرغ من الصلاة "','426','406','0');
INSERT INTO [t22592] VALUES (420,'(٣٨٣) - (حديث " أنه صلى الله عليه وسلم لما أسن , وأخذه اللحم اتخذ عمودا فى مصلاه يعتمد عليه "','426','406','0');
INSERT INTO [t22592] VALUES (421,'فصل فيما يبطل الصلاة','428','330','0');
INSERT INTO [t22592] VALUES (422,'(٣٨٤) - (" حديث عمرو بن سلمة "','428','421','0');
INSERT INTO [t22592] VALUES (423,'(٣٨٥) - (حديث: " حمله صلى الله عليه وسلم أمامة فى صلاته إذا قام حملها وإذا سجد وضعها ".','428','421','0');
INSERT INTO [t22592] VALUES (424,'(٣٨٦) - (حديث: " فتح الباب لعائشة وهو فى الصلاة "','430','421','0');
INSERT INTO [t22592] VALUES (425,'(٣٨٧) - (حديث: " أنه صلى الله عليه وسلم تقدم وتأخر فى صلاة الكسوف ".','431','421','0');
INSERT INTO [t22592] VALUES (426,'(٣٨٨) - (روى زياد بن علاقة قال: " صلى بنا المغيرة بن شعبة فلما صلى ركعتين قام ولم يجلس , فسبح به من خلفه فأشار إليهم: [أن] قوموا , فلما فرغ من صلاته سلم , وسجد سجدتين وسلم وقال: هكذا صنع رسول الله صلى الله عليه وسلم "','431','421','0');
INSERT INTO [t22592] VALUES (427,'(٣٨٩) - (قوله صلى الله عليه وسلم: " فإن استتم قائما فلا يجلس وليسجد سجدتين ".','433','421','0');
INSERT INTO [t22592] VALUES (428,'(٣٩٠) - (قوله صلى الله عليه وسلم: " إن صلاتنا هذه لا يصلح فيها شىء من كلام','433','421','0');
INSERT INTO [t22592] VALUES (429,'(٣٩١) - (قوله صلى الله عليه وسلم لما عرض له الشيطان فى صلاته: " أعوذ بالله منك , ألعنك بلعنة الله "','435','421','0');
INSERT INTO [t22592] VALUES (430,'(٣٩٢) - (حديث جابر مرفوعا: " القهقهة تنقض الصلاة ولا تنقض الوضوء ".','436','421','0');
INSERT INTO [t22592] VALUES (431,'(٣٩٣) - (حديث: " فأمرنا بالسكوت ونهينا عن الكلام "','440','421','0');
INSERT INTO [t22592] VALUES (432,'(٣٩٤) - (قوله صلى الله عليه وسلم: " إنما جعل الإمام ليؤتم به "','440','421','0');
INSERT INTO [t22592] VALUES (433,'(٣٩٤) - (قوله صلى الله عليه وسلم: " إنما جعل الإمام ليؤتم به "','440','421','0');
INSERT INTO [t22592] VALUES (434,'(٣٩٥) - (قول ابن عباس: " من نفخ فى صلاته فقد تكلم ".','445','421','0');
INSERT INTO [t22592] VALUES (435,'(٣٩٦) - (حديث الكسوف وفيه " ثم نفخ فقال أف أف "','446','421','0');
INSERT INTO [t22592] VALUES (436,'(٣٩٧) - (حديث أنه صلى الله عليه وسلم: " قرأ من المؤمنين إلى ذكر موسى وهارون ثم أخذته سعلة فركع ".','447','421','0');
INSERT INTO [t22592] VALUES (437,'باب سجود السهو','448','330','0');
INSERT INTO [t22592] VALUES (438,'(٣٩٨) - (قوله صلى الله عليه وسلم: " إذا نسى أحدكم فليسجد سجدتين ".','448','437','0');
INSERT INTO [t22592] VALUES (439,'(٣٩٩) - (حديث ابن مسعود: " صلى بنا رسول الله صلى الله عليه وسلم خمسا فلما انفتل من الصلاة توشوش القوم بينهم فقال: ما شأنكم؟ فقالوا: يا رسول الله هل زيد فى الصلاة شىء؟ قال: لا. قالوا: فإنك صليت خمسا فانفتل , فسجد سجدتين , ثم سلم ثم قال: إنما أنا بشر','448','437','0');
INSERT INTO [t22592] VALUES (440,'(٤٠٠) - (حديث عمران بن حصين قال: " سلم رسول الله صلى الله عليه وسلم فى ثلاث ركعات من العصر ثم قام فدخل','448','437','0');
INSERT INTO [t22592] VALUES (441,'(٤٠١) - (حديث ابن بحينة أنه صلى الله عليه وسلم: " قام فى الظهر من ركعتين فلم يجلس فقام الناس معه فلما قضى الصلاة انتظر الناس تسليمه كبر فسجد سجدتين قبل أن يسلم ثم سلم "','449','437','0');
INSERT INTO [t22592] VALUES (442,'(٤٠٢) - (حديث: " إذا شك أحدكم فى صلاته فليتحر الصواب فليتم عليه ثم ليسجد سجدتين ".','449','437','0');
INSERT INTO [t22592] VALUES (443,'(٤٠٣) - (حديث عمران بن حصين: " أن النبى صلى الله عليه وسلم {صلى} بهم فسها فسجد سجدتين , ثم تشهد , ثم سلم "','450','437','0');
INSERT INTO [t22592] VALUES (444,'(٤٠٤) - (حديث ابن عمر مرفوعا: " ليس على من خلف الإمام سهو , فإن سها إمامه فعليه وعلى من خلفه ".','453','437','0');
INSERT INTO [t22592] VALUES (445,'(٤٠٥) - (صح عنه صلى الله عليه وسلم: " أنه لما سجد لترك التشهد الأول والسلام','454','437','0');
INSERT INTO [t22592] VALUES (446,'(٤٠٦) - (حديث: " فإذا سجد فاسجدوا "','455','437','0');
INSERT INTO [t22592] VALUES (447,'(٤٠٧) - (قوله صلى الله عليه وسلم: " فعليه وعلى من خلفه "','455','437','0');
INSERT INTO [t22592] VALUES (448,'(٤٠٨) - (حديث المغيرة أن النبى صلى الله عليه وسلم قال: " إذا قام أحدكم من الركعتين فلم يستتم قائما فليجلس فإن استتم قائما فلا يجلس , وليسجد سجدتين "','455','437','0');
INSERT INTO [t22592] VALUES (449,'(٤٠٩) - (حديث: " إنما جعل الإمام ليؤتم به ".','455','437','0');
INSERT INTO [t22592] VALUES (450,'(٤١٠) - (حديث: " إنه لما قام عليه السلام من التشهد قام الناس معه "','455','437','0');
INSERT INTO [t22592] VALUES (451,'(٤١١) - (حديث أبى سعيد مرفوعا: " إذا شك أحدكم فى صلاته فلم يدر أصلى ثلاثا أو أربعا فليطرح الشك وليبن على ما استيقن , ثم يسجد سجدتين قبل أن يسلم فإن كان صلى خمسا شفعن له صلاته , وإن كان صلى أربعا كانتا ترغيما للشيطان ".','456','437','0');
INSERT INTO [t22592] VALUES (452,'باب صلاة التطوع','457','330','0');
INSERT INTO [t22592] VALUES (453,'(٤١٢) - قوله صلى الله عليه وسلم: " واعلموا أن من خير أعمالكم الصلاة ".','457','452','0');
INSERT INTO [t22592] VALUES (454,'(٤١٣) - (حديث: " وذروة سنامه الجهاد "','460','452','0');
INSERT INTO [t22592] VALUES (455,'(٤١٤) - (قال أبو الدرداء: " العالم والمتعلم فى الأجر سواء , وسائر الناس همج لا خير فيهم "','463','452','0');
INSERT INTO [t22592] VALUES (456,'(٤١٥) - (حديث: " أنه صلى الله عليه وسلم فعل صلاة الكسوف وأمر بها "','465','452','0');
INSERT INTO [t22592] VALUES (457,'(٤١٦) - (حديث: أنه صلى الله عليه وسلم , كان يستسقى تارة ويترك أخرى "','466','452','0');
INSERT INTO [t22592] VALUES (458,'(٤١٧) - (حديث بريدة مرفوعا: " من لم يوتر فليس منا "','468','452','0');
INSERT INTO [t22592] VALUES (459,'(٤١٨) - (حديث ابن عمر وابن عباس مرفوعا: " الوتر ركعة من آخر الليل "','470','452','0');
INSERT INTO [t22592] VALUES (460,'(٤١٩) - (حديث عائشة: " كان النبى صلى الله عليه وسلم يصلى بالليل إحدى عشرة ركعة , يوتر منها بواحدة ".','470','452','0');
INSERT INTO [t22592] VALUES (461,'(٤٢٠) - (حديث ابن عمر أنه: " كان يسلم من ركعتين حتى يأمر ببعض حاجته "','471','452','0');
INSERT INTO [t22592] VALUES (462,'(٤٢١) - (حديث عائشة: " كان النبى صلى الله عليه وسلم يوتر بثلاث لا يفصل فيهن ".','472','452','0');
INSERT INTO [t22592] VALUES (463,'(٤٢٢) - (حديث أبى سعيد مرفوعا: " أوتروا قبل أن تصبحوا "','474','452','0');
INSERT INTO [t22592] VALUES (464,'(٤٢٣) - (حديث: " إن الله قد أمدكم بصلاة هى خير لكم من حمر النعم , وهى الوتر , فصلوها فيما بين العشاء إلى طلوع الفجر ".','478','452','0');
INSERT INTO [t22592] VALUES (465,'(٤٢٤) - (حديث: أنه صح عنه صلى الله عليه وسلم من رواية أبى هريرة وأنس وابن عباس القنوت بعد الركوع','481','452','0');
INSERT INTO [t22592] VALUES (466,'(٤٢٥) - (عن عمر وعلى " أنهما كانا يقنتان بعد الركوع ".','486','452','0');
INSERT INTO [t22592] VALUES (467,'(٤٢٦) - (حديث أبى بن كعب: " أن النبى صلى الله عليه وسلم كان يقنت قبل الركوع ".','489','452','0');
INSERT INTO [t22592] VALUES (468,'(٤٢٧) - (روى الأثرم عن ابن مسعود: " أنه كان يقنت فى الوتر وكان إذا فرغ من القراءة كبر ورفع يديه ثم قنت ".','491','452','0');
INSERT INTO [t22592] VALUES (469,'(٤٢٨) - (حديث: " أن عمر رضى الله عنه قنت بسورتى أبى "','492','452','0');
INSERT INTO [t22592] VALUES (470,'(٤٢٩) - (ومما ورد: " اللهم اهدنا فيمن هديت , وعافنا فيمن عافيت وتولنا فيمن توليت , وبارك لنا فيما أعطيت , وقنا شر ما قضيت , إنك تقضى ولا يقضى عليك , إنه لا يذل من واليت , ولا يعز من عاديت , تباركت ربنا وتعاليت ". رواه أحمد - ولفظه له- والتر','494','452','0');
INSERT INTO [t22592] VALUES (471,'(٤٣٠) - (حديث على أنه صلى الله عليه وسلم كان يقول فى آخر وتره: " اللهم إنا نعوذ برضاك من سخطك وبعفوك من عقوبتك , بك منك , لا نحصى ثناء عليك , أنت كما أثنيت على نفسك ".','497','452','0');
INSERT INTO [t22592] VALUES (472,'(٤٣١) - (حديث الحسن بن على السابق وفى آخره: " وصلى الله على محمد ".','498','452','0');
INSERT INTO [t22592] VALUES (473,'(٤٣٢) - (عن عمر: " الدعاء موقوف بين السماء والأرض , لا يصعد منه شىء حتى تصلى على نبيك "','499','452','0');
INSERT INTO [t22592] VALUES (474,'(٤٣٣) - (حديث عمر: " كان النبى صلى الله عليه وسلم إذا رفع يديه فى الدعاء لا يحطهما حتى يمسح بهما وجهه "','500','452','0');
INSERT INTO [t22592] VALUES (475,'(٤٣٤) - (قوله صلى الله عليه وسلم فى حديث ابن عباس: " فإذا فرغت فامسح بهما وجهك ".','501','452','0');
INSERT INTO [t22592] VALUES (476,'(٤٣٥) - (حديث مالك الأشجعى قال: " قلت لأبى: يا أبت إنك صليت خلف رسول الله صلى الله عليه وسلم وأبى بكر وعمر وعثمان وعلى ها هنا بالكوفة نحو خمس سنين أكانوا يقنتون فى الفجر؟ قال: " أى بنى محدث "','504','452','0');
INSERT INTO [t22592] VALUES (477,'(٤٣٦) - (عن سعيد بن جبير قال: أشهد أنى سمعت ابن عباس يقول: " إن القنوت فى صلاة الفجر بدعة "','505','452','0');
INSERT INTO [t22592] VALUES (478,'(٤٣٧) - (حديث عائشة مرفوعا: " ركعتا الفجر خير من الدنيا وما فيها ".','505','452','0');
INSERT INTO [t22592] VALUES (479,'(٤٣٨) - (وعن أبى هريرة مرفوعا: " لا تدعوا ركعتى الفجر ولو طردتكم الخيل ".','505','452','0');
INSERT INTO [t22592] VALUES (480,'(٤٣٩) - (حديث عبيد مولى النبى صلى الله عليه وسلم: " أنه سئل: أكان رسول الله صلى الله عليه وسلم يأمر بصلاة بعد المكتوبة [أو] سوى المكتوبة؟ فقال: نعم بين المغرب والعشاء "','506','452','0');
INSERT INTO [t22592] VALUES (481,'(٤٤٠) - (قول ابن عمر: " حفظت عن رسول الله صلى الله عليه وسلم ركعتين قبل الظهر , وركعتين بعد الظهر , وركعتين بعد المغرب , وركعتين بعد العشاء , وركعتين قبل الغداة كانت ساعة لا أدخل على النبى صلى الله عليه وسلم فيها فحدثتنى حفصة أنه كان إذا طلع الفج','507','452','0');
INSERT INTO [t22592] VALUES (482,'(٤٤١) - (حديث أنه صلى الله عليه وسلم: " قضى ركعتى الفجر حين نام عنها , وقضى الركعتين اللتين قبل (١) الظهر بعد العصر "','509','452','0');
INSERT INTO [t22592] VALUES (483,'(٤٤٢) - (عن أبى سعيد مرفوعا: " من نام من وتره أو نسيه فليصله إذا ذكره "','511','452','0');
INSERT INTO [t22592] VALUES (484,'(٤٤٣) - (حديث: " عليكم بالصلاة فى بيوتكم فإن خير صلاة المرء فى بيته إلا المكتوبة "','511','452','0');
INSERT INTO [t22592] VALUES (485,'(٤٤٤) [١]- (قول معاوية: " أن النبى صلى الله عليه وسلم أمرنا بذلك , أن لا توصل صلاة بصلاة حتى نتكلم أو نخرج "','512','452','0');
INSERT INTO [t22592] VALUES (486,'(٤٤٥) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم كان يصلى فى شهر رمضان عشرين ركعة "','513','452','0');
INSERT INTO [t22592] VALUES (487,'(٤٤٦) - (عن يزيد بن رومان: " كان الناس فى زمن عمر بن الخطاب يقومون فى رمضان بثلاث وعشرين ركعة "','514','452','0');
INSERT INTO [t22592] VALUES (488,'(٤٤٧) - (عن أبى ذر أن النبى صلى الله عليه وسلم جمع أهله وأصحابه وقال: " إنه من قام مع الإمام حتى ينصرف كتب له قيام ليلة ".','515','452','0');
INSERT INTO [t22592] VALUES (489,'(٤٤٨) - (حديث:" اجعلوا آخر صلاتكم بالليل وترا "','515','452','0');
INSERT INTO [t22592] VALUES (490,'فصل','516','330','0');
INSERT INTO [t22592] VALUES (491,'(٤٤٩) - (حديث أبى هريرة مرفوعا: " أفضل الصلاة بعد الفريضة صلاة الليل ".','516','490','0');
INSERT INTO [t22592] VALUES (492,'(٤٥٠) - (قوله صلى الله عليه وسلم: " ينزل ربنا تبارك وتعالى كل ليلة إلى سماءالدنيا إذا مضى شطر الليل "','517','490','0');
INSERT INTO [t22592] VALUES (493,'(٤٥١) - (حديث:" أفضل الصلاة صلاة داود , كان ينام نصف الليل ويقوم ثلثه وينام سدسه "','521','490','0');
INSERT INTO [t22592] VALUES (494,'(٤٥٢) - (حديث: " عليكم بقيام الليل فإنه دأب الصالحين قبلكم , وهو قربة إلى ربكم , ومكفرة للسيئات ومنهاة عن الإثم "','521','490','0');
INSERT INTO [t22592] VALUES (495,'(٤٥٣) - (حديث أبى هريرة مرفوعا: " إذا قام أحدكم من الليل فليفتح صلاته بركعتين خفيفتين "','524','490','0');
INSERT INTO [t22592] VALUES (496,'(٤٥٤) - (حديث أبى الدرداء عن النبى صلى الله عليه وسلم قال: " من نام ونيته أن يقوم كتب له ما نوى وكان نومه صدقة عليه ".','526','490','0');
INSERT INTO [t22592] VALUES (497,'(٤٥٩) - (حديث أبى هريرة وأبى الدرداء فى صلاة الضحى','533','490','0');
INSERT INTO [t22592] VALUES (498,'(٤٦٠) - (حديث أبى سعيد: " كان النبى صلى الله عليه وسلم يصلى الضحى حتى نقول: لا يدعها , ويدعها حتى نقول: لا يصليها "','534','490','0');
INSERT INTO [t22592] VALUES (499,'(٤٦١) - (حديث: " وركعتى الضحى "','534','490','0');
INSERT INTO [t22592] VALUES (500,'(٤٦٢) - (حديث أنه صلى الله عليه وسلم صلاها أربعا , كما فى حديث عائشة.','535','490','0');
INSERT INTO [t22592] VALUES (501,'(٤٦٣) - (حديث: أنه صلاها صلاها ستا. كما فى حديث جابر بن عبد الله','538','490','0');
INSERT INTO [t22592] VALUES (502,'(٤٦٤) - (حديث أم هانىء: " أن النبى صلى الله عليه وسلم عام الفتح صلى ثمان ركعات سبحة الضحى "','540','490','0');
INSERT INTO [t22592] VALUES (503,'(٤٦٥) - (حديث: " قال الله تعالى: ابن آدم اركع لى أربع ركعات من أول النهار أكفك آخره "','541','490','0');
INSERT INTO [t22592] VALUES (504,'(٤٦٦) - (حديث: " صلاة الأوابين حين ترمض الفصال "','542','490','0');
INSERT INTO [t22592] VALUES (505,'(٤٦٧) - (حديث أبى قتادة أن النبى صلى الله عليه وسلم قال: " إذا دخل أحدكم المسجد فلا يجلس حتى يصلى ركعتين "','542','490','0');
INSERT INTO [t22592] VALUES (506,'(٤٦٨) - (حديث أبى هريرة: أن النبى صلى الله عليه وسلم قال لبلال عند صلاة الفجر: " يا بلال حدثنى بأرجى عمل عملته فى الإسلام , فإنى سمعت دف نعليك بين يدى فى الجنة " قال: ما عملت عملا أرجى عندى أنى لم أتطهر طهورا فى ساعة من ليل ولا نهار إلا صليت بذلك ال','543','490','0');
INSERT INTO [t22592] VALUES (507,'(٤٦٩) - (عن قتادة عن أنس فى قوله تعالى: " كانوا قليلا من الليل ما يهجعون ". قال: " كانوا يصلون فيما بين المغرب والعشاء ". وكذلك: " تتجافى جنوبهم عن المضاجع "','544','490','0');
INSERT INTO [t22592] VALUES (508,'(٤٧٠) - (وعن حذيفة قال: " صليت مع النبى صلى الله عليه وسلم المغرب , فلما قضى صلاته قام فلم يزل يصلى حتى صلى العشاء ثم خرج "','544','490','0');
INSERT INTO [t22592] VALUES (509,'فصل','546','330','0');
INSERT INTO [t22592] VALUES (510,'(٤٧١) - (حديث ابن عمر: " كان النبى صلى الله عليه وسلم يقرأ علينا السورة فيها السجدة فيسجد ونسجد معه حتى ما يجد أحدنا موضعا لجبهته "','546','509','0');
INSERT INTO [t22592] VALUES (511,'(٤٧٢) - (لقول ابن عمر: " كان النبى صلى الله عليه وسلم يقرأ علينا القرآن فإذا مر بالسجدة كبر وسجد , وسجدنا معه "','546','509','0');
INSERT INTO [t22592] VALUES (512,'(٤٧٣) - (لحديث عطاء: أن النبى صلى الله عليه وسلم أتى إلى نفر من أصحابه فقرأ رجل منهم سجدة ثم نظر إلى رسول الله صلى الله عليه وسلم فقال رسول الله صلى الله عليه وسلم: " إنك كنت إمامنا ولو سجدت سجدنا "','547','509','0');
INSERT INTO [t22592] VALUES (513,'(٤٧٤) - (حديث أبى بكرة: " أن النبى صلى الله عليه وسلم كان إذا أتاه أمر يسر به خر ساجدا "','548','509','0');
INSERT INTO [t22592] VALUES (514,'(٤٧٥) - (حديث: " أن أبا بكر سجد حين جاءه قتل مسيلمة "','552','509','0');
INSERT INTO [t22592] VALUES (515,'(٤٧٦) - (حديث أن عليا سجد حين وجد ذا الثدية فى الخوارج "','552','509','0');
INSERT INTO [t22592] VALUES (516,'(٤٧٧) - (حديث: " أن كعب بن مالك سجد لما بشر بتوبة الله عليه "','553','509','0');
INSERT INTO [t22592] VALUES (517,'فصل فى أوقات النهى','554','330','0');
INSERT INTO [t22592] VALUES (518,'(٤٧٨) - (حديث: " إذا طلع الفجر فلا صلاة إلا ركعتى الفجر "','554','517','0');
INSERT INTO [t22592] VALUES (519,'(٤٧٩) - (حديث أبى سعيد مرفوعا: " لا صلاة بعد صلاة الفجر حتى تطلع الشمس ولا صلاة بعد العصر حتى تغرب الشمس "','558','517','0');
INSERT INTO [t22592] VALUES (520,'(٤٨٠) - (حديث عقبة بن عامر: " ثلاث ساعات كان النبى صلى الله عليه وسلم ينهانا أن نصلى فيهن , أو أن نقبر فيهن موتانا: حين تطلع الشمس بازغة حتى ترتفع , وحين يقوم قائم الظهيرة حتى تميل الشمس , وحين تضيف للغروب حتى تغرب "','560','517','0');
INSERT INTO [t22592] VALUES (521,'(٤٨١) - (حديث جبير مرفوعا: " يا بنى عبد مناف لا تمنعوا أحدا طاف بهذا البيت وصلى أية ساعة من ليل أو نهار "','560','517','0');
INSERT INTO [t22592] VALUES (522,'(٤٨٢) - (حديث أم سلمة: " أنه صلى الله عليه وسلم قضاهما - يعنى الركعتين اللتين قبل الظهر - بعد العصر "','561','517','0');
INSERT INTO [t22592] VALUES (523,'(٤٨٣) - (حديث أبى ذر مرفوعا: " صل الصلاة لوقتها فإن أقيمت وأنت فى المسجد فصل , ولا تقل: إنى صليت فلا أصلى ".','561','517','0');
INSERT INTO [t22592] VALUES (524,'(٤٨٤) - (حديث: " من نام عن صلاة أو نسيها فليصلها إذا ذكرها "','562','517','0');
INSERT INTO [t22592] VALUES (525,'(٤٨٥) - (حديث على رضى الله عنه: " كان صلى الله عليه وسلم يقضى حاجته ثم يخرج فيقرأالقرآن ويأكل معنا اللحم ولا يحجبه , وربما قال: لا يحجزه من القرآن شىء ليس الجنابة "','563','517','0');
INSERT INTO [t22592] VALUES (526,'باب صلاة الجماعة','567','330','0');
INSERT INTO [t22592] VALUES (527,'(٤٨٦) - (حديث أبى هريرة مرفوعا: " أثقل الصلاة على المنافقين صلاة العشاء وصلاة الفجر , ولو يعلمون ما فيهما لأتوهما ولو حبوا , ولقد هممت أن آمر بالصلاة فتقام , ثم آمر رجلا يصلى بالناس , ثم أنطلق معى برجال معهم حزم من حطب إلى قوم لا يشهدون الصلاة ف','567','526','0');
INSERT INTO [t22592] VALUES (528,'(٤٨٧) - (حديث: " أنه لما استأذنه أعمى لا قائد له أن يرخص له أن يصلى فى بيته قال: هل تسمع النداء؟ فقال: نعم , قال: فأجب "','568','526','0');
INSERT INTO [t22592] VALUES (529,'(٤٨٨) - (وعن ابن مسعود قال: " لقد رأيتنا وما يتخلف عنها إلا منافق معلوم النفاق "','569','526','0');
INSERT INTO [t22592] VALUES (530,'(٤٨٩) - (حديث أبى موسى مرفوعا: " اثنان (١) فما فوقهما جماعة "','570','526','0');
INSERT INTO [t22592] VALUES (531,'(٤٩٠) - (وقال صلى الله عليه وسلم لمالك بن الحويرث: " وليؤمكما أكبركما ".','572','526','0');
INSERT INTO [t22592] VALUES (532,'(٤٩١) - (قوله صلى الله عليه وسلم: " لا صلاة لجار المسجد إلا فى المسجد "','573','526','0');
INSERT INTO [t22592] VALUES (533,'(٤٩٢) - (وقال ابن مسعود: " من سره أن يلقى الله غدا مسلما فليحافظ على هؤلاء الصلوات حيث ينادى بهن "','577','526','0');
INSERT INTO [t22592] VALUES (534,'(٤٩٣) - (حديث: " أنه صلى الله عليه وسلم أمر أم ورقة أن تؤم أهل دارها "','577','526','0');
INSERT INTO [t22592] VALUES (535,'(٤٩٤) - (حديث: " لا يؤمن الرجل فى بيته إلا بإذنه "','578','526','0');
INSERT INTO [t22592] VALUES (536,'(٤٩٥) - (حديث: " أن أبا بكر صلى حين غاب النبى صلى الله عليه وسلم , وفعله عبد الرحمن بن عوف فقال النبى صلى الله عليه وسلم أحسنتم "','580','526','0');
INSERT INTO [t22592] VALUES (537,'(٤٩٦) - (حديث أبى هريرة مرفوعا: " إذا جئتم إلى الصلاة , ونحن سجود فاسجدوا ولا تعدوها شيئا , ومن أدرك ركعة فقد أدرك الصلاة ". رواه أبو داود وفى لفظ له: " من أدرك الركوع أدرك الركعة "','582','526','0');
INSERT INTO [t22592] VALUES (538,'(٤٩٧) - (حديث: " إذا أقيمت الصلاة فلا صلاة إلا المكتوبة ".','588','526','0');
INSERT INTO [t22592] VALUES (539,'(٤٩٨) - (حديث: " أن عمر كان يضرب على الصلاة بعد الإقامة "','589','526','0');
INSERT INTO [t22592] VALUES (540,'(٤٩٩) - (حديث أبى هريرة: " وإذا قرأ فأنصتوا "','589','526','0');
INSERT INTO [t22592] VALUES (541,'(٥٠٠) - (قال صلى الله عليه وسلم: " من كان له إمام فقراءته له قراءة "','590','526','0');
INSERT INTO [t22592] VALUES (542,'(٥٠١) - (عن جابر مرفوعا:" كل صلاة لم يقرأ فيها بأم القرآن فهى خداج إلا وراءالإمام "','601','526','0');
INSERT INTO [t22592] VALUES (543,'(٥٠٢) - (قوله: " اقرأ بها فى نفسك " من قول أبى هريرة','602','526','0');
INSERT INTO [t22592] VALUES (544,'(٥٠٣) - (قال ابن مسعود: " وددت أن الذى يقرأ خلف الإمام ملىء فوه ترابا ".','603','526','0');
INSERT INTO [t22592] VALUES (545,'(٥٠٤) - (حديث: " أن النبى صلى الله عليه وسلم كان يصلى بأصحابه إلى سترة , ولم يأمرهم أن يستتروا بشىء , لأن سترة الإمام سترة لمن خلفه "','606','526','0');
INSERT INTO [t22592] VALUES (546,'(٥٠٥) - حديث الحسن عن سمرة:" أن النبى صلى الله عليه وسلم كان يسكت سكتتين إذا استفتح وإذا فرغ من القراءة كلها ". وفى رواية: " سكتة إذا كبر وسكتة إذا فرغ من قراءة غيرالمغضوب عليهم ولا الضالين "','606','526','0');
INSERT INTO [t22592] VALUES (547,'(٥٠٦) - قول جابر:" كنا نقرأ فى الظهر والعصر خلف الإمام فى الركعتين الأوليين بفاتحة الكتاب وسورة , وفى الآخرتين بفاتحة الكتاب "','610','526','0');
INSERT INTO [t22592] VALUES (548,'(٥٠٧) - (حديث: " إنما جعل الإمام ليؤتم به فإذا كبر فكبروا وإذا ركع فاركعوا , وإذا قال: سمع الله لمن حمده فقولوا: ربنا ولك الحمد , وإذا سجد فاسجدوا "','610','526','0');
INSERT INTO [t22592] VALUES (549,'(٥٠٨) - (فى حديث أبى موسى:" فإن الإمام يركع قبلكم ويرفع قبلكم "','610','526','0');
INSERT INTO [t22592] VALUES (550,'(٥٠٩) - (قوله صلى الله عليه وسلم: " لا تسبقونى بالركوع ولا بالسجود ولا بالقيام "','611','526','0');
INSERT INTO [t22592] VALUES (551,'- (قوله صلى الله عليه وسلم: " لا تسبقونى بالركوع ولا بالسجود ولا بالقيام "','611','526','0');
INSERT INTO [t22592] VALUES (552,'(٥١٠) - (عن أبى هريرة مرفوعا: " أما يخشى الذى يرفع رأسه قبل الإمام أن يحول الله رأسه رأس حمار "','612','526','0');
INSERT INTO [t22592] VALUES (553,'(٥١١) - (حديث: " عفى لأمتى عن الخطأ والنسيان "','613','526','0');
INSERT INTO [t22592] VALUES (554,'(٥١٢) - (حديث أبى هريرة مرفوعا: " إذا صلى أحدكم للناس فليخفف فإن فيهم السقيم والضعيف وذا الحاجة , وإذا صلى لنفسه فليطول ما شاء "','613','526','0');
INSERT INTO [t22592] VALUES (555,'(٥١٣) - (حديث ابن أبى أوفى: " كان النبى صلى الله عليه وسلم يقوم فى الركعة الأولى من صلاة الظهر حتى لا يسمع وقع قدم "','614','526','0');
INSERT INTO [t22592] VALUES (556,'(٥١٤) - (" وثبت عنه صلى الله عليه وسلم الانتظار فى صلاة الخوف لإدراك الجماعة ".','614','526','0');
INSERT INTO [t22592] VALUES (557,'(٥١٥) - (حديث: " لا تمنعوا إماء الله مساجد الله وبيوتهن خير لهن وليخرجن تفلات "','615','526','0');
INSERT INTO [t22592] VALUES (558,'فصل فى الإمامة','617','330','0');
INSERT INTO [t22592] VALUES (559,'(٥١٦) - (حديث: " يؤم القوم أقرؤهم لكتاب الله , فإن كانوا فى القراءة سواء فأعلمهم بالسنة , فإن كانو فى السنة سواء فأقدمهم هجرة "','617','558','0');
INSERT INTO [t22592] VALUES (560,'(٥١٧) - (قوله: " فإن كانوا فى الهجرة سواء فأقدمهم سنا "','617','558','0');
INSERT INTO [t22592] VALUES (561,'(٥١٨) - (قوله: " فإن كانوا فى الهجرة سواء فأقدمهم سنا "','617','558','0');
INSERT INTO [t22592] VALUES (562,'(٥١٩) - (حديث: " قدموا قريشا ولا تقدموها "','617','558','0');
INSERT INTO [t22592] VALUES (563,'(٥٢٠) - (حديث: " الأئمة من قريش "','620','558','0');
INSERT INTO [t22592] VALUES (564,'(٥٢١) - (حديث: " لا يؤمن الرجل الرجل فى بيته "','623','558','0');
INSERT INTO [t22592] VALUES (565,'(٥٢٢) - (حديث: لأن ابن عمر أتى أرضا له وعندها مسجد يصلى فيه مولى له , فصلى ابن عمر معهم , فسألوه أن يؤمهم فأبى وقال: " صاحب المسجد أحق ".','624','558','0');
INSERT INTO [t22592] VALUES (566,'(٥٢٣) - (قال أبو سعيد مولى أبى أسيد: " تزوجت وأنا مملوك فدعوت ناسا من أصحاب رسول الله صلى الله عليه وسلم فيهم أبو ذر وابن مسعود , وحذيفة فحضرت الصلاة فتقدم أبو ذر فقالوا: وراءك , فالتفت إلى أصحابه فقال: أكذلك؟ قالوا: نعم , فقدمونى "','624','558','0');
INSERT INTO [t22592] VALUES (567,'(٥٢٤) - (روى ابن ماجه عن جابر مرفوعا: " لا تؤمن امرأة رجلا , ولا أعرابى مهاجرا , ولا فاجر مؤمنا , إلا أن يقهره بسلطان يخاف سوطه وسيفه "','625','558','0');
INSERT INTO [t22592] VALUES (568,'(٥٢٥) - (حديث ابن عمر: " كان يصلى خلف الحجاج "','625','558','0');
INSERT INTO [t22592] VALUES (569,'(٥٢٦) - (حديث: " أن الحسن والحسين كانا يصليان وراء مروان "','626','558','0');
INSERT INTO [t22592] VALUES (570,'(٥٢٧) - (قال صلى الله عليه وسلم: " إن الصلاة المكتوبة واجبة خلف كل مسلم برا كان أو فاجرا وإن عمل الكبائر "','626','558','0');
INSERT INTO [t22592] VALUES (571,'(٥٢٨) - (قال البخارى فى " صحيحه ": " باب إمامة المفتون والمبتدع , وقال الحسن: صلى وعليه بدعته "','632','558','0');
INSERT INTO [t22592] VALUES (572,'(٥٢٩) - (روى البخارى عن عبيد الله بن عدى بن خيار: " أنه دخل على عثمان بن عفان وهو محصور فقال: إنك إمام عامة ونزل بك ما ترى , ويصلى لنا إمام فتنة ونتحرج , فقال: الصلاة أحسن ما يعمل الناس , فإذا','632','558','0');
INSERT INTO [t22592] VALUES (573,'(٥٣٠) - (حديث " أن النبي صلى الله عليه وسلم: كان يستخلف ابن أم مكتوم يؤم الناس وهو أعمى "','633','558','0');
INSERT INTO [t22592] VALUES (574,'(٥٣١) - (حديث: " أن النبى صلى الله عليه وسلم صلى بهم جالسا فصلى وراءه قوم قياما فأشار إليهم أن اجلسوا ثم قال: إنما جعل الإمام ليؤتم به , فلا تختلفوا عليه , فإذا صلى جالسا فصلوا جلوسا أجمعين "','635','558','0');
INSERT INTO [t22592] VALUES (575,'(٥٣٢) - (قال ابن مسعود: " لا يؤمن الغلام حتى تجب عليه الحدود " , وقال ابن عباس: " لا يؤمن الغلام حتى يحتلم "','635','558','0');
INSERT INTO [t22592] VALUES (576,'(٥٣٣) - (روى عن عمر: أنه صلى بالناس الصبح ثم خرج إلى الجرف فأهراق الماء , فوجد فى ثوبه احتلاما فأعاد الصلاة ولم يعد الناس ".','636','558','0');
INSERT INTO [t22592] VALUES (577,'(٥٣٤) - (قوله صلى الله عليه وسلم فى حديث محجن بن الأذرع [٢] : " فإذا جئت فصل معهم واجعلها نافلة "','636','558','0');
INSERT INTO [t22592] VALUES (578,'(٥٣٥) - (حديث أبى سعيد: " من يتصدق على ذا فيصلى معه "','638','558','0');
INSERT INTO [t22592] VALUES (579,'(٥٣٦) - (حديث: " إنما جعل الإمام ليؤتم به , فلا تختلفوا عليه "','640','558','0');
INSERT INTO [t22592] VALUES (580,'(٥٣٧) - (حديث معاذ: [فى صلاة المفترض وراء المتنفل]','640','558','0');
INSERT INTO [t22592] VALUES (581,'فصل','640','330','0');
INSERT INTO [t22592] VALUES (582,'(٥٣٨) - عن ابن مسعود أنه صلى بين علقمة والأسود وقال:','640','581','0');
INSERT INTO [t22592] VALUES (583,'(٥٣٨ / ١) - (حديث أنه صلى الله عليه وسلم: (كان إذا قام إلى الصلاة تقدم وقام أصحابه خلفه"','643','581','0');
INSERT INTO [t22592] VALUES (584,'(٥٣٩) - (ولمسلم وأبى داود: " أن جابرا وجبارا وقفا أحدهما عن يمينه وآخر عن يساره فأخذ بأيديهما حتى اقامهما خلفه "','644','581','0');
INSERT INTO [t22592] VALUES (585,'(٥٤٠) - (حديث: " أنه صلى الله عليه وسلم أدار ابن عباس وجابرا إلى يمينه لما وقفا عن يساره "','644','581','0');
INSERT INTO [t22592] VALUES (586,'(٥٤١) - (حديث وابصة بن معبد أن النبى صلى الله عليه وسلم: " رأى رجلا يصلى خلف الصف وحده فأمر أن يعيد "','645','581','0');
INSERT INTO [t22592] VALUES (587,'(٥٤٢) - (قول أنس: " صففت أنا واليتيم وراءه , والمرأة خلفنا فصلى بنا ركعتين "','651','581','0');
INSERT INTO [t22592] VALUES (588,'(٥٤٣) - (حديث: " أن عائشة قالت لنساء كن يصلين فى حجرتها: " لا تصلين بصلاة الإمام فإنكن دونه فى حجاب "','652','581','0');
INSERT INTO [t22592] VALUES (589,'(٥٤٤) - (حديث أن عمار بن ياسر كان بالمدائن، فأقيمت الصلاة، فتقدم عمار، فقام على دكان، والناس أسفل منه، فتقدم حذيفة، فأخذ بيده، فاتبعه عمار حتى أنزله حذيفة فلما فرغ من صلاته قال له حذيفة: ألم تسمع رسول الله صلى الله عليه وسلم يقول: "إذا أم الرجل القوم','653','581','0');
INSERT INTO [t22592] VALUES (590,'(٥٤٥) - (حديث: " أنه صلى الله عليه وسلم: " صلى على المنبر ونزل القهقرى فسجد فى أصل المنبر ثم عاد "','654','581','0');
INSERT INTO [t22592] VALUES (591,'(٥٤٦) - (حديث: " أن أبا هريرة: صلى على سطح المسجد بصلاة الإمام "','655','581','0');
INSERT INTO [t22592] VALUES (592,'(٥٤٧) - (حديث جابر أن النبى صلى الله عليه وسلم قال: " من أكل الثوم والبصل والكراث فلا يقربن مسجدنا فإن الملائكة تتأذى مما يتأذى منه بنو آدم "','656','581','0');
INSERT INTO [t22592] VALUES (593,'فصل','657','330','0');
INSERT INTO [t22592] VALUES (594,'(٥٤٨) - (حديث: أنه صلى الله عليه وسلم لما مرض تخلف عن المسجد , وقال مروا أبا بكر فليصل بالناس "','657','593','0');
INSERT INTO [t22592] VALUES (595,'(٥٤٩) - (حديث أن ابن مسعود قال: ولقد رأيتنا وما يتخلف عنها إلا منافق معلوم النفاق أو مريض','658','593','0');
INSERT INTO [t22592] VALUES (596,'(٥٥٠) - حديث عائشة مرفوعا: " لا صلاة بحضرة طعام ولا وهو يدافع الأخبثين "','658','593','0');
INSERT INTO [t22592] VALUES (597,'(٥٥١) - (حديث ابن عباس مرفوعا: " من سمع النداء فلم يمنعه من اتباعه عذر ـ قالوا فما العذر يا رسول الله؟ قال خوف أو مرض ـ لم يقبل الله منه الصلاة التى صلى "','658','593','0');
INSERT INTO [t22592] VALUES (598,'(٥٥٢) - (حديث: إن ابن عمر استصرخ على سعيد بن زيد وهو يتجمر للجمعة فأتاه بالعقيق وترك الجمعة "','661','593','0');
INSERT INTO [t22592] VALUES (599,'(٥٥٣) - (حديث ابن عمر عن النبى صلى الله عليه وسلم: " أنه كان يأمر المنادى فينادى بالصلاة صلوا فى رحالكم فى الليلة الباردة وفى الليلة المطيرة فى السفر "','661','593','0');
INSERT INTO [t22592] VALUES (600,'(٥٥٤) - (وروى فى الصحيحين عن ابن عباس: " فى يوم مطير ". وفى رواية لمسلم: " وكان يوم جمعة "','664','593','0');
INSERT INTO [t22592] VALUES (601,'(٥٥٥) - (حديث: إن رجلا صلى مع معاذ ثم انفرد فصلى وحده لما طول معاذ فلم ينكر عليه صلى الله عليه وسلم حين أخبره "','666','593','0');
INSERT INTO [t22592] VALUES (602,'باب صلاة أهل الأعذار','666','330','0');
INSERT INTO [t22592] VALUES (603,'(٥٥٦) - (حديث: " إذا أمرتكم بأمر فأتوا منه ما استطعتم "','666','602','0');
INSERT INTO [t22592] VALUES (604,'(٥٥٧) - (قوله صلى الله عليه وسلم لعمران بن حصين: " صل قائما فإن لم تستطع فقاعدا فإن لم تستطع فعلى جنب ".','666','602','0');
INSERT INTO [t22592] VALUES (605,'(٥٥٨) - (حديث على مرفوعا وفيه: " فإن لم يستطع أن يسجد أومأ إيماءا , ويجعل سجوده أخفض من ركوعه وإن لم يستطع أن يصلى قاعدا صلى على جنبه الأيمن مستقبل القبلة فإن لم يستطع صلى مستلقيا ورجلاه مما يلى القبلة "','666','602','0');
INSERT INTO [t22592] VALUES (606,'(٥٥٩) - (حديث: " إذا أمرتكم بأمر فأتوا منه ما استطعتم "','667','602','0');
INSERT INTO [t22592] VALUES (607,'(٥٦٠) - (حديث أبى موسى مرفوعا: " إذا مرض العبد أو سافر كتب له ما كان يعمل مقيما صحيحا "','667','602','0');
INSERT INTO [t22592] VALUES (608,'(٥٦١) - (حديث يعلى ابن أمية: " أن النبى صلى الله عليه وسلم , انتهى إلى مضيق هو وأصحابه وهو على راحلته والسماء من فوقهم , والبلة من أسفل منهم , فحضرت الصلاة فأمر المؤذن فأذن ثم تقدم فصلى بهم يعنى إيماءا يجعل السجود أخفض من الركوع "','669','602','0');
INSERT INTO [t22592] VALUES (609,'(٥٦٢) - (حديث: " إذا أمرتكم بأمر فأتوا منه ما استطعتم "','670','602','0');
INSERT INTO [t22592] VALUES (610,'فصل فى صلاة المسافر','671','330','0');
INSERT INTO [t22592] VALUES (611,'(٥٦٣) - (حديث: " أن النبى صلى الله عليه وسلم وخلفاءه داوموا على القصر "','671','610','0');
INSERT INTO [t22592] VALUES (612,'(٥٦٤) - (وروى أحمد عن ابن عمر مرفوعا: " إن الله يحب أن تؤتى رخصة كما يكره أن تؤتى معصيته "','677','610','0');
INSERT INTO [t22592] VALUES (613,'(٥٦٥) - (حديث ابن عباس مرفوعا: " يا أهل مكة لا تقصروا فى أقل من أربعة برد من مكة إلى عسفان "','681','610','0');
INSERT INTO [t22592] VALUES (614,'(٥٦٦) - (" حديث ابن عباس وابن عمر كانا لا يقصران فى أقل من أربعة برد "','684','610','0');
INSERT INTO [t22592] VALUES (615,'(٥٦٧) - (وقال البخارى فى صحيحه: " باب فى كم يقصر الصلاة , وسمى النبى صلى الله عليه وسلم يوما وليلة سفرا "','684','610','0');
INSERT INTO [t22592] VALUES (616,'(٥٦٨) - ( [قال البخارى] : " وكان ابن عباس وابن عمر يقصران ويفطران فى أربعة برد وهى ستة عشر فرسخا "','685','610','0');
INSERT INTO [t22592] VALUES (617,'(٥٦٩) - (حديث: " أنه صلى الله عليه وسلم إنما كان يقصر إذا ارتحل "','687','610','0');
INSERT INTO [t22592] VALUES (618,'(٥٧٠) - (حديث: " أن النبى صلى الله عليه وسلم صلى الظهر بالمدينة أربعا والعصر بذى الحليفة ركعتين "','688','610','0');
INSERT INTO [t22592] VALUES (619,'(٥٧١) - (حديث: " أن ابن عباس سئل: ما بال المسافر يصلى ركعتين حال الانفراد وأربعا إذا ائتم بمقيم؟ فقال: تلك السنة "','689','610','0');
INSERT INTO [t22592] VALUES (620,'(٥٧٢) - (حديث: " أن النبى صلى الله عليه وسلم أقام بمكة فصلى بها إحدى وعشرين صلاة يقصر فيها وذلك أنه قدم صبح رابعة , فأقام إلى يوم التروية (١) فصلى الصبح ثم خرج "','690','610','0');
INSERT INTO [t22592] VALUES (621,'(٥٧٣) - (حديث: " قال أنس: أقمنا بمكة عشرا نقصر الصلاة "','691','610','0');
INSERT INTO [t22592] VALUES (622,'(٥٧٤) - (حديث: " أن النبى صلى الله عليه وسلم أقام بتبوك عشرين يوما يقصر الصلاة "','691','610','0');
INSERT INTO [t22592] VALUES (623,'(٥٧٥) - (حديث: " أنه صلى الله عليه وسلم لما فتح مكة أقام بها تسعة عشر يوما يصلى ركعتين "','692','610','0');
INSERT INTO [t22592] VALUES (624,'(٥٧٦) - (قال أنس: " أقام أصحاب النبى صلى الله عليه وسلم برام هرمز تسعة أشهر يقصرون الصلاة "','695','610','0');
INSERT INTO [t22592] VALUES (625,'(٥٧٧) - (حديث: " أن ابن عمر أقام بأذربيجان ستة أشهر يقصر','695','610','0');
INSERT INTO [t22592] VALUES (626,'فصل فى الجمع','696','330','0');
INSERT INTO [t22592] VALUES (627,'(٥٧٨) - (حديث معاذ: " أن النبى صلى الله عليه وسلم كان فى غزوة تبوك إذا ارتحل قبل زيغ الشمس أخر الظهر حتى يجمعها إلى العصر يصليها جميعا , وإذا ارتحل بعد زيغ الشمس صلى الظهر والعصر جميعا , ثم سار , وكان يفعل مثل ذلك فى المغرب والعشاء "','696','626','0');
INSERT INTO [t22592] VALUES (628,'(٥٧٩) - (حديث أنس بمعناه , متفق عليه','700','626','0');
INSERT INTO [t22592] VALUES (629,'(٥٧٩/١) - (قال ابن عباس: جمع رسول الله صلى الله عليه وسلم بين الظهر والعصر والمغرب والعشاء بالمدينة من غير خوف ولا مطر. وفي رواية من غير خوف ولا سفر ".','702','626','0');
INSERT INTO [t22592] VALUES (630,'(٥٨٠) - (حديث: " أنه صلى الله عليه وسلم: أمر المستحاضة بالجمع بين','706','626','0');
INSERT INTO [t22592] VALUES (631,'(٥٨١) - (حديث: " أنه صلى الله عليه وسلم جمع بين المغرب والعشاء فى ليلة مطيرة "','707','626','0');
INSERT INTO [t22592] VALUES (632,'(٥٨٢) - (روى الأثرم عن أبى سلمة بن عبد الرحمن , أنه قال: " إن من السنة إذا كان يوم مطير أن يجمع بين المغرب والعشاء "','709','626','0');
INSERT INTO [t22592] VALUES (633,'(٥٨٣) - (ولمالك فى الموطأ عن نافع: " أن ابن عمر كان إذا جمع الأمراء بين المغرب والعشاء فى المطر جمع معهم "','709','626','0');
INSERT INTO [t22592] VALUES (634,'(٥٨٤) - (حديث: " أنه صلى الله عليه وسلم جمع فى مطر , وليس بينه حجرته والمسجد شىء "','709','626','0');
INSERT INTO [t22592] VALUES (635,'(٥٨٥) - (حديث: " إنما الأعمال بالنيات "','709','626','0');
INSERT INTO [t22592] VALUES (636,'(٥٨٥) - (حديث: " إنما الأعمال بالنيات "','709','626','0');
INSERT INTO [t22592] VALUES (637,'(٥٨٦) - (حديث: " أنه صلاها رسول الله صلى الله عليه وسلم "','710','626','0');
INSERT INTO [t22592] VALUES (638,'(٥٨٧) - (حديث: " أنه صلاها أيضا على , وأبو موسى , وحذيفة "','710','626','0');
INSERT INTO [t22592] VALUES (639,'(٥٨٨) - (حديث ابن عمر: " فإن كان الخوف أشد من ذلك صلوا رجالا قياما على أقدامهم وركبانا مستقبلى القبلة وغير مستقبليها "','713','626','0');
INSERT INTO [t22592] VALUES (640,'(٥٨٩) - (قال عبد الله بن أنيس: " بعثنى رسول الله صلى الله عليه وسلم إلى خالد بن سفيان الهذلى , قال: اذهب فاقتله , فرأيته وقد حضرت صلاة العصر , فقلت: إنى أخاف أن يكون بينى وبينه ما يؤخر الصلاة فانطلقت وأنا أصلى وأومىء إيماء نحوه "','715','626','0');
INSERT INTO [t22592] VALUES (641,'(٥٩٠) - (حديث: " أنه صلى الله عليه وسلم: أمرهم بالمشى إلى وجاه العدو , ثم يعودون لما بقى "','717','626','0');
INSERT INTO [t22592] VALUES (642,'باب صلاة الجمعة','718','330','0');
INSERT INTO [t22592] VALUES (643,'(٥٩١) - (روى ابن ماجه عن جابر قال: خطبنا رسول الله صلى الله عليه وسلم فقال: " واعلموا أن الله قد افترض عليكم الجمعة فى يومى هذا فى شهرى هذا فى عامى هذا فمن تركها فى حياتى أو بعدى وله إمام عادل أو جائر استخفافا بها أو جحودا بها فلا جمع الله له شمله','718','642','0');
INSERT INTO [t22592] VALUES (644,'(٥٩٢) - (وعن طارق بن شهاب مرفوعا: " الجماعة [١] حق واجب على كل مسلم إلا أربعة: عبد مملوك , أو امرأة , أو صبى , أو مريض "','722','642','0');
INSERT INTO [t22592] VALUES (645,'(٥٩٣) - (قال صلى الله عليه وسلم: " الجمعة على من سمع النداء "','726','642','0');
INSERT INTO [t22592] VALUES (646,'(٥٩٤) - (حديث: " أنه صلى الله عليه وسلم سافر هو وأصحابه فى الحج وغيره فلم يصل أحد منهم الجمعة فيه مع اجتماع الخلق الكثير "','728','642','0');
INSERT INTO [t22592] VALUES (647,'(٥٩٥) - (قال عبد الله بن سيدان السلمى: " شهدت الجمعة مع أبى بكر , فكانت خطبته وصلاته قبل نصف النهار , وشهدتها مع عمر فكانت خطبته وصلاته إلى أن أقول انتصف النهار ثم شهدتها مع عثمان فكانت خطبته وصلاته إلى أن أقول زال النهار , فما رأيت أحدا عاب ذلك','729','642','0');
INSERT INTO [t22592] VALUES (648,'(٥٩٦) - (قال أحمد: " وكذلك روى عن ابن مسعود وجابر وسعيد ومعاوية أنهم صلوا قبل الزوال فلم ينكر "','730','642','0');
INSERT INTO [t22592] VALUES (649,'(٥٩٧) - (وعن جابر: " كان رسول الله صلى الله عليه وسلم يصلى الجمعة ثم نذهب إلى جمالنا فنريحها حين تزول الشمس "','732','642','0');
INSERT INTO [t22592] VALUES (650,'(٥٩٨) - (قول سلمة بن الأكوع: " كنا نجمع مع النبى صلى الله عليه وسلم إذا زالت الشمس ثم نرجع فنتبع الفىء "','733','642','0');
INSERT INTO [t22592] VALUES (651,'(٥٩٩) - (حديث: " أن النبى صلى الله عليه وسلم لم يأمر قبائل العرب حول المدينة بجمعة "','734','642','0');
INSERT INTO [t22592] VALUES (652,'(٦٠٠) - (قول كعب بن مالك: " أول من جمع بنا أسعد بن زرارة','734','642','0');
INSERT INTO [t22592] VALUES (653,'(٦٠١) - (قال ابن جريج: " قلت لعطاء أكان يأمر [١] النبى صلى الله عليه وسلم؟ قال: نعم "','736','642','0');
INSERT INTO [t22592] VALUES (654,'(٦٠٢) - (قال أحمد: " بعث النبى صلى الله عليه وسلم مصعب بن عمير إلى أهل المدينة فلما كان يوم الجمعة جمع بهم وكانوا أربعين وكانت أول جمعة جمعت بالمدينة ".','737','642','0');
INSERT INTO [t22592] VALUES (655,'(٦٠٣) - (حديث جابر: " مضت السنة أن فى كل أربعين فما فوق جمعة وأضحى وفطر "','737','642','0');
INSERT INTO [t22592] VALUES (656,'(٦٠٤) - (حديث: " أن النبى صلى الله عليه وسلم كان يخطب خطبتين يقعد بينهما ".','738','642','0');
INSERT INTO [t22592] VALUES (657,'(٦٠٥) - (قال عائشة: " إنما أقرت الجمعة ركعتين من أجل الخطبة "','740','642','0');
INSERT INTO [t22592] VALUES (658,'(٦٠٦) - (حديث: " إنما الأعمال بالنيات "','740','642','0');
INSERT INTO [t22592] VALUES (659,'(٦٠٧) - (حديث: " كل كلام لا يبدأ فيه بالحمد لله فهو أجذم ".','741','642','0');
INSERT INTO [t22592] VALUES (660,'(٦٠٨) - (قال جابر: " كان رسول الله صلى الله عليه وسلم يخطب الناس يحمد الله ويثنى عليه بما هو أهله "','741','642','0');
INSERT INTO [t22592] VALUES (661,'(٦٠٩) - (قال جابر بن سمرة: " كان النبى صلى الله عليه وسلم يقرأ آيات ويذكر الناس "','741','642','0');
INSERT INTO [t22592] VALUES (662,'(٦١٠) - (قال: " صلوا كما رأيتمونى أصلى "','742','642','0');
INSERT INTO [t22592] VALUES (663,'(٦١١) - (عن جابر: " كان رسول الله صلى الله عليه وسلم إذا خطب احمرت عيناه وعلا صوته ".','742','642','0');
INSERT INTO [t22592] VALUES (664,'(٦١٢) - (قال عمر وعائشة: " قصرت الصلاة لأجل الخطبة ".','742','642','0');
INSERT INTO [t22592] VALUES (665,'(٦١٣) - (حديث: أنه صلى الله عليه وسلم: " كان إذا خطب يوم الجمعة دعا وأشار بأصبعه وأمن الناس "','742','642','0');
INSERT INTO [t22592] VALUES (666,'(٦١٤) - (قال جابر بن سمرة: " كان النبى صلى الله عليه وسلم يخطب قائما ثم يجلس ثم يقوم فيخطب , فمن حدثك أنه كان يخطب جالسا فقد كذب "','742','642','0');
INSERT INTO [t22592] VALUES (667,'(٦١٥) - (حديث: أنه صلى الله عليه وسلم: " كان يخطب على منبره "','742','642','0');
INSERT INTO [t22592] VALUES (668,'(٦١٦) - (حديث: " أنه صلى الله عليه وسلم خطب على سيف أو عصا "','746','642','0');
INSERT INTO [t22592] VALUES (669,'(٦١٧) - (قال ابن عمر: " كان النبى صلى الله عليه وسلم يخطب خطبتين وهو قائم , يفصل بينهما بجلوس "','747','642','0');
INSERT INTO [t22592] VALUES (670,'(٦١٨) - (حديث عمار مرفوعا: " إن طول صلاة الرجل وقصر خطبته مئنة من فقهه فأطيلوا الصلاة واقصروا الخطبة "','747','642','0');
INSERT INTO [t22592] VALUES (671,'(٦١٩) - (قول صلى الله عليه وسلم: " إذا قلت لصاحبك والإمام يخطب أنصت فقد لغوت "','748','642','0');
INSERT INTO [t22592] VALUES (672,'(٦٢٠) - (حديث: " أن النبى صلى الله عليه وسلم وخلفاءه لم يقيموا إلا جمعة واحدة "','749','642','0');
INSERT INTO [t22592] VALUES (673,'(٦٢١) - (حديث: " ومن أحرم بالجمعة فى وقتها وأدرك مع الإمام ركعة أتم جمعة "','749','642','0');
INSERT INTO [t22592] VALUES (674,'(٦٢٢) - (عن أبى هريرة مرفوعا: " من أدرك ركعة من الجمعة فقد أدرك الصلاة "','752','642','0');
INSERT INTO [t22592] VALUES (675,'(٦٢٣) - (وعنه مرفوعا: " من أدرك ركعة من الصلاة مع الإمام فقد أدرك الصلاة "','758','642','0');
INSERT INTO [t22592] VALUES (676,'(٦٢٤) - (حديث: " أنه صلى الله عليه وسلم: كان يصلى بعد الجمعة ركعتين "','758','642','0');
INSERT INTO [t22592] VALUES (677,'(٦٢٥) - (حديث أبى هريرة مرفوعا: " إذا صلى أحدكم الجمعة فليصل بعدها أربع ركعات "','760','642','0');
INSERT INTO [t22592] VALUES (678,'(٦٢٦) - (حديث أبى سعيد فى قراءة سورة الكهف فى يوم الجمعة "','761','642','0');
INSERT INTO [t22592] VALUES (679,'(٦٢٧) - (حديث: " أنه عليه السلام كان يقرأ فى فجرها (ألم السجدة) , وفى الثانية (هل أتى) "','763','642','0');
INSERT INTO [t22592] VALUES (680,'باب صلاة العيدين','764','330','0');
INSERT INTO [t22592] VALUES (681,'(٦٢٨) - (حديث: " أن النبى صلى الله عليه وسلم داوم على صلاة العيدين "','764','680','0');
INSERT INTO [t22592] VALUES (682,'(٦٢٩) - (قال عبد الله بن السائب: " شهدت العيد مع النبى صلى الله عليه وسلم فلما قضى الصلاة قال: إنا نخطب فمن أحب أن يجلس للخطبة فليجلس ومن أحب أن يذهب فليذهب "','764','680','0');
INSERT INTO [t22592] VALUES (683,'(٦٣٠) - (حديث أبى سعيد: " كان النبى صلى الله عليه وسلم يخرج فى الفطر والأضحى إلى المصلى "','766','680','0');
INSERT INTO [t22592] VALUES (684,'(٦٣١) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم خرج يوم الفطر فصلى ركعتين لم يصل قبلهما ولا بعدهما "','766','680','0');
INSERT INTO [t22592] VALUES (685,'(٦٣٢) - (حديث: " أنه صلى الله عليه وسلم وخلفاءه كانوا يصلونها بعد ارتفاع الشمس "','768','680','0');
INSERT INTO [t22592] VALUES (686,'(٦٣٣) - (روى الشافعي مرسلا: أن النبي صلى الله عليه وسلم كتب إلى عمرو بن','769','680','0');
INSERT INTO [t22592] VALUES (687,'(٦٣٤) - (حديث أبى عمير بن أنس عن عمومة له من الأنصار قالوا: " غم علينا هلال شوال فأصبحنا صياما , فجاء ركب من آخر النهار , فشهدوا عند رسول الله صلى الله عليه وسلم أنهم رأوا الهلال بالأمس , فأمر الناس أن يفطروا من يومهم , وأن يخرجوا لعيدهم من الغد "','770','680','0');
INSERT INTO [t22592] VALUES (688,'(٦٣٥) - (حديث أبى سعيد: " كان رسول الله صلى الله عليه وسلم يخرج يوم الفطر والأضحى إلى المصلى , فأول شىء يبدأ به , الصلاة "','771','680','0');
INSERT INTO [t22592] VALUES (689,'(٦٣٦) - (قال على رضى الله عنه: " إن من السنة أن تأتى العيد ماشيا "','771','680','0');
INSERT INTO [t22592] VALUES (690,'(٦٣٧) - (حديث جابر: " كان النبى صلى الله عليه وسلم إذا خرج إلى المصلى خالف الطريق "','772','680','0');
INSERT INTO [t22592] VALUES (691,'(٦٣٨) - (قال عمر: " صلاة العيد والأضحى ركعتان ركعتان , تمام غير قصر على لسان نبيكم , وقد خاب من افترى "','773','680','0');
INSERT INTO [t22592] VALUES (692,'(٦٣٩) - (حديث عائشة مرفوعا: " التكبير فى الفطر والأضحى: فى','774','680','0');
INSERT INTO [t22592] VALUES (693,'(٦٤٠) - (إن عمر رضى الله عنه كان يرفع يديه مع كل تكبيرة فى الجنازة وفى العيد ". "','780','680','0');
INSERT INTO [t22592] VALUES (694,'(٦٤١) - (وفى حديث وائل بن حجر: أنه صلى الله عليه وسلم " كان يرفع يديه مع التكبير "','781','680','0');
INSERT INTO [t22592] VALUES (695,'(٦٤٢) - (قال عقبة بن عامر: " سألت ابن مسعود عما يقوله بعد تكبيرات العيد قال: يحمد الله , ويثنى عليه ويصلى على النبى صلى الله عليه وسلم "','782','680','0');
INSERT INTO [t22592] VALUES (696,'(٦٤٣) - (قال ابن عمر: " كان النبى صلى الله عليه وسلم , يجهر بالقراءة فى العيدين والاستسقاء "','783','680','0');
INSERT INTO [t22592] VALUES (697,'(٦٤٤) - (قال سمرة: " كان النبى صلى الله عليه وسلم يقرأ فى العيدين: (سبح إسم ربك الأعلى) و (هل أتاك حديث الغاشية) " رواه أحمد. ولابن ماجه عن ابن عباس والنعمان بن بشير مرفوعا مثله.','784','680','0');
INSERT INTO [t22592] VALUES (698,'(٦٤٥) - (قال ابن عمر: " كان النبى صلى الله عليه وسلم وأبو بكر وعمر وعثمان يصلون العيدين قبل الخطبة "','786','680','0');
INSERT INTO [t22592] VALUES (699,'(٦٤٦) - (حديث جابر: " ... ثم قام متوكئا على بلال , فأمر بتقوى الله وحث على طاعته ووعظ الناس وذكرهم إلى آخره "','787','680','0');
INSERT INTO [t22592] VALUES (700,'(٦٤٧) - (قال سعد المؤذن: " كان النبى صلى الله عليه وسلم يكبر بين أضعاف الخطبة , يكثر التكبير فى خطبة العيدين "','787','680','0');
INSERT INTO [t22592] VALUES (701,'(٦٤٨) - (روى عن أنس " أنه إذا لم يشهدها (أى صلاة العيد) جمع أهله ومواليه ثم قام عبد الله بن [أبى] (١) عتبة مولاه فصلى بهم ركعتين يكبر فيهما ".','788','680','0');
INSERT INTO [t22592] VALUES (702,'(٦٤٩) - (عن على رضى الله عنه: " أنه كان يكبر حتى يسمع أهل الطريق "','789','680','0');
INSERT INTO [t22592] VALUES (703,'(٦٥٠) - (وروى الدارقطنى: " أن ابن عمر كان إذا غدا يوم الفطر , ويوم الأضحى يجهر بالتكبير حتى يأتى المصلى , ثم يكبر حتى يأتى الإمام "','790','680','0');
INSERT INTO [t22592] VALUES (704,'(٦٥١) - (قال البخارى: " كان ابن عمر وأبو هريرة يخرجان إلى السوق فى أيام العشر يكبران ويكبر الناس بتكبيرهما "','792','680','0');
INSERT INTO [t22592] VALUES (705,'(٦٥٢) - (قال ابن مسعود: " إنما التكبير على من صلى فى جماعة ".','792','680','0');
INSERT INTO [t22592] VALUES (706,'(٦٥٣) - (حديث جابر: " إن النبى صلى الله عليه وسلم صلى الصبح يوم عرفة ثم أقبل علينا فقال: الله أكبر ومد التكبير إلى آخر أيام التشريق ".','792','680','0');
INSERT INTO [t22592] VALUES (707,'(٦٥٤) - (حديث جابر: " كان النبى صلى الله عليه وسلم إذا صلى الصبح من غداة عرفة أقبل على أصحابه ويقول: على مكانكم ويقول: الله أكبر الله أكبر لا إله إلا الله , والله أكبر الله أكبر , ولله الحمد "','793','680','0');
INSERT INTO [t22592] VALUES (708,'(٦٥٥) - (حديث: " فعله صلى الله عليه وسلم لصلاة الكسوف , وأمره بها "','794','680','0');
INSERT INTO [t22592] VALUES (709,'(٦٥٦) - (قال صلى الله عليه وسلم: " فإذا رأيتم شيئا من ذلك فصلوا حتى ينجلى "','794','680','0');
INSERT INTO [t22592] VALUES (710,'(٦٥٧) - (قول جابر: " كسفت الشمس على عهد رسول الله صلى الله عليه وسلم فى يوم شديد الحر , فصلى بأصحابه فأطال القيام حتى جعلوا يخرون , ثم ركع فأطال , ثم رفع فأطال , ثم ركع فأطال ثم سجد سجدتين ثم قام فصنع نحو ذلك فكانت أربع ركعات , وأربع سجدات ".','796','680','0');
INSERT INTO [t22592] VALUES (711,'(٦٥٨) - (عن عائشة قالت: خسفت الشمس على عهد رسول الله صلى الله عليه وسلم فبعث مناديا فنادى: " الصلاة جامعة وخرج إلى المسجد','796','680','0');
INSERT INTO [t22592] VALUES (712,'(٦٥٩) - (حديث جابر: " أن النبى صلى الله عليه وسلم لما كسفت الشمس صلى ست ركعات بأربع سجدات "','797','680','0');
INSERT INTO [t22592] VALUES (713,'(٦٦٠) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم صلى فى كسوف ثمانى ركعات , فى أربع سجدات "','797','680','0');
INSERT INTO [t22592] VALUES (714,'(٦٦١) - (قول أبى بن كعب: " كسفت الشمس على عهد رسول الله صلى الله عليه وسلم فصلى بهم فقرأ بسورة من الطوال , وركع خمس ركعات وسجدتين , ثم قام إلى الثانية , فقرأ بسورة من الطوال , وركع خمس ركعات وسجدتين "','798','680','0');
INSERT INTO [t22592] VALUES (715,'(٦٦٢) - (روى من غير وجه بأسانيد حسان من حديث سمرة والنعمان بن بشير وعبد الله بن عمرو أنه صلى الله عليه وسلم " صلاها ركعتين , كل ركعة بركوع "','798','680','0');
INSERT INTO [t22592] VALUES (716,'(٦٦٣) - (قول قتادة: " انكسفت الشمس بعد العصر ونحن بمكة , فقاموا يدعون قياما , فسألت عن ذلك عطاء؟ فقال: هكذا كانوا يصنعون "','800','680','0');
INSERT INTO [t22592] VALUES (717,'(٦٦٤) - (قول عبد الله بن زيد: " خرج رسول الله صلى الله عليه وسلم يستسقى , فتوجه إلى القبلة يدعو وحول رداءه , وصلى ركعتين جهر فيهما بالقراءة "','801','680','0');
INSERT INTO [t22592] VALUES (718,'(٦٦٥) - (قال ابن عباس: " صلى النبى صلى الله عليه وسلم ركعتين , كما يصلى فى العيدين "','801','680','0');
INSERT INTO [t22592] VALUES (719,'(٦٦٦) - (عن جعفر بن محمد عن أبيه: " أن النبى صلى الله عليه وسلم وأبا بكر وعمر كانوا يصلون صلاة الاستسقاء يكبرون فيها سبعا وخمسا "','803','680','0');
INSERT INTO [t22592] VALUES (720,'(٦٦٧) - (وعن ابن عباس نحوه وزاد فيه: " وقرأ فى الأولى بسبح , وفى الثانية بالغاشية "','803','680','0');
INSERT INTO [t22592] VALUES (721,'(٦٦٨) - (وقالت عائشة: " خرج رسول الله صلى الله عليه وسلم حين بدا حاجب الشمس "','803','680','0');
INSERT INTO [t22592] VALUES (722,'(٦٦٩) - (قال ابن عباس: " خرج رسول الله صلى الله عليه وسلم للاستسقاء متذللا متواضعا متخشعا متضرعا "','804','680','0');
INSERT INTO [t22592] VALUES (723,'(٦٧٠) - (روى الطبرانى فى معجمه بإسناد عن الزهرى: " أن سليمان عليه السلام , خرج هو وأصحابه يستسقون فرأى نملة قائمة رافعة قوائهما تستسقى , فقال لأصحابه: ارجعوا فقد سقيتم بدعوة غيركم ".','805','680','0');
INSERT INTO [t22592] VALUES (724,'(٦٧١) - (قول ابن عباس: " صنع رسول الله صلى الله عليه وسلم فى الاستسقاء , كما صنع فى العيد','806','680','0');
INSERT INTO [t22592] VALUES (725,'(٦٧٢) - (توسل عمر بالعباس (رضى الله عنهما) , ومعاوية بيزيد','806','680','0');
INSERT INTO [t22592] VALUES (726,'(٦٧٣) - (قال الشعبى: " خرج عمر يستسقى , فلم يزد على الاستغفار. فقالوا: ما رأيناك استسقيت! فقال: لقد طلبت الغيث بمجاديح السماء الذى يستنزل به المطر , ثم قرأ (استغفروا ربكم إنه كان غفارا يرسل السماء عليكم مدرارا ... ) الآية. و (استغفروا ربكم ثم توبوا إل','809','680','0');
INSERT INTO [t22592] VALUES (727,'(٦٧٤) - (قول أنس: " كان النبى صلى الله عليه وسلم لا يرفع يديه فى شىء فى دعائه إلا فى الاستسقاء فإنه كان يرفع حتى يرى بياض إبطيه "','809','680','0');
INSERT INTO [t22592] VALUES (728,'(٦٧٥) - (حديث: " أنه صلى الله عليه وسلم حول إلى الناس ظهره , واستقبل القبلة يدعو , ثم حول رداءه "','810','680','0');
INSERT INTO [t22592] VALUES (729,'(٦٧٦) - (قول عبد الله بن زيد: " رأيت النبى صلى الله عليه وسلم حين استسقى أطال الدعاء وأكثر المسألة. قال: ثم تحول إلى القبلة , وحول رداءه , فقلبه ظهرا لبطن وتحول الناس معه "','810','680','0');
INSERT INTO [t22592] VALUES (730,'(٦٧٧) - (حديث: " إن الله يحب المللحين فى الدعاء "','811','680','0');
INSERT INTO [t22592] VALUES (731,'(٦٧٨) - (حديث أنس: " أصابنا ونحن مع رسول الله صلى الله عليه وسلم مطر فحسر ثوبه حتى أصابه من المطر فقلنا: لم صنعت هذا؟ قال: لأنه حديث عهد بربه "','811','680','0');
INSERT INTO [t22592] VALUES (732,'(٦٧٩) - (وروى أنه عليه السلام كان يقول إذا سال الوادى: " اخرجوا بنا إلى هذا الذى جعله الله طهورا فنتطهر به ".','812','680','0');
INSERT INTO [t22592] VALUES (733,'(٦٨٠) - (حديث الصحيحين عن أنس أن النبى صلى الله عليه وسلم قال: " اللهم حوالينا , ولا علينا , اللهم على الآكام والظراب وبطون الأودية ومنابت الشجر "','812','680','0');
INSERT INTO [t22592] VALUES (734,'(٦٨١) - (فى الصحيحين عن زيد بن خالد الجهنى: " صلى بنا رسول الله صلى الله عليه وسلم صلاة الصبح بالحديبية على أثر سماء كانت من الليل فلما انصرف أقبل على الناس فقال: هل تدرون ماذا قال ربكم؟ قالوا: الله ورسوله أعلم. قال: قال: أصبح من عبادى مؤمن بى وكا','812','680','0');
INSERT INTO [t22592] VALUES (735,'كتاب الجنائز','813','0','0');
INSERT INTO [t22592] VALUES (736,'[الأحاديث من ٦٨٢ - ٧٢٤]','813','735','0');
INSERT INTO [t22592] VALUES (737,'(٦٨٢) - (قوله صلى الله عليه وسلم: " أكثروا من ذكر هادم اللذات "','813','736','0');
INSERT INTO [t22592] VALUES (738,'(٦٨٣) - (حديث: " لا يتمنين أحدكم الموت لضر أصابه "','814','736','0');
INSERT INTO [t22592] VALUES (739,'(٦٨٤) - (حديث: " وإذا أردت بعبادك فتنة فاقبضنى إليك غير مفتون "','815','736','0');
INSERT INTO [t22592] VALUES (740,'(٦٨٥) - (حديث البراء: " أمرنا رسول الله صلى الله عليه وسلم باتباع الجنائز وعيادة المرضى "','816','736','0');
INSERT INTO [t22592] VALUES (741,'(٦٨٦) - (قوله صلى الله عليه وسلم: "لقنوا موتاكم لا إله إلا الله".','817','736','0');
INSERT INTO [t22592] VALUES (742,'(٦٨٧) - (قوله صلى الله عليه وسلم: " من كان آخر كلامه لا إله إلا الله دخل الجنة "','817','736','0');
INSERT INTO [t22592] VALUES (743,'(٦٨٨) - (عن معقل بن يسار: " اقرءوا ياسين على موتاكم "','818','736','0');
INSERT INTO [t22592] VALUES (744,'(٦٨٩) - (قال حذيفة: " وجهوني إلى القبلة "','820','736','0');
INSERT INTO [t22592] VALUES (745,'(٦٩٠) - (قال صلى الله عليه وسلم عن البيت الحرام: " قبلتكم أحياء وأمواتا "','822','736','0');
INSERT INTO [t22592] VALUES (746,'(٦٩١) - (روى البيهقى عن بكر بن عبد الله المزنى ولفظه: " وعلى ملة رسول الله "','824','736','0');
INSERT INTO [t22592] VALUES (747,'(٦٩٢) - (حديث عائشة وابن عباس: " أن أبا بكر قبل النبى صلى الله عليه وسلم بعد موته "','824','736','0');
INSERT INTO [t22592] VALUES (748,'(٦٩٣) - (قالت عائشة: " قبل النبى صلى الله عليه وسلم عثمان بن مظعون وهو ميت , حتى رأيت الدموع تسيل على وجهه "','825','736','0');
INSERT INTO [t22592] VALUES (749,'(٦٩٤) - (قوله صلى الله عليه وسلم فى الذى وقصته ناقته: " اغسلوه بماء وسدر , وكفنوه فى ثوبيه "','826','736','0');
INSERT INTO [t22592] VALUES (750,'(٦٩٥) - (قال ابن عمر: " لا يغسل موتاكم إلا المأمونون "','826','736','0');
INSERT INTO [t22592] VALUES (751,'(٦٩٦) - (حديث: " أن أبا بكر الصديق أوصى أن تغسله امرأته أسماء بنت عميس فقامت (١) بذلك "','826','736','0');
INSERT INTO [t22592] VALUES (752,'(٦٩٧) - (حديث: " أن أنسا أوصى أن يغسله محمد بن سيرين , ففعل "','827','736','0');
INSERT INTO [t22592] VALUES (753,'(٦٩٨) - (حديث على: " لا تبرز فخذك , ولا تنظر إلى فخذ حى ولا ميت ".','827','736','0');
INSERT INTO [t22592] VALUES (754,'(٦٩٩) - (روى حديث: " أن عليا غسل النبى صلى الله عليه وسلم , وبيده خرقة يمسح بها ما تحت القميص "','827','736','0');
INSERT INTO [t22592] VALUES (755,'(٧٠٠) - (قوله صلى الله عليه وسلم لعائشة: " لو مت لغسلتك وكفنتك "','828','736','0');
INSERT INTO [t22592] VALUES (756,'(٧٠١) - (حديث: " غسل على فاطمة رضى الله عنها "','830','736','0');
INSERT INTO [t22592] VALUES (757,'(٧٠٢) - (حديث عائشة: " لو استقبلنا من أمرنا ما استدبرنا ما غسل رسول الله صلى الله عليه وسلم إلا نساؤه "','830','736','0');
INSERT INTO [t22592] VALUES (758,'(٧٠٣) - (حديث: " لما مات إبراهيم بن النبى صلى الله عليه وسلم غسله النساء "','831','736','0');
INSERT INTO [t22592] VALUES (759,'(٧٠٤) - (حديث: " ابدأن بميامنها ومواضع الوضوء منها "','831','736','0');
INSERT INTO [t22592] VALUES (760,'(٧٠٥) - (حديث: " إذا أمرتكم بأمر فأتوا منه ما استطعتم ".','831','736','0');
INSERT INTO [t22592] VALUES (761,'(٧٠٦) - (حديث: " اغلسنها ثلاثا أو خمسا أو أكثر من ذلك إن رأيتن بماء وسدر ".','831','736','0');
INSERT INTO [t22592] VALUES (762,'(٧٠٧) - (حديث: " أمر بدفن شهداء أحد فى دمائهم ولم يغسلوا ولم يصل عليهم "','831','736','0');
INSERT INTO [t22592] VALUES (763,'(٧٠٨) - (حديث سعيد بن زيد مرفوعا: " من قتل دون دينه فهو شهيد , ومن قتل دون دمه فهو شهيد , ومن قتل دون ماله فهو شهيد , ومن قتل دون أهله فهو شهيد "','832','736','0');
INSERT INTO [t22592] VALUES (764,'(٧٠٩) - (حديث: " أمره صلى الله عليه وسلم بدفن شهداء أحد بدمائهم "','833','736','0');
INSERT INTO [t22592] VALUES (765,'(٧١٠) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم أمر بقتلى أحد أن ينزع عنهم الحديد والجلود وأن يدفعوا [فى] (١) ثيابهم بدمائهم "','833','736','0');
INSERT INTO [t22592] VALUES (766,'(٧١١) - (حديث أن صفية: " أرسلت إلى النبى صلى الله عليه وسلم ثوبين ليكفن حمزة فيهما فكفنه بأحدهما وكفن فى الآخر رجلا آخر ".','833','736','0');
INSERT INTO [t22592] VALUES (767,'(٧١٢) - (حديث: " أن النبى صلى الله عليه وسلم غسل سعد بن معاذ وصلى عليه وكان شهيدا "','834','736','0');
INSERT INTO [t22592] VALUES (768,'(٧١٣) - (حديث: " أن النبى صلى الله عليه وسلم قال يوم أحد: ما بال حنظلة بن الراهب! ؟ إنى رأيت الملائكة تغسله. قالوا: إنه سمع الهائعة فخرج وهو جنب ولم يغتسل "','835','736','0');
INSERT INTO [t22592] VALUES (769,'(٧١٤) - (حديث: " ادفنوهم بكلومهم "','836','736','0');
INSERT INTO [t22592] VALUES (770,'(٧١٥) - (خبر أنه: " صلى أبو أيوب على رجل , وصلى عمر على عظام بالشام , وصلى أبو عبيدة على رؤوس بالشام "','837','736','0');
INSERT INTO [t22592] VALUES (771,'(٧١٦) - (حديث المغيرة: " السقط يصلى عليه "','837','736','0');
INSERT INTO [t22592] VALUES (772,'(٧١٧) - (حديث على أنه قال للنبى صلى الله عليه وسلم: " إن عمك الشيخ الضال قد مات. قال: اذهب فواره "','838','736','0');
INSERT INTO [t22592] VALUES (773,'(٧١٨) - (حديث: " كفنوه فى ثوبيه "','839','736','0');
INSERT INTO [t22592] VALUES (774,'(٧١٩) - (حديث أم عطية: فلما فرغنا ألقى إلينا حقوة [١] فقال: " أشعرنها إياه - لم يزد على ذلك - "','839','736','0');
INSERT INTO [t22592] VALUES (775,'(٧٢٠) - (حديث: " ولا تخمروا رأسه ".','840','736','0');
INSERT INTO [t22592] VALUES (776,'(٧٢١) - (حديث: " أوصى أبو بكر الصديق أن يكفن فى ثوبين كان يمرض فيهما "','840','736','0');
INSERT INTO [t22592] VALUES (777,'(٧٢٢) - (حديث عائشة: " كفن رسول الله صلى الله عليه وسلم فى ثلاثة أثواب بيض سحولية جدد يمانية , ليس فيها قميص ولا عمامة , أدرج فيها إدراجا "','840','736','0');
INSERT INTO [t22592] VALUES (778,'(٧٢٣) - (حديث ليلى بنت قائف الثقفية قالت: " كنت فيمن غسل أم كلثوم ابنة النبى صلى الله عليه وسلم عند وفاتها فكان أول ما أعطانا رسول الله صلى الله عليه وسلم الحقا ثم الدرع ثم الخمار ثم الملحفة ثم أدرجت بعد ذلك فى الثوب الآخر "','841','736','0');
INSERT INTO [t22592] VALUES (779,'(٧٢٤) - (حديث: " أنه صلى الله عليه وسلم أمر بنزع الجلود عن الشهداء "','841','736','0');
INSERT INTO [t22592] VALUES (780,'فصل','841','735','0');
INSERT INTO [t22592] VALUES (781,'(٧٢٥) - (حديث: " صلوا على أطفالكم فإنهم [من] (١) أفراطكم','841','780','0');
INSERT INTO [t22592] VALUES (782,'(٧٢٦) - (قوله صلى الله عليه وسلم فى الغال: " صلوا على صاحبكم "','842','780','0');
INSERT INTO [t22592] VALUES (783,'(٧٢٧) - (حديث: " إن صاحبكم النجاشى قد مات فقوموا فصلوا عليه "','843','780','0');
INSERT INTO [t22592] VALUES (784,'(٧٢٨) - (حديث: " صلوا على من قال لا إله إلا الله','845','780','0');
INSERT INTO [t22592] VALUES (785,'(٧٢٩) - (حديث: " أن النبى صلى الله عليه وسلم كبر على النجاشى أربعا "','845','780','0');
INSERT INTO [t22592] VALUES (786,'(٧٣٠) - (حديث: " لا صلاة لمن لم يقرأ بأم القرآن "','846','780','0');
INSERT INTO [t22592] VALUES (787,'(٧٣١) - (حديث: " أن ابن عباس صلى على جنازة فقرأ بأم القرآن وقال: لأنه من السنة أو من تمام السنة "','846','780','0');
INSERT INTO [t22592] VALUES (788,'(٧٣٢) - (حديث: " إذا صليتم على الميت فأخلصوا له الدعاء "','847','780','0');
INSERT INTO [t22592] VALUES (789,'(٧٣٣) - (حديث: " تحليلها التسليم "','848','780','0');
INSERT INTO [t22592] VALUES (790,'(٧٣٤) - (حديث: " إن السنة فى الصلاة على الجنازة أن يكبر الإمام , ثم يقرأ بفاتحة الكتاب بعد التكبيرة الأولى ويقرأ فى نفسه , ثم يصلى على النبى صلى الله عليه وسلم ويخلص الدعاء للجنازة فى التكبيرتين ولا يقرأ فى شىء منهن ثم يسلم سرا فى نفسه رواه الشافع','848','780','0');
INSERT INTO [t22592] VALUES (791,'(٧٣٥) - (حديث زيد بن أرقم: " أن النبى صلى الله عليه وسلم كان يكبر على الجنازة أربعا ثم يقول ما شاء الله ثم ينصرف "','849','780','0');
INSERT INTO [t22592] VALUES (792,'(٧٣٦) - (روى الخلال , وحرب عن على: " أنه صلى على زيد بن المكفف (١) فسلم واحدة عن يمينه: السلام عليكم "','850','780','0');
INSERT INTO [t22592] VALUES (793,'(٧٣٦/١) - (قال أحمد: ومن يشك في الصلاة على القبر؟ "يروى عن النبي صلى الله عليه وسلم من ستة وجوه. كلها حسان".','851','780','0');
INSERT INTO [t22592] VALUES (794,'(٧٣٧) - (حديث: " أن النبى صلى الله عليه وسلم صلى على أم سعد بن عبادة بعد شهر "','854','780','0');
INSERT INTO [t22592] VALUES (795,'(٧٣٨) - (حديث: " صلاته صلى الله عليه وسلم على النجاشى "','854','780','0');
INSERT INTO [t22592] VALUES (796,'فصل','854','735','0');
INSERT INTO [t22592] VALUES (797,'(٧٣٩) - (حديث ابن عمر: " رأيت النبى صلى الله عليه وسلم وأبا بكر يمشون','854','796','0');
INSERT INTO [t22592] VALUES (798,'(٧٤٠) - (حديث المغيرة بن شعبة: " الراكب خلف الجنازة والماشى حيث شاء منها "','860','796','0');
INSERT INTO [t22592] VALUES (799,'(٧٤١) - (حديث على: " قام رسول الله صلى الله عليه وسلم ثم قعد "','860','796','0');
INSERT INTO [t22592] VALUES (800,'(٧٤٢) - (حديث: " لا تتبع الجنازة بصوت ولا نار "','861','796','0');
INSERT INTO [t22592] VALUES (801,'(٧٤٣) - (حديث: " احفروا وأوسعوا وأعمقوا "','862','796','0');
INSERT INTO [t22592] VALUES (802,'(٧٤٤) - (قوله صلى الله عليه وسلم للحافر: " أوسع من قبل الرأس وأوسع من','863','796','0');
INSERT INTO [t22592] VALUES (803,'(٧٤٥) - (عن ابن عباس: أنه كره أن يلقى تحت الميت فى القبر شىء "','864','796','0');
INSERT INTO [t22592] VALUES (804,'(٧٤٦) - (خبر أبى موسى: " لا تجعلوا بينى وبين الأرض شيئا','865','796','0');
INSERT INTO [t22592] VALUES (805,'(٧٤٧) - (حديث: " بسم الله وعلى ملة رسول الله "','865','796','0');
INSERT INTO [t22592] VALUES (806,'(٧٤٨) - (قوله صلى الله عليه وسلم فى الكعبة: " قبلتكم أحياء وأمواتا "','867','796','0');
INSERT INTO [t22592] VALUES (807,'(٧٤٩) - (حديث: " أن النبى صلى الله عليه وسلم كان يدفن كل ميت فى قبر "','867','796','0');
INSERT INTO [t22592] VALUES (808,'(٧٥٠) - (حديث: " أن النبى صلى الله عليه وسلم لما كثر القتلى يوم أحد كان يجمع بين الرجلين فى القبر الواحد ويسأل: أيهم أكثر أخذا للقرآن فيقدمه فى اللحد "','868','796','0');
INSERT INTO [t22592] VALUES (809,'(٧٥١) - (حديث أبى هريرة: " فحثى عليه من قبل رأسه ثلاثا "','868','796','0');
INSERT INTO [t22592] VALUES (810,'(٧٥٢) - (وللدارقطنى معناه من حديث عامر بن ربيعة وزاد: " وهو قائم "','870','796','0');
INSERT INTO [t22592] VALUES (811,'(٧٥٣) - (حديث: " أبى أمامة فى التلقين ". رواه أبو بكر عبد العزيز فى " الشافى "','871','796','0');
INSERT INTO [t22592] VALUES (812,'(٧٥٤) - (حديث: " لقنوا موتاكم لا إله إلا الله "','873','796','0');
INSERT INTO [t22592] VALUES (813,'(٧٥٥) - (حديث: " رش على قبر ابنه ماء ووضع عليه حصباء "','873','796','0');
INSERT INTO [t22592] VALUES (814,'(٧٥٦) - (حديث جابر: " أن النبى صلى الله عليه وسلم رفع قبره عن الأرض قدر شبر ".','875','796','0');
INSERT INTO [t22592] VALUES (815,'(٧٥٧) - (حديث جابر: " نهى النبى صلى الله عليه وسلم أن يجصص القبر وأن يبنى عليه , وأن يقعد عليه "','875','796','0');
INSERT INTO [t22592] VALUES (816,'(٧٥٨) - (روى أحمد: " أن النبى صلى الله عليه وسلم رأى رجلا قد اتكأ على قبر , فقال: لا تؤذه "','876','796','0');
INSERT INTO [t22592] VALUES (817,'(٧٥٩) - (قوله صلى الله عليه وسلم لعلى: " لا تدع تمثالا إلا طمسته ولا قبرا مشرفا إلا سويته "','877','796','0');
INSERT INTO [t22592] VALUES (818,'(٧٦٠) - (حديث بسير بن الخصاصية قال: " بينما أنا أمشى مع رسول الله صلى الله عليه وسلم , إذا رجل يمشى فى القبور , عليه نعلان , فقال: يا صاحب السبتيتين ألق سبتيتيك (١) , فنظر الرجل , فلما عرف رسول الله صلى الله عليه وسلم خلعهما , فرمى بهما ".','879','796','0');
INSERT INTO [t22592] VALUES (819,'(٧٦٠) - (حديث بسير بن الخصاصية قال: " بينما أنا أمشى مع رسول الله صلى الله عليه وسلم , إذا رجل يمشى فى القبور , عليه نعلان , فقال: يا صاحب السبتيتين ألق سبتيتيك (١) , فنظر الرجل , فلما عرف رسول الله صلى الله عليه وسلم خلعهما , فرمى بهما ".','879','796','0');
INSERT INTO [t22592] VALUES (820,'(٧٦١) - (قول ابن عباس: " لعن رسول الله صلى الله عليه وسلم زائرات القبور','879','796','0');
INSERT INTO [t22592] VALUES (821,'(٧٦٢) - (حديث: " أنه صلى الله عليه وسلم كان يدفن أصحابه بالبقيع "','881','796','0');
INSERT INTO [t22592] VALUES (822,'(٧٦٣) - (حديث عائشة مرفوعا: " كسر عظم الميت ككسر عظم الحى ".','881','796','0');
INSERT INTO [t22592] VALUES (823,'(٧٦٤) - (حديث عمرو بن حزم مرفوعا: " ما من مؤمن يعزى أخاه بمصيبة إلا كساه الله عز وجل من حلل الجنة ".','884','796','0');
INSERT INTO [t22592] VALUES (824,'(٧٦٥) - (عن ابن مسعود مرفوعا: " من عزى مصابا فله مثل أجره ". "','885','796','0');
INSERT INTO [t22592] VALUES (825,'(٧٦٦) - (روى حرب عن زرارة بن أبى أوفى قال: " عزى النبى صلى الله عليه وسلم رجلا على ولده فقال: آجرك الله , وأعظم لك الأجر ".','888','796','0');
INSERT INTO [t22592] VALUES (826,'(٧٦٧) - (قوله صلى الله عليه وسلم: " إن الله لا يعذب بدمع العين ولا بحزن القلب ولكن يعذب بهذا ـ وأشار إلى لسانه ـ أو يرحم "','889','796','0');
INSERT INTO [t22592] VALUES (827,'(٧٦٨) - (قالت أم عطية: " أخذ علينا النبى صلى الله عليه وسلم فى البيعة أن لا ننوح "','889','796','0');
INSERT INTO [t22592] VALUES (828,'(٧٦٩) - (وفى صحيح مسلم: " أن النبى صلى الله عليه وسلم لعن النائحة والمستمعة ".','890','796','0');
INSERT INTO [t22592] VALUES (829,'(٧٧٠) - (حديث ابن مسعود مرفوعا: " ليس منا من ضرب الخدود , وشق الجيوب , ودعا بدعوى الجاهلية "','891','796','0');
INSERT INTO [t22592] VALUES (830,'(٧٧١) - (عن أبى موسى: " أن النبى صلى الله عليه وسلم برىء من الصالقة والحالقة , والشاقة "','891','796','0');
INSERT INTO [t22592] VALUES (831,'(٧٧٢) - (قوله صلى الله عليه وسلم: " كنت نهيتكم عن زيارة القبور فزوروها , فإنها تذكركم الموت "','891','796','0');
INSERT INTO [t22592] VALUES (832,'(٧٧٢) - (قوله صلى الله عليه وسلم: " كنت نهيتكم عن زيارة القبور فزوروها , فإنها تذكركم الموت "','891','796','0');
INSERT INTO [t22592] VALUES (833,'(٧٧٣) - (حديث: " لا تشد الرحال إلا إلى ثلاثة مساجد ... "','894','796','0');
INSERT INTO [t22592] VALUES (834,'(٧٧٤) - (حديث ابن عباس مرفوعا: " ولعن الله زوارات القبو','900','796','0');
INSERT INTO [t22592] VALUES (835,'(٧٧٥) - (حديث: " أن عائشة زارت قبر أخيها عبد الرحمن رضى الله عنهما ".','901','796','0');
INSERT INTO [t22592] VALUES (836,'(٧٧٦) - (الأخبار الواردة بما يقول زائر القبور , عن أبى هريرة , وبريدة , وغيرهما.','903','796','0');
INSERT INTO [t22592] VALUES (837,'(٧٧٧) - (حديث: " أفشوا السلام "','905','796','0');
INSERT INTO [t22592] VALUES (838,'(٧٧٨) - (حديث على مرفوعا: " يجزىء عن الجماعة إذا مروا أن يسلم أحدهم , ويجزىء عن الجلوس أن يرد أحدهم "','910','796','0');
INSERT INTO [t22592] VALUES (839,'(٧٧٩) - (حديث أبى هريرة مرفوعا:" إذا عطس أحدكم فحمد الله فحق على كل مسلم سمعه أن يقول له: يرحمك الله "','912','796','0');
INSERT INTO [t22592] VALUES (840,'(٧٨٠) - (وعنه أيضا: " إذا عطس أحدكم فليقل: الحمد لله على كل حال , وليقل أخوه أو صاحبه: يرحمك الله , ويقول هو: يهديكم الله ويصلح بالكم "','912','796','0');
INSERT INTO [t22592] VALUES (841,'كتاب الزكاة','916','0','0');
INSERT INTO [t22592] VALUES (842,'[الأحاديث من ٧٨١ - ٧٩٠]','916','841','0');
INSERT INTO [t22592] VALUES (843,'(٧٨١) - (حديث: " بنى الإسلام على خمس: شهادة أن لا إله إلا الله وأن محمدا رسول الله وإقام الصلاة , وإيتاء الزكاة وصوم رمضان , وحج البيت "','916','842','0');
INSERT INTO [t22592] VALUES (844,'(٧٨٢) - (حديث معاذ: " إنك تأتى قوما من أهل الكتاب. فليكن أول ما تدعوهم إليه: شهادة أن لا إله إلا الله , فإن هم أطاعوك لذلك , فأعلمهم أن الله قد افترض عليهم صدقة تؤخذ من أغنيائهم , فترد على فقرائهم "','919','842','0');
INSERT INTO [t22592] VALUES (845,'(٧٨٣) - (حديث جابر مرفوعا: " ليس فى مال المكاتب زكاة حتى يعتق ".','919','842','0');
INSERT INTO [t22592] VALUES (846,'(٧٨٤) - (عن عائشة: " ليس فى الدين زكاة "','920','842','0');
INSERT INTO [t22592] VALUES (847,'(٧٨٥) - (قول على فى الدين الظنون (١) : " إن كان صادقا فليتركه [١] إذا قبضه , لما مضى "','921','842','0');
INSERT INTO [t22592] VALUES (848,'(٧٨٦) - (وعن ابن عباس نحوه','921','842','0');
INSERT INTO [t22592] VALUES (849,'(٧٨٧) - (حديث ابن عمر أن النبى صلى الله عليه وسلم قال: " لا زكاة فى مال حتى يحول عليه الحول "','922','842','0');
INSERT INTO [t22592] VALUES (850,'(٧٨٨) - (قوله صلى الله عليه وسلم: " ابتغوا فى أموال اليتامى كيلا تأكله الزكاة ".','926','842','0');
INSERT INTO [t22592] VALUES (851,'(٧٨٩) - (قال عثمان بمحضر من الصحابة: " هذا شهر زكاتكم فمن كان عليه دين فليؤده حتى تخرجوا زكاة أموالكم "','928','842','0');
INSERT INTO [t22592] VALUES (852,'(٧٩٠) - (حديث: " ... فدين الله أحق بالوفاء ... "','929','842','0');
INSERT INTO [t22592] VALUES (853,'باب زكاة السائمة','931','841','0');
INSERT INTO [t22592] VALUES (854,'(٧٩١) - (حديث بهز بن حكيم عن أبيه عن جده مرفوعا: " فى كل إبل سائمة فى كل أربعين ابنة لبون "','931','853','0');
INSERT INTO [t22592] VALUES (855,'(٧٩٢) - (حديث الصديق مرفوعا: " وفى الغنم فى سائمتها إذا كانت أربعين ففيها شاة "','932','853','0');
INSERT INTO [t22592] VALUES (856,'(٧٩٣) - (وفى آخر: " إذا كانت سائمة الرجل ناقصة من أربعين شاة شاة واحدة فليس فيها شىء إلا أن يشاء ربها "','935','853','0');
INSERT INTO [t22592] VALUES (857,'(٧٩٤) - (حديث أنس أن أبا بكر الصديق كتب له حين وجهه إلى البحرين: بسم الله الرحمن الرحيم " هذه فريضة الصدقة التى فرضها رسول الله صلى الله عليه وسلم على المسلمين التى أمر الله بها رسوله , فمن سئلها من المسلمين على وجهها فليعطها ومن سئل فوقها فلا يعطها','935','853','0');
INSERT INTO [t22592] VALUES (858,'(٧٩٥) - (قول معاذ: " بعثنى رسول الله صلى الله عليه وسلم أصدق أهل اليمن فأمرنى أن آخذ من البقر من كل ثلاثين تبيعا ومن كل أربعين مسنة "','936','853','0');
INSERT INTO [t22592] VALUES (859,'(٧٩٦) - (قول سعد [١] بن ديسم: أتانى رجلان على بعير فقالا: إنا','939','853','0');
INSERT INTO [t22592] VALUES (860,'(٧٩٧) - (حديث أنس فى كتاب الصدقات: " وفى سائمة الغنم إذا كانت أربعين إلى عشرين ومائة شاة , فإذا زادت على عشرين ومائة ففيها شاتان , فإذا زادت على مائتين إلى ثلاث ففيها ثلاث شياه , فإذا زادت على ثلاثمائة ففى كل مائة شاة , فليس فيها صدقة , إلا أن يشا','940','853','0');
INSERT INTO [t22592] VALUES (861,'فصل فى الخلطة','941','841','0');
INSERT INTO [t22592] VALUES (862,'(٧٩٨) - (روى أنس فى كتاب الصدقات: " ولا يجمع بين متفرق ولا يفرق بين مجتمع خشية الصدقة , وما كان من خليطين فإنهما يتراجعان بالسوية "','941','861','0');
INSERT INTO [t22592] VALUES (863,'باب زكاة الخارج من الأرض','941','841','0');
INSERT INTO [t22592] VALUES (864,'(٧٩٩) - (حديث: " فيما سقت السماء والعيون أو كان عثريا (١) العشر , وفيما سقى بالنضح نصف العشر "','941','863','0');
INSERT INTO [t22592] VALUES (865,'(٨٠٠) - (حديث: " ليس فيما دون خمسة أوسق صدقة "','943','863','0');
INSERT INTO [t22592] VALUES (866,'(٨٠٠/١) - (حديث: "لا زكاة في حب ولا ثمر حتى يبلغ خمسة أوسق".','944','863','0');
INSERT INTO [t22592] VALUES (867,'(٨٠١) - (روى موسى بن طلحة: " أن معاذا لم يأخذ من الخضروات صدقة "','944','863','0');
INSERT INTO [t22592] VALUES (868,'(٨٠٢) - (وروى الأثرم بإسناده عن سفيان بن عبد الله الثقفى: " أنه كتب إلى عمر ـ وكان عاملا له على الطائف ـ أن قبله حيطانا فيها من الفرسك (١) والرمان ما هو أكثر غلة من الكروم أضعافا , فكتب يستأمر فى العشر , فكتب إليه عمر: أن ليس عليها عشر , هى من العضاه','947','863','0');
INSERT INTO [t22592] VALUES (869,'(٨٠٣) - (خبر: " الوسق ستون صاعا "','948','863','0');
INSERT INTO [t22592] VALUES (870,'(٨٠٤) - (حديث أبى سعيد مرفوعا: " ليس فيما دون خمسة أوسق صدقة "','948','863','0');
INSERT INTO [t22592] VALUES (871,'(٨٠٥) - (حديث عائشة: " أن النبى صلى الله عليه وسلم كان يبعث عبد الله بن رواحة إلى يهود فيخرص عليهم النخل حين يطيب قبل أن يؤكل منه "','948','863','0');
INSERT INTO [t22592] VALUES (872,'فصل','950','841','0');
INSERT INTO [t22592] VALUES (873,'(٨٠٦) - (حديث ابن عمر: " فيما سقت السماء العشر وفيما سقى بالنضح نصف العشر ". رواه أحمد والبخارى والنسائى وأبى داود وابن ماجه: " فيما سقت السماء والأنهار والعيون , أو كان بعلا: العشر وفيما سقى بالسواقى والنضح: نصف العشر ".','950','872','0');
INSERT INTO [t22592] VALUES (874,'(٨٠٧) - (روى الدارقطنى عن عتاب بن أسيد: " أن النبى صلى الله عليه وسلم أمره أن يخرص العنب زبيبا كما يخرص التمر ".','950','872','0');
INSERT INTO [t22592] VALUES (875,'(٨٠٨) - (حديث ابن عمر: " القبالات ربا "','951','872','0');
INSERT INTO [t22592] VALUES (876,'(٨٠٩) - (عن ابن عباس: " إياكم والربا: ألا وهى القبالات , ألا وهى الذل والصغار "','951','872','0');
INSERT INTO [t22592] VALUES (877,'(٨١٠) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن رسول الله صلى الله عليه وسلم كان يؤخذ فى زمانه من قرب العسل من كل عشر قرب قربة من أوسطها ".','952','872','0');
INSERT INTO [t22592] VALUES (878,'(٨١١) - (روى الجوزجانى عن عمر: " أن أناسا سألوه فقالوا: إن رسول الله صلى الله عليه وسلم أقطع لنا واديا باليمن فيه خلايا من نحل , وإنا نجد ناسا يسرقونها. فقال عمر: إذا أديتم صدقتها من كل عشرة أفراق فرقا حميناها لكم "','955','872','0');
INSERT INTO [t22592] VALUES (879,'(٨١٢) - (حديث أبى هريرة مرفوعا: " وفى الركاز الخمس "','955','872','0');
INSERT INTO [t22592] VALUES (880,'(٨١٢/١) - (روى أبو عبيد بإسناده عن الشعبي "أن رجلا وجد ألف دينار مدفونة خارج المدينة فأتى بها عمر بن الخطاب فأخذ منها مائتي دينار، ودفع إلى الرجل بقيتها، وجعل عمر يقسم المائتين بين من حضره من المسلمين إلى أن فضل منها فضلة، فقال: أين صاحب الدنانير؟','956','872','0');
INSERT INTO [t22592] VALUES (881,'باب زكاة الأثمان','957','841','0');
INSERT INTO [t22592] VALUES (882,'(٨١٣) - (حديث عائشة وابن عمر: " كان يأخذ من كل عشرين مثقالا نصف مثقال "','957','881','0');
INSERT INTO [t22592] VALUES (883,'(٨١٤) - (حديث أنس مرفوعا: " وفى الرقة ربع العشر "','960','881','0');
INSERT INTO [t22592] VALUES (884,'(٨١٥) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " ليس فى أقل من عشرين مثقالا من الذهب ولا فى أقل من مئتى درهم صدقة "','960','881','0');
INSERT INTO [t22592] VALUES (885,'(٨١٦) - (حديث: " ليس فيما دون خمس أواق من الورق صدقة "','960','881','0');
INSERT INTO [t22592] VALUES (886,'(٨١٧) - (حديث جابر: " ليس فى الحلى زكاة "','962','881','0');
INSERT INTO [t22592] VALUES (887,'فصل','965','841','0');
INSERT INTO [t22592] VALUES (888,'(٨١٨) - (حديث: أنه صلى الله عليه وسلم " اتخذ خاتما من ورق "','965','887','0');
INSERT INTO [t22592] VALUES (889,'(٨١٩) - (قال الدارقطنى وغيره: المحفوظ " أن النبى صلى الله عليه وسلم كان يختتم فى يساره "','966','887','0');
INSERT INTO [t22592] VALUES (890,'(٨٢٠) - (حديث: " التختم باليمنى "','967','887','0');
INSERT INTO [t22592] VALUES (891,'(٨٢١) - (وفى البخارى من حديث أنس: " كان فصه منه "','972','887','0');
INSERT INTO [t22592] VALUES (892,'(٨٢٢) - (قال أنس: " كانت قبيعة سيف رسول الله صلى الله عليه وسلم فضة ".','973','887','0');
INSERT INTO [t22592] VALUES (893,'(٨٢٣) - (حديث: " إن عمر كان له سيف فيه سبائك من ذهب "','974','887','0');
INSERT INTO [t22592] VALUES (894,'(٨٢٤) - (حديث: " أمره صلى الله عليه وسلم عرفجة بن أسعد لما قطع أنفه يوم الكلاب أن يتخذ أنفا من ذهب "','976','887','0');
INSERT INTO [t22592] VALUES (895,'(٨٢٥) - (حديث: " أحل الحرير والذهب لإناث أمتى ".','977','887','0');
INSERT INTO [t22592] VALUES (896,'(٨٢٦) - (حديث: " تختموا بالعقيق فإنه مبارك ".','977','887','0');
INSERT INTO [t22592] VALUES (897,'باب زكاة العروض','978','841','0');
INSERT INTO [t22592] VALUES (898,'(٨٢٧) - (عن سمرة بن جندب: " أمرنا النبى صلى الله عليه وسلم أن نخرج الصدقة مما نعده للبيع "','978','897','0');
INSERT INTO [t22592] VALUES (899,'(٨٢٨) - (قول عمر لحماس: " أد زكاة مالك , فقال: ما لى إلا جعاب وأدم , فقال: قومها وأد زكاتها "','979','897','0');
INSERT INTO [t22592] VALUES (900,'(٨٢٩) - (حديث سمرة: " ... مما نعده للبيع "','979','897','0');
INSERT INTO [t22592] VALUES (901,'(٨٣٠) - (روى الجوزجانى بإسناده عن بلال بن الحارث المزنى: " أن رسول الله صلى الله عليه وسلم أخذ من معادن القبلية الصدقة "','979','897','0');
INSERT INTO [t22592] VALUES (902,'باب زكاة الفطر','982','841','0');
INSERT INTO [t22592] VALUES (903,'(٨٣١) - (حديث ابن عمر: " فرض رسول الله صلى الله عليه وسلم زكاة الفطر من رمضان','982','902','0');
INSERT INTO [t22592] VALUES (904,'(٨٣٢) - (حديث ابن عمر: فرض رسول الله صلى الله عليه وسلم زكاة الفطر من رمضان صاعا من تمر أو صاعا من شعير على العبد والحر والذكر والأنثى والصغير والكبير من المسلمين "','982','902','0');
INSERT INTO [t22592] VALUES (905,'(٨٣٣) - (حديث: " ابدأ بنفسك "','983','902','0');
INSERT INTO [t22592] VALUES (906,'(٨٣٤) - (وفى لفظ: " وابدأ بمن تعول "','984','902','0');
INSERT INTO [t22592] VALUES (907,'(٨٣٥) - (حديث ابن عمر: " أمر رسول الله صلى الله عليه وسلم بصدقة الفطر عن الصغير والكبير , والحر والعبد ممن تمونون "','987','902','0');
INSERT INTO [t22592] VALUES (908,'(٨٣٦) - (حديث: " ابدأ بنفسك ثم بمن تعول "','989','902','0');
INSERT INTO [t22592] VALUES (909,'(٨٣٧) - (قوله صلى الله عليه وسلم للأعرابى حين قال: " من أبر؟ قال: أمك , قال: ثم من؟ قال: أمك , قال: ثم من؟ قال: أباك "','989','902','0');
INSERT INTO [t22592] VALUES (910,'(٨٣٨) - (حديث: " أنت ومالك لأبيك "','991','902','0');
INSERT INTO [t22592] VALUES (911,'(٨٣٩) - (حديث: " أدوا صدقة الفطر عن من تمونون "','998','902','0');
INSERT INTO [t22592] VALUES (912,'(٨٤٠) - (روى أبو بكر عن على رضى الله عنه: " زكاة الفطر عن من جرت عليه نفقتك "','998','902','0');
INSERT INTO [t22592] VALUES (913,'(٨٤١) - (حديث عثمان فى تصدقه عن الجنين','998','902','0');
INSERT INTO [t22592] VALUES (914,'(٨٤٢) - (حديث ابن عمر المتفق عليه: " ... وأمر بها أن تؤدى قبل خروج الناس إلى الصلاة ".','999','902','0');
INSERT INTO [t22592] VALUES (915,'(٨٤٣) - (حديث ابن عباس: " من أداها قبل الصلاة فهى زكاة مقبولة , ومن أداها بعد الصلاة فهى صدقة من الصدقات ".','1000','902','0');
INSERT INTO [t22592] VALUES (916,'(٨٤٤) - (حديث: " أغنوهم عن الطلب فى هذا اليوم "','1000','902','0');
INSERT INTO [t22592] VALUES (917,'(٨٤٥) - (حديث: " كان عليه الصلاة والسلام يقسمها بين مستحقيها بعد الصلاة "','1002','902','0');
INSERT INTO [t22592] VALUES (918,'(٨٤٦) - (حديث ابن عمر: " كانوا يعطون قبل الفطر بيوم أو يومين "','1003','902','0');
INSERT INTO [t22592] VALUES (919,'(٨٤٧) - (حديث أبى سعيد: " كنا نخرج زكاة الفطر إذ كان فينا رسول الله صلى الله عليه وسلم صاعا من طعام , أو صاعا من شعير أو صاعا من تمر , أو صاعا من زبيب , أو صاعا من أقط "','1004','902','0');
INSERT INTO [t22592] VALUES (920,'(٨٤٨) - (زيادة تفرد بها ابن عيينة فيها فى حديث أبى سعيد (المتقدم) : " أو صاعا من دقيق ". قيل لابن عيينة: " إن أحدا لا يذكره فيه , مقال: بل هو فيه "','1008','902','0');
INSERT INTO [t22592] VALUES (921,'(٨٤٩) - (حديث عمر: " لا تشتره ولا تعد فى صدقتك , وإن اعطاكه بدرهم , فإن العائد فى صدقته كالعائد فى قيئه "','1008','902','0');
INSERT INTO [t22592] VALUES (922,'باب إخراج الزكاة','1009','841','0');
INSERT INTO [t22592] VALUES (923,'(٨٥٠) - (قال عثمان رضى الله عنه: " هذا شهر زكاتكم , فمن كان عليه دين فليقضه ثم يزكى بقية ماله ".','1009','922','0');
INSERT INTO [t22592] VALUES (924,'(٨٥١) - (" أمر على رضى الله عنه واجد الركاز أن يتصدق بخمسة "','1010','922','0');
INSERT INTO [t22592] VALUES (925,'(٨٥٢) - (حديث أبى هريرة مرفوعا: " إذا أعطيتم الزكاة فلا تنسوا ثوابها أن تقولوا: اللهم اجعلها مغنما ولا تجعلها مغرما "','1011','922','0');
INSERT INTO [t22592] VALUES (926,'(٨٥٣) - (قال عبد الله بن أبى أوفى: " كان النبى صلى الله عليه وسلم إذا أتاه قوم بصدقتهم قال: اللهم صلعلى آل فلان , فأتاه أبى بصدقته فقال: اللهم صل على آل أبى أوفى "','1012','922','0');
INSERT INTO [t22592] VALUES (927,'(٨٥٤) - (حديث: " إنما الأعمال بالنيات "','1013','922','0');
INSERT INTO [t22592] VALUES (928,'(٨٥٥) - (حديث معاذ: " فأعلمهم أن الله قد افترض عليهم صدقة تؤخذ من أغنيائهم فترد على فقرائهم "','1013','922','0');
INSERT INTO [t22592] VALUES (929,'(٨٥٦) - (" أن عمر أنكر على معاذ لما بعث إليه بثلث الصدقة ثم بشطرها ثم بها , وأجابه معاذ بأنه لم يبعث إليه شيئا , وهو يجد أحدا يأخذه','1013','922','0');
INSERT INTO [t22592] VALUES (930,'(٨٥٧) - (روى أبو عبيد فى الأموال عن على: " أن النبى صلى الله عليه وسلم تعجل من العباس صدقته سنتين "','1014','922','0');
INSERT INTO [t22592] VALUES (931,'(٨٥٨) - (ويعضده رواية مسلم: " فهى على ومثلها "','1017','922','0');
INSERT INTO [t22592] VALUES (932,'باب أهل الزكاة','1021','841','0');
INSERT INTO [t22592] VALUES (933,'(٨٥٩) - (حديث: " إن الله لم يرض بحكم نبى ولا غيره فى الصدقات حتى حكم هو فيها , فجزأها ثمانية أجزاء , فإن كنت من تلك الأجزاء أعطيتك ".','1021','932','0');
INSERT INTO [t22592] VALUES (934,'(٨٦٠) - (حديث: " أن النبى صلى الله عليه وسلم استعاذ من الفقر "','1021','932','0');
INSERT INTO [t22592] VALUES (935,'(٨٦١) - (حديث: " اللهم أحينى مسكينا وأمتنى مسكينا واحشرنى فى زمرة المساكين ".','1026','932','0');
INSERT INTO [t22592] VALUES (936,'(٨٦٢) - (حديث: " كان النبى صلى الله عليه وسلم يبعث على الصدقة سعاة','1031','932','0');
INSERT INTO [t22592] VALUES (937,'(٨٦٣) - (حديث: " أن النبى صلى الله عليه وسلم أعطى صفوان بن أمية يوم حنين قبل إسلامه ترغيبا له فى الإسلام "','1035','932','0');
INSERT INTO [t22592] VALUES (938,'(٨٦٤) - (عن أبى سعيد قال:" بعث على وهو باليمن بذهيبة فقسمها رسول الله صلى الله عليه وسلم بين أربعة نفر: الأقرع بن حابس الحنظلى وعيينة بن بدر الفزارى وعلقمة بن علاثة العامرى ثم أحد بنى كلاب وزيد الخير الطائى , ثم أحد بنى نبهان فغضبت قريش وقالوا:','1036','932','0');
INSERT INTO [t22592] VALUES (939,'(٨٦٥) - (قول ابن عباس فى المؤلفة قلوبهم: " هم قوم كانوا يأتون رسول الله صلى الله عليه وسلم وكان رسول الله صلى الله عليه وسلم يرضخ لهم من الصدقات فإذا أعطاهم من الصدقة قالوا: هذا دين صالح وإن كان غير ذلك عابوه "','1037','932','0');
INSERT INTO [t22592] VALUES (940,'(٨٦٦) - (حديث: " إن أبا بكر رضى الله عنه , أعطى عدى بن حاتم والزبرقان بن بدر مع حسن نياتهما وإسلامهما رجاء إسلام نظرائهما "','1037','932','0');
INSERT INTO [t22592] VALUES (941,'(٨٦٧) - (وعن أنس مرفوعا: " إن المسألة لا تحل إلا لثلاثة: لذى فقر مدقع , أو لذى غرم مفظع , أو لذى دم موجع "','1038','932','0');
INSERT INTO [t22592] VALUES (942,'(٨٦٨) - (حديث قبيصة بن مخارق الهلالى قال: " تحملت حمالة , فأتيت النبى صلى الله عليه وسلم , أسأله فيها , فقال: أقم حتى تأتينا الصدقة , فنأمر لك بها','1039','932','0');
INSERT INTO [t22592] VALUES (943,'(٨٦٩) - (حديث: " الحج والعمرة من سبيل الله "','1040','932','0');
INSERT INTO [t22592] VALUES (944,'(٨٧٠) - (حديث أبى سعيد مرفوعا: " لا تحل الصدقة لغنى , إلا فى سبيل الله , أو ابن السبيل , أو جار فقير يتصدق عليه , فيهدى لك , أو يدعوك " رواه أبو داود. وفى لفظ: " لا تحل الصدقة لغنى إلا لخمسة: للعامل عليها أو رجل اشتراها بماله , أو غارم أو غاز فى سبي','1045','932','0');
INSERT INTO [t22592] VALUES (945,'(٨٧١) - (حديث: " أن النبى صلى الله عليه وسلم بعث عمر ساعيا ولم يجعل له أجره , فلما جاء أعطاه "','1047','932','0');
INSERT INTO [t22592] VALUES (946,'(٨٧٢) - (حديث: " أن ابن عمر كان يدفع زكاته إلى من جاءه من سعاة ابن الزبير أو نجدة الحرورى "','1047','932','0');
INSERT INTO [t22592] VALUES (947,'(٨٧٣) - (حديث: " أنه قيل لابن عمر: إنهم يقلدون بها الكلاب , ويشربون بها الخمور , قال: ادفعها إليهم','1047','932','0');
INSERT INTO [t22592] VALUES (948,'(٨٧٤) - (حديث سهيل بن أبى صالح [عن أبيه] قال: " أتيت سعد بن أبى وقاص فقلت: عندى مال , وأريد إخراج زكاته , وهؤلاء القوم على ما ترى؟ قال: ادفعها إليه , فأتيت ابن عمر وأبا هريرة وأبا سعيد رضى الله عنهم فقالوا: مثل ذلك "','1048','932','0');
INSERT INTO [t22592] VALUES (949,'(٨٧٥) - (لحديث معاذ: " تؤخذ من أغنيائهم فترد إلى فقرائهم ".','1049','932','0');
INSERT INTO [t22592] VALUES (950,'(٨٧٦) - (لقوله صلى الله عليه وسلم: " لا حظ فيها لغنى , ولا لقوى مكتسب ".','1049','932','0');
INSERT INTO [t22592] VALUES (951,'(٨٧٧) - (وقوله: " لا تحل الصدقة لغنى ولا لذى مرة سوى "','1049','932','0');
INSERT INTO [t22592] VALUES (952,'(٨٧٨) - (قوله صلى الله عليه وسلم لزينب امرأة ابن مسعود: " زوجك وولدك أحق من تصدقت به عليهم "','1053','932','0');
INSERT INTO [t22592] VALUES (953,'(٨٧٩) - (قوله صلى الله عليه وسلم: " إن الصدقة لا تنبغى لآل محمد إنما هى أوساخ الناس "','1054','932','0');
INSERT INTO [t22592] VALUES (954,'(٨٨٠) - (حديث أبى رافع مرفوعا: " إنا لا تحل لنا الصدقة وإن موالى القوم منهم "','1055','932','0');
INSERT INTO [t22592] VALUES (955,'(٨٨١) - (قول صلى الله عليه وسلم للرجلين: " إن شئتما أعطيتكما منها ولا حفظ فيها لغنى ".','1055','932','0');
INSERT INTO [t22592] VALUES (956,'(٨٨٢) - (وقال للذى سأله من الصدقة: " إن كنت من تلك الأجزاء أعطيتك ".','1055','932','0');
INSERT INTO [t22592] VALUES (957,'(٨٨٣) - (قوله صلى الله عليه وسلم: " صدقتك على ذى الرحم صدقة وصلة "','1055','932','0');
INSERT INTO [t22592] VALUES (958,'(٨٨٤) - (حديث زينب وفيه: " أتجزىء الصدقة عنهما على أزواجهما وعلى أيتام فى حجورهما؟ قال: لهما أجران أجر القرابة وأجر الصدقة "','1056','932','0');
INSERT INTO [t22592] VALUES (959,'(٨٨٥) - (وقال صلى الله عليه وسلم: " إن الصدقة لتطفىء غضب الرب وتدفع ميتة السوء "','1058','932','0');
INSERT INTO [t22592] VALUES (960,'(٨٨٦) - (وعن أبى هريرة مرفوعا: " من تصدق بعدل تمرة من كسب طيب ـ ولا يصعد إلى الله إلا الطيب ـ فإن الله تعالى يقبلها بيمينه ثم يربيها لصاحبها كما يربى أحدكم فلوه حتى تكون مثل الجبل "','1061','932','0');
INSERT INTO [t22592] VALUES (961,'(٨٨٧) - (حديث: " سبعة يظلهم الله فى ظله ... ورجل تصدق بصدقة فأخفاها حتى لا تعلم شماله ما تنفق يمينه "','1063','932','0');
INSERT INTO [t22592] VALUES (962,'(٨٨٨) - (حديث ابن عباس: " كان رسول الله صلى الله عليه وسلم أجود الناس , وكان أجود ما يكون فى رمضان حين يلقاه جبريل "','1064','932','0');
INSERT INTO [t22592] VALUES (963,'(٨٨٩) - (عن أنس: " سئل رسول الله صلى الله عليه وسلم: أى الصدقة أفضل؟','1064','932','0');
INSERT INTO [t22592] VALUES (964,'(٨٩٠) - (وعن ابن عباس مرفوعا: " ما من أيام العمل الصالح فيها أحب إلى الله من هذه الأيام ـ يعنى أيام العشر ـ قالوا: يا رسول الله ولا الجهاد فى سبيل الله؟ قال: ولا الجهاد فى سبيل الله إلا رجل خرج بماله ونفسه , ثم لم يرجع من ذلك بشىء "','1065','932','0');
INSERT INTO [t22592] VALUES (965,'(٨٩١) - (حديث: " مازال جبريل يوصينى بالجار حتى ظننت أنه سيورثه "','1068','932','0');
INSERT INTO [t22592] VALUES (966,'(٨٩٢) - (حديث: " أفضل الصدقة على ذى الرحم الكاشح "','1072','932','0');
INSERT INTO [t22592] VALUES (967,'(٨٩٣) - (قوله صلى الله عليه وسلم: " وابدأبمن تعول , وخير الصدقة عن ظهر غنى ".','1074','932','0');
INSERT INTO [t22592] VALUES (968,'(٨٩٤) - (حديث: " كفى بالمرء إثما أن يضيع من يقوت "','1074','932','0');
INSERT INTO [t22592] VALUES (969,'(٨٩٥) - (عن أبى هريرة قال: " أمر رسول الله صلى الله عليه وسلم بالصدقة فقام رجل فقال: يا رسول الله عندى دينار قال: تصدق به على نفسك , قال: عندى آخر قال: تصدق به على ولدك قال: عندى آخر. قال: تصدق به على زوجتك , قال: عندى آخر. قال: تصدق به على خادمك','1076','932','0');
INSERT INTO [t22592] VALUES (970,'(٨٩٦) - (وقال صلى الله عليه وسلم: " لا ضرر ولا ضرار "','1076','932','0');
INSERT INTO [t22592] VALUES (971,'(٨٩٧) - (وقال صلى الله عليه وسلم: " أفضل الصدقة جهد من مقل إلى فقير فى السر ".','1082','932','0');
INSERT INTO [t22592] VALUES (972,'(٨٩٨) - (وروى أبو داود عن النبى صلى الله عليه وسلم قال: " يأتى (١) أحدكم بما يملك فيقول: هذه صدقة ثم يقعد يستكف الناس؟ ! خير الصدقة ما كان عن ظهر غنى "','1083','932','0');
INSERT INTO [t22592] VALUES (973,'(٨٩٩) - (وقال صلى الله عليه وسلم لسعد: " إنك أن تدع ورثتك أغنياء خير من أن تدعهم عالة يتكففون الناس "','1084','932','0');
INSERT INTO [t22592] VALUES (974,'(٩٠٠) - (حديث: " ثلاثة لا يكلمهم الله يوم القيامة , ولا يزكيهم ولهم عذاب أليم: المسبل , والمنان , والمنفق سلعته بالحلف الكاذب "','1085','932','0');
INSERT INTO [t22592] VALUES (975,'كتاب الصيام','1087','0','0');
INSERT INTO [t22592] VALUES (976,'[الأحاديث ٩٠١ - ٩٢٩]','1087','975','0');
INSERT INTO [t22592] VALUES (977,'(٩٠١) - (حديث ابن عمر: " بنى الإسلام على خمس "','1087','976','0');
INSERT INTO [t22592] VALUES (978,'(٩٠٢) - (وقرأ [١] صلى الله عليه وسلم: " صوموا لرؤيته , وأفطروا لرؤيته "','1087','976','0');
INSERT INTO [t22592] VALUES (979,'(٩٠٣) - (قوله صلى الله عليه وسلم فى حديث ابن عمر: " فإن غم عليكم فاقدروا له "','1092','976','0');
INSERT INTO [t22592] VALUES (980,'(٩٠٤) - (حديث: " كان ابن عمر إذا حال دون مطلعه غيم أو قتر أصبح صائما "','1094','976','0');
INSERT INTO [t22592] VALUES (981,'(٩٠٥) - (قرأ [١] صلى الله عليه وسلم: " صومكم يوم تصومون وأضحاكم يوم تضحون "','1095','976','0');
INSERT INTO [t22592] VALUES (982,'(٩٠٦) - (قوله صلى الله عليه وسلم: " من قام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه "','1098','976','0');
INSERT INTO [t22592] VALUES (983,'(٩٠٧) - (لحديث ابن عباس قال: " جاء أعرابى إلى النبى صلى الله عليه وسلم فقال: رأيت الهلال قال: أتشهد أن لا إله إلا الله وأن محمدا عبده ورسوله قال: نعم , قال: يا بلال: أذن فى الناس فليصوموا غدا "','1099','976','0');
INSERT INTO [t22592] VALUES (984,'(٩٠٨) - (وعن ابن عمر قال: " تراءى الناس الهلال فأخبرت النبى صلى الله عليه وسلم أنى رأيته فصام وأمر الناس بصيامه "','1100','976','0');
INSERT INTO [t22592] VALUES (985,'(٩٠٩) - (لحديث عبد الرحمن بن زيد بن الخطاب وفيه: " فإن شهد شاهدان مسلمان فصوموا وأفطروا "','1100','976','0');
INSERT INTO [t22592] VALUES (986,'(٩١٠) - (يقول عليه السلام: " صوموا لرؤيته "','1101','976','0');
INSERT INTO [t22592] VALUES (987,'(٩١١) - (حديث: " رفع القلم عن ثلاثة "','1101','976','0');
INSERT INTO [t22592] VALUES (988,'(٩١٢) - (يقول ابن عباس فى قوله تعالى: (وعلى الذين يطيقونه فدية) : " ليست بمنسوخة هى للكبير الذى لا يستطيع الصوم "','1101','976','0');
INSERT INTO [t22592] VALUES (989,'(٩١٣) - (والحامل والمرضع إذا خافتا على أولادهما أفطرتا وأطعمتا ".','1109','976','0');
INSERT INTO [t22592] VALUES (990,'(٩١٤) - (لحديث حفصة أن النبى صلى الله عليه وسلم قال: " من لم يبيت الصيام من الليل فلا صيام له "','1109','976','0');
INSERT INTO [t22592] VALUES (991,'(٩١٥) - (وقال صلى الله عليه وسلم: " لا يمنعنكم من سحوركم أذان بلال ولا الفجر المستطيل ولكن الفجر المستطير فى الأفق "','1114','976','0');
INSERT INTO [t22592] VALUES (992,'(٩١٦) - (وعن عمر مرفوعا: " إذا أقبل الليل من ها هنا وأدبر النهار من ها هنا وغربت الشمس أفطر الصائم "','1115','976','0');
INSERT INTO [t22592] VALUES (993,'(٩١٧) - (حديث أبى ذر عن النبى صلى الله عليه وسلم قال: " لا تزال أمتى بخير ما أخروا السحور وعجلوا الفطر "','1116','976','0');
INSERT INTO [t22592] VALUES (994,'(٩١٨) - (حديث أبى هريرة مرفوعا: " إذا كان يوم صوم أحدكم فلا يرفث يومئذ ولا يصخب فإنه شاتمه أحد أو قاتله فليقل أنى امرؤ صائم "','1117','976','0');
INSERT INTO [t22592] VALUES (995,'(٩١٩) - (حديث ابن عباس وأنس كان النبى صلى الله عليه وسلم إذا أفطر قال: " اللهم لك صمنا , وعلى رزقك أفطرنا , اللهم تقبل منا , إنك أنت السميع العليم".','1120','976','0');
INSERT INTO [t22592] VALUES (996,'(٩٢٠) - (عن ابن عمر مرفوعا كان إذا أفطر قال: " ذهب الظمأ وابتلت العروق وثبت (١) الأجر إن شاء الله ".','1123','976','0');
INSERT INTO [t22592] VALUES (997,'(٩٢١) - (وفى الخبر: " إن للصائم عند فطرة دعوة لا ترد "','1125','976','0');
INSERT INTO [t22592] VALUES (998,'(٩٢٢) - (حديث أنس: " كان رسول الله صلى الله عليه وسلم يفطر على رطبات قبل أن يصلى , فإن لم يكن فعلى تمرات , فإن لم تكن تمرات حسا حسوات من ماء ".','1129','976','0');
INSERT INTO [t22592] VALUES (999,'(٩٢٣) - (قوله صلى الله عليه وسلم: " ومن استقاء فليقض "','1135','976','0');
INSERT INTO [t22592] VALUES (1000,'(٩٢٤) - (الحديث الصحيح: " أليس إذا حاضت لم تصل ولم تصم ".','1137','976','0');
INSERT INTO [t22592] VALUES (1001,'(٩٢٥) - (حديث: " ليس من البر الصيام فى السفر " متفق عليه','1137','976','0');
INSERT INTO [t22592] VALUES (1002,'(٩٢٦) - (حديث: " هى رخصة من الله , فمن أخذ بها فحسن , ومن أحب أن يصوم , فلا جناح عليه "','1145','976','0');
INSERT INTO [t22592] VALUES (1003,'(٩٢٧) - (وعن حمزة بن عمرو الأسلمى: " أنه قال للنبى صلى الله عليه وسلم: أصوم فى السفر؟ قال: إن شئت فصم , وإن شئت فأفطر "','1146','976','0');
INSERT INTO [t22592] VALUES (1004,'(٩٢٨) - (لحديث أبى بصرة الغفارى: " أنه ركب سفينة من الفسطاط فى شهر رمضان فدفع , ثم قرب غداءه , فلم يجاوز البيوت حتى دعا بالسفرة , ثم قال: اقترب , قيل: ألست ترى البيوت؟ قال: أترغب عن سنة محمد صلى الله عليه وسلم؟ فأكل ".','1147','976','0');
INSERT INTO [t22592] VALUES (1005,'(٩٢٩) - (قال ابن عباس: " كانت رخصة للشيخ الكبير والمرأة الكبيرة , وهما يطيقان الصيام أن يفطرا , ويطعما مكان كل يوم مسكينا , والحبلى والمرضع , إذا خافتا على أولادهما أفطرتا وأطعمتا "','1148','976','0');
INSERT INTO [t22592] VALUES (1006,'فصل فى المفطرات','1149','975','0');
INSERT INTO [t22592] VALUES (1007,'(٩٣٠) - (حديث أبى هريرة مرفوعا: " من ذرعه القىء فليس عليه قضاء , ومن استقاء عمدا فليقض "','1149','1006','0');
INSERT INTO [t22592] VALUES (1008,'(٩٣١) - (حديث: " أفطر الحاجم والمحجوم ". رواه عن النبى صلى الله عليه وآله وسلم أحد عشر نفسا','1149','1006','0');
INSERT INTO [t22592] VALUES (1009,'(٩٣٢) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم احتجم , وهو صائم ".','1159','1006','0');
INSERT INTO [t22592] VALUES (1010,'(٩٣٣) - (حديث ابن عباس أنه: " كان يعد الحجام والمحاجم قبل مغيب الشمس , فإذا غابت احتجم "','1163','1006','0');
INSERT INTO [t22592] VALUES (1011,'(٩٣٤) - (حديث عائشة رضى الله عنها: " كان رسول الله صلى الله عليه وسلم يقبل وهو صائم ويباشر وهو صائم , ولكنه كان أملككم لإربه "','1164','1006','0');
INSERT INTO [t22592] VALUES (1012,'(٩٣٥) - (قوله صلى الله عليه وسلم للقيط بن صبرة: " ... وبالغ فى الاستنشاق إلا أن تكون صائما "','1169','1006','0');
INSERT INTO [t22592] VALUES (1013,'(٩٣٦) - (وروى أبو داود والبخارى فى تاريخه عن النبى صلى الله عليه وسلم: " أنه أمر بالإثمد المروح عند النوم وقال: ليتقه الصائم ".','1169','1006','0');
INSERT INTO [t22592] VALUES (1014,'(٩٣٧) - (قول ابن عباس: " لا بأس أن يذوق الخل والشىء يريد شراءه ".','1169','1006','0');
INSERT INTO [t22592] VALUES (1015,'(٩٣٨) - (حديث أبى هريرة مرفوعا: " من نسى وهو صائم , فأكل أو شرب , فليتم صومه , فإنما أطعمه الله وسقاه "','1170','1006','0');
INSERT INTO [t22592] VALUES (1016,'فصل','1172','975','0');
INSERT INTO [t22592] VALUES (1017,'(٩٣٩) - (حديث أبى هريرة: " أن رجلا قال: يا رسول الله وقعت على امرأتى وأنا صائم فقال رسول الله صلى الله عليه وسلم هل تجد رقبة تعتقها؟ قال: لا , قال: فهل تستطيع أن تصوم شهرين متتابعين؟ قال: لا , قال: هل تجد إطعام ستين مسكينا؟ قال: لا , فسكت فبينما','1172','1016','0');
INSERT INTO [t22592] VALUES (1018,'(٩٤٠) - (وقال صلى الله عليه وسلم للمجامع: " صم يوما مكانه ".','1177','1016','0');
INSERT INTO [t22592] VALUES (1019,'(٩٤١) - (" لأنه صلى الله عليه وسلم لم يأمر امرأة المواقع بكفارة "','1177','1016','0');
INSERT INTO [t22592] VALUES (1020,'(٩٤٢) - (حديث: " عفى لأمتى عن الخطأ والنيسان "','1177','1016','0');
INSERT INTO [t22592] VALUES (1021,'فصل','1178','975','0');
INSERT INTO [t22592] VALUES (1022,'(٩٤٣) - (عن ابن عمر مرفوعا [فى] قضاء رمضان: " إن شاء فرق وإن شاء تابع ".','1178','1021','0');
INSERT INTO [t22592] VALUES (1023,'(٩٤٤) - (لقول عائشة: " لقد كان يكون على الصيام من رمضان فما أقضيه حتى يجىء شعبان "','1181','1021','0');
INSERT INTO [t22592] VALUES (1024,'(٩٤٥) - (حديث عبد الله بن عمرو قال: قال رسول الله صلى الله عليه وسلم: " أحب الصيام إلى الله تعالى صيام داود كان يصوم يوما ويفطر يوما "','1182','1021','0');
INSERT INTO [t22592] VALUES (1025,'(٩٤٦) - (قول أبى هريرة: " أوصانى خليلى صلى الله عليه وسلم بثلاث: صيام ثلاثة أيام من كل شهر , وركعتى الضحى , وأن أوتر قبل أن أنام "','1183','1021','0');
INSERT INTO [t22592] VALUES (1026,'(٩٤٧) - (وعن أبى ذر قال: قال رسول الله صلى الله عليه وسلم: " يا أبا ذر إذا صمت من الشهر ثلاثة أيام فصم ثلاث عشرة وأربع عشرة وخمس عشرة "','1185','1021','0');
INSERT INTO [t22592] VALUES (1027,'(٩٤٨) - (لأنه صلى الله عليه وسلم كان يصومهما (الإثنين والخميس) فسئل عن ذلك فقال: " إن الأعمال تعرض يوم الإثنين والخميس "','1186','1021','0');
INSERT INTO [t22592] VALUES (1028,'(٩٤٩) - (وفى لفظ: " وأحب أن يعرض عملى وأنا صائم "','1188','1021','0');
INSERT INTO [t22592] VALUES (1029,'(٩٥٠) - (حديث أبى أيوب مرفوعا: " من صام رمضان وأتبعه ستا من شوال فكأنما صام الدهر "','1190','1021','0');
INSERT INTO [t22592] VALUES (1030,'(٩٥١) - (حديث أبى هريرة مرفوعا: " أفضل الصيام بعد رمضان شهر الله المحرم ".','1191','1021','0');
INSERT INTO [t22592] VALUES (1031,'(٩٥٢) - (حديث أبى قتادة عن النبى صلى الله عليه وسلم أنه قال فى صيام عاشوراء: " إنى أحتسب على الله أن يكفر السنة التى بعده "','1192','1021','0');
INSERT INTO [t22592] VALUES (1032,'(٩٥٣) - (حديث ابن عباس مرفوعا: " ما من أيام العمل الصالح فيهن أحب إلى الله من هذه الأيام العشر ".','1194','1021','0');
INSERT INTO [t22592] VALUES (1033,'(٩٥٤) - (وعن حفصة قالت: " أربع لم يكن يدعهن رسول الله صلى الله عليه وسلم: صيام عاشوراء والعشر , وثلاثة أيام من كل شهر , والركعتان قبل الغداة ".','1195','1021','0');
INSERT INTO [t22592] VALUES (1034,'(٩٥٥) - (حديث أبى قتادة مرفوعا: " صوم يوم عرفة يكفر سنتين ماضية ومستقبلة , وصوم عاشوراء يكفر سنة ماضية ".','1195','1021','0');
INSERT INTO [t22592] VALUES (1035,'(٩٥٦) - (حديث: " صوم يوم التروية كفارة سنة ".','1196','1021','0');
INSERT INTO [t22592] VALUES (1036,'(٩٥٧) - (روى عن أحمد عن خرشة بن الحر قال: " رأيت عمر يضرب أكف المترجبين حتى يضعوها فى الطعام ويقول: كلوا فإنما هو شهر كانت تعظمه الجاهلية "','1197','1021','0');
INSERT INTO [t22592] VALUES (1037,'(٩٥٨) - (وبإسناده عن ابن عمر أنه: " كان إذا رأى الناس وما يعدونه لرجب كرهه وقال: صوموا منه وأفطروا "','1198','1021','0');
INSERT INTO [t22592] VALUES (1038,'(٩٥٩) - (حديث أبى هريرة مرفوعا: " لا يصومن أحدكم يوم الجمعة إلا أن يصوم يوما قبله أو يوما بعده ".','1200','1021','0');
INSERT INTO [t22592] VALUES (1039,'(٩٦٠) - (حديث: " لا تصوموا يوم السبت إلا فيما افترض عليكم "','1202','1021','0');
INSERT INTO [t22592] VALUES (1040,'(٩٦١) - (لقول عمار: " من صام اليوم الذى يشك فيه فقد عصى أبا القاسم صلى الله عليه وسلم "','1209','1021','0');
INSERT INTO [t22592] VALUES (1041,'(٩٦٢) - (حديث أبى هريرة مرفوعا: " نهى عن صوم يومين: يوم الفطر , ويوم الأضحى "','1211','1021','0');
INSERT INTO [t22592] VALUES (1042,'(٩٦٣) - (حديث: " وأيام منى أيام أكل وشرب "','1212','1021','0');
INSERT INTO [t22592] VALUES (1043,'(٩٦٤) - (حديث ابن عمر وعائشة: " لم يرخص فى أيام التشريق أن يصمن , إلا لمن لم يجد الهدى ".','1216','1021','0');
INSERT INTO [t22592] VALUES (1044,'(٩٦٥) - (لحديث عائشة قلت: " يا رسول الله أهديت لنا هدية أو','1218','1021','0');
INSERT INTO [t22592] VALUES (1045,'كتاب الاعتكاف','1223','0','0');
INSERT INTO [t22592] VALUES (1046,'(٩٦٦) - (حديث عائشة: " كان رسول الله صلى الله عليه وسلم يعتكف العشر الأواخر من رمضان حتى توفاه الله ثم اعتكف أزواجه من بعده "','1223','1045','0');
INSERT INTO [t22592] VALUES (1047,'(٩٦٧) - (قوله صلى الله عليه وسلم: " من نذر أن يطيع الله فليطعه ".','1224','1045','0');
INSERT INTO [t22592] VALUES (1048,'(٩٦٨) - (قوله صلى الله عليه وسلم: " لا أحل المسجد لحائض ولا جنب "','1225','1045','0');
INSERT INTO [t22592] VALUES (1049,'(٩٦٩) - (قوله صلى الله عليه وسلم: " صلاة فى مسجدى هذا ... ".','1225','1045','0');
INSERT INTO [t22592] VALUES (1050,'(٩٧٠) - (لحديث أبى هريرة مرفوعا: " لا تشد الرحال إلا إلى ثلاثة مساجد , المسجد الحرام , ومسجدى هذا , والمسجد الأقصى "','1225','1045','0');
INSERT INTO [t22592] VALUES (1051,'(٩٧١) - (حديث أبى هريرة مرفوعا: " صلاة فى مسجدى هذا خير من ألف صلاة فيما سواه إلا المسجد الحرام "','1227','1045','0');
INSERT INTO [t22592] VALUES (1052,'(٩٧٢) - (لحديث جابر: " أن رجلا قال يوم الفتح: يا رسول الله إنى نذرت إن فتح الله عليك مكة أن أصلى فى بيت المقدس فقال: صل','1230','1045','0');
INSERT INTO [t22592] VALUES (1053,'(٩٧٣) - (لقول عائشة: " السنة للمعتكف ألا يخرج إلا لما لابد له منه ".','1231','1045','0');
INSERT INTO [t22592] VALUES (1054,'(٩٧٤) - (حديث: " وكان لا يدخل البيت إلا لحاجة الإنسان "','1231','1045','0');
INSERT INTO [t22592] VALUES (1055,'(٩٧٥) - (حديث: " إنما الأعمال بالنيات "','1232','1045','0');
INSERT INTO [t22592] VALUES (1056,'(٩٧٦) - (روى حرب عن ابن عباس: " إذا جامع المعتكف بطل اعتكافه واستأنف الاعتكاف "','1232','1045','0');
INSERT INTO [t22592] VALUES (1057,'(٩٧٧) - (حديث عائشة: " وكان لا يدخل البيت إلا لحاجة الإنسان "','1232','1045','0');
INSERT INTO [t22592] VALUES (1058,'(٩٧٨) - (قول عائشة: " إن كنت لأدخل البيت للحاجة , والمريض فيه , فلا أسأل عنه إلا وأنا مارة "','1232','1045','0');
INSERT INTO [t22592] VALUES (1059,'كتاب الحج','1233','0','0');
INSERT INTO [t22592] VALUES (1060,'[الأحاديث ٩٧٩ - ٩٩٥]','1233','1059','0');
INSERT INTO [t22592] VALUES (1061,'(٩٧٩) - (لحديث ابن عمر: " بنى الإسلام على خمس ... "','1233','1060','0');
INSERT INTO [t22592] VALUES (1062,'(٩٨٠) - (وعن أبى هريرة قال: " خطبنا رسول الله صلى الله عليه وسلم فقال: يا أيها الناس إن الله قد فرض عليكم الحج فحجوا. فقال رجل: أكل عام يا رسول الله؟ فسكت حتى قالها ثلاثا فقال رسول الله صلى الله عليه وسلم: لو قلت نعم لوجبت , ولما استطعتم. ثم قال:','1233','1060','0');
INSERT INTO [t22592] VALUES (1063,'(٩٨١) - (وعن عائشة أنها قالت: " يا رسول الله هل على النساء جهاد؟ قال نعم عليهن جهاد لا قتال فيه: الحج والعمرة "','1235','1060','0');
INSERT INTO [t22592] VALUES (1064,'(٩٨٢) - (ولمسلم عن ابن عباس: " دخلت العمرة فى الحج إلى يوم القيامة "','1236','1060','0');
INSERT INTO [t22592] VALUES (1065,'(٩٨٣) - (وعن الصبى بن معبد قال: " أتيت عمر رضى الله عنه فقلت: يا أمير المؤمنين إنى أسلمت وإنى وجدت الحج والعمرة مكتوبين على فأهللت بهما. فقال: هديت لسنة نبيك ".','1237','1060','0');
INSERT INTO [t22592] VALUES (1066,'(٩٨٤) - (حديث: " رفع القلم عن ثلاثة "','1238','1060','0');
INSERT INTO [t22592] VALUES (1067,'(٩٨٥) - (لحديث ابن عباس: " أن امرأة رفعت إلى النبى صلى الله عليه وسلم صبيا فقالت: ألهذا حج؟ قال: نعم ولك أجر "','1239','1060','0');
INSERT INTO [t22592] VALUES (1068,'(٩٨٦) - (وعنه أيضا مرفوعا: " أيما صبى حج ثم بلغ فعليه حجة','1239','1060','0');
INSERT INTO [t22592] VALUES (1069,'(٩٨٧) - (قال ابن عباس: " إذا أعتق العبد بعرفه أجزأه حجه ".','1243','1060','0');
INSERT INTO [t22592] VALUES (1070,'(٩٨٨) - (وعن أنس فى قوله عز وجل: (من استطاع إليه سبيلا) قال: " قيل: يا رسول الله ما السبيل؟ قال: الزاد والراحلة "','1244','1060','0');
INSERT INTO [t22592] VALUES (1071,'(٩٨٩) - (لحديث: " كفى بالمرء إثما أن يضيع من يقوت "','1251','1060','0');
INSERT INTO [t22592] VALUES (1072,'(٩٩٠) - (حديث ابن عباس مرفوعا: " تعجلوا إلى الحج ـ يعنى الفريضة ـ فإن أحدكم لا يدرى ما يعرض له ".','1252','1060','0');
INSERT INTO [t22592] VALUES (1073,'(٩٩١) - (لحديث: " لا تركب البحر إلا حاجا أو معتمرا أو غازيا فى سبيل الله ".','1253','1060','0');
INSERT INTO [t22592] VALUES (1074,'(٩٩٢) - (لحديث ابن عباس: " أن امرأة من خثعم قالت: يا رسول الله إن أبى أدركته فريضة الله فى الحج شيخا كبيرا لا يستطيع أن يستوى على الراحلة , فأحج عنه. قال: حجى عنه "','1254','1060','0');
INSERT INTO [t22592] VALUES (1075,'(٩٩٣) - لحديث ابن عباس: " أن امرأة قالت: يا رسول الله إن أمى نذرت أن تحج فلم تحج حتى ماتت. أفأحج عنها؟ قال نعم , حجى عنها , أرأيت لو كان على أمك دين أكنت قاضيته؟ اقضوا الله فالله أحق بالوفاء "','1254','1060','0');
INSERT INTO [t22592] VALUES (1076,'(٩٩٤) - (لحديث ابن عباس: " أن النبى صلى الله عليه وسلم سمع رجلا يقول: لبيك عن شبرمة. قال: حججت عن نفسك؟ قال: لا , قال: حج عن نفسك ثم حج عن شبرمة ".','1255','1060','0');
INSERT INTO [t22592] VALUES (1077,'(٩٩٥) - (حديث ابن عباس: " لا تسافر امرأة إلا مع [ذى] محرم , ولا يدخل عليها رجل إلا ومعها محرم "','1257','1060','0');
INSERT INTO [t22592] VALUES (1078,'باب الإحرام','1258','1059','0');
INSERT INTO [t22592] VALUES (1079,'(٩٩٦) - (حديث ابن عباس قال: " وقت رسول الله صلى الله عليه وسلم لأهل المدينة ذا الحليفة , ولأهل الشام الجحفة ولأهل نجد قرن , ولأهل اليمن يلملم , هن لهن , ولمن أتى عليهن من غير أهلهن ممن يريد الحج والعمرة , ومن كان دون ذلك , فمهله من أهله , وكذل','1258','1078','0');
INSERT INTO [t22592] VALUES (1080,'(٩٩٧) - (قول عمر: " انظروا حذوها من قديد ـ وفى لفظ ـ من طريقكم "','1258','1078','0');
INSERT INTO [t22592] VALUES (1081,'(٩٩٨) - (وفى صحيح مسلم عن جابر: " أن النبى صلى الله عليه وسلم وقت لأهل العراق ذات عرق "','1259','1078','0');
INSERT INTO [t22592] VALUES (1082,'(٩٩٩) - (وعن عائشة مرفوعا نحوه , رواه أبو داود والنسائى.','1260','1078','0');
INSERT INTO [t22592] VALUES (1083,'(١٠٠٠) - (و" وقت عمر أيضا لأهل العراق ذات عرق ".','1264','1078','0');
INSERT INTO [t22592] VALUES (1084,'(١٠٠١) - (عن أنس: " أنه كان يحرم من العقيق "','1264','1078','0');
INSERT INTO [t22592] VALUES (1085,'(١٠٠٢) - (وعن ابن عباس: " أن رسول الله صلى الله عليه وسلم وقت لأهل المشرق العقيق ".','1264','1078','0');
INSERT INTO [t22592] VALUES (1086,'(١٠٠٣) - (قول عائشة: " فمنا من أهل بعمرة ومنا من أهل بحج ومنا من أهل بهما "','1265','1078','0');
INSERT INTO [t22592] VALUES (1087,'(١٠٠٤) - (لحديث جابر: " أنه حج مع النبى صلى الله عليه وسلم وقد أهلوا بالحج مفردا فقال لهم: حلوا من إحرامكم بطواف بالبيت , وبين الصفا والمروة , وقصروا , وأقيموا حلالا حتى إذا كان يوم التروية , فأهلوا','1267','1078','0');
INSERT INTO [t22592] VALUES (1088,'(١٠٠٥) - (قول طاووس: " خرج رسول الله صلى الله عليه وسلم من المدينة لا يسمي حجا ينتظر القضاء فنزل عليه بين الصفا والمروة..... " إلخ) .','1268','1078','0');
INSERT INTO [t22592] VALUES (1089,'(١٠٠٦) - (حديث أنس قال: " قدم على على رسول الله صلى الله عليه وسلم من اليمن فقال: بم أهللت يا على؟ قال: أهللت بإهلال كإهلال النبى صلى الله عليه وسلم قال: لولا أن معى الهدى لأحللت "','1269','1078','0');
INSERT INTO [t22592] VALUES (1090,'(١٠٠٧) - (قول عائشة: " فمنا من أهل بعمرة ومنا من أهل بحج وعمرة ومنا من من أهل بحج "','1269','1078','0');
INSERT INTO [t22592] VALUES (1091,'(١٠٠٨) - (روى النسائي من حديث جابر: " أن النبي صلى الله عليه وسلم قال لعلي: بم أهللت؟ قال: قلت: اللهم إني أهل بما أهل به رسول الله صلى الله عليه وسلم ") .','1269','1078','0');
INSERT INTO [t22592] VALUES (1092,'(١٠٠٩) - (وعن عائشة: " أن رسول الله صلى الله عليه وسلم دخل على ضباعة بنت الزبير فقال لها: لعلك أردت الحج فقالت: والله ما أجدني إلا وجعة , فقال لها: حجي , واشترطي وقولي: اللهم إن محلي حيث حبستني "','1270','1078','0');
INSERT INTO [t22592] VALUES (1093,'(١٠١٠) - (وللنسائي في حديث ابن عباس: " فإن لك على ربك ما استثنيت ")','1270','1078','0');
INSERT INTO [t22592] VALUES (1094,'(١٠١١) - (وفي حديث عكرمة [عن ضباعة بنت الزبير بن عبد المطلب] : " فإن حبست أو مرضت , فقد حللت من ذلك بشرطك على ربك "','1272','1078','0');
INSERT INTO [t22592] VALUES (1095,'باب محظورات الإحرام','1274','1059','0');
INSERT INTO [t22592] VALUES (1096,'(١٠١٢) - (حديث ابن عمر: " أن النبي صلى الله عليه وسلم سئل: ما يلبس المحرم؟ فقال: لا يلبس القميص ولا العمامة ولا البرنس , ولا السراويل ولا ثوبا مسه ورس ولا زعفران , ولا الخفين إلا ألا يجد نعلين فليقطعهما حتى يكونا أسفل من الكعبين ".','1274','1095','0');
INSERT INTO [t22592] VALUES (1097,'(١٠١٣) - (لحديث ابن عباس: سمعت النبي صلى الله عليه وسلم يخطب','1277','1095','0');
INSERT INTO [t22592] VALUES (1098,'(١٠١٤) - (وفى رواية لأحمد فى حديث ابن عمر المتقدم (١٠١٢) : " سمعت رسول الله صلى الله عليه وسلم على المنبر ".','1280','1095','0');
INSERT INTO [t22592] VALUES (1099,'(١٠١٥) - (حديث: " نهيه صلى الله عليه وسلم المحرم عن لبس العمائم والبرانس "','1281','1095','0');
INSERT INTO [t22592] VALUES (1100,'(١٠١٦) - (وقوله صلى الله عليه وسلم في المحرم الذي وقصته ناقته: " ولا تخمروا رأسه فإنه يبعث يو القيامة ملبيا "','1281','1095','0');
INSERT INTO [t22592] VALUES (1101,'(١٠١٦/١) - (قول ابن عمر: (أضح لمن أحرمت له) .','1284','1095','0');
INSERT INTO [t22592] VALUES (1102,'(١٠١٧) - (حديث جابر (أن النبي صلى الله عليه وسلم امر بقبة من شعر فضربت له بنمرة فنزل بها) .','1285','1095','0');
INSERT INTO [t22592] VALUES (1103,'(١٠١٨) - (حديث أم الحصين: " حججت مع رسول الله صلى الله عليه وسلم حجة الوداع فرأيت أسامة وبلالا وأحدهما آخذ بخطام ناقة النبي صلى الله عليه وسلم والآخر رافع ثوبه يستره من الحر حتى رمى جمرة العقبة ".','1293','1095','0');
INSERT INTO [t22592] VALUES (1104,'(١٠١٨-١) - (في بعض ألفاظ حديث صاحب الراحلة: "ولا تخمروا وجهه ولا رأسه") .','1294','1095','0');
INSERT INTO [t22592] VALUES (1105,'(١٠١٩) - (حديث: " أنه صلى الله عليه وسلم غسل رأسه وهو محرم وحرك رأسه بيديه فأقبل بهما وأدبر "','1294','1095','0');
INSERT INTO [t22592] VALUES (1106,'(١٠٢٠) - (واغتسل عمر وقال: " لا يزيد الماء الشعر إلا شعثا ".','1294','1095','0');
INSERT INTO [t22592] VALUES (1107,'(١٠٢١) - (وعن ابن عباس: " قال لى عمر ونحن محرمون بالجحفة: تعالى أباقيك أينا أطول نفسا فى الماء "','1295','1095','0');
INSERT INTO [t22592] VALUES (1108,'(١٠٢٢) - (قال صلى الله عليه وسلم: " لا تنتقب المرأة المحرمة ولا تلبس القفازين ".','1295','1095','0');
INSERT INTO [t22592] VALUES (1109,'(١٠٢٣) - (ما روى عن أسماء: " أنها تغطية "','1296','1095','0');
INSERT INTO [t22592] VALUES (1110,'(١٠٢٤) - (لحديث عائشة: " كان الركبان يمرون بنا ونحن محرمات مع رسول الله صلى الله عليه وسلم فإذا حازونا [١] سدلت إحدانا جلبابها على وجهها فإذا جاوزونا كشفناه ".','1296','1095','0');
INSERT INTO [t22592] VALUES (1111,'(١٠٢٥) - (قوله فى الذى وقصته راحلته: " ولا تمسوه بطيب "','1297','1095','0');
INSERT INTO [t22592] VALUES (1112,'(١٠٢٦) - (قوله: " ولا يلبس ثوبا مسه ورس وزعفران "','1297','1095','0');
INSERT INTO [t22592] VALUES (1113,'(١٠٢٧) - (قوله صلى الله عليه وسلم: " عفى لأمتى عن الخطأ والنسيان وما استكرهوا عليه "','1297','1095','0');
INSERT INTO [t22592] VALUES (1114,'(١٠٢٨) - (حديث أبى قتادة: " أنه كان مع أصحاب له محرمين وهو لم يحرم فأبصروا حمارا وحشيا وأنا مشغول أخصف نعلى فلم يؤذنونى به , وأحبوا لو أنى أبصرته فركبت ونسيت السوط والرمح فقلت لهم ناولونى السوط والرمح فقالوا: والله لا نعينك عليه. ولما سألوا ا','1297','1095','0');
INSERT INTO [t22592] VALUES (1115,'(١٠٢٩) - (قول ابن عباس: " فى بيض النعام قيمته "','1299','1095','0');
INSERT INTO [t22592] VALUES (1116,'(١٠٣٠) - (وعن أبى هريرة مرفوعا: " فى بيض النعام ثمنه "','1300','1095','0');
INSERT INTO [t22592] VALUES (1117,'(١٠٣١) - (وحديث أبى هريرة مرفوعا: " أنه من صيد البحر "','1303','1095','0');
INSERT INTO [t22592] VALUES (1118,'(١٠٣٢) - (وعنه: " هو من صيد البحر لا جزاء فيه "','1304','1095','0');
INSERT INTO [t22592] VALUES (1119,'(١٠٣٣) - (قال ابن عباس: " هو من صيد البحر "','1304','1095','0');
INSERT INTO [t22592] VALUES (1120,'(١٠٣٤) - (عن ابن عمر قال: " هى أهون مقتول "','1304','1095','0');
INSERT INTO [t22592] VALUES (1121,'(١٠٣٥) - (وعن ابن عباس فيمن ألقاها ثم طلبها: " تلك ضالة لا تبتغى "','1305','1095','0');
INSERT INTO [t22592] VALUES (1122,'(١٠٣٦) (لحديث: " خمس فواسق يقتلن فى الحل والحرم: الحدأة والغراب والفأرة والعقرب والكلب العقور ـ وفى لفظ ـ الحية مكان العقرب ".','1305','1095','0');
INSERT INTO [t22592] VALUES (1123,'(١٠٣٧) - (لحديث عثمان أن النبى صلى الله عليه وسلم قال: " لا ينكح المحرم ولا ينكح ولا يخطب ". رواه الجماعة إلا البخارى وليس للترمذى فيه: " ولا يخطب "','1310','1095','0');
INSERT INTO [t22592] VALUES (1124,'(١٠٣٨) - (وعن أبى عطفان عن أبيه: " أن عمر فرق بينهما ـ يعنى رجلا تزوج وهو محرم ـ "','1312','1095','0');
INSERT INTO [t22592] VALUES (1125,'(١٠٣٩) - (وروى عن عمر: " فى الجراد الجزاء "','1312','1095','0');
INSERT INTO [t22592] VALUES (1126,'باب الفدية','1314','1059','0');
INSERT INTO [t22592] VALUES (1127,'(١٠٤٠) - (قوله صلى الله عليه وسلم لكعب بن عجرة: " لعلك آذاك هوام رأسك؟ قال: نعم يا رسول الله قال: احلق رأسك وصم ثلاثة أيام أو أطعم ستة مساكين أو انسك بشاة "','1314','1126','0');
INSERT INTO [t22592] VALUES (1128,'(١٠٤١) - (وقال ابن عباس فيمن وقع على امرأته فى العمرة قبل التقصير: " عليه فدية من صيام أو صدقة أو نسك "','1317','1126','0');
INSERT INTO [t22592] VALUES (1129,'(١٠٤٢) - (قال ابن عمر وعائشة:" لم يرخص فى أيام التشريق أن يصمن إلا لمن لم يجد الهدى ".','1317','1126','0');
INSERT INTO [t22592] VALUES (1130,'(١٠٤٣) - (لأن ابن عمر وابن عباس وعبد الله بن عمرو قالوا للواطئين: " أهديا هديا فإن لم تجدا فصوما ثلاثة أيام فى الحج وسبعة إذا رجعتم "','1317','1126','0');
INSERT INTO [t22592] VALUES (1131,'(١٠٤٤) - (وقول ابن عباس: " فى رجل أصاب اهله قبل أن يفيض يوم النحر ينحران جزورا بينهما وليس عليه الحج من قابل "','1318','1126','0');
INSERT INTO [t22592] VALUES (1132,'(١٠٤٥) - (قول ابن عباس: " فيمن وقع على امرأته قبل التقصير: عليه فدية من صيام أو صدقة أو نسك "','1319','1126','0');
INSERT INTO [t22592] VALUES (1133,'(١٠٤٦) - (حديث عائشة مرفوعا: " إذا رميتم وحلقتم فقد حل لكم الطيب والثياب وكل شىء إلا النساء "','1319','1126','0');
INSERT INTO [t22592] VALUES (1134,'(١٠٤٧) - (قالت عائشة: " طيبت رسول الله صلى الله عليه وسلم لإحرامه حين أحرم ولحله قبل أن يطوف بالبيت "','1320','1126','0');
INSERT INTO [t22592] VALUES (1135,'(١٠٤٨) - (قول ابن عمر: " لم يحل النبى صلى الله عليه وسلم من شىء حرم منه حتى قضى حجه ونحر هديه يوم النحر وطاف بالبيت ثم قد حل له كل شىء حرم منه ".','1324','1126','0');
INSERT INTO [t22592] VALUES (1136,'(١٠٤٩) - (حديث: " أن عمر رضى الله قضى فى حمار الوحش وبقره بقرة "','1325','1126','0');
INSERT INTO [t22592] VALUES (1137,'(١٠٥٠) - ("وفى الضبع كبش ; لأن النبى صلى الله عليه وسلم حكم فيها بذلك ".','1326','1126','0');
INSERT INTO [t22592] VALUES (1138,'(١٠٥١) - (" وقضى فيها عمر وابن عباس بكبش "','1329','1126','0');
INSERT INTO [t22592] VALUES (1139,'(١٠٥٢) - (" وفى الغزال شاة: قضى بها عمر وعلى وروى عن النبى صلى الله عليه وسلم من حديث جابر "','1329','1126','0');
INSERT INTO [t22592] VALUES (1140,'(١٠٥٣) - (" وفى اليربوع جفرة لها أربعة أشهر , روى عن عمر وابن مسعود وجابر "','1330','1126','0');
INSERT INTO [t22592] VALUES (1141,'(١٠٥٤) - (" وفى الأرنب عناق دون الجفرة يروى عن عمر: أنه قضى بذلك "','1331','1126','0');
INSERT INTO [t22592] VALUES (1142,'(١٠٥٥) - (" فى القطا والورش والفواخت شاة ". قضى به عمر وعثمان وابن عمرو وابن عباس','1331','1126','0');
INSERT INTO [t22592] VALUES (1143,'(١٠٥٦) - (وروى عن ابن عباس: " أنه قضى به فى حمام الإحرام ".','1331','1126','0');
INSERT INTO [t22592] VALUES (1144,'(١٠٥٦/١) - (" وروي عن ابن عباس وجابر أنهما قالا: "في الحجلة والقطاة والحبارى شاة شاة ".','1332','1126','0');
INSERT INTO [t22592] VALUES (1145,'(١٠٥٧) - (لحديث ابن عباس قال: قال رسول الله صلى الله عليه وسلم يوم فتح مكة: " إن هذا البلد حرمه الله يوم خلق السموات والأرض فهو حرام بحرمة الله إلى يوم القيامة ـ الحديث ـ وفيه ولا ينفر صيدها "','1332','1126','0');
INSERT INTO [t22592] VALUES (1146,'(١٠٥٨) - (" حديث على فى تحريم صيد حرم المدينة "','1334','1126','0');
INSERT INTO [t22592] VALUES (1147,'(١٠٥٩) - (وقوله: " ولا يعضد (سجرها) [١] ولا يحش حشيشها ". وفى رواية: " لا يختلى شوكها. فقال العباس: إلا الإذخر فإنه لابد لهم','1335','1126','0');
INSERT INTO [t22592] VALUES (1148,'(١٠٦٠) - (لما روى عن ابن عباس أنه قال: " فى الدوحة بقرة وفى الجزلة شاة "','1336','1126','0');
INSERT INTO [t22592] VALUES (1149,'(١٠٦١) - (لقول جابر: " كنا ننحر البدنة عن سبعة فقيل له: والبقرة؟ فقال: وهل هى إلا من البدن؟ "','1336','1126','0');
INSERT INTO [t22592] VALUES (1150,'(١٠٦٢) - (قول ابن عباس: " أتى النبى صلى الله عليه وسلم رجل فقال: إن على بدنة وأنا موسر , ولا أجدها فأشتريها , فأمره النبى صلى الله عليه وسلم أن يبتاع سبع شياة فيذبحهن "','1339','1126','0');
INSERT INTO [t22592] VALUES (1151,'باب أركان الحج وواجباته','1340','1059','0');
INSERT INTO [t22592] VALUES (1152,'(١٠٦٣) - (حديث: " إنما الأعمال بالنيات "','1340','1151','0');
INSERT INTO [t22592] VALUES (1153,'(١٠٦٤) - (حديث: " الحج عرفة "','1340','1151','0');
INSERT INTO [t22592] VALUES (1154,'(١٠٦٥) - (قول جابر: " لا يفوت الحج حتى يطلع الفجر من ليلة جمع. قال أبو الزبير: فقلت له: أقال رسول الله صلى الله عليه وسلم ذلك؟ قال: نعم "','1342','1151','0');
INSERT INTO [t22592] VALUES (1155,'(١٠٦٦) - (حديث عروة بن مضرس بن أوس بن حارثة بن لام الطائى قال: " أتيت رسول الله صلى الله عليه وسلم بالمزدلفة حين خرج إلى الصلاة فقلت: يا رسول الله إنى جئت من جبل طيىء , أكللت راحلتى وأتعبت','1342','1151','0');
INSERT INTO [t22592] VALUES (1156,'(١٠٦٧) - (حديث: " الحج عرفة , من جاء ليلة جمع قبل طلوع الفجر فقد أدرك ".','1344','1151','0');
INSERT INTO [t22592] VALUES (1157,'(١٠٦٨) - (روى: " أن عمر قال لهبار بن الأسود لما حج من الشام وقدم يوم النحر: ما حبسك؟ قال: حسبت أن اليوم عرفة , فلم يعذر بذلك "','1344','1151','0');
INSERT INTO [t22592] VALUES (1158,'(١٠٦٩) - (عن عائشة قالت: " حاضت صفية بنت حيى بعدما أفاضت. قالت: فذكرت ذلك لرسول الله صلى الله عليه وسلم فقال: أحابستنا','1344','1151','0');
INSERT INTO [t22592] VALUES (1159,'(١٠٧٠) - (قول ابن عمر: " أفاض رسول الله صلى الله عليه وسلم يوم النحر "','1347','1151','0');
INSERT INTO [t22592] VALUES (1160,'(١٠٧١) - (قول عائشة: " طاف رسول الله صلى الله عليه وسلم وطاف المسلمون ـ تعنى: بين الصفا والمروة ـ فكانت سنة فلعمرى ما أتم الله حج من لم يطف بين الصفا والمروة "','1349','1151','0');
INSERT INTO [t22592] VALUES (1161,'(١٠٧٢) - (حديث: " اسعوا فإن الله كتب عليكم السعى "','1352','1151','0');
INSERT INTO [t22592] VALUES (1162,'(١٠٧٣) - (حديث: " أن النبى صلى الله عليه وسلم وقف إلى الغروب "','1355','1151','0');
INSERT INTO [t22592] VALUES (1163,'(١٠٧٤) - (حديث: " خذوا عنى مناسككم "','1355','1151','0');
INSERT INTO [t22592] VALUES (1164,'(١٠٧٥) - (حديث: " أن النبى صلى الله عليه وسلم بات بمزدلفة , وقال: لتأخذوا عنى مناسككم ".','1356','1151','0');
INSERT INTO [t22592] VALUES (1165,'(١٠٧٦) - (عن ابن عباس قال: " كنت فيمن قدم النبى صلى الله عليه وسلم فى ضعفة أهله من مزدلفة إلى منى "','1356','1151','0');
INSERT INTO [t22592] VALUES (1166,'(١٠٧٧) - (عن عائشة قالت: " أرسل رسول الله صلى الله عليه وسلم بأم سلمة ليلة النحر فرمت الجمرة قبل الفجر ثم أفاضت "','1361','1151','0');
INSERT INTO [t22592] VALUES (1167,'(١٠٧٨) - (حديث عائشة: " ... ثم رجع إلى منى فمكث بها ليالى التشريق ... "','1363','1151','0');
INSERT INTO [t22592] VALUES (1168,'(١٠٧٩) - (حديث ابن عباس قال: " استأذن العباس رسول الله صلى الله عليه وسلم أن يبيت بمكة ليالى منى من أجل سقايته فأذن له "','1364','1151','0');
INSERT INTO [t22592] VALUES (1169,'(١٠٨٠) - (عن عاصم بن عدى: " أن النبى صلى الله عليه وسلم رخص لرعاء الإبل فى البيتوتة عن منى يرمون يوم النحر ثم يرمون من الغد ومن بعد الغد ليومين ثم يرمون يوم النفر "','1364','1151','0');
INSERT INTO [t22592] VALUES (1170,'(١٠٨١) - (حديث: " ... أن النبى صلى الله عليه وسلم بدأ برمى جمرة العقبة "','1365','1151','0');
INSERT INTO [t22592] VALUES (1171,'(١٠٨٢) - (حديث عائشة: " أن النبى صلى الله عليه وسلم رجع إلى منى فمكث بها ليالى أيام التشريق يرمى الجمرة إذا زالت الشمس , كل جمرة بسبع حصيات , يكبر مع كل حصاة يقف عند الأولى والثانية فيطيل القيام ويتضرع ويرمى الثالثة ولا يقف عندها ".','1366','1151','0');
INSERT INTO [t22592] VALUES (1172,'(١٠٨٣) - (حديث: " ... فيقصر ثم ليحلل "','1367','1151','0');
INSERT INTO [t22592] VALUES (1173,'(١٠٨٤) - (حديث: " دعا للمحلقين ثلاثا , وللمقصرين مرة "','1367','1151','0');
INSERT INTO [t22592] VALUES (1174,'(١٠٨٥) - (حديث أنس: " أن النبى صلى الله عليه وسلم أتى منى فأتى الجمرة فرماها , ثم أتى منزله بمنى ونحر , ثم قال للحلاق: خذ , وأشار إلى جانبه الأيمن ثم الأيسر وجعل يعطيه الناس "','1371','1151','0');
INSERT INTO [t22592] VALUES (1175,'(١٠٨٦) - (حديث ابن عباس: " أمر الناس أن يكون آخر عهدهم بالبيت , إلا أنه خفف عن المرأة الحائض "','1372','1151','0');
INSERT INTO [t22592] VALUES (1176,'(١٠٨٧) - (حديث: " إنما الأعمال بالنيات "','1374','1151','0');
INSERT INTO [t22592] VALUES (1177,'(١٠٨٨) - (حديث: " اسعوا فإن الله كتب عليكم السعى "','1374','1151','0');
INSERT INTO [t22592] VALUES (1178,'(١٠٨٩) - (عن ابن عمر أن النبى صلى الله عليه وسلم قال: " من لم يكن معه هدى فليطف بالبيت , وبين الصفا والمروة وليقصر وليحلل ".','1374','1151','0');
INSERT INTO [t22592] VALUES (1179,'(١٠٩٠) - (حديث: " أمره صلى الله عليه وسلم عائشة أن تعتمر من التنعيم "','1374','1151','0');
INSERT INTO [t22592] VALUES (1180,'(١٠٩١) - (حديث: " وليقصر وليحلل "','1375','1151','0');
INSERT INTO [t22592] VALUES (1181,'(١٠٩٢) - (حديث: " بات بمنى ليلة عرفة "','1375','1151','0');
INSERT INTO [t22592] VALUES (1182,'(١٠٩٣) - (حديث عائشة: " أن النبى صلى الله عليه وسلم حين قدم مكة توضأ ثم طاف بالبيت "','1375','1151','0');
INSERT INTO [t22592] VALUES (1183,'(١٠٩٤) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم وأصحابه اعتمروا من الجعرانة فرملوا بالبيت وجعلوا أرديتهم تحت آباطهم ثم قذفوها على عواتقهم اليسرى ".','1376','1151','0');
INSERT INTO [t22592] VALUES (1184,'(١٠٩٥) - (حديث جابر: " ... حتى أتينا البيت معه استلم الركن فرمل ثلاثا ومشى أربعا "','1377','1151','0');
INSERT INTO [t22592] VALUES (1185,'(١٠٩٦) - (حديث ابن عمر: " وليحرم أحدكم فى إزار ورداء ونعلين "','1377','1151','0');
INSERT INTO [t22592] VALUES (1186,'(١٠٩٧) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم كان إذا استوت به راحلته قائمة عند مسجد ذى الحليفة أهل فقال: لبيك اللهم لبيك ... "','1378','1151','0');
INSERT INTO [t22592] VALUES (1187,'(١٠٩٨) - (عن الفضل بن عباس قال: " كنت رديف النبى صلى الله عليه وسلم من جمع إلى منى فلم يزل يلبى حتى رمى جمرة العقبة "','1379','1151','0');
INSERT INTO [t22592] VALUES (1188,'(١٠٩٩) - (عن ابن عباس مرفوعا قال: (يلبي المعتمر حتى','1380','1151','0');
INSERT INTO [t22592] VALUES (1189,'(١١٠٠) - (حديث ابن عباس: " من ترك نسكا فعليه دم "','1383','1151','0');
INSERT INTO [t22592] VALUES (1190,'فصل','1384','1059','0');
INSERT INTO [t22592] VALUES (1191,'(١١٠١) - (حديث: " لا يطوف بالبيت عريان "','1384','1190','0');
INSERT INTO [t22592] VALUES (1192,'(١١٠٢) - (حديث ابن عباس: أن النبى صلى الله عليه وسلم قال: " الطواف بالبيت صلاة , إلا أنكم تتكلمون فيه "','1388','1190','0');
INSERT INTO [t22592] VALUES (1193,'(١١٠٣) - (حديث عائشة لما حاضت: " افعلى ما يفعل الحاج غير أن لا تطوفى بالبيت حتى تطهرى "','1388','1190','0');
INSERT INTO [t22592] VALUES (1194,'(١١٠٤) - (حديث: " أن النبى صلى الله عليه وسلم طاف سبعا "','1388','1190','0');
INSERT INTO [t22592] VALUES (1195,'(١١٠٥) - (حديث: " خذوا عنى مناسككم "','1389','1190','0');
INSERT INTO [t22592] VALUES (1196,'(١١٠٦) - (حديث: " الحجر من البيت "','1389','1190','0');
INSERT INTO [t22592] VALUES (1197,'(١١٠٧) - (حديث جابر: " أن النبى صلى الله عليه وسلم أتى الحجر فاستلمه ثم مشى على يمينه فرمل ثلاثا ومشى أربعا "','1391','1190','0');
INSERT INTO [t22592] VALUES (1198,'(١١٠٨) - (حديث: " الطواف بالبيت صلاة "','1391','1190','0');
INSERT INTO [t22592] VALUES (1199,'(١١٠٩) - (حديث: " إذا أقيمت الصلاة , فلا صلاة إلا المكتوبة "','1392','1190','0');
INSERT INTO [t22592] VALUES (1200,'(١١١٠) - (حديث ابن عمر: " كان رسول الله صلى الله عليه وسلم لا يدع أن يستلم الركن اليمانى والحجر فى كل طوافة " ـ قال نافع: " وكان ابن عمر يفعله ".','1392','1190','0');
INSERT INTO [t22592] VALUES (1201,'(١١١١) - (عن عمر: " أن النبى صلى الله عليه وسلم استقبل الحجر , ووضع شفتيه عليه يبكى طويلا , ثم التفت فإذا بعمر بن الخطاب يبكى فقال: يا عمر ها هنا تسكب العبرات "','1392','1190','0');
INSERT INTO [t22592] VALUES (1202,'(١١١٢) - (" السجود على الحجر فعله ابن عمر وابن عباس "','1393','1190','0');
INSERT INTO [t22592] VALUES (1203,'(١١١٣) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم استلمه بيده وقبل يده ".','1396','1190','0');
INSERT INTO [t22592] VALUES (1204,'(١١١٤) - (عن أبى الطفيل عامر بن واثلة قال: " رأيت رسول الله صلى الله عليه وسلم يطوف بالبيت ويستلم الركن بمحجن معه ويقبل المحجن "','1397','1190','0');
INSERT INTO [t22592] VALUES (1205,'(١١١٥) - (حديث: " قيل للزهرى: إن عطاء يقول: تجزئه المكتوبة من ركعتى الطواف , فقال: السنة أفضل , لم يطف النبى صلى الله عليه وسلم','1397','1190','0');
INSERT INTO [t22592] VALUES (1206,'(١١١٦) - (حديث: " إن النبى صلى الله عليه وسلم والى بين السعى "','1398','1190','0');
INSERT INTO [t22592] VALUES (1207,'(١١١٧) - (روى: " أن سودة بنت عبد الله بن عمر تمتعت فقضت طوافها فى ثلاثة أيام "','1398','1190','0');
INSERT INTO [t22592] VALUES (1208,'(١١١٨) - (حديث: " أن النبى صلى الله عليه وسلم سعى راكبا "','1398','1190','0');
INSERT INTO [t22592] VALUES (1209,'(١١١٩) - (حديث: " أن النبى صلى الله عليه وسلم سعى بعد الطواف , وقال: خذوا عنى مناسككم ".','1400','1190','0');
INSERT INTO [t22592] VALUES (1210,'(١١٢٠) - (حديث جابر:" أن النبى صلى الله عليه وسلم لما دنا من الصفا قرأ (إن الصفا والمروة من شعائر الله) أبدأ بما بدأ الله به , فبدأ بالصفا فرقى عليه.. ". الحديث رواه مسلم ولفظ النسائى: " ابدءوا بما بدأ الله به "','1400','1190','0');
INSERT INTO [t22592] VALUES (1211,'(١١٢١) - (حديث: " أنه صلى الله عليه وسلم قال لعائشة لما حاضت: افعلى ما يفعل الحاج , غير أن لا تطوفى بالبيت حتى تطهرى "','1403','1190','0');
INSERT INTO [t22592] VALUES (1212,'(١١٢٢) - (قالت عائشة: " إذا طافت المرأة بالبيت ثم صلت ركعتين ثم حاضت فلتطف بالصفا والمروة "','1403','1190','0');
INSERT INTO [t22592] VALUES (1213,'(١١٢٣) - (حديث جابر: " ماء زمزم لما شرب له "','1404','1190','0');
INSERT INTO [t22592] VALUES (1214,'(١١٢٤) - (حديث جابر: " أن النبى صلى الله عليه وسلم دعا بسجل من ماء زمزم فشرب منه وتوضأ "','1409','1190','0');
INSERT INTO [t22592] VALUES (1215,'(١١٢٥) - (عن ابن عباس مرفوعا: " إن آية ما بيننا وبين المنافقين [أنهم] لا يتضلعون من ماء زمزم ".','1409','1190','0');
INSERT INTO [t22592] VALUES (1216,'(١١٢٦) - (حديث ابن عباس أن رسول الله صلى الله عليه وسلم قال: " ماء زمزم لما شرب له , إن شربته تستشفى به شفاك الله , وإن شربته يشبعك أشبعك الله به , وإن شربته لقطع ظمئك قطعه الله , وهى هزمة جبريل وسقيا [الله] إسماعيل "','1413','1190','0');
INSERT INTO [t22592] VALUES (1217,'(١١٢٧) - (حديث: " من زارنى أو زار قبرى كنت له شافعا أو شهيدا ".','1417','1190','0');
INSERT INTO [t22592] VALUES (1218,'(١١٢٨) - (عن ابن عمر مرفوعا: " من حج فزار قبرى بعد','1419','1190','0');
INSERT INTO [t22592] VALUES (1219,'(١١٢٩) - (حديث جابر أن النبى صلى الله عليه وسلم قال: " صلاة فى مسجدى هذا أفضل من ألف صلاة فى سواه إلا المسجد الحرام , فصلاة فى المسجد الحرام أفضل من مئة ألف صلاة [فيما سواه] ".','1425','1190','0');
INSERT INTO [t22592] VALUES (1220,'(١١٣٠) - (عن أبى الدرداء مرفوعا: " الصلاة فى المسجد الحرام بمائة ألف صلاة , والصلاة فى مسجدى بألف صلاة , والصلاة فى بيت المقدس بخمس مائة صلاة ".','1426','1190','0');
INSERT INTO [t22592] VALUES (1221,'باب الفوات والإحصار','1428','1059','0');
INSERT INTO [t22592] VALUES (1222,'(١١٣١) - (حديث جابر: " لا يفوت الحج حتى يطلع الفجر من ليلة جمع "','1428','1221','0');
INSERT INTO [t22592] VALUES (1223,'(١١٣٢) - (عن عمر بن الخطاب: " أنه أمر أبا أيوب صاحب رسول الله صلى الله عليه وسلم وهبار بن الأسود حين فاتهما الحج , فأتيا يوم النحر أن يحلا بعمرة ثم يرجعا حلالا ثم يحجا عاما قابلا ويهديا , فمن لم يجد فصيام ثلاثة أيام فى الحج وسبعة إذا رجع إلى أهله "','1428','1221','0');
INSERT INTO [t22592] VALUES (1224,'(١١٣٣) - (" وللنجاد عن عطاء مرفوعا نحوه "','1429','1221','0');
INSERT INTO [t22592] VALUES (1225,'(١١٣٤) - (وللدارقطنى عن ابن عباس مرفوعا: " من فاته عرفات فقد فاته الحج وليتحلل بعمرة , وعليه الحج من قابل "','1429','1221','0');
INSERT INTO [t22592] VALUES (1226,'(١١٣٥) - (حديث ابن عمر: " أن رسول الله صلى الله عليه وسلم خرج معتمرا','1430','1221','0');
INSERT INTO [t22592] VALUES (1227,'(١١٣٦) - (روى عن ابن عمر أنه قال: " من حبس دون البيت بمرض فإنه لا يحل حتى يطوف بالبيت ". رواه مالك (ص ٢٧٠) .','1432','1221','0');
INSERT INTO [t22592] VALUES (1228,'باب الأضحية','1432','1059','0');
INSERT INTO [t22592] VALUES (1229,'(١١٣٧) - (حديث أنس: " ضحى النبى صلى الله عليه وسلم بكبشين أملحين أقرنين ذبحهما بيده وسمى وكبر "','1432','1228','0');
INSERT INTO [t22592] VALUES (1230,'(١١٣٨) - (حديث: " إن الرسول الله صلى الله عليه وسلم ضحى عمن لم يضح من أمته ".','1433','1228','0');
INSERT INTO [t22592] VALUES (1231,'(١١٣٩) - (روى عن أبى بكر وعمر: " أنهما كانا لا يضحيان عن أهلهما مخافة أن يرى ذلك واجبا "','1438','1228','0');
INSERT INTO [t22592] VALUES (1232,'(١١٤٠) - (حديث: " من نذر أن يطيع الله فليطعه "','1439','1228','0');
INSERT INTO [t22592] VALUES (1233,'(١١٤١) - (حديث أبى هريرة: " من اغتسل يوم الجمعة غسل الجنابة ثم راح فى الساعة الأولى فكأنما قرب بدنة , ومن راح فى الساعةالثانية فكأنما قرب بقرة , ومن راح فى الساعة الثالثة فكأنما قرب كبشا أقرن "','1439','1228','0');
INSERT INTO [t22592] VALUES (1234,'(١١٤٢) - (قول أبى أيوب: " كان الرجل فى عهد النبى صلى الله عليه وسلم يضحى بالشاة عنه وعن أهل بيته , فيأكلون ويطعمون , حتى تباهى الناس فصار كما ترى ".','1439','1228','0');
INSERT INTO [t22592] VALUES (1235,'(١١٤٣) - (قول أبى هريرة: " سمعت رسول الله صلى الله عليه وسلم يقول: نعم , أو: نعمت الأضحية الجذع من الضأن "','1440','1228','0');
INSERT INTO [t22592] VALUES (1236,'(١١٤٤) - (وفى حديث عقبة بن عامر: " فقلت يا رسول الله: أصابنى جذع , قال: ضح به ".','1440','1228','0');
INSERT INTO [t22592] VALUES (1237,'(١١٤٥) - (حديث: " لا تذبحوا إلا مسنة , فإن عز عليكم , فاذبحوا الجذع من الضأن ".','1442','1228','0');
INSERT INTO [t22592] VALUES (1238,'(١١٤٦) - (وعن مجاشع مرفوعا: " إن الجذع يوفى مما يوفى منه الثنية ".','1443','1228','0');
INSERT INTO [t22592] VALUES (1239,'(١١٤٧) - (عن أبى رافع قال: " ضحى رسول الله صلى الله عليه وسلم بكبشين أملحين موجوءين خصيين "','1444','1228','0');
INSERT INTO [t22592] VALUES (1240,'(١١٤٨) - (حديث البراء بن عازب: " أربع لا تجوز فى الأضاحى: العوراء البين عورها , والمريضة البين مرضها , والعرجاء البين ظلعها','1444','1228','0');
INSERT INTO [t22592] VALUES (1241,'(١١٤٩) - (حديث على: " نهى رسول الله صلى الله عليه وسلم , أن يضحى بأعضب الأذن والقرن "','1445','1228','0');
INSERT INTO [t22592] VALUES (1242,'(١١٥٠) - (عن ابن عمر: " أنه أتى على رجل قد أناخ بدنته ينحرها فقال: ابعثها قياما [مقيدة] سنة محمد , صلى الله عليه وسلم "','1448','1228','0');
INSERT INTO [t22592] VALUES (1243,'(١١٥١) - (حديث: " ضحى النبى صلى الله عليه وسلم بكبشين ذبحهما بيده ".','1449','1228','0');
INSERT INTO [t22592] VALUES (1244,'(١١٥٢) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم , ذبح يوم العيد كبشين ـ وفيه ـ ثم قال: بسم الله والله أكبر , اللهم هذا منك ولك "','1450','1228','0');
INSERT INTO [t22592] VALUES (1245,'(١١٥٣) - (حديث أنس قال: قال رسول الله صلى الله عليه وسلم , يوم النحر: " من كان ذبح قبل الصلاة فليعد "','1450','1228','0');
INSERT INTO [t22592] VALUES (1246,'(١١٥٤) - (وللبخارى: " من ذبح بعد الصلاة فقد تم نسكه , وأصاب سنة المسلمين "','1450','1228','0');
INSERT INTO [t22592] VALUES (1247,'(١١٥٥) - (حديث: أنه صلى الله عليه وسلم: " نهى عن ادخار لحوم الأضاحى فوق ثلاث ".','1452','1228','0');
INSERT INTO [t22592] VALUES (1248,'(١١٥٦) - (وقال جابر: " كنا لا نأكل من بدننا فوق ثلاث [منى] , فرخص لنا النبى صلى الله عليه وسلم! فقال: كلوا وتزودوا , فأكلنا وتزودنا ".','1453','1228','0');
INSERT INTO [t22592] VALUES (1249,'(١١٥٧) - (حديث جابر: " أن النبى صلى الله عليه وسلم أشرك عليا فى هديه قال: ثم أمر من كل بدنة ببضعة , فجعلت فى قدر فأكلا منها وشربا حسيا من مرقها ".','1455','1228','0');
INSERT INTO [t22592] VALUES (1250,'(١١٥٨) - (حديث ثوبان: " ذبح رسول الله صلى الله عليه وسلم , أضحيته , ثم قال: يا ثوبان , أصلح لى لحم هذه , فلم أزل أطعمه منه حتى قدم المدينة ".','1456','1228','0');
INSERT INTO [t22592] VALUES (1251,'(١١٥٩) - (حديث: " أن أزواج النبى صلى الله عليه وسلم , تمتعن معه فى حجة','1456','1228','0');
INSERT INTO [t22592] VALUES (1252,'(١١٦٠) - (حديث ابن عباس مرفوعا: " ويطعم أهل بيته , الثلث , ويطعم فقراء جيرانه الثلث , ويتصدق على السؤال بالثلث "','1458','1228','0');
INSERT INTO [t22592] VALUES (1253,'(١١٦١) - (حديث على: " أمرنى رسول الله صلى الله عليه وسلم , أن أقوم على بدنة [١] وأن أقسم جلودها وجلالها , ولا أعطى الجازر منها شيئا , وقال نحن نعطيه من عندنا " متفق على (ص ٢٧٦) .','1459','1228','0');
INSERT INTO [t22592] VALUES (1254,'(١١٦٢) - (حديث: " لا تعط فى جزارتها شيئا منها "','1459','1228','0');
INSERT INTO [t22592] VALUES (1255,'(١١٦٣) - (حديث أم سلمة أن النبى صلى الله عليه وسلم , قال: " إذا دخل العشر , وأراد أحدكم أن يضحى فلا يأخذ من شعره ولا من أظفاره شيئا حتى يضحى "','1460','1228','0');
INSERT INTO [t22592] VALUES (1256,'فصل فى العقيقة','1463','1059','0');
INSERT INTO [t22592] VALUES (1257,'(١١٦٤) - (حديث: " لأنه صلى الله عليه وسلم عق عن الحسن والحسين "','1463','1256','0');
INSERT INTO [t22592] VALUES (1258,'(١١٦٥) - (وقال صلى الله عليه وسلم: " كل غلام رهينة بعقيقته , تذبح عنه يوم سابعه , ويسمى فيه , ويحلق رأسه "','1469','1256','0');
INSERT INTO [t22592] VALUES (1259,'(١١٦٦) - (حديث عائشة مرفوعا: " عن الغلام شاتان مكافئتان , وعن الجارية شاة ".','1473','1256','0');
INSERT INTO [t22592] VALUES (1260,'(١١٦٧) - (حديث ابن عباس: "إن النبي صلى الله عليه وسلم عق عن الحسن والحسين كبشا كبشا")','1477','1256','0');
INSERT INTO [t22592] VALUES (1261,'(١١٦٨) - (حديث أنس مرفوعا: " يعق عنه من الإبل والبقر والغنم ".','1477','1256','0');
INSERT INTO [t22592] VALUES (1262,'(١١٦٩) - (حديث سمرة مرفوعا: " كل غلام رهينة بعقيقته تذبح عنه يوم سابعه , ويسمى فيه ويحلق رأسه ".','1478','1256','0');
INSERT INTO [t22592] VALUES (1263,'(١١٧٠) - (حديث بريدة , عن النبى صلى الله عليه وسلم قال فى العقيقة:','1478','1256','0');
INSERT INTO [t22592] VALUES (1264,'(١١٧١) - (" أهرقوا عنه دما وأميطوا عنه الأذى "','1480','1256','0');
INSERT INTO [t22592] VALUES (1265,'(١١٧٢) - (عن بريدة: " كنا نلطخ رأس الصبى بدم العقيقة , فلما جاء الإسلام كنا نلطخه بزعفران "','1484','1256','0');
INSERT INTO [t22592] VALUES (1266,'(١١٧٣) - (قول أبى رافع: " رأيت رسول الله صلى الله عليه وسلم , أذن فى أذن الحسن حين ولدته فاطمة بالصلاة "','1484','1256','0');
INSERT INTO [t22592] VALUES (1267,'(١١٧٤) - (روى ابن السنى عن الحسن بن على مرفوعا: " من ولد له ولد فأذن فى أذنه اليمنى وأقام فى اليسرى لم تضره أم الصبيان "','1485','1256','0');
INSERT INTO [t22592] VALUES (1268,'(١١٧٥) - (وقال صلى الله عليه وسلم لفاطمة لما ولدت الحسن: " احلقى رأسه وتصدقى بوزن شعره فضة على المساكين ".','1486','1256','0');
INSERT INTO [t22592] VALUES (1269,'(١١٧٦) - (حديث: " أحب الأسماء عبد الله وعبد الرحمن "','1490','1256','0');
INSERT INTO [t22592] VALUES (1270,'(١١٧٧) - (حديث سمرة مرفوعا: " لا تسمين غلامك يسارا ولا رباحا ولا نجيحا ولا أفلح , فإنك تقول: أثم هو فلا يكون , فيقول: لا "','1491','1256','0');
INSERT INTO [t22592] VALUES (1271,'(١١٧٨) - (حديث [أبى] (١) وهب الجشمى مرفوعا: " تسموا بأسماء الأنبياء ".','1492','1256','0');
INSERT INTO [t22592] VALUES (1272,'(١١٧٩) - (حديث عائشة: " تطبخ جدولا ولا يكسر لها عظم "','1493','1256','0');
INSERT INTO [t22592] VALUES (1273,'(١١٨٠) - (حديث أبى هريرة مرفوعا: " لا فرع ولا عتيرة "','1493','1256','0');
INSERT INTO [t22592] VALUES (1274,'(١١٨١) - (حديث الحارث بن عمرو: " أنه لقى رسول الله صلى الله عليه وسلم , فى حجة الوداع , قال: فقال رجل: يا رسول الله, الفرائع والعتائر؟ قال: من شاء فرع ومن شاء لم يفرع , ومن شاء عتر ومن شاء لم يعتر , فى الغنم الأضحية ".','1494','1256','0');
INSERT INTO [t22592] VALUES (1275,'كتاب الجهاد','1498','0','0');
INSERT INTO [t22592] VALUES (1276,'[الأحاديث ١١٨٢ - ١٢٢٠]','1498','1275','0');
INSERT INTO [t22592] VALUES (1277,'(١١٨٢) - (حديث أنس أن النبى صلى الله عليه وسلم , قال: " لغدوة أو روحة فى سبيل الله خير من الدنيا وما فيها "','1498','1276','0');
INSERT INTO [t22592] VALUES (1278,'(١١٨٣) - (وعن أبى عبس الحارثى مرفوعا: " من اغبرت قدماه فى سبيل الله حرمه الله على النار ".','1499','1276','0');
INSERT INTO [t22592] VALUES (1279,'(١١٨٤) - (وعن ابن أبى أوفى مرفوعا: " إن الجنة تحت ظلال السيوف ".','1501','1276','0');
INSERT INTO [t22592] VALUES (1280,'(١١٨٥) - (حديث عائشة: " قلت: يا رسول الله , هل على النساء جهاد؟ قال: جهاد لا قتال فيه , الحج والعمرة ".','1502','1276','0');
INSERT INTO [t22592] VALUES (1281,'(١١٨٦) - (عن ابن عمر قال: " عرضت على رسول الله صلى الله عليه وسلم يوم أحد وأنا ابن أربع عشرة سنة فلم يجزنى " متفق عليه (ص ٢٨٣) . وفى لفظ: " وعرضت عليه يوم الخندق فأجازنى ".','1503','1276','0');
INSERT INTO [t22592] VALUES (1282,'(١١٨٧) - (قوله صلى الله عليه وسلم: " إذا استنفرتكم فانفروا "','1503','1276','0');
INSERT INTO [t22592] VALUES (1283,'(١١٨٨) - (حديث: " أن عليا رضى الله عنه , شيع النبى صلى الله عليه وسلم , فى غزوة تبوك "','1506','1276','0');
INSERT INTO [t22592] VALUES (1284,'(١١٨٩) - (عن سهل بن معاذ عن أبيه عن النبى صلى الله عليه وسلم , أنه قال: " لأن أشيع غازيا , فأكنفه على رحله (١) غدوة أو روحة أحب إلى من الدنيا وما فيها ".','1507','1276','0');
INSERT INTO [t22592] VALUES (1285,'(١١٩٠) - (وعن أبى بكر الصديق: أنه شيع يزيد بن أبى سفيان حين بعثه إلى الشام ... الخبر وفيه: إنى أحتسب خطاى هذه فى سبيل الله ".','1508','1276','0');
INSERT INTO [t22592] VALUES (1286,'(١١٩١) - (حديث: " أن النبى صلى الله عليه وسلم شيع النفر الذين وجههم إلى كعب بن الأشرف إلى بقيع الغرقد "','1509','1276','0');
INSERT INTO [t22592] VALUES (1287,'(١١٩٢) - (حديث السائب بن يزيد قال: " لما قدم رسول الله صلى الله عليه وسلم من غزوة تبوك خرج الناس يتلقونه من ثنية الوداع , قال السائب: فخرجت مع الناس وأنا غلام ".','1510','1276','0');
INSERT INTO [t22592] VALUES (1288,'(١١٩٣) - (عن أبى سعيد الخدرى قال: " قيل: يا رسول الله , أى الناس أفضل؟ قال: مؤمن يجاهد فى سبيل الله بنفسه وماله "','1511','1276','0');
INSERT INTO [t22592] VALUES (1289,'(١١٩٤) - (حديث أم حرام مرفوعا: " المائد فى البحر (١) ـ الذى يصيبه القيء ـ له أجر شهيد , والغرق له أجر شهيدين "','1511','1276','0');
INSERT INTO [t22592] VALUES (1290,'(١١٩٥) - (وعن أبى أمامة سمعت رسول الله صلى الله عليه وسلم يقول: " شهيد البحر مثل شهيدى البر , والمائد فى البحر كالمتشحط فى دمه فى البر وما بين الموجتين كقاطع الدنيا فى طاعه الله وأن الله وكل ملك الموت بقبض الأرواح , إلا شهداء البحر فإنه يتولى يقب','1512','1276','0');
INSERT INTO [t22592] VALUES (1291,'(١١٩٦) - (حديث عبد الله بن عمرو (١) : " أن رسول الله صلى الله عليه وسلم , قال: يغفر الله للشهيد كل ذنب إلا الدين "','1512','1276','0');
INSERT INTO [t22592] VALUES (1292,'(١١٩٧) - (حديث أبى قتادة وفيه: " أرأيت إن قتلت فى سبيل الله تكفر عنى خطاياى؟ فقال صلى الله عليه وسلم , نعم وأنت صابر محتسب مقبل غير مدبر , إلا الدين فإن جبريل قال لى ذلك ".','1513','1276','0');
INSERT INTO [t22592] VALUES (1293,'(١١٩٨) - (حديث ابن مسعود: " سألت رسول الله صلى الله عليه وسلم: أى العمل أحب إلى الله؟ قال: الصلاة على وقتها , قلت: ثم أى؟ قال: بر الوالدين. قلت: ثم أى؟ قال: الجهاد فى سبيل الله "','1514','1276','0');
INSERT INTO [t22592] VALUES (1294,'(١١٩٩) - (وعن ابن عمرو (١) قال: " جاء رجل إلى النبى صلى الله عليه وسلم , فاستأذنه فى الجهاد , فقال: أحى والداك؟ قال: نعم. قال: ففيهما فجاهد ".','1514','1276','0');
INSERT INTO [t22592] VALUES (1295,'(١٢٠٠) - (لحديث سلمان مرفوعا: " رباط ليلة فى سبيل الله خير من صيام شهر , وقيامه , فإن مات أجرى عليه عمله الذى كان يعمله , وأجرى عليه رزقه , وأمن الفتان ".','1517','1276','0');
INSERT INTO [t22592] VALUES (1296,'(١٢٠١) - (ويروى عن النبى صلى الله عليه وسلم أنه قال: " تمام الرباط أربعون يوما ".','1518','1276','0');
INSERT INTO [t22592] VALUES (1297,'(١٢٠٢) - (وعن النبى صلى الله عليه وسلم: " الفرار من الزحف من الكبائر "','1519','1276','0');
INSERT INTO [t22592] VALUES (1298,'(١٢٠٣) - (حديث ابن عمر , وفيه: " فلما خرج رسول الله صلى الله عليه وسلم , قبل الصلاة قمنا فقلنا له: نحن الفرارون؟ فقال: لا بل أنتم العكارون. أنا فئة كل مسلم ".','1522','1276','0');
INSERT INTO [t22592] VALUES (1299,'(١٢٠٤) - (وعن عمر قال: " أنا فئة كل مسلم "','1523','1276','0');
INSERT INTO [t22592] VALUES (1300,'(١٢٠٥) - (وعن عمر أيضا , قال: " لو أن أبا عبيدة تحيز إلى , لكنت له فئة وكان أبو عبيدة فى العراق ".','1523','1276','0');
INSERT INTO [t22592] VALUES (1301,'(١٢٠٦) - (وقال ابن عباس: " من فر من اثنين فقد فر , ومن فر من ثلاثة فما فر "','1523','1276','0');
INSERT INTO [t22592] VALUES (1302,'(١٢٠٧) - (أنا برىء من [كل] مسلم [يقيم] بين أظهر المشركين , لا تراءى نارهما ".','1524','1276','0');
INSERT INTO [t22592] VALUES (1303,'(١٢٠٨) - (وعن معاوية وغيره مرفوعا: " لا تنقطع الهجرة حتى تنقطع التوبة ولا تنقطع التوبة حتى تطلع الشمس من مغربها "','1528','1276','0');
INSERT INTO [t22592] VALUES (1304,'(١٢٠٩) - (حديث: " لا هجرة بعد الفتح ".','1529','1276','0');
INSERT INTO [t22592] VALUES (1305,'(١٢١٠) - (حديث: " نهى عن قتل النساء والصبيان "','1529','1276','0');
INSERT INTO [t22592] VALUES (1306,'(١٢١١) - (حديث " سبي هوازن ".','1531','1276','0');
INSERT INTO [t22592] VALUES (1307,'(١٢١٢) - (حديث: " عائشة فى سبايا بنى المصطلق "','1532','1276','0');
INSERT INTO [t22592] VALUES (1308,'(١٢١٣) - (حديث: " قتل النبى صلى الله عليه وسلم , رجالا من بنى قريظة وهم بين الست مائة والسبع مائة ".','1533','1276','0');
INSERT INTO [t22592] VALUES (1309,'(١٢١٤) - (حديث: " أنه صلى الله عليه وسلم قتل يوم بدر النضر بن الحارث , وعقبة بن أبى معيط صبرا ".','1534','1276','0');
INSERT INTO [t22592] VALUES (1310,'(١٢١٥) - (حديث: " أنه صلى الله عليه وسلم قتل يوم أحد أبا عزة الجمحى "','1536','1276','0');
INSERT INTO [t22592] VALUES (1311,'(١٢١٦) - (حديث: " أنه صلى الله عليه وسلم , من على ثمامة بن أثال ".','1536','1276','0');
INSERT INTO [t22592] VALUES (1312,'(١٢١٦/١) - (حديث: "أنه صلى الله عليه وسلم من على أبي عزة الشاعر"','1537','1276','0');
INSERT INTO [t22592] VALUES (1313,'(١٢١٦/٢) - (حديث " أنه صلى الله عليه وسلم " من على أبي العاص بن الربيع ") .','1538','1276','0');
INSERT INTO [t22592] VALUES (1314,'(١٢١٧) - (حديث: " أنه صلى الله عليه وسلم , فدى رجلين من أصحابه برجل من المشركين من بنى عقيل "','1538','1276','0');
INSERT INTO [t22592] VALUES (1315,'(١٢١٨) - (حديث: " أنه صلى الله عليه وسلم فدى أهل بدر بمال "','1539','1276','0');
INSERT INTO [t22592] VALUES (1316,'(١٢١٩) - (روى: " أن عمر بن الخطاب رضى الله عنه , كتب إلى أمراء الأنصار ينهاهم عنه , يعنى بيع المسترق الكافر لكافر "','1544','1276','0');
INSERT INTO [t22592] VALUES (1317,'(١٢٢٠) - (حديث: " كل مولود يولد على الفطرة , فأبواه يهودانه , أو ينصرانه , أو يمجسانه "','1544','1276','0');
INSERT INTO [t22592] VALUES (1318,'فصل','1546','1275','0');
INSERT INTO [t22592] VALUES (1319,'(١٢٢١) - (حديث: إن الرسول الله صلى الله عليه وسلم قال يوم حنين: " من قتل رجلا فله سلبه. فقتل أبو طلحة يومئذ عشرين رجلا وأخذ أسلابهم ".','1546','1318','0');
INSERT INTO [t22592] VALUES (1320,'(١٢٢٢) - (حديث سلمة بن الأكوع وفيه: " قال: ثم تقدمت حتى أخذت بخطام الجمل فأنخته , فضربت رأس الرجل فندر ثم جئت بالجمل أقوده عليه رحله وسلاحه , فاستقبلنى رسول الله صلى الله عليه وسلم والناس معه فقال: من قتل الرجل؟ فقالوا: ابن الأكوع , قال: له سلبه','1549','1318','0');
INSERT INTO [t22592] VALUES (1321,'(١٢٢٣) - (روى عوف بن مالك وخالد بن الوليد: " أن رسول الله صلى الله عليه وسلم قضى بالسلب للقاتل ولم يخمس السلب ".','1550','1318','0');
INSERT INTO [t22592] VALUES (1322,'(١٢٢٤) - (وبارز البراء مرزبان الزارة فقتله فبلغ سواره ومنطقته ثلاثين ألفا فخمسه عمر ودفعه إليه "','1552','1318','0');
INSERT INTO [t22592] VALUES (1323,'(١٢٢٥) - (أن النبى صلى الله عليه وسلم قسم الغنائم كذلك - يعنى: فأعطى الغانمين أربعة أخماسها - "','1553','1318','0');
INSERT INTO [t22592] VALUES (1324,'(١٢٢٦) - (عن ابن عمر: " أن رسول الله صلى الله عليه وسلم أسهم يوم خيبر للفارس ثلاثة أسهم , سهمان لفرسه وسهم له "','1555','1318','0');
INSERT INTO [t22592] VALUES (1325,'(١٢٢٧) - (عن ابن عباس: " أن النبى صلى الله عليه وسلم أعطى الفارس ثلاثة أسهم وأعطى الراجل سهما "','1558','1318','0');
INSERT INTO [t22592] VALUES (1326,'(١٢٢٨) - (حديث ابن الأقمر (١) قال: " أغارت الخيل على الشام فأدركت العراب من يومها وأدركت الكودان ضحى الغد , وعلى الخيل رجل من همدان يقال له: المنذر بن أبى حميضة (٢) فقال: لا أجعل التى أدركت من يومها مثل التى لم تدرك (ففصل) [١] الخيل فقال عمر: هبلت','1559','1318','0');
INSERT INTO [t22592] VALUES (1327,'(١٢٢٩) - (عن مكحول: " أن النبى صلى الله عليه وسلم أعطى الفرس العربى سهمين وأعطى الهجين سهما "','1560','1318','0');
INSERT INTO [t22592] VALUES (1328,'(١٢٣٠) - (روى الأوزاعى: " أن رسول الله صلى الله عليه وسلم كان يسهم للخيل وكان لا يسهم للرجل فوق فرسين وإن كان معه عشرة أفراس "','1561','1318','0');
INSERT INTO [t22592] VALUES (1329,'(١٢٣١) - (عن أزهر بن عبد الله (١) " أن عمر كتب إلى أبى عبيدة بن الجراح أن أسهم للفرس سهمين وللفرسين أربعة أسهم ولصاحبهما سهما فذلك خمسة أسهم "','1562','1318','0');
INSERT INTO [t22592] VALUES (1330,'(١٢٣٢) - (روى الدارقطنى عن بشير بن عمرو بن محصن قال: " أسهم لى رسول الله صلى الله عليه وسلم لفرسى أربعة أسهم ولى سهما فأخذت خمسة أسهم "','1562','1318','0');
INSERT INTO [t22592] VALUES (1331,'(١٢٣٣) - (قال تميم بن فرع المهرى: " كنت فى الجيش الذى فتحوا الأسكندرية فى المرة الآخرة فلم يقسم لى عمرو شيئا وقال: غلام لم يحتلم , فسألوا أبا بصرة الغفارى , وعقبة بن عامر فقالا: انظروا فإن كان قد أشعر فاقسموا له , فنظر إلى بعض القوم فإذا أنا قد أنب','1563','1318','0');
INSERT INTO [t22592] VALUES (1332,'(١٢٣٤) - (عن عمير مولى آبى اللحم قال: " شهدت خيبر مع سادتى فكلموا فى رسول الله صلى الله عليه وسلم فأخبر أنى مملوك فأمر لى [بشىء] من خرثى المتاع "','1563','1318','0');
INSERT INTO [t22592] VALUES (1333,'(١٢٣٥) - (حديث الأسود بن يزيد: " أسهم لهم يوم القادسية "','1564','1318','0');
INSERT INTO [t22592] VALUES (1334,'(١٢٣٦) - (حديث ابن عباس: " كان رسول الله صلى الله عليه وسلم يغزو بالنساء فيداوين الجرحى ويحذين من الغنيمة , فأما بسهم فلم يضرب لهن "','1564','1318','0');
INSERT INTO [t22592] VALUES (1335,'(١٢٣٧) - (وعنه: " كان رسول الله صلى الله عليه وسلم يعطى المرأة والمملوك من الغنائم دون ما يصيب الجيش "','1565','1318','0');
INSERT INTO [t22592] VALUES (1336,'(١٢٣٨) - (حديث حشرج بن زياد عن جدته: " أن النبى صلى الله عليه وسلم أسهم لهن يوم خيبر "','1566','1318','0');
INSERT INTO [t22592] VALUES (1337,'(١٢٣٩) - (خبر: " أسهم أبو موسى يوم غزوة تستر لنسوة معه على','1567','1318','0');
INSERT INTO [t22592] VALUES (1338,'(١٢٤٠) - (حديث جبير بن مطعم: " أن النبى صلى الله عليه وسلم تناول بيده وبرة من بعير ثم قال: والذى نفسى بيده ما لى مما أفاء الله إلا الخمس , والخمس مردود عليكم "','1568','1318','0');
INSERT INTO [t22592] VALUES (1339,'(١٢٤١) - (حديث: " إذا أطعم الله نبيا طعمة ثم قبضه فهو للذى يقوم بها من بعده ".','1571','1318','0');
INSERT INTO [t22592] VALUES (1340,'(١٢٤٢) - (حديث جبير بن مطعم: " لما كان يوم خيبر قسم رسول الله صلى الله عليه وسلم سهم ذوى القربى بين بنى هشام وبنى المطلب , فأتيت أنا وعثمان ابن عفان فقلنا: يا رسول الله: أما بنو هاشم فلا ننكر فضلهم لمكانك الذى وضعك الله به منهم , فما بال إخواننا من','1573','1318','0');
INSERT INTO [t22592] VALUES (1341,'(١٢٤٣) - (وكان صلى الله عليه وسلم يعطى منه العباس ـ وهو غنى ويعطى صفية "','1574','1318','0');
INSERT INTO [t22592] VALUES (1342,'(١٢٤٤) - (حديث: " لا يتم بعد احتلام "','1574','1318','0');
INSERT INTO [t22592] VALUES (1343,'(١٢٤٥) - (قال عمر رضى الله عنه: " ما من أحد من المسلمين إلا له فى هذا المال نصيب إلا العبيد فليس لهم فيه شىء " وقرأ: (ما أفاء الله....) حتى بلغ (والذين جاءوا من بعدهم) . فقال: " هذه استوعبت المسلمين ولئن عشت ليأتين الراعى بسرو حمير نصيبه منها لم يع','1578','1318','0');
INSERT INTO [t22592] VALUES (1344,'باب عقد الذمة','1580','1275','0');
INSERT INTO [t22592] VALUES (1345,'(١٢٤٦) - (قول المغيرة: " أمرنا نبينا أن نقاتلكم حتى تعبدوا الله وحده أو تؤدوا الجزية "','1580','1344','0');
INSERT INTO [t22592] VALUES (1346,'(١٢٤٧) - (حديث بريدة: " ادعهم إلى أحد خصال ثلاث , ادعهم إلى الإسلام فإن أجابوك فاقبل منهم وكف عنهم , فإن أبوا فادعهم إلى إعطاء الجزية فإن أجابوك فاقبل منهم وكف عنهم , فإن أبوا فاستعن بالله وقاتلهم "','1581','1344','0');
INSERT INTO [t22592] VALUES (1347,'(١٢٤٨) - (حديث عبد الرحمن بن عوف: " سنوا بهم سنة أهل الكتاب ".','1583','1344','0');
INSERT INTO [t22592] VALUES (1348,'(١٢٤٩) - (حديث أخذ الجزية من مجوس هجر','1584','1344','0');
INSERT INTO [t22592] VALUES (1349,'(١٢٥٠) - (حديث: " لا ضرر ولا ضرار "','1586','1344','0');
INSERT INTO [t22592] VALUES (1350,'(١٢٥١) - (روى: " أنه قيل لابن عمر أن راهبا يشتم رسول الله صلى الله عليه وسلم فقال: لو سمعته لقتلته , إنا لم نعط الأمان على هذا "','1586','1344','0');
INSERT INTO [t22592] VALUES (1351,'(١٢٥٢) - (حديث أنس: " أن يهوديا قتل جارية على أوضاح لها فقتله رسول الله صلى الله عليه وسلم "','1587','1344','0');
INSERT INTO [t22592] VALUES (1352,'(١٢٥٣) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم أتى بيهوديين قد فجرا بعد إحصانهما فرجمهما ".','1588','1344','0');
INSERT INTO [t22592] VALUES (1353,'(١٢٥٤) - (حديث معاذ: " خذ من كل حالم دينار أو عدله معافرى ".','1590','1344','0');
INSERT INTO [t22592] VALUES (1354,'(١٢٥٥) - (خبر أسلم: " أن عمر رضى الله عنه كتب إلى أمراء الأجناد: لا تضربوا الجزية على النساء والصبيان ولا تضربوها إلا على من جرت عليه المواسى ".','1590','1344','0');
INSERT INTO [t22592] VALUES (1355,'(١٢٥٦) - (حديث عمر من قوله: " لا جزية على مملوك ".','1591','1344','0');
INSERT INTO [t22592] VALUES (1356,'(١٢٥٧) - (حديث ابن عباس: " ليس على المسلم جزية "','1594','1344','0');
INSERT INTO [t22592] VALUES (1357,'(١٢٥٨) - (حديث عمر من قوله: " إن أخذها فى كفه ثم أسلم ردها [عليه] ".','1594','1344','0');
INSERT INTO [t22592] VALUES (1358,'(١٢٥٩) - (وروى أبو عبيد: " أن يهوديا أسلم , فطولب بالجزية','1594','1344','0');
INSERT INTO [t22592] VALUES (1359,'(١٢٦٠) - (خبر ابن أبى نجيج: " قلت لمجاهد: ما شأن أهل الشام','1595','1344','0');
INSERT INTO [t22592] VALUES (1360,'(١٢٦١) - (خبر: " أن عمر زاد على ما فرض رسول الله صلى الله عليه وسلم ولم ينقص ".','1596','1344','0');
INSERT INTO [t22592] VALUES (1361,'(١٢٦٢) - (خبر الأحنف بن قيس: " أن عمر شرط على أهل الذمة ضيافة يوم وليلة , وأن يصلحوا القناطر وإن قتل رجل من المسلمين بأرضهم فعليهم ديته "','1597','1344','0');
INSERT INTO [t22592] VALUES (1362,'(١٢٦٣) - (خبر أسلم: " أن أهل الجزية من أهل الشام أتوا عمر رضى الله عنه فقالوا: إن المسلمين إذا مروا بنا كلفونا ذبح الغنم والدجاج فى ضيافتهم , فقال: أطعموهم مما تأكلون ولا تزيدوهم على ذلك "','1598','1344','0');
INSERT INTO [t22592] VALUES (1363,'فصل','1598','1275','0');
INSERT INTO [t22592] VALUES (1364,'(١٢٦٤) - (روى عن على رضى الله عنه أنه قال: " إنما بذلوا الجزية لتكون دماؤهم كدمائنا , وأموالهم كأموالنا ".','1598','1363','0');
INSERT INTO [t22592] VALUES (1365,'(١٢٦٥) - (خبر إسماعيل بن عياش عن غير واحد من أهل العلم قالوا: " كتب أهل الجزيرة إلى عبد الرحمن بن غنم إنا شرطنا على أنفسها أن لا نتشبه بالمسلمين فى لبس قلنسوة ولا عمامة ... الخ "','1598','1363','0');
INSERT INTO [t22592] VALUES (1366,'(١٢٦٦) - (وعن ابن عباس من قوله: " أيما مصر مصرته العرب فليس للعجم أن يبنوا فيه بيعة ولا يضربوا فيه ناقوسا ولا يشربوا فيه خمرا ولا يتخذوا فيه خنزيرا "','1600','1363','0');
INSERT INTO [t22592] VALUES (1367,'(١٢٦٧) - (خبر أن عمر: " أمر بجز نواصى أهل الذمة وأن يشدوا المناطق وأن يركبوا الأكف بالعرض "','1600','1363','0');
INSERT INTO [t22592] VALUES (1368,'(١٢٦٨) - (حديث: " الإسلام يعلو ولا يعلى "','1601','1363','0');
INSERT INTO [t22592] VALUES (1369,'(١٢٦٩) - (حديث: " من تشبه بقوم فهو منهم "','1604','1363','0');
INSERT INTO [t22592] VALUES (1370,'(١٢٧٠) - (حديث: " ليس منا من تشبه بغيرنا "','1606','1363','0');
INSERT INTO [t22592] VALUES (1371,'(١٢٧١) - (حديث أبى هريرة: " لا تبدءوا اليهود والنصارى بالسلام وإذا لقيتم أحدهم فى الطريق فاضطروه إلى أضيقها "','1606','1363','0');
INSERT INTO [t22592] VALUES (1372,'(١٢٧٢) - (حديث: " أن النبى صلى الله عليه وسلم عاد صبيا كان يخدمه وعرض عليه الإسلام فأسلم ".','1608','1363','0');
INSERT INTO [t22592] VALUES (1373,'(١٢٧٣) - (حديث: " أنه صلى الله عليه وسلم عاد أبا طالب , وعرض عليه الإسلام فلم يسلم "','1609','1363','0');
INSERT INTO [t22592] VALUES (1374,'(١٢٧٤) - (خبر ابن عمر: " أنه مر على رجل فسلم عليه فقيل له: إنه كافر , فقال: رد على ما سلمت عليك , فقال: أكثر الله مالك وولدك , ثم التفت إلى أصحابه فقال: أكثر للجزية ".','1610','1363','0');
INSERT INTO [t22592] VALUES (1375,'(١٢٧٥) - (حديث أبى بصرة قال: قال رسول الله صلى الله عليه وسلم: " إنا غادون [إلى يهود] فلا تبدءوهم بالسلام فإن سلموا عليكم فقولوا: وعليكم "','1611','1363','0');
INSERT INTO [t22592] VALUES (1376,'(١٢٧٦) - (حديث أنس: " نهينا , أو: أمرنا أن لا نزيد أهل الذمة على: وعليكم ".','1611','1363','0');
INSERT INTO [t22592] VALUES (1377,'(١٢٧٧) - (حديث أبى موسى: " أن اليهود كانوا يتعاطسون عند النبى صلى الله عليه وسلم رجاء أن يقول لهم: يرحمكم الله , فكان يقول لهم: يهديكم الله ويصلح بالكم ".','1614','1363','0');
INSERT INTO [t22592] VALUES (1378,'فصل','1614','1275','0');
INSERT INTO [t22592] VALUES (1379,'(١٢٧٨) - (روى عن عمر: " أنه رفع إليه رجل أراد استكراه امرأة مسلمة على الزنى فقال: ما على هذا صالحناكم فأمر به فصلب فى بيت المقدس "','1614','1378','0');
INSERT INTO [t22592] VALUES (1380,'(١٢٧٩) - (روى أنه قيل لابن عمر: " إن راهبا يشتم النبى صلى الله عليه وسلم فقال: لو سمعته لقتلته , إنا لم نعط الأمان على هذا "','1616','1378','0');
INSERT INTO [t22592] VALUES (1381,'(١٢٨٠) - (حديث: " الإسلام يجب ما قبله "','1616','1378','0');
INSERT INTO [t22592] VALUES (1382,'كتاب البيع','1619','0','0');
INSERT INTO [t22592] VALUES (1383,'[الأحاديث ١٢٨١ - ١٢٤٩]','1619','1382','0');
INSERT INTO [t22592] VALUES (1384,'(١٢٨١) - (حديث: " البيعان بالخيار ما لم يتفرقا "','1619','1383','0');
INSERT INTO [t22592] VALUES (1385,'(١٢٨٢) - (حديث: " ... وإنما لكل امرىء ما نوى ... ".','1620','1383','0');
INSERT INTO [t22592] VALUES (1386,'(١٢٨٣) - (حديث: " إنما البيع عن تراض "','1620','1383','0');
INSERT INTO [t22592] VALUES (1387,'(١٢٨٤) - (حديث: " إن أبا الدرداء اشترى من صبى عصفورا فأرسله ".','1621','1383','0');
INSERT INTO [t22592] VALUES (1388,'(١٢٨٥) - (حديث: " أن النبى صلى الله عليه وسلم اشترى من جابر بعيرا "','1621','1383','0');
INSERT INTO [t22592] VALUES (1389,'(١٢٨٦) - (حديث: " أن النبى صلى الله عليه وسلم اشترى من أعرابى فرسا "','1622','1383','0');
INSERT INTO [t22592] VALUES (1390,'(١٢٨٧) - (حديث: " أنه صلى الله عليه وسلم وكل عروة فى شراء شاة "','1622','1383','0');
INSERT INTO [t22592] VALUES (1391,'(١٢٨٨) - (حديث: " أنه صلى الله عليه وسلم باع مدبرا "','1624','1383','0');
INSERT INTO [t22592] VALUES (1392,'(١٢٨٩) - (حديث: " أنه باع حلسا وقدحا "','1625','1383','0');
INSERT INTO [t22592] VALUES (1393,'(١٢٩٠) - (حديث جابر أنه سمع النبى صلى الله عليه وسلم يقول: " إن الله حرم بيع الخمر والميتة والخنزير والأصنام ... ".','1625','1383','0');
INSERT INTO [t22592] VALUES (1394,'(١٢٩١) - (حديث أبي مسعود قال: " نهى النبي صلى الله عليه وسلم عن ثمن الكلب ومهر البغي وحلوان الكاهن "','1626','1383','0');
INSERT INTO [t22592] VALUES (1395,'(١٢٩٢) - (قوله صلى الله عليه وسلم لحكيم بن حزام: " لا تبع ما ليس عندك "','1627','1383','0');
INSERT INTO [t22592] VALUES (1396,'(١٢٩٣) - (حديث أبى سعيد: " أن النبى صلى الله عليه وسلم نهى عن شراء العبد وهو آبق "','1627','1383','0');
INSERT INTO [t22592] VALUES (1397,'(١٢٩٤) - (لمسلم عن أبى هريرة: " أن النبى صلى الله عليه وسلم نهى عن بيع الغرر "','1628','1383','0');
INSERT INTO [t22592] VALUES (1398,'فصل','1629','1382','0');
INSERT INTO [t22592] VALUES (1399,'(١٢٩٥) - (حديث: " إذا رأيتم من يبيع أو يبتاع فى المسجد فقولوا: لا أربح الله تجارتك "','1629','1398','0');
INSERT INTO [t22592] VALUES (1400,'(١٢٩٦) - (حديث: " نهى صلى الله عليه وسلم عن بيع السلاح فى الفتنة "','1630','1398','0');
INSERT INTO [t22592] VALUES (1401,'(١٢٩٧) - (حديث: " لا يبع بعضكم على بيع بعض "','1631','1398','0');
INSERT INTO [t22592] VALUES (1402,'(١٢٩٨) - (حديث أبى هريرة: " لا يسوم الرجل على سوم أخيه "','1631','1398','0');
INSERT INTO [t22592] VALUES (1403,'(١٢٩٨/١) - (حديث: " أن النبي صلى الله عليه وسلم باع فيمن يزيد "','1632','1398','0');
INSERT INTO [t22592] VALUES (1404,'(١٢٩٩) - (قال ابن عمر فى المصاحف: " وددت أن الأيدى تقطع فى بيعها "','1632','1398','0');
INSERT INTO [t22592] VALUES (1405,'(١٣٠٠) - (حديث: " أن النبى صلى الله عليه وسلم [كان] ينهى أن يسافر بالقرآن إلى أرض العدو مخافة أن تناله أيديهم "','1633','1398','0');
INSERT INTO [t22592] VALUES (1406,'(١٣٠١) - (حديث: " أن عمر أنكر على عبد الرحمن بن عوف حين باع جارية له كان يطؤها قبل استبرائها وقال: ما كنت بذلك بخليق ". وفيه قصة.','1634','1398','0');
INSERT INTO [t22592] VALUES (1407,'(١٣٠٢) - (حديث أبى سعيد: " أن النبى صلى الله عليه وسلم نهى عام أوطاس أن توطأ حامل حتى تضع ولا غير حامل حتى تحيض حيضة "','1634','1398','0');
INSERT INTO [t22592] VALUES (1408,'باب الشروط في البيع','1637','1382','0');
INSERT INTO [t22592] VALUES (1409,'(١٣٠٣) - (حديث: " المسلمون على شروطهم "','1637','1408','0');
INSERT INTO [t22592] VALUES (1410,'(١٣٠٤) - (حديث جابر: " أنه باع النبى صلى الله عليه وسلم جملا واشترط ظهره إلى المدينة "','1641','1408','0');
INSERT INTO [t22592] VALUES (1411,'(١٣٠٥) - (حديث ابن عمرو: " أن النبى صلى الله عليه وسلم نهى عن شرطين فى البيع ".','1641','1408','0');
INSERT INTO [t22592] VALUES (1412,'فصل','1643','1382','0');
INSERT INTO [t22592] VALUES (1413,'(١٣٠٦) - (حديث: " لا يحل سلف وبيع ولا شرطان فى بيع "','1643','1412','0');
INSERT INTO [t22592] VALUES (1414,'(١٣٠٧) - (قال ابن مسعود: " صفقتان فى صفقة ربا ".','1643','1412','0');
INSERT INTO [t22592] VALUES (1415,'(١٣٠٨) - (حديث: " من اشترط شرطا ليس فى كتاب الله , فهو باطل وإن كان مائة شرط ".','1647','1412','0');
INSERT INTO [t22592] VALUES (1416,'(١٣٠٩) - (حديث: " أن النبى صلى الله عليه وسلم أبطل الشرط ولم يبطل العقد ".','1648','1412','0');
INSERT INTO [t22592] VALUES (1417,'(١٣١٠) - (حديث: " البيعان بالخيار ما لم يتفرقا "','1648','1412','0');
INSERT INTO [t22592] VALUES (1418,'(١٣١٠/١) - (حديث: " البيعان بالخيار ما لم يتفرقا أو يخير أحدهما صاحبه فان خير أحدهما صاحبه فتبايعا على ذلك فقد وجب البيع ". وفي لفظ: " المتبايعان بالخيار ما لم يتفرقا إلا أن يكون البيع كان عن خيار فان كان البيع عن خيار فقد وجب البيع ".','1648','1412','0');
INSERT INTO [t22592] VALUES (1419,'(١٣١١) - (حديث عمرو بن شعيب عن أبيه عن جده مرفعا وفيه: " ولا يحل له أن يفارق صاحبه خشية أن يستقيله "','1650','1412','0');
INSERT INTO [t22592] VALUES (1420,'(١٣١٢) - (أثر ابن عمر: " كان إذا اشترى شيئا يعجبه مشى خطوات ليلزم البيع "','1651','1412','0');
INSERT INTO [t22592] VALUES (1421,'(١٣١٣) - (حديث: " المسلمون على شروطهم ".','1652','1412','0');
INSERT INTO [t22592] VALUES (1422,'(١٣١٤) - (حديث: " من باع عبدا وله مال فماله للبائع إلا أن يشترطه المبتاع "','1652','1412','0');
INSERT INTO [t22592] VALUES (1423,'(١٣١٥) - (حديث عائشة: " أن النبى صلى الله عليه وسلم قضى فى الخراج بالضمان ".','1653','1412','0');
INSERT INTO [t22592] VALUES (1424,'(١٣١٦) - (حديث: " الثلث والثلث كثير "','1655','1412','0');
INSERT INTO [t22592] VALUES (1425,'(١٣١٧) - (حديث: " لا تلقوا الجلب , فمن تلقاه فاشترى منه فإذا أتى [سيده] السوق فهو بالخيار "','1655','1412','0');
INSERT INTO [t22592] VALUES (1426,'(١٣١٨) - (حديث: " نهى رسول الله صلى الله عليه وسلم عن النجش "','1655','1412','0');
INSERT INTO [t22592] VALUES (1427,'(١٣١٩) - (حديث: " من غشنا فليس منا "','1656','1412','0');
INSERT INTO [t22592] VALUES (1428,'(١٣٢٠) - (حديث أبى هريرة مرفوعا: " لا تصروا الإبل والغنم , فمن ابتاعها فهو بخير النظرين بعد أن يحلبها , إن شاء أمسكها وإن شاء ردها وصاعا من تمر ".','1659','1412','0');
INSERT INTO [t22592] VALUES (1429,'(١٣٢١) - (حديث عقبة بن عامر مرفوعا: " المسلم أخو المسلم ولا يحل لمسلم باع من أخيه بيعا فيه عيب إلا يبينه له ".','1660','1412','0');
INSERT INTO [t22592] VALUES (1430,'(١٣٢٢) - (حديث ابن مسعود: " إذا اختلف المتبايعان , وليس بينهما بينة , فالقول ما يقول صاحب السلعة أو يترادان " رواه أحمد وأبو داود والنسائى وابن ماجه وزاد فيه: " والبيع قائم بعينه ". ولأحمد فى رواية: " والسلعة كما هى ". وفى لفظ: " تحالفا ".','1661','1412','0');
INSERT INTO [t22592] VALUES (1431,'(١٣٢٣) - (روي عن ابن مسعود: " أنه باع الأشعث رقيقا من رقيق الإمارة فقال: بعتك بعشرين ألفا , وقال الأشعث: اشتريت منك بعشرة , فقال عبد الله: سمعت رسول الله صلى الله عليه وسلم يقول: " إذا اختلف المتبايعان وليس بينهما بينة , والمبيع قائم بعينه , فالقو','1666','1412','0');
INSERT INTO [t22592] VALUES (1432,'(١٣٢٤) - (حديث عبد الملك بن عبيدة مرفوعا: " إذا اختلف المتبايعان استحلف البائع , ثم كان للمشترى الخيار إن شاء أخذ وإن شاء ترك "','1667','1412','0');
INSERT INTO [t22592] VALUES (1433,'(١٣٢٥) - (حديث ابن عمر: " مضت السنة أن ما أدركته الصفقة','1667','1412','0');
INSERT INTO [t22592] VALUES (1434,'(١٣٢٦) - (حديث ابن عمر: " كنا نبيع الإبل بالنقيع بالدراهم , فنأخذ عنهما الدنانير وبالعكس , فسألنا رسول الله صلى الله عليه وسلم فقال: لا بأس أن تأخذ بسعر يومها ما لم تفترقا وبينكما شىء "','1668','1412','0');
INSERT INTO [t22592] VALUES (1435,'(١٣٢٧) - (وقال النبى صلى الله عليه وسلم فى البكر , هو لك يا عبد الله بن عمر , فاصنع به ما شئت "','1670','1412','0');
INSERT INTO [t22592] VALUES (1436,'(١٣٢٧/١) - (حديث: " الخراج بالضمان "','1670','1412','0');
INSERT INTO [t22592] VALUES (1437,'(١٣٢٨) - (حديث: " من ابتاع طعاما , فلا يبعه حتى يستوفيه "','1670','1412','0');
INSERT INTO [t22592] VALUES (1438,'(١٣٢٩) - (وقال ابن عمر: " رأيت الذين يشترون الطعام مجازفة على عهد رسول الله صلى الله عليه وسلم ينهون أن يبيعوه حتى يؤووه إلى رحالهم "','1673','1412','0');
INSERT INTO [t22592] VALUES (1439,'(١٣٣٠) - (حديث عثمان رضى الله عنه: أن رسول الله صلى الله عليه وسلم قال: " إذا بعت فكل وإذا ابتعت فاكتل "','1674','1412','0');
INSERT INTO [t22592] VALUES (1440,'(١٣٣١) - (حديث: " إذا سميت الكيل فكل "','1676','1412','0');
INSERT INTO [t22592] VALUES (1441,'(١٣٣٢) - (حديث ابن عمر: " كنا نشتري الطعام من الركبان جزافا. فنهانا رسول الله صلى الله عليه وسلم أن نبيعه حتى ننقله من مكانه "','1677','1412','0');
INSERT INTO [t22592] VALUES (1442,'(١٣٣٣) - (حديث " إذا ابتعت فكتل "','1677','1412','0');
INSERT INTO [t22592] VALUES (1443,'(١٣٣٤) - (حديث أبى هريرة مرفوعا: " من أقال مسلما , أقال الله عثرته يوم القيامة ". رواه ابن ماجه وأبو داود , وليس فيه ذكر يوم القيامة','1677','1412','0');
INSERT INTO [t22592] VALUES (1444,'باب الربا','1678','1382','0');
INSERT INTO [t22592] VALUES (1445,'(١٣٣٥) - (حديث أبى هريرة: " اجتنبوا السبع الموبقات قالوا: وما هى يا رسول الله , قال: الشرك بالله , [والسحر] , وقتل النفس , التى حرم الله إلا بالحق , وأكل الربا , وأكل مال اليتيم , والتولى يوم الزحف , وقذف المحصنات الغافلات المؤمنات "','1678','1444','0');
INSERT INTO [t22592] VALUES (1446,'(١٣٣٦) - (حديث:" لعن الله آكل الربا وموكله وشاهديه وكاتبه "','1678','1444','0');
INSERT INTO [t22592] VALUES (1447,'(١٣٣٧) - (روى فى ربا الفضل عن ابن عباس ثم رجع "','1681','1444','0');
INSERT INTO [t22592] VALUES (1448,'(١٣٣٨) - (حديث: " لا ربا إلا فى النسيئة "','1683','1444','0');
INSERT INTO [t22592] VALUES (1449,'(١٣٣٩) - (حديث أبى سعيد: " الذهب بالذهب , والفضة بالفضة والبر بالبر , والشعير بالشعير , والتمر بالتمر , والملح بالملح مثلا بمثل , يدا بيد , فمن زاد أو استزاد فقد أربى , الآخذ والمعطى سواء "','1683','1444','0');
INSERT INTO [t22592] VALUES (1450,'(١٣٤٠) - (حديث: " لا تفعل , بع الجمع بالدراهم , ثم ابتع بالدراهم جنيبا , وقال فى الميزان مثل ذلك "','1685','1444','0');
INSERT INTO [t22592] VALUES (1451,'(١٣٤١) - (حديث معمر بن عبد الله: " أنه نهى عن بيع الطعام بالطعام وإلا مثلا بمثل ".','1685','1444','0');
INSERT INTO [t22592] VALUES (1452,'(١٣٤٢) - (حديث ابن عمر أن النبى صلى الله عليه وسلم قال: " المكيال مكيال أهل المدينة , والوزن وزن أهل مكة "','1686','1444','0');
INSERT INTO [t22592] VALUES (1453,'(١٣٤٣) - (حديث سعيد بن المسيب أن رسول الله صلى الله عليه وسلم قال: " لا ربا إلا فيما كيل أو وزن مما يؤكل أو يشرب " أخرجه الدارقطنى وقال: " الصحيح أنه من قوله ومن رفعه فقد وهم ".','1688','1444','0');
INSERT INTO [t22592] VALUES (1454,'(١٣٤٤) - (قال عمار: " العبد خير من العبدين , والثوب خير من الثوبين , فما كان يدا بيد فلا بأس به , إنما الربا فى النسء إلا ما كيل أو وزن "','1689','1444','0');
INSERT INTO [t22592] VALUES (1455,'فصل','1689','1382','0');
INSERT INTO [t22592] VALUES (1456,'(١٣٤٥) - (حديث أبى سعيد: " لا تبيعوا الذهب بالذهب إلا مثلا بمثل ولا تشفوا بعضعها على بعض , ولا تبيعوا منها غائبا بناجز "','1689','1455','0');
INSERT INTO [t22592] VALUES (1457,'(١٣٤٦) - (قوله صلى الله عليه وسلم فى حديث عبادة: " فإذا اختلفت هذه الأصناف فبيعوا كيف شئتم إذا كان يدا بيد "','1689','1455','0');
INSERT INTO [t22592] VALUES (1458,'(١٣٤٧) - (حديث عمر مرفوعا:" الذهب بالورق ربا إلا هاء وهاء , والبر بالبر ربا إلا هاء هاء "','1690','1455','0');
INSERT INTO [t22592] VALUES (1459,'(١٣٤٨) - (حديث: " لا بأس ببيع البر بالشعير والشعير أكثرهما يدا بيد ".','1691','1455','0');
INSERT INTO [t22592] VALUES (1460,'(١٣٤٩) - (حديث: " الذهب بالذهب وزنا بوزن , والفضة بالفضة وزنا بوزن , والبر بالبر كيلا بكيل , والشعير بالشعير كيلا بكيل "','1691','1455','0');
INSERT INTO [t22592] VALUES (1461,'(١٣٥٠) - (حديث: " نهى عن بيع الحى بالميت "','1691','1455','0');
INSERT INTO [t22592] VALUES (1462,'(١٣٥١) - (حديث سعيد بن المسيب مرفوعا: " نهى عن بيع اللحم بالحيوان "','1693','1455','0');
INSERT INTO [t22592] VALUES (1463,'(١٣٥٢) - (حديث سعد بن أبى وقاص: " أن النبى صلى الله عليه وسلم سئل عن بيع الرطب بالتمر , فقال: أينقص الرطب إذا يبس؟ قالوا: نعم , فنهى عن ذلك "','1694','1455','0');
INSERT INTO [t22592] VALUES (1464,'(١٣٥٣) - (حديث أنس: " أن النبى صلى الله عليه وسلم نهى عن المحاقلة " رواه البخارى. وقال جابر: " المحاقلة بيع الزرع بمائة فرق من الحنطة "','1696','1455','0');
INSERT INTO [t22592] VALUES (1465,'(١٣٥٤) - (قال جابر: " المحاقلة بيع الزرع بمائة فرق من','1696','1455','0');
INSERT INTO [t22592] VALUES (1466,'(١٣٥٥) - (حديث ابن عمر مرفوعا: " نهى عن بيع الثمار حتى تزهو وعن بيع السنبل حتى يبيض ويأمن العاهة "','1697','1455','0');
INSERT INTO [t22592] VALUES (1467,'(١٣٥٦) - (حديث فضالة قال: " أتى النبى صلى الله عليه وسلم بقلادة فيها ذهب وخرز , اشتراها رجل بتسعة دنانير أو سبعة فقال صلى الله عليه وسلم: لا حتى تميز بينهما , قال: فرده حتى ميز بينهما ". رواه أبو داود , ولمسلم: " أمر بالذهب الذى فى القلادة فنزع وحد','1698','1455','0');
INSERT INTO [t22592] VALUES (1468,'(١٣٥٧) - (حديث: " فإذا اختلفت هذه الأصناف , فبيعوا كيف شئتم يدا بيد ".','1699','1455','0');
INSERT INTO [t22592] VALUES (1469,'(١٣٥٨) - (حديث عبد الله بن عمرو: " أن النبى صلى الله عليه وسلم أمره أن يجهز جيشا فكان يأخذ البعير بالبعيرين إلى إبل الصدقة "','1700','1455','0');
INSERT INTO [t22592] VALUES (1470,'(١٣٥٩) - (حديث ابن عمر قال: " أتيت النبى صلى الله عليه وسلم فقلت: إنى أبيع الإبل بالنقيع , فأبيع بالدنانير , وآخذ الدراهم , وأبيع بالدراهم فآخذ الدنانير فقال: لا بأس أن تأخذ بسعر يومها ما لم تفرقا وبينكما شىء ". رواه الخمسة وفى لفظ بعضهم: " أبيع','1702','1455','0');
INSERT INTO [t22592] VALUES (1471,'باب بيع الأصول والثمار','1702','1382','0');
INSERT INTO [t22592] VALUES (1472,'(١٣٦٠) - (حديث: " المسلمون عند شروطهم ".','1702','1471','0');
INSERT INTO [t22592] VALUES (1473,'فصل','1702','1382','0');
INSERT INTO [t22592] VALUES (1474,'(١٣٦١) - (حديث: ومن باع نخلا بعد أن تؤبر , فثمرتها للذى','1702','1473','0');
INSERT INTO [t22592] VALUES (1475,'(١٣٦٢) - (حديث ابن عمر: أن النبى صلى الله عليه وسلم نهى عن بيع الثمار حتى يبدو صلاحها نهى البائع والمبتاع "','1703','1473','0');
INSERT INTO [t22592] VALUES (1476,'(١٣٦٣) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم نهى عن بيع النخل حتى يزهو وعن بيع السنبل حتى يبيض ويأمن العاهة , نهى البائع والمشترى "','1703','1473','0');
INSERT INTO [t22592] VALUES (1477,'(١٣٦٤) - (حديث أنس: " أرأيت إذا (١) منع الله الثمرة , بم يأخذ أحدكم مال أخيه؟ ".','1703','1473','0');
INSERT INTO [t22592] VALUES (1478,'(١٣٦٥) - (حديث: " أن النبى صلى الله عليه وسلم نهى عن بيع الثمرة حتى تزهو , قيل لأنس: وما زهوها؟ قال: تحمار وتصفار "','1705','1473','0');
INSERT INTO [t22592] VALUES (1479,'(١٣٦٦) - (حديث أنس مرفوعا: " نهى عن بيع العنب حتى يسود وعن بيع الحب حتى يشتد "','1706','1473','0');
INSERT INTO [t22592] VALUES (1480,'(١٣٦٧) - (حديث جابر: " أن النبى صلى الله عليه وسلم نهى عن بيع الثمرة حتى تطيب ". وفى رواية: " حتى تطعم "','1706','1473','0');
INSERT INTO [t22592] VALUES (1481,'(١٣٦٨) - (حديث جابر: " أن النبى صلى الله عليه وسلم أمر بوضع الجوائح ". وفى لفظ قال: " إن بعت من أخيك ثمرا فأصابته جائحة , فلا يحل لك أن تأخذ منه (١) شيئا , بم تأخذ مال أخيك بغير حق؟ "','1707','1473','0');
INSERT INTO [t22592] VALUES (1482,'باب السلم','1708','1382','0');
INSERT INTO [t22592] VALUES (1483,'(١٣٦٩) - (قال ابن عباس: " أشهد أن السلف المضمون إلى أجل مسمى قد أحله الله فى كتابه وأذن فيه , ثم قرأ: (يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه ... ) الآية "','1708','1482','0');
INSERT INTO [t22592] VALUES (1484,'(١٣٧٠) - (قول عبد الله بن أبى أوفى وعبد الرحمن بن أبزى: " كنا نصيب المغانم مع رسول الله صلى الله عليه وسلم فكان يأتينا أنباط من أنباط الشام','1708','1482','0');
INSERT INTO [t22592] VALUES (1485,'(١٣٧١) - (حديث أبى رافع: " استلف النبى صلى الله عليه وسلم من رجل بكرا "','1709','1482','0');
INSERT INTO [t22592] VALUES (1486,'(١٣٧٢) - (عن على: " أنه باع جملا له يدعى عصيفيرا بعشرين بعيرا إلى أجل معلوم "','1710','1482','0');
INSERT INTO [t22592] VALUES (1487,'(١٣٧٣) - (قال ابن عمر: " أن من الربا أبوابا لا تخفى وإن منها السلم فى السن ".','1710','1482','0');
INSERT INTO [t22592] VALUES (1488,'(١٣٧٤) - (قال الشعبى: " إنما كره ابن مسعود السلف فى الحيوان , لأنهم اشترطوا إنتاج فحل بنى فلان , فحل معلوم ".','1710','1482','0');
INSERT INTO [t22592] VALUES (1489,'(١٣٧٥) - (حديث: " من أسلف فى شىء , فلا يصرفه إلى غيره ".','1710','1482','0');
INSERT INTO [t22592] VALUES (1490,'(١٣٧٦) - (حديث: " من أسلف فى شىء فليسلف فى كيل معلوم ووزن معلوم إلى أجل معلوم ".','1711','1482','0');
INSERT INTO [t22592] VALUES (1491,'(١٣٧٧) - (عن ابن عباس قال: " لا تبايعوا إلى الحصاد والدياس ولا تتبايعوا إلا إلى أجل معلوم ".','1712','1482','0');
INSERT INTO [t22592] VALUES (1492,'(١٣٧٨) - (عن ابن عمر رضى الله عنه: " أنه كان يبايع إلى العطاء "','1712','1482','0');
INSERT INTO [t22592] VALUES (1493,'(١٣٧٩) - (روى الأثرم: " أن أنسا كاتب عبدا له على مال إلى أجل , فجاءه به قبل الأجل , فأبى أن يأخذه فأتى عمر بن الخطاب فأخذه منه وقال: اذهب فقد عتقت ".','1712','1482','0');
INSERT INTO [t22592] VALUES (1494,'(١٣٨٠) - (حديث: " أنه صلى الله عليه وسلم قدم المدينة وهم يسلفون فى الثمار السنة والسنتين والثلاث فقال: من أسلم فى شىء فليسلم فى كيل معلوم ووزن معلوم إلى أجل معلوم "','1713','1482','0');
INSERT INTO [t22592] VALUES (1495,'(١٣٨١) - (حديث:" أنه أسلف إليه صلى الله عليه وسلم رجل من اليهود دنانير فى تمر مسمى فقال اليهودى: من تمر حائط بنى فلان , فقال النبى صلى الله عليه وسلم أما من حائط بنى فلان فلا ولكن كيل مسمى إلى أجل مسمى ".','1713','1482','0');
INSERT INTO [t22592] VALUES (1496,'(١٣٨٢) - (حديث ابن عمر مرفوعا: " نهى عن بيع الكالىء بالكالىء "','1715','1482','0');
INSERT INTO [t22592] VALUES (1497,'(١٣٨٣) - (حديث: " من أسلف فى شىء فليسلف "','1717','1482','0');
INSERT INTO [t22592] VALUES (1498,'(١٣٨٤) - (حديث: " من أسلم فى شىء فلا يصرفه إلى غيره "','1717','1482','0');
INSERT INTO [t22592] VALUES (1499,'(١٣٨٥) - (حديث ابن عمر قال: قال رسول الله صلى الله عليه وسلم: " من أسلف فى','1717','1482','0');
INSERT INTO [t22592] VALUES (1500,'(١٣٨٦) - (حديث: " أنه صلى الله عليه وسلم نهى عن بيع الطعام قبل قبضه , وعن ربح ما لم يضمنه "','1718','1482','0');
INSERT INTO [t22592] VALUES (1501,'(١٣٨٧) - (ثبت عن ابن عباس أنه قال: " إذا أسلمت فى شىء إلى أجل , فإن أخذت ما أسلفت فيه , وإلا فخذ عرضا أنقص منه , ولا تربح مرتين "','1718','1482','0');
INSERT INTO [t22592] VALUES (1502,'باب القرض','1719','1382','0');
INSERT INTO [t22592] VALUES (1503,'(١٣٨٨) - (حديث: " أن النبى صلى الله عليه وسلم كان يستقرض "','1719','1502','0');
INSERT INTO [t22592] VALUES (1504,'(١٣٨٩) - (حديث ابن مسعود مرفوعا: " ما من مسلم يقرض مسلما قرضا مرتين إلا كان كصدقتها (١) مرة "','1720','1502','0');
INSERT INTO [t22592] VALUES (1505,'(١٣٩٠) - (حديث: " أن النبى صلى الله عليه وسلم استلف بكرا "','1724','1502','0');
INSERT INTO [t22592] VALUES (1506,'(١٣٩١) - (حديث: " المسلمون على شروطهم "','1725','1502','0');
INSERT INTO [t22592] VALUES (1507,'(١٣٩٢) - (حديث: " أن النبى صلى الله عليه وسلم استسلف بكرا فرد مثله ".','1725','1502','0');
INSERT INTO [t22592] VALUES (1508,'(١٣٩٣) - (حديث: " أن النبى صلى الله عليه وسلم استقرض (١) من يهودى شعيرا ورهنه درعه ".','1725','1502','0');
INSERT INTO [t22592] VALUES (1509,'(١٣٩٤) - (حديث عائشة: " قلت: يا رسول الله إن الجيران يستقرضون الخبز والخمير ويردون زيادة ونقصانا. فقال: " لا بأس إنما ذلك من مرافق الناس لا يراد به الفضل "','1727','1502','0');
INSERT INTO [t22592] VALUES (1510,'(١٣٩٥) - (وعن معاذ أنه سئل عن اقتراض الخبز الخمير , فقال: " سبحان الله إنما هذا من مكارم الأخلاق , فخذ الكبير , وأعط الصغير , وخذ الصغير , وأعط الكبير , خيركم أحسنكم قضاء , سمعت رسول الله صلى الله عليه وسلم يقول ذلك "','1728','1502','0');
INSERT INTO [t22592] VALUES (1511,'(١٣٩٦) - (حديث: " أن النبى صلى الله عليه وسلم نهى عن بيع وسلف ".','1728','1502','0');
INSERT INTO [t22592] VALUES (1512,'(١٣٩٧) - (عن أبى بن كعب وابن مسعود وابن عباس رضى الله عنهم: " أنهم كرهوه , ونهوا عن قرض جر منفعة "','1729','1502','0');
INSERT INTO [t22592] VALUES (1513,'(١٣٩٨) - (ويروى: " كل قرض جر منفعة فهو ربا "','1730','1502','0');
INSERT INTO [t22592] VALUES (1514,'(١٣٩٩) - (حديث: " أنه استسلف بكرا ورد خيرا منه , وقال: خيركم أحسنكم قضاء ".','1731','1502','0');
INSERT INTO [t22592] VALUES (1515,'(١٤٠٠) - (حديث أنس مرفوعا: " إذا أقرض أحدكم قرضا فأهدى إليه أو حمله على الدابة فلا يركبها ولا يقبله إلا أن يكون جرى بينه وبينه قبل ذلك "','1731','1502','0');
INSERT INTO [t22592] VALUES (1516,'(١٤٠١) - (روى الأثرم: " أن رجلا كان له على سماك عشرون درهما , فجعل يهدى إليه السمك , ويقومه حتى بلغ ثلاثة عشر درهما فسأل ابن','1732','1502','0');
INSERT INTO [t22592] VALUES (1517,'(١٤٠٢) - (روى: " أن ابن الزبير كان يأخذ من قوم بمكة دراهم , ثم يكتب لهم بها إلى مصعب بن الزبير بالعراق فيأخذونها منه , فسئل عن ذلك ابن عباس ن فلم ير به بأسا ".','1733','1502','0');
INSERT INTO [t22592] VALUES (1518,'(١٤٠٣) - (روى عن على: " أنه سئل عن مثل ذلك , فلم ير بأسا "','1733','1502','0');
INSERT INTO [t22592] VALUES (1519,'(١٤٠٤) - (حديث: " لا ضرر ولا ضرار "','1733','1502','0');
INSERT INTO [t22592] VALUES (1520,'(١٤٠٥) - (حديث عن عائشة: " أن النبى صلى الله عليه وسلم اشترى من يهودى طعاما','1733','1502','0');
INSERT INTO [t22592] VALUES (1521,'(١٤٠٦) - (حديث: " لا يغلق الرهن من صاحبه الذى رهنه , له غنمه وعليه غرمه ". رواه الشافعى والدارقطنى , وقال: " إسناده حسن متصل ".','1734','1502','0');
INSERT INTO [t22592] VALUES (1522,'(١٤٠٧) - (حديث: " لا يغلق الرهن "','1738','1502','0');
INSERT INTO [t22592] VALUES (1523,'(١٤٠٨) - (حديث معاوية بن عبد الله بن جعفر: " أن رجلا رهن دارا بالمدينة إلى أجل مسمى , فمضى الأجل , فقال الذى ارتهن: منزلى , فقال النبى صلى الله عليه وسلم: لا يغلق الرهن "','1739','1502','0');
INSERT INTO [t22592] VALUES (1524,'(١٤٠٩) - (روى البخارى وغيره عن أبى هريرة مرفوعا: " الظهر يركب بنفقته إذا كان مرهونا , ولبن الدر يشرب بنفقته إذا كان مرهونا , وعلى الذى يركب ويشرب النفقة "','1739','1502','0');
INSERT INTO [t22592] VALUES (1525,'(١٤١٠) - (حديث: " لا يغلق الرهن من راهنه , له غنمه وعليه غرمه ".','1740','1502','0');
INSERT INTO [t22592] VALUES (1526,'باب الضمان والكفالة','1740','1382','0');
INSERT INTO [t22592] VALUES (1527,'(١٤١١) - (قال ابن عباس: " الزعيم الكفيل "','1740','1526','0');
INSERT INTO [t22592] VALUES (1528,'(١٤١٢) - (حديث: " الزعيم غارم "','1740','1526','0');
INSERT INTO [t22592] VALUES (1529,'(١٤١٣) - (حديث: عن ابن عباس: " أن النبى صلى الله عليه وسلم تحمل عشرة دنانير عن رجل قد لزمه غريمه إلى شهر , وقضاها عنه "','1741','1526','0');
INSERT INTO [t22592] VALUES (1530,'فصل','1742','1382','0');
INSERT INTO [t22592] VALUES (1531,'١٤١٤) - (حديث: " الزعيم غارم "','1742','1530','0');
INSERT INTO [t22592] VALUES (1532,'(١٤١٥) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " لا كفالة فى حد "','1742','1530','0');
INSERT INTO [t22592] VALUES (1533,'(١٤١٦) - (حديث جابر: " أتى النبى صلى الله عليه وسلم برجل ليصلى عليه فقال: أعليه دين؟ قلنا: ديناران , فانصرف , فتحملها أبو قتادة , فصلى عليه النبى صلى الله عليه وسل','1743','1530','0');
INSERT INTO [t22592] VALUES (1534,'باب الحوالة','1744','1382','0');
INSERT INTO [t22592] VALUES (1535,'(١٤١٧) - (حديث: " الزعيم غارم "','1744','1534','0');
INSERT INTO [t22592] VALUES (1536,'(١٤١٨) - (حديث: " مطل الغنى ظلم وإذا أتبع أحدكم على ملىء','1744','1534','0');
INSERT INTO [t22592] VALUES (1537,'(١٤١٩) - (حديث: " المؤمنون على شروطهم "','1745','1534','0');
INSERT INTO [t22592] VALUES (1538,'باب الصلح','1745','1382','0');
INSERT INTO [t22592] VALUES (1539,'(١٤٢٠) - (عن أبى هريرة مرفوعا: " الصلح جائز بين المسلمين إلا','1745','1538','0');
INSERT INTO [t22592] VALUES (1540,'(١٤٢١) - (حديث: " أن النبى صلى الله عليه وسلم كلم غرماء جابر , فوضعوا عنه الشطر "','1746','1538','0');
INSERT INTO [t22592] VALUES (1541,'(١٤٢٢) - (حديث: " أن النبى صلى الله عليه وسلم كلم كعب بن مالك , فوضع عن غريمه الشطر "','1746','1538','0');
INSERT INTO [t22592] VALUES (1542,'(١٤٢٣) - (حديث: أن النبى صلى الله عليه وسلم قال لرجلين اختصما فى مواريث درست بينهما: " استهما وتوخيا الحق وليحلل أحدكما صاحبه "','1747','1538','0');
INSERT INTO [t22592] VALUES (1543,'(١٤٢٤) - (نهى عمر أن تباع العين بالدين " قاله ابن عمر','1748','1538','0');
INSERT INTO [t22592] VALUES (1544,'فصل','1748','1382','0');
INSERT INTO [t22592] VALUES (1545,'(١٤٢٥) - (حديث: " الصلح جائز بين المسلمين "','1748','1544','0');
INSERT INTO [t22592] VALUES (1546,'(١٤٢٦) - (قوله صلى الله عليه وسلم: " ... إلا صلحا حرم حلالا أو أحل حراما "','1748','1544','0');
INSERT INTO [t22592] VALUES (1547,'فصل','1748','1382','0');
INSERT INTO [t22592] VALUES (1548,'(١٤٢٧) - (روى: " أن الضحاك بن خليفة ساق خليجا من العريض , فأراد أن يمر فى أرض محمد بن مسلمة , فأبى , فكلم فيه عمرا فدعى محمدا , وأمره أن يخلى سبيله , فقال: لا والله , فقال له عمر: لم تمنع أخاك ما ينفعه وهو لك نافع تسقى به أولا وآخرا وهو لا يضرك؟ !','1748','1547','0');
INSERT INTO [t22592] VALUES (1549,'(١٤٢٧/١) - (حديث: "لا ضرر ولا ضرار"','1749','1547','0');
INSERT INTO [t22592] VALUES (1550,'(١٤٢٨) - (حديث: " لو أن أحدا اطلع إليك , فخذفته بحصاة ففقأت عينه لم يكن عليك جناح ".','1749','1547','0');
INSERT INTO [t22592] VALUES (1551,'(١٤٢٩) - (حديث: " لا ضرر ولا ضرار "','1749','1547','0');
INSERT INTO [t22592] VALUES (1552,'(١٤٣٠) - (حديث أبى هريرة يرفعه: " لا يمنعن جار جاره أن يضع خشبة على جداره ـ ثم يقول أبو هريرة: ما لى أراكم عنها معرضين , والله لأرمين بها بين أكتافكم "','1749','1547','0');
INSERT INTO [t22592] VALUES (1553,'(١٤٣١) - (حديث عمر: " لما اجتاز على دار العباس وقد نصب ميزابا إلى الطريق , فقلعه عمر فقال العباس تقلعه وقد نصبه رسول الله صلى الله عليه وسلم بيده؟ ! ! فقال عمر: والله لا تنصبه إلى على ظهرى , فانحنى حتى صعد على ظهره فنصبه "','1751','1547','0');
INSERT INTO [t22592] VALUES (1554,'(١٤٣٢) - (حديث: " لا ضرر ولا ضرار "','1753','1547','0');
INSERT INTO [t22592] VALUES (1555,'كتاب الحجر','1753','1382','0');
INSERT INTO [t22592] VALUES (1556,'(١٤٣٣) - (حديث: " من ترك حقا فلورثته "','1753','1555','0');
INSERT INTO [t22592] VALUES (1557,'(١٤٣٤) - (قوله صلى الله عليه وسلم: " لى الواجد ظلم (يجل) [١] عرضه وعقوبته " رواه أحمد وأبو داود وغيرهما.','1754','1555','0');
INSERT INTO [t22592] VALUES (1558,'(١٤٣٥) - (حديث: " أن النبى صلى الله عليه وسلم حجر على معاذ , وباع ماله فى دينه ".','1755','1555','0');
INSERT INTO [t22592] VALUES (1559,'(١٤٣٦) - (وعن عمر أنه خطب فقال: " ألا إن أسيفع جهينة رضى من دينه وأمانته بأن يقال: سبق الحاج فادان معرضا , فأصبح وقد دين به , فمن كان له عليه دين فليحضر غدا , فإنا بائعون ماله , وقاسموه بين غرمائه "','1757','1555','0');
INSERT INTO [t22592] VALUES (1560,'(١٤٣٧) - (حديث: " خذوا ما وجدتم وليس لكم إلا ذلك "','1758','1555','0');
INSERT INTO [t22592] VALUES (1561,'(١٤٣٨) - (حديث بريدة مرفوعا: " من أنظر معسرا فله بكل يوم مثليه صدقة ".','1758','1555','0');
INSERT INTO [t22592] VALUES (1562,'(١٤٣٩) - (حديث كعب بن مالك: " أن النبى صلى الله عليه وسلم حجر على معاذ , وباع ماله "','1759','1555','0');
INSERT INTO [t22592] VALUES (1563,'(١٤٤٠) - (روى: " أن رجلا قدم المدينة وذكر أن وراءه مالا , فداينه الناس , ولم يكن وراءه مال , فسماه النبى صلى الله عليه وسلم سرقا وباعه بخمسة أبعرة ".','1759','1555','0');
INSERT INTO [t22592] VALUES (1564,'(١٤٤١) - (روى أبو سعيد: " أن رجلا أصيب فى ثمار ابتاعها , فكثر دينه فقال النبى صلى الله عليه وسلم تصدقوا عليه , فتصدقوا عليه , فلم يبلغ ذلك وفاء دينه , فقال النبى صلى الله عليه وسلم خذوا ما وجدتم , وليس لكم إلا ذلك ".','1763','1555','0');
INSERT INTO [t22592] VALUES (1565,'فصل','1763','1382','0');
INSERT INTO [t22592] VALUES (1566,'(١٤٤٢) - (حديث: " من أدرك ماله بعينه عند رجل قد أفلس أو إنسان قد أفلس فهو أحق به من غيره "','1763','1565','0');
INSERT INTO [t22592] VALUES (1567,'(١٤٤٣) - (حديث: " أيما رجل باع متاعا فأفلس الذى ابتاعه , ولم يقبض الذى باعه من ثمنه شيئا , فوجد متاعه بعينه , فهو أحق به , وإن مات المشترى فصاحب المتاع أسوة الغرماء " رواه مالك وأبو داود , وهو مرسل وقد أسنده أبو داود من وجه ضعيف (ص ٣٨١) .','1767','1565','0');
INSERT INTO [t22592] VALUES (1568,'(١٤٤٤) - (حديث أبى هريرة: " أيما رجل أفلس فوجد رجل عنده ماله , ولم يكن اقتضى من ماله شيئا , فهو له " رواه أحمد.','1767','1565','0');
INSERT INTO [t22592] VALUES (1569,'(١٤٤٥) - (قوله صلى الله عليه وسلم: " من أدرك متاعه بعينه "','1768','1565','0');
INSERT INTO [t22592] VALUES (1570,'(١٤٤٦) - (حديث: " الخراج بالضمان "','1768','1565','0');
INSERT INTO [t22592] VALUES (1571,'(١٤٤٧) - (قوله صلى الله عليه وسلم: " خذوا ما وجدتم وليس لكم إلا ذلك "','1768','1565','0');
INSERT INTO [t22592] VALUES (1572,'(١٤٤٨) - (قوله صلى الله عليه وسلم: " خذوا ما وجدتم وليس لكم إلا ذلك "','1768','1565','0');
INSERT INTO [t22592] VALUES (1573,'فصل','1768','1382','0');
INSERT INTO [t22592] VALUES (1574,'(١٤٤٩) - (روى عروة بن الزبير: " أن عبد الله بن جعفر ابتاع بيعا فقال على: لآتين عثمان , فلأحجرن عليه , فأعلم ذلك ابن جعفر الزبير فقال: أنا شريكك فى بيعتك. فأتى على عثمان فقال: إن ابن جعفر قد ابتاع بيع كذا فاحجر عليه , فقال الزبير: أنا شريكه , فقال عثم','1768','1573','0');
INSERT INTO [t22592] VALUES (1575,'(١٤٥٠) - (حديث: " رفع القلم عن ثلاث: عن الصبى حتى يحتلم ... "','1769','1573','0');
INSERT INTO [t22592] VALUES (1576,'(١٤٥١) - (حديث: " لا يتم بعد احتلام "','1769','1573','0');
INSERT INTO [t22592] VALUES (1577,'(١٤٥٢) - (حديث ابن عمر: " عرضت على النبى صلى الله عليه وسلم يوم أحد , وأنا ابن أربع عشرة سنة , فلم يجزنى , وعرضت عليه يوم الخندق , وأنا ابن خمس عشرة سنة , فأجازنى "','1769','1573','0');
INSERT INTO [t22592] VALUES (1578,'(١٤٥٣) - (حديث سعد بن معاذ وقول الرسول له: " لقد حكمت بحكم الله من فوق سبعة أرقعة','1769','1573','0');
INSERT INTO [t22592] VALUES (1579,'(١٤٥٤) - (حديث: " لا يقبل الله صلاة حائض إلا بخمار "','1771','1573','0');
INSERT INTO [t22592] VALUES (1580,'فصل','1771','1382','0');
INSERT INTO [t22592] VALUES (1581,'(١٤٥٥) - (حديث عائشة: " أن قوله تعالى: (ومن كان فقيرا','1771','1580','0');
INSERT INTO [t22592] VALUES (1582,'(١٤٥٦) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن رجلا أتى النبى صلى الله عليه وسلم فقال: إنى فقير , وليس لى شىء ولى يتيم , فقال: كل من مال يتيمك غير مسرف "','1772','1580','0');
INSERT INTO [t22592] VALUES (1583,'(١٤٥٧) - (حديث عائشة مرفوعا: " إذا أنفقت المرأة من طعام زوجها غير مفسدة كان لها أجرها بما أنفقت ولزوجها أجر ما كسب , وللخازن مثل ذلك لا ينتقص بعضهم من أجر بعض شيئا "','1773','1580','0');
INSERT INTO [t22592] VALUES (1584,'(١٤٥٨) - (حديث: " إن دماءكم وأموالكم وأعراضكم حرام عليكم ... "','1773','1580','0');
INSERT INTO [t22592] VALUES (1585,'(١٤٥٩) - (حديث: " لا يحل مال امرىء مسلم إلا عن طيب نفس ".','1774','1580','0');
INSERT INTO [t22592] VALUES (1586,'باب الوكالة','1777','1382','0');
INSERT INTO [t22592] VALUES (1587,'(١٤٦٠) - (حديث عروة بن الجعد وغيره "','1777','1586','0');
INSERT INTO [t22592] VALUES (1588,'(١٤٦٠/١) - (حديث: " أن النبي صلى الله عليه وسلم وكل عمرو بن أمية في قبول نكاح أم حبيبة "','1777','1586','0');
INSERT INTO [t22592] VALUES (1589,'(١٤٦٠/٢) - (حديث " أن النبي صلى الله عليه وسلم وكل أبا رافع في قبول نكاح ميمونة "','1778','1586','0');
INSERT INTO [t22592] VALUES (1590,'(١٤٦١) - (حديث: " أنه صلى الله عليه وسلم كان يبعث عماله لقبض الصدقات وتفريقها "','1779','1586','0');
INSERT INTO [t22592] VALUES (1591,'(١٤٦٢) - (حديث معاذ وفيه: " فأخبرهم أن الله افترض عليهم صدقة تؤخذ من أغنيائهم , فترد إلى فقرائهم "','1779','1586','0');
INSERT INTO [t22592] VALUES (1592,'(١٤٦٣) - (حديث: " فإن قتل زيد فجعفر ... "','1779','1586','0');
INSERT INTO [t22592] VALUES (1593,'(١٤٦٤) - (حديث: " واغد يا أنيس إلى امرأة هذا , فإن اعترفت فارجمها , فاعترفت فأمر بها فرجمت "','1781','1586','0');
INSERT INTO [t22592] VALUES (1594,'(١٤٦٥) - (روى: " أن عليا وكل عقيلا عند أبى بكر وقال: ما قضى عليه , فهو على , وما قضى له فلى "','1782','1586','0');
INSERT INTO [t22592] VALUES (1595,'(١٤٦٦) - (أثر: " أن عليا رضى الله عنه وكل عبد الله بن جعفر عند عثمان وقال: إن للخصومة قحما ـ أى مهالك ـ وإن الشيطان يحضرها , وإنى أكره أن أحضرها "','1782','1586','0');
INSERT INTO [t22592] VALUES (1596,'فصل','1782','1382','0');
INSERT INTO [t22592] VALUES (1597,'(١٤٦٧) - (أثر ابن عباس: " أنه كان لا يرى بذلك بأسا , يعنى إن','1782','1596','0');
INSERT INTO [t22592] VALUES (1598,'كتاب الشركة','1783','1382','0');
INSERT INTO [t22592] VALUES (1599,'(١٤٦٨) - (حديث: " يقول الله تعالى: أنا ثالث الشريكين ما لم يخن أحدهما صاحبه , فإذا خان أحدهما صاحبه خرجت من بينهما "','1783','1598','0');
INSERT INTO [t22592] VALUES (1600,'(١٤٦٩) - (حديث: " وقال زيد: كنت أنا والبراء شريكين , فاشترينا فضة بنقد ونسيئة.. "','1785','1598','0');
INSERT INTO [t22592] VALUES (1601,'فصل فى المضاربة','1785','1382','0');
INSERT INTO [t22592] VALUES (1602,'(١٤٧٠) - (يروى: " إباحتها عن عمر , وعثمان , وعلى , وابن مسعود , وحكيم بن حزام , رضى الله عنهم , فى قصص مشهورة "','1785','1601','0');
INSERT INTO [t22592] VALUES (1603,'(١٤٧١) - (حديث: " أن النبى صلى الله عليه وسلم عامل أهل خيبر بشطر ما يخرج منها "','1789','1601','0');
INSERT INTO [t22592] VALUES (1604,'(١٤٧٢) - (حديث حكيم بن حزام قوله: " أنه كان يشترط على الرجل إذا أعطاه مالا مقارضة يضرب له به ألا تجعل مالى فى كبد رطبة ولا تحمله فى بحر , ولا تنزل به فى بطن مسيل , فإن فعلت شيئا من','1789','1601','0');
INSERT INTO [t22592] VALUES (1605,'(١٤٧٣) - (حديث: " المؤمنون عند شروطهم ".','1790','1601','0');
INSERT INTO [t22592] VALUES (1606,'فصل فى شركة الوجوه والأبدان','1790','1382','0');
INSERT INTO [t22592] VALUES (1607,'(١٤٧٤) - (قول ابن مسعود: " اشتركت أنا وسعد وعمار يوم بدر , فلم أجىء أنا وعمار بشىء , وجاء سعد بأسيرين "','1790','1606','0');
INSERT INTO [t22592] VALUES (1608,'(١٤٧٤/١) - (حديث: "أن النبي صلى الله عليه وسلم قال: من أخذ شيئا فهو له"','1790','1606','0');
INSERT INTO [t22592] VALUES (1609,'(١٤٧٥) - (حديث: " أن النبى صلى الله عليه وسلم أعطى خيبر على الشطر "','1790','1606','0');
INSERT INTO [t22592] VALUES (1610,'(١٤٧٦) - (حديث: " أن النبى صلى الله عليه وسلم نهى عن عسب الفحل وعن قفيز الطحان ".','1790','1606','0');
INSERT INTO [t22592] VALUES (1611,'باب المساقاة','1792','1382','0');
INSERT INTO [t22592] VALUES (1612,'(١٤٧٧) - (حديث ابن عمر: " عامل النبى صلى الله عليه وسلم أهل خيبر بشطر ما يخرج منها من ثمر أو زرع "','1792','1611','0');
INSERT INTO [t22592] VALUES (1613,'(١٤٧٨) - (حديث ابن عمر: " كنا نخابر أربعين سنة حتى حدثنا رافع بن خديج أن رسول الله صلى الله عليه وسلم نهى عن المخابرة "','1792','1611','0');
INSERT INTO [t22592] VALUES (1614,'(١٤٧٩) - (حديث رافع: " كنا نكرى الأرض بالناحية منها "','1796','1611','0');
INSERT INTO [t22592] VALUES (1615,'(١٤٨٠) - (حديث ابن عمر: " دفع رسول الله صلى الله عليه وسلم نخل خيبر وأرضها إليهم على أن يعملوها من أموالهم "','1797','1611','0');
INSERT INTO [t22592] VALUES (1616,'(١٤٨١) - (وعن عمر: " أنه كان يعامل الناس على إن عمر جاء بالبذر من عنده , فله الشطر , وإن جاءوا بالبذر , فلهم كذا "','1797','1611','0');
INSERT INTO [t22592] VALUES (1617,'(١٤٨٢) - (قول رافع: " أما بالذهب والفضة فلا بأس " ولمسلم: " أو بشىء معلوم مضمون فلا بأس "','1799','1611','0');
INSERT INTO [t22592] VALUES (1618,'(١٤٨٣) - (حديث ابن عباس موقوفا: " إن أمثل ما أنتم صانعون أن تستأجروا الأرض البيضاء من السنة إلى السنة "','1799','1611','0');
INSERT INTO [t22592] VALUES (1619,'(١٤٨٤) - (حديث رافع: " لا يكريها بطعام مسمى "','1800','1611','0');
INSERT INTO [t22592] VALUES (1620,'(١٤٨٥) - (قوله صلى الله عليه وسلم: " نقركم على ذلك ما شئنا "','1800','1611','0');
INSERT INTO [t22592] VALUES (1621,'(١٤٨٦) - (حديث: " المؤمنون على شروطهم "','1801','1611','0');
INSERT INTO [t22592] VALUES (1622,'(١٤٨٧) - (حديث: " أن النبى صلى الله عليه وسلم دفع خيبر إلى يهود على أن يعملوها من أموالهم "','1801','1611','0');
INSERT INTO [t22592] VALUES (1623,'باب الإجارة','1802','1382','0');
INSERT INTO [t22592] VALUES (1624,'(١٤٨٨) - (حديث: " أن موسى ـ عليه السلام ـ آجر نفسه ثمانى حجج أو عشرا على عفة فرجه وطعام بطنه "','1802','1623','0');
INSERT INTO [t22592] VALUES (1625,'(١٤٨٩) - (حديث: وفى الصحيح: " أن النبى صلى الله عليه وسلم استأجر رجلا من بنى الديل هاديا خريتا "','1802','1623','0');
INSERT INTO [t22592] VALUES (1626,'(١٤٨٩/١) - (وفيه يعني الصحيح: ثلاثة أنا خصمهم يوم القيامة رجل أعطى بي ثم غدر ورجل باع حرا فأكل ثمنه ورجل استأجر أجيرا فاستوفى منه ولم يؤته أجرته)','1803','1623','0');
INSERT INTO [t22592] VALUES (1627,'(١٤٩٠) - (حديث أبى سعيد مرفوعا: " نهى عن استئجار الأجير حتى يبين له أجره ".','1806','1623','0');
INSERT INTO [t22592] VALUES (1628,'فصل','1808','1382','0');
INSERT INTO [t22592] VALUES (1629,'(١٤٩١) - (حديث على: " أنه آجر نفسه من يهودى , يستقى له كل دلو بتمرة , وجاء به إلى النبى صلى الله عليه وسلم , فأكل منه "','1808','1628','0');
INSERT INTO [t22592] VALUES (1630,'(١٤٩٢) - (حديث عثمان بن أبى العاص: " واتخذ مؤذنا لا يأخذ على أذانه أجرا ".','1810','1628','0');
INSERT INTO [t22592] VALUES (1631,'(١٤٩٣) - (حديث أبى بن كعب قال: " علمت رجلا القرآن , فأهدى إلى قوسا , فذكرت ذلك للنبى صلى الله عليه وسلم فقال: إن أخذتها أخذت قوسا من نار , فرددتها ".','1811','1628','0');
INSERT INTO [t22592] VALUES (1632,'(١٤٩٤) - (حديث: " أحق ما أخذتم عليه أجرا كتاب الله "','1812','1628','0');
INSERT INTO [t22592] VALUES (1633,'فصل','1814','1382','0');
INSERT INTO [t22592] VALUES (1634,'(١٤٩٥) - (روى أحمد فى " المسند " عن على رضى الله عنه: " أنه كان يضمن الأجراء , ويقول: لا يصلح الناس إلا هذا "','1814','1633','0');
INSERT INTO [t22592] VALUES (1635,'(١٤٩٦) - (روى جعفر بن محمد عن أبيه {عن} على: " أنه كان يضمن الصباغ والصواغ , وقال: لا يصلح الناس إلا هذا "','1814','1633','0');
INSERT INTO [t22592] VALUES (1636,'(١٤٩٧) - (روى أن عمر: " قضى فى طفلة ماتت من الختان بديتها على عاقلة خاتنتها "','1815','1633','0');
INSERT INTO [t22592] VALUES (1637,'(١٤٩٨) - (قوله صلى الله عليه وسلم: " أعطوا الأجير أجره قبل أن يجف عرقه ".','1815','1633','0');
INSERT INTO [t22592] VALUES (1638,'(١٤٩٩) - (روى الأثرم عن ابن عمر قال: " لا يصلح الكرى بالضمان "','1819','1633','0');
INSERT INTO [t22592] VALUES (1639,'باب المسابقة','1820','1382','0');
INSERT INTO [t22592] VALUES (1640,'(١٥٠٠) - (روى مسلم مرفوعا: " ألا إن القوة الرمى "','1820','1639','0');
INSERT INTO [t22592] VALUES (1641,'(١٥٠١) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم سابق بين الخيل المضمرة من الحفيا إلى ثنية الوداع وبين التى لم تضمر من ثنية الوداع إلى مسجد بنى زريق "','1821','1639','0');
INSERT INTO [t22592] VALUES (1642,'(١٥٠٢) - (حديث: " سابق النبى صلى الله عليه وسلم عائشة على قدميه "','1822','1639','0');
INSERT INTO [t22592] VALUES (1643,'(١٥٠٣) - (حديث: " صارع ركانة فصرعه "','1824','1639','0');
INSERT INTO [t22592] VALUES (1644,'(١٥٠٤) - (حديث: " وسابق سلمة بن الأكوع رجلا من الأنصار بين يدى رسول الله صلى الله عليه وسلم "','1826','1639','0');
INSERT INTO [t22592] VALUES (1645,'(١٥٠٥) - (حديث:" أن النبى صلى الله عليه وسلم مر بقوم يرفعون حجرا ليعلموا الشديد منهم فلم ينكر عليهم "','1827','1639','0');
INSERT INTO [t22592] VALUES (1646,'(١٥٠٦) - (حديث أبى هريرة: " لا سبق إلا فى نصل أو خف أو حافر ".','1828','1639','0');
INSERT INTO [t22592] VALUES (1647,'(١٥٠٧) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم سبق بين الخيل وأعطى السابق "','1831','1639','0');
INSERT INTO [t22592] VALUES (1648,'(١٥٠٨) - (حديث ابن مسعود مرفوعا: " الخيل ثلاثة , فرس للرحمن , وفرس للإنسان , وفرس للشيطان , فأما فرس الرحمن فالذى يربط فى سبيل الله , فعلفه وروثه وبوله وذكر ما شاء الله أجر , وأما فرس الشيطان فالذى يقامر ويراهن عليه "','1833','1639','0');
INSERT INTO [t22592] VALUES (1649,'(١٥٠٩) - (حديث أبى هريرة: " من أدخل فرسا بين فرسين , وهو لا يأمن أن يسبق , فليس قمارا , ومن أدخل فرسا بين فرسين , وقد أمن أن يسبق فهو قمار "','1835','1639','0');
INSERT INTO [t22592] VALUES (1650,'(١٥١٠) - (حديث: " ما بين الغرضين روضة من رياض الجنة "','1837','1639','0');
INSERT INTO [t22592] VALUES (1651,'كتاب العارية','1838','1382','0');
INSERT INTO [t22592] VALUES (1652,'(١٥١١) - (حديث: " هل على غيرها؟ قال: لا إلا أن تطوع "','1838','1651','0');
INSERT INTO [t22592] VALUES (1653,'(١٥١٢) - (حديث: " أن النبى صلى الله عليه وسلم استعار من أبى طلحة فرسا فركبها "','1838','1651','0');
INSERT INTO [t22592] VALUES (1654,'(١٥١٣) - (حديث: " أن النبى صلى الله عليه وسلم استعار من صفوان بن أمية أدراعا "','1839','1651','0');
INSERT INTO [t22592] VALUES (1655,'(١٥١٣/١) - (حديث: " أنه صلى الله عليه وسلم ذكر في حق الإبل والبقر والغنم إعارة دلوها، وإطراق فحلها "','1841','1651','0');
INSERT INTO [t22592] VALUES (1656,'(١٥١٤) - (حديث: " لا ضرر ولا إضرار ".','1842','1651','0');
INSERT INTO [t22592] VALUES (1657,'فصل','1843','1382','0');
INSERT INTO [t22592] VALUES (1658,'(١٥١٥) - (حديث صفوان بن أمية: " بل عارية مضمونة "','1843','1657','0');
INSERT INTO [t22592] VALUES (1659,'(١٥١٥/١) - (وروي: " مؤداة ".','1843','1657','0');
INSERT INTO [t22592] VALUES (1660,'(١٥١٦) - (حديث سمرة مرفوعا: " على اليد ما أخذت حتى تؤديه "','1843','1657','0');
INSERT INTO [t22592] VALUES (1661,'كتاب الغصب','1845','1382','0');
INSERT INTO [t22592] VALUES (1662,'(١٥١٧) - (حديث: " إن دماءكم وأموالكم عليكم حرام "','1845','1661','0');
INSERT INTO [t22592] VALUES (1663,'(١٥١٨) - (حديث: " لا يأخذ أحدكم متاع أخيه لا لاعبا , ولا جادا , ومن أخذ عصا أخيه فليردها "','1845','1661','0');
INSERT INTO [t22592] VALUES (1664,'(١٥١٩) - (حديث رافع بن خديج: " من زرع فى أرض قوم بغير','1845','1661','0');
INSERT INTO [t22592] VALUES (1665,'(١٥٢٠) - (حديث: " ليس لعرق ظالم حق "','1848','1661','0');
INSERT INTO [t22592] VALUES (1666,'فصل','1852','1382','0');
INSERT INTO [t22592] VALUES (1667,'(١٥٢١) - (حديث: " الخراج بالضمان "','1852','1666','0');
INSERT INTO [t22592] VALUES (1668,'(١٥٢٢) - (حديث: " من أعتق شركا له فى عبد قوم عليه قيمة العدل "','1852','1666','0');
INSERT INTO [t22592] VALUES (1669,'(١٥٢٣) - (حديث: " القصعة لما كسرتها إحدى نسائه ".','1854','1666','0');
INSERT INTO [t22592] VALUES (1670,'فصل','1856','1382','0');
INSERT INTO [t22592] VALUES (1671,'(١٥٢٤) - (حديث: " العجماء جرحها جبار "','1856','1670','0');
INSERT INTO [t22592] VALUES (1672,'(١٥٢٥) - (حديث النعمان بن بشير مرفوعا: " من وقف دابة فى سابلة من سبل المسلمين , أو فى سوق من أسواقهم فما وطئت بيد أو رجل , فهو ضامن "','1856','1670','0');
INSERT INTO [t22592] VALUES (1673,'(١٥٢٦) - (حديث أبى هريرة مرفوعا: " الرجل جبار "','1856','1670','0');
INSERT INTO [t22592] VALUES (1674,'(١٥٢٧) - (حديث حرام (١) بن محيصة: " أن ناقة البراء بن عازب دخلت حائطا فأفسدت فيه فقضى نبى الله صلى الله عليه وسلم أن على أهل الحوائط حفظها بالنهار , وأن ما أفسدت المواشى بالليل ضامن على أهلها ". قال ابن عبد البر: " وإن كان مرسلا فهو مشهور , وحدث ب','1857','1670','0');
INSERT INTO [t22592] VALUES (1675,'(١٥٢٨) - (حديث ابن عمر: " من أريد ماله بغير حق فقاتل فقتل فهو شهيد ".','1858','1670','0');
INSERT INTO [t22592] VALUES (1676,'(١٥٢٩) - (حديث ابن عمر: " أن النبى صلى الله عليه وسلم أمره أن يأخذ مدية ثم خرج إلى أسواق المدينة , وفيها زقاق الخمر قد جلبت من الشام , فشققت','1859','1670','0');
INSERT INTO [t22592] VALUES (1677,'(١٥٣٠) - (حديث أبى الهياج: " قال: قال لى على رضى الله عنه: ألا أبعثك على ما بعثنى عليه رسول الله صلى الله عليه وسلم ألا تدع تمثالا إلا طمسته ولا قبرا مشرفا إلا سويته "','1863','1670','0');
INSERT INTO [t22592] VALUES (1678,'(١٥٣١) - (حديث: " أن النبى صلى الله عليه وسلم حرق مسجد الضرار , وأمر بهدمه "','1865','1670','0');
INSERT INTO [t22592] VALUES (1679,'باب الشفعة','1867','1382','0');
INSERT INTO [t22592] VALUES (1680,'(١٥٣٢) - (حديث جابر مرفوعا: " قضى بالشفعة فى كل ما لم يقسم.. "','1867','1679','0');
INSERT INTO [t22592] VALUES (1681,'(١٥٣٣) - (حديث أنس أن النبى صلى الله عليه وسلم قال: " لا شفعة لنصرانى ".','1869','1679','0');
INSERT INTO [t22592] VALUES (1682,'(١٥٣٤) - (حديث جابر: " هو أحق به بالثمن "','1869','1679','0');
INSERT INTO [t22592] VALUES (1683,'(١٥٣٥) - (حديث أبى هريرة مرفوعا: " لا ترتكبوا ما ارتكبت اليهود فتستحلوا محارم الله بأدنى الحيل ".','1870','1679','0');
INSERT INTO [t22592] VALUES (1684,'(١٥٣٦) - (حديث جابر: " الشفعة فيما لم يقسم فإذا وقعت الحدود فلا شفعة "','1870','1679','0');
INSERT INTO [t22592] VALUES (1685,'(١٥٣٧) - (وعنه أيضا: " إنما جعل رسول الله صلى الله عليه وسلم الشفعة فى كل ما لم يقسم فإذا وقعت الحدود وصرفت الطرق فلا شفعة "','1870','1679','0');
INSERT INTO [t22592] VALUES (1686,'(١٥٣٨) - (حديث أبى رافع مرفوعا: " الجار أحق بصقبة) [١] " رواه البخارى وأبو داود. * صحيح.','1871','1679','0');
INSERT INTO [t22592] VALUES (1687,'(١٥٣٩) - (حديث الحسن عن سمرة مرفوعا: " جار الدار أحق بالدار "','1872','1679','0');
INSERT INTO [t22592] VALUES (1688,'(١٥٤٠) - (حديث جابر: " الجار أحق بشفعته (١) ينتظر به وإن كان غائبا (إذ) [١] كان طريقهما واحدا " (ص ٤٤٣) .','1873','1679','0');
INSERT INTO [t22592] VALUES (1689,'(١٥٤١) - (حديث جابر: " قضى رسول الله صلى الله عليه وسلم بالشفعة فى كل شركة لم تقسم ربعة أو حائط "','1874','1679','0');
INSERT INTO [t22592] VALUES (1690,'(١٥٤٢) - (حديث ابن عمر: " الشفعة كحل العقال " رواه ابن ماجه. وفى لفظ: " الشعفة كنشط العقال , إن قيدت ثبتت , وإن تركت فاللوم على من تركها "','1874','1679','0');
INSERT INTO [t22592] VALUES (1691,'(١٥٤٣) - (حديث جابر: " هو أحق به بالثمن "','1875','1679','0');
INSERT INTO [t22592] VALUES (1692,'باب الوديعة','1876','1382','0');
INSERT INTO [t22592] VALUES (1693,'(١٥٤٤) - (وقال النبى صلى الله عليه وسلم: " أد الأمانة إلى من ائتمنك ... "','1876','1692','0');
INSERT INTO [t22592] VALUES (1694,'فصل','1878','1382','0');
INSERT INTO [t22592] VALUES (1695,'(١٥٤٥) - (حديث: " إن المسافر وماله لعلى فلت إلا ما وقى الله "','1878','1694','0');
INSERT INTO [t22592] VALUES (1696,'(١٥٤٦) - (حديث: " روى أنه صلى الله عليه وسلم كان عنده ودائع فلما أراد الهجرة أودعها عند أم أيمن وأمر عليا أن يردها إلى أهلها ".','1879','1694','0');
INSERT INTO [t22592] VALUES (1697,'فصل','1880','1382','0');
INSERT INTO [t22592] VALUES (1698,'(١٥٤٧) - (حديث عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " من أودع وديعة فلا ضمان عليه "','1880','1697','0');
INSERT INTO [t22592] VALUES (1699,'(١٥٤٨) - (خبر: " أن عمر بن الخطاب رضى الله عنه ضمن أنسا وديعة ذهبت من بين ماله "','1881','1697','0');
INSERT INTO [t22592] VALUES (1700,'باب إحياء الموات','1883','1382','0');
INSERT INTO [t22592] VALUES (1701,'(١٥٤٩) - (روى سعيد فى سننه عن طاووس مرفوعا: " عادى الأرض لله ورسوله ثم هى لكم بعد "','1883','1700','0');
INSERT INTO [t22592] VALUES (1702,'(١٥٥٠) - (حديث جابر مرفوعا: " من أحيا أرضا ميتة فهى له "','1884','1700','0');
INSERT INTO [t22592] VALUES (1703,'(١٥٥١) - (عن سعيد بن زيد مرفوعا: " من أحيا أرضا ميتة فهى له وليس لعرق ظالم حق "','1886','1700','0');
INSERT INTO [t22592] VALUES (1704,'(١٥٥٢) - (حديث: " الناس شركاء فى ثلاث: فى الماء والكلأ والنار " رواه الخلال وابن ماجه من حديث ابن عباس وزاد فيه: " وثمنه حرام "','1886','1700','0');
INSERT INTO [t22592] VALUES (1705,'(١٥٥٣) - (حديث: " من سبق إلى ما لم يسبق إليه أحد فهو له "','1889','1700','0');
INSERT INTO [t22592] VALUES (1706,'فصل','1890','1382','0');
INSERT INTO [t22592] VALUES (1707,'(١٥٥٤) - (حديث جابر مرفوعا: " من أحاط حائطا على أرض فهى له "','1890','1706','0');
INSERT INTO [t22592] VALUES (1708,'(١٥٥٥) - (حديث: " من سبق إلى ما لم يسبق إليه مسلم فهو أحق به "','1891','1706','0');
INSERT INTO [t22592] VALUES (1709,'باب الجعالة','1892','1382','0');
INSERT INTO [t22592] VALUES (1710,'(١٥٥٦) - (حديث أبى سعيد: " فى رقية اللديغ على قطيع من الغنم "','1892','1709','0');
INSERT INTO [t22592] VALUES (1711,'(١٥٥٧) - (حديث ابن أبى مليكة وعمرو بن دينار: " أن النبى صلى الله عليه وسلم جعل رد الآبق إذا جاء به خارجا من الحرم دينارا "','1893','1709','0');
INSERT INTO [t22592] VALUES (1712,'باب اللقطة','1895','1382','0');
INSERT INTO [t22592] VALUES (1713,'(١٥٥٨) - (حديث جابر قال: " رخص رسول الله صلى الله عليه وسلم فى العصا والسوط والحبل [وأشباهه] يلتقطه الرجل ينتفع به "','1895','1712','0');
INSERT INTO [t22592] VALUES (1714,'(١٥٥٩) - (حديث أنس: " أن النبى صلى الله عليه وسلم مر بتمرة فى الطريق فقال: لولا أنى أخاف أن تكون من الصدقة لأكلتها "','1895','1712','0');
INSERT INTO [t22592] VALUES (1715,'(١٥٦٠) - (عن سلمى بنت كعب قالت: " وجدت خاتما من ذهب فى','1895','1712','0');
INSERT INTO [t22592] VALUES (1716,'(١٥٦١) - (ورخص النبى صلى الله عليه وسلم فى الحبل فى حديث جابر ".','1896','1712','0');
INSERT INTO [t22592] VALUES (1717,'(١٥٦٢) - (حديث الشعبى مرفوعا: " من وجد دابة قد عجر عنها أهلها فسيبوها فأخذها فأحياها فهى له ـ قال عبيد الله بن حميد (١) بن عبد الرحمن فقلت ـ يعنى للشعبى ـ من حدثك بهذا؟ قال: غير واحد من أصحاب رسول الله صلى الله عليه وسلم "','1896','1712','0');
INSERT INTO [t22592] VALUES (1718,'(١٥٦٣) - (حديث جرير: " أنه أمر بالبقرة فطردت حتى توارت ثم قال: سمعت النبى صلى الله عليه وسلم يقول: لا يؤوى الضالة إلا ضال "','1897','1712','0');
INSERT INTO [t22592] VALUES (1719,'(١٥٦٤) - (حديث زيد بن خالد قال: " سئل رسول الله صلى الله عليه وسلم عن لقطة الذهب والورق فقال: اعرف وكاءها وعفاصها ثم عرفها سنة فإن لم تعرف فاستنفقها ولتكن وديعة عندك فإذا جاء طالبها يوما من الدهر فادفعها إليه , وسأله عن ضالة الإبل فقال: ما لك و','1898','1712','0');
INSERT INTO [t22592] VALUES (1720,'(١٥٦٥) - (حديث: " فى الضالة المكتومة غرامتها ومثلها معها "','1899','1712','0');
INSERT INTO [t22592] VALUES (1721,'(١٥٦٦) - (حديث زيد بن خالد: " فى النقدين والشاة "','1899','1712','0');
INSERT INTO [t22592] VALUES (1722,'فصل','1899','1382','0');
INSERT INTO [t22592] VALUES (1723,'(١٥٦٧) - (حديث: " هى لك أو لأخيك أو للذئب "','1899','1722','0');
INSERT INTO [t22592] VALUES (1724,'(١٥٦٨) - (حديث: " أنه صلى الله عليه وسلم أمر به زيد بن خالد وأبى بن كعب ولم يفرق "','1899','1722','0');
INSERT INTO [t22592] VALUES (1725,'(١٥٦٩) - (أثر: " إن عمر رضى الله عنه أمر واجدها بتعريفها على باب المسجد ".','1901','1722','0');
INSERT INTO [t22592] VALUES (1726,'(١٥٧٠) - (حديث: " فإن لم تعرف فاستنفقها " , وفى لفظ: " وإلا فهى كسبيل مالك ". وفى لفظ: " ثم كلها " , وفى لفظ: " فانتفع بها ". وفى لفظ: " فشأنك بها " , وفى لفظ: " فاستمتع بها "','1901','1722','0');
INSERT INTO [t22592] VALUES (1727,'(١٥٧١) - (حديث زيد: " فإذا جاء طالبها يوما من الدهر فادفعها إليه "','1902','1722','0');
INSERT INTO [t22592] VALUES (1728,'(١٥٧٢) - (حديث: " اعرف وكاءها وعفاصها "','1902','1722','0');
INSERT INTO [t22592] VALUES (1729,'باب اللقيط','1903','1382','0');
INSERT INTO [t22592] VALUES (1730,'(١٥٧٣) - (روى سنين أبو جميلة قال: " وجدت ملقوطا فأتيت به عمر بن الخطاب فقال عريفى: يا أمير المؤمنين إنه رجل صالح , فقال عمر: أكذلك هو؟ قال: نعم. فقال: اذهب به وهو حر ولك ولاؤه وعلينا نفقته " وفى لفظ: " وعلينا رضاعه " رواه سعيد فى سننه (ص ٤٦٥) .','1903','1729','0');
INSERT INTO [t22592] VALUES (1731,'فصل','1903','1382','0');
INSERT INTO [t22592] VALUES (1732,'(١٥٧٤) - (حديث: " إنما الولاء لمن أعتق ".','1903','1731','0');
INSERT INTO [t22592] VALUES (1733,'(١٥٧٥) - (قول عمر: " ولك ولاؤه " (ص ٤٦٧) .','1903','1731','0');
INSERT INTO [t22592] VALUES (1734,'(١٥٧٦) - (حديث واثلة بن الأسقع مرفوعا: " المرأة تحوز ثلاثة مواريث عتيقها ولقيطها وولدها الذى لاعنت عليه " رواه أبو داود والترمذى وحسنه , قال ابن المنذر: لا يثبت (ص ٤٦٧) .','1904','1731','0');
INSERT INTO [t22592] VALUES (1735,'(١٥٧٧) - (حديث عائشة قالت: " دخل على النبى صلى الله عليه وسلم مسرورا تبرق أسارير وجهه فقال: ألم ترى أن مجززا المدلجى نظر آنفا إلى زيد وأسامة وقد غطيا رءوسهما وبدت أقدامهما , فقال: إن هذه الأقدام بعضها من بعض " متفق عليه (ص ٤٦٨) .','1904','1731','0');
INSERT INTO [t22592] VALUES (1736,'(١٥٧٨) - (روى سليمان بن يسار عن عمر فى امرأة وطئها رجلان فى طهر فقال القائف: " قد اشتركا فيه جميعا فجعله عمر بينهما " رواه سعيد (ص ٤٦٨) .','1905','1731','0');
INSERT INTO [t22592] VALUES (1737,'(١٥٧٩) - (وبإسناده عن الشعبى قال: وعلى يقول: " هو ابنهما وهما أبواه يرثهما ويرثانه " رواه الزبير بن بكار عن عمر (ص ٤٦٨) .','1907','1731','0');
INSERT INTO [t22592] VALUES (1738,'كتاب الوقف','1908','1382','0');
INSERT INTO [t22592] VALUES (1739,'(١٥٨٠) - (حديث: " إذا مات ابن آدم انقطع عمله إلا من ثلاث: صدقة جارية , أو علم ينتفع به , أو ولد صالح يدعو له " رواه الجماعة إلا البخارى وابن ماجه (٢/٣) .','1908','1738','0');
INSERT INTO [t22592] VALUES (1740,'(١٥٨١) - (قال جابر: " لم يكن أحد من أصحاب النبى صلى الله عليه وسلم ذو مقدرة إلا وقف , ويجوز وقف الأرض والجزء المشاع " (٢/٣) .','1909','1738','0');
INSERT INTO [t22592] VALUES (1741,'(١٥٨٢) - (حديث ابن عمر: " أصاب عمر أرضا بخيبر فأتى النبى صلى الله عليه وسلم يستأمره فيها فقال: يا رسول الله إنى أصبت مالا بخيبر لم أصب مالا أنفس عندى منه فما تأمرنى فيه؟ فقال: إن شئت حبست أصلها وتصدقت بها , غير أنه لا يباع أصلها ولا يوهب ولا يورث , قال: فتصدق بها عمر فى الفقراء , وفى القربى , والرقاب , وفى سبيل الله , وابن السبيل , والضعيف , لا جناح على من وليها أن يأكل منها بالمعروف , أو يطعم صديقا غير متحول فيه ـ وفى لفظ: غير متأثل ـ " متفق عليه (٢/٣) .','1910','1738','0');
INSERT INTO [t22592] VALUES (1742,'(١٥٨٣) - (وعنه أيضا قال عمر للنبى صلى الله عليه وسلم: " إن المائة سهم التى بخيبر لم أصب مالا قط أعجب إلى منها وقد أردت أن أتصدق بها , فقال النبى صلى الله عليه وسلم احبس أصلها وسبل ثمرتها " رواه النسائى وابن ماجه (٢/٣) .','1911','1738','0');
INSERT INTO [t22592] VALUES (1743,'(١٥٨٤) - (حديث: " إن شئت حبست أصلها وسبلت ثمرتها " (٢/٤) .','1911','1738','0');
INSERT INTO [t22592] VALUES (1744,'فصل','1912','1382','0');
INSERT INTO [t22592] VALUES (1745,'(١٥٨٥) - (حديث: " أما خالد فقد احتبس أدراعه وأعتاده فى سبيل الله " متفق عليه (٢/٥)','1912','1744','0');
INSERT INTO [t22592] VALUES (1746,'(١٥٨٦) - (حديث أبى هريرة مرفوعا: " من احتبس فرسا فى سبيل الله إيمانا واحتسابا فإن شبعه وروثه وبوله فى ميزانه حسنات " رواه البخارى (٢/٥) .','1912','1744','0');
INSERT INTO [t22592] VALUES (1747,'(١٥٨٧) - (حديث: " يا رسول الله إن أبا معقل جعل ناضحه فى سبيل الله , فقال: اركبيه فإن الحج من سبيل الله " رواه أبو داود (٢/٥) .','1912','1744','0');
INSERT INTO [t22592] VALUES (1748,'(١٥٨٨) - (روى الخلال عن نافع: " أن حفصة ابتاعت حليا بعشرين ألفا','1913','1744','0');
INSERT INTO [t22592] VALUES (1749,'(١٥٨٩) - (حديث: " أن النبى صلى الله عليه وسلم غضب حين رأى مع عمر صحيفة فيها شىء من التوراة وقال: أفى شك أنت يا ابن الخطاب؟ ألم آت بها بيضاء نقية؟ لو كان أخى موسى حيا ما وسعه إلا اتباعى " (ص ٢/٦) .','1914','1744','0');
INSERT INTO [t22592] VALUES (1750,'(١٥٩٠) - (روى: " أن صفية بنت حيى زوج النبى صلى الله عليه وسلم وقفت على أخ لها يهودى " (٢/٦ ـ ٧) .','1918','1744','0');
INSERT INTO [t22592] VALUES (1751,'(١٥٩١) - (حديث حجر المدرى: " أن فى صدقة رسول الله صلى الله عليه وسلم أن يأكل (أهله) [٢] منها بالمعروف غير المنكر " (٢/٧) . [٣]','1918','1744','0');
INSERT INTO [t22592] VALUES (1752,'(١٥٩٣) - (قول عمر لما وقف: " لا جناح على من وليها أن يأكل منها أو يطعم صديقا غير متمول فيه " وكان الوقف فى يده إلى أن مات ثم بنته حفصة ثم ابنه عبد الله (٢/٧) .','1918','1744','0');
INSERT INTO [t22592] VALUES (1753,'(١٥٩٣) - (قول عمر: " إن حدث بى حدث الموت فإن ثمغا صدقة.. " ورواه أبو داود بنحوه.','1918','1744','0');
INSERT INTO [t22592] VALUES (1754,'(١٥٩٤) - (روى: " أن عثمان رضى الله عنه سبل بئر رومة وكان دلوه فيها كدلاء المسلمين " (٢/٩) .','1918','1744','0');
INSERT INTO [t22592] VALUES (1755,'فصل','1920','1382','0');
INSERT INTO [t22592] VALUES (1756,'(١٥٩٥) - (أثر: " أن الزبير وقف على ولده وجعل للمردودة من بناته أن تسكن غير مضرة ولا مضرا بها فإن استغنت بزوج فلا حق لها فيه (٢/١٠) .','1920','1755','0');
INSERT INTO [t22592] VALUES (1757,'(١٥٩٦) - (أثر: " أن عمر رضى الله عنه جعل النظر فى وقفه إلى ابنته حفصة ثم إلى ذى الرأى من أهلها " (٢/١٢) .','1920','1755','0');
INSERT INTO [t22592] VALUES (1758,'فصل','1920','1382','0');
INSERT INTO [t22592] VALUES (1759,'(١٥٩٧) - (حديث: " إن ابنى هذا سيد " (٢/١٦) .','1920','1758','0');
INSERT INTO [t22592] VALUES (1760,'(١٥٩٨) - (قوله صلى الله عليه وسلم فى حديث النعمان بن بشير: " ... اتقوا الله واعدلوا بين أولادكم. قال: فرجع أبى فرد تلك الصدقة ". رواه مسلم (٢/١٧) .','1921','1758','0');
INSERT INTO [t22592] VALUES (1761,'فصل','1922','1382','0');
INSERT INTO [t22592] VALUES (1762,'(١٥٩٩) - (قوله صلى الله عليه وسلم: " لا يباع أصلها ولا توهب ولا','1922','1761','0');
INSERT INTO [t22592] VALUES (1763,'(١٦٠٠) - (أثر: " أن شيبة بن عثمان الحجبى كان يتصدق بخلقان الكعبة , وأن عائشة أمرته بذلك " رواه الخلال بإسناده (٢٠/٢٠) .','1923','1761','0');
INSERT INTO [t22592] VALUES (1764,'باب الهبة','1924','1382','0');
INSERT INTO [t22592] VALUES (1765,'(١٦٠١) - (قوله صلى الله عليه وسلم: " تهادوا تحابوا " (٢/٢١) .','1924','1764','0');
INSERT INTO [t22592] VALUES (1766,'(١٦٠٢) - (حديث أبى هريرة: " سئل النبى صلى الله عليه وسلم أى الصدقة أفضل؟ قال: أن تصدق وأنت صحيح شحيح تأمل الغنى وتخشى الفقر , ولا تمهل حتى إذا بلغت الحلقوم قلت: لفلان كذا , ولفلان كذا " رواه مسلم بمعناه (٢/٢١) .','1927','1764','0');
INSERT INTO [t22592] VALUES (1767,'(١٦٠٣) - (حديث: " لأنه صلى الله عليه وسلم , كان يهدى ويهدى إليه , ويعطى ويعطى " (٢/٢٢) .','1927','1764','0');
INSERT INTO [t22592] VALUES (1768,'(١٦٠٤) حديث: " أنه صلى الله عليه وسلم كان يفرق الصدقات " (٢/٢١) .','1929','1764','0');
INSERT INTO [t22592] VALUES (1769,'(١٦٠٥) - (حديث: " أنه صلى الله عليه وسلم كان يأمر سعاته بأخذ الصدقات وتفريقها " (٢/٢١) .','1929','1764','0');
INSERT INTO [t22592] VALUES (1770,'(١٦٠٦) - (قوله صلى الله عليه وسلم لأم سلمة: " إنى قد أهديت إلى النجاشى (حله) [١] , وأواقى مسك , ولا أرى النجاشى إلا قد مات , ولا أرى هديتى إلا مردودة على , فإن ردت فهى لك " رواه أحمد.','1929','1764','0');
INSERT INTO [t22592] VALUES (1771,'(١٦٠٧) - (قوله صلى الله عليه وسلم: " أمسكوا عليكم أموالكم ولا تفسدوها فإنه من أعمر عمرى فهى للذى أعمرها حيا وميتا ولعقبه " رواه أحمد ومسلم. وفى لفظ: " قضى رسول الله صلى الله عليه وسلم , بالعمرى لمن وهبت له " متفق عليه.','1929','1764','0');
INSERT INTO [t22592] VALUES (1772,'(١٦٠٨) - (وعن جابر: " أن رجلا من الأنصار أعطى أمه حديقة من نخل حياتها , فماتت , فجاء إخوته , فقالوا: نحن فيه شرع سواء. قال: فأبى , فاختصموا إلى النبى صلى الله عليه وسلم , فقسمها بينهم ميراثا " رواه أحمد (٢/٢٢) .','1930','1764','0');
INSERT INTO [t22592] VALUES (1773,'(١٦٠٩) - (قوله صلى الله عليه وسلم: " لا تعمروا ولا ترقبوا فمن أعمر شيئا أو أرقبه فهو له حياته ومماته " رواه أحمد ومسلم (٢/٢٣) .','1932','1764','0');
INSERT INTO [t22592] VALUES (1774,'(١٦١٠) - (وفى حديث جابر مرفوعا: " العمرى جائزة لأهلها , والرقبى جائزة لأهلها " رواه الخمسة (٢/٢٣) .','1935','1764','0');
INSERT INTO [t22592] VALUES (1775,'(١٦١١) - (قوله صلى الله عليه وسلم: " المؤمنون عند شروطهم " (٢/٢٣) .','1935','1764','0');
INSERT INTO [t22592] VALUES (1776,'(١٦١٢) - (قال جابر: " إنما العمرى التى أجاز رسول الله صلى الله عليه وسلم أن يقول: هى لك , ولعقبك , فأما إذا قال هى لك ما عشت فإنها ترجع إلى صاحبها " متفق عليه (٢/٣٢) .','1935','1764','0');
INSERT INTO [t22592] VALUES (1777,'(١٦١٣) - (قول عمر: " من وهب هبة أراد بها الثواب فهو على هبته , يرجع فيها إذا لم يرض منها " رواه مالك فى الموطأ (٢/٢٤) .','1935','1764','0');
INSERT INTO [t22592] VALUES (1778,'(١٦١٤) - (وعن أبى هريرة مرفوعا: " الواهب أحق بهبته ما لم يثب منها " رواه ابن ماجه والدارقطنى والبيهقى (٢/٢٤) .','1939','1764','0');
INSERT INTO [t22592] VALUES (1779,'(١٦١٥) - (حديث: " المستعذر [١] يثاب من هبة " (٢/٢٤) .','1939','1764','0');
INSERT INTO [t22592] VALUES (1780,'(١٦١٦) - (حديث ابن مسعود مرفوعا: " لا تردوا الهدية " رواه أحمد.','1939','1764','0');
INSERT INTO [t22592] VALUES (1781,'(١٦١٧) - (حديث: " من صنع إليكم معروفا فكافئوه , فإن لم تجدوا ما تكافئونه فادعوا له حتى تروا أنكم قد كافأتموه " رواه أحمد وغيره.','1940','1764','0');
INSERT INTO [t22592] VALUES (1782,'فصل','1941','1382','0');
INSERT INTO [t22592] VALUES (1783,'(١٦١٨) - (روى , عن على وابن مسعود أنهما قالا: " الهبة إذا كانت معلومة فهى جائزة قبضت أو لم تقبض " (٢/٢٥) .','1941','1782','0');
INSERT INTO [t22592] VALUES (1784,'(١٦١٩) - (قال الصديق لما حضرته الوفاة لعائشة: " يا بنية: إنى كنت نحلتك جاد عشرين وسقا , ولو كنت جددتيه , واحتزيتيه كان لك , وإنما هو اليوم مال الوارث فاقتسموه , على كتاب الله تعالى ". رواه مالك فى " الموطأ " (٢/٢٥) .','1941','1782','0');
INSERT INTO [t22592] VALUES (1785,'(١٦٢٠) - (قوله صلى الله عليه وسلم لأم سلمة: " إنى قد أهديت إلى النجاشى (حله) [١] وأواقى مسك , ولا أرى النجاشى إلا قد مات ولا أرى هديتى إلا مردودة على , فإن ردت فهى لك , قالت: فكان ما قال رسول الله , صلى الله عليه وسلم , وردت عليه هديته , فأعطى كل إمرأة من نسائه أوقية من مسك , وأعطى أم سلمة بقية المسك (والحله) [٢] " رواه أحمد.','1942','1782','0');
INSERT INTO [t22592] VALUES (1786,'(١٥٦٦) - (قوله صلى الله عليه وسلم للرجلين: " اقتسما وتوخيا الحق , واستهما , ثم تحالا " (٢/٢٧) . [٣]','1942','1782','0');
INSERT INTO [t22592] VALUES (1787,'فصل','1942','1382','0');
INSERT INTO [t22592] VALUES (1788,'(١٦٢٢) - (حديث: " العائد فى هبته كالعائد يعود فى قيئه " متفق عليه (٢/٢٧) .','1942','1787','0');
INSERT INTO [t22592] VALUES (1789,'(١٦٢٣) - (حديث ابن عباس مرفوعا: " العائد فى هبته كالكلب','1944','1787','0');
INSERT INTO [t22592] VALUES (1790,'(١٦٢٤) - (قوله صلى الله عليه وسلم: " لا يحل للرجل أن يعطى العطية فيرجع فيها إلا الوالد فيما يعطى ولده " رواه الخمسة , وصححه الترمذى (٢/٢٧) .','1945','1787','0');
INSERT INTO [t22592] VALUES (1791,'(١٦٢٥) - (قوله صلى الله عليه وسلم: " أنت ومالك لأبيك ". رواه سعيد وابن ماجه والطبرانى فى معجمه مطولا (٢/٢٨) .','1945','1787','0');
INSERT INTO [t22592] VALUES (1792,'(١٦٢٦) - (عن عائشة مرفوعا: " إن أطيب ما أكلتم من كسبكم وإن أولادكم من كسبكم " رواه سعيد والترمذى وحسنه (٢/٢٨) .','1945','1787','0');
INSERT INTO [t22592] VALUES (1793,'(١٦٢٧) - (حديث: " لا ضرر ولا ضرار " (٢/٢٨) .','1947','1787','0');
INSERT INTO [t22592] VALUES (1794,'(١٦٢٨) - (عن ابن عباس مرفوعا: " سووا بين أولادكم ولو كنت مؤثرا لآثرت النساء " (٢/٢٩) .','1947','1787','0');
INSERT INTO [t22592] VALUES (1795,'(١٦٢٩) - (حديث النعمان: " لا تشهدنى على جور " متفق عليه (٢/٢٩) .','1948','1787','0');
INSERT INTO [t22592] VALUES (1796,'(١٦٣٠) - (خبر أبى بكر: " لما نحل عائشة " (٢/٣٠) .','1948','1787','0');
INSERT INTO [t22592] VALUES (1797,'(١٦٣١) - (حديث النعمان بن بشير: " أن أباه أتى به رسول الله صلى الله عليه وسلم فقال: إنى نحلت ابنى هذا غلاما كان لى , فقال رسول الله صلى الله عليه وسلم: أكل ولدك نحلته مثل هذا؟ فقال: لا. فقال: فأرجعه " متفق عليه (٢/٣٠) .','1948','1787','0');
INSERT INTO [t22592] VALUES (1798,'(١٦٣٢) - (قوله صلى الله عليه وسلم: " اتقوا الله واعدلوا بين أولادكم ". رواه مسلم (٢/٣٠) .','1948','1787','0');
INSERT INTO [t22592] VALUES (1799,'(١٦٣٣) - (قول الصديق: " وددت لو أنك حزتيه " (٢/٣٠) .','1949','1787','0');
INSERT INTO [t22592] VALUES (1800,'(١٦٣٤) - (قول عمر: " لا عطية إلا ما حازه الولد.. " (٢/٣٠) .','1949','1787','0');
INSERT INTO [t22592] VALUES (1801,'(١٦٣٥) - (حديث: " لا وصية لوارث " (٢/٣٠) .','1950','1787','0');
INSERT INTO [t22592] VALUES (1802,'(١٦٣٦) - (أثر: " أن الزبير خص المردودة من بناته " (٢/٣٠) .','1950','1787','0');
INSERT INTO [t22592] VALUES (1803,'فصل','1950','1382','0');
INSERT INTO [t22592] VALUES (1804,'(١٦٣٧) - (عن أبى موسى مرفوعا: " فناء أمتى بالطعن والطاعون. فقيل: يا رسول الله , هذا الطعن قد عرفناه , فما الطاعون؟ قال: وخز أعدائكم من الجن , وفى كل شهادة " رواه أحمد وأبو يعلى والبزار والطبرانى (٢/٣٢) .','1950','1803','0');
INSERT INTO [t22592] VALUES (1805,'(١٦٣٨) - (حديث عائشة: " غدة كغدة البعير , المقيم به كالشهيد , والفار منه كالفار من الزحف " رواه أحمد وأبو يعلى والطبرانى (٢/٣٢) .','1952','1803','0');
INSERT INTO [t22592] VALUES (1806,'(١٦٣٩) - (أثر: " أن عمر رضى الله عنه ولما جرح سقاه الطبيب لبنا فخرج من جرحه , فقال له الطبيب: اعهد إلى الناس , فعهد إليهم ووصى (٢/٣٢) .','1953','1803','0');
INSERT INTO [t22592] VALUES (1807,'(١٦٤٠) - (أثر: " أن عليا رضى عنه أوصى , وأمر ونهى بعد ضرب ابن ملجم " (٢/٣٣) .','1954','1803','0');
INSERT INTO [t22592] VALUES (1808,'(١٦٤١) - (قوله صلى الله عليه وسلم: " إن الله تصدق عليكم عند وفاتكم بثلث أموالكم زيادة فى أعمالكم " رواه أحمد وأبو داود والترمذى وحسنه (٢/٣٣) .','1956','1803','0');
INSERT INTO [t22592] VALUES (1809,'كتاب الوصايا','1960','0','0');
INSERT INTO [t22592] VALUES (1810,'[الأحاديث ١٦٤٢ - ١٦٥٨]','1960','1809','0');
INSERT INTO [t22592] VALUES (1811,'(١٦٤٢) - (أثر: أن أبا بكر: " وصى بالخلافة لعمر " (٢/٣٤) .','1960','1810','0');
INSERT INTO [t22592] VALUES (1812,'(١٦٤٣) - (أثر: " أن عمر وصى بالخلافة لأهل الشورى " (٢/٣٤) .','1961','1810','0');
INSERT INTO [t22592] VALUES (1813,'(١٦٤٤) - (حديث: " لا تمهل حتى إذا بلغت الحلقوم قلت: لفلان كذا , ولفلان كذا , وقد كان لفلان ".','1961','1810','0');
INSERT INTO [t22592] VALUES (1814,'(١٦٤٥) - (أثر: " أن صبيا من غسان أوصى إلى أخواله فرفع إلى عمر فأجاز وصيته " رواه سعيد وفى " الموطأ ": " أن الوصية بيعت بثلاثين ألفا " (٢/٣٥) .','1961','1810','0');
INSERT INTO [t22592] VALUES (1815,'(١٦٤٦) - (حديث: " أنه صلى الله عليه وسلم , كتب إلى عماله , كذا الخلفاء , إلى ولاتهم بالأحكام التى فيها الدماء والفروج , مختومة لا يدرى حاملها ما فيها " (٢/٣٥) .','1962','1810','0');
INSERT INTO [t22592] VALUES (1816,'(١٦٤٧) - (عن أنس: " كانوا يكتبون في صدور وصاياهم: بسم الله الرحمن الرحيم هذا ما أوصى به فلان ابن فلان: يشهد أن لا إله إلا الله وحده لا شريك له وأن محمدا عبده ورسوله وأن الساعة آتية لا ريب فيها وإن الله يبعث من في القبور وأوصى من ترك من أهله أن يتقوا الله ويصلحوا ذات بينهم ويطيعوا الله ورسوله إن كانوا مؤمنين وأوصاهم بما أوصى به إبراهيم بنيه ويعقوب (يا بني إن الله اصطفى لكم الدين فلا تموتن إلا وأنتم مسلمون) . رواه سعيد ورواه الدارقطني بنحوه " ٢ / ٣٦.','1964','1810','0');
INSERT INTO [t22592] VALUES (1817,'(١٦٤٨) - (قال (ابن عياش) [١] : " وددت لو أن الناس غضوا من الثلث لقول النبى صلى الله عليه وسلم: والثلث كثير " متفق عليه (٢/٣٦) .','1964','1810','0');
INSERT INTO [t22592] VALUES (1818,'(١٦٤٩) - (أثر: " أن أبا بكر الصديق أوصى بالخمس , وقال: رضيت بما رضى الله به لنفسه " (٢/٣٦) .','1965','1810','0');
INSERT INTO [t22592] VALUES (1819,'(١٦٥٠) - (قال على رضى الله عنه: " لأن أوصى بالخمس أحب إلى من الربع " (٢/٣٦ ـ ٣٧) .','1965','1810','0');
INSERT INTO [t22592] VALUES (1820,'(١٦٥١) - (قوله صلى الله عليه وسلم: " إنك أن تذر ورثتك أغنياء خير من أن تدعهم عالة يتكففون الناس " (٢/٣٧) .','1966','1810','0');
INSERT INTO [t22592] VALUES (1821,'(١٦٥٢) - (حديث ابن عمر مرفوعا: " ما حق امرىء مسلم له شىء يوصى به يبيت ليلتين إلا ووصيته مكتوبة عند رأسه " متفق عليه (٢٢/٣٧) .','1966','1810','0');
INSERT INTO [t22592] VALUES (1822,'(١٦٥٣) - (حديث: " نهيه صلى الله عليه وسلم سعدا عن الزيادة على الثلث " متفق عليه (٢/٣٧) .','1966','1810','0');
INSERT INTO [t22592] VALUES (1823,'(١٦٥٤) - (عن عمران بن حصين: " أن رجلا أعتق ستة مملوكين له عند موته ولم يكن له مال غيرهم , فجزأهم النبى صلى الله عليه وسلم , أثلاثا , ثم أقرع بينهم , فأعتق اثنين وأرق أربعة , وقال له قولا شديدا " رواه الجماعة إلا البخارى (٢/٣٧) .','1967','1810','0');
INSERT INTO [t22592] VALUES (1824,'(١٦٥٥) - (قوله صلى الله عليه وسلم: " لا وصية لوارث " رواه أحمد وأبو داود والترمذى وحسنه (٢/٣٧) .','1967','1810','0');
INSERT INTO [t22592] VALUES (1825,'(١٦٥٦) - (عن ابن عباس مرفوعا: " لا تجوز وصية لوارث إلا أن يشاء الورثة " رواه الدارقطنى (٢/٣٧) .','1976','1810','0');
INSERT INTO [t22592] VALUES (1826,'(١٦٥٧) - (وعن عمرو بن شعيب عن أبيه عن جده مرفوعا نحوه " رواه الدارقطنى (٢/٣٧) .','1977','1810','0');
INSERT INTO [t22592] VALUES (1827,'(١٦٥٨) - (قول عمر , رضى الله عنه: " يغير الرجل ما شاء فى وصيته " (٢/٣٩) .','1978','1810','0');
INSERT INTO [t22592] VALUES (1828,'باب الموصى له','1980','1809','0');
INSERT INTO [t22592] VALUES (1829,'(١٦٥٩) - (حديث " أن النبى صلى الله عليه وسلم غضب , حين رأى مع عمر شيئا مكتوبا من التوراة " (٢/٤١) .','1980','1828','0');
INSERT INTO [t22592] VALUES (1830,'١٦٦٠) - (حديث: " في أربعين شاة شاة ".) ٢ / ٤٤','1981','1828','0');
INSERT INTO [t22592] VALUES (1831,'(١٦٦١) - (روى عن أبى عبيدة: " أنه لما عبر الفرات أوصى إلى عمر , وأوصى إلى الزبير ستة من الصحابة " (٢/٤٥) .','1981','1828','0');
INSERT INTO [t22592] VALUES (1832,'(١٦٦٢) - (قوله صلى الله عليه وسلم: " أميركم زيد , فإن قتل فجعفر فإن قتل فعبد الله بن رواحة " رواه أحمد والنسائى (٢٢/٤٦) .','1981','1828','0');
INSERT INTO [t22592] VALUES (1833,'(١٦٦٣) - (روى: " أن ابن مسعود كتب فى وصيته أن مرجع وصيتى إلى الله , ثم إلى الزبير وابنه عبد الله (٢/٤٠) .','1981','1828','0');
INSERT INTO [t22592] VALUES (1834,'كتاب الفرائض','1983','0','0');
INSERT INTO [t22592] VALUES (1835,'[الأحاديث ١٦٦٤ - ١٦٨٣]','1983','1834','0');
INSERT INTO [t22592] VALUES (1836,'(١٦٦٤) - (حديث ابن مسعود مرفوعا: " تعلموا الفرائض وعلموها الناس فإنى امرؤ مقبوض وإن العلم سيقبض وتظهر الفتن حتى يختلف اثنان فى الفريضة فلا يجدان من يفصل بينهما " رواه أحمد والترمذى والحاكم ولفظه له (٢/٥٠) .','1983','1835','0');
INSERT INTO [t22592] VALUES (1837,'(١٦٦٥) - (عن أبى هريرة مرفوعا: " تعلموا الفرائض وعلموها فإنها نصف العلم وهو ينسى وهو أول علم ينزع من أمتى " رواه ابن ماجه والدارقطنى من حديث حفص بن عمر وقد ضعفه جماعة (٢/٥٠) .','1986','1835','0');
INSERT INTO [t22592] VALUES (1838,'(١٦٦٦) - (قال عمر: " إذا تحدثتم فتحدثوا بالفرائض وإذا لهوتم فالهو بالرمى " (٢٨٥٠) .','1987','1835','0');
INSERT INTO [t22592] VALUES (1839,'(١٦٦٧) - (قال على رضى الله عنه: " إن النبى صلى الله عليه وسلم قضى أن الدين قبل الوصية " رواه الترمذى وابن ماجه (٢/٥٠) .','1987','1835','0');
INSERT INTO [t22592] VALUES (1840,'(١٦٦٨) - (حديث ابن عمر مرفوعا: " الولاء لحمة كلحمة النسب " رواه ابن حبان والحاكم وصححه (٢/٥١) .','1989','1835','0');
INSERT INTO [t22592] VALUES (1841,'(١٦٦٩) - (خبر عوسجة مولى ابن عباس عنه: " أن رجلا مات ولم يترك وارثا إلا عبدا هو أعتقه فأعطاه النبى صلى الله عليه وسلم ميراثه " رواه أحمد وأبو داود والترمذى ـ وحسنه ـ وابن ماجه (٢/٥٢) .','1994','1835','0');
INSERT INTO [t22592] VALUES (1842,'(١٦٧٠) - (عن عمر رضى الله عنه: أنه أعطى دية ابن قتادة المدلجى لأخيه دون أبيه وكان حذفه بسيف فقتله ". وقال عمر: " سمعت رسول الله صلى الله عليه وسلم يقول: " ليس لقاتل شىء " رواه مالك فى الموطأ (٢/٥٢) .','1995','1835','0');
INSERT INTO [t22592] VALUES (1843,'(١٦٧١) - (ولأحمد عن عمرو بن شعيب عن أبيه عن جده نحوه (٢/٥٢) .','1997','1835','0');
INSERT INTO [t22592] VALUES (1844,'(١٦٧٢) - (عن ابن عباس مرفوعا: " من قتل قتيلا فإنه لا يرثه وإن','1998','1835','0');
INSERT INTO [t22592] VALUES (1845,'(١٦٧٣) - (قال صلى الله عليه وسلم: " من باع عبدا وله مال فماله للبائع إلا أن يشترطه المبتاع " (٢/٥٣) .','1999','1835','0');
INSERT INTO [t22592] VALUES (1846,'(١٦٧٤) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " المكاتب عبد ما بقى عليه درهم ". رواه أبو داود (٢/٥٣) .','1999','1835','0');
INSERT INTO [t22592] VALUES (1847,'(١٦٧٥) - (حديث أسامة بن زيد مرفوعا: " لا يرث الكافر المسلم ولا المسلم الكافر " متفق عليه (٢/٥٣) .','2000','1835','0');
INSERT INTO [t22592] VALUES (1848,'(١٦٧٦) - (حديث: " أن النبى صلى الله عليه وسلم أعطى الجد السدس " (٢/٥٣) .','2001','1835','0');
INSERT INTO [t22592] VALUES (1849,'(١٦٧٧) - (حديث جابر قال: " جاءت امرأة سعد بن الربيع بابنتيها إلى رسول الله صلى الله عليه وسلم فقالت: هاتان ابنتا سعد قتل أبوهما معك يوم أحد شهيدا وإن عمهما أخذ مالهما فلم يدع لهما شيئا من ماله','2001','1835','0');
INSERT INTO [t22592] VALUES (1850,'(١٦٧٨) - (قال ابن عباس لعثمان: " ليس الأخوان إخوة فى لسان قومك فلم تحجب بهما الأم؟ فقال: لا أستطيع أن أرد شيئا كان قبلى ومضى فى البلدان وتوارث الناس به " (٢/٥٩) .','2002','1835','0');
INSERT INTO [t22592] VALUES (1851,'(١٦٧٩) - (قال ابن عباس: " لها الثلث كاملا لظاهر الآية " (٢/٥٩) .','2003','1835','0');
INSERT INTO [t22592] VALUES (1852,'(١٦٨٠) - (حديث (قبيعة) [١] بن ذؤيب قال: " جاءت الجدة إلى أبى بكر تطلب ميراثها فقال: ما لك فى كتاب الله شىء وما أعلم لك فى سنة رسول الله صلى الله عليه وسلم شيئا ولكن ارجعى حتى أسأل الناس فقال المغيرة بن شعبة: حضرت رسول الله صلى الله عليه وسلم أعطاها السدس. فقال: هل معك غيرك فشهد له محمد بن مسلمة فأمضاه لها أبو بكر , فلما كان عمر جاءت الجدة الأخرى فقال عمر: ما لك فى كتاب الله شىء وما كان القضاء الذى قضى به إلا فى غيرك , وما أنا بزائد فى الفرائض شيئا ولكن هو ذاك السدس , فإن اجتمعتما فهو لكما وأيكما خلت به فهو لها " صححه الترمذى (٢/٦١) .','2004','1835','0');
INSERT INTO [t22592] VALUES (1853,'(١٦٨١) - (عن عبادة بن الصامت: " أن النبى صلى الله عليه وسلم قضى للجدتين من الميراث بالسدس بينهما " رواه عبد الله فى زوائد المسند (٢/٦١) .','2006','1835','0');
INSERT INTO [t22592] VALUES (1854,'(١٦٨٢) - (روى سعيد بإسناده عن إبراهيم النخعى: " أن النبى صلى الله عليه وسلم ورث ثلاث جدات اثنتين من قبل الأب وواحدة من قبل الأم " وأخرجه أبو عبيد والدارقطنى (٢/٦١) .','2007','1835','0');
INSERT INTO [t22592] VALUES (1855,'(١٦٨٣) - (حديث ابن مسعود: " وقد سئل عن بنت وبنت ابن وأخت فقال: أقضى فيها بما قضى رسول الله صلى الله عليه وسلم للابنة النصف ولابنة الابن السدس تكملة الثلثين وما بقى فللأخت " رواه البخارى مختصرا (٢/٦٢) .','2007','1835','0');
INSERT INTO [t22592] VALUES (1856,'فصل','2008','1834','0');
INSERT INTO [t22592] VALUES (1857,'(١٦٨٤) - (عن على رضى الله عنه: " من سره أن يقتحم جراثيم جهنم فليقض بين الجد والإخوة " (٢/٦٣) .','2008','1856','0');
INSERT INTO [t22592] VALUES (1858,'(١٦٨٥) - (قال ابن مسعود: " سلونا عن عضلكم واتركونا من الجد لا حياه الله ولا بياه " (٢/٦٣) .','2009','1856','0');
INSERT INTO [t22592] VALUES (1859,'(١٦٨٦) - (روى عن عمر أنه لما طعن وحضرته الوفاة قال: " احفظوا عنى ثلاثا: لا أقول فى الجد شيئا ولا أقول فى الكلالة شيئا ولا أولى عليكم أحدا " (٢/٦٣) .','2009','1856','0');
INSERT INTO [t22592] VALUES (1860,'باب الحجب','2011','1834','0');
INSERT INTO [t22592] VALUES (1861,'(١٦٨٧) - (حديث ابن مسعود: " أول جدة أطعمها رسول الله صلى الله عليه وسلم السدس , أم أب مع ابنها , وابنها حى " رواه الترمذى. ورواه سعيد بلفظ: " أول جدة أطعمت السدس , أم أب مع ابنها " (٢/٦٩) .','2011','1860','0');
INSERT INTO [t22592] VALUES (1862,'(١٦٨٨) - (حديث على: " أن النبى صلى الله عليه وسلم قضى بالدين قبل الوصية وأن أعيان بنى الأم يتوارثون دون بنى العلات , يرث الرجل أخاه لأبيه وأمه دون أخيه لأبيه " رواه أحمد والترمذى من رواية الحارث عن على (ص ٦٩) .','2011','1860','0');
INSERT INTO [t22592] VALUES (1863,'باب العصبات','2012','1834','0');
INSERT INTO [t22592] VALUES (1864,'(١٦٨٩) - (حديث ابن مسعود السابق وفيه: " وما بقى فللأخت " رواه البخارى (٢/٧١) .','2012','1863','0');
INSERT INTO [t22592] VALUES (1865,'(١٦٩٠) - (حديث: " ألحقوا الفرائض بأهلها فما بقى فلأولى رجل ذكر " (٢/٧٢) .','2012','1863','0');
INSERT INTO [t22592] VALUES (1866,'(١٦٩١) - (حديث قوله صلى الله عليه وسلم لأخى سعد: " ... وما بقى فهو لك " (٢/٧٢) .','2013','1863','0');
INSERT INTO [t22592] VALUES (1867,'(١٦٩٢) - (قوله صلى الله عليه وسلم: " فما أبقت الفروض فلأولى رجل ذكر " (٢/٧٣) .','2013','1863','0');
INSERT INTO [t22592] VALUES (1868,'(١٦٩٣) - (يروى أن عمر أسقط ولد الأبوين فقال بعضهم أو بعض الصحابة: " يا أمير المؤمنين هب أن أبانا كان حمارا أليست أمنا واحدة؟ فشرك بينهم " (٢/٧٣) .','2013','1863','0');
INSERT INTO [t22592] VALUES (1869,'فصل','2014','1834','0');
INSERT INTO [t22592] VALUES (1870,'(١٦٩٤) - (حديث: " الولاء لمن أعتق " متفق عليه (٢/٧٤) .','2014','1869','0');
INSERT INTO [t22592] VALUES (1871,'(١٦٩٥) - (حديث: " الولاء لحمة كلحمة النسب " (٢/٧٤) .','2014','1869','0');
INSERT INTO [t22592] VALUES (1872,'(١٦٩٦) - (روى سعيد بسنده: " كان لبنت حمزة مولى أعتقته , فمات وترك ابنته ومولاته , فأعطى النبى صلى الله عليه وسلم ابنته النصف , وأعطى مولاته','2014','1869','0');
INSERT INTO [t22592] VALUES (1873,'(١٦٩٧) - (حديث زياد بن أبى مريم: " أن امرأة أعتقت عبدا لها ثم توفيت وتركت ابنا لها وأخاها ثم توفى مولاها من بعدها فأتى أخو المرأة وابنها رسول الله صلى الله عليه وسلم فى ميراثه فقال صلى الله عليه وسلم: ميراثه لابن المرأة , فقال أخوها: يا رسول الله لو جر جريرة كانت على ويكون ميراثه لهذا؟ قال: نعم " رواه أحمد (٢/٧٥) .','2016','1869','0');
INSERT INTO [t22592] VALUES (1874,'باب الرد وذوى الأرحام','2017','1834','0');
INSERT INTO [t22592] VALUES (1875,'(١٦٩٨) - (حديث: " من ترك مالا فللوارث " متفق عليه (٢/٧٦) .','2017','1874','0');
INSERT INTO [t22592] VALUES (1876,'(١٦٩٩) - (روى عن عثمان أنه رد على زوج ".','2017','1874','0');
INSERT INTO [t22592] VALUES (1877,'(١٧٠٠) - (عن عمر مرفوعا: " الخال وارث من لا وارث له , يعقل عنه ويرثه ".','2017','1874','0');
INSERT INTO [t22592] VALUES (1878,'(١٧٠١) - (روى أبو عبيد بإسناده: " أن ثابت بن الدحداح مات ولم يخلف إلا ابنة أخ له فقضى النبى صلى الله عليه وسلم بميراثه لابنة أخيه " (٢/٧٨) .','2021','1874','0');
INSERT INTO [t22592] VALUES (1879,'(١٧٠٢) - (روى عن على وعبد الله: " أنهما نزلا بنت البنت بمنزلة البنت وبنت الأخ بمنزلة الأخ , وبنت الأخت بمنزلة الأخت والعمة منزلة الأب والخالة منزلة الأم " وروى ذلك عن عمر فى العمة والخالة (٢/٧٩) .','2022','1874','0');
INSERT INTO [t22592] VALUES (1880,'(١٧٠٣) - (وعن على: " أنه نزل العمة بمنزلة العم " (٢/٧٩) .','2023','1874','0');
INSERT INTO [t22592] VALUES (1881,'(١٧٠٤) - (عن الزهرى أن النبى صلى الله عليه وسلم قال: " العمة بمنزلة الأب إذا لم يكن بينهما أب والخالة بمنزلة الأم إذا لم يكن بينهما أم " رواه أحمد (٢/٧٩) .','2023','1874','0');
INSERT INTO [t22592] VALUES (1882,'باب أصول المسائل','2025','1834','0');
INSERT INTO [t22592] VALUES (1883,'(١٧٠٥) - (خبر: " أن ابن عباس رضى الله عنهما كان لا يحجب الأم عن الثلث إلى السدس إلا بثلاثة من الإخوة أو الأخوات , ولايرى العول , ويرد النقص مع ازدحام الفروض على من يصير عصبة فى بعض الأحوال بتعصيب ذكر لهن " (٢/٨٣) .','2025','1882','0');
INSERT INTO [t22592] VALUES (1884,'(١٧٠٦) - (المباهلة أول مسألة عائلة حدثت فى زمن عمر بن الخطاب رضى الله عنه فجمع الصحابة للمشورة فيها فقال العباس: " أرى أن يقسم المال بينهم على قدر سهامهم , فأخذ به عمر واتبعه الناس على ذلك حتى خالفهم ابن عباس فقال: من شاء باهلته أن المسائل لا تعول , إن الذى أحصى رمل عالج عددا أعدل من أن يجعل فى مال نصفا ونصفا ... " (٢/٨٣) .','2025','1882','0');
INSERT INTO [t22592] VALUES (1885,'باب ميراث الحمل','2027','1834','0');
INSERT INTO [t22592] VALUES (1886,'(١٧٠٧) - (حديث أبى هريرة مرفوعا: " إذا استهل المولود صارخا ورث " رواه أحمد وأبو داود)','2027','1885','0');
INSERT INTO [t22592] VALUES (1887,'باب ميراث المفقود','2030','1834','0');
INSERT INTO [t22592] VALUES (1888,'(١٧٠٨) - (روى عن عمر: " أنه أمر ولى المفقود أن يطلقها " (٢/٨٨) .','2030','1887','0');
INSERT INTO [t22592] VALUES (1889,'(١٧٠٩) - (قال عبيد بن عمير: " فقد رجل فى عهد عمر فجاءت امرأته إلى عمر فذكرت ذلك له فقال: انطلقى فتربصى أربع سنين ففعلت ثم أتته فقال: انطلقى فاعتدى أربعة أشهر وعشرا ففعلت , ثم أتته فقال: أين ولى هذا الرجل؟ فجاء وليه فقال: طلقها ففعل.','2030','1887','0');
INSERT INTO [t22592] VALUES (1890,'باب ميراث الخنثى','2032','1834','0');
INSERT INTO [t22592] VALUES (1891,'(١٧١٠) - (روى الكلبى عن أبى صالح عن ابن عباس: " أن النبى صلى الله عليه وسلم سئل عن مولود , له قبل وذكر , من أين يورث؟ قال: [يورث] من حيث يبول " (٢/٩٠) .','2032','1890','0');
INSERT INTO [t22592] VALUES (1892,'(١٧١١) - (روى أنه صلى الله عليه وسلم أتى بخنثى من الأنصار فقال: " ورثوه من أول مايبول منه ".','2032','1890','0');
INSERT INTO [t22592] VALUES (1893,'باب ميراث الغرقى ونحوهم','2032','1834','0');
INSERT INTO [t22592] VALUES (1894,'(١٧١٢) - (قال الشعبى: " وقع الطاعون بالشام عام عمواس فجعل أهل البيت يموتون عن آخرهم فكتب فى ذلك إلى عمر , فكتب عمر: أن ورثوا بعضهم من بعض " (٢/٩٣) .','2032','1893','0');
INSERT INTO [t22592] VALUES (1895,'(١٧١٣) - (روى عن إياس المزنى: " أن النبى صلى الله عليه وسلم سئل عن قوم وقع عليهم بيت فقال: يرث بعضهم بعضا ". ورواه سعيد فى " سننه " عن إياس موقوفا (٢/٩٣) .','2034','1893','0');
INSERT INTO [t22592] VALUES (1896,'باب ميراث أهل الملل','2035','1834','0');
INSERT INTO [t22592] VALUES (1897,'(١٧١٤) - (حديث أسامة بن زيد: " لا يرث الكافر المسلم ولا المسلم الكافر " متفق عليه.','2035','1896','0');
INSERT INTO [t22592] VALUES (1898,'(١٧١٥) - (حديث جابر مرفوعا: " لا يرث المسلم النصرانى إلا أن يكون عبده أو أمته " رواه الدارقطنى.','2035','1896','0');
INSERT INTO [t22592] VALUES (1899,'(١٧١٦) - (حديث: " من أسلم على شىء فهو له " رواه سعيد من طريقين عن عروة وابن أبى مليكة عن النبى صلى الله عليه وسلم (٢/٩٤) .','2036','1896','0');
INSERT INTO [t22592] VALUES (1900,'(١٧١٧) - (وعن ابن عباس مرفوعا: " كل قسم قسم فى الجاهلية فهو على ما قسم وكل قسم أدركه الإسلام فإنه على قسم الإسلام " رواه أبو داود وابن ماجه.','2037','1896','0');
INSERT INTO [t22592] VALUES (1901,'(١٧١٨) - (حدث عبد الله بن أرقم عثمان: " أن عمر قضى أنه من أسلم على ميراث قبل أن يقسم فله نصيبه فقضى به عثمان ". رواه ابن عبد البر فى التمهيد (٢/٩٤) .','2038','1896','0');
INSERT INTO [t22592] VALUES (1902,'(١٧١٩) - (حديث: " لا يتوارث أهل ملتين شتى " رواه أبو داود (٢/٩٥) .','2038','1896','0');
INSERT INTO [t22592] VALUES (1903,'(١٧٢٠) - (حديث: " أن النبى صلى الله عليه وسلم لم يأخذ من تركة المنافقين شيئا ولا جعله فيئا (٢/٩٦) .','2038','1896','0');
INSERT INTO [t22592] VALUES (1904,'باب ميراث المطلقة','2039','1834','0');
INSERT INTO [t22592] VALUES (1905,'(١٧٢١) - (روى: " أن عثمان رضى الله عنه ورث تماضر بنت الأصبغ الكلبية من عبد الرحمن بن عوف وكان طلقها فى مرض موته فبتها " (٢/٩٨) .','2039','1904','0');
INSERT INTO [t22592] VALUES (1906,'(١٧٢٢) - (وروى أبو سلمة بن عبد الرحمن " أن أباه طلق أمه , وهو مريض , فمات , فورثته بعد انقضاء عدتها " (٢/٩٨) .','2040','1904','0');
INSERT INTO [t22592] VALUES (1907,'(١٧٢٣) - (وروى عروة: "أن عثمان قال لعبد الرحمن: لئن مت لأورثنها منك، قال قد علمت ذلك")','2041','1904','0');
INSERT INTO [t22592] VALUES (1908,'(١٧٢٤) - (روى عن ابن الزبير أنه قال: " لا ترث مبتوتة ".','2041','1904','0');
INSERT INTO [t22592] VALUES (1909,'(١٧٢٥) - (حديث: " من باع عبدا وله مال فماله للبائع , إلا أن يشترطه المبتاع " (٢/٩٨) .','2041','1904','0');
INSERT INTO [t22592] VALUES (1910,'(١٧٢٦) - (حديث ابن عباس مرفوعا: " قال فى العبد يعتق بعضه: يرث ويورث على قدر ما عتق منه " رواه عبد الله بن أحمد بإسناده (٢/١٠١) .','2041','1904','0');
INSERT INTO [t22592] VALUES (1911,'باب الولاء','2043','1834','0');
INSERT INTO [t22592] VALUES (1912,'(١٧٢٧) - (قوله صلى الله عليه وسلم: " الولاء لمن أعتق " (٢/١٠٢ , ١٠٣) .','2043','1911','0');
INSERT INTO [t22592] VALUES (1913,'(١٧٢٨) - (قول على: " الولاء شعبة من الرق " (٢/١٠٣) .','2043','1911','0');
INSERT INTO [t22592] VALUES (1914,'فصل','2043','1834','0');
INSERT INTO [t22592] VALUES (1915,'(١٧٢٩) - (روى سعيد عن الحسن مرفوعا: " الميراث للعصبة , فإن لم يكن عصبة فللمولى " (٢/١٠٣) .','2043','1914','0');
INSERT INTO [t22592] VALUES (1916,'(١٧٣٠) - (وعنه أيضا: " أن رجلا أعتق عبدا , فقال للنبى صلى الله عليه وسلم: ماترى فى ماله؟ فقال: إن مات ولم يدع وارثا فهو لك " (٢/١٠٣) .','2044','1914','0');
INSERT INTO [t22592] VALUES (1917,'(١٧٣١) - (عن ابن عمر مرفوعا: " الولاء لحمة كلحمة النسب ". رواه الشافعى وابن حبان ورواه الخلال من حديث عبد الله بن أبى أوفى (٢/١٠٤) .','2044','1914','0');
INSERT INTO [t22592] VALUES (1918,'(١٧٣٢) - (حديث: " ألحقوا الفرائض بأهلها , فما بقى فلأولى رجل ذكر " (٢/١٠٤) .','2044','1914','0');
INSERT INTO [t22592] VALUES (1919,'(١٧٣٣) - (عن عبد الله بن شداد , قال: " أعتقت ابنة حمزة مولى لها , فمات وترك ابنة , وابنة حمزة , فأعطى النبى صلى الله عليه وسلم ابنته: النصف , وابنة حمزة: النصف " رواه النسائى وابن ماجه (٢/١٠٤) .','2044','1914','0');
INSERT INTO [t22592] VALUES (1920,'(١٧٣٤) - (روى سعيد بإسناده عن الزهرى: أن النبى صلى الله عليه وسلم , قال: " المولى أخ فى الدين , وولى نعمة يرثه أولى الناس بالمعتق " (٢/١٠٤) .','2044','1914','0');
INSERT INTO [t22592] VALUES (1921,'(١٧٣٥) - (روى أحمد عن زياد بن أبى مريم: " أن امرأة أعتقت عبدا لها , ثم توفيت وتركت ابنا لها وأخاها , ثم توفى مولاها , فأتى أخو المرأة وابنها رسول الله صلى الله عليه وسلم فى ميراثه , فقال صلى الله عليه وسلم: ميراثه لابن المرأة , فقال أخو المرأة: يا رسول الله , لو جر جريرة كانت على , ويكون ميراثه لهذا؟ قال: نعم ".','2045','1914','0');
INSERT INTO [t22592] VALUES (1922,'(١٧٣٦) - (عن إبراهيم قال: " اختصم على والزبير فى مولى صفية , فقال على: مولى عمتى وأنا أعقل عنه , وقال الزبير: مولى أمى وأنا أرثه فقضى عمر على على بالعقل , وقضى للزبير بالميراث " رواه سعيد واحتج به أحمد (٢/١٠٤) .','2045','1914','0');
INSERT INTO [t22592] VALUES (1923,'(١٧٣٧) - (حديث ابن عمر قال: " نهى رسول الله صلى الله عليه وسلم , عن بيع الولاء وهبته " (٢/١٠٥) .','2045','1914','0');
INSERT INTO [t22592] VALUES (1924,'(١٧٣٨) - (حديث: " الولاء لحمة كلحمة النسب لا يباع ولا يوهب " رواه الخلال (٢/١٠٥) .','2045','1914','0');
INSERT INTO [t22592] VALUES (1925,'(١٧٣٩) - (روى عن عمر وابنه وعلى وابن عباس وابن مسعود: " لا يصح أن يأذن لعتيقه فيوالى من شاء " (٢/١٠٥) .','2045','1914','0');
INSERT INTO [t22592] VALUES (1926,'(١٧٤٠) - (عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " ميراث الولاء للكبر من الذكور , ولا يرث النساء من الولاء , إلا ولاء من أعتق "','2046','1914','0');
INSERT INTO [t22592] VALUES (1927,'(١٧٤١) - (روى عبد الرحمن (١) عن الزبير: " أنه لما قدم خيبر رأى فتية لعسا , فأعجبه ظرفهم وحالهم فسأل عنهم , فقيل له: إنهم موال لرافع بن خديج , وأبوهم مملوك لآل الحرقة , فاشترى الزبير أباهم فأعتقه , وقال لأولاده: انتسبوا إلى فإن ولاءكم لى , فقال رافع بن خديج: الولاء لى , لأنهم عتقوا بعتقى أمهم , فاحتكموا إلى عثمان: فقضى بالولاء للزبير فاجتمعت الصحابة عليه " (٢/١٠٦) .','2046','1914','0');
INSERT INTO [t22592] VALUES (1928,'كتاب العتق','2048','0','0');
INSERT INTO [t22592] VALUES (1929,'[الأحاديث ١٧٤٢ - ١٧٤٣]','2048','1928','0');
INSERT INTO [t22592] VALUES (1930,'(١٧٤٢) - قال صلى الله عليه وسلم: " من أعتق رقبة مؤمنة اعتق الله تعالى بكل إرب منها إربا منه من النار حتى إنه ليعتق اليد باليد , والرجل بالرجل , والفرج بالفرج " متفق عليه.','2048','1929','0');
INSERT INTO [t22592] VALUES (1931,'(١٧٤٣) - (حديث: " المسلمون على شروطهم " (٢/١٠٩) .','2048','1929','0');
INSERT INTO [t22592] VALUES (1932,'فصل','2048','1928','0');
INSERT INTO [t22592] VALUES (1933,'(١٧٤٤) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن زنباعا أبا روح وجد غلاما له مع جاريته فقطع ذكره , وجدع أنفه , فأتى العبد النبى صلى الله عليه وسلم , فذكر له ذلك , فقال النبى صلى الله عليه وسلم: ما حملك على ما فعلت؟ قال: فعل كذا كذا , قال: اذهب فأنت حر " رواه أحمد وغيره (٢/١١٠) .','2048','1932','0');
INSERT INTO [t22592] VALUES (1934,'(١٧٤٥) - (روى: " أن رجلا أقعد أمة له فى مقلى حار , فأحرق عجزها , فأعتقها عمر , رضى الله عنه , وأوجعه ضربا " حكاه أحمد فى رواية ابن منصور (٢/١١٠) .','2049','1932','0');
INSERT INTO [t22592] VALUES (1935,'(١٧٤٦) - (حديث الحسن عن سمرة مرفوعا: " من ملك ذا رحم محرم فهو حر " رواه الخمسة وحسنه الترمذى (٢/١١١) .','2049','1932','0');
INSERT INTO [t22592] VALUES (1936,'(١٧٤٧) - (حديث: " لا (يجزىء) [١] ولد والده , إلا أن يجده مملوكا فيشتريه فيعتقه " رواه مسلم (٢/١١١) .','2051','1932','0');
INSERT INTO [t22592] VALUES (1937,'(١٧٤٨) - (روى الأثرم عن ابن مسعود أنه قال لغلامه عمير: " يا عمير! إنى أريد أن أعتقك عتقا هنيئا , فأخبرنى بمالك إنى سمعت رسول الله صلى الله عليه وسلم يقول: أيما رجل أعتق عبده , أو غلامه , فلم يخبره بماله فإنه لسيده " (٢/١١١) .','2051','1932','0');
INSERT INTO [t22592] VALUES (1938,'(١٧٤٩) - (حديث ابن عمر مرفوعا: " من أعتق عبدا وله مال فماله لعبده " رواه أحمد وغيره. قال أحمد: يرويه عبيد الله (١) بن أبى جعفر من أهل مصر وهو ضعيف الحديث , كان صاحب فقه , فأما الحديث فليس فيه بالقوى (٢/١١١) .','2052','1932','0');
INSERT INTO [t22592] VALUES (1939,'(١٧٥٠) - (حديث ابن عمر مرفوعا: " من أعتق شركا له فى عبد , فكان له ما يبلغ ثمن العبد , قوم عليه قيمة عدل فأعطى شركاءه حصصهم , وعتق عليه العبد , وإلا فقد عتق عليه ما عتق " رواه الجماعة والدارقطنى وزاد " ورق ما بقى " (٢/١١٢) .','2053','1932','0');
INSERT INTO [t22592] VALUES (1940,'فصل','2053','1928','0');
INSERT INTO [t22592] VALUES (1941,'(١٧٥١) - (حديث: " لا طلاق , ولا عتاق ولا بيع فيما لا يملك ابن آدم " (٢/١١٣) .','2053','1940','0');
INSERT INTO [t22592] VALUES (1942,'فصل','2055','1928','0');
INSERT INTO [t22592] VALUES (1943,'(١٧٥٢) - (قال سفينة: " أعتقتنى أم سلمة وشرطت على أن أخدم النبى صلى الله عليه وسلم ما عاش " رواه أحمد وابن ماجه.','2055','1942','0');
INSERT INTO [t22592] VALUES (1944,'باب التدبير','2056','1928','0');
INSERT INTO [t22592] VALUES (1945,'(١٧٥٣) - (حديث جابر: " أن رجلا أعتق مملوكا عن دبر فاحتاج , فقال رسول الله صلى الله عليه وسلم: من يشتريه منى؟ فباعه من نعيم بن عبد الله بثمانمائة درهم فدفعها إليه وقال: أنت أحوج منه " متفق عليه (٢/١١٦) .','2056','1944','0');
INSERT INTO [t22592] VALUES (1946,'(١٧٥٤) - (عن أبى هريرة وابن مسعود: " يجوز كتابة المدبر " رواه الأثرم.','2056','1944','0');
INSERT INTO [t22592] VALUES (1947,'(١٧٥٥) - (عن محمد بن قيس بن الأحنف عن أبيه عن جده: " أنه أعتق غلاما له عن دبر وكاتبه , فأدى بعضا وبقى بعض , ومات مولاه فأتوا ابن مسعود فقال: ما أخذ فهو له , ومابقى فلا شىء لكم " رواه البخارى فى تاريخه.','2056','1944','0');
INSERT INTO [t22592] VALUES (1948,'(١٧٥٦) - (روى ابن عمر أن النبى صلى الله عليه وسلم قال: " لا يباع المدبر ولا يشترى " (٢/١١٨) .','2057','1944','0');
INSERT INTO [t22592] VALUES (1949,'(١٧٥٧) - (روى الدارقطنى عن عمرة: أن عائشة أصابها مرض وأن بعض بنى أخيها ذكروا شكواها لرجل من الزط يتطبب وأنه قال لهم: إنكم لتذكرون امرأة مسحورة سحرتها جارية لها , فى حجر الجارية الآن صبى قد بال فى حجرها , فذكروا ذلك لعائشة فقالت: ادعوا لى فلانة الجارية لها , فقالوا: فى حجرها فلان صبى لهم قد بال فى حجرها , فقالت: إيتونى بها , فأتيت بها فقالت: سحرتينى؟ قالت: نعم. قالت: لمه؟ قالت: أردت أن أعتق , وكانت عائشة أعتقتها عن دبر منها , فقال: إن لله على أن لا تعتقى أبدا , انظروا أسوأ العرب ملكة فبيعوها منه , واشترت','2057','1944','0');
INSERT INTO [t22592] VALUES (1950,'(١٧٥٨) - (قال عمر وابنه وجابر: " ولد المدبرة بمنزلتها " (٢/١١٩) .','2058','1944','0');
INSERT INTO [t22592] VALUES (1951,'(١٧٥٩) - (روى عن ابن عمر أنه: " دبر أمتين له وكان يطؤهما " (٢/١١٩) .','2059','1944','0');
INSERT INTO [t22592] VALUES (1952,'(١٧٦٠) - (أن عمر أجبر أنسا على كتابة سيرين ".','2060','1944','0');
INSERT INTO [t22592] VALUES (1953,'(١٧٦١) - (حديث: " لا يحل مال امرىء مسلم إلا عن طيب نفس منه ".','2060','1944','0');
INSERT INTO [t22592] VALUES (1954,'(١٧٦٢) - (أن عليا رضى الله قال: " الكتابة على نجمين والإيتاء من الثانى " (٢/١٢١) .','2060','1944','0');
INSERT INTO [t22592] VALUES (1955,'(١٧٦٣) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " المكاتب عبد ما بقى عليه درهم " رواه أبو داود.','2060','1944','0');
INSERT INTO [t22592] VALUES (1956,'فصل','2061','1928','0');
INSERT INTO [t22592] VALUES (1957,'(١٧٦٤) - (قالت بريرة لعائشة: " إنى كاتبت أهلى على تسع أواق فى كل عام أوقية فأعينينى على كتابتى. فقال النبى صلى الله عليه وسلم لعائشة: اشتريها " متفق عليه (٢/١٢٤) .','2061','1956','0');
INSERT INTO [t22592] VALUES (1958,'فصل','2061','1928','0');
INSERT INTO [t22592] VALUES (1959,'(١٧٦٥) - (روى أبو بكر بإسناده عن على مرفوعا فى قوله تعالى: (وآتوهم من مال الله الذى آتاكم) قال: " ربع الكتابة ".','2061','1958','0');
INSERT INTO [t22592] VALUES (1960,'(١٧٦٦) - (قال على رضى الله عنه: " الكتابة على نجمين والإيتاء من الثانى ".','2061','1958','0');
INSERT INTO [t22592] VALUES (1961,'(١٧٦٧) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " أيما عبد كوتب على مائة أوقية فأداها إلا عشر أوقيات فهو رقيق " رواه الخمسة إلا النسائى. وفى لفظ: " المكاتب عبد مابقى عليه درهم " رواه أبو داود.','2062','1958','0');
INSERT INTO [t22592] VALUES (1962,'(١٧٦٨) - (روى الأثرم عن عمر وابنه وعائشة وزيد بن ثابت أنهم قالوا: " المكاتب عبد مابقى عليه درهم ".','2062','1958','0');
INSERT INTO [t22592] VALUES (1963,'(١٧٦٩) - (حديث أم سلمة مرفوعا: " إذا كان لإحداكن مكاتب وكان عنده ما يؤدى (فلتحجب) [٢] منه " صححه الترمذى (٢/١٢٧) .','2062','1958','0');
INSERT INTO [t22592] VALUES (1964,'(١٧٧٠) - (روى سعيد عن أبى قلابة قال: " كن أزواج النبى صلى الله عليه وسلم لا يحتجبن من مكاتب ما بقى عليه دينار " (٢/١٢٧) .','2063','1958','0');
INSERT INTO [t22592] VALUES (1965,'باب أحكام أم الولد','2065','1928','0');
INSERT INTO [t22592] VALUES (1966,'(١٧٧١) - (حديث إبن عباس مرفوعا: " من وطئ أمته فولدت فهي معتقة عن دبر منه " رواه أحمد وابن ماجه) ٢/١٢٩','2065','1965','0');
INSERT INTO [t22592] VALUES (1967,'(١٧٧٢) - (وعنه أيضا قال: " ذكرت أم إبراهيم عند رسول الله صلى الله عليه وسلم فقال: أعتقها ولدها " رواه ابن ماجه والدارقطنى (٢/١٢٩) .','2066','1965','0');
INSERT INTO [t22592] VALUES (1968,'(١٧٧٣) - (قال عمر: " أبعد ما اختلطت دماؤكم ودماؤهن ولحومكم ولحومهن بعتموهن؟ " (٢/١٢٩ ـ ١٣٠) .','2067','1965','0');
INSERT INTO [t22592] VALUES (1969,'(١٧٧٤) - (قول الرسول صلى الله عليه وسلم: " فهى معتقة عن دبر منه " (٢/١٣٠) .','2067','1965','0');
INSERT INTO [t22592] VALUES (1970,'(١٧٧٥) - (حديث: " معتقة من بعده " (٢/١٣٠) .','2067','1965','0');
INSERT INTO [t22592] VALUES (1971,'(١٧٧٦) - (حديث ابن عمر مرفوعا: " نهى عن بيع أمهات الأولاد وقال: لا يبعن ولا يوهبن ولا يورثن , يستمتع منها السيد مادام حيا فإذا مات فهى حرة " رواه الدارقطنى ورواه مالك فى الموطأ والدارقطنى من طريق آخر عن ابن عمر عن عمر موقوفا (٢/١٣٠) .','2067','1965','0');
INSERT INTO [t22592] VALUES (1972,'(١٧٧٧) - (حديث جابر: " بعنا أمهات الأولاد على عهد رسول الله صلى الله عليه وسلم وعهد أبى بكر فلما كان عمر نهانا فانتهينا " رواه أبو داود (٢/١٣١) .','2069','1965','0');
INSERT INTO [t22592] VALUES (1973,'(١٧٧٨) - (روى سعيد بإسناده عن عبيدة قال: " خطب على رضى','2069','1965','0');
INSERT INTO [t22592] VALUES (1974,'(١٧٧٩) - (وروى عنه أنه قال: " بعث على إلى وإلى شريح أن اقضوا كما كنتم تقضون فإنى أكره الاختلاف " (٢/١٣١) .','2070','1965','0');
INSERT INTO [t22592] VALUES (1975,'(١٧٨٠) - (قال ابن عمر وابن عباس وغيرهما: " ولدها بمنزلتها " (٢/١٣٢) .','2071','1965','0');
INSERT INTO [t22592] VALUES (1976,'كتاب النكاح','2072','0','0');
INSERT INTO [t22592] VALUES (1977,'[الأحاديث ١٧٨١ - ١٨١٢]','2072','1976','0');
INSERT INTO [t22592] VALUES (1978,'(١٧٨١) - (حديث: " يا معشر الشباب: من استطاع منكم الباءة فليتزوج فإنه أغض للبصر وأحصن للفرج ومن لم يستطع فعليه بالصوم فإنه له وجاء " رواه الجماعة من حديث ابن مسعود (٢/١٣٤) .','2072','1977','0');
INSERT INTO [t22592] VALUES (1979,'(١٧٨٢) - (قال النبى صلى الله عليه وسلم: " إنى أتزوج النساء فمن رغب عن سنتى فليس منى " متفق عليه (٢/١٣٤) .','2073','1977','0');
INSERT INTO [t22592] VALUES (1980,'(١٧٨٢) - (قال ابن عباس لسعيد بن جبير: "تزوج فإن خير هذه الأمة أكثرها نساء" رواه أحمد والبخاري) ٢/١٣٤','2073','1977','0');
INSERT INTO [t22592] VALUES (1981,'(١٧٨٣) - (حديث أبى هريرة مرفوعا: " تنكح المرأة لأربع: لمالها , ولحسبها , ولجمالها , ولدينها , فاظفر بذات الدين تربت يداك " متفق عليه (٢/١٣٥) .','2074','1977','0');
INSERT INTO [t22592] VALUES (1982,'(١٧٨٤) - (حديث أنس مرفوعا: " تزوجوا الودود الولود فإنى مكاثر بكم الأمم يوم القيامة " رواه سعيد.','2075','1977','0');
INSERT INTO [t22592] VALUES (1983,'(١٧٨٥) - (قال الرسول صلى الله عليه وسلم لجابر: " فهلا بكرا تلاعبها وتلاعبك " متفق عليه.','2076','1977','0');
INSERT INTO [t22592] VALUES (1984,'(١٧٨٦) - (عن أبى هريرة قال: " قيل يا رسول الله: أى النساء خير؟ قال: التى تسره إذا نظر , وتطيعه إذا أمر , ولاتخالفه فى نفسها ولا فى ماله بما يكره " رواه أحمد والنسائى.','2077','1977','0');
INSERT INTO [t22592] VALUES (1985,'(١٧٨٧) - (فى حديث أبى هريرة رضى الله عنه: " والعينان زناهما النظر ... " متفق عليه.','2078','1977','0');
INSERT INTO [t22592] VALUES (1986,'(١٧٨٨) - (عن جرير قال: " سألت رسول الله صلى الله عليه وسلم عن نظر الفجاءة فقال: اصرف بصرك " رواه أحمد ومسلم وأبو داود.','2078','1977','0');
INSERT INTO [t22592] VALUES (1987,'(١٧٨٩) - (قال ابن مسعود: " إذا أعجبت أحدكم امرأة فليذكر مناتنها " (٢/١٣٦) .','2079','1977','0');
INSERT INTO [t22592] VALUES (1988,'(١٧٩٠) - (قال ابن عباس فى قوله: (إلا ماظهر منها) الوجه والكفين.','2080','1977','0');
INSERT INTO [t22592] VALUES (1989,'(١٧٩١) - (حديث جابر مرفوعا: " إذا خطب أحدكم المرأة فإن استطاع أن ينظر منها إلى ما يدعوه إلى نكاحها فليفعل , قال: فخطبت جارية من بنى سلمة فكنت أتخبأ لها حتى رأيت منها بعض ما دعانى إلى نكاحها " رواه أحمد وأبو داود (٢/١٣٧) .','2080','1977','0');
INSERT INTO [t22592] VALUES (1990,'(١٧٩٢) - (روى أبو حفص بإسناده: " أن ابن عمر كان يضع يده بين ثدييها (يعنى الجارية) وعلى عجزها من فوق الثياب ويكشف عن ساقها " ذكره فى (فى الوقع) [١] .','2081','1977','0');
INSERT INTO [t22592] VALUES (1991,'(١٧٩٣) - (قال النبى صلى الله عليه وسلم لعائشة: " ائذنى له فإنه عمك ".','2081','1977','0');
INSERT INTO [t22592] VALUES (1992,'(١٧٩٤) - (حديث: " لا يقبل الله صلاة حائض إلا بخمار ".','2083','1977','0');
INSERT INTO [t22592] VALUES (1993,'(١٧٩٥) - (روى أبو بكر بإسناده: " أن أسماء بنت أبى بكر دخلت على النبى صلى الله عليه وسلم فى ثياب رقاق فأعرض عنها وقال: يا أسماء إن المرأة إذا بلغت المحيض لم يصلح أن يرى منها إلا هذا وهذا , وأشار إلى وجهه وكفيه " ورواه أبو داود وقال: هذا مرسل (٢/١٣٨) .','2083','1977','0');
INSERT INTO [t22592] VALUES (1994,'(١٧٩٦) - (قال ابن المنذر ثبت: " أن عمر قال لأمة رآها متقنعة: اكشفى رأسك ولا تشبهى بالحرائر , وضربها بالدرة ".','2083','1977','0');
INSERT INTO [t22592] VALUES (1995,'(١٧٩٧) - (حديث: " أن النبى صلى الله عليه وسلم لم يمنع المخنث من الدخول على نسائه فلما وصف ابنة غيلان وفهم أمر النساء أمر بحجبه " (٢/١٣٨) .','2085','1977','0');
INSERT INTO [t22592] VALUES (1996,'(١٧٩٨) - (حديث: " أن أبا طيبة حجم أزواج النبى صلى الله عليه وسلم وهو','2085','1977','0');
INSERT INTO [t22592] VALUES (1997,'(١٧٩٩) - (وعن أنس: " أن النبى صلى الله عليه وسلم أتى فاطمة بعبد قد وهبه لها , قال: وعلى فاطمة ثوب إذا قنعت به رأسها لم يبلغ رجليها وإذا غطت به رجليها لم يبلغ رأسها فلما رأى النبى صلى الله عليه وسلم ما تلقى قال: إنه ليس عليك بأس إنما هو أبوك وغلامك " رواه أبو داود (٢/١٣٩) .','2086','1977','0');
INSERT INTO [t22592] VALUES (1998,'(١٨٠٠) - (حديث: " إذا كان لإحداكن مكاتب وعنده مايؤدى فلتحتجب منه " صححه الترمذى (٢/١٣٩) .','2086','1977','0');
INSERT INTO [t22592] VALUES (1999,'(١٨٠١) - (حديث: " أنه صلى الله عليه وسلم أمر بالكشف عن مؤتزر بنى قريظة " (٢/١٣٩) [١]','2086','1977','0');
INSERT INTO [t22592] VALUES (2000,'(١٨٠٢) - (عن عثمان أنه أتى بغلام قد سرق فقال: " انظروا إلى مؤتزره فلم يجدوه أنبت الشعر فلم يقطعه " (٢/١٣٩) . [١]','2087','1977','0');
INSERT INTO [t22592] VALUES (2001,'(١٨٠٣) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " إذا زوج أحدكم جاريته عبده أو أجيره فلا ينظر إلى ما دون السرة والركبة فإنه عورة " رواه أبو داود (٢/١٤٠)','2087','1977','0');
INSERT INTO [t22592] VALUES (2002,'(١٨٠٤) - (قال صلى الله عليه وسلم لفاطمة بنت قيس: " اعتدى فى بيت ابن أم مكتوم فإنه رجل أعمى تضعين ثيابك فلا يراك " متفق عليه (٢/١٤٠) .','2087','1977','0');
INSERT INTO [t22592] VALUES (2003,'(١٨٠٥) - (قالت عائشة: " كان رسول الله صلى الله عليه وسلم يسترنى بردائه وأنا أنظر إلى الحبشة يلعبون فى المسجد " متفق عليه (٢/١٤٠) .','2090','1977','0');
INSERT INTO [t22592] VALUES (2004,'(١٨٠٦) - (حديث نبهان عن أم سلمة قالت: " كنت قاعدة عند','2090','1977','0');
INSERT INTO [t22592] VALUES (2005,'(١٨٠٧) - (حديث: " إذا كان لإحداكن مكاتب (فلتحجب) [١] منه ".','2091','1977','0');
INSERT INTO [t22592] VALUES (2006,'(١٨٠٨) - (حديث أبى سعيد مرفوعا: " لا ينظر الرجل إلى عورة الرجل ولا تنظر المرأة إلى عورة المرأة , ولا يفضى الرجل إلى الرجل فى الثوب الواحد ولا المرأة إلى المرأة فى الثوب الواحد " رواه أحمد ومسلم (٢/١٤٢) .','2091','1977','0');
INSERT INTO [t22592] VALUES (2007,'(١٨٠٩) - (روى الشعبى قال: " قدم وفد عبد القيس على النبى صلى الله عليه وسلم وفيهم غلام أمرد ظاهر الوضاءة فأجلسه النبى صلى الله عليه وسلم وراء ظهره " رواه أبو حفص (٢/١٤١) .','2092','1977','0');
INSERT INTO [t22592] VALUES (2008,'(١٨١٠) - (حديث بهز بن حكيم عن أبيه عن جده قال: " قلت يا رسول الله عوراتنا ما نأتى منها ومانذر؟ قال: احفظ عورتك , إلا من زوجتك أو ما ملكت يمينك " حسنه الترمذى (٢/١٤١) .','2092','1977','0');
INSERT INTO [t22592] VALUES (2009,'(١٨١١) - (روى أبو حفص عن أبى ليلى قال: " كنا جلوسا عند النبى صلى الله عليه وسلم فجاء الحسن فجعل يتمرغ عليه فرفع مقدم قميصه ـ أراه قال ـ فقبل زبيبه ".','2093','1977','0');
INSERT INTO [t22592] VALUES (2010,'(١٨١٢) - (حديث عائشة: " ما رأيت فرج رسول الله صلى الله عليه وسلم قط " رواه ابن ماجه. وفى لفظ: " ما رأيته من النبى صلى الله عليه وسلم ولا رآه منى ".','2093','1977','0');
INSERT INTO [t22592] VALUES (2011,'فصل','2095','1976','0');
INSERT INTO [t22592] VALUES (2012,'(١٨١٣) - (حديث جابر مرفوعا: " من كان يؤمن بالله واليوم الآخر فلا يخلون بامرأة ليس معها ذو محرم منها فإن ثالثهما الشيطان " رواه أحمد وعن ابن عباس معناه , متفق عليه.','2095','2011','0');
INSERT INTO [t22592] VALUES (2013,'(١٨١٤) - (حديث: " دخل النبى صلى الله عليه وسلم على أم سلمة وهى متأيمة من أبى سلمة فقال: لقد علمت أنى رسول الله وخيرته من خلقه وموضعى من قومى ... وكانت تلك خطبته " رواه الدارقطنى (٢/١٤٣) .','2096','2011','0');
INSERT INTO [t22592] VALUES (2014,'(١٨١٦) - (حديث أبى هريرة مرفوعا: " لا يخطب الرجل على خطبة','2097','2011','0');
INSERT INTO [t22592] VALUES (2015,'(١٨١٧) - (حديث ابن عمر يرفعه: " لا يخطب الرجل على خطبة الرجل حتى يترك الخاطب قبله أو يأذن الخاطب " رواه أحمد والبخارى والنسائى (٢/١٤٣) .','2098','2011','0');
INSERT INTO [t22592] VALUES (2016,'(١٨١٨) - (عن عروة: " أن النبى صلى الله عليه وسلم خطب عائشة إلى أبى بكر " رواه البخارى مختصرا مرسلا (٢/١٤٤) .','2099','2011','0');
INSERT INTO [t22592] VALUES (2017,'(١٨١٩) - (عن أم سلمة قالت: " لما مات أبو سلمة أرسل إلى رسول الله صلى الله عليه وسلم يخطبنى وأجبته " رواه مسلم مختصرا.','2099','2011','0');
INSERT INTO [t22592] VALUES (2018,'(١٨٢٠) - (روى أبو حفص العكبرى مرفوعا: " أمسوا بالإملاك فإنه أعظم للبركة " (٢/١٤٤) .','2101','2011','0');
INSERT INTO [t22592] VALUES (2019,'(١٨٢١) - (يسن أن يخطب قبله بخطبة ابن مسعود " رواه الترمذى وصححه.','2101','2011','0');
INSERT INTO [t22592] VALUES (2020,'(١٨٢٢) - (حديث ابن عمر: " أنه كان إذا دعى ليزوج قال: الحمد لله وصلى الله على سيدنا محمد , إن فلانا يخطب إليكم فإن أنكحتموه فالحمد لله وإن رددتموه فسبحان الله " (٢/١٤٥) .','2101','2011','0');
INSERT INTO [t22592] VALUES (2021,'(١٨٢٣) - (حديث: " أن رجلا قال للنبى صلى الله عليه وسلم زوجنيها فقال: زوجتكها بما معك من القرآن " (٢/١٤٥) .','2102','2011','0');
INSERT INTO [t22592] VALUES (2022,'(١٨٢٤) - (عن رجل من بنى سليم قال: " خطبت إلى النبى صلى الله عليه وسلم أمامة بنت عبد المطلب فأنكحنى من غير أن يتشهد " رواه أبو داود (ص ١٤٥) .','2102','2011','0');
INSERT INTO [t22592] VALUES (2023,'باب ركنى النكاح وشروطه','2104','1976','0');
INSERT INTO [t22592] VALUES (2024,'(١٨٢٥) - (حديث أنس مرفوعا: " أعتق صفية وجعل عتقها صداقها " متفق عليه (٢/١٤٦) .','2104','2023','0');
INSERT INTO [t22592] VALUES (2025,'(١٨٢٦) - (حديث:" ثلاث جدهن جد وهزلهن جد: الطلاق والنكاح والرجعة " حسنه الترمذى (ص ١٤٦) .','2104','2023','0');
INSERT INTO [t22592] VALUES (2026,'(١٨٢٧) - (روى أن ابن عمر: " زوج ابنه وهو صغير فاختصموا إلى زيد فأجازاه جميعا " رواه الأثرم (٢/١٤٧ ـ ١٤٨) .','2108','2023','0');
INSERT INTO [t22592] VALUES (2027,'(١٨٢٨) - (حديث أبى هريرة مرفوعا: " لا تنكح الأيم حتى تستأمر , ولا تنكح البكر حتى تستأذن , قالوا: يا رسول الله: وكيف إذنها؟ قال: أن تسكت " متفق عليه.','2108','2023','0');
INSERT INTO [t22592] VALUES (2028,'(١٨٢٩) - (قالت عائشة: " إذا بلغت الجارية تسع سنين فهى امرأة " رواه أحمد. وروى عن ابن عمر مرفوعا (٢/١٤٨) .','2109','2023','0');
INSERT INTO [t22592] VALUES (2029,'(١٨٣٠) - (حديث: " أن الخنساء زوجها أبوها وهى ثيب فكرهت ذلك فرد رسول الله صلى الله عليه وسلم نكاحه قال ابن عبد البر: هو حديث مجمع على صحته ولا نعلم مخالفا له إلا الحسن (٢/١٤٨) .','2109','2023','0');
INSERT INTO [t22592] VALUES (2030,'(١٨٣١) - (حديث: " أن عائشة تزوجت وهى ابنة ست " متفق عليه.','2110','2023','0');
INSERT INTO [t22592] VALUES (2031,'(١٨٣٢) - (روى الأثرم: " أن قدامة بن مظعون تزوج ابنة الزبير حين نفست فقيل له , فقال: ابنة الذبح (١) إن مت ورثتنى وإن عشت كانت امرأتى ".','2111','2023','0');
INSERT INTO [t22592] VALUES (2032,'(١٨٣٣) - (حديث ابن عباس: " الأيم أحق بنفسها من وليها والبكر تستأمر وإذنها صماتها " رواه أبو داود.','2111','2023','0');
INSERT INTO [t22592] VALUES (2033,'(١٨٣٤) - (حديث: " تستأمر اليتيمة فى نفسها فإن سكتت فهو إذنها وإن أبت فلا جواز عليها " رواه أحمد وأبو داود.','2112','2023','0');
INSERT INTO [t22592] VALUES (2034,'(١٨٣٥) - (روى: " أن قدامة بن مظعون زوج ابنة أخيه من عبد الله بن عمر فرفع ذلك إلى النبى صلى الله عليه وسلم فقال: إنها يتيمة ولا تنكح إلا بإذنها " رواه أحمد والدارقطنى بأبسط من هذا (٢/١٥٠) .','2113','2023','0');
INSERT INTO [t22592] VALUES (2035,'(١٨٣٦) - (حديث: " الثيب تعرب عن نفسها , والبكر رضاها صماتها " رواه الأثرم (ص ١٥٠)','2114','2023','0');
INSERT INTO [t22592] VALUES (2036,'(١٨٣٧) - (قالت عائشة: " يا رسول الله: إن البكر تستحيى. قال: رضاها صماتها " متفق عليه (ص ١٥٠) .','2115','2023','0');
INSERT INTO [t22592] VALUES (2037,'(١٨٣٨) - (حديث أبى هريرة: " ... فإن بكت أو سكتت , فهو رضاها , وإن أبت فلا جواز عليها " رواه أبو بكر.','2115','2023','0');
INSERT INTO [t22592] VALUES (2038,'(١٨٣٩) - (حديث: " لا نكاح إلا بولى " رواه الخمسة إلا النسائى وصححه أحمد وابن معين (ص ١٥٠) .','2115','2023','0');
INSERT INTO [t22592] VALUES (2039,'(١٨٤٠) - (عن عائشة مرفوعا: " أيما امرأة نكحت بغير إذن وليها فنكاحها باطل , فنكاحها باطل , فنكاحها باطل فإن دخل بها فلها المهر بما استحل من فرجها , وإن اشتجروا فالسلطان ولى من لا ولى لها " رواه الخمسة إلا النسائى (ص ١٥٠) .','2123','2023','0');
INSERT INTO [t22592] VALUES (2040,'(١٨٤١) - (عن أبى هريرة مرفوعا: " لا تزوج المرأة المرأة , ولا تزوج المرأة نفسها , فإن الزانية هى التى تزوج نفسها " رواه ابن ماجه والدارقطنى (ص ١٥١) .','2128','2023','0');
INSERT INTO [t22592] VALUES (2041,'(١٨٤٢) - (عن عكرمة بن خالد قال: " جمعت الطريق ركبا فجعلت امرأة منهن ثيبا أمرها بيد رجل غير ولى فأنكحها فبلغ ذلك عمر فجلد الناكح والمنكح ورد نكاحهما " رواه الشافعى والدارقطنى (ص ١٥١) .','2129','2023','0');
INSERT INTO [t22592] VALUES (2042,'(١٨٤٣) - (نزلت آية (فلا تعضلوهن أن ينكحن أزواجهن) فى معقل ابن يسار حين امتنع من تزويج أخته فدعاه النبى صلى الله عليه وسلم فزوجها " رواه البخارى وغيره بمعناه.','2130','2023','0');
INSERT INTO [t22592] VALUES (2043,'(١٨٤٤) - (قول ابن عباس: " لا نكاح إلا بشاهدى عدل وولى مرشد ".','2131','2023','0');
INSERT INTO [t22592] VALUES (2044,'(١٨٤٥) - (روى عن ابن عباس مرفوعا: " لا نكاح إلا بولى وشاهدى عدل , وأيما امرأة أنكحها ولى مسخوط فنكاحها باطل ".','2131','2023','0');
INSERT INTO [t22592] VALUES (2045,'(١٨٤٦) - (حديث: " أم سلمة أنها لما انقضت عدتها أرسل إليها رسول الله صلى الله عليه وسلم يخطبها , فقالت: يا رسول الله: ليس أحد من أوليائى شاهدا. قال: ليس من أوليائك شاهد ولا غائب يكره ذلك , فقالت لابنها: يا عمر قم فزوج رسول الله صلى الله عليه وسلم فزوجه " رواه أحمد والنسائى.','2131','2023','0');
INSERT INTO [t22592] VALUES (2046,'(١٨٤٧) - (قال على بن أبى طالب: " إذا بلغ النساء نص الحقائق فالعصبة أولى ". رواه أبو عبيد فى الغريب (ص ١٥٣) .','2131','2023','0');
INSERT INTO [t22592] VALUES (2047,'(١٨٤٨) - (حديث: " ... فإن اشتجروا فالسلطان ولى من لا ولى له " (ص ١٥٤) .','2132','2023','0');
INSERT INTO [t22592] VALUES (2048,'فصل','2132','1976','0');
INSERT INTO [t22592] VALUES (2049,'(١٨٤٩) - (حديث: " إن الرسول صلى الله عليه وسلم وكل أبا رافع فى تزويجه ميمونة " رواه مالك.','2132','2048','0');
INSERT INTO [t22592] VALUES (2050,'(١٨٥٠) - (حديث: " أنه صلى الله عليه وسلم وكل عمرو بن أمية فى تزويجه أم حبيبة " (ص ١٥٤) .','2133','2048','0');
INSERT INTO [t22592] VALUES (2051,'(١٨٥١) - (روى: " أن رجلا من العرب ترك ابنته عند عمر , وقال: إذا وجدت كفئا فزوجه ولو بشراك نعله , فزوجها عثمان بن عفان " (ص ١٥٥) .','2133','2048','0');
INSERT INTO [t22592] VALUES (2052,'(١٨٥٢) - (قول عمر: " إذا أنكح وليان فالأول أحق ما لم يدخل بها الثانى " (ص ١٥٦) .','2134','2048','0');
INSERT INTO [t22592] VALUES (2053,'(١٨٥٣) - (روى سمرة عنه صلى الله عليه وسلم قال: " أيما امرأة زوجها وليان فهى للأول " رواه أبو داود والترمذى وأخرجه النسائى عنه و (١) عن عقبة (ص ١٥٦) .','2134','2048','0');
INSERT INTO [t22592] VALUES (2054,'(١٩١١) ـ (وروى نحوه عن على) . [١]','2135','2048','0');
INSERT INTO [t22592] VALUES (2055,'(١٨٥٤) - (روى البخارى عن عبد الرحمن بن عوف: " أنه قال لأم حكيم ابنة قارظ: أتجعلين أمرك إلى؟ قالت: نعم. قال: قد تزوجتك " (ص ١٥٧) .','2135','2048','0');
INSERT INTO [t22592] VALUES (2056,'(١٨٥٥) - (أن المغيرة بن شعبة أمر رجلا أن يزوجه امرأة , المغيرة أولى بها منه " رواه أبو داود (ص ١٥٧) .','2136','2048','0');
INSERT INTO [t22592] VALUES (2057,'(١٨٥٦) - (حديث أنس: " أن النبى صلى الله عليه وسلم أعتق صفية وجعل عتقها صداقها " رواه أحمد وأبو داود والترمذى وصححه (ص ١٥٧) .','2137','2048','0');
INSERT INTO [t22592] VALUES (2058,'(١٨٥٧) - (عن صفية قالت: " أعتقنى رسول الله صلى الله عليه وسلم وجعل عتقى صداقى " رواه الأثرم (ص ١٥٧) .','2137','2048','0');
INSERT INTO [t22592] VALUES (2059,'(١٨٥٨) - (حديث: " لا نكاح إلا بولى وشاهدين " ذكره أحمد (ص ١٥٧) .','2138','2048','0');
INSERT INTO [t22592] VALUES (2060,'(١٨٥٩) - (حديث عائشة مرفوعا: " لابد فى النكاح من حضور','2140','2048','0');
INSERT INTO [t22592] VALUES (2061,'(١٨٦٠) - (حديث عمران بن حصين مرفوعا: " لا نكاح إلا بولى وشاهدى عدل " ذكره أحمد فى رواية ابنه عبد الله ورواه الخلال (ص ١٥٨) .','2141','2048','0');
INSERT INTO [t22592] VALUES (2062,'(١٨٦١) - (روى مالك فى الموطأ عن أبى الزبير: " أن عمر بن الخطاب أتى بنكاح لم يشهد عليه إلا رجل وامرأة فقال: هذا نكاح السر ولا أجيزه ولو كنت تقدمت فيه لرجمت " (ص ١٥٨) .','2141','2048','0');
INSERT INTO [t22592] VALUES (2063,'(١٨٦٢) - (حديث ابن عباس مرفوعا: " البغايا اللواتى يزوجن أنفسهن بغير بينة " رواه الترمذى (ص ١٥٨) .','2141','2048','0');
INSERT INTO [t22592] VALUES (2064,'(١٨٦٣) - (فى البخارى: " أن أبا حذيفة أنكح سالما ابنة أخيه الوليد بن عتبة وهو مولى لامرأة من الأنصار " (ص ١٥٨) .','2142','2048','0');
INSERT INTO [t22592] VALUES (2065,'(١٨٦٤) - (حديث: " أمر النبى صلى الله عليه وسلم فاطمة بنت قيس أن تنكح أسامة فنكحها بأمره " متفق عليه (ص ١٥٨) .','2144','2048','0');
INSERT INTO [t22592] VALUES (2066,'(١٨٦٥) - (قال ابن مسعود لأخته: " أنشدك الله ألا تنكحى إلا مسلما وإن كان أحمر روميا أو أسود حبشيا " (ص ١٥٨ ـ ١٥٩) [١]','2144','2048','0');
INSERT INTO [t22592] VALUES (2067,'(١٨٦٦) - (حديث جابر مرفوعا: " لا ينكح النساء إلا الأكفاء ولا يزوجهن إلا الأولياء " رواه الدارقطنى (ص ١٥٩) .','2144','2048','0');
INSERT INTO [t22592] VALUES (2068,'(١٨٦٧) - (قال عمر رضى الله عنه: " لأمنعن تزوج (١) ذوات الأحساب إلا من الأكفاء " رواه الدارقطنى (ص ١٥٩) .','2145','2048','0');
INSERT INTO [t22592] VALUES (2069,'(١٨٦٨) - (عن أبى حاتم المزنى مرفوعا: " إذا أتاكم من ترضون دينه وخلقه فأنكحوه , إن لا تفعلوه تكن فتنة فى الأرض وفساد كبير. قالوا: يا رسول الله وإن كان فيه؟ قال: إذا جاءكم من ترضون دينه وخلقه فأنكحوه ... ثلاث مرات " رواه الترمذى وقال: * حسن.','2146','2048','0');
INSERT INTO [t22592] VALUES (2070,'(١٨٦٩) - (حديث: " العرب بعضهم لبعض أكفاء إلا حائكا أو حجاما " (ص ١٦٠) .','2148','2048','0');
INSERT INTO [t22592] VALUES (2071,'(١٨٧٠) - (حديث: " الحسب المال " (ص ١٦٠) .','2150','2048','0');
INSERT INTO [t22592] VALUES (2072,'(١٨٧١) - (حديث: " إن أحساب الناس بينهم هذا المال " رواه النسائى بمعناه (ص ١٦٠) .','2152','2048','0');
INSERT INTO [t22592] VALUES (2073,'(١٨٧٢) - (حديث: " اللهم أحينى مسكينا وأمتنى مسكينا " رواه الترمذى (ص ١٦٠) .','2152','2048','0');
INSERT INTO [t22592] VALUES (2074,'(١٨٧٣) - (حديث: " أن الرسول صلى الله عليه وسلم خير بريرة حين عتقت تحت العبد " (ص ١٦٠ ـ ١٦١) .','2152','2048','0');
INSERT INTO [t22592] VALUES (2075,'(١٨٧٤) - (قال سلمان لجرير: " إنكم معشر العرب لا نتقدمكم فى صلاتكم , ولا ننكح نساءكم إن الله فضلكم علينا بمحمد صلى الله عليه وسلم وجعله فيكم " رواه البزار بسند جيد. ورواه سعيد بمعناه (ص ١٦١) .','2158','2048','0');
INSERT INTO [t22592] VALUES (2076,'باب المحرمات فى النكاح','2162','1976','0');
INSERT INTO [t22592] VALUES (2077,'(١٨٧٥) - (قال الرسول صلى الله عليه وسلم ـ لما ذكر هاجر أم إسماعيل ـ: " تلك أمكم يابنى ماء السماء ".','2162','2076','0');
INSERT INTO [t22592] VALUES (2078,'(١٨٧٦) - (حديث: " يحرم من الرضاع ما يحرم من النسب " متفق عليه (ص ١٦١) .','2162','2076','0');
INSERT INTO [t22592] VALUES (2079,'(١٨٧٧) - (عن على مرفوعا: " إن الله حرم من الرضاعة ما حرم من النسب " رواه أحمد والترمذى وصححه.','2165','2076','0');
INSERT INTO [t22592] VALUES (2080,'(١٨٧٨) - (قال ابن عباس: " أبهموا ما أبهمه القرآن " (٢/١٦٣) .','2165','2076','0');
INSERT INTO [t22592] VALUES (2081,'(١٨٧٩) - (حديث عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " أيما رجل نكح امرأة دخل بها أو لم يدخل فلا يحل له نكاح أمها " رواه ابن ماجه ورواه أبو حفص بمعناه.','2166','2076','0');
INSERT INTO [t22592] VALUES (2082,'(١٨٨٠) - (روى عن عمر وعلى: " أنهما رخصا فيها (يعنى الربيبة) إذا لم تكن فى حجره " (٢/١٦٤) .','2167','2076','0');
INSERT INTO [t22592] VALUES (2083,'(١٨٨١) - (عن ابن عباس: " أن وطء الحرام لا يحرم ".','2167','2076','0');
INSERT INTO [t22592] VALUES (2084,'(١٨٨٢) - (عن أبى هريرة مرفوعا: " لا تجمعوا بين المرأة وعمتها ولا بين المرأة وخالتها " متفق عليه (٢/١٦٥) .','2168','2076','0');
INSERT INTO [t22592] VALUES (2085,'(١٨٨٣) - (قول الرسول صلى الله عليه وسلم لغيلان بن سلمة حين أسلم وتحته عشر نسوة: " أمسك أربعا وفارق سائرهن " رواه الترمذى.','2171','2076','0');
INSERT INTO [t22592] VALUES (2086,'(١٨٨٤) - (قال نوفل بن معاوية: " أسلمت وتحتى خمس نسوة , فقال النبى صلى الله عليه وسلم: فارق واحدة منهن " رواه الشافعى (ص ١٦٧) .','2175','2076','0');
INSERT INTO [t22592] VALUES (2087,'(١٨٨٥) - (عن قيس بن الحارث قال: " أسلمت وعندى ثمانية نسوة فأتيت النبى صلى الله عليه وسلم فذكرت ذلك له فقال: اختر منهن أربعا " رواه أبو داود وابن ماجه (ص ١٦٧) .','2175','2076','0');
INSERT INTO [t22592] VALUES (2088,'(١٨٨٦) - (نهى النبى صلى الله عليه وسلم مرثد بن أبى مرثد الغنوى أن ينكح عناقا " رواه أبو داود والترمذى والنسائى.','2176','2076','0');
INSERT INTO [t22592] VALUES (2089,'(١٨٨٧) - (قال عليه الصلاة والسلام لامرأة رفاعة لما أرادت أن ترجع إليه بعد أن طلقها ثلاثا وتزوجت بعبد الرحمن بن الزبير: " لا حتى تذوقى عسيلته ويذوق عسيلتك " رواه الجماعة.','2177','2076','0');
INSERT INTO [t22592] VALUES (2090,'(١٨٨٨) - (حديث عثمان مرفوعا: " لا ينكح المحرم ولا ينكح ولا يخطب ". رواه الجماعة إلا البخارى. ولم يذكر الترمذى الخطبة (ص ١٦٩) .','2181','2076','0');
INSERT INTO [t22592] VALUES (2091,'(١٨٨٩) - (وضعف أحمد رواية من روى عن حذيفة: " أنه تزوج مجوسية " , فقال: أبو وائل يقول: " يهودية ". وهو أوثق (ص ١٦٩) .','2181','2076','0');
INSERT INTO [t22592] VALUES (2092,'(١٨٩٠) - (حديث: " المسلمون على شروطهم ".','2181','2076','0');
INSERT INTO [t22592] VALUES (2093,'(١٨٩١) - (قول عمر: " مقاطع الحقوق عند الشروط ".','2182','2076','0');
INSERT INTO [t22592] VALUES (2094,'باب الشروط فى النكاح','2183','1976','0');
INSERT INTO [t22592] VALUES (2095,'(١٨٩٢) - (حديث: " إن أحق ما أوفيتم به من الشروط ما استحللتم به الفروج " متفق عليه.','2183','2094','0');
INSERT INTO [t22592] VALUES (2096,'(١٨٩٣) - (روى الأثرم: " أن رجلا تزوج امرأة وشرط لها دارها ثم أراد نقلها فخاصموه إلى عمر فقال: لها شرطها , فقال الرجل: إذا يطلقننا , فقال عمر: مقاطع الحقوق عند الشروط " (ص ١٧١) .','2183','2094','0');
INSERT INTO [t22592] VALUES (2097,'(١٨٩٤) - (نهى الرسول صلى الله عليه وسلم أن تشترط المرأة طلاق أختها " متفق عليه.','2184','2094','0');
INSERT INTO [t22592] VALUES (2098,'(١٨٩٥) - (حديث ابن عمر أن النبى صلى الله عليه وسلم: " نهى عن الشغار " متفق عليه (ص ١٧٣) .','2185','2094','0');
INSERT INTO [t22592] VALUES (2099,'(١٨٩٦) - (عن الأعرج: " أن العباس بن عبد الله بن عباس أنكح عبد الرحمن بن الحكم ابنته , وأنكحه عبد الرحمن ابنته وكانا جعلا صداقا فكتب معاوية إلى مروان يأمره أن يفرق بينهما , وقال فى كتابه: هذا الشغار الذى نهى عنه رسول الله صلى الله عليه وسلم " رواه أحمد وأبو داود (ص ١٧٢) .','2187','2094','0');
INSERT INTO [t22592] VALUES (2100,'(١٨٩٧) - (حديث: " لعن الله المحلل والمحلل له " رواه أبو داود وابن ماجه والترمذى (ص ١٧٣) .','2187','2094','0');
INSERT INTO [t22592] VALUES (2101,'(١٨٩٨) - (روى نافع عن ابن عمر أن رجلا قال له: " تزوجتها أحلها لزوجها لم يأمرنى ولم يعلم قال: لا إلا نكاح رغبة إن أعجبتك أمسكتها وإن كرهتها فارقتها , قال: وإن كنا نعده على عهد رسول الله صلى الله عليه وسلم سفاحا. وقال لا يزالا زانيين وإن مكثا عشرين سنة إذا علم أنه يريد أن يحلها (ص ١٧٣) .','2191','2094','0');
INSERT INTO [t22592] VALUES (2102,'(١٨٩٩) - (جاء رجل إلى ابن عباس فقال: " إن عمى طلق امرأته ثلاثا أيحلها له رجل؟ قال: من يخادع الله يخدعه " (٢/١٧٣) [١]','2192','2094','0');
INSERT INTO [t22592] VALUES (2103,'(١٩٠٠) - (روى أبو حفص بإسناده عن محمد بن سيرين قال: " قدم مكة رجل ومعه إخوة له صغار وعليه إزار من بين يديه رقعة ومن خلفه رقعة , فسأل عمر فلم يعطه شيئا. فبينما هو كذلك إذ (نزع) [٢] الشيطان بين رجل من قريش وبين امرأته فطلقها ثلاثا فقال: هل لك أن تعطى ذا الرقعتين شيئا ويحلك لى؟ قالت: نعم إن شئت ". رواه سعيد بنحوه.','2192','2094','0');
INSERT INTO [t22592] VALUES (2104,'(١٩٠١) - (حديث الربيع بن سبرة قال: " أشهد على أبى أنه حدث أن رسول الله صلى الله عليه وسلم نهى عنه فى حجة الوداع " وفى لفظ: " أن رسول الله صلى الله عليه وسلم حرم متعة النساء " رواه أبو داود (ص ١٧٤) .','2192','2094','0');
INSERT INTO [t22592] VALUES (2105,'(١٩٠٢) - (لمسلم عن سبرة: " أمرنا رسول الله صلى الله عليه وسلم بالمتعة عام الفتح حين دخلنا مكة ثم لم نخرج حتى نهانا عنها " (ص ١٧٥) .','2195','2094','0');
INSERT INTO [t22592] VALUES (2106,'(١٩٠٣) - (حكى عن ابن عباس: " الرجوع عن قوله بجواز المتعة " (٢/١٧٥) .','2196','2094','0');
INSERT INTO [t22592] VALUES (2107,'فصل','2200','1976','0');
INSERT INTO [t22592] VALUES (2108,'(١٩٠٤) - (حديث عروة عن عائشة: " أن بريرة أعتقت وكان زوجها عبدا فخيرها رسول الله صلى الله عليه وسلم ". رواه أحمد ومسلم وأبو داود والترمذى وصححه (ص ١٧٧) .','2200','2107','0');
INSERT INTO [t22592] VALUES (2109,'(١٩٠٥) - (خبر الأسود عن عائشة: " أنه صلى الله عليه وسلم خير بريرة وكان زوجها حرا ". رواه النسائى ".','2200','2107','0');
INSERT INTO [t22592] VALUES (2110,'(١٩٠٦) - (وروى القاسم وعروة عنها: " أنه كان عبدا ". رواه البخارى.','2200','2107','0');
INSERT INTO [t22592] VALUES (2111,'(١٩٠٧) - (قال ابن عباس: " كان زوج بريرة عبدا أسود لبنى المغيرة يقال له: مغيث ". رواه البخارى وغيره.','2200','2107','0');
INSERT INTO [t22592] VALUES (2112,'(١٩٠٨) - (قوله صلى الله عليه وسلم لبريرة: " إن قربك فلا خيار لك ". رواه أبو داود (ص ١٧٧) .','2201','2107','0');
INSERT INTO [t22592] VALUES (2113,'(١٩٠٩) - (روى نافع عن ابن عمر: " أن لها الخيار ما لم يمسها ". رواه مالك (٢/١٧٨) .','2201','2107','0');
INSERT INTO [t22592] VALUES (2114,'باب حكم العيوب فى النكاح','2202','1976','0');
INSERT INTO [t22592] VALUES (2115,'(١٩١٠) - (روى أبو عبيد بإسناده عن سليمان بن يسار: " أن ابن سند تزوج امرأة وهو خصى فقال له عمر: أعلمتها؟ قال: لا.','2202','2114','0');
INSERT INTO [t22592] VALUES (2116,'(١٩١١) - (روى عن عمر وعثمان وابن مسعود والمغيرة بن شعبة: " أن العنين يؤجل سنة ".','2202','2114','0');
INSERT INTO [t22592] VALUES (2117,'(١٩١٢) - (حديث: " أن النبى صلى الله عليه وسلم تزوج امرأة من بنى غفار فرأى بكشحها بياضا فقال لها: البسى ثيابك والحقى بأهلك ". رواه أحمد وسعيد فى " سننه " (٢/١٧٩) .','2206','2114','0');
INSERT INTO [t22592] VALUES (2118,'باب نكاح الكفار','2209','1976','0');
INSERT INTO [t22592] VALUES (2119,'(١٩١٤) - (حديث: " ولدت من نكاح لا سفاح " (٢/١٨٢) .','2209','2118','0');
INSERT INTO [t22592] VALUES (2120,'(١٩١٥) - (أسلم خلق كثير فى عصر رسول الله صلى الله عليه وسلم , فأقرهم على أنكحتهم ولم يكشف عن كيفيتها ".','2214','2118','0');
INSERT INTO [t22592] VALUES (2121,'(١٩١٦) - (حديث: " أخذ الرسول صلى الله عليه وسلم الجزية من مجوس هجر ".','2216','2118','0');
INSERT INTO [t22592] VALUES (2122,'(١٩١٧) - (كتب عمر أن فرقوا بين كل ذى رحم من المجوس " (٢/١٨٢) . [٢]','2216','2118','0');
INSERT INTO [t22592] VALUES (2123,'(١٩١٨) - (عن ابن عباس: " أن رجلا جاء مسلما على عهد النبى صلى الله عليه وسلم ثم جاءت امرأته مسلمة بعده فقال: يا رسول الله إنها كانت مسلمة معى فردها عليه " رواه أبو داود (ص ١٨٣) .','2216','2118','0');
INSERT INTO [t22592] VALUES (2124,'(١٩١٩) - (حديث مالك فى الموطأ عن ابن شهاب قال: " كان بين إسلام صفوان بن أمية وامرأته بنت الوليد بن المغيرة نحو من شهر , أسلمت يوم الفتح وبقى صفوان حتى شهد حنينا والطائف , وهو كافر ثم أسلم فلم يفرق النبى صلى الله عليه وسلم بينهما واستقرت عنده امرأته بذلك النكاح ".','2217','2118','0');
INSERT INTO [t22592] VALUES (2125,'(١٩٢٠) - (قال ابن شبرمة: " كان الناس على عهد رسول الله صلى الله عليه وسلم يسلم الرجل قبل المرأة والمرأة قبل الرجل فأيهما أسلم قبل انقضاء عدة المرأة','2218','2118','0');
INSERT INTO [t22592] VALUES (2126,'(١٩٢١) - (حديث: " أن النبى صلى الله عليه وسلم رد زينب على أبى العاص بالنكاح الأول " رواه أبو داود (ص ١٨٤) .','2219','2118','0');
INSERT INTO [t22592] VALUES (2127,'(١٩٢٢) - (حديث عمرو بن شعيب أنه: " ردها بنكاح جديد " (ص ٢/١٨٤) .','2221','2118','0');
INSERT INTO [t22592] VALUES (2128,'كتاب الصداق','2223','1976','0');
INSERT INTO [t22592] VALUES (2129,'(١٩٢٣) - (قوله صلى الله عليه وسلم لعبد الرحمن: " ما أصدقتها؟ قال: وزن نواة من ذهب " (٢/١٨٧) .','2223','2128','0');
INSERT INTO [t22592] VALUES (2130,'(١٩٢٤) - (روى: " أنه صلى الله عليه وسلم زوج رجلا امرأة ولم يسم لها مهرا ".','2224','2128','0');
INSERT INTO [t22592] VALUES (2131,'(١٩٢٥) - (حديث: " التمس ولو خاتما من حديد ".','2225','2128','0');
INSERT INTO [t22592] VALUES (2132,'(١٩٢٦) - (عن عامر بن ربيعة أن امرأة من فزارة تزوجت على نعلين , فقال رسول الله صلى الله عليه وسلم: " أرضيت من مالك ونفسك بنعلين؟ قالت: نعم فأجازه " رواه أحمد والترمذى وصححه (ص ١٨٧) .','2226','2128','0');
INSERT INTO [t22592] VALUES (2133,'(١٩٢٧) - (قال عمر: " لا تغالوا فى صدقات النساء " رواه أبو داود والنسائى (ص ١٨٨) .','2227','2128','0');
INSERT INTO [t22592] VALUES (2134,'(١٩٢٨) - (عن عائشة مرفوعا: " أعظم النساء بركة أيسرهن مؤنة " رواه أبو حفص , ورواه أحمد بنحوه.','2228','2128','0');
INSERT INTO [t22592] VALUES (2135,'(١٩٢٩) - (روى أن النبى صلى الله عليه وسلم زوج رجلا على سورة من القرآن ثم قال: " لا تكون لأحد بعدك مهرا " رواه النجاد وسعيد فى سننه (ص ١٨٨) .','2230','2128','0');
INSERT INTO [t22592] VALUES (2136,'(١٩٣٠) - (حديث الموهوبة وقوله صلى الله عليه وسلم فيه: " زوجتكها بما معك من القرآن " متفق عليه (٢/١٨٨) .','2231','2128','0');
INSERT INTO [t22592] VALUES (2137,'(١٩٣١) - (حديث ابن عمرو مرفوعا: " لا يحل للرجل أن ينكح امرأة بطلاق أخرى " رواه أحمد.','2231','2128','0');
INSERT INTO [t22592] VALUES (2138,'(١٩٣٢) - (قول عمر: " لا تغالوا فى صداق النساء ".','2231','2128','0');
INSERT INTO [t22592] VALUES (2139,'(١٩٣٣) - (حديث جابر مرفوعا: " أيما عبد تزوج بغير إذن سيده فهو عاهر " رواه الترمذى وحسنه.','2231','2128','0');
INSERT INTO [t22592] VALUES (2140,'فصل','2234','1976','0');
INSERT INTO [t22592] VALUES (2141,'(١٩٣٤) - (حديث: " إن أعطيتها إزارك جلست ولا إزار لك ".','2234','2140','0');
INSERT INTO [t22592] VALUES (2142,'(١٩٣٥) - (حديث عمرو به شعيب عن أبيه عن جده مرفوعا: " ولى العقد الزوج " رواه الدارقطنى.','2234','2140','0');
INSERT INTO [t22592] VALUES (2143,'فصل فيما يسقط الصداق وينصفه','2236','1976','0');
INSERT INTO [t22592] VALUES (2144,'(١٩٣٦) - (عن محمد بن عبد الرحمن بن ثوبان مرفوعا: " من كشف خمار امرأة ونظر إليها وجب الصداق دخل بها أو لم يدخل " رواه الدارقطنى (٢/١٩٦) .','2236','2143','0');
INSERT INTO [t22592] VALUES (2145,'(١٩٣٧) - (روى الإمام أحمد والأثرم عن زرارة بن أوفى قال: " قضى الخلفاء الراشدون المهديون أن من أغلق بابا أو أرخى سترا فقد وجب المهر ووجبت العدة " رواه أيضا عن الأحنف وابن عمر وعلى.','2236','2143','0');
INSERT INTO [t22592] VALUES (2146,'فصل','2237','1976','0');
INSERT INTO [t22592] VALUES (2147,'(١٩٣٨) - (حديث: " البينة على المدعى واليمين على من أنكر " (٢/١٩٨) .','2237','2146','0');
INSERT INTO [t22592] VALUES (2148,'(١٩٣٩) - (عن ابن مسعود أنه سئل عن امرأة تزوجها رجل ولم يفرض لها صداقا ولم يدخل بها حتى مات فقال ابن مسعود: " لها صداق','2237','2146','0');
INSERT INTO [t22592] VALUES (2149,'(١٩٤٠) - (عن عقبة بن عامر أن النبى صلى الله عليه وسلم قال لرجل: " أترضى أن أزوجك فلانة؟ قال: نعم , وقال للمرأة: أترضين أن أزوجك فلانا فقالت: نعم. فدخل بها الرجل ولم يفرض لها صداقا ولم يعطها شيئا فلما حضرته الوفاة قال: إن رسول الله زوجنى فلانة ولم أفرض لها صداقا ولم أعطها شيئا , فأشهدكم أنى قد أعطيتها سهمى بخيبر فأخذت سهما فباعته بمئة ألف " رواه أبو داود (٢/١٩٩) .','2240','2146','0');
INSERT INTO [t22592] VALUES (2150,'(١٩٤١) - (روى عن على: " لكل مطلقة متاع " (ص ٢٠١) .','2241','2146','0');
INSERT INTO [t22592] VALUES (2151,'(١٩٤٢) - (قال ابن عباس: " أعلى المتعة خادم ثم دون ذلك النفقة ثم دون ذلك الكسوة ".','2241','2146','0');
INSERT INTO [t22592] VALUES (2152,'فصل','2242','1976','0');
INSERT INTO [t22592] VALUES (2153,'(١٩٤٣) - (وفى بعض ألفاظ حديث عائشة: " ولها الذى أعطاها بما أصاب منها " رواه البرقانى والخلال بإسنادهما.','2242','2152','0');
INSERT INTO [t22592] VALUES (2154,'(١٩٤٤) - (حديث: " فلها المهر بما استحل من فرجها " (٢/٢٠٢) .','2242','2152','0');
INSERT INTO [t22592] VALUES (2155,'باب الوليمة وآداب الأكل','2243','1976','0');
INSERT INTO [t22592] VALUES (2156,'(١٩٤٥) - (حديث: " إنه صلى الله عليه وسلم فعل الوليمة " رواه أنس (٢/٢٠٤) .','2243','2155','0');
INSERT INTO [t22592] VALUES (2157,'(١٩٤٦) - (وأمر بها عبد الرحمن بن عوف حين قال: تزوجت , فقال له: " أولم ولم بشاة " متفق عليه.','2243','2155','0');
INSERT INTO [t22592] VALUES (2158,'(١٩٤٧) - (حديث: " شر الطعام طعام الوليمة يدعى إليها الأغنياء ويترك الفقراء , ومن لم يجب فقد عصى الله ورسوله ".','2243','2155','0');
INSERT INTO [t22592] VALUES (2159,'(١٩٤٨) - (حديث عن ابن عمر مرفوعا: " أجيبوا هذه الدعوة إذا دعيتم لها " " وكان ابن عمر يأتى الدعوة فى العرس وغير العرس , ويأتيها وهو صائم " متفق عليهما (٢/٢٠٤) .','2245','2155','0');
INSERT INTO [t22592] VALUES (2160,'(١٩٤٩) - (حديث (ابن عمر) [١] مرفوعا: " من كان يؤمن بالله واليوم الآخر فلا يقعد على مائدة يدار عليها الخمر " رواه أحمد.','2246','2155','0');
INSERT INTO [t22592] VALUES (2161,'(١٩٥٠) - (حديث: " الوليمة أول يوم حق والثانى معروف والثالث رياء وسمعة " رواه أحمد وأبو داود وابن ماجه.','2248','2155','0');
INSERT INTO [t22592] VALUES (2162,'(١٩٥١) - (قوله صلى الله عليه وسلم: " إذا اجتمع الداعيان فأجب أقربهما بابا فإن أقربهما بابا أقربهما جوارا , فإن سبق أحدهما فأجب الذى سبق ".','2251','2155','0');
INSERT INTO [t22592] VALUES (2163,'(١٩٥٢) - (حديث: " أنه صلى الله عليه وسلم كان فى دعوة وكان معه جماعة فاعتزل رجل من القوم ناحية فقال صلى الله عليه وسلم: دعاكم أخوكم وتكلف لكم. كل يوما , ثم صم يوما مكانه إن شئت " (٢/٢٠٦) .','2251','2155','0');
INSERT INTO [t22592] VALUES (2164,'(١٩٥٣) - (حديث أبى هريرة مرفوعا: " إذا دعى أحدكم فليجب , وإن كان صائما فليدع , وإن كان مفطرا فليطعم " رواه أبو داود.','2254','2155','0');
INSERT INTO [t22592] VALUES (2165,'(١٩٥٤) - (حديث ابن عمر مرفوعا: " من دخل على غير دعوة دخل سارقا وخرج مغيرا " رواه أبو داود (٢/٢٠٦) .','2255','2155','0');
INSERT INTO [t22592] VALUES (2166,'(١٩٥٥) - (حديث أبى هريرة مرفوعا: " إذا دعى أحدكم إلى طعام فجاء مع السول فذلك إذن لك " رواه أحمد وأبو داود.','2256','2155','0');
INSERT INTO [t22592] VALUES (2167,'(١٩٥٦) - (قال ابن مسعود: " إذا دعيت فقد أذن لك " رواه أحمد.','2257','2155','0');
INSERT INTO [t22592] VALUES (2168,'(١٩٥٧) - (روى أحمد فى المسند: أن سلمان دخل عليه رجل فدعا له بما كان عنده فقال: لولا أن رسول الله صلى الله عليه وسلم نهانا أو قال: لولا أنا نهينا أن يتكلف أحد لصاحبه لتكلفنا لك ".','2257','2155','0');
INSERT INTO [t22592] VALUES (2169,'(١٩٥٨) - (حديث: " أن رسول الله صلى الله عليه وسلم نحر خمس بدنات وقال: من شاء اقتطع " رواه أحمد وأبو داود.','2259','2155','0');
INSERT INTO [t22592] VALUES (2170,'(١٩٥٩) - (حديث: " نهى رسول الله صلى الله عليه وسلم عن النهبى والمثلة " رواه أحمد والبخارى (٢/٢٠٧) .','2259','2155','0');
INSERT INTO [t22592] VALUES (2171,'(١٩٦٠) - (حديث أبى هريرة: " قسم النبى صلى الله عليه وسلم يوما بين أصحابه','2259','2155','0');
INSERT INTO [t22592] VALUES (2172,'(١٩٦١) - (حديث عائشة: " دخل عليها رسول الله صلى الله عليه وسلم فرأى كسرة ملقاة فأخذها فمسحها ثم أكلها , وقال: يا عائشة أكرمى كريمك فإنها ما نفرت عن قوم فعادت إليهم " رواه ابن ماجه ورواه ابن أبى الدنيا فى كتاب الشكر له بنحوه ولفظه: " أحسنى جوار نعم الله عليك ".','2260','2155','0');
INSERT INTO [t22592] VALUES (2173,'(١٩٦٢) - (حديث: " أنه كان صلى الله عليه وسلم يحتز من كتف شاة " رواه البخارى (٢/٢٠٨)','2263','2155','0');
INSERT INTO [t22592] VALUES (2174,'(١٩٦٣) - (حديث أنس مرفوعا: " من أحب أن يكثر خير بيته فليتوضأ إذا حضر غداؤه وإذا رفع " إسناده ضعيف , رواه ابن ماجه وغيره.','2263','2155','0');
INSERT INTO [t22592] VALUES (2175,'(١٩٦٤) - (وعن سلمان مرفوعا: " بركة الطعام الوضوء قبله وبعده " (٢/٢٠٨) .','2263','2155','0');
INSERT INTO [t22592] VALUES (2176,'(١٩٦٥) - (حديث عائشة مرفوعا: " إذا أكل أحدكم فليذكر اسم الله فإن نسى أن يذكر اسم الله فى أوله فليقل: بسم الله أوله وآخره " (٢/٢٠٨) .','2264','2155','0');
INSERT INTO [t22592] VALUES (2177,'(١٩٦٦) - (حديث: " أنه صلى الله عليه وسلم جثا على الأكل , وقال: أما أنا فلا آكل متكئا " رواه مسلم (٢/٢٠٩) .','2267','2155','0');
INSERT INTO [t22592] VALUES (2178,'(١٩٦٧) - (عن أنس: " أنه صلى الله عليه وسلم أكل مقعيا تمرا " وفى لفظ: " يأكل منه أكلا ذريعا " رواه مسلم (٢/٢٠٩) .','2268','2155','0');
INSERT INTO [t22592] VALUES (2179,'(١٩٦٨) - (قوله صلى الله عليه وسلم لعمر بن أبى سلمة: " يا غلام: سم الله وكل بيمينك , وكل مما يليك " متفق عليه (٢/٢٠٩) .','2269','2155','0');
INSERT INTO [t22592] VALUES (2180,'(١٩٦٩) - (عن كعب بن مالك قال: " كان رسول الله صلى الله عليه وسلم يأكل بثلاث أصابع ولا يمسح يده حتى يلعقها " رواه الخلال.','2271','2155','0');
INSERT INTO [t22592] VALUES (2181,'(١٩٧٠) - (حديث جابر: " أمر رسول الله صلى الله عليه وسلم بلعق الأصابع والصحفة وقال: إنكم لا تدرون فى أيه البركة " رواه مسلم.','2271','2155','0');
INSERT INTO [t22592] VALUES (2182,'(١٩٧١) - (حديث جابر مرفوعا: " إذا وقعت لقمة أحدكم فليأخذها فليمط ما كان بها من أذى ثم ليأكلها ولا يدعها للشيطان " رواه مسلم.','2273','2155','0');
INSERT INTO [t22592] VALUES (2183,'(١٩٧٢) - (قول عائشة: " كنت أتعرق العرق فأناوله النبى صلى الله عليه وسلم فيضع فاه على موضع فى " (٢/٢١٠) .','2273','2155','0');
INSERT INTO [t22592] VALUES (2184,'(١٩٧٣) - (حديث: " أكل معه صلى الله عليه وسلم عمر بن أبى سلمة وهو صغير ".','2273','2155','0');
INSERT INTO [t22592] VALUES (2185,'(١٩٧٤) - (أثر ابن عمر: " ترك الخلال يوهن الأسنان " (٢/٢١٠) .','2273','2155','0');
INSERT INTO [t22592] VALUES (2186,'(١٩٧٥) - (حديث: " تخللوا من الطعام فإنه ليس شىء أشد على الملك الذى على العبد أن يجد من أحدكم ريح الطعام ".','2274','2155','0');
INSERT INTO [t22592] VALUES (2187,'(١٩٧٦) - (حديث أبى هريرة مرفوعا: " من أكل فما تخلل فليلفظ وما لاك بلسانه فليبلع. من فعل فقد أحسن ومن لا فلا حرج " رواه أحمد وأبو داود وابن ماجه (٢/٢١٠) .','2276','2155','0');
INSERT INTO [t22592] VALUES (2188,'(١٩٧٧) - (حديث عن ابن عباس مرفوعا: " نهى أن يتنفس فى الإناء أو ينفخ فيه " (٢/٢١٠) .','2276','2155','0');
INSERT INTO [t22592] VALUES (2189,'(١٩٧٨) - (قال أبو هريرة: " لا يؤكل طعام حتى يذهب بخاره " رواه البيهقى بإسناد حسن (٢/٢١٠) .','2277','2155','0');
INSERT INTO [t22592] VALUES (2190,'(١٩٧٩) - (حديث: " أكله صلى الله عليه وسلم بكفه كلها " ولم يصححه الإمام أحمد (٢/٢١١) . [١]','2278','2155','0');
INSERT INTO [t22592] VALUES (2191,'(١٩٨٠) - (قوله صلى الله عليه وسلم: " ... وكل مما يليك " (٢/٢١١) .','2278','2155','0');
INSERT INTO [t22592] VALUES (2192,'(١٩٨١) - (وفى لفظ آخر: " كلوا من جوانبها , ودعوا ذروتها , يبارك فيها " رواهما ابن ماجه (٢/٢١١) .','2279','2155','0');
INSERT INTO [t22592] VALUES (2193,'(١٩٨٢) - (عن ابن عمر: " نهى رسول الله صلى الله عليه وسلم عن مطعمين: عن الجلوس على مائدة يشرب عليها الخمر , وأن يأكل وهو منبطح على بطنه " رواه أبو داود.','2280','2155','0');
INSERT INTO [t22592] VALUES (2194,'(١٩٨٣) - (حديث: " ما ملأ آدمى وعاء شرا من بطن ... " الحديث رواه الترمذى والنسائى وابن ماجه (٢/٢١١) .','2281','2155','0');
INSERT INTO [t22592] VALUES (2195,'(١٩٨٤) - (عن سمرة بن جندب أنه قيل له: " إن ابنك بات البارحة بشما , فقال: أما لو مات لم أصل عليه " (٢/٢١١) .','2283','2155','0');
INSERT INTO [t22592] VALUES (2196,'(١٩٨٥) - (قوله صلى الله عليه وسلم لأبى هريرة: " اشرب ـ أى من اللبن ـ فشرب. ثم أمره ثانيا وثالثا حتى قال: والذى بعثك بالحق ما أجد له مساغا " رواه البخارى (٢/٢١٢) .','2283','2155','0');
INSERT INTO [t22592] VALUES (2197,'(١٩٨٦) - (حديث: " لا ضرر ولا ضرار ".','2284','2155','0');
INSERT INTO [t22592] VALUES (2198,'(١٩٨٧) - (حديث أنس فى الدباء وفيه: " فجعلت أجمع الدباء بين يديه " رواه البخارى (٢/٢١٢) .','2284','2155','0');
INSERT INTO [t22592] VALUES (2199,'فصل','2287','1976','0');
INSERT INTO [t22592] VALUES (2200,'(١٩٨٨) - (حديث: " إن الله ليرضى عن العبد أن يأكل الأكلة فيحمده عليها " رواه مسلم (٢/٢١٣) .','2287','2199','0');
INSERT INTO [t22592] VALUES (2201,'(١٩٨٩) - (حديث معاذ بن أنس الجهنى مرفوعا: " من أكل طعاما فقال: الحمد لله الذى أطعمنى هذا ورزقنيه من غير حول منى ولا قوة غفر له ما تقدم من ذنبه " رواه ابن ماجه (٢/٢١٣) .','2287','2199','0');
INSERT INTO [t22592] VALUES (2202,'(١٩٩٠) - (قول جابر: " صنع أبو الهيثم بن التيهان للنبى صلى الله عليه وسلم طعاما فدعاه وأصحابه فلما فرغوا قال: أثيبوا أخاكم.','2288','2199','0');
INSERT INTO [t22592] VALUES (2203,'(١٩٩١) - (حديث: "من صنع إليكم معروفا فكافئوه") [١]','2289','2199','0');
INSERT INTO [t22592] VALUES (2204,'(١٩٩٢) - (حديث: " قال أبو أيوب: كان رسول الله صلى الله عليه وسلم إذا أتى بطعام أكله وبعث بفضله إلى فيسأل أبو أيوب عن موضع أصابعه فيتبع موضع أصابعه " (٢/٢١٣)','2289','2199','0');
INSERT INTO [t22592] VALUES (2205,'(١٩٩٣) - (حديث عائشة مرفوعا: " أعلنوا هذا النكاح واضربوا عليه بالغربال " رواه ابن ماجه.','2290','2199','0');
INSERT INTO [t22592] VALUES (2206,'(١٩٩٤) - (حديث: " فصل ما بين الحلال والحرام الدف والصوت فى النكاح " رواه الخمسة إلا أبا داود (ص ٢١٤) .','2290','2199','0');
INSERT INTO [t22592] VALUES (2207,'(١٩٩٥) - (حديث: " أنه صلى الله عليه وسلم قال للأنصار (١) :','2291','2199','0');
INSERT INTO [t22592] VALUES (2208,'(١٩٩٦) - (حديث: " كان صلى الله عليه وسلم يكره نكاح السر حتى يضرب بدف ويقال: أتيناكم أتيناكم فحيونا نحييكم " رواه عبد الله بن أحمد فى المسند (٢/٢١٤) .','2292','2199','0');
INSERT INTO [t22592] VALUES (2209,'باب عشرة النساء','2293','1976','0');
INSERT INTO [t22592] VALUES (2210,'(١٩٩٧) - (حديث: " استوصوا بالنساء خيرا " رواه مسلم.','2293','2209','0');
INSERT INTO [t22592] VALUES (2211,'(١٩٩٨) - (حديث: " لو كنت آمرا أحدا أن يسجد لأحد لأمرت المرأة أن تسجد لزوجها " رواه الترمذى.','2294','2209','0');
INSERT INTO [t22592] VALUES (2212,'(١٩٩٩) - (حديث جابر بن عتيك مرفوعا: " إن من الغيرة ما يحب الله ومن الغيرة ما يبغض الله , ومن الخيلاء ما يحب الله ومنها ما يبغض','2298','2209','0');
INSERT INTO [t22592] VALUES (2213,'(٢٠٠٠) - (حديث: " أن النبى صلى الله عليه وسلم بنى بعائشة وهى بنت تسع سنين ".','2300','2209','0');
INSERT INTO [t22592] VALUES (2214,'فصل','2301','1976','0');
INSERT INTO [t22592] VALUES (2215,'(٢٠٠١) - (قال جابر: " من بين يديها ومن خلفها غير أن لا يأتيها إلا فى المأتى ".','2301','2214','0');
INSERT INTO [t22592] VALUES (2216,'(٢٠٠٢) - (حديث: " إذا باتت المرأة هاجرة فراش زوجها لعنتها الملائكة حتى تصبح " متفق عليه (٢/٢١٦) .','2302','2214','0');
INSERT INTO [t22592] VALUES (2217,'(٢٠٠٣) - (حديث: " لا ضرر ولا ضرار ".','2303','2214','0');
INSERT INTO [t22592] VALUES (2218,'(٢٠٠٤) - (حديث أبى هريرة مرفوعا: " لا يحل للمراة أن تصوم وزوجها شاهد إلا بإذنه " متفق عليه (٢/٢١٧) .','2303','2214','0');
INSERT INTO [t22592] VALUES (2219,'(٢٠٠٥) - (حديث: " إن الله لا يستحيى من الحق لا تأتوا النساء فى أعجازهن " رواه ابن ماجه.','2305','2214','0');
INSERT INTO [t22592] VALUES (2220,'(٢٠٠٦) - (حديث أبى هريرة مرفوعا: " من أتى حائضا أو امرأة فى دبرها فقد كفر بما أنزل على محمد صلى الله عليه وسلم " رواه الأثرم.','2308','2214','0');
INSERT INTO [t22592] VALUES (2221,'(٢٠٠٧) - (عن عمر (١) : " نهى رسول الله صلى الله عليه وسلم أن يعزل عن الحرة إلا بإذنها " رواه أحمد وابن ماجه (٢/٢١٧) .','2310','2214','0');
INSERT INTO [t22592] VALUES (2222,'(٢٠٠٨) - (حديث: " لا تكثروا الكلام عند مجامعة النساء فإنه منه يكون الخرس والفأفأة " رواه أبو حفص.','2310','2214','0');
INSERT INTO [t22592] VALUES (2223,'(٢٠٠٩) - (حديث: " إذا أتى أحدكم أهله فليستتر ولا يتجرد تجرد العيرين " رواه ابن ماجه.','2311','2214','0');
INSERT INTO [t22592] VALUES (2224,'(٢٠١٠) - (حديث أنس مرفوعا وفيه: " ثم إذا قضى حاجته فلا يعجلها حتى تقضى حاجتها " رواه أحمد وأبو حفص.','2311','2214','0');
INSERT INTO [t22592] VALUES (2225,'(٢٠١١) - (حديث: " نهيه صلى الله عليه وسلم عن أن يحدثا بما جرى بينهما " رواه أبو داود (٢/٢١٨) .','2313','2214','0');
INSERT INTO [t22592] VALUES (2226,'(٢٠١٢) - (حديث عن ابن عباس مرفوعا: " لو أن أحدكم حين يأتى أهله قال: بسم الله اللهم جنبنا الشيطان وجنب الشيطان ما رزقتنا فولد بينهما ولد لم يضره الشيطان أبدا " متفق عليه (٢/٢١٨) .','2315','2214','0');
INSERT INTO [t22592] VALUES (2227,'فصل','2316','1976','0');
INSERT INTO [t22592] VALUES (2228,'(٢٠١٣) - (حديث عائشة مرفوعا: " ولو أن رجلا أمر امرأته أن تنقل من جبل أحمر إلى جبل أسود ومن جبل أسود إلى جبل أحمر لكان نولها أن تفعل " رواه أحمد وابن ماجه (٢/٢١٩) .','2316','2227','0');
INSERT INTO [t22592] VALUES (2229,'(٢٠١٥) - (حديث أنس: " أن رجلا سافر ومنع زوجته من الخروج فمرض أبوها فاستأذنت رسول الله صلى الله عليه وسلم فى حضور جنازته فقال لها: اتق الله ولا تخالفى زوجك , (فأوصى) [١] الله إليه أنى قد غفرت لها بطاعتها زوجها " رواه ابن بطة فى " أحكام النساء " (٢/٢١٩) . [٢]','2316','2227','0');
INSERT INTO [t22592] VALUES (2230,'فصل','2318','1976','0');
INSERT INTO [t22592] VALUES (2231,'(٢٠١٥) - (قوله صلى الله عليه وسلم لعبد الله بن عمرو: " إن لزوجك عليك حقا " متفق عليه','2318','2230','0');
INSERT INTO [t22592] VALUES (2232,'(٢٠١٦) - (روى الشعبى: " أن كعب بن سور (١) كان جالسا عند عمر بن الخطاب فجاءت امرأة فقالت: يا أمير المؤمنين ما رأيت رجلا قط أفضل من زوجى والله إنه ليبيت ليله قائما ويظل نهاره صائما فاستغفر لها وأثنى عليها واستحيت المرأة وقامت راجعة. فقال كعب: يا أمير المؤمنين هلا أعديت المرأة على زوجها فلقد أبلغت فى الشكوى فقال لكعب: اقض بينهما فإنك فهمت من أمرها ما لم أفهم. قال: فإنى أرى كأنها امرأة عليها ثلاث نسوة هى رابعتهن فأقضى بثلاثة أيام ولياليهن يتعبد فيهن ولها يوم وليلة. فقال عمر: والله ما رأيك الأول بأعجب من الآخر , اذهب فأنت قاض على البصرة , نعم القاضى أنت " رواه سعيد.','2320','2230','0');
INSERT INTO [t22592] VALUES (2233,'(٢٠١٧) - (عن أبى هريرة مرفوعا: " من كان له امرأتان , فمال إلى إحداهما جاء يوم القيامة , وشقه مائل " رواه أبو داود (٢/٢٢٢) .','2320','2230','0');
INSERT INTO [t22592] VALUES (2234,'(٢٠١٨) - (وعن عائشة: " كان رسول الله صلى الله عليه وسلم يقسم بيننا فيعدل ثم يقول: اللهم هذا قسمى فيما أملك فلا تلمنى فيما لا أملك " رواه أبو داود (٢/٢٢٢) .','2321','2230','0');
INSERT INTO [t22592] VALUES (2235,'(٢٠١٩) - (قوله صلى الله عليه وسلم لأم سلمة: " فإن سبعت لك , سبعت لنسائى " رواه أحمد ومسلم (٢/٢٢٢) .','2323','2230','0');
INSERT INTO [t22592] VALUES (2236,'(٢٠٢٠) - (حديث: " أن سودة وهبت يومها لعائشة " متفق عليه (٢/٢٢٢) .','2324','2230','0');
INSERT INTO [t22592] VALUES (2237,'(٢٠٢١) - (حديث عائشة: " قبض رسول الله صلى الله عليه وسلم فى بيتى وفى يومى وإنما قبض نهارا " (٢/٢٢٢) .','2325','2230','0');
INSERT INTO [t22592] VALUES (2238,'(٢٠٢٢) - (عن على: " لزوجة أمة مع حرة ليلة من ثلاث ليال " رواه الدارقطنى.','2326','2230','0');
INSERT INTO [t22592] VALUES (2239,'(٢٠٢٤) - (وقال صلى الله عليه وسلم: " اللهم هذا قسمى فيما أملك فلا تلمنى فيما لا أملك " (٢/٢٢٣) .','2327','2230','0');
INSERT INTO [t22592] VALUES (2240,'فصل','2328','1976','0');
INSERT INTO [t22592] VALUES (2241,'(٢٠٢٥) - (حديث أبى قلابة عن أنس قال: " من السنة إذا تزوج [الرجل] (١) البكر على الثيب أقام عندها سبعا وقسم , وإذا تزوج الثيب أقام عندها ثلاثا ثم قسم. قال أبو قلابة: لو شئت لقلت: إن أنسا رفعه إلى النبى صلى الله عليه وسلم " أخرجاه.','2328','2240','0');
INSERT INTO [t22592] VALUES (2242,'(٢٠٢٦) - (حديث: وعن معاذ مرفوعا: " أنفق على عيالك من طولك ولا ترفع عنهم عصاك أدبا وأخفهم فى الله " رواه أحمد (٢/٢٢٤) .','2329','2240','0');
INSERT INTO [t22592] VALUES (2243,'(٢٠٢٧) - (قال ابن عباس: " لا تضاجعها فى فراشك ".','2331','2240','0');
INSERT INTO [t22592] VALUES (2244,'(٢٠٢٨) - (حديث: " هجر النبى صلى الله عليه وسلم نساءه فلم يدخل عليهن شهرا " متفق عليه','2331','2240','0');
INSERT INTO [t22592] VALUES (2245,'(٢٠٢٩) - (حديث أبى هريرة: " لا يحل لمسلم أن يهجر أخاه فوق ثلاثة أيام " متفق عليه (٢/٢٢٥) .','2332','2240','0');
INSERT INTO [t22592] VALUES (2246,'(٢٠٣٠) - (حديث عمرو بن الأحوص مرفوعا وفيه: " فإن فعلن فاهجروهن فى المضاجع واضربوهن ضربا غير مبرح " رواه ابن ماجه والترمذى وصححه.','2336','2240','0');
INSERT INTO [t22592] VALUES (2247,'(٢٠٣١) - (حديث: " لا يجلد أحدكم امرأته جلد العبد ثم يضاجعها فى آخر اليوم ".','2337','2240','0');
INSERT INTO [t22592] VALUES (2248,'(٢٠٣٢) - (حديث: " لا يجلد أحدكم (قومه) [١] عشرة أسواط إلا فى حد من حدود الله " متفق عليه.','2337','2240','0');
INSERT INTO [t22592] VALUES (2249,'(٢٠٣٣) - (قوله صلى الله عليه وسلم: " ولا تضرب الوجه ولا تقبح ولا تهجر إلا فى البيت " رواه أحمد وأبو داود (٢/٢٢٥) .','2337','2240','0');
INSERT INTO [t22592] VALUES (2250,'(٢٠٣٤) - (حديث: " لا يسأل الرجل فيم ضرب امرأته " رواه أبو داود (٢/٢٢٥) .','2338','2240','0');
INSERT INTO [t22592] VALUES (2251,'كتاب الطلاق','2340','0','0');
INSERT INTO [t22592] VALUES (2252,'كتاب الخلع','2340','2251','0');
INSERT INTO [t22592] VALUES (2253,'(٢٠٣٥) - (حديث: " أيما امرأة سألت زوجها الطلاق من غير ما بأس فحرام عليها رائحة الجنة " رواه الخمسة إلا النسائى (٢/٢٢٦) .','2340','2252','0');
INSERT INTO [t22592] VALUES (2254,'(٢٠٣٦) - (قوله صلى الله عليه وسلم لثابت بن قيس: " اقبل الحديقة وطلقها تطليقة " رواه البخارى.','2341','2252','0');
INSERT INTO [t22592] VALUES (2255,'(٢٠٣٧) - (قوله صلى الله عليه وسلم فى حديث جميلة: " ولا تزدد " رواه ابن ماجه.','2343','2252','0');
INSERT INTO [t22592] VALUES (2256,'(٢٠٣٨) - (حديث عن على: " أن النبى صلى الله عليه وسلم كره أن يأخذ من المختلعة أكثر مما أعطاها " رواه أبو حفص (٢/٢٢٧) .','2345','2252','0');
INSERT INTO [t22592] VALUES (2257,'(٢٠٣٩) - (حديث: " اقبل الحديقة وطلقها تطليقة " رواه البخارى وفى رواية: " فأمره ففارقها ".','2345','2252','0');
INSERT INTO [t22592] VALUES (2258,'كتاب الطلاق','2346','2251','0');
INSERT INTO [t22592] VALUES (2259,'(٢٠٤٠) - (حديث: " أبغض الحلال إلى الله الطلاق ".','2346','2258','0');
INSERT INTO [t22592] VALUES (2260,'(٢٠٤١) - (حديث: " إنما الطلاق لمن أخذ بالساق ".','2348','2258','0');
INSERT INTO [t22592] VALUES (2261,'(٢٠٤٢) - (حديث: " كل الطلاق (١) جائز إلا طلاق المعتوه والمغلوب على عقله " رواه الترمذى (٢/٢٣١) .','2350','2258','0');
INSERT INTO [t22592] VALUES (2262,'(٢٠٤٣) - (حديث: " رفع القلم عن ثلاثة: عن الصبى حتى يبلغ , وعن النائم حتى يستيقظ , وعن المجنون حتى يفيق ".','2351','2258','0');
INSERT INTO [t22592] VALUES (2263,'(٢٠٤٤) - (روى [ابن] (١) وبرة الكلبى قال: " أرسلنى خالد بن الوليد إلى عمر رضى الله عنه فأتيته فى المسجد ومعه عثمان وعلى وطلحة والزبير وعبد الرحمن فقلت: إن خالدا يقول: إن الناس انهمكوا فى الخمر وتحاقروا عقوبته فقال عمر: هؤلاء عندك فسلهم.','2351','2258','0');
INSERT INTO [t22592] VALUES (2264,'(٢٠٤٥) - (قول عثمان: " ليس لمجنون ولا لسكران طلاق " (٢/٢٣٢) .','2351','2258','0');
INSERT INTO [t22592] VALUES (2265,'(٢٠٤٦) - (وقال ابن عباس: " طلاق السكران والمستكره ليس بجائز ".','2352','2258','0');
INSERT INTO [t22592] VALUES (2266,'(٢٠٤٧) - (حديث عائشة مرفوعا: " لا طلاق ولا عتاق (١) فى إغلاق " رواه أحمد وأبو داود وابن ماجه.','2353','2258','0');
INSERT INTO [t22592] VALUES (2267,'(٢٠٤٨) - (روى سعيد وأبو عبيد: " أن رجلا على عهد عمر تدلى فى','2354','2258','0');
INSERT INTO [t22592] VALUES (2268,'فصل','2356','2251','0');
INSERT INTO [t22592] VALUES (2269,'(٢٠٤٩) - (عن زرارة بن ربيعة عن أبيه عن عثمان: " فى أمرك بيدك , القضاء ما قضت " رواه البخارى فى تاريخه (٢/٢٣٤) .','2356','2268','0');
INSERT INTO [t22592] VALUES (2270,'(٢٠٥٠) - (عن علي في رجل جعل أمر امرأته بيدها قال: "هو لها حتى ينكل") ٢/٢٣٤','2357','2268','0');
INSERT INTO [t22592] VALUES (2271,'باب سنة الطلاق وبدعته','2358','2251','0');
INSERT INTO [t22592] VALUES (2272,'(٢٠٥١) - (قال ابن مسعود وابن عباس: " طاهرا من غير جماع " (٢/٢٣٥) .','2358','2271','0');
INSERT INTO [t22592] VALUES (2273,'(٢٠٥٢) - (حديث فاطمة: " أن زوجها أرسل إليها بتطليقة بقيت لها فى طلاقها ".','2359','2271','0');
INSERT INTO [t22592] VALUES (2274,'(٢٠٥٣) - (حديث امرأة رفاعة جاء فيه: " أنه طلقها آخر ثلاث تطليقات " متفق عليه (٢/٢٣٥) .','2359','2271','0');
INSERT INTO [t22592] VALUES (2275,'(٢٠٥٤) - (فى حديث ابن عمر قال: " قلت: يا رسول الله: أرأيت لو أنى طلقتها ثلاثا كان يحل لى أن أراجعها؟ قال: إذا عصيت ربك وبانت منك امرأتك " رواه الدارقطنى (٢/٢٣٥) .','2359','2271','0');
INSERT INTO [t22592] VALUES (2276,'(٢٠٥٥) - (عن مجاهد قال: " جلست عند ابن عباس فجاءه رجل فقال: إنه طلق امرأته ثلاثا فسكت حتى ظننت أنه رادها إليه ثم قال: ينطلق أحدكم فيركب الأحموقة ثم يقول: يا ابن عباس. وإن الله قال: (ومن يتق الله يجعل له مخرجا) وإنك لم تتق الله , فلم أجد لك مخرجا.','2360','2271','0');
INSERT INTO [t22592] VALUES (2277,'(٢٠٥٦) - (عن مجاهد: " أن ابن عباس سئل عن رجل طلق امرأته مائة , فقال: عصيت ربك وفارقت امرأتك " رواه الدارقطنى (٢/٢٣٦) .','2363','2271','0');
INSERT INTO [t22592] VALUES (2278,'(٢٠٥٧) - (عن سعيد بن جبير عن ابن عباس: " أن رجلا طلق امرأته ألفا قال: يكفيك من ذلك ثلاث " (٢/٢٣٦) .','2363','2271','0');
INSERT INTO [t22592] VALUES (2279,'(٢٠٥٨) - (وعن سعيد أيضا: " أن ابن عباس سئل عن رجل طلق امرأته عدد النجوم؟ قال: أخطأ السنة , وحرمت عليه امرأته " رواهن الدارقطنى (٢/٢٣٦) .','2364','2271','0');
INSERT INTO [t22592] VALUES (2280,'(٢٠٥٩) - (عن ابن عمر أنه طلق امرأته وهى حائض فسأل عمر النبى صلى الله عليه وسلم عن ذلك فقال له: " مره فليراجعها ثم ليتركها حتى تطهر ثم تحيض ثم تطهر ثم إن شاء أمسك بعد وإن شاء طلق قبل أن يمس فتلك العدة التى أمر الله أن يطلق لها النساء " متفق عليه.','2364','2271','0');
INSERT INTO [t22592] VALUES (2281,'(٢٠٦٠) - (حديث سالم عن أبيه وفيه: " فليطلقها طاهرا أو حاملا " رواه مسلم.','2378','2271','0');
INSERT INTO [t22592] VALUES (2282,'باب صريح الطلاق وكنايته','2379','2251','0');
INSERT INTO [t22592] VALUES (2283,'(٢٠٦١) - (حديث أبى هريرة مرفوعا: " ثلاث جدهن جد وهزلهن جد النكاح والطلاق والرجعة " رواه الخمسة إلى النسائى (٢/٢٣٨) .','2379','2282','0');
INSERT INTO [t22592] VALUES (2284,'(٢٠٦٢) - (حديث: " إن الله تجاوز لأمتى عما حدثت به أنفسها ما لم تتكلم به أو تعمل " صححه الترمذى (٢/٢٣٩) .','2379','2282','0');
INSERT INTO [t22592] VALUES (2285,'فصل','2379','2251','0');
INSERT INTO [t22592] VALUES (2286,'(٢٠٦٣) - (حديث ركانة: " أنه طلق البتة فاستحلفه النبى صلى الله عليه وسلم: ما أردت إلا واحدة , فحلف , فردها عليه " رواه أبو داود.','2379','2285','0');
INSERT INTO [t22592] VALUES (2287,'(٢٠٦٤) - (قال النبى صلى الله عليه وسلم لابنة الجون: " الحقى بأهلك " متفق عليه (٢/٢٤٠) .','2385','2285','0');
INSERT INTO [t22592] VALUES (2288,'(٢٠٦٥) - (قال لسودة: " اعتدى فجعلها طلقة " متفق عليه (٢/٢٤٠) .','2386','2285','0');
INSERT INTO [t22592] VALUES (2289,'باب ما يختلف به عدد الطلاق','2388','2251','0');
INSERT INTO [t22592] VALUES (2290,'(٢٠٦٦) - (عن عائشة مرفوعا: " طلاق العبد اثنتان فلا تحل له حتى تنكح زوجا غيره " رواه الدارقطنى (٢/٢٤٢) .','2388','2289','0');
INSERT INTO [t22592] VALUES (2291,'(٢٠٦٧) - (عن عمر قال: " ينكح العبد امرأتين ويطلق تطليقتين وتعتد الأمة حيضتين " رواه الدارقطنى (٢/٢٤٢) .','2390','2289','0');
INSERT INTO [t22592] VALUES (2292,'باب تعليق الطلاق','2391','2251','0');
INSERT INTO [t22592] VALUES (2293,'(٢٠٦٨) - (لو قال: " إن تزوجت امرأة أو فلانة فهى طالق , لم يقع بتزويجها " روى عن ابن عباس , ورواه الترمذى عن على وجابر بن عبد الله (٢/٢٤٩) .','2391','2292','0');
INSERT INTO [t22592] VALUES (2294,'(٢٠٦٩) - (عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " لا نذر لابن آدم فيما لا يملك ولا عتق فيما لا يملك ولا طلاق فيما لا يملك " رواه أحمد وأبو داود والترمذى وحسنه (٢/٢٤٩) .','2392','2292','0');
INSERT INTO [t22592] VALUES (2295,'(٢٠٧٠) - (وعن المسور بن مخرمة مرفوعا: " لا طلاق قبل نكاح ولا عتاق قبل ملك " رواه ابن ماجه.','2392','2292','0');
INSERT INTO [t22592] VALUES (2296,'فصل فى مسائل متفرقة','2394','2251','0');
INSERT INTO [t22592] VALUES (2297,'(٢٠٧١) - (قال ابن عباس: " إذا قال الرجل لامرأته: أنت طالق إن شاء الله فهى طالق " (٢/٢٥١) .','2394','2296','0');
INSERT INTO [t22592] VALUES (2298,'(٢٠٧٢) - (حديث: " رفع القلم عن ثلاثة ... ".','2394','2296','0');
INSERT INTO [t22592] VALUES (2299,'(٢٠٧٢) - (حديث: " عفى لأمتى عن الخطأ والنسيان ".','2395','2296','0');
INSERT INTO [t22592] VALUES (2300,'فصل','2395','2251','0');
INSERT INTO [t22592] VALUES (2301,'(٢٠٧٤) - (حديث: " دع ما يريبك إلى ما لا يريبك ".','2395','2300','0');
INSERT INTO [t22592] VALUES (2302,'(٢٠٧٥) - (حديث: من اتقى الشبهات فقد استبرأ لدينه وعرضه " (٢/٢٥٣) .','2396','2300','0');
INSERT INTO [t22592] VALUES (2303,'باب الرجعة','2397','2251','0');
INSERT INTO [t22592] VALUES (2304,'(٢٠٧٦) - (حديث ابن عمر حين طلق امرأته فقال النبى صلى الله عليه وسلم: " مره فليراجعها " متفق عليه.','2397','2303','0');
INSERT INTO [t22592] VALUES (2305,'(٢٠٧٧) - (طلق النبى صلى الله عليه وسلم حفصة ثم راجعها " رواه أبو داود والنسائى وابن ماجه (٢/٢٥٤) .','2397','2303','0');
INSERT INTO [t22592] VALUES (2306,'(٢٠٧٨) - (سئل عمران بن حصين عن الرجل يطلق امرأته ثم يقع بها ولم يشهد على طلاقها ولا على رجعتها فقال: " طلقت لغير سنة وراجعت لغير سنة , أشهد على طلاقها وعلى رجعتها ولا تعد " رواه أبو داود (٢/٢٥٦) .','2399','2303','0');
INSERT INTO [t22592] VALUES (2307,'(٢٠٧٩) - (روى أبو بكر فى الشافعى بسنده إلى خلاس قال: " طلق رجل امرأته علانية وراجعها سرا وأمر الشاهدين بكتمانها ـ أى الرجعة ـ فاختصموا إلى على فجلد الشاهدين واتهمهما ولم يجعل له عليها رجعة " (٢/٢٥٦) . [١]','2400','2303','0');
INSERT INTO [t22592] VALUES (2308,'فصل','2401','2251','0');
INSERT INTO [t22592] VALUES (2309,'(٢٠٨٠) - (قال ابن عباس: " كان الرجل إذا طلق امرأته فهو أحق برجعتها وإن طلقها ثلاثا فنسخ ذلك قوله تعالى: (الطلاق مرتان) إلى قوله: (فإن طلقها فلا تحل له من بعد حتى تنكح زوجا غيره) " رواه أبو داود والنسائى.','2401','2308','0');
INSERT INTO [t22592] VALUES (2310,'(٢٠٨١) - (قوله صلى الله عليه وسلم لامرأة رفاعة: " أتريدين أن ترجعى إلى رفاعة؟ لا حتى تذوقى عسيلته ويذوق عسيلتك " متفق عليه (٢/٢٥٧) .','2403','2308','0');
INSERT INTO [t22592] VALUES (2311,'(٢٠٨٢) - (عن ابن عمر سئل النبي صلى الله عليه وسلم عن الرجل يطلق امراته ثلاثا فيتزوجها آخر فيغلق الباب ويرخي الستر ثم يطلقها قبل أن يدخل بها: هل تحل للأول، قال: حتى تذوق العسيلة" رواه أحمد والنسائي وقال: حتى يجامعها الآخر) .','2403','2308','0');
INSERT INTO [t22592] VALUES (2312,'(٢٠٨٣) - (عن عائشة مرفوعا: " العسيلة هى الجماع " رواه أحمد والنسائى.','2403','2308','0');
INSERT INTO [t22592] VALUES (2313,'كتاب الإيلاء','2405','2251','0');
INSERT INTO [t22592] VALUES (2314,'(٢٠٨٤) - (حديث: " من حلف على يمين فرأى غيرها خيرا منها فليأت الذى هو خير وليكفر عن يمينه " متفق عليه.','2405','2313','0');
INSERT INTO [t22592] VALUES (2315,'(٢٠٨٥) - (عن ابن عمر قال: " إذا مضت أربعة أشهر يوقف حتى يطلق ولا يقع عليه الطلاق حتى يطلق ـ يعنى: المؤلى ـ " رواه البخارى , قال: " ويذكر ذلك عن عثمان وعلى وأبى الدرداء وعائشة واثنى عشر رجلا من أصحاب النبى صلى الله عليه وسلم " (٢/٢٦١) .','2409','2313','0');
INSERT INTO [t22592] VALUES (2316,'(٢٠٨٦) - (عن سليمان بن يسار قال: " أدركت بضعة عشر من أصحاب النبى صلى الله عليه وسلم كلهم يوقفون المؤلى " رواه الشافعى والدارقطنى (٢/٢٦١) .','2412','2313','0');
INSERT INTO [t22592] VALUES (2317,'كتاب الظهار','2413','2251','0');
INSERT INTO [t22592] VALUES (2318,'(٢٠٨٧) - (حديث: " نزلت الآيات ( ... وإنهم ليقولون منكرا من القول وزورا ... ) فى خويلة بنت مالك بن ثعلبة حين ظاهر منها ابن عمها أوس بن الصامت فجاءت تشكوه إلى رسول الله صلى الله عليه وسلم وتجادله فيه ويقول: اتقى الله فإنه ابن عمك فما برحت حتى نزل القرآن " رواه أبو داود وصححه (٢/٢٦٢) .','2413','2317','0');
INSERT INTO [t22592] VALUES (2319,'(٢٠٨٨) - (فى المتفق عليه عن ابن عباس قال: " إذا حرم الرجل امرأته فهى يمين يكفرها " (٢/٢٦٣) .','2415','2317','0');
INSERT INTO [t22592] VALUES (2320,'(٢٠٨٩) - (روى الأثرم بإسناده عن عائشة بنت طلحة أنها قالت: " إن تزوجت مصعب بن الزبير فهو على كظهر أبى: فسألت أهل المدينة فرأوا أن عليها الكفارة ". وروى سعيد: " أنها استفتت أصحاب رسول الله','2415','2317','0');
INSERT INTO [t22592] VALUES (2321,'فصل','2416','2251','0');
INSERT INTO [t22592] VALUES (2322,'(٢٠٩٠) - (قول عمر رضى الله عنه فى رجل قال: " إن تزوجت فلانة فهى على كظهر أمى ثم تزوجها. قال: عليه كفارة الظهار " رواه أحمد (٢/٢٦٥) .','2416','2321','0');
INSERT INTO [t22592] VALUES (2323,'(٢٠٩١) - (حديث سلمة بن صخر وفيه: " ظاهرت من امرأتى حتى ينسلخ شهر رمضان وأخبر النبى صلى الله عليه وسلم أنه أصاب فيه فأمره بالكفارة " رواه أحمد وأبو داود والترمذى وحسنه.','2416','2321','0');
INSERT INTO [t22592] VALUES (2324,'(٢٠٩٢) - (قوله صلى الله عليه وسلم: " فلا تقربها حتى تفعل ما أمرك الله به " رواه أهل السنن وصححه الترمذى (٢/٢٦٦) .','2419','2321','0');
INSERT INTO [t22592] VALUES (2325,'(٢٠٩٣) - (حديث: " وإنما لكل امرىء ما نوى ".','2420','2321','0');
INSERT INTO [t22592] VALUES (2326,'(٢٠٩٤) - (حديث: " أمره صلى الله عليه وسلم سلمة بن صخر بالإطعام حين أخبره بشدة شبقه وشهوته بقوله: وهل أصبت ما أصبت إلا من الصيام ".','2420','2321','0');
INSERT INTO [t22592] VALUES (2327,'(٢٠٩٥) - (حديث: " أمر صلى الله عليه وسلم أوس بن الصامت بالإطعام حين قالت امرأته: إنه شيخ كبير ما به من صيام ".','2421','2321','0');
INSERT INTO [t22592] VALUES (2328,'(٢٠٩٦) - (روى أحمد عن أبى يزيد المدنى قال: " جاءت امرأة من بنى بياضة بنصف وسق شعير , فقال رسول الله صلى الله عليه وسلم للمظاهر: أطعم هذا فإن مدى شعير مكان مد بر " (٢/٢٦٨) .','2421','2321','0');
INSERT INTO [t22592] VALUES (2329,'(٢٠٩٧) - (حديث: " إنما الأعمال بالنيات ... ".','2421','2321','0');
INSERT INTO [t22592] VALUES (2330,'(٢٠٩٨) - (حديث: " عن ابن عباس أن هلال بن أمية قذف امرأته فقال النبى صلى الله عليه وسلم: البينة وإلا حد فى ظهرك. فقال هلال: والذى بعثك بالحق إنى لصادق ولينزلن الله فى أمرى ما يبرىء ظهرى من الحد , فنزلت (والذين يرمون أزواجهم ... ) " رواه البخارى (٢/٢٦٩) .','2422','2321','0');
INSERT INTO [t22592] VALUES (2331,'(٢٠٩٩) - (حديث ابن عباس وفيه: " أن هلالا جاء فشهد ثم قامت فشهدت "','2424','2321','0');
INSERT INTO [t22592] VALUES (2332,'(٢١٠٠) - (قال سهل: " فتلاعنا وأنا مع الناس عند النبى صلى الله عليه وسلم " رواه الجماعة إلا الترمذى.','2424','2321','0');
INSERT INTO [t22592] VALUES (2333,'(٢١٠١) - (عن ابن عباس: " أن هلال بن أمية قذف امرأته فقال رسول الله صلى الله عليه وسلم أرسلوا إليها , فجاءت فتلا عليها آية اللعان وذكرهما وأخبرهما أن عذاب الآخرة أشد من عذاب الدنيا , فقال هلال: والله لقد صدقت عليها , فقالت: كذب. فقال النبى صلى الله عليه وسلم: لاعنوا بينهما , فقيل لهلال: اشهد ... الحديث " رواه أحمد وأبو داود (٢/٢٧١) .','2426','2321','0');
INSERT INTO [t22592] VALUES (2334,'فصل','2427','2251','0');
INSERT INTO [t22592] VALUES (2335,'(٢١٠٢) - (قول ابن عباس فى حديثه: " ففرق رسول الله صلى الله عليه وسلم بينهما " (٢/٢٧٣) .','2427','2334','0');
INSERT INTO [t22592] VALUES (2336,'(٢١٠٣) - (" فى حديث عويمر أنه قذف امرأته فتلاعنا عند النبى صلى الله عليه وسلم فقال عويمر: كذبت عليها يا رسول الله أن أمسكتها.','2427','2334','0');
INSERT INTO [t22592] VALUES (2337,'(٢١٠٤) - (قول سهل: " مضت السنة فى المتلاعنين أن يفرق بينهما ثم لا يجتمعا أبدا " رواه الجوزجانى (٢/٢٧٣) .','2427','2334','0');
INSERT INTO [t22592] VALUES (2338,'(٢١٠٥) - (قال عمر رضى الله عنه: " المتلاعنان يفرق بينهما ولا يجتمعان أبدا " رواه سعيد , وعن على وابن مسعود نحوه.','2428','2334','0');
INSERT INTO [t22592] VALUES (2339,'(٢١٠٦) - (قال النبى صلى الله عليه وسلم: " انظروها فإن جاءت به كذا وكذا ... الحديث " (٢/٢٦٣) .','2428','2334','0');
INSERT INTO [t22592] VALUES (2340,'فصل فيما يلحق من النسب','2429','2251','0');
INSERT INTO [t22592] VALUES (2341,'(٢١٠٧) - (روى الوليد بن مسلم: " قلت لمالك بن أنس: حديث عائشة: لا تزيد المرأة على السنتين فى الحمل. قال مالك: سبحان الله! من يقول هذا؟ ! هذه جارتنا امرأة محمد بن عجلان تحمل أربع سنين " (٢/٢٧٤) .','2429','2340','0');
INSERT INTO [t22592] VALUES (2342,'(٢١٠٨) - (حديث: " الولد للفراش وللعاهر الحجر " متفق عليه.','2430','2340','0');
INSERT INTO [t22592] VALUES (2343,'(٢١٠٩) - (حديث: " واضربوهم عليها لعشر وفرقوا بينهم فى المضاجع " رواه أبو داود.','2430','2340','0');
INSERT INTO [t22592] VALUES (2344,'فصل','2430','2251','0');
INSERT INTO [t22592] VALUES (2345,'(٢١١٠) - (حديث: " أن سعدا نازع عبد بن زمعة فى ابن وليدة زمعة , فقال عبد بن زمعة: هو أخى وابن وليدة أبى , ولد على فراشه.','2430','2344','0');
INSERT INTO [t22592] VALUES (2346,'(٢١١١) - (قال عمر رضى الله عنه: " ما بال رجال يطئون ولائدهم ثم يعزلون؟ ! لا تأتينى وليدة يعترف سيدها أنه ألم بها إلا ألحقت به ولدها فاعزلوا بعد ذلك أو اتركوا " (١) رواه الشافعى فى " مسنده ".','2430','2344','0');
INSERT INTO [t22592] VALUES (2347,'(٢١١٢) - (حديث: " المسلمون عند شروطهم " (٢/٢٧٦) .','2431','2344','0');
INSERT INTO [t22592] VALUES (2348,'كتاب العدة','2432','2251','0');
INSERT INTO [t22592] VALUES (2349,'(٢١١٣) - (قال ابن عباس: " تعتد بأقصى الأجلين "','2432','2348','0');
INSERT INTO [t22592] VALUES (2350,'(٢١١٤) - (حديث: " لا يحل لامرأة تؤمن بالله واليوم الآخر أن تحد على ميت فوق ثلاث إلا على زوج أربعة أشهر وعشرا " متفق عليه (٢/٢٧٨) .','2433','2348','0');
INSERT INTO [t22592] VALUES (2351,'(٢١١٥) - (روى أحمد بإسناده عن زرارة بن أوفى قال: " قضى الخلفاء الراشدون أن من أغلق بابا أو أرخى حجابا فقد وجب المهر ووجبت العدة " (٢/٢٧٩) .','2435','2348','0');
INSERT INTO [t22592] VALUES (2352,'(٢١١٦) - (عن أبى بن كعب: " قلت: يا رسول الله وأولات الأحمال أجلهن أن يضعن حملهن , للمطلقة ثلاثا أو للمتوفى عنها؟ فقال: هى للمطلقة ثلاثا , وللمتوفى عنها " رواه أحمد والدارقطنى (٢/٢٨٠) .','2436','2348','0');
INSERT INTO [t22592] VALUES (2353,'(٢١١٧) - (عن الزبير بن العوام: " أنها كانت عنده أم كلثوم بنت عقبة فقالت له وهى حامل: طيب نفسى بتطليقة. فطلقها تطليقة.','2437','2348','0');
INSERT INTO [t22592] VALUES (2354,'(٢١١٨) - (حديث: " تدع الصلاة أيام أقرائها " رواه أبو داود (٢/٢٨٠) .','2439','2348','0');
INSERT INTO [t22592] VALUES (2355,'(٢١١٩) - (حديث: " إذا أتى قرؤك فلا تصلى , وإذا مر قرؤك فتطهرى ثم صلى ما بين القرء إلى القرء " رواه النسائى (٢/٢٨٠) .','2439','2348','0');
INSERT INTO [t22592] VALUES (2356,'(٢١٢٠) - (قالت عائشة رضى الله عنها: " أمرت بريرة أن تعتد بثلاث حيض " رواه ابن ماجه.','2440','2348','0');
INSERT INTO [t22592] VALUES (2357,'(٢١٢١) - (حديث ابن عمر مرفوعا: " طلاق الأمة طلقتان وقرؤها حيضتان " رواه أبو داود.','2441','2348','0');
INSERT INTO [t22592] VALUES (2358,'(٢١٢٢) - (قول عمر: " عدة أم الولد حيضتان ولو لم تحض كان عدتها شهرين " رواه الأثرم (٢/٢٨٢) .','2441','2348','0');
INSERT INTO [t22592] VALUES (2359,'(٢١٢٣) - (عن محمد بن يحيى بن حبان: " أنه كانت عند جده امرأتان: هاشمية وأنصارية فطلق الأنصارية وهى ترضع فمرت بها سنة ثم هلك ولم تحض , فقالت الأنصارية لم أحض , فاختصموا إلى عثمان فقضى لها بالميراث فلامت الهاشمية عثمان فقال: هذا عمل ابن عمك هو أشار علينا بهذا ـ يعنى: على بن أبى طالب رضى الله عنه ـ " رواه الأثرم.','2441','2348','0');
INSERT INTO [t22592] VALUES (2360,'فصل','2443','2251','0');
INSERT INTO [t22592] VALUES (2361,'(٢١٢٤) - (خبر على رضى الله عنه: " أنه قضى فى التى تتزوج فى عدتها أنه يفرق بينهما ولها الصداق بما استحل من فرجها وتكمل ما أفسدت من عدة الأول وتعتد من الآخر " رواه مالك.','2443','2360','0');
INSERT INTO [t22592] VALUES (2362,'(٢١٢٥) - (قال عمر: " أيما امرأة نكحت فى عدتها ولم يدخل بها الذى تزوجها فرق بينهما , ثم اعتدت بقية عدتها من زوجها الأول , وكان خاطبا من الخطاب وإن دخل بها فرق بينهما ثم اعتدت بقية عدتها من زوجها الأول , ثم اعتدت من الآخر ولم ينكحها أبدا " رواه الشافعى.','2444','2360','0');
INSERT INTO [t22592] VALUES (2363,'(٢١٢٦) - (روى عن على أنه قال: " إذا انقضت عدتها فهو خاطب من الخطاب ـ يعنى: الزوج الثانى ـ فقال عمر: ردوا الجهالات إلى السنة ورجع إلى قول على " قاله فى الكافى.','2444','2360','0');
INSERT INTO [t22592] VALUES (2364,'فصل','2445','2251','0');
INSERT INTO [t22592] VALUES (2365,'(٢١٢٧) - (حديث: " لا يحل لامرأة تؤمن بالله واليوم الآخر أن تحد على ميت فوق ثلاث ليال إلى على زوج أربعة أشهر وعشرا " (٢/٢٨٥) .','2445','2364','0');
INSERT INTO [t22592] VALUES (2366,'(٢١٢٨) - (حديث: " ... ولا تلبس ثوبا مصبوغا إلا ثوب عصب ... " الحديث متفق عليه.','2445','2364','0');
INSERT INTO [t22592] VALUES (2367,'(٢١٢٩) - (عن أم سلمة مرفوعا: " المتوفى عنها لا تلبس المعصفر من الثياب ولا الممشق ولا الحلى ولا تختضب ولا تكتحل " رواه النسائى.','2445','2364','0');
INSERT INTO [t22592] VALUES (2368,'(٢١٣٠) - (فى حديث أم عطية: " ولا تمس طيبا " أخرجاه.','2446','2364','0');
INSERT INTO [t22592] VALUES (2369,'(٢١٣١) - (حديث فريعة وفيه: " ... امكثى فى بيتك الذى أتاك فيه نعى زوجك حتى يبلغ الكتاب أجله. فاعتدت فيه أربعة أشهر وعشرا " رواه الخمسة وصححه الترمذى.','2446','2364','0');
INSERT INTO [t22592] VALUES (2370,'(٢١٣٢) - (عن سعيد بن المسيب قال: " توفى أزواج نساؤهم حاجات أو معتمرات فردهن عمر من ذى الحليفة حتى يعتددن فى بيوتهن " رواه سعيد.','2447','2364','0');
INSERT INTO [t22592] VALUES (2371,'(٢١٣٣) - (قوله صلى الله عليه وسلم لعائشة: " إن الله لا يحب الفحش ولا التفحش ".','2448','2364','0');
INSERT INTO [t22592] VALUES (2372,'(٢١٣٤) - (قوله صلى الله عليه وسلم: " اخرجى فجذى نخلك " رواه أبو داود','2450','2364','0');
INSERT INTO [t22592] VALUES (2373,'(٢١٣٥) - (روى مجاهد قال: " استشهد رجال يوم أحد فجاء نساؤهم رسول الله صلى الله عليه وسلم وقلن: يا رسول الله: نستوحش بالليل فنبيت عند إحدانا حتى إذا أصبحنا بادرنا بيوتنا. فقال رسول الله صلى الله عليه وسلم: تحدثن عند إحداكن ما بدا لكن فإذا أردتن النوم فلتأت كل امرأة إلى بيتها ".','2451','2364','0');
INSERT INTO [t22592] VALUES (2374,'(٢١٣٦) - (روى مالك فى الموطأ عن يحيى بن سعيد: " أنه بلغه أن','2451','2364','0');
INSERT INTO [t22592] VALUES (2375,'باب استبراء الإماء','2453','2251','0');
INSERT INTO [t22592] VALUES (2376,'(٢١٣٧) - (قوله صلى الله عليه وسلم: " من كان يؤمن بالله واليوم الآخر فلا يسقى ماءه ولد غيره " رواه أحمد وأبو داود والترمذى (٢/٢٨٨) .','2453','2375','0');
INSERT INTO [t22592] VALUES (2377,'(٢١٣٨) - (عن أبى سعيد أن النبى صلى الله عليه وسلم قال فى سبى أوطاس: " لا توطأ حامل حتى تضع ولا غير حامل حتى تحيض حيضة " رواه أحمد (٣/٦٢) وأبو داود (٢١٥٧) .','2454','2375','0');
INSERT INTO [t22592] VALUES (2378,'(٢١٣٩) - (قال ابن عمر: " إذا وهبت الوليدة التى توطأ أو بيعت أو عتقت فلتستبرىء بحيضة ولا تستبرىء العذراء " حكاه البخارى فى صحيحه.','2454','2375','0');
INSERT INTO [t22592] VALUES (2379,'(٢١٤٠) - (أثر عمر: " أنه أنكر على عبد الرحمن بن عوف حين باع جارية له كان يطؤها قبل استبرائها قال: ما كنت لذلك بخليق " (٢/٢٨٩) .','2455','2375','0');
INSERT INTO [t22592] VALUES (2380,'(٢١٤١) - (روى عن عمرو بن العاص أنه قال: " لا تفسدوا علينا سنة نبينا صلى الله عليه وسلم عدة أم الولد إذا توفى عنها سيدها أربعة أشهر وعشرا " ولا يصح قاله أحمد.','2455','2375','0');
INSERT INTO [t22592] VALUES (2381,'(٢١٤٣) - (قول ابن مسعود: " إن النطفة أربعون يوما ثم علقة أربعون يوما ثم مضغة بعد ذلك فإذا خرجت الثمانون صار بعدها مضغة وهى لحمة فيتبين حينئذ ".','2456','2375','0');
INSERT INTO [t22592] VALUES (2382,'كتاب الرضاع','2458','2251','0');
INSERT INTO [t22592] VALUES (2383,'(٢١٤٤) - (قال عمر رضى الله عنه: " اللبن نسبة فلا تسق من يهودية ولا نصرانية " (٢/٢٩٢) .','2458','2382','0');
INSERT INTO [t22592] VALUES (2384,'(٢١٤٥) - (حديث عائشة: " الرضاعة تحرم ما تحرم الولادة " متفق عليه (٢/٢٩٣) .','2458','2382','0');
INSERT INTO [t22592] VALUES (2385,'(٢١٤٦) - (حديث ابن عباس: قال رسول الله صلى الله عليه وسلم فى ابنة حمزة: " لا تحل لى يحرم من الرضاع ما يحرم من النسب وهى ابنة أخى من الرضاعة " متفق عليه.','2458','2382','0');
INSERT INTO [t22592] VALUES (2386,'(٢١٤٧) - (حديث عائشة قالت: " أنزل فى القرآن عشر رضعات معلومات يحرمن فنسخ من ذلك خمس رضعات وصار إلى خمس رضعات معلومات يحرمن , فتوفى رسول الله صلى الله عليه وسلم والأمر على ذلك " رواه مسلم.','2458','2382','0');
INSERT INTO [t22592] VALUES (2387,'(٢١٤٨) - (حديث: " لا تحرم المصة ولا المصتان " رواه مسلم.','2459','2382','0');
INSERT INTO [t22592] VALUES (2388,'(٢١٤٩) - (وفى حديث آخر: " لا تحرم الإملاجة , ولا الإملاجتان "','2460','2382','0');
INSERT INTO [t22592] VALUES (2389,'(٢١٥٠) - (قوله صلى الله عليه وسلم: " لا يحرم من الرضاع إلا ما فتق الأمعاء وكان قبل الفطام " صححه الترمذى (٢/٢٩٤) .','2461','2382','0');
INSERT INTO [t22592] VALUES (2390,'(٢١٥١) - (حديث عائشة مرفوعا: " إنما الرضاعة من المجاعة " متفق عليه.','2462','2382','0');
INSERT INTO [t22592] VALUES (2391,'(٢١٥٢) - (حديث أم سلمة قالت: أبى سائر أزواج النبى صلى الله عليه وسلم أن يدخلن عليهن أحدا بتلك الرضاعة وقلن لعائشة: ما نرى هذه إلا (رضعة أرضعها) [١] رسول الله صلى الله عليه وسلم لسالم خاصة ". رواه أحمد ومسلم والنسائى وابن ماجه (٢/٢٩٤) .','2463','2382','0');
INSERT INTO [t22592] VALUES (2392,'(٢١٥٣) - (حديث ابن مسعود مرفوعا: " لا رضاع إلا ما (أنشر) [٢] العظم وأنبت اللحم " رواه أبو داود (٢/٢٩٥) .','2463','2382','0');
INSERT INTO [t22592] VALUES (2393,'(٢١٥٤) - (حديث عقبة بن الحارث قال: " تزوجت أم يحيى بنت أبى إهاب فجاءت أمة سوداء فقالت: قد أرضعتكما فأتيت النبى صلى الله عليه وسلم فذكرت ذلك له فقال: وكيف وقد زعمت ذلك؟! " متفق عليه. وفى لفظ للنسائى: " فأتيت من قبل وجهه فقلت: إنها كاذبة فقال: كيف وقد زعمت أنها قد أرضعتكما؟! خل سبيلها " (٢/٢٩٥) .','2464','2382','0');
INSERT INTO [t22592] VALUES (2394,'(٢١٥٥) - (حديث: " يحرم من الرضاع ما يحرم من الولادة ".','2466','2382','0');
INSERT INTO [t22592] VALUES (2395,'كتاب النفقات','2467','2251','0');
INSERT INTO [t22592] VALUES (2396,'(٢١٥٦) - (حديث جابر مرفوعا: " اتقوا الله فى النساء فإنهن عوان عندكم أخذتموهن بأمانة الله واستحللتم فروجهن بكلمة الله ولهن عليكم رزقهن وكسوتهن بالمعروف " رواه مسلم وأبو داود (٢/٢٩٧) .','2467','2395','0');
INSERT INTO [t22592] VALUES (2397,'(٢١٥٨) - (قال النبى صلى الله عليه وسلم: " خذى ما يكفيك وولدك بالمعروف ". [١]','2467','2395','0');
INSERT INTO [t22592] VALUES (2398,'(٢١٥٩) - (كتب عمر إلى أمراء الأجناد فى رجال غابوا عن نسائهم يأمرهم أن ينفقوا أو يطلقوا , فإن طلقوا بعثوا بنفقة ما مضى ".','2468','2395','0');
INSERT INTO [t22592] VALUES (2399,'فصل','2468','2251','0');
INSERT INTO [t22592] VALUES (2400,'(٢١٦٠) - (فى بعض أخبار فاطمة بنت قيس: " لا نفقة لك إلا أن تكونى حاملا " رواه أحمد وأبو داود والنسائى ورواه مسلم بمعناه (٢/٣٠٠) .','2468','2399','0');
INSERT INTO [t22592] VALUES (2401,'(٢١٦١) - (حديث عن أبى هريرة مرفوعا فى الرجل لا يجد ما ينفق على امرأته قال: " يفرق بينهما " رواه الدارقطنى.','2469','2399','0');
INSERT INTO [t22592] VALUES (2402,'(٢١٦٢) - (كتب عمر إلى أمراء الأجناد فى رجال غابوا عن نسائهم يأمرهم أن ينفقوا أو يطلقوا ... ".','2470','2399','0');
INSERT INTO [t22592] VALUES (2403,'فصل','2470','2251','0');
INSERT INTO [t22592] VALUES (2404,'(٢١٦٣) - (حديث: " أن رجلا سأل النبى صلى الله عليه وسلم: من أبر؟ قال: أمك وأباك وأختك وأخاك. وفى لفظ: ومولاك الذى هو أدناك حقا واجبا ورحما موصولا " رواه أبو داود (٢/٣٠٣) .','2470','2403','0');
INSERT INTO [t22592] VALUES (2405,'(٢١٦٤) - (قضى عمر رضى الله عنه على بنى عم منفوس بنفقته " (٢/٣٠٣) .','2471','2403','0');
INSERT INTO [t22592] VALUES (2406,'(٢١٦٥) - (حديث جابر مرفوعا: " إذا كان أحدكم فقيرا فليبدأ بنفسه فإن كان فضل فعلى عياله فإن كان فضل فعلى قرابته " صححه الترمذى.','2471','2403','0');
INSERT INTO [t22592] VALUES (2407,'(٢١٦٦) - (وفى لفظ: " ابدأ بنفسك , ثم بمن تعول " صححه الترمذى.','2471','2403','0');
INSERT INTO [t22592] VALUES (2408,'(٢١٦٧) - (قال النبى صلى الله عليه وسلم فى الحسن: " إن هذا سيد "','2471','2403','0');
INSERT INTO [t22592] VALUES (2409,'(٢١٦٨) - (حديث: " ابدأ بنفسك ثم بمن تعول ".','2472','2403','0');
INSERT INTO [t22592] VALUES (2410,'(٢١٦٩) - (حديث: " أن رجلا قال يا رسول الله من أبر؟ قال: أمك. قال: ثم من؟ قال: أمك. قال: ثم من؟ قال: أمك. قال: ثم من؟ قال: أباك " متفق عليه (٢/٣٠٥) .','2472','2403','0');
INSERT INTO [t22592] VALUES (2411,'(٢١٧٠) - (حديث بهز بن حكيم عن أبيه عن جده قال: " قلت: يا رسول الله من أبر؟ قال: أمك. قلت: ثم من؟ قال: أمك. قلت: ثم من؟ قال: أمك. قلت: ثم من؟ قال: أباك ثم الأقرب فالأقرب " رواه أحمد وأبو داود والترمذى (٢/٣٠٥) .','2472','2403','0');
INSERT INTO [t22592] VALUES (2412,'(٢١٧١) - (حديث عن طارق المحاربى مرفوعا: " ابدأ بمن تعول: أمك وأباك وأختك وأخاك ثم أدناك أدناك " رواه النسائى.','2473','2403','0');
INSERT INTO [t22592] VALUES (2413,'فصل','2473','2251','0');
INSERT INTO [t22592] VALUES (2414,'(٢١٧٢) - (حديث أبى هريرة مرفوعا: " وللمملوك طعامه وكسوته بالمعروف ولا يكلف من العمل ما لا يطيق " رواه أحمد ومسلم والشافعى فى مسنده.','2473','2413','0');
INSERT INTO [t22592] VALUES (2415,'(٢١٧٣) - (حديث ابن عمر مرفوعا: " من لطم غلامه فكفارته عتقه " رواه مسلم.','2474','2413','0');
INSERT INTO [t22592] VALUES (2416,'(٢١٧٤) - (حديث أبى ذر: " ولا تكلفوهم ما يغلبهم فإن كلفتموهم فأعينوهم عليه " متفق عليه.','2474','2413','0');
INSERT INTO [t22592] VALUES (2417,'(٢١٧٥) - (حديث: " لا ضرر ولا ضرار ".','2474','2413','0');
INSERT INTO [t22592] VALUES (2418,'(٢١٧٦) - (حديث أبى ذر مرفوعا: " ... هم إخوانكم وخولكم جعلهم الله تحت أيديكم فمن كان أخوه تحت يده فليطعمه مما يأكل وليلبسه مما يلبس ... " الحديث متفق عليه (٢/٣٠٨) .','2474','2413','0');
INSERT INTO [t22592] VALUES (2419,'(٥١٥٨) والترمذى (١/٣٥٣) وابن ماجه (٣٦٩٠) والبيهقى (٨/٧) وأحمد (٥/١٥٨ و١٦١) (من) [١] المعرور بن سويد قال: " رأيت أبا ذر , وعليه حلة , وعلى غلامه مثلها , فسألته عن ذلك؟ قال: فذكر , أنه ساب رجلا على عهد رسول الله , فعيره بأمه , قتال: فأتى الرجل النبى صلى الله عليه وسلم , فذكر ذلك له , فقال النبى صلى الله عليه وسلم: إنك امرؤ فيك جاهلية , إخوانكم وخولكم ... " الحديث.','2475','2413','0');
INSERT INTO [t22592] VALUES (2420,'(٢١٧٧) - (عن أبى هريرة مرفوعا: " إذا أتى أحدكم خادمه بطعامه فإن لم يجلسه معه , فليناوله لقمة أو لقمتين أو أكلة أو أكلتين فإنه ولى حره وعلاجه " رواه الجماعة.','2475','2413','0');
INSERT INTO [t22592] VALUES (2421,'(٢١٧٨) - (وعن أنس قال: " كان عامة وصية رسول الله صلى الله عليه وسلم وهو يغرغر بنفسه: الصلاة , وما ملكت إيمانكم " رواه أحمد وأبو داود وابن ماجه.','2477','2413','0');
INSERT INTO [t22592] VALUES (2422,'(٢١٧٩) - (حديث جرير مرفوعا: " أيما عبد أبق فقد برئت منه الذمة " وفى لفظ: " إذا أبق العبد لم تقبل له صلاة " رواه مسلم.','2479','2413','0');
INSERT INTO [t22592] VALUES (2423,'(٢١٨٠) - (حديث: " لا يجلد فوق عشرة أسواط , إلا فى حد من حدود الله " رواه الجماعة إلا النسائى.','2479','2413','0');
INSERT INTO [t22592] VALUES (2424,'(٢١٨١) - (حديث: " عبدك يقول: أطعمنى وإلا فبعنى وامرأتك تقول: أطعمنى أو طلقنى " رواه أحمد والدارقطنى بمعناه (٢/٣٠٩) .','2480','2413','0');
INSERT INTO [t22592] VALUES (2425,'فصل','2480','2251','0');
INSERT INTO [t22592] VALUES (2426,'(٢١٨٢) - (حديث ابن عمر مرفوعا: " عذبت امرأة فى هرة حبستها حتى ماتت جوعا فلا هى أطعمتها ولا هى أرسلتها تأكل من خشاش الأرض " متفق عليه (٢/٣٠٩) .','2480','2425','0');
INSERT INTO [t22592] VALUES (2427,'(٢١٨٣) - (حديث عمران: " أن النبى صلى الله عليه وسلم كان فى سفر فلعنت امرأة ناقة فقال: خذوا ما عليها ودعوها فإنها ملعونة.','2480','2425','0');
INSERT INTO [t22592] VALUES (2428,'(٢١٨٤) - (حديث أبى برزة: " لا تصاحبنا (١) ناقة عليها لعنة " رواه مسلم وأحمد.','2481','2425','0');
INSERT INTO [t22592] VALUES (2429,'(٢١٨٥) - (حديث: " أنه صلى الله عليه وسلم لعن من وسم أو ضرب الوجه ونهى عنه " (٢/٣٠٩) .','2481','2425','0');
INSERT INTO [t22592] VALUES (2430,'(٢١٨٦) - (حديث: " بينما رجل يسوق بقرة أراد أن يركبها إذ قالت: إنى لم أخلق لذلك إنما خلقت للحرث " متفق عليه.','2482','2425','0');
INSERT INTO [t22592] VALUES (2431,'باب الحضانة','2484','2251','0');
INSERT INTO [t22592] VALUES (2432,'(٢١٨٧) - (حديث: " أنت أحق به ما لم تنكحى " رواه أبو داود.','2484','2431','0');
INSERT INTO [t22592] VALUES (2433,'(٢١٨٨) - (قضى أبو بكر الصديق رضى الله عنه بعاصم بن عمر بن الخطاب لأمه أم عاصم وقال لعمر: ريحها وشمها ولطفها خير له منك ".','2484','2431','0');
INSERT INTO [t22592] VALUES (2434,'(٢١٨٩) - (قضى أبو بكر على عمر رضى الله عنهما أن يدفع ابنه إلى جدته وهى بقباء وعمر بالمدينة ". قاله أحمد (٢/٣١١) .','2485','2431','0');
INSERT INTO [t22592] VALUES (2435,'(٢١٩٠) - (قوله صلى الله عليه وسلم: " الخالة بمنزلة الأم " متفق عليه.','2485','2431','0');
INSERT INTO [t22592] VALUES (2436,'(٢١٩١) - (حديث: " أنه صلى الله عليه وسلم لم ينكر على على وجعفر مخاصمتهما زيدا فى حضانة ابنة حمزة ".','2489','2431','0');
INSERT INTO [t22592] VALUES (2437,'فصل','2489','2251','0');
INSERT INTO [t22592] VALUES (2438,'(٢١٩٢) - (حديث أبى هريرة: " أن النبى صلى الله عليه وسلم خير غلاما بين أبيه وأمه " رواه سعيد والشافعى (٢/٣١٣) .','2489','2437','0');
INSERT INTO [t22592] VALUES (2439,'(٢١٩٣) - (وعنه أيضا: " جاءت امرأة إلى النبى صلى الله عليه وسلم فقالت: يا رسول الله إن زوجى يريد أن يذهب بابنى وقد سقانى من بئر أبى عنبة , وقد نفعنى. فقال رسول الله صلى الله عليه وسلم: هذا أبوك وهذه أمك , فخذ بيد أيهما شئت , فأخذ بيد أمه , فانطلقت به " رواه أبو داود والنسائى (٢/٣١٣) .','2491','2437','0');
INSERT INTO [t22592] VALUES (2440,'(٢١٩٤) - (عن عمر: " أنه خير غلاما بين أبيه وأمه " رواه سعيد.','2491','2437','0');
INSERT INTO [t22592] VALUES (2441,'(٢١٩٥) - (عن عمارة الحربى:" خيرنى على بين أمى وعمى وكنت','2491','2437','0');
INSERT INTO [t22592] VALUES (2442,'كتاب الجنايات','2493','0','0');
INSERT INTO [t22592] VALUES (2443,'[الأحاديث ٢١٩٦ - ٢٢٠٦]','2493','2442','0');
INSERT INTO [t22592] VALUES (2444,'(٢١٩٦) - (حديث ابن مسعود مرفوعا: " لا يحل دم امرىء مسلم يشهد أن لا إله إلا الله وأنى رسول الله إلا بإحدى ثلاث: الثيب الزانى , والنفس بالنفس , والتارك لدينه المفارق للجماعة " متفق عليه.','2493','2443','0');
INSERT INTO [t22592] VALUES (2445,'(٢١٩٧) - (قوله صلى الله عليه وسلم: " ألا إن دية الخطأ شبه العمد ما كان بالسوط','2495','2443','0');
INSERT INTO [t22592] VALUES (2446,'(٢١٩٨) - (قال النبى صلى الله عليه وسلم: " من قتل له قتيل فهو بخير النظرين , إما أن يقتل وإما أن يفدى " متفق عليه (٢/٣١٦) .','2498','2443','0');
INSERT INTO [t22592] VALUES (2447,'(٢١٩٩) - (حديث عمرو بن شعيب عن أبيه عن جده: " من قتل متعمدا دفع إلى أولياء المقتول فإن شاءوا قتلوا وإن شاءوا أخذوا الدية , وهى ثلاثون حقة وثلاثون جذعة وأربعون خلفة وما صولحوا عليه فهو لهم وذلك لتشديد العقل " رواه الترمذى وقال: حسن غريب.','2499','2443','0');
INSERT INTO [t22592] VALUES (2448,'(٢٢٠٠) - (فى الحديث الصحيح: " وما زاد الله عبدا بعفو إلا عزا " (٢/٣١٧) .','2499','2443','0');
INSERT INTO [t22592] VALUES (2449,'(٢٢٠١) - (روى سعيد بن المسيب عن عمر: " أنه قتل سبعة من أهل صنعاء قتلوا رجلا , وقال: لو تمالأ عليه أهل صنعاء لقتلتهم به جميعا ".','2499','2443','0');
INSERT INTO [t22592] VALUES (2450,'(٢٢٠٢) - (وعن على: " أنه قتل ثلاثة قتلوا رجلا ".','2501','2443','0');
INSERT INTO [t22592] VALUES (2451,'(٢٢٠٣) - (عن ابن عباس: " أنه قتل جماعة قتلوا واحدا " (٢/٣١٧) .','2501','2443','0');
INSERT INTO [t22592] VALUES (2452,'(٢٢٠٤) - (حديث: " ألا إن فى قتيل خطأ العمد قتيل السوط والعصا مائة من الإبل " رواه أبو داود.','2502','2443','0');
INSERT INTO [t22592] VALUES (2453,'(٢٢٠٥) - (حديث أبى هريرة: " اقتتلت امرأتان من هذيل فرمت إحداهما الأخرى بحجر فقتلتها وما فى بطنها , فقضى النبى صلى الله عليه وسلم أن دية جنينها عبد أو وليدة , وقضى بدية المرأة على عاقلتها " متفق عليه.','2502','2443','0');
INSERT INTO [t22592] VALUES (2454,'(٢٢٠٦) - (حديث: " أنه صلى الله عليه وسلم لما سئل عن المرأة التى ضربت','2502','2443','0');
INSERT INTO [t22592] VALUES (2455,'باب شروط القصاص فى النفس','2505','2442','0');
INSERT INTO [t22592] VALUES (2456,'(٢٢٠٧) - (حديث:" رفع القلم عن ثلاثة: عن الصبى حتى يبلغ , وعن المجنون حتى يفيق , وعن النائم حتى يستيقظ " (٢/٣٢٠) .','2505','2455','0');
INSERT INTO [t22592] VALUES (2457,'(٢٢٠٨) - (حديث: " المسلمون تتكافأ دماؤهم ويسعى بذمتهم أدناهم ولا يقتل مؤمن بكافر " رواه أحمد وأبو داود (٢/٣٢١) .','2505','2455','0');
INSERT INTO [t22592] VALUES (2458,'(٢٢٠٩) - (وفى لفظ: " لا يقتل مسلم بكافر " رواه البخارى وأبو داود.','2506','2455','0');
INSERT INTO [t22592] VALUES (2459,'(٢٢١٠) - (عن على: " من السنة أن لا يقتل مؤمن بكافر " رواه أحمد.','2507','2455','0');
INSERT INTO [t22592] VALUES (2460,'(٢٢١١) - (قول على: " من السنة أن لا يقتل حر بعبد " رواه أحمد وعن ابن عباس مرفوعا مثله , رواه الدارقطنى.','2507','2455','0');
INSERT INTO [t22592] VALUES (2461,'(٢٢١٢) - (عن عمرو بن حزم أن النبى صلى الله عليه وسلم: " كتب إلى أهل اليمن أن الرجل يقتل بالمرأة " رواه النسائى ".','2508','2455','0');
INSERT INTO [t22592] VALUES (2462,'(٢٢١٣) - (عن أنس أن يهوديا رض رأس جارية بين حجرين فقيل لها: من فعل هذا بك: فلان أو فلان؟ حتى سمى اليهودى فأومت برأسها فجىء به فاعترف , فأمر به النبى صلى الله عليه وسلم فرض رأسه بحجرين " رواه الجماعة.','2508','2455','0');
INSERT INTO [t22592] VALUES (2463,'(٢٢١٤) - (حديث عمرو وابن عباس مرفوعا: " لا يقتل والد','2508','2455','0');
INSERT INTO [t22592] VALUES (2464,'(٢٢١٥) - (عن عمر رضى الله عنه: " أنه أخذ من قتادة المدلجى دية ابنه " رواه مالك.','2512','2455','0');
INSERT INTO [t22592] VALUES (2465,'(٢٢١٦) - (روى عن على رضى الله عنه: " أنه سئل عمن وجد مع امرأته رجلا فقتله , فقال: إن لم يأت بأربعة شهداء فليعط برمته ".','2514','2455','0');
INSERT INTO [t22592] VALUES (2466,'(٢٢١٧) - (روى عن عمر: " أنه كان يوما يتغدى إذ جاء رجل يعدو وفى يده سيف ملطخ بالدم ووراءه قوم يعدون خلفه فجاء حتى جلس مع عمر فجاء الآخرون فقالوا: يا أمير المؤمنين إن هذا قتل صاحبنا فقال له عمر ما تقول؟ فقال: يا أمير المؤمنين إنى ضربت فخذى امرأتى فإن كان بينهما أحد فقد قتلته فقال عمر: ما تقولون؟ قالوا: يا أمير المؤمنين إنه','2514','2455','0');
INSERT INTO [t22592] VALUES (2467,'باب شروط استيفاء القصاص','2516','2442','0');
INSERT INTO [t22592] VALUES (2468,'(٢٢١٨) - (إن معاوية حبس هدبة بن خشرم فى قصاص حتى بلغ ابن القتيل " (٢/٣٢٤) .','2516','2467','0');
INSERT INTO [t22592] VALUES (2469,'(٢٢١٩) - (إن الحسن رضى الله عنه: " قتل ابن ملجم وفى الورثة صغار فلم ينكر " وقيل " قتله لكفره " وقيل " لسعيه فى الأرض بالفساد ".','2516','2467','0');
INSERT INTO [t22592] VALUES (2470,'(٢٢٢٠) - (قوله صلى الله عليه وسلم: " فأهله بين خيرتين " (٢/٣٢٥) .','2516','2467','0');
INSERT INTO [t22592] VALUES (2471,'(٢٢٢١) - (قوله صلى الله عليه وسلم: " من يعذرنى من رجل بلغنى أذاه فى أهلى , وما علمت على أهلى إلا خيرا.','2519','2467','0');
INSERT INTO [t22592] VALUES (2472,'(٢٢٢٢) - (وعن زيد بن وهب أن عمر رضى الله عنه: " أتى برجل قتل قتيلا فجاء ورثة المقتول ليقتلوه , فقالت امرأة المقتول ـ وهى أخت القاتل ـ: قد عفوت عن حقى , فقال عمر: الله أكبر عتق القتيل " رواه أبو داود.','2519','2467','0');
INSERT INTO [t22592] VALUES (2473,'(٢٢٢٤) - (روى قتادة: " أن عمر رفع إليه رجل قتل رجلا فجاء (١) أولاد المقتول , وقد عفا بعضهم , فقال عمر لابن مسعود: ما تقول؟ قال: إنه قد أحرز من القتل. فضرب على كتفه وقال: كنيف ملىء علما " [١] .','2520','2467','0');
INSERT INTO [t22592] VALUES (2474,'(٢٢٢٥) - (عن زيد بن وهب أن رجلا دخل على امرأته فوجد عندها رجلا فقتلها فاستعدى عليه إخوتها عمر رضى الله عنه فقال بعض إخوتها قد تصدقت فقضى لسائرهم بالدية " (٢/٣٢٦) .','2521','2467','0');
INSERT INTO [t22592] VALUES (2475,'(٢٢٢٥) - (حديث شداد بن أوس مرفوعا: " إذا قتلت المرأة عمدا لم تقتل حتى تضع ما فى بطنها وحتى تكفل ولدها " رواه مسلم [١] .','2521','2467','0');
INSERT INTO [t22592] VALUES (2476,'(٢٢٢٦) - (حديث: " قوله صلى الله عليه وسلم للغامدية: ارجعى حتى تضعى ما فى بطنك , ثم قال لها: ارجعى حتى ترضعيه ... الحديث " رواه أحمد ومسلم وأبو داود.','2522','2467','0');
INSERT INTO [t22592] VALUES (2477,'فصل','2524','2442','0');
INSERT INTO [t22592] VALUES (2478,'(٢٢٢٧) - (عن أبى هريرة مرفوعا: " من اطلع فى بيت قوم بغير إذنهم فقد حل لهم أن يفقئوا عينه " رواه أحمد ومسلم.','2524','2477','0');
INSERT INTO [t22592] VALUES (2479,'(٢٢٢٨) - (حديث عن عبادة مرفوعا: " منزل الرجل حريمه فمن دخل على حريمك فاقتله " قاله أحمد.','2525','2477','0');
INSERT INTO [t22592] VALUES (2480,'(٢٢٢٩) - (حديث: " لا قود إلا بالسيف " رواه ابن ماجه.','2525','2477','0');
INSERT INTO [t22592] VALUES (2481,'(٢٢٣٠) - (حديث: " نهى رسول الله صلى الله عليه وسلم عن المثلة " رواه النسائى.','2530','2477','0');
INSERT INTO [t22592] VALUES (2482,'(٢٢٣١) - (حديث: إذا قتلتم فأحسنوا القتلة ".','2533','2477','0');
INSERT INTO [t22592] VALUES (2483,'(٢٢٣٣) - (حديث: " من حرق حرقناه ومن غرق غرقناه ".','2534','2477','0');
INSERT INTO [t22592] VALUES (2484,'باب شروط القصاص','2535','2442','0');
INSERT INTO [t22592] VALUES (2485,'(٢٢٣٤) - (فيما دون النفس حديث أنس بن النضر وفيه: " كتاب الله القصاص " رواه البخارى وغيره.','2535','2484','0');
INSERT INTO [t22592] VALUES (2486,'(٢٢٣٥) - (روى نمران بن جارية (١) عن أبيه: " أن رجلا ضرب رجلا على ساعده بالسيف فقطع من غير مفصل فاستعدى عليه النبى صلى الله عليه وسلم فأمر له النبى صلى الله عليه وسلم بالدية. فقال: إنى أريد القصاص. قال: خذ الدية بارك الله لك فيها , ولم يقض له بالقصاص ".','2535','2484','0');
INSERT INTO [t22592] VALUES (2487,'فصل','2537','2442','0');
INSERT INTO [t22592] VALUES (2488,'(٢٢٣٦) - (حديث من قول عمر وعلى: " من مات من حد أو قصاص لا دية له , الحد (١) قتله " رواه سعيد بمعناه.','2537','2487','0');
INSERT INTO [t22592] VALUES (2489,'(٢٢٣٧) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن رجلا طعن بقرن فى ركبته فجاء إلى النبى صلى الله عليه وسلم فقال: أقدنى.','2538','2487','0');
INSERT INTO [t22592] VALUES (2490,'كتاب الديات','2540','2442','0');
INSERT INTO [t22592] VALUES (2491,'(٢٢٣٨) - (حديث النسائى ومالك فى الموطأ: " أنه صلى الله عليه وسلم كتب لعمرو بن حزم كتابا إلى أهل اليمن فيه: الفرائض والسنن والديات , وقال فيه: وفى النفس مائة من الإبل " (٢/٣٣٣) .','2540','2490','0');
INSERT INTO [t22592] VALUES (2492,'(٢٢٣٩) - (حديث أبى هريرة: " اقتتلت امرأتان من هذيل , فرمت إحداهما الأخرى بحجر فقتلتها وما فى بطنها , فقضى رسول الله صلى الله عليه وسلم بدية المرأة على عاقلتها " متفق عليه.','2540','2490','0');
INSERT INTO [t22592] VALUES (2493,'(٢٢٤٠) - (قال الشعبى فيما يرويه عن على: " إن ثلاث جوار اجتمعن فركبت إحداهن على عنق الأخرى وقرصت الثالثة المركوبة فقمصت فسقطت الراكبة فوقصت عنقها فماتت , فرفعت إلى على فقضى بالدية أثلاثا على عواقلهن , وألقى الثلث الذى قابل فعل الواقصة لأنها أعانت على نفسها " (٢/٣٣٥) [١] .','2540','2490','0');
INSERT INTO [t22592] VALUES (2494,'(٢٢٤١) - (روى: " أن عمر بعث إلى امرأة مغيبة كان رجل يدخل عليها فقالت يا ويلها ما لها ولعمر , فبينما هى فى الطريق إذ فزعت فضربها الطلق فألقت ولدا فصاح الصبى صيحتين ثم مات. فاستشار عمر أصحاب النبى صلى الله عليه وسلم فأشار بعضهم أن ليس عليك شىء إنما أنت وال مؤدب , وصمت على فأقبل عليه عمر فقال: ما تقول يا أبا الحسن؟ فقال: إن كانوا قالوا برأيهم فقد أخطأ رأيهم وإن كانوا قالوا فى هواك فلم ينصحوا لك , إن ديته عليك لأنك أفزعتها فألقته. فقال عمر: أقسمت عليك لا تبرح حتى تقسمها على قومك ".','2541','2490','0');
INSERT INTO [t22592] VALUES (2495,'(٢٢٤٢) - (روى: " أن عامر بن الأكوع يوم خيبر رجع سيفه عليه فقتله " (٢/٢٣٨) .','2541','2490','0');
INSERT INTO [t22592] VALUES (2496,'فصل فى مقادير ديات النفس','2543','2442','0');
INSERT INTO [t22592] VALUES (2497,'(٢٢٤٣) - (روى مالك والنسائى أن فى كتاب عمرو بن حزم: " وفى النفس مائة من الإبل " (٢/٣٣٩) .','2543','2496','0');
INSERT INTO [t22592] VALUES (2498,'(٢٢٤٤) - (روى عطاء عن جابر قال: " فرض رسول الله صلى الله عليه وسلم فى الدية على أهل الإبل مائة من الإبل وعلى أهل البقر مائتى بقرة وعلى أهل الشاة ألفى شاة " رواه أبو داود.','2543','2496','0');
INSERT INTO [t22592] VALUES (2499,'(٢٢٤٥) - (عن عكرمة عن ابن عباس: " أن رجلا من بنى عدى قتل فجعل النبى صلى الله عليه وسلم ديته اثنى عشر ألف درهم " رواه أبو داود.','2544','2496','0');
INSERT INTO [t22592] VALUES (2500,'(٢٢٤٦) - (وفى كتاب عمرو بن حزم: " وعلى أهل الذهب ألف دينار ".','2545','2496','0');
INSERT INTO [t22592] VALUES (2501,'(٢٢٤٧) - (عن عمرو بن شعيب عن أبيه عن جده: " أن عمر قام خطيبا فقال: إن الإبل قد غلت , قال: فقوم على أهل الذهب ألف دينار وعلى أهل الورق اثنى عشر ألفا وعلى أهل البقر مائتى بقرة وعلى أهل الشاة ألفى شاة وعلى أهل الحلل مائتى حلة " رواه أبو داود.','2545','2496','0');
INSERT INTO [t22592] VALUES (2502,'(٢٢٤٨) - (حديث: " فى النفس المؤمنة مائة من الإبل ".','2545','2496','0');
INSERT INTO [t22592] VALUES (2503,'(٢٢٤٩) - (قول عمر رضى الله عنه: " إن الإبل قد غلت ... " الخ.','2546','2496','0');
INSERT INTO [t22592] VALUES (2504,'(٢٢٥٠) - (فى كتاب عمرو بن حزم: " دية المرأة على النصف من دية الرجل ".','2546','2496','0');
INSERT INTO [t22592] VALUES (2505,'(٢٢٥١) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " دية المعاهد نصف دية المسلم " وفى لفظ: " أن النبى صلى الله عليه وسلم قضى بأن عقل أهل الكتاب نصف عقل المسلمين " رواه أحمد.','2547','2496','0');
INSERT INTO [t22592] VALUES (2506,'(٢٢٥٢) - (فى كتاب عمرو بن حزم: " دية المرأة على النصف من دية الرجل وكذا جراح الكتابى على نصف جراح المسلم ".','2548','2496','0');
INSERT INTO [t22592] VALUES (2507,'(٢٢٥٣) - (حديث: " سنوا بهم سنة أهل الكتاب ".','2548','2496','0');
INSERT INTO [t22592] VALUES (2508,'(٢٢٥٤) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " عقل المرأة مثل عقل الرجل حتى يبلغ الثلث من ديتها " رواه النسائى','2548','2496','0');
INSERT INTO [t22592] VALUES (2509,'(٢٥٥٥) - (قال ربيعة بن أبى عبد الرحمن: " قلت لسعيد بن المسيب كم فى أصبع المرأة؟ قال عشر من الإبل قلت: فكم فى أصبعين؟ قال: عشرون , قلت: ففى ثلاث أصابع؟ قال: ثلاثون , قلت: ففى أربع؟ قال: عشرون , قال: فقلت: لما عظم جرحها واشتدت مصيبتها نقص عقلها؟ ! قال سعيد: أعراقى أنت؟ قلت: بل عالم متثبت أو جاهل متعلم , قال: هى السنة يا ابن أخى " رواه مالك فى الموطأ وسعيد بن منصور فى سننه [١] .','2549','2496','0');
INSERT INTO [t22592] VALUES (2510,'(٢٢٥٦) - (قوله فى الحديث: " حتى يبلغ الثلث ".','2549','2496','0');
INSERT INTO [t22592] VALUES (2511,'(٢٢٥٧) - (حديث: " والثلث كثير ".','2550','2496','0');
INSERT INTO [t22592] VALUES (2512,'(٢٢٥٨) - (روى أبو نجيح (١) : " أن امرأة وطئت فى الطواف فقضى عثمان فيها بستة آلاف وألفين تغليظا للحرم " (٢/٣٤٢) .','2550','2496','0');
INSERT INTO [t22592] VALUES (2513,'(٢٢٥٩) - (عن ابن عمر أنه قال: " من قتل فى الحرم , أو ذا رحم أو فى الشهر الحرام فعليه دية وثلث ".','2550','2496','0');
INSERT INTO [t22592] VALUES (2514,'(٢٢٦٠) - (وعن ابن عباس: " أن رجلا قتل رجلا فى الشهر الحرام وفى البلد الحرام فقال: ديته اثنا عشر ألفا , وللشهر الحرام أربعة * ضعيف.','2551','2496','0');
INSERT INTO [t22592] VALUES (2515,'(٢٢٦١) - (وفى الحديث: " وأنتم يا خزاعة قد قتلتم هذا القتيل من هذيل وأنا والله عاقله ... " الحديث.','2551','2496','0');
INSERT INTO [t22592] VALUES (2516,'(٢٢٦٢) - (أثر: " وإن قتل مسلم كافرا عمدا أضعفت ديته لإزالة القود " قضى به عثمان رضى الله عنه رواه أحمد.','2552','2496','0');
INSERT INTO [t22592] VALUES (2517,'فصل','2553','2442','0');
INSERT INTO [t22592] VALUES (2518,'(٢٢٦٤) - (حديث أبى هريرة قال: " اقتتلت امرأتان من هذيل فرمت إحداهما الأخرى بحجر فقتلتها وما فى بطنها فاختصموا إلى رسول الله صلى الله عليه وسلم فقضى أن دية جنينها عبد أو أمة وقضى بدية {المرأة على} عاقلتها , وورثها ولدها ومن معه " متفق عليه (٢/٣٤٣) .','2553','2517','0');
INSERT INTO [t22592] VALUES (2519,'(٢٢٦٥) - (عن عمر أنه استشار الناس فى إملاص المرأة: " فقال المغيرة بن شعبة: شهدت رسول الله صلى الله عليه وسلم قضى فيه بغرة: عبد أو أمة , قال: لتأتين بمن يشهد معك فشهد له محمد بن مسلمة " متفق عليه.','2553','2517','0');
INSERT INTO [t22592] VALUES (2520,'(٢٢٦٦) - (روى عن عمر وزيد أنهما قالا فى الغرة: " قيمتها خمس من الإبل " (٢/٣٤٣) .','2553','2517','0');
INSERT INTO [t22592] VALUES (2521,'(٢٢٦٧) - (حديث عمرو بن حزم مرفوعا: " وفى الذكر الدية وفى الأنف إذا أوعب جدعا الدية وفى اللسان الدية " رواه أحمد والنسائى واللفظ له (٢/٣٤٥) .','2553','2517','0');
INSERT INTO [t22592] VALUES (2522,'(٢٢٦٨) - (حديث عمرو بن حزم مرفوعا وفيه: ".. وفي الشفتين: الدية، وفي بيضتين: الدية، وفي الذكر: الدية، وفي الصلب: الدية، وفي العينين: الدية، وفي الرجل الواحدة: نصف الدية،.." الحديث) ٢/٣٤٥','2554','2517','0');
INSERT INTO [t22592] VALUES (2523,'(٢٢٦٩) - (وروى مالك فى " الموطأ " أن رسول الله صلى الله عليه وسلم قال: " وفى العين خمسون من الإبل ".','2554','2517','0');
INSERT INTO [t22592] VALUES (2524,'(٢٢٧٠) - (وفى عين الأعور دية كاملة لأنه يروى عن عمر وعثمان وعلى وابن عمر: " أنهم قضوا بذلك " ولم يعرف لهم مخالف فى عصرهم.','2555','2517','0');
INSERT INTO [t22592] VALUES (2525,'(٢٢٧١) - (حديث ابن عباس مرفوعا: " دية أصابع اليدين والرجلين عشرة من الإبل لكل أصبع " صححه الترمذى ـ وعن أبى موسى مرفوعا نحوه , رواه أحمد وأبو داود والنسائى.','2556','2517','0');
INSERT INTO [t22592] VALUES (2526,'(٢٢٧٢) - (وعن أبى موسى مرفوعا نحوه , رواه أحمد وأبو داود والنسائى (٢/٣٤٦) .','2558','2517','0');
INSERT INTO [t22592] VALUES (2527,'(٢٢٧٣) - (حديث عمرو بن حزم مرفوعا: " وفى كل أصبع من أصابع اليد والرجل عشر من الإبل ".','2559','2517','0');
INSERT INTO [t22592] VALUES (2528,'(٢٢٧٤) - (وفى ظفر لم يعد أو عاد أسود خمس دية الأصبع " روى عن ابن عباس (٢/٣٤٦) .','2559','2517','0');
INSERT INTO [t22592] VALUES (2529,'(٢٢٧٥) - (فى حديث عمرو بن حزم مرفوعا: " وفى السن خمس من الإبل " رواه النسائى (٢/٣٤٦) .','2560','2517','0');
INSERT INTO [t22592] VALUES (2530,'(٢٢٧٦) - (عن عمرو بن شعيب عن أبيه عن جده: " فى الأسنان خمس خمس ".','2560','2517','0');
INSERT INTO [t22592] VALUES (2531,'(٢٢٧٧) - (حديث ابن عباس مرفوعا: " الأصابع سواء , والأسنان سواء الثنية والضرس سواء " رواه أبو داود وابن ماجه.','2560','2517','0');
INSERT INTO [t22592] VALUES (2532,'(٢٢٧٨) - (حديث: " وفى السمع الدية ".','2561','2517','0');
INSERT INTO [t22592] VALUES (2533,'فصل فى دية المنافع','2562','2442','0');
INSERT INTO [t22592] VALUES (2534,'(٢٢٧٩) - (قضى عمر فى رجل ضرب رجلا فذهب سمعه وبصره ونكاحه وعقله بأربع ديات والرجل حى " ذكره أحمد (٢/٣٤٧) .','2562','2533','0');
INSERT INTO [t22592] VALUES (2535,'(٢٢٨٠) - (فى كتاب (عمر) [٤] بن حزم: " وفى العقل الدية ".','2562','2533','0');
INSERT INTO [t22592] VALUES (2536,'(٢٢٨١) - (وفى كتاب عمرو بن حزم: " وفى الصلب الدية "','2563','2533','0');
INSERT INTO [t22592] VALUES (2537,'(٢٢٨٢) - (روى أن عثمان: " قضى بثلث الدية فيمن ضرب إنسانا حتى أحدث ".','2563','2533','0');
INSERT INTO [t22592] VALUES (2538,'فصل فى دية الشجة والجائفة','2564','2442','0');
INSERT INTO [t22592] VALUES (2539,'(٢٢٨٣) - (قال مكحول: " قضى رسول الله صلى الله عليه وسلم فى الموضحة بخمس من الإبل ولم يقض فيما دونها " قاله فى الكافى (٢/٣٥٠) .','2564','2538','0');
INSERT INTO [t22592] VALUES (2540,'(٢٢٨٤) - (فى كتاب عمرو بن حزم: " وفى الموضحة خمس من الإبل " رواه النسائى (٢/٣٥٠) .','2565','2538','0');
INSERT INTO [t22592] VALUES (2541,'(٢٢٨٥) - (عن عمرو بن شعيب عن أبيه عن جده: " فى المواضح خمس خمس من الإبل " رواه الخمسة.','2566','2538','0');
INSERT INTO [t22592] VALUES (2542,'(٢٢٨٦) - (فى كتاب عمرو بن حزم: " وفى المنقلة خمس عشرة من الإبل ".','2566','2538','0');
INSERT INTO [t22592] VALUES (2543,'(٢٢٨٧) - (وفي حديث عمرو بن شعيب عن أبيه عن جده مرفوعا مثل ذلك. رواه احمد وأبو داود) .','2566','2538','0');
INSERT INTO [t22592] VALUES (2544,'(٢٢٨٨) - (وفى حديث عمرو بن شعيب عن أبيه عن جده مرفوعا مثل ذلك , رواه أحمد وأبو داود (٢/٣٥١) .','2566','2538','0');
INSERT INTO [t22592] VALUES (2545,'(٢٢٨٩) - (فى كتاب عمرو بن حزم مرفوعا: " وفى المأمومة ثلث الدية " رواه النسائى.','2567','2538','0');
INSERT INTO [t22592] VALUES (2546,'(٢٢٩٠) - (وعن عبد الله بن عمرو مرفوعا: مثله , رواه أحمد.','2567','2538','0');
INSERT INTO [t22592] VALUES (2547,'(٢٢٩١) - (روى أسلم مولى عمر: " أن عمر رضى الله عنه قضى فى الترقوة وفى الضلع بجمل " رواه سعيد بسنده (٢/٣٥١) .','2567','2538','0');
INSERT INTO [t22592] VALUES (2548,'(٢٢٩٢) - (روى سعيد عن عمرو بن شعيب: " أن عمرو بن العاص كتب إلى عمر فى إحدى الزندين إذا كسر , فكتب إليه عمر أن فيه بعيرين , وإذا كسر الزندان ففيهما أربعة من الإبل ".','2568','2538','0');
INSERT INTO [t22592] VALUES (2549,'(٢٢٩٣) - (حديث عمرو بن شعيب عن أبيه عن جده: " قضى رسول الله صلى الله عليه وسلم فى العين القائمة السادة لمكانها بثلث ديتها وفى اليد الشلاء إذا قطعت بثلث ديتها , وفى السن السوداء إذا قلعت ثلث ديتها " رواه النسائى (٢/٣٥٢) .','2568','2538','0');
INSERT INTO [t22592] VALUES (2550,'(٢٢٩٤) - (أثر: " أن عمر قضى بمثل ذلك ".','2568','2538','0');
INSERT INTO [t22592] VALUES (2551,'(٢٢٩٥) - (روى عن على وزيد بن ثابت: " فى الشعر الدية ".','2569','2538','0');
INSERT INTO [t22592] VALUES (2552,'فصل','2569','2442','0');
INSERT INTO [t22592] VALUES (2553,'(٢٢٩٦) - (فى كتاب عمرو بن حزم: " وفى الجائفة ثلث الدية " رواه النسائى.','2569','2552','0');
INSERT INTO [t22592] VALUES (2554,'(٢٢٩٧) - (حديث عمرو بن شعيب عن أبيه عن جده مرفوعا وفيه: " وفى الجائفة ثلث العقل " رواه أحمد وأبو داود.','2569','2552','0');
INSERT INTO [t22592] VALUES (2555,'(٢٢٩٨) - (روى سعيد بن المسيب: " أن رجلا رمى رجلا بسهم فأنفذه فقضى أبو بكر بثلثى الدية " أخرجه سعيد فى سننه.','2570','2552','0');
INSERT INTO [t22592] VALUES (2556,'(٢٢٩٩) - (عن عمرو بن شعيب عن أبيه: " عن جده أن عمر قضى فى الجائفة إذا نفذت الجوف بأرش جائفتين ".','2571','2552','0');
INSERT INTO [t22592] VALUES (2557,'(٢٣٠٠) - (قضى عمر رضى الله عنه فى الإفضاء: " ثلث الدية ".','2571','2552','0');
INSERT INTO [t22592] VALUES (2558,'باب العاقلة','2572','2442','0');
INSERT INTO [t22592] VALUES (2559,'(٢٣٠١) - (حديث أبى هريرة: " قضى رسول الله صلى الله عليه وسلم فى جنين امرأة من بنى لحيان سقط ميتا بغرة عبد أو أمة ثم إن المرأة التى قضى عليها بالغرة توفيت فقضى رسول الله صلى الله عليه وسلم أن ميراثها فى بنيها وزوجها وأن العقل على عصبتها " وفى رواية: " اقتتلت امرأتان من هذيل فرمت إحداهما الأخرى بحجر فقتلتها وما فى بطنها فاختصموا إلى النبى صلى الله عليه وسلم فقضى أن دية جنينها غرة عبد أو وليدة وقضى بدية المرأة على عاقلتها " متفق عليه.','2572','2558','0');
INSERT INTO [t22592] VALUES (2560,'(٢٣٠٢) - (عن عمرو بن شعيب عن أبيه عن جده: " أن النبى صلى الله عليه وسلم قضى أن يعقل عن المرأة عصبتها من كانوا , ولا يرثون منها إلا ما فضل من ورثتها " رواه الخمسة إلا الترمذى.','2572','2558','0');
INSERT INTO [t22592] VALUES (2561,'(٢٣٠٣) - (حديث: " لا يجنى عليك ولا تجنى عليه ".','2572','2558','0');
INSERT INTO [t22592] VALUES (2562,'(٢٣٠٤) - (قول ابن عباس: " لا تحمل العاقلة عمدا ولا عبدا ولا صلحا ولا اعترافا " حكاه عنه أحمد (٢/٣٥٥) .','2576','2558','0');
INSERT INTO [t22592] VALUES (2563,'(٢٣٠٥) - (قال عمر: " العمد والعبد والصلح والاعتراف لا تعقله العاقلة " رواه الدارقطنى.','2576','2558','0');
INSERT INTO [t22592] VALUES (2564,'(٢٣٠٦) - (قال الزهرى: " مضت السنة أن العاقلة لا تحمل شيئا من دية العمد إلا أن يشاءوا " رواه مالك فى " الموطأ ".','2577','2558','0');
INSERT INTO [t22592] VALUES (2565,'(٢٣٠٧) - (روى عن عمر رضى الله عنه: " أنه قضى فى الدية أن لا تحمل منها العاقلة شيئا حتى تبلغ عقل المأمومة ".','2577','2558','0');
INSERT INTO [t22592] VALUES (2566,'(٢٣٠٨) - (روى عن عمر وعلى: " أنهما قضيا بالدية على العاقلة فى ثلاث سنين " وروى نحوه عن ابن عباس.','2577','2558','0');
INSERT INTO [t22592] VALUES (2567,'باب كفارة القتل','2579','2442','0');
INSERT INTO [t22592] VALUES (2568,'(٢٣٠٩) - (عن واثلة بن الأسقع قال: " أتينا رسول الله صلى الله عليه وسلم فى صاحب لنا أوجب (يعنى النار) بالقتل فقال: أعتقوا عنه يعتق الله بكل عضو منه عضوا منه من النار " رواه أحمد وأبو داود.','2579','2567','0');
INSERT INTO [t22592] VALUES (2569,'كتاب الحدود','2580','0','0');
INSERT INTO [t22592] VALUES (2570,'[الأحاديث ٢٣١٠ - ٢٣٣٦]','2580','2569','0');
INSERT INTO [t22592] VALUES (2571,'(٢٣١٠) - (حديث: " رفع القلم عن ثلاثة ... ".','2580','2570','0');
INSERT INTO [t22592] VALUES (2572,'(٢٣١١) - (حديث: " عفى لأمتى عن الخطأ والنسيان وما استكرهوا عليه " رواه النسائى.','2580','2570','0');
INSERT INTO [t22592] VALUES (2573,'(٢٣١٢) - (روى سعيد فى سننه عن طارق بن شهاب قال: " أتى عمر رضى الله عنه بامرأة قد زنت , قالت: إنى كنت نائمة فلم استيقظ إلا برجل قد جثم على فخلى سبيلها ولم يضربها ".','2580','2570','0');
INSERT INTO [t22592] VALUES (2574,'(٢٣١٣) - (روى: " أنه أتى بامرأة استسقت راعيا فأبى أن يسقيها إلا أن تمكنه من نفسها فقال لعلى: ما ترى فيها؟ قال: إنها مضطرة فأعطاها شيئا وتركها ".','2581','2570','0');
INSERT INTO [t22592] VALUES (2575,'(٢٣١٤) - (روى عن عمر وعلى أنهما قالا: " لا حد إلا على من علمه ". (٢/٣٦١) .','2582','2570','0');
INSERT INTO [t22592] VALUES (2576,'(٢٣١٥) - (روى سعيد بن المسيب قال: " ذكر الزنا بالشام فقال رجل: زنيت البارحة , قالوا: ما تقول؟ قال: ما علمت أن الله حرمه , فكتب بها إلى عمر فكتب: إن كان يعلم أن الله حرمه فحدوه وإن لم يكن علم فأعلموه فإن عاد فارجموه ".','2583','2570','0');
INSERT INTO [t22592] VALUES (2577,'(٢٣١٦) - (حديث: " ادرءوا الحدود بالشبهات ما استطعتم ". (٢/٣٦١) .','2583','2570','0');
INSERT INTO [t22592] VALUES (2578,'(٢٣١٧) - (قال صلى الله عليه وسلم: " فهلا قبل أن تأتينى به ".','2585','2570','0');
INSERT INTO [t22592] VALUES (2579,'(٢٣١٨) - (عن ابن عمر مرفوعا: " من حالت شفاعته دون حد من حدود الله فهو مضاد لله فى أمره " رواه أحمد وأبو داود (٢/٣٦١) .','2589','2570','0');
INSERT INTO [t22592] VALUES (2580,'(٢٣١٩) - (حديث: " أن أسامة بن زيد لما شفع فى المخزومية التى سرقت غضب النبى صلى الله عليه وسلم , وقال: " أتشفع فى حد من حدود الله؟ ! " رواه أحمد ومسلم بمعناه.','2591','2570','0');
INSERT INTO [t22592] VALUES (2581,'(٢٣٢٠) - (حديث: " أنه صلى الله عليه وسلم كان يقيم الحدود فى حياته وكذا خلفاؤه من بعده " (٢/٣٦١) .','2592','2570','0');
INSERT INTO [t22592] VALUES (2582,'(٢٣٢١) - (قوله صلى الله عليه وسلم: " واغد يا أنيس إلى امرأة هذا فإن اعترفت فارجمها فاعترفت فرجمها ".','2592','2570','0');
INSERT INTO [t22592] VALUES (2583,'(٢٣٢٢) - (حديث: " أمر برجم ماعز ولم يحضره ".','2592','2570','0');
INSERT INTO [t22592] VALUES (2584,'(٢٣٢٣) - (حديث: " أنه قال فى سارق أتى به: اذهبوا به فاقطعوه ".','2599','2570','0');
INSERT INTO [t22592] VALUES (2585,'(٢٣٢٤) - (روى سعيد: " أن فاطمة حدت جارية لها ".','2599','2570','0');
INSERT INTO [t22592] VALUES (2586,'(٢٣٢٥) - (قوله صلى الله عليه وسلم: " أقيموا الحدود على ما ملكت أيمانكم " رواه أحمد وأبو داود.','2599','2570','0');
INSERT INTO [t22592] VALUES (2587,'(٢٣٢٦) - (عن أبى هريرة وزيد بن خالد الجهنى قالا: " سئل رسول الله صلى الله عليه وسلم عن الأمة إذا زنت ولم تحصن , قال: إن زنت فاجلدوها ثم إن زنت فاجلدوها ثم إن زنت فاجلدوها ثم بيعوها لو بضفير " ـ قال ابن شهاب: لا أدرى بعد الثالثة أو الرابعة ـ متفق عليه.','2600','2570','0');
INSERT INTO [t22592] VALUES (2588,'(٢٣٢٧) - (حديث حكيم بن حزام أن النبى صلى الله عليه وسلم: " نهى أن يستقاد بالمسجد وأن تنشد الأشعار وأن تقام فيه الحدود " رواه أحمد وأبو داود والدارقطنى بمعناه.','2601','2570','0');
INSERT INTO [t22592] VALUES (2589,'(٢٣٢٨) - (روى مالك عن زيد بن أسلم مرسلا: " أن رجلا اعترف عند النبى صلى الله عليه وسلم فأتى بسوط مكسور فقال: فوق هذا , فأتى بسوط جديد لم تكسر ثمرته , فقال: بين هذين ".','2603','2570','0');
INSERT INTO [t22592] VALUES (2590,'(٢٣٢٩) - (عن على رضى الله عنه قال: " ضرب بين ضربين وسوط بين سوطين " (٢/٣٦٣) .','2604','2570','0');
INSERT INTO [t22592] VALUES (2591,'(٢٣٣٠) - (قال ابن مسعود: " ليس فى ديننا مد ولا قيد ولا تجريد ".','2604','2570','0');
INSERT INTO [t22592] VALUES (2592,'(٢٣٣١) - (قال على رضى الله عنه: " اضرب وأوجع واتق الرأس والوجه " وقال: " لكل من الجسد حظ إلا الوجه والفرج ".','2605','2570','0');
INSERT INTO [t22592] VALUES (2593,'(٢٣٣٢) - (قول على رضى الله عنه: " تضرب المرأة جالسة والرجل قائما ".','2605','2570','0');
INSERT INTO [t22592] VALUES (2594,'(٢٣٣٣) - (فى حديث الجهنية: " فأمر بها رسول الله صلى الله عليه وسلم فشدت عليها ثيابها ... الحديث " رواه أحمد ومسلم وأبو داود.','2606','2570','0');
INSERT INTO [t22592] VALUES (2595,'(٢٣٣٤) - (خبر عبادة , وفيه: " ... ومن أصاب من ذلك شيئا , فعوقب به , فهو كفارة له " متفق عليه (٢/٣٦٤) .','2606','2570','0');
INSERT INTO [t22592] VALUES (2596,'(٢٣٣٥) - (حديث: " إن الله ستير يحب الستر ".','2607','2570','0');
INSERT INTO [t22592] VALUES (2597,'(٢٣٣٦) - (قول ابن مسعود رضى الله عنه: " إذا اجتمع حدان أحدهما: القتل أحاط القتل بذلك " رواه سعيد.','2608','2570','0');
INSERT INTO [t22592] VALUES (2598,'باب حد الزنا','2609','2569','0');
INSERT INTO [t22592] VALUES (2599,'(٢٣٣٧) - (عن عبد الله بن مسعود قال: " سألت رسول الله صلى الله عليه وسلم أى الذنب أعظم , قال: أن تجعل لله ندا وهو خلقك , قلت: ثم أى؟ قال: أن تقتل ولدك مخافة أن يطعم معك , قلت: ثم أى قال: أن تزانى بحليلة جارك " متفق عليه.','2609','2598','0');
INSERT INTO [t22592] VALUES (2600,'(٢٣٣٨) - (حديث عمر قال: " إن الله بعث محمدا صلى الله عليه وسلم بالحق وأنزل عليه الكتاب فكان فيما أنزل عليه آية الرجم فقرأتها وعقلتها ووعيتها. ورجم رسول الله صلى الله عليه وسلم ورجمنا بعده. فأخشى إن طال','2609','2598','0');
INSERT INTO [t22592] VALUES (2601,'(٢٣٣٩) - (حديث: " أن النبى صلى الله عليه وسلم رحم ماعزا والغامدية , ورجم الخلفاء بعده ".','2610','2598','0');
INSERT INTO [t22592] VALUES (2602,'(٢٣٤٠) - (عن على رضى الله عنه: " أنه ضرب شراحة (١) يوم الخميس ورجمها يوم الجمعة وقال: جلدتها بكتاب الله ورجمتها بسنة رسول الله صلى الله عليه وسلم " رواه أحمد والبخارى.','2611','2598','0');
INSERT INTO [t22592] VALUES (2603,'(٢٣٤١) - (حديث عبادة: " والثيب بالثيب جلد مائة والرجم " رواه مسلم وغيره.','2616','2598','0');
INSERT INTO [t22592] VALUES (2604,'(٢٣٤٢) - (أن النبى صلى الله عليه وسلم: " رجم ماعزا والغامدية ولم يجلدهما , وقال لأنيس: فإن اعترفت فارجمها " وعمر رجم ولم يجلد.','2617','2598','0');
INSERT INTO [t22592] VALUES (2605,'(٢٣٤٣) - (روى ابن عمر: " أن النبى صلى الله عليه وسلم أمر برجم اليهوديين الزانيين فرجما " متفق عليه.','2617','2598','0');
INSERT INTO [t22592] VALUES (2606,'(٢٣٤٤) - (روى الترمذى عن ابن عمر: أن النبى صلى الله عليه وسلم " ضرب وغرب , وأن أبا بكر ضرب وغرب وأن عمر ضرب وغرب ".','2617','2598','0');
INSERT INTO [t22592] VALUES (2607,'(٢٣٤٥) - (عن عبد الله بن عياش المخزومى قال: " أمرنى عمر بن الخطاب فى فتية من قريش فجلدنا ولائد من ولائد الإمارة خمسين خمسين فى الزنا " رواه مالك.','2618','2598','0');
INSERT INTO [t22592] VALUES (2608,'(٢٣٤٦) - (حديث: " أن النبى صلى الله عليه وسلم لم يأمر بتعذيب الأمة إذا زنت','2618','2598','0');
INSERT INTO [t22592] VALUES (2609,'(٢٣٤٧) - (حديث أبى هريرة وزيد بن خالد فى رجلين اختصما إلى رسول الله صلى الله عليه وسلم وكان ابن أحدهما عسيفا عند الآخر فزنى بامرأته وفيه: قال رسول الله صلى الله عليه وسلم: " وعلى (ابنكن) [١] جلد مائة وتغريب عام واغد يا أنيس إلى امرأة هذا فإن اعترفت فارجمها قال: فغدا عليها فاعترفت فرجمها " رواه الجماعة.','2619','2598','0');
INSERT INTO [t22592] VALUES (2610,'(٢٣٤٨) - (عن ابن عباس مرفوعا: " من وقع على بهيمة فاقتلوه واقتلوها " رواه أحمد وأبو داود والترمذى وضعفه الطحاوى.','2619','2598','0');
INSERT INTO [t22592] VALUES (2611,'(٢٣٤٩) - (حديث أبى موسى مرفوعا: " إذا أتى الرجل الرجل فهما زانيان ".','2622','2598','0');
INSERT INTO [t22592] VALUES (2612,'(٢٣٥٠) - (وعن ابن عباس مرفوعا: " من وجدتموه يعمل عمل قوم لوط فاقتلوا الفاعل والمفعول به " رواه الخمسة إلا النسائى.','2622','2598','0');
INSERT INTO [t22592] VALUES (2613,'(٢٣٥١) - (روى البراء قال: " لقيت عمى ومعه الراية فقلت: أين تريد؟ قال: بعثنى رسول الله صلى الله عليه وسلم إلى رجل تزوج امرأة أبيه بعده أن أضرب عنقه وآخذ ماله " حسنه الترمذى.','2624','2598','0');
INSERT INTO [t22592] VALUES (2614,'(٢٣٥٢) - (روى ابن ماجه بإسناده مرفوعا: " من وقع على ذات محرم فاقتلوه ".','2628','2598','0');
INSERT INTO [t22592] VALUES (2615,'(٢٣٥٣) - (حديث ابن مسعود أن رجلا جاء إلى النبى صلى الله عليه وسلم فقال: إنى وجدت امرأة فى البستان فأصبت منها كل شىء غير أنى لم أنكحها فافعل بى ما شئت , فقرأ عليه النبى صلى الله عليه وسلم (وأقم الصلاة طرفى النهار وزلفا من الليل إن الحسنات يذهبن السيئات) " رواه النسائى.','2629','2598','0');
INSERT INTO [t22592] VALUES (2616,'(٢٣٥٤) - (وعن أبى هريرة فى حديث الأسلمى " فأقبل عليه فى الخامسة قال: " أنكتها؟ قال: نعم. قال: كما يغيب المرود فى المكحلة , والرشاء فى البئر؟ قال: نعم. وفى آخره: فأمر به فرجم " رواه أبو داود والدارقطنى.','2630','2598','0');
INSERT INTO [t22592] VALUES (2617,'(٢٣٥٥) - (حديث عائشة مرفوعا: " ادرءوا الحدود عن المسلمين ما استطعتم , فإن كان له مخرج فخلوا سبيله فإن الإمام أن يخطىء فى العفو خير من أن يخطىء فى العقوبة " رواه الترمذى.','2631','2598','0');
INSERT INTO [t22592] VALUES (2618,'(٢٣٥٦) - (عن أبى هريرة مرفوعا: ادفعوا الحدود ما وجدتم لها مدفعا " رواه ابن ماجه.','2632','2598','0');
INSERT INTO [t22592] VALUES (2619,'(٢٣٥٧) - (حديث " أن ماعز بن مالك اعترف عند النبى صلى الله عليه وسلم الأولى والثانية والثالثة فرده فقيل له: إنك إن اعترفت الرابعة رجمك فاعترف الرابعة فحبسه , ثم سأل عنه فقالوا: لا نعلم إلا خيرا فأمر به فرجم " روى من طرق عن ابن عباس وجابر وبريدة وأبى بكر الصديق.','2632','2598','0');
INSERT INTO [t22592] VALUES (2620,'(٢٣٥٨) - (حديث: " أن الغامدية أقرت عنده بذلك فى مجالس " رواه مسلم.','2633','2598','0');
INSERT INTO [t22592] VALUES (2621,'(٢٣٥٩) - (قول بريدة: " كنا أصحاب محمد صلى الله عليه وسلم نتحدث أن الغامدية وماعزا لو رجعا بعد اعترافهما أو قال: لو لم يرجعا بعد اعترافهما لم يطلبهما وإنما رجمهم بعد الرابعة " رواه أبو داود.','2633','2598','0');
INSERT INTO [t22592] VALUES (2622,'(٢٣٦٠) - (فى حديث أبى هريرة: " فذكروا ذلك لرسول الله صلى الله عليه وسلم أى أن ماعزا فر حين وجد مس الحجارة ومس الموت , فقال رسول الله صلى الله عليه وسلم: هلا تركتموه؟ " رواه أحمد وابن ماجه والترمذى وحسنه.','2634','2598','0');
INSERT INTO [t22592] VALUES (2623,'(٢٣٦١) - (أثر: " أن عمر رضى الله عنه لما شهد عنده أبو بكرة , ونافع وشبل بن معبد على المغيرة بن شعبة بالزنى حدهم حد القذف , لما تخلف الرابع زياد فلم يشهد ".','2634','2598','0');
INSERT INTO [t22592] VALUES (2624,'(٢٣٦٢) - (أثر: " إن عمر رضى الله عنه أتى بامرأة ليس لها زوج قد حملت فسألها عمر فقالت: إنى امرأة ثقيلة الرأس وقع على رجل وأنا نائمة فما استيقظت حتى فرغ , فدرأ عنها الحد " رواه سعيد.','2636','2598','0');
INSERT INTO [t22592] VALUES (2625,'(٢٣٦٣) - (وعن على وابن عباس: " إذا كان فى الحد لعل وعسى فهو معطل ". [١]','2637','2598','0');
INSERT INTO [t22592] VALUES (2626,'(٢٣٦٤) - (قول [عمر] " أو كان الحبل أو الاعتراف ".','2637','2598','0');
INSERT INTO [t22592] VALUES (2627,'باب حد القذف','2638','2569','0');
INSERT INTO [t22592] VALUES (2628,'(٢٣٦٥) - (قوله صلى الله عليه وسلم: " اجتنبوا السبع الموبقات , قالوا: وما هن يا رسول الله؟ قال: الشرك بالله , والسحر , وقتل النفس التى حرم الله إلا بالحق , وأكل الربا , وأكل مال اليتم , والتولى يوم الزحف , وقذف المحصنات الغافلات المؤمنات " متفق عليه.','2638','2627','0');
INSERT INTO [t22592] VALUES (2629,'فصل','2638','2569','0');
INSERT INTO [t22592] VALUES (2630,'(٢٣٦٦) - (روى عنه صلى الله عليه وسلم أنه قال: " أيعجز أحدكم أن يكون كأبى ضمضم كان إذا أصبح يقول: تصدقت بعرضى ". رواه ابن السنى.','2638','2629','0');
INSERT INTO [t22592] VALUES (2631,'(٢٣٦٧) - (حديث: " أيما امرأة أدخلت على قوم من ليس منهم , ليست من الله فى شىء , ولن يدخلها الله جنته , وأيما رجل جحد ولده وهو ينظر إليه احتجب الله منه وفضحه على رءوس الأولين والآخرين " رواه أبو داود.','2640','2629','0');
INSERT INTO [t22592] VALUES (2632,'فصل','2641','2569','0');
INSERT INTO [t22592] VALUES (2633,'(٢٣٦٨) - (حديث الأشعث بن قيس مرفوعا: " لا أوتى برجل يقول: إن كنانة ليست من قريش إلا جلدته ".','2641','2632','0');
INSERT INTO [t22592] VALUES (2634,'(٢٣٦٩) - (وروى عن ابن مسعود أنه قال: " لا حد إلا فى اثنتين: قذف محصنة , أو نفى رجل عن أبيه ".','2642','2632','0');
INSERT INTO [t22592] VALUES (2635,'(٢٣٧٠) - (حديث: " العينان تزنيان وزناهما النظر , واليدان تزنيان وزناهما البطش , والرجلان تزنيان وزناهما المشى , ويصدق ذلك الفرج أو يكذبه ".','2642','2632','0');
INSERT INTO [t22592] VALUES (2636,'(٢٣٧١) - (روى سالم عن أبيه أن رجلا قال: " ما أنا بزان ولا أمى بزانية , فجلده عمر الحد ".','2645','2632','0');
INSERT INTO [t22592] VALUES (2637,'(٢٣٧٢) - (روى الأثرم أن عثمان جلد رجلا قال لآخر: " يا ابن شامة الوذر: يعرض بزنى أمه ".','2645','2632','0');
INSERT INTO [t22592] VALUES (2638,'باب حد المسكر','2646','2569','0');
INSERT INTO [t22592] VALUES (2639,'(٢٣٧٣) - (عن ابن عمر مرفوعا: " كل مسكر خمر وكل خمر حرام " رواه مسلم.','2646','2638','0');
INSERT INTO [t22592] VALUES (2640,'(٢٣٧٤) - (قال عمر: " نزل تحريم الخمر وهى من العنب والتمر والعسل والحنطة والشعير والخمرة ما خامر العقل " متفق عليه.','2647','2638','0');
INSERT INTO [t22592] VALUES (2641,'(٢٣٧٥) - (عن ابن عمر مرفوعا: " ما أسكر كثيره فقليله حرام " رواه أحمد وابن ماجه والدارقطنى.','2648','2638','0');
INSERT INTO [t22592] VALUES (2642,'(٢٣٧٦) - (عن عائشة مرفوعا: " ما أسكر الفرق منه فملء الكف من حرام " رواه أبو داود.','2650','2638','0');
INSERT INTO [t22592] VALUES (2643,'(٢٣٧٧) - (حديث: " أن عمر استشار الناس فى حد الخمر فقال عبد الرحمن: اجعله كأخف الحدود , ثمانين , فضرب عمر ثمانين , وكتب به إلى خالد وأبى عبيدة بالشام " رواه أحمد ومسلم.','2651','2638','0');
INSERT INTO [t22592] VALUES (2644,'(٢٣٧٨) - (عن على أنه قال فى المشورة: " إذا سكر هذى , وإذا هذى افترى , فحدوه حد المفترى " رواه الجوزجانى والدارقطنى.','2652','2638','0');
INSERT INTO [t22592] VALUES (2645,'(٢٣٧٩) - (روى عن ابن شهاب أنه سئل عن حد العبد فى الخمر فقال: " بلغنى أن عليه نصف حد الحر فى الخمر وأن عمر وعثمان وعبد الله بن عمر قد جلدوا عبيدهم نصف الحد فى الخمر " رواه مالك فى " الموطأ ".','2654','2638','0');
INSERT INTO [t22592] VALUES (2646,'(٢٣٨٠) - (روى حصين بن المنذر: " أن عليا جلد الوليد بن عقبة فى الخمر أربعين ثم قال: جلد النبى صلى الله عليه وسلم أربعين وأبو بكر أربعين وعمر ثمانين وكل سنة وهذا أحب إلى " رواه مسلم.','2654','2638','0');
INSERT INTO [t22592] VALUES (2647,'(٢٣٨١) - (عن على قال: ما كنت لأقيم حدا على أحد فيموت وأجد فى نفسى منه شيئا إلا صاحب الخمر فإنه لو مات وديته وذلك أن رسول الله صلى الله عليه وسلم لم يسنه " متفق عليه.','2655','2638','0');
INSERT INTO [t22592] VALUES (2648,'(٢٣٨٢) - (حديث: " عفى لأمتى عن الخطأ والنسيان وما استكرهوا عليه ".','2655','2638','0');
INSERT INTO [t22592] VALUES (2649,'(٢٣٨٣) - (ثبت عن عمر أنه قال: " لا حد إلا على من علمه ".','2655','2638','0');
INSERT INTO [t22592] VALUES (2650,'(٢٣٨٤) - (حديث: " من تشبه بقوم فهو منهم ".','2655','2638','0');
INSERT INTO [t22592] VALUES (2651,'(٢٣٨٥) - (حديث ابن عمر مرفوعا: " لعن الله الخمر وشاربها وساقيها وبائعها ومبتاعها وعاصرها ومعتصرها وحاملها والمحمولة إليه " رواه أبو داود.','2656','2638','0');
INSERT INTO [t22592] VALUES (2652,'(٢٣٨٦) - (حديث: " اشربوا العصير ثلاثا ما لم يغل " رواه الشالنجى.','2656','2638','0');
INSERT INTO [t22592] VALUES (2653,'(٢٣٨٧) - (عن ابن عمر فى العصير: " اشربه ما لم يأخذه شيطان , قيل: وفى كم يأخذه شيطانه؟ قال: ثلاثة " حكاه أحمد وغيره.','2656','2638','0');
INSERT INTO [t22592] VALUES (2654,'(٢٣٨٨) - (عن ابن عباس: " أن النبى صلى الله عليه وسلم كان ينبذ له الزبيب','2656','2638','0');
INSERT INTO [t22592] VALUES (2655,'(٢٣٨٩) - (عن أبى هريرة قال: " علمت [أن] رسول الله صلى الله عليه وسلم كان يصوم فتحينت فطره بنبيذ صنعته فى دباء ثم أتيته فإذا هو ينش فقال: اضرب بهذا الحائظ فإن هذا شراب من لم يؤمن بالله واليوم الآخر " رواه أبو داود والنسائى.','2657','2638','0');
INSERT INTO [t22592] VALUES (2656,'(٢٣٩٠) - (أثر: " إن أبا موسى كان يشرب من الطلاء (١) ما ذهب ثلثاه وبقى ثلثه " رواه النسائى.','2658','2638','0');
INSERT INTO [t22592] VALUES (2657,'(٢٣٩١) - (وله مثله عن عمر وأبى الدرداء ".','2658','2638','0');
INSERT INTO [t22592] VALUES (2658,'(٢٣٩٢) - (قال البخارى: " رأى عمر وأبو عبيدة ومعاذ شرب الطلاء على الثلث , وشرب البراء وأبو جحيفة على النصف ".','2659','2638','0');
INSERT INTO [t22592] VALUES (2659,'باب التعزير','2660','2569','0');
INSERT INTO [t22592] VALUES (2660,'(٢٣٩٣) - (روى عن على رضى الله عنه أنه سئل عن قول الرجل للرجل: يا فاسق , يا خبيث؟ قال: " هن فواحش فيهن تعزير وليس فيهن حد ".','2660','2659','0');
INSERT INTO [t22592] VALUES (2661,'(٢٣٩٤) - (روى ابن مسعود أن رجلا أتى النبى صلى الله عليه وسلم فقال: " إنى لقيت امرأة فأصبت منها ما دون أن أطأها , فقال: أصليت معنا قال: نعم , فتلا عليه: (إن الحسنات يذهبن السيئات) " متفق عليه.','2661','2659','0');
INSERT INTO [t22592] VALUES (2662,'(٢٣٩٥) - (حديث: " أنت ومالك لأبيك ".','2661','2659','0');
INSERT INTO [t22592] VALUES (2663,'(٢٣٩٦) - (حديث أبى بردة مرفوعا: " لا يجلد أحد فوق عشرة أسواط إلا فى حد من حدود الله " متفق عليه.','2661','2659','0');
INSERT INTO [t22592] VALUES (2664,'(٢٣٩٧) - (حديث: " أنه صلى الله عليه وسلم حبس رجلا فى تهمة ثم خلى عنه " رواه أحمد وأبو داود.','2661','2659','0');
INSERT INTO [t22592] VALUES (2665,'(٢٣٩٨) - (روى سعيد بن المسيب عن عمر: " فى أمة بين رجلين وطئها أحدهما يجلد الحد إلا سوطا " رواه الأثرم واحتج به أحمد.','2662','2659','0');
INSERT INTO [t22592] VALUES (2666,'(٢٣٩٩) - (روى أحمد: " أن عليا رضى الله عنه أتى بالنجاشى وقد شرب خمرا فى رمضان فجلده الحد وعشرين سوطا لفطره فى رمضان ".','2663','2659','0');
INSERT INTO [t22592] VALUES (2667,'(٢٤٠٠) - (عن عمر: " فى شاهد الزور يضرب ظهره ويحلق رأسه ويسخم وجهه ويطاف به ويطال حبسه ".','2664','2659','0');
INSERT INTO [t22592] VALUES (2668,'(٢٤٠١) - (حديث رواه الحسن بن عرفة فى " جزئه (١) ": " فى تحريم الاستمناء باليد ".','2664','2659','0');
INSERT INTO [t22592] VALUES (2669,'(٢٤٠٢) - (عن عائشة مرفوعا: " تقطع اليد فى ربع دينار فصاعدا " متفق عليه.','2666','2659','0');
INSERT INTO [t22592] VALUES (2670,'(٢٤٠٣) - (حديث جابر مرفوعا: " ليس على المنتهب قطع " رواه أبو داود.','2668','2659','0');
INSERT INTO [t22592] VALUES (2671,'(٢٤٠٤) - (حديث: " ليس على الخائن والمختلس قطع " رواه أبو داود والترمذى.','2671','2659','0');
INSERT INTO [t22592] VALUES (2672,'(٢٤٠٥) - (حديث ابن عمر: " كانت مخزومية تستعير المتاع وتجحده','2671','2659','0');
INSERT INTO [t22592] VALUES (2673,'(٢٤٠٦) - (قول عمر: " لاحد إلا على من علمه ".','2673','2659','0');
INSERT INTO [t22592] VALUES (2674,'(٢٤٠٧) - (حديث هشام بن عروة عن أبيه عن عائشة: " أن رسول الله صلى الله عليه وسلم أتى برجل يسرق الصبيان ثم يخرج بهم فيبيعهم فى أرض أخرى فأمر بيده فقطعت " رواه الدارقطنى.','2673','2659','0');
INSERT INTO [t22592] VALUES (2675,'(٢٤٠٨) - (حديث عائشة مرفوعا: " لا تقطع اليد إلا فى ربع دينار فصاعدا " رواه أحمد ومسلم والنسائى وابن ماجه.','2673','2659','0');
INSERT INTO [t22592] VALUES (2676,'(٢٤٠٩) - (وعن عائشة مرفوعا: " اقطعوا فى ربع دينار ولا تقطعوا فيما هو أدنى من ذلك ـ وكان ربع الدنيار يومئذ ثلاثة دراهم والدينار اثنا عشر درهما ـ " رواه أحمد','2674','2659','0');
INSERT INTO [t22592] VALUES (2677,'(٢٤١٠) - (حديث أبى هريرة: " لعن الله السارق يسرق الحبل فتقطع يده ويسرق البيضة فتقطع يده " متفق عليه.','2674','2659','0');
INSERT INTO [t22592] VALUES (2678,'(٢٤١١) - (حديث ابن عمر أن النبى صلى الله عليه وسلم: " قطع يد السارق سرق ترسا (١) من صنعة النساء ثمنه ثلاثة دراهم " رواه أحمد وأبو داود والنسائى.','2674','2659','0');
INSERT INTO [t22592] VALUES (2679,'(٢٤١٢) - (وعنه أيضا مرفوعا: " قطع فى مجن قيمته ثلاثا دراهم " رواه الجماعة.','2675','2659','0');
INSERT INTO [t22592] VALUES (2680,'(٢٤١٣) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن رجلا من مزينة سأل النبى صلى الله عليه وسلم عن الثمار فقال: ما أخذ فى أكمامه (١) واحتمل ففيه قيمته ومثله معه وما أخذ من أجرانه (٢) ففيه القطع إذا بلغ ثمن المجن " رواه أبو داود وابن ماجه , وفى لفظ: " ومن سرق منه شيئا بعد أن يؤويه الجرين فبلغ ثمن المجن فعليه القطع ". رواه النسائى وزاد: " وما لم يبلغ ثمن المجن ففيه غرامة مثليه وجلدات نكال ".','2675','2659','0');
INSERT INTO [t22592] VALUES (2681,'(٢٤١٤) - (عن رافع بن خديج مرفوعا: " لا قطع فى ثمر ولا كثر "رواه الخمسة.','2678','2659','0');
INSERT INTO [t22592] VALUES (2682,'(٢٤١٥) - (أن صفوان بن أمية نام فى المسجد وتوسد رداء فأخذ من تحت رأسه , فأمر النبى صلى الله عليه وسلم أن يقطع سارقه " الحديث رواه الخمسة إلا الترمذى.','2680','2659','0');
INSERT INTO [t22592] VALUES (2683,'(٢٤١٦) - (قول عائشة رضى الله عنها: " سارق أمواتنا كسارق أحيائنا ".','2680','2659','0');
INSERT INTO [t22592] VALUES (2684,'(٢٣١٧) - (روى عن ابن الزبير: " أنه قطع نباشا ".','2680','2659','0');
INSERT INTO [t22592] VALUES (2685,'(٢٤١٨) - (حديث: " أنت ومالك لأبيك ".','2681','2659','0');
INSERT INTO [t22592] VALUES (2686,'(٢٤١٩) - (روى مالك أن عبد الله بن عمرو الحضرمى قال لعمر: إن عبدى سرق مرآة امرأتى ثمنها ستون درهما فقال: أرسله لا قطع عليه غلامك أخذ متاعكم ".','2681','2659','0');
INSERT INTO [t22592] VALUES (2687,'(٢٤٢١) - (وقال ابن مسعود: " لا قطع , مالك سرق مالك ".','2682','2659','0');
INSERT INTO [t22592] VALUES (2688,'(٢٤٢٢) - (قال عمر وابن مسعود: " من سرق من بيت المال فلا قطع , ما من أحد إلا وله فى هذا المال حق ".','2682','2659','0');
INSERT INTO [t22592] VALUES (2689,'(٢٤٢٣) - (روى سعيد عن على: " ليس على من سرق من بيت','2682','2659','0');
INSERT INTO [t22592] VALUES (2690,'(٢٤٢٤) - (روى ابن ماجه عن ابن عباس: " أن عبدا من رقيق الخمس سرق من الخمس فرفع إلى النبى صلى الله عليه وسلم فلم يقطعه وقال: مال الله سرق بعضه بعضا ".','2683','2659','0');
INSERT INTO [t22592] VALUES (2691,'(٢٤٢٥) - (عن القاسم بن عبد الرحمن أن عليا رضى الله عنه أتاه رجل فقال: " إنى سرقت فطرده ثم عاد مرة أخرى فقال: إنى سرقت فأمر به أن يقطع " رواه الجوزجانى وفى لفظ: " لا يقطع السارق حتى يشهد على نفسه مرتين " حكاه أحمد فى رواية مهنا.','2684','2659','0');
INSERT INTO [t22592] VALUES (2692,'(٢٤٢٦) - (حديث أبى أمية المخزومى: " أن النبى صلى الله عليه وسلم أتى بلص قد اعترف فقال: ما إخالك سرقت. قال: بلى فأعاد عليه مرتين أو','2684','2659','0');
INSERT INTO [t22592] VALUES (2693,'(٢٤٢٧) - (روى عن عمر رضى الله عنه: " أنه أتى برجل فقال: أسرقت؟ , قل: لا , فقال: لا , فتركه ".','2685','2659','0');
INSERT INTO [t22592] VALUES (2694,'(٢٤٢٨) - (قول عمر رضى الله عنه: " لا قطع فى عام سنة ".','2686','2659','0');
INSERT INTO [t22592] VALUES (2695,'(٢٤٢٩) - (فى قراءة عبد الله بن مسعود: " فاقطعوا أيمانهما ".','2687','2659','0');
INSERT INTO [t22592] VALUES (2696,'(٢٤٣٠) - (روي عن أبي بكر وعمر رضي الله عنهما أنهما قالا: " إذا سرق السارق فاقطعوا يمينه من مفصل الكوع ") .','2687','2659','0');
INSERT INTO [t22592] VALUES (2697,'(٢٤٣١) - (حديث " اقطعوه واحسموه " رواه الدارقطني. وقال ابن المنذر: في إسناده مقال) .','2689','2659','0');
INSERT INTO [t22592] VALUES (2698,'(٢٤٣٢) - (حديث فضالة بن عبيد " أن النبي صلى الله عليه وسلم أتي بسارق فقطعت يده ثم أمر فعلقت في عنقه " رواه الخمسة إلا أحمد , وفي إسناده الحجاج بن أرطاة , وهو ضعيف) .','2690','2659','0');
INSERT INTO [t22592] VALUES (2699,'(٢٤٣٣) - (أثر: " أن عليا رضى الله عنه فعل ذلك بالذى قطعه ".','2691','2659','0');
INSERT INTO [t22592] VALUES (2700,'(٢٤٣٤) - (حديث أبي هريرة مرفوعا في السارق " إن سرق','2691','2659','0');
INSERT INTO [t22592] VALUES (2701,'(٢٤٣٥) - (روي عن علي أنه كان يقطع من شطر القدم ويترك له عقبا يمشي عليه) .','2695','2659','0');
INSERT INTO [t22592] VALUES (2702,'(٢٤٣٦) - (أثر " أتي عمر رضي الله عنه برجل أقطع الزند والرجل قد سرق فأمر به عمر أن تقطع رجله فقال علي: إنما قال الله تعالى * (إنما جزاء الذين يحاربون الله ورسوله....) * الآية , وقد قطعت يد هذا ورجله فلا ينبغي أن تقطع رجله فتدعه ليس له قائمة يمشي عليها , إما أن تعزره وإما أن تستودعه السجن فاستودعه السجن " , رواه سعيد) .','2695','2659','0');
INSERT INTO [t22592] VALUES (2703,'(٢٤٣٧) - (عن سعيد المقبرى قال: " حضرت على بن أبى طالب أتى برجل مقطوع اليد والرجل قد سرق فقال لأصحابه: ما ترون فى هذا؟ قالوا: اقطعه يا أمير المؤمنين قال: قتلته إذا وما عليه القتل , بأى شىء يأكل الطعام بأى شىء يتوضأ للصلاة بأى شىء يغتسل من جنابته؟ بأى شىء يقوم لحاجته؟ فرده إلى السجن أياما ثم أخرجه فاستشار أصحابه فقالوا مثل قولهم الأول , وقال لهم مثل ما قال أولا فجلده جلدا شديدا ثم أرسله " رواه سعيد.','2696','2659','0');
INSERT INTO [t22592] VALUES (2704,'(٢٤٣٨) - (حديث أبى هريرة: " من سرق فاقطعوا يده ثم إن سرق فاقطعوا رجله ".','2696','2659','0');
INSERT INTO [t22592] VALUES (2705,'(٢٤٣٩) - (أثر " أن أبا بكر وعمر قطعا اليد اليسرى في المرة الثالثة ") .','2697','2659','0');
INSERT INTO [t22592] VALUES (2706,'باب حد قطاع الطريق','2698','2569','0');
INSERT INTO [t22592] VALUES (2707,'(٢٤٤٠) - (قال ابن عباس نزلت {إنما جزاء الذين يحاربون الله ورسوله ويسعون} في قطاع الطريق من المسلمين " وحكي: في المرتدين , وقال أنس: نزلت في العرنيين الذين استاقوا إبل الصدقة وارتدوا) .','2698','2706','0');
INSERT INTO [t22592] VALUES (2708,'(٢٤٤١) - (" وحكى عن ابن عمر أنها نزلت فى المرتدين ".','2699','2706','0');
INSERT INTO [t22592] VALUES (2709,'(٢٤٤٢) - (وقال أنس: " نزلت فى العرنيين الذين استاقوا إبل الصدقة وارتدوا ".','2700','2706','0');
INSERT INTO [t22592] VALUES (2710,'(٢٤٤٣) - (روى الشافعى بإسناده عن ابن عباس: " إذا قتلوا وأخذوا المال: قتلوا وصلبوا. وإذا قتلوا ولم يأخذوا المال. قتلوا ولم يصلبوا. وإذا أخذوا المال ولم يقتلوا: قطعت أيديهم وأرجلهم من خلاف , وإذا أخافوا السبيل ولم يأخذوا مالا: نفوا من الأرض ".','2700','2706','0');
INSERT INTO [t22592] VALUES (2711,'(٢٤٤٤) - (روى أبو داود بإسناده عن ابن عباس قال: " وادع رسول الله أبا برزة الأسلمى فجاء ناس يريدون الإسلام فقطع عليهم أصحابه فنزل جبريل عليه السلام بالحد فيهم أن من قتل وأخذ المال قتل وصلب , ومن قتل ولم يأخذ المال قتل. ومن أخذ المال ولم يقتل قطعت يده ورجله من خلاف ".','2700','2706','0');
INSERT INTO [t22592] VALUES (2712,'(٢٤٤٥) - (قال ابن عباس: " نفيهم إذا هربوا أن يطلبوا حتى يؤخذوا فتقام عليهم الحدود ".','2700','2706','0');
INSERT INTO [t22592] VALUES (2713,'فصل في دفع الأذى','2701','2569','0');
INSERT INTO [t22592] VALUES (2714,'(٢٤٤٦) - (حديث أبى هريرة: " جاء رجل فقال: يا رسول الله! أرأيت إن جاء رجل يريد أخذ مالى؟ قال: فلا تعطه قال: أرأيت أن قاتلنى قال: قاتله. قال: أرأيت إن قتلنى؟ قال: فأنت شهيد. قال: أرأيت إن قتلته؟ قال: هو فى النار " رواه أحمد ومسلم. وفى لفظ لأحمد: " أنه قال له أولا: أنشده الله. قال: فإنى أبى؟ قال: قاتله ".','2701','2713','0');
INSERT INTO [t22592] VALUES (2715,'(٢٤٤٧) - (وعن ابن عمر مرفوعا: " من أريد ماله بغير حق فقاتل فقتل فهو شهيد " رواه الخلال بإسناده.','2702','2713','0');
INSERT INTO [t22592] VALUES (2716,'(٢٤٤٨) - (قول أنس: " فزع أهل المدينة ذات ليلة فانطلق أناس قبل الصوت فتلقاهم النبى صلى الله عليه وسلم راجعا وقد سبقهم إلى الصوت وهو على فرس لأبى طلحة عرى فى عنقه السيف وهو يقول لم تراعوا لم تراعوا " متفق عليه.','2702','2713','0');
INSERT INTO [t22592] VALUES (2717,'(٢٤٤٩) - (حديث: " انصر أخاك ظالما أو مظلوما ".','2703','2713','0');
INSERT INTO [t22592] VALUES (2718,'(٢٤٥٠) - (روى أحمد وغيره: " النهى عن خذلان المسلم والأمر بنصر المظلوم ".','2704','2713','0');
INSERT INTO [t22592] VALUES (2719,'(٢٤٥١) - (روى عن النبى صلى الله عليه وسلم أنه قال فى الفتنة: " اجلس فى بيتك فإن خفت أن يبهرك شعاع السيف فغط وجهك " وفى لفظ: " فكن كخير ابنى آدم " وفى لفظ: " فكن عبد الله المقتول ولا تكن عبد الله القاتل ".','2706','2713','0');
INSERT INTO [t22592] VALUES (2720,'باب قتال البغاة','2711','2569','0');
INSERT INTO [t22592] VALUES (2721,'(٢٤٥٢) - (حديث: " من أتاكم وأمركم جميع على رجل واحد يريد أن يشق عصاكم ويفرق جماعتكم فاقتلوه ".','2711','2720','0');
INSERT INTO [t22592] VALUES (2722,'(٢٤٥٣) - (عن ابن عباس مرفوعا: " من رأى من أميره شيئا يكرهه فليصبر عليه فإنه من فارق الجماعة شبرا فميتته جاهلية " متفق عليه.','2711','2720','0');
INSERT INTO [t22592] VALUES (2723,'(٢٤٥٤) - (قال الشيخ تقى الدين: " قد أوجب النبى صلى الله عليه وسلم تأمير الواحد فى الاجتماع القليل العارض فى السفر , وهو تنبيه على أنواع الاجتماع ".','2712','2720','0');
INSERT INTO [t22592] VALUES (2724,'(٢٤٥٥) - (قوله صلى الله عليه وسلم فى حديث العرباض وغيره: " والسمع والطاعة وإن تأمر عليكم عبد ... " الحديث.','2713','2720','0');
INSERT INTO [t22592] VALUES (2725,'(٢٤٥٦) - (حديث: " ما أفلح قوم ولوا أمرهم امرأة " رواه البخارى.','2715','2720','0');
INSERT INTO [t22592] VALUES (2726,'(٢٤٥٧) - (حديث: " إلا أن تروا كفرا بواحا عندكم فيه من الله برهان ".','2716','2720','0');
INSERT INTO [t22592] VALUES (2727,'(٢٤٥٨) - (أن عليا رضى الله عنه: " راسل أهل البصرة يوم الجمل قبل الوقعة , وأمر أصحابه أن لا يبدءوهم بقتال وقال: إن هذا يوم من (فلح فيه فلح) [١] يوم القيامة ".','2716','2720','0');
INSERT INTO [t22592] VALUES (2728,'(٢٤٥٩) - (روى عبد الله بن شداد: " أن عليا رضى الله عنه لما اعتزله الحرورية بعث إليهم عبد الله بن عباس فواضعوه كتاب الله ثلاثة أيام فرجع منهم أربعة آلاف ".','2717','2720','0');
INSERT INTO [t22592] VALUES (2729,'(٢٤٦١) - (أثر أن عليا رضى الله عنه قال: " إياكم وصاحب البرنس " يعنى محمد بن طلحة السجاد. [١]','2719','2720','0');
INSERT INTO [t22592] VALUES (2730,'(٢٤٦١) - (قول مروان صرخ صارخ لعلى يوم الجمل: " لا يقتلن مدبر , ولا يذفف على جريج , ولا يهتك ستر , ومن أغلق بابه فهو آمن ومن ألقى السلاح فهو آمن " رواه سعيد. وعن عمار نحوه.','2719','2720','0');
INSERT INTO [t22592] VALUES (2731,'(٢٤٦٢) - (روى ابن مسعود أن النبى صلى الله عليه وسلم قال: يا ابن أم عبد ما حكم من بغى على أمتى؟ فقلت: الله ورسوله أعلم , فقال: لا يقتل مدبرهم ولا يجاز على جريحهم , ولا يقتل أسيرهم , ولا يقسم فيئهم ".','2720','2720','0');
INSERT INTO [t22592] VALUES (2732,'(٢٤٦٣) - (عن أبى أمامة قال: " شهدت صفين , فكانوا لا يجزون على جريح , ولا يطلبون موليا , ولا يسلبون قتيلا ".','2720','2720','0');
INSERT INTO [t22592] VALUES (2733,'(٢٤٦٤) - (عن على أنه قال يوم الجمل: " من عرف شيئا من ماله مع أحد فليأخذه فعرف بعضهم قدرا مع أصحاب على وهو يطبخ فيها فسأله إمهاله حتى ينطبخ الطبيخ , فأبى وكبه وأخذها ".','2721','2720','0');
INSERT INTO [t22592] VALUES (2734,'(٢٤٦٥) - (قال الزهرى: " هاجت الفتنة وأصحاب رسول الله صلى الله عليه وسلم متوافرون , وفيهم البدريون فأجمعوا أنه لا يقاد أحد , ولا يؤخذ مال على تأويل القرآن إلا ما وجد بعنيه " ذكره أحمد فى رواية الأثرم.','2722','2720','0');
INSERT INTO [t22592] VALUES (2735,'(٢٤٦٦) - (أثر: " أن ابن عمر وسلمة بن الأكوع [كان] يأتيهم ساعى نجدة الحرورى فيدفعون إليه زكاتهم ".','2722','2720','0');
INSERT INTO [t22592] VALUES (2736,'(٢٤٦٧) - (أثر: " أن عليا سمع رجلا يقول: لا حكم إلا الله تعريضا بالرد عليه فى التحكيم فقال على: كلمة حق أريد بها باطل , ثم قال: لكم علينا ثلاث: لا نمنعكم مساجد الله أن تذكروا فيها اسم الله , ولا نمنعكم الفىء ما دامت أيديكم معنا ولا نبدؤكم بقتال ".','2723','2720','0');
INSERT INTO [t22592] VALUES (2737,'(٢٤٦٨) - (روى: " أن عليا كان فى صلاة الفجر فناداه رجل من الخوارج (لئن أشركت ليحبطن عملك) فأجابه على رضى الله عنه (فاصبر إن وعد الله حق) ولم يعزره ".','2724','2720','0');
INSERT INTO [t22592] VALUES (2738,'(٢٤٦٩) - (أن عليا قال فى الحرورية: " لا تبدءوهم بقتال ".','2725','2720','0');
INSERT INTO [t22592] VALUES (2739,'(٢٤٧٠) - (حديث أبى سعيد مرفوعا وفيه: " ... يمرقون من الدين كما يمرق السهم من الرمية فأينما لقيتموهم فاقتلوهم فإن قتلهم أجر لمن قتلهم يوم القيامة " رواه البخارى وفى لفظ: " لا يجاوز إيمانهم حناجرهم , لئن أدركتهم لأقتلنهم قتل عاد ".','2726','2720','0');
INSERT INTO [t22592] VALUES (2740,'باب حكم المرتد','2730','2569','0');
INSERT INTO [t22592] VALUES (2741,'(٢٤٧١) - (حديث ابن عباس مرفوعا: " من بدل دينه فاقتلوه " رواه الجماعة إلا مسلما.','2730','2740','0');
INSERT INTO [t22592] VALUES (2742,'(٢٤٧٢) - (روى الدارقطنى: " أن امرأة يقال لها أم مروان ارتدت','2731','2740','0');
INSERT INTO [t22592] VALUES (2743,'(٢٤٧٣) - (حديث: " لا نبى بعدى " (٢/٤٠٤) .','2733','2740','0');
INSERT INTO [t22592] VALUES (2744,'(٢٤٧٤) - (روى مالك والشافعى: " أنه قدم على عمر رجل من قبل أبى موسى فقال له عمر: هل كان من مغربة خبر؟ قال: نعم , رجل كفر بعد إسلامه فقال: ما فعلتم به؟ قال: قربناه فضربنا عنقه. قال عمر: فهلا حبستموه ثلاثا وأطعمتموه كل يوم رغيفا واستتبتموه لعله يتوب أو يراجع أمر الله؟ ! اللهم إنى لم أحضر ولم أرض إذ بلغنى ".','2736','2740','0');
INSERT INTO [t22592] VALUES (2745,'(٢٤٧٥) - (عن أنس مرفوعا: " أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله وأن محمدا رسول الله فإذا قالوها عصموا منى دماءهم وأموالهم إلا بحقها ".','2737','2740','0');
INSERT INTO [t22592] VALUES (2746,'(٢٤٧٦) - (حديث: " إن الله كتب الاحسان على كل شىء , فإذا قتلتم فأحسنوا القتلة ".','2738','2740','0');
INSERT INTO [t22592] VALUES (2747,'(٢٤٧٧) - (حديث: " من بدل دينه فاقتلوه , ولا تعذبوا بعذاب الله " رواه البخارى وأبو داود.','2738','2740','0');
INSERT INTO [t22592] VALUES (2748,'(٢٤٧٨) - (حديث: " أن عليا رضى الله عنه أسلم وهو ابن ثمان سنين " رواه البخارى فى " تاريخه ".','2738','2740','0');
INSERT INTO [t22592] VALUES (2749,'(٢٤٧٩) - (حديث ابن مسعود: " أن النبى صلى الله عليه وسلم دخل الكنيسة فإذا هو بيهودى يقرأ عليهم التوراة فقرأ حتى إذا أتى على صفة النبى صلى الله عليه وسلم وأمته فقال: هذه صفتك وصفة أمتك , أشهد أن لا إله إلا الله وأنك رسول الله فقال رسول الله صلى الله عليه وسلم: لوا أخاكم " رواه أحمد.','2740','2740','0');
INSERT INTO [t22592] VALUES (2750,'(٢٤٨٠) - (عن أنس أن يهوديا قال للنبى صلى الله عليه وسلم: " أشهد أنك رسول الله , ثم مات فقال رسول الله صلى الله عليه وسلم: صلوا على صاحبكم ".','2741','2740','0');
INSERT INTO [t22592] VALUES (2751,'(٢٤٨١) - (حديث عن المقداد أنه قال: " يا رسول الله أرأيت إن لقيت رجلا من الكفار فقاتلنى فضرب إحدى يدى بالسيف فقطعها ثم لاذ منى','2741','2740','0');
INSERT INTO [t22592] VALUES (2752,'(٢٤٨٢) - (عن عمران بن حصين قال: " أصاب المسلمون رجلا من بنى عقيل فأتوا به النبى صلى الله عليه وسلم فقال: يا محمد إنى مسلم , فقال رسول الله صلى الله عليه وسلم: لو كنت قلت وأنت تملك أمرك أفلحت كل الفلاح " رواه مسلم.','2742','2740','0');
INSERT INTO [t22592] VALUES (2753,'كتاب الأطعمة','2743','0','0');
INSERT INTO [t22592] VALUES (2754,'[الأحاديث ٢٤٨٣ - ٢٤٩٢]','2743','2753','0');
INSERT INTO [t22592] VALUES (2755,'(٢٤٨٣) - (قوله صلى الله عليه وسلم فى الحمر: " أكفئوها فإنها رجس " (٢/٤١٠) .','2743','2754','0');
INSERT INTO [t22592] VALUES (2756,'(٢٤٨٤) - (حديث جابر: " أن النبى صلى الله عليه وسلم نهى يوم خيبر عن لحوم الحمر الأهلية , وأذن فى لحوم الخيل " متفق عليه.','2743','2754','0');
INSERT INTO [t22592] VALUES (2757,'(٣٧٨٨) والنسائى (٢/١٩٩) والدارمى (٢/٨٧) والطحاوى (٢/٣١٨) والبيهقى (٩/٣٢٦ ـ ٣٢٧) وأحمد (٣/٣٦١) من طرق عن حماد بن زيد عن عمرو بن دينار عن محمد بن على عن جابر به , إلا أن البخارى قال: " رخص " , مكان " أذن ".','2744','2754','0');
INSERT INTO [t22592] VALUES (2758,'(٢٤٨٥) - (حديث أبى ثعلبة الخشنى: " نهى رسول الله صلى الله عليه وسلم عن أكل كل ذى ناب من السباع " متفق عليه.','2744','2754','0');
INSERT INTO [t22592] VALUES (2759,'(٢٤٨٦) - (عن أبى هريرة (١) مرفوعا: " كل ذى ناب حرام " رواه مسلم.','2745','2754','0');
INSERT INTO [t22592] VALUES (2760,'(٢٤٨٧) - (حديث: " نهيه صلى الله عليه وسلم عن أكل الهر وأكل ثمنها " رواه أبو داود وابن ماجه.','2746','2754','0');
INSERT INTO [t22592] VALUES (2761,'(٢٤٨٨) - (حديث ابن عباس: " نهى رسول الله صلى الله عليه وسلم عن أكل كل ذى ناب من السباع وكل ذى مخلب من الطير " رواه الجماعة إلا البخارى والترمذى.','2747','2754','0');
INSERT INTO [t22592] VALUES (2762,'(٢٤٨٩) - (حديث: " أنه صلى الله عليه وسلم أمر بقتل الفأرة فى الحرم ".','2748','2754','0');
INSERT INTO [t22592] VALUES (2763,'(٢٤٩٠) - (حديث ابن عباس: " نهى رسول الله صلى الله عليه وسلم عن قتل أربع من الدواب: النملة والنحلة والهدهد والصرد " رواه أحمد وأبو داود وابن ماجه (١) .','2748','2754','0');
INSERT INTO [t22592] VALUES (2764,'(٢٤٩١) - (حديث: " نهى صلى الله عليه وسلم عن قتل الخطاطيف " رواه البيهقى مرسلا.','2750','2754','0');
INSERT INTO [t22592] VALUES (2765,'(٢٤٩٢) - (حديث أبى هريرة: " ذكر القنفذ لرسول الله صلى الله عليه وسلم فقال: هو خبيثة من الخبائث " رواه أبو داود.','2750','2754','0');
INSERT INTO [t22592] VALUES (2766,'فصل','2751','2753','0');
INSERT INTO [t22592] VALUES (2767,'(٢٤٩٣) - (قالت أسماء: " نحرنا فرسا على عهد رسول الله صلى الله عليه وسلم فأكلناه ونحن بالمدينة " متفق عليه.','2751','2766','0');
INSERT INTO [t22592] VALUES (2768,'(٢٤٩٤) - (حديث " قال عبد الرحمن: قلت لجابر: الضبع صيد هى؟ قال: نعم. قلت: آكلها؟ قال: نعم. قلت: أقاله رسول الله صلى الله عليه وسلم؟ قال: نعم " رواه الخمسة , وصححه الترمذى.','2751','2766','0');
INSERT INTO [t22592] VALUES (2769,'(٢٤٩٥) - (قال أنس: " أنفجنا أرنبا فسعى القوم فلغبوا فأخذتها , فجئت إلى أبى طلحة فذبحها وبعث بوركها أو قال: فخذها إلى النبى صلى الله عليه وسلم فقبله " متفق عليه.','2752','2766','0');
INSERT INTO [t22592] VALUES (2770,'(٢٤٩٦) - (عن محمد بن صفوان: " أنه صاد أرنبين فذبحهما بمروة (١) فأتى رسول الله صلى الله عليه وسلم فأمره بأكلهما " رواه أحمد والنسائى وابن ماجه.','2752','2766','0');
INSERT INTO [t22592] VALUES (2771,'(٢٤٩٧) - (حديث أبى سعيد: " كنا معشر أصحاب رسول الله صلى الله عليه وسلم لأن يهدى إلى أحدنا ضب أحب إليه من دجاجة ".','2753','2766','0');
INSERT INTO [t22592] VALUES (2772,'(٢٤٩٨) - (حديث: " أن خالد بن الوليد أكل الضب ورسول الله صلى الله عليه وسلم ينظر " متفق عليه.','2753','2766','0');
INSERT INTO [t22592] VALUES (2773,'(٢٤٩٩) - (قول أبى موسى: " رأيت النبى صلى الله عليه وسلم يأكل الدجاج " متفق عليه.','2754','2766','0');
INSERT INTO [t22592] VALUES (2774,'(٢٥٠٠) - (وعن سفينة قال: " أكلت مع رسول الله لحم حبارى " رواه أبو داود.','2754','2766','0');
INSERT INTO [t22592] VALUES (2775,'(٢٥٠١) - (قوله صلى الله عليه وسلم فى البحر: " هو الطهور ماؤه الحل ميتته ".','2755','2766','0');
INSERT INTO [t22592] VALUES (2776,'(٢٥٠٢) - (روى البخارى: " أن الحسن بن على ركب على سرج من جلود كلاب الماء ".','2755','2766','0');
INSERT INTO [t22592] VALUES (2777,'(٢٥٠٣) - (حديث ابن عمر: " نهى النبى صلى الله عليه وسلم عن أكل الجلالة وألبانها " رواه أحمد وأبو داود.','2755','2766','0');
INSERT INTO [t22592] VALUES (2778,'(٢٥٠٤) - (حديث ابن عباس: " نهى النبى صلى الله عليه وسلم عن شرب لبن الجلالة " رواه أحمد وأبو داود والترمذى وصححه.','2757','2766','0');
INSERT INTO [t22592] VALUES (2779,'(٢٥٠٥) - (أثر ابن عمر: " كان إذا أراد أكل الجلالة حبسها ثلاثا ".','2757','2766','0');
INSERT INTO [t22592] VALUES (2780,'(٢٥٠٦) - (حديث عبد الله بن عمرو بن العاص: " نهى رسول الله صلى الله عليه وسلم','2757','2766','0');
INSERT INTO [t22592] VALUES (2781,'(٢٥٠٧) - (عن ابن عباس قال: " كنا نكرى أراضى رسول الله صلى الله عليه وسلم ونشرط عليهم أن لا يدملوها (١) بعذرة الناس ".','2758','2766','0');
INSERT INTO [t22592] VALUES (2782,'(٢٥٠٨) - (حديث: " أن النبى صلى الله عليه وسلم كره أكل الغدة (٢) ". [١]','2758','2766','0');
INSERT INTO [t22592] VALUES (2783,'(٢٥٠٩) - (نقل أبو طالب: " نهى النبى صلى الله عليه وسلم عن أذن القلب ".','2758','2766','0');
INSERT INTO [t22592] VALUES (2784,'(٢٥١٠) - (عن جابر مرفوعا: " من أكل الثوم والبصل والكراث فلا يقربن مسجدنا فإن الملائكة تتأذى مما يتأذى منه بنو آدم " متفق عليه.','2760','2766','0');
INSERT INTO [t22592] VALUES (2785,'(٢٥١١) - (حديث أبى أيوب فى الطعام الذى فيه الثوم قال فيه: " أحرام هو يا رسول الله؟ قال: لا ولكننى أكرهه من أجل ريحه " حسنه الترمذى.','2760','2766','0');
INSERT INTO [t22592] VALUES (2786,'(٢٥١٢) - (عن على رضى الله عنه مرفوعا وموقوفا: " النهى عن أكل الثوم إلا مطبوخا " رواه الترمذى.','2761','2766','0');
INSERT INTO [t22592] VALUES (2787,'(٢٥١٣) - (عن عائشة قالت: " إن آخر طعام أكله رسول الله صلى الله عليه وسلم فيه بصل " رواه أبو داود.','2762','2766','0');
INSERT INTO [t22592] VALUES (2788,'(٢٥١٤) - (قال عمر فى خطبته فى البصل والثوم: " فمن أكلهما فليمتهما طبخا " رواه مسلم والنسائى وابن ماجه.','2762','2766','0');
INSERT INTO [t22592] VALUES (2789,'(٢٥١٥) - (حديث عبد الله بن حذافة: " أن ملك الروم حبسه ومعه لحم خنزيز مشوى وماء ممزوج بخمر ثلاثة أيام فأبى أن يأكله وقال: لقد أحله الله لى , ولكن لم أكن لأشمتك بدين الإسلام ".','2762','2766','0');
INSERT INTO [t22592] VALUES (2790,'(٢٥١٦) - (قول أبى زينب التميمى: " سافرت مع أنس بن مالك','2763','2766','0');
INSERT INTO [t22592] VALUES (2791,'(٢٥١٧) - (قال عمر: " يأكل ولا يتخذ خبنة ".','2764','2766','0');
INSERT INTO [t22592] VALUES (2792,'(٢٥١٨) - (عن رافع أن رسول الله صلى الله عليه وسلم قال: " لا ترم وكل ما وقع أشبعك الله وأرواك " صححه الترمذى.','2764','2766','0');
INSERT INTO [t22592] VALUES (2793,'(٢٥١٩) - (حديث عمرو بن شعيب عن أبيه عن جده: " أن النبى صلى الله عليه وسلم سئل عن الثمر المعلق فقال: ما أصاب منه من ذى حاجة غير متخذ خبنة فلا شىء عليه , ومن أخذ منه من غير حاجة فعليه غرامة مثليه','2765','2766','0');
INSERT INTO [t22592] VALUES (2794,'(٢٥٢٠) - (قال ابن عباس: " إن كان عليها حائط فهو حريم فلا تأكل " (٢/٤٢٠) .','2766','2766','0');
INSERT INTO [t22592] VALUES (2795,'(٢٥٢١) - (حديث سمرة فى الماشية " صحححه الترمذى.','2766','2766','0');
INSERT INTO [t22592] VALUES (2796,'(٢٥٢٢) - (حديث ابن عمر: " لا يحلب أحد ماشية أحد إلا بإذنه " متفق عليه.','2767','2766','0');
INSERT INTO [t22592] VALUES (2797,'(٢٥٢٣) - (حديث: " من كان يؤمن بالله واليوم الآخر فليكرم ضيفه جائزته , قالوا: وما جائزته يا رسول الله. قال: يومه وليلته والضيافة ثلاثة أيام , وما زاد على ذلك فهو صدقة , ولا يحل له أن يثوى عنده حتى يؤثمه: قيل يا رسول الله كيف يؤثمه؟ قال: يقيم عنده وليس عنده ما يقريه ".','2768','2766','0');
INSERT INTO [t22592] VALUES (2798,'(٢٥٢٥) - (قوله صلى الله عليه وسلم: " من كان يؤمن بالله واليوم الآخر فليكرم ضيفه ".','2769','2766','0');
INSERT INTO [t22592] VALUES (2799,'باب الذكاة','2770','2753','0');
INSERT INTO [t22592] VALUES (2800,'(٢٥٢٦) - (حديث ابن عمر مرفوعا: " أحل لنا ميتتان ودمان فأما الميتتان فالحوت والجراد , وأما الدمان فالكبد والطحال " رواه أحمد وابن ماجه والدارقطنى.','2770','2799','0');
INSERT INTO [t22592] VALUES (2801,'(٢٥٢٧) - (حديث كعب بن مالك (١) " أنه كانت لديه غنم ترعى بسلع (٢) فأبصرت جارية لنا بشاة من غنمها موتاء، فكسرت حجرا فذبحتها به، فقال لهم: لا تأكلوا حتى أسأل النبي صلى الله عليه وسلم أو أرسل إليه من يسأله وإنه سأل النبي صلى الله عليه وسلم عن ذلك أو أرسل إليه فأمر بأكلها " رواه احمد والبخاري)','2770','2799','0');
INSERT INTO [t22592] VALUES (2802,'(٢٥٢٨) - (قال البخارى: قال ابن عباس: " طعامهم','2770','2799','0');
INSERT INTO [t22592] VALUES (2803,'(٢٥٢٢) - (عن رافع بن خديج مرفوعا: " ما أنهر الدم فكل , ليس السن والظفر " متفق عليه.','2771','2799','0');
INSERT INTO [t22592] VALUES (2804,'(٢٥٣٠) - (عن عمر أنه نادى: " إن النحر فى اللبة أو الحلق لمن قدر " أخرجه سعيد ورواه الدارقطنى مرفوعا بنحوه [١] .','2772','2799','0');
INSERT INTO [t22592] VALUES (2805,'(٢٥٣١) - (حديث أبى هريرة قال: " نهى النبى صلى الله عليه وسلم عن شريطة الشيطان وهى التى تذبح فيقطع الجلد ولا تفرى الأوداج ثم تترك حتى تموت " رواه أبو داود.','2772','2799','0');
INSERT INTO [t22592] VALUES (2806,'(٢٥٣٢) - (قول على رضى الله عنه: " فيمن ضرب وجه ثور بالسيف تلك ذكاة ".','2773','2799','0');
INSERT INTO [t22592] VALUES (2807,'(٢٥٣٣) - (قال ابن عباس: " فى ذئب عدا على شاة فوضع قصبها بالأرض فأدركها فذبحها قال: يلقى ما أصاب الأرض منها ويأكل سائرها ".','2773','2799','0');
INSERT INTO [t22592] VALUES (2808,'(٢٥٣٤) - (حديث رافع بن خديج قال: " كنا مع النبى صلى الله عليه وسلم فند بعير , وكان فى القوم خيل يسير فطلبوه فأعياهم فأهوى إليه رجل بسهم فحبسه الله , فقال النبى صلى الله عليه وسلم: إن لهذه البهائم أوابد كأوابد الوحش , فما غلبكم منها فاصنعوا به كذا " وفى لفظ: " فما ند عليكم فاصنعوا به هكذا " متفق عليه.','2773','2799','0');
INSERT INTO [t22592] VALUES (2809,'(٢٥٣٥) - (حديث أبى العشراء عن أبيه مرفوعا: " لو طعنت فى فخذها لأجزأك " رواه الخمسة.','2774','2799','0');
INSERT INTO [t22592] VALUES (2810,'(٢٥٣٦) - (ثبت أنه صلى الله عليه وسلم كان إذا ذبح قال: " بسم الله والله أكبر ".','2774','2799','0');
INSERT INTO [t22592] VALUES (2811,'(٢٥٣٧) - (عن راشد بن سعد قال: قال رسول الله صلى الله عليه وسلم: " ذبيحة المسلم حلال , وإن لم يسم إذا لم يتعمد " أخرجه سعيد.','2775','2799','0');
INSERT INTO [t22592] VALUES (2812,'(٢٥٣٨) - (حديث: " عفى لأمتى عن الخطأ والنيسان ".','2777','2799','0');
INSERT INTO [t22592] VALUES (2813,'فصل','2778','2753','0');
INSERT INTO [t22592] VALUES (2814,'(٢٥٣٩) - (حديث جابر مرفوعا: " ذكاة الجنين ذكاة أمه " رواه أبو داود بإسناد جيد. ورواه الدارقطنى من حديث ابن عمر وأبى هريرة.','2778','2813','0');
INSERT INTO [t22592] VALUES (2815,'(٢٥٤٠) - (حديث: " وإن ذبحتم فأحسنوا الذبحة وليحد أحدكم شفرته وليرح ذبيحته " رواه أحمد والنسائى وابن ماجه.','2782','2813','0');
INSERT INTO [t22592] VALUES (2816,'(٢٥٤١) - (حديث أبى هريرة: " بعث النبى صلى الله عليه وسلم بديل بن ورقاء الخزاعى على جمل أورق يصيح فى فجاج منى بكلمات منها: لا تعجلوا الأنفس أن تزهق , وأيام منى أيام أكل وشرب وبعال " رواه الدارقطنى.','2782','2813','0');
INSERT INTO [t22592] VALUES (2817,'(٢٥٤٢) - (قال عمر: " لا تعجلوا الأنفس حتى تزهق ".','2782','2813','0');
INSERT INTO [t22592] VALUES (2818,'(٢٥٤٤) - (أثر ابن عمر: " أنه كان يستحب أن يستقبل القبلة [إذا ذبح] ".','2784','2813','0');
INSERT INTO [t22592] VALUES (2819,'(٢٥٤٥) - (حديث أن النبى صلى الله عليه وسلم قال لعدى بن حاتم: " فإن وقعت فى الماء فلا تأكل فإنك لا تدرى الماء قتله أو سهمك " متفق عليه (ص ٤٢٧) .','2784','2813','0');
INSERT INTO [t22592] VALUES (2820,'(٢٥٤٦) - (قوله صلى الله عليه وسلم: " فإن أخذ الكلب ذكاة " متفق عليه.','2786','2813','0');
INSERT INTO [t22592] VALUES (2821,'(٢٥٤٧) - (حديث: " ما أنهر الدم وذكر اسم الله عليه فكل ".','2786','2813','0');
INSERT INTO [t22592] VALUES (2822,'(٢٥٤٨) - (حديث عدى بن حاتم: " قلت: يا رسول الله إنى أرمى بالمعراض الصيد فأصيب , فقال: إذا رميت بالمعراض فخزق فكله , وإن أصاب بعرضه فلا تأكله " متفق عليه.','2787','2813','0');
INSERT INTO [t22592] VALUES (2823,'(٢٥٤٩) - (حديث: " أمر الرسول صلى الله عليه وسلم بقتل الكلب الأسود وقال: إنه شيطان " متفق عليه.','2787','2813','0');
INSERT INTO [t22592] VALUES (2824,'(٢٥٥٠) - (قال ابن عباس: " هى الكلاب المعلمة وكل طير تعلم الصيد والفهود والصقور وأشباهها ".','2788','2813','0');
INSERT INTO [t22592] VALUES (2825,'(٢٥٥١) - (حديث: " فإن أكل فلا تأكل فإنى أخاف أن يكون أمسك على نفسه " متفق عليه.','2788','2813','0');
INSERT INTO [t22592] VALUES (2826,'(٢٥٥٢) - (قال ابن عباس: " إذا أكل الكلب فلا تأكل فإن أكل الصقر فكل " رواه الخلال.','2789','2813','0');
INSERT INTO [t22592] VALUES (2827,'(٢٥٥٣) - (حديث: " ما أنهر الدم وذكر اسم الله عليه فكل ".','2789','2813','0');
INSERT INTO [t22592] VALUES (2828,'(٢٥٥٤) - (حديث: " إذا أرسلت كلبك المعلم وذكرت اسم الله عليه فكل " متفق عليه.','2790','2813','0');
INSERT INTO [t22592] VALUES (2829,'(٢٥٥٦) - (حديث عدى بن حاتم قال: " سألت رسول الله صلى الله عليه وسلم عن الصيد فقال: إذا رميت سهمك فاذكر اسم الله فإن وجدته قتل فكل إلا أن تجده وقع فى ماء فإنك لا تدرى الماء قتله أو سهمك " متفق عليه.','2790','2813','0');
INSERT INTO [t22592] VALUES (2830,'كتاب الأيمان','2791','0','0');
INSERT INTO [t22592] VALUES (2831,'[الأحاديث ٢٥٥٧ - ٢٥٦٤]','2791','2830','0');
INSERT INTO [t22592] VALUES (2832,'(٢٥٥٧) - (حديث: " من كان حالفا فليحلف بالله أو ليصمت " متفق عليه.','2791','2831','0');
INSERT INTO [t22592] VALUES (2833,'(٢٥٥٨) - (حديث: " لا تسافروا بالقرآن إلى أرض العدو ".','2791','2831','0');
INSERT INTO [t22592] VALUES (2834,'(٢٥٥٩) - (قالت عائشة: " ما بين دفتى المصحف كلام الله ".','2792','2831','0');
INSERT INTO [t22592] VALUES (2835,'(٢٥٦٠) - (حديث: " إن الله ينهاكم أن تحلفوا بآبائكم فمن كان حالفا فليحلف بالله أو ليصمت " متفق عليه.','2793','2831','0');
INSERT INTO [t22592] VALUES (2836,'(٢٥٦١) - (عن ابن عمر مرفوعا: " من حلف بغير الله فقد كفر أو أشرك " حسنه الترمذى.','2795','2831','0');
INSERT INTO [t22592] VALUES (2837,'(٢٥٦٢) - (قال ابن مسعود: " لأن أحلف بالله كاذبا أحب إلى من أن أحلف بغير صادقا ".','2797','2831','0');
INSERT INTO [t22592] VALUES (2838,'(٢٥٦٣) - (حديث: " من حلف باللات والعزى فليقل: لا إله إلا الله ".','2798','2831','0');
INSERT INTO [t22592] VALUES (2839,'(٢٥٦٤) - (عن أبى هريرة مرفوعا: " خمس ليس لها كفارة: الشرك بالله ... " الحديث رواه أحمد.','2799','2831','0');
INSERT INTO [t22592] VALUES (2840,'فصل','2800','2830','0');
INSERT INTO [t22592] VALUES (2841,'(٢٥٦٥) - (حديث: " رفع القلم عن ثلاثة ".','2800','2840','0');
INSERT INTO [t22592] VALUES (2842,'(٢٥٦٦) - (حديث: " رفع عن أمتى الخطأ والنسيان وما استكرهوا عليه ".','2800','2840','0');
INSERT INTO [t22592] VALUES (2843,'(٢٥٦٧) - (حديث عائشة مرفوعا: " اللغو فى اليمين كلام الرجل فى بيته: لا والله وبلى والله " رواه أبو داود ورواه البخارى وغيره موقوفا.','2800','2840','0');
INSERT INTO [t22592] VALUES (2844,'(٢٥٦٨) - (حديث أبي هريرة مرفوعا " خمس ليس لهن كفارة: ذكر منهن الحلف على يمين فاجرة يقتطع بها مال امرئ مسلم")','2802','2840','0');
INSERT INTO [t22592] VALUES (2845,'(٢٥٦٩) - (قول عمر: " يا رسول الله ألم تخبرنا أنا سنأتى البيت ونطوف به؟ قال: بلى , أفأخبرتك أنك آتيه الآن؟ قال: لا. قال: فإنك آتيه ومطوف به ".','2802','2840','0');
INSERT INTO [t22592] VALUES (2846,'(٢٥٧٠) - (حديث: " من حلف فقال: إن شاء الله لم يحنث " رواه أحمد والترمذى.','2802','2840','0');
INSERT INTO [t22592] VALUES (2847,'(٢٥٧١) - (عن ابن عمر مرفوعا: " من حلف على يمين فقال إن شاء الله فلا حنث عليه " رواه الخمسة إلا أبا داود.','2804','2840','0');
INSERT INTO [t22592] VALUES (2848,'(٢٥٧٢) - (حديث: " إنما الأعمال بالنيات ".','2805','2840','0');
INSERT INTO [t22592] VALUES (2849,'فصل','2806','2830','0');
INSERT INTO [t22592] VALUES (2850,'(٢٥٧٣) - (حديث: " أنه عليه السلام قال: لن أعود إلى شرب العسل " متفق عليه.','2806','2849','0');
INSERT INTO [t22592] VALUES (2851,'(٢٥٧٤) - (عن ابن عباس وابن عمر:" أن النبى صلى الله عليه وسلم جعل تحريم الحلال يمينا ".','2806','2849','0');
INSERT INTO [t22592] VALUES (2852,'(٢٥٧٥) - (حديث ثابت بن الضحاك مرفوعا: " من حلف على يمين بملة غير الإسلام كاذبا فهو كما قال " رواه الجماعة إلا أبا داود.','2807','2849','0');
INSERT INTO [t22592] VALUES (2853,'(٢٥٧٦) - (عن بريد مرفوعا: " من قال: هو برىء من الإسلام فإن كان كاذبا فهو كما قال , وإن كان صادقا فهو لم يعد إلى الإسلام سالما " رواه أحمد والنسائى وابن ماجه.','2807','2849','0');
INSERT INTO [t22592] VALUES (2854,'(٢٥٧٧) - (حديث زيد بن ثابت: " أن النبى صلى الله عليه وسلم سئل عن الرجل يقول: هو يهودى أو نصرانى أو مجوسى أو برىء من الإسلام فى اليمين يحلف بها فيحنث فى هذه الأشياء؟ فقال: عليه كفارة يمين " رواه أبو بكر.','2808','2849','0');
INSERT INTO [t22592] VALUES (2855,'فصل','2809','2830','0');
INSERT INTO [t22592] VALUES (2856,'(٢٥٧٨) - (قرأ أبى وابن مسعود: (فصيام ثلاثة أيام متتابعات) .','2809','2855','0');
INSERT INTO [t22592] VALUES (2857,'(٢٥٧٩) - (حديث عبد الرحمن بن سمرة مرفوعا: " إذا حلفت على يمين فرأيت غيرها خيرا منها فكفر عن يمينك وائت الذى هو خير ".','2810','2855','0');
INSERT INTO [t22592] VALUES (2858,'باب جامع الأيمان','2811','2830','0');
INSERT INTO [t22592] VALUES (2859,'(٢٥٨٠) - (حديث: " ... وإنما لكل امرىء ما نوى ... " (ص ٤٤٠/٢) .','2811','2858','0');
INSERT INTO [t22592] VALUES (2860,'فصل','2811','2830','0');
INSERT INTO [t22592] VALUES (2861,'(٢٥٨١) - (وفى الحديث: " ثم يخرج إلى بيت من بيوت الله ... " (٢/٤٤٢) .','2811','2860','0');
INSERT INTO [t22592] VALUES (2862,'(٢٥٨٢) - (حديث: " بئس البيت الحمام " رواه أبو داود وغيره.','2811','2860','0');
INSERT INTO [t22592] VALUES (2863,'فصل','2813','2830','0');
INSERT INTO [t22592] VALUES (2864,'فصل','2813','2830','0');
INSERT INTO [t22592] VALUES (2865,'(٢٥٨٣) - (حديث: أحل لنا ميتتان ودمان ".','2813','2864','0');
INSERT INTO [t22592] VALUES (2866,'فصل','2813','2830','0');
INSERT INTO [t22592] VALUES (2867,'(٢٥٨٤) - (حديث: " ما بين دفتى المصحف كلام الله ".','2813','2866','0');
INSERT INTO [t22592] VALUES (2868,'كتاب النذر','2814','2830','0');
INSERT INTO [t22592] VALUES (2869,'(٢٥٨٥) - (حديث ابن عمر: " نهى النبى صلى الله عليه وسلم عن النذر وقال: إنه لا يرد شيئا " , وفى لفظ: " لا يأت بخير وإنما يستخرج به من البخيل " رواه الجماعة إلا الترمذى.','2814','2868','0');
INSERT INTO [t22592] VALUES (2870,'(٢٥٨٦) - (حديث عقبة بن عامر مرفوعا: " كفارة النذر إذا لم يسم كفارة يمين " رواه ابن ماجه والترمذى وقال: حسن صحيح غريب.','2815','2868','0');
INSERT INTO [t22592] VALUES (2871,'(٢٥٨٧) - (حديث عمران بن حصين: سمعت رسول الله صلى الله عليه وسلم يقول: " لا نذر فى غضب وكفارته كفارة يمين " رواه سعيد فى سننه.','2817','2868','0');
INSERT INTO [t22592] VALUES (2872,'(٢٥٨٨) - (روى أبو داود وسعيد بن منصور: " أن امرأة قالت: يا رسول الله إنى نذرت أن أضرب على رأسك بالدف , فقال رسول الله صلى الله عليه وسلم أوف بنذرك ".','2819','2868','0');
INSERT INTO [t22592] VALUES (2873,'(٢٥٨٩) - (حديث عائشة مرفوعا: " من نذر أن يطيع الله فليطعه ومن نذر أن يعصى الله فلا يعصه " رواه الجماعة إلا مسلما.','2820','2868','0');
INSERT INTO [t22592] VALUES (2874,'(٢٥٩٠) - (حديث عائشة مرفوعا: " لا نذر فى معصية وكفارته كفارة يمين " رواه الخمسة واحتج به أحمد.','2820','2868','0');
INSERT INTO [t22592] VALUES (2875,'(٢٥٩١) - (حديث ابن عباس: " بينما النبى صلى الله عليه وسلم يخطب إذا هو برجل قائم فسأل عنه فقالوا: أبو إسرائيل نذر أن يقول فى الشمس ولا يستظل ولا يتكلم ويصوم فقال النبى صلى الله عليه وسلم: مروه فليجلس وليستظل وليتكلم وليتم صومه " رواه البخارى.','2824','2868','0');
INSERT INTO [t22592] VALUES (2876,'(٢٥٩٢) - (قول عقبه بن عامر: " نذرت أختى أن تمشى إلى بيت الله حافية غير مختمرة فسألت النبى صلى الله عليه وسلم فقال: إن الله لا يصنع بشقاء أختك شيئا مرها فلتختمر ولتركب ولتصم ثلاثة أيام " رواه الخمسة.','2824','2868','0');
INSERT INTO [t22592] VALUES (2877,'(٢٥٩٣) - (أثر أن ابن عباس: " أفتى فى امرأة نذرت أن تمشى إلى قباء فماتت أن تمشى ابنتها عنها ".','2827','2868','0');
INSERT INTO [t22592] VALUES (2878,'(٢٥٩٥) - (روى سعيد: " أن عائشة اعتكفت عن أخيها عبد الرحمن بعدما مات ". [١]','2828','2868','0');
INSERT INTO [t22592] VALUES (2879,'(٢٥٩٦) - (حديث: " من نذر أن يطيع الله فليطعه ".','2828','2868','0');
INSERT INTO [t22592] VALUES (2880,'(٢٥٩٧) - (حديث جابر: " فيمن نذر الصلاة فى المسجد الأقصى , يجزئه فى المسجد الحرام " رواه أحمد وأبو داود.','2828','2868','0');
INSERT INTO [t22592] VALUES (2881,'كتاب القضاء','2829','0','0');
INSERT INTO [t22592] VALUES (2882,'[الأحاديث ٢٥٩٨ - ٢٦٠٥]','2829','2881','0');
INSERT INTO [t22592] VALUES (2883,'(٢٥٩٨) - (حديث: " إذا اجتهد الحاكم فأصاب فله أجران وإن أخطأ فله أجر " متفق عليه.','2829','2882','0');
INSERT INTO [t22592] VALUES (2884,'(٢٥٩٩) - (حديث: " النبى صلى الله عليه وسلم حكم بين الناس ".','2832','2882','0');
INSERT INTO [t22592] VALUES (2885,'(٢٥٠٠) - (حديث: " أنه صلى الله عليه وسلم بعث عليا إلى اليمن للقضاء ". [١]','2832','2882','0');
INSERT INTO [t22592] VALUES (2886,'(٢٦٠١) - (حديث: " لا تسأل الإمارة ... " الحديث متفق عليه.','2834','2882','0');
INSERT INTO [t22592] VALUES (2887,'(٢٦٠٢) - (حديث: " أميركم زيد فإن قتل فجعفر فإن قتل فعبد الله بن رواحة " رواه البخارى','2834','2882','0');
INSERT INTO [t22592] VALUES (2888,'(٢٦٠٣) - (أثر: " أن عمر رضى الله عنه بعث فى كل مصر قاضيا وواليا ".','2835','2882','0');
INSERT INTO [t22592] VALUES (2889,'(٢٦٠٤) - (حديث: " أنه صلى الله عليه وسلم كتب لعمرو بن حزم حين بعثه لليمن ". وقد مضى.','2835','2882','0');
INSERT INTO [t22592] VALUES (2890,'(٢٦٠٥) - (أثر أن عمر كتب إلى أهل الكوفة: " أما بعد فإنى قد بعثت إليكم عمارا أميرا وعبد الله قاضيا فاسمعوا لهما وأطيعوا ".','2835','2882','0');
INSERT INTO [t22592] VALUES (2891,'فصل','2836','2881','0');
INSERT INTO [t22592] VALUES (2892,'(٢٦٠٦) - (روى عن عمر رضى الله عن: " أنه استعمل زيد بن ثابت على القضاء وفرض له رزقا ".','2836','2891','0');
INSERT INTO [t22592] VALUES (2893,'(٢٦٠٧) - (روى عن عمر: " أنه رزق شريحا فى كل شهر مائة درهم ".','2837','2891','0');
INSERT INTO [t22592] VALUES (2894,'(٢٦٠٨) - (روى: أن أبا بكر الصديق رضى الله عنه لما ولى الخلافة أخذ الذراع وخرج إلى السوق فقيل له: لا يسعك هذا , فقال: ما كنت أدع أهلى يضيعون , ففرضوا له كل يوم درهمين ".','2837','2891','0');
INSERT INTO [t22592] VALUES (2895,'(٢٦٠٩) - (أثر: " أن عمر بعث إلى الكوفة عمار بن ياسر واليا وابن مسعود قاضيا , وعثمان بن حنيف ماسحا وفرض لهم كل يوم شاة نصفها لعمار والنصف الآخر بين عبد الله وعثمان. وكتب إلى معاذ بن جبل وأبى عبيدة حين بعثهما إلى الشام أن انظرا رجالا من صالحى من قبلكم فاستعملوهم على القضاء وارزقوهم وأوسعوا عليهم من مال الله تعالى ".','2839','2891','0');
INSERT INTO [t22592] VALUES (2896,'(٢٦١٠) - (أثر: " أن عمر رضى الله عنه كتب إلى معاذ بن جبل , وأبى عبيدة حين بعثهما إلى الشام: أن انظرا رجالا من صالحى من قبلكم , فاستعملوهم على القضاء وارزقوهم , وأوسعوا عليهم من مال الله تعالى ".','2840','2891','0');
INSERT INTO [t22592] VALUES (2897,'(٢٦١١) - (قال عمر رضى الله عنه: " لأعزلن أبا مريم ـ يعنى: عن قضاء البصرة ـ وأولى رجلا إذا رآه الفاجر فرقه , فعزله وولى كعب بن (سوار) [١] ".','2840','2891','0');
INSERT INTO [t22592] VALUES (2898,'(٢٦١٢) - (أثر: " أن عليا ولى أبا الأسود ثم عزله فقال: لم عزلتنى وما خنت وما جنيت به , قال: إنى رأتيك يعلو كلامك على الخصمين ".','2840','2891','0');
INSERT INTO [t22592] VALUES (2899,'فصل','2841','2881','0');
INSERT INTO [t22592] VALUES (2900,'(٢٦١٣) - (حديث: " ما أفلح قوم ولوا أمرهم امرأة " رواه البخارى.','2841','2899','0');
INSERT INTO [t22592] VALUES (2901,'(٢٦١٤) - (حديث: " القضاة ثلاثة ... " رواه أبو داود والترمذى وابن ماجه.','2841','2899','0');
INSERT INTO [t22592] VALUES (2902,'(٢٦١٥) - (حديث أبى شريح وفيه أنه قال: " يا رسول الله: إن قومى إذا اختلفوا فى شىء أتونى فحكمت بينهم فرضى كلا الفريقين.','2843','2899','0');
INSERT INTO [t22592] VALUES (2903,'(٢٦١٦) - (أثر: أن عمر وأبيا تحاكما إلى زيد بن ثابت وتحاكم عثمان وطلحة إلى جبير بن مطعم ولم يكن أحد منهما قاضيا ".','2844','2899','0');
INSERT INTO [t22592] VALUES (2904,'فصل في آداب القاضي','2845','2881','0');
INSERT INTO [t22592] VALUES (2905,'(٢٦١٧) - (قال على رضى الله عنه: " لا ينبغى للقاضى أن يكون قاضيا حتى تكمل فيه خمس خصال: عفيف , حليم , عالم بما كان قبله , يستشير ذوى الألباب لا يخاف فى الله لومة لائم ".','2845','2904','0');
INSERT INTO [t22592] VALUES (2906,'(٢٦١٨) - (حديث أم سلمة: أن النبى صلى الله عليه وسلم قال: " من ابتلى بالقضاء بين المسلمين فليعدل بينهم فى لحظه وإشارته ومقعده , ولا','2845','2904','0');
INSERT INTO [t22592] VALUES (2907,'(٢٦١٩) - (أثر أن عمر كتب إلى أبى موسى: " (واس) [١] بين الناس فى وجهك ومجلسك وعدلك ولا يطمع شريف فى حنيفك ".','2847','2904','0');
INSERT INTO [t22592] VALUES (2908,'(٢٦٢٠) - (روى إبراهيم التيمى: أن عليا رضى الله عنه حاكم يهوديا إلى شريح فقام شريح من مجلسه وأجلس عليا فيه فقال على رضى الله عنه: لو كان خصمى مسلما لجلست معه بين يديك ولكنى سمعت رسول الله صلى الله عليه وسلم يقول: " لا تساووهم فى المجالس ".','2848','2904','0');
INSERT INTO [t22592] VALUES (2909,'(٢٦٢٠) - (حديث ابن عمرو قال: " لعن رسول الله صلى الله عليه وسلم الراشى والمرتشى " صححه الترمذى. ورواه أبو هريرة وزاد: " فى الحكم ". ورواه أبو بكر فى " زاد المسافر " وزاد: " والرائش ".','2849','2904','0');
INSERT INTO [t22592] VALUES (2910,'(٢٦٢٢) - (حديث أبى حميد الساعدى مرفوعا: " هدايا العمال غلول " رواه أحمد.','2852','2904','0');
INSERT INTO [t22592] VALUES (2911,'(٢٦٢٣) - (روى أبو الأسود المالكى عن أبيه عن جده مرفوعا: " ما عدل وال اتجر فى رعيته أبدا ".','2856','2904','0');
INSERT INTO [t22592] VALUES (2912,'(٢٦٢٤) - (وقال شريح: " شرط على عمر حين ولانى القضاء أن لا أبيع ولا أبتاع ولا أرتشى ولا أقضى وأنا غضبان ".','2856','2904','0');
INSERT INTO [t22592] VALUES (2913,'(٢٦٢٥) - (روى عن على رضى الله عنه: أنه نزل به رجل فقال: ألك خصم؟ قال: نعم , قال: تحول عنا , فإنى سمعت رسول الله صلى الله عليه وسلم يقول:','2856','2904','0');
INSERT INTO [t22592] VALUES (2914,'(٢٦٢٦) - (حديث أبى بكر مرفوعا: " لا يقضين حكم بين اثنين وهو غضبان " متفق عليه.','2858','2904','0');
INSERT INTO [t22592] VALUES (2915,'(٢٦٢٧) - (حديث: " أن النبى صلى الله عليه وسلم حكم فى حال غضبه فى حديث مخاصمة الأنصارى والزبير فى شراج الحرة " رواه الجماعة.','2859','2904','0');
INSERT INTO [t22592] VALUES (2916,'(٢٦٢٨) - (حديث بريدة مرفوعا: " القضاة ثلاثة: واحد فى الجنة واثنان فى النار , فأما الذى فى الجنة فرجل عرف الحق فقضى به , ورجل عرف الحق فجار فى الحكم فهو فى النار , ورجل قضى للناس على جهل فهو فى النار " رواه أبو داود وابن ماجه.','2859','2904','0');
INSERT INTO [t22592] VALUES (2917,'(٢٦٢٩) - (حديث: " أن النبى صلى الله عليه وسلم استكتب زيد بن ثابت ومعاوية بن أبى سفيان وغيرهما ".','2859','2904','0');
INSERT INTO [t22592] VALUES (2918,'(٢٦٣٠) - (قال عمر: " لا تؤمنوهم وقد خونهم الله ولا تقربوهم وقد أبعدهم الله ولا تعزوهم وقد أذلهم الله ".','2861','2904','0');
INSERT INTO [t22592] VALUES (2919,'باب طريق الحكم وصفته','2863','2881','0');
INSERT INTO [t22592] VALUES (2920,'(٢٦٣١) - (حديث: " إنما أقضى على نحو ما أسمع ".','2863','2919','0');
INSERT INTO [t22592] VALUES (2921,'(٢٦٣٢) - (روى: " أن رجلين اختصما إلى النبى صلى الله عليه وسلم: حضرمى وكندى فقال الحضرمى: يا رسول الله: إن هذا غلبنى على أرض لى , فقال الكندى: هى أرضى وفى يدى ليس له فيها حق فقال النبى صلى الله عليه وسلم للحضرمى: ألك بينة؟ فقال: لا.','2863','2919','0');
INSERT INTO [t22592] VALUES (2922,'(٢٦٣٣) - (حديث: " قبل النبى صلى الله عليه وسلم شهادة الأعرابى برؤية الهلال ".','2864','2919','0');
INSERT INTO [t22592] VALUES (2923,'(٢٦٣٤) - (قول عمر رضى الله عنه: " المسلون عدول بعضهم على بعضهم ".','2864','2919','0');
INSERT INTO [t22592] VALUES (2924,'(٢٦٢٤) - (قوله صلى الله عليه وسلم: " إنكم تختصمون إلى ولعل بعضكم أن يكون ألحن بحجته من بعض فأقضى على نحو ما أسمع " رواه الجماعة. [١]','2864','2919','0');
INSERT INTO [t22592] VALUES (2925,'(٢٦٣٦) - (قول عمر فى كتابه إلى أبى موسى الأشعرى: " واجعل لمن أدعى حقا غائبا أمدا ينتهى إليه , فإن أحضر بينه أخذت له حقه وإلا','2865','2919','0');
INSERT INTO [t22592] VALUES (2926,'(٢٦٣٧) - (روى سليمان بن حرب (١) قال: " شهد رجل عند عمر بن الخطاب رضى الله عنه فقال له عمر: إنى لست أعرفك ولا يضرك أنى لا أعرفك فائتنى بمن يعرفك , فقال رجل: أنا أعرفه يا أمير المؤمنين , قال: بأى شىء تعرفه؟ فقال: بالعدالة. قال: هو جارك الأدنى تعرف ليله ونهاره ومدخله ومخرجه؟ قال: لا. قال: فعاملك بالدرهم والدينار الذى يستدل بهما على الورع؟ قال: لا. قال: فصاحبك فى السفر الذى يستدل به على مكارم الأخلاق؟ قال: لا. قال: فلست تعرفه , ثم قال للرجل: ائتنى بمن يعرفك ".','2866','2919','0');
INSERT INTO [t22592] VALUES (2927,'(٢٦٣٨) - (فى حديث الحضرمى والكندى: " شاهداك أو يمينه. فقال: إنه لا يتورع فى شىء. قال: ليس لك إلا ذلك " رواه مسلم.','2867','2919','0');
INSERT INTO [t22592] VALUES (2928,'(٢٦٣٩) - (روى عن عمر أنه قال: " البينة العادلة أحق من اليمين الفاجرة ".','2869','2919','0');
INSERT INTO [t22592] VALUES (2929,'(٢٦٤٠) - (حديث ابن عمر: " أنه باع زيد بن ثابت عبدا فادعى عليه زيد أنه باعه إياه عالما بعيبه , فأنكره ابن عمر فتحاكما إلى عثمان فقال عثمان لابن عمر: احلف أنك ما علمت به عيبا , فأبى ابن عمر أن يحلف فرد عليه العبد " رواه أحمد.','2869','2919','0');
INSERT INTO [t22592] VALUES (2930,'(٢٦٤١) - (قول النبى صلى الله عليه وسلم: " اليمين على المدعى عليه ".','2870','2919','0');
INSERT INTO [t22592] VALUES (2931,'(٢٦٤٢) - (حديث ابن عمر أن النبى صلى الله عليه وسلم: " رد اليمين على طالب (١) الحق " رواه الدارقطنى.','2873','2919','0');
INSERT INTO [t22592] VALUES (2932,'(٢٦٤٣) - (روي أن المقداد اقترض من عثمان مالا فتحاكما إلى عمر فقال عثمان: هو سبعة آلاف وقال المقداد: هو أربعة آلاف، فقال المقداد لعثمان: احلف أنه سبعة آلاف، فقال عمر: أنصفك. احلف أنها كما تقول وخذها" رواه أبو عبيد)','2874','2919','0');
INSERT INTO [t22592] VALUES (2933,'(٢٦٣٣) - (قال علي " إن رد اليمين له أصل في الكتاب والسنة، اما الكتاب فقوله تعالى: {أو يخافوا أن ترد أيمان بعد أيمانهم} وأما السنة فحديث القسامة")','2875','2919','0');
INSERT INTO [t22592] VALUES (2934,'فصل','2875','2881','0');
INSERT INTO [t22592] VALUES (2935,'(٢٦٤٥) - (حديث: " فمن قضيت له بشىء من حق أخيه فلا يأخذ منه شيئا فإنما أقطع له قطعة من النار " متفق عليه.','2875','2934','0');
INSERT INTO [t22592] VALUES (2936,'فصل','2875','2881','0');
INSERT INTO [t22592] VALUES (2937,'(٢٦٤٦) - (حديث هند قالت: " يا رسول الله إن أبا سفيان رجل شحيح وليس يعطينى من النفقة ما يكفينى وولدى. فقال: خذى ما يكفيك وولدك بالمعروف " متفق عليه.','2875','2936','0');
INSERT INTO [t22592] VALUES (2938,'(٢٦٤٧) - (حديث على مرفوعا: " إذا تقاضى إليك رجلان فلا تقضى للأول حتى تسمع كلام الآخر , فإنك إذا فعلت ذلك تبين لك القضاء " حسنه الترمذى.','2876','2936','0');
INSERT INTO [t22592] VALUES (2939,'(٢٦٤٨) - (روى أن أبا بكر رضى الله عنه: " كتب إلى المهاجر بن أبى أمية أن ابعث إلى بقيس بن المكشوح فى وثاق , فأحلفه خمسين يمينا على منبر رسول الله صلى الله عليه وسلم أنه ما قتل والديه ".','2876','2936','0');
INSERT INTO [t22592] VALUES (2940,'(٢٦٤٩) - (روى الضحاك بن سفيان قال: " كتب إلى رسول الله صلى الله عليه وسلم أن أورث امرأة أشيم الضبابى من دية زوجها " رواه أبو داود والترمذى.','2876','2936','0');
INSERT INTO [t22592] VALUES (2941,'(٢٦٥٠) - (حديث أنه صلى الله عليه وسلم: " كتب إلى ملوك الأطراف وإلى عماله وسعاته ".','2877','2936','0');
INSERT INTO [t22592] VALUES (2942,'باب القسمة','2878','2881','0');
INSERT INTO [t22592] VALUES (2943,'(٢٦٥١) - (حديث: " إنما الشفعة فيما لم يقسم ".','2878','2942','0');
INSERT INTO [t22592] VALUES (2944,'(٢٦٥٢) - (حديث: " قسم النبى صلى الله عليه وسلم الغنائم بين أصحابه ".','2878','2942','0');
INSERT INTO [t22592] VALUES (2945,'(٢٦٥٣) - (حديث: " لا ضرر ولا ضرار " رواه أحمد ومالك فى " الموطأ ".','2878','2942','0');
INSERT INTO [t22592] VALUES (2946,'باب الدعاوى والبينات','2879','2881','0');
INSERT INTO [t22592] VALUES (2947,'(٢٦٥٤) - (حديث ابن عباس مرفوعا: " لو يعطى الناس بدعواهم لادعى ناس دماء رجال وأموالهم , ولكن اليمين على المدعى عليه " رواه أحمد ومسلم.','2879','2946','0');
INSERT INTO [t22592] VALUES (2948,'(٢٦٥٥) - (حديث: " شاهداك أو يمينه ليس لك إلا ذلك ".','2879','2946','0');
INSERT INTO [t22592] VALUES (2949,'(٢٦٥٦) - (حديث أبى موسى: " أن رجلين اختصما إلى رسول الله صلى الله عليه وسلم فى دابة ليس لأحدهما بينة فجعلها بينهما نصفين " رواه الخمسة إلا الترمذى.','2879','2946','0');
INSERT INTO [t22592] VALUES (2950,'(٢٦٥٧) - (حديث الحضرمى والكندى ".','2883','2946','0');
INSERT INTO [t22592] VALUES (2951,'(٢٦٥٨) - (حديث أبى موسى: " أن رجليين ادعيا بعيرا على عهد رسول الله صلى الله عليه وسلم فبعث كل منهما بشاهدين , فقسمه النبى صلى الله عليه وسلم بينهما " رواه أبو داود.','2883','2946','0');
INSERT INTO [t22592] VALUES (2952,'(٢٦٥٩) - (حديث أبى هريرة: " أن رجلين تداعيا عينا لم يكن لواحد منهما بينة فأمرهما رسول الله صلى الله عليه وسلم أن يستهما على اليمين أحبا أم كرها " رواه أبو داود.','2884','2946','0');
INSERT INTO [t22592] VALUES (2953,'(٢٦٦٠) - (روى الشافعى عن ابن المسيب: " أن رجلين اختصما إلى رسول الله صلى الله عليه وسلم فى أمر فجاء كل واحد منهما بشهود عدول على عدة واحدة فأسهم النبى صلى الله عليه وسلم بينهما ".','2884','2946','0');
INSERT INTO [t22592] VALUES (2954,'(٢٦٦١) - (حديث: " البينة على المدعى , واليمين على المدعى عليه ". وفى لفظ: " واليمين على من أنكر " رواه الترمذى (٣/٤٧٩) .','2885','2946','0');
INSERT INTO [t22592] VALUES (2955,'(٢٦٦٢) - (حديث: " شاهداك أو يمينه " (٤/٤٧٩) .','2885','2946','0');
INSERT INTO [t22592] VALUES (2956,'(٢٦٦٣) - (عن ابن عباس: " أن النبى صلى الله عليه وسلم , قضى باليمين على المدعى عليه " متفق عليه (٥/٤٧٩) .','2885','2946','0');
INSERT INTO [t22592] VALUES (2957,'كتاب الشهادات','2886','2881','0');
INSERT INTO [t22592] VALUES (2958,'(٢٦٦٤) - (حديث: " شاهداك أو يمينه " (١/٤٨١) .','2886','2957','0');
INSERT INTO [t22592] VALUES (2959,'(٢٦٦٥) - (عن أبى هريرة مرفوعا: " يكون فى آخر الزمان أمراء ظلمة , ووزراء فسقه , وقضاة خونة , وفقهاء كذبة , فمن أدرك منكم ذلك الزمان فلا يكونن لهم كاتبا , ولا عريفا , ولا شرطيا " رواه الطبرانى (٢/٤٨٢) .','2886','2957','0');
INSERT INTO [t22592] VALUES (2960,'(٢٦٦٦) - (حديث: " لا ضرر ولا ضرار " (٢/٤٧٢) .','2888','2957','0');
INSERT INTO [t22592] VALUES (2961,'(٢٦٦٧) - (قال ابن عباس: " سئل النبى صلى الله عليه وسلم عن الشهادة , فقال: ترى الشمس؟ قال: على مثلها فاشهد , أو دع " رواه الخلال (١/٤٨٣) .','2888','2957','0');
INSERT INTO [t22592] VALUES (2962,'(٢٦٦٨) - (حديث جابر: " أنه صلى الله عليه وسلم أجاز شهادة أهل الذمة بعضهم على بعض " رواه ابن ماجه من رواية مجالد , وهو ضعيف (١/٤٨٦) .','2889','2957','0');
INSERT INTO [t22592] VALUES (2963,'(٢٦٦٩) - (عن عمرو بن شعيب عن أبيه عن جده مرفوعا: " لا تجوز شهادة خائن , ولا خائنة , ولا ذى غمر على أخيه " رواه أحمد وأبو داود (٢/٤٨٧) .','2889','2957','0');
INSERT INTO [t22592] VALUES (2964,'(٢٦٧٠) - (حديث أبى موسى مرفوعا: " من لعب بالنردشير فقد عصى الله ورسوله " رواه أبو داود (٢/٤٨٨) .','2890','2957','0');
INSERT INTO [t22592] VALUES (2965,'(٢٦٧١) - (عن واثلة بن الأسقع مرفوعا: " إن لله عز وجل فى كل يوم ثلاثمائة وستين نظرة , ليس لصاحب الشاه منها نصيب " رواه أبو بكر (٢/٤٨٩) .','2893','2957','0');
INSERT INTO [t22592] VALUES (2966,'(٢٦٧٢) - (أثر: " أن عليا رضى الله عنه مر على قوم يلعبون بالشطرنج , فقال: ما هذه التماثيل التى أنتم لها عاكفون؟ ! ".','2894','2957','0');
INSERT INTO [t22592] VALUES (2967,'(٢٦٧٣) - (روى أبو مسعود البدرى مرفوعا: " إن مما أدرك الناس من كلام النبوة الأولى إذا لم تستح فاصنع ما شئت " رواه البخارى (٢/٤٨٩) .','2895','2957','0');
INSERT INTO [t22592] VALUES (2968,'فصل','2895','2881','0');
INSERT INTO [t22592] VALUES (2969,'(٢٧٦٤) - (حديث أبى هريرة مرفوعا: " لا تجوز شهادة بدوى على صاحب قرية " (٢/٤٩٠) .','2895','2968','0');
INSERT INTO [t22592] VALUES (2970,'باب موانع الشهادة','2898','2881','0');
INSERT INTO [t22592] VALUES (2971,'(٢٦٧٥) - (عن عائشة مرفوعا: " لا تجوز شهادة خائن ولا خائنة , ولا ذى غمر على أخيه , ولا ظنين فى قرابة ولا ولاء " ورواه الخلال بنحوه من حديث عمر وأبى هريرة. ورواه أحمد وأبو داود بنحوه من حديث عمرو بن شعيب (٢/٤٩١) .','2898','2970','0');
INSERT INTO [t22592] VALUES (2972,'(٢٦٧٦) - (فاطمة بضعة منى يريبنى ما رابها " (٢/٤٩١) .','2899','2970','0');
INSERT INTO [t22592] VALUES (2973,'(٢٦٧٧) - (حديث: " المكاتب عبد ما بقى عليه درهم " (٢/٤٩١) .','2900','2970','0');
INSERT INTO [t22592] VALUES (2974,'(٢٦٧٨) - (حديث: " ولا ذى غمر على أخيه " (٢/٤٩٢) .','2900','2970','0');
INSERT INTO [t22592] VALUES (2975,'باب أقسام المشهود به','2901','2881','0');
INSERT INTO [t22592] VALUES (2976,'(٢٦٧٩) - (أثر: " أن أبا بكرة ونافع بن الحارث وشبل بن معبد شهدوا على المغيرة بن شعبة بالزنى عند عمر بن الخطاب رضى الله عنه , ولما لم يصرح زياد بذلك بل قال: رأيت أمرا قبيحا , فرح عمر , وحمد الله , ولم يقم الحد عليه ".','2901','2975','0');
INSERT INTO [t22592] VALUES (2977,'(٢٦٨٠) - (قوله صلى الله عليه وسلم لهلال بن أمية: " أربعة شهداء , وإلا حد فى ظهرك ... " الحديث.','2901','2975','0');
INSERT INTO [t22592] VALUES (2978,'(٢٦٨١) - (حديث قبيصة: " ... ورجل أصابته فاقة حتى يقول ثلاثة من ذوى الحجى من قومه: لقد أصابت فلانا فاقة " الحديث , رواه أحمد ومسلم وأبو داود والنسائى (٢/٤٩٤) .','2901','2975','0');
INSERT INTO [t22592] VALUES (2979,'(٢٦٨٢) - (روى عن الزهرى قال: " جرت السنة من عهد رسول الله صلى الله عليه وسلم , أن لا تقبل شهادة النساء فى الحدود " قاله فى الكافى (٢/٤٩٤) .','2901','2975','0');
INSERT INTO [t22592] VALUES (2980,'(٢٦٨٣) - (حديث ابن عباس: " أن رسول الله صلى الله عليه وسلم قضى باليمين مع الشاهد " رواه أحمد والترمذى وابن ماجه , ولأحمد فى رواية: " إنما ذلك فى الأموال " ورواه أيضا عن جابر مرفوعا.','2902','2975','0');
INSERT INTO [t22592] VALUES (2981,'(٢٦٨٤) - (عن حذيفة: " أن النبى صلى الله عليه وسلم , أجاز شهادة القابلة وحدها " ذكره الفقهاء فى كتبهم (٢/٤٩٦) .','2912','2975','0');
INSERT INTO [t22592] VALUES (2982,'باب اليمين فى الدعاوى','2913','2881','0');
INSERT INTO [t22592] VALUES (2983,'(٢٦٨٥) - (البينة على المدعى , واليمين على من أنكر " هذه قطعة من حديث خرجه النووى عن ابن عباس (٢/٥٠١) .','2913','2982','0');
INSERT INTO [t22592] VALUES (2984,'(٢٦٨٦) - (لو يعطى الناس بدعواهم لادعى قوم دماء رجال وأموالهم " (١/٥٠٢) .','2913','2982','0');
INSERT INTO [t22592] VALUES (2985,'(٢٦٨٧) - (حديث ابن عباس: " أن النبى صلى الله عليه وسلم , استحلف رجلا , فقال: قل والله الذى لا إله إلا هو ماله عندى شىء " رواه أبو داود (٢/٥٠٢) .','2913','2982','0');
INSERT INTO [t22592] VALUES (2986,'(٢٦٨٨) - (حديث النسائى عن القاسم بن عبد الرحمن عن النبى صلى الله عليه وسلم: " لا تضطروا الناس فى أيمانهم أن يحلفوا على ما لا يعلمون " (٢/٥٠٢) .','2914','2982','0');
INSERT INTO [t22592] VALUES (2987,'(٢٦٨٩) - (وفى حديث الحضرمى: " ولكن أحلفه: والله ما يعلم أنها أرضى اغتصبنيها أبوه " رواه أبو داود (٢/٥٠٢) .','2915','2982','0');
INSERT INTO [t22592] VALUES (2988,'فصل','2915','2881','0');
INSERT INTO [t22592] VALUES (2989,'(٢٦٩٠) - (استحلف النبى صلى الله عليه وسلم ركانة بن عبد يزيد فى الطلاق: والله ما أردت إلا واحدة؟ فقال: والله ما أردت إلا واحدة " (٢/٥٠٣) .','2915','2988','0');
INSERT INTO [t22592] VALUES (2990,'(٢٦٩١) - (قال عثمان لابن عمر: " تحلف بالله لقد بعته وما به داء تعلمه ".','2915','2988','0');
INSERT INTO [t22592] VALUES (2991,'(٢٦٩٢) - (فلك يمينه فقال: إنه رجل فاجر لا يبالى على ما حلف عليه , قال: ليس لك إلا ذلك " (٢/٥٠٣) .','2916','2988','0');
INSERT INTO [t22592] VALUES (2992,'(٢٦٩٣) - (قال الأشعث بن قيس: " كان بينى وبين رجل من اليهود أرض فجحدنى , فقدمته إلى النبى صلى الله عليه وسلم فقال لى: هل لك بينة؟ قلت: لا , قال لليهودى: احلف ثلاثا , قلت: إذا يحلف فيذهب بمالى. فأنزل الله تعالى: (إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا) إلى آخر الآية " رواه أبو داود (٢/٥٠٤) .','2916','2988','0');
INSERT INTO [t22592] VALUES (2993,'(٢٦٩٤) - (أثر: " أن عمر حلف فى حكومته لأبى فى النخل فى مجلس زيد ".','2916','2988','0');
INSERT INTO [t22592] VALUES (2994,'(٢٦٩٥) - (حديث أبى هريرة قال: قال رسول الله صلى الله عليه وسلم ـ يعنى لليهود ـ: " نشدتكم بالله الذى أنزل التوارة على موسى: ما تجدون فى التوارة على من زنى؟ " رواه أبو داود (٢/٥٠٤) .','2916','2988','0');
INSERT INTO [t22592] VALUES (2995,'(٢٦٩٦) - (فى سنن ابن ماجه مرفوعا: " هى ـ يعنى صخرة القدس ـ من الجنة " (٢/٥٠٥) .','2917','2988','0');
INSERT INTO [t22592] VALUES (2996,'(٢٦٩٧) - (روى مالك والشافعى وأحمد , عن جابر مرفوعا: " من حلف على منبرى هذا يمينا آثمة فليتبوأ مقعده من النار " (٢/٥٠٥) .','2919','2988','0');
INSERT INTO [t22592] VALUES (2997,'(٢٦٩٨) - (حديث ابن عمر مرفوعا: " ومن حلف له بالله فليرض " رواه ابن ماجه (٢/٥٠٥) .','2920','2988','0');
INSERT INTO [t22592] VALUES (2998,'كتاب الإقرار','2921','2881','0');
INSERT INTO [t22592] VALUES (2999,'(٢٦٩٩) - (قوله صلى الله عليه وسلم: " واغد يا أنيس إلى امرأة هذا: فإن اعترفت فارجمها " (٢/٥٠٥) .','2921','2998','0');
INSERT INTO [t22592] VALUES (3000,'(٢٧٠٠) - (حديث: " أن النبى صلى الله عليه وسلم رجم ماعزا والغامدية والجهنية بإقرارهم " (٢/٥٠٥) .','2921','2998','0');
INSERT INTO [t22592] VALUES (3001,'(٢٧٠١) - (رفع القلم عن ثلاثة " (٢/٥٠٦) .','2921','2998','0');
INSERT INTO [t22592] VALUES (3002,'(٢٧٠٢) - (عفى لأمتى عن الخطأ , والنسيان , وما استكرهوا عليه ".','2921','2998','0');
INSERT INTO [t22592] VALUES (3003,'باب ما يحصل به الإقرار وما يغيره','2922','2881','0');
INSERT INTO [t22592] VALUES (3004,'(٢٧٠٣) - (حديث عمرو بن عبسة: " ... فدخلت عليه , فقلت: يا رسول الله: أتعرفنى؟ فقال: نعم أنت الذى لقيتنى بمكة , فقال: فقلت: بلى " (٢/٥٠٩) .','2922','3003','0');
INSERT INTO [t22592] VALUES (3005,'(٢٧٠٤) - (لأن عليا , رضى الله عنه , أسلم وهو ابن ثمان سنين " (٢/٢٥١) .','2924','3003','0');
INSERT INTO [t22592] VALUES (3006,'(٢٧٠٥) - (وقد صح عنه , صلى الله عليه وسلم: " أنه عرض الإسلام على ابن صياد صغيرا " متفق عليه (٢/٥٢١) .','2924','3003','0');
INSERT INTO [t22592] VALUES (3007,'(٢٧٠٦) - (فى الصحيح: " أن النبى صلى الله عليه وسلم , عرض الإسلام على أبى طالب , وهو فى النزع " (٢/٥٢١) .','2925','3003','0');
INSERT INTO [t22592] VALUES (3008,'(٢٧٠٧) - (عن ابن مسعود: " أن النبى صلى الله عليه وسلم , دخل الكنيسة , فإذا هو بيهود , وإذا يهودى يقرأ عليهم التوراة , فلما أتوا على صفة النبى صلى الله عليه وسلم , أمسكوا , وفى ناحيتها رجل مريض , فقال النبى صلى الله عليه وسلم: مالكم أمسكتم؟ فقال المريض: إنهم أتوا على صفة نبى فأمسكوا , ثم جاءه المريض يحبو , حتى أخذ التوارة فقرأ حتى أتى على صفة النبى صلى الله عليه وسلم , وأمته فقال: هذه صفتك وصفة أمتك أشهد أن لا إله إلا الله , وإنك رسول الله , فقال النبى صلى الله عليه وسلم لأصحابه: لوا أخاكم " رواه أحمد (٢/٥٢٢) .','2926','3003','0');
