INSERT INTO [t22707] VALUES (43,'المجمع الثاني مجمع نيقية ٣٢٥ م','338','41','0');
INSERT INTO [t22707] VALUES (44,'المجمع الآريوسي','345','43','0');
INSERT INTO [t22707] VALUES (45,'المجمع الثالث مجمع القسطنطينية ٣٨١ م','346','41','0');
