INSERT INTO [t22707] VALUES (47,'المجمع الخامس مجمع أفسيس الثاني ٤٤٩ م','349','41','0');
INSERT INTO [t22707] VALUES (48,'المجمع السادس مجمع خلقدون ٤٥١ م','350','41','0');
INSERT INTO [t22707] VALUES (49,'المجمع السابع في أيام إنسطاس الملك','352','41','0');
INSERT INTO [t22707] VALUES (50,'المجمع الثامن مجمع القسطنطينية','354','41','0');
INSERT INTO [t22707] VALUES (51,'[المجمع التاسع في أيام معاوية بن أبي سفيان]','355','41','0');
INSERT INTO [t22707] VALUES (52,'المجمع العاشر لما مات الملك وولي بعده ابنه واجتمع فريق المجمع السادس','357','41','0');
INSERT INTO [t22707] VALUES (53,'فصل: في أنه لا يمكن الإيمان بنبي أصلا مع جحود نبوة رسول الله صلى الله عليه وسلم','360','0','0');
