INSERT INTO [t22797] VALUES (1,'مقدمة المحقق','3','0','0');
INSERT INTO [t22797] VALUES (2,'البيهقي في سطور','42','1','0');
INSERT INTO [t22797] VALUES (3,'مقدمة المؤلف','45','0','0');
INSERT INTO [t22797] VALUES (4,'١ - باب: ما جاء في تخصيص قريش بالتقديم والاتباع والتعلم منهم؛ لكثرة علمهم ورجحان عقلهم وقوة رأيهم، وما في بعضه من الإشارة إلى الشافعي رحمه الله، لزيادة علمه من بين قريش بعد الصحابة رضي الله عنهم، وانتشاره في مشارق الأرض ومغاربها وانتفاع المسلمين به','59','0','0');
INSERT INTO [t22797] VALUES (5,'٢ - باب: ما جاء في قول الله عز وجل: {وإنه لذكر لك ولقومك} وما للعرب ثم لقريش فيه من الشرف، وما وجب بذلك على المسلمين من حبهم، والشافعي من جملتهم رحمه الله','73','0','0');
INSERT INTO [t22797] VALUES (6,'٣ - باب: ما جاء في تخصيص بني هاشم بالاصطفاء، وفي تخصيصهم تخصيص بني المطلب الذين ينتمي إليهم الشافعي رحمه الله، لقول النبي صلى الله عليه وسلم: «بنو هاشم وبنو المطلب شيء واحد»','80','0','0');
INSERT INTO [t22797] VALUES (7,'٤ - باب: ما جاء في تخيير القبائل، وأن خيارهم في الجاهلية خيارهم في الإسلام إذا فقهوا، وفي ذلك إشارة إلى الشافعي، رحمه الله، لكونه من خيار القبائل، ثم ما ظهر من فقهه في دين الله، تبارك وتعالى، وتفقهه','88','0','0');
INSERT INTO [t22797] VALUES (8,'٥ - باب: ما جاء في تفضيل أهل اليمن بالإيمان والفقه والحكمة. ومكة والمدينة يمانيتان. ثم الإشارة إلى عالم أهل المدينة، ومولد الشافعي بغزة، وهي من الأرض المقدسة، وعداد أهلها في اليمن ومنشؤه بمكة والمدينة، وأكثر علمه مأخوذ من أهل مكة والمدينة','90','0','0');
INSERT INTO [t22797] VALUES (9,'٦ - باب: ما جاء في إخبار المصطفى، صلى الله عليه وسلم، بأنه يبعث لهذه الأمة على رأس كل مائة سنة من يجدد لها دينها، وتأويل من تأوله على رأس المائتين بالشافعي، رحمه الله','95','0','0');
INSERT INTO [t22797] VALUES (10,'٧ - باب: ما حضرني فيمن آذى قرابة رسول الله، صلى الله عليه وسلم، أو أراد هوانهم، أو بغاهم العواثر، مع ما فيه من البيان: أن قريشا أهل أمانة، وأن رحم النبي، صلى الله عليه وسلم، موصولة في الدنيا والآخرة، وأن سببه ونسبه لا ينقطعان','102','0','0');
INSERT INTO [t22797] VALUES (11,'٨ - باب: ما جاء في مولد الشافعي المطلبي، رحمه الله','113','0','0');
INSERT INTO [t22797] VALUES (12,'٩ - باب: ما جاء في نسب الشافعي، رضي الله عنه','118','0','0');
INSERT INTO [t22797] VALUES (13,'١٠ - باب: ما جاء في تسليمه إلى المعلم','134','0','0');
INSERT INTO [t22797] VALUES (14,'١١ - باب: ما جاء في اشتغاله بتعلم الأدب والشعر، وسبب أخذه في تعلم العلم','138','0','0');
INSERT INTO [t22797] VALUES (15,'١٢ - باب: ما جاء في رحلته إلى أبي عبد الله: مالك بن انس، الإمام رحمه الله، في تعلم العلم','142','0','0');
INSERT INTO [t22797] VALUES (16,'١٣ - باب: ما جاء في خروجه إلى اليمن ومقامه بها، ثم في حمله من اليمن إلى هارون، وما جرى بينه وبين محمد بن الحسن من المناظرة','147','0','0');
INSERT INTO [t22797] VALUES (17,'١٤ - باب: ما جاء في رؤيا الشافعي وهو في الحبس، وتصديق الله سبحانه رؤياه فيما عبر به','190','0','0');
INSERT INTO [t22797] VALUES (18,'١٥ - باب: ما يستدل به على كبير محل الشافعي عند هارون الرشيد بعد ما جرى مما قدمنا ذكره، ثم عند المأمون','194','0','0');
INSERT INTO [t22797] VALUES (19,'١٦ - باب: ما يستدل به على عود الحال فيما بين الشافعي وبين محمد ابن الحسن، رحمهما الله، إلى الجميل، وتعظيم كل واحد منهما صاحبه وتوقيره','200','0','0');
INSERT INTO [t22797] VALUES (20,'١٧ - باب: ما جاء في كتبة الشافعي كتب محمد بن الحسن؛ ليعلم أقاويل أهل العراق فيمكنه أن يناظرهم، ويناقضهم بما يخالف منها أصولهم','204','0','0');
INSERT INTO [t22797] VALUES (21,'١٨ - باب: ما جاء في صحة نية الشافعي، وجميل قصده في وضع الكتب ومناظرة من خالفه','215','0','0');
INSERT INTO [t22797] VALUES (22,'١٩ - باب: ما جاء في حسن مناظرة الشافعي، رحمه الله، وغلبته بالعلم، والبيان كل من ناظره','220','0','0');
INSERT INTO [t22797] VALUES (23,'٢٠ - باب: ما جاء في قدوم الشافعي، رضي الله عنه، العراق، أيام المأمون للتدريس والتعليم، وانتفاع المسلمين بعلمه','262','0','0');
INSERT INTO [t22797] VALUES (24,'٢١ - باب: ما جاء في سبب تصنيف الشافعي، رحمه الله، كتاب الرسالة القديمة','272','0','0');
INSERT INTO [t22797] VALUES (25,'٢٢ - باب: ما جاء في قدوم الشافعي، رحمه الله، مصر وتصنيفه بها الكتب المصرية [الجديدة] وانتفاع المسلمين بها','279','0','0');
INSERT INTO [t22797] VALUES (26,'٢٣ - باب: ذكر عدد ما وصل إلينا من مصنفات الشافعي، رحمه الله','288','0','0');
INSERT INTO [t22797] VALUES (27,'٢٤ - باب: ما يستدل به على رغبة علماء عصر الشافعي ومن بعدهم في كتبه، والاقتباس من علمه، والانتفاع به، وحسن الثناء عليه','302','0','0');
INSERT INTO [t22797] VALUES (28,'٢٥ - باب: ما يستدل به على حفظ الشافعي، رحمه الله تعالى، لكتاب الله، عز وجل، ومعرفته بالقراءات، وحسن صوته بالقراءة','318','0','0');
INSERT INTO [t22797] VALUES (29,'٢٦ - باب: ما يستدل به على معرفة الشافعي، رحمه الله، بتفسير القرآن ومعانيه، وسبب نزوله','326','0','0');
INSERT INTO [t22797] VALUES (30,'٢٧ - باب: ما يستدل به على معرفة الشافعي بمعاني أخبار رسول الله، صلى الله عليه وسلم','343','0','0');
INSERT INTO [t22797] VALUES (31,'٢٨ - باب: ما يستدل به على فقه الشافعي، وتقدمه فيه، وحسن استنباطه','380','0','0');
INSERT INTO [t22797] VALUES (32,'٢٩ - باب: ما يستدل به على معرفة الشافعي بأصول الفقه','410','0','0');
INSERT INTO [t22797] VALUES (33,'٣٠ - باب: ما يستدل به على معرفة الشافعي بأصول الكلام، وصحة اعتقاده فيها','427','0','0');
INSERT INTO [t22797] VALUES (34,'٣١ - باب: ما يؤثر عنه، رحمه الله، في دلائل التوحيد','441','0','0');
INSERT INTO [t22797] VALUES (35,'٣٢ - باب: ما يؤثر عن الشافعي، رحمه الله، في أسماء الله، وصفات ذاته، وأن القرآن كلام الله وكلامه من صفات ذاته','445','0','0');
INSERT INTO [t22797] VALUES (36,'٣٣ - باب: ما يؤثر عنه في إثبات المشيئة لله عز وجل وهي من صفات الذات، وفي إثبات القدر وخلق الأفعال، وعذاب القبر','454','0','0');
INSERT INTO [t22797] VALUES (37,'٣٤ - باب: ما يؤثر عنه في إثبات الرؤية','461','0','0');
INSERT INTO [t22797] VALUES (38,'٣٥ - باب: ما يؤثر عنه في تفضيل النبي، صلى الله عليه وسلم، على جميع الخلق، وإثبات الشفاعة له','464','0','0');
INSERT INTO [t22797] VALUES (39,'٣٦ - باب: ما يؤثر عنه في الذنوب التي هي دون الكفر بالله عز وجل','469','0','0');
INSERT INTO [t22797] VALUES (40,'٣٧ - باب: ما يؤثر عنه فيما يلحق الميت من فعل غيره','472','0','0');
INSERT INTO [t22797] VALUES (41,'٣٨ - باب: ما يؤثر عنه في الخلفاء الأربعة رضي الله عنهم','474','0','0');
INSERT INTO [t22797] VALUES (42,'٣٩ - باب: ما يؤثر عنه في جملة الصحابة، رضي الله عنهم وعنه','484','0','0');
INSERT INTO [t22797] VALUES (43,'٤٠ - باب: ما يؤثر عنه في قتال أمير المؤمنين: علي بن أبي طالب أهل القبلة','487','0','0');
INSERT INTO [t22797] VALUES (44,'٤١ - باب: ما جاء عن الشافعي، رحمه الله، في مجانبة أهل الأهواء، وبغضه إياهم، وذمه كلامهم، وإزرائه بهم، ودقه عليهم، ومناظرته إياهم','494','0','0');
INSERT INTO [t22797] VALUES (45,'٤٢ - باب: ما يستدل به على حسن اعتقاد الشافعي في متابعة السنة، ومجانبة البدعة','513','0','0');
INSERT INTO [t22797] VALUES (46,'٤٣ - باب: ما يستدل به على معرفة الشافعي رضي الله عنه بالحديث','528','0','0');
INSERT INTO [t22797] VALUES (47,'٤٤ - باب: ما يستدل به على معرفة الشافعي رحمه الله بالجرح والتعديل','542','0','0');
INSERT INTO [t22797] VALUES (48,'٤٥ - باب ما يستدل به على معرفة الشافعي رضي الله عنه بصحة الحديث وعلته','596','0','0');
INSERT INTO [t22797] VALUES (49,'٤٦ - باب ما يستدل به على إتقان الشافعي رحمه الله في الرواية، ومذهبه في قبول الأخبار، واحتياطه فيها','616','0','0');
INSERT INTO [t22797] VALUES (50,'٤٧ - باب ما يستدل به على فصاحة الشافعي، ومعرفته باللغة وديوان العرب','632','0','0');
INSERT INTO [t22797] VALUES (51,'٤٨ - باب ذكر أبيات تؤثر مما أنشد الشافعي لنفسه أو أنشد لغيره','651','0','0');
INSERT INTO [t22797] VALUES (52,'٤٩ - باب ما يستدل به على معرفة الشافعي، رحمه الله، بالطب','705','0','0');
INSERT INTO [t22797] VALUES (53,'٥٠ - باب ما يستدل به على معرفة الشافعي بالنجوم','716','0','0');
INSERT INTO [t22797] VALUES (54,'٥١ - باب ما يستدل به على معرفة الشافعي، رحمه الله، بالرمي والفروسية','718','0','0');
INSERT INTO [t22797] VALUES (55,'٥٢ - باب ما يؤثر عن الشافعي، رحمه الله، في فراسته وإصابته فيها','721','0','0');
INSERT INTO [t22797] VALUES (56,'٥٣ - باب ما يؤثر عن الشافعي، رحمه الله، في فضل العلم والترغيب في تعلمه وتعليمه والعمل به','729','0','0');
INSERT INTO [t22797] VALUES (57,'٥٤ - باب ما يستدل به على اجتهاد الشافعي، رحمه الله، في طاعة ربه، وزهده في الدنيا وحضه عليه','748','0','0');
INSERT INTO [t22797] VALUES (58,'٥٥ - باب ما يستدل به على تمكن الشافعي، رحمه الله، من عقله، وما يؤثر عنه في الآداب','776','0','0');
INSERT INTO [t22797] VALUES (59,'٥٦ - باب ما يستدل به على سخاوة الشافعي، رحمه الله، وحسن جوده، وحسن عهده، وما يؤثر عنه في السخاء','811','0','0');
INSERT INTO [t22797] VALUES (60,'٥٧ - باب ما يستدل به على شهادة أئمة المسلمين وعلمائهم للشافعي، رضي الله عنه، بالتقدم في العلم، واعترافهم له به، وحسن ثنائهم عليه، وجميل دعائهم له','828','0','0');
INSERT INTO [t22797] VALUES (61,'٥٨ - باب ما يؤثر من خضاب الشافعي، رحمه الله، ولباسه وهيئته، ونقش خاتمه','874','0','0');
INSERT INTO [t22797] VALUES (62,'٥٩ - باب ذكر وصية الشافعي، رضي الله عنه وأرضاه','879','0','0');
INSERT INTO [t22797] VALUES (63,'٦٠ - باب ذكر مرض الشافعي، رحمه الله، وأوجاعه، ووفاته، وتربته، ومقدار سنه، وغير ذلك','882','0','0');
INSERT INTO [t22797] VALUES (64,'٦١ - باب ذكر أهل الشافعي وأولاده، رحمهم الله','897','0','0');
INSERT INTO [t22797] VALUES (65,'٦٢ - باب ذكر من روى عنهم الشافعي من علماء الحجاز واليمن ومصر والعراق وخراسان','902','0','0');
INSERT INTO [t22797] VALUES (66,'٦٣ - باب ذكر أصحاب الشافعي، رحمه الله، الذين حملوا عنه العلم أو رووا عنه حديثا، أو حكوا عنه حكاية','915','0','0');
INSERT INTO [t22797] VALUES (67,'٦٤ - باب ذكر من قعد في مجلس الشافعي بعد وفاته، ومن قام من أصحابه بنشر علمه','928','0','0');
