INSERT INTO [t22812] VALUES (1,'١- «ما وسعني سمائي ولا أرضي، بل وسعني قلب عبدي المؤمن»','3','0','0');
INSERT INTO [t22812] VALUES (2,'٢- «القلب بيت الرب»','4','0','0');
INSERT INTO [t22812] VALUES (3,'٣- «كنت كنزا لا أعرف، فأحببت أن أعرف فخلقت خلقا. فعرفتهم بي، فبي عرفوني»','5','0','0');
INSERT INTO [t22812] VALUES (4,'٤- «أنا من الله، والمؤمنون مني»','6','0','0');
INSERT INTO [t22812] VALUES (5,'٥- «لا راحة للمؤمن دون لقاء ربه»','7','0','0');
INSERT INTO [t22812] VALUES (6,'٦- «إن الله عز وجل لما خلق العقل قال له: أقبل، فأقبل، ثم قال له: أدبر، فأدبر. فقال: وعزتي وجلالي ما خلقت خلقا أشرف منك، فبك آخذ وبك أعطي»','8','0','0');
INSERT INTO [t22812] VALUES (7,'٧- «حب الدنيا رأس كل خطيئة»','9','0','0');
INSERT INTO [t22812] VALUES (8,'٨- «الدنيا خطوة رجل مؤمن»','10','0','0');
INSERT INTO [t22812] VALUES (9,'٩- «من بورك له في شيء فليلزمه»','11','0','0');
INSERT INTO [t22812] VALUES (10,'١٠- «من ألزم نفسه شيئا لزمه»','11','0','0');
INSERT INTO [t22812] VALUES (11,'١١- «اتخذوا مع الفقراء أيادي فإن لهم في غد دولة وأي دولة»','12','0','0');
INSERT INTO [t22812] VALUES (12,'١٢- «الفقر فخري، وبه أفتخر»','12','0','0');
INSERT INTO [t22812] VALUES (13,'١٣- «أن أبا محذورة أنشد بين يدي النبي صلى الله عليه وسلم: قد لسعت حية الهوى كبدي ... فـ لا طبيب لها ولا راقي إلى آخرها. وتواجد رسول الله صلى الله عليه وسلم، ووقعت البردة عن كتفيه، فتقاسمها فقراء الصفة وجعلوها رقعا في ثيابهم»','13','0','0');
INSERT INTO [t22812] VALUES (14,'١٤- «أن عمر بن الخطاب قال: كان رسول الله صلى الله عليه وسلم إذا تكلم مع أبي بكر كنت بينهما كالزنجي الذي لا يفهم»','14','0','0');
INSERT INTO [t22812] VALUES (15,'١٥- «أنا مدينة العلم وعلي بابها»','15','0','0');
INSERT INTO [t22812] VALUES (16,'١٦- «يعتذر إلى الفقراء يوم القيامة ويقول - يعني الله تعالى -: وعزتي وجلالي ما زويت الدنيا عنكم لهوانكم علي، ولكن أردت أن أرفع قدركم في هذا اليوم. انطلقوا إلى الموقف، فمن أحسن إليكم بكسرة، أو سقاكم شربة من ماء أو كساكم خرقة انطلقوا به إلى الجنة»','16','0','0');
INSERT INTO [t22812] VALUES (17,'١٧- «أنه صلى الله عليه وسلم لما قدم المدينة في الهجرة خرجت بنات النجار بالدفوف وهن يقلن: طلع البدر علينا ... من ثنيات الوداع إلى آخر الشعر. فقال لهم رسول الله صلى الله عليه وسلم: «هزوا كرابيلكم بارك الله فيكم»','17','0','0');
INSERT INTO [t22812] VALUES (18,'١٨- «لو وزن إيمان أبي بكر بإيمان الناس لرجح إيمان أبي بكر على إيمان الناس»','18','0','0');
INSERT INTO [t22812] VALUES (19,'١٩- «اللهم إنك أخرجتني من أحب البقاع إلي، فأسكني في أحب البقاع إليك»','19','0','0');
INSERT INTO [t22812] VALUES (20,'٢٠- «من زارني وزار أبي إبراهيم في عام واحد دخل الجنة»','20','0','0');
INSERT INTO [t22812] VALUES (21,'٢١- «فقراؤكم حسناتكم»','21','0','0');
INSERT INTO [t22812] VALUES (22,'٢٢- «البركة مع أكابركم»','22','0','0');
INSERT INTO [t22812] VALUES (23,'٢٣- «أكرموا ظهوركم فإن فيها منافع للناس»','23','0','0');
INSERT INTO [t22812] VALUES (24,'٢٤- «الشيخ في قومه كالنبي في أمته»','24','0','0');
INSERT INTO [t22812] VALUES (25,'٢٥- «لو وزن خوف المؤمن ورجاؤه لاعتدلا»','25','0','0');
INSERT INTO [t22812] VALUES (26,'٢٦- «عن علي رضي الله عنه، أن أعرابيا صلى ونقر صلاته، فقال له علي: لا تنقر صلاتك. فقال الأعرابي: يا علي! لو نقرها أبوك ما دخل النار»','26','0','0');
INSERT INTO [t22812] VALUES (27,'٢٧- «ويروونه عن عمر أيضا»','26','0','0');
INSERT INTO [t22812] VALUES (28,'٢٨- «ويروون عن عمر أنه قتل أباه»','27','0','0');
INSERT INTO [t22812] VALUES (29,'٢٩- «كنت نبيا وآدم بين الماء والطين، وكنت نبيا ولا آدم ولا ماء ولا طين»','28','0','0');
INSERT INTO [t22812] VALUES (30,'٣٠- «العازب فراشه من نار»','29','0','0');
INSERT INTO [t22812] VALUES (31,'٣١- «مسكين رجل بلا امرأة، ومسكينة امرأة بلا رجل»','29','0','0');
INSERT INTO [t22812] VALUES (32,'٣٢- «ويروون عن إبراهيم صلى الله عليه وسلم أنه لما بنى البيت صلى في كل ركن ألف ركعة، فأوحى الله إليه: يا إبراهيم! أفضل من هذا سد جوعة أو ستر عورة»','30','0','0');
INSERT INTO [t22812] VALUES (33,'٣٣- «إذا ذكر إبراهيم الخليل، وذكرت أنا فصلوا عليه ثم صلوا علي. وإذا ذكرت أنا والأنبياء غيره فصلوا علي ثم صلوا عليهم»','31','0','0');
INSERT INTO [t22812] VALUES (34,'٣٤- «من قال: أنا في الجنة؛ فهو في النار. ومن قال أنا في النار؛ فهو كما قال»','32','0','0');
INSERT INTO [t22812] VALUES (35,'٣٥- «من أخلص لله عز وجل أربعين يوما تفجرت ينابيع الحكمة من قلبه على لسانه»','33','0','0');
INSERT INTO [t22812] VALUES (36,'٣٦- «من أكل مع مغفور له غفر له»','34','0','0');
INSERT INTO [t22812] VALUES (37,'٣٧- «من أشبع جوعة أو ستر عورة ضمنت له على الله الجنة»','35','0','0');
INSERT INTO [t22812] VALUES (38,'٣٨- «صدقة السر تطفئ غضب الرب»','36','0','0');
INSERT INTO [t22812] VALUES (39,'٣٩- «لا تكرهوا الفتن، فإن فيها حصاد المنافقين»','37','0','0');
INSERT INTO [t22812] VALUES (40,'٤٠- «سب صحابتي ذنب لا يغفر»','38','0','0');
INSERT INTO [t22812] VALUES (41,'٤١- «ما ينقص مال من صدقة، بل يزيد بها»','39','0','0');
INSERT INTO [t22812] VALUES (42,'٤٢- «يوم الجمعة حج المساكين»','40','0','0');
INSERT INTO [t22812] VALUES (43,'٤٣- «ما سعد من سعد إلا بالدعاء، وما شقي من شقي إلا بالدعاء»','41','0','0');
INSERT INTO [t22812] VALUES (44,'٤٤- «الدعاء مخ العبادة»','41','0','0');
INSERT INTO [t22812] VALUES (45,'٤٥- «من علم أخاه آية من كتاب الله عز وجل فقد ملك رقه»','42','0','0');
INSERT INTO [t22812] VALUES (46,'٤٦- «اطلعت على ذنوب أمتي، فلم أجد ذنبا أعظم ممن تعلم آية ثم نسيها»','43','0','0');
INSERT INTO [t22812] VALUES (47,'٤٧- «من وسع على أهله في يوم عاشوراء وسع الله عليه سائر سنته»','44','0','0');
INSERT INTO [t22812] VALUES (48,'٤٨- أن «آية من القرآن خير من محمد وآله»','45','0','0');
INSERT INTO [t22812] VALUES (49,'٤٩- «أنا من العرب، وليس الأعراب مني»','46','0','0');
INSERT INTO [t22812] VALUES (50,'٥٠- «اللهم أحيني مسكينا، وأمتني مسكينا، واحشرني في زمرة المساكين»','47','0','0');
INSERT INTO [t22812] VALUES (51,'٥١- «إذا سمعتم عني حديثا فاعرضوه على الكتاب والسنة فإن وافق فارووه عني، وإن لم يوافق فلا ترووه عني»','48','0','0');
INSERT INTO [t22812] VALUES (52,'٨٠- «يا علي اتخذ لك نعلين من حديد، وأفنهما في طلب العلم»','49','0','0');
INSERT INTO [t22812] VALUES (53,'٨١- «اطلب العلم ولو بالصين»','49','0','0');
INSERT INTO [t22812] VALUES (54,'٥٢- «يا علي، كن عالما أو متعلما أو مستمعا واعيا ولا تكن الرابعة فتهلك»','50','0','0');
INSERT INTO [t22812] VALUES (55,'٥٣- «يقول الله تعالى: لاقوني بنياتكم ولا تلاقوني بأعمالكم»','51','0','0');
INSERT INTO [t22812] VALUES (56,'٥٤- «من علم علما نافعا وأخفاه عن المسلمين ألجمه الله بلجام من نار»','52','0','0');
INSERT INTO [t22812] VALUES (57,'٥٥- «من قدم إبريقا لمتوضئ فكأنما قدم جوادا مسروجا ملجوما يقاتل عليه في سبيل الله تعالى»','53','0','0');
INSERT INTO [t22812] VALUES (58,'٥٦- «يأتي على أمتي زمان، القابض فيه على دينه كالقابض على الجمر»','54','0','0');
INSERT INTO [t22812] VALUES (59,'٥٧- «يأتي على أمتي زمان ما يسلم بدينه إلا من يفر من شاهق إلى شاهق»','55','0','0');
INSERT INTO [t22812] VALUES (60,'٥٨- «حسنات الأبرار سيئات المقربين»','56','0','0');
INSERT INTO [t22812] VALUES (61,'٥٩- «بدأ الإسلام غريبا، وسيعود غريبا كما بدأ فطوبى للغرباء»','57','0','0');
INSERT INTO [t22812] VALUES (62,'٦٠- «سيجري بين أصحابي هنيئة: القاتل والمقتول في الجنة»','58','0','0');
INSERT INTO [t22812] VALUES (63,'٦١- «إذا وصلتم إلى ما شجر بين أصحابي فأمسكوا وإذا وصلتم إلى القضاء والقدر فأمسكوا»','59','0','0');
INSERT INTO [t22812] VALUES (64,'٦٢- «إذا كثرت الفتن فعليكم بأطراف اليمن»','60','0','0');
INSERT INTO [t22812] VALUES (65,'٦٣- «مصر كنانة الله في أرضه، ما طلبها عدو إلا أهلكه الله»','61','0','0');
INSERT INTO [t22812] VALUES (66,'٦٤- «إن في آخر الزمان يكون أجر أحدهم كأجر سبعين منكم» يقوله للصحابة، فقالت الصحابة رضي الله عنهم: منهم؟ فقال: «منكم» ثلاثا «لأنكم تجدون على الخير أعوانا ولا يجدون على الخير أعوانا»','62','0','0');
INSERT INTO [t22812] VALUES (67,'٦٥- «من تزوج امرأة لمالها أحرمه الله مالها وجمالها»','63','0','0');
INSERT INTO [t22812] VALUES (68,'٦٦- «تزوجوا فقراء يغنيكم الله»','64','0','0');
INSERT INTO [t22812] VALUES (69,'٦٧- «من بات في حراسة كلب بات في غضب الله»','65','0','0');
INSERT INTO [t22812] VALUES (70,'٦٨- «أنه أمر النساء بالغنج لأزواجهن عند الجماع»','66','0','0');
INSERT INTO [t22812] VALUES (71,'٦٩- «أنه قال لسلمان الفارسي وهو يأكل العنب: يا سلمان! كل العنب دو دو»','67','0','0');
INSERT INTO [t22812] VALUES (72,'٧٠- «الجنة تحت أقدام الأمهات»','68','0','0');
INSERT INTO [t22812] VALUES (73,'٧١- «من كسر قلبا فعليه جبره»','69','0','0');
INSERT INTO [t22812] VALUES (74,'٧٢- «أحق ما أخذتم عليه الأجر القرآن»','71','0','0');
INSERT INTO [t22812] VALUES (75,'٧٣- «من ظلم ذميا كان الله خصمه يوم القيامة - أو كنت خصمه يوم القيامة -»','72','0','0');
INSERT INTO [t22812] VALUES (76,'٧٤- «من أسرج في مسجد سراجا لم تزل الملائكة وحملة العرش يستغفرون له ما دام في ذلك المسجد ضوء من ذلك السراج»','73','0','0');
INSERT INTO [t22812] VALUES (77,'٧٥- «لكل شيء تحية، وتحية المسجد ركعتان»','74','0','0');
INSERT INTO [t22812] VALUES (78,'٧٦- «أنه مد رجليه في المسجد، فأوحى الله إليه: يا محمد! ما أنت في منزل عائشة»','75','0','0');
INSERT INTO [t22812] VALUES (79,'٧٧- «لو كان المؤمن في ذروة جبل قيض الله له من يؤذيه، أو شيطانا يؤذيه»','76','0','0');
INSERT INTO [t22812] VALUES (80,'٧٨- «أدبني ربي فأحسن تأديبي»','77','0','0');
INSERT INTO [t22812] VALUES (81,'٧٩- «لو كانت الدنيا دما عبيطا لكان قوت المؤمن منها حلالا»','78','0','0');
