INSERT INTO [t22853] VALUES (1,'مقدمة','3','0','0');
INSERT INTO [t22853] VALUES (2,'الحديث الأول: ((إذا جعلت إصبعيك في أذنك، سمعت خرير الكوثر))','10','0','0');
INSERT INTO [t22853] VALUES (3,'الحديث الثاني: ((إذا عسر على المرأة ولدها أخذ إناء نظيفا يكتب فيه: (كأنهم يوم يرون ما يوعدون) إلى آخر الآية، و: (كأنهم يوم يرونها لم يلبثوا إلا عشية أو ضحاها) و: (لقد كان في قصصهم عبرة لأولي الألباب) إلى آخر الآبة، ثم يغسل ويسقي المرأة منه، وينضح على بطنها وفرجها))','13','0','0');
INSERT INTO [t22853] VALUES (4,'الحديث الثالث: ((إذا كثرت ذنوب العبد، ولم يكن له ما يكفرها من العمل، ابتلاه الله عز وجل بالحزن ليكفرها عنه))','18','0','0');
INSERT INTO [t22853] VALUES (5,'الحديث الرابع: إن رسول الله صلى الله عليه وعلى آله وسلم: ((أري الناس قبله أو ما شاء الله من ذلك، فكأنه تقاصر أعمال أمته أن لا يبلغوا من العمل مثل الذي بلغ غيرهم في طول العمر، فأعطاه الله ليلة القدر خير من ألف شهر))','22','0','0');
INSERT INTO [t22853] VALUES (6,'الحديث الخامس: ((اسم الله الذي إذا دعي به أجاب، وإذا سئل به أعطى: دعوة يونس ابن متى))','27','0','0');
INSERT INTO [t22853] VALUES (7,'الحديث السادس: ((أفضل الحسنات، تكرمة الجلساء))','32','0','0');
INSERT INTO [t22853] VALUES (8,'الحديث السابع: ((اللهم إني ضعيف، فوق في رضاك ضعفي، وخذ إلى الخير بناصيتي، واجعل الإسلام منتهى رضائي، اللهم إني ضعيف فقوني، وذليل فأعزني، وفقير فارزقني))','34','0','0');
INSERT INTO [t22853] VALUES (9,'الحديث الثامن: ((اللهم هذه قسمتي فيما أملك، فلا تلمني فيما تكلك ولا أملك))','38','0','0');
INSERT INTO [t22853] VALUES (10,'الحديث التاسع: ((اللهم لا تقتلنا بغضبك، ولا تهلكنا بعذابك، وعافنا قبل ذلك))','40','0','0');
INSERT INTO [t22853] VALUES (11,'الحديث العاشر: ((إنما سماهم الله الأبرار، لأنهم بروا الآباء والأبناء))','43','0','0');
INSERT INTO [t22853] VALUES (12,'الحديث الحادي عشر: ((الأنبياء قادة، والفقهاء سادة، ومجالستهم زيادة))','46','0','0');
INSERT INTO [t22853] VALUES (13,'الحديث الثاني عشر: ((تبيض وجوه أهل السنة والجماعة، وتسود وجوه أهل البدع))','54','0','0');
INSERT INTO [t22853] VALUES (14,'الحديث الثالث عشر: ((تعلموا العلم، فإن تعلمه لله خشية، وطلبه عبادة، ومذاكرته تسبيح، والبحث عنه جهاد، وتعليمه لمن لا يعلمه صدقة، وبذله لأهله قربة، لأنه معالم الحلال والحرام، ومنار سبيل أهل الجنة، وهو الأنيس في الوحشة، والصاحب في الغربة، والمحدث في الخلوة، والدليل على السراء والضراء، والسلاح على الأعداء، والزين عند الأخلاء، يرفع الله به أقواما فيجعلهم في الخير قادة وأئمة، تقتص آثارهم، ويقتدى بأفعالهم، وينتهى إلى رأيهم، ترغب الملائكة في خلتهم، وبأجنحتها تمسحهم، يستغفر لهم كل رطب ويابس، وحيتان البجر وهوامه، وسباع البر وأنعامه، لأن العلم حياة القلوب من الجهل، ومصابيح الأبصار من الظلم. يبلغ العبد بالعلم منازل الأخيار، والدرجات العلى في الدنيا والآخرة. التفكر فيه بعدل الصيام، ومدارسته تعدل القيام، به توصل الأرحام، وب يعرف الحلال من الحرام، هو إمام العمل، والعمل تابعه، يلهمه السعداء ويحرمه الإشقياء))','59','0','0');
INSERT INTO [t22853] VALUES (15,'الحديث الرابع عشر: ((تكفير كل لحاء ركعتين))','65','0','0');
INSERT INTO [t22853] VALUES (16,'الحديث الخامس عشر: ((جهد البلاء كثرة العيال مع قلة الشئ))','69','0','0');
INSERT INTO [t22853] VALUES (17,'الحديث السابع عشر: ((حسنات الأبرار سيئات المقربين))','72','0','0');
INSERT INTO [t22853] VALUES (18,'الحديث السابع عشر: ((الدنيا دار من لا دار له، [ومال من لا مال له] ، ولها يجمع من لا عقل له))','76','0','0');
INSERT INTO [t22853] VALUES (19,'الحديث الثامن عشر: ((صاحب الشئ أحق أن يحمله إلا أن يكون ضعيفا يعجز عنه، فيعينه أخوه المسلم))','82','0','0');
INSERT INTO [t22853] VALUES (20,'الحديث التاسع عشر: ((القلوب أربعة: قلب أجرد فيه مثل السراج يزهر، وقلب اغلف مربوط على غلافه، وقلب منكوس، وقلب مصفح فأما القلب الأجرد، فقلب المؤمن سراجه فيه نوره. وأما القلب الأغلف، فقلب الكافر. وأما القلب المنكوس، فقلب المنافق، عرف ثم أنكر. وأما القلب المصفح، فقلب فيه إيمان ونفاق، فمثل الإيمان فيه كمثل البقلة يمدها الماء الطيب، ومثل النفاق فيه كمثل القرحة يمدها القبح والدم، فأي المادتين غليت على الأخرى غليت عليه))','87','0','0');
INSERT INTO [t22853] VALUES (21,'الحديث العشرون: ((الكيس من دان نفسه وعمل لما بعد الموت، والعاجز من أتبع نفسه هواها وتمنى على الله))','94','0','0');
INSERT INTO [t22853] VALUES (22,'الحديث الحادي والعشرون: ((لو خشع قلب هذا لخشعت جوارحه))','101','0','0');
INSERT INTO [t22853] VALUES (23,'الحديث الثاني والعشرون: ((ما اجتمع الحلال والحرام، إلا غلب الحرام الحلال))','107','0','0');
INSERT INTO [t22853] VALUES (24,'الحديث الثالث والعشرون: ((من استمع إلى آية من كتاب الله تعالى كتب له حسنة مضاعفة، ومن تلاها كانت له نورا يوم القيامة))','109','0','0');
INSERT INTO [t22853] VALUES (25,'الحديث الرابع والعشرون: ((لا يزال المسروق منه في تهمة من هو برئ، حتى يكون أعظم جرما من السارق))','114','0','0');
INSERT INTO [t22853] VALUES (26,'الحديث الخامس والعشرين: ((يوشك أن لا يبقى من الإسلام إلا اسمه، ومن القرآن إلا رسمه، مساجدهم عامر، وهي خراب من الهدي، فقهاؤهم شر من تحت أديم السماء، من عندهم خرجت الفتنة، وفيهم تعود))','117','0','0');
