INSERT INTO [t22928] VALUES (1,'المقدمة','2','0','0');
INSERT INTO [t22928] VALUES (2,'إعراب الاستعاذة','3','0','0');
INSERT INTO [t22928] VALUES (3,'الفاتحة','4','0','0');
INSERT INTO [t22928] VALUES (4,'قال تعالى: (بسم الله الرحمن الرحيم (١))','4','3','0');
INSERT INTO [t22928] VALUES (5,'قال تعالى: (الحمد لله رب العالمين (٢) الرحمن الرحيم (٣))','6','3','0');
INSERT INTO [t22928] VALUES (6,'قال تعالى: (مالك يوم الدين (٤))','7','3','0');
INSERT INTO [t22928] VALUES (7,'قال تعالى: (إياك نعبد وإياك نستعين (٥))','7','3','0');
INSERT INTO [t22928] VALUES (8,'قال تعالى: (اهدنا الصراط المستقيم (٦) صراط الذين أنعمت عليهم) .','8','3','0');
INSERT INTO [t22928] VALUES (9,'قال تعالى: (غير المغضوب عليهم ولا الضالين)','10','3','0');
INSERT INTO [t22928] VALUES (10,'البقرة','15','0','0');
INSERT INTO [t22928] VALUES (11,'قال تعالى: (ذلك الكتاب لا ريب فيه هدى للمتقين (٢)) .','15','10','0');
INSERT INTO [t22928] VALUES (12,'قال تعالى: (الذين يؤمنون بالغيب ويقيمون الصلاة ومما رزقناهم ينفقون (٣)) .','17','10','0');
INSERT INTO [t22928] VALUES (13,'قال تعالى: (والذين يؤمنون بما أنزل إليك وما أنزل من قبلك وبالآخرة هم يوقنون (٤)) .','20','10','0');
INSERT INTO [t22928] VALUES (14,'قال تعالى: (أولئك على هدى من ربهم وأولئك هم المفلحون (٥)) .','21','10','0');
INSERT INTO [t22928] VALUES (15,'قال تعالى: (إن الذين كفروا سواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون (٦)) .','22','10','0');
INSERT INTO [t22928] VALUES (16,'قال تعالى: (ختم الله على قلوبهم وعلى سمعهم وعلى أبصارهم غشاوة ولهم عذاب عظيم (٧))','23','10','0');
INSERT INTO [t22928] VALUES (17,'قال تعالى: (ومن الناس من يقول آمنا بالله وباليوم الآخر وما هم بمؤمنين (٨)) .','25','10','0');
INSERT INTO [t22928] VALUES (18,'قال تعالى: (يخادعون الله والذين آمنوا وما يخدعون إلا أنفسهم وما يشعرون (٩)) .','26','10','0');
INSERT INTO [t22928] VALUES (19,'قال تعالى: (في قلوبهم مرض فزادهم الله مرضا ولهم عذاب أليم بما كانوا يكذبون (١٠)) .','27','10','0');
INSERT INTO [t22928] VALUES (20,'قال تعالى: (وإذا قيل لهم لا تفسدوا في الأرض قالوا إنما نحن مصلحون (١١)) .','28','10','0');
INSERT INTO [t22928] VALUES (21,'قال تعالى: (ألا إنهم هم المفسدون ولكن لا يشعرون (١٢)) .','30','10','0');
INSERT INTO [t22928] VALUES (22,'قال تعالى: (وإذا قيل لهم آمنوا كما آمن الناس قالوا أنؤمن كما آمن السفهاء ألا إنهم هم السفهاء ولكن لا يعلمون (١٣)) .','31','10','0');
INSERT INTO [t22928] VALUES (23,'قال تعالى: (وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلوا إلى شياطينهم قالوا إنا معكم إنما نحن مستهزئون (١٤) الله يستهزئ بهم ويمدهم في طغيانهم يعمهون (١٥)) .','31','10','0');
INSERT INTO [t22928] VALUES (24,'قال تعالى: (أولئك الذين اشتروا الضلالة بالهدى فما ربحت تجارتهم وما كانوا مهتدين (١٦)) .','32','10','0');
INSERT INTO [t22928] VALUES (25,'قال تعالى: (مثلهم كمثل الذي استوقد نارا فلما أضاءت ما حوله ذهب الله بنورهم وتركهم في ظلمات لا يبصرون (١٧)) .','33','10','0');
INSERT INTO [t22928] VALUES (26,'قال تعالى: (صم بكم عمي فهم لا يرجعون) ((١٨)) .','35','10','0');
INSERT INTO [t22928] VALUES (27,'قال تعالى: (أو كصيب من السماء فيه ظلمات ورعد وبرق يجعلون أصابعهم في آذانهم من الصواعق حذر الموت والله محيط بالكافرين (١٩)) .','35','10','0');
INSERT INTO [t22928] VALUES (28,'قال تعالى: (يكاد البرق يخطف أبصارهم كلما أضاء لهم مشوا فيه وإذا أظلم عليهم قاموا ولو شاء الله لذهب بسمعهم وأبصارهم إن الله على كل شيء قدير (٢٠)) .','37','10','0');
INSERT INTO [t22928] VALUES (29,'قال تعالى: (يا أيها الناس اعبدوا ربكم الذي خلقكم والذين من قبلكم لعلكم تتقون (٢١)) .','38','10','0');
INSERT INTO [t22928] VALUES (30,'قال تعالى: (الذي جعل لكم الأرض فراشا والسماء بناء وأنزل من السماء ماء فأخرج به من الثمرات رزقا لكم فلا تجعلوا لله أندادا وأنتم تعلمون (٢٢)) .','39','10','0');
INSERT INTO [t22928] VALUES (31,'قال تعالى: (وإن كنتم في ريب مما نزلنا على عبدنا فأتوا بسورة من مثله وادعوا شهداءكم من دون الله إن كنتم صادقين (٢٣)) .','40','10','0');
INSERT INTO [t22928] VALUES (32,'قال تعالى: (فإن لم تفعلوا ولن تفعلوا فاتقوا النار التي وقودها الناس والحجارة أعدت للكافرين (٢٤)) .','41','10','0');
INSERT INTO [t22928] VALUES (33,'قال تعالى: (وبشر الذين آمنوا وعملوا الصالحات أن لهم جنات تجري من تحتها الأنهار كلما رزقوا منها من ثمرة رزقا قالوا هذا الذي رزقنا من قبل وأتوا به متشابها ولهم فيها أزواج مطهرة وهم فيها خالدون (٢٥)) .','42','10','0');
INSERT INTO [t22928] VALUES (34,'قال تعالى: (إن الله لا يستحيي أن يضرب مثلا ما بعوضة فما فوقها فأما الذين آمنوا فيعلمون أنه الحق من ربهم وأما الذين كفروا فيقولون ماذا أراد الله بهذا مثلا يضل به كثيرا ويهدي به كثيرا وما يضل به إلا الفاسقين (٢٦)) .','43','10','0');
INSERT INTO [t22928] VALUES (35,'قال تعالى: (الذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض أولئك هم الخاسرون (٢٧)) .','45','10','0');
INSERT INTO [t22928] VALUES (36,'قال تعالى: (كيف تكفرون بالله وكنتم أمواتا فأحياكم ثم يميتكم ثم يحييكم ثم إليه ترجعون) ((٢٨)) .','46','10','0');
INSERT INTO [t22928] VALUES (37,'قال تعالى: (هو الذي خلق لكم ما في الأرض جميعا ثم استوى إلى السماء فسواهن سبع سموات وهو بكل شيء عليم) (٢٩) .','46','10','0');
INSERT INTO [t22928] VALUES (38,'قال تعالى: (وإذ قال ربك للملائكة إني جاعل في الأرض خليفة قالوا أتجعل فيها من يفسد فيها ويسفك الدماء ونحن نسبح بحمدك ونقدس لك قال إني أعلم ما لا تعلمون (٣٠)) .','47','10','0');
INSERT INTO [t22928] VALUES (39,'قال تعالى: (وعلم آدم الأسماء كلها ثم عرضهم على الملائكة فقال أنبئوني بأسماء هؤلاء إن كنتم صادقين (٣١)) .','49','10','0');
INSERT INTO [t22928] VALUES (40,'قال تعالى: (قالوا سبحانك لا علم لنا إلا ما علمتنا إنك أنت العليم الحكيم (٣٢)) .','50','10','0');
INSERT INTO [t22928] VALUES (41,'قال تعالى: (قال يا آدم أنبئهم بأسمائهم فلما أنبأهم بأسمائهم قال ألم أقل لكم إني أعلم غيب السماوات والأرض وأعلم ما تبدون وما كنتم تكتمون (٣٣)) .','51','10','0');
INSERT INTO [t22928] VALUES (42,'قال تعالى: (وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى واستكبر وكان من الكافرين (٣٤)) .','51','10','0');
INSERT INTO [t22928] VALUES (43,'قال تعالى: (وقلنا يا آدم اسكن أنت وزوجك الجنة وكلا منها رغدا حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين) ((٣٥)) .','53','10','0');
INSERT INTO [t22928] VALUES (44,'قال تعالى: (فأزلهما الشيطان عنها فأخرجهما مما كانا فيه وقلنا اهبطوا بعضكم لبعض عدو ولكم في الأرض مستقر ومتاع إلى حين (٣٦)) .','54','10','0');
INSERT INTO [t22928] VALUES (45,'قال تعالى: (فتلقى آدم من ربه كلمات فتاب عليه إنه هو التواب الرحيم (٣٧)) .','55','10','0');
INSERT INTO [t22928] VALUES (46,'قال تعالى: (قلنا اهبطوا منها جميعا فإما يأتينكم مني هدى فمن تبع هداي فلا خوف عليهم ولا هم يحزنون (٣٨)) .','55','10','0');
INSERT INTO [t22928] VALUES (47,'قال تعالى: (والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار هم فيها خالدون) ((٣٩)) .','57','10','0');
INSERT INTO [t22928] VALUES (48,'قال تعالى: (يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأوفوا بعهدي أوف بعهدكم وإياي فارهبون (٤٠)) .','57','10','0');
INSERT INTO [t22928] VALUES (49,'قال تعالى: (وآمنوا بما أنزلت مصدقا لما معكم ولا تكونوا أول كافر به ولا تشتروا بآياتي ثمنا قليلا وإياي فاتقون) ((٤١)) .','58','10','0');
INSERT INTO [t22928] VALUES (50,'قال تعالى: (ولا تلبسوا الحق بالباطل وتكتموا الحق وأنتم تعلمون) ((٤٢)) .','59','10','0');
INSERT INTO [t22928] VALUES (51,'قال تعالى: (وأقيموا الصلاة وآتوا الزكاة واركعوا مع الراكعين (٤٣)) .','59','10','0');
INSERT INTO [t22928] VALUES (52,'قال تعالى: (أتأمرون الناس بالبر وتنسون أنفسكم وأنتم تتلون الكتاب أفلا تعقلون) ((٤٤)) .','60','10','0');
INSERT INTO [t22928] VALUES (53,'قال تعالى: (واستعينوا بالصبر والصلاة وإنها لكبيرة إلا على الخاشعين) ((٤٥)) .','60','10','0');
INSERT INTO [t22928] VALUES (54,'قال تعالى: (الذين يظنون أنهم ملاقو ربهم وأنهم إليه راجعون) ((٤٦)) .','60','10','0');
INSERT INTO [t22928] VALUES (55,'قال تعالى: (يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأني فضلتكم على العالمين (٤٧)) .','61','10','0');
INSERT INTO [t22928] VALUES (56,'قال تعالى: (واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها شفاعة ولا يؤخذ منها عدل ولا هم ينصرون (٤٨)) .','61','10','0');
INSERT INTO [t22928] VALUES (57,'قال تعالى: (وإذ نجيناكم من آل فرعون يسومونكم سوء العذاب يذبحون أبناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم) ((٤٩)) .','62','10','0');
INSERT INTO [t22928] VALUES (58,'قال تعالى: (وإذ فرقنا بكم البحر فأنجيناكم وأغرقنا آل فرعون وأنتم تنظرون (٥٠)) .','63','10','0');
INSERT INTO [t22928] VALUES (59,'قال تعالى: (وإذ واعدنا موسى أربعين ليلة ثم اتخذتم العجل من بعده وأنتم ظالمون) ((٥١)) .','63','10','0');
INSERT INTO [t22928] VALUES (60,'قال تعالى: (ثم عفونا عنكم من بعد ذلك لعلكم تشكرون) ((٥٢)) .','64','10','0');
INSERT INTO [t22928] VALUES (61,'قال تعالى: (وإذ آتينا موسى الكتاب والفرقان لعلكم تهتدون (٥٣)) .','64','10','0');
INSERT INTO [t22928] VALUES (62,'قال تعالى: (وإذ قال موسى لقومه يا قوم إنكم ظلمتم أنفسكم باتخاذكم العجل فتوبوا إلى بارئكم فاقتلوا أنفسكم ذلكم خير لكم عند بارئكم فتاب عليكم إنه هو التواب الرحيم (٥٤)) .','64','10','0');
INSERT INTO [t22928] VALUES (63,'قال تعالى: (وإذ قلتم يا موسى لن نؤمن لك حتى نرى الله جهرة فأخذتكم الصاعقة وأنتم تنظرون (٥٥)) .','65','10','0');
INSERT INTO [t22928] VALUES (64,'قال تعالى: (وظللنا عليكم الغمام وأنزلنا عليكم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا أنفسهم يظلمون (٥٧)) .','66','10','0');
INSERT INTO [t22928] VALUES (65,'قال تعالى: (وإذ قلنا ادخلوا هذه القرية فكلوا منها حيث شئتم رغدا وادخلوا الباب سجدا وقولوا حطة نغفر لكم خطاياكم وسنزيد المحسنين) ((٥٨)) .','66','10','0');
INSERT INTO [t22928] VALUES (66,'قال تعالى: (فبدل الذين ظلموا قولا غير الذي قيل لهم فأنزلنا على الذين ظلموا رجزا من السماء بما كانوا يفسقون (٥٩)) .','67','10','0');
INSERT INTO [t22928] VALUES (67,'قال تعالى: (وإذ استسقى موسى لقومه فقلنا اضرب بعصاك الحجر فانفجرت منه اثنتا عشرة عينا قد علم كل أناس مشربهم كلوا واشربوا من رزق الله ولا تعثوا في الأرض مفسدين (٦٠)) .','68','10','0');
INSERT INTO [t22928] VALUES (68,'قال تعالى: (وإذ قلتم يا موسى لن نصبر على طعام واحد فادع لنا ربك يخرج لنا مما تنبت الأرض من بقلها وقثائها وفومها وعدسها وبصلها قال أتستبدلون الذي هو أدنى بالذي هو خير اهبطوا مصرا فإن لكم ما سألتم وضربت عليهم الذلة والمسكنة وباءوا بغضب من الله ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون النبيين بغير الحق ذلك بما عصوا وكانوا يعتدون (٦١)) .','68','10','0');
INSERT INTO [t22928] VALUES (69,'قال تعالى: (إن الذين آمنوا والذين هادوا والنصارى والصابئين من آمن بالله واليوم الآخر وعمل صالحا فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون (٦٣)) .','71','10','0');
INSERT INTO [t22928] VALUES (70,'قال تعالى: (وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون (٦٣)) .','72','10','0');
INSERT INTO [t22928] VALUES (71,'قال تعالى: (ثم توليتم من بعد ذلك فلولا فضل الله عليكم ورحمته لكنتم من الخاسرين (٦٤)) .','73','10','0');
INSERT INTO [t22928] VALUES (72,'قال تعالى: (ولقد علمتم الذين اعتدوا منكم في السبت فقلنا لهم كونوا قردة خاسئين (٦٥)) .','73','10','0');
INSERT INTO [t22928] VALUES (73,'قال تعالى: (فجعلناها نكالا لما بين يديها وما خلفها وموعظة للمتقين (٦٦)) .','74','10','0');
INSERT INTO [t22928] VALUES (74,'قال تعالى: (وإذ قال موسى لقومه إن الله يأمركم أن تذبحوا بقرة قالوا أتتخذنا هزوا قال أعوذ بالله أن أكون من الجاهلين (٦٧)) .','74','10','0');
INSERT INTO [t22928] VALUES (75,'قال تعالى: (قالوا ادع لنا ربك يبين لنا ما هي قال إنه يقول إنها بقرة لا فارض ولا بكر عوان بين ذلك فافعلوا ما تؤمرون (٦٨)) .','75','10','0');
INSERT INTO [t22928] VALUES (76,'قال تعالى: (قالوا ادع لنا ربك يبين لنا ما لونها قال إنه يقول إنها بقرة صفراء فاقع لونها تسر الناظرين (٦٩)) .','76','10','0');
INSERT INTO [t22928] VALUES (77,'قال تعالى: (قالوا ادع لنا ربك يبين لنا ما هي إن البقر تشابه علينا وإنا إن شاء الله لمهتدون (٧٠)) .','76','10','0');
INSERT INTO [t22928] VALUES (78,'قال تعالى: (قال إنه يقول إنها بقرة لا ذلول تثير الأرض ولا تسقي الحرث مسلمة لا شية فيها قالوا الآن جئت بالحق فذبحوها وما كادوا يفعلون (٧١)) .','77','10','0');
INSERT INTO [t22928] VALUES (79,'قال تعالى: (وإذ قتلتم نفسا فادارأتم فيها والله مخرج ما كنتم تكتمون (٧٢)) .','79','10','0');
INSERT INTO [t22928] VALUES (80,'قال تعالى: (فقلنا اضربوه ببعضها كذلك يحيي الله الموتى ويريكم آياته لعلكم تعقلون (٧٣)) .','79','10','0');
INSERT INTO [t22928] VALUES (81,'قال تعالى: (ثم قست قلوبكم من بعد ذلك فهي كالحجارة أو أشد قسوة وإن من الحجارة لما يتفجر منه الأنهار وإن منها لما يشقق فيخرج منه الماء وإن منها لما يهبط من خشية الله وما الله بغافل عما تعملون (٧٤)) .','80','10','0');
INSERT INTO [t22928] VALUES (82,'قال تعالى: (أفتطمعون أن يؤمنوا لكم وقد كان فريق منهم يسمعون كلام الله ثم يحرفونه من بعد ما عقلوه وهم يعلمون (٧٥)) .','80','10','0');
INSERT INTO [t22928] VALUES (83,'قال تعالى: (وإذا خلا بعضهم إلى بعض قالوا أتحدثونهم بما فتح الله عليكم ليحاجوكم به عند ربكم أفلا تعقلون (٧٦)) .','81','10','0');
INSERT INTO [t22928] VALUES (84,'قال تعالى: (ومنهم أميون لا يعلمون الكتاب إلا أماني وإن هم إلا يظنون (٧٨)) .','81','10','0');
INSERT INTO [t22928] VALUES (85,'قال تعالى: (فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا فويل لهم مما كتبت أيديهم وويل لهم مما يكسبون (٧٩)) .','82','10','0');
INSERT INTO [t22928] VALUES (86,'قال تعالى: (وقالوا لن تمسنا النار إلا أياما معدودة قل أتخذتم عند الله عهدا فلن يخلف الله عهده أم تقولون على الله ما لا تعلمون (٨٠)) .','82','10','0');
INSERT INTO [t22928] VALUES (87,'قال تعالى: (بلى من كسب سيئة وأحاطت به خطيئته فأولئك أصحاب النار هم فيها خالدون (٨١)) .','83','10','0');
INSERT INTO [t22928] VALUES (88,'قال تعالى: (وإذ أخذنا ميثاق بني إسرائيل لا تعبدون إلا الله وبالوالدين إحسانا وذي القربى واليتامى والمساكين وقولوا للناس حسنا وأقيموا الصلاة وآتوا الزكاة ثم توليتم إلا قليلا منكم وأنتم معرضون (٨٣)) .','84','10','0');
INSERT INTO [t22928] VALUES (89,'قال تعالى: (وإذ أخذنا ميثاقكم لا تسفكون دماءكم ولا تخرجون أنفسكم من دياركم ثم أقررتم وأنتم تشهدون (٨٤)) .','86','10','0');
INSERT INTO [t22928] VALUES (90,'قال تعالى: (ثم أنتم هؤلاء تقتلون أنفسكم وتخرجون فريقا منكم من ديارهم تظاهرون عليهم بالإثم والعدوان وإن يأتوكم أسارى تفادوهم وهو محرم عليكم إخراجهم أفتؤمنون ببعض الكتاب وتكفرون ببعض فما جزاء من يفعل ذلك منكم إلا خزي في الحياة الدنيا ويوم القيامة يردون إلى أشد العذاب وما الله بغافل عما تعملون (٨٥)) .','87','10','0');
INSERT INTO [t22928] VALUES (91,'قال تعالى: (ولقد آتينا موسى الكتاب وقفينا من بعده بالرسل وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس أفكلما جاءكم رسول بما لا تهوى أنفسكم استكبرتم ففريقا كذبتم وفريقا تقتلون (٨٧)) .','89','10','0');
INSERT INTO [t22928] VALUES (92,'قال تعالى: (وقالوا قلوبنا غلف بل لعنهم الله بكفرهم فقليلا ما يؤمنون (٨٨)) .','90','10','0');
INSERT INTO [t22928] VALUES (93,'قال تعالى: (ولما جاءهم كتاب من عند الله مصدق لما معهم وكانوا من قبل يستفتحون على الذين كفروا فلما جاءهم ما عرفوا كفروا به فلعنة الله على الكافرين (٨٩)) .','91','10','0');
INSERT INTO [t22928] VALUES (94,'قال تعالى: (بئسما اشتروا به أنفسهم أن يكفروا بما أنزل الله بغيا أن ينزل الله من فضله على من يشاء من عباده فباءوا بغضب على غضب وللكافرين عذاب مهين (٩٠)) .','92','10','0');
INSERT INTO [t22928] VALUES (95,'قال تعالى: (وإذا قيل لهم آمنوا بما أنزل الله قالوا نؤمن بما أنزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم قل فلم تقتلون أنبياء الله من قبل إن كنتم مؤمنين (٩١)) .','93','10','0');
INSERT INTO [t22928] VALUES (96,'قال تعالى: (ولقد جاءكم موسى بالبينات ثم اتخذتم العجل من بعده وأنتم ظالمون (٩٢)) .','94','10','0');
INSERT INTO [t22928] VALUES (97,'قال تعالى: (وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واسمعوا قالوا سمعنا وعصينا وأشربوا في قلوبهم العجل بكفرهم قل بئسما يأمركم به إيمانكم إن كنتم مؤمنين (٩٣)) .','94','10','0');
INSERT INTO [t22928] VALUES (98,'قال تعالى: (قل إن كانت لكم الدار الآخرة عند الله خالصة من دون الناس فتمنوا الموت إن كنتم صادقين (٩٤)) .','95','10','0');
INSERT INTO [t22928] VALUES (99,'قال تعالى: (ولن يتمنوه أبدا بما قدمت أيديهم والله عليم بالظالمين (٩٥)) .','96','10','0');
INSERT INTO [t22928] VALUES (100,'قال تعالى: (ولتجدنهم أحرص الناس على حياة ومن الذين أشركوا يود أحدهم لو يعمر ألف سنة وما هو بمزحزحه من العذاب أن يعمر والله بصير بما يعملون (٩٦)) .','96','10','0');
INSERT INTO [t22928] VALUES (101,'قال تعالى: (قل من كان عدوا لجبريل فإنه نزله على قلبك بإذن الله مصدقا لما بين يديه وهدى وبشرى للمؤمنين (٩٧)) .','97','10','0');
INSERT INTO [t22928] VALUES (102,'قال تعالى: (من كان عدوا لله وملائكته ورسله وجبريل وميكال فإن الله عدو للكافرين (٩٨)) .','98','10','0');
INSERT INTO [t22928] VALUES (103,'قال تعالى: (أوكلما عاهدوا عهدا نبذه فريق منهم. . . . .) .','98','10','0');
INSERT INTO [t22928] VALUES (104,'قال تعالى: (ولما جاءهم رسول من عند الله مصدق لما معهم نبذ فريق من الذين أوتوا الكتاب كتاب الله وراء ظهورهم كأنهم لا يعلمون (١٠١)) .','99','10','0');
INSERT INTO [t22928] VALUES (105,'قال تعالى: (واتبعوا ما تتلو الشياطين على ملك سليمان وما كفر سليمان ولكن الشياطين كفروا يعلمون الناس السحر وما أنزل على الملكين ببابل هاروت وماروت وما يعلمان من أحد حتى يقولا إنما نحن فتنة فلا تكفر فيتعلمون منهما ما يفرقون به بين المرء وزوجه وما هم بضارين به من أحد إلا بإذن الله ويتعلمون ما يضرهم ولا ينفعهم ولقد علموا لمن اشتراه ما له في الآخرة من خلاق ولبئس ما شروا به أنفسهم لو كانوا يعلمون (١٠٢)) .','99','10','0');
INSERT INTO [t22928] VALUES (106,'قال تعالى: (ولو أنهم آمنوا واتقوا لمثوبة من عند الله خير لو كانوا يعلمون (١٠٣) .','102','10','0');
INSERT INTO [t22928] VALUES (107,'قال تعالى: (يا أيها الذين آمنوا لا تقولوا راعنا. . . . . . . . (١٠٤)) .','102','10','0');
INSERT INTO [t22928] VALUES (108,'قال تعالى: (ما يود الذين كفروا من أهل الكتاب ولا المشركين أن ينزل عليكم من خير من ربكم والله يختص برحمته من يشاء. . . . . . (١٠٥)) .','103','10','0');
INSERT INTO [t22928] VALUES (109,'قال تعالى: (ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها. . . . . . . . . . . . (١٠٦)) .','103','10','0');
INSERT INTO [t22928] VALUES (110,'قال تعالى: (ألم تعلم أن الله له ملك السماوات والأرض وما لكم من دون الله من ولي ولا نصير (١٠٧)) .','104','10','0');
INSERT INTO [t22928] VALUES (111,'قال تعالى: (أم تريدون أن تسألوا رسولكم كما سئل موسى من قبل ومن يتبدل الكفر بالإيمان فقد ضل سواء السبيل (١٠٨)) .','105','10','0');
INSERT INTO [t22928] VALUES (112,'قال تعالى: (ود كثير من أهل الكتاب لو يردونكم من بعد إيمانكم كفارا حسدا من عند أنفسهم من بعد ما تبين لهم الحق فاعفوا واصفحوا حتى يأتي الله بأمره إن الله على كل شيء قدير (١٠٩)) .','105','10','0');
INSERT INTO [t22928] VALUES (113,'قال تعالى: (وأقيموا الصلاة وآتوا الزكاة وما تقدموا لأنفسكم من خير تجدوه عند الله. . . . . . . . . . . . (١١٠)) .','106','10','0');
INSERT INTO [t22928] VALUES (114,'قال تعالى: (وقالوا لن يدخل الجنة إلا من كان هودا أو نصارى تلك أمانيهم قل هاتوا برهانكم إن كنتم صادقين (١١١)) .','106','10','0');
INSERT INTO [t22928] VALUES (115,'قال تعالى: (بلى من أسلم وجهه لله وهو محسن فله أجره عند ربه ولا خوف عليهم ولا هم يحزنون (١١٢)) .','107','10','0');
INSERT INTO [t22928] VALUES (116,'قال تعالى: (وقالت اليهود ليست النصارى على شيء وقالت النصارى ليست اليهود على شيء وهم يتلون الكتاب كذلك قال الذين لا يعلمون مثل قولهم فالله يحكم بينهم يوم القيامة فيما كانوا فيه يختلفون (١١٣)) .','107','10','0');
INSERT INTO [t22928] VALUES (117,'قال تعالى: (ومن أظلم ممن منع مساجد الله أن يذكر فيها اسمه وسعى في خرابها أولئك ما كان لهم أن يدخلوها إلا خائفين لهم في الدنيا خزي ولهم في الآخرة عذاب عظيم (١١٤)) .','108','10','0');
INSERT INTO [t22928] VALUES (118,'قال تعالى: (ولله المشرق والمغرب فأينما تولوا فثم وجه الله إن الله واسع عليم (١١٥)) .','109','10','0');
INSERT INTO [t22928] VALUES (119,'قال تعالى: (وقالوا اتخذ الله ولدا سبحانه بل له ما في السماوات والأرض كل له قانتون ((١١٦)) .','109','10','0');
INSERT INTO [t22928] VALUES (120,'قال تعالى: (بديع السماوات والأرض وإذا قضى أمرا فإنما يقول له كن فيكون (١١٧)) .','110','10','0');
INSERT INTO [t22928] VALUES (121,'قال تعالى: (وقال الذين لا يعلمون لولا يكلمنا الله أو تأتينا آية كذلك قال الذين من قبلهم مثل قولهم. . . . . . (١١٨)) .','111','10','0');
INSERT INTO [t22928] VALUES (122,'قال تعالى: (إنا أرسلناك بالحق بشيرا ونذيرا ولا تسأل عن أصحاب الجحيم (١١٩)) .','111','10','0');
INSERT INTO [t22928] VALUES (123,'قال تعالى: (ولن ترضى عنك اليهود ولا النصارى حتى تتبع ملتهم قل إن هدى الله هو الهدى ولئن اتبعت أهواءهم بعد الذي جاءك من العلم ما لك من الله من ولي ولا نصير (١٢٠)) .','111','10','0');
INSERT INTO [t22928] VALUES (124,'قال تعالى: (الذين آتيناهم الكتاب يتلونه حق تلاوته أولئك يؤمنون به. . . . . (١٢١)) .','112','10','0');
INSERT INTO [t22928] VALUES (125,'قال تعالى: (وإذ ابتلى إبراهيم ربه بكلمات فأتمهن قال إني جاعلك للناس إماما قال ومن ذريتي قال لا ينال عهدي الظالمين (١٢٤)) .','112','10','0');
INSERT INTO [t22928] VALUES (126,'قال تعالى: (وإذ جعلنا البيت مثابة للناس وأمنا واتخذوا من مقام إبراهيم مصلى وعهدنا إلى إبراهيم وإسماعيل أن طهرا بيتي للطائفين والعاكفين والركع السجود (١٢٥)) .','113','10','0');
INSERT INTO [t22928] VALUES (127,'قال تعالى {وإذ قال إبراهيم رب اجعل هذا بلدا آمنا وارزق أهله من الثمرات من آمن منهم بالله واليوم الآخر قال ومن كفر فأمتعه قليلا ثم أضطره إلى عذاب النار وبئس المصير}','114','10','0');
INSERT INTO [t22928] VALUES (128,'قال تعالى: (وإذ يرفع إبراهيم القواعد من البيت وإسماعيل ربنا تقبل منا. . . . . . (١٢٧)) .','116','10','0');
INSERT INTO [t22928] VALUES (129,'قال تعالى: (ربنا واجعلنا مسلمين لك ومن ذريتنا أمة مسلمة لك وأرنا مناسكنا. . . . . . . . (١٢٨)) .','116','10','0');
INSERT INTO [t22928] VALUES (130,'قال تعالى {ربنا وابعث فيهم رسولا منهم يتلو عليهم آياتك}','117','10','0');
INSERT INTO [t22928] VALUES (131,'قال تعالى: (ومن يرغب عن ملة إبراهيم إلا من سفه نفسه ولقد اصطفيناه في الدنيا وإنه في الآخرة لمن الصالحين (١٣٠)) .','117','10','0');
INSERT INTO [t22928] VALUES (132,'قال تعالى: (إذ قال له ربه أسلم قال أسلمت لرب العالمين (١٣١)) .','118','10','0');
INSERT INTO [t22928] VALUES (133,'قال تعالى: (ووصى بها إبراهيم بنيه ويعقوب يا بني إن الله اصطفى لكم الدين فلا تموتن إلا وأنتم مسلمون (١٣٢)) .','118','10','0');
INSERT INTO [t22928] VALUES (134,'قال تعالى: (أم كنتم شهداء إذ حضر يعقوب الموت إذ قال لبنيه ما تعبدون من بعدي قالوا نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلها واحدا ونحن له مسلمون (١٣٣)) .','119','10','0');
INSERT INTO [t22928] VALUES (135,'قال تعالى: (تلك أمة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسألون عما كانوا يعملون (١٣٤)) .','120','10','0');
INSERT INTO [t22928] VALUES (136,'قال تعالى: (وقالوا كونوا هودا أو نصارى تهتدوا قل بل ملة إبراهيم حنيفا. . . . . (١٣٥)) .','121','10','0');
INSERT INTO [t22928] VALUES (137,'قال تعالى: (قولوا آمنا بالله وما أنزل إلينا وما أنزل إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى وما أوتي النبيون من ربهم لا نفرق بين أحد منهم. . . . . . . (١٣٦)) .','122','10','0');
INSERT INTO [t22928] VALUES (138,'قال تعالى: (فإن آمنوا بمثل ما آمنتم به فقد اهتدوا. . . (١٣٧)) .','122','10','0');
INSERT INTO [t22928] VALUES (139,'قال تعالى: (صبغة الله ومن أحسن من الله صبغة (١٣٨)) .','123','10','0');
INSERT INTO [t22928] VALUES (140,'قال تعالى: (أم تقولون إن إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط كانوا هودا أو نصارى قل أأنتم أعلم أم الله ومن أظلم ممن كتم شهادة عنده من الله وما الله بغافل عما تعملون (١٤٠)) .','123','10','0');
INSERT INTO [t22928] VALUES (141,'قال تعالى: (سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها. . . . . . . (١٤٢)) .','124','10','0');
INSERT INTO [t22928] VALUES (142,'قال تعالى: (وكذلك جعلناكم أمة وسطا لتكونوا شهداء على الناس ويكون الرسول عليكم شهيدا وما جعلنا القبلة التي كنت عليها إلا لنعلم من يتبع الرسول ممن ينقلب على عقبيه وإن كانت لكبيرة إلا على الذين هدى الله وما كان الله ليضيع إيمانكم إن الله بالناس لرءوف رحيم (١٤٣)) .','124','10','0');
INSERT INTO [t22928] VALUES (143,'قال تعالى: (قد نرى تقلب وجهك في السماء فلنولينك قبلة ترضاها فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره وإن الذين أوتوا الكتاب ليعلمون أنه الحق من ربهم. . . . . . (١٤٤)) .','126','10','0');
INSERT INTO [t22928] VALUES (144,'قال تعالى: (ولئن أتيت الذين أوتوا الكتاب بكل آية ما تبعوا قبلتك وما أنت بتابع قبلتهم وما بعضهم بتابع قبلة بعض ولئن اتبعت أهواءهم من بعد ما جاءك من العلم إنك إذا لمن الظالمين (١٤٥)) .','126','10','0');
INSERT INTO [t22928] VALUES (145,'قال تعالى: (الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم. . . . . . (١٤٦)) .','127','10','0');
INSERT INTO [t22928] VALUES (146,'قال تعالى: (الحق من ربك فلا تكونن من الممترين (١٤٧)) .','127','10','0');
INSERT INTO [t22928] VALUES (147,'قال تعالى: (ولكل وجهة هو موليها فاستبقوا الخيرات أين ما تكونوا يأت بكم الله جميعا إن الله على كل شيء قدير (١٤٨)) .','127','10','0');
INSERT INTO [t22928] VALUES (148,'قال تعالى: (ومن حيث خرجت فول وجهك شطر المسجد الحرام وإنه للحق من ربك. . . . . . . . (١٤٩)) .','128','10','0');
INSERT INTO [t22928] VALUES (149,'قال تعالى: (ومن حيث خرجت فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره لئلا يكون للناس عليكم حجة إلا الذين ظلموا منهم فلا تخشوهم واخشوني ولأتم نعمتي عليكم ولعلكم تهتدون (١٥٠)) .','128','10','0');
INSERT INTO [t22928] VALUES (150,'قال تعالى: (كما أرسلنا فيكم رسولا منكم يتلو عليكم آياتنا ويزكيكم ويعلمكم الكتاب والحكمة ويعلمكم ما لم تكونوا تعلمون (١٥١)) .','129','10','0');
INSERT INTO [t22928] VALUES (151,'قال تعالى: (ولا تقولوا لمن يقتل في سبيل الله أموات بل أحياء ولكن لا تشعرون (١٥٤)) .','129','10','0');
INSERT INTO [t22928] VALUES (152,'قال تعالى: (ولنبلونكم بشيء من الخوف والجوع ونقص من الأموال والأنفس والثمرات وبشر الصابرين (١٥٥)) .','129','10','0');
INSERT INTO [t22928] VALUES (153,'قال تعالى: (الذين إذا أصابتهم مصيبة قالوا إنا لله وإنا إليه راجعون (١٥٦)) .','130','10','0');
INSERT INTO [t22928] VALUES (154,'قال تعالى: (أولئك عليهم صلوات من ربهم ورحمة وأولئك هم المهتدون (١٥٧)) .','130','10','0');
INSERT INTO [t22928] VALUES (155,'قال تعالى: (إن الصفا والمروة من شعائر الله فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما ومن تطوع خيرا فإن الله شاكر عليم (١٥٨)) .','131','10','0');
INSERT INTO [t22928] VALUES (156,'قال تعالى: (إن الذين يكتمون ما أنزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب أولئك يلعنهم الله ويلعنهم اللاعنون (١٥٩)) .','132','10','0');
INSERT INTO [t22928] VALUES (157,'قال تعالى: (إلا الذين تابوا وأصلحوا وبينوا. . . . (١٦٠)) .','132','10','0');
INSERT INTO [t22928] VALUES (158,'قال تعالى: (إن الذين كفروا وماتوا وهم كفار أولئك عليهم لعنة الله والملائكة والناس أجمعين (١٦١)) .','133','10','0');
INSERT INTO [t22928] VALUES (159,'قال تعالى: (خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون (١٦٢)) .','133','10','0');
INSERT INTO [t22928] VALUES (160,'قال تعالى: (وإلهكم إله واحد لا إله إلا هو الرحمن الرحيم (١٦٣)) .','133','10','0');
INSERT INTO [t22928] VALUES (161,'قال تعالى: (إن في خلق السماوات والأرض واختلاف الليل والنهار والفلك التي تجري في البحر بما ينفع الناس وما أنزل الله من السماء من ماء فأحيا به الأرض بعد موتها وبث فيها من كل دابة وتصريف الرياح والسحاب المسخر بين السماء والأرض لآيات لقوم يعقلون (١٦٤)) .','134','10','0');
INSERT INTO [t22928] VALUES (162,'قال تعالى: (ومن الناس من يتخذ من دون الله أندادا يحبونهم كحب الله والذين آمنوا أشد حبا لله ولو يرى الذين ظلموا إذ يرون العذاب أن القوة لله جميعا وأن الله شديد العذاب (١٦٥)) .','135','10','0');
INSERT INTO [t22928] VALUES (163,'قال تعالى: (إذ تبرأ الذين اتبعوا من الذين اتبعوا ورأوا العذاب وتقطعت بهم الأسباب (١٦٦) وقال الذين اتبعوا لو أن لنا كرة فنتبرأ منهم كما تبرؤوا منا كذلك يريهم الله أعمالهم حسرات عليهم وما هم بخارجين من النار) .','137','10','0');
INSERT INTO [t22928] VALUES (164,'قال تعالى: (يا أيها الناس كلوا مما في الأرض حلالا طيبا ولا تتبعوا خطوات الشيطان إنه لكم عدو مبين (١٦٨)) .','139','10','0');
INSERT INTO [t22928] VALUES (165,'قال تعالى: (إنما يأمركم بالسوء والفحشاء وأن تقولوا على الله ما لا تعلمون (١٦٩)) .','140','10','0');
INSERT INTO [t22928] VALUES (166,'قال تعالى: (وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما ألفينا عليه آباءنا أولو كان آباؤهم لا يعقلون شيئا ولا يهتدون (١٧٠)) .','140','10','0');
INSERT INTO [t22928] VALUES (167,'قال تعالى: (ومثل الذين كفروا كمثل الذي ينعق بما لا يسمع إلا دعاء ونداء صم بكم عمي فهم لا يعقلون (١٧١)) .','141','10','0');
INSERT INTO [t22928] VALUES (168,'قال تعالى: (يا أيها الذين آمنوا كلوا من طيبات ما رزقناكم (١٧٢)) .','141','10','0');
INSERT INTO [t22928] VALUES (169,'قال تعالى: (إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل به لغير الله فمن اضطر غير باغ ولا عاد فلا إثم عليه إن الله غفور رحيم (١٧٣)) .','141','10','0');
INSERT INTO [t22928] VALUES (170,'قال تعالى: (إن الذين يكتمون ما أنزل الله من الكتاب ويشترون به ثمنا قليلا أولئك ما يأكلون في بطونهم إلا النار ولا يكلمهم الله يوم القيامة ولا يزكيهم ولهم عذاب أليم (١٧٤)) .','142','10','0');
INSERT INTO [t22928] VALUES (171,'قال تعالى: (أولئك الذين اشتروا الضلالة بالهدى والعذاب بالمغفرة فما أصبرهم على النار (١٧٥)) .','143','10','0');
INSERT INTO [t22928] VALUES (172,'قال تعالى: (ذلك بأن الله نزل الكتاب بالحق. . . . . . (١٧٦)) .','143','10','0');
INSERT INTO [t22928] VALUES (173,'قال تعالى: (ليس البر أن تولوا وجوهكم قبل المشرق والمغرب ولكن البر من آمن بالله واليوم الآخر والملائكة والكتاب والنبيين وآتى المال على حبه ذوي القربى واليتامى والمساكين وابن السبيل والسائلين وفي الرقاب وأقام الصلاة وآتى الزكاة والموفون بعهدهم إذا عاهدوا والصابرين في البأساء والضراء وحين البأس أولئك الذين صدقوا وأولئك هم المتقون (١٧٧)) .','144','10','0');
INSERT INTO [t22928] VALUES (174,'قال تعالى: (يا أيها الذين آمنوا كتب عليكم القصاص في القتلى الحر بالحر والعبد بالعبد والأنثى بالأنثى فمن عفي له من أخيه شيء فاتباع بالمعروف وأداء إليه بإحسان ذلك تخفيف من ربكم ورحمة فمن اعتدى بعد ذلك فله عذاب أليم (١٧٨)) .','146','10','0');
INSERT INTO [t22928] VALUES (175,'قال تعالى: (ولكم في القصاص حياة يا أولي الألباب لعلكم تتقون (١٧٩)) .','147','10','0');
INSERT INTO [t22928] VALUES (176,'قال تعالى: (كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين بالمعروف حقا على المتقين (١٨٠)) .','147','10','0');
INSERT INTO [t22928] VALUES (177,'قال تعالى: (فمن بدله بعدما سمعه فإنما إثمه على الذين يبدلونه. . . . . . . . (١٨١)) .','148','10','0');
INSERT INTO [t22928] VALUES (178,'قال تعالى: (فمن خاف من موص جنفا أو إثما. . . (١٨٢)) .','149','10','0');
INSERT INTO [t22928] VALUES (179,'قال تعالى: (يا أيها الذين آمنوا كتب عليكم الصيام كما كتب على الذين من قبلكم. . . . . . (١٨٣)) .','149','10','0');
INSERT INTO [t22928] VALUES (180,'قال تعالى: (أياما معدودات فمن كان منكم مريضا أو على سفر فعدة من أيام أخر وعلى الذين يطيقونه فدية طعام مسكين فمن تطوع خيرا فهو خير له وأن تصوموا خير لكم إن كنتم تعلمون (١٧٨)) .','150','10','0');
INSERT INTO [t22928] VALUES (181,'قال تعالى: (شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا أو على سفر فعدة من أيام أخر يريد الله بكم اليسر ولا يريد بكم العسر ولتكملوا العدة ولتكبروا الله على ما هداكم (١٨٥)) .','152','10','0');
INSERT INTO [t22928] VALUES (182,'قال تعالى: (وإذا سألك عبادي عني فإني قريب أجيب دعوة الداع إذا دعان فليستجيبوا لي وليؤمنوا بي لعلهم يرشدون (١٨٦)) .','154','10','0');
INSERT INTO [t22928] VALUES (183,'قال تعالى: (أحل لكم ليلة الصيام الرفث إلى نسائكم هن لباس لكم وأنتم لباس لهن علم الله أنكم كنتم تختانون أنفسكم فتاب عليكم وعفا عنكم فالآن باشروهن وابتغوا ما كتب الله لكم وكلوا واشربوا حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر ثم أتموا الصيام إلى الليل ولا تباشروهن وأنتم عاكفون في المساجد تلك حدود الله فلا تقربوها كذلك يبين الله آياته للناس لعلهم يتقون (١٨٧)) .','155','10','0');
INSERT INTO [t22928] VALUES (184,'قال تعالى: (ولا تأكلوا أموالكم بينكم بالباطل وتدلوا بها إلى الحكام لتأكلوا فريقا من أموال الناس بالإثم وأنتم تعلمون (١٨٨)) .','156','10','0');
INSERT INTO [t22928] VALUES (185,'قال تعالى: (يسألونك عن الأهلة قل هي مواقيت للناس والحج وليس البر بأن تأتوا البيوت من ظهورها ولكن البر من اتقى وأتوا البيوت من أبوابها واتقوا الله لعلكم تفلحون (١٨٩)) .','157','10','0');
INSERT INTO [t22928] VALUES (186,'قال تعالى: (ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم فيه فإن قاتلوكم فاقتلوهم كذلك جزاء الكافرين (١٩١)) .','158','10','0');
INSERT INTO [t22928] VALUES (187,'قال تعالى: (فإن الله غفور رحيم) ; أي لهم (١٩٢)) .','159','10','0');
INSERT INTO [t22928] VALUES (188,'قال تعالى: (وقاتلوهم حتى لا تكون فتنة ويكون الدين لله فإن انتهوا فلا عدوان إلا على الظالمين (١٩٣)) .','159','10','0');
INSERT INTO [t22928] VALUES (189,'قال تعالى: (الشهر الحرام بالشهر الحرام والحرمات قصاص فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم واتقوا الله واعلموا أن الله مع المتقين (١٩٤)) .','159','10','0');
INSERT INTO [t22928] VALUES (190,'قال تعالى: (وأنفقوا في سبيل الله ولا تلقوا بأيديكم إلى التهلكة وأحسنوا إن الله يحب المحسنين (١٩٥)) .','159','10','0');
INSERT INTO [t22928] VALUES (191,'قال تعالى: (وأتموا الحج والعمرة لله فإن أحصرتم فما استيسر من الهدي ولا تحلقوا رءوسكم حتى يبلغ الهدي محله فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة أو نسك فإذا أمنتم فمن تمتع بالعمرة إلى الحج فما استيسر من الهدي فمن لم يجد فصيام ثلاثة أيام في الحج وسبعة إذا رجعتم تلك عشرة كاملة ذلك لمن لم يكن أهله حاضري المسجد الحرام واتقوا الله واعلموا أن الله شديد العقاب (١٩٦)) .','160','10','0');
INSERT INTO [t22928] VALUES (192,'قال تعالى: (الحج أشهر معلومات فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج وما تفعلوا من خير يعلمه الله وتزودوا فإن خير الزاد التقوى واتقون يا أولي الألباب (١٩٧)) .','161','10','0');
INSERT INTO [t22928] VALUES (193,'قال تعالى: (ليس عليكم جناح أن تبتغوا فضلا من ربكم فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام واذكروه كما هداكم وإن كنتم من قبله لمن الضالين (١٩٨)) .','163','10','0');
INSERT INTO [t22928] VALUES (194,'قال تعالى {ثم أفيضوا من حيث أفاض الناس واستغفروا الله إن الله غفور رحيم}','164','10','0');
INSERT INTO [t22928] VALUES (195,'قال تعالى: (فإذا قضيتم مناسككم فاذكروا الله كذكركم آباءكم أو أشد ذكرا فمن الناس من يقول ربنا آتنا في الدنيا وما له في الآخرة من خلاق (٢٠٠)) .','165','10','0');
INSERT INTO [t22928] VALUES (196,'قال تعالى: (ومنهم من يقول ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار (٢٠١)) .','166','10','0');
INSERT INTO [t22928] VALUES (197,'قال تعالى: (واذكروا الله في أيام معدودات فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه لمن اتقى واتقوا الله واعلموا أنكم إليه تحشرون (٢٠٣)) .','166','10','0');
INSERT INTO [t22928] VALUES (198,'قال تعالى: (ومن الناس من يعجبك قوله في الحياة الدنيا ويشهد الله على ما في قلبه وهو ألد الخصام (٢٠٤)) .','167','10','0');
INSERT INTO [t22928] VALUES (199,'قال تعالى: (وإذا تولى سعى في الأرض ليفسد فيها ويهلك الحرث والنسل والله لا يحب الفساد (٢٠٥)) .','168','10','0');
INSERT INTO [t22928] VALUES (200,'قال تعالى: (وإذا قيل له اتق الله أخذته العزة بالإثم فحسبه جهنم ولبئس المهاد (٢٠٦)) .','168','10','0');
INSERT INTO [t22928] VALUES (201,'قال تعالى: (ومن الناس من يشري نفسه ابتغاء مرضاة الله والله رءوف بالعباد (٢٠٧)) .','169','10','0');
INSERT INTO [t22928] VALUES (202,'قال تعالى: (يا أيها الذين آمنوا ادخلوا في السلم كافة ولا تتبعوا خطوات الشيطان إنه لكم عدو مبين (٢٠٨)) .','169','10','0');
INSERT INTO [t22928] VALUES (203,'قال تعالى: (هل ينظرون إلا أن يأتيهم الله في ظلل من الغمام والملائكة وقضي الأمر وإلى الله ترجع الأمور (٢١٠)) .','170','10','0');
INSERT INTO [t22928] VALUES (204,'قال تعالى: (سل بني إسرائيل كم آتيناهم من آية بينة ومن يبدل نعمة الله من بعد ما جاءته فإن الله شديد العقاب (٢١١)) .','170','10','0');
INSERT INTO [t22928] VALUES (205,'قال تعالى: (زين للذين كفروا الحياة الدنيا ويسخرون من الذين آمنوا والذين اتقوا فوقهم يوم القيامة والله يرزق من يشاء بغير حساب (٢١٢)) .','171','10','0');
INSERT INTO [t22928] VALUES (206,'قال تعالى: (كان الناس أمة واحدة فبعث الله النبيين مبشرين ومنذرين وأنزل معهم الكتاب بالحق ليحكم بين الناس فيما اختلفوا فيه وما اختلف فيه إلا الذين أوتوه من بعد ما جاءتهم البينات بغيا بينهم فهدى الله الذين آمنوا لما اختلفوا فيه من الحق بإذنه والله يهدي من يشاء إلى صراط مستقيم (٢١٣)) .','171','10','0');
INSERT INTO [t22928] VALUES (207,'قال تعالى: (أم حسبتم أن تدخلوا الجنة ولما يأتكم مثل الذين خلوا من قبلكم مستهم البأساء والضراء وزلزلوا حتى يقول الرسول والذين آمنوا معه متى نصر الله ألا إن نصر الله قريب (٢١٤)) .','172','10','0');
INSERT INTO [t22928] VALUES (208,'قال تعالى: (يسألونك ماذا ينفقون قل ما أنفقتم من خير فللوالدين والأقربين واليتامى والمساكين وابن السبيل وما تفعلوا من خير فإن الله به عليم (٢١٥)) .','173','10','0');
INSERT INTO [t22928] VALUES (209,'قال تعالى: (كتب عليكم القتال وهو كره لكم وعسى أن تكرهوا شيئا وهو خير لكم وعسى أن تحبوا شيئا وهو شر لكم والله يعلم وأنتم لا تعلمون (٣١٦)) .','174','10','0');
INSERT INTO [t22928] VALUES (210,'قال تعالى: (يسألونك عن الشهر الحرام قتال فيه قل قتال فيه كبير وصد عن سبيل الله وكفر به والمسجد الحرام وإخراج أهله منه أكبر عند الله والفتنة أكبر من القتل ولا يزالون يقاتلونكم حتى يردوكم عن دينكم إن استطاعوا ومن يرتدد منكم عن دينه فيمت وهو كافر فأولئك حبطت أعمالهم في الدنيا والآخرة وأولئك أصحاب النار هم فيها خالدون (٢١٧)) .','174','10','0');
INSERT INTO [t22928] VALUES (211,'قال تعالى: (يسألونك عن الخمر والميسر قل فيهما إثم كبير ومنافع للناس وإثمهما أكبر من نفعهما ويسألونك ماذا ينفقون قل العفو كذلك يبين الله لكم الآيات لعلكم تتفكرون (١١٩)) .','177','10','0');
INSERT INTO [t22928] VALUES (212,'قال تعالى: (في الدنيا والآخرة ويسألونك عن اليتامى قل إصلاح لهم خير وإن تخالطوهم فإخوانكم والله يعلم المفسد من المصلح ولو شاء الله لأعنتكم إن الله عزيز حكيم (٢٢٠)) .','177','10','0');
INSERT INTO [t22928] VALUES (213,'قال تعالى: (ولا تنكحوا المشركات حتى يؤمن ولأمة مؤمنة خير من مشركة ولو أعجبتكم ولا تنكحوا المشركين حتى يؤمنوا ولعبد مؤمن خير من مشرك ولو أعجبكم أولئك يدعون إلى النار والله يدعو إلى الجنة والمغفرة بإذنه ويبين آياته للناس لعلهم يتذكرون (٢٢١)) .','178','10','0');
INSERT INTO [t22928] VALUES (214,'قال تعالى: (ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فإذا تطهرن فأتوهن من حيث أمركم الله إن الله يحب التوابين ويحب المتطهرين (٢٢٢)) .','179','10','0');
INSERT INTO [t22928] VALUES (215,'قال تعالى: (نساؤكم حرث لكم فأتوا حرثكم أنى شئتم وقدموا لأنفسكم واتقوا الله واعلموا أنكم ملاقوه وبشر المؤمنين (٢٢٣)) .','179','10','0');
INSERT INTO [t22928] VALUES (216,'قال تعالى: (ولا تجعلوا الله عرضة لأيمانكم أن تبروا وتتقوا وتصلحوا بين الناس والله سميع عليم (٢٢٤)) .','180','10','0');
INSERT INTO [t22928] VALUES (217,'قال تعالى: (لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم (٢٢٥)) .','180','10','0');
INSERT INTO [t22928] VALUES (218,'قال تعالى: (للذين يؤلون من نسائهم تربص أربعة أشهر فإن فاءوا فإن الله غفور رحيم (٢٢٦)) .','180','10','0');
INSERT INTO [t22928] VALUES (219,'قال تعالى: (وإن عزموا الطلاق فإن الله سميع عليم (٢٢٧)) .','181','10','0');
INSERT INTO [t22928] VALUES (220,'قال تعالى: (والمطلقات يتربصن بأنفسهن ثلاثة قروء ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن إن كن يؤمن بالله واليوم الآخر وبعولتهن أحق بردهن في ذلك إن أرادوا إصلاحا ولهن مثل الذي عليهن بالمعروف وللرجال عليهن درجة والله عزيز حكيم (٢٢٨)) .','181','10','0');
INSERT INTO [t22928] VALUES (221,'قال تعالى: (الطلاق مرتان فإمساك بمعروف أو تسريح بإحسان ولا يحل لكم أن تأخذوا مما آتيتموهن شيئا إلا أن يخافا ألا يقيما حدود الله فإن خفتم ألا يقيما حدود الله فلا جناح عليهما فيما افتدت به تلك حدود الله فلا تعتدوها ومن يتعد حدود الله فأولئك هم الظالمون (٢٢٥)) .','183','10','0');
INSERT INTO [t22928] VALUES (222,'قال تعالى: (فإن طلقها فلا تحل له من بعد حتى تنكح زوجا غيره فإن طلقها فلا جناح عليهما أن يتراجعا إن ظنا أن يقيما حدود الله وتلك حدود الله يبينها لقوم يعلمون (٢٣٠)) .','184','10','0');
INSERT INTO [t22928] VALUES (223,'قال تعالى: (وإذا طلقتم النساء فبلغن أجلهن فأمسكوهن بمعروف أو سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا ومن يفعل ذلك فقد ظلم نفسه ولا تتخذوا آيات الله هزوا واذكروا نعمة الله عليكم وما أنزل عليكم من الكتاب والحكمة يعظكم به واتقوا الله واعلموا أن الله بكل شيء عليم (٢٣١)) .','184','10','0');
INSERT INTO [t22928] VALUES (224,'قال تعالى: (وإذا طلقتم النساء فبلغن أجلهن فلا تعضلوهن أن ينكحن أزواجهن إذا تراضوا بينهم بالمعروف ذلك يوعظ به من كان منكم يؤمن بالله واليوم الآخر ذلكم أزكى لكم وأطهر والله يعلم وأنتم لا تعلمون (٢٣٢)) .','184','10','0');
INSERT INTO [t22928] VALUES (225,'قال تعالى: (والوالدات يرضعن أولادهن حولين كاملين لمن أراد أن يتم الرضاعة وعلى المولود له رزقهن وكسوتهن بالمعروف لا تكلف نفس إلا وسعها لا تضار والدة بولدها ولا مولود له بولده وعلى الوارث مثل ذلك فإن أرادا فصالا عن تراض منهما وتشاور فلا جناح عليهما وإن أردتم أن تسترضعوا أولادكم فلا جناح عليكم إذا سلمتم ما آتيتم بالمعروف واتقوا الله واعلموا أن الله بما تعملون بصير (٢٣٣)) .','185','10','0');
INSERT INTO [t22928] VALUES (226,'قال تعالى: (والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا فإذا بلغن أجلهن فلا جناح عليكم فيما فعلن في أنفسهن بالمعروف والله بما تعملون خبير (٢٣٤)) .','187','10','0');
INSERT INTO [t22928] VALUES (227,'قال تعالى: (ولا جناح عليكم فيما عرضتم به من خطبة النساء أو أكننتم في أنفسكم علم الله أنكم ستذكرونهن ولكن لا تواعدوهن سرا إلا أن تقولوا قولا معروفا ولا تعزموا عقدة النكاح حتى يبلغ الكتاب أجله واعلموا أن الله يعلم ما في أنفسكم فاحذروه واعلموا أن الله غفور حليم (٢٣٥)) .','188','10','0');
INSERT INTO [t22928] VALUES (228,'قال تعالى: (لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف حقا على المحسنين (٢٣٦)) .','189','10','0');
INSERT INTO [t22928] VALUES (229,'قال تعالى: (وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم إلا أن يعفون أو يعفو الذي بيده عقدة النكاح وأن تعفوا أقرب للتقوى ولا تنسوا الفضل بينكم إن الله بما تعملون بصير (٢٣٧)) .','190','10','0');
INSERT INTO [t22928] VALUES (230,'قال تعالى: (حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين (٢٣٨)) .','191','10','0');
INSERT INTO [t22928] VALUES (231,'قال تعالى: (فإن خفتم فرجالا أو ركبانا فإذا أمنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون (٢٣٩)) .','192','10','0');
INSERT INTO [t22928] VALUES (232,'قال تعالى: (والذين يتوفون منكم ويذرون أزواجا وصية لأزواجهم متاعا إلى الحول غير إخراج فإن خرجن فلا جناح عليكم في ما فعلن في أنفسهن من معروف والله عزيز حكيم (٢٤٠)) .','192','10','0');
INSERT INTO [t22928] VALUES (233,'قال تعالى: (وللمطلقات متاع بالمعروف حقا على المتقين (٢٤١)) .','193','10','0');
INSERT INTO [t22928] VALUES (234,'قال تعالى: (كذلك يبين الله لكم آياته لعلكم تعقلون (٢٤٢)) .','193','10','0');
INSERT INTO [t22928] VALUES (235,'قال تعالى: (ألم تر إلى الذين خرجوا من ديارهم وهم ألوف حذر الموت فقال لهم الله موتوا ثم أحياهم إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون (٢٤٣)) .','194','10','0');
INSERT INTO [t22928] VALUES (236,'قال تعالى: (وقاتلوا في سبيل الله واعلموا أن الله سميع عليم (٢٤٤)) .','194','10','0');
INSERT INTO [t22928] VALUES (237,'قال تعالى: (من ذا الذي يقرض الله قرضا حسنا فيضاعفه له أضعافا كثيرة والله يقبض ويبسط وإليه ترجعون (٢٤٥)) .','194','10','0');
INSERT INTO [t22928] VALUES (238,'قال تعالى: (ألم تر إلى الملإ من بني إسرائيل من بعد موسى إذ قالوا لنبي لهم ابعث لنا ملكا نقاتل في سبيل الله قال هل عسيتم إن كتب عليكم القتال ألا تقاتلوا قالوا وما لنا ألا نقاتل في سبيل الله وقد أخرجنا من ديارنا وأبنائنا فلما كتب عليهم القتال تولوا إلا قليلا منهم والله عليم بالظالمين (٢٤٦)) .','196','10','0');
INSERT INTO [t22928] VALUES (239,'قال تعالى: (وقال لهم نبيهم إن الله قد بعث لكم طالوت ملكا قالوا أنى يكون له الملك علينا ونحن أحق بالملك منه ولم يؤت سعة من المال قال إن الله اصطفاه عليكم وزاده بسطة في العلم والجسم والله يؤتي ملكه من يشاء والله واسع عليم (٢٤٧)) .','198','10','0');
INSERT INTO [t22928] VALUES (240,'قال تعالى: (وقال لهم نبيهم إن آية ملكه أن يأتيكم التابوت فيه سكينة من ربكم وبقية مما ترك آل موسى وآل هارون تحمله الملائكة إن في ذلك لآية لكم إن كنتم مؤمنين (٢٤٨)) .','199','10','0');
INSERT INTO [t22928] VALUES (241,'قال تعالى: (فلما فصل طالوت بالجنود قال إن الله مبتليكم بنهر فمن شرب منه فليس مني ومن لم يطعمه فإنه مني إلا من اغترف غرفة بيده فشربوا منه إلا قليلا منهم فلما جاوزه هو والذين آمنوا معه قالوا لا طاقة لنا اليوم بجالوت وجنوده قال الذين يظنون أنهم ملاقو الله كم من فئة قليلة غلبت فئة كثيرة بإذن الله والله مع الصابرين (٢٤٩)) .','199','10','0');
INSERT INTO [t22928] VALUES (242,'قال تعالى: (ولما برزوا لجالوت وجنوده قالوا ربنا أفرغ علينا صبرا وثبت أقدامنا وانصرنا على القوم الكافرين (٢٥٠)) .','201','10','0');
INSERT INTO [t22928] VALUES (243,'قال تعالى: (فهزموهم بإذن الله وقتل داود جالوت وآتاه الله الملك والحكمة وعلمه مما يشاء ولولا دفع الله الناس بعضهم ببعض لفسدت الأرض ولكن الله ذو فضل على العالمين (٢٥١)) .','201','10','0');
INSERT INTO [t22928] VALUES (244,'قال تعالى: (تلك آيات الله نتلوها عليك بالحق وإنك لمن المرسلين (٢٥٢)) .','202','10','0');
INSERT INTO [t22928] VALUES (245,'قال تعالى: (تلك الرسل فضلنا بعضهم على بعض منهم من كلم الله ورفع بعضهم درجات وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس ولو شاء الله ما اقتتل الذين من بعدهم من بعد ما جاءتهم البينات ولكن اختلفوا فمنهم من آمن ومنهم من كفر ولو شاء الله ما اقتتلوا ولكن الله يفعل ما يريد (٢٥٣)) .','202','10','0');
INSERT INTO [t22928] VALUES (246,'قال تعالى: (يا أيها الذين آمنوا أنفقوا مما رزقناكم من قبل أن يأتي يوم لا بيع فيه ولا خلة ولا شفاعة والكافرون هم الظالمون (٢٤٥)) .','203','10','0');
INSERT INTO [t22928] VALUES (247,'قال تعالى: (الله لا إله إلا هو الحي القيوم لا تأخذه سنة ولا نوم له ما في السماوات وما في الأرض من ذا الذي يشفع عنده إلا بإذنه يعلم ما بين أيديهم وما خلفهم ولا يحيطون بشيء من علمه إلا بما شاء وسع كرسيه السماوات والأرض ولا يئوده حفظهما وهو العلي العظيم (٢٥٥)) .','203','10','0');
INSERT INTO [t22928] VALUES (248,'قال تعالى: (لا إكراه في الدين قد تبين الرشد من الغي فمن يكفر بالطاغوت ويؤمن بالله فقد استمسك بالعروة الوثقى لا انفصام لها والله سميع عليم (٢٥٦)) .','206','10','0');
INSERT INTO [t22928] VALUES (249,'قال تعالى: (الله ولي الذين آمنوا يخرجهم من الظلمات إلى النور والذين كفروا أولياؤهم الطاغوت يخرجونهم من النور إلى الظلمات أولئك أصحاب النار هم فيها خالدون (٢٥٧)) .','207','10','0');
INSERT INTO [t22928] VALUES (250,'قال تعالى: (ألم تر إلى الذي حاج إبراهيم في ربه أن آتاه الله الملك إذ قال إبراهيم ربي الذي يحيي ويميت قال أنا أحيي وأميت قال إبراهيم فإن الله يأتي بالشمس من المشرق فأت بها من المغرب فبهت الذي كفر والله لا يهدي القوم الظالمين (٢٥٨)) .','207','10','0');
INSERT INTO [t22928] VALUES (251,'قال تعالى: (أو كالذي مر على قرية وهي خاوية على عروشها قال أنى يحيي هذه الله بعد موتها فأماته الله مائة عام ثم بعثه قال كم لبثت قال لبثت يوما أو بعض يوم قال بل لبثت مائة عام فانظر إلى طعامك وشرابك لم يتسنه وانظر إلى حمارك ولنجعلك آية للناس وانظر إلى العظام كيف ننشزها ثم نكسوها لحما فلما تبين له قال أعلم أن الله على كل شيء قدير (٢٥٩)) .','208','10','0');
INSERT INTO [t22928] VALUES (252,'قال تعالى: (وإذ قال إبراهيم رب أرني كيف تحيي الموتى قال أولم تؤمن قال بلى ولكن ليطمئن قلبي قال فخذ أربعة من الطير فصرهن إليك ثم اجعل على كل جبل منهن جزءا ثم ادعهن يأتينك سعيا واعلم أن الله عزيز حكيم (٢٦٠)) .','212','10','0');
INSERT INTO [t22928] VALUES (253,'قال تعالى: (مثل الذين ينفقون أموالهم في سبيل الله كمثل حبة أنبتت سبع سنابل في كل سنبلة مائة حبة والله يضاعف لمن يشاء والله واسع عليم (٢٦١)) .','214','10','0');
INSERT INTO [t22928] VALUES (254,'قال تعالى: (الذين ينفقون أموالهم في سبيل الله ثم لا يتبعون ما أنفقوا منا ولا أذى لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون (٢٦٢)) .','214','10','0');
INSERT INTO [t22928] VALUES (255,'قال تعالى: (قول معروف ومغفرة خير من صدقة يتبعها أذى والله غني حليم (٢٦٣)) .','214','10','0');
INSERT INTO [t22928] VALUES (256,'قال تعالى: (يا أيها الذين آمنوا لا تبطلوا صدقاتكم بالمن والأذى كالذي ينفق ماله رئاء الناس ولا يؤمن بالله واليوم الآخر فمثله كمثل صفوان عليه تراب فأصابه وابل فتركه صلدا لا يقدرون على شيء مما كسبوا والله لا يهدي القوم الكافرين (٢٦٤)) .','215','10','0');
INSERT INTO [t22928] VALUES (257,'قال تعالى: (ومثل الذين ينفقون أموالهم ابتغاء مرضاة الله وتثبيتا من أنفسهم كمثل جنة بربوة أصابها وابل فآتت أكلها ضعفين فإن لم يصبها وابل فطل والله بما تعملون بصير (٢٦٥)) .','216','10','0');
INSERT INTO [t22928] VALUES (258,'قال تعالى: (أيود أحدكم أن تكون له جنة من نخيل وأعناب تجري من تحتها الأنهار له فيها من كل الثمرات وأصابه الكبر وله ذرية ضعفاء فأصابها إعصار فيه نار فاحترقت كذلك يبين الله لكم الآيات لعلكم تتفكرون (٢٦٦)) .','218','10','0');
INSERT INTO [t22928] VALUES (259,'قال تعالى: (يا أيها الذين آمنوا أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه واعلموا أن الله غني حميد (٢٦٧)) .','219','10','0');
INSERT INTO [t22928] VALUES (260,'قال تعالى: (الشيطان يعدكم الفقر ويأمركم بالفحشاء والله يعدكم مغفرة منه وفضلا والله واسع عليم (٢٦٨)) .','221','10','0');
INSERT INTO [t22928] VALUES (261,'قال تعالى: (يؤتي الحكمة من يشاء ومن يؤت الحكمة فقد أوتي خيرا كثيرا وما يذكر إلا أولو الألباب (٢٦٩)) .','221','10','0');
INSERT INTO [t22928] VALUES (262,'قال تعالى: (وما أنفقتم من نفقة أو نذرتم من نذر فإن الله يعلمه وما للظالمين من أنصار (٢٧٠)) .','221','10','0');
INSERT INTO [t22928] VALUES (263,'قال تعالى: (إن تبدوا الصدقات فنعما هي وإن تخفوها وتؤتوها الفقراء فهو خير لكم ويكفر عنكم من سيئاتكم والله بما تعملون خبير (٢٧١)) .','221','10','0');
INSERT INTO [t22928] VALUES (264,'قال تعالى: (للفقراء الذين أحصروا في سبيل الله لا يستطيعون ضربا في الأرض يحسبهم الجاهل أغنياء من التعفف تعرفهم بسيماهم لا يسألون الناس إلحافا وما تنفقوا من خير فإن الله به عليم (٢٣٧)) .','223','10','0');
INSERT INTO [t22928] VALUES (265,'قال تعالى: (الذين ينفقون أموالهم بالليل والنهار سرا وعلانية فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون (٢٧٤)) .','224','10','0');
INSERT INTO [t22928] VALUES (266,'قال تعالى: (الذين يأكلون الربا لا يقومون إلا كما يقوم الذي يتخبطه الشيطان من المس ذلك بأنهم قالوا إنما البيع مثل الربا وأحل الله البيع وحرم الربا فمن جاءه موعظة من ربه فانتهى فله ما سلف وأمره إلى الله ومن عاد فأولئك أصحاب النار هم فيها خالدون (٢٧٥)) .','224','10','0');
INSERT INTO [t22928] VALUES (267,'قال تعالى: (يمحق الله الربا ويربي الصدقات والله لا يحب كل كفار أثيم (٢٧٦)) .','225','10','0');
INSERT INTO [t22928] VALUES (268,'قال تعالى: (يا أيها الذين آمنوا اتقوا الله وذروا ما بقي من الربا إن كنتم مؤمنين (٢٧٨)) .','225','10','0');
INSERT INTO [t22928] VALUES (269,'قال تعالى: (فإن لم تفعلوا فأذنوا بحرب من الله ورسوله وإن تبتم فلكم رءوس أموالكم لا تظلمون ولا تظلمون (٢٧٩)) .','225','10','0');
INSERT INTO [t22928] VALUES (270,'قال تعالى: (وإن كان ذو عسرة فنظرة إلى ميسرة وأن تصدقوا خير لكم إن كنتم تعلمون (٢٨٠)) .','226','10','0');
INSERT INTO [t22928] VALUES (271,'قال تعالى: (واتقوا يوما ترجعون فيه إلى الله ثم توفى كل نفس ما كسبت وهم لا يظلمون (٢٨١)) .','227','10','0');
INSERT INTO [t22928] VALUES (272,'قال تعالى: (يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه وليكتب بينكم كاتب بالعدل ولا يأب كاتب أن يكتب كما علمه الله فليكتب وليملل الذي عليه الحق وليتق الله ربه ولا يبخس منه شيئا فإن كان الذي عليه الحق سفيها أو ضعيفا أو لا يستطيع أن يمل هو فليملل وليه بالعدل واستشهدوا شهيدين من رجالكم فإن لم يكونا رجلين فرجل وامرأتان ممن ترضون من الشهداء أن تضل إحداهما فتذكر إحداهما الأخرى ولا يأب الشهداء إذا ما دعوا ولا تسأموا أن تكتبوه صغيرا أو كبيرا إلى أجله ذلكم أقسط عند الله وأقوم للشهادة وأدنى ألا ترتابوا إلا أن تكون تجارة حاضرة تديرونها بينكم فليس عليكم جناح ألا تكتبوها وأشهدوا إذا تبايعتم ولا يضار كاتب ولا شهيد وإن تفعلوا فإنه فسوق بكم واتقوا الله ويعلمكم الله والله بكل شيء عليم (٢٨٢)) .','228','10','0');
INSERT INTO [t22928] VALUES (273,'قال تعالى: (وإن كنتم على سفر ولم تجدوا كاتبا فرهان مقبوضة فإن أمن بعضكم بعضا فليؤد الذي اؤتمن أمانته وليتق الله ربه ولا تكتموا الشهادة ومن يكتمها فإنه آثم قلبه والله بما تعملون عليم (٢٨٣)) .','233','10','0');
INSERT INTO [t22928] VALUES (274,'قال تعالى: (لله ما في السماوات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء والله على كل شيء قدير (٢٨٤)) .','234','10','0');
INSERT INTO [t22928] VALUES (275,'قال تعالى: (آمن الرسول بما أنزل إليه من ربه والمؤمنون كل آمن بالله وملائكته وكتبه ورسله لا نفرق بين أحد من رسله وقالوا سمعنا وأطعنا غفرانك ربنا وإليك المصير (٢٨٥)) .','234','10','0');
INSERT INTO [t22928] VALUES (276,'قال تعالى: (لا يكلف الله نفسا إلا وسعها لها ما كسبت وعليها ما اكتسبت ربنا لا تؤاخذنا إن نسينا أو أخطأنا ربنا ولا تحمل علينا إصرا كما حملته على الذين من قبلنا ربنا ولا تحملنا ما لا طاقة لنا به واعف عنا واغفر لنا وارحمنا أنت مولانا فانصرنا على القوم الكافرين (٢٨٦)) .','235','10','0');
INSERT INTO [t22928] VALUES (277,'آل عمران','236','0','0');
INSERT INTO [t22928] VALUES (278,'قال تعالى: (الم (١) الله لا إله إلا هو الحي القيوم (٢)) .','236','277','0');
INSERT INTO [t22928] VALUES (279,'قال تعالى: (نزل عليك الكتاب بالحق مصدقا لما بين يديه وأنزل التوراة والإنجيل (٣) من قبل هدى للناس وأنزل الفرقان إن الذين كفروا بآيات الله لهم عذاب شديد والله عزيز ذو انتقام (٤)) .','236','277','0');
INSERT INTO [t22928] VALUES (280,'قال تعالى: (إن الله لا يخفى عليه شيء في الأرض ولا في السماء (٥)) .','238','277','0');
INSERT INTO [t22928] VALUES (281,'قال تعالى: (هو الذي يصوركم في الأرحام كيف يشاء لا إله إلا هو العزيز الحكيم (٦)) .','238','277','0');
INSERT INTO [t22928] VALUES (282,'قال تعالى: (هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب وأخر متشابهات فأما الذين في قلوبهم زيغ فيتبعون ما تشابه منه ابتغاء الفتنة وابتغاء تأويله وما يعلم تأويله إلا الله والراسخون في العلم يقولون آمنا به كل من عند ربنا وما يذكر إلا أولو الألباب (٧)) .','238','277','0');
INSERT INTO [t22928] VALUES (283,'قال تعالى: (ربنا لا تزغ قلوبنا بعد إذ هديتنا وهب لنا من لدنك رحمة إنك أنت الوهاب (٨)) .','240','277','0');
INSERT INTO [t22928] VALUES (284,'قال تعالى: (ربنا إنك جامع الناس ليوم لا ريب فيه إن الله لا يخلف الميعاد (٩)) .','241','277','0');
INSERT INTO [t22928] VALUES (285,'قال تعالى: (إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا وأولئك هم وقود النار (١٠)) .','241','277','0');
INSERT INTO [t22928] VALUES (286,'قال تعالى: (كدأب آل فرعون والذين من قبلهم كذبوا بآياتنا فأخذهم الله بذنوبهم والله شديد العقاب (١١)) .','242','277','0');
INSERT INTO [t22928] VALUES (287,'قال تعالى: (قل للذين كفروا ستغلبون وتحشرون إلى جهنم وبئس المهاد (١٢)) .','243','277','0');
INSERT INTO [t22928] VALUES (288,'قال تعالى: (قد كان لكم آية في فئتين التقتا فئة تقاتل في سبيل الله وأخرى كافرة يرونهم مثليهم رأي العين والله يؤيد بنصره من يشاء إن في ذلك لعبرة لأولي الأبصار (١٣)) .','243','277','0');
INSERT INTO [t22928] VALUES (289,'قال تعالى: (زين للناس حب الشهوات من النساء والبنين والقناطير المقنطرة من الذهب والفضة والخيل المسومة والأنعام والحرث ذلك متاع الحياة الدنيا والله عنده حسن المآب (١٤)) .','245','277','0');
INSERT INTO [t22928] VALUES (290,'قال تعالى: (قل أؤنبئكم بخير من ذلكم للذين اتقوا عند ربهم جنات تجري من تحتها الأنهار خالدين فيها وأزواج مطهرة ورضوان من الله والله بصير بالعباد (١٥)) .','246','277','0');
INSERT INTO [t22928] VALUES (291,'قال تعالى: (الذين يقولون ربنا إننا آمنا فاغفر لنا ذنوبنا وقنا عذاب النار (١٦)) .','247','277','0');
INSERT INTO [t22928] VALUES (292,'قال تعالى: (الصابرين والصادقين والقانتين والمنفقين والمستغفرين بالأسحار (١٧)) .','247','277','0');
INSERT INTO [t22928] VALUES (293,'قال تعالى: (شهد الله أنه لا إله إلا هو والملائكة وأولو العلم قائما بالقسط لا إله إلا هو العزيز الحكيم (١٨)) .','248','277','0');
INSERT INTO [t22928] VALUES (294,'قال تعالى: (إن الدين عند الله الإسلام وما اختلف الذين أوتوا الكتاب إلا من بعد ما جاءهم العلم بغيا بينهم ومن يكفر بآيات الله فإن الله سريع الحساب (١٩)) .','249','277','0');
INSERT INTO [t22928] VALUES (295,'قال تعالى: (فإن حاجوك فقل أسلمت وجهي لله ومن اتبعن وقل للذين أوتوا الكتاب والأميين أأسلمتم فإن أسلموا فقد اهتدوا وإن تولوا فإنما عليك البلاغ والله بصير بالعباد (٢٠)) .','249','277','0');
INSERT INTO [t22928] VALUES (296,'قال تعالى: (إن الذين يكفرون بآيات الله ويقتلون النبيين بغير حق ويقتلون الذين يأمرون بالقسط من الناس فبشرهم بعذاب أليم (٢١)) .','250','277','0');
INSERT INTO [t22928] VALUES (297,'قال تعالى: (ألم تر إلى الذين أوتوا نصيبا من الكتاب يدعون إلى كتاب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون (٢٣)) .','250','277','0');
INSERT INTO [t22928] VALUES (298,'قال تعالى: (ذلك بأنهم قالوا لن تمسنا النار إلا أياما معدودات وغرهم في دينهم ما كانوا يفترون (٢٤)) .','250','277','0');
INSERT INTO [t22928] VALUES (299,'قال تعالى: (فكيف إذا جمعناهم ليوم لا ريب فيه ووفيت كل نفس ما كسبت وهم لا يظلمون (٢٥)) .','251','277','0');
INSERT INTO [t22928] VALUES (300,'قال تعالى: (قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء بيدك الخير إنك على كل شيء قدير (٢٦)) .','251','277','0');
INSERT INTO [t22928] VALUES (301,'قال تعالى: (تولج الليل في النهار وتولج النهار في الليل وتخرج الحي من الميت وتخرج الميت من الحي وترزق من تشاء بغير حساب (٢٧)) .','252','277','0');
INSERT INTO [t22928] VALUES (302,'قال تعالى: (لا يتخذ المؤمنون الكافرين أولياء من دون المؤمنين ومن يفعل ذلك فليس من الله في شيء إلا أن تتقوا منهم تقاة ويحذركم الله نفسه وإلى الله المصير (٢٨)) .','252','277','0');
INSERT INTO [t22928] VALUES (303,'قال تعالى: (قل إن تخفوا ما في صدوركم أو تبدوه يعلمه الله ويعلم ما في السماوات وما في الأرض والله على كل شيء قدير (٢٩)) .','253','277','0');
INSERT INTO [t22928] VALUES (304,'قال تعالى: (يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو أن بينها وبينه أمدا بعيدا ويحذركم الله نفسه والله رءوف بالعباد (٣٠)) .','253','277','0');
INSERT INTO [t22928] VALUES (305,'قال تعالى: (قل أطيعوا الله والرسول فإن تولوا فإن الله لا يحب الكافرين (٣٢)) .','254','277','0');
INSERT INTO [t22928] VALUES (306,'قال تعالى: (إن الله اصطفى آدم ونوحا وآل إبراهيم وآل عمران على العالمين (٣٣) ذرية بعضها من بعض والله سميع عليم (٣٤)) .','254','277','0');
INSERT INTO [t22928] VALUES (307,'قال تعالى: (إذ قالت امرأة عمران رب إني نذرت لك ما في بطني محررا فتقبل مني إنك أنت السميع العليم (٣٥)) .','254','277','0');
INSERT INTO [t22928] VALUES (308,'قال تعالى: (فلما وضعتها قالت رب إني وضعتها أنثى والله أعلم بما وضعت وليس الذكر كالأنثى وإني سميتها مريم وإني أعيذها بك وذريتها من الشيطان الرجيم (٣٦)) .','255','277','0');
INSERT INTO [t22928] VALUES (309,'قال تعالى: (فتقبلها ربها بقبول حسن وأنبتها نباتا حسنا وكفلها زكريا كلما دخل عليها زكريا المحراب وجد عندها رزقا قال يا مريم أنى لك هذا قالت هو من عند الله إن الله يرزق من يشاء بغير حساب (٣٧)) .','255','277','0');
INSERT INTO [t22928] VALUES (310,'قال تعالى: (هنالك دعا زكريا ربه قال رب هب لي من لدنك ذرية طيبة إنك سميع الدعاء (٣٨)) .','257','277','0');
INSERT INTO [t22928] VALUES (311,'قال تعالى: (فنادته الملائكة وهو قائم يصلي في المحراب أن الله يبشرك بيحيى مصدقا بكلمة من الله وسيدا وحصورا ونبيا من الصالحين (٣٩)) .','257','277','0');
INSERT INTO [t22928] VALUES (312,'قال تعالى: (قال رب أنى يكون لي غلام وقد بلغني الكبر وامرأتي عاقر قال كذلك الله يفعل ما يشاء (٤٠)) .','258','277','0');
INSERT INTO [t22928] VALUES (313,'قال تعالى: (قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا واذكر ربك كثيرا وسبح بالعشي والإبكار (٤١)) .','259','277','0');
INSERT INTO [t22928] VALUES (314,'قال تعالى: (وإذ قالت الملائكة يا مريم إن الله اصطفاك وطهرك واصطفاك على نساء العالمين (٤٢)) .','259','277','0');
INSERT INTO [t22928] VALUES (315,'قال تعالى: (ذلك من أنباء الغيب نوحيه إليك وما كنت لديهم إذ يلقون أقلامهم أيهم يكفل مريم وما كنت لديهم إذ يختصمون (٤٤)) .','260','277','0');
INSERT INTO [t22928] VALUES (316,'قال تعالى: (إذ قالت الملائكة يا مريم إن الله يبشرك بكلمة منه اسمه المسيح عيسى ابن مريم وجيها في الدنيا والآخرة ومن المقربين (٤٥)) .','261','277','0');
INSERT INTO [t22928] VALUES (317,'قال تعالى: (ويكلم الناس في المهد وكهلا ومن الصالحين (٤٦)) .','262','277','0');
INSERT INTO [t22928] VALUES (318,'قال تعالى: (قالت رب أنى يكون لي ولد ولم يمسسني بشر قال كذلك الله يخلق ما يشاء إذا قضى أمرا فإنما يقول له كن فيكون (٤٧)) .','262','277','0');
INSERT INTO [t22928] VALUES (319,'قال تعالى: (ويعلمه الكتاب والحكمة والتوراة والإنجيل (٤٨) ورسولا إلى بني إسرائيل أني قد جئتكم بآية من ربكم أني أخلق لكم من الطين كهيئة الطير فأنفخ فيه فيكون طيرا بإذن الله وأبرئ الأكمه والأبرص وأحيي الموتى بإذن الله وأنبئكم بما تأكلون وما تدخرون في بيوتكم إن في ذلك لآية لكم إن كنتم مؤمنين (٤٩)) .','262','277','0');
INSERT INTO [t22928] VALUES (320,'قال تعالى: (ومصدقا لما بين يدي من التوراة ولأحل لكم بعض الذي حرم عليكم وجئتكم بآية من ربكم فاتقوا الله وأطيعون (٥٠)) .','264','277','0');
INSERT INTO [t22928] VALUES (321,'قال تعالى: (فلما أحس عيسى منهم الكفر قال من أنصاري إلى الله قال الحواريون نحن أنصار الله آمنا بالله واشهد بأنا مسلمون (٥٢)) .','265','277','0');
INSERT INTO [t22928] VALUES (322,'قال تعالى: (ربنا آمنا بما أنزلت واتبعنا الرسول فاكتبنا مع الشاهدين (٥٣)) .','266','277','0');
INSERT INTO [t22928] VALUES (323,'قال تعالى: (ومكروا ومكر الله والله خير الماكرين (٥٤)) .','266','277','0');
INSERT INTO [t22928] VALUES (324,'قال تعالى: (إذ قال الله يا عيسى إني متوفيك ورافعك إلي ومطهرك من الذين كفروا وجاعل الذين اتبعوك فوق الذين كفروا إلى يوم القيامة ثم إلي مرجعكم فأحكم بينكم فيما كنتم فيه تختلفون (٥٥)) .','266','277','0');
INSERT INTO [t22928] VALUES (325,'قال تعالى: (فأما الذين كفروا فأعذبهم عذابا شديدا في الدنيا والآخرة وما لهم من ناصرين (٥٦) وأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم والله لا يحب الظالمين (٥٧) .','267','277','0');
INSERT INTO [t22928] VALUES (326,'قال تعالى: (ذلك نتلوه عليك من الآيات والذكر الحكيم (٥٨)) .','267','277','0');
INSERT INTO [t22928] VALUES (327,'قال تعالى: (إن مثل عيسى عند الله كمثل آدم خلقه من تراب ثم قال له كن فيكون (٥٩)) .','267','277','0');
INSERT INTO [t22928] VALUES (328,'قال تعالى: (فمن حاجك فيه من بعد ما جاءك من العلم فقل تعالوا ندع أبناءنا وأبناءكم ونساءنا ونساءكم وأنفسنا وأنفسكم ثم نبتهل فنجعل لعنة الله على الكاذبين (٦١)) .','268','277','0');
INSERT INTO [t22928] VALUES (329,'قال تعالى: (إن هذا لهو القصص الحق وما من إله إلا الله وإن الله لهو العزيز الحكيم (٦٢)) .','269','277','0');
INSERT INTO [t22928] VALUES (330,'قال تعالى: (فإن تولوا فإن الله عليم بالمفسدين (٦٣)) .','269','277','0');
INSERT INTO [t22928] VALUES (331,'قال تعالى: (قل يا أهل الكتاب تعالوا إلى كلمة سواء بيننا وبينكم ألا نعبد إلا الله ولا نشرك به شيئا ولا يتخذ بعضنا بعضا أربابا من دون الله فإن تولوا فقولوا اشهدوا بأنا مسلمون (٦٤)) .','269','277','0');
INSERT INTO [t22928] VALUES (332,'قال تعالى: (يا أهل الكتاب لم تحاجون في إبراهيم وما أنزلت التوراة والإنجيل إلا من بعده أفلا تعقلون (٦٥)) .','270','277','0');
INSERT INTO [t22928] VALUES (333,'قال تعالى: (ها أنتم هؤلاء حاججتم فيما لكم به علم فلم تحاجون فيما ليس لكم به علم والله يعلم وأنتم لا تعلمون (٦٦)) .','270','277','0');
INSERT INTO [t22928] VALUES (334,'قال تعالى: (إن أولى الناس بإبراهيم للذين اتبعوه وهذا النبي والذين آمنوا والله ولي المؤمنين (٦٨)) .','271','277','0');
INSERT INTO [t22928] VALUES (335,'قال تعالى: (وقالت طائفة من أهل الكتاب آمنوا بالذي أنزل على الذين آمنوا وجه النهار واكفروا آخره لعلهم يرجعون (٧٢)) .','271','277','0');
INSERT INTO [t22928] VALUES (336,'قال تعالى: (ولا تؤمنوا إلا لمن تبع دينكم قل إن الهدى هدى الله أن يؤتى أحد مثل ما أوتيتم أو يحاجوكم عند ربكم قل إن الفضل بيد الله يؤتيه من يشاء والله واسع عليم (٧٣)) .','271','277','0');
INSERT INTO [t22928] VALUES (337,'قال تعالى: (ومن أهل الكتاب من إن تأمنه بقنطار يؤده إليك ومنهم من إن تأمنه بدينار لا يؤده إليك إلا ما دمت عليه قائما ذلك بأنهم قالوا ليس علينا في الأميين سبيل ويقولون على الله الكذب وهم يعلمون (٧٥)) .','273','277','0');
INSERT INTO [t22928] VALUES (338,'قال تعالى: (بلى من أوفى بعهده واتقى فإن الله يحب المتقين (٧٦)) .','274','277','0');
INSERT INTO [t22928] VALUES (339,'قال تعالى: (وإن منهم لفريقا يلوون ألسنتهم بالكتاب لتحسبوه من الكتاب وما هو من الكتاب (٧٨)) .','274','277','0');
INSERT INTO [t22928] VALUES (340,'قال تعالى: (ما كان لبشر أن يؤتيه الله الكتاب والحكم والنبوة ثم يقول للناس كونوا عبادا لي من دون الله ولكن كونوا ربانيين بما كنتم تعلمون الكتاب وبما كنتم تدرسون (٧٩)) .','275','277','0');
INSERT INTO [t22928] VALUES (341,'قال تعالى: (ولا يأمركم أن تتخذوا الملائكة والنبيين أربابا أيأمركم بالكفر بعد إذ أنتم مسلمون (٨٠)) .','276','277','0');
INSERT INTO [t22928] VALUES (342,'قال تعالى: (وإذ أخذ الله ميثاق النبيين لما آتيتكم من كتاب وحكمة ثم جاءكم رسول مصدق لما معكم لتؤمنن به ولتنصرنه قال أأقررتم وأخذتم على ذلكم إصري قالوا أقررنا قال فاشهدوا وأنا معكم من الشاهدين (٨١)) .','276','277','0');
INSERT INTO [t22928] VALUES (343,'قال تعالى {فمن تولى بعد ذلك فأولئك هم الفاسقون}','278','277','0');
INSERT INTO [t22928] VALUES (344,'قال تعالى: (أفغير دين الله يبغون وله أسلم من في السماوات والأرض طوعا وكرها وإليه يرجعون (٨٢)) .','278','277','0');
INSERT INTO [t22928] VALUES (345,'قال تعالى: (قل آمنا بالله وما أنزل علينا وما أنزل على إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى والنبيون من ربهم لا نفرق بين أحد منهم ونحن له مسلمون (٨٤)) .','278','277','0');
INSERT INTO [t22928] VALUES (346,'قال تعالى: (ومن يبتغ غير الإسلام دينا فلن يقبل منه وهو في الآخرة من الخاسرين (٨٥)) .','279','277','0');
INSERT INTO [t22928] VALUES (347,'قال تعالى: (كيف يهدي الله قوما كفروا بعد إيمانهم وشهدوا أن الرسول حق وجاءهم البينات والله لا يهدي القوم الظالمين (٨٦)) .','279','277','0');
INSERT INTO [t22928] VALUES (348,'قال تعالى: (أولئك جزاؤهم أن عليهم لعنة الله والملائكة والناس أجمعين (٨٧)) .','279','277','0');
INSERT INTO [t22928] VALUES (349,'قال تعالى: (خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون (٨٨)) .','280','277','0');
INSERT INTO [t22928] VALUES (350,'قال تعالى: (إن الذين كفروا وماتوا وهم كفار فلن يقبل من أحدهم ملء الأرض ذهبا ولو افتدى به أولئك لهم عذاب أليم وما لهم من ناصرين (٩١)) .','280','277','0');
INSERT INTO [t22928] VALUES (351,'قال تعالى: (لن تنالوا البر حتى تنفقوا مما تحبون وما تنفقوا من شيء فإن الله به عليم (٩٢)) .','280','277','0');
INSERT INTO [t22928] VALUES (352,'قال تعالى: (كل الطعام كان حلا لبني إسرائيل إلا ما حرم إسرائيل على نفسه من قبل أن تنزل التوراة قل فأتوا بالتوراة فاتلوها إن كنتم صادقين (٩٣)) .','280','277','0');
INSERT INTO [t22928] VALUES (353,'قال تعالى: (فمن افترى على الله الكذب من بعد ذلك فأولئك هم الظالمون (٩٤)) .','281','277','0');
INSERT INTO [t22928] VALUES (354,'قال تعالى: (قل صدق الله فاتبعوا ملة إبراهيم حنيفا وما كان من المشركين (٩٥)) .','281','277','0');
INSERT INTO [t22928] VALUES (355,'قال تعالى: (إن أول بيت وضع للناس للذي ببكة مباركا وهدى للعالمين (٩٦)) .','281','277','0');
INSERT INTO [t22928] VALUES (356,'قال تعالى: (فيه آيات بينات مقام إبراهيم ومن دخله كان آمنا ولله على الناس حج البيت من استطاع إليه سبيلا ومن كفر فإن الله غني عن العالمين (٩٧)) .','281','277','0');
INSERT INTO [t22928] VALUES (357,'قال تعالى: (قل يا أهل الكتاب لم تصدون عن سبيل الله من آمن تبغونها عوجا وأنتم شهداء وما الله بغافل عما تعملون (٩٩)) .','283','277','0');
INSERT INTO [t22928] VALUES (358,'قال تعالى: (يا أيها الذين آمنوا إن تطيعوا فريقا من الذين أوتوا الكتاب يردوكم بعد إيمانكم كافرين (١٠٠)) .','283','277','0');
INSERT INTO [t22928] VALUES (359,'قال تعالى: (واعتصموا بحبل الله جميعا ولا تفرقوا واذكروا نعمة الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا وكنتم على شفا حفرة من النار فأنقذكم منها كذلك يبين الله لكم آياته لعلكم تهتدون (١٠٣)) .','283','277','0');
INSERT INTO [t22928] VALUES (360,'قال تعالى: (ولتكن منكم أمة يدعون إلى الخير ويأمرون بالمعروف وينهون عن المنكر وأولئك هم المفلحون (١٠٤)) .','284','277','0');
INSERT INTO [t22928] VALUES (361,'قال تعالى: (ولا تكونوا كالذين تفرقوا واختلفوا من بعد ما جاءهم البينات وأولئك لهم عذاب عظيم (١٠٥)) .','285','277','0');
INSERT INTO [t22928] VALUES (362,'قال تعالى: (يوم تبيض وجوه وتسود وجوه فأما الذين اسودت وجوههم أكفرتم بعد إيمانكم فذوقوا العذاب بما كنتم تكفرون (١٠٦)) .','285','277','0');
INSERT INTO [t22928] VALUES (363,'قال تعالى: (تلك آيات الله نتلوها عليك بالحق وما الله يريد ظلما للعالمين (١٠٨)) .','285','277','0');
INSERT INTO [t22928] VALUES (364,'قال تعالى: (كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ولو آمن أهل الكتاب لكان خيرا لهم منهم المؤمنون وأكثرهم الفاسقون (١١٠)) .','285','277','0');
INSERT INTO [t22928] VALUES (365,'قال تعالى: (لن يضروكم إلا أذى وإن يقاتلوكم يولوكم الأدبار ثم لا ينصرون (١١١)) .','286','277','0');
INSERT INTO [t22928] VALUES (366,'قال تعالى: (ضربت عليهم الذلة أين ما ثقفوا إلا بحبل من الله وحبل من الناس وباءوا بغضب من الله وضربت عليهم المسكنة ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون الأنبياء بغير حق ذلك بما عصوا وكانوا يعتدون (١١٢)) .','286','277','0');
INSERT INTO [t22928] VALUES (367,'قال تعالى: (ليسوا سواء من أهل الكتاب أمة قائمة يتلون آيات الله آناء الليل وهم يسجدون (١١٣)) .','287','277','0');
INSERT INTO [t22928] VALUES (368,'قال تعالى: (وما يفعلوا من خير فلن يكفروه والله عليم بالمتقين (١١٥)) .','287','277','0');
INSERT INTO [t22928] VALUES (369,'قال تعالى: (مثل ما ينفقون في هذه الحياة الدنيا كمثل ريح فيها صر أصابت حرث قوم ظلموا أنفسهم فأهلكته وما ظلمهم الله ولكن أنفسهم يظلمون (١١٧)) .','288','277','0');
INSERT INTO [t22928] VALUES (370,'قال تعالى: (يا أيها الذين آمنوا لا تتخذوا بطانة من دونكم لا يألونكم خبالا ودوا ما عنتم قد بدت البغضاء من أفواههم وما تخفي صدورهم أكبر قد بينا لكم الآيات إن كنتم تعقلون (١١٨)) .','288','277','0');
INSERT INTO [t22928] VALUES (371,'قال تعالى: (ها أنتم أولاء تحبونهم ولا يحبونكم وتؤمنون بالكتاب كله وإذا لقوكم قالوا آمنا وإذا خلوا عضوا عليكم الأنامل من الغيظ قل موتوا بغيظكم إن الله عليم بذات الصدور (١١٩)) .','289','277','0');
INSERT INTO [t22928] VALUES (372,'قال تعالى: (إن تمسسكم حسنة تسؤهم وإن تصبكم سيئة يفرحوا بها وإن تصبروا وتتقوا لا يضركم كيدهم شيئا إن الله بما يعملون محيط (١٢٠)) .','289','277','0');
INSERT INTO [t22928] VALUES (373,'قال تعالى: (وإذ غدوت من أهلك تبوئ المؤمنين مقاعد للقتال والله سميع عليم (١٢١)) .','290','277','0');
INSERT INTO [t22928] VALUES (374,'قال تعالى: (إذ همت طائفتان منكم أن تفشلا والله وليهما وعلى الله فليتوكل المؤمنون (١٢٢)) .','290','277','0');
INSERT INTO [t22928] VALUES (375,'قال تعالى: (ولقد نصركم الله ببدر وأنتم أذلة فاتقوا الله لعلكم تشكرون (١٢٣)) .','291','277','0');
INSERT INTO [t22928] VALUES (376,'قال تعالى: (إذ تقول للمؤمنين ألن يكفيكم أن يمدكم ربكم بثلاثة آلاف من الملائكة منزلين (١٢٤) بلى إن تصبروا وتتقوا ويأتوكم من فورهم هذا يمددكم ربكم بخمسة آلاف من الملائكة مسومين (١٢٥)) .','291','277','0');
INSERT INTO [t22928] VALUES (377,'قال تعالى: (وما جعله الله إلا بشرى لكم ولتطمئن قلوبكم به وما النصر إلا من عند الله العزيز الحكيم (١٢٦)) .','292','277','0');
INSERT INTO [t22928] VALUES (378,'قال تعالى: (ليقطع طرفا من الذين كفروا أو يكبتهم فينقلبوا خائبين (١٢٧)) .','292','277','0');
INSERT INTO [t22928] VALUES (379,'قال تعالى: (ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون (١٢٨)) .','292','277','0');
INSERT INTO [t22928] VALUES (380,'قال تعالى: (يا أيها الذين آمنوا لا تأكلوا الربا أضعافا مضاعفة واتقوا الله لعلكم تفلحون (١٣٠)) .','293','277','0');
INSERT INTO [t22928] VALUES (381,'قال تعالى: (وسارعوا إلى مغفرة من ربكم وجنة عرضها السموات والأرض أعدت للمتقين (١٣٣)) .','293','277','0');
INSERT INTO [t22928] VALUES (382,'قال تعالى: (الذين ينفقون في السراء والضراء والكاظمين الغيظ والعافين عن الناس والله يحب المحسنين (١٣٤)) .','293','277','0');
INSERT INTO [t22928] VALUES (383,'قال تعالى: (والذين إذا فعلوا فاحشة أو ظلموا أنفسهم ذكروا الله فاستغفروا لذنوبهم ومن يغفر الذنوب إلا الله ولم يصروا على ما فعلوا وهم يعلمون (١٣٥)) .','294','277','0');
INSERT INTO [t22928] VALUES (384,'قال تعالى: (أولئك جزاؤهم مغفرة من ربهم وجنات تجري من تحتها الأنهار خالدين فيها ونعم أجر العاملين (١٣٦)) .','294','277','0');
INSERT INTO [t22928] VALUES (385,'قال تعالى: (قد خلت من قبلكم سنن فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين (١٣٧)) .','294','277','0');
INSERT INTO [t22928] VALUES (386,'قال تعالى: (ولا تهنوا ولا تحزنوا وأنتم الأعلون إن كنتم مؤمنين (١٣٩)) .','295','277','0');
INSERT INTO [t22928] VALUES (387,'قال تعالى: (إن يمسسكم قرح فقد مس القوم قرح مثله وتلك الأيام نداولها بين الناس وليعلم الله الذين آمنوا ويتخذ منكم شهداء والله لا يحب الظالمين (١٤٠) وليمحص الله الذين آمنوا ويمحق الكافرين (١٤١)) .','295','277','0');
INSERT INTO [t22928] VALUES (388,'قال تعالى: (أم حسبتم أن تدخلوا الجنة ولما يعلم الله الذين جاهدوا منكم ويعلم الصابرين (١٤٢)) .','296','277','0');
INSERT INTO [t22928] VALUES (389,'قال تعالى: (ولقد كنتم تمنون الموت من قبل أن تلقوه فقد رأيتموه وأنتم تنظرون (١٤٣)) .','296','277','0');
INSERT INTO [t22928] VALUES (390,'قال تعالى: (وما محمد إلا رسول قد خلت من قبله الرسل أفإن مات أو قتل انقلبتم على أعقابكم ومن ينقلب على عقبيه فلن يضر الله شيئا وسيجزي الله الشاكرين (١٤٤)) .','297','277','0');
INSERT INTO [t22928] VALUES (391,'قال تعالى: (وما كان لنفس أن تموت إلا بإذن الله كتابا مؤجلا ومن يرد ثواب الدنيا نؤته منها ومن يرد ثواب الآخرة نؤته منها وسنجزي الشاكرين (١٤٥)) .','298','277','0');
INSERT INTO [t22928] VALUES (392,'قال تعالى: (وكأين من نبي قاتل معه ربيون كثير فما وهنوا لما أصابهم في سبيل الله وما ضعفوا وما استكانوا والله يحب الصابرين (١٤٦)) .','298','277','0');
INSERT INTO [t22928] VALUES (393,'قال تعالى: (وما كان قولهم إلا أن قالوا ربنا اغفر لنا ذنوبنا وإسرافنا في أمرنا وثبت أقدامنا وانصرنا على القوم الكافرين (١٤٧)) .','301','277','0');
INSERT INTO [t22928] VALUES (394,'قال تعالى: (بل الله مولاكم وهو خير الناصرين (١٥٠)) .','301','277','0');
INSERT INTO [t22928] VALUES (395,'قال تعالى: (سنلقي في قلوب الذين كفروا الرعب بما أشركوا بالله ما لم ينزل به سلطانا ومأواهم النار وبئس مثوى الظالمين (١٥١)) .','301','277','0');
INSERT INTO [t22928] VALUES (396,'قال تعالى: (ولقد صدقكم الله وعده إذ تحسونهم بإذنه حتى إذا فشلتم وتنازعتم في الأمر وعصيتم من بعد ما أراكم ما تحبون منكم من يريد الدنيا ومنكم من يريد الآخرة ثم صرفكم عنهم ليبتليكم ولقد عفا عنكم والله ذو فضل على المؤمنين (١٥٢)) .','302','277','0');
INSERT INTO [t22928] VALUES (397,'قال تعالى: (إذ تصعدون ولا تلوون على أحد والرسول يدعوكم في أخراكم فأثابكم غما بغم لكيلا تحزنوا على ما فاتكم ولا ما أصابكم والله خبير بما تعملون (١٥٣)) .','302','277','0');
INSERT INTO [t22928] VALUES (398,'قال تعالى: (ثم أنزل عليكم من بعد الغم أمنة نعاسا يغشى طائفة منكم وطائفة قد أهمتهم أنفسهم يظنون بالله غير الحق ظن الجاهلية يقولون هل لنا من الأمر من شيء قل إن الأمر كله لله يخفون في أنفسهم ما لا يبدون لك يقولون لو كان لنا من الأمر شيء ما قتلنا هاهنا قل لو كنتم في بيوتكم لبرز الذين كتب عليهم القتل إلى مضاجعهم وليبتلي الله ما في صدوركم وليمحص ما في قلوبكم والله عليم بذات الصدور (١٥٤)) .','303','277','0');
INSERT INTO [t22928] VALUES (399,'قال تعالى: (يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا لإخوانهم إذا ضربوا في الأرض أو كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا ليجعل الله ذلك حسرة في قلوبهم والله يحيي ويميت والله بما تعملون بصير (١٥٦)) .','304','277','0');
INSERT INTO [t22928] VALUES (400,'قال تعالى: (ولئن قتلتم في سبيل الله أو متم لمغفرة من الله ورحمة خير مما يجمعون (١٥٧)) .','305','277','0');
INSERT INTO [t22928] VALUES (401,'قال تعالى: (ولئن متم أو قتلتم لإلى الله تحشرون (١٥٨)) .','306','277','0');
INSERT INTO [t22928] VALUES (402,'قال تعالى: (فبما رحمة من الله لنت لهم ولو كنت فظا غليظ القلب لانفضوا من حولك فاعف عنهم واستغفر لهم وشاورهم في الأمر فإذا عزمت فتوكل على الله إن الله يحب المتوكلين (١٥٩)) .','306','277','0');
INSERT INTO [t22928] VALUES (403,'قال تعالى: (إن ينصركم الله فلا غالب لكم وإن يخذلكم فمن ذا الذي ينصركم من بعده وعلى الله فليتوكل المؤمنون (١٦٠)) .','307','277','0');
INSERT INTO [t22928] VALUES (404,'قال تعالى: (وما كان لنبي أن يغل ومن يغلل يأت بما غل يوم القيامة ثم توفى كل نفس ما كسبت وهم لا يظلمون (١٦١)) .','307','277','0');
INSERT INTO [t22928] VALUES (405,'قال تعالى: (أفمن اتبع رضوان الله كمن باء بسخط من الله ومأواه جهنم وبئس المصير (١٦٢)) .','308','277','0');
INSERT INTO [t22928] VALUES (406,'قال تعالى: (هم درجات عند الله والله بصير بما يعملون (١٦٣)) .','308','277','0');
INSERT INTO [t22928] VALUES (407,'قال تعالى: (لقد من الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين (١٦٤)) .','308','277','0');
INSERT INTO [t22928] VALUES (408,'قال تعالى: (أولما أصابتكم مصيبة قد أصبتم مثليها قلتم أنى هذا قل هو من عند أنفسكم إن الله على كل شيء قدير (١٦٥)) .','308','277','0');
INSERT INTO [t22928] VALUES (409,'قال تعالى: (وما أصابكم يوم التقى الجمعان فبإذن الله وليعلم المؤمنين (١٦٦) وليعلم الذين نافقوا وقيل لهم تعالوا قاتلوا في سبيل الله أو ادفعوا قالوا لو نعلم قتالا لاتبعناكم هم للكفر يومئذ أقرب منهم للإيمان يقولون بأفواههم ما ليس في قلوبهم والله أعلم بما يكتمون (١٦٧)) .','308','277','0');
INSERT INTO [t22928] VALUES (410,'قال تعالى: (الذين قالوا لإخوانهم وقعدوا لو أطاعونا ما قتلوا قل فادرءوا عن أنفسكم الموت إن كنتم صادقين (١٦٨)) .','309','277','0');
INSERT INTO [t22928] VALUES (411,'قال تعالى: (ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون (١٦٩)) .','310','277','0');
INSERT INTO [t22928] VALUES (412,'قال تعالى: (فرحين بما آتاهم الله من فضله ويستبشرون بالذين لم يلحقوا بهم من خلفهم ألا خوف عليهم ولا هم يحزنون (١٧٠)) .','310','277','0');
INSERT INTO [t22928] VALUES (413,'قال تعالى: (يستبشرون بنعمة من الله وفضل وأن الله لا يضيع أجر المؤمنين (١٧١) الذين استجابوا لله والرسول من بعد ما أصابهم القرح للذين أحسنوا منهم واتقوا أجر عظيم (١٧٢)) .','311','277','0');
INSERT INTO [t22928] VALUES (414,'قال تعالى: (الذين قال لهم الناس إن الناس قد جمعوا لكم فاخشوهم فزادهم إيمانا وقالوا حسبنا الله ونعم الوكيل (١٧٣)) .','311','277','0');
INSERT INTO [t22928] VALUES (415,'قال تعالى: (فانقلبوا بنعمة من الله وفضل لم يمسسهم سوء واتبعوا رضوان الله والله ذو فضل عظيم (١٧٤)) .','312','277','0');
INSERT INTO [t22928] VALUES (416,'قال تعالى: (إنما ذلكم الشيطان يخوف أولياءه فلا تخافوهم وخافون إن كنتم مؤمنين (١٧٥)) .','312','277','0');
INSERT INTO [t22928] VALUES (417,'قال تعالى: (ولا يحزنك الذين يسارعون في الكفر إنهم لن يضروا الله شيئا يريد الله ألا يجعل لهم حظا في الآخرة ولهم عذاب عظيم (١٧٦)) .','313','277','0');
INSERT INTO [t22928] VALUES (418,'قال تعالى: (ولا يحسبن الذين كفروا أنما نملي لهم خير لأنفسهم إنما نملي لهم ليزدادوا إثما ولهم عذاب مهين (١٧٨)) .','313','277','0');
INSERT INTO [t22928] VALUES (419,'قال تعالى: (ما كان الله ليذر المؤمنين على ما أنتم عليه حتى يميز الخبيث من الطيب وما كان الله ليطلعكم على الغيب ولكن الله يجتبي من رسله من يشاء فآمنوا بالله ورسله وإن تؤمنوا وتتقوا فلكم أجر عظيم (١٧٩)) .','315','277','0');
INSERT INTO [t22928] VALUES (420,'قال تعالى: (ولا يحسبن الذين يبخلون بما آتاهم الله من فضله هو خيرا لهم بل هو شر لهم سيطوقون ما بخلوا به يوم القيامة ولله ميراث السماوات والأرض والله بما تعملون خبير (١٨٠)) .','315','277','0');
INSERT INTO [t22928] VALUES (421,'قال تعالى: (لقد سمع الله قول الذين قالوا إن الله فقير ونحن أغنياء سنكتب ما قالوا وقتلهم الأنبياء بغير حق ونقول ذوقوا عذاب الحريق (١٨١)) .','316','277','0');
INSERT INTO [t22928] VALUES (422,'قال تعالى: (ذلك بما قدمت أيديكم وأن الله ليس بظلام للعبيد (١٨٢)) .','317','277','0');
INSERT INTO [t22928] VALUES (423,'قال تعالى: (الذين قالوا إن الله عهد إلينا ألا نؤمن لرسول حتى يأتينا بقربان تأكله النار قل قد جاءكم رسل من قبلي بالبينات وبالذي قلتم فلم قتلتموهم إن كنتم صادقين (١٨٣)) .','317','277','0');
INSERT INTO [t22928] VALUES (424,'قال تعالى: (فإن كذبوك فقد كذب رسل من قبلك جاءوا بالبينات والزبر والكتاب المنير (١٨٤)) .','318','277','0');
INSERT INTO [t22928] VALUES (425,'قال تعالى: (كل نفس ذائقة الموت وإنما توفون أجوركم يوم القيامة فمن زحزح عن النار وأدخل الجنة فقد فاز وما الحياة الدنيا إلا متاع الغرور (١٨٥)) .','318','277','0');
INSERT INTO [t22928] VALUES (426,'قال تعالى: (لتبلون في أموالكم وأنفسكم ولتسمعن من الذين أوتوا الكتاب من قبلكم ومن الذين أشركوا أذى كثيرا وإن تصبروا وتتقوا فإن ذلك من عزم الأمور (١٨٦)) .','319','277','0');
INSERT INTO [t22928] VALUES (427,'قال تعالى: (وإذ أخذ الله ميثاق الذين أوتوا الكتاب لتبيننه للناس ولا تكتمونه فنبذوه وراء ظهورهم واشتروا به ثمنا قليلا فبئس ما يشترون (١٨٧)) .','319','277','0');
INSERT INTO [t22928] VALUES (428,'قال تعالى: (لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب ولهم عذاب أليم (١٨٨)) .','319','277','0');
INSERT INTO [t22928] VALUES (429,'قال تعالى: (الذين يذكرون الله قياما وقعودا وعلى جنوبهم ويتفكرون في خلق السماوات والأرض ربنا ما خلقت هذا باطلا سبحانك فقنا عذاب النار (١٩١) ربنا إنك من تدخل النار فقد أخزيته وما للظالمين من أنصار) (١٩٢) .','321','277','0');
INSERT INTO [t22928] VALUES (430,'قال تعالى: (ربنا إننا سمعنا مناديا ينادي للإيمان أن آمنوا بربكم فآمنا ربنا فاغفر لنا ذنوبنا وكفر عنا سيئاتنا وتوفنا مع الأبرار) (١٩٣) .','322','277','0');
INSERT INTO [t22928] VALUES (431,'قال تعالى: (ربنا وآتنا ما وعدتنا على رسلك ولا تخزنا يوم القيامة إنك لا تخلف الميعاد) (١٩٤) .','323','277','0');
INSERT INTO [t22928] VALUES (432,'قال تعالى: (فاستجاب لهم ربهم أني لا أضيع عمل عامل منكم من ذكر أو أنثى بعضكم من بعض فالذين هاجروا وأخرجوا من ديارهم وأوذوا في سبيلي وقاتلوا وقتلوا لأكفرن عنهم سيئاتهم ولأدخلنهم جنات تجري من تحتها الأنهار ثوابا من عند الله والله عنده حسن الثواب) (١٩٥) .','323','277','0');
INSERT INTO [t22928] VALUES (433,'قال تعالى: (متاع قليل ثم مأواهم جهنم وبئس المهاد) (١٩٧) .','324','277','0');
INSERT INTO [t22928] VALUES (434,'قال تعالى: (لكن الذين اتقوا ربهم لهم جنات تجري من تحتها الأنهار خالدين فيها نزلا من عند الله وما عند الله خير للأبرار) (١٩٨) .','324','277','0');
INSERT INTO [t22928] VALUES (435,'قال تعالى: (وإن من أهل الكتاب لمن يؤمن بالله وما أنزل إليكم وما أنزل إليهم خاشعين لله لا يشترون بآيات الله ثمنا قليلا أولئك لهم أجرهم عند ربهم إن الله سريع الحساب) (١٩٩) .','325','277','0');
INSERT INTO [t22928] VALUES (436,'النساء','327','0','0');
INSERT INTO [t22928] VALUES (437,'قال تعالى: (يا أيها الناس اتقوا ربكم الذي خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا الله الذي تساءلون به والأرحام إن الله كان عليكم رقيبا) (١) .','327','436','0');
INSERT INTO [t22928] VALUES (438,'قال تعالى: (وآتوا اليتامى أموالهم ولا تتبدلوا الخبيث بالطيب ولا تأكلوا أموالهم إلى أموالكم إنه كان حوبا كبيرا) (٢) .','328','436','0');
INSERT INTO [t22928] VALUES (439,'قال تعالى: (وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ذلك أدنى ألا تعولوا) (٣) .','328','436','0');
INSERT INTO [t22928] VALUES (440,'قال تعالى: (وآتوا النساء صدقاتهن نحلة فإن طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا) (٤) .','330','436','0');
INSERT INTO [t22928] VALUES (441,'قال تعالى: (ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا) (٥) .','331','436','0');
INSERT INTO [t22928] VALUES (442,'قال تعالى: (وابتلوا اليتامى حتى إذا بلغوا النكاح فإن آنستم منهم رشدا فادفعوا إليهم أموالهم ولا تأكلوها إسرافا وبدارا أن يكبروا ومن كان غنيا فليستعفف ومن كان فقيرا فليأكل بالمعروف فإذا دفعتم إليهم أموالهم فأشهدوا عليهم وكفى بالله حسيبا) (٦) .','332','436','0');
INSERT INTO [t22928] VALUES (443,'قال تعالى: (للرجال نصيب مما ترك الوالدان والأقربون وللنساء نصيب مما ترك الوالدان والأقربون مما قل منه أو كثر نصيبا مفروضا) (٧) .','333','436','0');
INSERT INTO [t22928] VALUES (444,'قال تعالى: (وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا) (٨) .','334','436','0');
INSERT INTO [t22928] VALUES (445,'قال تعالى: (وليخش الذين لو تركوا من خلفهم ذرية ضعافا خافوا عليهم فليتقوا الله وليقولوا قولا سديدا) (٩) .','334','436','0');
INSERT INTO [t22928] VALUES (446,'قال تعالى: (إن الذين يأكلون أموال اليتامى ظلما إنما يأكلون في بطونهم نارا وسيصلون سعيرا) (١٠) .','334','436','0');
INSERT INTO [t22928] VALUES (447,'قال تعالى: (يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك وإن كانت واحدة فلها النصف ولأبويه لكل واحد منهما السدس مما ترك إن كان له ولد فإن لم يكن له ولد وورثه أبواه فلأمه الثلث فإن كان له إخوة فلأمه السدس من بعد وصية يوصي بها أو دين آباؤكم وأبناؤكم لا تدرون أيهم أقرب لكم نفعا فريضة من الله إن الله كان عليما حكيما) (١١) .','335','436','0');
INSERT INTO [t22928] VALUES (448,'قال تعالى: (ولكم نصف ما ترك أزواجكم إن لم يكن لهن ولد فإن كان لهن ولد فلكم الربع مما تركن من بعد وصية يوصين بها أو دين ولهن الربع مما تركتم إن لم يكن لكم ولد فإن كان لكم ولد فلهن الثمن مما تركتم من بعد وصية توصون بها أو دين وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت فلكل واحد منهما السدس فإن كانوا أكثر من ذلك فهم شركاء في الثلث من بعد وصية يوصى بها أو دين غير مضار وصية من الله والله عليم حليم) (١٢) .','336','436','0');
INSERT INTO [t22928] VALUES (449,'قال تعالى: (تلك حدود الله ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار خالدين فيها وذلك الفوز العظيم) (١٣ (ومن يعص الله ورسوله ويتعد حدوده يدخله نارا خالدا فيها وله عذاب مهين) (١٤) .','338','436','0');
INSERT INTO [t22928] VALUES (450,'قال تعالى: (واللاتي يأتين الفاحشة من نسائكم فاستشهدوا عليهن أربعة منكم فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا) (١٥) .','339','436','0');
INSERT INTO [t22928] VALUES (451,'قال تعالى: (واللذان يأتيانها منكم فآذوهما فإن تابا وأصلحا فأعرضوا عنهما إن الله كان توابا رحيما) (٦) .','339','436','0');
INSERT INTO [t22928] VALUES (452,'قال تعالى: (إنما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فأولئك يتوب الله عليهم وكان الله عليما حكيما) (١٧) .','340','436','0');
INSERT INTO [t22928] VALUES (453,'قال تعالى: (وليست التوبة للذين يعملون السيئات حتى إذا حضر أحدهم الموت قال إني تبت الآن ولا الذين يموتون وهم كفار أولئك أعتدنا لهم عذابا أليما) (١٨) .','341','436','0');
INSERT INTO [t22928] VALUES (454,'قال تعالى: (يا أيها الذين آمنوا لا يحل لكم أن ترثوا النساء كرها ولا تعضلوهن لتذهبوا ببعض ما آتيتموهن إلا أن يأتين بفاحشة مبينة وعاشروهن بالمعروف فإن كرهتموهن فعسى أن تكرهوا شيئا ويجعل الله فيه خيرا كثيرا) (١٩) .','341','436','0');
INSERT INTO [t22928] VALUES (455,'قال تعالى: (وإن أردتم استبدال زوج مكان زوج وآتيتم إحداهن قنطارا فلا تأخذوا منه شيئا أتأخذونه بهتانا وإثما مبينا) (٢٠) .','342','436','0');
INSERT INTO [t22928] VALUES (456,'قال تعالى: (وكيف تأخذونه وقد أفضى بعضكم إلى بعض وأخذن منكم ميثاقا غليظا) (٢١) .','343','436','0');
INSERT INTO [t22928] VALUES (457,'قال تعالى: (ولا تنكحوا ما نكح آباؤكم من النساء إلا ما قد سلف إنه كان فاحشة ومقتا وساء سبيلا) (٢٢) .','344','436','0');
INSERT INTO [t22928] VALUES (458,'قال تعالى: (حرمت عليكم أمهاتكم وبناتكم وأخواتكم وعماتكم وخالاتكم وبنات الأخ وبنات الأخت وأمهاتكم اللاتي أرضعنكم وأخواتكم من الرضاعة وأمهات نسائكم وربائبكم اللاتي في حجوركم من نسائكم اللاتي دخلتم بهن فإن لم تكونوا دخلتم بهن فلا جناح عليكم وحلائل أبنائكم الذين من أصلابكم وأن تجمعوا بين الأختين إلا ما قد سلف إن الله كان غفورا رحيما) (٢٣) .','345','436','0');
INSERT INTO [t22928] VALUES (459,'قال تعالى: (والمحصنات من النساء إلا ما ملكت أيمانكم كتاب الله عليكم وأحل لكم ما وراء ذلكم أن تبتغوا بأموالكم محصنين غير مسافحين فما استمتعتم به منهن فآتوهن أجورهن فريضة ولا جناح عليكم فيما تراضيتم به من بعد الفريضة إن الله كان عليما حكيما) (٢٤) .','346','436','0');
INSERT INTO [t22928] VALUES (460,'قال تعالى: (ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات والله أعلم بإيمانكم بعضكم من بعض فانكحوهن بإذن أهلهن وآتوهن أجورهن بالمعروف محصنات غير مسافحات ولا متخذات أخدان فإذا أحصن فإن أتين بفاحشة فعليهن نصف ما على المحصنات من العذاب ذلك لمن خشي العنت منكم وأن تصبروا خير لكم والله غفور رحيم) (٢٥) .','348','436','0');
INSERT INTO [t22928] VALUES (461,'قال تعالى: (يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم ويتوب عليكم والله عليم حكيم) (٢٦) .','351','436','0');
INSERT INTO [t22928] VALUES (462,'قال تعالى: (والله يريد أن يتوب عليكم ويريد الذين يتبعون الشهوات أن تميلوا ميلا عظيما) (٢٧) .','351','436','0');
INSERT INTO [t22928] VALUES (463,'قال تعالى: (يريد الله أن يخفف عنكم وخلق الإنسان ضعيفا) (٢٨) .','351','436','0');
INSERT INTO [t22928] VALUES (464,'قال تعالى: (يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم ولا تقتلوا أنفسكم إن الله كان بكم رحيما) (٢٩) .','351','436','0');
INSERT INTO [t22928] VALUES (465,'قال تعالى: (ومن يفعل ذلك عدوانا وظلما فسوف نصليه نارا وكان ذلك على الله يسيرا) (٣٠) .','352','436','0');
INSERT INTO [t22928] VALUES (466,'قال تعالى: (إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما) (٣١) .','352','436','0');
INSERT INTO [t22928] VALUES (467,'قال تعالى: (ولا تتمنوا ما فضل الله به بعضكم على بعض للرجال نصيب مما اكتسبوا وللنساء نصيب مما اكتسبن واسألوا الله من فضله إن الله كان بكل شيء عليما) (٣٢) .','353','436','0');
INSERT INTO [t22928] VALUES (468,'قال تعالى: (ولكل جعلنا موالي مما ترك الوالدان والأقربون والذين عقدت أيمانكم فآتوهم نصيبهم إن الله كان على كل شيء شهيدا) (٣٣) .','353','436','0');
INSERT INTO [t22928] VALUES (469,'قال تعالى: (الرجال قوامون على النساء بما فضل الله بعضهم على بعض وبما أنفقوا من أموالهم فالصالحات قانتات حافظات للغيب بما حفظ الله واللاتي تخافون نشوزهن فعظوهن واهجروهن في المضاجع واضربوهن فإن أطعنكم فلا تبغوا عليهن سبيلا إن الله كان عليا كبيرا) (٣٤) .','354','436','0');
INSERT INTO [t22928] VALUES (470,'قال تعالى: (وإن خفتم شقاق بينهما فابعثوا حكما من أهله وحكما من أهلها إن يريدا إصلاحا يوفق الله بينهما إن الله كان عليما خبيرا) (٣٥) .','356','436','0');
INSERT INTO [t22928] VALUES (471,'قال تعالى: (واعبدوا الله ولا تشركوا به شيئا وبالوالدين إحسانا وبذي القربى واليتامى والمساكين والجار ذي القربى والجار الجنب والصاحب بالجنب وابن السبيل وما ملكت أيمانكم إن الله لا يحب من كان مختالا فخورا) (٣٦) .','356','436','0');
INSERT INTO [t22928] VALUES (472,'قال تعالى: (الذين يبخلون ويأمرون الناس بالبخل ويكتمون ما آتاهم الله من فضله وأعتدنا للكافرين عذابا مهينا) (٣٧) .','357','436','0');
INSERT INTO [t22928] VALUES (473,'قال تعالى: (والذين ينفقون أموالهم رئاء الناس ولا يؤمنون بالله ولا باليوم الآخر ومن يكن الشيطان له قرينا فساء قرينا) (٣٨) .','357','436','0');
INSERT INTO [t22928] VALUES (474,'قال تعالى {وماذا عليهم لو آمنوا بالله واليوم الآخر وأنفقوا من ما رزقهم الله وكان الله بهم عليما}','358','436','0');
INSERT INTO [t22928] VALUES (475,'قال تعالى: (إن الله لا يظلم مثقال ذرة وإن تك حسنة يضاعفها ويؤت من لدنه أجرا عظيما) (٤٠) .','359','436','0');
INSERT INTO [t22928] VALUES (476,'قال تعالى: (فكيف إذا جئنا من كل أمة بشهيد وجئنا بك على هؤلاء شهيدا) (٤١) .','360','436','0');
INSERT INTO [t22928] VALUES (477,'قال تعالى: (يومئذ يود الذين كفروا وعصوا الرسول لو تسوى بهم الأرض ولا يكتمون الله حديثا) (٤٢) .','360','436','0');
INSERT INTO [t22928] VALUES (478,'قال تعالى: (يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم إن الله كان عفوا غفورا) (٤٣) .','361','436','0');
INSERT INTO [t22928] VALUES (479,'قال تعالى: (ألم تر إلى الذين أوتوا نصيبا من الكتاب يشترون الضلالة ويريدون أن تضلوا السبيل (٤٤) والله أعلم بأعدائكم وكفى بالله وليا وكفى بالله نصيرا (٤٥)) .','363','436','0');
INSERT INTO [t22928] VALUES (480,'قال تعالى: (من الذين هادوا يحرفون الكلم عن مواضعه ويقولون سمعنا وعصينا واسمع غير مسمع وراعنا ليا بألسنتهم وطعنا في الدين ولو أنهم قالوا سمعنا وأطعنا واسمع وانظرنا لكان خيرا لهم وأقوم ولكن لعنهم الله بكفرهم فلا يؤمنون إلا قليلا) (٤٦) .','363','436','0');
INSERT INTO [t22928] VALUES (481,'قال تعالى: (يا أيها الذين أوتوا الكتاب آمنوا بما نزلنا مصدقا لما معكم من قبل أن نطمس وجوها فنردها على أدبارها أو نلعنهم كما لعنا أصحاب السبت وكان أمر الله مفعولا) (٤٧) .','365','436','0');
INSERT INTO [t22928] VALUES (482,'قال تعالى: (إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى إثما عظيما) (٤٨) .','365','436','0');
INSERT INTO [t22928] VALUES (483,'قال تعالى: (ألم تر إلى الذين يزكون أنفسهم بل الله يزكي من يشاء ولا يظلمون فتيلا) (٤٩) .','365','436','0');
INSERT INTO [t22928] VALUES (484,'قال تعالى: (انظر كيف يفترون على الله الكذب وكفى به إثما مبينا) (٥٠) .','365','436','0');
INSERT INTO [t22928] VALUES (485,'قال تعالى: (ألم تر إلى الذين أوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت ويقولون للذين كفروا هؤلاء أهدى من الذين آمنوا سبيلا) (٥١) .','366','436','0');
INSERT INTO [t22928] VALUES (486,'قال تعالى: (أم لهم نصيب من الملك فإذا لا يؤتون الناس نقيرا) (٥٣) .','366','436','0');
INSERT INTO [t22928] VALUES (487,'قال تعالى: (فمنهم من آمن به ومنهم من صد عنه وكفى بجهنم سعيرا (٥٥) إن الذين كفروا بآياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب إن الله كان عزيزا حكيما) (٥٦) .','366','436','0');
INSERT INTO [t22928] VALUES (488,'قال تعالى: (والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا لهم فيها أزواج مطهرة وندخلهم ظلا ظليلا) (٥٧) .','367','436','0');
INSERT INTO [t22928] VALUES (489,'قال تعالى: (إن الله يأمركم أن تؤدوا الأمانات إلى أهلها وإذا حكمتم بين الناس أن تحكموا بالعدل إن الله نعما يعظكم به إن الله كان سميعا بصيرا) (٥٨) .','367','436','0');
INSERT INTO [t22928] VALUES (490,'قال تعالى: (يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر ذلك خير وأحسن تأويلا) (٥٩) .','368','436','0');
INSERT INTO [t22928] VALUES (491,'قال تعالى: (ألم تر إلى الذين يزعمون أنهم آمنوا بما أنزل إليك وما أنزل من قبلك يريدون أن يتحاكموا إلى الطاغوت وقد أمروا أن يكفروا به ويريد الشيطان أن يضلهم ضلالا بعيدا) (٦٠) .','368','436','0');
INSERT INTO [t22928] VALUES (492,'قال تعالى: (وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول رأيت المنافقين يصدون عنك صدودا) (٦١) .','369','436','0');
INSERT INTO [t22928] VALUES (493,'قال تعالى: (فكيف إذا أصابتهم مصيبة بما قدمت أيديهم ثم جاءوك يحلفون بالله إن أردنا إلا إحسانا وتوفيقا) (٦٢) .','369','436','0');
INSERT INTO [t22928] VALUES (494,'قال تعالى: (أولئك الذين يعلم الله ما في قلوبهم فأعرض عنهم وعظهم وقل لهم في أنفسهم قولا بليغا) (٦٣) .','369','436','0');
INSERT INTO [t22928] VALUES (495,'قال تعالى: (وما أرسلنا من رسول إلا ليطاع بإذن الله ولو أنهم إذ ظلموا أنفسهم جاءوك فاستغفروا الله واستغفر لهم الرسول لوجدوا الله توابا رحيما) (٦٤) .','370','436','0');
INSERT INTO [t22928] VALUES (496,'قال تعالى: (فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما) (٦٥) .','370','436','0');
INSERT INTO [t22928] VALUES (497,'قال تعالى: (ولو أنا كتبنا عليهم أن اقتلوا أنفسكم أو اخرجوا من دياركم ما فعلوه إلا قليل منهم ولو أنهم فعلوا ما يوعظون به لكان خيرا لهم وأشد تثبيتا (٦٦) وإذا لآتيناهم من لدنا أجرا عظيما (٦٧) ولهديناهم صراطا مستقيما) (٦٨) .','371','436','0');
INSERT INTO [t22928] VALUES (498,'قال تعالى: (ومن يطع الله والرسول فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقا) (٦٩) .','372','436','0');
INSERT INTO [t22928] VALUES (499,'قال تعالى: (ذلك الفضل من الله وكفى بالله عليما) (٧٠) .','372','436','0');
INSERT INTO [t22928] VALUES (500,'قال تعالى: (يا أيها الذين آمنوا خذوا حذركم فانفروا ثبات أو انفروا جميعا) (٧١) .','372','436','0');
INSERT INTO [t22928] VALUES (501,'قال تعالى: (وإن منكم لمن ليبطئن فإن أصابتكم مصيبة قال قد أنعم الله علي إذ لم أكن معهم شهيدا) (٧٢) .','372','436','0');
INSERT INTO [t22928] VALUES (502,'قال تعالى: (ولئن أصابكم فضل من الله ليقولن كأن لم تكن بينكم وبينه مودة يا ليتني كنت معهم فأفوز فوزا عظيما) (٧٣) .','373','436','0');
INSERT INTO [t22928] VALUES (503,'قال تعالى: (فليقاتل في سبيل الله الذين يشرون الحياة الدنيا بالآخرة ومن يقاتل في سبيل الله فيقتل أو يغلب فسوف نؤتيه أجرا عظيما) (٧٤) .','373','436','0');
INSERT INTO [t22928] VALUES (504,'قال تعالى: (وما لكم لا تقاتلون في سبيل الله والمستضعفين من الرجال والنساء والولدان الذين يقولون ربنا أخرجنا من هذه القرية الظالم أهلها واجعل لنا من لدنك وليا واجعل لنا من لدنك نصيرا) (٧٥) .','373','436','0');
INSERT INTO [t22928] VALUES (505,'قال تعالى: (ألم تر إلى الذين قيل لهم كفوا أيديكم وأقيموا الصلاة وآتوا الزكاة فلما كتب عليهم القتال إذا فريق منهم يخشون الناس كخشية الله أو أشد خشية وقالوا ربنا لم كتبت علينا القتال لولا أخرتنا إلى أجل قريب قل متاع الدنيا قليل والآخرة خير لمن اتقى ولا تظلمون فتيلا) (٧٧) .','374','436','0');
INSERT INTO [t22928] VALUES (506,'قال تعالى: (أينما تكونوا يدرككم الموت ولو كنتم في بروج مشيدة وإن تصبهم حسنة يقولوا هذه من عند الله وإن تصبهم سيئة يقولوا هذه من عندك قل كل من عند الله فمال هؤلاء القوم لا يكادون يفقهون حديثا) (٧٨) .','375','436','0');
INSERT INTO [t22928] VALUES (507,'قال تعالى: (ما أصابك من حسنة فمن الله وما أصابك من سيئة فمن نفسك وأرسلناك للناس رسولا وكفى بالله شهيدا) (٧٩) .','375','436','0');
INSERT INTO [t22928] VALUES (508,'قال تعالى: (من يطع الرسول فقد أطاع الله ومن تولى فما أرسلناك عليهم حفيظا) (٨٠) .','376','436','0');
INSERT INTO [t22928] VALUES (509,'قال تعالى: (ويقولون طاعة فإذا برزوا من عندك بيت طائفة منهم غير الذي تقول والله يكتب ما يبيتون فأعرض عنهم وتوكل على الله وكفى بالله وكيلا) (٨١) .','376','436','0');
INSERT INTO [t22928] VALUES (510,'قال تعالى: (وإذا جاءهم أمر من الأمن أو الخوف أذاعوا به ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم ولولا فضل الله عليكم ورحمته لاتبعتم الشيطان إلا قليلا) (٨٣) .','377','436','0');
INSERT INTO [t22928] VALUES (511,'قال تعالى: (فقاتل في سبيل الله لا تكلف إلا نفسك وحرض المؤمنين عسى الله أن يكف بأس الذين كفروا والله أشد بأسا وأشد تنكيلا) (٨٤) .','377','436','0');
INSERT INTO [t22928] VALUES (512,'قال تعالى: (من يشفع شفاعة حسنة يكن له نصيب منها ومن يشفع شفاعة سيئة يكن له كفل منها وكان الله على كل شيء مقيتا) (٨٥) .','378','436','0');
INSERT INTO [t22928] VALUES (513,'قال تعالى: (وإذا حييتم بتحية فحيوا بأحسن منها أو ردوها إن الله كان على كل شيء حسيبا) (٨٦) .','378','436','0');
INSERT INTO [t22928] VALUES (514,'قال تعالى: (الله لا إله إلا هو ليجمعنكم إلى يوم القيامة لا ريب فيه ومن أصدق من الله حديثا) (٨٧) .','378','436','0');
INSERT INTO [t22928] VALUES (515,'قال تعالى: (فما لكم في المنافقين فئتين والله أركسهم بما كسبوا أتريدون أن تهدوا من أضل الله ومن يضلل الله فلن تجد له سبيلا) (٨٨) .','379','436','0');
INSERT INTO [t22928] VALUES (516,'قال تعالى: (ودوا لو تكفرون كما كفروا فتكونون سواء فلا تتخذوا منهم أولياء حتى يهاجروا في سبيل الله فإن تولوا فخذوهم واقتلوهم حيث وجدتموهم ولا تتخذوا منهم وليا ولا نصيرا) (٨٩) .','379','436','0');
INSERT INTO [t22928] VALUES (517,'قال تعالى: (إلا الذين يصلون إلى قوم بينكم وبينهم ميثاق أو جاءوكم حصرت صدورهم أن يقاتلوكم أو يقاتلوا قومهم ولو شاء الله لسلطهم عليكم فلقاتلوكم فإن اعتزلوكم فلم يقاتلوكم وألقوا إليكم السلم فما جعل الله لكم عليهم سبيلا) (٩٠) .','379','436','0');
INSERT INTO [t22928] VALUES (518,'قال تعالى: (ستجدون آخرين يريدون أن يأمنوكم ويأمنوا قومهم كلما ردوا إلى الفتنة أركسوا فيها.) (٩١) .','380','436','0');
INSERT INTO [t22928] VALUES (519,'قال تعالى: (وما كان لمؤمن أن يقتل مؤمنا إلا خطأ ومن قتل مؤمنا خطأ فتحرير رقبة مؤمنة ودية مسلمة إلى أهله إلا أن يصدقوا فإن كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة وإن كان من قوم بينكم وبينهم ميثاق فدية مسلمة إلى أهله وتحرير رقبة مؤمنة فمن لم يجد فصيام شهرين متتابعين توبة من الله وكان الله عليما حكيما) (٩٢) .','381','436','0');
INSERT INTO [t22928] VALUES (520,'قال تعالى: (ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما) (٩٣) .','382','436','0');
INSERT INTO [t22928] VALUES (521,'قال تعالى: (يا أيها الذين آمنوا إذا ضربتم في سبيل الله فتبينوا ولا تقولوا لمن ألقى إليكم السلام لست مؤمنا تبتغون عرض الحياة الدنيا فعند الله مغانم كثيرة كذلك كنتم من قبل فمن الله عليكم فتبينوا إن الله كان بما تعملون خبيرا) (٩٤) .','382','436','0');
INSERT INTO [t22928] VALUES (522,'قال تعالى: (لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين درجة وكلا وعد الله الحسنى وفضل الله المجاهدين على القاعدين أجرا عظيما) (٩٥) .','383','436','0');
INSERT INTO [t22928] VALUES (523,'قال تعالى: (درجات منه ومغفرة ورحمة وكان الله غفورا رحيما) (٩٦) .','385','436','0');
INSERT INTO [t22928] VALUES (524,'قال تعالى: (إن الذين توفاهم الملائكة ظالمي أنفسهم قالوا فيم كنتم قالوا كنا مستضعفين في الأرض قالوا ألم تكن أرض الله واسعة فتهاجروا فيها فأولئك مأواهم جهنم وساءت مصيرا) (٩٧) .','385','436','0');
INSERT INTO [t22928] VALUES (525,'قال تعالى: (إلا المستضعفين من الرجال والنساء والولدان لا يستطيعون حيلة ولا يهتدون سبيلا) (٩٨) .','386','436','0');
INSERT INTO [t22928] VALUES (526,'قال تعالى: (ومن يهاجر في سبيل الله يجد في الأرض مراغما كثيرا وسعة ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت فقد وقع أجره على الله وكان الله غفورا رحيما) (١٠٠) .','386','436','0');
INSERT INTO [t22928] VALUES (527,'قال تعالى: (وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا إن الكافرين كانوا لكم عدوا مبينا) (١٠١) .','387','436','0');
INSERT INTO [t22928] VALUES (528,'قال تعالى: (وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك وليأخذوا أسلحتهم فإذا سجدوا فليكونوا من ورائكم ولتأت طائفة أخرى لم يصلوا فليصلوا معك وليأخذوا حذرهم وأسلحتهم ود الذين كفروا لو تغفلون عن أسلحتكم وأمتعتكم فيميلون عليكم ميلة واحدة ولا جناح عليكم إن كان بكم أذى من مطر أو كنتم مرضى أن تضعوا أسلحتكم وخذوا حذركم إن الله أعد للكافرين عذابا مهينا) (١٠٢) .','387','436','0');
INSERT INTO [t22928] VALUES (529,'قال تعالى: (فإذا قضيتم الصلاة فاذكروا الله قياما وقعودا وعلى جنوبكم فإذا اطمأننتم فأقيموا الصلاة إن الصلاة كانت على المؤمنين كتابا موقوتا) (١٠٣) .','387','436','0');
INSERT INTO [t22928] VALUES (530,'قال تعالى: (ولا تهنوا في ابتغاء القوم إن تكونوا تألمون فإنهم يألمون كما تألمون وترجون من الله ما لا يرجون وكان الله عليما حكيما) (١٠٤) .','388','436','0');
INSERT INTO [t22928] VALUES (531,'قال تعالى: (إنا أنزلنا إليك الكتاب بالحق لتحكم بين الناس بما أراك الله ولا تكن للخائنين خصيما) (١٠٥) .','388','436','0');
INSERT INTO [t22928] VALUES (532,'قال تعالى: (يستخفون من الناس ولا يستخفون من الله وهو معهم إذ يبيتون ما لا يرضى من القول وكان الله بما يعملون محيطا) (١٠٨) .','388','436','0');
INSERT INTO [t22928] VALUES (533,'قال تعالى: (ها أنتم هؤلاء جادلتم عنهم في الحياة الدنيا فمن يجادل الله عنهم يوم القيامة أم من يكون عليهم وكيلا) (١٠٩) .','389','436','0');
INSERT INTO [t22928] VALUES (534,'قال تعالى: (ومن يعمل سوءا أو يظلم نفسه ثم يستغفر الله يجد الله غفورا رحيما) (١١٠) .','389','436','0');
INSERT INTO [t22928] VALUES (535,'قال تعالى: (ومن يكسب خطيئة أو إثما ثم يرم به بريئا فقد احتمل بهتانا وإثما مبينا) (١١٢) .','389','436','0');
INSERT INTO [t22928] VALUES (536,'قال تعالى: (ولولا فضل الله عليك ورحمته لهمت طائفة منهم أن يضلوك وما يضلون إلا أنفسهم وما يضرونك من شيء وأنزل الله عليك الكتاب والحكمة وعلمك ما لم تكن تعلم وكان فضل الله عليك عظيما) (١١٣) .','389','436','0');
INSERT INTO [t22928] VALUES (537,'قال تعالى: (لا خير في كثير من نجواهم إلا من أمر بصدقة أو معروف أو إصلاح بين الناس ومن يفعل ذلك ابتغاء مرضاة الله فسوف نؤتيه أجرا عظيما) (١١٤) .','390','436','0');
INSERT INTO [t22928] VALUES (538,'قال تعالى: (ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم وساءت مصيرا) (١١٥) .','391','436','0');
INSERT INTO [t22928] VALUES (539,'قال تعالى: (إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد ضل ضلالا بعيدا) (١١٦) .','391','436','0');
INSERT INTO [t22928] VALUES (540,'قال تعالى: (إن يدعون من دونه إلا إناثا وإن يدعون إلا شيطانا مريدا) (١١٧) .','391','436','0');
INSERT INTO [t22928] VALUES (541,'قال تعالى: (لعنه الله وقال لأتخذن من عبادك نصيبا مفروضا) (١١٨) .','392','436','0');
INSERT INTO [t22928] VALUES (542,'قال تعالى: (ولأضلنهم ولأمنينهم ولآمرنهم فليبتكن آذان الأنعام ولآمرنهم فليغيرن خلق الله ومن يتخذ الشيطان وليا من دون الله فقد خسر خسرانا مبينا) (١١٩) .','392','436','0');
INSERT INTO [t22928] VALUES (543,'قال تعالى: (يعدهم ويمنيهم وما يعدهم الشيطان إلا غرورا) (١٢٠) .','392','436','0');
INSERT INTO [t22928] VALUES (544,'قال تعالى: (أولئك مأواهم جهنم ولا يجدون عنها محيصا) (١٢١) .','392','436','0');
INSERT INTO [t22928] VALUES (545,'قال تعالى: (والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا وعد الله حقا ومن أصدق من الله قيلا) (١٢٢) .','392','436','0');
INSERT INTO [t22928] VALUES (546,'قال تعالى: (ليس بأمانيكم ولا أماني أهل الكتاب من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا) (١٢٣) .','393','436','0');
INSERT INTO [t22928] VALUES (547,'قال تعالى: (ومن يعمل من الصالحات من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة ولا يظلمون نقيرا) (١٢٤) .','393','436','0');
INSERT INTO [t22928] VALUES (548,'قال تعالى: (ومن أحسن دينا ممن أسلم وجهه لله وهو محسن واتبع ملة إبراهيم حنيفا واتخذ الله إبراهيم خليلا) (١٢٥) .','393','436','0');
INSERT INTO [t22928] VALUES (549,'قال تعالى: (ويستفتونك في النساء قل الله يفتيكم فيهن وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تؤتونهن ما كتب لهن وترغبون أن تنكحوهن والمستضعفين من الولدان وأن تقوموا لليتامى بالقسط وما تفعلوا من خير فإن الله كان به عليما) (١٢٧) .','394','436','0');
INSERT INTO [t22928] VALUES (550,'قال تعالى: (وإن امرأة خافت من بعلها نشوزا أو إعراضا فلا جناح عليهما أن يصلحا بينهما صلحا والصلح خير وأحضرت الأنفس الشح وإن تحسنوا وتتقوا فإن الله كان بما تعملون خبيرا) (١٢٨) .','395','436','0');
INSERT INTO [t22928] VALUES (551,'قال تعالى: (ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم فلا تميلوا كل الميل فتذروها كالمعلقة وإن تصلحوا وتتقوا فإن الله كان غفورا رحيما) (١٢٩) .','397','436','0');
INSERT INTO [t22928] VALUES (552,'قال تعالى: (ولله ما في السماوات وما في الأرض ولقد وصينا الذين أوتوا الكتاب من قبلكم وإياكم أن اتقوا الله وإن تكفروا فإن لله ما في السماوات وما في الأرض وكان الله غنيا حميدا) (١٣١) .','397','436','0');
INSERT INTO [t22928] VALUES (553,'قال تعالى: (يا أيها الذين آمنوا كونوا قوامين بالقسط شهداء لله ولو على أنفسكم أو الوالدين والأقربين إن يكن غنيا أو فقيرا فالله أولى بهما فلا تتبعوا الهوى أن تعدلوا وإن تلووا أو تعرضوا فإن الله كان بما تعملون خبيرا) (١٣٥) .','397','436','0');
INSERT INTO [t22928] VALUES (554,'قال تعالى: (إن الذين آمنوا ثم كفروا ثم آمنوا ثم كفروا ثم ازدادوا كفرا لم يكن الله ليغفر لهم ولا ليهديهم سبيلا) (١٣٧) .','399','436','0');
INSERT INTO [t22928] VALUES (555,'قال تعالى: (الذين يتخذون الكافرين أولياء من دون المؤمنين أيبتغون عندهم العزة فإن العزة لله جميعا) (١٣٩) .','399','436','0');
INSERT INTO [t22928] VALUES (556,'قال تعالى: (وقد نزل عليكم في الكتاب أن إذا سمعتم آيات الله يكفر بها ويستهزأ بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره إنكم إذا مثلهم إن الله جامع المنافقين والكافرين في جهنم جميعا) (١٤٠) .','399','436','0');
INSERT INTO [t22928] VALUES (557,'قال تعالى: (الذين يتربصون بكم فإن كان لكم فتح من الله قالوا ألم نكن معكم وإن كان للكافرين نصيب قالوا ألم نستحوذ عليكم ونمنعكم من المؤمنين فالله يحكم بينكم يوم القيامة ولن يجعل الله للكافرين على المؤمنين سبيلا) (١٤١) .','400','436','0');
INSERT INTO [t22928] VALUES (558,'قال تعالى: (إن المنافقين يخادعون الله وهو خادعهم وإذا قاموا إلى الصلاة قاموا كسالى يراءون الناس ولا يذكرون الله إلا قليلا) (١٤٢) .','401','436','0');
INSERT INTO [t22928] VALUES (559,'قال تعالى: (مذبذبين بين ذلك لا إلى هؤلاء ولا إلى هؤلاء ومن يضلل الله فلن تجد له سبيلا) (١٤٣) .','401','436','0');
INSERT INTO [t22928] VALUES (560,'قال تعالى: (إن المنافقين في الدرك الأسفل من النار ولن تجد لهم نصيرا) (١٤٥) .','402','436','0');
INSERT INTO [t22928] VALUES (561,'قال تعالى: (إلا الذين تابوا وأصلحوا واعتصموا بالله وأخلصوا دينهم لله فأولئك مع المؤمنين وسوف يؤت الله المؤمنين أجرا عظيما) (١٤٦) .','402','436','0');
INSERT INTO [t22928] VALUES (562,'قال تعالى: (ما يفعل الله بعذابكم إن شكرتم وآمنتم وكان الله شاكرا عليما) (١٤٧) .','402','436','0');
INSERT INTO [t22928] VALUES (563,'قال تعالى: (لا يحب الله الجهر بالسوء من القول إلا من ظلم وكان الله سميعا عليما) (١٤٨) .','403','436','0');
INSERT INTO [t22928] VALUES (564,'قال تعالى: (إن الذين يكفرون بالله ورسله ويريدون أن يفرقوا بين الله ورسله ويقولون نؤمن ببعض ونكفر ببعض ويريدون أن يتخذوا بين ذلك سبيلا) (١٥٠) .','403','436','0');
INSERT INTO [t22928] VALUES (565,'قال تعالى: (أولئك هم الكافرون حقا وأعتدنا للكافرين عذابا مهينا) (١٥١) .','403','436','0');
INSERT INTO [t22928] VALUES (566,'قال تعالى: (يسألك أهل الكتاب أن تنزل عليهم كتابا من السماء فقد سألوا موسى أكبر من ذلك فقالوا أرنا الله جهرة فأخذتهم الصاعقة بظلمهم ثم اتخذوا العجل من بعد ما جاءتهم البينات فعفونا عن ذلك وآتينا موسى سلطانا مبينا) (١٣٥) .','404','436','0');
INSERT INTO [t22928] VALUES (567,'قال تعالى: (ورفعنا فوقهم الطور بميثاقهم وقلنا لهم ادخلوا الباب سجدا وقلنا لهم لا تعدوا في السبت وأخذنا منهم ميثاقا غليظا) (١٥٤) .','404','436','0');
INSERT INTO [t22928] VALUES (568,'قال تعالى: (فبما نقضهم ميثاقهم وكفرهم بآيات الله وقتلهم الأنبياء بغير حق وقولهم قلوبنا غلف بل طبع الله عليها بكفرهم فلا يؤمنون إلا قليلا) (١٥٥) .','404','436','0');
INSERT INTO [t22928] VALUES (569,'قال تعالى: (وبكفرهم وقولهم على مريم بهتانا عظيما) (١٥٦) .','405','436','0');
INSERT INTO [t22928] VALUES (570,'قال تعالى: (وقولهم إنا قتلنا المسيح عيسى ابن مريم رسول الله وما قتلوه وما صلبوه ولكن شبه لهم وإن الذين اختلفوا فيه لفي شك منه ما لهم به من علم إلا اتباع الظن وما قتلوه يقينا) (١٥٧) (بل رفعه الله إليه وكان الله عزيزا حكيما) (١٥٨) .','405','436','0');
INSERT INTO [t22928] VALUES (571,'قال تعالى: (وإن من أهل الكتاب إلا ليؤمنن به قبل موته ويوم القيامة يكون عليهم شهيدا) (١٥٩) .','407','436','0');
INSERT INTO [t22928] VALUES (572,'قال تعالى: (فبظلم من الذين هادوا حرمنا عليهم طيبات أحلت لهم وبصدهم عن سبيل الله كثيرا) (١٦٠) .','408','436','0');
INSERT INTO [t22928] VALUES (573,'قال تعالى: (وأخذهم الربا وقد نهوا عنه وأكلهم أموال الناس بالباطل وأعتدنا للكافرين منهم عذابا أليما) (١٦١) .','408','436','0');
INSERT INTO [t22928] VALUES (574,'قال تعالى: (لكن الراسخون في العلم منهم والمؤمنون يؤمنون بما أنزل إليك وما أنزل من قبلك والمقيمين الصلاة والمؤتون الزكاة والمؤمنون بالله واليوم الآخر أولئك سنؤتيهم أجرا عظيما) (١٦٢) .','408','436','0');
INSERT INTO [t22928] VALUES (575,'قال تعالى: (إنا أوحينا إليك كما أوحينا إلى نوح والنبيين من بعده وأوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وعيسى وأيوب ويونس وهارون وسليمان وآتينا داود زبورا) (١٦٣) .','409','436','0');
INSERT INTO [t22928] VALUES (576,'قال تعالى: (ورسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك وكلم الله موسى تكليما) (١٦٤) .','410','436','0');
INSERT INTO [t22928] VALUES (577,'قال تعالى: (رسلا مبشرين ومنذرين لئلا يكون للناس على الله حجة بعد الرسل وكان الله عزيزا حكيما) (١٦٥) .','410','436','0');
INSERT INTO [t22928] VALUES (578,'قال تعالى: (لكن الله يشهد بما أنزل إليك أنزله بعلمه والملائكة يشهدون وكفى بالله شهيدا) (١٦٦) .','411','436','0');
INSERT INTO [t22928] VALUES (579,'قال تعالى: (إن الذين كفروا وظلموا لم يكن الله ليغفر لهم ولا ليهديهم طريقا) (١٦٨) .','411','436','0');
INSERT INTO [t22928] VALUES (580,'قال تعالى: (إلا طريق جهنم خالدين فيها أبدا وكان ذلك على الله يسيرا) (١٦٩) .','412','436','0');
INSERT INTO [t22928] VALUES (581,'قال تعالى: (يا أيها الناس قد جاءكم الرسول بالحق من ربكم فآمنوا خيرا لكم وإن تكفروا فإن لله ما في السماوات والأرض وكان الله عليما حكيما) (١٧٠) .','412','436','0');
INSERT INTO [t22928] VALUES (582,'قال تعالى: (يا أهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله إلا الحق إنما المسيح عيسى ابن مريم رسول الله وكلمته ألقاها إلى مريم وروح منه فآمنوا بالله ورسله ولا تقولوا ثلاثة انتهوا خيرا لكم إنما الله إله واحد سبحانه أن يكون له ولد له ما في السماوات وما في الأرض وكفى بالله وكيلا (١٧١) لن يستنكف المسيح أن يكون عبدا لله ولا الملائكة المقربون ومن يستنكف عن عبادته ويستكبر فسيحشرهم إليه جميعا) (١٧٢) .','412','436','0');
INSERT INTO [t22928] VALUES (583,'قال تعالى {يا أيها الناس قد جاءكم برهان من ربكم وأنزلنا إليكم نورا مبينا}','414','436','0');
INSERT INTO [t22928] VALUES (584,'قال تعالى: (فأما الذين آمنوا بالله واعتصموا به فسيدخلهم في رحمة منه وفضل ويهديهم إليه صراطا مستقيما) (١٧٥) .','414','436','0');
INSERT INTO [t22928] VALUES (585,'قال تعالى: (يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد فإن كانتا اثنتين فلهما الثلثان مما ترك وإن كانوا إخوة رجالا ونساء فللذكر مثل حظ الأنثيين يبين الله لكم أن تضلوا والله بكل شيء عليم) (١٧٦) .','414','436','0');
INSERT INTO [t22928] VALUES (586,'المائدة','416','0','0');
INSERT INTO [t22928] VALUES (587,'قال تعالى: (يا أيها الذين آمنوا أوفوا بالعقود أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم غير محلي الصيد وأنتم حرم إن الله يحكم ما يريد) (١) .','416','586','0');
INSERT INTO [t22928] VALUES (588,'قال تعالى: (يا أيها الذين آمنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا آمين البيت الحرام يبتغون فضلا من ربهم ورضوانا وإذا حللتم فاصطادوا ولا يجرمنكم شنآن قوم أن صدوكم عن المسجد الحرام أن تعتدوا وتعاونوا على البر والتقوى ولا تعاونوا على الإثم والعدوان واتقوا الله إن الله شديد العقاب) (٢) .','416','586','0');
INSERT INTO [t22928] VALUES (589,'قال تعالى: (حرمت عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما أكل السبع إلا ما ذكيتم وما ذبح على النصب وأن تستقسموا بالأزلام ذلكم فسق اليوم يئس الذين كفروا من دينكم فلا تخشوهم واخشون اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا فمن اضطر في مخمصة غير متجانف لإثم فإن الله غفور رحيم) (٣) .','418','586','0');
INSERT INTO [t22928] VALUES (590,'قال تعالى: (يسألونك ماذا أحل لهم قل أحل لكم الطيبات وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله فكلوا مما أمسكن عليكم واذكروا اسم الله عليه واتقوا الله إن الله سريع الحساب) (٤) .','420','586','0');
INSERT INTO [t22928] VALUES (591,'قال تعالى: (اليوم أحل لكم الطيبات وطعام الذين أوتوا الكتاب حل لكم وطعامكم حل لهم والمحصنات من المؤمنات والمحصنات من الذين أوتوا الكتاب من قبلكم إذا آتيتموهن أجورهن محصنين غير مسافحين ولا متخذي أخدان ومن يكفر بالإيمان فقد حبط عمله وهو في الآخرة من الخاسرين) (٥) .','421','586','0');
INSERT INTO [t22928] VALUES (592,'قال تعالى: (يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين وإن كنتم جنبا فاطهروا وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم منه ما يريد الله ليجعل عليكم من حرج ولكن يريد ليطهركم وليتم نعمته عليكم لعلكم تشكرون) (٦) .','422','586','0');
INSERT INTO [t22928] VALUES (593,'قال تعالى: (واذكروا نعمة الله عليكم وميثاقه الذي واثقكم به إذ قلتم سمعنا وأطعنا واتقوا الله إن الله عليم بذات الصدور) (٧) .','426','586','0');
INSERT INTO [t22928] VALUES (594,'قال تعالى: (وعد الله الذين آمنوا وعملوا الصالحات لهم مغفرة وأجر عظيم) (٩) .','426','586','0');
INSERT INTO [t22928] VALUES (595,'قال تعالى: (يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ هم قوم أن يبسطوا إليكم أيديهم فكف أيديهم عنكم واتقوا الله وعلى الله فليتوكل المؤمنون) (١١) .','426','586','0');
INSERT INTO [t22928] VALUES (596,'قال تعالى: (ولقد أخذ الله ميثاق بني إسرائيل وبعثنا منهم اثني عشر نقيبا وقال الله إني معكم لئن أقمتم الصلاة وآتيتم الزكاة وآمنتم برسلي وعزرتموهم وأقرضتم الله قرضا حسنا لأكفرن عنكم سيئاتكم ولأدخلنكم جنات تجري من تحتها الأنهار فمن كفر بعد ذلك منكم فقد ضل سواء السبيل) (١٢) .','427','586','0');
INSERT INTO [t22928] VALUES (597,'قال تعالى: (فبما نقضهم ميثاقهم لعناهم وجعلنا قلوبهم قاسية يحرفون الكلم عن مواضعه ونسوا حظا مما ذكروا به ولا تزال تطلع على خائنة منهم إلا قليلا منهم فاعف عنهم واصفح إن الله يحب المحسنين) (١٣) .','427','586','0');
INSERT INTO [t22928] VALUES (598,'قال تعالى: (ومن الذين قالوا إنا نصارى أخذنا ميثاقهم فنسوا حظا مما ذكروا به فأغرينا بينهم العداوة والبغضاء إلى يوم القيامة وسوف ينبئهم الله بما كانوا يصنعون) (١٤) .','428','586','0');
INSERT INTO [t22928] VALUES (599,'قال تعالى: (يا أهل الكتاب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتاب ويعفو عن كثير قد جاءكم من الله نور وكتاب مبين) (١٥) .','429','586','0');
INSERT INTO [t22928] VALUES (600,'قال تعالى: (يهدي به الله من اتبع رضوانه سبل السلام ويخرجهم من الظلمات إلى النور بإذنه ويهديهم إلى صراط مستقيم) (١٦) .','429','586','0');
INSERT INTO [t22928] VALUES (601,'قال تعالى: (لقد كفر الذين قالوا إن الله هو المسيح ابن مريم قل فمن يملك من الله شيئا إن أراد أن يهلك المسيح ابن مريم وأمه ومن في الأرض جميعا ولله ملك السماوات والأرض وما بينهما يخلق ما يشاء والله على كل شيء قدير) (١٧) .','430','586','0');
INSERT INTO [t22928] VALUES (602,'قال تعالى: (وقالت اليهود والنصارى نحن أبناء الله وأحباؤه قل فلم يعذبكم بذنوبكم بل أنتم بشر ممن خلق يغفر لمن يشاء ويعذب من يشاء ولله ملك السماوات والأرض وما بينهما وإليه المصير) (١٨) .','430','586','0');
INSERT INTO [t22928] VALUES (603,'قال تعالى: (يا أهل الكتاب قد جاءكم رسولنا يبين لكم على فترة من الرسل أن تقولوا ما جاءنا من بشير ولا نذير فقد جاءكم بشير ونذير والله على كل شيء قدير) (١٩) .','430','586','0');
INSERT INTO [t22928] VALUES (604,'قال تعالى: (وإذ قال موسى لقومه يا قوم اذكروا نعمة الله عليكم إذ جعل فيكم أنبياء وجعلكم ملوكا وآتاكم ما لم يؤت أحدا من العالمين) (٢٠) .','431','586','0');
INSERT INTO [t22928] VALUES (605,'قال تعالى: (يا قوم ادخلوا الأرض المقدسة التي كتب الله لكم ولا ترتدوا على أدباركم فتنقلبوا خاسرين) (٢١) .','431','586','0');
INSERT INTO [t22928] VALUES (606,'قال تعالى: (قالوا يا موسى إن فيها قوما جبارين وإنا لن ندخلها حتى يخرجوا منها فإن يخرجوا منها فإنا داخلون) (٢٢) .','431','586','0');
INSERT INTO [t22928] VALUES (607,'قال تعالى: (قال رجلان من الذين يخافون أنعم الله عليهما ادخلوا عليهم الباب فإذا دخلتموه فإنكم غالبون وعلى الله فتوكلوا إن كنتم مؤمنين) (٢٣) .','431','586','0');
INSERT INTO [t22928] VALUES (608,'قال تعالى: (قالوا يا موسى إنا لن ندخلها أبدا ما داموا فيها فاذهب أنت وربك فقاتلا إنا هاهنا قاعدون) (٢٤) .','432','586','0');
INSERT INTO [t22928] VALUES (609,'قال تعالى: (قال رب إني لا أملك إلا نفسي وأخي فافرق بيننا وبين القوم الفاسقين) (٢٥) .','432','586','0');
INSERT INTO [t22928] VALUES (610,'قال تعالى: (قال فإنها محرمة عليهم أربعين سنة يتيهون في الأرض فلا تأس على القوم الفاسقين) (٢٦) .','432','586','0');
INSERT INTO [t22928] VALUES (611,'قال تعالى: (واتل عليهم نبأ ابني آدم بالحق إذ قربا قربانا فتقبل من أحدهما ولم يتقبل من الآخر قال لأقتلنك قال إنما يتقبل الله من المتقين) (٢٧) .','433','586','0');
INSERT INTO [t22928] VALUES (612,'قال تعالى: (إني أريد أن تبوء بإثمي وإثمك فتكون من أصحاب النار وذلك جزاء الظالمين) (٢٩) .','433','586','0');
INSERT INTO [t22928] VALUES (613,'قال تعالى: (فطوعت له نفسه قتل أخيه فقتله فأصبح من الخاسرين) (٣٠) .','433','586','0');
INSERT INTO [t22928] VALUES (614,'قال تعالى: (فبعث الله غرابا يبحث في الأرض ليريه كيف يواري سوأة أخيه قال يا ويلتا أعجزت أن أكون مثل هذا الغراب فأواري سوأة أخي فأصبح من النادمين) (٣١) .','434','586','0');
INSERT INTO [t22928] VALUES (615,'قال تعالى: (من أجل ذلك كتبنا على بني إسرائيل أنه من قتل نفسا بغير نفس أو فساد في الأرض فكأنما قتل الناس جميعا ومن أحياها فكأنما أحيا الناس جميعا ولقد جاءتهم رسلنا بالبينات ثم إن كثيرا منهم بعد ذلك في الأرض لمسرفون) (٣٢) .','434','586','0');
INSERT INTO [t22928] VALUES (616,'قال تعالى: (إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فسادا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض ذلك لهم خزي في الدنيا ولهم في الآخرة عذاب عظيم) (٣٣) .','435','586','0');
INSERT INTO [t22928] VALUES (617,'قال تعالى: (إلا الذين تابوا من قبل أن تقدروا عليهم فاعلموا أن الله غفور رحيم) (٣٤) .','435','586','0');
INSERT INTO [t22928] VALUES (618,'قال تعالى: (يا أيها الذين آمنوا اتقوا الله وابتغوا إليه الوسيلة وجاهدوا في سبيله لعلكم تفلحون) (٣٥) .','436','586','0');
INSERT INTO [t22928] VALUES (619,'قال تعالى: (إن الذين كفروا لو أن لهم ما في الأرض جميعا ومثله معه ليفتدوا به من عذاب يوم القيامة ما تقبل منهم ولهم عذاب أليم) (٣٦) .','436','586','0');
INSERT INTO [t22928] VALUES (620,'قال تعالى: (والسارق والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالا من الله والله عزيز حكيم) (٣٨) .','436','586','0');
INSERT INTO [t22928] VALUES (621,'قال تعالى: (يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم ومن الذين هادوا سماعون للكذب سماعون لقوم آخرين لم يأتوك يحرفون الكلم من بعد مواضعه يقولون إن أوتيتم هذا فخذوه وإن لم تؤتوه فاحذروا ومن يرد الله فتنته فلن تملك له من الله شيئا أولئك الذين لم يرد الله أن يطهر قلوبهم لهم في الدنيا خزي ولهم في الآخرة عذاب عظيم) (٤١) .','437','586','0');
INSERT INTO [t22928] VALUES (622,'قال تعالى: (سماعون للكذب أكالون للسحت فإن جاءوك فاحكم بينهم أو أعرض عنهم وإن تعرض عنهم فلن يضروك شيئا وإن حكمت فاحكم بينهم بالقسط إن الله يحب المقسطين) (٤٢) .','438','586','0');
INSERT INTO [t22928] VALUES (623,'قال تعالى: (وكيف يحكمونك وعندهم التوراة فيها حكم الله ثم يتولون من بعد ذلك وما أولئك بالمؤمنين) (٤٣) .','439','586','0');
INSERT INTO [t22928] VALUES (624,'قال تعالى: (إنا أنزلنا التوراة فيها هدى ونور يحكم بها النبيون الذين أسلموا للذين هادوا والربانيون والأحبار بما استحفظوا من كتاب الله وكانوا عليه شهداء فلا تخشوا الناس واخشون ولا تشتروا بآياتي ثمنا قليلا ومن لم يحكم بما أنزل الله فأولئك هم الكافرون) (٤٤) .','439','586','0');
INSERT INTO [t22928] VALUES (625,'قال تعالى: (وكتبنا عليهم فيها أن النفس بالنفس والعين بالعين والأنف بالأنف والأذن بالأذن والسن بالسن والجروح قصاص فمن تصدق به فهو كفارة له ومن لم يحكم بما أنزل الله فأولئك هم الظالمون) (٤٥) .','440','586','0');
INSERT INTO [t22928] VALUES (626,'قال تعالى: (وقفينا على آثارهم بعيسى ابن مريم مصدقا لما بين يديه من التوراة وآتيناه الإنجيل فيه هدى ونور ومصدقا لما بين يديه من التوراة وهدى وموعظة للمتقين) (٤٦) .','440','586','0');
INSERT INTO [t22928] VALUES (627,'قال تعالى: (وليحكم أهل الإنجيل بما أنزل الله فيه ومن لم يحكم بما أنزل الله فأولئك هم الفاسقون) (٤٧) .','441','586','0');
INSERT INTO [t22928] VALUES (628,'قال تعالى: (وأنزلنا إليك الكتاب بالحق مصدقا لما بين يديه من الكتاب ومهيمنا عليه فاحكم بينهم بما أنزل الله ولا تتبع أهواءهم عما جاءك من الحق لكل جعلنا منكم شرعة ومنهاجا ولو شاء الله لجعلكم أمة واحدة ولكن ليبلوكم في ما آتاكم فاستبقوا الخيرات إلى الله مرجعكم جميعا فينبئكم بما كنتم فيه تختلفون) (٤٨) .','441','586','0');
INSERT INTO [t22928] VALUES (629,'قال تعالى: (وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم واحذرهم أن يفتنوك عن بعض ما أنزل الله إليك فإن تولوا فاعلم أنما يريد الله أن يصيبهم ببعض ذنوبهم وإن كثيرا من الناس لفاسقون) (٤٩) .','442','586','0');
INSERT INTO [t22928] VALUES (630,'قال تعالى: (أفحكم الجاهلية يبغون ومن أحسن من الله حكما لقوم يوقنون) (٥٠) .','443','586','0');
INSERT INTO [t22928] VALUES (631,'قال تعالى: (يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء بعضهم أولياء بعض ومن يتولهم منكم فإنه منهم إن الله لا يهدي القوم الظالمين) (٥١) .','444','586','0');
INSERT INTO [t22928] VALUES (632,'قال تعالى: (فترى الذين في قلوبهم مرض يسارعون فيهم يقولون نخشى أن تصيبنا دائرة فعسى الله أن يأتي بالفتح أو أمر من عنده فيصبحوا على ما أسروا في أنفسهم نادمين) (٥٢) .','444','586','0');
INSERT INTO [t22928] VALUES (633,'قال تعالى: (ويقول الذين آمنوا أهؤلاء الذين أقسموا بالله جهد أيمانهم إنهم لمعكم حبطت أعمالهم فأصبحوا خاسرين) (٥٣) .','445','586','0');
INSERT INTO [t22928] VALUES (634,'قال تعالى: (يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقوم يحبهم ويحبونه أذلة على المؤمنين أعزة على الكافرين يجاهدون في سبيل الله ولا يخافون لومة لائم ذلك فضل الله يؤتيه من يشاء والله واسع عليم) (٥٤) .','446','586','0');
INSERT INTO [t22928] VALUES (635,'قال تعالى: (إنما وليكم الله ورسوله والذين آمنوا الذين يقيمون الصلاة ويؤتون الزكاة وهم راكعون) (٥٥) .','447','586','0');
INSERT INTO [t22928] VALUES (636,'قال تعالى: (ومن يتول الله ورسوله والذين آمنوا فإن حزب الله هم الغالبون) (٥٦) .','447','586','0');
INSERT INTO [t22928] VALUES (637,'قال تعالى: (يا أيها الذين آمنوا لا تتخذوا الذين اتخذوا دينكم هزوا ولعبا من الذين أوتوا الكتاب من قبلكم والكفار أولياء واتقوا الله إن كنتم مؤمنين) (٥٧) .','447','586','0');
INSERT INTO [t22928] VALUES (638,'قال تعالى: (وإذا ناديتم إلى الصلاة اتخذوها هزوا ولعبا ذلك بأنهم قوم لا يعقلون) (٥٨) .','448','586','0');
INSERT INTO [t22928] VALUES (639,'قال تعالى: (قل يا أهل الكتاب هل تنقمون منا إلا أن آمنا بالله وما أنزل إلينا وما أنزل من قبل وأن أكثركم فاسقون) (٥٩) .','448','586','0');
INSERT INTO [t22928] VALUES (640,'قال تعالى: (قل هل أنبئكم بشر من ذلك مثوبة عند الله من لعنه الله وغضب عليه وجعل منهم القردة والخنازير وعبد الطاغوت أولئك شر مكانا وأضل عن سواء السبيل) (٦٠) .','449','586','0');
INSERT INTO [t22928] VALUES (641,'قال تعالى: (وإذا جاءوكم قالوا آمنا وقد دخلوا بالكفر وهم قد خرجوا به والله أعلم بما كانوا يكتمون) (٦١) .','450','586','0');
INSERT INTO [t22928] VALUES (642,'قال تعالى: (وترى كثيرا منهم يسارعون في الإثم والعدوان وأكلهم السحت لبئس ما كانوا يعملون) (٦٢) .','450','586','0');
INSERT INTO [t22928] VALUES (643,'قال تعالى: (وقالت اليهود يد الله مغلولة غلت أيديهم ولعنوا بما قالوا بل يداه مبسوطتان ينفق كيف يشاء وليزيدن كثيرا منهم ما أنزل إليك من ربك طغيانا وكفرا وألقينا بينهم العداوة والبغضاء إلى يوم القيامة كلما أوقدوا نارا للحرب أطفأها الله ويسعون في الأرض فسادا والله لا يحب المفسدين) (٦٤) .','450','586','0');
INSERT INTO [t22928] VALUES (644,'قال تعالى: (ولو أنهم أقاموا التوراة والإنجيل وما أنزل إليهم من ربهم لأكلوا من فوقهم ومن تحت أرجلهم منهم أمة مقتصدة وكثير منهم ساء ما يعملون) (٦٦) .','451','586','0');
INSERT INTO [t22928] VALUES (645,'قال تعالى: (يا أيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته والله يعصمك من الناس إن الله لا يهدي القوم الكافرين) (٦٧) .','451','586','0');
INSERT INTO [t22928] VALUES (646,'قال تعالى: (إن الذين آمنوا والذين هادوا والصابئون والنصارى من آمن بالله واليوم الآخر وعمل صالحا فلا خوف عليهم ولا هم يحزنون) (٦٩) .','451','586','0');
INSERT INTO [t22928] VALUES (647,'قال تعالى: (لقد أخذنا ميثاق بني إسرائيل وأرسلنا إليهم رسلا كلما جاءهم رسول بما لا تهوى أنفسهم فريقا كذبوا وفريقا يقتلون) (٧٠) .','453','586','0');
INSERT INTO [t22928] VALUES (648,'قال تعالى: (وحسبوا ألا تكون فتنة فعموا وصموا ثم تاب الله عليهم ثم عموا وصموا كثير منهم والله بصير بما يعملون) (٧١) .','453','586','0');
INSERT INTO [t22928] VALUES (649,'قال تعالى: (لقد كفر الذين قالوا إن الله ثالث ثلاثة وما من إله إلا إله واحد وإن لم ينتهوا عما يقولون ليمسن الذين كفروا منهم عذاب أليم) (٧٣) .','454','586','0');
INSERT INTO [t22928] VALUES (650,'قال تعالى: (ما المسيح ابن مريم إلا رسول قد خلت من قبله الرسل وأمه صديقة كانا يأكلان الطعام انظر كيف نبين لهم الآيات ثم انظر أنى يؤفكون) (٧٥) .','454','586','0');
INSERT INTO [t22928] VALUES (651,'قال تعالى: (قل أتعبدون من دون الله ما لا يملك لكم ضرا ولا نفعا والله هو السميع العليم) (٧٦) .','455','586','0');
INSERT INTO [t22928] VALUES (652,'قال تعالى: (قل يا أهل الكتاب لا تغلوا في دينكم غير الحق ولا تتبعوا أهواء قوم قد ضلوا.) (٧٧) .','455','586','0');
INSERT INTO [t22928] VALUES (653,'قال تعالى: (لعن الذين كفروا من بني إسرائيل على لسان داود وعيسى ابن مريم ذلك بما عصوا وكانوا يعتدون) (٧٨) .','455','586','0');
INSERT INTO [t22928] VALUES (654,'قال تعالى: (لتجدن أشد الناس عداوة للذين آمنوا اليهود والذين أشركوا ولتجدن أقربهم مودة للذين آمنوا الذين قالوا إنا نصارى ذلك بأن منهم قسيسين ورهبانا وأنهم لا يستكبرون) (٨٢) .','456','586','0');
INSERT INTO [t22928] VALUES (655,'قال تعالى: (وإذا سمعوا ما أنزل إلى الرسول ترى أعينهم تفيض من الدمع مما عرفوا من الحق يقولون ربنا آمنا فاكتبنا مع الشاهدين) (٨٣) .','456','586','0');
INSERT INTO [t22928] VALUES (656,'قال تعالى: (وما لنا لا نؤمن بالله وما جاءنا من الحق ونطمع أن يدخلنا ربنا مع القوم الصالحين) (٨٤) .','457','586','0');
INSERT INTO [t22928] VALUES (657,'قال تعالى: (وكلوا مما رزقكم الله حلالا طيبا واتقوا الله الذي أنتم به مؤمنون) (٨٨) .','457','586','0');
INSERT INTO [t22928] VALUES (658,'قال تعالى: (لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما عقدتم الأيمان فكفارته إطعام عشرة مساكين من أوسط ما تطعمون أهليكم أو كسوتهم أو تحرير رقبة فمن لم يجد فصيام ثلاثة أيام ذلك كفارة أيمانكم إذا حلفتم واحفظوا أيمانكم كذلك يبين الله لكم آياته لعلكم تشكرون) (٨٩) .','458','586','0');
INSERT INTO [t22928] VALUES (659,'قال تعالى: (يا أيها الذين آمنوا إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون) (٩٠) .','459','586','0');
INSERT INTO [t22928] VALUES (660,'قال تعالى: (إنما يريد الشيطان أن يوقع بينكم العداوة والبغضاء في الخمر والميسر ويصدكم عن ذكر الله وعن الصلاة فهل أنتم منتهون) (٩١) .','460','586','0');
INSERT INTO [t22928] VALUES (661,'قال تعالى: (ليس على الذين آمنوا وعملوا الصالحات جناح فيما طعموا إذا ما اتقوا وآمنوا وعملوا الصالحات) (٩٣) .','460','586','0');
INSERT INTO [t22928] VALUES (662,'قال تعالى: (يا أيها الذين آمنوا ليبلونكم الله بشيء من الصيد تناله أيديكم ورماحكم ليعلم الله من يخافه بالغيب فمن اعتدى بعد ذلك فله عذاب أليم) (٩٤) .','460','586','0');
INSERT INTO [t22928] VALUES (663,'قال تعالى: (يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديا بالغ الكعبة أو كفارة طعام مساكين أو عدل ذلك صياما ليذوق وبال أمره عفا الله عما سلف ومن عاد فينتقم الله منه والله عزيز ذو انتقام) (٩٥) .','461','586','0');
INSERT INTO [t22928] VALUES (664,'قال تعالى: (أحل لكم صيد البحر وطعامه متاعا لكم وللسيارة وحرم عليكم صيد البر ما دمتم حرما واتقوا الله الذي إليه تحشرون) (٩٦) .','463','586','0');
INSERT INTO [t22928] VALUES (665,'قال تعالى: (جعل الله الكعبة البيت الحرام قياما للناس والشهر الحرام والهدي والقلائد ذلك لتعلموا أن الله يعلم ما في السماوات وما في الأرض وأن الله بكل شيء عليم) (٩٧) .','463','586','0');
INSERT INTO [t22928] VALUES (666,'قال تعالى: (يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم وإن تسألوا عنها حين ينزل القرآن تبد لكم عفا الله عنها والله غفور حليم) (١٠١) .','464','586','0');
INSERT INTO [t22928] VALUES (667,'قال تعالى: (قد سألها قوم من قبلكم ثم أصبحوا بها كافرين) (١٠٢) .','465','586','0');
INSERT INTO [t22928] VALUES (668,'قال تعالى: (ما جعل الله من بحيرة ولا سائبة ولا وصيلة ولا حام ولكن الذين كفروا يفترون على الله الكذب وأكثرهم لا يعقلون) (١٠٣) .','465','586','0');
INSERT INTO [t22928] VALUES (669,'قال تعالى: (وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول قالوا حسبنا ما وجدنا عليه آباءنا أولو كان آباؤهم لا يعلمون شيئا ولا يهتدون) (١٠٤) .','466','586','0');
INSERT INTO [t22928] VALUES (670,'قال تعالى: (يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم إلى الله مرجعكم جميعا فينبئكم بما كنتم تعملون) (١٠٥) .','466','586','0');
INSERT INTO [t22928] VALUES (671,'قال تعالى: (يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية اثنان ذوا عدل منكم أو آخران من غيركم إن أنتم ضربتم في الأرض فأصابتكم مصيبة الموت تحبسونهما من بعد الصلاة فيقسمان بالله إن ارتبتم لا نشتري به ثمنا ولو كان ذا قربى ولا نكتم شهادة الله إنا إذا لمن الآثمين) (١٠٦) .','467','586','0');
INSERT INTO [t22928] VALUES (672,'قال تعالى: (فإن عثر على أنهما استحقا إثما فآخران يقومان مقامهما من الذين استحق عليهم الأوليان فيقسمان بالله لشهادتنا أحق من شهادتهما وما اعتدينا إنا إذا لمن الظالمين) (١٠٧) .','469','586','0');
INSERT INTO [t22928] VALUES (673,'قال تعالى: (ذلك أدنى أن يأتوا بالشهادة على وجهها أو يخافوا أن ترد أيمان بعد أيمانهم واتقوا الله واسمعوا والله لا يهدي القوم الفاسقين) (١٠٨) .','471','586','0');
INSERT INTO [t22928] VALUES (674,'قال تعالى: (يوم يجمع الله الرسل فيقول ماذا أجبتم قالوا لا علم لنا إنك أنت علام الغيوب) (١٠٩) .','471','586','0');
INSERT INTO [t22928] VALUES (675,'قال تعالى: (إذ قال الله يا عيسى ابن مريم اذكر نعمتي عليك وعلى والدتك إذ أيدتك بروح القدس تكلم الناس في المهد وكهلا وإذ علمتك الكتاب والحكمة والتوراة والإنجيل وإذ تخلق من الطين كهيئة الطير بإذني فتنفخ فيها فتكون طيرا بإذني وتبرئ الأكمه والأبرص بإذني وإذ تخرج الموتى بإذني وإذ كففت بني إسرائيل عنك إذ جئتهم بالبينات فقال الذين كفروا منهم إن هذا إلا سحر مبين) (١١٠) .','472','586','0');
INSERT INTO [t22928] VALUES (676,'قال تعالى: (وإذ أوحيت إلى الحواريين أن آمنوا بي وبرسولي قالوا آمنا واشهد بأننا مسلمون) المائدة: ١١١) .','473','586','0');
INSERT INTO [t22928] VALUES (677,'قال تعالى: (إذ قال الحواريون يا عيسى ابن مريم هل يستطيع ربك أن ينزل علينا مائدة من السماء قال اتقوا الله إن كنتم مؤمنين) (١١٢) .','474','586','0');
INSERT INTO [t22928] VALUES (678,'قال تعالى: (قالوا نريد أن نأكل منها وتطمئن قلوبنا ونعلم أن قد صدقتنا ونكون عليها من الشاهدين) (١١٣) قال عيسى ابن مريم اللهم ربنا أنزل علينا مائدة من السماء تكون لنا عيدا لأولنا وآخرنا وآية منك وارزقنا وأنت خير الرازقين) (١١٤) .','474','586','0');
INSERT INTO [t22928] VALUES (679,'قال تعالى: (قال الله إني منزلها عليكم فمن يكفر بعد منكم فإني أعذبه عذابا لا أعذبه أحدا من العالمين) (١١٥) .','475','586','0');
INSERT INTO [t22928] VALUES (680,'قال تعالى: (وإذ قال الله يا عيسى ابن مريم أأنت قلت للناس اتخذوني وأمي إلهين من دون الله قال سبحانك ما يكون لي أن أقول ما ليس لي بحق إن كنت قلته فقد علمته تعلم ما في نفسي ولا أعلم ما في نفسك إنك أنت علام الغيوب) (١١٦) .','476','586','0');
INSERT INTO [t22928] VALUES (681,'قال تعالى: (ما قلت لهم إلا ما أمرتني به أن اعبدوا الله ربي وربكم وكنت عليهم شهيدا ما دمت فيهم فلما توفيتني كنت أنت الرقيب عليهم وأنت على كل شيء شهيد) (١١٧) .','477','586','0');
INSERT INTO [t22928] VALUES (682,'قال تعالى: (إن تعذبهم فإنهم عبادك وإن تغفر لهم فإنك أنت العزيز الحكيم) (١١٨) .','478','586','0');
INSERT INTO [t22928] VALUES (683,'قال تعالى: (قال الله هذا يوم ينفع الصادقين صدقهم لهم جنات تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم ورضوا عنه ذلك الفوز العظيم) (١١٩) .','478','586','0');
INSERT INTO [t22928] VALUES (684,'الأنعام','480','0','0');
INSERT INTO [t22928] VALUES (685,'قال تعالى: (الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور ثم الذين كفروا بربهم يعدلون) (١) .','480','684','0');
INSERT INTO [t22928] VALUES (686,'قال تعالى: (هو الذي خلقكم من طين ثم قضى أجلا وأجل مسمى عنده ثم أنتم تمترون) (٢) .','480','684','0');
INSERT INTO [t22928] VALUES (687,'قال تعالى: (وهو الله في السماوات وفي الأرض يعلم سركم وجهركم ويعلم ما تكسبون) (٣) .','480','684','0');
INSERT INTO [t22928] VALUES (688,'قال تعالى: (وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين) (٤) .','481','684','0');
INSERT INTO [t22928] VALUES (689,'قال تعالى: (فقد كذبوا بالحق لما جاءهم فسوف يأتيهم أنباء ما كانوا به يستهزئون) (٥) .','481','684','0');
INSERT INTO [t22928] VALUES (690,'قال تعالى: (ألم يروا كم أهلكنا من قبلهم من قرن مكناهم في الأرض ما لم نمكن لكم وأرسلنا السماء عليهم مدرارا وجعلنا الأنهار تجري من تحتهم فأهلكناهم بذنوبهم وأنشأنا من بعدهم قرنا آخرين) (٦) .','482','684','0');
INSERT INTO [t22928] VALUES (691,'قال تعالى: (ولو نزلنا عليك كتابا في قرطاس فلمسوه بأيديهم لقال الذين كفروا إن هذا إلا سحر مبين) (٧) .','483','684','0');
INSERT INTO [t22928] VALUES (692,'قال تعالى: (ولو جعلناه ملكا لجعلناه رجلا وللبسنا عليهم ما يلبسون) (٩) .','483','684','0');
INSERT INTO [t22928] VALUES (693,'قال تعالى: (ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون) (١٠) .','483','684','0');
INSERT INTO [t22928] VALUES (694,'قال تعالى: (قل سيروا في الأرض ثم انظروا كيف كان عاقبة المكذبين) (١١) .','484','684','0');
INSERT INTO [t22928] VALUES (695,'قال تعالى: (قل لمن ما في السماوات والأرض قل لله كتب على نفسه الرحمة ليجمعنكم إلى يوم القيامة لا ريب فيه الذين خسروا أنفسهم فهم لا يؤمنون) (١٢) .','484','684','0');
INSERT INTO [t22928] VALUES (696,'قال تعالى: (قل أغير الله أتخذ وليا فاطر السماوات والأرض وهو يطعم ولا يطعم قل إني أمرت أن أكون أول من أسلم ولا تكونن من المشركين) (١٤) .','484','684','0');
INSERT INTO [t22928] VALUES (697,'قال تعالى: (من يصرف عنه يومئذ فقد رحمه وذلك الفوز المبين) (١٦) .','485','684','0');
INSERT INTO [t22928] VALUES (698,'قال تعالى: (وإن يمسسك الله بضر فلا كاشف له إلا هو وإن يمسسك بخير فهو على كل شيء قدير) (١٧) .','486','684','0');
INSERT INTO [t22928] VALUES (699,'قال تعالى: (وهو القاهر فوق عباده وهو الحكيم الخبير) (١٨) .','486','684','0');
INSERT INTO [t22928] VALUES (700,'قال تعالى: (قل أي شيء أكبر شهادة قل الله شهيد بيني وبينكم وأوحي إلي هذا القرآن لأنذركم به ومن بلغ أئنكم لتشهدون أن مع الله آلهة أخرى قل لا أشهد قل إنما هو إله واحد وإنني بريء مما تشركون) (١٩) .','487','684','0');
INSERT INTO [t22928] VALUES (701,'قال تعالى: (الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم الذين خسروا أنفسهم فهم لا يؤمنون) (٢٠) .','488','684','0');
INSERT INTO [t22928] VALUES (702,'قال تعالى: (ويوم نحشرهم جميعا ثم نقول للذين أشركوا أين شركاؤكم الذين كنتم تزعمون) (٢٢) .','488','684','0');
INSERT INTO [t22928] VALUES (703,'قال تعالى: (ثم لم تكن فتنتهم إلا أن قالوا والله ربنا ما كنا مشركين) (٢٣) .','488','684','0');
INSERT INTO [t22928] VALUES (704,'قال تعالى: (ومنهم من يستمع إليك وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن يروا كل آية لا يؤمنوا بها حتى إذا جاءوك يجادلونك يقول الذين كفروا إن هذا إلا أساطير الأولين) (٢٥) .','489','684','0');
INSERT INTO [t22928] VALUES (705,'قال تعالى: (وهم ينهون عنه وينأون عنه وإن يهلكون إلا أنفسهم وما يشعرون) (٢٦) .','489','684','0');
INSERT INTO [t22928] VALUES (706,'قال تعالى: (ولو ترى إذ وقفوا على النار فقالوا يا ليتنا نرد ولا نكذب بآيات ربنا ونكون من المؤمنين) (٢٧) .','490','684','0');
INSERT INTO [t22928] VALUES (707,'قال تعالى: (وقالوا إن هي إلا حياتنا الدنيا وما نحن بمبعوثين) (٢٩) .','490','684','0');
INSERT INTO [t22928] VALUES (708,'قال تعالى: (ولو ترى إذ وقفوا على ربهم قال أليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون) (٣٠) .','490','684','0');
INSERT INTO [t22928] VALUES (709,'قال تعالى: (قد خسر الذين كذبوا بلقاء الله حتى إذا جاءتهم الساعة بغتة قالوا يا حسرتنا على ما فرطنا فيها وهم يحملون أوزارهم على ظهورهم ألا ساء ما يزرون) (٣١) .','491','684','0');
INSERT INTO [t22928] VALUES (710,'قال تعالى: (وما الحياة الدنيا إلا لعب ولهو وللدار الآخرة خير للذين يتقون أفلا تعقلون) (٣٢) .','491','684','0');
INSERT INTO [t22928] VALUES (711,'قال تعالى: (قد نعلم إنه ليحزنك الذي يقولون فإنهم لا يكذبونك ولكن الظالمين بآيات الله يجحدون) (٣٣) .','492','684','0');
INSERT INTO [t22928] VALUES (712,'قال تعالى: (ولقد كذبت رسل من قبلك فصبروا على ما كذبوا وأوذوا حتى أتاهم نصرنا ولا مبدل لكلمات الله ولقد جاءك من نبأ المرسلين) (٣٤) .','492','684','0');
INSERT INTO [t22928] VALUES (713,'قال تعالى: (وإن كان كبر عليك إعراضهم فإن استطعت أن تبتغي نفقا في الأرض أو سلما في السماء فتأتيهم بآية ولو شاء الله لجمعهم على الهدى فلا تكونن من الجاهلين) (٣٥) .','493','684','0');
INSERT INTO [t22928] VALUES (714,'قال تعالى: (إنما يستجيب الذين يسمعون والموتى يبعثهم الله ثم إليه يرجعون) (٣٦) .','493','684','0');
INSERT INTO [t22928] VALUES (715,'قال تعالى: (وقالوا لولا نزل عليه آية من ربه قل إن الله قادر على أن ينزل آية ولكن أكثرهم لا يعلمون) (٣٧) .','494','684','0');
INSERT INTO [t22928] VALUES (716,'قال تعالى: (وما من دابة في الأرض ولا طائر يطير بجناحيه إلا أمم أمثالكم ما فرطنا في الكتاب من شيء ثم إلى ربهم يحشرون) (٣٨) .','494','684','0');
INSERT INTO [t22928] VALUES (717,'قال تعالى: (والذين كذبوا بآياتنا صم وبكم في الظلمات من يشأ الله يضلله ومن يشأ يجعله على صراط مستقيم) (٣٩) .','495','684','0');
INSERT INTO [t22928] VALUES (718,'قال تعالى: (قل أرأيتكم إن أتاكم عذاب الله أو أتتكم الساعة أغير الله تدعون إن كنتم صادقين) (٤٠) .','495','684','0');
INSERT INTO [t22928] VALUES (719,'قال تعالى: (بل إياه تدعون فيكشف ما تدعون إليه إن شاء وتنسون ما تشركون) (٤١) .','497','684','0');
INSERT INTO [t22928] VALUES (720,'قال تعالى: (ولقد أرسلنا إلى أمم من قبلك فأخذناهم بالبأساء والضراء لعلهم يتضرعون) (٤٢) .','497','684','0');
INSERT INTO [t22928] VALUES (721,'قال تعالى: (فلولا إذ جاءهم بأسنا تضرعوا ولكن قست قلوبهم وزين لهم الشيطان ما كانوا يعملون) (٤٣) .','497','684','0');
INSERT INTO [t22928] VALUES (722,'قال تعالى: (فلما نسوا ما ذكروا به فتحنا عليهم أبواب كل شيء حتى إذا فرحوا بما أوتوا أخذناهم بغتة فإذا هم مبلسون) (٤٤) .','498','684','0');
INSERT INTO [t22928] VALUES (723,'قال تعالى: (قل أرأيتم إن أخذ الله سمعكم وأبصاركم وختم على قلوبكم من إله غير الله يأتيكم به انظر كيف نصرف الآيات ثم هم يصدفون) (٤٦) .','498','684','0');
INSERT INTO [t22928] VALUES (724,'قال تعالى: (قل أرأيتكم إن أتاكم عذاب الله بغتة أو جهرة هل يهلك إلا القوم الظالمون) (٤٧) .','498','684','0');
INSERT INTO [t22928] VALUES (725,'قال تعالى: (وما نرسل المرسلين إلا مبشرين ومنذرين فمن آمن وأصلح فلا خوف عليهم ولا هم يحزنون) (٤٨) .','499','684','0');
INSERT INTO [t22928] VALUES (726,'قال تعالى: (والذين كذبوا بآياتنا يمسهم العذاب بما كانوا يفسقون) (٤٩) .','499','684','0');
INSERT INTO [t22928] VALUES (727,'قال تعالى: (ولا تطرد الذين يدعون ربهم بالغداة والعشي يريدون وجهه ما عليك من حسابهم من شيء وما من حسابك عليهم من شيء فتطردهم فتكون من الظالمين) (٥٢) .','499','684','0');
INSERT INTO [t22928] VALUES (728,'قال تعالى: (وكذلك فتنا بعضهم ببعض ليقولوا أهؤلاء من الله عليهم من بيننا أليس الله بأعلم بالشاكرين) (٥٣) .','500','684','0');
INSERT INTO [t22928] VALUES (729,'قال تعالى: (وإذا جاءك الذين يؤمنون بآياتنا فقل سلام عليكم كتب ربكم على نفسه الرحمة أنه من عمل منكم سوءا بجهالة ثم تاب من بعده وأصلح فأنه غفور رحيم) (٥٤) .','500','684','0');
INSERT INTO [t22928] VALUES (730,'قال تعالى: (وكذلك نفصل الآيات ولتستبين سبيل المجرمين) (٥٥) .','502','684','0');
INSERT INTO [t22928] VALUES (731,'قال تعالى: (قل إني على بينة من ربي وكذبتم به ما عندي ما تستعجلون به إن الحكم إلا لله يقص الحق وهو خير الفاصلين) (٥٧) .','502','684','0');
INSERT INTO [t22928] VALUES (732,'قال تعالى: (وعنده مفاتح الغيب لا يعلمها إلا هو ويعلم ما في البر والبحر وما تسقط من ورقة إلا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس إلا في كتاب مبين) (٥٩)','503','684','0');
INSERT INTO [t22928] VALUES (733,'قال تعالى: (وهو الذي يتوفاكم بالليل ويعلم ما جرحتم بالنهار ثم يبعثكم فيه ليقضى أجل مسمى ثم إليه مرجعكم ثم ينبئكم بما كنتم تعملون) (٦٠) .','503','684','0');
INSERT INTO [t22928] VALUES (734,'قال تعالى: (وهو القاهر فوق عباده ويرسل عليكم حفظة حتى إذا جاء أحدكم الموت توفته رسلنا وهم لا يفرطون) (٦١) .','504','684','0');
INSERT INTO [t22928] VALUES (735,'قال تعالى: (ثم ردوا إلى الله مولاهم الحق ألا له الحكم وهو أسرع الحاسبين) (٦٢) .','505','684','0');
INSERT INTO [t22928] VALUES (736,'قال تعالى: (قل من ينجيكم من ظلمات البر والبحر تدعونه تضرعا وخفية لئن أنجانا من هذه لنكونن من الشاكرين) (٦٣) .','505','684','0');
INSERT INTO [t22928] VALUES (737,'قال تعالى: (قل هو القادر على أن يبعث عليكم عذابا من فوقكم أو من تحت أرجلكم أو يلبسكم شيعا ويذيق بعضكم بأس بعض انظر كيف نصرف الآيات لعلهم يفقهون) (٦٥) .','506','684','0');
INSERT INTO [t22928] VALUES (738,'قال تعالى: (وكذب به قومك وهو الحق قل لست عليكم بوكيل) (٦٦) .','506','684','0');
INSERT INTO [t22928] VALUES (739,'قال تعالى: (لكل نبإ مستقر وسوف تعلمون) (٦٧) .','507','684','0');
INSERT INTO [t22928] VALUES (740,'قال تعالى: (وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم حتى يخوضوا في حديث غيره وإما ينسينك الشيطان فلا تقعد بعد الذكرى مع القوم الظالمين) (٦٨) .','507','684','0');
INSERT INTO [t22928] VALUES (741,'قال تعالى: (وما على الذين يتقون من حسابهم من شيء ولكن ذكرى لعلهم يتقون) (٦٩) .','507','684','0');
INSERT INTO [t22928] VALUES (742,'قال تعالى: (وذر الذين اتخذوا دينهم لعبا ولهوا وغرتهم الحياة الدنيا وذكر به أن تبسل نفس بما كسبت ليس لها من دون الله ولي ولا شفيع وإن تعدل كل عدل لا يؤخذ منها أولئك الذين أبسلوا بما كسبوا لهم شراب من حميم وعذاب أليم بما كانوا يكفرون) (٧٠) .','507','684','0');
INSERT INTO [t22928] VALUES (743,'قال تعالى: (قل أندعو من دون الله ما لا ينفعنا ولا يضرنا ونرد على أعقابنا بعد إذ هدانا الله كالذي استهوته الشياطين في الأرض حيران له أصحاب يدعونه إلى الهدى ائتنا قل إن هدى الله هو الهدى وأمرنا لنسلم لرب العالمين) (٧١) .','508','684','0');
INSERT INTO [t22928] VALUES (744,'قال تعالى: (وأن أقيموا الصلاة واتقوه وهو الذي إليه تحشرون) (٧٢) .','509','684','0');
INSERT INTO [t22928] VALUES (745,'قال تعالى: (وهو الذي خلق السماوات والأرض بالحق ويوم يقول كن فيكون قوله الحق وله الملك يوم ينفخ في الصور عالم الغيب والشهادة وهو الحكيم الخبير) (٧٣) .','509','684','0');
INSERT INTO [t22928] VALUES (746,'قال تعالى: (وإذ قال إبراهيم لأبيه آزر أتتخذ أصناما آلهة إني أراك وقومك في ضلال مبين) (٧٤) .','511','684','0');
INSERT INTO [t22928] VALUES (747,'قال تعالى: (وكذلك نري إبراهيم ملكوت السماوات والأرض وليكون من الموقنين) (٧٥) .','512','684','0');
INSERT INTO [t22928] VALUES (748,'قال تعالى: (فلما جن عليه الليل رأى كوكبا قال هذا ربي فلما أفل قال لا أحب الآفلين) (٧٦) .','512','684','0');
INSERT INTO [t22928] VALUES (749,'قال تعالى: (فلما رأى الشمس بازغة قال هذا ربي هذا أكبر فلما أفلت قال يا قوم إني بريء مما تشركون) (٧٨) .','513','684','0');
INSERT INTO [t22928] VALUES (750,'قال تعالى: (إني وجهت وجهي للذي فطر السماوات والأرض حنيفا وما أنا من المشركين) (٧٩) .','513','684','0');
INSERT INTO [t22928] VALUES (751,'قال تعالى: (وحاجه قومه قال أتحاجوني في الله وقد هداني ولا أخاف ما تشركون به إلا أن يشاء ربي شيئا وسع ربي كل شيء علما أفلا تتذكرون) (٨٠) .','513','684','0');
INSERT INTO [t22928] VALUES (752,'قال تعالى: (وكيف أخاف ما أشركتم ولا تخافون أنكم أشركتم بالله ما لم ينزل به عليكم سلطانا فأي الفريقين أحق بالأمن إن كنتم تعلمون) (٨١) .','515','684','0');
INSERT INTO [t22928] VALUES (753,'قال تعالى: (الذين آمنوا ولم يلبسوا إيمانهم بظلم أولئك لهم الأمن وهم مهتدون) (٨٢) .','515','684','0');
INSERT INTO [t22928] VALUES (754,'قال تعالى: (وتلك حجتنا آتيناها إبراهيم على قومه نرفع درجات من نشاء إن ربك حكيم عليم) (٨٣) .','515','684','0');
INSERT INTO [t22928] VALUES (755,'قال تعالى: (ووهبنا له إسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل ومن ذريته داود وسليمان وأيوب ويوسف وموسى وهارون وكذلك نجزي المحسنين (٨٤) وزكريا ويحيى وعيسى وإلياس كل من الصالحين (٨٥) وإسماعيل واليسع ويونس ولوطا وكلا فضلنا على العالمين) (٨٦) .','516','684','0');
INSERT INTO [t22928] VALUES (756,'قال تعالى: (ومن آبائهم وذرياتهم وإخوانهم واجتبيناهم وهديناهم إلى صراط مستقيم) (٨٧) .','517','684','0');
INSERT INTO [t22928] VALUES (757,'قال تعالى: (ذلك هدى الله يهدي به من يشاء من عباده ولو أشركوا لحبط عنهم ما كانوا يعملون (٨٨) أولئك الذين آتيناهم الكتاب والحكم والنبوة فإن يكفر بها هؤلاء فقد وكلنا بها قوما ليسوا بها بكافرين) (٨٩) .','518','684','0');
INSERT INTO [t22928] VALUES (758,'قال تعالى: (أولئك الذين هدى الله فبهداهم اقتده قل لا أسألكم عليه أجرا إن هو إلا ذكرى للعالمين) (٩٠) .','518','684','0');
INSERT INTO [t22928] VALUES (759,'قال تعالى: (وما قدروا الله حق قدره إذ قالوا ما أنزل الله على بشر من شيء قل من أنزل الكتاب الذي جاء به موسى نورا وهدى للناس تجعلونه قراطيس تبدونها وتخفون كثيرا وعلمتم ما لم تعلموا أنتم ولا آباؤكم قل الله ثم ذرهم في خوضهم يلعبون) (٩١) .','519','684','0');
INSERT INTO [t22928] VALUES (760,'قال تعالى: (وهذا كتاب أنزلناه مبارك مصدق الذي بين يديه ولتنذر أم القرى ومن حولها والذين يؤمنون بالآخرة يؤمنون به وهم على صلاتهم يحافظون) (٩٢) .','520','684','0');
INSERT INTO [t22928] VALUES (761,'قال تعالى: (ومن أظلم ممن افترى على الله كذبا أو قال أوحي إلي ولم يوح إليه شيء ومن قال سأنزل مثل ما أنزل الله ولو ترى إذ الظالمون في غمرات الموت والملائكة باسطو أيديهم أخرجوا أنفسكم اليوم تجزون عذاب الهون بما كنتم تقولون على الله غير الحق وكنتم عن آياته تستكبرون) (٩٣) .','521','684','0');
INSERT INTO [t22928] VALUES (762,'قال تعالى: (ولقد جئتمونا فرادى كما خلقناكم أول مرة وتركتم ما خولناكم وراء ظهوركم وما نرى معكم شفعاءكم الذين زعمتم أنهم فيكم شركاء لقد تقطع بينكم وضل عنكم ما كنتم تزعمون) (٩٤) .','522','684','0');
INSERT INTO [t22928] VALUES (763,'قال تعالى: (إن الله فالق الحب والنوى يخرج الحي من الميت ومخرج الميت من الحي ذلكم الله فأنى تؤفكون (٩٥) فالق الإصباح وجعل الليل سكنا والشمس والقمر حسبانا ذلك تقدير العزيز العليم) (٩٦) .','523','684','0');
INSERT INTO [t22928] VALUES (764,'قال تعالى: (وهو الذي أنشأكم من نفس واحدة فمستقر ومستودع قد فصلنا الآيات لقوم يفقهون) (٩٨) .','524','684','0');
INSERT INTO [t22928] VALUES (765,'قال تعالى: (وهو الذي أنزل من السماء ماء فأخرجنا به نبات كل شيء فأخرجنا منه خضرا نخرج منه حبا متراكبا ومن النخل من طلعها قنوان دانية وجنات من أعناب والزيتون والرمان مشتبها وغير متشابه انظروا إلى ثمره إذا أثمر وينعه إن في ذلكم لآيات لقوم يؤمنون) (٩٩) .','525','684','0');
INSERT INTO [t22928] VALUES (766,'قال تعالى: (وجعلوا لله شركاء الجن وخلقهم وخرقوا له بنين وبنات بغير علم سبحانه وتعالى عما يصفون) (١٠٠) .','527','684','0');
INSERT INTO [t22928] VALUES (767,'قال تعالى: (بديع السماوات والأرض أنى يكون له ولد ولم تكن له صاحبة وخلق كل شيء وهو بكل شيء عليم) (١٠١) .','527','684','0');
INSERT INTO [t22928] VALUES (768,'قال تعالى: (ذلكم الله ربكم لا إله إلا هو خالق كل شيء فاعبدوه وهو على كل شيء وكيل) (١٠٢) .','528','684','0');
INSERT INTO [t22928] VALUES (769,'قال تعالى: (قد جاءكم بصائر من ربكم فمن أبصر فلنفسه ومن عمي فعليها وما أنا عليكم بحفيظ) (١٠٤) .','528','684','0');
INSERT INTO [t22928] VALUES (770,'قال تعالى: (وكذلك نصرف الآيات وليقولوا درست ولنبينه لقوم يعلمون) (١٠٥) .','529','684','0');
INSERT INTO [t22928] VALUES (771,'قال تعالى: (اتبع ما أوحي إليك من ربك لا إله إلا هو وأعرض عن المشركين) (١٠٦) .','530','684','0');
INSERT INTO [t22928] VALUES (772,'قال تعالى: (ولو شاء الله ما أشركوا وما جعلناك عليهم حفيظا وما أنت عليهم بوكيل) (١٠٧) .','530','684','0');
INSERT INTO [t22928] VALUES (773,'قال تعالى: (ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم كذلك زينا لكل أمة عملهم ثم إلى ربهم مرجعهم فينبئهم بما كانوا يعملون) (١٠٨) .','530','684','0');
INSERT INTO [t22928] VALUES (774,'قال تعالى: (وأقسموا بالله جهد أيمانهم لئن جاءتهم آية ليؤمنن بها قل إنما الآيات عند الله وما يشعركم أنها إذا جاءت لا يؤمنون) (١٠٩) .','531','684','0');
INSERT INTO [t22928] VALUES (775,'قال تعالى: (ونقلب أفئدتهم وأبصارهم كما لم يؤمنوا به أول مرة ونذرهم في طغيانهم يعمهون) (١١٠) .','532','684','0');
INSERT INTO [t22928] VALUES (776,'قال تعالى: (ولو أننا نزلنا إليهم الملائكة وكلمهم الموتى وحشرنا عليهم كل شيء قبلا ما كانوا ليؤمنوا إلا أن يشاء الله ولكن أكثرهم يجهلون) (١١١) .','532','684','0');
INSERT INTO [t22928] VALUES (777,'قال تعالى: (وكذلك جعلنا لكل نبي عدوا شياطين الإنس والجن يوحي بعضهم إلى بعض زخرف القول غرورا ولو شاء ربك ما فعلوه فذرهم وما يفترون) (١١٢) .','533','684','0');
INSERT INTO [t22928] VALUES (778,'قال تعالى: (ولتصغى إليه أفئدة الذين لا يؤمنون بالآخرة وليرضوه وليقترفوا ما هم مقترفون) (١١٣) .','534','684','0');
INSERT INTO [t22928] VALUES (779,'قال تعالى: (أفغير الله أبتغي حكما وهو الذي أنزل إليكم الكتاب مفصلا والذين آتيناهم الكتاب يعلمون أنه منزل من ربك بالحق فلا تكونن من الممترين) (١١٤) .','534','684','0');
INSERT INTO [t22928] VALUES (780,'قال تعالى: (وتمت كلمة ربك صدقا وعدلا لا مبدل لكلماته وهو السميع العليم) (١١٥) .','535','684','0');
INSERT INTO [t22928] VALUES (781,'قال تعالى: (إن ربك هو أعلم من يضل عن سبيله وهو أعلم بالمهتدين) (١١٧) .','535','684','0');
INSERT INTO [t22928] VALUES (782,'قال تعالى: (وما لكم ألا تأكلوا مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم إلا ما اضطررتم إليه وإن كثيرا ليضلون بأهوائهم بغير علم إن ربك هو أعلم بالمعتدين) (١١٩) .','536','684','0');
INSERT INTO [t22928] VALUES (783,'قال تعالى: (ولا تأكلوا مما لم يذكر اسم الله عليه وإنه لفسق وإن الشياطين ليوحون إلى أوليائهم ليجادلوكم وإن أطعتموهم إنكم لمشركون) (١٢١) .','537','684','0');
INSERT INTO [t22928] VALUES (784,'قال تعالى: (أومن كان ميتا فأحييناه وجعلنا له نورا يمشي به في الناس كمن مثله في الظلمات ليس بخارج منها كذلك زين للكافرين ما كانوا يعملون (١٢٢) وكذلك جعلنا في كل قرية أكابر مجرميها ليمكروا فيها وما يمكرون إلا بأنفسهم وما يشعرون) (١٢٣) .','537','684','0');
INSERT INTO [t22928] VALUES (785,'قال تعالى: (وإذا جاءتهم آية قالوا لن نؤمن حتى نؤتى مثل ما أوتي رسل الله الله أعلم حيث يجعل رسالته سيصيب الذين أجرموا صغار عند الله وعذاب شديد بما كانوا يمكرون) (١٢٤) .','537','684','0');
INSERT INTO [t22928] VALUES (786,'قال تعالى: (فمن يرد الله أن يهديه يشرح صدره للإسلام ومن يرد أن يضله يجعل صدره ضيقا حرجا كأنما يصعد في السماء كذلك يجعل الله الرجس على الذين لا يؤمنون) (١٢٥) .','538','684','0');
INSERT INTO [t22928] VALUES (787,'قال تعالى: (وهذا صراط ربك مستقيما قد فصلنا الآيات لقوم يذكرون) (١٢٦) .','539','684','0');
INSERT INTO [t22928] VALUES (788,'قال تعالى: (لهم دار السلام عند ربهم) (١٢٧) .','539','684','0');
INSERT INTO [t22928] VALUES (789,'قال تعالى: (ويوم يحشرهم جميعا يا معشر الجن قد استكثرتم من الإنس وقال أولياؤهم من الإنس ربنا استمتع بعضنا ببعض وبلغنا أجلنا الذي أجلت لنا قال النار مثواكم خالدين فيها إلا ما شاء الله إن ربك حكيم عليم) (١٢٨) .','539','684','0');
INSERT INTO [t22928] VALUES (790,'قال تعالى: (يا معشر الجن والإنس ألم يأتكم رسل منكم يقصون عليكم آياتي وينذرونكم لقاء يومكم هذا قالوا شهدنا على أنفسنا وغرتهم الحياة الدنيا وشهدوا على أنفسهم أنهم كانوا كافرين) (١٣٠) .','540','684','0');
INSERT INTO [t22928] VALUES (791,'قال تعالى: (ذلك أن لم يكن ربك مهلك القرى بظلم وأهلها غافلون) (١٣١) .','540','684','0');
INSERT INTO [t22928] VALUES (792,'قال تعالى: (ولكل درجات مما عملوا وما ربك بغافل عما يعملون) (١٣٢) .','540','684','0');
INSERT INTO [t22928] VALUES (793,'قال تعالى: (وربك الغني ذو الرحمة إن يشأ يذهبكم ويستخلف من بعدكم ما يشاء كما أنشأكم من ذرية قوم آخرين) (١٣٣) .','540','684','0');
INSERT INTO [t22928] VALUES (794,'قال تعالى: (إن ما توعدون لآت وما أنتم بمعجزين) (١٣٤) .','541','684','0');
INSERT INTO [t22928] VALUES (795,'قال تعالى: (قل يا قوم اعملوا على مكانتكم إني عامل فسوف تعلمون من تكون له عاقبة الدار إنه لا يفلح الظالمون) (١٣٥) .','541','684','0');
INSERT INTO [t22928] VALUES (796,'قال تعالى: (وجعلوا لله مما ذرأ من الحرث والأنعام نصيبا فقالوا هذا لله بزعمهم وهذا لشركائنا فما كان لشركائهم فلا يصل إلى الله وما كان لله فهو يصل إلى شركائهم ساء ما يحكمون) (١٣٦) .','541','684','0');
INSERT INTO [t22928] VALUES (797,'قال تعالى: (وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون) (١٣٧) .','541','684','0');
INSERT INTO [t22928] VALUES (798,'قال تعالى: (وقالوا هذه أنعام وحرث حجر لا يطعمها إلا من نشاء بزعمهم وأنعام حرمت ظهورها وأنعام لا يذكرون اسم الله عليها افتراء عليه سيجزيهم بما كانوا يفترون) (١٣٨) .','542','684','0');
INSERT INTO [t22928] VALUES (799,'قال تعالى: (وقالوا ما في بطون هذه الأنعام خالصة لذكورنا ومحرم على أزواجنا وإن يكن ميتة فهم فيه شركاء سيجزيهم وصفهم إنه حكيم عليم) (١٣٩) .','543','684','0');
INSERT INTO [t22928] VALUES (800,'قال تعالى: (قد خسر الذين قتلوا أولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله قد ضلوا وما كانوا مهتدين) (١٤٠) .','544','684','0');
INSERT INTO [t22928] VALUES (801,'قال تعالى: (وهو الذي أنشأ جنات معروشات وغير معروشات والنخل والزرع مختلفا أكله والزيتون والرمان متشابها وغير متشابه كلوا من ثمره إذا أثمر وآتوا حقه يوم حصاده ولا تسرفوا إنه لا يحب المسرفين) (١٤١) .','544','684','0');
INSERT INTO [t22928] VALUES (802,'قال تعالى: (ومن الأنعام حمولة وفرشا كلوا مما رزقكم الله) (١٤٢) .','544','684','0');
INSERT INTO [t22928] VALUES (803,'قال تعالى: (ثمانية أزواج من الضأن اثنين ومن المعز اثنين قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين نبئوني بعلم إن كنتم صادقين) (١٤٣) .','545','684','0');
INSERT INTO [t22928] VALUES (804,'قال تعالى: (ومن الإبل اثنين ومن البقر اثنين قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين أم كنتم شهداء إذ وصاكم الله بهذا فمن أظلم ممن افترى على الله كذبا ليضل الناس بغير علم إن الله لا يهدي القوم الظالمين) (١٤٤) .','545','684','0');
INSERT INTO [t22928] VALUES (805,'قال تعالى: (قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير فإنه رجس أو فسقا أهل لغير الله به فمن اضطر غير باغ ولا عاد فإن ربك غفور رحيم) (١٤٥) .','545','684','0');
INSERT INTO [t22928] VALUES (806,'قال تعالى: (وعلى الذين هادوا حرمنا كل ذي ظفر ومن البقر والغنم حرمنا عليهم شحومهما إلا ما حملت ظهورهما أو الحوايا أو ما اختلط بعظم ذلك جزيناهم ببغيهم وإنا لصادقون) (١٤٦) .','546','684','0');
INSERT INTO [t22928] VALUES (807,'قال تعالى: (فإن كذبوك فقل ربكم ذو رحمة واسعة ولا يرد بأسه عن القوم المجرمين) (١٤٧) .','547','684','0');
INSERT INTO [t22928] VALUES (808,'قال تعالى: (سيقول الذين أشركوا لو شاء الله ما أشركنا ولا آباؤنا ولا حرمنا من شيء كذلك كذب الذين من قبلهم حتى ذاقوا بأسنا قل هل عندكم من علم فتخرجوه لنا إن تتبعون إلا الظن وإن أنتم إلا تخرصون) (١٤٨) .','547','684','0');
INSERT INTO [t22928] VALUES (809,'قال تعالى: (قل هلم شهداءكم الذين يشهدون أن الله حرم هذا فإن شهدوا فلا تشهد معهم ولا تتبع أهواء الذين كذبوا بآياتنا والذين لا يؤمنون بالآخرة وهم بربهم يعدلون) (١٥٠) .','547','684','0');
INSERT INTO [t22928] VALUES (810,'قال تعالى: (قل تعالوا أتل ما حرم ربكم عليكم ألا تشركوا به شيئا وبالوالدين إحسانا ولا تقتلوا أولادكم من إملاق نحن نرزقكم وإياهم ولا تقربوا الفواحش ما ظهر منها وما بطن ولا تقتلوا النفس التي حرم الله إلا بالحق ذلكم وصاكم به لعلكم تعقلون) (١٥١) .','548','684','0');
INSERT INTO [t22928] VALUES (811,'قال تعالى: (ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده وأوفوا الكيل والميزان بالقسط لا نكلف نفسا إلا وسعها وإذا قلتم فاعدلوا ولو كان ذا قربى وبعهد الله أوفوا ذلكم وصاكم به لعلكم تذكرون) (١٥٢) .','549','684','0');
INSERT INTO [t22928] VALUES (812,'قال تعالى: (وأن هذا صراطي مستقيما فاتبعوه ولا تتبعوا السبل فتفرق بكم عن سبيله ذلكم وصاكم به لعلكم تتقون) (٥٣) .','550','684','0');
INSERT INTO [t22928] VALUES (813,'قال تعالى: (ثم آتينا موسى الكتاب تماما على الذي أحسن وتفصيلا لكل شيء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون) (١٥٤) .','551','684','0');
INSERT INTO [t22928] VALUES (814,'قال تعالى: (وهذا كتاب أنزلناه مبارك فاتبعوه واتقوا لعلكم ترحمون) (١٥٥) .','551','684','0');
INSERT INTO [t22928] VALUES (815,'قال تعالى: (أن تقولوا إنما أنزل الكتاب على طائفتين من قبلنا وإن كنا عن دراستهم لغافلين) (١٥٦) .','551','684','0');
INSERT INTO [t22928] VALUES (816,'قال تعالى: (أو تقولوا لو أنا أنزل علينا الكتاب لكنا أهدى منهم فقد جاءكم بينة من ربكم وهدى ورحمة فمن أظلم ممن كذب بآيات الله وصدف عنها سنجزي الذين يصدفون عن آياتنا سوء العذاب بما كانوا يصدفون) (١٥٧) .','552','684','0');
INSERT INTO [t22928] VALUES (817,'قال تعالى: (هل ينظرون إلا أن تأتيهم الملائكة أو يأتي ربك أو يأتي بعض آيات ربك يوم يأتي بعض آيات ربك لا ينفع نفسا إيمانها لم تكن آمنت من قبل أو كسبت في إيمانها خيرا قل انتظروا إنا منتظرون) (١٥٨) .','552','684','0');
INSERT INTO [t22928] VALUES (818,'قال تعالى: (إن الذين فرقوا دينهم وكانوا شيعا لست منهم في شيء إنما أمرهم إلى الله ثم ينبئهم بما كانوا يفعلون) (١٥٩) .','553','684','0');
INSERT INTO [t22928] VALUES (819,'قال تعالى: (من جاء بالحسنة فله عشر أمثالها ومن جاء بالسيئة فلا يجزى إلا مثلها وهم لا يظلمون) (١٦٠) .','553','684','0');
INSERT INTO [t22928] VALUES (820,'قال تعالى: (قل إنني هداني ربي إلى صراط مستقيم دينا قيما ملة إبراهيم حنيفا وما كان من المشركين) (١٦١) .','554','684','0');
INSERT INTO [t22928] VALUES (821,'قال تعالى: (قل إن صلاتي ونسكي ومحياي ومماتي لله رب العالمين) (١٦٣) .','554','684','0');
INSERT INTO [t22928] VALUES (822,'قال تعالى: (قل أغير الله أبغي ربا وهو رب كل شيء) [الأنعام: ١٦٤] .','554','684','0');
INSERT INTO [t22928] VALUES (823,'قال تعالى: (وهو الذي جعلكم خلائف الأرض ورفع بعضكم فوق بعض درجات ليبلوكم في ما آتاكم إن ربك سريع العقاب وإنه لغفور رحيم) (١٦٥) .','555','684','0');
INSERT INTO [t22928] VALUES (824,'الأعراف','556','0','0');
INSERT INTO [t22928] VALUES (825,'قال تعالى: (المص (١) كتاب أنزل إليك فلا يكن في صدرك حرج منه لتنذر به وذكرى للمؤمنين) (٢) .','556','824','0');
INSERT INTO [t22928] VALUES (826,'قال تعالى: (اتبعوا ما أنزل إليكم من ربكم ولا تتبعوا من دونه أولياء قليلا ما تذكرون) (٣) .','557','824','0');
INSERT INTO [t22928] VALUES (827,'قال تعالى: (وكم من قرية أهلكناها فجاءها بأسنا بياتا أو هم قائلون) (٤) .','557','824','0');
INSERT INTO [t22928] VALUES (828,'قال تعالى: (فما كان دعواهم إذ جاءهم بأسنا إلا أن قالوا إنا كنا ظالمين) (٥) .','558','824','0');
INSERT INTO [t22928] VALUES (829,'قال تعالى: (فلنقصن عليهم بعلم وما كنا غائبين) (٧) .','558','824','0');
INSERT INTO [t22928] VALUES (830,'قال تعالى: (والوزن يومئذ الحق فمن ثقلت موازينه فأولئك هم المفلحون) (٨) .','558','824','0');
INSERT INTO [t22928] VALUES (831,'قال تعالى: (ومن خفت موازينه فأولئك الذين خسروا أنفسهم بما كانوا بآياتنا يظلمون) (٩) .','558','824','0');
INSERT INTO [t22928] VALUES (832,'قال تعالى {ولقد مكناكم في الأرض وجعلنا لكم فيها معايش قليلا ما تشكرون}','559','824','0');
INSERT INTO [t22928] VALUES (833,'قال تعالى: (ولقد خلقناكم ثم صورناكم ثم قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس لم يكن من الساجدين) (١١) .','559','824','0');
INSERT INTO [t22928] VALUES (834,'قال تعالى: (قال ما منعك ألا تسجد إذ أمرتك قال أنا خير منه خلقتني من نار وخلقته من طين) (١٢) .','559','824','0');
INSERT INTO [t22928] VALUES (835,'قال تعالى: (قال فاهبط منها فما يكون لك أن تتكبر فيها فاخرج إنك من الصاغرين) (١٣) .','560','824','0');
INSERT INTO [t22928] VALUES (836,'قال تعالى: (قال فبما أغويتني لأقعدن لهم صراطك المستقيم) (١٦) .','560','824','0');
INSERT INTO [t22928] VALUES (837,'قال تعالى: (ثم لآتينهم من بين أيديهم ومن خلفهم وعن أيمانهم وعن شمائلهم ولا تجد أكثرهم شاكرين) (١٧) .','560','824','0');
INSERT INTO [t22928] VALUES (838,'قال تعالى: (قال اخرج منها مذءوما مدحورا لمن تبعك منهم لأملأن جهنم منكم أجمعين) (١٨) .','560','824','0');
INSERT INTO [t22928] VALUES (839,'قال تعالى: (ويا آدم اسكن أنت وزوجك الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين) (١٩) .','561','824','0');
INSERT INTO [t22928] VALUES (840,'قال تعالى: (فوسوس لهما الشيطان ليبدي لهما ما ووري عنهما من سوآتهما وقال ما نهاكما ربكما عن هذه الشجرة إلا أن تكونا ملكين أو تكونا من الخالدين) (٢٠) .','561','824','0');
INSERT INTO [t22928] VALUES (841,'قال تعالى: (وقاسمهما إني لكما لمن الناصحين) (٢١) .','561','824','0');
INSERT INTO [t22928] VALUES (842,'قال تعالى: (فدلاهما بغرور فلما ذاقا الشجرة بدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة وناداهما ربهما ألم أنهكما عن تلكما الشجرة وأقل لكما إن الشيطان لكما عدو مبين) (٢٢) .','562','824','0');
INSERT INTO [t22928] VALUES (843,'قال تعالى: (قال فيها تحيون وفيها تموتون ومنها تخرجون) (٢٥) .','562','824','0');
INSERT INTO [t22928] VALUES (844,'قال تعالى: (يا بني آدم قد أنزلنا عليكم لباسا يواري سوآتكم وريشا ولباس التقوى ذلك خير ذلك من آيات الله لعلهم يذكرون) (٢٦) .','563','824','0');
INSERT INTO [t22928] VALUES (845,'قال تعالى: (يا بني آدم لا يفتننكم الشيطان كما أخرج أبويكم من الجنة ينزع عنهما لباسهما ليريهما سوآتهما إنه يراكم هو وقبيله من حيث لا ترونهم إنا جعلنا الشياطين أولياء للذين لا يؤمنون) (٢٧) .','563','824','0');
INSERT INTO [t22928] VALUES (846,'قال تعالى: (قل أمر ربي بالقسط وأقيموا وجوهكم عند كل مسجد وادعوه مخلصين له الدين كما بدأكم تعودون) (٢٩) (فريقا هدى وفريقا حق عليهم الضلالة إنهم اتخذوا الشياطين أولياء من دون الله ويحسبون أنهم مهتدون) (٣٠) .','564','824','0');
INSERT INTO [t22928] VALUES (847,'قال تعالى: (يا بني آدم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا إنه لا يحب المسرفين) (٣١) .','565','824','0');
INSERT INTO [t22928] VALUES (848,'قال تعالى: (قل من حرم زينة الله التي أخرج لعباده والطيبات من الرزق قل هي للذين آمنوا في الحياة الدنيا خالصة يوم القيامة كذلك نفصل الآيات لقوم يعلمون) (٣٢) .','565','824','0');
INSERT INTO [t22928] VALUES (849,'قال تعالى: (قل إنما حرم ربي الفواحش ما ظهر منها وما بطن والإثم والبغي بغير الحق وأن تشركوا بالله ما لم ينزل به سلطانا وأن تقولوا على الله ما لا تعلمون) (٣٣) .','566','824','0');
INSERT INTO [t22928] VALUES (850,'قال تعالى: (ولكل أمة أجل فإذا جاء أجلهم لا يستأخرون ساعة ولا يستقدمون) (٣٤) .','566','824','0');
INSERT INTO [t22928] VALUES (851,'قال تعالى: (يا بني آدم إما يأتينكم رسل منكم يقصون عليكم آياتي فمن اتقى وأصلح فلا خوف عليهم ولا هم يحزنون) (٣٥) .','567','824','0');
INSERT INTO [t22928] VALUES (852,'قال تعالى: (فمن أظلم ممن افترى على الله كذبا أو كذب بآياته أولئك ينالهم نصيبهم من الكتاب حتى إذا جاءتهم رسلنا يتوفونهم قالوا أين ما كنتم تدعون من دون الله قالوا ضلوا عنا وشهدوا على أنفسهم أنهم كانوا كافرين) (٣٧) .','567','824','0');
INSERT INTO [t22928] VALUES (853,'قال تعالى: (قال ادخلوا في أمم قد خلت من قبلكم من الجن والإنس في النار كلما دخلت أمة لعنت أختها حتى إذا اداركوا فيها جميعا قالت أخراهم لأولاهم ربنا هؤلاء أضلونا فآتهم عذابا ضعفا من النار قال لكل ضعف ولكن لا تعلمون) (٣٨) .','567','824','0');
INSERT INTO [t22928] VALUES (854,'قال تعالى: (إن الذين كذبوا بآياتنا واستكبروا عنها لا تفتح لهم أبواب السماء ولا يدخلون الجنة حتى يلج الجمل في سم الخياط وكذلك نجزي المجرمين) (٤٠) .','568','824','0');
INSERT INTO [t22928] VALUES (855,'قال تعالى: (لهم من جهنم مهاد ومن فوقهم غواش وكذلك نجزي الظالمين) (٤١) .','569','824','0');
INSERT INTO [t22928] VALUES (856,'قال تعالى: (والذين آمنوا وعملوا الصالحات لا نكلف نفسا إلا وسعها أولئك أصحاب الجنة هم فيها خالدون) (٤٢) .','569','824','0');
INSERT INTO [t22928] VALUES (857,'قال تعالى: (ونزعنا ما في صدورهم من غل تجري من تحتهم الأنهار وقالوا الحمد لله الذي هدانا لهذا وما كنا لنهتدي لولا أن هدانا الله لقد جاءت رسل ربنا بالحق ونودوا أن تلكم الجنة أورثتموها بما كنتم تعملون) (٤٣) .','569','824','0');
INSERT INTO [t22928] VALUES (858,'قال تعالى: (ونادى أصحاب الجنة أصحاب النار أن قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا قالوا نعم فأذن مؤذن بينهم أن لعنة الله على الظالمين) (٤٤) .','571','824','0');
INSERT INTO [t22928] VALUES (859,'قال تعالى: (الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالآخرة كافرون) (٤٥) .','572','824','0');
INSERT INTO [t22928] VALUES (860,'قال تعالى: (وبينهما حجاب وعلى الأعراف رجال يعرفون كلا بسيماهم ونادوا أصحاب الجنة أن سلام عليكم لم يدخلوها وهم يطمعون) (٤٦) .','572','824','0');
INSERT INTO [t22928] VALUES (861,'قال تعالى: (وإذا صرفت أبصارهم تلقاء أصحاب النار قالوا ربنا لا تجعلنا مع القوم الظالمين) (٤٧) .','572','824','0');
INSERT INTO [t22928] VALUES (862,'قال تعالى: (ونادى أصحاب الأعراف رجالا يعرفونهم بسيماهم قالوا ما أغنى عنكم جمعكم وما كنتم تستكبرون) (٤٨) .','573','824','0');
INSERT INTO [t22928] VALUES (863,'قال تعالى: (أهؤلاء الذين أقسمتم لا ينالهم الله برحمة ادخلوا الجنة لا خوف عليكم ولا أنتم تحزنون) (٤٩) .','573','824','0');
INSERT INTO [t22928] VALUES (864,'قال تعالى: (ونادى أصحاب النار أصحاب الجنة أن أفيضوا علينا من الماء أو مما رزقكم الله قالوا إن الله حرمهما على الكافرين) (٥٠) .','573','824','0');
INSERT INTO [t22928] VALUES (865,'قال تعالى: (الذين اتخذوا دينهم لهوا ولعبا وغرتهم الحياة الدنيا فاليوم ننساهم كما نسوا لقاء يومهم هذا وما كانوا بآياتنا يجحدون) (٥١) .','574','824','0');
INSERT INTO [t22928] VALUES (866,'قال تعالى: (ولقد جئناهم بكتاب فصلناه على علم هدى ورحمة لقوم يؤمنون) (٥٢) .','574','824','0');
INSERT INTO [t22928] VALUES (867,'قال تعالى: (هل ينظرون إلا تأويله يوم يأتي تأويله يقول الذين نسوه من قبل قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا لنا أو نرد فنعمل غير الذي كنا نعمل قد خسروا أنفسهم وضل عنهم ما كانوا يفترون) (٥٣) .','574','824','0');
INSERT INTO [t22928] VALUES (868,'قال تعالى: (إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يغشي الليل النهار يطلبه حثيثا والشمس والقمر والنجوم مسخرات بأمره ألا له الخلق والأمر تبارك الله رب العالمين) (٥٤) .','575','824','0');
INSERT INTO [t22928] VALUES (869,'قال تعالى: (ادعوا ربكم تضرعا وخفية إنه لا يحب المعتدين) (٥٥) .','575','824','0');
INSERT INTO [t22928] VALUES (870,'قال تعالى: (ولا تفسدوا في الأرض بعد إصلاحها وادعوه خوفا وطمعا إن رحمة الله قريب من المحسنين) (٥٦) .','576','824','0');
INSERT INTO [t22928] VALUES (871,'قال تعالى: (وهو الذي يرسل الرياح بشرا بين يدي رحمته حتى إذا أقلت سحابا ثقالا سقناه لبلد ميت فأنزلنا به الماء فأخرجنا به من كل الثمرات كذلك نخرج الموتى لعلكم تذكرون) (٥٧) .','576','824','0');
INSERT INTO [t22928] VALUES (872,'قال تعالى: (والبلد الطيب يخرج نباته بإذن ربه والذي خبث لا يخرج إلا نكدا كذلك نصرف الآيات لقوم يشكرون) (٥٨) .','577','824','0');
INSERT INTO [t22928] VALUES (873,'قال تعالى: (لقد أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غيره إني أخاف عليكم عذاب يوم عظيم) (٥٩) .','578','824','0');
INSERT INTO [t22928] VALUES (874,'قال تعالى: (قال الملأ من قومه إنا لنراك في ضلال مبين) (٦٠) .','578','824','0');
INSERT INTO [t22928] VALUES (875,'قال تعالى: (قال يا قوم ليس بي ضلالة ولكني رسول من رب العالمين (٦١) أبلغكم رسالات ربي وأنصح لكم وأعلم من الله ما لا تعلمون) (٦٢) .','578','824','0');
INSERT INTO [t22928] VALUES (876,'قال تعالى: (أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم ولتتقوا ولعلكم ترحمون) (٦٣) .','579','824','0');
INSERT INTO [t22928] VALUES (877,'قال تعالى: (فكذبوه فأنجيناه والذين معه في الفلك وأغرقنا الذين كذبوا بآياتنا إنهم كانوا قوما عمين) (٦٤) .','579','824','0');
INSERT INTO [t22928] VALUES (878,'قال تعالى: (وإلى عاد أخاهم هودا قال يا قوم اعبدوا الله ما لكم من إله غيره أفلا تتقون) (٦٥) .','579','824','0');
INSERT INTO [t22928] VALUES (879,'قال تعالى: (أبلغكم رسالات ربي وأنا لكم ناصح أمين) (٦٨) .','579','824','0');
INSERT INTO [t22928] VALUES (880,'قال تعالى: (أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم واذكروا إذ جعلكم خلفاء من بعد قوم نوح وزادكم في الخلق بسطة فاذكروا آلاء الله لعلكم تفلحون) (٦٩) .','580','824','0');
INSERT INTO [t22928] VALUES (881,'قال تعالى: (قالوا أجئتنا لنعبد الله وحده ونذر ما كان يعبد آباؤنا فأتنا بما تعدنا إن كنت من الصادقين) (٧٠) .','580','824','0');
INSERT INTO [t22928] VALUES (882,'قال تعالى: (قال قد وقع عليكم من ربكم رجس وغضب أتجادلونني في أسماء سميتموها أنتم وآباؤكم ما نزل الله بها من سلطان فانتظروا إني معكم من المنتظرين) (٧١) .','580','824','0');
INSERT INTO [t22928] VALUES (883,'قال تعالى: (وإلى ثمود أخاهم صالحا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بينة من ربكم هذه ناقة الله لكم آية فذروها تأكل في أرض الله ولا تمسوها بسوء فيأخذكم عذاب أليم) (٧٣) .','580','824','0');
INSERT INTO [t22928] VALUES (884,'قال تعالى: (واذكروا إذ جعلكم خلفاء من بعد عاد وبوأكم في الأرض تتخذون من سهولها قصورا وتنحتون الجبال بيوتا فاذكروا آلاء الله ولا تعثوا في الأرض مفسدين) (٧٤) .','581','824','0');
INSERT INTO [t22928] VALUES (885,'قال تعالى: (قال الملأ الذين استكبروا من قومه للذين استضعفوا لمن آمن منهم أتعلمون أن صالحا مرسل من ربه قالوا إنا بما أرسل به مؤمنون) (٧٥) .','581','824','0');
INSERT INTO [t22928] VALUES (886,'قال تعالى: (فأخذتهم الرجفة فأصبحوا في دارهم جاثمين) (٧٨) .','582','824','0');
INSERT INTO [t22928] VALUES (887,'قال تعالى: (ولوطا إذ قال لقومه أتأتون الفاحشة ما سبقكم بها من أحد من العالمين إنكم لتأتون الرجال شهوة من دون النساء بل أنتم قوم مسرفون) (٨١) .','582','824','0');
INSERT INTO [t22928] VALUES (888,'قال تعالى: (وما كان جواب قومه إلا أن قالوا أخرجوهم من قريتكم إنهم أناس يتطهرون) (٨٢) .','582','824','0');
INSERT INTO [t22928] VALUES (889,'قال تعالى: (وأمطرنا عليهم مطرا فانظر كيف كان عاقبة المجرمين) (٨٤) .','583','824','0');
INSERT INTO [t22928] VALUES (890,'قال تعالى: (وإلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بينة من ربكم فأوفوا الكيل والميزان ولا تبخسوا الناس أشياءهم ولا تفسدوا في الأرض بعد إصلاحها ذلكم خير لكم إن كنتم مؤمنين) (٨٥) .','583','824','0');
INSERT INTO [t22928] VALUES (891,'قال تعالى: (ولا تقعدوا بكل صراط توعدون وتصدون عن سبيل الله من آمن به وتبغونها عوجا واذكروا إذ كنتم قليلا فكثركم وانظروا كيف كان عاقبة المفسدين) (٨٦) .','583','824','0');
INSERT INTO [t22928] VALUES (892,'قال تعالى: (قال الملأ الذين استكبروا من قومه لنخرجنك يا شعيب والذين آمنوا معك من قريتنا أو لتعودن في ملتنا قال أولو كنا كارهين) (٨٨) .','583','824','0');
INSERT INTO [t22928] VALUES (893,'قال تعالى: (قد افترينا على الله كذبا إن عدنا في ملتكم بعد إذ نجانا الله منها وما يكون لنا أن نعود فيها إلا أن يشاء الله ربنا وسع ربنا كل شيء علما على الله توكلنا ربنا افتح بيننا وبين قومنا بالحق وأنت خير الفاتحين) (٨٩) .','583','824','0');
INSERT INTO [t22928] VALUES (894,'قال تعالى: (وقال الملأ الذين كفروا من قومه لئن اتبعتم شعيبا إنكم إذا لخاسرون) (٩٠) .','584','824','0');
INSERT INTO [t22928] VALUES (895,'قال تعالى: (الذين كذبوا شعيبا كأن لم يغنوا فيها الذين كذبوا شعيبا كانوا هم الخاسرين) (٩٢) .','584','824','0');
INSERT INTO [t22928] VALUES (896,'قال تعالى: (ثم بدلنا مكان السيئة الحسنة حتى عفوا وقالوا قد مس آباءنا الضراء والسراء فأخذناهم بغتة وهم لا يشعرون) (٩٥) .','585','824','0');
INSERT INTO [t22928] VALUES (897,'قال تعالى: (أفأمن أهل القرى أن يأتيهم بأسنا بياتا وهم نائمون أوأمن أهل القرى أن يأتيهم بأسنا ضحى وهم يلعبون) (٩٨) .','585','824','0');
INSERT INTO [t22928] VALUES (898,'قال تعالى: (أفأمنوا مكر الله فلا يأمن مكر الله إلا القوم الخاسرون) (٩٩) .','585','824','0');
INSERT INTO [t22928] VALUES (899,'قال تعالى: (أولم يهد للذين يرثون الأرض من بعد أهلها أن لو نشاء أصبناهم بذنوبهم ونطبع على قلوبهم فهم لا يسمعون) (١٠٠) .','585','824','0');
INSERT INTO [t22928] VALUES (900,'قال تعالى: (تلك القرى نقص عليك من أنبائها ولقد جاءتهم رسلهم بالبينات فما كانوا ليؤمنوا بما كذبوا من قبل كذلك يطبع الله على قلوب الكافرين) (١٠١) .','585','824','0');
INSERT INTO [t22928] VALUES (901,'قال تعالى: (وما وجدنا لأكثرهم من عهد وإن وجدنا أكثرهم لفاسقين) (١٠٢) .','586','824','0');
INSERT INTO [t22928] VALUES (902,'قال تعالى {ثم بعثنا من بعدهم موسى بآياتنا إلى فرعون وملئه فظلموا بها فانظر كيف كان عاقبة المفسدين}','586','824','0');
INSERT INTO [t22928] VALUES (903,'قال تعالى: (حقيق على أن لا أقول على الله إلا الحق قد جئتكم ببينة من ربكم فأرسل معي بني إسرائيل) (١٠٥) .','586','824','0');
INSERT INTO [t22928] VALUES (904,'قال تعالى: (فألقى عصاه فإذا هي ثعبان مبين) (١٠٧) .','587','824','0');
INSERT INTO [t22928] VALUES (905,'قال تعالى: (يريد أن يخرجكم من أرضكم فماذا تأمرون. قالوا أرجه وأخاه وأرسل في المدائن حاشرين) .','587','824','0');
INSERT INTO [t22928] VALUES (906,'قال تعالى: (يأتوك بكل ساحر عليم) (١١٢) .','588','824','0');
INSERT INTO [t22928] VALUES (907,'قال تعالى: (وجاء السحرة فرعون قالوا إن لنا لأجرا إن كنا نحن الغالبين) (١١٣) .','588','824','0');
INSERT INTO [t22928] VALUES (908,'قال تعالى: (قالوا يا موسى إما أن تلقي وإما أن نكون نحن الملقين) (١١٥) .','588','824','0');
INSERT INTO [t22928] VALUES (909,'قال تعالى: (قال ألقوا فلما ألقوا سحروا أعين الناس واسترهبوهم وجاءوا بسحر عظيم) (١١٦) .','589','824','0');
INSERT INTO [t22928] VALUES (910,'قال تعالى: (وأوحينا إلى موسى أن ألق عصاك فإذا هي تلقف ما يأفكون) (١١٧) .','589','824','0');
INSERT INTO [t22928] VALUES (911,'قال تعالى: (قالوا آمنا برب العالمين) (١٢١) (رب موسى وهارون) (١٢٢) .','589','824','0');
INSERT INTO [t22928] VALUES (912,'قال تعالى: (قال فرعون آمنتم به قبل أن آذن لكم إن هذا لمكر مكرتموه في المدينة لتخرجوا منها أهلها فسوف تعلمون) (١٢٣) .','589','824','0');
INSERT INTO [t22928] VALUES (913,'قال تعالى: (وما تنقم منا إلا أن آمنا بآيات ربنا لما جاءتنا ربنا أفرغ علينا صبرا وتوفنا مسلمين) (١٢٦) .','590','824','0');
INSERT INTO [t22928] VALUES (914,'قال تعالى: (وقال الملأ من قوم فرعون أتذر موسى وقومه ليفسدوا في الأرض ويذرك وآلهتك قال سنقتل أبناءهم ونستحيي نساءهم وإنا فوقهم قاهرون) (١٢٧) .','590','824','0');
INSERT INTO [t22928] VALUES (915,'قال تعالى: (قال موسى لقومه استعينوا بالله واصبروا إن الأرض لله يورثها من يشاء من عباده والعاقبة للمتقين) (١٢٨) .','590','824','0');
INSERT INTO [t22928] VALUES (916,'قال تعالى: (ولقد أخذنا آل فرعون بالسنين ونقص من الثمرات لعلهم يذكرون) (١٣٠) .','590','824','0');
INSERT INTO [t22928] VALUES (917,'قال تعالى: (فإذا جاءتهم الحسنة قالوا لنا هذه وإن تصبهم سيئة يطيروا بموسى ومن معه ألا إنما طائرهم عند الله ولكن أكثرهم لا يعلمون) (١٣٠) .','591','824','0');
INSERT INTO [t22928] VALUES (918,'قال تعالى: (وقالوا مهما تأتنا به من آية لتسحرنا بها فما نحن لك بمؤمنين) (١٣٢) .','591','824','0');
INSERT INTO [t22928] VALUES (919,'قال تعالى: (فأرسلنا عليهم الطوفان والجراد والقمل والضفادع والدم آيات مفصلات فاستكبروا وكانوا قوما مجرمين) (١٣٣) .','591','824','0');
INSERT INTO [t22928] VALUES (920,'قال تعالى: (ولما وقع عليهم الرجز قالوا يا موسى ادع لنا ربك بما عهد عندك لئن كشفت عنا الرجز لنؤمنن لك ولنرسلن معك بني إسرائيل) (١٣٤) .','592','824','0');
INSERT INTO [t22928] VALUES (921,'قال تعالى: (فلما كشفنا عنهم الرجز إلى أجل هم بالغوه إذا هم ينكثون) (١٣٥) .','592','824','0');
INSERT INTO [t22928] VALUES (922,'قال تعالى: (وأورثنا القوم الذين كانوا يستضعفون مشارق الأرض ومغاربها التي باركنا فيها وتمت كلمة ربك الحسنى على بني إسرائيل بما صبروا ودمرنا ما كان يصنع فرعون وقومه وما كانوا يعرشون) (١٣٧) .','592','824','0');
INSERT INTO [t22928] VALUES (923,'قال تعالى: (وجاوزنا ببني إسرائيل البحر فأتوا على قوم يعكفون على أصنام لهم قالوا يا موسى اجعل لنا إلها كما لهم آلهة قال إنكم قوم تجهلون) (١٣٨) .','593','824','0');
INSERT INTO [t22928] VALUES (924,'قال تعالى: (إن هؤلاء متبر ما هم فيه وباطل ما كانوا يعملون) (١٣٩) .','594','824','0');
INSERT INTO [t22928] VALUES (925,'قال تعالى: (قال أغير الله أبغيكم إلها وهو فضلكم على العالمين) (١٤٠) .','594','824','0');
INSERT INTO [t22928] VALUES (926,'قال تعالى: (وواعدنا موسى ثلاثين ليلة وأتممناها بعشر فتم ميقات ربه أربعين ليلة وقال موسى لأخيه هارون اخلفني في قومي وأصلح ولا تتبع سبيل المفسدين) (١٤٢) .','594','824','0');
INSERT INTO [t22928] VALUES (927,'قال تعالى: (ولما جاء موسى لميقاتنا وكلمه ربه قال رب أرني أنظر إليك قال لن تراني ولكن انظر إلى الجبل فإن استقر مكانه فسوف تراني فلما تجلى ربه للجبل جعله دكا وخر موسى صعقا فلما أفاق قال سبحانك تبت إليك وأنا أول المؤمنين) (١٤٣) .','594','824','0');
INSERT INTO [t22928] VALUES (928,'قال تعالى: (وكتبنا له في الألواح من كل شيء موعظة وتفصيلا لكل شيء فخذها بقوة وأمر قومك يأخذوا بأحسنها سأريكم دار الفاسقين) (١٤٥) .','595','824','0');
INSERT INTO [t22928] VALUES (929,'قال تعالى: (سأصرف عن آياتي الذين يتكبرون في الأرض بغير الحق وإن يروا كل آية لا يؤمنوا بها وإن يروا سبيل الرشد لا يتخذوه سبيلا وإن يروا سبيل الغي يتخذوه سبيلا ذلك بأنهم كذبوا بآياتنا وكانوا عنها غافلين) (١٤٦) .','595','824','0');
INSERT INTO [t22928] VALUES (930,'قال تعالى: (والذين كذبوا بآياتنا ولقاء الآخرة حبطت أعمالهم هل يجزون إلا ما كانوا يعملون) (١٤٧) .','595','824','0');
INSERT INTO [t22928] VALUES (931,'قال تعالى: (واتخذ قوم موسى من بعده من حليهم عجلا جسدا له خوار ألم يروا أنه لا يكلمهم ولا يهديهم سبيلا اتخذوه وكانوا ظالمين) (١٤٨) .','595','824','0');
INSERT INTO [t22928] VALUES (932,'قال تعالى: (ولما سقط في أيديهم ورأوا أنهم قد ضلوا قالوا لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخاسرين) (١٤٩) .','596','824','0');
INSERT INTO [t22928] VALUES (933,'قال تعالى: (ولما رجع موسى إلى قومه غضبان أسفا قال بئسما خلفتموني من بعدي أعجلتم أمر ربكم وألقى الألواح وأخذ برأس أخيه يجره إليه قال ابن أم إن القوم استضعفوني وكادوا يقتلونني فلا تشمت بي الأعداء ولا تجعلني مع القوم الظالمين) (١٥٠) .','596','824','0');
INSERT INTO [t22928] VALUES (934,'قال تعالى: (والذين عملوا السيئات ثم تابوا من بعدها وآمنوا إن ربك من بعدها لغفور رحيم) (١٣٥) .','597','824','0');
INSERT INTO [t22928] VALUES (935,'قال تعالى: (ولما سكت عن موسى الغضب أخذ الألواح وفي نسختها هدى ورحمة للذين هم لربهم يرهبون) (١٥٤) .','597','824','0');
INSERT INTO [t22928] VALUES (936,'قال تعالى: (واختار موسى قومه سبعين رجلا لميقاتنا فلما أخذتهم الرجفة قال رب لو شئت أهلكتهم من قبل وإياي أتهلكنا بما فعل السفهاء منا إن هي إلا فتنتك تضل بها من تشاء وتهدي من تشاء أنت ولينا فاغفر لنا وارحمنا وأنت خير الغافرين) (١٥٥) .','598','824','0');
INSERT INTO [t22928] VALUES (937,'قال تعالى: (واكتب لنا في هذه الدنيا حسنة وفي الآخرة إنا هدنا إليك قال عذابي أصيب به من أشاء ورحمتي وسعت كل شيء فسأكتبها للذين يتقون ويؤتون الزكاة والذين هم بآياتنا يؤمنون) (١٥٦) .','598','824','0');
INSERT INTO [t22928] VALUES (938,'قال تعالى: (الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات ويحرم عليهم الخبائث ويضع عنهم إصرهم والأغلال التي كانت عليهم فالذين آمنوا به وعزروه ونصروه واتبعوا النور الذي أنزل معه أولئك هم المفلحون) (١٥٧) .','598','824','0');
INSERT INTO [t22928] VALUES (939,'قال تعالى: (قل يا أيها الناس إني رسول الله إليكم جميعا الذي له ملك السماوات والأرض لا إله إلا هو يحيي ويميت فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته واتبعوه لعلكم تهتدون) (١٥٨) .','599','824','0');
INSERT INTO [t22928] VALUES (940,'قال تعالى: (وقطعناهم اثنتي عشرة أسباطا أمما وأوحينا إلى موسى إذ استسقاه قومه أن اضرب بعصاك الحجر فانبجست منه اثنتا عشرة عينا قد علم كل أناس مشربهم وظللنا عليهم الغمام وأنزلنا عليهم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا أنفسهم يظلمون) (١٦٠) .','600','824','0');
INSERT INTO [t22928] VALUES (941,'قال تعالى: (وإذ قيل لهم اسكنوا هذه القرية وكلوا منها حيث شئتم وقولوا حطة وادخلوا الباب سجدا نغفر لكم خطيئاتكم سنزيد المحسنين) (١٦١) .','600','824','0');
INSERT INTO [t22928] VALUES (942,'قال تعالى: (واسألهم عن القرية التي كانت حاضرة البحر إذ يعدون في السبت إذ تأتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تأتيهم كذلك نبلوهم بما كانوا يفسقون) (١٦٣) .','600','824','0');
INSERT INTO [t22928] VALUES (943,'قال تعالى: (وإذ قالت أمة منهم لم تعظون قوما الله مهلكهم أو معذبهم عذابا شديدا قالوا معذرة إلى ربكم ولعلهم يتقون) (١٦٤) .','601','824','0');
INSERT INTO [t22928] VALUES (944,'قال تعالى: (فلما نسوا ما ذكروا به أنجينا الذين ينهون عن السوء وأخذنا الذين ظلموا بعذاب بئيس بما كانوا يفسقون) (١٦٥) .','601','824','0');
INSERT INTO [t22928] VALUES (945,'قال تعالى: (وإذ تأذن ربك ليبعثن عليهم إلى يوم القيامة من يسومهم سوء العذاب إن ربك لسريع العقاب وإنه لغفور رحيم) (١٦٧) .','602','824','0');
INSERT INTO [t22928] VALUES (946,'قال تعالى: (وقطعناهم في الأرض أمما منهم الصالحون ومنهم دون ذلك وبلوناهم بالحسنات والسيئات لعلهم يرجعون) (١٦٨) .','603','824','0');
INSERT INTO [t22928] VALUES (947,'قال تعالى: (فخلف من بعدهم خلف ورثوا الكتاب يأخذون عرض هذا الأدنى ويقولون سيغفر لنا وإن يأتهم عرض مثله يأخذوه ألم يؤخذ عليهم ميثاق الكتاب أن لا يقولوا على الله إلا الحق ودرسوا ما فيه والدار الآخرة خير للذين يتقون أفلا تعقلون) (١٩٦) .','603','824','0');
INSERT INTO [t22928] VALUES (948,'قال تعالى: (والذين يمسكون بالكتاب وأقاموا الصلاة إنا لا نضيع أجر المصلحين) (١٩٠) .','603','824','0');
INSERT INTO [t22928] VALUES (949,'قال تعالى: (وإذ نتقنا الجبل فوقهم كأنه ظلة وظنوا أنه واقع بهم خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون) (١٧١) .','604','824','0');
INSERT INTO [t22928] VALUES (950,'قال تعالى: (وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم وأشهدهم على أنفسهم ألست بربكم قالوا بلى شهدنا أن تقولوا يوم القيامة إنا كنا عن هذا غافلين (١٧٢) أو تقولوا إنما أشرك آباؤنا من قبل وكنا ذرية من بعدهم أفتهلكنا بما فعل المبطلون) (١٧٣) .','604','824','0');
INSERT INTO [t22928] VALUES (951,'قال تعالى: (ولو شئنا لرفعناه بها ولكنه أخلد إلى الأرض واتبع هواه فمثله كمثل الكلب إن تحمل عليه يلهث أو تتركه يلهث ذلك مثل القوم الذين كذبوا بآياتنا فاقصص القصص لعلهم يتفكرون) (١٧٦) .','605','824','0');
INSERT INTO [t22928] VALUES (952,'قال تعالى: (ساء مثلا القوم الذين كذبوا بآياتنا وأنفسهم كانوا يظلمون) (١٧٧) .','605','824','0');
INSERT INTO [t22928] VALUES (953,'قال تعالى {ولقد ذرأنا لجهنم كثيرا من الجن والإنس لهم قلوب لا يفقهون بها}','605','824','0');
INSERT INTO [t22928] VALUES (954,'قال تعالى: (ولله الأسماء الحسنى فادعوه بها وذروا الذين يلحدون في أسمائه سيجزون ما كانوا يعملون) (١٨٠) .','605','824','0');
INSERT INTO [t22928] VALUES (955,'قال تعالى: (وممن خلقنا أمة يهدون بالحق وبه يعدلون) (١٨١) .','605','824','0');
INSERT INTO [t22928] VALUES (956,'قال تعالى: (والذين كذبوا بآياتنا سنستدرجهم من حيث لا يعلمون) (١٨٢) .','605','824','0');
INSERT INTO [t22928] VALUES (957,'قال تعالى: (وأملي لهم إن كيدي متين) (١٨٣) .','606','824','0');
INSERT INTO [t22928] VALUES (958,'قال تعالى: (أولم يتفكروا ما بصاحبهم من جنة إن هو إلا نذير مبين) (١٨٤) .','606','824','0');
INSERT INTO [t22928] VALUES (959,'قال تعالى: (أولم ينظروا في ملكوت السماوات والأرض وما خلق الله من شيء وأن عسى أن يكون قد اقترب أجلهم فبأي حديث بعده يؤمنون) (١٨٥) .','606','824','0');
INSERT INTO [t22928] VALUES (960,'قال تعالى: (من يضلل الله فلا هادي له ويذرهم في طغيانهم يعمهون) (١٨٦) .','606','824','0');
INSERT INTO [t22928] VALUES (961,'قال تعالى: (يسألونك عن الساعة أيان مرساها قل إنما علمها عند ربي لا يجليها لوقتها إلا هو ثقلت في السماوات والأرض لا تأتيكم إلا بغتة يسألونك كأنك حفي عنها قل إنما علمها عند الله ولكن أكثر الناس لا يعلمون) (١٨٧) .','607','824','0');
INSERT INTO [t22928] VALUES (962,'قال تعالى: (قل لا أملك لنفسي نفعا ولا ضرا إلا ما شاء الله ولو كنت أعلم الغيب لاستكثرت من الخير وما مسني السوء إن أنا إلا نذير وبشير لقوم يؤمنون) (١٨٨) .','608','824','0');
INSERT INTO [t22928] VALUES (963,'قال تعالى: (هو الذي خلقكم من نفس واحدة وجعل منها زوجها ليسكن إليها فلما تغشاها حملت حملا خفيفا فمرت به فلما أثقلت دعوا الله ربهما لئن آتيتنا صالحا لنكونن من الشاكرين) (١٨٩) .','608','824','0');
INSERT INTO [t22928] VALUES (964,'قال تعالى: (فلما آتاهما صالحا جعلا له شركاء فيما آتاهما فتعالى الله عما يشركون) (١٩٠) .','608','824','0');
INSERT INTO [t22928] VALUES (965,'قال تعالى: (وإن تدعوهم إلى الهدى لا يتبعوكم سواء عليكم أدعوتموهم أم أنتم صامتون) (١٩٣) .','608','824','0');
INSERT INTO [t22928] VALUES (966,'قال تعالى: (إن الذين تدعون من دون الله عباد أمثالكم فادعوهم فليستجيبوا لكم إن كنتم صادقين) (١٩٤) .','609','824','0');
INSERT INTO [t22928] VALUES (967,'قال تعالى: (ألهم أرجل يمشون بها أم لهم أيد يبطشون بها أم لهم أعين يبصرون بها أم لهم آذان يسمعون بها قل ادعوا شركاءكم ثم كيدون فلا تنظرون) (١٩٥) .','609','824','0');
INSERT INTO [t22928] VALUES (968,'قال تعالى: (إن وليي الله الذي نزل الكتاب وهو يتولى الصالحين) (١٩٦) .','609','824','0');
INSERT INTO [t22928] VALUES (969,'قال تعالى: (إن الذين اتقوا إذا مسهم طائف من الشيطان تذكروا فإذا هم مبصرون) (٢٠١) .','610','824','0');
INSERT INTO [t22928] VALUES (970,'قال تعالى: (وإخوانهم يمدونهم في الغي ثم لا يقصرون) (٢٠٢) .','610','824','0');
INSERT INTO [t22928] VALUES (971,'قال تعالى: (وإذا قرئ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون) (٢٠٤) .','610','824','0');
INSERT INTO [t22928] VALUES (972,'قال تعالى: (واذكر ربك في نفسك تضرعا وخيفة ودون الجهر من القول بالغدو والآصال ولا تكن من الغافلين) (٢٠٥) .','610','824','0');
INSERT INTO [t22928] VALUES (973,'الأنفال','612','0','0');
INSERT INTO [t22928] VALUES (974,'قال تعالى: (يسألونك عن الأنفال قل الأنفال لله والرسول فاتقوا الله وأصلحوا ذات بينكم وأطيعوا الله ورسوله إن كنتم مؤمنين) (١) .','612','973','0');
INSERT INTO [t22928] VALUES (975,'قال تعالى: (إنما المؤمنون الذين إذا ذكر الله وجلت قلوبهم وإذا تليت عليهم آياته زادتهم إيمانا وعلى ربهم يتوكلون) (٢) .','612','973','0');
INSERT INTO [t22928] VALUES (976,'قال تعالى: (أولئك هم المؤمنون حقا لهم درجات عند ربهم ومغفرة ورزق كريم) (٤) .','613','973','0');
INSERT INTO [t22928] VALUES (977,'قال تعالى: (كما أخرجك ربك من بيتك بالحق وإن فريقا من المؤمنين لكارهون) (٥) .','613','973','0');
INSERT INTO [t22928] VALUES (978,'قال تعالى: (وإذ يعدكم الله إحدى الطائفتين أنها لكم وتودون أن غير ذات الشوكة تكون لكم ويريد الله أن يحق الحق بكلماته ويقطع دابر الكافرين) (٧) .','614','973','0');
INSERT INTO [t22928] VALUES (979,'قال تعالى: (إذ تستغيثون ربكم فاستجاب لكم أني ممدكم بألف من الملائكة مردفين) (٩) .','614','973','0');
INSERT INTO [t22928] VALUES (980,'قال تعالى: (وما جعله الله إلا بشرى ولتطمئن به قلوبكم وما النصر إلا من عند الله إن الله عزيز حكيم) (١٠) .','615','973','0');
INSERT INTO [t22928] VALUES (981,'قال تعالى: (إذ يغشيكم النعاس أمنة منه وينزل عليكم من السماء ماء ليطهركم به ويذهب عنكم رجز الشيطان وليربط على قلوبكم ويثبت به الأقدام) (١١) .','615','973','0');
INSERT INTO [t22928] VALUES (982,'قال تعالى: (إذ يوحي ربك إلى الملائكة أني معكم فثبتوا الذين آمنوا سألقي في قلوب الذين كفروا الرعب فاضربوا فوق الأعناق واضربوا منهم كل بنان) (١٢) .','616','973','0');
INSERT INTO [t22928] VALUES (983,'قال تعالى: (ذلك بأنهم شاقوا الله ورسوله ومن يشاقق الله ورسوله فإن الله شديد العقاب) (١٣) .','616','973','0');
INSERT INTO [t22928] VALUES (984,'قال تعالى: (ذلكم فذوقوه وأن للكافرين عذاب النار) (١٤) .','616','973','0');
INSERT INTO [t22928] VALUES (985,'قال تعالى: (يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا فلا تولوهم الأدبار) (١٥) .','617','973','0');
INSERT INTO [t22928] VALUES (986,'قال تعالى: (ومن يولهم يومئذ دبره إلا متحرفا لقتال أو متحيزا إلى فئة فقد باء بغضب من الله ومأواه جهنم وبئس المصير) (١٦) .','617','973','0');
INSERT INTO [t22928] VALUES (987,'قال تعالى: (ذلكم وأن الله موهن كيد الكافرين) (١٨) .','617','973','0');
INSERT INTO [t22928] VALUES (988,'قال تعالى: (إن تستفتحوا فقد جاءكم الفتح وإن تنتهوا فهو خير لكم وإن تعودوا نعد ولن تغني عنكم فئتكم شيئا ولو كثرت وأن الله مع المؤمنين) (١٩) .','617','973','0');
INSERT INTO [t22928] VALUES (989,'قال تعالى: (إن شر الدواب عند الله الصم البكم الذين لا يعقلون) (٢٢) .','617','973','0');
INSERT INTO [t22928] VALUES (990,'قال تعالى: (واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة واعلموا أن الله شديد العقاب) .','618','973','0');
INSERT INTO [t22928] VALUES (991,'قال تعالى: (واذكروا إذ أنتم قليل مستضعفون في الأرض تخافون أن يتخطفكم الناس فآواكم وأيدكم بنصره ورزقكم من الطيبات لعلكم تشكرون) (٢٦) .','618','973','0');
INSERT INTO [t22928] VALUES (992,'قال تعالى: (يا أيها الذين آمنوا لا تخونوا الله والرسول وتخونوا أماناتكم وأنتم تعلمون) (٢٧) .','619','973','0');
INSERT INTO [t22928] VALUES (993,'قال تعالى: (وإذ يمكر بك الذين كفروا ليثبتوك أو يقتلوك أو يخرجوك ويمكرون ويمكر الله والله خير الماكرين) (٣٠) .','619','973','0');
INSERT INTO [t22928] VALUES (994,'قال تعالى: (وإذ قالوا اللهم إن كان هذا هو الحق من عندك فأمطر علينا حجارة من السماء أو ائتنا بعذاب أليم) (٣٢) .','619','973','0');
INSERT INTO [t22928] VALUES (995,'قال تعالى: (وما لهم ألا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا أولياءه إن أولياؤه إلا المتقون ولكن أكثرهم لا يعلمون) (٣٤) .','619','973','0');
INSERT INTO [t22928] VALUES (996,'قال تعالى: (وما كان صلاتهم عند البيت إلا مكاء وتصدية فذوقوا العذاب بما كنتم تكفرون) (٣٥) .','619','973','0');
INSERT INTO [t22928] VALUES (997,'قال تعالى: (ليميز الله الخبيث من الطيب ويجعل الخبيث بعضه على بعض فيركمه جميعا فيجعله في جهنم أولئك هم الخاسرون) (٣٧) .','620','973','0');
INSERT INTO [t22928] VALUES (998,'قال تعالى: (وإن تولوا فاعلموا أن الله مولاكم نعم المولى ونعم النصير) (٤٠) .','620','973','0');
INSERT INTO [t22928] VALUES (999,'قال تعالى: (واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل إن كنتم آمنتم بالله وما أنزلنا على عبدنا يوم الفرقان يوم التقى الجمعان والله على كل شيء قدير) (٤٦) .','620','973','0');
INSERT INTO [t22928] VALUES (1000,'قال تعالى: (إذ أنتم بالعدوة الدنيا وهم بالعدوة القصوى والركب أسفل منكم ولو تواعدتم لاختلفتم في الميعاد ولكن ليقضي الله أمرا كان مفعولا ليهلك من هلك عن بينة ويحيا من حي عن بينة وإن الله لسميع عليم) (٤٢) .','621','973','0');
INSERT INTO [t22928] VALUES (1001,'قال تعالى: (إذ يريكهم الله في منامك قليلا ولو أراكهم كثيرا لفشلتم ولتنازعتم في الأمر ولكن الله سلم إنه عليم بذات الصدور) (٤٣) .','623','973','0');
INSERT INTO [t22928] VALUES (1002,'قال تعالى: (وأطيعوا الله ورسوله ولا تنازعوا فتفشلوا وتذهب ريحكم واصبروا إن الله مع الصابرين) (٤٦) .','623','973','0');
INSERT INTO [t22928] VALUES (1003,'قال تعالى: (ولا تكونوا كالذين خرجوا من ديارهم بطرا ورئاء الناس ويصدون عن سبيل الله والله بما يعملون محيط) (٤٧) .','623','973','0');
INSERT INTO [t22928] VALUES (1004,'قال تعالى: (وإذ زين لهم الشيطان أعمالهم وقال لا غالب لكم اليوم من الناس وإني جار لكم فلما تراءت الفئتان نكص على عقبيه وقال إني بريء منكم إني أرى ما لا ترون إني أخاف الله والله شديد العقاب) (٤٨) .','624','973','0');
INSERT INTO [t22928] VALUES (1005,'قال تعالى: (إذ يقول المنافقون والذين في قلوبهم مرض غر هؤلاء دينهم ومن يتوكل على الله فإن الله عزيز حكيم) (٤٩) .','624','973','0');
INSERT INTO [t22928] VALUES (1006,'قال تعالى: (ولو ترى إذ يتوفى الذين كفروا الملائكة يضربون وجوههم وأدبارهم وذوقوا عذاب الحريق) (٥٠) .','624','973','0');
INSERT INTO [t22928] VALUES (1007,'قال تعالى: (كدأب آل فرعون والذين من قبلهم كفروا بآيات الله فأخذهم الله بذنوبهم إن الله قوي شديد العقاب) (٥٢) .','625','973','0');
INSERT INTO [t22928] VALUES (1008,'قال تعالى: (ذلك بأن الله لم يك مغيرا نعمة أنعمها على قوم حتى يغيروا ما بأنفسهم وأن الله سميع عليم) (٥٣) .','625','973','0');
INSERT INTO [t22928] VALUES (1009,'قال تعالى: (الذين عاهدت منهم ثم ينقضون عهدهم في كل مرة وهم لا يتقون) (٥٦) .','625','973','0');
INSERT INTO [t22928] VALUES (1010,'قال تعالى: (فإما تثقفنهم في الحرب فشرد بهم من خلفهم لعلهم يذكرون) (٥٧) .','625','973','0');
INSERT INTO [t22928] VALUES (1011,'قال تعالى: (وإما تخافن من قوم خيانة فانبذ إليهم على سواء إن الله لا يحب الخائنين) (٥٨) .','626','973','0');
INSERT INTO [t22928] VALUES (1012,'قال تعالى: (ولا يحسبن الذين كفروا سبقوا إنهم لا يعجزون) (٥٩) .','626','973','0');
INSERT INTO [t22928] VALUES (1013,'قال تعالى: (وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم وآخرين من دونهم لا تعلمونهم الله يعلمهم وما تنفقوا من شيء في سبيل الله يوف إليكم وأنتم لا تظلمون) (٦٠) .','627','973','0');
INSERT INTO [t22928] VALUES (1014,'قال تعالى: (وإن جنحوا للسلم فاجنح لها وتوكل على الله) (٦١) .','627','973','0');
INSERT INTO [t22928] VALUES (1015,'قال تعالى: (يا أيها النبي حسبك الله ومن اتبعك من المؤمنين) (٦٤) .','628','973','0');
INSERT INTO [t22928] VALUES (1016,'قال تعالى: (يا أيها النبي حرض المؤمنين على القتال إن يكن منكم عشرون صابرون يغلبوا مائتين وإن يكن منكم مائة يغلبوا ألفا من الذين كفروا بأنهم قوم لا يفقهون) (٦٥) .','628','973','0');
INSERT INTO [t22928] VALUES (1017,'قال تعالى: (ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض تريدون عرض الدنيا والله يريد الآخرة والله عزيز حكيم) (٦٧) .','629','973','0');
INSERT INTO [t22928] VALUES (1018,'قال تعالى: (لولا كتاب من الله سبق لمسكم فيما أخذتم عذاب عظيم) (٦٨) .','629','973','0');
INSERT INTO [t22928] VALUES (1019,'قال تعالى: (فكلوا مما غنمتم حلالا طيبا واتقوا الله إن الله غفور رحيم) (٦٩) .','629','973','0');
INSERT INTO [t22928] VALUES (1020,'قال تعالى: (وإن يريدوا خيانتك فقد خانوا الله من قبل فأمكن منهم والله عليم حكيم) (٧١) .','629','973','0');
INSERT INTO [t22928] VALUES (1021,'قال تعالى: (إن الذين آمنوا وهاجروا وجاهدوا بأموالهم وأنفسهم في سبيل الله والذين آووا ونصروا أولئك بعضهم أولياء بعض والذين آمنوا ولم يهاجروا ما لكم من ولايتهم من شيء حتى يهاجروا وإن استنصروكم في الدين فعليكم النصر إلا على قوم بينكم وبينهم ميثاق والله بما تعملون بصير) (٧٢) .','630','973','0');
INSERT INTO [t22928] VALUES (1022,'قال تعالى: (والذين كفروا بعضهم أولياء بعض إلا تفعلوه تكن فتنة في الأرض وفساد كبير) (٧٣) .','630','973','0');
INSERT INTO [t22928] VALUES (1023,'قال تعالى: (والذين آمنوا من بعد وهاجروا وجاهدوا معكم فأولئك منكم وأولو الأرحام بعضهم أولى ببعض في كتاب الله إن الله بكل شيء عليم) (٧٥) .','630','973','0');
INSERT INTO [t22928] VALUES (1024,'التوبة','631','0','0');
INSERT INTO [t22928] VALUES (1025,'قال تعالى: (براءة من الله ورسوله إلى الذين عاهدتم من المشركين (١) فسيحوا في الأرض أربعة أشهر واعلموا أنكم غير معجزي الله وأن الله مخزي الكافرين) (٢) .','631','1024','0');
INSERT INTO [t22928] VALUES (1026,'قال تعالى: (وأذان من الله ورسوله إلى الناس يوم الحج الأكبر أن الله بريء من المشركين ورسوله فإن تبتم فهو خير لكم وإن توليتم فاعلموا أنكم غير معجزي الله وبشر الذين كفروا بعذاب أليم) (٣) .','631','1024','0');
INSERT INTO [t22928] VALUES (1027,'قال تعالى: (إلا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا ولم يظاهروا عليكم أحدا فأتموا إليهم عهدهم إلى مدتهم إن الله يحب المتقين) (٤) .','632','1024','0');
INSERT INTO [t22928] VALUES (1028,'قال تعالى: (فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد فإن تابوا وأقاموا الصلاة وآتوا الزكاة فخلوا سبيلهم إن الله غفور رحيم) (٥) .','632','1024','0');
INSERT INTO [t22928] VALUES (1029,'قال تعالى: (وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه ذلك بأنهم قوم لا يعلمون) (٧) .','633','1024','0');
INSERT INTO [t22928] VALUES (1030,'قال تعالى: (كيف يكون للمشركين عهد عند الله وعند رسوله إلا الذين عاهدتم عند المسجد الحرام فما استقاموا لكم فاستقيموا لهم إن الله يحب المتقين) (٧) .','633','1024','0');
INSERT INTO [t22928] VALUES (1031,'قال تعالى: (كيف وإن يظهروا عليكم لا يرقبوا فيكم إلا ولا ذمة يرضونكم بأفواههم وتأبى قلوبهم وأكثرهم فاسقون) (٨) .','634','1024','0');
INSERT INTO [t22928] VALUES (1032,'قال تعالى: (فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم في الدين ونفصل الآيات لقوم يعلمون) (١١) .','634','1024','0');
INSERT INTO [t22928] VALUES (1033,'قال تعالى: (وإن نكثوا أيمانهم من بعد عهدهم وطعنوا في دينكم فقاتلوا أئمة الكفر إنهم لا أيمان لهم لعلهم ينتهون) (١٢) .','634','1024','0');
INSERT INTO [t22928] VALUES (1034,'قال تعالى: (ألا تقاتلون قوما نكثوا أيمانهم وهموا بإخراج الرسول وهم بدءوكم أول مرة أتخشونهم فالله أحق أن تخشوه إن كنتم مؤمنين) (١٣) .','635','1024','0');
INSERT INTO [t22928] VALUES (1035,'قال تعالى: (ويذهب غيظ قلوبهم ويتوب الله على من يشاء والله عليم حكيم) (١٥) .','635','1024','0');
INSERT INTO [t22928] VALUES (1036,'قال تعالى: (ما كان للمشركين أن يعمروا مساجد الله شاهدين على أنفسهم بالكفر أولئك حبطت أعمالهم وفي النار هم خالدون) (١٧) .','635','1024','0');
INSERT INTO [t22928] VALUES (1037,'قال تعالى: (أجعلتم سقاية الحاج وعمارة المسجد الحرام كمن آمن بالله واليوم الآخر وجاهد في سبيل الله لا يستوون عند الله والله لا يهدي القوم الظالمين) (١٩) .','636','1024','0');
INSERT INTO [t22928] VALUES (1038,'قال تعالى: (يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها نعيم مقيم) (٢١) .','636','1024','0');
INSERT INTO [t22928] VALUES (1039,'قال تعالى: (لقد نصركم الله في مواطن كثيرة ويوم حنين إذ أعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت ثم وليتم مدبرين) (٢٥) .','636','1024','0');
INSERT INTO [t22928] VALUES (1040,'قال تعالى: (قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب حتى يعطوا الجزية عن يد وهم صاغرون) (٢٩) .','636','1024','0');
INSERT INTO [t22928] VALUES (1041,'قال تعالى: (وقالت اليهود عزير ابن الله وقالت النصارى المسيح ابن الله ذلك قولهم بأفواههم يضاهئون قول الذين كفروا من قبل قاتلهم الله أنى يؤفكون) (٣٠) .','637','1024','0');
INSERT INTO [t22928] VALUES (1042,'قال تعالى: (اتخذوا أحبارهم ورهبانهم أربابا من دون الله والمسيح ابن مريم وما أمروا إلا ليعبدوا إلها واحدا لا إله إلا هو سبحانه عما يشركون) (٣١) .','638','1024','0');
INSERT INTO [t22928] VALUES (1043,'قال تعالى: (يريدون أن يطفئوا نور الله بأفواههم ويأبى الله إلا أن يتم نوره ولو كره الكافرون) (٣٢) .','638','1024','0');
INSERT INTO [t22928] VALUES (1044,'قال تعالى: (يا أيها الذين آمنوا إن كثيرا من الأحبار والرهبان ليأكلون أموال الناس بالباطل ويصدون عن سبيل الله والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم) (٣٤) .','638','1024','0');
INSERT INTO [t22928] VALUES (1045,'قال تعالى: (يوم يحمى عليها في نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم هذا ما كنزتم لأنفسكم فذوقوا ما كنتم تكنزون) (٣٥) .','639','1024','0');
INSERT INTO [t22928] VALUES (1046,'قال تعالى: (إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ذلك الدين القيم فلا تظلموا فيهن أنفسكم وقاتلوا المشركين كافة كما يقاتلونكم كافة واعلموا أن الله مع المتقين) (٣٦) .','639','1024','0');
INSERT INTO [t22928] VALUES (1047,'قال تعالى: (إنما النسيء زيادة في الكفر يضل به الذين كفروا يحلونه عاما ويحرمونه عاما ليواطئوا عدة ما حرم الله فيحلوا ما حرم الله زين لهم سوء أعمالهم والله لا يهدي القوم الكافرين) (٣٧) .','640','1024','0');
INSERT INTO [t22928] VALUES (1048,'قال تعالى: (يا أيها الذين آمنوا ما لكم إذا قيل لكم انفروا في سبيل الله اثاقلتم إلى الأرض أرضيتم بالحياة الدنيا من الآخرة فما متاع الحياة الدنيا في الآخرة إلا قليل) (٣٨) .','641','1024','0');
INSERT INTO [t22928] VALUES (1049,'قال تعالى: (إلا تنصروه فقد نصره الله إذ أخرجه الذين كفروا ثاني اثنين إذ هما في الغار إذ يقول لصاحبه لا تحزن إن الله معنا فأنزل الله سكينته عليه وأيده بجنود لم تروها وجعل كلمة الذين كفروا السفلى وكلمة الله هي العليا والله عزيز حكيم) (٤٠) .','641','1024','0');
INSERT INTO [t22928] VALUES (1050,'قال تعالى: (لو كان عرضا قريبا وسفرا قاصدا لاتبعوك ولكن بعدت عليهم الشقة وسيحلفون بالله لو استطعنا لخرجنا معكم يهلكون أنفسهم والله يعلم إنهم لكاذبون) (٤٢) .','642','1024','0');
INSERT INTO [t22928] VALUES (1051,'قال تعالى: (عفا الله عنك لم أذنت لهم حتى يتبين لك الذين صدقوا وتعلم الكاذبين) (٤٣) .','642','1024','0');
INSERT INTO [t22928] VALUES (1052,'قال تعالى: (لو خرجوا فيكم ما زادوكم إلا خبالا ولأوضعوا خلالكم يبغونكم الفتنة وفيكم سماعون لهم والله عليم بالظالمين) (٤٧) .','643','1024','0');
INSERT INTO [t22928] VALUES (1053,'قال تعالى: (ومنهم من يقول ائذن لي ولا تفتني ألا في الفتنة سقطوا وإن جهنم لمحيطة بالكافرين) (٤٩) .','643','1024','0');
INSERT INTO [t22928] VALUES (1054,'قال تعالى: (قل هل تربصون بنا إلا إحدى الحسنيين ونحن نتربص بكم أن يصيبكم الله بعذاب من عنده أو بأيدينا فتربصوا إنا معكم متربصون) (٥٢) .','643','1024','0');
INSERT INTO [t22928] VALUES (1055,'قال تعالى: (وما منعهم أن تقبل منهم نفقاتهم إلا أنهم كفروا بالله وبرسوله ولا يأتون الصلاة إلا وهم كسالى ولا ينفقون إلا وهم كارهون) (٥٤) .','643','1024','0');
INSERT INTO [t22928] VALUES (1056,'قال تعالى: (لو يجدون ملجأ أو مغارات أو مدخلا لولوا إليه وهم يجمحون) (٥٧) .','644','1024','0');
INSERT INTO [t22928] VALUES (1057,'قال تعالى: (ومنهم من يلمزك في الصدقات فإن أعطوا منها رضوا وإن لم يعطوا منها إذا هم يسخطون) (٥٨) .','644','1024','0');
INSERT INTO [t22928] VALUES (1058,'قال تعالى: (إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل فريضة من الله والله عليم حكيم) (٦٠) .','644','1024','0');
INSERT INTO [t22928] VALUES (1059,'قال تعالى: (ومنهم الذين يؤذون النبي ويقولون هو أذن قل أذن خير لكم يؤمن بالله ويؤمن للمؤمنين ورحمة للذين آمنوا منكم والذين يؤذون رسول الله لهم عذاب أليم) (٦١) .','645','1024','0');
INSERT INTO [t22928] VALUES (1060,'قال تعالى: (يحلفون بالله لكم ليرضوكم والله ورسوله أحق أن يرضوه إن كانوا مؤمنين) (٦٢) .','645','1024','0');
INSERT INTO [t22928] VALUES (1061,'قال تعالى: (ألم يعلموا أنه من يحادد الله ورسوله فأن له نار جهنم خالدا فيها ذلك الخزي العظيم) (٦٣) .','646','1024','0');
INSERT INTO [t22928] VALUES (1062,'قال تعالى: (يحذر المنافقون أن تنزل عليهم سورة تنبئهم بما في قلوبهم قل استهزئوا إن الله مخرج ما تحذرون) (٦٤) .','647','1024','0');
INSERT INTO [t22928] VALUES (1063,'قال تعالى: (ولئن سألتهم ليقولن إنما كنا نخوض ونلعب قل أبالله وآياته ورسوله كنتم تستهزئون) (٦٥) .','647','1024','0');
INSERT INTO [t22928] VALUES (1064,'قال تعالى: (المنافقون والمنافقات بعضهم من بعض يأمرون بالمنكر وينهون عن المعروف ويقبضون أيديهم نسوا الله فنسيهم إن المنافقين هم الفاسقون) (٦٧) .','647','1024','0');
INSERT INTO [t22928] VALUES (1065,'قال تعالى: (كالذين من قبلكم كانوا أشد منكم قوة وأكثر أموالا وأولادا فاستمتعوا بخلاقهم فاستمتعتم بخلاقكم كما استمتع الذين من قبلكم بخلاقهم وخضتم كالذي خاضوا أولئك حبطت أعمالهم في الدنيا والآخرة وأولئك هم الخاسرون) (٦٩) .','647','1024','0');
INSERT INTO [t22928] VALUES (1066,'قال تعالى: (ألم يأتهم نبأ الذين من قبلهم قوم نوح وعاد وثمود وقوم إبراهيم وأصحاب مدين والمؤتفكات أتتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون) (٧٠) .','648','1024','0');
INSERT INTO [t22928] VALUES (1067,'قال تعالى: (وعد الله المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ومساكن طيبة في جنات عدن ورضوان من الله أكبر ذلك هو الفوز العظيم) (٧٢) .','648','1024','0');
INSERT INTO [t22928] VALUES (1068,'قال تعالى: (يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ومأواهم جهنم وبئس المصير) (٧٣) .','648','1024','0');
INSERT INTO [t22928] VALUES (1069,'قال تعالى: (يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم وهموا بما لم ينالوا وما نقموا إلا أن أغناهم الله ورسوله من فضله فإن يتوبوا يك خيرا لهم وإن يتولوا يعذبهم الله عذابا أليما في الدنيا والآخرة وما لهم في الأرض من ولي ولا نصير) (٧٤) .','648','1024','0');
INSERT INTO [t22928] VALUES (1070,'قال تعالى: (ومنهم من عاهد الله لئن آتانا من فضله لنصدقن ولنكونن من الصالحين) (٧٥) .','649','1024','0');
INSERT INTO [t22928] VALUES (1071,'قال تعالى: (الذين يلمزون المطوعين من المؤمنين في الصدقات والذين لا يجدون إلا جهدهم فيسخرون منهم سخر الله منهم ولهم عذاب أليم) (٧٩) .','649','1024','0');
INSERT INTO [t22928] VALUES (1072,'قال تعالى: (استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم ذلك بأنهم كفروا بالله ورسوله والله لا يهدي القوم الفاسقين) (٨٠) .','649','1024','0');
INSERT INTO [t22928] VALUES (1073,'قال تعالى: (فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله وقالوا لا تنفروا في الحر قل نار جهنم أشد حرا لو كانوا يفقهون) (٨١) .','650','1024','0');
INSERT INTO [t22928] VALUES (1074,'قال تعالى: (فليضحكوا قليلا وليبكوا كثيرا جزاء بما كانوا يكسبون) (٨٢) .','650','1024','0');
INSERT INTO [t22928] VALUES (1075,'قال تعالى: (فإن رجعك الله إلى طائفة منهم فاستأذنوك للخروج فقل لن تخرجوا معي أبدا ولن تقاتلوا معي عدوا إنكم رضيتم بالقعود أول مرة فاقعدوا مع الخالفين) (٨٣) .','650','1024','0');
INSERT INTO [t22928] VALUES (1076,'قال تعالى: (ولا تصل على أحد منهم مات أبدا ولا تقم على قبره إنهم كفروا بالله ورسوله وماتوا وهم فاسقون) (٨٤) .','650','1024','0');
INSERT INTO [t22928] VALUES (1077,'قال تعالى: (وإذا أنزلت سورة أن آمنوا بالله وجاهدوا مع رسوله استأذنك أولو الطول منهم وقالوا ذرنا نكن مع القاعدين) (٨٦) .','650','1024','0');
INSERT INTO [t22928] VALUES (1078,'قال تعالى: (رضوا بأن يكونوا مع الخوالف وطبع على قلوبهم فهم لا يفقهون) (٨٧) .','651','1024','0');
INSERT INTO [t22928] VALUES (1079,'قال تعالى: (وجاء المعذرون من الأعراب ليؤذن لهم وقعد الذين كذبوا الله ورسوله سيصيب الذين كفروا منهم عذاب أليم) (٩٠) .','651','1024','0');
INSERT INTO [t22928] VALUES (1080,'قال تعالى: (ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج إذا نصحوا لله ورسوله ما على المحسنين من سبيل والله غفور رحيم) (٩١) .','651','1024','0');
INSERT INTO [t22928] VALUES (1081,'قال تعالى: (ولا على الذين إذا ما أتوك لتحملهم قلت لا أجد ما أحملكم عليه تولوا وأعينهم تفيض من الدمع حزنا ألا يجدوا ما ينفقون) (٩٢) .','651','1024','0');
INSERT INTO [t22928] VALUES (1082,'قال تعالى: (إنما السبيل على الذين يستأذنونك وهم أغنياء رضوا بأن يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون) (٩٣) .','652','1024','0');
INSERT INTO [t22928] VALUES (1083,'قال تعالى: (يعتذرون إليكم إذا رجعتم إليهم قل لا تعتذروا لن نؤمن لكم قد نبأنا الله من أخباركم وسيرى الله عملكم ورسوله ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون) (٩٤) .','652','1024','0');
INSERT INTO [t22928] VALUES (1084,'قال تعالى: (سيحلفون بالله لكم إذا انقلبتم إليهم لتعرضوا عنهم فأعرضوا عنهم إنهم رجس ومأواهم جهنم جزاء بما كانوا يكسبون) (٩٥) .','652','1024','0');
INSERT INTO [t22928] VALUES (1085,'قال تعالى: (الأعراب أشد كفرا ونفاقا وأجدر ألا يعلموا حدود ما أنزل الله على رسوله والله عليم حكيم) (٩٧) .','653','1024','0');
INSERT INTO [t22928] VALUES (1086,'قال تعالى: (ومن الأعراب من يتخذ ما ينفق مغرما ويتربص بكم الدوائر عليهم دائرة السوء والله سميع عليم) (٩٨) .','653','1024','0');
INSERT INTO [t22928] VALUES (1087,'قال تعالى: (ومن الأعراب من يؤمن بالله واليوم الآخر ويتخذ ما ينفق قربات عند الله وصلوات الرسول ألا إنها قربة لهم سيدخلهم الله في رحمته إن الله غفور رحيم) (٩٩) .','653','1024','0');
INSERT INTO [t22928] VALUES (1088,'قال تعالى: (والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وأعد لهم جنات تجري تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم) (١٠٠) .','653','1024','0');
INSERT INTO [t22928] VALUES (1089,'قال تعالى: (وممن حولكم من الأعراب منافقون ومن أهل المدينة مردوا على النفاق لا تعلمهم نحن نعلمهم سنعذبهم مرتين ثم يردون إلى عذاب عظيم) (١٠١) .','654','1024','0');
INSERT INTO [t22928] VALUES (1090,'قال تعالى: (وآخرون اعترفوا بذنوبهم خلطوا عملا صالحا وآخر سيئا عسى الله أن يتوب عليهم إن الله غفور رحيم) (١٠٢) .','654','1024','0');
INSERT INTO [t22928] VALUES (1091,'قال تعالى: (خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم إن صلاتك سكن لهم والله سميع عليم) (١٠٣) .','655','1024','0');
INSERT INTO [t22928] VALUES (1092,'قال تعالى: (ألم يعلموا أن الله هو يقبل التوبة عن عباده ويأخذ الصدقات وأن الله هو التواب الرحيم) (١٠٤) .','656','1024','0');
INSERT INTO [t22928] VALUES (1093,'قال تعالى: (وآخرون مرجون لأمر الله إما يعذبهم وإما يتوب عليهم والله عليم حكيم) (١٠٦) .','656','1024','0');
INSERT INTO [t22928] VALUES (1094,'قال تعالى: (والذين اتخذوا مسجدا ضرارا وكفرا وتفريقا بين المؤمنين وإرصادا لمن حارب الله ورسوله من قبل وليحلفن إن أردنا إلا الحسنى والله يشهد إنهم لكاذبون) (١٠٧) .','656','1024','0');
INSERT INTO [t22928] VALUES (1095,'قال تعالى: (لا تقم فيه أبدا لمسجد أسس على التقوى من أول يوم أحق أن تقوم فيه فيه رجال يحبون أن يتطهروا والله يحب المطهرين) (١٠٨) .','657','1024','0');
INSERT INTO [t22928] VALUES (1096,'قال تعالى: (أفمن أسس بنيانه على تقوى من الله ورضوان خير أم من أسس بنيانه على شفا جرف هار فانهار به في نار جهنم والله لا يهدي القوم الظالمين) (١٠٩) .','657','1024','0');
INSERT INTO [t22928] VALUES (1097,'قال تعالى: (إن الله اشترى من المؤمنين أنفسهم وأموالهم بأن لهم الجنة يقاتلون في سبيل الله فيقتلون ويقتلون وعدا عليه حقا في التوراة والإنجيل والقرآن ومن أوفى بعهده من الله فاستبشروا ببيعكم الذي بايعتم به وذلك هو الفوز العظيم) (١١١) .','658','1024','0');
INSERT INTO [t22928] VALUES (1098,'قال تعالى: (التائبون العابدون الحامدون السائحون الراكعون الساجدون الآمرون بالمعروف والناهون عن المنكر والحافظون لحدود الله وبشر المؤمنين) (١١٢) .','659','1024','0');
INSERT INTO [t22928] VALUES (1099,'قال تعالى: (لقد تاب الله على النبي والمهاجرين والأنصار الذين اتبعوه في ساعة العسرة من بعد ما كاد يزيغ قلوب فريق منهم ثم تاب عليهم إنه بهم رءوف رحيم) (١١٧) .','659','1024','0');
INSERT INTO [t22928] VALUES (1100,'قال تعالى: (وعلى الثلاثة الذين خلفوا حتى إذا ضاقت عليهم الأرض بما رحبت وضاقت عليهم أنفسهم وظنوا أن لا ملجأ من الله إلا إليه ثم تاب عليهم ليتوبوا إن الله هو التواب الرحيم) (١١٨) .','659','1024','0');
INSERT INTO [t22928] VALUES (1101,'قال تعالى: (ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله ولا يرغبوا بأنفسهم عن نفسه ذلك بأنهم لا يصيبهم ظمأ ولا نصب ولا مخمصة في سبيل الله ولا يطئون موطئا يغيظ الكفار ولا ينالون من عدو نيلا إلا كتب لهم به عمل صالح إن الله لا يضيع أجر المحسنين) (١٢٠) .','660','1024','0');
INSERT INTO [t22928] VALUES (1102,'قال تعالى: (وما كان المؤمنون لينفروا كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون) (١٢٢) .','660','1024','0');
INSERT INTO [t22928] VALUES (1103,'قال تعالى: (يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار وليجدوا فيكم غلظة واعلموا أن الله مع المتقين) (١٢٣) .','660','1024','0');
INSERT INTO [t22928] VALUES (1104,'قال تعالى: (وإذا ما أنزلت سورة نظر بعضهم إلى بعض هل يراكم من أحد ثم انصرفوا صرف الله قلوبهم بأنهم قوم لا يفقهون) (١٢٧) .','660','1024','0');
INSERT INTO [t22928] VALUES (1105,'قال تعالى: (لقد جاءكم رسول من أنفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم) (١٢٨) .','660','1024','0');
INSERT INTO [t22928] VALUES (1106,'يونس','661','0','0');
INSERT INTO [t22928] VALUES (1107,'قال تعالى: (الر تلك آيات الكتاب الحكيم (١)) .','661','1106','0');
INSERT INTO [t22928] VALUES (1108,'قال تعالى: (أكان للناس عجبا أن أوحينا إلى رجل منهم أن أنذر الناس وبشر الذين آمنوا أن لهم قدم صدق عند ربهم قال الكافرون إن هذا لساحر مبين (٢)) .','661','1106','0');
INSERT INTO [t22928] VALUES (1109,'قال تعالى: (إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يدبر الأمر ما من شفيع إلا من بعد إذنه ذلكم الله ربكم فاعبدوه أفلا تذكرون (٣))','661','1106','0');
INSERT INTO [t22928] VALUES (1110,'قال تعالى: (إليه مرجعكم جميعا وعد الله حقا إنه يبدأ الخلق ثم يعيده ليجزي الذين آمنوا وعملوا الصالحات بالقسط والذين كفروا لهم شراب من حميم وعذاب أليم بما كانوا يكفرون (٤))','662','1106','0');
INSERT INTO [t22928] VALUES (1111,'قال تعالى: (هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب ما خلق الله ذلك إلا بالحق يفصل الآيات لقوم يعلمون (٥)) .','662','1106','0');
INSERT INTO [t22928] VALUES (1112,'قال تعالى: (إن الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمأنوا بها والذين هم عن آياتنا غافلون (٧) أولئك مأواهم النار بما كانوا يكسبون (٨)) .','663','1106','0');
INSERT INTO [t22928] VALUES (1113,'قال تعالى: (إن الذين آمنوا وعملوا الصالحات يهديهم ربهم بإيمانهم تجري من تحتهم الأنهار في جنات النعيم (٩)) .','663','1106','0');
INSERT INTO [t22928] VALUES (1114,'قال تعالى: (دعواهم فيها سبحانك اللهم وتحيتهم فيها سلام وآخر دعواهم أن الحمد لله رب العالمين (١٠)) .','663','1106','0');
INSERT INTO [t22928] VALUES (1115,'قال تعالى: (ولو يعجل الله للناس الشر استعجالهم بالخير لقضي إليهم أجلهم فنذر الذين لا يرجون لقاءنا في طغيانهم يعمهون (١١)) .','664','1106','0');
INSERT INTO [t22928] VALUES (1116,'قال تعالى: (وإذا مس الإنسان الضر دعانا لجنبه أو قاعدا أو قائما فلما كشفنا عنه ضره مر كأن لم يدعنا إلى ضر مسه كذلك زين للمسرفين ما كانوا يعملون (١٢)) .','664','1106','0');
INSERT INTO [t22928] VALUES (1117,'قال تعالى: (ولقد أهلكنا القرون من قبلكم لما ظلموا وجاءتهم رسلهم بالبينات وما كانوا ليؤمنوا كذلك نجزي القوم المجرمين (١٣)) .','665','1106','0');
INSERT INTO [t22928] VALUES (1118,'قال تعالى: (ثم جعلناكم خلائف في الأرض من بعدهم لننظر كيف تعملون (١٤)) .','665','1106','0');
INSERT INTO [t22928] VALUES (1119,'قال تعالى: (قل لو شاء الله ما تلوته عليكم ولا أدراكم به فقد لبثت فيكم عمرا من قبله أفلا تعقلون (١٦)) .','665','1106','0');
INSERT INTO [t22928] VALUES (1120,'قال تعالى: (ويعبدون من دون الله ما لا يضرهم ولا ينفعهم ويقولون هؤلاء شفعاؤنا عند الله قل أتنبئون الله بما لا يعلم في السماوات ولا في الأرض سبحانه وتعالى عما يشركون (١٨)) .','666','1106','0');
INSERT INTO [t22928] VALUES (1121,'قال تعالى: (وإذا أذقنا الناس رحمة من بعد ضراء مستهم إذا لهم مكر في آياتنا قل الله أسرع مكرا إن رسلنا يكتبون ما تمكرون (٢١)) .','666','1106','0');
INSERT INTO [t22928] VALUES (1122,'قال تعالى: (هو الذي يسيركم في البر والبحر حتى إذا كنتم في الفلك وجرين بهم بريح طيبة وفرحوا بها جاءتها ريح عاصف وجاءهم الموج من كل مكان وظنوا أنهم أحيط بهم دعوا الله مخلصين له الدين لئن أنجيتنا من هذه لنكونن من الشاكرين (٢٢)) .','666','1106','0');
INSERT INTO [t22928] VALUES (1123,'قال تعالى: (فلما أنجاهم إذا هم يبغون في الأرض بغير الحق يا أيها الناس إنما بغيكم على أنفسكم متاع الحياة الدنيا ثم إلينا مرجعكم فننبئكم بما كنتم تعملون (٢٣)) .','667','1106','0');
INSERT INTO [t22928] VALUES (1124,'قال تعالى: (إنما مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض مما يأكل الناس والأنعام حتى إذا أخذت الأرض زخرفها وازينت وظن أهلها أنهم قادرون عليها أتاها أمرنا ليلا أو نهارا فجعلناها حصيدا كأن لم تغن بالأمس كذلك نفصل الآيات لقوم يتفكرون (٢٤)) .','668','1106','0');
INSERT INTO [t22928] VALUES (1125,'قال تعالى: (للذين أحسنوا الحسنى وزيادة ولا يرهق وجوههم قتر ولا ذلة أولئك أصحاب الجنة هم فيها خالدون (٢٦)) .','669','1106','0');
INSERT INTO [t22928] VALUES (1126,'قال تعالى: (والذين كسبوا السيئات جزاء سيئة بمثلها وترهقهم ذلة ما لهم من الله من عاصم كأنما أغشيت وجوههم قطعا من الليل مظلما أولئك أصحاب النار هم فيها خالدون (٢٧)) .','669','1106','0');
INSERT INTO [t22928] VALUES (1127,'قال تعالى: (ويوم نحشرهم جميعا ثم نقول للذين أشركوا مكانكم أنتم وشركاؤكم فزيلنا بينهم وقال شركاؤهم ما كنتم إيانا تعبدون (٢٨)) .','670','1106','0');
INSERT INTO [t22928] VALUES (1128,'قال تعالى: (هنالك تبلو كل نفس ما أسلفت وردوا إلى الله مولاهم الحق وضل عنهم ما كانوا يفترون (٣٠)) .','670','1106','0');
INSERT INTO [t22928] VALUES (1129,'قال تعالى: (كذلك حقت كلمت ربك على الذين فسقوا أنهم لا يؤمنون (٣٣)) .','671','1106','0');
INSERT INTO [t22928] VALUES (1130,'قال تعالى: (قل هل من شركائكم من يبدأ الخلق ثم يعيده قل الله يبدأ الخلق ثم يعيده فأنى تؤفكون) (٣٤) (قل هل من شركائكم من يهدي إلى الحق قل الله يهدي للحق أفمن يهدي إلى الحق أحق أن يتبع أمن لا يهدي إلا أن يهدى فما لكم كيف تحكمون (٣٥)) .','671','1106','0');
INSERT INTO [t22928] VALUES (1131,'قال تعالى: (وما يتبع أكثرهم إلا ظنا إن الظن لا يغني من الحق شيئا إن الله عليم بما يفعلون (١٢))','671','1106','0');
INSERT INTO [t22928] VALUES (1132,'قال تعالى: (وما كان هذا القرآن أن يفترى من دون الله ولكن تصديق الذي بين يديه وتفصيل الكتاب لا ريب فيه من رب العالمين (٣٧)) .','672','1106','0');
INSERT INTO [t22928] VALUES (1133,'قال تعالى: (بل كذبوا بما لم يحيطوا بعلمه ولما يأتهم تأويله كذلك كذب الذين من قبلهم فانظر كيف كان عاقبة الظالمين (٣٩)) .','672','1106','0');
INSERT INTO [t22928] VALUES (1134,'قال تعالى: (ومنهم من يستمعون إليك أفأنت تسمع الصم ولو كانوا لا يعقلون ومنهم من ينظر إليك أفأنت تهدي العمي ولو كانوا لا يبصرون (٤٣)) .','672','1106','0');
INSERT INTO [t22928] VALUES (1135,'قال تعالى: (إن الله لا يظلم الناس شيئا ولكن الناس أنفسهم يظلمون (٤٤)) .','673','1106','0');
INSERT INTO [t22928] VALUES (1136,'قال تعالى: (ويوم يحشرهم كأن لم يلبثوا إلا ساعة من النهار يتعارفون بينهم قد خسر الذين كذبوا بلقاء الله وما كانوا مهتدين (٤٥)) .','673','1106','0');
INSERT INTO [t22928] VALUES (1137,'قال تعالى: (وإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا مرجعهم ثم الله شهيد على ما يفعلون (٤٦)) .','673','1106','0');
INSERT INTO [t22928] VALUES (1138,'قال تعالى: (قل أرأيتم إن أتاكم عذابه بياتا أو نهارا ماذا يستعجل منه المجرمون (٥٠)) .','674','1106','0');
INSERT INTO [t22928] VALUES (1139,'قال تعالى: (أثم إذا ما وقع آمنتم به آلآن وقد كنتم به تستعجلون (٥١)) .','674','1106','0');
INSERT INTO [t22928] VALUES (1140,'قال تعالى: (ويستنبئونك أحق هو قل إي وربي إنه لحق وما أنتم بمعجزين (٥٣)) .','674','1106','0');
INSERT INTO [t22928] VALUES (1141,'قال تعالى: (ولو أن لكل نفس ظلمت ما في الأرض لافتدت به وأسروا الندامة لما رأوا العذاب وقضي بينهم بالقسط وهم لا يظلمون (٥٤)) .','674','1106','0');
INSERT INTO [t22928] VALUES (1142,'قال تعالى: (يا أيها الناس قد جاءتكم موعظة من ربكم وشفاء لما في الصدور وهدى ورحمة للمؤمنين (٥٧)) .','675','1106','0');
INSERT INTO [t22928] VALUES (1143,'قال تعالى: (قل بفضل الله وبرحمته فبذلك فليفرحوا هو خير مما يجمعون (٥٨)) .','675','1106','0');
INSERT INTO [t22928] VALUES (1144,'قال تعالى: (قل أرأيتم ما أنزل الله لكم من رزق فجعلتم منه حراما وحلالا قل آلله أذن لكم أم على الله تفترون (٥٩)) .','675','1106','0');
INSERT INTO [t22928] VALUES (1145,'قال تعالى: (وما تكون في شأن وما تتلو منه من قرآن ولا تعملون من عمل إلا كنا عليكم شهودا إذ تفيضون فيه وما يعزب عن ربك من مثقال ذرة في الأرض ولا في السماء ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين (٦١)) .','675','1106','0');
INSERT INTO [t22928] VALUES (1146,'قال تعالى: (الذين آمنوا وكانوا يتقون لهم البشرى في الحياة الدنيا وفي الآخرة لا تبديل لكلمات الله ذلك هو الفوز العظيم (٦٤)) .','676','1106','0');
INSERT INTO [t22928] VALUES (1147,'قال تعالى: (ولا يحزنك قولهم إن العزة لله جميعا هو السميع العليم (٦٥)) .','676','1106','0');
INSERT INTO [t22928] VALUES (1148,'قال تعالى: (ألا إن لله من في السماوات ومن في الأرض وما يتبع الذين يدعون من دون الله شركاء إن يتبعون إلا الظن وإن هم إلا يخرصون (٦٦)) .','677','1106','0');
INSERT INTO [t22928] VALUES (1149,'قال تعالى: (قالوا اتخذ الله ولدا سبحانه هو الغني له ما في السماوات وما في الأرض إن عندكم من سلطان بهذا أتقولون على الله ما لا تعلمون (٦٨)) .','677','1106','0');
INSERT INTO [t22928] VALUES (1150,'قال تعالى: (متاع في الدنيا ثم إلينا مرجعهم ثم نذيقهم العذاب الشديد بما كانوا يكفرون (٧٠)) .','677','1106','0');
INSERT INTO [t22928] VALUES (1151,'قال تعالى: (واتل عليهم نبأ نوح إذ قال لقومه يا قوم إن كان كبر عليكم مقامي وتذكيري بآيات الله فعلى الله توكلت فأجمعوا أمركم وشركاءكم ثم لا يكن أمركم عليكم غمة ثم اقضوا إلي ولا تنظرون (٧١)) .','677','1106','0');
INSERT INTO [t22928] VALUES (1152,'قال تعالى: (ثم بعثنا من بعده رسلا إلى قومهم فجاءوهم بالبينات فما كانوا ليؤمنوا بما كذبوا به من قبل كذلك نطبع على قلوب المعتدين (٧٤)) .','678','1106','0');
INSERT INTO [t22928] VALUES (1153,'قال تعالى: (قال موسى أتقولون للحق لما جاءكم أسحر هذا ولا يفلح الساحرون (٧٧)) .','679','1106','0');
INSERT INTO [t22928] VALUES (1154,'قال تعالى: (قالوا أجئتنا لتلفتنا عما وجدنا عليه آباءنا وتكون لكما الكبرياء في الأرض وما نحن لكما بمؤمنين (٧٨)) .','679','1106','0');
INSERT INTO [t22928] VALUES (1155,'قال تعالى: (فلما ألقوا قال موسى ما جئتم به السحر إن الله سيبطله إن الله لا يصلح عمل المفسدين (٨١)) .','679','1106','0');
INSERT INTO [t22928] VALUES (1156,'قال تعالى: (فما آمن لموسى إلا ذرية من قومه على خوف من فرعون وملئهم أن يفتنهم وإن فرعون لعال في الأرض وإنه لمن المسرفين (٨٣)) .','680','1106','0');
INSERT INTO [t22928] VALUES (1157,'قال تعالى: (وأوحينا إلى موسى وأخيه أن تبوءا لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة وأقيموا الصلاة وبشر المؤمنين (٨٧)) .','681','1106','0');
INSERT INTO [t22928] VALUES (1158,'قال تعالى: (وقال موسى ربنا إنك آتيت فرعون وملأه زينة وأموالا في الحياة الدنيا ربنا ليضلوا عن سبيلك ربنا اطمس على أموالهم واشدد على قلوبهم فلا يؤمنوا حتى يروا العذاب الأليم (٨٨)) .','681','1106','0');
INSERT INTO [t22928] VALUES (1159,'قال تعالى: (قال قد أجيبت دعوتكما فاستقيما ولا تتبعان سبيل الذين لا يعلمون (٨٩)) .','682','1106','0');
INSERT INTO [t22928] VALUES (1160,'قال تعالى: (وجاوزنا ببني إسرائيل البحر فأتبعهم فرعون وجنوده بغيا وعدوا حتى إذا أدركه الغرق قال آمنت أنه لا إله إلا الذي آمنت به بنو إسرائيل وأنا من المسلمين (٩٠)) .','682','1106','0');
INSERT INTO [t22928] VALUES (1161,'قال تعالى: (آلآن وقد عصيت قبل وكنت من المفسدين (٩١)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1162,'قال تعالى: (فاليوم ننجيك ببدنك لتكون لمن خلفك آية وإن كثيرا من الناس عن آياتنا لغافلون (٩٢)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1163,'قال تعالى: (ولقد بوأنا بني إسرائيل مبوأ صدق ورزقناهم من الطيبات فما اختلفوا حتى جاءهم العلم إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون (٩٢)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1164,'قال تعالى: (فلولا كانت قرية آمنت فنفعها إيمانها إلا قوم يونس لما آمنوا كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم إلى حين (٩٨)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1165,'قال تعالى: (قل انظروا ماذا في السماوات والأرض وما تغني الآيات والنذر عن قوم لا يؤمنون (١٠١)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1166,'قال تعالى: (ثم ننجي رسلنا والذين آمنوا كذلك حقا علينا ننج المؤمنين (١٠٣)) .','683','1106','0');
INSERT INTO [t22928] VALUES (1167,'هود','685','0','0');
INSERT INTO [t22928] VALUES (1168,'قال تعالى: (الر كتاب أحكمت آياته ثم فصلت من لدن حكيم خبير (١)) .','685','1167','0');
INSERT INTO [t22928] VALUES (1169,'قال تعالى: (ألا تعبدوا إلا الله إنني لكم منه نذير وبشير (٢)) .','685','1167','0');
INSERT INTO [t22928] VALUES (1170,'قال تعالى: (وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى ويؤت كل ذي فضل فضله وإن تولوا فإني أخاف عليكم عذاب يوم كبير (٣)) .','686','1167','0');
INSERT INTO [t22928] VALUES (1171,'قال تعالى: (ألا إنهم يثنون صدورهم ليستخفوا منه ألا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون إنه عليم بذات الصدور (٥)) .','686','1167','0');
INSERT INTO [t22928] VALUES (1172,'قال تعالى: (وما من دابة في الأرض إلا على الله رزقها ويعلم مستقرها ومستودعها كل في كتاب مبين (٦)) .','687','1167','0');
INSERT INTO [t22928] VALUES (1173,'قال تعالى: (ولئن أخرنا عنهم العذاب إلى أمة معدودة ليقولن ما يحبسه ألا يوم يأتيهم ليس مصروفا عنهم وحاق بهم ما كانوا به يستهزئون (٨)) .','687','1167','0');
INSERT INTO [t22928] VALUES (1174,'قال تعالى: (ولئن أذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني إنه لفرح فخور (١٠)) .','688','1167','0');
INSERT INTO [t22928] VALUES (1175,'قال تعالى: (إلا الذين صبروا وعملوا الصالحات أولئك لهم مغفرة وأجر كبير (١١)) .','688','1167','0');
INSERT INTO [t22928] VALUES (1176,'قال تعالى: (فلعلك تارك بعض ما يوحى إليك وضائق به صدرك أن يقولوا لولا أنزل عليه كنز أو جاء معه ملك إنما أنت نذير والله على كل شيء وكيل (١٢)) .','688','1167','0');
INSERT INTO [t22928] VALUES (1177,'قال تعالى: (أولئك الذين ليس لهم في الآخرة إلا النار وحبط ما صنعوا فيها وباطل ما كانوا يعملون (١٦)) .','688','1167','0');
INSERT INTO [t22928] VALUES (1178,'قال تعالى: (أفمن كان على بينة من ربه ويتلوه شاهد منه ومن قبله كتاب موسى إماما ورحمة أولئك يؤمنون به ومن يكفر به من الأحزاب فالنار موعده فلا تك في مرية منه إنه الحق من ربك ولكن أكثر الناس لا يؤمنون (١٧)) .','689','1167','0');
INSERT INTO [t22928] VALUES (1179,'قال تعالى: (أولئك لم يكونوا معجزين في الأرض وما كان لهم من دون الله من أولياء يضاعف لهم العذاب ما كانوا يستطيعون السمع وما كانوا يبصرون (٢٠)) .','690','1167','0');
INSERT INTO [t22928] VALUES (1180,'قال تعالى: (لا جرم أنهم في الآخرة هم الأخسرون (٢٢)) .','690','1167','0');
INSERT INTO [t22928] VALUES (1181,'قال تعالى: (مثل الفريقين كالأعمى والأصم والبصير والسميع هل يستويان مثلا أفلا تذكرون (٢٤)) .','690','1167','0');
INSERT INTO [t22928] VALUES (1182,'قال تعالى: (ولقد أرسلنا نوحا إلى قومه إني لكم نذير مبين (٢٥))','691','1167','0');
INSERT INTO [t22928] VALUES (1183,'قال تعالى: (أن لا تعبدوا إلا الله إني أخاف عليكم عذاب يوم أليم (٢٦)) .','691','1167','0');
INSERT INTO [t22928] VALUES (1184,'قال تعالى: (فقال الملأ الذين كفروا من قومه ما نراك إلا بشرا مثلنا وما نراك اتبعك إلا الذين هم أراذلنا بادي الرأي وما نرى لكم علينا من فضل بل نظنكم كاذبين (٢٧)) .','691','1167','0');
INSERT INTO [t22928] VALUES (1185,'قال تعالى: (قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني رحمة من عنده فعميت عليكم أنلزمكموها وأنتم لها كارهون (٢٨)) .','692','1167','0');
INSERT INTO [t22928] VALUES (1186,'قال تعالى: (ولا أقول لكم عندي خزائن الله ولا أعلم الغيب ولا أقول إني ملك ولا أقول للذين تزدري أعينكم لن يؤتيهم الله خيرا الله أعلم بما في أنفسهم إني إذا لمن الظالمين (٣١)) .','693','1167','0');
INSERT INTO [t22928] VALUES (1187,'قال تعالى: (قالوا يا نوح قد جادلتنا فأكثرت جدالنا فأتنا بما تعدنا إن كنت من الصادقين (٣٢)) .','693','1167','0');
INSERT INTO [t22928] VALUES (1188,'قال تعالى: (ولا ينفعكم نصحي إن أردت أن أنصح لكم إن كان الله يريد أن يغويكم هو ربكم وإليه ترجعون (٣٤)) .','693','1167','0');
INSERT INTO [t22928] VALUES (1189,'قال تعالى: (أم يقولون افتراه قل إن افتريته فعلي إجرامي وأنا بريء مما تجرمون (٣٥)) .','694','1167','0');
INSERT INTO [t22928] VALUES (1190,'قال تعالى: (وأوحي إلى نوح أنه لن يؤمن من قومك إلا من قد آمن فلا تبتئس بما كانوا يفعلون (٣٦)) .','694','1167','0');
INSERT INTO [t22928] VALUES (1191,'قال تعالى: (واصنع الفلك بأعيننا ووحينا ولا تخاطبني في الذين ظلموا إنهم مغرقون (٣٧)) .','694','1167','0');
INSERT INTO [t22928] VALUES (1192,'قال تعالى: (حتى إذا جاء أمرنا وفار التنور قلنا احمل فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول ومن آمن وما آمن معه إلا قليل (٤٠)) .','694','1167','0');
INSERT INTO [t22928] VALUES (1193,'قال تعالى: (وقال اركبوا فيها بسم الله مجراها ومرساها إن ربي لغفور رحيم (٤١))','695','1167','0');
INSERT INTO [t22928] VALUES (1194,'قال تعالى: (وهي تجري بهم في موج كالجبال ونادى نوح ابنه وكان في معزل يا بني اركب معنا ولا تكن مع الكافرين (٤٢)) .','696','1167','0');
INSERT INTO [t22928] VALUES (1195,'قال تعالى: (قال سآوي إلى جبل يعصمني من الماء قال لا عاصم اليوم من أمر الله إلا من رحم وحال بينهما الموج فكان من المغرقين (٤٣)) .','697','1167','0');
INSERT INTO [t22928] VALUES (1196,'قال تعالى: (وقيل يا أرض ابلعي ماءك ويا سماء أقلعي وغيض الماء وقضي الأمر واستوت على الجودي وقيل بعدا للقوم الظالمين (٤٤)) .','697','1167','0');
INSERT INTO [t22928] VALUES (1197,'قال تعالى: (قال يا نوح إنه ليس من أهلك إنه عمل غير صالح فلا تسألني ما ليس لك به علم إني أعظك أن تكون من الجاهلين (٤٦)) .','698','1167','0');
INSERT INTO [t22928] VALUES (1198,'قال تعالى: (قال رب إني أعوذ بك أن أسألك ما ليس لي به علم وإلا تغفر لي وترحمني أكن من الخاسرين (٤٧)) .','699','1167','0');
INSERT INTO [t22928] VALUES (1199,'قال تعالى: (قيل يا نوح اهبط بسلام منا وبركات عليك وعلى أمم ممن معك وأمم سنمتعهم ثم يمسهم منا عذاب أليم (٤٨)) .','699','1167','0');
INSERT INTO [t22928] VALUES (1200,'قال تعالى: (تلك من أنباء الغيب نوحيها إليك ما كنت تعلمها أنت ولا قومك من قبل هذا فاصبر إن العاقبة للمتقين (٤٩)) .','699','1167','0');
INSERT INTO [t22928] VALUES (1201,'قال تعالى: (وإلى عاد أخاهم هودا قال يا قوم اعبدوا الله ما لكم من إله غيره إن أنتم إلا مفترون (٥٠)) .','700','1167','0');
INSERT INTO [t22928] VALUES (1202,'قال تعالى: (ويا قوم استغفروا ربكم ثم توبوا إليه يرسل السماء عليكم مدرارا ويزدكم قوة إلى قوتكم ولا تتولوا مجرمين (٥٢)) .','700','1167','0');
INSERT INTO [t22928] VALUES (1203,'قال تعالى {قالوا يا هود ما جئتنا ببينة وما نحن بتاركي آلهتنا عن قولك وما نحن لك بمؤمنين}','700','1167','0');
INSERT INTO [t22928] VALUES (1204,'قال تعالى: (إن نقول إلا اعتراك بعض آلهتنا بسوء قال إني أشهد الله واشهدوا أني بريء مما تشركون (٥٤)) .','700','1167','0');
INSERT INTO [t22928] VALUES (1205,'قال تعالى: (فإن تولوا فقد أبلغتكم ما أرسلت به إليكم ويستخلف ربي قوما غيركم ولا تضرونه شيئا إن ربي على كل شيء حفيظ (٥٧)) .','700','1167','0');
INSERT INTO [t22928] VALUES (1206,'قال تعالى: (وأتبعوا في هذه الدنيا لعنة ويوم القيامة ألا إن عادا كفروا ربهم ألا بعدا لعاد قوم هود (٦٠)) .','701','1167','0');
INSERT INTO [t22928] VALUES (1207,'قال تعالى: (قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني منه رحمة فمن ينصرني من الله إن عصيته فما تزيدونني غير تخسير (٦٣)) .','701','1167','0');
INSERT INTO [t22928] VALUES (1208,'قال تعالى: (فلما جاء أمرنا نجينا صالحا والذين آمنوا معه برحمة منا ومن خزي يومئذ إن ربك هو القوي العزيز (٦٦)) .','701','1167','0');
INSERT INTO [t22928] VALUES (1209,'قال تعالى: (وأخذ الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين (٦٧)) .','702','1167','0');
INSERT INTO [t22928] VALUES (1210,'قال تعالى: (كأن لم يغنوا فيها ألا إن ثمود كفروا ربهم ألا بعدا لثمود (٦٨)) .','702','1167','0');
INSERT INTO [t22928] VALUES (1211,'قال تعالى: (ولقد جاءت رسلنا إبراهيم بالبشرى قالوا سلاما قال سلام فما لبث أن جاء بعجل حنيذ (٦٩)) .','702','1167','0');
INSERT INTO [t22928] VALUES (1212,'قال تعالى: (وامرأته قائمة فضحكت فبشرناها بإسحاق ومن وراء إسحاق يعقوب (٧١)) .','703','1167','0');
INSERT INTO [t22928] VALUES (1213,'قال تعالى: (قالت يا ويلتا أألد وأنا عجوز وهذا بعلي شيخا إن هذا لشيء عجيب (٧٢)) .','704','1167','0');
INSERT INTO [t22928] VALUES (1214,'قال تعالى: (قالوا أتعجبين من أمر الله رحمت الله وبركاته عليكم أهل البيت إنه حميد مجيد (٧٣)) .','705','1167','0');
INSERT INTO [t22928] VALUES (1215,'قال تعالى: (فلما ذهب عن إبراهيم الروع وجاءته البشرى يجادلنا في قوم لوط (٧٤)) .','705','1167','0');
INSERT INTO [t22928] VALUES (1216,'قال تعالى: (إن إبراهيم لحليم أواه منيب (٧٥)) .','705','1167','0');
INSERT INTO [t22928] VALUES (1217,'قال تعالى: (يا إبراهيم أعرض عن هذا إنه قد جاء أمر ربك وإنهم آتيهم عذاب غير مردود (٧٦)) .','705','1167','0');
INSERT INTO [t22928] VALUES (1218,'قال تعالى: (ولما جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقال هذا يوم عصيب (٧٧) وجاءه قومه يهرعون إليه ومن قبل كانوا يعملون السيئات قال ياقوم هؤلاء بناتي هن أطهر لكم فاتقوا الله ولا تخزوني في ضيفي أليس منكم رجل رشيد (٧٨)) .','706','1167','0');
INSERT INTO [t22928] VALUES (1219,'قال تعالى: (قالوا لقد علمت ما لنا في بناتك من حق وإنك لتعلم ما نريد (٧٩)) .','706','1167','0');
INSERT INTO [t22928] VALUES (1220,'قال تعالى: (قال لو أن لي بكم قوة أو آوي إلى ركن شديد (٨٠)) .','707','1167','0');
INSERT INTO [t22928] VALUES (1221,'قال تعالى: (قالوا يا لوط إنا رسل ربك لن يصلوا إليك فأسر بأهلك بقطع من الليل ولا يلتفت منكم أحد إلا امرأتك إنه مصيبها ما أصابهم إن موعدهم الصبح أليس الصبح بقريب (٨١))','707','1167','0');
INSERT INTO [t22928] VALUES (1222,'قال تعالى: (فلما جاء أمرنا جعلنا عاليها سافلها وأمطرنا عليها حجارة من سجيل منضود (٨٢)) .','707','1167','0');
INSERT INTO [t22928] VALUES (1223,'قال تعالى: (وإلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من إله غيره ولا تنقصوا المكيال والميزان إني أراكم بخير وإني أخاف عليكم عذاب يوم محيط (٨٤)) .','708','1167','0');
INSERT INTO [t22928] VALUES (1224,'قال تعالى: (قالوا يا شعيب أصلاتك تأمرك أن نترك ما يعبد آباؤنا أو أن نفعل في أموالنا ما نشاء إنك لأنت الحليم الرشيد (٨٧)) .','708','1167','0');
INSERT INTO [t22928] VALUES (1225,'قال تعالى: (ويا قوم لا يجرمنكم شقاقي أن يصيبكم مثل ما أصاب قوم نوح أو قوم هود أو قوم صالح وما قوم لوط منكم ببعيد (٨٩)) .','709','1167','0');
INSERT INTO [t22928] VALUES (1226,'قال تعالى: (قال يا قوم أرهطي أعز عليكم من الله واتخذتموه وراءكم ظهريا إن ربي بما تعملون محيط (٩٢)) .','709','1167','0');
INSERT INTO [t22928] VALUES (1227,'قال تعالى: (ويا قوم اعملوا على مكانتكم إني عامل سوف تعلمون من يأتيه عذاب يخزيه ومن هو كاذب وارتقبوا إني معكم رقيب (٩٣)) .','709','1167','0');
INSERT INTO [t22928] VALUES (1228,'قال تعالى {كأن لم يغنوا فيها ألا بعدا لمدين كما بعدت ثمود}','709','1167','0');
INSERT INTO [t22928] VALUES (1229,'قال تعالى: (يقدم قومه يوم القيامة فأوردهم النار وبئس الورد المورود (٩٨)) .','709','1167','0');
INSERT INTO [t22928] VALUES (1230,'قال تعالى: (ذلك من أنباء القرى نقصه عليك منها قائم وحصيد (١٠٠)) .','710','1167','0');
INSERT INTO [t22928] VALUES (1231,'قال تعالى: (وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة إن أخذه أليم شديد (١٠٢)) .','710','1167','0');
INSERT INTO [t22928] VALUES (1232,'قال تعالى: (إن في ذلك لآية لمن خاف عذاب الآخرة ذلك يوم مجموع له الناس وذلك يوم مشهود (١٠٣)) .','710','1167','0');
INSERT INTO [t22928] VALUES (1233,'قال تعالى: (يوم يأت لا تكلم نفس إلا بإذنه فمنهم شقي وسعيد (١٠٥)) .','710','1167','0');
INSERT INTO [t22928] VALUES (1234,'قال تعالى: (فأما الذين شقوا ففي النار لهم فيها زفير وشهيق (١٠٦) خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك إن ربك فعال لما يريد (١٠٧) وأما الذين سعدوا ففي الجنة خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك عطاء غير مجذوذ (١٠٨)) .','711','1167','0');
INSERT INTO [t22928] VALUES (1235,'قال تعالى: (فلا تك في مرية مما يعبد هؤلاء ما يعبدون إلا كما يعبد آباؤهم من قبل وإنا لموفوهم نصيبهم غير منقوص (١٠٩)) .','712','1167','0');
INSERT INTO [t22928] VALUES (1236,'قال تعالى: (وإن كلا لما ليوفينهم ربك أعمالهم إنه بما يعملون خبير (١١١)) .','712','1167','0');
INSERT INTO [t22928] VALUES (1237,'قال تعالى: (فاستقم كما أمرت ومن تاب معك ولا تطغوا إنه بما تعملون بصير (١١٢)) .','714','1167','0');
INSERT INTO [t22928] VALUES (1238,'قال تعالى: (ولا تركنوا إلى الذين ظلموا فتمسكم النار وما لكم من دون الله من أولياء ثم لا تنصرون (١١٣)) .','714','1167','0');
INSERT INTO [t22928] VALUES (1239,'قال تعالى: (وأقم الصلاة طرفي النهار وزلفا من الليل إن الحسنات يذهبن السيئات ذلك ذكرى للذاكرين (١١٤)) .','715','1167','0');
INSERT INTO [t22928] VALUES (1240,'قال تعالى: (فلولا كان من القرون من قبلكم أولو بقية ينهون عن الفساد في الأرض إلا قليلا ممن أنجينا منهم واتبع الذين ظلموا ما أترفوا فيه وكانوا مجرمين (١١٦))','715','1167','0');
INSERT INTO [t22928] VALUES (1241,'قال تعالى: (إلا من رحم ربك ولذلك خلقهم وتمت كلمة ربك لأملأن جهنم من الجنة والناس أجمعين (١١٩)) .','715','1167','0');
INSERT INTO [t22928] VALUES (1242,'قال تعالى: (وكلا نقص عليك من أنباء الرسل ما نثبت به فؤادك وجاءك في هذه الحق وموعظة وذكرى للمؤمنين (١٢٠)) .','716','1167','0');
INSERT INTO [t22928] VALUES (1243,'يوسف','717','0','0');
INSERT INTO [t22928] VALUES (1244,'قال تعالى: (الر تلك آيات الكتاب المبين (١)) .','717','1243','0');
INSERT INTO [t22928] VALUES (1245,'قال تعالى: (إنا أنزلناه قرآنا عربيا لعلكم تعقلون (٢)) .','717','1243','0');
INSERT INTO [t22928] VALUES (1246,'قال تعالى: (نحن نقص عليك أحسن القصص بما أوحينا إليك هذا القرآن وإن كنت من قبله لمن الغافلين (٣)) .','717','1243','0');
INSERT INTO [t22928] VALUES (1247,'قال تعالى: (إذ قال يوسف لأبيه يا أبت إني رأيت أحد عشر كوكبا والشمس والقمر رأيتهم لي ساجدين (٤)) .','718','1243','0');
INSERT INTO [t22928] VALUES (1248,'قال تعالى: (قال يا بني لا تقصص رؤياك على إخوتك فيكيدوا لك كيدا إن الشيطان للإنسان عدو مبين (٥)) .','719','1243','0');
INSERT INTO [t22928] VALUES (1249,'قال تعالى: (وكذلك يجتبيك ربك ويعلمك من تأويل الأحاديث ويتم نعمته عليك وعلى آل يعقوب كما أتمها على أبويك من قبل إبراهيم وإسحاق إن ربك عليم حكيم (٦)) .','719','1243','0');
INSERT INTO [t22928] VALUES (1250,'قال تعالى: (لقد كان في يوسف وإخوته آيات للسائلين (٧)) .','720','1243','0');
INSERT INTO [t22928] VALUES (1251,'قال تعالى: (اقتلوا يوسف أو اطرحوه أرضا يخل لكم وجه أبيكم وتكونوا من بعده قوما صالحين (٩)) .','720','1243','0');
INSERT INTO [t22928] VALUES (1252,'قال تعالى: (قال قائل منهم لا تقتلوا يوسف وألقوه في غيابة الجب يلتقطه بعض السيارة إن كنتم فاعلين (١٠)) .','720','1243','0');
INSERT INTO [t22928] VALUES (1253,'قال تعالى: (قالوا يا أبانا ما لك لا تأمنا على يوسف وإنا له لناصحون (١١)) .','721','1243','0');
INSERT INTO [t22928] VALUES (1254,'قال تعالى: (أرسله معنا غدا يرتع ويلعب وإنا له لحافظون (١٢)) .','721','1243','0');
INSERT INTO [t22928] VALUES (1255,'قال تعالى: {قال إني ليحزنني أن تذهبوا به وأخاف أن يأكله الذئب وأنتم عنه غافلون}','722','1243','0');
INSERT INTO [t22928] VALUES (1256,'قال تعالى: (قالوا لئن أكله الذئب ونحن عصبة إنا إذا لخاسرون (١٤)) .','722','1243','0');
INSERT INTO [t22928] VALUES (1257,'قال تعالى: (فلما ذهبوا به وأجمعوا أن يجعلوه في غيابة الجب وأوحينا إليه لتنبئنهم بأمرهم هذا وهم لا يشعرون (١٥)) .','722','1243','0');
INSERT INTO [t22928] VALUES (1258,'قال تعالى: (وجاءوا أباهم عشاء يبكون (١٦)) .','722','1243','0');
INSERT INTO [t22928] VALUES (1259,'قال تعالى: (وجاءوا على قميصه بدم كذب قال بل سولت لكم أنفسكم أمرا فصبر جميل والله المستعان على ما تصفون (١٨)) .','723','1243','0');
INSERT INTO [t22928] VALUES (1260,'قال تعالى: (وجاءت سيارة فأرسلوا واردهم فأدلى دلوه قال يا بشرى هذا غلام وأسروه بضاعة والله عليم بما يعملون (١٩)) .','723','1243','0');
INSERT INTO [t22928] VALUES (1261,'قال تعالى: (وشروه بثمن بخس دراهم معدودة وكانوا فيه من الزاهدين (٢٠)) .','724','1243','0');
INSERT INTO [t22928] VALUES (1262,'قال تعالى: (وقال الذي اشتراه من مصر لامرأته أكرمي مثواه عسى أن ينفعنا أو نتخذه ولدا وكذلك مكنا ليوسف في الأرض ولنعلمه من تأويل الأحاديث والله غالب على أمره ولكن أكثر الناس لا يعلمون (٢١)) .','724','1243','0');
INSERT INTO [t22928] VALUES (1263,'قال تعالى: (وراودته التي هو في بيتها عن نفسه وغلقت الأبواب وقالت هيت لك قال معاذ الله إنه ربي أحسن مثواي إنه لا يفلح الظالمون (٢٣)) .','724','1243','0');
INSERT INTO [t22928] VALUES (1264,'قال تعالى: (ولقد همت به وهم بها لولا أن رأى برهان ربه كذلك لنصرف عنه السوء والفحشاء إنه من عبادنا المخلصين (٢٤)) .','726','1243','0');
INSERT INTO [t22928] VALUES (1265,'قال تعالى: (واستبقا الباب وقدت قميصه من دبر وألفيا سيدها لدى الباب قالت ما جزاء من أراد بأهلك سوءا إلا أن يسجن أو عذاب أليم (٢٥)) .','726','1243','0');
INSERT INTO [t22928] VALUES (1266,'قال تعالى: (يوسف أعرض عن هذا واستغفري لذنبك إنك كنت من الخاطئين (٢٩)) .','726','1243','0');
INSERT INTO [t22928] VALUES (1267,'قال تعالى: (وقال نسوة في المدينة امرأة العزيز تراود فتاها عن نفسه قد شغفها حبا إنا لنراها في ضلال مبين (٣٠)) .','727','1243','0');
INSERT INTO [t22928] VALUES (1268,'قال تعالى: (فلما سمعت بمكرهن أرسلت إليهن وأعتدت لهن متكأ وآتت كل واحدة منهن سكينا وقالت اخرج عليهن فلما رأينه أكبرنه وقطعن أيديهن وقلن حاش لله ما هذا بشرا إن هذا إلا ملك كريم (٣١)) .','727','1243','0');
INSERT INTO [t22928] VALUES (1269,'قال تعالى: (قال رب السجن أحب إلي مما يدعونني إليه وإلا تصرف عني كيدهن أصب إليهن وأكن من الجاهلين (٣٣)) .','729','1243','0');
INSERT INTO [t22928] VALUES (1270,'قال تعالى: (ثم بدا لهم من بعد ما رأوا الآيات ليسجننه حتى حين (٣٥)) .','729','1243','0');
INSERT INTO [t22928] VALUES (1271,'قال تعالى: (ودخل معه السجن فتيان قال أحدهما إني أراني أعصر خمرا وقال الآخر إني أراني أحمل فوق رأسي خبزا تأكل الطير منه نبئنا بتأويله إنا نراك من المحسنين (٣٦)) .','729','1243','0');
INSERT INTO [t22928] VALUES (1272,'قال تعالى: (يا صاحبي السجن أأرباب متفرقون خير أم الله الواحد القهار (٣٩) ما تعبدون من دونه إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان إن الحكم إلا لله أمر ألا تعبدوا إلا إياه ذلك الدين القيم ولكن أكثر الناس لا يعلمون (٤٠) ياصاحبي السجن أما أحدكما فيسقي ربه خمرا وأما الآخر فيصلب فتأكل الطير من رأسه قضي الأمر الذي فيه تستفتيان (٤١)) .','730','1243','0');
INSERT INTO [t22928] VALUES (1273,'قال تعالى: (وقال للذي ظن أنه ناج منهما اذكرني عند ربك فأنساه الشيطان ذكر ربه فلبث في السجن بضع سنين (٤٢)) .','730','1243','0');
INSERT INTO [t22928] VALUES (1274,'قال تعالى: (وقال الملك إني أرى سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات يا أيها الملأ أفتوني في رؤياي إن كنتم للرؤيا تعبرون (٤٣))','730','1243','0');
INSERT INTO [t22928] VALUES (1275,'قال تعالى: (قالوا أضغاث أحلام وما نحن بتأويل الأحلام بعالمين (٤٤)) .','730','1243','0');
INSERT INTO [t22928] VALUES (1276,'قال تعالى: (وقال الذي نجا منهما وادكر بعد أمة أنا أنبئكم بتأويله فأرسلون (٤٥)) .','731','1243','0');
INSERT INTO [t22928] VALUES (1277,'قال تعالى: (قال تزرعون سبع سنين دأبا فما حصدتم فذروه في سنبله إلا قليلا مما تأكلون (٤٧)) .','731','1243','0');
INSERT INTO [t22928] VALUES (1278,'قال تعالى: (ثم يأتي من بعد ذلك عام فيه يغاث الناس وفيه يعصرون (٤٩)) .','731','1243','0');
INSERT INTO [t22928] VALUES (1279,'قال تعالى: (قال ما خطبكن إذ راودتن يوسف عن نفسه قلن حاش لله ما علمنا عليه من سوء قالت امرأة العزيز الآن حصحص الحق أنا راودته عن نفسه وإنه لمن الصادقين (٥١)) .','732','1243','0');
INSERT INTO [t22928] VALUES (1280,'قال تعالى: (ذلك ليعلم أني لم أخنه بالغيب وأن الله لا يهدي كيد الخائنين (٥٢)) .','732','1243','0');
INSERT INTO [t22928] VALUES (1281,'قال تعالى: (وما أبرئ نفسي إن النفس لأمارة بالسوء إلا ما رحم ربي إن ربي غفور رحيم (٥٣)) .','732','1243','0');
INSERT INTO [t22928] VALUES (1282,'قال تعالى: (وكذلك مكنا ليوسف في الأرض يتبوأ منها حيث يشاء نصيب برحمتنا من نشاء ولا نضيع أجر المحسنين (٥٦)) .','732','1243','0');
INSERT INTO [t22928] VALUES (1283,'قال تعالى: (وقال لفتيانه اجعلوا بضاعتهم في رحالهم لعلهم يعرفونها إذا انقلبوا إلى أهلهم لعلهم يرجعون (٦٢)) .','733','1243','0');
INSERT INTO [t22928] VALUES (1284,'قال تعالى: (فلما رجعوا إلى أبيهم قالوا يا أبانا منع منا الكيل فأرسل معنا أخانا نكتل وإنا له لحافظون (٦٣)) .','733','1243','0');
INSERT INTO [t22928] VALUES (1285,'قال تعالى: (قال هل آمنكم عليه إلا كما أمنتكم على أخيه من قبل فالله خير حافظا وهو أرحم الراحمين (٦٤)) .','733','1243','0');
INSERT INTO [t22928] VALUES (1286,'قال تعالى: (ولما فتحوا متاعهم وجدوا بضاعتهم ردت إليهم قالوا يا أبانا ما نبغي هذه بضاعتنا ردت إلينا ونمير أهلنا ونحفظ أخانا ونزداد كيل بعير ذلك كيل يسير (٦٥)) .','734','1243','0');
INSERT INTO [t22928] VALUES (1287,'قال تعالى: (قال لن أرسله معكم حتى تؤتون موثقا من الله لتأتنني به إلا أن يحاط بكم فلما آتوه موثقهم قال الله على ما نقول وكيل (٦٦)) .','734','1243','0');
INSERT INTO [t22928] VALUES (1288,'قال تعالى: (ولما دخلوا من حيث أمرهم أبوهم ما كان يغني عنهم من الله من شيء إلا حاجة في نفس يعقوب قضاها وإنه لذو علم لما علمناه ولكن أكثر الناس لا يعلمون (٦٨)) .','735','1243','0');
INSERT INTO [t22928] VALUES (1289,'قال تعالى: (ولما دخلوا على يوسف آوى إليه أخاه قال إني أنا أخوك فلا تبتئس بما كانوا يعملون (٦٩)) .','735','1243','0');
INSERT INTO [t22928] VALUES (1290,'قال تعالى: (قالوا نفقد صواع الملك ولمن جاء به حمل بعير وأنا به زعيم (٧٢) قالوا تالله لقد علمتم ما جئنا لنفسد في الأرض وما كنا سارقين (٧٣) قالوا فما جزاؤه إن كنتم كاذبين (٧٤) قالوا جزاؤه من وجد في رحله فهو جزاؤه كذلك نجزي الظالمين (٧٥)) .','735','1243','0');
INSERT INTO [t22928] VALUES (1291,'قال تعالى: (فبدأ بأوعيتهم قبل وعاء أخيه ثم استخرجها من وعاء أخيه كذلك كدنا ليوسف ما كان ليأخذ أخاه في دين الملك إلا أن يشاء الله نرفع درجات من نشاء وفوق كل ذي علم عليم (٧٦)) .','736','1243','0');
INSERT INTO [t22928] VALUES (1292,'قال تعالى: (قالوا إن يسرق فقد سرق أخ له من قبل فأسرها يوسف في نفسه ولم يبدها لهم قال أنتم شر مكانا والله أعلم بما تصفون (٧٧)) .','737','1243','0');
INSERT INTO [t22928] VALUES (1293,'قال تعالى: (قالوا يا أيها العزيز إن له أبا شيخا كبيرا فخذ أحدنا مكانه إنا نراك من المحسنين (٧٨)) .','738','1243','0');
INSERT INTO [t22928] VALUES (1294,'قال تعالى: (قال معاذ الله أن نأخذ إلا من وجدنا متاعنا عنده إنا إذا لظالمون (٧٩)) .','738','1243','0');
INSERT INTO [t22928] VALUES (1295,'قال تعالى: (فلما استيأسوا منه خلصوا نجيا قال كبيرهم ألم تعلموا أن أباكم قد أخذ عليكم موثقا من الله ومن قبل ما فرطتم في يوسف فلن أبرح الأرض حتى يأذن لي أبي أو يحكم الله لي وهو خير الحاكمين (٨٠)) .','738','1243','0');
INSERT INTO [t22928] VALUES (1296,'قال تعالى: (ارجعوا إلى أبيكم فقولوا يا أبانا إن ابنك سرق وما شهدنا إلا بما علمنا وما كنا للغيب حافظين (٨١)) .','739','1243','0');
INSERT INTO [t22928] VALUES (1297,'قال تعالى: (واسأل القرية التي كنا فيها والعير التي أقبلنا فيها وإنا لصادقون (٨٢)) .','739','1243','0');
INSERT INTO [t22928] VALUES (1298,'قال تعالى: (وتولى عنهم وقال يا أسفى على يوسف وابيضت عيناه من الحزن فهو كظيم (٨٤)) .','740','1243','0');
INSERT INTO [t22928] VALUES (1299,'قال تعالى: (قالوا تالله تفتأ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين (٨٥)) .','740','1243','0');
INSERT INTO [t22928] VALUES (1300,'قال تعالى: (يا بني اذهبوا فتحسسوا من يوسف وأخيه ولا تيأسوا من روح الله إنه لا ييئس من روح الله إلا القوم الكافرون (٨٧)) .','740','1243','0');
INSERT INTO [t22928] VALUES (1301,'قال تعالى: (فلما دخلوا عليه قالوا يا أيها العزيز مسنا وأهلنا الضر وجئنا ببضاعة مزجاة فأوف لنا الكيل وتصدق علينا إن الله يجزي المتصدقين (٨٨)) .','740','1243','0');
INSERT INTO [t22928] VALUES (1302,'قال تعالى: (قالوا أئنك لأنت يوسف قال أنا يوسف وهذا أخي قد من الله علينا إنه من يتق ويصبر فإن الله لا يضيع أجر المحسنين (٩٠)) .','740','1243','0');
INSERT INTO [t22928] VALUES (1303,'قال تعالى: (قال لا تثريب عليكم اليوم يغفر الله لكم وهو أرحم الراحمين (٩٢)) .','741','1243','0');
INSERT INTO [t22928] VALUES (1304,'قال تعالى: (اذهبوا بقميصي هذا فألقوه على وجه أبي يأت بصيرا وأتوني بأهلكم أجمعين (٩٣)) .','742','1243','0');
INSERT INTO [t22928] VALUES (1305,'قال تعالى: (ورفع أبويه على العرش وخروا له سجدا وقال يا أبت هذا تأويل رؤياي من قبل قد جعلها ربي حقا وقد أحسن بي إذ أخرجني من السجن وجاء بكم من البدو من بعد أن نزغ الشيطان بيني وبين إخوتي إن ربي لطيف لما يشاء إنه هو العليم الحكيم (١٠٠)) .','742','1243','0');
INSERT INTO [t22928] VALUES (1306,'قال تعالى: (رب قد آتيتني من الملك وعلمتني من تأويل الأحاديث فاطر السماوات والأرض أنت وليي في الدنيا والآخرة توفني مسلما وألحقني بالصالحين (١٠١)) .','743','1243','0');
INSERT INTO [t22928] VALUES (1307,'قال تعالى: (وكأين من آية في السماوات والأرض يمرون عليها وهم عنها معرضون وما يؤمن أكثرهم بالله إلا وهم مشركون (١٠٦) أفأمنوا أن تأتيهم غاشية من عذاب الله أو تأتيهم الساعة بغتة وهم لا يشعرون (١٠٧) قل هذه سبيلي أدعو إلى الله على بصيرة أنا ومن اتبعني وسبحان الله وما أنا من المشركين (١٠٨) وما أرسلنا من قبلك إلا رجالا نوحي إليهم من أهل القرى أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ولدار الآخرة خير للذين اتقوا أفلا تعقلون (١٠٩)) .','743','1243','0');
INSERT INTO [t22928] VALUES (1308,'قال تعالى: (حتى إذا استيأس الرسل وظنوا أنهم قد كذبوا جاءهم نصرنا فنجي من نشاء ولا يرد بأسنا عن القوم المجرمين (١١٠)) .','744','1243','0');
INSERT INTO [t22928] VALUES (1309,'قال تعالى: (لقد كان في قصصهم عبرة لأولي الألباب ما كان حديثا يفترى ولكن تصديق الذي بين يديه وتفصيل كل شيء وهدى ورحمة لقوم يؤمنون (١٢١)) .','745','1243','0');
INSERT INTO [t22928] VALUES (1310,'الرعد','746','0','0');
INSERT INTO [t22928] VALUES (1311,'قال تعالى: (المر تلك آيات الكتاب والذي أنزل إليك من ربك الحق ولكن أكثر الناس لا يؤمنون (١))','746','1310','0');
INSERT INTO [t22928] VALUES (1312,'قال تعالى: الله الذي رفع السماوات بغير عمد ترونها ثم استوى على العرش وسخر الشمس والقمر كل يجري لأجل مسمى يدبر الأمر يفصل الآيات لعلكم بلقاء ربكم توقنون (٢))','746','1310','0');
INSERT INTO [t22928] VALUES (1313,'قال تعالى: (وهو الذي مد الأرض وجعل فيها رواسي وأنهارا ومن كل الثمرات جعل فيها زوجين اثنين يغشي الليل النهار إن في ذلك لآيات لقوم يتفكرون (٣))','747','1310','0');
INSERT INTO [t22928] VALUES (1314,'قال تعالى: (وفي الأرض قطع متجاورات وجنات من أعناب وزرع ونخيل صنوان وغير صنوان يسقى بماء واحد ونفضل بعضها على بعض في الأكل إن في ذلك لآيات لقوم يعقلون (٤)) .','747','1310','0');
INSERT INTO [t22928] VALUES (1315,'قال تعالى: (وإن تعجب فعجب قولهم أئذا كنا ترابا أئنا لفي خلق جديد أولئك الذين كفروا بربهم وأولئك الأغلال في أعناقهم وأولئك أصحاب النار هم فيها خالدون (٥))','748','1310','0');
INSERT INTO [t22928] VALUES (1316,'قال تعالى: (ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلات وإن ربك لذو مغفرة للناس على ظلمهم وإن ربك لشديد العقاب (٦)) .','748','1310','0');
INSERT INTO [t22928] VALUES (1317,'قال تعالى: (ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلات وإن ربك لذو مغفرة للناس على ظلمهم وإن ربك لشديد العقاب (٧)) .','749','1310','0');
INSERT INTO [t22928] VALUES (1318,'قال تعالى: (الله يعلم ما تحمل كل أنثى وما تغيض الأرحام وما تزداد وكل شيء عنده بمقدار (٨)) .','749','1310','0');
INSERT INTO [t22928] VALUES (1319,'قال تعالى: (عالم الغيب والشهادة الكبير المتعال (٩)) .','750','1310','0');
INSERT INTO [t22928] VALUES (1320,'قال تعالى: {سواء منكم من أسر القول ومن جهر به ومن هو مستخف بالليل وسارب بالنهار}','750','1310','0');
INSERT INTO [t22928] VALUES (1321,'قال تعالى: (له معقبات من بين يديه ومن خلفه يحفظونه من أمر الله إن الله لا يغير ما بقوم حتى يغيروا ما بأنفسهم وإذا أراد الله بقوم سوءا فلا مرد له وما لهم من دونه من وال (١١)) .','750','1310','0');
INSERT INTO [t22928] VALUES (1322,'قال تعالى: (هو الذي يريكم البرق خوفا وطمعا وينشئ السحاب الثقال (١٢)) .','751','1310','0');
INSERT INTO [t22928] VALUES (1323,'قال تعالى: (ويسبح الرعد بحمده والملائكة من خيفته ويرسل الصواعق فيصيب بها من يشاء وهم يجادلون في الله وهو شديد المحال (١٣)) .','751','1310','0');
INSERT INTO [t22928] VALUES (1324,'قال تعالى: (له دعوة الحق والذين يدعون من دونه لا يستجيبون لهم بشيء إلا كباسط كفيه إلى الماء ليبلغ فاه وما هو ببالغه وما دعاء الكافرين إلا في ضلال (١٤)) .','751','1310','0');
INSERT INTO [t22928] VALUES (1325,'قال تعالى: (ولله يسجد من في السماوات والأرض طوعا وكرها وظلالهم بالغدو والآصال (١٥)) .','752','1310','0');
INSERT INTO [t22928] VALUES (1326,'قال تعالى: (قل من رب السماوات والأرض قل الله قل أفاتخذتم من دونه أولياء لا يملكون لأنفسهم نفعا ولا ضرا قل هل يستوي الأعمى والبصير أم هل تستوي الظلمات والنور أم جعلوا لله شركاء خلقوا كخلقه فتشابه الخلق عليهم قل الله خالق كل شيء وهو الواحد القهار (١٦)) .','752','1310','0');
INSERT INTO [t22928] VALUES (1327,'قال تعالى: (أنزل من السماء ماء فسالت أودية بقدرها فاحتمل السيل زبدا رابيا ومما يوقدون عليه في النار ابتغاء حلية أو متاع زبد مثله كذلك يضرب الله الحق والباطل فأما الزبد فيذهب جفاء وأما ما ينفع الناس فيمكث في الأرض كذلك يضرب الله الأمثال (١٧)) .','753','1310','0');
INSERT INTO [t22928] VALUES (1328,'قال تعالى: (للذين استجابوا لربهم الحسنى والذين لم يستجيبوا له لو أن لهم ما في الأرض جميعا ومثله معه لافتدوا به أولئك لهم سوء الحساب ومأواهم جهنم وبئس المهاد (١٨)) .','753','1310','0');
INSERT INTO [t22928] VALUES (1329,'قال تعالى: (الذين يوفون بعهد الله ولا ينقضون الميثاق (٢٠)) .','753','1310','0');
INSERT INTO [t22928] VALUES (1330,'قال تعالى: (جنات عدن يدخلونها ومن صلح من آبائهم وأزواجهم وذرياتهم والملائكة يدخلون عليهم من كل باب (٢٣)) .','754','1310','0');
INSERT INTO [t22928] VALUES (1331,'قال تعالى: (سلام عليكم بما صبرتم فنعم عقبى الدار (٢٤)) .','754','1310','0');
INSERT INTO [t22928] VALUES (1332,'قال تعالى: (الله يبسط الرزق لمن يشاء ويقدر وفرحوا بالحياة الدنيا وما الحياة الدنيا في الآخرة إلا متاع (٢٦)) .','754','1310','0');
INSERT INTO [t22928] VALUES (1333,'قال تعالى: (الذين آمنوا وتطمئن قلوبهم بذكر الله ألا بذكر الله تطمئن القلوب (٢٨)) .','754','1310','0');
INSERT INTO [t22928] VALUES (1334,'قال تعالى: (الذين آمنوا وعملوا الصالحات طوبى لهم وحسن مآب (٢٩)) .','755','1310','0');
INSERT INTO [t22928] VALUES (1335,'قال تعالى: (كذلك أرسلناك في أمة قد خلت من قبلها أمم لتتلو عليهم الذي أوحينا إليك وهم يكفرون بالرحمن قل هو ربي لا إله إلا هو عليه توكلت وإليه متاب (٣٠)) .','755','1310','0');
INSERT INTO [t22928] VALUES (1336,'قال تعالى: (ولو أن قرآنا سيرت به الجبال أو قطعت به الأرض أو كلم به الموتى بل لله الأمر جميعا أفلم ييأس الذين آمنوا أن لو يشاء الله لهدى الناس جميعا ولا يزال الذين كفروا تصيبهم بما صنعوا قارعة أو تحل قريبا من دارهم حتى يأتي وعد الله إن الله لا يخلف الميعاد (٣١)) .','755','1310','0');
INSERT INTO [t22928] VALUES (1337,'قال تعالى: (أفمن هو قائم على كل نفس بما كسبت وجعلوا لله شركاء قل سموهم أم تنبئونه بما لا يعلم في الأرض أم بظاهر من القول بل زين للذين كفروا مكرهم وصدوا عن السبيل ومن يضلل الله فما له من هاد (٣٣)) .','756','1310','0');
INSERT INTO [t22928] VALUES (1338,'قال تعالى: (مثل الجنة التي وعد المتقون تجري من تحتها الأنهار أكلها دائم وظلها تلك عقبى الذين اتقوا وعقبى الكافرين النار (٣٥)) .','756','1310','0');
INSERT INTO [t22928] VALUES (1339,'قال تعالى: (أولم يروا أنا نأتي الأرض ننقصها من أطرافها والله يحكم لا معقب لحكمه وهو سريع الحساب (٤١)) .','757','1310','0');
INSERT INTO [t22928] VALUES (1340,'قال تعالى: (وقد مكر الذين من قبلهم فلله المكر جميعا يعلم ما تكسب كل نفس وسيعلم الكفار لمن عقبى الدار (٤٢)) .','757','1310','0');
INSERT INTO [t22928] VALUES (1341,'قال تعالى: (ويقول الذين كفروا لست مرسلا قل كفى بالله شهيدا بيني وبينكم ومن عنده علم الكتاب (٤٣)) .','757','1310','0');
INSERT INTO [t22928] VALUES (1342,'إبراهيم','759','0','0');
INSERT INTO [t22928] VALUES (1343,'قال تعالى: (الر كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور بإذن ربهم إلى صراط العزيز الحميد (١)) .','759','1342','0');
INSERT INTO [t22928] VALUES (1344,'قال تعالى: (الله الذي له ما في السماوات وما في الأرض وويل للكافرين من عذاب شديد (٢)) .','759','1342','0');
INSERT INTO [t22928] VALUES (1345,'قال تعالى: (الذين يستحبون الحياة الدنيا على الآخرة ويصدون عن سبيل الله ويبغونها عوجا أولئك في ضلال بعيد (٣)) .','760','1342','0');
INSERT INTO [t22928] VALUES (1346,'قال تعالى: (وما أرسلنا من رسول إلا بلسان قومه ليبين لهم فيضل الله من يشاء ويهدي من يشاء وهو العزيز الحكيم (٤)) .','760','1342','0');
INSERT INTO [t22928] VALUES (1347,'قال تعالى: (ولقد أرسلنا موسى بآياتنا أن أخرج قومك من الظلمات إلى النور وذكرهم بأيام الله إن في ذلك لآيات لكل صبار شكور (٥)) .','760','1342','0');
INSERT INTO [t22928] VALUES (1348,'قال تعالى: (وإذ قال موسى لقومه اذكروا نعمة الله عليكم إذ أنجاكم من آل فرعون يسومونكم سوء العذاب ويذبحون أبناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم (٦)) .','760','1342','0');
INSERT INTO [t22928] VALUES (1349,'قال تعالى: (وإذ تأذن ربكم لئن شكرتم لأزيدنكم ولئن كفرتم إن عذابي لشديد (٧)) .','761','1342','0');
INSERT INTO [t22928] VALUES (1350,'قال تعالى: (ألم يأتكم نبأ الذين من قبلكم قوم نوح وعاد وثمود والذين من بعدهم لا يعلمهم إلا الله جاءتهم رسلهم بالبينات فردوا أيديهم في أفواههم وقالوا إنا كفرنا بما أرسلتم به وإنا لفي شك مما تدعوننا إليه مريب (٩)) .','761','1342','0');
INSERT INTO [t22928] VALUES (1351,'قال تعالى: (قالت رسلهم أفي الله شك فاطر السماوات والأرض يدعوكم ليغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى قالوا إن أنتم إلا بشر مثلنا تريدون أن تصدونا عما كان يعبد آباؤنا فأتونا بسلطان مبين (١٠)) .','761','1342','0');
INSERT INTO [t22928] VALUES (1352,'قال تعالى: (قالت لهم رسلهم إن نحن إلا بشر مثلكم ولكن الله يمن على من يشاء من عباده وما كان لنا أن نأتيكم بسلطان إلا بإذن الله وعلى الله فليتوكل المؤمنون (١١)) .','762','1342','0');
INSERT INTO [t22928] VALUES (1353,'قال تعالى: (وما لنا ألا نتوكل على الله وقد هدانا سبلنا ولنصبرن على ما آذيتمونا وعلى الله فليتوكل المتوكلون (١٢)) .','762','1342','0');
INSERT INTO [t22928] VALUES (1354,'قال تعالى: (واستفتحوا وخاب كل جبار عنيد (١٥)) .','762','1342','0');
INSERT INTO [t22928] VALUES (1355,'قال تعالى: (يتجرعه ولا يكاد يسيغه ويأتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ (١٧)) .','762','1342','0');
INSERT INTO [t22928] VALUES (1356,'قال تعالى: (مثل الذين كفروا بربهم أعمالهم كرماد اشتدت به الريح في يوم عاصف لا يقدرون مما كسبوا على شيء ذلك هو الضلال البعيد (١٨)) .','762','1342','0');
INSERT INTO [t22928] VALUES (1357,'قال تعالى: (ألم تر أن الله خلق السماوات والأرض بالحق إن يشأ يذهبكم ويأت بخلق جديد (١٩)) .','763','1342','0');
INSERT INTO [t22928] VALUES (1358,'قال تعالى: (وبرزوا لله جميعا فقال الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا من عذاب الله من شيء قالوا لو هدانا الله لهديناكم سواء علينا أجزعنا أم صبرنا ما لنا من محيص (٢١)) .','763','1342','0');
INSERT INTO [t22928] VALUES (1359,'قال تعالى: (وقال الشيطان لما قضي الأمر إن الله وعدكم وعد الحق ووعدتكم فأخلفتكم وما كان لي عليكم من سلطان إلا أن دعوتكم فاستجبتم لي فلا تلوموني ولوموا أنفسكم ما أنا بمصرخكم وما أنتم بمصرخي إني كفرت بما أشركتموني من قبل إن الظالمين لهم عذاب أليم (٢٢)) .','764','1342','0');
INSERT INTO [t22928] VALUES (1360,'قال تعالى: (وأدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار خالدين فيها بإذن ربهم تحيتهم فيها سلام (٢٣)) .','765','1342','0');
INSERT INTO [t22928] VALUES (1361,'قال تعالى: (ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة أصلها ثابت وفرعها في السماء (٢٤)) .','765','1342','0');
INSERT INTO [t22928] VALUES (1362,'قال تعالى: (ومثل كلمة خبيثة كشجرة خبيثة اجتثت من فوق الأرض ما لها من قرار (٢٦)) .','766','1342','0');
INSERT INTO [t22928] VALUES (1363,'قال تعالى: (يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة ويضل الله الظالمين ويفعل الله ما يشاء (٢٧)) .','766','1342','0');
INSERT INTO [t22928] VALUES (1364,'قال تعالى: (ألم تر إلى الذين بدلوا نعمة الله كفرا وأحلوا قومهم دار البوار (٢٨)) .','766','1342','0');
INSERT INTO [t22928] VALUES (1365,'قال تعالى: (قل لعبادي الذين آمنوا يقيموا الصلاة وينفقوا مما رزقناهم سرا وعلانية من قبل أن يأتي يوم لا بيع فيه ولا خلال (٣١))','766','1342','0');
INSERT INTO [t22928] VALUES (1366,'قال تعالى: (وسخر لكم الفلك لتجري في البحر بأمره وسخر لكم الأنهار (٣٣)) .','767','1342','0');
INSERT INTO [t22928] VALUES (1367,'قال تعالى: (وآتاكم من كل ما سألتموه وإن تعدوا نعمة الله لا تحصوها إن الإنسان لظلوم كفار (٣٤)) .','767','1342','0');
INSERT INTO [t22928] VALUES (1368,'قال تعالى: (وإذ قال إبراهيم رب اجعل هذا البلد آمنا واجنبني وبني أن نعبد الأصنام (٣٥)) .','767','1342','0');
INSERT INTO [t22928] VALUES (1369,'قال تعالى: (رب إنهن أضللن كثيرا من الناس فمن تبعني فإنه مني ومن عصاني فإنك غفور رحيم (٣٦)) .','768','1342','0');
INSERT INTO [t22928] VALUES (1370,'قال تعالى: (ربنا إني أسكنت من ذريتي بواد غير ذي زرع عند بيتك المحرم ربنا ليقيموا الصلاة فاجعل أفئدة من الناس تهوي إليهم وارزقهم من الثمرات لعلهم يشكرون (٣٧)) .','768','1342','0');
INSERT INTO [t22928] VALUES (1371,'قال تعالى: (الحمد لله الذي وهب لي على الكبر إسماعيل وإسحاق إن ربي لسميع الدعاء (٣٩)) .','769','1342','0');
INSERT INTO [t22928] VALUES (1372,'قال تعالى: (رب اجعلني مقيم الصلاة ومن ذريتي ربنا وتقبل دعاء (٤٠)) .','769','1342','0');
INSERT INTO [t22928] VALUES (1373,'قال تعالى: (ولا تحسبن الله غافلا عما يعمل الظالمون إنما يؤخرهم ليوم تشخص فيه الأبصار (٤٢)) .','769','1342','0');
INSERT INTO [t22928] VALUES (1374,'قال تعالى: (مهطعين مقنعي رءوسهم لا يرتد إليهم طرفهم وأفئدتهم هواء (٤٣) وأنذر الناس يوم يأتيهم العذاب فيقول الذين ظلموا ربنا أخرنا إلى أجل قريب نجب دعوتك ونتبع الرسل أولم تكونوا أقسمتم من قبل ما لكم من زوال (٤٤)) .','769','1342','0');
INSERT INTO [t22928] VALUES (1375,'قال تعالى: (وسكنتم في مساكن الذين ظلموا أنفسهم وتبين لكم كيف فعلنا بهم وضربنا لكم الأمثال (٤٥)) .','770','1342','0');
INSERT INTO [t22928] VALUES (1376,'قال تعالى: (وقد مكروا مكرهم وعند الله مكرهم وإن كان مكرهم لتزول منه الجبال (٤٦)) .','770','1342','0');
INSERT INTO [t22928] VALUES (1377,'قال تعالى: (فلا تحسبن الله مخلف وعده رسله إن الله عزيز ذو انتقام (٤٧)) .','771','1342','0');
INSERT INTO [t22928] VALUES (1378,'قال تعالى: (سرابيلهم من قطران وتغشى وجوههم النار (٥٠)) .','772','1342','0');
INSERT INTO [t22928] VALUES (1379,'قال تعالى: (ليجزي الله كل نفس ما كسبت إن الله سريع الحساب (٥١)) .','772','1342','0');
INSERT INTO [t22928] VALUES (1380,'قال تعالى: (هذا بلاغ للناس ولينذروا به وليعلموا أنما هو إله واحد وليذكر أولو الألباب (٥٢)) .','772','1342','0');
INSERT INTO [t22928] VALUES (1381,'الحجر','773','0','0');
INSERT INTO [t22928] VALUES (1382,'قال تعالى: (الر تلك آيات الكتاب وقرآن مبين (١)) .','773','1381','0');
INSERT INTO [t22928] VALUES (1383,'قال تعالى: (ربما يود الذين كفروا لو كانوا مسلمين (٢))','773','1381','0');
INSERT INTO [t22928] VALUES (1384,'قال تعالى: (وما أهلكنا من قرية إلا ولها كتاب معلوم (٤)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1385,'قال تعالى: (لو ما تأتينا بالملائكة إن كنت من الصادقين (٧)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1386,'قال تعالى: (ما ننزل الملائكة إلا بالحق وما كانوا إذا منظرين (٨)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1387,'قال تعالى: (إنا نحن نزلنا الذكر وإنا له لحافظون (٩)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1388,'قال تعالى: (وما يأتيهم من رسول إلا كانوا به يستهزئون (١١)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1389,'قال تعالى: (كذلك نسلكه في قلوب المجرمين (١٢)) .','774','1381','0');
INSERT INTO [t22928] VALUES (1390,'قال تعالى: (ولو فتحنا عليهم بابا من السماء فظلوا فيه يعرجون (١٤) لقالوا إنما سكرت أبصارنا بل نحن قوم مسحورون (١٥)) .','775','1381','0');
INSERT INTO [t22928] VALUES (1391,'قال تعالى: (وحفظناها من كل شيطان رجيم (١٧) إلا من استرق السمع فأتبعه شهاب مبين (١٨)) .','775','1381','0');
INSERT INTO [t22928] VALUES (1392,'قال تعالى: (والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل شيء موزون (١٩)) .','776','1381','0');
INSERT INTO [t22928] VALUES (1393,'قال تعالى: (وجعلنا لكم فيها معايش ومن لستم له برازقين (٢٠)) .','776','1381','0');
INSERT INTO [t22928] VALUES (1394,'قال تعالى: (وإن من شيء إلا عندنا خزائنه وما ننزله إلا بقدر معلوم (٢١)) .','776','1381','0');
INSERT INTO [t22928] VALUES (1395,'قال تعالى: (وأرسلنا الرياح لواقح فأنزلنا من السماء ماء فأسقيناكموه وما أنتم له بخازنين (٢٢)) .','776','1381','0');
INSERT INTO [t22928] VALUES (1396,'قال تعالى: (وإنا لنحن نحيي ونميت ونحن الوارثون (٢٣)) .','777','1381','0');
INSERT INTO [t22928] VALUES (1397,'قال تعالى: (ولقد خلقنا الإنسان من صلصال من حمأ مسنون (٢٦)) .','777','1381','0');
INSERT INTO [t22928] VALUES (1398,'قال تعالى: (والجان خلقناه من قبل من نار السموم (٢٧)) .','777','1381','0');
INSERT INTO [t22928] VALUES (1399,'قال تعالى: (فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين فسجد الملائكة كلهم أجمعون (٣٠)) .','778','1381','0');
INSERT INTO [t22928] VALUES (1400,'(قال تعالى: (وإن عليك اللعنة إلى يوم الدين (٣٥)) .','778','1381','0');
INSERT INTO [t22928] VALUES (1401,'قال تعالى: (قال رب بما أغويتني لأزينن لهم في الأرض ولأغوينهم أجمعين (٣٩)) .','778','1381','0');
INSERT INTO [t22928] VALUES (1402,'قال تعالى: (إلا عبادك منهم المخلصين (٤٠)) .','778','1381','0');
INSERT INTO [t22928] VALUES (1403,'قال تعالى: (قال هذا صراط علي مستقيم (٤١)) ) .','778','1381','0');
INSERT INTO [t22928] VALUES (1404,'قال تعالى: (إن عبادي ليس لك عليهم سلطان إلا من اتبعك من الغاوين (٤٢)) .','779','1381','0');
INSERT INTO [t22928] VALUES (1405,'قال تعالى: (وإن جهنم لموعدهم أجمعين) .','779','1381','0');
INSERT INTO [t22928] VALUES (1406,'قال تعالى: (لها سبعة أبواب لكل باب منهم جزء مقسوم (٤٤)) .','779','1381','0');
INSERT INTO [t22928] VALUES (1407,'قال تعالى: (إن المتقين في جنات وعيون (٤٥) ادخلوها بسلام آمنين (٤٦)) .','780','1381','0');
INSERT INTO [t22928] VALUES (1408,'قال تعالى: (ونزعنا ما في صدورهم من غل إخوانا على سرر متقابلين (٤٧)) .','780','1381','0');
INSERT INTO [t22928] VALUES (1409,'قال تعالى: (لا يمسهم فيها نصب وما هم منها بمخرجين (٤٨)) .','780','1381','0');
INSERT INTO [t22928] VALUES (1410,'قال تعالى: (نبئ عبادي أني أنا الغفور الرحيم (٤٩) وأن عذابي هو العذاب الأليم (٥٠)) .','781','1381','0');
INSERT INTO [t22928] VALUES (1411,'قال تعالى: (إذ دخلوا عليه فقالوا سلاما قال إنا منكم وجلون (٥٢)) .','781','1381','0');
INSERT INTO [t22928] VALUES (1412,'قال تعالى: (قال أبشرتموني على أن مسني الكبر فبم تبشرون (٥٤)) .','781','1381','0');
INSERT INTO [t22928] VALUES (1413,'قال تعالى: (قال ومن يقنط من رحمة ربه إلا الضالون (٥٦)) .','782','1381','0');
INSERT INTO [t22928] VALUES (1414,'قال تعالى: (قالوا إنا أرسلنا إلى قوم مجرمين (٥٨) إلا آل لوط إنا لمنجوهم أجمعين إلا امرأته قدرنا إنها لمن الغابرين (٥٩))','782','1381','0');
INSERT INTO [t22928] VALUES (1415,'قال تعالى: (وقضينا إليه ذلك الأمر أن دابر هؤلاء مقطوع مصبحين (٦٦)) .','783','1381','0');
INSERT INTO [t22928] VALUES (1416,'قال تعالى: (قالوا أولم ننهك عن العالمين (٧٠)) .','783','1381','0');
INSERT INTO [t22928] VALUES (1417,'قال تعالى: (قال هؤلاء بناتي إن كنتم فاعلين (٧١)) .','783','1381','0');
INSERT INTO [t22928] VALUES (1418,'قال تعالى: (لعمرك إنهم لفي سكرتهم يعمهون (٧٢)) .','783','1381','0');
INSERT INTO [t22928] VALUES (1419,'قال تعالى: (كما أنزلنا على المقتسمين (٩٠) الذين جعلوا القرآن عضين (٩١)) .','784','1381','0');
INSERT INTO [t22928] VALUES (1420,'قال تعالى: (فاصدع بما تؤمر وأعرض عن المشركين (٩٤)) .','784','1381','0');
INSERT INTO [t22928] VALUES (1421,'قال تعالى: (الذين يجعلون مع الله إلها آخر فسوف يعلمون (٩٦)) .','784','1381','0');
INSERT INTO [t22928] VALUES (1422,'النحل','785','0','0');
INSERT INTO [t22928] VALUES (1423,'قال تعالى: (أتى أمر الله فلا تستعجلوه سبحانه وتعالى عما يشركون (١)) .','785','1422','0');
INSERT INTO [t22928] VALUES (1424,'قال تعالى: (ينزل الملائكة بالروح من أمره على من يشاء من عباده أن أنذروا أنه لا إله إلا أنا فاتقون (٢)) .','785','1422','0');
INSERT INTO [t22928] VALUES (1425,'قال تعالى: (خلق الإنسان من نطفة فإذا هو خصيم مبين (٤)) .','785','1422','0');
INSERT INTO [t22928] VALUES (1426,'قال تعالى: (والأنعام خلقها لكم فيها دفء ومنافع ومنها تأكلون (٥) ولكم فيها جمال حين تريحون وحين تسرحون (٦)) .','786','1422','0');
INSERT INTO [t22928] VALUES (1427,'قال تعالى: (وتحمل أثقالكم إلى بلد لم تكونوا بالغيه إلا بشق الأنفس إن ربكم لرءوف رحيم (٧)) .','787','1422','0');
INSERT INTO [t22928] VALUES (1428,'قال تعالى: (والخيل والبغال والحمير لتركبوها وزينة ويخلق ما لا تعلمون (٨)) .','787','1422','0');
INSERT INTO [t22928] VALUES (1429,'قال تعالى: (وعلى الله قصد السبيل ومنها جائر ولو شاء لهداكم أجمعين (٩)) .','787','1422','0');
INSERT INTO [t22928] VALUES (1430,'قال تعالى: (هو الذي أنزل من السماء ماء لكم منه شراب ومنه شجر فيه تسيمون ينبت لكم به الزرع والزيتون والنخيل والأعناب ومن كل الثمرات إن في ذلك لآية لقوم يتفكرون (١١)) .','787','1422','0');
INSERT INTO [t22928] VALUES (1431,'قال تعالى: (وسخر لكم الليل والنهار والشمس والقمر والنجوم مسخرات بأمره إن في ذلك لآيات لقوم يعقلون (١٢)) .','788','1422','0');
INSERT INTO [t22928] VALUES (1432,'قال تعالى: (وما ذرأ لكم في الأرض مختلفا ألوانه إن في ذلك لآية لقوم يذكرون (١٣)) .','788','1422','0');
INSERT INTO [t22928] VALUES (1433,'قال تعالى: (وهو الذي سخر البحر لتأكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون (١٤)) .','788','1422','0');
INSERT INTO [t22928] VALUES (1434,'قال تعالى: (وألقى في الأرض رواسي أن تميد بكم وأنهارا وسبلا لعلكم تهتدون وعلامات وبالنجم هم يهتدون (١٥)) .','788','1422','0');
INSERT INTO [t22928] VALUES (1435,'قال تعالى: (أموات غير أحياء وما يشعرون أيان يبعثون (٢١)) .','789','1422','0');
INSERT INTO [t22928] VALUES (1436,'قال تعالى: (وإذا قيل لهم ماذا أنزل ربكم قالوا أساطير الأولين (٢٤)) .','789','1422','0');
INSERT INTO [t22928] VALUES (1437,'قال تعالى: (ليحملوا أوزارهم كاملة يوم القيامة ومن أوزار الذين يضلونهم بغير علم ألا ساء ما يزرون (٢٥)) .','790','1422','0');
INSERT INTO [t22928] VALUES (1438,'قال تعالى: (قد مكر الذين من قبلهم فأتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم وأتاهم العذاب من حيث لا يشعرون (٢٦)) .','790','1422','0');
INSERT INTO [t22928] VALUES (1439,'قال تعالى: (ثم يوم القيامة يخزيهم ويقول أين شركائي الذين كنتم تشاقون فيهم قال الذين أوتوا العلم إن الخزي اليوم والسوء على الكافرين (٢٧)) .','790','1422','0');
INSERT INTO [t22928] VALUES (1440,'قال تعالى: (الذين تتوفاهم الملائكة ظالمي أنفسهم فألقوا السلم ما كنا نعمل من سوء بلى إن الله عليم بما كنتم تعملون (٢٨)) .','791','1422','0');
INSERT INTO [t22928] VALUES (1441,'قال تعالى: (وقيل للذين اتقوا ماذا أنزل ربكم قالوا خيرا للذين أحسنوا في هذه الدنيا حسنة ولدار الآخرة خير ولنعم دار المتقين (٣٠)) .','791','1422','0');
INSERT INTO [t22928] VALUES (1442,'قال تعالى: (جنات عدن يدخلونها تجري من تحتها الأنهار لهم فيها ما يشاءون كذلك يجزي الله المتقين (٣١)) .','791','1422','0');
INSERT INTO [t22928] VALUES (1443,'قال تعالى: (الذين تتوفاهم الملائكة طيبين يقولون سلام عليكم ادخلوا الجنة بما كنتم تعملون (٣٢)) .','792','1422','0');
INSERT INTO [t22928] VALUES (1444,'قال تعالى: (ولقد بعثنا في كل أمة رسولا أن اعبدوا الله واجتنبوا الطاغوت فمنهم من هدى الله ومنهم من حقت عليه الضلالة فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين (٣٦)) .','792','1422','0');
INSERT INTO [t22928] VALUES (1445,'قال تعالى: (إن تحرص على هداهم فإن الله لا يهدي من يضل وما لهم من ناصرين (٣٧)) .','792','1422','0');
INSERT INTO [t22928] VALUES (1446,'قال تعالى: (إنما قولنا لشيء إذا أردناه أن نقول له كن فيكون (٤٠))','792','1422','0');
INSERT INTO [t22928] VALUES (1447,'قال تعالى: (والذين هاجروا في الله من بعد ما ظلموا لنبوئنهم في الدنيا حسنة ولأجر الآخرة أكبر لو كانوا يعلمون (٤١)) .','793','1422','0');
INSERT INTO [t22928] VALUES (1448,'قال تعالى: (الذين صبروا وعلى ربهم يتوكلون (٤٢)) .','793','1422','0');
INSERT INTO [t22928] VALUES (1449,'قال تعالى: (بالبينات والزبر وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم ولعلهم يتفكرون (٤٤)) .','793','1422','0');
INSERT INTO [t22928] VALUES (1450,'قال تعالى: (أو يأخذهم على تخوف فإن ربكم لرءوف رحيم (٤٧)) .','794','1422','0');
INSERT INTO [t22928] VALUES (1451,'قال تعالى: (أولم يروا إلى ما خلق الله من شيء يتفيأ ظلاله عن اليمين والشمائل سجدا لله وهم داخرون (٤٨)) .','794','1422','0');
INSERT INTO [t22928] VALUES (1452,'قال تعالى: (ولله يسجد ما في السماوات وما في الأرض من دابة والملائكة وهم لا يستكبرون (٤٩)) .','794','1422','0');
INSERT INTO [t22928] VALUES (1453,'قال تعالى: (يخافون ربهم من فوقهم ويفعلون ما يؤمرون (٥٠)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1454,'قال تعالى: (وقال الله لا تتخذوا إلهين اثنين إنما هو إله واحد فإياي فارهبون (٥١)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1455,'قال تعالى: (وله ما في السماوات والأرض وله الدين واصبا أفغير الله تتقون (٥٢)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1456,'قال تعالى: (وما بكم من نعمة فمن الله ثم إذا مسكم الضر فإليه تجأرون (٥٣)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1457,'قال تعالى: (ثم إذا كشف الضر عنكم إذا فريق منكم بربهم يشركون (٥٤)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1458,'قال تعالى: (ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون (٥٥)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1459,'قال تعالى: (ويجعلون لله البنات سبحانه ولهم ما يشتهون (٥٧)) .','795','1422','0');
INSERT INTO [t22928] VALUES (1460,'قال تعالى: (وإذا بشر أحدهم بالأنثى ظل وجهه مسودا وهو كظيم (٥٨)) .','796','1422','0');
INSERT INTO [t22928] VALUES (1461,'قال تعالى: (يتوارى من القوم من سوء ما بشر به أيمسكه على هون أم يدسه في التراب ألا ساء ما يحكمون (٥٩)) .','796','1422','0');
INSERT INTO [t22928] VALUES (1462,'قال تعالى: (ويجعلون لله ما يكرهون وتصف ألسنتهم الكذب أن لهم الحسنى لا جرم أن لهم النار وأنهم مفرطون (٦٢)) .','796','1422','0');
INSERT INTO [t22928] VALUES (1463,'قال تعالى: (وما أنزلنا عليك الكتاب إلا لتبين لهم الذي اختلفوا فيه وهدى ورحمة لقوم يؤمنون (٦٤)) .','797','1422','0');
INSERT INTO [t22928] VALUES (1464,'قال تعالى: (وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين (٦٦)) .','797','1422','0');
INSERT INTO [t22928] VALUES (1465,'قال تعالى: (ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا إن في ذلك لآية لقوم يعقلون (٦٧)) .','798','1422','0');
INSERT INTO [t22928] VALUES (1466,'قال تعالى: (وأوحى ربك إلى النحل أن اتخذي من الجبال بيوتا ومن الشجر ومما يعرشون (٦٨)) .','799','1422','0');
INSERT INTO [t22928] VALUES (1467,'قال تعالى: (ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا يخرج من بطونها شراب مختلف ألوانه فيه شفاء للناس إن في ذلك لآية لقوم يتفكرون (٦٩)) .','799','1422','0');
INSERT INTO [t22928] VALUES (1468,'قال تعالى: (والله خلقكم ثم يتوفاكم ومنكم من يرد إلى أرذل العمر لكي لا يعلم بعد علم شيئا إن الله عليم قدير (٧٠)) .','799','1422','0');
INSERT INTO [t22928] VALUES (1469,'قال تعالى: (والله فضل بعضكم على بعض في الرزق فما الذين فضلوا برادي رزقهم على ما ملكت أيمانهم فهم فيه سواء أفبنعمة الله يجحدون (٧١)) .','799','1422','0');
INSERT INTO [t22928] VALUES (1470,'قال تعالى: (ويعبدون من دون الله ما لا يملك لهم رزقا من السماوات والأرض شيئا ولا يستطيعون (٧٣)) .','799','1422','0');
INSERT INTO [t22928] VALUES (1471,'قال تعالى: (ضرب الله مثلا عبدا مملوكا لا يقدر على شيء ومن رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا هل يستوون الحمد لله بل أكثرهم لا يعلمون (٧٥)) .','800','1422','0');
INSERT INTO [t22928] VALUES (1472,'قال تعالى: (وضرب الله مثلا رجلين أحدهما أبكم لا يقدر على شيء وهو كل على مولاه أينما يوجهه لا يأت بخير هل يستوي هو ومن يأمر بالعدل وهو على صراط مستقيم (٧٦)) .','800','1422','0');
INSERT INTO [t22928] VALUES (1473,'قال تعالى: (ولله غيب السماوات والأرض وما أمر الساعة إلا كلمح البصر أو هو أقرب إن الله على كل شيء قدير (٧٧)) .','800','1422','0');
INSERT INTO [t22928] VALUES (1474,'قال تعالى: (والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا وجعل لكم السمع والأبصار والأفئدة لعلكم تشكرون (٧٨)) .','801','1422','0');
INSERT INTO [t22928] VALUES (1475,'قال تعالى: (ألم يروا إلى الطير مسخرات في جو السماء ما يمسكهن إلا الله إن في ذلك لآيات لقوم يؤمنون (٧٩)) .','801','1422','0');
INSERT INTO [t22928] VALUES (1476,'قال تعالى: (والله جعل لكم من بيوتكم سكنا وجعل لكم من جلود الأنعام بيوتا تستخفونها يوم ظعنكم ويوم إقامتكم ومن أصوافها وأوبارها وأشعارها أثاثا ومتاعا إلى حين (٨٠)) .','801','1422','0');
INSERT INTO [t22928] VALUES (1477,'قال تعالى: (ويوم نبعث من كل أمة شهيدا ثم لا يؤذن للذين كفروا ولا هم يستعتبون (٨٤)) .','802','1422','0');
INSERT INTO [t22928] VALUES (1478,'قال تعالى: (إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون (٩٠)) .','802','1422','0');
INSERT INTO [t22928] VALUES (1479,'قال تعالى: (وأوفوا بعهد الله إذا عاهدتم ولا تنقضوا الأيمان بعد توكيدها وقد جعلتم الله عليكم كفيلا إن الله يعلم ما تفعلون (٩١)) .','802','1422','0');
INSERT INTO [t22928] VALUES (1480,'قال تعالى: (ولا تكونوا كالتي نقضت غزلها من بعد قوة أنكاثا تتخذون أيمانكم دخلا بينكم أن تكون أمة هي أربى من أمة إنما يبلوكم الله به وليبينن لكم يوم القيامة ما كنتم فيه تختلفون (٩٢))','802','1422','0');
INSERT INTO [t22928] VALUES (1481,'قال تعالى: (ولا تتخذوا أيمانكم دخلا بينكم فتزل قدم بعد ثبوتها وتذوقوا السوء بما صددتم عن سبيل الله ولكم عذاب عظيم (٩٤)) .','803','1422','0');
INSERT INTO [t22928] VALUES (1482,'قال تعالى: (من عمل صالحا من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة ولنجزينهم أجرهم بأحسن ما كانوا يعملون)','803','1422','0');
INSERT INTO [t22928] VALUES (1483,'قال تعالى: (فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم (٩٨)) .','803','1422','0');
INSERT INTO [t22928] VALUES (1484,'قال تعالى: (إنما سلطانه على الذين يتولونه والذين هم به مشركون (١٠٠)) .','803','1422','0');
INSERT INTO [t22928] VALUES (1485,'قال تعالى: (وإذا بدلنا آية مكان آية والله أعلم بما ينزل قالوا إنما أنت مفتر بل أكثرهم لا يعلمون (١٠١)) .','803','1422','0');
INSERT INTO [t22928] VALUES (1486,'قال تعالى: (قل نزله روح القدس من ربك بالحق ليثبت الذين آمنوا وهدى وبشرى للمسلمين (١٠٢)) .','803','1422','0');
INSERT INTO [t22928] VALUES (1487,'قال تعالى: (ولقد نعلم أنهم يقولون إنما يعلمه بشر لسان الذي يلحدون إليه أعجمي وهذا لسان عربي مبين (١٠٣)) .','804','1422','0');
INSERT INTO [t22928] VALUES (1488,'قال تعالى: (من كفر بالله من بعد إيمانه إلا من أكره وقلبه مطمئن بالإيمان ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم (١٠٦)) .','804','1422','0');
INSERT INTO [t22928] VALUES (1489,'قال تعالى: (ثم إن ربك للذين هاجروا من بعد ما فتنوا ثم جاهدوا وصبروا إن ربك من بعدها لغفور رحيم (١١٠)) .','805','1422','0');
INSERT INTO [t22928] VALUES (1490,'قال تعالى: (يوم تأتي كل نفس تجادل عن نفسها وتوفى كل نفس ما عملت وهم لا يظلمون (١١١)) .','805','1422','0');
INSERT INTO [t22928] VALUES (1491,'قال تعالى: (وضرب الله مثلا قرية كانت آمنة مطمئنة يأتيها رزقها رغدا من كل مكان فكفرت بأنعم الله فأذاقها الله لباس الجوع والخوف بما كانوا يصنعون (١١٢)) .','805','1422','0');
INSERT INTO [t22928] VALUES (1492,'قال تعالى: (ولا تقولوا لما تصف ألسنتكم الكذب هذا حلال وهذا حرام لتفتروا على الله الكذب إن الذين يفترون على الله الكذب لا يفلحون (١٦٦)) .','805','1422','0');
INSERT INTO [t22928] VALUES (1493,'قال تعالى: (متاع قليل ولهم عذاب أليم (١١٧)) .','806','1422','0');
INSERT INTO [t22928] VALUES (1494,'قال تعالى: (شاكرا لأنعمه اجتباه وهداه إلى صراط مستقيم (١٢١)) .','806','1422','0');
INSERT INTO [t22928] VALUES (1495,'قال تعالى: (وإن عاقبتم فعاقبوا بمثل ما عوقبتم به ولئن صبرتم لهو خير للصابرين (١٢٦)) .','806','1422','0');
INSERT INTO [t22928] VALUES (1496,'قال تعالى: (واصبر وما صبرك إلا بالله ولا تحزن عليهم ولا تك في ضيق مما يمكرون (١٢٧)) .','807','1422','0');
INSERT INTO [t22928] VALUES (1497,'الإسراء','808','0','0');
INSERT INTO [t22928] VALUES (1498,'قال تعالى: (سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصى الذي باركنا حوله لنريه من آياتنا إنه هو السميع البصير (١)) .','808','1497','0');
INSERT INTO [t22928] VALUES (1499,'قال تعالى: (وآتينا موسى الكتاب وجعلناه هدى لبني إسرائيل ألا تتخذوا من دوني وكيلا ذرية من حملنا مع نوح إنه كان عبدا شكورا (٢)) .','808','1497','0');
INSERT INTO [t22928] VALUES (1500,'قال تعالى: (وقضينا إلى بني إسرائيل في الكتاب لتفسدن في الأرض مرتين ولتعلن علوا كبيرا (٤) فإذا جاء وعد أولاهما بعثنا عليكم عبادا لنا أولي بأس شديد فجاسوا خلال الديار وكان وعدا مفعولا (٥)) .','809','1497','0');
INSERT INTO [t22928] VALUES (1501,'قال تعالى: (ثم رددنا لكم الكرة عليهم وأمددناكم بأموال وبنين وجعلناكم أكثر نفيرا (٦)) .','810','1497','0');
INSERT INTO [t22928] VALUES (1502,'قال تعالى: (إن أحسنتم أحسنتم لأنفسكم وإن أسأتم فلها فإذا جاء وعد الآخرة ليسوءوا وجوهكم وليدخلوا المسجد كما دخلوه أول مرة وليتبروا ما علوا تتبيرا (٧)) .','810','1497','0');
INSERT INTO [t22928] VALUES (1503,'قال تعالى: (عسى ربكم أن يرحمكم وإن عدتم عدنا وجعلنا جهنم للكافرين حصيرا (٨)) .','811','1497','0');
INSERT INTO [t22928] VALUES (1504,'قال تعالى: (إن هذا القرآن يهدي للتي هي أقوم ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا كبيرا (٩) وأن الذين لا يؤمنون بالآخرة أعتدنا لهم عذابا أليما (١٠)) .','811','1497','0');
INSERT INTO [t22928] VALUES (1505,'قال تعالى: (ويدع الإنسان بالشر دعاءه بالخير وكان الإنسان عجولا (١١)) .','811','1497','0');
INSERT INTO [t22928] VALUES (1506,'قال تعالى: (وجعلنا الليل والنهار آيتين فمحونا آية الليل وجعلنا آية النهار مبصرة لتبتغوا فضلا من ربكم ولتعلموا عدد السنين والحساب وكل شيء فصلناه تفصيلا (١٢)) .','811','1497','0');
INSERT INTO [t22928] VALUES (1507,'قال تعالى: (وكل إنسان ألزمناه طائره في عنقه ونخرج له يوم القيامة كتابا يلقاه منشورا (١٣)) .','812','1497','0');
INSERT INTO [t22928] VALUES (1508,'قال تعالى: (اقرأ كتابك كفى بنفسك اليوم عليك حسيبا (١٤)) .','812','1497','0');
INSERT INTO [t22928] VALUES (1509,'قال تعالى: (وإذا أردنا أن نهلك قرية أمرنا مترفيها ففسقوا فيها فحق عليها القول فدمرناها تدميرا (١٦٥)) .','812','1497','0');
INSERT INTO [t22928] VALUES (1510,'قال تعالى: (وكم أهلكنا من القرون من بعد نوح وكفى بربك بذنوب عباده خبيرا بصيرا (١٧)) .','813','1497','0');
INSERT INTO [t22928] VALUES (1511,'قال تعالى: (من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا له جهنم يصلاها مذموما مدحورا (١٨)) .','813','1497','0');
INSERT INTO [t22928] VALUES (1512,'قال تعالى: (ومن أراد الآخرة وسعى لها سعيها وهو مؤمن فأولئك كان سعيهم مشكورا (١٩)) .','813','1497','0');
INSERT INTO [t22928] VALUES (1513,'قال تعالى: (كلا نمد هؤلاء وهؤلاء من عطاء ربك وما كان عطاء ربك محظورا (٢٠)) .','813','1497','0');
INSERT INTO [t22928] VALUES (1514,'قال تعالى: (انظر كيف فضلنا بعضهم على بعض وللآخرة أكبر درجات وأكبر تفضيلا (٢١)) .','814','1497','0');
INSERT INTO [t22928] VALUES (1515,'قال تعالى: (وقضى ربك ألا تعبدوا إلا إياه وبالوالدين إحسانا إما يبلغن عندك الكبر أحدهما أو كلاهما فلا تقل لهما أف ولا تنهرهما وقل لهما قولا كريما (٢٣)) .','814','1497','0');
INSERT INTO [t22928] VALUES (1516,'قال تعالى: (واخفض لهما جناح الذل من الرحمة وقل ربي ارحمهما كما ربياني صغيرا (٢٤)) .','815','1497','0');
INSERT INTO [t22928] VALUES (1517,'قال تعالى: (وإما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا (٢٨)) .','815','1497','0');
INSERT INTO [t22928] VALUES (1518,'قال تعالى: (ولا تجعل يدك مغلولة إلى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا (٢٩)) .','815','1497','0');
INSERT INTO [t22928] VALUES (1519,'قال تعالى: (ولا تقتلوا أولادكم خشية إملاق نحن نرزقهم وإياكم إن قتلهم كان خطئا كبيرا (٣١) ولا تقربوا الزنا إنه كان فاحشة وساء سبيلا (٣٢)) .','815','1497','0');
INSERT INTO [t22928] VALUES (1520,'قال تعالى: (ولا تقتلوا النفس التي حرم الله إلا بالحق ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل إنه كان منصورا (٣٣)) .','816','1497','0');
INSERT INTO [t22928] VALUES (1521,'قال تعالى: (ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده وأوفوا بالعهد إن العهد كان مسئولا (٣٤)) .','817','1497','0');
INSERT INTO [t22928] VALUES (1522,'قال تعالى: (ولا تقف ما ليس لك به علم إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا (٣٦)) .','817','1497','0');
INSERT INTO [t22928] VALUES (1523,'قال تعالى: (ولا تمش في الأرض مرحا إنك لن تخرق الأرض ولن تبلغ الجبال طولا (٣٧)) .','819','1497','0');
INSERT INTO [t22928] VALUES (1524,'قال تعالى: (كل ذلك كان سيئه عند ربك مكروها (٣٨)) .','819','1497','0');
INSERT INTO [t22928] VALUES (1525,'قال تعالى: (ذلك مما أوحى إليك ربك من الحكمة ولا تجعل مع الله إلها آخر فتلقى في جهنم ملوما مدحورا (٣٩)) .','819','1497','0');
INSERT INTO [t22928] VALUES (1526,'قال تعالى: (أفأصفاكم ربكم بالبنين واتخذ من الملائكة إناثا إنكم لتقولون قولا عظيما (٤٠)) .','819','1497','0');
INSERT INTO [t22928] VALUES (1527,'قال تعالى: (ولقد صرفنا في هذا القرآن ليذكروا وما يزيدهم إلا نفورا (٤١)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1528,'قال تعالى: (قل لو كان معه آلهة كما يقولون إذا لابتغوا إلى ذي العرش سبيلا (٤٢)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1529,'قال تعالى: (سبحانه وتعالى عما يقولون علوا كبيرا (٤٣)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1530,'قال تعالى: (وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا (٤٥)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1531,'قال تعالى: (وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإذا ذكرت ربك في القرآن وحده ولوا على أدبارهم نفورا (٤٦)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1532,'قال تعالى: (نحن أعلم بما يستمعون به إذ يستمعون إليك وإذ هم نجوى إذ يقول الظالمون إن تتبعون إلا رجلا مسحورا (٤٧) انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا (٤٨) وقالوا أئذا كنا عظاما ورفاتا أئنا لمبعوثون خلقا جديدا (٤٩)) .','820','1497','0');
INSERT INTO [t22928] VALUES (1533,'قال تعالى: (أو خلقا مما يكبر في صدوركم فسيقولون من يعيدنا قل الذي فطركم أول مرة فسينغضون إليك رءوسهم ويقولون متى هو قل عسى أن يكون قريبا (٥١)) .','821','1497','0');
INSERT INTO [t22928] VALUES (1534,'قال تعالى: (يوم يدعوكم فتستجيبون بحمده وتظنون إن لبثتم إلا قليلا (٥٢)) .','821','1497','0');
INSERT INTO [t22928] VALUES (1535,'قال تعالى: (وقل لعبادي يقولوا التي هي أحسن إن الشيطان ينزغ بينهم إن الشيطان كان للإنسان عدوا مبينا (٥٣)) .','822','1497','0');
INSERT INTO [t22928] VALUES (1536,'قال تعالى: (وربك أعلم بمن في السماوات والأرض ولقد فضلنا بعض النبيين على بعض وآتينا داود زبورا (٥٥)) .','822','1497','0');
INSERT INTO [t22928] VALUES (1537,'قال تعالى: (أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب ويرجون رحمته ويخافون عذابه إن عذاب ربك كان محذورا (٥٧)) .','822','1497','0');
INSERT INTO [t22928] VALUES (1538,'قال تعالى: (وما منعنا أن نرسل بالآيات إلا أن كذب بها الأولون وآتينا ثمود الناقة مبصرة فظلموا بها وما نرسل بالآيات إلا تخويفا (٥٩)) .','822','1497','0');
INSERT INTO [t22928] VALUES (1539,'قال تعالى: (وإذ قلنا لك إن ربك أحاط بالناس وما جعلنا الرؤيا التي أريناك إلا فتنة للناس والشجرة الملعونة في القرآن ونخوفهم فما يزيدهم إلا طغيانا كبيرا (٦٠)) .','823','1497','0');
INSERT INTO [t22928] VALUES (1540,'قال تعالى: (قال أرأيتك هذا الذي كرمت علي لئن أخرتني إلى يوم القيامة لأحتنكن ذريته إلا قليلا (٦٢)) .','823','1497','0');
INSERT INTO [t22928] VALUES (1541,'قال تعالى: (قال اذهب فمن تبعك منهم فإن جهنم جزاؤكم جزاء موفورا (٦٣) واستفزز من استطعت منهم بصوتك وأجلب عليهم بخيلك ورجلك وشاركهم في الأموال والأولاد وعدهم وما يعدهم الشيطان إلا غرورا (٦٤)) .','823','1497','0');
INSERT INTO [t22928] VALUES (1542,'قال تعالى: (ربكم الذي يزجي لكم الفلك في البحر لتبتغوا من فضله إنه كان بكم رحيما (٦٦)) .','824','1497','0');
INSERT INTO [t22928] VALUES (1543,'قال تعالى: (وإذا مسكم الضر في البحر ضل من تدعون إلا إياه فلما نجاكم إلى البر أعرضتم وكان الإنسان كفورا (٦٧)) .','824','1497','0');
INSERT INTO [t22928] VALUES (1544,'قال تعالى: (أفأمنتم أن يخسف بكم جانب البر أو يرسل عليكم حاصبا ثم لا تجدوا لكم وكيلا (٦٨)) .','824','1497','0');
INSERT INTO [t22928] VALUES (1545,'قال تعالى: (أم أمنتم أن يعيدكم فيه تارة أخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ثم لا تجدوا لكم علينا به تبيعا (٦٩)) .','824','1497','0');
INSERT INTO [t22928] VALUES (1546,'قال تعالى: (يوم ندعوا كل أناس بإمامهم فمن أوتي كتابه بيمينه فأولئك يقرءون كتابهم ولا يظلمون فتيلا (٧١)) .','825','1497','0');
INSERT INTO [t22928] VALUES (1547,'قال تعالى: (ومن كان في هذه أعمى فهو في الآخرة أعمى وأضل سبيلا (٧٢)) .','825','1497','0');
INSERT INTO [t22928] VALUES (1548,'قال تعالى: (ولولا أن ثبتناك لقد كدت تركن إليهم شيئا قليلا (٧٤)) .','826','1497','0');
INSERT INTO [t22928] VALUES (1549,'قال تعالى: (وإن كادوا ليستفزونك من الأرض ليخرجوك منها وإذا لا يلبثون خلافك إلا قليلا (٧٦)) .','826','1497','0');
INSERT INTO [t22928] VALUES (1550,'قال تعالى: (سنة من قد أرسلنا قبلك من رسلنا ولا تجد لسنتنا تحويلا (٧٧)) .','826','1497','0');
INSERT INTO [t22928] VALUES (1551,'قال تعالى: (أقم الصلاة لدلوك الشمس إلى غسق الليل وقرآن الفجر إن قرآن الفجر كان مشهودا (٧٨)) .','827','1497','0');
INSERT INTO [t22928] VALUES (1552,'قال تعالى: (ومن الليل فتهجد به نافلة لك عسى أن يبعثك ربك مقاما محمودا (٧٩)) .','827','1497','0');
INSERT INTO [t22928] VALUES (1553,'قال تعالى: (وننزل من القرآن ما هو شفاء ورحمة للمؤمنين ولا يزيد الظالمين إلا خسارا (٨٢)) .','827','1497','0');
INSERT INTO [t22928] VALUES (1554,'قال تعالى: (وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر كان يئوسا (٨٣)) .','828','1497','0');
INSERT INTO [t22928] VALUES (1555,'قال تعالى: (قل كل يعمل على شاكلته فربكم أعلم بمن هو أهدى سبيلا (٨٤)) .','828','1497','0');
INSERT INTO [t22928] VALUES (1556,'قال تعالى: (ويسألونك عن الروح قل الروح من أمر ربي وما أوتيتم من العلم إلا قليلا (٨٥)) .','828','1497','0');
INSERT INTO [t22928] VALUES (1557,'قال تعالى: (إلا رحمة من ربك إن فضله كان عليك كبيرا (٨٧)) .','828','1497','0');
INSERT INTO [t22928] VALUES (1558,'قال تعالى: (قل لئن اجتمعت الإنس والجن على أن يأتوا بمثل هذا القرآن لا يأتون بمثله ولو كان بعضهم لبعض ظهيرا (٨٨)) .','828','1497','0');
INSERT INTO [t22928] VALUES (1559,'قال تعالى: (وقالوا لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا (٩٠)) .','829','1497','0');
INSERT INTO [t22928] VALUES (1560,'قال تعالى: (أو تسقط السماء كما زعمت علينا كسفا أو تأتي بالله والملائكة قبيلا (٩٢)) .','829','1497','0');
INSERT INTO [t22928] VALUES (1561,'قال تعالى: (وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا أبعث الله بشرا رسولا (٩٤))','829','1497','0');
INSERT INTO [t22928] VALUES (1562,'قال تعالى: (قل لو كان في الأرض ملائكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا (٩٥)) .','830','1497','0');
INSERT INTO [t22928] VALUES (1563,'قال تعالى: (ومن يهد الله فهو المهتدي ومن يضلل فلن تجد لهم أولياء من دونه ونحشرهم يوم القيامة على وجوههم عميا وبكما وصما مأواهم جهنم كلما خبت زدناهم سعيرا (٩٧)) .','830','1497','0');
INSERT INTO [t22928] VALUES (1564,'قال تعالى: (ذلك جزاؤهم بأنهم كفروا بآياتنا وقالوا أئذا كنا عظاما ورفاتا أئنا لمبعوثون خلقا جديدا (٩٨)) .','830','1497','0');
INSERT INTO [t22928] VALUES (1565,'قال تعالى: (قل لو أنتم تملكون خزائن رحمة ربي إذا لأمسكتم خشية الإنفاق وكان الإنسان قتورا (١٠٠)) .','830','1497','0');
INSERT INTO [t22928] VALUES (1566,'قال تعالى: (ولقد آتينا موسى تسع آيات بينات فاسأل بني إسرائيل إذ جاءهم فقال له فرعون إني لأظنك يا موسى مسحورا (١٠١)) .','831','1497','0');
INSERT INTO [t22928] VALUES (1567,'قال تعالى: (قال لقد علمت ما أنزل هؤلاء إلا رب السماوات والأرض بصائر وإني لأظنك يا فرعون مثبورا (١٠٢)) .','831','1497','0');
INSERT INTO [t22928] VALUES (1568,'قال تعالى: (وقلنا من بعده لبني إسرائيل اسكنوا الأرض فإذا جاء وعد الآخرة جئنا بكم لفيفا (١٠٤)) .','832','1497','0');
INSERT INTO [t22928] VALUES (1569,'قال تعالى: (وبالحق أنزلناه وبالحق نزل وما أرسلناك إلا مبشرا ونذيرا (١٠٥)) .','832','1497','0');
INSERT INTO [t22928] VALUES (1570,'قال تعالى: (وقرآنا فرقناه لتقرأه على الناس على مكث ونزلناه تنزيلا (١٠٦)) .','832','1497','0');
INSERT INTO [t22928] VALUES (1571,'قال تعالى: (ويخرون للأذقان يبكون ويزيدهم خشوعا (١٠٩)) .','832','1497','0');
INSERT INTO [t22928] VALUES (1572,'قال تعالى: (قل ادعوا الله أو ادعوا الرحمن أيا ما تدعوا فله الأسماء الحسنى ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا (١١٠)) .','833','1497','0');
INSERT INTO [t22928] VALUES (1573,'قال تعالى: (وقل الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك ولم يكن له ولي من الذل وكبره تكبيرا (١١١)) .','833','1497','0');
INSERT INTO [t22928] VALUES (1574,'الكهف','834','0','0');
INSERT INTO [t22928] VALUES (1575,'قال تعالى: (الحمد لله الذي أنزل على عبده الكتاب ولم يجعل له عوجا (١) قيما لينذر بأسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا حسنا (٢)) .','834','1574','0');
INSERT INTO [t22928] VALUES (1576,'قال تعالى: (ماكثين فيه أبدا (٣)) .','834','1574','0');
INSERT INTO [t22928] VALUES (1577,'قال تعالى: (ما لهم به من علم ولا لآبائهم كبرت كلمة تخرج من أفواههم إن يقولون إلا كذبا (٥) فلعلك باخع نفسك على آثارهم إن لم يؤمنوا بهذا الحديث أسفا (٦)) .','835','1574','0');
INSERT INTO [t22928] VALUES (1578,'قال تعالى: (إنا جعلنا ما على الأرض زينة لها لنبلوهم أيهم أحسن عملا (٧)) .','835','1574','0');
INSERT INTO [t22928] VALUES (1579,'قال تعالى: (أم حسبت أن أصحاب الكهف والرقيم كانوا من آياتنا عجبا (٩) إذ أوى الفتية إلى الكهف فقالوا ربنا آتنا من لدنك رحمة وهيئ لنا من أمرنا رشدا (١٠)) .','835','1574','0');
INSERT INTO [t22928] VALUES (1580,'قال تعالى: (فضربنا على آذانهم في الكهف سنين عددا (١١)) .','836','1574','0');
INSERT INTO [t22928] VALUES (1581,'قال تعالى: (ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا (١٢))','836','1574','0');
INSERT INTO [t22928] VALUES (1582,'قال تعالى: (وربطنا على قلوبهم إذ قاموا فقالوا ربنا رب السماوات والأرض لن ندعو من دونه إلها لقد قلنا إذا شططا (١٤)) .','836','1574','0');
INSERT INTO [t22928] VALUES (1583,'قال تعالى: (هؤلاء قومنا اتخذوا من دونه آلهة لولا يأتون عليهم بسلطان بين فمن أظلم ممن افترى على الله كذبا (١٥)) .','836','1574','0');
INSERT INTO [t22928] VALUES (1584,'قال تعالى: (وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا (١٦)) .','837','1574','0');
INSERT INTO [t22928] VALUES (1585,'قال تعالى: (وترى الشمس إذا طلعت تزاور عن كهفهم ذات اليمين وإذا غربت تقرضهم ذات الشمال وهم في فجوة منه ذلك من آيات الله من يهد الله فهو المهتدي ومن يضلل فلن تجد له وليا مرشدا (١٧)) .','837','1574','0');
INSERT INTO [t22928] VALUES (1586,'قال تعالى: (وتحسبهم أيقاظا وهم رقود ونقلبهم ذات اليمين وذات الشمال وكلبهم باسط ذراعيه بالوصيد لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا (١٨)) .','838','1574','0');
INSERT INTO [t22928] VALUES (1587,'قال تعالى: (وكذلك بعثناهم ليتساءلوا بينهم قال قائل منهم كم لبثتم قالوا لبثنا يوما أو بعض يوم قالوا ربكم أعلم بما لبثتم فابعثوا أحدكم بورقكم هذه إلى المدينة فلينظر أيها أزكى طعاما فليأتكم برزق منه وليتلطف ولا يشعرن بكم أحدا (١٩)) .','838','1574','0');
INSERT INTO [t22928] VALUES (1588,'قال تعالى: (وكذلك أعثرنا عليهم ليعلموا أن وعد الله حق وأن الساعة لا ريب فيها إذ يتنازعون بينهم أمرهم فقالوا ابنوا عليهم بنيانا ربهم أعلم بهم قال الذين غلبوا على أمرهم لنتخذن عليهم مسجدا (٢١)) .','839','1574','0');
INSERT INTO [t22928] VALUES (1589,'قال تعالى: (سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ويقولون سبعة وثامنهم كلبهم قل ربي أعلم بعدتهم ما يعلمهم إلا قليل فلا تمار فيهم إلا مراء ظاهرا ولا تستفت فيهم منهم أحدا (٢٢)) .','839','1574','0');
INSERT INTO [t22928] VALUES (1590,'قال تعالى: (ولا تقولن لشيء إني فاعل ذلك غدا (٢٣) إلا أن يشاء الله واذكر ربك إذا نسيت وقل عسى أن يهديني ربي لأقرب من هذا رشدا (٢٤)) .','840','1574','0');
INSERT INTO [t22928] VALUES (1591,'قال تعالى: (ولبثوا في كهفهم ثلاثمائة سنين وازدادوا تسعا (٢٥) قل الله أعلم بما لبثوا له غيب السماوات والأرض أبصر به وأسمع ما لهم من دونه من ولي ولا يشرك في حكمه أحدا (٢٦))','841','1574','0');
INSERT INTO [t22928] VALUES (1592,'قال تعالى: (واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه ولا تعد عيناك عنهم تريد زينة الحياة الدنيا ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه وكان أمره فرطا (٢٨)) .','841','1574','0');
INSERT INTO [t22928] VALUES (1593,'قال تعالى: (وقل الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر إنا أعتدنا للظالمين نارا أحاط بهم سرادقها وإن يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقا (٢٩)) .','842','1574','0');
INSERT INTO [t22928] VALUES (1594,'قال تعالى: (إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملا (٣٠)) .','842','1574','0');
INSERT INTO [t22928] VALUES (1595,'قال تعالى: (أولئك لهم جنات عدن تجري من تحتهم الأنهار يحلون فيها من أساور من ذهب ويلبسون ثيابا خضرا من سندس وإستبرق متكئين فيها على الأرائك نعم الثواب وحسنت مرتفقا (٣١)) .','843','1574','0');
INSERT INTO [t22928] VALUES (1596,'قال تعالى: (واضرب لهم مثلا رجلين جعلنا لأحدهما جنتين من أعناب وحففناهما بنخل وجعلنا بينهما زرعا (٣٢) كلتا الجنتين آتت أكلها ولم تظلم منه شيئا وفجرنا خلالهما نهرا (٣٣) وكان له ثمر فقال لصاحبه وهو يحاوره أنا أكثر منك مالا وأعز نفرا (٣٤))','843','1574','0');
INSERT INTO [t22928] VALUES (1597,'قال تعالى: (ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا (٣٥)) .','844','1574','0');
INSERT INTO [t22928] VALUES (1598,'قال تعالى: (وما أظن الساعة قائمة ولئن رددت إلى ربي لأجدن خيرا منها منقلبا (٣٦)) .','844','1574','0');
INSERT INTO [t22928] VALUES (1599,'قال تعالى: (لكنا هو الله ربي ولا أشرك بربي أحدا (٣٨)) .','844','1574','0');
INSERT INTO [t22928] VALUES (1600,'قال تعالى: (ولولا إذ دخلت جنتك قلت ما شاء الله لا قوة إلا بالله إن ترن أنا أقل منك مالا وولدا (٣٩)) .','845','1574','0');
INSERT INTO [t22928] VALUES (1601,'قال تعالى: (فعسى ربي أن يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا أو يصبح ماؤها غورا فلن تستطيع له طلبا (٤١)) .','845','1574','0');
INSERT INTO [t22928] VALUES (1602,'قال تعالى: (وأحيط بثمره فأصبح يقلب كفيه على ما أنفق فيها وهي خاوية على عروشها ويقول يا ليتني لم أشرك بربي أحدا (٤٢)) .','846','1574','0');
INSERT INTO [t22928] VALUES (1603,'قال تعالى: (ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا (٤٣)) .','846','1574','0');
INSERT INTO [t22928] VALUES (1604,'قال تعالى {هنالك الولاية لله الحق هو خير ثوابا وخير عقبا}','846','1574','0');
INSERT INTO [t22928] VALUES (1605,'قال تعالى: (واضرب لهم مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض فأصبح هشيما تذروه الرياح وكان الله على كل شيء مقتدرا (٤٥)) .','847','1574','0');
INSERT INTO [t22928] VALUES (1606,'قال تعالى: (ويوم نسير الجبال وترى الأرض بارزة وحشرناهم فلم نغادر منهم أحدا (٤٧)) .','847','1574','0');
INSERT INTO [t22928] VALUES (1607,'قال تعالى: (وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم أول مرة بل زعمتم ألن نجعل لكم موعدا (٤٨)) .','847','1574','0');
INSERT INTO [t22928] VALUES (1608,'قال تعالى: (ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها ووجدوا ما عملوا حاضرا ولا يظلم ربك أحدا (٤٩)) .','847','1574','0');
INSERT INTO [t22928] VALUES (1609,'قال تعالى: (وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس كان من الجن ففسق عن أمر ربه أفتتخذونه وذريته أولياء من دوني وهم لكم عدو بئس للظالمين بدلا (٥٠)) .','848','1574','0');
INSERT INTO [t22928] VALUES (1610,'قال تعالى: (ما أشهدتهم خلق السماوات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا (٥١)) .','848','1574','0');
INSERT INTO [t22928] VALUES (1611,'قال تعالى: (ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا (٥٢)) .','848','1574','0');
INSERT INTO [t22928] VALUES (1612,'قال تعالى: (ورأى المجرمون النار فظنوا أنهم مواقعوها ولم يجدوا عنها مصرفا (٥٣)) .','849','1574','0');
INSERT INTO [t22928] VALUES (1613,'قال تعالى: (ولقد صرفنا في هذا القرآن للناس من كل مثل وكان الإنسان أكثر شيء جدلا (٥٤)) .','849','1574','0');
INSERT INTO [t22928] VALUES (1614,'قال تعالى: (وما منع الناس أن يؤمنوا إذ جاءهم الهدى ويستغفروا ربهم إلا أن تأتيهم سنة الأولين أو يأتيهم العذاب قبلا (٥٥)) .','849','1574','0');
INSERT INTO [t22928] VALUES (1615,'قال تعالى: (وما نرسل المرسلين إلا مبشرين ومنذرين ويجادل الذين كفروا بالباطل ليدحضوا به الحق واتخذوا آياتي وما أنذروا هزوا (٥٦)) .','849','1574','0');
INSERT INTO [t22928] VALUES (1616,'قال تعالى: (ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه إنا جعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن تدعهم إلى الهدى فلن يهتدوا إذا أبدا (٧)) .','850','1574','0');
INSERT INTO [t22928] VALUES (1617,'قال تعالى: (وربك الغفور ذو الرحمة لو يؤاخذهم بما كسبوا لعجل لهم العذاب بل لهم موعد لن يجدوا من دونه موئلا (٥٨)) .','850','1574','0');
INSERT INTO [t22928] VALUES (1618,'قال تعالى: (وتلك القرى أهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا (٥٩)) .','850','1574','0');
INSERT INTO [t22928] VALUES (1619,'قال تعالى: (وإذ قال موسى لفتاه لا أبرح حتى أبلغ مجمع البحرين أو أمضي حقبا (٦٠)) .','850','1574','0');
INSERT INTO [t22928] VALUES (1620,'قال تعالى: (فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله في البحر سربا (٦١))','851','1574','0');
INSERT INTO [t22928] VALUES (1621,'قال تعالى: (قال أرأيت إذ أوينا إلى الصخرة فإني نسيت الحوت وما أنسانيه إلا الشيطان أن أذكره واتخذ سبيله في البحر عجبا (٦٣)) .','851','1574','0');
INSERT INTO [t22928] VALUES (1622,'قال تعالى: (قال ذلك ما كنا نبغ فارتدا على آثارهما قصصا فوجدا عبدا من عبادنا آتيناه رحمة من عندنا وعلمناه من لدنا علما (٦) .','852','1574','0');
INSERT INTO [t22928] VALUES (1623,'قال تعالى: (قال له موسى هل أتبعك على أن تعلمني مما علمت رشدا (٦٦)) .','852','1574','0');
INSERT INTO [t22928] VALUES (1624,'قال تعالى: (وكيف تصبر على ما لم تحط به خبرا (٦٨)) .','852','1574','0');
INSERT INTO [t22928] VALUES (1625,'قال تعالى: (قال فإن اتبعتني فلا تسألني عن شيء حتى أحدث لك منه ذكرا (٧٠)) .','852','1574','0');
INSERT INTO [t22928] VALUES (1626,'قال تعالى: (فانطلقا حتى إذا ركبا في السفينة خرقها قال أخرقتها لتغرق أهلها لقد جئت شيئا إمرا (٧١)) .','853','1574','0');
INSERT INTO [t22928] VALUES (1627,'قال تعالى: (قال لا تؤاخذني بما نسيت ولا ترهقني من أمري عسرا (٧٣)) .','853','1574','0');
INSERT INTO [t22928] VALUES (1628,'قال تعالى: (فانطلقا حتى إذا لقيا غلاما فقتله قال أقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا (٧٤)) .','853','1574','0');
INSERT INTO [t22928] VALUES (1629,'قال تعالى: (قال إن سألتك عن شيء بعدها فلا تصاحبني قد بلغت من لدني عذرا (٧٦)) .','853','1574','0');
INSERT INTO [t22928] VALUES (1630,'قال تعالى: (فانطلقا حتى إذا أتيا أهل قرية استطعما أهلها فأبوا أن يضيفوهما فوجدا فيها جدارا يريد أن ينقض فأقامه قال لو شئت لاتخذت عليه أجرا (٧٧)) .','854','1574','0');
INSERT INTO [t22928] VALUES (1631,'قال تعالى: (قال هذا فراق بيني وبينك سأنبئك بتأويل ما لم تستطع عليه صبرا (٧٨)) .','855','1574','0');
INSERT INTO [t22928] VALUES (1632,'قال تعالى: (أما السفينة فكانت لمساكين يعملون في البحر فأردت أن أعيبها وكان وراءهم ملك يأخذ كل سفينة غصبا (٧٩)) .','855','1574','0');
INSERT INTO [t22928] VALUES (1633,'قال تعالى {وأما الغلام فكان أبواه مؤمنين فخشينا أن يرهقهما طغيانا وكفرا}','855','1574','0');
INSERT INTO [t22928] VALUES (1634,'قال تعالى: (فأردنا أن يبدلهما ربهما خيرا منه زكاة وأقرب رحما (٨١)) .','855','1574','0');
INSERT INTO [t22928] VALUES (1635,'قال تعالى: (وأما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز لهما وكان أبوهما صالحا فأراد ربك أن يبلغا أشدهما ويستخرجا كنزهما رحمة من ربك وما فعلته عن أمري ذلك تأويل ما لم تسطع عليه صبرا (٨٢)) .','855','1574','0');
INSERT INTO [t22928] VALUES (1636,'قال تعالى: (ويسألونك عن ذي القرنين قل سأتلو عليكم منه ذكرا (٨٣)) .','855','1574','0');
INSERT INTO [t22928] VALUES (1637,'قال تعالى: (إنا مكنا له في الأرض وآتيناه من كل شيء سببا (٨٤)) .','856','1574','0');
INSERT INTO [t22928] VALUES (1638,'قال تعالى: (فأتبع سببا (٨٥)) .','856','1574','0');
INSERT INTO [t22928] VALUES (1639,'قال تعالى: (حتى إذا بلغ مغرب الشمس وجدها تغرب في عين حمئة ووجد عندها قوما قلنا يا ذا القرنين إما أن تعذب وإما أن تتخذ فيهم حسنا (٧٨٦)) .','856','1574','0');
INSERT INTO [t22928] VALUES (1640,'قال تعالى: (وأما من آمن وعمل صالحا فله جزاء الحسنى وسنقول له من أمرنا يسرا (٨٨)) .','856','1574','0');
INSERT INTO [t22928] VALUES (1641,'قال تعالى: (حتى إذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا (٩٠)) .','857','1574','0');
INSERT INTO [t22928] VALUES (1642,'قال تعالى: (كذلك وقد أحطنا بما لديه خبرا (٩١)) .','857','1574','0');
INSERT INTO [t22928] VALUES (1643,'قال تعالى: (حتى إذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا (٩٣)) .','857','1574','0');
INSERT INTO [t22928] VALUES (1644,'قال تعالى: (قالوا يا ذا القرنين إن يأجوج ومأجوج مفسدون في الأرض فهل نجعل لك خرجا على أن تجعل بيننا وبينهم سدا (٩٤)) .','858','1574','0');
INSERT INTO [t22928] VALUES (1645,'قال تعالى: (قال ما مكني فيه ربي خير فأعينوني بقوة أجعل بينكم وبينهم ردما (٩٥) آتوني زبر الحديد حتى إذا ساوى بين الصدفين قال انفخوا حتى إذا جعله نارا قال آتوني أفرغ عليه قطرا (٩٦)) .','858','1574','0');
INSERT INTO [t22928] VALUES (1646,'قال تعالى: (فما اسطاعوا أن يظهروه وما استطاعوا له نقبا (٩٧)) .','859','1574','0');
INSERT INTO [t22928] VALUES (1647,'قال تعالى: (قال هذا رحمة من ربي فإذا جاء وعد ربي جعله دكاء وكان وعد ربي حقا (٩٨)) .','859','1574','0');
INSERT INTO [t22928] VALUES (1648,'قال تعالى: (الذين كانت أعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا (١٠١)) .','859','1574','0');
INSERT INTO [t22928] VALUES (1649,'قال تعالى: (أفحسب الذين كفروا أن يتخذوا عبادي من دوني أولياء إنا أعتدنا جهنم للكافرين نزلا (١٠٢))','859','1574','0');
INSERT INTO [t22928] VALUES (1650,'قال تعالى: (قل هل ننبئكم بالأخسرين أعمالا (١٠٣)) .','860','1574','0');
INSERT INTO [t22928] VALUES (1651,'قال تعالى: (أولئك الذين كفروا بآيات ربهم ولقائه فحبطت أعمالهم فلا نقيم لهم يوم القيامة وزنا (١٠٥)) .','860','1574','0');
INSERT INTO [t22928] VALUES (1652,'قال تعالى: (ذلك جزاؤهم جهنم بما كفروا واتخذوا آياتي ورسلي هزوا (١٠٦)) .','860','1574','0');
INSERT INTO [t22928] VALUES (1653,'قال تعالى: (إن الذين آمنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا (١٠٧)) .','860','1574','0');
INSERT INTO [t22928] VALUES (1654,'قال تعالى: (خالدين فيها لا يبغون عنها حولا (١٠٨)) .','861','1574','0');
INSERT INTO [t22928] VALUES (1655,'قال تعالى: (قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله مددا (١٠٩)) .','861','1574','0');
INSERT INTO [t22928] VALUES (1656,'قال تعالى: (قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه أحدا (١١٠)) .','861','1574','0');
INSERT INTO [t22928] VALUES (1657,'مريم','862','0','0');
INSERT INTO [t22928] VALUES (1658,'قال تعالى: (كهيعص (١)) .','862','1657','0');
INSERT INTO [t22928] VALUES (1659,'قال تعالى: (ذكر رحمة ربك عبده زكريا (٢) إذ نادى ربه نداء خفيا (٣)) .','862','1657','0');
INSERT INTO [t22928] VALUES (1660,'قال تعالى: (قال رب إني وهن العظم مني واشتعل الرأس شيبا ولم أكن بدعائك رب شقيا (٤)) .','863','1657','0');
INSERT INTO [t22928] VALUES (1661,'قال تعالى: (وإني خفت الموالي من ورائي وكانت امرأتي عاقرا فهب لي من لدنك وليا (٥)) .','863','1657','0');
INSERT INTO [t22928] VALUES (1662,'قال تعالى: (يرثني ويرث من آل يعقوب واجعله رب رضيا (٦) يازكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا (٧)) .','863','1657','0');
INSERT INTO [t22928] VALUES (1663,'قال تعالى: (قال رب أنى يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا (٨)) .','864','1657','0');
INSERT INTO [t22928] VALUES (1664,'قال تعالى: (قال كذلك قال ربك هو علي هين وقد خلقتك من قبل ولم تك شيئا (٩)) .','864','1657','0');
INSERT INTO [t22928] VALUES (1665,'قال تعالى: (قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاث ليال سويا (١٠)) .','864','1657','0');
INSERT INTO [t22928] VALUES (1666,'قال تعالى: (فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا (١١) يايحيى خذ الكتاب بقوة وآتيناه الحكم صبيا (١٢) وحنانا من لدنا وزكاة وكان تقيا (١٣) وبرا بوالديه ولم يكن جبارا عصيا (١٤)) .','865','1657','0');
INSERT INTO [t22928] VALUES (1667,'قال تعالى: (واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا (١٦) فاتخذت من دونهم حجابا فأرسلنا إليها روحنا فتمثل لها بشرا سويا (١٧)) .','865','1657','0');
INSERT INTO [t22928] VALUES (1668,'قال تعالى: (قال إنما أنا رسول ربك لأهب لك غلاما زكيا (١٩)) .','866','1657','0');
INSERT INTO [t22928] VALUES (1669,'قال تعالى: (قالت أنى يكون لي غلام ولم يمسسني بشر ولم أك بغيا () ٢٠) .','866','1657','0');
INSERT INTO [t22928] VALUES (1670,'قال تعالى: (قال كذلك قال ربك هو علي هين ولنجعله آية للناس ورحمة منا وكان أمرا مقضيا (٢١)) .','866','1657','0');
INSERT INTO [t22928] VALUES (1671,'قال تعالى: (فحملته فانتبذت به مكانا قصيا (٢٢)) .','866','1657','0');
INSERT INTO [t22928] VALUES (1672,'قال تعالى: (فأجاءها المخاض إلى جذع النخلة قالت يا ليتني مت قبل هذا وكنت نسيا منسيا (٢٣)) .','867','1657','0');
INSERT INTO [t22928] VALUES (1673,'قال تعالى: (فناداها من تحتها ألا تحزني قد جعل ربك تحتك سريا (٢٤)) .','868','1657','0');
INSERT INTO [t22928] VALUES (1674,'قال تعالى: (وهزي إليك بجذع النخلة تساقط عليك رطبا جنيا (٢٥)) .','868','1657','0');
INSERT INTO [t22928] VALUES (1675,'قال تعالى: (فكلي واشربي وقري عينا فإما ترين من البشر أحدا فقولي إني نذرت للرحمن صوما فلن أكلم اليوم إنسيا (٢٦)) .','869','1657','0');
INSERT INTO [t22928] VALUES (1676,'قال تعالى: (فأتت به قومها تحمله قالوا يا مريم لقد جئت شيئا فريا (٢٧)) .','870','1657','0');
INSERT INTO [t22928] VALUES (1677,'قال تعالى: (فأشارت إليه قالوا كيف نكلم من كان في المهد صبيا (٢٩)) .','870','1657','0');
INSERT INTO [t22928] VALUES (1678,'قال تعالى: (وبرا بوالدتي ولم يجعلني جبارا شقيا (٣٢)) .','870','1657','0');
INSERT INTO [t22928] VALUES (1679,'قال تعالى: (والسلام علي يوم ولدت ويوم أموت ويوم أبعث حيا (٣٣)) .','871','1657','0');
INSERT INTO [t22928] VALUES (1680,'قال تعالى: (ذلك عيسى ابن مريم قول الحق الذي فيه يمترون (٣٤)) .','871','1657','0');
INSERT INTO [t22928] VALUES (1681,'قال تعالى: (وإن الله ربي وربكم فاعبدوه هذا صراط مستقيم (٣٦)) .','872','1657','0');
INSERT INTO [t22928] VALUES (1682,'قال تعالى: (أسمع بهم وأبصر يوم يأتوننا لكن الظالمون اليوم في ضلال مبين (٣٨)) .','872','1657','0');
INSERT INTO [t22928] VALUES (1683,'قال تعالى: (وأنذرهم يوم الحسرة إذ قضي الأمر وهم في غفلة وهم لا يؤمنون (٣٩)) .','872','1657','0');
INSERT INTO [t22928] VALUES (1684,'قال تعالى: (إذ قال لأبيه يا أبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا (٤٢)) .','872','1657','0');
INSERT INTO [t22928] VALUES (1685,'قال تعالى: (قال أراغب أنت عن آلهتي يا إبراهيم لئن لم تنته لأرجمنك واهجرني مليا (٤٦)) .','873','1657','0');
INSERT INTO [t22928] VALUES (1686,'قال تعالى: (فلما اعتزلهم وما يعبدون من دون الله وهبنا له إسحاق ويعقوب وكلا جعلنا نبيا (٤٩)) .','873','1657','0');
INSERT INTO [t22928] VALUES (1687,'قال تعالى: (وناديناه من جانب الطور الأيمن وقربناه نجيا (٥٢) ووهبنا له من رحمتنا أخاه هارون نبيا (٥٣)) .','873','1657','0');
INSERT INTO [t22928] VALUES (1688,'قال تعالى: (ورفعناه مكانا عليا (٥٧)) .','873','1657','0');
INSERT INTO [t22928] VALUES (1689,'قال تعالى: (أولئك الذين أنعم الله عليهم من النبيين من ذرية آدم وممن حملنا مع نوح ومن ذرية إبراهيم وإسرائيل وممن هدينا واجتبينا إذا تتلى عليهم آيات الرحمن خروا سجدا وبكيا (٥٨) فخلف من بعدهم خلف أضاعوا الصلاة واتبعوا الشهوات فسوف يلقون غيا (٥٩)) .','873','1657','0');
INSERT INTO [t22928] VALUES (1690,'قال تعالى: (جنات عدن التي وعد الرحمن عباده بالغيب إنه كان وعده مأتيا (٦١)) .','874','1657','0');
INSERT INTO [t22928] VALUES (1691,'قال تعالى: (وما نتنزل إلا بأمر ربك له ما بين أيدينا وما خلفنا وما بين ذلك وما كان ربك نسيا (٦٤)) .','874','1657','0');
INSERT INTO [t22928] VALUES (1692,'قال تعالى: (رب السماوات والأرض وما بينهما فاعبده واصطبر لعبادته هل تعلم له سميا (٦٥)) .','874','1657','0');
INSERT INTO [t22928] VALUES (1693,'قال تعالى: (ويقول الإنسان أإذا ما مت لسوف أخرج حيا (٦٦)) .','874','1657','0');
INSERT INTO [t22928] VALUES (1694,'قال تعالى: (أولا يذكر الإنسان أنا خلقناه من قبل ولم يك شيئا (٦٧) فوربك لنحشرنهم والشياطين ثم لنحضرنهم حول جهنم جثيا (٦٨)) .','874','1657','0');
INSERT INTO [t22928] VALUES (1695,'قال تعالى: (ثم لننزعن من كل شيعة أيهم أشد على الرحمن عتيا (٦٩)) .','875','1657','0');
INSERT INTO [t22928] VALUES (1696,'قال تعالى: (وإن منكم إلا واردها كان على ربك حتما مقضيا (٧١)) .','876','1657','0');
INSERT INTO [t22928] VALUES (1697,'قال تعالى: (وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للذين آمنوا أي الفريقين خير مقاما وأحسن نديا (٧٣)) .','876','1657','0');
INSERT INTO [t22928] VALUES (1698,'قال تعالى: (وكم أهلكنا قبلهم من قرن هم أحسن أثاثا ورئيا (٧٤)) .','876','1657','0');
INSERT INTO [t22928] VALUES (1699,'قال تعالى: (قل من كان في الضلالة فليمدد له الرحمن مدا حتى إذا رأوا ما يوعدون إما العذاب وإما الساعة فسيعلمون من هو شر مكانا وأضعف جندا (٧٥) ويزيد الله الذين اهتدوا هدى والباقيات الصالحات خير عند ربك ثوابا وخير مردا (٧٦)) .','877','1657','0');
INSERT INTO [t22928] VALUES (1700,'قال تعالى: (أفرأيت الذي كفر بآياتنا وقال لأوتين مالا وولدا (٧٧)) .','878','1657','0');
INSERT INTO [t22928] VALUES (1701,'قال تعالى: (أطلع الغيب أم اتخذ عند الرحمن عهدا (٧٨)) .','878','1657','0');
INSERT INTO [t22928] VALUES (1702,'قال تعالى: (كلا سنكتب ما يقول ونمد له من العذاب مدا (٧٩) ونرثه ما يقول ويأتينا فردا (٨٠) واتخذوا من دون الله آلهة ليكونوا لهم عزا (٨١) كلا سيكفرون بعبادتهم ويكونون عليهم ضدا (٨٢)) .','878','1657','0');
INSERT INTO [t22928] VALUES (1703,'قال تعالى: (يوم نحشر المتقين إلى الرحمن وفدا (٨٥) ونسوق المجرمين إلى جهنم وردا (٨٦) لا يملكون الشفاعة إلا من اتخذ عند الرحمن عهدا (٨٧)) .','879','1657','0');
INSERT INTO [t22928] VALUES (1704,'قال تعالى: (لقد جئتم شيئا إدا (٨٩)) .','879','1657','0');
INSERT INTO [t22928] VALUES (1705,'قال تعالى: (تكاد السماوات يتفطرن منه وتنشق الأرض وتخر الجبال هدا (٩٠)) .','879','1657','0');
INSERT INTO [t22928] VALUES (1706,'قال تعالى: (أن دعوا للرحمن ولدا (٩١)) .','880','1657','0');
INSERT INTO [t22928] VALUES (1707,'قال تعالى: (إن كل من في السماوات والأرض إلا آتي الرحمن عبدا (٩٣)) .','880','1657','0');
INSERT INTO [t22928] VALUES (1708,'قال تعالى: (فإنما يسرناه بلسانك لتبشر به المتقين وتنذر به قوما لدا (٩٧)) .','880','1657','0');
INSERT INTO [t22928] VALUES (1709,'طه','881','0','0');
INSERT INTO [t22928] VALUES (1710,'قال تعالى: (ما أنزلنا عليك القرآن لتشقى (٢) إلا تذكرة لمن يخشى (٣)) .','881','1709','0');
INSERT INTO [t22928] VALUES (1711,'قال تعالى: (تنزيلا ممن خلق الأرض والسماوات العلا (٤)) .','881','1709','0');
INSERT INTO [t22928] VALUES (1712,'قال تعالى: (له ما في السماوات وما في الأرض وما بينهما وما تحت الثرى (٦)) .','882','1709','0');
INSERT INTO [t22928] VALUES (1713,'قال تعالى: (وإن تجهر بالقول فإنه يعلم السر وأخفى (٧)) .','882','1709','0');
INSERT INTO [t22928] VALUES (1714,'قال تعالى: (وهل أتاك حديث موسى (٩) إذ رأى نارا فقال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بقبس أو أجد على النار هدى (١٠)) .','882','1709','0');
INSERT INTO [t22928] VALUES (1715,'قال تعالى: (فلما أتاها نودي يا موسى (١١) إني أنا ربك فاخلع نعليك إنك بالوادي المقدس طوى (١٢)) .','883','1709','0');
INSERT INTO [t22928] VALUES (1716,'قال تعالى: (وأنا اخترتك فاستمع لما يوحى (١٣)) .','883','1709','0');
INSERT INTO [t22928] VALUES (1717,'قال تعالى: (إنني أنا الله لا إله إلا أنا فاعبدني وأقم الصلاة لذكري (١٤)) .','884','1709','0');
INSERT INTO [t22928] VALUES (1718,'قال تعالى: (إن الساعة آتية أكاد أخفيها لتجزى كل نفس بما تسعى (١٥)) .','884','1709','0');
INSERT INTO [t22928] VALUES (1719,'قال تعالى: (فلا يصدنك عنها من لا يؤمن بها واتبع هواه فتردى (١٦)) .','884','1709','0');
INSERT INTO [t22928] VALUES (1720,'قال تعالى: (وما تلك بيمينك يا موسى (١٧)) .','884','1709','0');
INSERT INTO [t22928] VALUES (1721,'قال تعالى: (قال هي عصاي أتوكأ عليها وأهش بها على غنمي ولي فيها مآرب أخرى (١٨) قال ألقها ياموسى (١٩) فألقاها فإذا هي حية تسعى (٢٠)) .','885','1709','0');
INSERT INTO [t22928] VALUES (1722,'قال تعالى: (قال خذها ولا تخف سنعيدها سيرتها الأولى (٢١) واضمم يدك إلى جناحك تخرج بيضاء من غير سوء آية أخرى (٢٢) لنريك من آياتنا الكبرى (٢٣)) .','885','1709','0');
INSERT INTO [t22928] VALUES (1723,'قال تعالى: (قال رب اشرح لي صدري (٢٥) ويسر لي أمري (٢٦) واحلل عقدة من لساني (٢٧)) .','886','1709','0');
INSERT INTO [t22928] VALUES (1724,'قال تعالى: (واجعل لي وزيرا من أهلي (٢٩) هارون أخي (٣٠) اشدد به أزري (٣١) وأشركه في أمري (٣٢)) .','887','1709','0');
INSERT INTO [t22928] VALUES (1725,'قال تعالى: (كي نسبحك كثيرا (٣٣) ونذكرك كثيرا (٣٤)) .','887','1709','0');
INSERT INTO [t22928] VALUES (1726,'قال تعالى: (قد أوتيت سؤلك يا موسى (٣٦)) . والسؤال والسؤل بمعنى المفعول، مثل الأكل بمعنى المأكول.','887','1709','0');
INSERT INTO [t22928] VALUES (1727,'قال تعالى: (إذ أوحينا إلى أمك ما يوحى (٣٨) أن اقذفيه في التابوت فاقذفيه في اليم فليلقه اليم بالساحل يأخذه عدو لي وعدو له وألقيت عليك محبة مني ولتصنع على عيني (٣٩) إذ تمشي أختك فتقول هل أدلكم على من يكفله فرجعناك إلى أمك كي تقر عينها ولا تحزن وقتلت نفسا فنجيناك من الغم وفتناك فتونا فلبثت سنين في أهل مدين ثم جئت على قدر ياموسى (٤٠)) .','888','1709','0');
INSERT INTO [t22928] VALUES (1728,'قال تعالى: (قالا ربنا إننا نخاف أن يفرط علينا أو أن يطغى (٤٥)) .','888','1709','0');
INSERT INTO [t22928] VALUES (1729,'قال تعالى: (قال فمن ربكما يا موسى (٤٩) قال ربنا الذي أعطى كل شيء خلقه ثم هدى (٥٠)) .','889','1709','0');
INSERT INTO [t22928] VALUES (1730,'قال تعالى: (قال علمها عند ربي في كتاب لا يضل ربي ولا ينسى (٥٢)) .','889','1709','0');
INSERT INTO [t22928] VALUES (1731,'قال تعالى: (الذي جعل لكم الأرض مهدا وسلك لكم فيها سبلا وأنزل من السماء ماء فأخرجنا به أزواجا من نبات شتى (٥٣) كلوا وارعوا أنعامكم إن في ذلك لآيات لأولي النهى (٥٤)) .','890','1709','0');
INSERT INTO [t22928] VALUES (1732,'قال تعالى: (فلنأتينك بسحر مثله فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا أنت مكانا سوى (٥٨)) .','890','1709','0');
INSERT INTO [t22928] VALUES (1733,'قال تعالى: (قال موعدكم يوم الزينة وأن يحشر الناس ضحى (٥٩)) .','891','1709','0');
INSERT INTO [t22928] VALUES (1734,'قال تعالى: (قال لهم موسى ويلكم لا تفتروا على الله كذبا فيسحتكم بعذاب وقد خاب من افترى (٦١)) .','891','1709','0');
INSERT INTO [t22928] VALUES (1735,'قال تعالى: (قالوا إن هذان لساحران يريدان أن يخرجاكم من أرضكم بسحرهما ويذهبا بطريقتكم المثلى (٦٣)) .','891','1709','0');
INSERT INTO [t22928] VALUES (1736,'قال تعالى: (فأجمعوا كيدكم ثم ائتوا صفا وقد أفلح اليوم من استعلى (٦٤)) .','892','1709','0');
INSERT INTO [t22928] VALUES (1737,'قال تعالى: (قالوا يا موسى إما أن تلقي وإما أن نكون أول من ألقى (٦٥)) .','892','1709','0');
INSERT INTO [t22928] VALUES (1738,'قال تعالى: (قال بل ألقوا فإذا حبالهم وعصيهم يخيل إليه من سحرهم أنها تسعى (٦٦)) .','893','1709','0');
INSERT INTO [t22928] VALUES (1739,'قال تعالى: (وألق ما في يمينك تلقف ما صنعوا إنما صنعوا كيد ساحر ولا يفلح الساحر حيث أتى (٦٩)) .','893','1709','0');
INSERT INTO [t22928] VALUES (1740,'قال تعالى: (قال آمنتم له قبل أن آذن لكم إنه لكبيركم الذي علمكم السحر فلأقطعن أيديكم وأرجلكم من خلاف ولأصلبنكم في جذوع النخل ولتعلمن أينا أشد عذابا وأبقى (٧١)) .','894','1709','0');
INSERT INTO [t22928] VALUES (1741,'قال تعالى: (قالوا لن نؤثرك على ما جاءنا من البينات والذي فطرنا فاقض ما أنت قاض إنما تقضي هذه الحياة الدنيا (٧٢))','894','1709','0');
INSERT INTO [t22928] VALUES (1742,'قال تعالى: (إنا آمنا بربنا ليغفر لنا خطايانا وما أكرهتنا عليه من السحر والله خير وأبقى (٧٣)) .','895','1709','0');
INSERT INTO [t22928] VALUES (1743,'قال تعالى: (إنه من يأت ربه مجرما فإن له جهنم لا يموت فيها ولا يحيا (٧٤)) .','895','1709','0');
INSERT INTO [t22928] VALUES (1744,'قال تعالى: (جنات عدن تجري من تحتها الأنهار خالدين فيها وذلك جزاء من تزكى (٧٦)) .','895','1709','0');
INSERT INTO [t22928] VALUES (1745,'قال تعالى: (ولقد أوحينا إلى موسى أن أسر بعبادي فاضرب لهم طريقا في البحر يبسا لا تخاف دركا ولا تخشى (٧٧)) .','895','1709','0');
INSERT INTO [t22928] VALUES (1746,'قال تعالى: (فأتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم (٧٨)) .','896','1709','0');
INSERT INTO [t22928] VALUES (1747,'قال تعالى: (يا بني إسرائيل قد أنجيناكم من عدوكم وواعدناكم جانب الطور الأيمن ونزلنا عليكم المن والسلوى (٨٠) كلوا من طيبات ما رزقناكم ولا تطغوا فيه فيحل عليكم غضبي ومن يحلل عليه غضبي فقد هوى (٨١))','896','1709','0');
INSERT INTO [t22928] VALUES (1748,'قال تعالى: (وما أعجلك عن قومك يا موسى (٨٣)) .','897','1709','0');
INSERT INTO [t22928] VALUES (1749,'قال تعالى: (قال هم أولاء على أثري وعجلت إليك رب لترضى (٨٤)) .','897','1709','0');
INSERT INTO [t22928] VALUES (1750,'قال تعالى: (فرجع موسى إلى قومه غضبان أسفا قال يا قوم ألم يعدكم ربكم وعدا حسنا أفطال عليكم العهد أم أردتم أن يحل عليكم غضب من ربكم فأخلفتم موعدي (٨٦)) .','897','1709','0');
INSERT INTO [t22928] VALUES (1751,'قال تعالى: (قالوا ما أخلفنا موعدك بملكنا ولكنا حملنا أوزارا من زينة القوم فقذفناها فكذلك ألقى السامري (٨٧) فأخرج لهم عجلا جسدا له خوار فقالوا هذا إلهكم وإله موسى فنسي (٨٨)) .','897','1709','0');
INSERT INTO [t22928] VALUES (1752,'قال تعالى: (أفلا يرون ألا يرجع إليهم قولا ولا يملك لهم ضرا ولا نفعا (٨٩)) .','898','1709','0');
INSERT INTO [t22928] VALUES (1753,'قال تعالى: (قال يا هارون ما منعك إذ رأيتهم ضلوا (٩٢) ألا تتبعني أفعصيت أمري (٩٣) قال ياابن أم لا تأخذ بلحيتي ولا برأسي إني خشيت أن تقول فرقت بين بني إسرائيل ولم ترقب قولي (٩٤)) .','898','1709','0');
INSERT INTO [t22928] VALUES (1754,'قال تعالى: (قال بصرت بما لم يبصروا به فقبضت قبضة من أثر الرسول فنبذتها وكذلك سولت لي نفسي (٩٦)) .','899','1709','0');
INSERT INTO [t22928] VALUES (1755,'قال تعالى: (قال فاذهب فإن لك في الحياة أن تقول لا مساس وإن لك موعدا لن تخلفه وانظر إلى إلهك الذي ظلت عليه عاكفا لنحرقنه ثم لننسفنه في اليم نسفا (٩٧)) .','899','1709','0');
INSERT INTO [t22928] VALUES (1756,'قال تعالى: (إنما إلهكم الله الذي لا إله إلا هو وسع كل شيء علما (٩٨) كذلك نقص عليك من أنباء ما قد سبق وقد آتيناك من لدنا ذكرا (٩٩)) .','900','1709','0');
INSERT INTO [t22928] VALUES (1757,'قال تعالى: (خالدين فيه وساء لهم يوم القيامة حملا (١٠١)) .','901','1709','0');
INSERT INTO [t22928] VALUES (1758,'قال تعالى: (يوم ينفخ في الصور ونحشر المجرمين يومئذ زرقا (١٠٢) يتخافتون بينهم إن لبثتم إلا عشرا (١٠٣)) .','901','1709','0');
INSERT INTO [t22928] VALUES (1759,'قال تعالى: (فيذرها قاعا صفصفا (١٠٦) لا ترى فيها عوجا ولا أمتا (٠٧) يومئذ يتبعون الداعي لا عوج له وخشعت الأصوات للرحمن فلا تسمع إلا همسا (١٠٨)) .','901','1709','0');
INSERT INTO [t22928] VALUES (1760,'قال تعالى: (يومئذ لا تنفع الشفاعة إلا من أذن له الرحمن ورضي له قولا (١٠٩)) .','901','1709','0');
INSERT INTO [t22928] VALUES (1761,'قال تعالى: (وعنت الوجوه للحي القيوم وقد خاب من حمل ظلما (١١١)) .','902','1709','0');
INSERT INTO [t22928] VALUES (1762,'قال تعالى: (ومن يعمل من الصالحات وهو مؤمن فلا يخاف ظلما ولا هضما (١١٢))','902','1709','0');
INSERT INTO [t22928] VALUES (1763,'قال تعالى: (وكذلك أنزلناه قرآنا عربيا وصرفنا فيه من الوعيد لعلهم يتقون أو يحدث لهم ذكرا (١١٣)) .','902','1709','0');
INSERT INTO [t22928] VALUES (1764,'قال تعالى: (فتعالى الله الملك الحق ولا تعجل بالقرآن من قبل أن يقضى إليك وحيه وقل رب زدني علما (١١٤)) .','902','1709','0');
INSERT INTO [t22928] VALUES (1765,'قال تعالى: (ولقد عهدنا إلى آدم من قبل فنسي ولم نجد له عزما ١١٥) .','902','1709','0');
INSERT INTO [t22928] VALUES (1766,'قال تعالى: (وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى (١١٦)) .','902','1709','0');
INSERT INTO [t22928] VALUES (1767,'قال تعالى: (فقلنا يا آدم إن هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى (١١٧)) .','903','1709','0');
INSERT INTO [t22928] VALUES (1768,'قال تعالى: (وأنك لا تظمأ فيها ولا تضحى (١١٩)) .','903','1709','0');
INSERT INTO [t22928] VALUES (1769,'قال تعالى: (فوسوس إليه الشيطان قال يا آدم هل أدلك على شجرة الخلد وملك لا يبلى (١٢٠)) .','903','1709','0');
INSERT INTO [t22928] VALUES (1770,'قال تعالى: (فأكلا منها فبدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة وعصى آدم ربه فغوى (١٢١)) .','903','1709','0');
INSERT INTO [t22928] VALUES (1771,'قال تعالى: (ومن أعرض عن ذكري فإن له معيشة ضنكا ونحشره يوم القيامة أعمى (١٢٤))','904','1709','0');
INSERT INTO [t22928] VALUES (1772,'قال تعالى: (قال كذلك أتتك آياتنا فنسيتها وكذلك اليوم تنسى (١٢٦)) .','904','1709','0');
INSERT INTO [t22928] VALUES (1773,'قال تعالى: (أفلم يهد لهم كم أهلكنا قبلهم من القرون يمشون في مساكنهم إن في ذلك لآيات لأولي النهى (١٢٨)) .','904','1709','0');
INSERT INTO [t22928] VALUES (1774,'قال تعالى: (ولولا كلمة سبقت من ربك لكان لزاما وأجل مسمى (١٢٩)) .','905','1709','0');
INSERT INTO [t22928] VALUES (1775,'قال تعالى: (فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها ومن آناء الليل فسبح وأطراف النهار لعلك ترضى (١٣٠)) .','905','1709','0');
INSERT INTO [t22928] VALUES (1776,'قال تعالى: (ولا تمدن عينيك إلى ما متعنا به أزواجا منهم زهرة الحياة الدنيا لنفتنهم فيه ورزق ربك خير وأبقى (١٣١)) .','905','1709','0');
INSERT INTO [t22928] VALUES (1777,'قال تعالى: (وأمر أهلك بالصلاة واصطبر عليها لا نسألك رزقا نحن نرزقك والعاقبة للتقوى (١٣٢))','906','1709','0');
INSERT INTO [t22928] VALUES (1778,'قال تعالى: (وقالوا لولا يأتينا بآية من ربه أولم تأتهم بينة ما في الصحف الأولى (١٣٣) ولو أنا أهلكناهم بعذاب من قبله لقالوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك من قبل أن نذل ونخزى (١٣٤)) .','906','1709','0');
INSERT INTO [t22928] VALUES (1779,'قال تعالى: (قل كل متربص فتربصوا فستعلمون من أصحاب الصراط السوي ومن اهتدى (١٣٥)) .','907','1709','0');
INSERT INTO [t22928] VALUES (1780,'الأنبياء','908','0','0');
INSERT INTO [t22928] VALUES (1781,'قال تعالى: (اقترب للناس حسابهم وهم في غفلة معرضون (١)) .','908','1780','0');
INSERT INTO [t22928] VALUES (1782,'قال تعالى: (ما يأتيهم من ذكر من ربهم محدث إلا استمعوه وهم يلعبون (٢) لاهية قلوبهم وأسروا النجوى الذين ظلموا هل هذا إلا بشر مثلكم أفتأتون السحر وأنتم تبصرون (٣)) .','908','1780','0');
INSERT INTO [t22928] VALUES (1783,'قال تعالى: (قال ربي يعلم القول في السماء والأرض وهو السميع العليم (٤)) .','909','1780','0');
INSERT INTO [t22928] VALUES (1784,'قال تعالى: (بل قالوا أضغاث أحلام بل افتراه بل هو شاعر فليأتنا بآية كما أرسل الأولون (٥) ما آمنت قبلهم من قرية أهلكناها أفهم يؤمنون (٦) وما أرسلنا قبلك إلا رجالا نوحي إليهم فاسألوا أهل الذكر إن كنتم لا تعلمون (٧)) .','909','1780','0');
INSERT INTO [t22928] VALUES (1785,'قال تعالى: (وما جعلناهم جسدا لا يأكلون الطعام وما كانوا خالدين (٨)) .','909','1780','0');
INSERT INTO [t22928] VALUES (1786,'قال تعالى: (لقد أنزلنا إليكم كتابا فيه ذكركم أفلا تعقلون (١٠) وكم قصمنا من قرية كانت ظالمة وأنشأنا بعدها قوما آخرين (١١)) .','909','1780','0');
INSERT INTO [t22928] VALUES (1787,'قال تعالى: (فلما أحسوا بأسنا إذا هم منها يركضون (١٢)) .','910','1780','0');
INSERT INTO [t22928] VALUES (1788,'قال تعالى: (فما زالت تلك دعواهم حتى جعلناهم حصيدا خامدين (١٥) وما خلقنا السماء والأرض وما بينهما لاعبين (١٦) لو أردنا أن نتخذ لهوا لاتخذناه من لدنا إن كنا فاعلين (١٧) بل نقذف بالحق على الباطل فيدمغه فإذا هو زاهق ولكم الويل مما تصفون (١٨)) .','910','1780','0');
INSERT INTO [t22928] VALUES (1789,'قال تعالى: (وله من في السماوات والأرض ومن عنده لا يستكبرون عن عبادته ولا يستحسرون (١٩)) .','911','1780','0');
INSERT INTO [t22928] VALUES (1790,'قال تعالى: (يسبحون الليل والنهار لا يفترون (٢٠)) .','911','1780','0');
INSERT INTO [t22928] VALUES (1791,'قال تعالى: (أم اتخذوا آلهة من الأرض هم ينشرون (٢١)) .','911','1780','0');
INSERT INTO [t22928] VALUES (1792,'قال تعالى: (لو كان فيهما آلهة إلا الله لفسدتا فسبحان الله رب العرش عما يصفون (٢٢)) .','911','1780','0');
INSERT INTO [t22928] VALUES (1793,'قال تعالى: (أم اتخذوا من دونه آلهة قل هاتوا برهانكم هذا ذكر من معي وذكر من قبلي بل أكثرهم لا يعلمون الحق فهم معرضون (٢٤)) .','912','1780','0');
INSERT INTO [t22928] VALUES (1794,'قال تعالى: (وقالوا اتخذ الرحمن ولدا سبحانه بل عباد مكرمون (٢٦) لا يسبقونه بالقول وهم بأمره يعملون (٢٧))','913','1780','0');
INSERT INTO [t22928] VALUES (1795,'قال تعالى: (ومن يقل منهم إني إله من دونه فذلك نجزيه جهنم كذلك نجزي الظالمين (٢٩)) .','913','1780','0');
INSERT INTO [t22928] VALUES (1796,'قال تعالى: (أولم ير الذين كفروا أن السماوات والأرض كانتا رتقا ففتقناهما وجعلنا من الماء كل شيء حي أفلا يؤمنون (٣٠)) .','913','1780','0');
INSERT INTO [t22928] VALUES (1797,'قال تعالى: (وجعلنا في الأرض رواسي أن تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون (٣١)) .','914','1780','0');
INSERT INTO [t22928] VALUES (1798,'قال تعالى: (وهو الذي خلق الليل والنهار والشمس والقمر كل في فلك يسبحون (٣٣)) .','914','1780','0');
INSERT INTO [t22928] VALUES (1799,'قال تعالى: (وما جعلنا لبشر من قبلك الخلد أفإن مت فهم الخالدون (٣٤)) .','914','1780','0');
INSERT INTO [t22928] VALUES (1800,'قال تعالى: (كل نفس ذائقة الموت ونبلوكم بالشر والخير فتنة وإلينا ترجعون (٣٥))','914','1780','0');
INSERT INTO [t22928] VALUES (1801,'قال تعالى: (وإذا رآك الذين كفروا إن يتخذونك إلا هزوا أهذا الذي يذكر آلهتكم وهم بذكر الرحمن هم كافرون (٣٦)) .','915','1780','0');
INSERT INTO [t22928] VALUES (1802,'قال تعالى: (خلق الإنسان من عجل سأريكم آياتي فلا تستعجلون (٣٧) ويقولون متى هذا الوعد إن كنتم صادقين (٣٨) لو يعلم الذين كفروا حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون (٣٩) بل تأتيهم بغتة فتبهتهم فلا يستطيعون ردها ولا هم ينظرون (٤٠))','915','1780','0');
INSERT INTO [t22928] VALUES (1803,'قال تعالى: (قل من يكلؤكم بالليل والنهار من الرحمن بل هم عن ذكر ربهم معرضون (٤١)) .','915','1780','0');
INSERT INTO [t22928] VALUES (1804,'قال تعالى: (أم لهم آلهة تمنعهم من دوننا لا يستطيعون نصر أنفسهم ولا هم منا يصحبون (٤٣)) .','915','1780','0');
INSERT INTO [t22928] VALUES (1805,'قال تعالى: (بل متعنا هؤلاء وآباءهم حتى طال عليهم العمر أفلا يرون أنا نأتي الأرض ننقصها من أطرافها أفهم الغالبون (٤٤)) .','915','1780','0');
INSERT INTO [t22928] VALUES (1806,'قال تعالى: (قل إنما أنذركم بالوحي ولا يسمع الصم الدعاء إذا ما ينذرون (٤٥)) .','916','1780','0');
INSERT INTO [t22928] VALUES (1807,'قال تعالى: (ولئن مستهم نفحة من عذاب ربك ليقولن يا ويلنا إنا كنا ظالمين (٤٦)) .','916','1780','0');
INSERT INTO [t22928] VALUES (1808,'قال تعالى: (ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا وإن كان مثقال حبة من خردل أتينا بها وكفى بنا حاسبين (٤٧)) .','916','1780','0');
INSERT INTO [t22928] VALUES (1809,'قال تعالى: (ولقد آتينا موسى وهارون الفرقان وضياء وذكرا للمتقين (٤٨)) .','916','1780','0');
INSERT INTO [t22928] VALUES (1810,'قال تعالى: (الذين يخشون ربهم بالغيب وهم من الساعة مشفقون (٤٩)) .','917','1780','0');
INSERT INTO [t22928] VALUES (1811,'قال تعالى: (إذ قال لأبيه وقومه ما هذه التماثيل التي أنتم لها عاكفون (٥٢)) .','917','1780','0');
INSERT INTO [t22928] VALUES (1812,'قال تعالى: (قال بل ربكم رب السماوات والأرض الذي فطرهن وأنا على ذلكم من الشاهدين (٥٦))','917','1780','0');
INSERT INTO [t22928] VALUES (1813,'قال تعالى: (فجعلهم جذاذا إلا كبيرا لهم لعلهم إليه يرجعون (٥٨)) .','917','1780','0');
INSERT INTO [t22928] VALUES (1814,'قال تعالى: (قالوا من فعل هذا بآلهتنا إنه لمن الظالمين (٥٩))','918','1780','0');
INSERT INTO [t22928] VALUES (1815,'قال تعالى: (قالوا سمعنا فتى يذكرهم يقال له إبراهيم (٦٠))','918','1780','0');
INSERT INTO [t22928] VALUES (1816,'قال تعالى: (قالوا فأتوا به على أعين الناس لعلهم يشهدون (٦١)) .','918','1780','0');
INSERT INTO [t22928] VALUES (1817,'قال تعالى: (قال بل فعله كبيرهم هذا فاسألوهم إن كانوا ينطقون (٦٣)) .','918','1780','0');
INSERT INTO [t22928] VALUES (1818,'قال تعالى: (ثم نكسوا على رءوسهم لقد علمت ما هؤلاء ينطقون (٦٥) قال أفتعبدون من دون الله ما لا ينفعكم شيئا ولا يضركم (٦٦)) .','919','1780','0');
INSERT INTO [t22928] VALUES (1819,'قال تعالى: (قلنا يا نار كوني بردا وسلاما على إبراهيم (٦٩)) .','919','1780','0');
INSERT INTO [t22928] VALUES (1820,'قال تعالى: (ووهبنا له إسحاق ويعقوب نافلة وكلا جعلنا صالحين (٧٢) وجعلناهم أئمة يهدون بأمرنا وأوحينا إليهم فعل الخيرات وإقام الصلاة وإيتاء الزكاة وكانوا لنا عابدين (٧٣))','919','1780','0');
INSERT INTO [t22928] VALUES (1821,'قال تعالى: (ولوطا آتيناه حكما وعلما ونجيناه من القرية التي كانت تعمل الخبائث إنهم كانوا قوم سوء فاسقين (٧٤)) .','919','1780','0');
INSERT INTO [t22928] VALUES (1822,'قال تعالى: (ونصرناه من القوم الذين كذبوا بآياتنا إنهم كانوا قوم سوء فأغرقناهم أجمعين (٧٧) وداود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين (٧٨)) .','920','1780','0');
INSERT INTO [t22928] VALUES (1823,'قال تعالى: (ففهمناها سليمان وكلا آتينا حكما وعلما وسخرنا مع داود الجبال يسبحن والطير وكنا فاعلين (٧٩)) .','920','1780','0');
INSERT INTO [t22928] VALUES (1824,'قال تعالى: (وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم فهل أنتم شاكرون (٨٠) ولسليمان الريح عاصفة تجري بأمره إلى الأرض التي باركنا فيها وكنا بكل شيء عالمين (٨١)) .','920','1780','0');
INSERT INTO [t22928] VALUES (1825,'قال تعالى: (ومن الشياطين من يغوصون له ويعملون عملا دون ذلك وكنا لهم حافظين (٨٢)) .','921','1780','0');
INSERT INTO [t22928] VALUES (1826,'قال تعالى: (فاستجبنا له فكشفنا ما به من ضر وآتيناه أهله ومثلهم معهم رحمة من عندنا وذكرى للعابدين (٨٤) وإسماعيل وإدريس وذا الكفل كل من الصابرين (٨٥) وأدخلناهم في رحمتنا إنهم من الصالحين (٨٦) وذا النون إذ ذهب مغاضبا فظن أن لن نقدر عليه فنادى في الظلمات أن لا إله إلا أنت سبحانك إني كنت من الظالمين (٨٧))','921','1780','0');
INSERT INTO [t22928] VALUES (1827,'قال تعالى: (فاستجبنا له ونجيناه من الغم وكذلك ننجي المؤمنين (٨٨)) .','921','1780','0');
INSERT INTO [t22928] VALUES (1828,'قال تعالى: (فاستجبنا له ووهبنا له يحيى وأصلحنا له زوجه إنهم كانوا يسارعون في الخيرات ويدعوننا رغبا ورهبا وكانوا لنا خاشعين (٩٠))','922','1780','0');
INSERT INTO [t22928] VALUES (1829,'قال تعالى: (والتي أحصنت فرجها فنفخنا فيها من روحنا وجعلناها وابنها آية للعالمين (٩١))','922','1780','0');
INSERT INTO [t22928] VALUES (1830,'قال تعالى: (إن هذه أمتكم أمة واحدة وأنا ربكم فاعبدون (٩٢)) .','923','1780','0');
INSERT INTO [t22928] VALUES (1831,'قال تعالى: (وتقطعوا أمرهم بينهم كل إلينا راجعون (٩٣) فمن يعمل من الصالحات وهو مؤمن فلا كفران لسعيه وإنا له كاتبون (٩٤))','923','1780','0');
INSERT INTO [t22928] VALUES (1832,'قال تعالى: (وحرام على قرية أهلكناها أنهم لا يرجعون (٩٥) حتى إذا فتحت يأجوج ومأجوج وهم من كل حدب ينسلون (٩٦) .','923','1780','0');
INSERT INTO [t22928] VALUES (1833,'قال تعالى: (واقترب الوعد الحق فإذا هي شاخصة أبصار الذين كفروا يا ويلنا قد كنا في غفلة من هذا بل كنا ظالمين (٩٧)) .','924','1780','0');
INSERT INTO [t22928] VALUES (1834,'قال تعالى: (إنكم وما تعبدون من دون الله حصب جهنم أنتم لها واردون (٩٨)) .','925','1780','0');
INSERT INTO [t22928] VALUES (1835,'قال تعالى: (إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون (١٠١) لا يسمعون حسيسها وهم في ما اشتهت أنفسهم خالدون (١٠٢) لا يحزنهم الفزع الأكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون (١٠٣)) .','925','1780','0');
INSERT INTO [t22928] VALUES (1836,'قال تعالى: (يوم نطوي السماء كطي السجل للكتب كما بدأنا أول خلق نعيده وعدا علينا إنا كنا فاعلين (١٠٤)) .','925','1780','0');
INSERT INTO [t22928] VALUES (1837,'قال تعالى: (ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون (١٠٥)) .','926','1780','0');
INSERT INTO [t22928] VALUES (1838,'قال تعالى: (وما أرسلناك إلا رحمة للعالمين (١٠٧)) .','926','1780','0');
INSERT INTO [t22928] VALUES (1839,'قال تعالى: (قل إنما يوحى إلي أنما إلهكم إله واحد فهل أنتم مسلمون (١٠٨)) .','926','1780','0');
INSERT INTO [t22928] VALUES (1840,'قال تعالى: (فإن تولوا فقل آذنتكم على سواء وإن أدري أقريب أم بعيد ما توعدون إنه يعلم الجهر من القول ويعلم ما تكتمون (١١٠))','927','1780','0');
INSERT INTO [t22928] VALUES (1841,'قال تعالى: (قال رب احكم بالحق وربنا الرحمن المستعان على ما تصفون (١١٢)) .','927','1780','0');
INSERT INTO [t22928] VALUES (1842,'الحج','928','0','0');
INSERT INTO [t22928] VALUES (1843,'قال تعالى: (يا أيها الناس اتقوا ربكم إن زلزلة الساعة شيء عظيم (١)) .','928','1842','0');
INSERT INTO [t22928] VALUES (1844,'قال تعالى: (يوم ترونها تذهل كل مرضعة عما أرضعت وتضع كل ذات حمل حملها وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد (٢)) .','928','1842','0');
INSERT INTO [t22928] VALUES (1845,'قال تعالى: (ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد (٣)) .','929','1842','0');
INSERT INTO [t22928] VALUES (1846,'قال تعالى: (كتب عليه أنه من تولاه فأنه يضله ويهديه إلى عذاب السعير (٤)) .','929','1842','0');
INSERT INTO [t22928] VALUES (1847,'قال تعالى: (يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ونقر في الأرحام ما نشاء إلى أجل مسمى ثم نخرجكم طفلا ثم لتبلغوا أشدكم ومنكم من يتوفى ومنكم من يرد إلى أرذل العمر لكيلا يعلم من بعد علم شيئا وترى الأرض هامدة فإذا أنزلنا عليها الماء اهتزت وربت وأنبتت من كل زوج بهيج (٥)) .','929','1842','0');
INSERT INTO [t22928] VALUES (1848,'قال تعالى: (ذلك بأن الله هو الحق وأنه يحيي الموتى وأنه على كل شيء قدير (٦)) .','930','1842','0');
INSERT INTO [t22928] VALUES (1849,'قال تعالى: (ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير (٨) ثاني عطفه ليضل عن سبيل الله له في الدنيا خزي ونذيقه يوم القيامة عذاب الحريق (٩)) .','931','1842','0');
INSERT INTO [t22928] VALUES (1850,'قال تعالى: (ومن الناس من يعبد الله على حرف فإن أصابه خير اطمأن به وإن أصابته فتنة انقلب على وجهه خسر الدنيا والآخرة ذلك هو الخسران المبين (١١)) .','931','1842','0');
INSERT INTO [t22928] VALUES (1851,'قال تعالى: (يدعو لمن ضره أقرب من نفعه لبئس المولى ولبئس العشير (١٢)) .','931','1842','0');
INSERT INTO [t22928] VALUES (1852,'قال تعالى: (من كان يظن أن لن ينصره الله في الدنيا والآخرة فليمدد بسبب إلى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ (١٥)) .','932','1842','0');
INSERT INTO [t22928] VALUES (1853,'قال تعالى: (وكذلك أنزلناه آيات بينات وأن الله يهدي من يريد (١٦)) .','933','1842','0');
INSERT INTO [t22928] VALUES (1854,'قال تعالى: (إن الذين آمنوا والذين هادوا والصابئين والنصارى والمجوس والذين أشركوا إن الله يفصل بينهم يوم القيامة إن الله على كل شيء شهيد (١٧)) .','933','1842','0');
INSERT INTO [t22928] VALUES (1855,'قال تعالى: (ألم تر أن الله يسجد له من في السماوات ومن في الأرض والشمس والقمر والنجوم والجبال والشجر والدواب وكثير من الناس وكثير حق عليه العذاب ومن يهن الله فما له من مكرم إن الله يفعل ما يشاء (١٨)) .','933','1842','0');
INSERT INTO [t22928] VALUES (1856,'قال تعالى: (هذان خصمان اختصموا في ربهم فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم (١٩) يصهر به ما في بطونهم والجلود (٢٠)) .','934','1842','0');
INSERT INTO [t22928] VALUES (1857,'قال تعالى: (كلما أرادوا أن يخرجوا منها من غم أعيدوا فيها وذوقوا عذاب الحريق (٢٢)) .','934','1842','0');
INSERT INTO [t22928] VALUES (1858,'قال تعالى: (إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير (٢٣)) .','935','1842','0');
INSERT INTO [t22928] VALUES (1859,'قال تعالى: (وهدوا إلى الطيب من القول وهدوا إلى صراط الحميد (٢٤)) .','935','1842','0');
INSERT INTO [t22928] VALUES (1860,'قال تعالى: (إن الذين كفروا ويصدون عن سبيل الله والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد ومن يرد فيه بإلحاد بظلم نذقه من عذاب أليم (٢٥))','935','1842','0');
INSERT INTO [t22928] VALUES (1861,'قال تعالى: (وإذ بوأنا لإبراهيم مكان البيت أن لا تشرك بي شيئا وطهر بيتي للطائفين والقائمين والركع السجود (٢٦)) .','936','1842','0');
INSERT INTO [t22928] VALUES (1862,'قال تعالى: (وأذن في الناس بالحج يأتوك رجالا وعلى كل ضامر يأتين من كل فج عميق (٢٧)) .','937','1842','0');
INSERT INTO [t22928] VALUES (1863,'قال تعالى: (ليشهدوا منافع لهم ويذكروا اسم الله في أيام معلومات) .','937','1842','0');
INSERT INTO [t22928] VALUES (1864,'قال تعالى: (ذلك ومن يعظم حرمات الله فهو خير له عند ربه وأحلت لكم الأنعام إلا ما يتلى عليكم فاجتنبوا الرجس من الأوثان واجتنبوا قول الزور (٣٠)) .','937','1842','0');
INSERT INTO [t22928] VALUES (1865,'قال تعالى: (حنفاء لله غير مشركين به ومن يشرك بالله فكأنما خر من السماء فتخطفه الطير أو تهوي به الريح في مكان سحيق (٣١)) .','938','1842','0');
INSERT INTO [t22928] VALUES (1866,'قال تعالى: (ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب (٣٢)) .','938','1842','0');
INSERT INTO [t22928] VALUES (1867,'قال تعالى: (لكم فيها منافع إلى أجل مسمى ثم محلها إلى البيت العتيق (٣٣) ولكل أمة جعلنا منسكا ليذكروا اسم الله على ما رزقهم من بهيمة الأنعام فإلهكم إله واحد فله أسلموا وبشر المخبتين (٣٤)) .','938','1842','0');
INSERT INTO [t22928] VALUES (1868,'قال تعالى: (الذين إذا ذكر الله وجلت قلوبهم والصابرين على ما أصابهم والمقيمي الصلاة ومما رزقناهم ينفقون (٣٥)) .','939','1842','0');
INSERT INTO [t22928] VALUES (1869,'قال تعالى: (والبدن جعلناها لكم من شعائر الله لكم فيها خير فاذكروا اسم الله عليها صواف فإذا وجبت جنوبها فكلوا منها وأطعموا القانع والمعتر كذلك سخرناها لكم لعلكم تشكرون (٣٦)) .','939','1842','0');
INSERT INTO [t22928] VALUES (1870,'قال تعالى: (لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم كذلك سخرها لكم لتكبروا الله على ما هداكم وبشر المحسنين (٣٧)) .','940','1842','0');
INSERT INTO [t22928] VALUES (1871,'قال تعالى: (إن الله يدافع عن الذين آمنوا إن الله لا يحب كل خوان كفور (٣٨)) .','940','1842','0');
INSERT INTO [t22928] VALUES (1872,'قال تعالى: (أذن للذين يقاتلون بأنهم ظلموا وإن الله على نصرهم لقدير (٣٩)) .','940','1842','0');
INSERT INTO [t22928] VALUES (1873,'قال تعالى: (الذين أخرجوا من ديارهم بغير حق إلا أن يقولوا ربنا الله ولولا دفع الله الناس بعضهم ببعض لهدمت صوامع وبيع وصلوات ومساجد يذكر فيها اسم الله كثيرا ولينصرن الله من ينصره إن الله لقوي عزيز (٤٠)) .','941','1842','0');
INSERT INTO [t22928] VALUES (1874,'قال تعالى: (الذين إن مكناهم في الأرض أقاموا الصلاة وآتوا الزكاة وأمروا بالمعروف ونهوا عن المنكر ولله عاقبة الأمور (٤١) وإن يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود (٤٢) وقوم إبراهيم وقوم لوط (٤٣) وأصحاب مدين وكذب موسى فأمليت للكافرين ثم أخذتهم فكيف كان نكير (٤٤))','941','1842','0');
INSERT INTO [t22928] VALUES (1875,'قال تعالى: (فكأين من قرية أهلكناها وهي ظالمة فهي خاوية على عروشها وبئر معطلة وقصر مشيد (٤٥)) .','941','1842','0');
INSERT INTO [t22928] VALUES (1876,'قال تعالى: (أفلم يسيروا في الأرض فتكون لهم قلوب يعقلون بها أو آذان يسمعون بها فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور (٤٦)) .','942','1842','0');
INSERT INTO [t22928] VALUES (1877,'قال تعالى: (والذين سعوا في آياتنا معاجزين أولئك أصحاب الجحيم (٥١)) .','942','1842','0');
INSERT INTO [t22928] VALUES (1878,'قال تعالى: (وما أرسلنا من قبلك من رسول ولا نبي إلا إذا تمنى ألقى الشيطان في أمنيته فينسخ الله ما يلقي الشيطان ثم يحكم الله آياته والله عليم حكيم (٥٢) ليجعل ما يلقي الشيطان فتنة للذين في قلوبهم مرض والقاسية قلوبهم وإن الظالمين لفي شقاق بعيد (٥٣)) .','942','1842','0');
INSERT INTO [t22928] VALUES (1879,'قال تعالى: (وليعلم الذين أوتوا العلم أنه الحق من ربك فيؤمنوا به فتخبت له قلوبهم وإن الله لهادي الذين آمنوا إلى صراط مستقيم (٥٤) ولا يزال الذين كفروا في مرية منه حتى تأتيهم الساعة بغتة أو يأتيهم عذاب يوم عقيم (٥٥)) .','942','1842','0');
INSERT INTO [t22928] VALUES (1880,'قال تعالى: (الملك يومئذ لله يحكم بينهم فالذين آمنوا وعملوا الصالحات في جنات النعيم (٥٦)) .','943','1842','0');
INSERT INTO [t22928] VALUES (1881,'قال تعالى: (ليدخلنهم مدخلا يرضونه وإن الله لعليم حليم (٥٩))','943','1842','0');
INSERT INTO [t22928] VALUES (1882,'قال تعالى: (ذلك ومن عاقب بمثل ما عوقب به ثم بغي عليه لينصرنه الله إن الله لعفو غفور (٦٠)) .','943','1842','0');
INSERT INTO [t22928] VALUES (1883,'قال تعالى: (ذلك بأن الله هو الحق وأن ما يدعون من دونه هو الباطل وأن الله هو العلي الكبير (٦٢)) .','944','1842','0');
INSERT INTO [t22928] VALUES (1884,'قال تعالى: (ألم تر أن الله أنزل من السماء ماء فتصبح الأرض مخضرة إن الله لطيف خبير (٦٣))','944','1842','0');
INSERT INTO [t22928] VALUES (1885,'قال تعالى: (ألم تر أن الله سخر لكم ما في الأرض والفلك تجري في البحر بأمره ويمسك السماء أن تقع على الأرض إلا بإذنه إن الله بالناس لرءوف رحيم (٦٥)) .','944','1842','0');
INSERT INTO [t22928] VALUES (1886,'قال تعالى: (لكل أمة جعلنا منسكا هم ناسكوه فلا ينازعنك في الأمر وادع إلى ربك إنك لعلى هدى مستقيم (٦٧)) .','945','1842','0');
INSERT INTO [t22928] VALUES (1887,'قال تعالى: (وإذا تتلى عليهم آياتنا بينات تعرف في وجوه الذين كفروا المنكر يكادون يسطون بالذين يتلون عليهم آياتنا قل أفأنبئكم بشر من ذلكم النار وعدها الله الذين كفروا وبئس المصير (٧٢)) .','945','1842','0');
INSERT INTO [t22928] VALUES (1888,'قال تعالى: (يا أيها الناس ضرب مثل فاستمعوا له إن الذين تدعون من دون الله لن يخلقوا ذبابا ولو اجتمعوا له وإن يسلبهم الذباب شيئا لا يستنقذوه منه ضعف الطالب والمطلوب (٧٣)) .','946','1842','0');
INSERT INTO [t22928] VALUES (1889,'قال تعالى: (الله يصطفي من الملائكة رسلا ومن الناس إن الله سميع بصير (٧٥)) .','946','1842','0');
INSERT INTO [t22928] VALUES (1890,'قال تعالى: (وجاهدوا في الله حق جهاده هو اجتباكم وما جعل عليكم في الدين من حرج ملة أبيكم إبراهيم هو سماكم المسلمين من قبل وفي هذا ليكون الرسول شهيدا عليكم وتكونوا شهداء على الناس فأقيموا الصلاة وآتوا الزكاة واعتصموا بالله هو مولاكم فنعم المولى ونعم النصير (٧٨)) .','946','1842','0');
INSERT INTO [t22928] VALUES (1891,'المؤمنون','947','0','0');
INSERT INTO [t22928] VALUES (1892,'قال تعالى: (قد أفلح المؤمنون (١)) .','947','1891','0');
INSERT INTO [t22928] VALUES (1893,'قال تعالى: (والذين هم لفروجهم حافظون (٥) إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين (٦)) .','947','1891','0');
INSERT INTO [t22928] VALUES (1894,'قال تعالى: (والذين هم لأماناتهم وعهدهم راعون (٨ والذين هم على صلواتهم يحافظون (٩)) .','947','1891','0');
INSERT INTO [t22928] VALUES (1895,'قال تعالى: (الذين يرثون الفردوس هم فيها خالدون (١١)) .','948','1891','0');
INSERT INTO [t22928] VALUES (1896,'قال تعالى: (ولقد خلقنا الإنسان من سلالة من طين (١٢)) .','948','1891','0');
INSERT INTO [t22928] VALUES (1897,'قال تعالى: (ثم جعلناه نطفة في قرار مكين ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم أنشأناه خلقا آخر فتبارك الله أحسن الخالقين (١٤)) .','948','1891','0');
INSERT INTO [t22928] VALUES (1898,'قال تعالى: (ثم إنكم بعد ذلك لميتون (١٥)) .','948','1891','0');
INSERT INTO [t22928] VALUES (1899,'قال تعالى: (وأنزلنا من السماء ماء بقدر فأسكناه في الأرض وإنا على ذهاب به لقادرون (١٨)) .','948','1891','0');
INSERT INTO [t22928] VALUES (1900,'قال تعالى: (وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للآكلين (٢٠)) .','949','1891','0');
INSERT INTO [t22928] VALUES (1901,'قال تعالى: (وإن لكم في الأنعام لعبرة نسقيكم مما في بطونها ولكم فيها منافع كثيرة ومنها تأكلون (٢١)) .','950','1891','0');
INSERT INTO [t22928] VALUES (1902,'قال تعالى: (فأوحينا إليه أن اصنع الفلك بأعيننا ووحينا فإذا جاء أمرنا وفار التنور فاسلك فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول منهم ولا تخاطبني في الذين ظلموا إنهم مغرقون (٢٧)) .','950','1891','0');
INSERT INTO [t22928] VALUES (1903,'قال تعالى: (وقل رب أنزلني منزلا مباركا وأنت خير المنزلين (٢٩) إن في ذلك لآيات وإن كنا لمبتلين (٣٠)) .','950','1891','0');
INSERT INTO [t22928] VALUES (1904,'قال تعالى: (أيعدكم أنكم إذا متم وكنتم ترابا وعظاما أنكم مخرجون (٣٥)) .','950','1891','0');
INSERT INTO [t22928] VALUES (1905,'قال تعالى: (هيهات هيهات لما توعدون (٣٦)) .','951','1891','0');
INSERT INTO [t22928] VALUES (1906,'قال تعالى: (قال عما قليل ليصبحن نادمين (٤٠)) .','952','1891','0');
INSERT INTO [t22928] VALUES (1907,'قال تعالى: (ثم أرسلنا رسلنا تترى كلما جاء أمة رسولها كذبوه فأتبعنا بعضهم بعضا وجعلناهم أحاديث فبعدا لقوم لا يؤمنون (٤٤)) .','952','1891','0');
INSERT INTO [t22928] VALUES (1908,'قال تعالى: (ثم أرسلنا موسى وأخاه هارون بآياتنا وسلطان مبين (٤٥)) .','952','1891','0');
INSERT INTO [t22928] VALUES (1909,'قال تعالى: (فقالوا أنؤمن لبشرين مثلنا وقومهما لنا عابدون (٤٧)) .','953','1891','0');
INSERT INTO [t22928] VALUES (1910,'قال تعالى: (وجعلنا ابن مريم وأمه آية وآويناهما إلى ربوة ذات قرار ومعين (٥٠)) .','953','1891','0');
INSERT INTO [t22928] VALUES (1911,'قال تعالى: (وإن هذه أمتكم أمة واحدة وأنا ربكم فاتقون (٥٢) فتقطعوا أمرهم بينهم زبرا كل حزب بما لديهم فرحون (٥٣))','953','1891','0');
INSERT INTO [t22928] VALUES (1912,'قال تعالى: (أيحسبون أنما نمدهم به من مال وبنين (٥٥) نسارع لهم في الخيرات بل لا يشعرون (٥٦))','954','1891','0');
INSERT INTO [t22928] VALUES (1913,'قال تعالى: (والذين يؤتون ما آتوا وقلوبهم وجلة أنهم إلى ربهم راجعون (٦٠)) .','954','1891','0');
INSERT INTO [t22928] VALUES (1914,'قال تعالى: (أولئك يسارعون في الخيرات وهم لها سابقون (٦١)) .','955','1891','0');
INSERT INTO [t22928] VALUES (1915,'قال تعالى: (حتى إذا أخذنا مترفيهم بالعذاب إذا هم يجأرون (٦٤)) .','955','1891','0');
INSERT INTO [t22928] VALUES (1916,'قال تعالى: (قد كانت آياتي تتلى عليكم فكنتم على أعقابكم تنكصون (٦٦) مستكبرين به سامرا تهجرون (٦٧)) .','955','1891','0');
INSERT INTO [t22928] VALUES (1917,'قال تعالى: (وإن الذين لا يؤمنون بالآخرة عن الصراط لناكبون (٧٤)) .','956','1891','0');
INSERT INTO [t22928] VALUES (1918,'قال تعالى: (ولقد أخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون (٧٦)) .','956','1891','0');
INSERT INTO [t22928] VALUES (1919,'قال تعالى: (وهو الذي أنشأ لكم السمع والأبصار والأفئدة قليلا ما تشكرون (٧٨)) .','956','1891','0');
INSERT INTO [t22928] VALUES (1920,'قال تعالى: (قل لمن الأرض ومن فيها إن كنتم تعلمون (٨٤) سيقولون لله قل أفلا تذكرون (٨٥)) .','956','1891','0');
INSERT INTO [t22928] VALUES (1921,'قال تعالى: (عالم الغيب والشهادة فتعالى عما يشركون (٩٢)) .','957','1891','0');
INSERT INTO [t22928] VALUES (1922,'قال تعالى: (رب فلا تجعلني في القوم الظالمين (٩٤) وإنا على أن نريك ما نعدهم لقادرون (٩٥)) .','957','1891','0');
INSERT INTO [t22928] VALUES (1923,'قال تعالى: (حتى إذا جاء أحدهم الموت قال رب ارجعون (٩٩)) .','957','1891','0');
INSERT INTO [t22928] VALUES (1924,'قال تعالى: (فإذا نفخ في الصور فلا أنساب بينهم يومئذ ولا يتساءلون (١٠١)) .','957','1891','0');
INSERT INTO [t22928] VALUES (1925,'قال تعالى: (قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين (١٠٦)) .','958','1891','0');
INSERT INTO [t22928] VALUES (1926,'قال تعالى: (فاتخذتموهم سخريا حتى أنسوكم ذكري وكنتم منهم تضحكون (١١٠)) .','958','1891','0');
INSERT INTO [t22928] VALUES (1927,'قال تعالى: (إني جزيتهم اليوم بما صبروا أنهم هم الفائزون (١١١)) .','958','1891','0');
INSERT INTO [t22928] VALUES (1928,'قال تعالى: (قال كم لبثتم في الأرض عدد سنين (١١٢) قالوا لبثنا يوما أو بعض يوم فاسأل العادين (١١٣) قال إن لبثتم إلا قليلا لو أنكم كنتم تعلمون (١١٤) أفحسبتم أنما خلقناكم عبثا وأنكم إلينا لا ترجعون (١١٥))','958','1891','0');
INSERT INTO [t22928] VALUES (1929,'قال تعالى: (فتعالى الله الملك الحق لا إله إلا هو رب العرش الكريم (١١٦)) .','959','1891','0');
INSERT INTO [t22928] VALUES (1930,'قال تعالى: (ومن يدع مع الله إلها آخر لا برهان له به فإنما حسابه عند ربه إنه لا يفلح الكافرون (١١٧)) .','959','1891','0');
INSERT INTO [t22928] VALUES (1931,'النور','960','0','0');
INSERT INTO [t22928] VALUES (1932,'قال تعالى: (سورة أنزلناها وفرضناها وأنزلنا فيها آيات بينات لعلكم تذكرون (١)) .','960','1931','0');
INSERT INTO [t22928] VALUES (1933,'قال تعالى: (الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ولا تأخذكم بهما رأفة في دين الله (١)) .','960','1931','0');
INSERT INTO [t22928] VALUES (1934,'قال تعالى: (والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدا وأولئك هم الفاسقون (٤)) .','961','1931','0');
INSERT INTO [t22928] VALUES (1935,'قال تعالى: (إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم (٥)) .','961','1931','0');
INSERT INTO [t22928] VALUES (1936,'قال تعالى: (والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله إنه لمن الصادقين (٦)) .','962','1931','0');
INSERT INTO [t22928] VALUES (1937,'قال تعالى: (والخامسة أن لعنة الله عليه إن كان من الكاذبين (٧)) .','962','1931','0');
INSERT INTO [t22928] VALUES (1938,'قال تعالى: (ويدرأ عنها العذاب أن تشهد أربع شهادات بالله إنه لمن الكاذبين (٨)) .','963','1931','0');
INSERT INTO [t22928] VALUES (1939,'قال تعالى: (والخامسة أن غضب الله عليها إن كان من الصادقين (٩)) .','963','1931','0');
INSERT INTO [t22928] VALUES (1940,'قال تعالى: (ولولا فضل الله عليكم ورحمته وأن الله تواب حكيم (١٠)) .','963','1931','0');
INSERT INTO [t22928] VALUES (1941,'قال تعالى: (إن الذين جاءوا بالإفك عصبة منكم لا تحسبوه شرا لكم بل هو خير لكم لكل امرئ منهم ما اكتسب من الإثم والذي تولى كبره منهم له عذاب عظيم (١١)) .','963','1931','0');
INSERT INTO [t22928] VALUES (1942,'قال تعالى: (إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم (١٥)) .','964','1931','0');
INSERT INTO [t22928] VALUES (1943,'قال تعالى: (يعظكم الله أن تعودوا لمثله أبدا إن كنتم مؤمنين (١٧)) .','964','1931','0');
INSERT INTO [t22928] VALUES (1944,'قال تعالى: (يا أيها الذين آمنوا لا تتبعوا خطوات الشيطان ومن يتبع خطوات الشيطان فإنه يأمر بالفحشاء والمنكر ولولا فضل الله عليكم ورحمته ما زكا منكم من أحد أبدا ولكن الله يزكي من يشاء والله سميع عليم (٢١)) .','964','1931','0');
INSERT INTO [t22928] VALUES (1945,'قال تعالى: (ولا يأتل أولو الفضل منكم والسعة أن يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله وليعفوا وليصفحوا ألا تحبون أن يغفر الله لكم والله غفور رحيم (٢٢)) .','964','1931','0');
INSERT INTO [t22928] VALUES (1946,'قال تعالى: (يوم تشهد عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون (٢٤)) .','965','1931','0');
INSERT INTO [t22928] VALUES (1947,'قال تعالى: (يومئذ يوفيهم الله دينهم الحق ويعلمون أن الله هو الحق المبين (٢٥)) .','965','1931','0');
INSERT INTO [t22928] VALUES (1948,'قال تعالى: (الخبيثات للخبيثين والخبيثون للخبيثات والطيبات للطيبين والطيبون للطيبات أولئك مبرءون مما يقولون لهم مغفرة ورزق كريم (٢٦)) .','965','1931','0');
INSERT INTO [t22928] VALUES (1949,'قال تعالى: (ليس عليكم جناح أن تدخلوا بيوتا غير مسكونة فيها متاع لكم والله يعلم ما تبدون وما تكتمون (٢٩)) .','965','1931','0');
INSERT INTO [t22928] VALUES (1950,'قال تعالى: (قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم ذلك أزكى لهم إن الله خبير بما يصنعون (٣٠)) .','965','1931','0');
INSERT INTO [t22928] VALUES (1951,'قال تعالى: (وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن ولا يبدين زينتهن إلا ما ظهر منها وليضربن بخمرهن على جيوبهن ولا يبدين زينتهن إلا لبعولتهن أو آبائهن أو آباء بعولتهن أو أبنائهن أو أبناء بعولتهن أو إخوانهن أو بني إخوانهن أو بني أخواتهن أو نسائهن أو ما ملكت أيمانهن أو التابعين غير أولي الإربة من الرجال أو الطفل الذين لم يظهروا على عورات النساء ولا يضربن بأرجلهن ليعلم ما يخفين من زينتهن وتوبوا إلى الله جميعا أيها المؤمنون لعلكم تفلحون (٣١)) .','966','1931','0');
INSERT INTO [t22928] VALUES (1952,'قال تعالى: (وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا وآتوهم من مال الله الذي آتاكم ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا لتبتغوا عرض الحياة الدنيا ومن يكرهن فإن الله من بعد إكراههن غفور رحيم (٣٣)) .','966','1931','0');
INSERT INTO [t22928] VALUES (1953,'قال تعالى: (الله نور السماوات والأرض مثل نوره كمشكاة فيها مصباح المصباح في زجاجة الزجاجة كأنها كوكب دري يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية يكاد زيتها يضيء ولو لم تمسسه نار نور على نور يهدي الله لنوره من يشاء ويضرب الله الأمثال للناس والله بكل شيء عليم (٣٥)) .','966','1931','0');
INSERT INTO [t22928] VALUES (1954,'قال تعالى: (في بيوت أذن الله أن ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والآصال رجال لا تلهيهم تجارة ولا بيع عن ذكر الله وإقام الصلاة وإيتاء الزكاة يخافون يوما تتقلب فيه القلوب والأبصار (٣٧)) .','967','1931','0');
INSERT INTO [t22928] VALUES (1955,'قال تعالى: (ليجزيهم الله أحسن ما عملوا ويزيدهم من فضله والله يرزق من يشاء بغير حساب) .','968','1931','0');
INSERT INTO [t22928] VALUES (1956,'قال تعالى: (والذين كفروا أعمالهم كسراب بقيعة يحسبه الظمآن ماء حتى إذا جاءه لم يجده شيئا ووجد الله عنده فوفاه حسابه والله سريع الحساب (٣٩)) .','968','1931','0');
INSERT INTO [t22928] VALUES (1957,'قال تعالى: (أو كظلمات في بحر لجي يغشاه موج من فوقه موج من فوقه سحاب ظلمات بعضها فوق بعض إذا أخرج يده لم يكد يراها ومن لم يجعل الله له نورا فما له من نور (٤٠)) .','969','1931','0');
INSERT INTO [t22928] VALUES (1958,'قال تعالى: (ألم تر أن الله يسبح له من في السماوات والأرض والطير صافات كل قد علم صلاته وتسبيحه والله عليم بما يفعلون (٤١)) .','971','1931','0');
INSERT INTO [t22928] VALUES (1959,'قال تعالى: (ألم تر أن الله يزجي سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلاله وينزل من السماء من جبال فيها من برد فيصيب به من يشاء ويصرفه عن من يشاء يكاد سنا برقه يذهب بالأبصار (٤٣)) .','971','1931','0');
INSERT INTO [t22928] VALUES (1960,'قال تعالى: (والله خلق كل دابة من ماء فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على أربع يخلق الله ما يشاء إن الله على كل شيء قدير (٤٥)) .','972','1931','0');
INSERT INTO [t22928] VALUES (1961,'قال تعالى: (وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون (٤٨)) .','972','1931','0');
INSERT INTO [t22928] VALUES (1962,'قال تعالى: (إنما كان قول المؤمنين إذا دعوا إلى الله ورسوله ليحكم بينهم أن يقولوا سمعنا وأطعنا وأولئك هم المفلحون (٥١)) .','972','1931','0');
INSERT INTO [t22928] VALUES (1963,'قال تعالى: (ومن يطع الله ورسوله ويخش الله ويتقه فأولئك هم الفائزون (٥٢)) .','973','1931','0');
INSERT INTO [t22928] VALUES (1964,'قال تعالى: (وأقسموا بالله جهد أيمانهم لئن أمرتهم ليخرجن قل لا تقسموا طاعة معروفة إن الله خبير بما تعملون (٥٣) قل أطيعوا الله وأطيعوا الرسول فإن تولوا فإنما عليه ما حمل وعليكم ما حملتم وإن تطيعوه تهتدوا وما على الرسول إلا البلاغ المبين (٥٤)) .','973','1931','0');
INSERT INTO [t22928] VALUES (1965,'قال تعالى: (وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم أمنا يعبدونني لا يشركون بي شيئا ومن كفر بعد ذلك فأولئك هم الفاسقون (٥٥)) .','973','1931','0');
INSERT INTO [t22928] VALUES (1966,'قال تعالى: (لا تحسبن الذين كفروا معجزين في الأرض ومأواهم النار ولبئس المصير (٥٧)) .','973','1931','0');
INSERT INTO [t22928] VALUES (1967,'قال تعالى: (يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ثلاث عورات لكم ليس عليكم ولا عليهم جناح بعدهن طوافون عليكم بعضكم على بعض كذلك يبين الله لكم الآيات والله عليم حكيم وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم كذلك يبين الله لكم آياته والله عليم حكيم (٥٨)) .','974','1931','0');
INSERT INTO [t22928] VALUES (1968,'قال تعالى: (والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة وأن يستعففن خير لهن والله سميع عليم (٦٠)) .','975','1931','0');
INSERT INTO [t22928] VALUES (1969,'قال تعالى: (ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ولا على أنفسكم أن تأكلوا من بيوتكم أو بيوت آبائكم أو بيوت أمهاتكم أو بيوت إخوانكم أو بيوت أخواتكم أو بيوت أعمامكم أو بيوت عماتكم أو بيوت أخوالكم أو بيوت خالاتكم أو ما ملكتم مفاتحه أو صديقكم ليس عليكم جناح أن تأكلوا جميعا أو أشتاتا فإذا دخلتم بيوتا فسلموا على أنفسكم تحية من عند الله مباركة طيبة كذلك يبين الله لكم الآيات لعلكم تعقلون (٦١)) .','975','1931','0');
INSERT INTO [t22928] VALUES (1970,'قال تعالى: (لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا قد يعلم الله الذين يتسللون منكم لواذا فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم (٦٣)) .','975','1931','0');
INSERT INTO [t22928] VALUES (1971,'الفرقان','977','0','0');
INSERT INTO [t22928] VALUES (1972,'قال تعالى: (تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا (١)) .','977','1971','0');
INSERT INTO [t22928] VALUES (1973,'قال تعالى: (الذي له ملك السماوات والأرض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا (٢)) .','977','1971','0');
INSERT INTO [t22928] VALUES (1974,'قال تعالى: (وقال الذين كفروا إن هذا إلا إفك افتراه وأعانه عليه قوم آخرون فقد جاءوا ظلما وزورا (٤) وقالوا أساطير الأولين اكتتبها فهي تملى عليه بكرة وأصيلا (٥)) .','977','1971','0');
INSERT INTO [t22928] VALUES (1975,'قال تعالى: (وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق لولا أنزل إليه ملك فيكون معه نذيرا (٧) أو يلقى إليه كنز أو تكون له جنة يأكل منها وقال الظالمون إن تتبعون إلا رجلا مسحورا (٨)) .','977','1971','0');
INSERT INTO [t22928] VALUES (1976,'قال تعالى: (تبارك الذي إن شاء جعل لك خيرا من ذلك جنات تجري من تحتها الأنهار ويجعل لك قصورا (١٠)) .','978','1971','0');
INSERT INTO [t22928] VALUES (1977,'قال تعالى: (إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا (١٢) وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا (١٣)) .','978','1971','0');
INSERT INTO [t22928] VALUES (1978,'قال تعالى: (لهم فيها ما يشاءون خالدين كان على ربك وعدا مسئولا (١٦)) .','978','1971','0');
INSERT INTO [t22928] VALUES (1979,'قال تعالى: (ويوم يحشرهم وما يعبدون من دون الله فيقول أأنتم أضللتم عبادي هؤلاء أم هم ضلوا السبيل (١٧)) .','979','1971','0');
INSERT INTO [t22928] VALUES (1980,'قال تعالى: (قالوا سبحانك ما كان ينبغي لنا أن نتخذ من دونك من أولياء ولكن متعتهم وآباءهم حتى نسوا الذكر وكانوا قوما بورا (١٨)) .','979','1971','0');
INSERT INTO [t22928] VALUES (1981,'قال تعالى: (وما أرسلنا قبلك من المرسلين إلا إنهم ليأكلون الطعام ويمشون في الأسواق وجعلنا بعضكم لبعض فتنة أتصبرون وكان ربك بصيرا (٢٠)) .','980','1971','0');
INSERT INTO [t22928] VALUES (1982,'قال تعالى: (يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا (٢٢)) .','980','1971','0');
INSERT INTO [t22928] VALUES (1983,'قال تعالى: (ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا (٢٥)) .','981','1971','0');
INSERT INTO [t22928] VALUES (1984,'قال تعالى: (الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا (٢٦)) .','981','1971','0');
INSERT INTO [t22928] VALUES (1985,'قال تعالى {ويوم يعض الظالم على يديه يقول يا ليتني اتخذت مع الرسول سبيلا}','982','1971','0');
INSERT INTO [t22928] VALUES (1986,'قال تعالى: (وقال الرسول يا رب إن قومي اتخذوا هذا القرآن مهجورا (٣٠)) .','982','1971','0');
INSERT INTO [t22928] VALUES (1987,'قال تعالى: (وقال الذين كفروا لولا نزل عليه القرآن جملة واحدة كذلك لنثبت به فؤادك ورتلناه ترتيلا (٣٢)) .','982','1971','0');
INSERT INTO [t22928] VALUES (1988,'قال تعالى: (ولا يأتونك بمثل إلا جئناك بالحق وأحسن تفسيرا (٣٣)) .','982','1971','0');
INSERT INTO [t22928] VALUES (1989,'قال تعالى: (الذين يحشرون على وجوههم إلى جهنم أولئك شر مكانا وأضل سبيلا (٣٤)) .','982','1971','0');
INSERT INTO [t22928] VALUES (1990,'قال تعالى: (ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا (٣٥)) .','983','1971','0');
INSERT INTO [t22928] VALUES (1991,'قال تعالى: (فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا (٣٦) وقوم نوح لما كذبوا الرسل أغرقناهم وجعلناهم للناس آية وأعتدنا للظالمين عذابا أليما (٣٧) وعادا وثمود وأصحاب الرس وقرونا بين ذلك كثيرا (٣٨) وكلا ضربنا له الأمثال وكلا تبرنا تتبيرا (٣٩)) .','983','1971','0');
INSERT INTO [t22928] VALUES (1992,'قال تعالى: (ولقد أتوا على القرية التي أمطرت مطر السوء أفلم يكونوا يرونها بل كانوا لا يرجون نشورا (٤٠)) .','983','1971','0');
INSERT INTO [t22928] VALUES (1993,'قال تعالى: (وإذا رأوك إن يتخذونك إلا هزوا أهذا الذي بعث الله رسولا (٤١)) .','984','1971','0');
INSERT INTO [t22928] VALUES (1994,'قال تعالى: (إن كاد ليضلنا عن آلهتنا لولا أن صبرنا عليها وسوف يعلمون حين يرون العذاب من أضل سبيلا (٤٢)) .','984','1971','0');
INSERT INTO [t22928] VALUES (1995,'قال تعالى: (وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا (٤٧)) .','984','1971','0');
INSERT INTO [t22928] VALUES (1996,'قال تعالى: (لنحيي به بلدة ميتا ونسقيه مما خلقنا أنعاما وأناسي كثيرا (٤٩) ولقد صرفناه بينهم ليذكروا فأبى أكثر الناس إلا كفورا (٥٠) ولو شئنا لبعثنا في كل قرية نذيرا (٥١) فلا تطع الكافرين وجاهدهم به جهادا كبيرا (٥٢))','984','1971','0');
INSERT INTO [t22928] VALUES (1997,'قال تعالى: (وهو الذي مرج البحرين هذا عذب فرات وهذا ملح أجاج وجعل بينهما برزخا وحجرا محجورا (٥٣)) .','985','1971','0');
INSERT INTO [t22928] VALUES (1998,'قال تعالى: (ويعبدون من دون الله ما لا ينفعهم ولا يضرهم وكان الكافر على ربه ظهيرا (٥٥)) .','985','1971','0');
INSERT INTO [t22928] VALUES (1999,'قال تعالى: (قل ما أسألكم عليه من أجر إلا من شاء أن يتخذ إلى ربه سبيلا (٥٧)) .','985','1971','0');
INSERT INTO [t22928] VALUES (2000,'قال تعالى: (وتوكل على الحي الذي لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا (٥٨)) .','985','1971','0');
INSERT INTO [t22928] VALUES (2001,'قال تعالى: (الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش الرحمن فاسأل به خبيرا (٥٩)) .','986','1971','0');
INSERT INTO [t22928] VALUES (2002,'قال تعالى: (وإذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا وزادهم نفورا (٦٠)) .','986','1971','0');
INSERT INTO [t22928] VALUES (2003,'قال تعالى: (تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا (٦١) وهو الذي جعل الليل والنهار خلفة لمن أراد أن يذكر أو أراد شكورا (٦٢)) .','987','1971','0');
INSERT INTO [t22928] VALUES (2004,'قال تعالى: (وعباد الرحمن الذين يمشون على الأرض هونا وإذا خاطبهم الجاهلون قالوا سلاما (٦٣)) .','987','1971','0');
INSERT INTO [t22928] VALUES (2005,'قال تعالى: (إنها ساءت مستقرا ومقاما (٦٦) والذين إذا أنفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما (٦٧) والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله إلا بالحق ولا يزنون ومن يفعل ذلك يلق أثاما (٦٨)) .','988','1971','0');
INSERT INTO [t22928] VALUES (2006,'قال تعالى: (يضاعف له العذاب يوم القيامة ويخلد فيه مهانا (٦٩) إلا من تاب وآمن وعمل عملا صالحا فأولئك يبدل الله سيئاتهم حسنات وكان الله غفورا رحيما (٧٠)) .','988','1971','0');
INSERT INTO [t22928] VALUES (2007,'قال تعالى: (والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا قرة أعين واجعلنا للمتقين إماما (٧٤)) .','988','1971','0');
INSERT INTO [t22928] VALUES (2008,'قال تعالى: (أولئك يجزون الغرفة بما صبروا ويلقون فيها تحية وسلاما خالدين فيها حسنت مستقرا ومقاما () ٧٦)) .','989','1971','0');
INSERT INTO [t22928] VALUES (2009,'قال تعالى: (قل ما يعبأ بكم ربي لولا دعاؤكم فقد كذبتم فسوف يكون لزاما (٧٧)) .','989','1971','0');
INSERT INTO [t22928] VALUES (2010,'الشعراء','990','0','0');
INSERT INTO [t22928] VALUES (2011,'قال تعالى: (طسم (١) تلك آيات الكتاب المبين (٢) لعلك باخع نفسك ألا يكونوا مؤمنين (٣)) (طسم) : مثل الم، وقد ذكر في أول البقرة. (تلك آيات الكتاب) : مثل: ذلك الكتاب. و (أن لا يكونوا) : مفعول له؛ أي لئلا؛ أو مخافة أن لا.','990','2010','0');
INSERT INTO [t22928] VALUES (2012,'قال تعالى: (إن نشأ ننزل عليهم من السماء آية فظلت أعناقهم لها خاضعين (٤)) .','990','2010','0');
INSERT INTO [t22928] VALUES (2013,'قال تعالى: (أولم يروا إلى الأرض كم أنبتنا فيها من كل زوج كريم (٧)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2014,'قال تعالى: (وإذ نادى ربك موسى أن ائت القوم الظالمين (١٠)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2015,'قال تعالى: (قوم فرعون ألا يتقون (١١)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2016,'قال تعالى: (ويضيق صدري ولا ينطلق لساني فأرسل إلى هارون (١٣)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2017,'قال تعالى: (فأتيا فرعون فقولا إنا رسول رب العالمين (١٦)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2018,'قال تعالى: (قال ألم نربك فينا وليدا ولبثت فينا من عمرك سنين (١٨) وفعلت فعلتك التي فعلت وأنت من الكافرين (١٩)) .','991','2010','0');
INSERT INTO [t22928] VALUES (2019,'قال تعالى: (وتلك نعمة تمنها علي أن عبدت بني إسرائيل (٢٢)) .','992','2010','0');
INSERT INTO [t22928] VALUES (2020,'قال تعالى: (قال فرعون وما رب العالمين (٢٣) قال رب السماوات والأرض وما بينهما إن كنتم موقنين (٢٤)) .','992','2010','0');
INSERT INTO [t22928] VALUES (2021,'قال تعالى: (قال للملأ حوله إن هذا لساحر عليم (٣٤)) .','992','2010','0');
INSERT INTO [t22928] VALUES (2022,'قال تعالى: (فألقوا حبالهم وعصيهم وقالوا بعزة فرعون إنا لنحن الغالبون (٤٤)) .','992','2010','0');
INSERT INTO [t22928] VALUES (2023,'قال تعالى: (إنا نطمع أن يغفر لنا ربنا خطايانا أن كنا أول المؤمنين (٥١)) .','992','2010','0');
INSERT INTO [t22928] VALUES (2024,'قال تعالى: (إن هؤلاء لشرذمة قليلون (٥٤) وإنهم لنا لغائظون (٥٥) وإنا لجميع حاذرون (٥٦))','993','2010','0');
INSERT INTO [t22928] VALUES (2025,'قال تعالى: (كذلك وأورثناها بني إسرائيل (٥٩)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2026,'قال تعالى: (فأتبعوهم مشرقين (٦٠)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2027,'قال تعالى: (فلما تراءى الجمعان قال أصحاب موسى إنا لمدركون (٦١)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2028,'قال تعالى: (وأزلفنا ثم الآخرين (٦٤)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2029,'قال تعالى: (إذ قال لأبيه وقومه ما تعبدون (٧٠)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2030,'قال تعالى: (قال هل يسمعونكم إذ تدعون (٧٢)) .','993','2010','0');
INSERT INTO [t22928] VALUES (2031,'قال تعالى: (قالوا بل وجدنا آباءنا كذلك يفعلون (٧٤)) .','994','2010','0');
INSERT INTO [t22928] VALUES (2032,'قال تعالى: (فإنهم عدو لي إلا رب العالمين (٧٧)) .','994','2010','0');
INSERT INTO [t22928] VALUES (2033,'قال تعالى: (الذي خلقني فهو يهدين (٧٨)) .','994','2010','0');
INSERT INTO [t22928] VALUES (2034,'قال تعالى: (واجعلني من ورثة جنة النعيم (٨٥)) .','994','2010','0');
INSERT INTO [t22928] VALUES (2035,'قال تعالى: (ولا تخزني يوم يبعثون (٨٧) يوم لا ينفع مال ولا بنون (٨٨) إلا من أتى الله بقلب سليم (٨٩)) .','994','2010','0');
INSERT INTO [t22928] VALUES (2036,'قال تعالى: (إذ نسويكم برب العالمين (٩٨)) .','995','2010','0');
INSERT INTO [t22928] VALUES (2037,'قال تعالى: (فلو أن لنا كرة فنكون من المؤمنين (١٠٢)) .','995','2010','0');
INSERT INTO [t22928] VALUES (2038,'قال تعالى: (قالوا أنؤمن لك واتبعك الأرذلون (١١١)) .','995','2010','0');
INSERT INTO [t22928] VALUES (2039,'قال تعالى: (فافتح بيني وبينهم فتحا ونجني ومن معي من المؤمنين (١١٨)) .','996','2010','0');
INSERT INTO [t22928] VALUES (2040,'قال تعالى: (أتبنون بكل ريع آية تعبثون (١٢٨) وتتخذون مصانع لعلكم تخلدون (١٢٩)) .','996','2010','0');
INSERT INTO [t22928] VALUES (2041,'قال تعالى: (واتقوا الذي أمدكم بما تعلمون (١٣٢) أمدكم بأنعام وبنين (١٣٣)) .','996','2010','0');
INSERT INTO [t22928] VALUES (2042,'قال تعالى: (قالوا سواء علينا أوعظت أم لم تكن من الواعظين (١٣٦) إن هذا إلا خلق الأولين (١٣٧))','996','2010','0');
INSERT INTO [t22928] VALUES (2043,'قال تعالى: (أتتركون في ما هاهنا آمنين (١٤٦) في جنات وعيون (١٤٧)) .','996','2010','0');
INSERT INTO [t22928] VALUES (2044,'قال تعالى: (وتنحتون من الجبال بيوتا فارهين (١٤٩)) .','997','2010','0');
INSERT INTO [t22928] VALUES (2045,'قال تعالى: (قال إني لعملكم من القالين (١٦٨)) .','997','2010','0');
INSERT INTO [t22928] VALUES (2046,'قال تعالى: (كذب أصحاب الأيكة المرسلين (١٧٦)) .','997','2010','0');
INSERT INTO [t22928] VALUES (2047,'قال تعالى: (واتقوا الذي خلقكم والجبلة الأولين (١٨٤)) .','997','2010','0');
INSERT INTO [t22928] VALUES (2048,'قال تعالى: (وإنه لتنزيل رب العالمين (١٩٢) نزل به الروح الأمين (١٩٣)) .','997','2010','0');
INSERT INTO [t22928] VALUES (2049,'قال تعالى: (بلسان عربي مبين (١٩٥)) .','998','2010','0');
INSERT INTO [t22928] VALUES (2050,'قال تعالى: (أولم يكن لهم آية أن يعلمه علماء بني إسرائيل (١٩٧)) .','998','2010','0');
INSERT INTO [t22928] VALUES (2051,'قال تعالى: (ولو نزلناه على بعض الأعجمين (١٩٨)) .','998','2010','0');
INSERT INTO [t22928] VALUES (2052,'قال تعالى: (كذلك سلكناه في قلوب المجرمين (٢٠٠)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2053,'قال تعالى: (فيأتيهم بغتة وهم لا يشعرون (٢٠٢) فيقولوا هل نحن منظرون (٢٠٣)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2054,'قال تعالى: (ما أغنى عنهم ما كانوا يمتعون (٢٠٧)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2055,'قال تعالى: (ذكرى وما كنا ظالمين (٢٠٩)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2056,'قال تعالى: (يلقون السمع وأكثرهم كاذبون (٢٢٣)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2057,'قال تعالى: (ألم تر أنهم في كل واد يهيمون (٢٢٥)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2058,'قال تعالى: (إلا الذين آمنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا وسيعلم الذين ظلموا أي منقلب ينقلبون (٢٢٧)) .','999','2010','0');
INSERT INTO [t22928] VALUES (2059,'النمل','1000','0','0');
INSERT INTO [t22928] VALUES (2060,'قال تعالى: (طس تلك آيات القرآن وكتاب مبين (١)) .','1000','2059','0');
INSERT INTO [t22928] VALUES (2061,'قال تعالى: (هدى وبشرى للمؤمنين (٢)) .','1000','2059','0');
INSERT INTO [t22928] VALUES (2062,'قال تعالى: (إذ قال موسى لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون (٧)) .','1000','2059','0');
INSERT INTO [t22928] VALUES (2063,'قال تعالى: (فلما جاءها نودي أن بورك من في النار ومن حولها وسبحان الله رب العالمين (٨)) .','1001','2059','0');
INSERT INTO [t22928] VALUES (2064,'قال تعالى: (يا موسى إنه أنا الله العزيز الحكيم (٩)) .','1002','2059','0');
INSERT INTO [t22928] VALUES (2065,'قال تعالى: (وألق عصاك فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب يا موسى لا تخف إني لا يخاف لدي المرسلون (١٠)) .','1002','2059','0');
INSERT INTO [t22928] VALUES (2066,'قال تعالى: (إلا من ظلم ثم بدل حسنا بعد سوء فإني غفور رحيم (١١)) .','1002','2059','0');
INSERT INTO [t22928] VALUES (2067,'قال تعالى: (وأدخل يدك في جيبك تخرج بيضاء من غير سوء في تسع آيات إلى فرعون وقومه إنهم كانوا قوما فاسقين (١٢) فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين (١٣) وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا فانظر كيف كان عاقبة المفسدين (١٤) ولقد آتينا داود وسليمان علما وقالا الحمد لله الذي فضلنا على كثير من عباده المؤمنين (١٥) وورث سليمان داود وقال يا أيها الناس علمنا منطق الطير وأوتينا من كل شيء إن هذا لهو الفضل المبين (١٦) وحشر لسليمان جنوده من الجن والإنس والطير فهم يوزعون (١٧) حتى إذا أتوا على وادي النمل قالت نملة يا أيها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون (١٨) فتبسم ضاحكا من قولها وقال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأدخلني برحمتك في عبادك الصالحين (١٩))','1002','2059','0');
INSERT INTO [t22928] VALUES (2068,'قال تعالى: (لأعذبنه عذابا شديدا أو لأذبحنه أو ليأتيني بسلطان مبين (٢١) فمكث غير بعيد فقال أحطت بما لم تحط به وجئتك من سبإ بنبإ يقين (٢٢) إني وجدت امرأة تملكهم وأوتيت من كل شيء ولها عرش عظيم (٢٣))','1003','2059','0');
INSERT INTO [t22928] VALUES (2069,'قال تعالى: (ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون (٢٥)) .','1004','2059','0');
INSERT INTO [t22928] VALUES (2070,'قال تعالى: (اذهب بكتابي هذا فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون (٢٨)) .','1004','2059','0');
INSERT INTO [t22928] VALUES (2071,'قال تعالى: (إنه من سليمان وإنه بسم الله الرحمن الرحيم (٣٠)) .','1005','2059','0');
INSERT INTO [t22928] VALUES (2072,'قال تعالى: (ألا تعلوا علي وأتوني مسلمين (٣١)) .','1005','2059','0');
INSERT INTO [t22928] VALUES (2073,'قال تعالى: (قالوا نحن أولو قوة وأولو بأس شديد والأمر إليك فانظري ماذا تأمرين (٣٣) قالت إن الملوك إذا دخلوا قرية أفسدوها وجعلوا أعزة أهلها أذلة وكذلك يفعلون (٣٤)) .','1005','2059','0');
INSERT INTO [t22928] VALUES (2074,'قال تعالى: (فلما جاء سليمان قال أتمدونني بمال فما آتاني الله خير مما آتاكم بل أنتم بهديتكم تفرحون (٣٦)) .','1005','2059','0');
INSERT INTO [t22928] VALUES (2075,'قال تعالى: (قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك وإني عليه لقوي أمين (٣٩) قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك فلما رآه مستقرا عنده قال هذا من فضل ربي ليبلوني أأشكر أم أكفر ومن شكر فإنما يشكر لنفسه ومن كفر فإن ربي غني كريم (٤٠) قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون (٤١)) .','1006','2059','0');
INSERT INTO [t22928] VALUES (2076,'قال تعالى: (وصدها ما كانت تعبد من دون الله إنها كانت من قوم كافرين (٤٣) قيل لها ادخلي الصرح فلما رأته حسبته لجة وكشفت عن ساقيها قال إنه صرح ممرد من قوارير قالت رب إني ظلمت نفسي وأسلمت مع سليمان لله رب العالمين (٤٤)) .','1006','2059','0');
INSERT INTO [t22928] VALUES (2077,'قال تعالى: (ولقد أرسلنا إلى ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون (٤٥) قال ياقوم لم تستعجلون بالسيئة قبل الحسنة لولا تستغفرون الله لعلكم ترحمون (٤٦) قالوا اطيرنا بك وبمن معك قال طائركم عند الله بل أنتم قوم تفتنون (٤٧) وكان في المدينة تسعة رهط يفسدون في الأرض ولا يصلحون (٤٨))','1006','2059','0');
INSERT INTO [t22928] VALUES (2078,'قال تعالى: (قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون (٤٩)) .','1007','2059','0');
INSERT INTO [t22928] VALUES (2079,'قال تعالى: (فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين (٥١)) .','1007','2059','0');
INSERT INTO [t22928] VALUES (2080,'قال تعالى: (فتلك بيوتهم خاوية بما ظلموا إن في ذلك لآية لقوم يعلمون (٥٢)) .','1008','2059','0');
INSERT INTO [t22928] VALUES (2081,'قال تعالى: (ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون (٥٤) أئنكم لتأتون الرجال شهوة من دون النساء بل أنتم قوم تجهلون (٥٥))','1008','2059','0');
INSERT INTO [t22928] VALUES (2082,'قال تعالى: (قل الحمد لله وسلام على عباده الذين اصطفى آلله خير أم ما يشركون (٥٩)) .','1008','2059','0');
INSERT INTO [t22928] VALUES (2083,'قال تعالى: (أم من خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة ما كان لكم أن تنبتوا شجرها أإله مع الله بل هم قوم يعدلون (٦٠) أم من جعل الأرض قرارا وجعل خلالها أنهارا وجعل لها رواسي وجعل بين البحرين حاجزا أإله مع الله بل أكثرهم لا يعلمون (٦١) أم من يجيب المضطر إذا دعاه ويكشف السوء ويجعلكم خلفاء الأرض أإله مع الله قليلا ما تذكرون (٦٢) أم من يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته أإله مع الله تعالى الله عما يشركون (٦٣)) .','1008','2059','0');
INSERT INTO [t22928] VALUES (2084,'قال تعالى: (قل لا يعلم من في السماوات والأرض الغيب إلا الله وما يشعرون أيان يبعثون (٦٥)) .','1009','2059','0');
INSERT INTO [t22928] VALUES (2085,'قال تعالى: (بل ادارك علمهم في الآخرة بل هم في شك منها بل هم منها عمون (٦٦)) .','1009','2059','0');
INSERT INTO [t22928] VALUES (2086,'قال تعالى: (وقال الذين كفروا أئذا كنا ترابا وآباؤنا أئنا لمخرجون (٦٧)) .','1010','2059','0');
INSERT INTO [t22928] VALUES (2087,'قال تعالى: (وآباؤنا) : هو معطوف على الضمير في «كنا» من غير توكيد؛ لأن المفعول فصل فجرى مجرى التوكيد.','1010','2059','0');
INSERT INTO [t22928] VALUES (2088,'قال تعالى: (قل عسى أن يكون ردف لكم بعض الذي تستعجلون (٧٢)) .','1010','2059','0');
INSERT INTO [t22928] VALUES (2089,'قال تعالى: (وإن ربك ليعلم ما تكن صدورهم وما يعلنون (٧٤) وما من غائبة في السماء والأرض إلا في كتاب مبين (٧٥) إن هذا القرآن يقص على بني إسرائيل أكثر الذي هم فيه يختلفون (٧٦) وإنه لهدى ورحمة للمؤمنين (٧٧) إن ربك يقضي بينهم بحكمه وهو العزيز العليم (٧٨) فتوكل على الله إنك على الحق المبين (٧٩) إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين (٨٠) وما أنت بهادي العمي عن ضلالتهم إن تسمع إلا من يؤمن بآياتنا فهم مسلمون (٨١)) .','1010','2059','0');
INSERT INTO [t22928] VALUES (2090,'قال تعالى: (وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم أن الناس كانوا بآياتنا لا يوقنون (٨٢) ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون (٨٣) حتى إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أم ماذا كنتم تعملون (٨٤) ووقع القول عليهم بما ظلموا فهم لا ينطقون (٨٥) ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا إن في ذلك لآيات لقوم يؤمنون (٨٦) ويوم ينفخ في الصور ففزع من في السماوات ومن في الأرض إلا من شاء الله وكل أتوه داخرين (٨٧)) .','1011','2059','0');
INSERT INTO [t22928] VALUES (2091,'قال تعالى: (وترى الجبال تحسبها جامدة وهي تمر مر السحاب صنع الله الذي أتقن كل شيء إنه خبير بما تفعلون (٨٨)) .','1011','2059','0');
INSERT INTO [t22928] VALUES (2092,'قال تعالى: (من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون (٨٩)) .','1012','2059','0');
INSERT INTO [t22928] VALUES (2093,'قال تعالى: (خير منها) : يجوز أن يكون المعنى أفضل منها، فيكون «من» في موضع نصب. ويجوز أن يكون بمعنى فضل فيكون «منها» في موضع رفع صفة لخير؛ أي فله خير حاصل بسببها.','1012','2059','0');
INSERT INTO [t22928] VALUES (2094,'قال تعالى: (ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون (٩٠)) .','1012','2059','0');
INSERT INTO [t22928] VALUES (2095,'قال تعالى: (هل تجزون) : أي يقال لهم، وهو في موضع نصب على الحال؛ أي فكبت وجوههم مقولا لهم هل تجزون.','1012','2059','0');
INSERT INTO [t22928] VALUES (2096,'قال تعالى: (إنما أمرت أن أعبد رب هذه البلدة الذي حرمها وله كل شيء وأمرت أن أكون من المسلمين (٩١)) .','1012','2059','0');
INSERT INTO [t22928] VALUES (2097,'القصص','1013','0','0');
INSERT INTO [t22928] VALUES (2098,'قال تعالى: (طسم (١) تلك آيات الكتاب المبين (٢) نتلوا عليك من نبأ موسى وفرعون بالحق لقوم يؤمنون (٣)) .','1013','2097','0');
INSERT INTO [t22928] VALUES (2099,'قال تعالى: (إن فرعون علا في الأرض وجعل أهلها شيعا يستضعف طائفة منهم يذبح أبناءهم ويستحيي نساءهم إنه كان من المفسدين (٤)) .','1013','2097','0');
INSERT INTO [t22928] VALUES (2100,'قال تعالى: (ونمكن لهم في الأرض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون (٦) وأوحينا إلى أم موسى أن أرضعيه فإذا خفت عليه فألقيه في اليم ولا تخافي ولا تحزني إنا رادوه إليك وجاعلوه من المرسلين (٧)) .','1013','2097','0');
INSERT INTO [t22928] VALUES (2101,'قال تعالى: (فالتقطه آل فرعون ليكون لهم عدوا وحزنا إن فرعون وهامان وجنودهما كانوا خاطئين (٨)) .','1013','2097','0');
INSERT INTO [t22928] VALUES (2102,'قال تعالى: (وقالت امرأة فرعون قرة عين لي ولك لا تقتلوه عسى أن ينفعنا أو نتخذه ولدا وهم لا يشعرون (٩)) .','1013','2097','0');
INSERT INTO [t22928] VALUES (2103,'قال تعالى: (وأصبح فؤاد أم موسى فارغا إن كادت لتبدي به لولا أن ربطنا على قلبها لتكون من المؤمنين (١٠)) .','1014','2097','0');
INSERT INTO [t22928] VALUES (2104,'قال تعالى: (وقالت لأخته قصيه فبصرت به عن جنب وهم لا يشعرون (١١) وحرمنا عليه المراضع من قبل فقالت هل أدلكم على أهل بيت يكفلونه لكم وهم له ناصحون (١٢) فرددناه إلى أمه كي تقر عينها ولا تحزن ولتعلم أن وعد الله حق ولكن أكثرهم لا يعلمون (١٣) ولما بلغ أشده واستوى آتيناه حكما وعلما وكذلك نجزي المحسنين (١٤) ودخل المدينة على حين غفلة من أهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسى فقضى عليه قال هذا من عمل الشيطان إنه عدو مضل مبين (١٥))','1014','2097','0');
INSERT INTO [t22928] VALUES (2105,'قال تعالى: (قال رب بما أنعمت علي فلن أكون ظهيرا للمجرمين (١٧) فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه قال له موسى إنك لغوي مبين (١٨)) .','1015','2097','0');
INSERT INTO [t22928] VALUES (2106,'قال تعالى: (ولما ورد ماء مدين وجد عليه أمة من الناس يسقون ووجد من دونهم امرأتين تذودان قال ما خطبكما قالتا لا نسقي حتى يصدر الرعاء وأبونا شيخ كبير (٢٣) فسقى لهما ثم تولى إلى الظل فقال رب إني لما أنزلت إلي من خير فقير (٢٤) فجاءته إحداهما تمشي على استحياء قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا فلما جاءه وقص عليه القصص قال لا تخف نجوت من القوم الظالمين (٢٥) قالت إحداهما يا أبت استأجره إن خير من استأجرت القوي الأمين (٢٦) قال إني أريد أن أنكحك إحدى ابنتي هاتين على أن تأجرني ثماني حجج فإن أتممت عشرا فمن عندك وما أريد أن أشق عليك ستجدني إن شاء الله من الصالحين (٢٧)) .','1015','2097','0');
INSERT INTO [t22928] VALUES (2107,'قال تعالى: (قال ذلك بيني وبينك أيما الأجلين قضيت فلا عدوان علي والله على ما نقول وكيل (٢٨)) .','1016','2097','0');
INSERT INTO [t22928] VALUES (2108,'قال تعالى: (فلما قضى موسى الأجل وسار بأهله آنس من جانب الطور نارا قال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بخبر أو جذوة من النار لعلكم تصطلون (٢٩)) .','1016','2097','0');
INSERT INTO [t22928] VALUES (2109,'قال تعالى: (فلما أتاها نودي من شاطئ الوادي الأيمن في البقعة المباركة من الشجرة أن يا موسى إني أنا الله رب العالمين (٣٠)) .','1017','2097','0');
INSERT INTO [t22928] VALUES (2110,'قال تعالى: (اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم إليك جناحك من الرهب فذانك برهانان من ربك إلى فرعون وملئه إنهم كانوا قوما فاسقين (٣٢) قال رب إني قتلت منهم نفسا فأخاف أن يقتلون (٣٣) وأخي هارون هو أفصح مني لسانا فأرسله معي ردءا يصدقني إني أخاف أن يكذبون (٣٤))','1017','2097','0');
INSERT INTO [t22928] VALUES (2111,'قال تعالى: (قال سنشد عضدك بأخيك ونجعل لكما سلطانا فلا يصلون إليكما بآياتنا أنتما ومن اتبعكما الغالبون (٣٥) فلما جاءهم موسى بآياتنا بينات قالوا ما هذا إلا سحر مفترى وما سمعنا بهذا في آبائنا الأولين (٣٦) وقال موسى ربي أعلم بمن جاء بالهدى من عنده ومن تكون له عاقبة الدار إنه لا يفلح الظالمون (٣٧)) .','1018','2097','0');
INSERT INTO [t22928] VALUES (2112,'قال تعالى: (وجعلناهم أئمة يدعون إلى النار ويوم القيامة لا ينصرون (٤١) وأتبعناهم في هذه الدنيا لعنة ويوم القيامة هم من المقبوحين (٤٢)) .','1018','2097','0');
INSERT INTO [t22928] VALUES (2113,'قال تعالى: (ولقد آتينا موسى الكتاب من بعد ما أهلكنا القرون الأولى بصائر للناس وهدى ورحمة لعلهم يتذكرون (٤٣))','1018','2097','0');
INSERT INTO [t22928] VALUES (2114,'قال تعالى: (وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر وما كنت من الشاهدين (٤٤) ولكنا أنشأنا قرونا فتطاول عليهم العمر وما كنت ثاويا في أهل مدين تتلو عليهم آياتنا ولكنا كنا مرسلين (٤٥) وما كنت بجانب الطور إذ نادينا ولكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون (٤٦)) .','1018','2097','0');
INSERT INTO [t22928] VALUES (2115,'قال تعالى: (فلما جاءهم الحق من عندنا قالوا لولا أوتي مثل ما أوتي موسى أولم يكفروا بما أوتي موسى من قبل قالوا سحران تظاهرا وقالوا إنا بكل كافرون (٤٨)) .','1019','2097','0');
INSERT INTO [t22928] VALUES (2116,'قال تعالى: (فإن لم يستجيبوا لك فاعلم أنما يتبعون أهواءهم ومن أضل ممن اتبع هواه بغير هدى من الله إن الله لا يهدي القوم الظالمين (٥٠) ولقد وصلنا لهم القول لعلهم يتذكرون (٥١) الذين آتيناهم الكتاب من قبله هم به يؤمنون (٥٢) وإذا يتلى عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين (٥٣) أولئك يؤتون أجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون (٥٤)) .','1019','2097','0');
INSERT INTO [t22928] VALUES (2117,'قال تعالى: (وقالوا إن نتبع الهدى معك نتخطف من أرضنا أولم نمكن لهم حرما آمنا يجبى إليه ثمرات كل شيء رزقا من لدنا ولكن أكثرهم لا يعلمون (٥٧))','1020','2097','0');
INSERT INTO [t22928] VALUES (2118,'قال تعالى: (وكم أهلكنا من قرية بطرت معيشتها فتلك مساكنهم لم تسكن من بعدهم إلا قليلا وكنا نحن الوارثين (٥٨)) .','1020','2097','0');
INSERT INTO [t22928] VALUES (2119,'قال تعالى: (وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها وما عند الله خير وأبقى أفلا تعقلون (٦٠)) .','1020','2097','0');
INSERT INTO [t22928] VALUES (2120,'قال تعالى: (أفمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين (٦١)) .','1021','2097','0');
INSERT INTO [t22928] VALUES (2121,'قال تعالى: (قال الذين حق عليهم القول ربنا هؤلاء الذين أغوينا أغويناهم كما غوينا تبرأنا إليك ما كانوا إيانا يعبدون (٦٣)) .','1021','2097','0');
INSERT INTO [t22928] VALUES (2122,'قال تعالى: (وربك يخلق ما يشاء ويختار ما كان لهم الخيرة سبحان الله وتعالى عما يشركون (٦٨)) .','1021','2097','0');
INSERT INTO [t22928] VALUES (2123,'قال تعالى: (قل أرأيتم إن جعل الله عليكم الليل سرمدا إلى يوم القيامة من إله غير الله يأتيكم بضياء أفلا تسمعون (٧١)) .','1022','2097','0');
INSERT INTO [t22928] VALUES (2124,'قال تعالى: (ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون (٧٤) ونزعنا من كل أمة شهيدا فقلنا هاتوا برهانكم فعلموا أن الحق لله وضل عنهم ما كانوا يفترون (٧٥)) .','1022','2097','0');
INSERT INTO [t22928] VALUES (2125,'قال تعالى: (إن قارون كان من قوم موسى فبغى عليهم وآتيناه من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة إذ قال له قومه لا تفرح إن الله لا يحب الفرحين (٧٦)) .','1022','2097','0');
INSERT INTO [t22928] VALUES (2126,'قال تعالى: (وابتغ فيما آتاك الله الدار الآخرة ولا تنس نصيبك من الدنيا وأحسن كما أحسن الله إليك ولا تبغ الفساد في الأرض إن الله لا يحب المفسدين (٧٧)) .','1022','2097','0');
INSERT INTO [t22928] VALUES (2127,'قال تعالى: (قال إنما أوتيته على علم عندي أولم يعلم أن الله قد أهلك من قبله من القرون من هو أشد منه قوة وأكثر جمعا ولا يسأل عن ذنوبهم المجرمون (٧٨)) .','1023','2097','0');
INSERT INTO [t22928] VALUES (2128,'قال تعالى: (فخرج على قومه في زينته قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم (٧٩) وقال الذين أوتوا العلم ويلكم ثواب الله خير لمن آمن وعمل صالحا ولا يلقاها إلا الصابرون (٨٠)) .','1023','2097','0');
INSERT INTO [t22928] VALUES (2129,'قال تعالى: (وأصبح الذين تمنوا مكانه بالأمس يقولون ويكأن الله يبسط الرزق لمن يشاء من عباده ويقدر لولا أن من الله علينا لخسف بنا ويكأنه لا يفلح الكافرون (٨٢) و (بالأمس) : ظرف لتمنوا.','1024','2097','0');
INSERT INTO [t22928] VALUES (2130,'قال تعالى: (تلك الدار الآخرة نجعلها للذين لا يريدون علوا في الأرض ولا فسادا والعاقبة للمتقين (٨٣)) .','1024','2097','0');
INSERT INTO [t22928] VALUES (2131,'قال تعالى: (إن الذي فرض عليك القرآن لرادك إلى معاد قل ربي أعلم من جاء بالهدى ومن هو في ضلال مبين (٨٥)) .','1024','2097','0');
INSERT INTO [t22928] VALUES (2132,'قال تعالى: (وما كنت ترجو أن يلقى إليك الكتاب إلا رحمة من ربك فلا تكونن ظهيرا للكافرين (٨٦)) .','1025','2097','0');
INSERT INTO [t22928] VALUES (2133,'قال تعالى: (ولا تدع مع الله إلها آخر لا إله إلا هو كل شيء هالك إلا وجهه له الحكم وإليه ترجعون (٨٨)) .','1025','2097','0');
INSERT INTO [t22928] VALUES (2134,'العنكبوت','1026','0','0');
INSERT INTO [t22928] VALUES (2135,'قال تعالى: (أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون (٢)) .','1026','2134','0');
INSERT INTO [t22928] VALUES (2136,'قال تعالى: (أم حسب الذين يعملون السيئات أن يسبقونا ساء ما يحكمون (٤)) .','1026','2134','0');
INSERT INTO [t22928] VALUES (2137,'قال تعالى: (من كان يرجو لقاء الله فإن أجل الله لآت وهو السميع العليم (٥)) .','1026','2134','0');
INSERT INTO [t22928] VALUES (2138,'قال تعالى: (ووصينا الإنسان بوالديه حسنا وإن جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما إلي مرجعكم فأنبئكم بما كنتم تعملون (٨)) .','1026','2134','0');
INSERT INTO [t22928] VALUES (2139,'قال تعالى: (والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين (٩)) .','1027','2134','0');
INSERT INTO [t22928] VALUES (2140,'قال تعالى: (وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء إنهم لكاذبون (١٢) وليحملن أثقالهم وأثقالا مع أثقالهم وليسألن يوم القيامة عما كانوا يفترون (١٣) ولقد أرسلنا نوحا إلى قومه فلبث فيهم ألف سنة إلا خمسين عاما فأخذهم الطوفان وهم ظالمون (١٤) فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين (١٥) وإبراهيم إذ قال لقومه اعبدوا الله واتقوه ذلكم خير لكم إن كنتم تعلمون (١٦)) .','1027','2134','0');
INSERT INTO [t22928] VALUES (2141,'قال تعالى: (قل سيروا في الأرض فانظروا كيف بدأ الخلق ثم الله ينشئ النشأة الآخرة إن الله على كل شيء قدير (٢٠)) .','1027','2134','0');
INSERT INTO [t22928] VALUES (2142,'قال تعالى: (وما أنتم بمعجزين في الأرض ولا في السماء وما لكم من دون الله من ولي ولا نصير (٢٢)) .','1028','2134','0');
INSERT INTO [t22928] VALUES (2143,'قال تعالى: (وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا ومأواكم النار وما لكم من ناصرين (٢٥)) .','1028','2134','0');
INSERT INTO [t22928] VALUES (2144,'قال تعالى: (ولوطا إذ قال لقومه إنكم لتأتون الفاحشة ما سبقكم بها من أحد من العالمين (٢٨)) .','1029','2134','0');
INSERT INTO [t22928] VALUES (2145,'قال تعالى: (ولما أن جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن إنا منجوك وأهلك إلا امرأتك كانت من الغابرين (٣٣) إنا منزلون على أهل هذه القرية رجزا من السماء بما كانوا يفسقون (٣٤) ولقد تركنا منها آية بينة لقوم يعقلون (٣٥) وإلى مدين أخاهم شعيبا فقال ياقوم اعبدوا الله وارجوا اليوم الآخر ولا تعثوا في الأرض مفسدين (٣٦) فكذبوه فأخذتهم الرجفة فأصبحوا في دارهم جاثمين (٣٧) وعادا وثمود وقد تبين لكم من مساكنهم وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين (٣٨) وقارون وفرعون وهامان ولقد جاءهم موسى بالبينات فاستكبروا في الأرض وما كانوا سابقين (٣٩) فكلا أخذنا بذنبه فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا وما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون (٤٠)) .','1029','2134','0');
INSERT INTO [t22928] VALUES (2146,'قال تعالى: (مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا وإن أوهن البيوت لبيت العنكبوت لو كانوا يعلمون (٤١)) .','1030','2134','0');
INSERT INTO [t22928] VALUES (2147,'قال تعالى: (إن الله يعلم ما يدعون من دونه من شيء وهو العزيز الحكيم وتلك الأمثال نضربها للناس وما يعقلها إلا العالمون (٤٣)) .','1030','2134','0');
INSERT INTO [t22928] VALUES (2148,'قال تعالى: (ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم وقولوا آمنا بالذي أنزل إلينا وأنزل إليكم وإلهنا وإلهكم واحد ونحن له مسلمون (٤٦)) .','1031','2134','0');
INSERT INTO [t22928] VALUES (2149,'قال تعالى: (أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم إن في ذلك لرحمة وذكرى لقوم يؤمنون (٥١))','1031','2134','0');
INSERT INTO [t22928] VALUES (2150,'قال تعالى: (والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الأنهار خالدين فيها نعم أجر العاملين (٥٨) الذين صبروا وعلى ربهم يتوكلون (٥٩)) .','1031','2134','0');
INSERT INTO [t22928] VALUES (2151,'قال تعالى: (وكأين من دابة لا تحمل رزقها الله يرزقها وإياكم وهو السميع العليم (٦٠)) .','1031','2134','0');
INSERT INTO [t22928] VALUES (2152,'قال تعالى: (وما هذه الحياة الدنيا إلا لهو ولعب وإن الدار الآخرة لهي الحيوان لو كانوا يعلمون (٦٤)) .','1032','2134','0');
INSERT INTO [t22928] VALUES (2153,'قال تعالى: (ليكفروا بما آتيناهم وليتمتعوا فسوف يعلمون (٦٦)) .','1032','2134','0');
INSERT INTO [t22928] VALUES (2154,'الروم','1033','0','0');
INSERT INTO [t22928] VALUES (2155,'قال تعالى: (غلبت الروم (٢) في أدنى الأرض وهم من بعد غلبهم سيغلبون (٣) في بضع سنين لله الأمر من قبل ومن بعد ويومئذ يفرح المؤمنون (٤) بنصر الله ينصر من يشاء وهو العزيز الرحيم (٥)) .','1033','2154','0');
INSERT INTO [t22928] VALUES (2156,'قال تعالى: (وعد الله لا يخلف الله وعده ولكن أكثر الناس لا يعلمون (٦)) .','1033','2154','0');
INSERT INTO [t22928] VALUES (2157,'قال تعالى: (أولم يتفكروا في أنفسهم ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى وإن كثيرا من الناس بلقاء ربهم لكافرون (٨)) .','1034','2154','0');
INSERT INTO [t22928] VALUES (2158,'قال تعالى: (أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أشد منهم قوة وأثاروا الأرض وعمروها أكثر مما عمروها وجاءتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون (٩)) .','1034','2154','0');
INSERT INTO [t22928] VALUES (2159,'قال تعالى: (ثم كان عاقبة الذين أساءوا السوءى أن كذبوا بآيات الله وكانوا بها يستهزئون (١٠)) .','1034','2154','0');
INSERT INTO [t22928] VALUES (2160,'قال تعالى: (ويوم تقوم الساعة يبلس المجرمون (١٢)) .','1035','2154','0');
INSERT INTO [t22928] VALUES (2161,'قال تعالى: (فسبحان الله حين تمسون وحين تصبحون (١٧)) .','1035','2154','0');
INSERT INTO [t22928] VALUES (2162,'قال تعالى: (وله الحمد في السماوات والأرض وعشيا وحين تظهرون (١٨)) .','1035','2154','0');
INSERT INTO [t22928] VALUES (2163,'قال تعالى: (ومن آياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الأرض بعد موتها إن في ذلك لآيات لقوم يعقلون (٢٤)) .','1035','2154','0');
INSERT INTO [t22928] VALUES (2164,'قال تعالى: (ومن آياته أن تقوم السماء والأرض بأمره ثم إذا دعاكم دعوة من الأرض إذا أنتم تخرجون (٢٥)) .','1036','2154','0');
INSERT INTO [t22928] VALUES (2165,'قال تعالى: (وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه وله المثل الأعلى في السماوات والأرض وهو العزيز الحكيم (٢٧)) .','1036','2154','0');
INSERT INTO [t22928] VALUES (2166,'قال تعالى: (ضرب لكم مثلا من أنفسكم هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم فأنتم فيه سواء تخافونهم كخيفتكم أنفسكم كذلك نفصل الآيات لقوم يعقلون (٢٨)) .','1037','2154','0');
INSERT INTO [t22928] VALUES (2167,'قال تعالى: (فأقم وجهك للدين حنيفا فطرة الله التي فطر الناس عليها لا تبديل لخلق الله ذلك الدين القيم ولكن أكثر الناس لا يعلمون (٣٠) منيبين إليه واتقوه وأقيموا الصلاة ولا تكونوا من المشركين (٣١)) .','1037','2154','0');
INSERT INTO [t22928] VALUES (2168,'قال تعالى: (من الذين فرقوا دينهم وكانوا شيعا كل حزب بما لديهم فرحون (٣٢)) .','1037','2154','0');
INSERT INTO [t22928] VALUES (2169,'قال تعالى: (ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون (٣٤)) .','1037','2154','0');
INSERT INTO [t22928] VALUES (2170,'قال تعالى: (أم أنزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون (٣٥)) .','1037','2154','0');
INSERT INTO [t22928] VALUES (2171,'قال تعالى: (وإذا أذقنا الناس رحمة فرحوا بها وإن تصبهم سيئة بما قدمت أيديهم إذا هم يقنطون (٣٦)) .','1038','2154','0');
INSERT INTO [t22928] VALUES (2172,'قال تعالى: (وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله وما آتيتم من زكاة تريدون وجه الله فأولئك هم المضعفون (٣٩)) .','1038','2154','0');
INSERT INTO [t22928] VALUES (2173,'قال تعالى: (ظهر الفساد في البر والبحر بما كسبت أيدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون (٤١)) .','1038','2154','0');
INSERT INTO [t22928] VALUES (2174,'قال تعالى: (ولقد أرسلنا من قبلك رسلا إلى قومهم فجاءوهم بالبينات فانتقمنا من الذين أجرموا وكان حقا علينا نصر المؤمنين (٤٧)) .','1038','2154','0');
INSERT INTO [t22928] VALUES (2175,'قال تعالى: (الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله فإذا أصاب به من يشاء من عباده إذا هم يستبشرون (٤٨)) .','1038','2154','0');
INSERT INTO [t22928] VALUES (2176,'قال تعالى: (وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين (٤٩)) .','1039','2154','0');
INSERT INTO [t22928] VALUES (2177,'قال تعالى: (فانظر إلى آثار رحمة الله كيف يحيي الأرض بعد موتها إن ذلك لمحيي الموتى وهو على كل شيء قدير (٥٠) ولئن أرسلنا ريحا فرأوه مصفرا لظلوا من بعده يكفرون (٥١)) .','1039','2154','0');
INSERT INTO [t22928] VALUES (2178,'قال تعالى: (الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة يخلق ما يشاء وهو العليم القدير (٥٤)) .','1039','2154','0');
INSERT INTO [t22928] VALUES (2179,'قال تعالى: (فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون (٥٧)) .','1039','2154','0');
INSERT INTO [t22928] VALUES (2180,'لقمان','1040','0','0');
INSERT INTO [t22928] VALUES (2181,'قال تعالى: (تلك آيات الكتاب الحكيم (٢) هدى ورحمة للمحسنين (٣)) .','1040','2180','0');
INSERT INTO [t22928] VALUES (2182,'قال تعالى: (ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا أولئك لهم عذاب مهين (٦)) .','1040','2180','0');
INSERT INTO [t22928] VALUES (2183,'قال تعالى: (وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا فبشره بعذاب أليم (٧)) .','1040','2180','0');
INSERT INTO [t22928] VALUES (2184,'قال تعالى: (إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم (٨) خالدين فيها وعد الله حقا وهو العزيز الحكيم (٩) خلق السماوات بغير عمد ترونها وألقى في الأرض رواسي أن تميد بكم وبث فيها من كل دابة وأنزلنا من السماء ماء فأنبتنا فيها من كل زوج كريم (١٠)) .','1040','2180','0');
INSERT INTO [t22928] VALUES (2185,'قال تعالى: (هذا خلق الله فأروني ماذا خلق الذين من دونه بل الظالمون في ضلال مبين (١١) ولقد آتينا لقمان الحكمة أن اشكر لله ومن يشكر فإنما يشكر لنفسه ومن كفر فإن الله غني حميد (١٢) وإذ قال لقمان لابنه وهو يعظه يابني لا تشرك بالله إن الشرك لظلم عظيم (١٣)) .','1041','2180','0');
INSERT INTO [t22928] VALUES (2186,'قال تعالى: (ووصينا الإنسان بوالديه حملته أمه وهنا على وهن وفصاله في عامين أن اشكر لي ولوالديك إلي المصير (١٤)) .','1041','2180','0');
INSERT INTO [t22928] VALUES (2187,'قال تعالى: (وإن جاهداك على أن تشرك بي ما ليس لك به علم فلا تطعهما وصاحبهما في الدنيا معروفا واتبع سبيل من أناب إلي ثم إلي مرجعكم فأنبئكم بما كنتم تعملون (١٥)) .','1041','2180','0');
INSERT INTO [t22928] VALUES (2188,'قال تعالى: (يا بني إنها إن تك مثقال حبة من خردل فتكن في صخرة أو في السماوات أو في الأرض يأت بها الله إن الله لطيف خبير (١٦)) .','1041','2180','0');
INSERT INTO [t22928] VALUES (2189,'قال تعالى: (واقصد في مشيك واغضض من صوتك إن أنكر الأصوات لصوت الحمير (١٩)) .','1042','2180','0');
INSERT INTO [t22928] VALUES (2190,'قال تعالى: (ألم تروا أن الله سخر لكم ما في السماوات وما في الأرض وأسبغ عليكم نعمه ظاهرة وباطنة ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير (٢٠)) .','1042','2180','0');
INSERT INTO [t22928] VALUES (2191,'قال تعالى: (ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله إن الله عزيز حكيم (٢٧)) .','1042','2180','0');
INSERT INTO [t22928] VALUES (2192,'قال تعالى: (ما خلقكم ولا بعثكم إلا كنفس واحدة إن الله سميع بصير (٢٨)) .','1043','2180','0');
INSERT INTO [t22928] VALUES (2193,'قال تعالى: (ألم تر أن الفلك تجري في البحر بنعمة الله ليريكم من آياته إن في ذلك لآيات لكل صبار شكور (٣١)) .','1043','2180','0');
INSERT INTO [t22928] VALUES (2194,'قال تعالى: (يا أيها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا إن وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور (٣٣)) .','1043','2180','0');
INSERT INTO [t22928] VALUES (2195,'قال تعالى: (إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام وما تدري نفس ماذا تكسب غدا وما تدري نفس بأي أرض تموت إن الله عليم خبير (٣٤)) .','1043','2180','0');
INSERT INTO [t22928] VALUES (2196,'السجدة','1044','0','0');
INSERT INTO [t22928] VALUES (2197,'قال تعالى: (الم (١) تنزيل الكتاب لا ريب فيه من رب العالمين (٢))','1044','2196','0');
INSERT INTO [t22928] VALUES (2198,'قال تعالى: (أم يقولون افتراه بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يهتدون (٣)) .','1044','2196','0');
INSERT INTO [t22928] VALUES (2199,'قال تعالى: (يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون (٥)) .','1044','2196','0');
INSERT INTO [t22928] VALUES (2200,'قال تعالى: (ذلك عالم الغيب والشهادة العزيز الرحيم (٦) الذي أحسن كل شيء خلقه وبدأ خلق الإنسان من طين (٧)) .','1045','2196','0');
INSERT INTO [t22928] VALUES (2201,'قال تعالى: (وقالوا أئذا ضللنا في الأرض أئنا لفي خلق جديد بل هم بلقاء ربهم كافرون (١٠)) .','1045','2196','0');
INSERT INTO [t22928] VALUES (2202,'قال تعالى: (ولو ترى إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون (١٢)) .','1045','2196','0');
INSERT INTO [t22928] VALUES (2203,'قال تعالى: (فذوقوا بما نسيتم لقاء يومكم هذا إنا نسيناكم وذوقوا عذاب الخلد بما كنتم تعملون (١٤)) .','1046','2196','0');
INSERT INTO [t22928] VALUES (2204,'قال تعالى: (تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون (١٦)) .','1046','2196','0');
INSERT INTO [t22928] VALUES (2205,'قال تعالى: (فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون (١٧)) .','1046','2196','0');
INSERT INTO [t22928] VALUES (2206,'قال تعالى: (أفمن كان مؤمنا كمن كان فاسقا لا يستوون (١٨)) .','1046','2196','0');
INSERT INTO [t22928] VALUES (2207,'قال تعالى: (وأما الذين فسقوا فمأواهم النار كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون (٢٠)) .','1047','2196','0');
INSERT INTO [t22928] VALUES (2208,'قال تعالى {ولقد آتينا موسى الكتاب فلا تكن في مرية من لقائه وجعلناه هدى لبني إسرائيل}','1047','2196','0');
INSERT INTO [t22928] VALUES (2209,'قال تعالى: (وجعلنا منهم أئمة يهدون بأمرنا لما صبروا وكانوا بآياتنا يوقنون (٢٤)) .','1047','2196','0');
INSERT INTO [t22928] VALUES (2210,'قال تعالى: (أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم إن في ذلك لآيات أفلا يسمعون (٢٦)) .','1047','2196','0');
INSERT INTO [t22928] VALUES (2211,'الأحزاب','1048','0','0');
INSERT INTO [t22928] VALUES (2212,'قال تعالى: (واتبع ما يوحى إليك من ربك إن الله كان بما تعملون خبيرا (٣)) .','1048','2211','0');
INSERT INTO [t22928] VALUES (2213,'قال تعالى: (ما جعل الله لرجل من قلبين في جوفه وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم وما جعل أدعياءكم أبناءكم ذلكم قولكم بأفواهكم والله يقول الحق وهو يهدي السبيل (٤)) .','1048','2211','0');
INSERT INTO [t22928] VALUES (2214,'قال تعالى: (ادعوهم لآبائهم هو أقسط عند الله فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم وليس عليكم جناح فيما أخطأتم به ولكن ما تعمدت قلوبكم وكان الله غفورا رحيما (٥)) .','1048','2211','0');
INSERT INTO [t22928] VALUES (2215,'قال تعالى: (النبي أولى بالمؤمنين من أنفسهم وأزواجه أمهاتهم وأولو الأرحام بعضهم أولى ببعض في كتاب الله من المؤمنين والمهاجرين إلا أن تفعلوا إلى أوليائكم معروفا كان ذلك في الكتاب مسطورا (٦)) .','1048','2211','0');
INSERT INTO [t22928] VALUES (2216,'قال تعالى: (وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم وأخذنا منهم ميثاقا غليظا (٧)) .','1049','2211','0');
INSERT INTO [t22928] VALUES (2217,'قال تعالى: (يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها وكان الله بما تعملون بصيرا (٩)) .','1049','2211','0');
INSERT INTO [t22928] VALUES (2218,'قال تعالى: (إذ جاءوكم من فوقكم ومن أسفل منكم وإذ زاغت الأبصار وبلغت القلوب الحناجر وتظنون بالله الظنونا (١٠) هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا (١١)) .','1049','2211','0');
INSERT INTO [t22928] VALUES (2219,'قال تعالى: (وإذ قالت طائفة منهم يا أهل يثرب لا مقام لكم فارجعوا ويستأذن فريق منهم النبي يقولون إن بيوتنا عورة وما هي بعورة إن يريدون إلا فرارا (١٣)) .','1050','2211','0');
INSERT INTO [t22928] VALUES (2220,'قال تعالى: (ولو دخلت عليهم من أقطارها ثم سئلوا الفتنة لأتوها وما تلبثوا بها إلا يسيرا (١٤)) .','1050','2211','0');
INSERT INTO [t22928] VALUES (2221,'قال تعالى: (ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار وكان عهد الله مسئولا (١٥)) .','1050','2211','0');
INSERT INTO [t22928] VALUES (2222,'قال تعالى: (قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا ولا يأتون البأس إلا قليلا (١٨)) .','1051','2211','0');
INSERT INTO [t22928] VALUES (2223,'قال تعالى: (أشحة عليكم فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي يغشى عليه من الموت فإذا ذهب الخوف سلقوكم بألسنة حداد أشحة على الخير أولئك لم يؤمنوا فأحبط الله أعمالهم وكان ذلك على الله يسيرا (١٩)) .','1051','2211','0');
INSERT INTO [t22928] VALUES (2224,'قال تعالى: (يحسبون الأحزاب لم يذهبوا وإن يأت الأحزاب يودوا لو أنهم بادون في الأعراب يسألون عن أنبائكم ولو كانوا فيكم ما قاتلوا إلا قليلا (٢٠)) .','1051','2211','0');
INSERT INTO [t22928] VALUES (2225,'قال تعالى: (لقد كان لكم في رسول الله أسوة حسنة لمن كان يرجو الله واليوم الآخر وذكر الله كثيرا (٢١)) .','1051','2211','0');
INSERT INTO [t22928] VALUES (2226,'قال تعالى: (ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم إلا إيمانا وتسليما (٢٢)) .','1052','2211','0');
INSERT INTO [t22928] VALUES (2227,'قال تعالى: (ليجزي الله الصادقين بصدقهم ويعذب المنافقين إن شاء أو يتوب عليهم إن الله كان غفورا رحيما (٢٤)) .','1052','2211','0');
INSERT INTO [t22928] VALUES (2228,'قال تعالى: (ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا (٢٥)) .','1052','2211','0');
INSERT INTO [t22928] VALUES (2229,'قال تعالى: (وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتأسرون فريقا (٢٦)) .','1052','2211','0');
INSERT INTO [t22928] VALUES (2230,'قال تعالى: (ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين وأعتدنا لها رزقا كريما (٣١)) .','1053','2211','0');
INSERT INTO [t22928] VALUES (2231,'قال تعالى: (يا نساء النبي لستن كأحد من النساء إن اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا (٣٢)) .','1053','2211','0');
INSERT INTO [t22928] VALUES (2232,'قال تعالى: (وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الأولى وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا (٣٣)) .','1053','2211','0');
INSERT INTO [t22928] VALUES (2233,'قال تعالى: (إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة وأجرا عظيما (٣٥)) .','1054','2211','0');
INSERT INTO [t22928] VALUES (2234,'قال تعالى: (وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم ومن يعص الله ورسوله فقد ضل ضلالا مبينا (٣)) .','1054','2211','0');
INSERT INTO [t22928] VALUES (2235,'قال تعالى: (وإذ تقول للذي أنعم الله عليه وأنعمت عليه أمسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله أحق أن تخشاه فلما قضى زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا منهن وطرا وكان أمر الله مفعولا (٣٧)) .','1054','2211','0');
INSERT INTO [t22928] VALUES (2236,'قال تعالى: (الذين يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله وكفى بالله حسيبا (٣٩)) .','1054','2211','0');
INSERT INTO [t22928] VALUES (2237,'قال تعالى: (ما كان محمد أبا أحد من رجالكم ولكن رسول الله وخاتم النبيين وكان الله بكل شيء عليما (٤٠)) .','1054','2211','0');
INSERT INTO [t22928] VALUES (2238,'قال تعالى: (يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها فمتعوهن وسرحوهن سراحا جميلا (٤٩)) .','1055','2211','0');
INSERT INTO [t22928] VALUES (2239,'قال تعالى: (يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها خالصة لك من دون المؤمنين قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج وكان الله غفورا رحيما (٥٠)) .','1055','2211','0');
INSERT INTO [t22928] VALUES (2240,'قال تعالى: (ترجي من تشاء منهن وتؤوي إليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك ذلك أدنى أن تقر أعينهن ولا يحزن ويرضين بما آتيتهن كلهن والله يعلم ما في قلوبكم وكان الله عليما حليما (٥١)) .','1056','2211','0');
INSERT INTO [t22928] VALUES (2241,'قال تعالى: (لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك وكان الله على كل شيء رقيبا (٥٢)) .','1056','2211','0');
INSERT INTO [t22928] VALUES (2242,'قال تعالى: (يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم إلى طعام غير ناظرين إناه ولكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا مستأنسين لحديث إن ذلكم كان يؤذي النبي فيستحيي منكم والله لا يستحيي من الحق وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ذلكم أطهر لقلوبكم وقلوبهن وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا إن ذلكم كان عند الله عظيما (٣٥)) .','1057','2211','0');
INSERT INTO [t22928] VALUES (2243,'قال تعالى: (يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ذلك أدنى أن يعرفن فلا يؤذين وكان الله غفورا رحيما (٥٩)) .','1057','2211','0');
INSERT INTO [t22928] VALUES (2244,'قال تعالى: (لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها إلا قليلا (٦٠) ملعونين أينما ثقفوا أخذوا وقتلوا تقتيلا (٦١)) .','1057','2211','0');
INSERT INTO [t22928] VALUES (2245,'قال تعالى: (سنة الله في الذين خلوا من قبل ولن تجد لسنة الله تبديلا (٦٢)) .','1057','2211','0');
INSERT INTO [t22928] VALUES (2246,'قال تعالى: (يوم تقلب وجوههم في النار يقولون يا ليتنا أطعنا الله وأطعنا الرسولا (٦٦)) .','1058','2211','0');
INSERT INTO [t22928] VALUES (2247,'قال تعالى: (إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان إنه كان ظلوما جهولا (٧٢) ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات وكان الله غفورا رحيما (٧٣)) .','1058','2211','0');
INSERT INTO [t22928] VALUES (2248,'سبأ','1059','0','0');
INSERT INTO [t22928] VALUES (2249,'قال تعالى: (الحمد لله الذي له ما في السماوات وما في الأرض وله الحمد في الآخرة وهو الحكيم الخبير (١)) .','1059','2248','0');
INSERT INTO [t22928] VALUES (2250,'قال تعالى: (يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور (٢)) .','1059','2248','0');
INSERT INTO [t22928] VALUES (2251,'قال تعالى: (وقال الذين كفروا لا تأتينا الساعة قل بلى وربي لتأتينكم عالم الغيب لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين (٣)) .','1059','2248','0');
INSERT INTO [t22928] VALUES (2252,'قال تعالى: (ليجزي الذين آمنوا وعملوا الصالحات أولئك لهم مغفرة ورزق كريم (٤)) .','1059','2248','0');
INSERT INTO [t22928] VALUES (2253,'قال تعالى: (والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز أليم (٥))','1059','2248','0');
INSERT INTO [t22928] VALUES (2254,'قال تعالى: (ويرى الذين أوتوا العلم الذي أنزل إليك من ربك هو الحق ويهدي إلى صراط العزيز الحميد (٦)) .','1060','2248','0');
INSERT INTO [t22928] VALUES (2255,'قال تعالى: (وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد أفترى على الله كذبا أم به جنة بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد (٨)) .','1060','2248','0');
INSERT INTO [t22928] VALUES (2256,'قال تعالى: (أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء إن في ذلك لآية لكل عبد منيب (٩)) .','1060','2248','0');
INSERT INTO [t22928] VALUES (2257,'قال تعالى: (ولقد آتينا داود منا فضلا يا جبال أوبي معه والطير وألنا له الحديد (١٠) أن اعمل سابغات وقدر في السرد واعملوا صالحا إني بما تعملون بصير (١١)) .','1061','2248','0');
INSERT INTO [t22928] VALUES (2258,'قال تعالى: (ولسليمان الريح غدوها شهر ورواحها شهر وأسلنا له عين القطر ومن الجن من يعمل بين يديه بإذن ربه ومن يزغ منهم عن أمرنا نذقه من عذاب السعير (١٢) يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات اعملوا آل داود شكرا وقليل من عبادي الشكور (١٣)) .','1061','2248','0');
INSERT INTO [t22928] VALUES (2259,'قال تعالى: (فلما قضينا عليه الموت ما دلهم على موته إلا دابة الأرض تأكل منسأته فلما خر تبينت الجن أن لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين (١٤)) .','1062','2248','0');
INSERT INTO [t22928] VALUES (2260,'قال تعالى: (لقد كان لسبأ في مسكنهم آية جنتان عن يمين وشمال كلوا من رزق ربكم واشكروا له بلدة طيبة ورب غفور (١٥)) .','1063','2248','0');
INSERT INTO [t22928] VALUES (2261,'قال تعالى: (فأعرضوا فأرسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي أكل خمط وأثل وشيء من سدر قليل (١٦)) .','1063','2248','0');
INSERT INTO [t22928] VALUES (2262,'قال تعالى: (فقالوا ربنا باعد بين أسفارنا وظلموا أنفسهم فجعلناهم أحاديث ومزقناهم كل ممزق إن في ذلك لآيات لكل صبار شكور (١٩)) .','1063','2248','0');
INSERT INTO [t22928] VALUES (2263,'قال تعالى: (ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين (٢٠)) .','1064','2248','0');
INSERT INTO [t22928] VALUES (2264,'قال تعالى: (وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة ممن هو منها في شك وربك على كل شيء حفيظ (٢١))','1064','2248','0');
INSERT INTO [t22928] VALUES (2265,'قال تعالى: (ولا تنفع الشفاعة عنده إلا لمن أذن له حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم (٢٣)) .','1065','2248','0');
INSERT INTO [t22928] VALUES (2266,'قال تعالى: (قل من يرزقكم من السماوات والأرض قل الله وإنا أو إياكم لعلى هدى أو في ضلال مبين (٢٤)) .','1065','2248','0');
INSERT INTO [t22928] VALUES (2267,'قال تعالى: (وما أرسلناك إلا كافة للناس بشيرا ونذيرا ولكن أكثر الناس لا يعلمون (٢٨)) .','1066','2248','0');
INSERT INTO [t22928] VALUES (2268,'قال تعالى: (قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون (٣٠)) .','1066','2248','0');
INSERT INTO [t22928] VALUES (2269,'قال تعالى: (وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار إذ تأمروننا أن نكفر بالله ونجعل له أندادا وأسروا الندامة لما رأوا العذاب وجعلنا الأغلال في أعناق الذين كفروا هل يجزون إلا ما كانوا يعملون (٣٣)) .','1066','2248','0');
INSERT INTO [t22928] VALUES (2270,'قال تعالى: (وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفى إلا من آمن وعمل صالحا فأولئك لهم جزاء الضعف بما عملوا وهم في الغرفات آمنون (٣٧)) .','1067','2248','0');
INSERT INTO [t22928] VALUES (2271,'قال تعالى: (قل إن ربي يبسط الرزق لمن يشاء من عباده ويقدر له وما أنفقتم من شيء فهو يخلفه وهو خير الرازقين (٣٩)) .','1067','2248','0');
INSERT INTO [t22928] VALUES (2272,'قال تعالى: (ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون (٤٠)) .','1067','2248','0');
INSERT INTO [t22928] VALUES (2273,'قال تعالى: (قل إنما أعظكم بواحدة أن تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة إن هو إلا نذير لكم بين يدي عذاب شديد (٤٦)) .','1067','2248','0');
INSERT INTO [t22928] VALUES (2274,'قال تعالى: (قل إن ربي يقذف بالحق علام الغيوب (٤٨)) .','1068','2248','0');
INSERT INTO [t22928] VALUES (2275,'قال تعالى: (ولو ترى إذ فزعوا فلا فوت وأخذوا من مكان قريب (٥١) وقالوا آمنا به وأنى لهم التناوش من مكان بعيد وقد كفروا به من قبل ويقذفون بالغيب من مكان بعيد (٥٢)) .','1068','2248','0');
INSERT INTO [t22928] VALUES (2276,'فاطر','1069','0','0');
INSERT INTO [t22928] VALUES (2277,'قال تعالى: (الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنى وثلاث ورباع يزيد في الخلق ما يشاء إن الله على كل شيء قدير (١)) .','1069','2276','0');
INSERT INTO [t22928] VALUES (2278,'قال تعالى: (ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له من بعده وهو العزيز الحكيم (٢)) .','1069','2276','0');
INSERT INTO [t22928] VALUES (2279,'قال تعالى: (يا أيها الناس اذكروا نعمة الله عليكم هل من خالق غير الله يرزقكم من السماء والأرض لا إله إلا هو فأنى تؤفكون (٣)) .','1069','2276','0');
INSERT INTO [t22928] VALUES (2280,'قال تعالى: (الذين كفروا لهم عذاب شديد والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير (٧)) .','1070','2276','0');
INSERT INTO [t22928] VALUES (2281,'قال تعالى: (أفمن زين له سوء عمله فرآه حسنا فإن الله يضل من يشاء ويهدي من يشاء فلا تذهب نفسك عليهم حسرات إن الله عليم بما يصنعون (٨)) .','1070','2276','0');
INSERT INTO [t22928] VALUES (2282,'قال تعالى: (من كان يريد العزة فلله العزة جميعا إليه يصعد الكلم الطيب والعمل الصالح يرفعه والذين يمكرون السيئات لهم عذاب شديد ومكر أولئك هو يبور (١٠)) .','1070','2276','0');
INSERT INTO [t22928] VALUES (2283,'قال تعالى: (وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح أجاج ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون (١٢))','1071','2276','0');
INSERT INTO [t22928] VALUES (2284,'قال تعالى: (ولا تزر وازرة وزر أخرى وإن تدع مثقلة إلى حملها لا يحمل منه شيء ولو كان ذا قربى إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة ومن تزكى فإنما يتزكى لنفسه وإلى الله المصير (١٨)) .','1071','2276','0');
INSERT INTO [t22928] VALUES (2285,'قال تعالى: (وما يستوي الأعمى والبصير (١٩) ولا الظلمات ولا النور (٢٠) ولا الظل ولا الحرور (٢١) وما يستوي الأحياء ولا الأموات إن الله يسمع من يشاء وما أنت بمسمع من في القبور (٢٢)) .','1071','2276','0');
INSERT INTO [t22928] VALUES (2286,'قال تعالى: (وإن يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير (٢٥)) .','1071','2276','0');
INSERT INTO [t22928] VALUES (2287,'قال تعالى: (ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود (٢٧) ومن الناس والدواب والأنعام مختلف ألوانه كذلك إنما يخشى الله من عباده العلماء إن الله عزيز غفور (٢٨)) .','1071','2276','0');
INSERT INTO [t22928] VALUES (2288,'قال تعالى: (إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور (٢٩) ليوفيهم أجورهم ويزيدهم من فضله إنه غفور شكور (٣٠)) .','1072','2276','0');
INSERT INTO [t22928] VALUES (2289,'قال تعالى: (والذي أوحينا إليك من الكتاب هو الحق مصدقا لما بين يديه إن الله بعباده لخبير بصير (٣١)) .','1072','2276','0');
INSERT INTO [t22928] VALUES (2290,'قال تعالى: (جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير (٣٣)) .','1072','2276','0');
INSERT INTO [t22928] VALUES (2291,'قال تعالى: (الذي أحلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب (٣٥)) .','1072','2276','0');
INSERT INTO [t22928] VALUES (2292,'قال تعالى: (والذين كفروا لهم نار جهنم لا يقضى عليهم فيموتوا ولا يخفف عنهم من عذابها كذلك نجزي كل كفور (٣٦)) .','1073','2276','0');
INSERT INTO [t22928] VALUES (2293,'قال تعالى: (وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل أولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير فذوقوا فما للظالمين من نصير قال تعالى: (٣٧)) .','1073','2276','0');
INSERT INTO [t22928] VALUES (2294,'قال تعالى: (إن الله يمسك السماوات والأرض أن تزولا ولئن زالتا إن أمسكهما من أحد من بعده إنه كان حليما غفورا (٤١) وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدى من إحدى الأمم فلما جاءهم نذير ما زادهم إلا نفورا (٤٢) استكبارا في الأرض ومكر السيئ ولا يحيق المكر السيئ إلا بأهله فهل ينظرون إلا سنة الأولين فلن تجد لسنة الله تبديلا ولن تجد لسنة الله تحويلا (٤٣)) .','1073','2276','0');
INSERT INTO [t22928] VALUES (2295,'يس','1075','0','0');
INSERT INTO [t22928] VALUES (2296,'قال تعالى: (يس (١) والقرآن الحكيم (٢)) .','1075','2295','0');
INSERT INTO [t22928] VALUES (2297,'قال تعالى: (إنك لمن المرسلين (٣) على صراط مستقيم (٤) تنزيل العزيز الرحيم (٥) لتنذر قوما ما أنذر آباؤهم فهم غافلون (٦)) .','1075','2295','0');
INSERT INTO [t22928] VALUES (2298,'قال تعالى: (وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون (٩)) .','1076','2295','0');
INSERT INTO [t22928] VALUES (2299,'قال تعالى: (إنا نحن نحيي الموتى ونكتب ما قدموا وآثارهم وكل شيء أحصيناه في إمام مبين (١٢)) .','1076','2295','0');
INSERT INTO [t22928] VALUES (2300,'قال تعالى: (واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون (١٣) إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون (١٤)) .','1076','2295','0');
INSERT INTO [t22928] VALUES (2301,'قال تعالى: (قالوا طائركم معكم أئن ذكرتم بل أنتم قوم مسرفون (١٩)) .','1076','2295','0');
INSERT INTO [t22928] VALUES (2302,'قال تعالى: (وما لي لا أعبد الذي فطرني وإليه ترجعون (٢٢)) .','1077','2295','0');
INSERT INTO [t22928] VALUES (2303,'قال تعالى: (أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون (٢٣)) .','1077','2295','0');
INSERT INTO [t22928] VALUES (2304,'قال تعالى: (قيل ادخل الجنة قال يا ليت قومي يعلمون (٢٦) بما غفر لي ربي وجعلني من المكرمين (٢٧)) .','1077','2295','0');
INSERT INTO [t22928] VALUES (2305,'قال تعالى: (وما أنزلنا على قومه من بعده من جند من السماء وما كنا منزلين (٢٨)) .','1077','2295','0');
INSERT INTO [t22928] VALUES (2306,'قال تعالى: (إن كانت إلا صيحة واحدة فإذا هم خامدون (٢٩)) .','1078','2295','0');
INSERT INTO [t22928] VALUES (2307,'قال تعالى: (يا حسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون (٣٠) ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون (٣١)) .','1078','2295','0');
INSERT INTO [t22928] VALUES (2308,'قال تعالى: (وإن كل لما جميع لدينا محضرون (٣٢)) .','1078','2295','0');
INSERT INTO [t22928] VALUES (2309,'قال تعالى: (وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون (٣٣)) .','1079','2295','0');
INSERT INTO [t22928] VALUES (2310,'قال تعالى: (وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون (٣٤) ليأكلوا من ثمره وما عملته أيديهم أفلا يشكرون (٣٥))','1079','2295','0');
INSERT INTO [t22928] VALUES (2311,'قال تعالى: (والقمر قدرناه منازل حتى عاد كالعرجون القديم (٣٩) لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار وكل في فلك يسبحون (٤٠)) .','1079','2295','0');
INSERT INTO [t22928] VALUES (2312,'قال تعالى: (وآية لهم أنا حملنا ذريتهم في الفلك المشحون (٤١)) .','1080','2295','0');
INSERT INTO [t22928] VALUES (2313,'قال تعالى: (وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون (٤٣)) .','1080','2295','0');
INSERT INTO [t22928] VALUES (2314,'قال تعالى: (إلا رحمة منا ومتاعا إلى حين (٤٤)) .','1080','2295','0');
INSERT INTO [t22928] VALUES (2315,'قال تعالى: (ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون (٤٩)) .','1081','2295','0');
INSERT INTO [t22928] VALUES (2316,'قال تعالى: (قالوا يا ويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون (٥٢)) .','1081','2295','0');
INSERT INTO [t22928] VALUES (2317,'قال تعالى: (إن أصحاب الجنة اليوم في شغل فاكهون (٥٥)) .','1081','2295','0');
INSERT INTO [t22928] VALUES (2318,'قال تعالى: (هم وأزواجهم في ظلال على الأرائك متكئون (٥٦) لهم فيها فاكهة ولهم ما يدعون (٥٧) سلام قولا من رب رحيم (٥٨)) .','1081','2295','0');
INSERT INTO [t22928] VALUES (2319,'قال تعالى: (ولقد أضل منكم جبلا كثيرا أفلم تكونوا تعقلون (٦٢)) .','1082','2295','0');
INSERT INTO [t22928] VALUES (2320,'قال تعالى: (وما علمناه الشعر وما ينبغي له إن هو إلا ذكر وقرآن مبين (٦٩) لينذر من كان حيا ويحق القول على الكافرين (٧٠)) .','1082','2295','0');
INSERT INTO [t22928] VALUES (2321,'قال تعالى: (وذللناها لهم فمنها ركوبهم ومنها يأكلون (٧٢))','1082','2295','0');
INSERT INTO [t22928] VALUES (2322,'قال تعالى: (وضرب لنا مثلا ونسي خلقه قال من يحيي العظام وهي رميم (٧٨)) .','1083','2295','0');
INSERT INTO [t22928] VALUES (2323,'قال تعالى: (إنما أمره إذا أراد شيئا أن يقول له كن فيكون (٨٢)) .','1083','2295','0');
INSERT INTO [t22928] VALUES (2324,'الصافات','1084','0','0');
INSERT INTO [t22928] VALUES (2325,'قال تعالى: (والصافات صفا (١) فالزاجرات زجرا (٢)) .','1084','2324','0');
INSERT INTO [t22928] VALUES (2326,'قال تعالى: (رب السماوات والأرض وما بينهما ورب المشارق (٥)) .','1084','2324','0');
INSERT INTO [t22928] VALUES (2327,'قال تعالى: (إنا زينا السماء الدنيا بزينة الكواكب (٦)) .','1084','2324','0');
INSERT INTO [t22928] VALUES (2328,'قال تعالى: (وحفظا من كل شيطان مارد (٧)) .','1085','2324','0');
INSERT INTO [t22928] VALUES (2329,'قال تعالى: (لا يسمعون إلى الملأ الأعلى ويقذفون من كل جانب (٨) دحورا ولهم عذاب واصب (٩) إلا من خطف الخطفة فأتبعه شهاب ثاقب (١٠)) .','1085','2324','0');
INSERT INTO [t22928] VALUES (2330,'قال تعالى: (بل عجبت ويسخرون (١٢)) .','1085','2324','0');
INSERT INTO [t22928] VALUES (2331,'قال تعالى: (احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون (٢٢) من دون الله فاهدوهم إلى صراط الجحيم (٢٣) وقفوهم إنهم مسئولون (٢٤) ما لكم لا تناصرون (٢٥) بل هم اليوم مستسلمون (٢٦) وأقبل بعضهم على بعض يتساءلون (٢٧)) .','1085','2324','0');
INSERT INTO [t22928] VALUES (2332,'قال تعالى: (إنكم لذائقو العذاب الأليم (٣٨)) .','1086','2324','0');
INSERT INTO [t22928] VALUES (2333,'قال تعالى: (أولئك لهم رزق معلوم (٤١) فواكه وهم مكرمون (٤٢) في جنات النعيم (٤٣) على سرر متقابلين (٤٤) يطاف عليهم بكأس من معين (٤٥) بيضاء لذة للشاربين (٤٦) لا فيها غول ولا هم عنها ينزفون (٤٧)) .','1086','2324','0');
INSERT INTO [t22928] VALUES (2334,'قال تعالى: (قال هل أنتم مطلعون (٥٤)) .','1086','2324','0');
INSERT INTO [t22928] VALUES (2335,'قال تعالى: (أفما نحن بميتين (٥٨) إلا موتتنا الأولى وما نحن بمعذبين (٥٩) إن هذا لهو الفوز العظيم (٦٠) لمثل هذا فليعمل العاملون (٦١) أذلك خير نزلا أم شجرة الزقوم (٦٢) إنا جعلناها فتنة للظالمين (٦٣) إنها شجرة تخرج في أصل الجحيم (٦٤) طلعها كأنه رءوس الشياطين (٦٥ فإنهم لآكلون منها فمالئون منها البطون (٦٦) ثم إن لهم عليها لشوبا من حميم (٦٧)) .','1087','2324','0');
INSERT INTO [t22928] VALUES (2336,'قال تعالى: (فانظر كيف كان عاقبة المنذرين (٧٣) إلا عباد الله المخلصين (٧٤) ولقد نادانا نوح فلنعم المجيبون (٧٥) ونجيناه وأهله من الكرب العظيم (٧٦) وجعلنا ذريته هم الباقين (٧٧) وتركنا عليه في الآخرين (٧٨) سلام على نوح في العالمين (٧٩) إنا كذلك نجزي المحسنين (٨٠)','1087','2324','0');
INSERT INTO [t22928] VALUES (2337,'قال تعالى: (إذ جاء ربه بقلب سليم (٨٤) إذ قال لأبيه وقومه ماذا تعبدون (٨٥) أئفكا آلهة دون الله تريدون (٨٦)) .','1088','2324','0');
INSERT INTO [t22928] VALUES (2338,'قال تعالى: (فراغ عليهم ضربا باليمين (٩٣) فأقبلوا إليه يزفون (٩٤)) .','1088','2324','0');
INSERT INTO [t22928] VALUES (2339,'قال تعالى: (والله خلقكم وما تعملون (٩٦) قالوا ابنوا له بنيانا فألقوه في الجحيم (٩٧)) .','1088','2324','0');
INSERT INTO [t22928] VALUES (2340,'قال تعالى: (فلما بلغ معه السعي قال يا بني إني أرى في المنام أني أذبحك فانظر ماذا ترى (١٠٢)) .','1089','2324','0');
INSERT INTO [t22928] VALUES (2341,'قال تعالى: (فلما أسلما وتله للجبين (١٠٣)) .','1089','2324','0');
INSERT INTO [t22928] VALUES (2342,'قال تعالى: (وبشرناه بإسحاق نبيا من الصالحين (١١٢)) .','1089','2324','0');
INSERT INTO [t22928] VALUES (2343,'قال تعالى: (وإن إلياس لمن المرسلين (١٢٣) إذ قال لقومه ألا تتقون (١٢٤)) .','1089','2324','0');
INSERT INTO [t22928] VALUES (2344,'قال تعالى: (الله ربكم ورب آبائكم الأولين (١٢٦)) .','1089','2324','0');
INSERT INTO [t22928] VALUES (2345,'قال تعالى: (سلام على إل ياسين (١٣٠)) .','1090','2324','0');
INSERT INTO [t22928] VALUES (2346,'قال تعالى: (وبالليل أفلا تعقلون (١٣٨)) .','1090','2324','0');
INSERT INTO [t22928] VALUES (2347,'قال تعالى: (فلولا أنه كان من المسبحين (١٤٣) للبث في بطنه إلى يوم يبعثون (١٤٤)) .','1090','2324','0');
INSERT INTO [t22928] VALUES (2348,'قال تعالى: (وأرسلناه إلى مائة ألف أو يزيدون (١٤٧)) .','1090','2324','0');
INSERT INTO [t22928] VALUES (2349,'قال تعالى: (أصطفى البنات على البنين (١٥٣)) .','1091','2324','0');
INSERT INTO [t22928] VALUES (2350,'قال تعالى: (ما لكم كيف تحكمون (١٥٤)) .','1091','2324','0');
INSERT INTO [t22928] VALUES (2351,'قال تعالى: (إلا عباد الله المخلصين (١٦٠)) .','1091','2324','0');
INSERT INTO [t22928] VALUES (2352,'قال تعالى: (فإنكم وما تعبدون (١٦١) ما أنتم عليه بفاتنين (١٦٢) إلا من هو صالي الجحيم (١٦٣) وما منا إلا له مقام معلوم (١٦٤)) .','1091','2324','0');
INSERT INTO [t22928] VALUES (2353,'ص','1093','0','0');
INSERT INTO [t22928] VALUES (2354,'قال تعالى: (ص والقرآن ذي الذكر (١) بل الذين كفروا في عزة وشقاق (٢) كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص (٣)) .','1093','2353','0');
INSERT INTO [t22928] VALUES (2355,'قال تعالى: (وانطلق الملأ منهم أن امشوا واصبروا على آلهتكم إن هذا لشيء يراد (٦)) .','1094','2353','0');
INSERT INTO [t22928] VALUES (2356,'قال تعالى: (أم لهم ملك السماوات والأرض وما بينهما فليرتقوا في الأسباب (١٠)) .','1094','2353','0');
INSERT INTO [t22928] VALUES (2357,'قال تعالى: (جند ما هنالك مهزوم من الأحزاب (١١)) .','1095','2353','0');
INSERT INTO [t22928] VALUES (2358,'قال تعالى: (وثمود وقوم لوط وأصحاب الأيكة أولئك الأحزاب (١٣)) .','1095','2353','0');
INSERT INTO [t22928] VALUES (2359,'قال تعالى: (وما ينظر هؤلاء إلا صيحة واحدة ما لها من فواق (١٥)) .','1095','2353','0');
INSERT INTO [t22928] VALUES (2360,'قال تعالى: (اصبر على ما يقولون واذكر عبدنا داود ذا الأيد إنه أواب (١٧) إنا سخرنا الجبال معه يسبحن بالعشي والإشراق (١٨)) .','1095','2353','0');
INSERT INTO [t22928] VALUES (2361,'قال تعالى: (وهل أتاك نبأ الخصم إذ تسوروا المحراب (٢١) إذ دخلوا على داود ففزع منهم قالوا لا تخف خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا إلى سواء الصراط (٢٢)) .','1095','2353','0');
INSERT INTO [t22928] VALUES (2362,'قال تعالى: (إن هذا أخي له تسع وتسعون نعجة ولي نعجة واحدة فقال أكفلنيها وعزني في الخطاب (٢٣) قال لقد ظلمك بسؤال نعجتك إلى نعاجه وإن كثيرا من الخلطاء ليبغي بعضهم على بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم وظن داود أنما فتناه فاستغفر ربه وخر راكعا وأناب (٢٤) فغفرنا له ذلك وإن له عندنا لزلفى وحسن مآب (٢٥)) .','1096','2353','0');
INSERT INTO [t22928] VALUES (2363,'قال تعالى: (يا داود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله إن الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب (٢٤)) .','1096','2353','0');
INSERT INTO [t22928] VALUES (2364,'قال تعالى: (وما خلقنا السماء والأرض وما بينهما باطلا ذلك ظن الذين كفروا فويل للذين كفروا من النار (٢٧)) .','1097','2353','0');
INSERT INTO [t22928] VALUES (2365,'قال تعالى: (كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب (٢٩)) .','1097','2353','0');
INSERT INTO [t22928] VALUES (2366,'قال تعالى: (ووهبنا لداود سليمان نعم العبد إنه أواب (٣٠)) .','1097','2353','0');
INSERT INTO [t22928] VALUES (2367,'قال تعالى: (إذ عرض عليه بالعشي الصافنات الجياد (٣١)) .','1097','2353','0');
INSERT INTO [t22928] VALUES (2368,'قال تعالى: (فقال إني أحببت حب الخير عن ذكر ربي حتى توارت بالحجاب (٣٢) ردوها علي فطفق مسحا بالسوق والأعناق (٣٣)) .','1097','2353','0');
INSERT INTO [t22928] VALUES (2369,'قال تعالى: (ولقد فتنا سليمان وألقينا على كرسيه جسدا ثم أناب (٣٤) قال رب اغفر لي وهب لي ملكا لا ينبغي لأحد من بعدي إنك أنت الوهاب (٣٥) فسخرنا له الريح تجري بأمره رخاء حيث أصاب (٣٦) والشياطين كل بناء وغواص (٣٧)) .','1098','2353','0');
INSERT INTO [t22928] VALUES (2370,'قال تعالى: (هذا عطاؤنا فامنن أو أمسك بغير حساب (٣٩)) .','1098','2353','0');
INSERT INTO [t22928] VALUES (2371,'قال تعالى: (وإن له عندنا لزلفى وحسن مآب (٤٠)) .','1098','2353','0');
INSERT INTO [t22928] VALUES (2372,'قال تعالى: (واذكر عبدنا أيوب إذ نادى ربه أني مسني الشيطان بنصب وعذاب (٤١)) .','1098','2353','0');
INSERT INTO [t22928] VALUES (2373,'قال تعالى: (ووهبنا له أهله ومثلهم معهم رحمة منا وذكرى لأولي الألباب (٤٣)) .','1099','2353','0');
INSERT INTO [t22928] VALUES (2374,'قال تعالى: (واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدي والأبصار (٤٥)) .','1099','2353','0');
INSERT INTO [t22928] VALUES (2375,'قال تعالى: (إنا أخلصناهم بخالصة ذكرى الدار (٤٦)) .','1099','2353','0');
INSERT INTO [t22928] VALUES (2376,'قال تعالى: (هذا ذكر وإن للمتقين لحسن مآب (٤٩) جنات عدن مفتحة لهم الأبواب (٥٠)) .','1100','2353','0');
INSERT INTO [t22928] VALUES (2377,'قال تعالى: (متكئين فيها يدعون فيها بفاكهة كثيرة وشراب (٥١)) .','1100','2353','0');
INSERT INTO [t22928] VALUES (2378,'قال تعالى: (هذا ما توعدون ليوم الحساب (٥٣)) .','1101','2353','0');
INSERT INTO [t22928] VALUES (2379,'قال تعالى: (إن هذا لرزقنا ما له من نفاد (٥٤)) .','1101','2353','0');
INSERT INTO [t22928] VALUES (2380,'قال تعالى: (هذا وإن للطاغين لشر مآب (٥٥) جهنم يصلونها فبئس المهاد (٥٦)) .','1101','2353','0');
INSERT INTO [t22928] VALUES (2381,'قال تعالى: (هذا فليذوقوه حميم وغساق (٥٧)) .','1101','2353','0');
INSERT INTO [t22928] VALUES (2382,'قال تعالى: (وآخر من شكله أزواج (٥٨) هذا فوج مقتحم معكم لا مرحبا بهم إنهم صالوا النار (٥٩)) .','1102','2353','0');
INSERT INTO [t22928] VALUES (2383,'قال تعالى: (قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار (٦١)) .','1102','2353','0');
INSERT INTO [t22928] VALUES (2384,'قال تعالى: (أتخذناهم سخريا أم زاغت عنهم الأبصار (٦٣)) .','1103','2353','0');
INSERT INTO [t22928] VALUES (2385,'قال تعالى: (إن ذلك لحق تخاصم أهل النار (٦٤)) .','1103','2353','0');
INSERT INTO [t22928] VALUES (2386,'قال تعالى: (رب السماوات والأرض وما بينهما العزيز الغفار (٦٦)) .','1103','2353','0');
INSERT INTO [t22928] VALUES (2387,'قال تعالى: (ما كان لي من علم بالملأ الأعلى إذ يختصمون (٦٩) إن يوحى إلي إلا أنما أنا نذير مبين (٧٠)) .','1104','2353','0');
INSERT INTO [t22928] VALUES (2388,'قال تعالى: (إذ قال ربك للملائكة إني خالق بشرا من طين (٧١)) .','1104','2353','0');
INSERT INTO [t22928] VALUES (2389,'قال تعالى: (قال فالحق والحق أقول (٨٤)) .','1104','2353','0');
INSERT INTO [t22928] VALUES (2390,'قال تعالى: (ولتعلمن نبأه بعد حين (٨٨)) .','1104','2353','0');
INSERT INTO [t22928] VALUES (2391,'الزمر','1105','0','0');
INSERT INTO [t22928] VALUES (2392,'قال تعالى: (تنزيل الكتاب من الله العزيز الحكيم (١) إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين (٢) ألا لله الدين الخالص والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون إن الله لا يهدي من هو كاذب كفار (٣)) .','1105','2391','0');
INSERT INTO [t22928] VALUES (2393,'قال تعالى: (خلق السماوات والأرض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لأجل مسمى ألا هو العزيز الغفار (٥) خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ذلكم الله ربكم له الملك لا إله إلا هو فأنى تصرفون (٦)) .','1105','2391','0');
INSERT INTO [t22928] VALUES (2394,'قال تعالى: (إن تكفروا فإن الله غني عنكم ولا يرضى لعباده الكفر وإن تشكروا يرضه لكم ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون إنه عليم بذات الصدور (٧) وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله قل تمتع بكفرك قليلا إنك من أصحاب النار (٨)) .','1106','2391','0');
INSERT INTO [t22928] VALUES (2395,'قال تعالى: (أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه قل هل يستوي الذين يعلمون والذين لا يعلمون إنما يتذكر أولو الألباب (٩) قل ياعبادي الذين آمنوا اتقوا ربكم للذين أحسنوا في هذه الدنيا حسنة وأرض الله واسعة إنما يوفى الصابرون أجرهم بغير حساب (١٠)) .','1106','2391','0');
INSERT INTO [t22928] VALUES (2396,'قال تعالى: (قل الله أعبد مخلصا له ديني (١٤)) .','1106','2391','0');
INSERT INTO [t22928] VALUES (2397,'قال تعالى: (لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده يا عباد فاتقون (١٦) والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله لهم البشرى فبشر عباد (١٧)) .','1106','2391','0');
INSERT INTO [t22928] VALUES (2398,'قال تعالى: (أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار (١٩) لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار وعد الله لا يخلف الله الميعاد (٢٠)) .','1107','2391','0');
INSERT INTO [t22928] VALUES (2399,'قال تعالى: (ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما إن في ذلك لذكرى لأولي الألباب (٢١)) .','1107','2391','0');
INSERT INTO [t22928] VALUES (2400,'قال تعالى: (أفمن شرح الله صدره للإسلام فهو على نور من ربه فويل للقاسية قلوبهم من ذكر الله أولئك في ضلال مبين (٢٢)) .','1107','2391','0');
INSERT INTO [t22928] VALUES (2401,'قال تعالى: (الله نزل أحسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلى ذكر الله ذلك هدى الله يهدي به من يشاء ومن يضلل الله فما له من هاد (٢٣)) .','1107','2391','0');
INSERT INTO [t22928] VALUES (2402,'قال تعالى: (ولقد ضربنا للناس في هذا القرآن من كل مثل لعلهم يتذكرون قرآنا عربيا غير ذي عوج لعلهم يتقون (٢٨)) .','1108','2391','0');
INSERT INTO [t22928] VALUES (2403,'قال تعالى: (ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا الحمد لله بل أكثرهم لا يعلمون (٢٩)) .','1108','2391','0');
INSERT INTO [t22928] VALUES (2404,'قال تعالى: (والذي جاء بالصدق وصدق به أولئك هم المتقون (٣٣)) .','1108','2391','0');
INSERT INTO [t22928] VALUES (2405,'قال تعالى: (ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته قل حسبي الله عليه يتوكل المتوكلون (٣٨)) .','1108','2391','0');
INSERT INTO [t22928] VALUES (2406,'قال تعالى: (قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون (٤٦)) .','1108','2391','0');
INSERT INTO [t22928] VALUES (2407,'قال تعالى: (فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته على علم بل هي فتنة ولكن أكثرهم لا يعلمون (٤٦)) .','1109','2391','0');
INSERT INTO [t22928] VALUES (2408,'قال تعالى: (أن تقول نفس يا حسرتا على ما فرطت في جنب الله وإن كنت لمن الساخرين (٥٦)) .','1109','2391','0');
INSERT INTO [t22928] VALUES (2409,'قال تعالى: (بلى قد جاءتك آياتي فكذبت بها واستكبرت وكنت من الكافرين (٥٩)) .','1109','2391','0');
INSERT INTO [t22928] VALUES (2410,'قال تعالى: (ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة أليس في جهنم مثوى للمتكبرين (٦٠)) .','1109','2391','0');
INSERT INTO [t22928] VALUES (2411,'قال تعالى: (وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون (٦١)) .','1109','2391','0');
INSERT INTO [t22928] VALUES (2412,'قال تعالى: (قل أفغير الله تأمروني أعبد أيها الجاهلون (٦٤)) .','1110','2391','0');
INSERT INTO [t22928] VALUES (2413,'قال تعالى: (وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه سبحانه وتعالى عما يشركون (٦٧)) .','1110','2391','0');
INSERT INTO [t22928] VALUES (2414,'قال تعالى: (وسيق الذين اتقوا ربهم إلى الجنة زمرا حتى إذا جاءوها وفتحت أبوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين (٧٣)) .','1111','2391','0');
INSERT INTO [t22928] VALUES (2415,'قال تعالى: (وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء فنعم أجر العاملين (٧٤) وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم وقضي بينهم بالحق وقيل الحمد لله رب العالمين (٧٥)) .','1111','2391','0');
INSERT INTO [t22928] VALUES (2416,'غافر','1112','0','0');
INSERT INTO [t22928] VALUES (2417,'قال تعالى: (حم (١) تنزيل الكتاب من الله العزيز العليم (٢)) .','1112','2416','0');
INSERT INTO [t22928] VALUES (2418,'قال تعالى: (غافر الذنب وقابل التوب شديد العقاب ذي الطول لا إله إلا هو إليه المصير (٣)) .','1112','2416','0');
INSERT INTO [t22928] VALUES (2419,'قال تعالى: (وكذلك حقت كلمة ربك على الذين كفروا أنهم أصحاب النار (٦)) .','1112','2416','0');
INSERT INTO [t22928] VALUES (2420,'قال تعالى: (الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين آمنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم (٧)) .','1112','2416','0');
INSERT INTO [t22928] VALUES (2421,'قال تعالى: (ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم إنك أنت العزيز الحكيم (٨)) .','1113','2416','0');
INSERT INTO [t22928] VALUES (2422,'قال تعالى: (إن الذين كفروا ينادون لمقت الله أكبر من مقتكم أنفسكم إذ تدعون إلى الإيمان فتكفرون (١٠)) .','1113','2416','0');
INSERT INTO [t22928] VALUES (2423,'قال تعالى: (ذلكم بأنه إذا دعي الله وحده كفرتم وإن يشرك به تؤمنوا فالحكم لله العلي الكبير (١٢)) .','1113','2416','0');
INSERT INTO [t22928] VALUES (2424,'قال تعالى: (رفيع الدرجات ذو العرش يلقي الروح من أمره على من يشاء من عباده لينذر يوم التلاق (١٥)) .','1113','2416','0');
INSERT INTO [t22928] VALUES (2425,'قال تعالى: (يوم هم بارزون لا يخفى على الله منهم شيء لمن الملك اليوم لله الواحد القهار اليوم تجزى كل نفس بما كسبت لا ظلم اليوم إن الله سريع الحساب (١٧))','1114','2416','0');
INSERT INTO [t22928] VALUES (2426,'قال تعالى: (وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين ما للظالمين من حميم ولا شفيع يطاع (١٨)) .','1114','2416','0');
INSERT INTO [t22928] VALUES (2427,'قال تعالى: (وقال فرعون ذروني أقتل موسى وليدع ربه إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد (٢٦)) .','1115','2416','0');
INSERT INTO [t22928] VALUES (2428,'قال تعالى: (وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم وإن يك كاذبا فعليه كذبه وإن يك صادقا يصبكم بعض الذي يعدكم إن الله لا يهدي من هو مسرف كذاب (٢٨)) .','1115','2416','0');
INSERT INTO [t22928] VALUES (2429,'قال تعالى: (يا قوم لكم الملك اليوم ظاهرين في الأرض فمن ينصرنا من بأس الله إن جاءنا قال فرعون ما أريكم إلا ما أرى وما أهديكم إلا سبيل الرشاد (٢٩)) .','1115','2416','0');
INSERT INTO [t22928] VALUES (2430,'قال تعالى: (ويا قوم إني أخاف عليكم يوم التناد (٣٢) يوم تولون مدبرين ما لكم من الله من عاصم ومن يضلل الله فما له من هاد (٣٣)) .','1116','2416','0');
INSERT INTO [t22928] VALUES (2431,'قال تعالى: (الذين يجادلون في آيات الله بغير سلطان أتاهم كبر مقتا عند الله وعند الذين آمنوا كذلك يطبع الله على كل قلب متكبر جبار (٣٥)) .','1116','2416','0');
INSERT INTO [t22928] VALUES (2432,'قال تعالى: (وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب (٣٦) أسباب السماوات فأطلع إلى إله موسى وإني لأظنه كاذبا وكذلك زين لفرعون سوء عمله وصد عن السبيل وما كيد فرعون إلا في تباب (٣٧)) .','1117','2416','0');
INSERT INTO [t22928] VALUES (2433,'قال تعالى: (ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار (٤١) تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز الغفار (٤٢)) .','1117','2416','0');
INSERT INTO [t22928] VALUES (2434,'قال تعالى: (فستذكرون ما أقول لكم وأفوض أمري إلى الله إن الله بصير بالعباد (٤٤)) .','1117','2416','0');
INSERT INTO [t22928] VALUES (2435,'قال تعالى: (النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة أدخلوا آل فرعون أشد العذاب (٤٦)) .','1117','2416','0');
INSERT INTO [t22928] VALUES (2436,'قال تعالى: (وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار (٤٧)) .','1118','2416','0');
INSERT INTO [t22928] VALUES (2437,'قال تعالى: (وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب (٤٩)) .','1118','2416','0');
INSERT INTO [t22928] VALUES (2438,'قال تعالى: (يوم لا ينفع الظالمين معذرتهم ولهم اللعنة ولهم سوء الدار (٥٢)) .','1118','2416','0');
INSERT INTO [t22928] VALUES (2439,'قال تعالى: (وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء قليلا ما تتذكرون (٥٨)) .','1118','2416','0');
INSERT INTO [t22928] VALUES (2440,'قال تعالى: (الذين كذبوا بالكتاب وبما أرسلنا به رسلنا فسوف يعلمون إذ الأغلال في أعناقهم والسلاسل يسحبون (٧١)) .','1119','2416','0');
INSERT INTO [t22928] VALUES (2441,'قال تعالى: (ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك وما كان لرسول أن يأتي بآية إلا بإذن الله فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون (٧٨)) .','1119','2416','0');
INSERT INTO [t22928] VALUES (2442,'قال تعالى: (ويريكم آياته فأي آيات الله تنكرون (٨١)) .','1119','2416','0');
INSERT INTO [t22928] VALUES (2443,'قال تعالى: (فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون (٨٣)) .','1119','2416','0');
INSERT INTO [t22928] VALUES (2444,'قال تعالى: (فلم يك ينفعهم إيمانهم لما رأوا بأسنا سنة الله التي قد خلت في عباده وخسر هنالك الكافرون (٨٥)) .','1119','2416','0');
INSERT INTO [t22928] VALUES (2445,'فصلت','1120','0','0');
INSERT INTO [t22928] VALUES (2446,'قال تعالى: (حم (١) تنزيل من الرحمن الرحيم (٢) كتاب فصلت آياته قرآنا عربيا لقوم يعلمون (٣)) .','1120','2445','0');
INSERT INTO [t22928] VALUES (2447,'قال تعالى: (وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب فاعمل إننا عاملون (٥)) .','1120','2445','0');
INSERT INTO [t22928] VALUES (2448,'قال تعالى: (إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون (٨)) .','1120','2445','0');
INSERT INTO [t22928] VALUES (2449,'قال تعالى: (وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام سواء للسائلين (١٠)) .','1120','2445','0');
INSERT INTO [t22928] VALUES (2450,'قال تعالى: (ثم استوى إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين (١١)) .','1121','2445','0');
INSERT INTO [t22928] VALUES (2451,'قال تعالى: (فقضاهن سبع سماوات في يومين وأوحى في كل سماء أمرها وزينا السماء الدنيا بمصابيح وحفظا ذلك تقدير العزيز العليم (١٢)) (وحفظا) أي وحفظناها حفظا، أو للحفظ.','1121','2445','0');
INSERT INTO [t22928] VALUES (2452,'قال تعالى: (إذ جاءتهم الرسل من بين أيديهم ومن خلفهم ألا تعبدوا إلا الله قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون (١٤)) .','1121','2445','0');
INSERT INTO [t22928] VALUES (2453,'قال تعالى: (فأرسلنا عليهم ريحا صرصرا في أيام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ولعذاب الآخرة أخزى وهم لا ينصرون (١٦)) .','1121','2445','0');
INSERT INTO [t22928] VALUES (2454,'قال تعالى: (وأما ثمود فهديناهم فاستحبوا العمى على الهدى فأخذتهم صاعقة العذاب الهون بما كانوا يكسبون (١٧))','1122','2445','0');
INSERT INTO [t22928] VALUES (2455,'قال تعالى: (ويوم يحشر أعداء الله إلى النار فهم يوزعون (١٩))','1122','2445','0');
INSERT INTO [t22928] VALUES (2456,'قال تعالى: (وما كنتم تستترون أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون (٢٢)) .','1122','2445','0');
INSERT INTO [t22928] VALUES (2457,'قال تعالى: (وذلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين (٢٣)) .','1122','2445','0');
INSERT INTO [t22928] VALUES (2458,'قال تعالى: (فإن يصبروا فالنار مثوى لهم وإن يستعتبوا فما هم من المعتبين (٢٤)) .','1123','2445','0');
INSERT INTO [t22928] VALUES (2459,'قال تعالى: (وقال الذين كفروا لا تسمعوا لهذا القرآن والغوا فيه لعلكم تغلبون (٢٦)) .','1123','2445','0');
INSERT INTO [t22928] VALUES (2460,'قال تعالى: (ذلك جزاء أعداء الله النار لهم فيها دار الخلد جزاء بما كانوا بآياتنا يجحدون (٢٨)) .','1123','2445','0');
INSERT INTO [t22928] VALUES (2461,'قال تعالى: (إن الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة ألا تخافوا ولا تحزنوا وأبشروا بالجنة التي كنتم توعدون (٣٠)) .','1123','2445','0');
INSERT INTO [t22928] VALUES (2462,'قال تعالى: (نحن أولياؤكم في الحياة الدنيا وفي الآخرة ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون (٣١) نزلا من غفور رحيم (٣٢))','1124','2445','0');
INSERT INTO [t22928] VALUES (2463,'قال تعالى: (ولا تستوي الحسنة ولا السيئة ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم (٣٤) وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم (٣٥)) .','1124','2445','0');
INSERT INTO [t22928] VALUES (2464,'قال تعالى: (ومن آياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن إن كنتم إياه تعبدون (٣٧)) .','1124','2445','0');
INSERT INTO [t22928] VALUES (2465,'قال تعالى: (إن الذين كفروا بالذكر لما جاءهم وإنه لكتاب عزيز (٤١)) .','1124','2445','0');
INSERT INTO [t22928] VALUES (2466,'قال تعالى: (ولو جعلناه قرآنا أعجميا لقالوا لولا فصلت آياته أأعجمي وعربي قل هو للذين آمنوا هدى وشفاء والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى أولئك ينادون من مكان بعيد (٤٤)) .','1125','2445','0');
INSERT INTO [t22928] VALUES (2467,'قال تعالى: (من عمل صالحا فلنفسه ومن أساء فعليها وما ربك بظلام للعبيد (٤٦)) .','1125','2445','0');
INSERT INTO [t22928] VALUES (2468,'قال تعالى: (إليه يرد علم الساعة وما تخرج من ثمرات من أكمامها وما تحمل من أنثى ولا تضع إلا بعلمه ويوم يناديهم أين شركائي قالوا آذناك ما منا من شهيد (٤٧) وضل عنهم ما كانوا يدعون من قبل وظنوا ما لهم من محيص (٤٨))','1125','2445','0');
INSERT INTO [t22928] VALUES (2469,'قال تعالى: (لا يسأم الإنسان من دعاء الخير وإن مسه الشر فيئوس قنوط (٤٩) ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ (٥٠)) .','1126','2445','0');
INSERT INTO [t22928] VALUES (2470,'قال تعالى: (سنريهم آياتنا في الآفاق وفي أنفسهم حتى يتبين لهم أنه الحق أولم يكف بربك أنه على كل شيء شهيد (٥٣)) .','1126','2445','0');
INSERT INTO [t22928] VALUES (2471,'الشورى','1127','0','0');
INSERT INTO [t22928] VALUES (2472,'قال تعالى: (كذلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم (٣) له ما في السماوات وما في الأرض وهو العلي العظيم (٤)) .','1127','2471','0');
INSERT INTO [t22928] VALUES (2473,'قال تعالى: (وكذلك أوحينا إليك قرآنا عربيا لتنذر أم القرى ومن حولها وتنذر يوم الجمع لا ريب فيه فريق في الجنة وفريق في السعير (٧)) .','1127','2471','0');
INSERT INTO [t22928] VALUES (2474,'قال تعالى: (ولو شاء الله لجعلهم أمة واحدة ولكن يدخل من يشاء في رحمته والظالمون ما لهم من ولي ولا نصير (٨)) .','1127','2471','0');
INSERT INTO [t22928] VALUES (2475,'قال تعالى: (وما اختلفتم فيه من شيء فحكمه إلى الله ذلكم الله ربي عليه توكلت وإليه أنيب (١٠)) .','1128','2471','0');
INSERT INTO [t22928] VALUES (2476,'قال تعالى: (فاطر السماوات والأرض جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا يذرؤكم فيه ليس كمثله شيء وهو السميع البصير (١١)) .','1128','2471','0');
INSERT INTO [t22928] VALUES (2477,'قال تعالى: (شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين ولا تتفرقوا فيه كبر على المشركين ما تدعوهم إليه الله يجتبي إليه من يشاء ويهدي إليه من ينيب (١٣)) .','1128','2471','0');
INSERT INTO [t22928] VALUES (2478,'قال تعالى: (الله الذي أنزل الكتاب بالحق والميزان وما يدريك لعل الساعة قريب (١٧)) .','1129','2471','0');
INSERT INTO [t22928] VALUES (2479,'قال تعالى: (ترى الظالمين مشفقين مما كسبوا وهو واقع بهم والذين آمنوا وعملوا الصالحات في روضات الجنات لهم ما يشاءون عند ربهم ذلك هو الفضل الكبير (٢٢)) .','1129','2471','0');
INSERT INTO [t22928] VALUES (2480,'قال تعالى: (ذلك الذي يبشر الله عباده الذين آمنوا وعملوا الصالحات قل لا أسألكم عليه أجرا إلا المودة في القربى ومن يقترف حسنة نزد له فيها حسنا إن الله غفور شكور (٢٣)) .','1129','2471','0');
INSERT INTO [t22928] VALUES (2481,'قال تعالى: (أم يقولون افترى على الله كذبا فإن يشأ الله يختم على قلبك ويمح الله الباطل ويحق الحق بكلماته إنه عليم بذات الصدور (٢٤)) .','1129','2471','0');
INSERT INTO [t22928] VALUES (2482,'قال تعالى: (ويستجيب الذين آمنوا وعملوا الصالحات ويزيدهم من فضله والكافرون لهم عذاب شديد (٢٦)) .','1129','2471','0');
INSERT INTO [t22928] VALUES (2483,'قال تعالى: (ومن آياته خلق السماوات والأرض وما بث فيهما من دابة وهو على جمعهم إذا يشاء قدير (٢٩)) .','1130','2471','0');
INSERT INTO [t22928] VALUES (2484,'قال تعالى: (وما أصابكم من مصيبة فبما كسبت أيديكم ويعفو عن كثير (٣٠)) .','1130','2471','0');
INSERT INTO [t22928] VALUES (2485,'قال تعالى: (ومن آياته الجوار في البحر كالأعلام (٣٢) إن يشأ يسكن الريح فيظللن رواكد على ظهره إن في ذلك لآيات لكل صبار شكور (٣٣) أو يوبقهن بما كسبوا ويعف عن كثير (٣٤) ويعلم الذين يجادلون في آياتنا ما لهم من محيص (٣٥)) .','1130','2471','0');
INSERT INTO [t22928] VALUES (2486,'قال تعالى: (فما أوتيتم من شيء فمتاع الحياة الدنيا وما عند الله خير وأبقى للذين آمنوا وعلى ربهم يتوكلون (٣٦)) .','1131','2471','0');
INSERT INTO [t22928] VALUES (2487,'قال تعالى: (والذين يجتنبون كبائر الإثم والفواحش وإذا ما غضبوا هم يغفرون (٣٧)) .','1131','2471','0');
INSERT INTO [t22928] VALUES (2488,'قال تعالى: (ولمن صبر وغفر إن ذلك لمن عزم الأمور (٤٣)) .','1132','2471','0');
INSERT INTO [t22928] VALUES (2489,'قال تعالى: (وما كان لهم من أولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل (٤٦)) .','1132','2471','0');
INSERT INTO [t22928] VALUES (2490,'قال تعالى: (فإن أعرضوا فما أرسلناك عليهم حفيظا إن عليك إلا البلاغ وإنا إذا أذقنا الإنسان منا رحمة فرح بها وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور (٤٨)) .','1132','2471','0');
INSERT INTO [t22928] VALUES (2491,'قال تعالى: (أو يزوجهم ذكرانا وإناثا ويجعل من يشاء عقيما إنه عليم قدير (٥٠)) .','1132','2471','0');
INSERT INTO [t22928] VALUES (2492,'قال تعالى: (وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب أو يرسل رسولا فيوحي بإذنه ما يشاء إنه علي حكيم (٥١)) .','1132','2471','0');
INSERT INTO [t22928] VALUES (2493,'قال تعالى: (وكذلك أوحينا إليك روحا من أمرنا ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا وإنك لتهدي إلى صراط مستقيم (٥٢)) .','1133','2471','0');
INSERT INTO [t22928] VALUES (2494,'قال تعالى: (صراط الله الذي له ما في السماوات وما في الأرض ألا إلى الله تصير الأمور (٥٣)) .','1133','2471','0');
INSERT INTO [t22928] VALUES (2495,'الزخرف','1134','0','0');
INSERT INTO [t22928] VALUES (2496,'قال تعالى: (حم (١) والكتاب المبين (٢)) .','1134','2495','0');
INSERT INTO [t22928] VALUES (2497,'قال تعالى: (إنا جعلناه قرآنا عربيا لعلكم تعقلون (٣) وإنه في أم الكتاب لدينا لعلي حكيم (٤) أفنضرب عنكم الذكر صفحا أن كنتم قوما مسرفين (٥))','1134','2495','0');
INSERT INTO [t22928] VALUES (2498,'قال تعالى: (وكم أرسلنا من نبي في الأولين (٦) وما يأتيهم من نبي إلا كانوا به يستهزئون (٧) فأهلكنا أشد منهم بطشا ومضى مثل الأولين (٨)) .','1134','2495','0');
INSERT INTO [t22928] VALUES (2499,'قال تعالى: (وإذا بشر أحدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم (١٧)) .','1135','2495','0');
INSERT INTO [t22928] VALUES (2500,'قال تعالى: (قال أولو جئتكم بأهدى مما وجدتم عليه آباءكم قالوا إنا بما أرسلتم به كافرون (٢٤)) .','1135','2495','0');
INSERT INTO [t22928] VALUES (2501,'قال تعالى: (وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون (٢٦)) .','1135','2495','0');
INSERT INTO [t22928] VALUES (2502,'قال تعالى: (وقالوا لولا نزل هذا القرآن على رجل من القريتين عظيم (٣١)) .','1136','2495','0');
INSERT INTO [t22928] VALUES (2503,'قال تعالى: (ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون (٣٣)) .','1136','2495','0');
INSERT INTO [t22928] VALUES (2504,'قال تعالى: (حتى إذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين (٣٨)) .','1136','2495','0');
INSERT INTO [t22928] VALUES (2505,'قال تعالى: (ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون (٣٩)) .','1136','2495','0');
INSERT INTO [t22928] VALUES (2506,'قال تعالى: (أم أنا خير من هذا الذي هو مهين ولا يكاد يبين (٥٢) فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين (٥٣)) .','1137','2495','0');
INSERT INTO [t22928] VALUES (2507,'قال تعالى: (فجعلناهم سلفا ومثلا للآخرين (٥٦)) .','1137','2495','0');
INSERT INTO [t22928] VALUES (2508,'قال تعالى: (ولما ضرب ابن مريم مثلا إذا قومك منه يصدون (٥٧)) .','1138','2495','0');
INSERT INTO [t22928] VALUES (2509,'قال تعالى: (ولو نشاء لجعلنا منكم ملائكة في الأرض يخلفون (٦٠)) .','1138','2495','0');
INSERT INTO [t22928] VALUES (2510,'قال تعالى: (هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون (٦٦)) .','1138','2495','0');
INSERT INTO [t22928] VALUES (2511,'قال تعالى {يطاف عليهم بصحاف من ذهب وأكواب}','1138','2495','0');
INSERT INTO [t22928] VALUES (2512,'قال تعالى: (لا يفتر عنهم وهم فيه مبلسون (٧٥)) .','1138','2495','0');
INSERT INTO [t22928] VALUES (2513,'قال تعالى: (ونادوا يا مالك ليقض علينا ربك قال إنكم ماكثون (٧٧)) .','1138','2495','0');
INSERT INTO [t22928] VALUES (2514,'قال تعالى: (قل إن كان للرحمن ولد فأنا أول العابدين (٨١)) .','1139','2495','0');
INSERT INTO [t22928] VALUES (2515,'قال تعالى: (وهو الذي في السماء إله وفي الأرض إله وهو الحكيم العليم (٨٤)) .','1139','2495','0');
INSERT INTO [t22928] VALUES (2516,'قال تعالى: (وقيله يا رب إن هؤلاء قوم لا يؤمنون (٨٨)) .','1139','2495','0');
INSERT INTO [t22928] VALUES (2517,'الدخان','1141','0','0');
INSERT INTO [t22928] VALUES (2518,'قال تعالى: (إنا أنزلناه في ليلة مباركة إنا كنا منذرين (٣)) .','1141','2517','0');
INSERT INTO [t22928] VALUES (2519,'قال تعالى: (فيها يفرق كل أمر حكيم (٤)) .','1141','2517','0');
INSERT INTO [t22928] VALUES (2520,'قال تعالى: (أمرا من عندنا إنا كنا مرسلين (٥)) .','1141','2517','0');
INSERT INTO [t22928] VALUES (2521,'قال تعالى: (رحمة من ربك إنه هو السميع العليم (٦)) .','1141','2517','0');
INSERT INTO [t22928] VALUES (2522,'قال تعالى: (رب السماوات والأرض وما بينهما إن كنتم موقنين (٧)) .','1142','2517','0');
INSERT INTO [t22928] VALUES (2523,'قال تعالى: (لا إله إلا هو يحيي ويميت ربكم ورب آبائكم الأولين (٨)) .','1142','2517','0');
INSERT INTO [t22928] VALUES (2524,'قال تعالى: (فارتقب يوم تأتي السماء بدخان مبين (١٠)) .','1142','2517','0');
INSERT INTO [t22928] VALUES (2525,'قال تعالى: (يغشى الناس هذا عذاب أليم (١١) ربنا اكشف عنا العذاب إنا مؤمنون (١٢) أنى لهم الذكرى وقد جاءهم رسول مبين (١٣)) .','1142','2517','0');
INSERT INTO [t22928] VALUES (2526,'قال تعالى: (إنا كاشفوا العذاب قليلا إنكم عائدون (١٥) يوم نبطش البطشة الكبرى إنا منتقمون (١٦)) .','1143','2517','0');
INSERT INTO [t22928] VALUES (2527,'قال تعالى: (أن أدوا إلي عباد الله إني لكم رسول أمين (١٨)) .','1143','2517','0');
INSERT INTO [t22928] VALUES (2528,'قال تعالى: (وإني عذت بربي وربكم أن ترجمون (٢٠) وإن لم تؤمنوا لي فاعتزلون (٢١) فدعا ربه أن هؤلاء قوم مجرمون (٢٢)) .','1143','2517','0');
INSERT INTO [t22928] VALUES (2529,'قال تعالى: (واترك البحر رهوا إنهم جند مغرقون (٢٤) كم تركوا من جنات وعيون (٢٥) وزروع ومقام كريم (٢٦) ونعمة كانوا فيها فاكهين (٢٧) كذلك وأورثناها قوما آخرين (٢٨)) .','1143','2517','0');
INSERT INTO [t22928] VALUES (2530,'قال تعالى: (ولقد نجينا بني إسرائيل من العذاب المهين (٣٠) من فرعون إنه كان عاليا من المسرفين (٣١) ولقد اخترناهم على علم على العالمين (٣٢)) .','1144','2517','0');
INSERT INTO [t22928] VALUES (2531,'قال تعالى: (أهم خير أم قوم تبع والذين من قبلهم أهلكناهم إنهم كانوا مجرمين (٣٧) وما خلقنا السماوات والأرض وما بينهما لاعبين (٣٨)) .','1144','2517','0');
INSERT INTO [t22928] VALUES (2532,'قال تعالى: (إن يوم الفصل ميقاتهم أجمعين (٤٠) يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون (٤١)) .','1144','2517','0');
INSERT INTO [t22928] VALUES (2533,'قال تعالى: (إلا من رحم الله إنه هو العزيز الرحيم (٤١)) .','1144','2517','0');
INSERT INTO [t22928] VALUES (2534,'قال تعالى: (كالمهل يغلي في البطون (٤٥) كغلي الحميم (٤٦) خذوه فاعتلوه إلى سواء الجحيم (٤٧)) .','1145','2517','0');
INSERT INTO [t22928] VALUES (2535,'قال تعالى: (ذق إنك أنت العزيز الكريم (٤٩) إن هذا ما كنتم به تمترون (٥٠) إن المتقين في مقام أمين (٥١) في جنات وعيون (٥٢) يلبسون من سندس وإستبرق متقابلين (٥٣) كذلك وزوجناهم بحور عين (٥٤) يدعون فيها بكل فاكهة آمنين (٥٥) لا يذوقون فيها الموت إلا الموتة الأولى ووقاهم عذاب الجحيم (٥٦) فضلا من ربك ذلك هو الفوز العظيم (٥٧)) .','1145','2517','0');
INSERT INTO [t22928] VALUES (2536,'الجاثية','1147','0','0');
INSERT INTO [t22928] VALUES (2537,'قال تعالى: (وفي خلقكم وما يبث من دابة آيات لقوم يوقنون (٤) واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون (٥)) .','1147','2536','0');
INSERT INTO [t22928] VALUES (2538,'قال تعالى: (تلك آيات الله نتلوها عليك بالحق فبأي حديث بعد الله وآياته يؤمنون (٦)) .','1147','2536','0');
INSERT INTO [t22928] VALUES (2539,'قال تعالى: (ويل لكل أفاك أثيم (٧) يسمع آيات الله تتلى عليه ثم يصر مستكبرا كأن لم يسمعها فبشره بعذاب أليم (٨)) .','1148','2536','0');
INSERT INTO [t22928] VALUES (2540,'قال تعالى: (من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ولهم عذاب عظيم (١٠) هذا هدى والذين كفروا بآيات ربهم لهم عذاب من رجز أليم (١١)) .','1148','2536','0');
INSERT INTO [t22928] VALUES (2541,'قال تعالى: (وسخر لكم ما في السماوات وما في الأرض جميعا منه إن في ذلك لآيات لقوم يتفكرون (١٣)) .','1148','2536','0');
INSERT INTO [t22928] VALUES (2542,'قال تعالى: (قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون (١٤)) .','1148','2536','0');
INSERT INTO [t22928] VALUES (2543,'قال تعالى: (أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ساء ما يحكمون (٢١)) .','1149','2536','0');
INSERT INTO [t22928] VALUES (2544,'قال تعالى: (أفرأيت من اتخذ إلهه هواه وأضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله أفلا تذكرون (٢٣)) .','1149','2536','0');
INSERT INTO [t22928] VALUES (2545,'قال تعالى: (ولله ملك السماوات والأرض ويوم تقوم الساعة يومئذ يخسر المبطلون (٢٧)) .','1150','2536','0');
INSERT INTO [t22928] VALUES (2546,'قال تعالى: (وترى كل أمة جاثية كل أمة تدعى إلى كتابها اليوم تجزون ما كنتم تعملون (٢٨)) .','1150','2536','0');
INSERT INTO [t22928] VALUES (2547,'قال تعالى: (هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون (٢٩)) .','1150','2536','0');
INSERT INTO [t22928] VALUES (2548,'قال تعالى: (وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين (٣٢)) .','1150','2536','0');
INSERT INTO [t22928] VALUES (2549,'قال تعالى: (وله الكبرياء في السماوات والأرض وهو العزيز الحكيم) .','1150','2536','0');
INSERT INTO [t22928] VALUES (2550,'الأحقاف','1151','0','0');
INSERT INTO [t22928] VALUES (2551,'قال تعالى: (قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين (٤)) .','1151','2550','0');
INSERT INTO [t22928] VALUES (2552,'قال تعالى: (ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون (٥)) .','1151','2550','0');
INSERT INTO [t22928] VALUES (2553,'قال تعالى: (قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم إن أتبع إلا ما يوحى إلي وما أنا إلا نذير مبين (٩)) .','1151','2550','0');
INSERT INTO [t22928] VALUES (2554,'قال تعالى: (قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم إن الله لا يهدي القوم الظالمين (١٠)) .','1151','2550','0');
INSERT INTO [t22928] VALUES (2555,'قال تعالى: (وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه وإذ لم يهتدوا به فسيقولون هذا إفك قديم (١١)) .','1152','2550','0');
INSERT INTO [t22928] VALUES (2556,'قال تعالى: (ومن قبله كتاب موسى إماما ورحمة وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين (١٢)) .','1152','2550','0');
INSERT INTO [t22928] VALUES (2557,'قال تعالى: (إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون (١٣) أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون (١٤)) .','1152','2550','0');
INSERT INTO [t22928] VALUES (2558,'قال تعالى: (ووصينا الإنسان بوالديه إحسانا حملته أمه كرها ووضعته كرها وحمله وفصاله ثلاثون شهرا حتى إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي إني تبت إليك وإني من المسلمين (١٥)) .','1153','2550','0');
INSERT INTO [t22928] VALUES (2559,'قال تعالى: (أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة وعد الصدق الذي كانوا يوعدون (١٦) والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هذا إلا أساطير الأولين (١٧) أولئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين (١٨)) .','1153','2550','0');
INSERT INTO [t22928] VALUES (2560,'قال تعالى: (ولكل درجات مما عملوا وليوفيهم أعمالهم وهم لا يظلمون (١٩)) .','1154','2550','0');
INSERT INTO [t22928] VALUES (2561,'قال تعالى: (ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون (٢٠)) .','1154','2550','0');
INSERT INTO [t22928] VALUES (2562,'قال تعالى: (فلما رأوه عارضا مستقبل أوديتهم قالوا هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب أليم (٢٤) تدمر كل شيء بأمر ربها فأصبحوا لا يرى إلا مساكنهم كذلك نجزي القوم المجرمين (٢٥)) .','1154','2550','0');
INSERT INTO [t22928] VALUES (2563,'قال تعالى: (ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون (٢٦)) .','1155','2550','0');
INSERT INTO [t22928] VALUES (2564,'قال تعالى: (فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة بل ضلوا عنهم وذلك إفكهم وما كانوا يفترون (٢٨)) .','1155','2550','0');
INSERT INTO [t22928] VALUES (2565,'قال تعالى: (وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا فلما قضي ولوا إلى قومهم منذرين (٢٩))','1155','2550','0');
INSERT INTO [t22928] VALUES (2566,'قال تعالى: (أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى بلى إنه على كل شيء قدير (٣٣)) .','1156','2550','0');
INSERT INTO [t22928] VALUES (2567,'قال تعالى: (فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار بلاغ فهل يهلك إلا القوم الفاسقون (٣٥)) .','1156','2550','0');
INSERT INTO [t22928] VALUES (2568,'محمد','1157','0','0');
INSERT INTO [t22928] VALUES (2569,'قال تعالى: (الذين كفروا وصدوا عن سبيل الله أضل أعمالهم (١) والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم وأصلح بالهم (٢))','1157','2568','0');
INSERT INTO [t22928] VALUES (2570,'قال تعالى: (فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض والذين قتلوا في سبيل الله فلن يضل أعمالهم (٤)) .','1157','2568','0');
INSERT INTO [t22928] VALUES (2571,'قال تعالى: (ويدخلهم الجنة عرفها لهم (٦)) .','1157','2568','0');
INSERT INTO [t22928] VALUES (2572,'قال تعالى: (والذين كفروا فتعسا لهم وأضل أعمالهم (٨) ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم (٩) أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين أمثالها (١٠)) .','1157','2568','0');
INSERT INTO [t22928] VALUES (2573,'قال تعالى: (وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم (١٣)) .','1158','2568','0');
INSERT INTO [t22928] VALUES (2574,'قال تعالى: (أفمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم (١٤)) .','1158','2568','0');
INSERT INTO [t22928] VALUES (2575,'قال تعالى: (مثل الجنة التي وعد المتقون فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ولهم فيها من كل الثمرات ومغفرة من ربهم كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم (١٥)) .','1158','2568','0');
INSERT INTO [t22928] VALUES (2576,'قال تعالى: (ومنهم من يستمع إليك حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا أولئك الذين طبع الله على قلوبهم واتبعوا أهواءهم (١٦) والذين اهتدوا زادهم هدى وآتاهم تقواهم (١٧)) .','1159','2568','0');
INSERT INTO [t22928] VALUES (2577,'قال تعالى: (فهل ينظرون إلا الساعة أن تأتيهم بغتة فقد جاء أشراطها فأنى لهم إذا جاءتهم ذكراهم (١٨)) .','1159','2568','0');
INSERT INTO [t22928] VALUES (2578,'قال تعالى: (ويقول الذين آمنوا لولا نزلت سورة فإذا أنزلت سورة محكمة وذكر فيها القتال رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت فأولى لهم (٢٠) طاعة وقول معروف فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم (٢١) فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم (٢٢)) .','1159','2568','0');
INSERT INTO [t22928] VALUES (2579,'قال تعالى: (أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم (٢٣)) .','1160','2568','0');
INSERT INTO [t22928] VALUES (2580,'قال تعالى: (إن الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى الشيطان سول لهم وأملى لهم (٢٥)) .','1160','2568','0');
INSERT INTO [t22928] VALUES (2581,'قال تعالى: (فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم (٢٧)) .','1161','2568','0');
INSERT INTO [t22928] VALUES (2582,'قال تعالى: (هاأنتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ومن يبخل فإنما يبخل عن نفسه والله الغني وأنتم الفقراء وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم (٣٨)) .','1161','2568','0');
INSERT INTO [t22928] VALUES (2583,'الفتح','1162','0','0');
INSERT INTO [t22928] VALUES (2584,'قال تعالى: (ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما (٥) ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم وأعد لهم جهنم وساءت مصيرا (٦)) .','1162','2583','0');
INSERT INTO [t22928] VALUES (2585,'قال تعالى: (لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا (٩)) .','1162','2583','0');
INSERT INTO [t22928] VALUES (2586,'قال تعالى: (إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم فمن نكث فإنما ينكث على نفسه ومن أوفى بما عاهد عليه الله فسيؤتيه أجرا عظيما (١٠)) .','1162','2583','0');
INSERT INTO [t22928] VALUES (2587,'قال تعالى: (سيقول المخلفون إذا انطلقتم إلى مغانم لتأخذوها ذرونا نتبعكم يريدون أن يبدلوا كلام الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا لا يفقهون إلا قليلا (١٥)) .','1163','2583','0');
INSERT INTO [t22928] VALUES (2588,'قال تعالى: (قل للمخلفين من الأعراب ستدعون إلى قوم أولي بأس شديد تقاتلونهم أو يسلمون فإن تطيعوا يؤتكم الله أجرا حسنا وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما (١٦)) .','1163','2583','0');
INSERT INTO [t22928] VALUES (2589,'قال تعالى: (لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا (١٨) ومغانم كثيرة يأخذونها وكان الله عزيزا حكيما (١٩)) .','1163','2583','0');
INSERT INTO [t22928] VALUES (2590,'قال تعالى: (وأخرى لم تقدروا عليها قد أحاط الله بها وكان الله على كل شيء قديرا (٢١) ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا (٢٢) سنة الله التي قد خلت من قبل ولن تجد لسنة الله تبديلا (٢٣)) .','1163','2583','0');
INSERT INTO [t22928] VALUES (2591,'قال تعالى: (هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ليدخل الله في رحمته من يشاء لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما (٢٥)) .','1164','2583','0');
INSERT INTO [t22928] VALUES (2592,'قال تعالى: (إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته على رسوله وعلى المؤمنين وألزمهم كلمة التقوى وكانوا أحق بها وأهلها وكان الله بكل شيء عليما (٢٦)) .','1164','2583','0');
INSERT INTO [t22928] VALUES (2593,'قال تعالى: (لقد صدق الله رسوله الرؤيا بالحق لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا (٢٧)) .','1165','2583','0');
INSERT INTO [t22928] VALUES (2594,'قال تعالى: (هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا (٢٨)) .','1165','2583','0');
INSERT INTO [t22928] VALUES (2595,'قال تعالى: (محمد رسول الله والذين معه أشداء على الكفار رحماء بينهم تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا سيماهم في وجوههم من أثر السجود ذلك مثلهم في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما (٢٩)) .','1165','2583','0');
INSERT INTO [t22928] VALUES (2596,'الحجرات','1167','0','0');
INSERT INTO [t22928] VALUES (2597,'قال تعالى: (يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله واتقوا الله إن الله سميع عليم (١)) .','1167','2596','0');
INSERT INTO [t22928] VALUES (2598,'قال تعالى: (يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون (٢)) .','1167','2596','0');
INSERT INTO [t22928] VALUES (2599,'قال تعالى: (إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة وأجر عظيم (٣)) .','1167','2596','0');
INSERT INTO [t22928] VALUES (2600,'قال تعالى: (يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين (٦)) .','1167','2596','0');
INSERT INTO [t22928] VALUES (2601,'قال تعالى: (واعلموا أن فيكم رسول الله لو يطيعكم في كثير من الأمر لعنتم ولكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان أولئك هم الراشدون (٧)) .','1167','2596','0');
INSERT INTO [t22928] VALUES (2602,'قال تعالى: (فضلا من الله ونعمة والله عليم حكيم (٨) وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا إن الله يحب المقسطين (٩)) .','1168','2596','0');
INSERT INTO [t22928] VALUES (2603,'قال تعالى: (إنما المؤمنون إخوة فأصلحوا بين أخويكم واتقوا الله لعلكم ترحمون (١٠)) .','1168','2596','0');
INSERT INTO [t22928] VALUES (2604,'قال تعالى: (يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ولا تجسسوا ولا يغتب بعضكم بعضا أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه واتقوا الله إن الله تواب رحيم (١٢)) .','1168','2596','0');
INSERT INTO [t22928] VALUES (2605,'قال تعالى: (يا أيها الناس إنا خلقناكم من ذكر وأنثى وجعلناكم شعوبا وقبائل لتعارفوا إن أكرمكم عند الله أتقاكم إن الله عليم خبير (١٣)) .','1168','2596','0');
INSERT INTO [t22928] VALUES (2606,'قال تعالى: (قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا إن الله غفور رحيم (١٤)) .','1169','2596','0');
INSERT INTO [t22928] VALUES (2607,'ق','1170','0','0');
INSERT INTO [t22928] VALUES (2608,'قال تعالى: (ق والقرآن المجيد (١) بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب (٢) أئذا متنا وكنا ترابا ذلك رجع بعيد (٣)) .','1170','2607','0');
INSERT INTO [t22928] VALUES (2609,'قال تعالى: (أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج (٦) والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج (٧) تبصرة وذكرى لكل عبد منيب (٨)) .','1170','2607','0');
INSERT INTO [t22928] VALUES (2610,'قال تعالى: (ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد (٩) والنخل باسقات لها طلع نضيد (١٠) رزقا للعباد وأحيينا به بلدة ميتا كذلك الخروج (١١)) .','1171','2607','0');
INSERT INTO [t22928] VALUES (2611,'قال تعالى: (ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ونحن أقرب إليه من حبل الوريد (١٦)) .','1171','2607','0');
INSERT INTO [t22928] VALUES (2612,'قال تعالى: (إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد (١٧) ما يلفظ من قول إلا لديه رقيب عتيد (١٨)) .','1171','2607','0');
INSERT INTO [t22928] VALUES (2613,'قال تعالى: (وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد (١٩)) .','1172','2607','0');
INSERT INTO [t22928] VALUES (2614,'قال تعالى: (وجاءت كل نفس معها سائق وشهيد (٢١) لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد (٢٢)) .','1172','2607','0');
INSERT INTO [t22928] VALUES (2615,'قال تعالى: (وقال قرينه هذا ما لدي عتيد (٢٣)) .','1172','2607','0');
INSERT INTO [t22928] VALUES (2616,'قال تعالى: (ألقيا في جهنم كل كفار عنيد (٢٤)) .','1172','2607','0');
INSERT INTO [t22928] VALUES (2617,'قال تعالى: (مناع للخير معتد مريب الذي جعل مع الله إلها آخر فألقياه في العذاب الشديد (٢٦)) .','1173','2607','0');
INSERT INTO [t22928] VALUES (2618,'قال تعالى: (وأزلفت الجنة للمتقين غير بعيد (٣١) هذا ما توعدون لكل أواب حفيظ (٣٢)) .','1173','2607','0');
INSERT INTO [t22928] VALUES (2619,'قال تعالى: (من خشي الرحمن بالغيب وجاء بقلب منيب (٣٣) ادخلوها بسلام ذلك يوم الخلود (٣٤)) .','1173','2607','0');
INSERT INTO [t22928] VALUES (2620,'قال تعالى: (لهم ما يشاءون فيها ولدينا مزيد (٣٥) وكم أهلكنا قبلهم من قرن هم أشد منهم بطشا فنقبوا في البلاد هل من محيص (٣٦)) .','1174','2607','0');
INSERT INTO [t22928] VALUES (2621,'قال تعالى: (ومن الليل فسبحه وأدبار السجود (٤٠) واستمع يوم ينادي المنادي من مكان قريب (٤١) يوم يسمعون الصيحة بالحق ذلك يوم الخروج (٤٢) إنا نحن نحيي ونميت وإلينا المصير (٤٣) يوم تشقق الأرض عنهم سراعا ذلك حشر علينا يسير (٤٤)) .','1174','2607','0');
INSERT INTO [t22928] VALUES (2622,'الذاريات','1175','0','0');
INSERT INTO [t22928] VALUES (2623,'قال تعالى: (والذاريات ذروا (١) فالحاملات وقرا (٢) فالجاريات يسرا (٣) فالمقسمات أمرا (٤)) .','1175','2622','0');
INSERT INTO [t22928] VALUES (2624,'قال تعالى: (يؤفك عنه من أفك (٩)) .','1175','2622','0');
INSERT INTO [t22928] VALUES (2625,'قال تعالى: (يوم هم على النار يفتنون (١٣)) .','1175','2622','0');
INSERT INTO [t22928] VALUES (2626,'قال تعالى: (إن المتقين في جنات وعيون (١٥) آخذين ما آتاهم ربهم إنهم كانوا قبل ذلك محسنين (١٦)) .','1176','2622','0');
INSERT INTO [t22928] VALUES (2627,'قال تعالى: (كانوا قليلا من الليل ما يهجعون (١٧) وبالأسحار هم يستغفرون (١٨)) .','1176','2622','0');
INSERT INTO [t22928] VALUES (2628,'قال تعالى: (وفي أنفسكم أفلا تبصرون (٢١)) .','1177','2622','0');
INSERT INTO [t22928] VALUES (2629,'قال تعالى: (وفي السماء رزقكم وما توعدون (٢٢)) .','1177','2622','0');
INSERT INTO [t22928] VALUES (2630,'قال تعالى: (فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون (٢٣)) .','1177','2622','0');
INSERT INTO [t22928] VALUES (2631,'قال تعالى: (هل أتاك حديث ضيف إبراهيم المكرمين (٢٤) إذ دخلوا عليه فقالوا سلاما قال سلام قوم منكرون (٢٥)) .','1178','2622','0');
INSERT INTO [t22928] VALUES (2632,'قال تعالى: (فأقبلت امرأته في صرة فصكت وجهها وقالت عجوز عقيم قالوا كذلك قال ربك إنه هو الحكيم العليم (٣٠)) .','1178','2622','0');
INSERT INTO [t22928] VALUES (2633,'قال تعالى: (لنرسل عليهم حجارة من طين (٣٣) مسومة عند ربك للمسرفين (٣٤)) .','1178','2622','0');
INSERT INTO [t22928] VALUES (2634,'قال تعالى: (وفي موسى إذ أرسلناه إلى فرعون بسلطان مبين (٣٨) فتولى بركنه وقال ساحر أو مجنون (٣٩)) .','1178','2622','0');
INSERT INTO [t22928] VALUES (2635,'قال تعالى: (وفي عاد إذ أرسلنا عليهم الريح العقيم (٤١) ما تذر من شيء أتت عليه إلا جعلته كالرميم (٤٢) وفي ثمود إذ قيل لهم تمتعوا حتى حين (٤٣)) .','1178','2622','0');
INSERT INTO [t22928] VALUES (2636,'قال تعالى: (وقوم نوح من قبل إنهم كانوا قوما فاسقين (٤٦) والسماء بنيناها بأيد وإنا لموسعون (٤٧) والأرض فرشناها فنعم الماهدون (٤٨) ومن كل شيء خلقنا زوجين لعلكم تذكرون (٤٩)) .','1179','2622','0');
INSERT INTO [t22928] VALUES (2637,'قال تعالى: (كذلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون (٥٢)) .','1179','2622','0');
INSERT INTO [t22928] VALUES (2638,'قال تعالى: (إن الله هو الرزاق ذو القوة المتين (٥٨)) .','1179','2622','0');
INSERT INTO [t22928] VALUES (2639,'الطور','1180','0','0');
INSERT INTO [t22928] VALUES (2640,'قال تعالى: (والطور (١) وكتاب مسطور (٢)) .','1180','2639','0');
INSERT INTO [t22928] VALUES (2641,'قال تعالى: (في رق منشور (٣)) .','1180','2639','0');
INSERT INTO [t22928] VALUES (2642,'قال تعالى: (ما له من دافع (٨) يوم تمور السماء مورا (٩) وتسير الجبال سيرا (١٠) فويل يومئذ للمكذبين (١١) الذين هم في خوض يلعبون (١٢) يوم يدعون إلى نار جهنم دعا (١٣)) .','1180','2639','0');
INSERT INTO [t22928] VALUES (2643,'قال تعالى: (أفسحر هذا أم أنتم لا تبصرون (١٥) اصلوها فاصبروا أو لا تصبروا سواء عليكم إنما تجزون ما كنتم تعملون (١٦)) .','1180','2639','0');
INSERT INTO [t22928] VALUES (2644,'قال تعالى: (إن المتقين في جنات ونعيم (١٧) فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم (١٨) كلوا واشربوا هنيئا بما كنتم تعملون (١٩) متكئين على سرر مصفوفة وزوجناهم بحور عين (٢٠)) .','1180','2639','0');
INSERT INTO [t22928] VALUES (2645,'قال تعالى: (والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء كل امرئ بما كسب رهين (٢١) وأمددناهم بفاكهة ولحم مما يشتهون (٢٢) يتنازعون فيها كأسا لا لغو فيها ولا تأثيم (٢٣)) .','1181','2639','0');
INSERT INTO [t22928] VALUES (2646,'قال تعالى: (إنا كنا من قبل ندعوه إنه هو البر الرحيم (٢٨)) .','1181','2639','0');
INSERT INTO [t22928] VALUES (2647,'قال تعالى: (فذكر فما أنت بنعمة ربك بكاهن ولا مجنون (٢٩) أم يقولون شاعر نتربص به ريب المنون (٣٠)) .','1181','2639','0');
INSERT INTO [t22928] VALUES (2648,'قال تعالى: (أم لهم سلم يستمعون فيه فليأت مستمعهم بسلطان مبين (٣٨)) .','1181','2639','0');
INSERT INTO [t22928] VALUES (2649,'قال تعالى: (وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم (٤٤) فذرهم حتى يلاقوا يومهم الذي فيه يصعقون (٤٥) يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون (٤٦)) .','1182','2639','0');
INSERT INTO [t22928] VALUES (2650,'قال تعالى: (ومن الليل فسبحه وإدبار النجوم (٤٩)) .','1182','2639','0');
INSERT INTO [t22928] VALUES (2651,'النجم','1183','0','0');
INSERT INTO [t22928] VALUES (2652,'قال تعالى: (والنجم إذا هوى (١) ما ضل صاحبكم وما غوى (٢) وما ينطق عن الهوى (٣)) .','1183','2651','0');
INSERT INTO [t22928] VALUES (2653,'قال تعالى: (إن هو إلا وحي يوحى (٤) علمه شديد القوى (٥)) .','1183','2651','0');
INSERT INTO [t22928] VALUES (2654,'قال تعالى: (ذو مرة فاستوى (٦) وهو بالأفق الأعلى (٧) ثم دنا فتدلى (٨) فكان قاب قوسين أو أدنى (٩)) .','1183','2651','0');
INSERT INTO [t22928] VALUES (2655,'قال تعالى: (ما كذب الفؤاد ما رأى (١١) أفتمارونه على ما يرى (١٢) ولقد رآه نزلة أخرى (١٣) عند سدرة المنتهى (١٤) عندها جنة المأوى (١٥)) .','1183','2651','0');
INSERT INTO [t22928] VALUES (2656,'قال تعالى: (إذ يغشى السدرة ما يغشى (١٦) ما زاغ البصر وما طغى (١٧) لقد رأى من آيات ربه الكبرى (١٨)) .','1184','2651','0');
INSERT INTO [t22928] VALUES (2657,'قال تعالى: (أفرأيتم اللات والعزى (١٩) ومناة الثالثة الأخرى (٢٠)) .','1184','2651','0');
INSERT INTO [t22928] VALUES (2658,'قال تعالى: (ألكم الذكر وله الأنثى (٢١) تلك إذا قسمة ضيزى (٢٢)) .','1185','2651','0');
INSERT INTO [t22928] VALUES (2659,'قال تعالى: (إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان إن يتبعون إلا الظن وما تهوى الأنفس ولقد جاءهم من ربهم الهدى (٢٣) أم للإنسان ما تمنى (٢٤)) .','1185','2651','0');
INSERT INTO [t22928] VALUES (2660,'قال تعالى: (وكم من ملك في السماوات لا تغني شفاعتهم شيئا. . . (٢٦)) .','1186','2651','0');
INSERT INTO [t22928] VALUES (2661,'قال تعالى: (ولله ما في السماوات وما في الأرض ليجزي الذين أساءوا بما عملوا. . . (٣١)) .','1186','2651','0');
INSERT INTO [t22928] VALUES (2662,'قال تعالى: (الذين يجتنبون كبائر الإثم والفواحش إلا اللمم (٣٢)) .','1186','2651','0');
INSERT INTO [t22928] VALUES (2663,'قال تعالى: (أعنده علم الغيب فهو يرى أم لم ينبأ بما في صحف موسى وإبراهيم الذي وفى (٣٧)) .','1186','2651','0');
INSERT INTO [t22928] VALUES (2664,'قال تعالى: (ألا تزر وازرة وزر أخرى (٣٨)) .','1186','2651','0');
INSERT INTO [t22928] VALUES (2665,'قال تعالى: (وأن ليس للإنسان إلا ما سعى (٣٩)) .','1187','2651','0');
INSERT INTO [t22928] VALUES (2666,'قال تعالى: (وأن سعيه سوف يرى (٤٠))','1187','2651','0');
INSERT INTO [t22928] VALUES (2667,'قال تعالى: (ثم يجزاه الجزاء الأوفى (٤١)) .','1187','2651','0');
INSERT INTO [t22928] VALUES (2668,'قال تعالى: (وأنه هو أغنى وأقنى (٤٨)) .','1187','2651','0');
INSERT INTO [t22928] VALUES (2669,'قال تعالى: (وأنه أهلك عادا الأولى (٥٠)) .','1187','2651','0');
INSERT INTO [t22928] VALUES (2670,'قال تعالى: (وثمود فما أبقى (٥١) وقوم نوح من قبل إنهم كانوا هم أظلم وأطغى (٥٢) والمؤتفكة أهوى (٥٣) فغشاها ما غشى (٥٤)) .','1188','2651','0');
INSERT INTO [t22928] VALUES (2671,'قال تعالى: (ليس لها من دون الله كاشفة (٥٨)) . (كاشفة) : مصدر مثل العاقبة والعافية؛ أي ليس لها من دون الله كشف. ويجوز أن يكون التقدير: ليس لها كاشف، والهاء للمبالغة مثل راوية وعلامة. والله أعلم.','1188','2651','0');
INSERT INTO [t22928] VALUES (2672,'القمر','1189','0','0');
INSERT INTO [t22928] VALUES (2673,'قال تعالى: (وكذبوا واتبعوا أهواءهم وكل أمر مستقر (٣)) .','1189','2672','0');
INSERT INTO [t22928] VALUES (2674,'قال تعالى: (حكمة بالغة فما تغن النذر (٥)) .','1189','2672','0');
INSERT INTO [t22928] VALUES (2675,'قال تعالى: (فتول عنهم يوم يدعو الداع إلى شيء نكر (٦)) .','1189','2672','0');
INSERT INTO [t22928] VALUES (2676,'قال تعالى: (خشعا أبصارهم يخرجون من الأجداث كأنهم جراد منتشر (٧) مهطعين إلى الداع يقول الكافرون هذا يوم عسر (٨)) .','1189','2672','0');
INSERT INTO [t22928] VALUES (2677,'قال تعالى: (كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر (٩)) .','1190','2672','0');
INSERT INTO [t22928] VALUES (2678,'قال تعالى: (فدعا ربه أني مغلوب فانتصر (١٠)) .','1190','2672','0');
INSERT INTO [t22928] VALUES (2679,'قال تعالى: (وفجرنا الأرض عيونا فالتقى الماء على أمر قد قدر (١٢) وحملناه على ذات ألواح ودسر (١٣) تجري بأعيننا جزاء لمن كان كفر (١٤)) .','1190','2672','0');
INSERT INTO [t22928] VALUES (2680,'قال تعالى: (ولقد تركناها آية فهل من مدكر (١٥) فكيف كان عذابي ونذر (١٦)) .','1191','2672','0');
INSERT INTO [t22928] VALUES (2681,'قال تعالى: (إنا أرسلنا عليهم ريحا صرصرا في يوم نحس مستمر (١٩) تنزع الناس كأنهم أعجاز نخل منقعر (٢٠)) .','1191','2672','0');
INSERT INTO [t22928] VALUES (2682,'قال تعالى: (فقالوا أبشرا منا واحدا نتبعه إنا إذا لفي ضلال وسعر (٢٤)) .','1191','2672','0');
INSERT INTO [t22928] VALUES (2683,'قال تعالى: (أؤلقي الذكر عليه من بيننا بل هو كذاب أشر (٢٥)) .','1192','2672','0');
INSERT INTO [t22928] VALUES (2684,'قال تعالى: (إنا مرسلو الناقة فتنة لهم فارتقبهم واصطبر (٢٧) ونبئهم أن الماء قسمة بينهم كل شرب محتضر (٢٨)) .','1192','2672','0');
INSERT INTO [t22928] VALUES (2685,'قال تعالى: (إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر (٣١)) .','1192','2672','0');
INSERT INTO [t22928] VALUES (2686,'قال تعالى: (إنا أرسلنا عليهم حاصبا إلا آل لوط نجيناهم بسحر (٣٤) نعمة من عندنا كذلك نجزي من شكر (٣٥)) .','1192','2672','0');
INSERT INTO [t22928] VALUES (2687,'قال تعالى: (إنا كل شيء خلقناه بقدر (٤٩)) .','1192','2672','0');
INSERT INTO [t22928] VALUES (2688,'قال تعالى: (وكل شيء فعلوه في الزبر (٥٢)) .','1193','2672','0');
INSERT INTO [t22928] VALUES (2689,'قال تعالى: (إن المتقين في جنات ونهر (٥٤) في مقعد صدق عند مليك مقتدر (٥٥)) .','1193','2672','0');
INSERT INTO [t22928] VALUES (2690,'الرحمن','1194','0','0');
INSERT INTO [t22928] VALUES (2691,'قال تعالى: (الرحمن (١)) .','1194','2690','0');
INSERT INTO [t22928] VALUES (2692,'قال تعالى: (خلق الإنسان (٣) علمه البيان (٤)) .','1194','2690','0');
INSERT INTO [t22928] VALUES (2693,'قال تعالى: (الشمس والقمر بحسبان (٥) والنجم والشجر يسجدان (٦) والسماء رفعها ووضع الميزان (٧)) .','1194','2690','0');
INSERT INTO [t22928] VALUES (2694,'قال تعالى: (ألا تطغوا في الميزان (٨) وأقيموا الوزن بالقسط ولا تخسروا الميزان (٩)) .','1194','2690','0');
INSERT INTO [t22928] VALUES (2695,'قال تعالى: (والأرض وضعها للأنام (١٠) فيها فاكهة والنخل ذات الأكمام (١١)) .','1195','2690','0');
INSERT INTO [t22928] VALUES (2696,'قال تعالى: (والحب ذو العصف والريحان (١٢)) .','1195','2690','0');
INSERT INTO [t22928] VALUES (2697,'قال تعالى: (خلق الإنسان من صلصال كالفخار (١٤) وخلق الجان من مارج من نار (١٥)) .','1195','2690','0');
INSERT INTO [t22928] VALUES (2698,'قال تعالى: (رب المشرقين ورب المغربين (١٧) فبأي آلاء ربكما تكذبان (١٨) مرج البحرين يلتقيان (١٩) بينهما برزخ لا يبغيان (٢٠)) .','1195','2690','0');
INSERT INTO [t22928] VALUES (2699,'قال تعالى: (يخرج منهما اللؤلؤ والمرجان (٢٢)) .','1195','2690','0');
INSERT INTO [t22928] VALUES (2700,'قال تعالى: (وله الجوار المنشآت في البحر كالأعلام (٢٤) فبأي آلاء ربكما تكذبان (٢٥) كل من عليها فان (٢٦)) .','1196','2690','0');
INSERT INTO [t22928] VALUES (2701,'قال تعالى: (ويبقى وجه ربك ذو الجلال والإكرام (٢٧)) .','1196','2690','0');
INSERT INTO [t22928] VALUES (2702,'قال تعالى: (يسأله من في السماوات والأرض كل يوم هو في شأن (٢٩)) .','1196','2690','0');
INSERT INTO [t22928] VALUES (2703,'قال تعالى: (سنفرغ لكم أيه الثقلان (٣١)) .','1196','2690','0');
INSERT INTO [t22928] VALUES (2704,'قال تعالى: (يا معشر الجن والإنس إن استطعتم أن تنفذوا من أقطار السماوات والأرض فانفذوا لا تنفذون إلا بسلطان (٣٣)) .','1196','2690','0');
INSERT INTO [t22928] VALUES (2705,'قال تعالى: (يرسل عليكما شواظ من نار ونحاس فلا تنتصران (٣٥)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2706,'قال تعالى: (فإذا انشقت السماء فكانت وردة كالدهان (٣٧)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2707,'قال تعالى: (فيومئذ لا يسأل عن ذنبه إنس ولا جان (٣٩)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2708,'قال تعالى: (يطوفون بينها وبين حميم آن (٤٤)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2709,'قال تعالى: (ذواتا أفنان (٤٨)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2710,'قال تعالى: (متكئين على فرش بطائنها من إستبرق وجنى الجنتين دان (٥٤)) .','1197','2690','0');
INSERT INTO [t22928] VALUES (2711,'قال تعالى: (فيهن قاصرات الطرف لم يطمثهن إنس قبلهم ولا جان (٥٦)) .','1198','2690','0');
INSERT INTO [t22928] VALUES (2712,'قال تعالى: (هل جزاء الإحسان إلا الإحسان (٦٠)) .','1198','2690','0');
INSERT INTO [t22928] VALUES (2713,'قال تعالى: (فيهن خيرات حسان (٧٠) فبأي آلاء ربكما تكذبان (٧١) حور مقصورات في الخيام (٧٢)) .','1198','2690','0');
INSERT INTO [t22928] VALUES (2714,'قال تعالى: (متكئين على رفرف خضر وعبقري حسان (٧٦)) .','1198','2690','0');
INSERT INTO [t22928] VALUES (2715,'قال تعالى: (تبارك اسم ربك ذي الجلال والإكرام (٧٨)) .','1198','2690','0');
INSERT INTO [t22928] VALUES (2716,'الواقعة','1199','0','0');
INSERT INTO [t22928] VALUES (2717,'قال تعالى: (إذا وقعت الواقعة (١) ليس لوقعتها كاذبة (٢)) .','1199','2716','0');
INSERT INTO [t22928] VALUES (2718,'قال تعالى: (إذا رجت الأرض رجا (٤)) .','1199','2716','0');
INSERT INTO [t22928] VALUES (2719,'قال تعالى: (فأصحاب الميمنة ما أصحاب الميمنة (٨)) .','1200','2716','0');
INSERT INTO [t22928] VALUES (2720,'قال تعالى: (والسابقون السابقون (١٠) أولئك المقربون (١١) في جنات النعيم (١٢) ثلة من الأولين (١٣) وقليل من الآخرين (١٤) على سرر موضونة (١٥) متكئين عليها متقابلين (١٦)) .','1200','2716','0');
INSERT INTO [t22928] VALUES (2721,'قال تعالى: (يطوف عليهم ولدان مخلدون بأكواب وأباريق وكأس من معين (١٨)) .','1200','2716','0');
INSERT INTO [t22928] VALUES (2722,'قال تعالى: (وحور عين) (٢٢) .','1201','2716','0');
INSERT INTO [t22928] VALUES (2723,'قال تعالى: (جزاء بما كانوا يعملون (٢٤)) .','1201','2716','0');
INSERT INTO [t22928] VALUES (2724,'قال تعالى: (لا يسمعون فيها لغوا ولا تأثيما (٢٥) إلا قيلا سلاما سلاما (٢٦)) .','1201','2716','0');
INSERT INTO [t22928] VALUES (2725,'قال تعالى: (وفاكهة كثيرة (٣٢) لا مقطوعة ولا ممنوعة (٣٣)) .','1201','2716','0');
INSERT INTO [t22928] VALUES (2726,'قال تعالى: (إنا أنشأناهن إنشاء (٣٥) فجعلناهن أبكارا (٣٦) عربا أترابا (٣٧)) .','1201','2716','0');
INSERT INTO [t22928] VALUES (2727,'قال تعالى: (لأصحاب اليمين (٣٨) ثلة من الأولين (٣٩) وثلة من الآخرين (٤٠) وأصحاب الشمال ما أصحاب الشمال (٤١) في سموم وحميم (٤٢) وظل من يحموم (٤٣)) .','1202','2716','0');
INSERT INTO [t22928] VALUES (2728,'قال تعالى: (لآكلون من شجر من زقوم (٥٢) فمالئون منها البطون (٥٣) فشاربون عليه من الحميم (٥٤) فشاربون شرب الهيم (٥٥)) .','1202','2716','0');
INSERT INTO [t22928] VALUES (2729,'قال تعالى: (وإنه لقسم لو تعلمون عظيم (٧٦) إنه لقرآن كريم (٧٧) في كتاب مكنون (٧٨)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2730,'قال تعالى: (لا يمسه إلا المطهرون (٧٩) تنزيل من رب العالمين (٨٠)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2731,'قال تعالى: (وتجعلون رزقكم أنكم تكذبون (٨٢)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2732,'قال تعالى: (ترجعونها إن كنتم صادقين (٨٧)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2733,'قال تعالى: (فأما إن كان من المقربين (٨٨) فروح وريحان وجنة نعيم (٨٩)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2734,'قال تعالى: (فنزل من حميم (٩٣) وتصلية جحيم (٩٤) إن هذا لهو حق اليقين (٩٥) فسبح باسم ربك العظيم (٩٦)) .','1203','2716','0');
INSERT INTO [t22928] VALUES (2735,'الحديد','1204','0','0');
INSERT INTO [t22928] VALUES (2736,'قال تعالى: (له ملك السماوات والأرض يحيي ويميت وهو على كل شيء قدير (٢)) .','1204','2735','0');
INSERT INTO [t22928] VALUES (2737,'قال تعالى: (وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا بربكم وقد أخذ ميثاقكم إن كنتم مؤمنين (٨)) .','1204','2735','0');
INSERT INTO [t22928] VALUES (2738,'قال تعالى: (وما لكم ألا تنفقوا في سبيل الله ولله ميراث السماوات والأرض لا يستوي منكم من أنفق من قبل الفتح وقاتل أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا وكلا وعد الله الحسنى والله بما تعملون خبير (١٠)) .','1204','2735','0');
INSERT INTO [t22928] VALUES (2739,'قال تعالى: (يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ذلك هو الفوز العظيم (١٢)) .','1204','2735','0');
INSERT INTO [t22928] VALUES (2740,'قال تعالى: (يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب (١٣) ينادونهم ألم نكن معكم قالوا بلى ولكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتى جاء أمر الله وغركم بالله الغرور (١٤)) .','1205','2735','0');
INSERT INTO [t22928] VALUES (2741,'قال تعالى: (فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا مأواكم النار هي مولاكم وبئس المصير (١٥)) .','1205','2735','0');
INSERT INTO [t22928] VALUES (2742,'قال تعالى: (ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم وكثير منهم فاسقون (١٦)) .','1205','2735','0');
INSERT INTO [t22928] VALUES (2743,'قال تعالى: (إن المصدقين والمصدقات وأقرضوا الله قرضا حسنا يضاعف لهم ولهم أجر كريم (١٨)) .','1206','2735','0');
INSERT INTO [t22928] VALUES (2744,'قال تعالى: (والذين آمنوا بالله ورسله أولئك هم الصديقون والشهداء عند ربهم لهم أجرهم ونورهم والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم (١٩)) .','1206','2735','0');
INSERT INTO [t22928] VALUES (2745,'قال تعالى: (اعلموا أنما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الأموال والأولاد كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما وفي الآخرة عذاب شديد ومغفرة من الله ورضوان وما الحياة الدنيا إلا متاع الغرور (٢٠) سابقوا إلى مغفرة من ربكم وجنة عرضها كعرض السماء والأرض أعدت للذين آمنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم (٢١)) .','1206','2735','0');
INSERT INTO [t22928] VALUES (2746,'قال تعالى: (ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها. . . (٢٢)) .','1207','2735','0');
INSERT INTO [t22928] VALUES (2747,'قال تعالى: (لكي لا تأسوا على ما فاتكم ولا تفرحوا بما آتاكم والله لا يحب كل مختال فخور (٢٢)) .','1207','2735','0');
INSERT INTO [t22928] VALUES (2748,'قال تعالى: (الذين يبخلون ويأمرون الناس بالبخل ومن يتول فإن الله هو الغني الحميد (٢٤)) .','1207','2735','0');
INSERT INTO [t22928] VALUES (2749,'قال تعالى: (لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط وأنزلنا الحديد فيه بأس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب إن الله قوي عزيز (٢٥)) .','1207','2735','0');
INSERT INTO [t22928] VALUES (2750,'قال تعالى: (ثم قفينا على آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها فآتينا الذين آمنوا منهم أجرهم وكثير منهم فاسقون (٢٧)) .','1207','2735','0');
INSERT INTO [t22928] VALUES (2751,'قال تعالى: (لئلا يعلم أهل الكتاب ألا يقدرون على شيء من فضل الله وأن الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم (٢٩)) .','1208','2735','0');
INSERT INTO [t22928] VALUES (2752,'المجادلة','1209','0','0');
INSERT INTO [t22928] VALUES (2753,'قال تعالى: (قد سمع الله قول التي تجادلك في زوجها وتشتكي إلى الله والله يسمع تحاوركما إن الله سميع بصير (١)) .','1209','2752','0');
INSERT INTO [t22928] VALUES (2754,'قال تعالى: (الذين يظاهرون منكم من نسائهم ما هن أمهاتهم إن أمهاتهم إلا اللائي ولدنهم وإنهم ليقولون منكرا من القول وزورا وإن الله لعفو غفور (٢)) .','1209','2752','0');
INSERT INTO [t22928] VALUES (2755,'قال تعالى: (والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل أن يتماسا ذلكم توعظون به والله بما تعملون خبير (٣)) .','1209','2752','0');
INSERT INTO [t22928] VALUES (2756,'قال تعالى: (يوم يبعثهم الله جميعا فينبئهم بما عملوا أحصاه الله ونسوه والله على كل شيء شهيد (٦)) .','1210','2752','0');
INSERT INTO [t22928] VALUES (2757,'قال تعالى: (ألم تر أن الله يعلم ما في السماوات وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم ولا أدنى من ذلك ولا أكثر إلا هو معهم أين ما كانوا ثم ينبئهم بما عملوا يوم القيامة إن الله بكل شيء عليم (٧)) .','1210','2752','0');
INSERT INTO [t22928] VALUES (2758,'قال تعالى: (ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصية الرسول وإذا جاءوك حيوك بما لم يحيك به الله ويقولون في أنفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير (٨)) .','1210','2752','0');
INSERT INTO [t22928] VALUES (2759,'قال تعالى: (أأشفقتم أن تقدموا بين يدي نجواكم صدقات فإذ لم تفعلوا وتاب الله عليكم فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله والله خبير بما تعملون (١٣)) .','1210','2752','0');
INSERT INTO [t22928] VALUES (2760,'قال تعالى: (استحوذ عليهم الشيطان فأنساهم ذكر الله أولئك حزب الشيطان ألا إن حزب الشيطان هم الخاسرون (١٩)) .','1211','2752','0');
INSERT INTO [t22928] VALUES (2761,'قال تعالى: (كتب الله لأغلبن أنا ورسلي إن الله قوي عزيز (٢١)) .','1211','2752','0');
INSERT INTO [t22928] VALUES (2762,'قال تعالى: (لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم أولئك كتب في قلوبهم الإيمان وأيدهم بروح منه ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها رضي الله عنهم ورضوا عنه أولئك حزب الله ألا إن حزب الله هم المفلحون (٢٢)) .','1211','2752','0');
INSERT INTO [t22928] VALUES (2763,'الحشر','1212','0','0');
INSERT INTO [t22928] VALUES (2764,'قال تعالى: (هو الذي أخرج الذين كفروا من أهل الكتاب من ديارهم لأول الحشر ما ظننتم أن يخرجوا وظنوا أنهم مانعتهم حصونهم من الله فأتاهم الله من حيث لم يحتسبوا وقذف في قلوبهم الرعب يخربون بيوتهم بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار (٢)) .','1212','2763','0');
INSERT INTO [t22928] VALUES (2765,'قال تعالى: (ما قطعتم من لينة أو تركتموها قائمة على أصولها فبإذن الله وليخزي الفاسقين (٥)) .','1212','2763','0');
INSERT INTO [t22928] VALUES (2766,'قال تعالى: (وما أفاء الله على رسوله منهم فما أوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شيء قدير (٦) ما أفاء الله على رسوله من أهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الأغنياء منكم وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا واتقوا الله إن الله شديد العقاب (٧)) .','1212','2763','0');
INSERT INTO [t22928] VALUES (2767,'قال تعالى: (للفقراء المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله أولئك هم الصادقون (٨)) .','1212','2763','0');
INSERT INTO [t22928] VALUES (2768,'قال تعالى: (والذين تبوءوا الدار والإيمان من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة مما أوتوا ويؤثرون على أنفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فأولئك هم المفلحون (٩)) .','1212','2763','0');
INSERT INTO [t22928] VALUES (2769,'قال تعالى: (لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الأدبار ثم لا ينصرون (١٢)) .','1213','2763','0');
INSERT INTO [t22928] VALUES (2770,'قال تعالى: (لا يقاتلونكم جميعا إلا في قرى محصنة أو من وراء جدر بأسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بأنهم قوم لا يعقلون (١٤)) .','1213','2763','0');
INSERT INTO [t22928] VALUES (2771,'قال تعالى: (كمثل الذين من قبلهم قريبا ذاقوا وبال أمرهم ولهم عذاب أليم (١٥)) .','1213','2763','0');
INSERT INTO [t22928] VALUES (2772,'قال تعالى: (فكان عاقبتهما أنهما في النار خالدين فيها وذلك جزاء الظالمين (١٧)) .','1213','2763','0');
INSERT INTO [t22928] VALUES (2773,'قال تعالى: (هو الله الخالق البارئ المصور له الأسماء الحسنى يسبح له ما في السماوات والأرض وهو العزيز الحكيم (٢٤)) .','1213','2763','0');
INSERT INTO [t22928] VALUES (2774,'الممتحنة','1214','0','0');
INSERT INTO [t22928] VALUES (2775,'قال تعالى: (يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء تلقون إليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول وإياكم أن تؤمنوا بالله ربكم إن كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي تسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ومن يفعله منكم فقد ضل سواء السبيل (١)) .','1214','2774','0');
INSERT INTO [t22928] VALUES (2776,'قال تعالى: (لن تنفعكم أرحامكم ولا أولادكم يوم القيامة يفصل بينكم. . . (٣)) .','1214','2774','0');
INSERT INTO [t22928] VALUES (2777,'قال تعالى: (قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا برآء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتى تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك وما أملك لك من الله من شيء ربنا عليك توكلنا وإليك أنبنا وإليك المصير (٤)) .','1214','2774','0');
INSERT INTO [t22928] VALUES (2778,'قال تعالى: (لقد كان لكم فيهم أسوة حسنة لمن كان يرجو الله واليوم الآخر. . . (٦)) .','1215','2774','0');
INSERT INTO [t22928] VALUES (2779,'قال تعالى: (لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم. . . (٨)) .','1215','2774','0');
INSERT INTO [t22928] VALUES (2780,'قال تعالى: (ولا تمسكوا بعصم الكوافر واسألوا ما أنفقتم. . . (١٠)) .','1216','2774','0');
INSERT INTO [t22928] VALUES (2781,'قال تعالى: (يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور (١٣)) .','1216','2774','0');
INSERT INTO [t22928] VALUES (2782,'الصف','1217','0','0');
INSERT INTO [t22928] VALUES (2783,'قال تعالى: (كبر مقتا عند الله أن تقولوا ما لا تفعلون (٢)) .','1217','2782','0');
INSERT INTO [t22928] VALUES (2784,'قال تعالى: (إن الله يحب الذين يقاتلون في سبيله صفا كأنهم بنيان مرصوص (٤)) .','1217','2782','0');
INSERT INTO [t22928] VALUES (2785,'قال تعالى: (وإذ قال عيسى ابن مريم يا بني إسرائيل إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد فلما جاءهم بالبينات قالوا هذا سحر مبين (٦)) .','1217','2782','0');
INSERT INTO [t22928] VALUES (2786,'قال تعالى: (يريدون ليطفئوا نور الله بأفواههم والله متم نوره ولو كره الكافرون (٨) هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون (٩))','1217','2782','0');
INSERT INTO [t22928] VALUES (2787,'قال تعالى: (تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم. . . (١١)) .','1217','2782','0');
INSERT INTO [t22928] VALUES (2788,'قال تعالى: (يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار. . . (١٢)) .','1218','2782','0');
INSERT INTO [t22928] VALUES (2789,'قال تعالى: (وأخرى تحبونها نصر من الله وفتح قريب. . . (١٣)) .','1218','2782','0');
INSERT INTO [t22928] VALUES (2790,'قال تعالى: (يا أيها الذين آمنوا كونوا أنصار الله كما قال عيسى ابن مريم للحواريين من أنصاري إلى الله قال الحواريون نحن أنصار الله فآمنت طائفة من بني إسرائيل وكفرت طائفة فأيدنا الذين آمنوا على عدوهم فأصبحوا ظاهرين (١٤)) .','1218','2782','0');
INSERT INTO [t22928] VALUES (2791,'الجمعة','1219','0','0');
INSERT INTO [t22928] VALUES (2792,'قال تعالى: (يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم (١)) .','1219','2791','0');
INSERT INTO [t22928] VALUES (2793,'قال تعالى: (وآخرين منهم لما يلحقوا بهم وهو العزيز الحكيم (٣)) .','1219','2791','0');
INSERT INTO [t22928] VALUES (2794,'قال تعالى: (مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل أسفارا بئس مثل القوم الذين كذبوا بآيات الله والله لا يهدي القوم الظالمين (٥)) .','1219','2791','0');
INSERT INTO [t22928] VALUES (2795,'قال تعالى: (قل إن الموت الذي تفرون منه فإنه ملاقيكم. . . (٨)) .','1219','2791','0');
INSERT INTO [t22928] VALUES (2796,'قال تعالى: (يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع ذلكم خير لكم إن كنتم تعلمون (٩)) .','1220','2791','0');
INSERT INTO [t22928] VALUES (2797,'قال تعالى: (وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما. . . (١١)) .','1220','2791','0');
INSERT INTO [t22928] VALUES (2798,'المنافقون','1221','0','0');
INSERT INTO [t22928] VALUES (2799,'قال تعالى: (وإذا رأيتهم تعجبك أجسامهم وإن يقولوا تسمع لقولهم كأنهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قاتلهم الله أنى يؤفكون (٤)) .','1221','2798','0');
INSERT INTO [t22928] VALUES (2800,'قال تعالى: (وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورأيتهم يصدون وهم مستكبرون (٥)) (سواء عليهم أستغفرت لهم أم لم تستغفر لهم لن يغفر الله لهم إن الله لا يهدي القوم الفاسقين (٦))','1221','2798','0');
INSERT INTO [t22928] VALUES (2801,'قال تعالى: (يقولون لئن رجعنا إلى المدينة ليخرجن الأعز منها الأذل. . . (٨)) .','1221','2798','0');
INSERT INTO [t22928] VALUES (2802,'قال تعالى: (وأنفقوا من ما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا أخرتني إلى أجل قريب فأصدق وأكن من الصالحين (١٠)) .','1222','2798','0');
INSERT INTO [t22928] VALUES (2803,'التغابن','1223','0','0');
INSERT INTO [t22928] VALUES (2804,'قال تعالى: (ذلك بأنه كانت تأتيهم رسلهم بالبينات فقالوا أبشر يهدوننا فكفروا وتولوا واستغنى الله والله غني حميد (٦)) .','1223','2803','0');
INSERT INTO [t22928] VALUES (2805,'قال تعالى: (يوم يجمعكم ليوم الجمع ذلك يوم التغابن. . . (٩)) .','1223','2803','0');
INSERT INTO [t22928] VALUES (2806,'قال تعالى: (ما أصاب من مصيبة إلا بإذن الله ومن يؤمن بالله يهد قلبه. . . (١١)) .','1223','2803','0');
INSERT INTO [t22928] VALUES (2807,'قال تعالى: (فاتقوا الله ما استطعتم واسمعوا وأطيعوا وأنفقوا خيرا لأنفسكم ومن يوق شح نفسه فأولئك هم المفلحون (١٦)) .','1223','2803','0');
INSERT INTO [t22928] VALUES (2808,'الطلاق','1224','0','0');
INSERT INTO [t22928] VALUES (2809,'قال تعالى: (يا أيها النبي إذا طلقتم النساء فطلقوهن لعدتهن. . . (١)) .','1224','2808','0');
INSERT INTO [t22928] VALUES (2810,'قال تعالى: (ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه إن الله بالغ أمره قد جعل الله لكل شيء قدرا (٣)) .','1224','2808','0');
INSERT INTO [t22928] VALUES (2811,'قال تعالى: (واللائي لم يحضن وأولات الأحمال أجلهن أن يضعن حملهن. . . (٤)) .','1224','2808','0');
INSERT INTO [t22928] VALUES (2812,'قال تعالى: (أسكنوهن من حيث سكنتم من وجدكم. . . (٦)) .','1224','2808','0');
INSERT INTO [t22928] VALUES (2813,'قال تعالى: (أعد الله لهم عذابا شديدا فاتقوا الله يا أولي الألباب الذين آمنوا قد أنزل الله إليكم ذكرا رسولا يتلو عليكم آيات الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا قد أحسن الله له رزقا (١١)) .','1225','2808','0');
INSERT INTO [t22928] VALUES (2814,'قال تعالى: (الله الذي خلق سبع سماوات ومن الأرض مثلهن يتنزل الأمر بينهن. . . (١٢)) .','1225','2808','0');
INSERT INTO [t22928] VALUES (2815,'التحريم','1226','0','0');
INSERT INTO [t22928] VALUES (2816,'قال تعالى: (يا أيها النبي لم تحرم ما أحل الله لك تبتغي مرضاة أزواجك. . . (١)) .','1226','2815','0');
INSERT INTO [t22928] VALUES (2817,'قال تعالى: (قد فرض الله لكم تحلة أيمانكم. . . (٢)) .','1226','2815','0');
INSERT INTO [t22928] VALUES (2818,'قال تعالى: (وإذ أسر النبي إلى بعض أزواجه حديثا فلما نبأت به وأظهره الله عليه عرف بعضه وأعرض عن بعض فلما نبأها به قالت من أنبأك هذا قال نبأني العليم الخبير (٣)) .','1226','2815','0');
INSERT INTO [t22928] VALUES (2819,'قال تعالى: (إن تتوبا إلى الله فقد صغت قلوبكما وإن تظاهرا عليه فإن الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير (٤)) .','1226','2815','0');
INSERT INTO [t22928] VALUES (2820,'قال تعالى: (عسى ربه إن طلقكن أن يبدله أزواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وأبكارا (٥)) .','1227','2815','0');
INSERT INTO [t22928] VALUES (2821,'قال تعالى: (يا أيها الذين آمنوا قوا أنفسكم وأهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون (٦)) .','1227','2815','0');
INSERT INTO [t22928] VALUES (2822,'قال تعالى: (يا أيها الذين آمنوا توبوا إلى الله توبة نصوحا عسى ربكم أن يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الأنهار يوم لا يخزي الله النبي والذين آمنوا معه نورهم يسعى بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا واغفر لنا إنك على كل شيء قدير (٨)) .','1227','2815','0');
INSERT INTO [t22928] VALUES (2823,'قال تعالى: (ضرب الله مثلا للذين كفروا امرأة نوح وامرأة لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين (١٠)) .','1228','2815','0');
INSERT INTO [t22928] VALUES (2824,'قال تعالى: (وضرب الله مثلا للذين آمنوا امرأة فرعون إذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين (١١)) .','1228','2815','0');
INSERT INTO [t22928] VALUES (2825,'قال تعالى: (ومريم ابنت عمران التي أحصنت فرجها فنفخنا فيه من روحنا. . . (١٢)) .','1228','2815','0');
INSERT INTO [t22928] VALUES (2826,'الملك','1229','0','0');
INSERT INTO [t22928] VALUES (2827,'قال تعالى: (الذي خلق سبع سماوات طباقا ما ترى في خلق الرحمن من تفاوت فارجع البصر هل ترى من فطور (٣) ثم ارجع البصر كرتين ينقلب إليك البصر خاسئا وهو حسير (٤)) .','1229','2826','0');
INSERT INTO [t22928] VALUES (2828,'قال تعالى: (وللذين كفروا بربهم عذاب جهنم وبئس المصير (٦)) .','1229','2826','0');
INSERT INTO [t22928] VALUES (2829,'قال تعالى: (فاعترفوا بذنبهم فسحقا لأصحاب السعير (١١)) .','1229','2826','0');
INSERT INTO [t22928] VALUES (2830,'قال تعالى: (ألا يعلم من خلق وهو اللطيف الخبير (١٤)) .','1229','2826','0');
INSERT INTO [t22928] VALUES (2831,'قال تعالى: (هو الذي جعل لكم الأرض ذلولا فامشوا في مناكبها وكلوا من رزقه وإليه النشور (١٥) أأمنتم من في السماء أن يخسف بكم الأرض فإذا هي تمور (١٦) أم أمنتم من في السماء أن يرسل عليكم حاصبا فستعلمون كيف نذير (١٧)) .','1229','2826','0');
INSERT INTO [t22928] VALUES (2832,'قال تعالى: (أولم يروا إلى الطير فوقهم صافات ويقبضن ما يمسكهن إلا الرحمن إنه بكل شيء بصير (١٩)) .','1230','2826','0');
INSERT INTO [t22928] VALUES (2833,'قال تعالى: (أمن هذا الذي هو جند لكم ينصركم من دون الرحمن. . . (٢٠)) .','1230','2826','0');
INSERT INTO [t22928] VALUES (2834,'قال تعالى: (أفمن يمشي مكبا على وجهه أهدى أمن يمشي سويا على صراط مستقيم (٢٢)) .','1230','2826','0');
INSERT INTO [t22928] VALUES (2835,'قال تعالى: (قل أرأيتم إن أصبح ماؤكم غورا فمن يأتيكم بماء معين (٣٠)) .','1230','2826','0');
INSERT INTO [t22928] VALUES (2836,'القلم','1231','0','0');
INSERT INTO [t22928] VALUES (2837,'قال تعالى: (ن والقلم وما يسطرون (١)) .','1231','2836','0');
INSERT INTO [t22928] VALUES (2838,'قال تعالى: (بأييكم المفتون (٦)) .','1231','2836','0');
INSERT INTO [t22928] VALUES (2839,'قال تعالى: (ودوا لو تدهن فيدهنون (٩)) .','1231','2836','0');
INSERT INTO [t22928] VALUES (2840,'قال تعالى: (أن كان ذا مال وبنين (١٤) إذا تتلى عليه آياتنا قال أساطير الأولين (١٥)) .','1231','2836','0');
INSERT INTO [t22928] VALUES (2841,'قال تعالى: (إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين (١٧)) .','1231','2836','0');
INSERT INTO [t22928] VALUES (2842,'قال تعالى: (وغدوا على حرد قادرين (٢٥)) .','1232','2836','0');
INSERT INTO [t22928] VALUES (2843,'قال تعالى: (إن للمتقين عند ربهم جنات النعيم (٣٤)) .','1232','2836','0');
INSERT INTO [t22928] VALUES (2844,'قال تعالى: (أم لكم أيمان علينا بالغة إلى يوم القيامة. . . (٣٩)) .','1232','2836','0');
INSERT INTO [t22928] VALUES (2845,'قال تعالى: (يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون (٤٢) خاشعة أبصارهم ترهقهم ذلة وقد كانوا يدعون إلى السجود وهم سالمون (٤٣)) .','1232','2836','0');
INSERT INTO [t22928] VALUES (2846,'قال تعالى: (فذرني ومن يكذب بهذا الحديث. . . (٤٤)) .','1232','2836','0');
INSERT INTO [t22928] VALUES (2847,'الحاقة','1233','0','0');
INSERT INTO [t22928] VALUES (2848,'قال تعالى: (الحاقة (١) ما الحاقة (٢) وما أدراك ما الحاقة (٣)) .','1233','2847','0');
INSERT INTO [t22928] VALUES (2849,'قال تعالى: (فأما ثمود فأهلكوا بالطاغية (٥)) .','1233','2847','0');
INSERT INTO [t22928] VALUES (2850,'قال تعالى: (سخرها عليهم سبع ليال وثمانية أيام حسوما فترى القوم فيها صرعى كأنهم أعجاز نخل خاوية (٧)) .','1233','2847','0');
INSERT INTO [t22928] VALUES (2851,'قال تعالى: (فهل ترى لهم من باقية (٨)) .','1233','2847','0');
INSERT INTO [t22928] VALUES (2852,'قال تعالى: (وجاء فرعون ومن قبله والمؤتفكات بالخاطئة (٩)) .','1233','2847','0');
INSERT INTO [t22928] VALUES (2853,'قال تعالى: (لنجعلها لكم تذكرة وتعيها أذن واعية (١٢)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2854,'قال تعالى: (فإذا نفخ في الصور نفخة واحدة (١٣)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2855,'قال تعالى: (وحملت الأرض والجبال فدكتا دكة واحدة (٢٤) فيومئذ وقعت الواقعة (٢٥) وانشقت السماء فهي يومئذ واهية (٢٦)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2856,'قال تعالى: (فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه (١٩) إني ظننت أني ملاق حسابيه (٢٠) فهو في عيشة راضية (٢١)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2857,'قال تعالى: (ما أغنى عني ماليه (٢٨)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2858,'قال تعالى: (ثم الجحيم صلوه (٣١) ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه (٣٢)) .','1234','2847','0');
INSERT INTO [t22928] VALUES (2859,'قال تعالى: (فليس له اليوم هاهنا حميم (٣٥) ولا طعام إلا من غسلين (٣٦)) .','1235','2847','0');
INSERT INTO [t22928] VALUES (2860,'قال تعالى: (وما هو بقول شاعر قليلا ما تؤمنون (٤١) ولا بقول كاهن قليلا ما تذكرون (٤٢) تنزيل من رب العالمين (٤٣)) .','1235','2847','0');
INSERT INTO [t22928] VALUES (2861,'قال تعالى: (لأخذنا منه باليمين (٤٥)) .','1235','2847','0');
INSERT INTO [t22928] VALUES (2862,'قال تعالى: (فما منكم من أحد عنه حاجزين (٤٧) وإنه لتذكرة للمتقين (٤٨)) .','1235','2847','0');
INSERT INTO [t22928] VALUES (2863,'المعارج','1236','0','0');
INSERT INTO [t22928] VALUES (2864,'قال تعالى: (سأل سائل بعذاب واقع (١) للكافرين ليس له دافع (٢)) .','1236','2863','0');
INSERT INTO [t22928] VALUES (2865,'قال تعالى: (من الله ذي المعارج (٣) تعرج الملائكة) .','1236','2863','0');
INSERT INTO [t22928] VALUES (2866,'قال تعالى: (يوم تكون السماء كالمهل (٨) وتكون الجبال كالعهن (٩) ولا يسأل حميم حميما (١٠)) .','1236','2863','0');
INSERT INTO [t22928] VALUES (2867,'قال تعالى: (يبصرونهم يود المجرم لو يفتدي من عذاب يومئذ ببنيه (١١)) .','1236','2863','0');
INSERT INTO [t22928] VALUES (2868,'قال تعالى: (نزاعة للشوى (١٦) تدعو من أدبر وتولى (١٧)) .','1237','2863','0');
INSERT INTO [t22928] VALUES (2869,'قال تعالى: (إن الإنسان خلق هلوعا (١٩) إذا مسه الشر جزوعا (٢٠) وإذا مسه الخير منوعا (٢١)) .','1237','2863','0');
INSERT INTO [t22928] VALUES (2870,'قال تعالى: (إلا المصلين (٢٢)) .','1237','2863','0');
INSERT INTO [t22928] VALUES (2871,'قال تعالى: (أولئك في جنات مكرمون (٣٥) فمال الذين كفروا قبلك مهطعين (٣٦) عن اليمين وعن الشمال عزين (٣٧)) .','1237','2863','0');
INSERT INTO [t22928] VALUES (2872,'قال تعالى: (يوم يخرجون من الأجداث سراعا كأنهم إلى نصب يوفضون (٤٣) خاشعة أبصارهم ترهقهم ذلة ذلك اليوم الذي كانوا يوعدون (٤٤)) .','1238','2863','0');
INSERT INTO [t22928] VALUES (2873,'نوح','1239','0','0');
INSERT INTO [t22928] VALUES (2874,'قال تعالى: (إنا أرسلنا نوحا إلى قومه أن أنذر قومك) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2875,'قال تعالى: (ألم تروا كيف خلق الله سبع سماوات (١٥)) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2876,'قال تعالى: (والله أنبتكم من الأرض نباتا (١٧)) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2877,'قال تعالى: (لتسلكوا منها سبلا فجاجا (٢٠)) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2878,'قال تعالى: (ومكروا مكرا كبارا (٢٢) وقالوا لا تذرن آلهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا (٢٣)) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2879,'قال تعالى: (مما خطيئاتهم أغرقوا فأدخلوا نارا فلم يجدوا لهم من دون الله أنصارا (٢٥) وقال نوح رب لا تذر على الأرض من الكافرين ديارا (٢٦)) .','1239','2873','0');
INSERT INTO [t22928] VALUES (2880,'الجن','1240','0','0');
INSERT INTO [t22928] VALUES (2881,'قال تعالى: (قل أوحي إلي أنه استمع نفر من الجن فقالوا إنا سمعنا قرآنا عجبا (١) يهدي إلى الرشد فآمنا به ولن نشرك بربنا أحدا (٢)) .','1240','2880','0');
INSERT INTO [t22928] VALUES (2882,'قال تعالى: (وأنه كان يقول سفيهنا على الله شططا (٤) وأنا ظننا أن لن تقول الإنس والجن على الله كذبا (٥)) .','1241','2880','0');
INSERT INTO [t22928] VALUES (2883,'قال تعالى: (وأنا كنا نقعد منها مقاعد للسمع فمن يستمع الآن يجد له شهابا رصدا (٩) وأنا لا ندري أشر أريد بمن في الأرض أم أراد بهم ربهم رشدا (١٠)) .','1241','2880','0');
INSERT INTO [t22928] VALUES (2884,'قال تعالى: (وأنا منا الصالحون ومنا دون ذلك كنا طرائق قددا (١١) وأنا ظننا أن لن نعجز الله في الأرض ولن نعجزه هربا (١٢)) .','1241','2880','0');
INSERT INTO [t22928] VALUES (2885,'قال تعالى: (وأن لو استقاموا على الطريقة لأسقيناهم ماء غدقا (١٦)) .','1241','2880','0');
INSERT INTO [t22928] VALUES (2886,'قال تعالى: (وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا (١٩)) .','1242','2880','0');
INSERT INTO [t22928] VALUES (2887,'قال تعالى: (إلا بلاغا من الله ورسالاته ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدا (٢٣) حتى إذا رأوا ما يوعدون فسيعلمون من أضعف ناصرا وأقل عددا (٢٤)) .','1242','2880','0');
INSERT INTO [t22928] VALUES (2888,'قال تعالى: (عالم الغيب فلا يظهر على غيبه أحدا (٢٦) إلا من ارتضى من رسول فإنه يسلك من بين يديه ومن خلفه رصدا (٢٧) ليعلم أن قد أبلغوا رسالات ربهم وأحاط بما لديهم وأحصى كل شيء عددا (٢٨)) .','1242','2880','0');
INSERT INTO [t22928] VALUES (2889,'المزمل','1243','0','0');
INSERT INTO [t22928] VALUES (2890,'قال تعالى: (يا أيها المزمل (١)) .','1243','2889','0');
INSERT INTO [t22928] VALUES (2891,'قال تعالى: (نصفه أو انقص منه قليلا (٣)) .','1243','2889','0');
INSERT INTO [t22928] VALUES (2892,'قال تعالى: (إن ناشئة الليل هي أشد وطئا وأقوم قيلا (٦)) .','1243','2889','0');
INSERT INTO [t22928] VALUES (2893,'قال تعالى: (واذكر اسم ربك وتبتل إليه تبتيلا (٨)) .','1244','2889','0');
INSERT INTO [t22928] VALUES (2894,'قال تعالى: (رب المشرق والمغرب لا إله إلا هو فاتخذه وكيلا (٩)) .','1244','2889','0');
INSERT INTO [t22928] VALUES (2895,'قال تعالى: (وذرني والمكذبين أولي النعمة ومهلهم قليلا (١١)) .','1244','2889','0');
INSERT INTO [t22928] VALUES (2896,'قال تعالى: (يوم ترجف الأرض والجبال وكانت الجبال كثيبا مهيلا (١٤)) .','1244','2889','0');
INSERT INTO [t22928] VALUES (2897,'قال تعالى: (فعصى فرعون الرسول. . . (١٦)) .','1244','2889','0');
INSERT INTO [t22928] VALUES (2898,'قال تعالى: (فكيف تتقون إن كفرتم يوما يجعل الولدان شيبا (١٧) السماء منفطر به كان وعده مفعولا (١٨)) .','1245','2889','0');
INSERT INTO [t22928] VALUES (2899,'قال تعالى: (إن ربك يعلم أنك تقوم أدنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك والله يقدر الليل والنهار علم أن لن تحصوه فتاب عليكم فاقرءوا ما تيسر من القرآن علم أن سيكون منكم مرضى وآخرون يضربون في الأرض يبتغون من فضل الله وآخرون يقاتلون في سبيل الله فاقرءوا ما تيسر منه وأقيموا الصلاة وآتوا الزكاة وأقرضوا الله قرضا حسنا وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا وأعظم أجرا واستغفروا الله إن الله غفور رحيم (٢٠)) .','1245','2889','0');
INSERT INTO [t22928] VALUES (2900,'المدثر','1246','0','0');
INSERT INTO [t22928] VALUES (2901,'قال تعالى: (يا أيها المدثر (١)) (المدثر) : كالمزمل. وقد ذكر.','1246','2900','0');
INSERT INTO [t22928] VALUES (2902,'قال تعالى: (ولا تمنن تستكثر (٦)) .','1246','2900','0');
INSERT INTO [t22928] VALUES (2903,'قال تعالى: (فإذا نقر في الناقور (٨) فذلك يومئذ يوم عسير (٩) على الكافرين غير يسير (١٠)) .','1246','2900','0');
INSERT INTO [t22928] VALUES (2904,'قال تعالى: (ذرني ومن خلقت وحيدا (١١)) .','1247','2900','0');
INSERT INTO [t22928] VALUES (2905,'قال تعالى: (لا تبقي ولا تذر (٢٨) لواحة للبشر (٢٩)) .','1247','2900','0');
INSERT INTO [t22928] VALUES (2906,'قال تعالى: (وما جعلنا أصحاب النار إلا ملائكة وما جعلنا عدتهم إلا فتنة للذين كفروا ليستيقن الذين أوتوا الكتاب ويزداد الذين آمنوا إيمانا ولا يرتاب الذين أوتوا الكتاب والمؤمنون وليقول الذين في قلوبهم مرض والكافرون ماذا أراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك إلا هو وما هي إلا ذكرى للبشر (٣١) كلا والقمر (٣٢) والليل إذ أدبر (٣٣)) .','1247','2900','0');
INSERT INTO [t22928] VALUES (2907,'قال تعالى: (نذيرا للبشر (٣٦))','1247','2900','0');
INSERT INTO [t22928] VALUES (2908,'قال تعالى: (لمن شاء منكم أن يتقدم أو يتأخر (٣٧)) .','1248','2900','0');
INSERT INTO [t22928] VALUES (2909,'قال تعالى: (إلا أصحاب اليمين (٣٩) في جنات يتساءلون (٤٠)) .','1248','2900','0');
INSERT INTO [t22928] VALUES (2910,'قال تعالى: (ما سلككم في سقر (٤٢) قالوا لم نك من المصلين (٤٣)) .','1248','2900','0');
INSERT INTO [t22928] VALUES (2911,'قال تعالى: (فما لهم عن التذكرة معرضين (٤٩) كأنهم حمر مستنفرة (٥٠) فرت من قسورة (٥١)) .','1248','2900','0');
INSERT INTO [t22928] VALUES (2912,'قال تعالى: (بل يريد كل امرئ منهم أن يؤتى صحفا منشرة (٥٢)) .','1249','2900','0');
INSERT INTO [t22928] VALUES (2913,'قال تعالى: (كلا إنه تذكرة (٥٤)) .','1249','2900','0');
INSERT INTO [t22928] VALUES (2914,'قال تعالى: (وما يذكرون إلا أن يشاء. . . (٥٦)) .','1249','2900','0');
INSERT INTO [t22928] VALUES (2915,'القيامة','1250','0','0');
INSERT INTO [t22928] VALUES (2916,'قال تعالى: (لا أقسم بيوم القيامة (١)) .','1250','2915','0');
INSERT INTO [t22928] VALUES (2917,'قال تعالى: (بلى قادرين على أن نسوي بنانه (٤) بل يريد الإنسان ليفجر أمامه (٥) يسأل أيان يوم القيامة (٦)) .','1251','2915','0');
INSERT INTO [t22928] VALUES (2918,'قال تعالى: (إلى ربك يومئذ المستقر (١٢)) .','1251','2915','0');
INSERT INTO [t22928] VALUES (2919,'قال تعالى: (بل الإنسان على نفسه بصيرة (١٤)) .','1251','2915','0');
INSERT INTO [t22928] VALUES (2920,'قال تعالى: (وجوه يومئذ ناضرة (٢٢) إلى ربها ناظرة (٢٣)) .','1251','2915','0');
INSERT INTO [t22928] VALUES (2921,'قال تعالى: (كلا إذا بلغت التراقي (١٦) وقيل من راق (١٧)) .','1252','2915','0');
INSERT INTO [t22928] VALUES (2922,'قال تعالى: (فلا صدق ولا صلى (٣١) ولكن كذب وتولى (٣٢) ثم ذهب إلى أهله يتمطى (٣٣)) .','1252','2915','0');
INSERT INTO [t22928] VALUES (2923,'قال تعالى: (أولى لك فأولى (٣٤)) .','1252','2915','0');
INSERT INTO [t22928] VALUES (2924,'قال تعالى: (أيحسب الإنسان أن يترك سدى (٣٦) ألم يك نطفة من مني يمنى (٣٧) ثم كان علقة فخلق فسوى (٣٨) فجعل منه الزوجين الذكر والأنثى (٣٩) أليس ذلك بقادر على أن يحيي الموتى (٤٠)) .','1253','2915','0');
INSERT INTO [t22928] VALUES (2925,'الإنسان','1254','0','0');
INSERT INTO [t22928] VALUES (2926,'قال تعالى: (هل أتى على الإنسان حين من الدهر لم يكن شيئا مذكورا (١)) .','1254','2925','0');
INSERT INTO [t22928] VALUES (2927,'قال تعالى: (إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا (٢)) .','1254','2925','0');
INSERT INTO [t22928] VALUES (2928,'قال تعالى: (إنا هديناه السبيل إما شاكرا وإما كفورا (٣)) .','1254','2925','0');
INSERT INTO [t22928] VALUES (2929,'قال تعالى: (إنا أعتدنا للكافرين سلاسلا وأغلالا وسعيرا (٤)) .','1254','2925','0');
INSERT INTO [t22928] VALUES (2930,'قال تعالى: (إن الأبرار يشربون من كأس كان مزاجها كافورا (٥) عينا يشرب بها عباد الله يفجرونها تفجيرا (٦)) .','1255','2925','0');
INSERT INTO [t22928] VALUES (2931,'قال تعالى: (يوفون بالنذر ويخافون يوما. . . (٧)) .','1255','2925','0');
INSERT INTO [t22928] VALUES (2932,'قال تعالى: (وجزاهم بما صبروا جنة وحريرا (١٢) متكئين فيها على الأرائك لا يرون فيها شمسا ولا زمهريرا (١٣) ودانية عليهم ظلالها وذللت قطوفها تذليلا (١٤)) .','1256','2925','0');
INSERT INTO [t22928] VALUES (2933,'قال تعالى: (ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا (١٥) قواريرا من فضة قدروها تقديرا (١٦)) .','1256','2925','0');
INSERT INTO [t22928] VALUES (2934,'قال تعالى: (عينا فيها تسمى سلسبيلا (١٨)) .','1257','2925','0');
INSERT INTO [t22928] VALUES (2935,'قال تعالى: (عاليهم ثياب سندس خضر وإستبرق. . . (٢١)) .','1257','2925','0');
INSERT INTO [t22928] VALUES (2936,'قال تعالى: (فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا (٢٤)) .','1258','2925','0');
INSERT INTO [t22928] VALUES (2937,'قال تعالى: (وما تشاءون إلا أن يشاء الله. . . (٣٠)) .','1258','2925','0');
INSERT INTO [t22928] VALUES (2938,'قال تعالى: (يدخل من يشاء في رحمته والظالمين أعد لهم عذابا أليما (٣١)) .','1258','2925','0');
INSERT INTO [t22928] VALUES (2939,'المرسلات','1259','0','0');
INSERT INTO [t22928] VALUES (2940,'قال تعالى: (والمرسلات عرفا (١) فالعاصفات عصفا (٢) والناشرات نشرا (٣) فالفارقات فرقا (٤) فالملقيات ذكرا (٥) عذرا أو نذرا (٦)) .','1259','2939','0');
INSERT INTO [t22928] VALUES (2941,'قال تعالى: (إنما توعدون لواقع (٧)) .','1259','2939','0');
INSERT INTO [t22928] VALUES (2942,'قال تعالى: (فإذا النجوم طمست (٨)) .','1259','2939','0');
INSERT INTO [t22928] VALUES (2943,'قال تعالى: (وإذا الرسل أقتت (١١)) .','1260','2939','0');
INSERT INTO [t22928] VALUES (2944,'قال تعالى: (لأي يوم أجلت (١٢) ليوم الفصل (١٣)) .','1260','2939','0');
INSERT INTO [t22928] VALUES (2945,'قال تعالى: (ويل يومئذ للمكذبين (١٥)) .','1260','2939','0');
INSERT INTO [t22928] VALUES (2946,'قال تعالى: (ألم نهلك الأولين (١٦) ثم نتبعهم الآخرين (١٧)) .','1260','2939','0');
INSERT INTO [t22928] VALUES (2947,'قال تعالى: (إلى قدر معلوم (٢٢) فقدرنا فنعم القادرون (٢٣))','1261','2939','0');
INSERT INTO [t22928] VALUES (2948,'قال تعالى: (ألم نجعل الأرض كفاتا (٢٥) أحياء وأمواتا (٢٦)) .','1261','2939','0');
INSERT INTO [t22928] VALUES (2949,'قال تعالى: (وأسقيناكم ماء فراتا (٢٧)) .','1261','2939','0');
INSERT INTO [t22928] VALUES (2950,'قال تعالى: (انطلقوا إلى ظل ذي ثلاث شعب (٣٠) لا ظليل ولا يغني من اللهب (٣١) إنها ترمي بشرر كالقصر (٣٢) كأنه جمالة صفر (٣٣)) .','1261','2939','0');
INSERT INTO [t22928] VALUES (2951,'قال تعالى: (هذا يوم لا ينطقون (٣٥)) .','1262','2939','0');
INSERT INTO [t22928] VALUES (2952,'قال تعالى: (ولا يؤذن لهم فيعتذرون (٣٦)) .','1262','2939','0');
INSERT INTO [t22928] VALUES (2953,'قال تعالى: (كلوا وتمتعوا قليلا إنكم مجرمون (٤٦)) .','1262','2939','0');
INSERT INTO [t22928] VALUES (2954,'النبأ','1263','0','0');
INSERT INTO [t22928] VALUES (2955,'قال تعالى: (عم يتساءلون (١) عن النبأ العظيم (٢) الذي هم فيه مختلفون (٣)) .','1263','2954','0');
INSERT INTO [t22928] VALUES (2956,'قال تعالى: (وخلقناكم أزواجا (٨)) .','1263','2954','0');
INSERT INTO [t22928] VALUES (2957,'قال تعالى: (وجنات ألفافا (١٦)) .','1263','2954','0');
INSERT INTO [t22928] VALUES (2958,'قال تعالى: (يوم ينفخ في الصور فتأتون أفواجا (١٨)) .','1263','2954','0');
INSERT INTO [t22928] VALUES (2959,'قال تعالى: (إن جهنم كانت مرصادا (٢١) للطاغين مآبا (٢٢) لابثين فيها أحقابا (٢٣) لا يذوقون فيها بردا ولا شرابا (٢٤) إلا حميما وغساقا (٢٥) جزاء وفاقا (٢٦)) .','1264','2954','0');
INSERT INTO [t22928] VALUES (2960,'قال تعالى: (وكذبوا بآياتنا كذابا (٢٨) وكل شيء أحصيناه كتابا (٢٩)) .','1264','2954','0');
INSERT INTO [t22928] VALUES (2961,'قال تعالى: (إن للمتقين مفازا (٣١) حدائق وأعنابا (٣٢) وكواعب أترابا (٣٣) وكأسا دهاقا (٣٤) لا يسمعون فيها لغوا ولا كذابا (٣٥)) (حدائق) : بدل من «مفازا» .','1264','2954','0');
INSERT INTO [t22928] VALUES (2962,'قال تعالى: (جزاء من ربك عطاء حسابا (٣٦) رب السماوات والأرض وما بينهما الرحمن لا يملكون منه خطابا (٣٧)) .','1264','2954','0');
INSERT INTO [t22928] VALUES (2963,'قال تعالى: (يوم يقوم الروح والملائكة صفا لا يتكلمون إلا من أذن له الرحمن. . .)) .','1265','2954','0');
INSERT INTO [t22928] VALUES (2964,'قال تعالى: (إنا أنذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه. . . (٤٠)) .','1265','2954','0');
INSERT INTO [t22928] VALUES (2965,'النازعات','1266','0','0');
INSERT INTO [t22928] VALUES (2966,'قال تعالى: (والنازعات غرقا (١) والناشطات نشطا (٢) والسابحات سبحا (٣) فالسابقات سبقا (٤) فالمدبرات أمرا (٥) يوم ترجف الراجفة (٦) تتبعها الرادفة (٧))','1266','2965','0');
INSERT INTO [t22928] VALUES (2967,'قال تعالى: (يقولون أئنا لمردودون في الحافرة (١٠)) .','1266','2965','0');
INSERT INTO [t22928] VALUES (2968,'قال تعالى: (اذهب إلى فرعون إنه طغى (١٧)) .','1266','2965','0');
INSERT INTO [t22928] VALUES (2969,'قال تعالى: (فقل هل لك إلى أن تزكى (١٨)) .','1266','2965','0');
INSERT INTO [t22928] VALUES (2970,'قال تعالى: (فأخذه الله نكال الآخرة والأولى (٢٥) إن في ذلك لعبرة لمن يخشى (٢٦)) .','1266','2965','0');
INSERT INTO [t22928] VALUES (2971,'قال تعالى: (أأنتم أشد خلقا أم السماء بناها (٢٧) رفع سمكها فسواها (٢٨) وأغطش ليلها وأخرج ضحاها (٢٩) والأرض بعد ذلك دحاها (٣٠) أخرج منها ماءها ومرعاها (٣١) والجبال أرساها (٣٢) متاعا لكم ولأنعامكم (٣٣)) .','1267','2965','0');
INSERT INTO [t22928] VALUES (2972,'قال تعالى: (فإذا جاءت الطامة الكبرى (٣٤) يوم يتذكر الإنسان ما سعى (٣٥)) .','1267','2965','0');
INSERT INTO [t22928] VALUES (2973,'قال تعالى: (فإن الجحيم هي المأوى (٣٩)) .','1267','2965','0');
INSERT INTO [t22928] VALUES (2974,'قال تعالى: (كأنهم يوم يرونها لم يلبثوا إلا عشية أو ضحاها (٤٦)) .','1267','2965','0');
INSERT INTO [t22928] VALUES (2975,'عبس','1268','0','0');
INSERT INTO [t22928] VALUES (2976,'قال تعالى: (عبس وتولى (١) أن جاءه الأعمى (٢)) .','1268','2975','0');
INSERT INTO [t22928] VALUES (2977,'قال تعالى: (أو يذكر فتنفعه الذكرى (٤) أما من استغنى (٥) فأنت له تصدى (٦)) .','1268','2975','0');
INSERT INTO [t22928] VALUES (2978,'قال تعالى: (كلا إنها تذكرة (١١) فمن شاء ذكره (١٢) في صحف مكرمة (١٣) مرفوعة مطهرة (١٤) بأيدي سفرة (١٥)) .','1268','2975','0');
INSERT INTO [t22928] VALUES (2979,'قال تعالى: (قتل الإنسان ما أكفره (١٧)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2980,'قال تعالى: (من أي شيء خلقه (١٨) من نطفة خلقه فقدره (١٩)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2981,'قال تعالى: (ثم السبيل يسره (٢٠)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2982,'قال تعالى: (كلا لما يقض ما أمره (٢٣)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2983,'قال تعالى: (فلينظر الإنسان إلى طعامه (٢٤) أنا صببنا (٢٥)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2984,'قال تعالى: (فإذا جاءت الصاخة (٣٣)) .','1269','2975','0');
INSERT INTO [t22928] VALUES (2985,'التكوير','1270','0','0');
INSERT INTO [t22928] VALUES (2986,'قال تعالى: (إذا الشمس كورت (١)) .','1270','2985','0');
INSERT INTO [t22928] VALUES (2987,'قال تعالى: (فلا أقسم بالخنس (١٥) الجوار الكنس (١٦)) .','1270','2985','0');
INSERT INTO [t22928] VALUES (2988,'قال تعالى: (إنه لقول رسول كريم (١٩) ذي قوة عند ذي العرش مكين (٢٠) مطاع ثم أمين (٢١)) .','1270','2985','0');
INSERT INTO [t22928] VALUES (2989,'قال تعالى: (ولقد رآه بالأفق المبين (٢٣) وما هو على الغيب بضنين (٢٤)) .','1270','2985','0');
INSERT INTO [t22928] VALUES (2990,'قال تعالى: (فأين تذهبون (٢٦) إن هو إلا ذكر للعالمين (٢٧) لمن شاء منكم أن يستقيم (٢٨) وما تشاءون إلا أن يشاء الله رب العالمين (٢٩)) .','1270','2985','0');
INSERT INTO [t22928] VALUES (2991,'الانفطار','1271','0','0');
INSERT INTO [t22928] VALUES (2992,'قال تعالى: (إذا السماء انفطرت (١)) .','1271','2991','0');
INSERT INTO [t22928] VALUES (2993,'قال تعالى: (يا أيها الإنسان ما غرك بربك الكريم (٦) الذي خلقك فسواك فعدلك (٧)) .','1271','2991','0');
INSERT INTO [t22928] VALUES (2994,'قال تعالى: (في أي صورة ما شاء ركبك (٨)) .','1271','2991','0');
INSERT INTO [t22928] VALUES (2995,'قال تعالى: (وإن عليكم لحافظين (١٠) كراما كاتبين (١١) يعلمون ما تفعلون (١٢)) .','1271','2991','0');
INSERT INTO [t22928] VALUES (2996,'قال تعالى: (وإن الفجار لفي جحيم (١٤) يصلونها يوم الدين (١٥)) .','1271','2991','0');
INSERT INTO [t22928] VALUES (2997,'قال تعالى: (يوم لا تملك نفس لنفس شيئا والأمر يومئذ لله (١٩)) .','1272','2991','0');
INSERT INTO [t22928] VALUES (2998,'المطففين','1273','0','0');
INSERT INTO [t22928] VALUES (2999,'قال تعالى: (وإذا كالوهم أو وزنوهم يخسرون (٣)) .','1273','2998','0');
INSERT INTO [t22928] VALUES (3000,'قال تعالى: (ألا يظن أولئك أنهم مبعوثون (٤)) .','1273','2998','0');
INSERT INTO [t22928] VALUES (3001,'قال تعالى: (ليوم عظيم (٥) يوم يقوم الناس لرب العالمين (٦) كلا إن كتاب الفجار لفي سجين (٧)) .','1273','2998','0');
INSERT INTO [t22928] VALUES (3002,'قال تعالى: (كتاب مرقوم (٩)) .','1274','2998','0');
INSERT INTO [t22928] VALUES (3003,'قال تعالى: (ثم يقال هذا الذي كنتم به تكذبون (١٧) كلا إن كتاب الأبرار لفي عليين (١٨) وما أدراك ما عليون (١٩)) .','1274','2998','0');
INSERT INTO [t22928] VALUES (3004,'قال تعالى: (على الأرائك ينظرون (٢٣)) .','1274','2998','0');
INSERT INTO [t22928] VALUES (3005,'قال تعالى: (عينا يشرب بها المقربون (٢٨)) .','1274','2998','0');
INSERT INTO [t22928] VALUES (3006,'قال تعالى: (هل ثوب الكفار ما كانوا يفعلون (٣٦)) .','1274','2998','0');
INSERT INTO [t22928] VALUES (3007,'الانشقاق','1275','0','0');
INSERT INTO [t22928] VALUES (3008,'قال تعالى: (إذا السماء انشقت (١)) .','1275','3007','0');
INSERT INTO [t22928] VALUES (3009,'قال تعالى: (يا أيها الإنسان إنك كادح إلى ربك كدحا فملاقيه (٦)) .','1275','3007','0');
INSERT INTO [t22928] VALUES (3010,'قال تعالى: (وينقلب إلى أهله مسرورا (٩) وأما من أوتي كتابه وراء ظهره (١٠) فسوف يدعو ثبورا (١١)) .','1275','3007','0');
INSERT INTO [t22928] VALUES (3011,'قال تعالى: (والليل وما وسق (١٧)) .','1275','3007','0');
INSERT INTO [t22928] VALUES (3012,'قال تعالى: (لتركبن طبقا عن طبق (١٩) فما لهم لا يؤمنون (٢٠)) .','1275','3007','0');
INSERT INTO [t22928] VALUES (3013,'قال تعالى: (إلا الذين آمنوا وعملوا الصالحات. . . (٢٥)) .','1276','3007','0');
INSERT INTO [t22928] VALUES (3014,'البروج','1277','0','0');
INSERT INTO [t22928] VALUES (3015,'قال تعالى: (والسماء ذات البروج (١) واليوم الموعود (٢) وشاهد ومشهود (٣) قتل أصحاب الأخدود (٤) النار ذات الوقود (٥) إذ هم عليها قعود (٦)) .','1277','3014','0');
INSERT INTO [t22928] VALUES (3016,'قال تعالى: (إن الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق (١٠)) .','1277','3014','0');
INSERT INTO [t22928] VALUES (3017,'قال تعالى: (وهو الغفور الودود (١٤) ذو العرش المجيد (١٥)) .','1277','3014','0');
INSERT INTO [t22928] VALUES (3018,'قال تعالى: (هل أتاك حديث الجنود (١٧) فرعون وثمود (١٨)) .','1277','3014','0');
INSERT INTO [t22928] VALUES (3019,'قال تعالى: (بل هو قرآن مجيد (٢١) في لوح محفوظ (٢٢)) .','1277','3014','0');
INSERT INTO [t22928] VALUES (3020,'الطارق','1278','0','0');
INSERT INTO [t22928] VALUES (3021,'قال تعالى {إن كل نفس لما عليها حافظ}','1278','3020','0');
INSERT INTO [t22928] VALUES (3022,'قال تعالى: (خلق من ماء دافق (٦)) .','1278','3020','0');
INSERT INTO [t22928] VALUES (3023,'قال تعالى: (إنه على رجعه لقادر (٨) يوم تبلى السرائر (٩)) .','1278','3020','0');
INSERT INTO [t22928] VALUES (3024,'قال تعالى: (فمهل الكافرين أمهلهم رويدا (١٧)) .','1279','3020','0');
INSERT INTO [t22928] VALUES (3025,'الأعلى','1280','0','0');
INSERT INTO [t22928] VALUES (3026,'قال تعالى: (سبح اسم ربك الأعلى (١)) .','1280','3025','0');
INSERT INTO [t22928] VALUES (3027,'قال تعالى: (فجعله غثاء أحوى (٥)) .','1280','3025','0');
INSERT INTO [t22928] VALUES (3028,'قال تعالى: (سنقرئك فلا تنسى (٦)) .','1280','3025','0');
INSERT INTO [t22928] VALUES (3029,'قال تعالى: (بل تؤثرون الحياة الدنيا (١٦)) .','1280','3025','0');
INSERT INTO [t22928] VALUES (3030,'الغاشية','1281','0','0');
INSERT INTO [t22928] VALUES (3031,'قال تعالى: (وجوه يومئذ خاشعة (٢) عاملة ناصبة (٣)) .','1281','3030','0');
INSERT INTO [t22928] VALUES (3032,'قال تعالى: (ليس لهم طعام إلا من ضريع (٦)) .','1281','3030','0');
INSERT INTO [t22928] VALUES (3033,'قال تعالى: (إلا من تولى وكفر (٢٣) فيعذبه الله العذاب الأكبر (٢٤) إن إلينا إيابهم (٢٥)) .','1281','3030','0');
INSERT INTO [t22928] VALUES (3034,'الفجر','1282','0','0');
INSERT INTO [t22928] VALUES (3035,'قال تعالى: (والفجر (١) وليال عشر (٢) والشفع والوتر (٣) والليل إذا يسر (٤)) .','1282','3034','0');
INSERT INTO [t22928] VALUES (3036,'قال تعالى: (ألم تر كيف فعل ربك بعاد (٦) إرم ذات العماد (٧)) .','1282','3034','0');
INSERT INTO [t22928] VALUES (3037,'قال تعالى: (وثمود الذين جابوا الصخر بالواد (٩) وفرعون ذي الأوتاد (١٠)) .','1282','3034','0');
INSERT INTO [t22928] VALUES (3038,'قال تعالى: (الذين طغوا في البلاد (١١)) .','1283','3034','0');
INSERT INTO [t22928] VALUES (3039,'قال تعالى: (فأما الإنسان إذا ما ابتلاه ربه فأكرمه ونعمه فيقول ربي أكرمن (١٥)) .','1283','3034','0');
INSERT INTO [t22928] VALUES (3040,'قال تعالى: (ولا تحاضون على طعام المسكين (١٨)) .','1283','3034','0');
INSERT INTO [t22928] VALUES (3041,'قال تعالى: (وجاء ربك والملك صفا صفا (٢٢) وجيء يومئذ بجهنم يومئذ يتذكر الإنسان وأنى له الذكرى (٢٣) يقول ياليتني قدمت لحياتي (٢٤))','1283','3034','0');
INSERT INTO [t22928] VALUES (3042,'قال تعالى: (فيومئذ لا يعذب عذابه أحد (٢٥) ولا يوثق وثاقه أحد (٢٦) يا أيتها النفس المطمئنة (٢٧) ارجعي إلى ربك راضية مرضية (٢٨)) .','1284','3034','0');
INSERT INTO [t22928] VALUES (3043,'البلد','1285','0','0');
INSERT INTO [t22928] VALUES (3044,'قال تعالى: (لا أقسم بهذا البلد (١) وأنت حل بهذا البلد (٢) ووالد وما ولد (٣) لقد خلقنا الإنسان في كبد (٤)) .','1285','3043','0');
INSERT INTO [t22928] VALUES (3045,'قال تعالى: (فلا اقتحم العقبة (١١)) .','1285','3043','0');
INSERT INTO [t22928] VALUES (3046,'قال تعالى: (وما أدراك ما العقبة (١٢) فك رقبة (١٣) أو إطعام في يوم ذي مسغبة (١٤) يتيما ذا مقربة (١٥)) .','1285','3043','0');
INSERT INTO [t22928] VALUES (3047,'قال تعالى: (ثم كان من الذين آمنوا. . . (١٧)) .','1286','3043','0');
INSERT INTO [t22928] VALUES (3048,'قال تعالى: (عليهم نار مؤصدة (٢٠)) .','1286','3043','0');
INSERT INTO [t22928] VALUES (3049,'الشمس','1287','0','0');
INSERT INTO [t22928] VALUES (3050,'قال تعالى: (والشمس وضحاها (١) والقمر إذا تلاها (٢) والنهار إذا جلاها (٣) والليل إذا يغشاها (٤) والسماء وما بناها (٥) والأرض وما طحاها (٦) ونفس وما سواها (٧) فألهمها فجورها وتقواها (٨) قد أفلح من زكاها (٩) وقد خاب من دساها (١٠)) .','1287','3049','0');
INSERT INTO [t22928] VALUES (3051,'قال تعالى: (كذبت ثمود بطغواها (١١) إذ انبعث أشقاها (١٢)) .','1287','3049','0');
INSERT INTO [t22928] VALUES (3052,'قال تعالى: (فقال لهم رسول الله ناقة الله وسقياها (١٣)) .','1287','3049','0');
INSERT INTO [t22928] VALUES (3053,'قال تعالى: (ولا يخاف عقباها (١٥)) .','1287','3049','0');
INSERT INTO [t22928] VALUES (3054,'الليل','1288','0','0');
INSERT INTO [t22928] VALUES (3055,'قال تعالى: (وما خلق الذكر والأنثى (٣)) .','1288','3054','0');
INSERT INTO [t22928] VALUES (3056,'قال تعالى: (وما يغني عنه ماله إذا تردى (١١)) .','1288','3054','0');
INSERT INTO [t22928] VALUES (3057,'قال تعالى: (فأنذرتكم نارا تلظى (١٤)) .','1288','3054','0');
INSERT INTO [t22928] VALUES (3058,'قال تعالى: (وما لأحد عنده من نعمة تجزى (١٩) إلا ابتغاء وجه ربه الأعلى (٢٠)) .','1288','3054','0');
INSERT INTO [t22928] VALUES (3059,'الضحى','1289','0','0');
INSERT INTO [t22928] VALUES (3060,'قال تعالى: (ما ودعك ربك وما قلى (٣)) .','1289','3059','0');
INSERT INTO [t22928] VALUES (3061,'قال تعالى: (فأما اليتيم فلا تقهر (٩) وأما السائل فلا تنهر (١٠) وأما بنعمة ربك فحدث (١١)) .','1289','3059','0');
INSERT INTO [t22928] VALUES (3062,'الشرح','1290','0','0');
INSERT INTO [t22928] VALUES (3063,'قال تعالى: (فإن مع العسر يسرا (٥) إن مع العسر يسرا (٦)) .','1290','3062','0');
INSERT INTO [t22928] VALUES (3064,'التين','1291','0','0');
INSERT INTO [t22928] VALUES (3065,'قال تعالى: (وطور سينين (٢)) .','1291','3064','0');
INSERT INTO [t22928] VALUES (3066,'قال تعالى: (لقد خلقنا الإنسان في أحسن تقويم (٤)) .','1291','3064','0');
INSERT INTO [t22928] VALUES (3067,'قال تعالى: (ثم رددناه أسفل سافلين (٥)) .','1291','3064','0');
INSERT INTO [t22928] VALUES (3068,'قال تعالى: (فما يكذبك بعد بالدين (٧)) .','1291','3064','0');
INSERT INTO [t22928] VALUES (3069,'قال تعالى: (أليس الله بأحكم الحاكمين (٨)) .','1291','3064','0');
INSERT INTO [t22928] VALUES (3070,'العلق','1292','0','0');
INSERT INTO [t22928] VALUES (3071,'قال تعالى: (اقرأ باسم ربك الذي خلق (١)) .','1292','3070','0');
INSERT INTO [t22928] VALUES (3072,'قال تعالى: (كلا إن الإنسان ليطغى (٦) أن رآه استغنى (٧)) .','1292','3070','0');
INSERT INTO [t22928] VALUES (3073,'قال تعالى: (كلا لئن لم ينته لنسفعا بالناصية (١٥) ناصية كاذبة خاطئة (١٦)) .','1292','3070','0');
INSERT INTO [t22928] VALUES (3074,'قال تعالى: (فليدع ناديه (١٧) سندع الزبانية (١٨)) .','1292','3070','0');
INSERT INTO [t22928] VALUES (3075,'القدر','1293','0','0');
INSERT INTO [t22928] VALUES (3076,'قال تعالى: (إنا أنزلناه في ليلة القدر (١)) .','1293','3075','0');
INSERT INTO [t22928] VALUES (3077,'قال تعالى: (تنزل الملائكة والروح فيها بإذن ربهم من كل أمر (٤)) .','1293','3075','0');
INSERT INTO [t22928] VALUES (3078,'قال تعالى: (سلام هي حتى مطلع الفجر (٥)) .','1293','3075','0');
INSERT INTO [t22928] VALUES (3079,'البينة','1294','0','0');
INSERT INTO [t22928] VALUES (3080,'قال تعالى: (لم يكن الذين كفروا من أهل الكتاب والمشركين منفكين حتى تأتيهم البينة (١)) .','1294','3079','0');
INSERT INTO [t22928] VALUES (3081,'قال تعالى: (رسول من الله يتلو صحفا مطهرة (٢) فيها كتب قيمة (٣)) .','1294','3079','0');
INSERT INTO [t22928] VALUES (3082,'قال تعالى: (وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة وذلك دين القيمة (٥)) .','1294','3079','0');
INSERT INTO [t22928] VALUES (3083,'قال تعالى: (إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها أولئك هم شر البرية (٦)) .','1294','3079','0');
INSERT INTO [t22928] VALUES (3084,'قال تعالى: (جزاؤهم عند ربهم جنات عدن تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم ورضوا عنه ذلك لمن خشي ربه (٨)) .','1295','3079','0');
INSERT INTO [t22928] VALUES (3085,'الزلزلة','1296','0','0');
INSERT INTO [t22928] VALUES (3086,'قال تعالى: (إذا زلزلت الأرض زلزالها (١) وأخرجت الأرض أثقالها (٢) وقال الإنسان ما لها (٣) يومئذ تحدث أخبارها (٤)) .','1296','3085','0');
INSERT INTO [t22928] VALUES (3087,'قال تعالى: (بأن ربك أوحى لها (٥) يومئذ يصدر الناس أشتاتا ليروا أعمالهم (٦)) .','1296','3085','0');
INSERT INTO [t22928] VALUES (3088,'قال تعالى: (فمن يعمل مثقال ذرة خيرا يره (٧) ومن يعمل مثقال ذرة شرا يره (٨)) .','1296','3085','0');
INSERT INTO [t22928] VALUES (3089,'العاديات','1297','0','0');
INSERT INTO [t22928] VALUES (3090,'قال تعالى: (والعاديات ضبحا (١) فالموريات قدحا (٢) فالمغيرات صبحا (٣)) .','1297','3089','0');
INSERT INTO [t22928] VALUES (3091,'قال تعالى: (فأثرن به نقعا (٤) فوسطن به جمعا (٥)) .','1297','3089','0');
INSERT INTO [t22928] VALUES (3092,'قال تعالى: (إن الإنسان لربه لكنود (٦) وإنه على ذلك لشهيد (٧) وإنه لحب الخير لشديد (٨)) .','1297','3089','0');
INSERT INTO [t22928] VALUES (3093,'قال تعالى: (أفلا يعلم إذا بعثر ما في القبور (٩) وحصل ما في الصدور (١٠) إن ربهم بهم يومئذ لخبير (١١)) .','1297','3089','0');
INSERT INTO [t22928] VALUES (3094,'القارعة','1298','0','0');
INSERT INTO [t22928] VALUES (3095,'قال تعالى: (القارعة (١) ما القارعة (٢)) .','1298','3094','0');
INSERT INTO [t22928] VALUES (3096,'قال تعالى: (يوم يكون الناس كالفراش المبثوث (٤)) .','1298','3094','0');
INSERT INTO [t22928] VALUES (3097,'قال تعالى: (فهو في عيشة راضية (٧)) .','1298','3094','0');
INSERT INTO [t22928] VALUES (3098,'قال تعالى: (فأمه هاوية (٩) وما أدراك ما هيه (١٠) نار حامية (١١)) .','1298','3094','0');
INSERT INTO [t22928] VALUES (3099,'التكاثر','1299','0','0');
INSERT INTO [t22928] VALUES (3100,'قال تعالى: (كلا لو تعلمون علم اليقين (٥)) .','1299','3099','0');
INSERT INTO [t22928] VALUES (3101,'قال تعالى: (لترون الجحيم (٦) ثم لترونها عين اليقين (٧)) .','1299','3099','0');
INSERT INTO [t22928] VALUES (3102,'العصر','1300','0','0');
INSERT INTO [t22928] VALUES (3103,'قال تعالى: (إلا الذين آمنوا وعملوا الصالحات وتواصوا بالحق وتواصوا بالصبر (٣)) والجمهور على إسكان باء «الصبر» ، وكسرها قوم، وهو على لغة من ينقل الضمة والكسرة في الوقف إلى الساكن قبلها حرصا على بيان الإعراب.','1300','3102','0');
INSERT INTO [t22928] VALUES (3104,'الهمزة','1301','0','0');
INSERT INTO [t22928] VALUES (3105,'قال تعالى: (ويل لكل همزة لمزة (١) الذي جمع مالا وعدده (٢) يحسب أن ماله أخلده (٣)) .','1301','3104','0');
INSERT INTO [t22928] VALUES (3106,'قال تعالى: (كلا لينبذن في الحطمة (٤)) .','1301','3104','0');
INSERT INTO [t22928] VALUES (3107,'قال تعالى: (نار الله الموقدة (٦) التي تطلع على الأفئدة (٧) إنها عليهم مؤصدة (٨) في عمد ممددة (٩)) .','1301','3104','0');
INSERT INTO [t22928] VALUES (3108,'الفيل','1303','0','0');
INSERT INTO [t22928] VALUES (3109,'قال تعالى: (وأرسل عليهم طيرا أبابيل (٣) ترميهم بحجارة من سجيل (٤) فجعلهم كعصف مأكول (٥)) .','1303','3108','0');
INSERT INTO [t22928] VALUES (3110,'قريش','1304','0','0');
INSERT INTO [t22928] VALUES (3111,'قال تعالى: (لإيلاف قريش (١) إيلافهم رحلة الشتاء والصيف (٢)) .','1304','3110','0');
INSERT INTO [t22928] VALUES (3112,'قال تعالى: (الذي أطعمهم من جوع وآمنهم من خوف (٤)) .','1304','3110','0');
INSERT INTO [t22928] VALUES (3113,'الماعون','1305','0','0');
INSERT INTO [t22928] VALUES (3114,'قال تعالى: (فذلك الذي يدع اليتيم (٢)) .','1305','3113','0');
INSERT INTO [t22928] VALUES (3115,'الكوثر','1306','0','0');
INSERT INTO [t22928] VALUES (3116,'قال تعالى: (فصل لربك وانحر (٢) إن شانئك هو الأبتر (٣)) .','1306','3115','0');
INSERT INTO [t22928] VALUES (3117,'الكافرون','1307','0','0');
INSERT INTO [t22928] VALUES (3118,'قال تعالى: (قل يا أيها الكافرون (١) لا أعبد ما تعبدون (٢)) .','1307','3117','0');
INSERT INTO [t22928] VALUES (3119,'النصر','1308','0','0');
INSERT INTO [t22928] VALUES (3120,'قال تعالى: (ورأيت الناس يدخلون في دين الله أفواجا (٢)) .','1308','3119','0');
INSERT INTO [t22928] VALUES (3121,'المسد','1309','0','0');
INSERT INTO [t22928] VALUES (3122,'قال تعالى: (تبت يدا أبي لهب وتب (١))','1309','3121','0');
INSERT INTO [t22928] VALUES (3123,'قال تعالى: (ما أغنى عنه ماله وما كسب (٢)) .','1309','3121','0');
INSERT INTO [t22928] VALUES (3124,'قال تعالى: (وامرأته حمالة الحطب (٤) في جيدها حبل من مسد (٥)) .','1309','3121','0');
INSERT INTO [t22928] VALUES (3125,'الإخلاص','1310','0','0');
INSERT INTO [t22928] VALUES (3126,'قال تعالى: (قل هو الله أحد الله (١)) .','1310','3125','0');
INSERT INTO [t22928] VALUES (3127,'قال تعالى: (ولم يكن له كفوا أحد (٤)) .','1310','3125','0');
INSERT INTO [t22928] VALUES (3128,'الفلق','1311','0','0');
INSERT INTO [t22928] VALUES (3129,'قال تعالى: (قل أعوذ برب الفلق (١) من شر ما خلق (٢) ومن شر غاسق إذا وقب (٣) ومن شر النفاثات في العقد (٤)) .','1311','3128','0');
INSERT INTO [t22928] VALUES (3130,'الناس','1312','0','0');
INSERT INTO [t22928] VALUES (3131,'قال تعالى: (قل أعوذ برب الناس (١)) .','1312','3130','0');
INSERT INTO [t22928] VALUES (3132,'قال تعالى: (من شر الوسواس الخناس (٤) الذي يوسوس في صدور الناس (٥)) .','1312','3130','0');
INSERT INTO [t22928] VALUES (3133,'قال تعالى: (من الجنة والناس (٦)) .','1312','3130','0');
INSERT INTO [t22928] VALUES (3134,'الفاتحة','1313','0','0');
