INSERT INTO [t2300] VALUES (621,'ماحكم الشرع في نظركم في زيارة قبور الأولياء والصالحين هل هو محرم وهل يجوز لنا أن نزورهم؟','611','602','0');
INSERT INTO [t2300] VALUES (622,'ماحكم الشرع في نظركم في زيارة قبور الأولياء والصالحين هل هو محرم وهل يجوز لنا أن نزورهم؟','611','602','0');
INSERT INTO [t2300] VALUES (623,'هذا مستمع من المملكة المغربية يقول في هذا السؤال هل زيارة الأولياء تجوز أم لا وإذا كانت تجوز كيف الزيارة وكيف يكون لنا أن نترحم عليهم؟','612','602','0');
