INSERT INTO [t2300] VALUES (1037,'السؤال: ما هي شروط الحديث الصحيح؟','1022','1036','0');
INSERT INTO [t2300] VALUES (1038,'حامد بن إبراهيم من هيئة الأمر بالمعروف والنهي عن المنكر يقول هل يأثم الإنسان إذا لم يعمل بالأحاديث التي انفرد بروايتها شخص واحد؟','1023','1036','0');
INSERT INTO [t2300] VALUES (1039,'الأحاديث الموقوفة هل يعمل بها وأيضا الأحاديث المقطوعة هل تعتبر من الضعيفة؟','1024','1036','0');
