INSERT INTO [t2300] VALUES (1041,'إذا قرأت في كتب السنة أجد فيها أحاديث صحيحة وضعيفة وموضوعة فهل يجوز الاستدلال بها والعمل بها أم لا؟','1026','1036','0');
