INSERT INTO [t2300] VALUES (4698,'هذا السائل سوداني أبو حذيفة يذكر بأنه خارج السودان في العمل منذ ثلاثة سنوات يقول ولم أقم بواجب الأضحية ولم أقم أيضا بتوكيل أحد في ذبحها هل علي كفارة أفيدوني في ذلك؟','4680','4693','0');
