INSERT INTO [t2300] VALUES (4710,'أحسن الله إليكم وبارك فيكم فضيلة الشيخ هذا السائل الفاضل إبراهيم يقول الأغنام الموسومة في أذنيها ولم ينقص من أذنيها شيء وإنما هو شق طولي في الأذن وهذا الوسم يستخدمه الرعاة في الأغنام للتعرف عليها عندما تذهب عند الآخرين من الرعاة هل هذا الوسم أو الشق يجعلها غير صالحة للأضحية؟','4692','4693','0');
INSERT INTO [t2300] VALUES (4711,'يقول هل يجوز ذبح الطلي المخصي أضحية أو لا يجوز؟','4693','4693','0');
INSERT INTO [t2300] VALUES (4712,'إذا اشترى إنسان ذبيحتين يقصد إحداهما للأضحية والأخرى لحما فهل يشترط أن يعين التي سيضحي بها بعينها ولا يجوز له تبديلها بالأخرى؟','4694','4693','0');
