INSERT INTO [t2300] VALUES (4715,'بارك الله فيكم سائل يقول لي والدة متوفاة وأريد أن أضحي عنها من مالي فهل أشركها في أضحيتي وأهل بيتي أم أضحي عنها بأضحية خاصة؟','4697','4693','0');
