INSERT INTO [t2300] VALUES (4718,'بارك الله فيكم فضيلة الشيخ المستمع محمد عبد الله من الرياض يقول بأن له أخا تعرض لحادث توفي بعدها هل يجوز لنا أن نضحي له أو نحج عنه إلى بيت الله الحرام نرجو الافادة؟','4700','4693','0');
INSERT INTO [t2300] VALUES (4719,'جزاكم الله خيرا من أسئلة هذا السائل يقول هل تجوز الأضحية عن الميت؟','4701','4693','0');
INSERT INTO [t2300] VALUES (4720,'هذا السائل يقول يا فضيلة الشيخ بالنسبة للأضحية عن الميت في تقاليدنا القديمة يقول بأن الميت له سبع أضاحي هل هذا صحيح؟','4702','4693','0');
INSERT INTO [t2300] VALUES (4721,'لديه سؤال يقول فيه، يوجد عندنا عادة إذا دخل شهر ذي الحجة في العشر الأولى يذبح كل بيت ذبيحة ويقول اللهم اجعله لأرواح موتانا، وهذا شيء كل عام ويسمونه عندنا حج الأموات، هل هذا صحيح؟','4703','4693','0');
