INSERT INTO [t2300] VALUES (6276,'السؤال: تقول السائلة بالنسبة لوضع الحناء والحليب والبصل والثوم وغيرها من المأكولات التي توضع على شعر الرأس هل في ذلك بأس؟','6257','6275','0');
INSERT INTO [t2300] VALUES (6277,'السؤال: تقول هل يجوز استعمال بعض المواد الغذائية كالطحين والبيض والعسل واللبن ونحوها كعلاج لما قد يصيب الوجه من أمراض كالكلف ونحوه وإن لم يكن لمرض بل لمجرد تجميل البشرة فهل يجوز أيضا؟','6258','6275','0');
INSERT INTO [t2300] VALUES (6278,'السؤال: تقول إذا وضعت المرأة في وجهها بعض الأطعمة لغرض إزالة البقع أو لصفاء البشرة مثل الليمون والطماطم والخيار فما حكم الشرع في هذا العمل في نظركم فضيلة الشيخ؟','6259','6275','0');
